INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES (1145, "آب: اسم من الأصل الاكادي  )  ? هو الشهر الخامس في السنة البابلية ? كذلك في السنة العبرية المقدسة ? يقابل الشهر الحادي عشر في التقويم المدني. وهو يقابل", "جزءاً من شهري يوليو واغسطس (تموز ? آب) في التقويم الميلادي. ? يصوم اليهود في اليوم التاسع من هذا الشهر تذكاراً لخراب أورشليم وتخريب الهيكل.", ":ابو", ":ابو");
INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES (1146, "أبْنيْر: ومعنى الاسم )  او ((الاب نور))  وكان رئيساً لجيش شاول الملك وهو ابن نير عم شاول(1صم 14: 50) واول مرة التقى فيها بداود كانت عندما قتل داود جليات الفلسطيني( 1صم 17: 55-58) وقد رافق ابنير شاول في مطاردته لداود(1 صم 26: 5وما بعده) ولما مات شاول اخذ ابنير ايشبوشت بن شاول ونادى به ملكاً في محنايم(2 صم 2: 8) وكان من نتيجة ذلك ان اشتعلت الحرب بين بيت شاول وبيت داود وانكسر ابنير ورجال اسرائيل امام عبيد داود فسعى عسائيل اخو يوآب رئيس جيش داود وراء ابنير فقتله ابنير. واستمرت العداوة قوية بين بيت شاول وبيت داود(2 صم 2: 12-3: 6). وكلنت لشاول سرية اسمها رصفة بنت أية اخذها ابنير لنفسه ولما سأله ايشبوشت عن سبب ذلك اغتاظ جداً وأخذ البنيامينيين الى جانبه. وذهب الى داود الى حبرون ومعه عشرون رجلاً منهم ثم اتفق معه على ان يجمع جميع اسرائيل الى جانبه. واذ ذهب لاتمام هذا الاتفاق علم يوآب بما حدث، ومن دون ان يست", "اذن داود ارسل واعاد ابنير بزعم التحدث اليه ولكنه قتله غيلة انتقاماً لاخيه عسائيل. ولما سمع داود الخبر حزن عليه حزناً عميقاً وبكاه مع جميع الشعب ورثاه وقال (( الا تعلمون ان رئيساً وعظيماً سقط اليوم في اسرائيل))  (2صم 3: 6-38). ومع ان داود لم يكن له شأن البتة في موت ابنير الا ان موت ابنير سلب المعارضين لداود قوتهم فانحاز اليه كل الشعب ونودي به ملكاً على كل اسرائيل.", "ابي:نور", "ابي:نور");
