INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES (1208, "بَشّر تبشيراً: ابلغ الخبر الطيب-البشارة. وكان مخلصنا يبشّر ويعلم في الهيكل وخارجه. وكثيراً ما كان يعظ الجموع من البحر أو على الجبل. وأوصى تلاميذه من بعده أن يذهبوا ويبشّروا جميع الأمم في أقطار المسكونة. فالتبشير بدأ في المسيحية. وأما في زمان العبرانيين القدماء فلم يكن ثمة تبشير. إلا أنهم بعد السبي، على ما يخبرنا الكتاب المقدس 1 أخبار ابتدأوا أن يفسروا الشريعة للشعب.", "مبشّر: تطلق هذه الكلمة في العهد الجديد على من يعظ ببشارة الخلاص، متنقّلاّ من مكان إلى آخر، لا يستقر في مكان مخصوص، إنما همه التجوّل يعظ بالإنجيل ويؤسس الكنائس باسم المسيح (1ع 21: 8 واف 4: 11 و 2 تي 4: 5). وكان المبشرون مساعدين للرسل في أشغالهم وكانوا رفاقهم في أسفارهم فكان الرسول بولس يأخذهم في معيته حيث كان يذهب ليزور الكنائس (1ع 20: 4 و 5) ويرسلهم حاملين أخباراً مختلفة بخصوص بشرى الخلاص ليعلونها للأخوة البعيدين عنه (في 2: 19-23) حتى أنه كان يسلمهم أشغالاً مهمة ليتمّموها مدة غيابه (1 تي 1: 3 و 3: 14 و 15 و 4: 13). وقد أشير بالرموز الآتية إلى أصحاب ", "nan", "None");
