INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES (1210, "بَصَلْئيل: اسم عبري معناه ) وقد ورد هذا الاسم: (1) لرجل من سبط يهوذا اشتهرا بالحذق في الصناعات الدقيقة", "فصنع الأدوات اللازمة للهيكل (خر 31: 1). (2) لرجل ذكر عنه عزرا النبي أنه تزوّج بامرأة غريبة (عز 10: 30).", "في:ظلالله", "فيلبي :ظل الله");
