INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES (1366, "بوّني: اسم عبري ربما كان معناه (( مبني أو مُقام )) وهو لاوي في أيام نحميا ( نح 11: 15", ").", ":مبنيأومُقام", ":مبني أو مُقام");
INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES (1367, "تَحْفنيس: ( 1 ) امرأة فرعون ملك مصر في عصر سليمان وهي التي اقترن باختها هدد ( 1 مل 11: 18-20 ) وكان زوجها من الأسرة الثانية ةالعشرين لملوك مصر.", "( 2 ) مدينة في مصر السفلى وتدعى تحفنيس ( ار 2: 16 ) أو تحفنحيس ( ار 43: 7 و 8 و 9 و 44: 1 و 46: 14 وحز 30: 18 ) التجأ إليها اليهود هرباً من انتقام البابليين بعد قتل جدليا ( ار 43: 7-13 ) ويظهر أن اليهود اتخذوها مسكناً دائماً لهم ( ار 44: 1 و 46: 14 ) وقرئت هذه الكلمة في النسخة السبعينية تفنيس وهي بذاتها دفنة المدينة المحصنة الواقعة على الفرع البلوذي من النيل ( هيرودوت جزء 2: 30، 107 ) وربما يقصد بها تل دفنة الواقع على بعد 10 أميال غربي القنيطرة.", "1", "1");
INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES (1368, "تَرشِيش: اسم فينيقي معناه (( معمل للتكرير )).", "( 1 ) شعب متناسل من ياوان ( تك 10: 4 ) ويتضح من دخول يونان إلى السفينة ليهرب إلى ترشيش من يافا ( يون 1: 3 ) إنها مدينة وإن الطريق إليها كانت عبر مياه البحر الأبيض المتوسط. وكذلك كانت سفن سليمان وحيرام تأتي كل ثلاث سنوات مرة إلى ترشيش حاملة ذهباً وفضة وعاجاً وقروداً وطواويس ( 1 مل 10: 22 و2 أخبار 9: 21 ) ولما كان يقتضي لتلك السفن ثلاث سنين لكي تعود من سفرها يستنتج أن تكون ترشيش بلداً بعيداً ( اش 66: 19 ). ويعتقد أن تكون ترشيش هي ترتيوس وهي واقعة في جنوب اسبانيا قرب جبل طارق ( هيرودت جزء 4: 152 ) ولعل ترتيوس هذه هي قرطجنة المدينة الواقعة شمالي افريقيا وكانت قائمة إلى أوائل العصر المسيحي. وكانت ترشيش غنية جداً في الثروة المعدنية كالفضة المطرقة المصنوعة ألواحاً ( ار 10: 9 ) والحديد والقصدير ( حز 27: 12 ) وقد كانت سفن ترشيش مخصصة للتجارة منها وإليها. ثم بعد ذلك أصبحت سفن ترشيش من العظمة بمكان بحيث تسافر إلى كل جهة. وكانت الرياح تصدم سفن ترشيش المحملة بأثمن البضائع وتكسرها في مياه بحر الروم ( الأبيض المتوسط ) ( مز 48: 7 واش 2: 16 و23: 1 و14 و60: 9 وحز 27: 25 ) وإن يهوشافاط بنى بعضاً من هذه السفن العظيمة فكانت تسافر إلى أوفير الواقعة على فرضة عصيون جابر ( إيلات ) على خليج العقبة في البحر الأحمر ( 2 أخبار 9: 21 و1 مل 10: 22 ). أما القول سفن ترشيش يقصد منه السفن المسافرة إلى ترشيش ( 2 أخبار 20: 36 و1 مل 9: 28 و2 أخبار 9: 21 ) وبمعنى آخر ربما يقصد بسفن ترشيش سفن التكرير على شبه السفن التي تمخر عباب البحر الأبيض المتوسط ولها اتصال بالمناجم ومعامل التكرير في سردينية وفي عهد متأخر سفن جنوبي اسبانيا مع فينيقية أو ربما يكون المقصود بسفن ترشيش السفن الكبيرة ( اطلب سفينة ). ", ":معملللتكرير", "None");
