INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES (1369, "تِرْصة: اسم عبري معناه (( فرح وانشراح )) مدينة جميلة للغاية ( نش 6: 4 ) وكانت أصلاً للكنعانيين وهي إحدى مدنهم التي خربها يشوع بن نون وأعطاها لأسباط بني إسرائيل ( يش 12: 24 ) وكانت مركزاً لمملكة أسباط إسرائيل العشرة نحو خمسين سنة ( 1 ملو 14: 17 و15: 21 و33 و16: 6 و23 ) حتى بنى عمري السامرة ( 23 و24 ).", "ويمكن أن يكون موقعها رابية التل الفارغة الكبيرة على بعد 7 أميال شمالي شرقي مدينة نابلس ( شكيم ).", ":فرحوانشراح", "None");
