INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES ( القدس (( (( البحر المتوسط 1 مل 13: 4-6 ((17: 6 ((17: 14-16 ((17: 17-24 ((18: 38 ((18: 41-45 2 مل 1: 10-12 ))2: 7 و 8 و 14 ))2: 11 2 مل 2: 21 و 22 ((2: 24 ((3: 16-20 ((4: 2-7 ((4: 32-37 ((4: 38-41 ((4: 42-44 ((5: 10-27 ((6: 5-7 ((6: 8-12 ((6: 18 ((6: 20 ((13: 21 ((19: 35 ((20: 9-11 2 اخبار 26: 16-21 يون 2: 1-10 الفترة الرابعة خلاص شدرخ وميشخ وعبدنغو من اتون النار خلاص دانيال من الاسود                                  	بابل ((	دا 3: 19-27 ((6: 16-23 الفترة الخامسة تحويل الماء إلى خمر ابراء ابن خادم الملك الهرب من الجموع ملء الشباك بالسمك ابراء المجنون في المجمع ابراء حماة بطرس ابراء المرضى في المساء ابراء الابرص ابراء خادم قائد المئة اقامة ابن الارملة من الموت تهدئة الانواء ابراء المجنونين في جدرة ابراء المفلوج اقامة ابنة يايرس من الموت ابراء امرأة من نزيف دم اعادة البصر لأعميين ابراء المجنون الاخرس ابراء المشلول عند بركة بيت حسدا ابراء ذي اليد اليابسة ابراء المجنون الاعمى الاخرس اشباع الخمسة الآف   بيت صيدا المشي فوق الماء ابراء ابنة المرأة الكنعانية ابراء الاصم الابكم اشباع الاربعة الآف اعادة البصر للاعمى ابراء الولد المصروع المجنون اخراج قطعة النقود من فم السمكة ابراء االمولود اعمى ابراء المرأة ذات المرض المزمن ابراء الرجل المستسقي ابراء البرص العشرة اعادة البصر لأعميين اقامة لعازر من الموت اتلاف شجرة التين العميقة ابراء اذن ملخس صيد السمك بعد القيامة قانا (( , "None", "None", "None", "None");
