INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES ( السامرة اريحا بيت عنيا جبل الزيتون جثشيماني بحر الجليل	يو 2: 1-11 يو 4: 46-54 لو 4: 30 لو 5: 1-11 مر 1: 23-26 و لو 4: 35 مت 8: 15 و مر 1: 30 و 31 و لو 4: 39 مت 8: 16-17 ومر1: 34 و لو 4: 40 مت 8: 3 و مر 1: 41 و 42 و لو 5: 13 مت 8: 5-13 ولو 7: 1-7 لو 7: 11-17 مت 8: 23-27 ومر4: 35-41 ولو 8: 22-25 مت 8: 28-34 و مر 5: 1-20 و لوقا 8: 26-39 مت 9: 1-8 ومر 2: 3-11 و لو 5: 18-26 مت 9: 18-26 و مر 5: 22-43 ولو 8: 40-48 مت 9: 20-22 و مر 5: 25-34 ولو 8: 43-43 مت 9: 27-31 مت 9: 32-33 يو 5: 1-9 مت 12: 10-13 و مر 3: 1-5 و لو 6: 6-11 مت 12: 22-23 ولو 11: 14 و 15 مت 14:15-21ومر6:30 -44ولو9:10ويو6:1-14 مت 14: 25-27 ومر 6: 45-52 ويو 6: 19-21 مت 15: 21-28 و مر 7: 24-30 مر 7: 31-37 مت 15: 32-38 ومر 8: 1-10 مر 8: 22-26 مت 17: 14-21 و مر 9:17-29 ولو 9: 37-43 مت 17: 24-27 يو ص 9 لو 13: 11-17 لو 14: 1-14 ((17: 11-17 مت 20: 30-34 يو ص 11 مت 21: 18-21 ومر 11: 12-14 لو 22: 50-51 يو 21: 1-14 هذا إلى جانب العجائب التي ارتبطت برسالة المسيح في الارض مثل: بشارة الملائكة، ولادته من عذراء، نشيد الملائكة، نجم المجوس، نزول الروح القدس في هيئة حمامة عند المعودية، والتجلي عند جبل عال، رتماء الجنود عند تسليمه، انفتاح القبور بعد موت يسوع، انشقاق حجاب الهيكل، الظلمة تغطي الارض، الهزة وتزلزل الارض، القيامة من الموت، دخول يسوع الغرفة وابوابها مغلقة، والصعود إلى السماء. الفترة السادسة كل المعجزات التي اجريت في هذه الفترة من سفر اعمال الرسل انسكاب الروح القدس في يوم الخمسين ابراء المرضى بالجملة, "None", "None", "None", "None");
INSERT INTO bible_dictionary (id, word, meaning, verse_id, full_verse_reference) VALUES ( ابراء الاعرج موت حنانيا وسفيرة انقاذ الرسل من السجن عجائب استفانوس عجائب فيلبس ابراء اينياس اقامة غزالة من الموت انقاذ بطرس من السجن اصابة الساحر عليم بالعمى عجائب بولس وبرنابا شفاء المقعد قيام بولس بعد رجمه طرد روح العرافة من الجارية انقاذ بولس وسيلا من السجن عجائب بولس اقامة افتيخس من الموت نجاة بولس من الحيّة ابراء ابي بوبليوس واخرين  	القدس ((, "None", "None", "None", "None");
