INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1, 'اب', 'لفظ يطلقه المسيحيون على الله لأنه الآب السماوي( مت 11: 25 غل 1: 1) ? غير هذا انظر أبو.'),
(2, 'آب', 'اسم من الأصل الاكادي  (( ابو))  ? هو الشهر الخامس في السنة البابلية ? كذلك في السنة العبرية المقدسة ? يقابل الشهر الحادي عشر في التقويم المدني. وهو يقابل\nجزءاً من شهري يوليو واغسطس (تموز ? آب) في التقويم الميلادي. ? يصوم اليهود في اليوم التاسع من هذا الشهر تذكاراً لخراب أورشليم وتخريب الهيكل.'),
(3, 'آبص', 'وردت بهذه الصورة في العبرية في حالة الوقف. أما فيما عدا ذلك فقد وردت هكذا (( إِبص))  وربما معناها (( قصدير))  أو (( أبيض))  وهي احدى مدن يساكر( يش 19: 20) وربما مكان علاقة بالقاضي إِبصان، ويحتمل أنها من نفس المكان المعروف حديثاً باسم عين الحبوص أو العبوص بين عولام وسرين.'),
(4, 'آبل', 'كلمة عبرية ربما تعني (( مرج))  أو ((مياه))  من (( وبل))  أي (( أمطرت وابلاًً))  وقد وردت في 2 صم 20: 14?18 في حادثة تمرد شبع  بن بكري على داود. ويغلب على الظن أنها نفس آبل بيت معكة. انظر 2 صم 20: 15.'),
(5, 'آبل بيت معكة', 'اسم عبري معناه (( مرج بيت الظلم))  أو (( مرج بيت شخص اسمه معكة)) . وكانت مدينة حصينة في نفتالي( 2 صم 20: 15? 1ملوك 15: 20). وكانت كذلك شهيرة بالحكمة وبتمسكها بالعوائد الاسرائيلية(2صم20: 18) وقد هرب إليها شبع بن بكري عندما فشل في تمرده على داود، وقد تأهب لمهاجمة المدينة للقبض عليه ولكن امرأة حكيمة تقدمت وكلمت يوآب واتفقت معه على قتل شبع وبذلك أنقذت مدينتها من الدمار(2صم20: 14-22) وقد أخذ يتهدد المدينة عندما طلب آسا ملك يهوذا معونته على بعشا ملك اسرائيل( 1 ملوك 15: 20). وفي سنة 734? ?. غزا تغلث فلاسر المدينة وأخذ سكانها في السبي إلى آشور(2 ملوك15: 29). وربما مكانها اليوم هو المعروف باسم تل آبل أو تل القمح. وهي قرية غرب الأردن على رابية تشرف على الوادي مسافة اثني عشر ميلاً شمال بحيرة الحولة مقابل دان ويحيط بها سهل مياهه وفيرة، وأرضه خصبة ولذا فقد أطلق عليه في القديم اسم (( آبل مايم))  أو (( آبل المياه)) ( 2 اخبار 16: 4).'),
(6, 'آبل شطيم', 'اسم عبري ومعناه (( مرج السنط))  وقد ورد الاسم بهذه الصورة في عدد33: 49. أما فيما عدا ذلك فقد ورد باسم (( شطيم))  فقط انظره في موضعه.'),
(7, 'آبل الكروم', 'اسم عبري ومعناه (( مرج الكروم))  وهو في موضع شرقي الأردن. وقد تعقب يفتاح بني عمون إلى ذلك المكان( قض 11: 33) وربما تقوم مكانه حديثاً خربة السوق على الطريق من عمان إلى حسبان.'),
(8, 'آبل محولة', 'اسم عبري ومعناه (( مرج الرقص))  كان هذا الموضع حسبما يظهر، يقع في وادي الأردن(1 ملوك4: 12).وهو المكان الذي طرد جدعون والثلاثمائة الذين معه المديانيين إلى حافته(قض 7: 22). وهناك أقام أليشع(1ملوك 19: 16) وقد عين جيروم موضعه على بعد عشرة ? ميال رومانية جنوبي بيسان. وقد ظن بعضهم أن مكانها حديثاً هو عين حلوة على بعد تسعة أميال ونصف جنوبي بيسان. ويغلب على الظن ان المكان أصلاً كان يقع بالقرب من تل أبي سفري عند التقاء وادي المالح بوادي الحلوة.'),
(9, 'آبل مصرايم', 'انظر تحت (( اطاد)) .'),
(10, 'آبل المياه', 'اسم عبري ومعناه ((مرج المياه))  وهو اسم آخر لآبل بيت معكة(2 اخبار16: 4).'),
(11, 'الأبنوس', 'نوع من الخشب من الفصيلة التي يطلقون عليها في اللاتينية اسم Diospyros Ebenum والأجزاء الداخلية من هذا الخشب سوداء وصلبة جداً وثقيلة. ويستخدم في أعمال التطعيم ? الزخرفة.وتعمل منه الآلات الموسيقية والتماثيل الصغيرة والأدوات المزخرفة لأنه قابل للصقل واللمعان إلى درجة كبرى.وكان شعب ددان يتاجرون فيه في أسواق صور وربما كانوا يجلبونه من الهند أو الحبشة(حزقيال27: 15).'),
(12, 'آحاز', 'اسم عبري ومعناه ((هو امسك))  أي ((الرب امسك)) .(1)    الملك الحادي عشر من ملوك يهوذا( وقد ورد اسمه بصورة أَحاز في مت 1: 9 وقد ذكر في نقوش تغلث فلاسر ملك آشور باسم يوحزي الذي يقابله يهو أحاز في العبرية) وقد خلف أباه يوثام في الملك وهو في العشرين من العمر. وكان ذلك في سنة 736 ?.?. وقد تعلق قلبه بحب الأصنام من أول حكمه. فعبر ابنه في النار... وذبح وأوقد على المرتفعات وعلى التلال وتحت كل شجرة خضراء(2 ملوك 16: 3?4). وقد تحالف رصين ملك آرام وفقح بن رمليا ملك اسرائيل ضد آحاز وحاصراه في أورشليم( 2ملوك16: 5?(2) اش 7: 1) فأرسل الرب إليه النبي أشعيا. قبل وصول القوات الغازية،(3) ليحثه على وجوب الاتكال على الرب وعدم دعوة قوات أجنبية لمعونته،(4) ولكنه لم يؤمن بقول الرب ورفض أن يطلب أية علامة منه. عندئذ نطق النبي بنبوته المشهورة الخاصة بميلاد عمانوئيل( اش 7: 1-16). كذلك انظر عمانوئيل. واتجه آحاز إلى تغلث فلاسر ملك آشور طالباً معونته بعد أن دفع ثمن هذه المعونة من ذخائر الهيكل وذخائر قصر الملك فزحف تغلث فلاسر لتقديم المعونة له. ويبدو أن رصين ? فقح عندما علما باقتراب جيوش الآشوريين رفعا الحصار عن أورشليم. فهاجم تغلث فلاسر أرض الفلسطينيين وزحف على السامرة ثم سار وأخذ دمشق وقتل رصين. وقد ذهب آحاز إلى دمشق مع غيره من الملوك الخاضعين لآشور لتقديم فروض الولاء لتغلث فلاسر( 2ملوك ?16?2 اخبار ?28) كما ذكر في النقوش الآشورية. وبينما هو هناك أعجب بمذبح الوثن وأمر أن يُصنع مذبح يشبهه في أورشليم.وقد أقام آحاز ((درجات))  كانت تستخدم لقياس الوقت وكانت عبارة عن درجات أو سلسلة من الدرجات مبنية حول عمود قصير ويعرف الوقت بها في سير الشمس الظاهر في الظل الذي يقع على الدرجات(قارن 2ملوك20: 19-21 واش 38: 8) انظر ((درجات آحاز)) .ومن أعمال هذا الملك أنه قطع أتراس القواعد ورفع عنها المرحضة أنزل البحر عن الاثني عشر ثوراً من نحاس التي أقامها سليمان وجعل البحر على رصيف من حجارة(2 ملوك 16: 17).ولم يقتصر هذا الملك على إقامة مذبح الوثن في أورشليم بل أغلق أبواب الرواق وأطفأ السرج فلم يوقد بخوراً ولم يصعد محرقة لاله اسرائيل(2 اخبار29: 7). وهو الذي بنى المذابح التي على سطح ((عليَّة آحاز))  ويحتمل أنه بناهافوق ساحة الهيكل لعبادة الأجسام السماوية(2ملوك23: 12). ويحدثنا الكتاب عن الكثير من عبادته الوثنية وأعمال الارتداد التي سادت الأمة في عصره(2 اخبار28: 22 وما بعده).وفي السنوات الأخيرة من ملكه اقتحم الفلسطينيون مدن السواحل وجنوبي يهوذا-وكذا أتى الادوميون وضربوا يهوذا(2 اخبار 20: 28?19) فطلب معونة تغلث فلاسر ولكن ملك آشور ضايقه ولم يساعده. أما آحاز في ضيقه فقد ازداد خيانة للرب(2 اخبار 28: 20-22).وقد تنبأ في عصره هوشع وميخا وأشعياء.ومات آحاز في السادسة والثلاثين من عمره سنة 721 قبل الميلاد بعد أن حكم ستة عشر عاماً فيها أساء الحكم وعمل الشر في عيني الرب.(5)    آحاز ابن ميخا من نسل يوناثان(1 اخبار8: 35? 36? 9: 42).'),
(13, 'آحود', 'اسم عبري ومعناه (( الاتحاد))  وهو رجل من بنيامين( 1 اخبار 8: 6).'),
(14, 'آدم', 'اسم عبري ومعناه ((إنسان))  أو (( الجنس البشري))  وكذلك معناه لغوياً ((أحمر))  من (( آدام))  العبرية.ويقول بعض الثقاة أنها جاءت في الأصل الآكادي أو الآشوري ((آدامو))  أي ((يعمل))  أو (( ينتج)) . وهو الإنسان الأول. والإنسان من صنع الله كبقية المخلوقات( تك 1: 26). وقد خلقه الله ذكراً وأنثى(تك 1: 17? مت19: 4-6). وقد جبل الرجل أولاً ثم الأنثى( تك 2: 7?20-23?أتيموثاوس2: 13). وقد جبله اله من تراب الأرض ونفخ في أنفه نسمة حياة( تك 2: 7). خلقه الله على صورته( تك 1: 26?27). ويشير الرسول بولس إلى أن التشابه مع صورة الله هو في المعرفة والبر ? قداسة الحق(أفسس4: 22?23 وكولوسي3: 9?10)وقد أعطى الإنسان سلطاناًعلى الحيوانات(تك 1: 26-28). أمر أن يثمر ويكثر ويملأ الأرض ويخضعها(تك1: 28). واشترك مع الخليقة في نوال استحسان الله إذ قيل (( ورأى الله أن كل ما عمله حسن جداً))  (تك 1: 31). وقد وضع آدم في جنة عدن ليعملها ويحفظها. وقد أمره أن يعطي الحيوانات اسماء(تك 2: 19). وقد صنع الله له معيناً نظيره إذ أخذ ضلعاً من أضلاعه وبناها امرأة وأحضرها إليه(تك 2: 21?22). وقد أمره الله أن لا يأكل من شجرة معرفة الخير والشر لئلا يموت موتاً(تك 2: 16?17) ولكنه تعدى الأمر فحق عليه حكم الموت ولعنت الأرض بسببه وحكم عليه أن يأكل منها بالتعب كل أيام حياته. وطرد من جنة عدن(تك3: 17-19). ومن بعد ذلك ولد له ولدان وهما قايين وهابيل. ثم لما كان في السنة المائة ? الثلاثين من عمره ولد له ابن اسمه شبيث. وكانت أيام حياته تسعمائة وثلاثين سنة.وفي رومية 5:12 يذكر الرسول بولس أنه بآدم ((دخلت الخطية إلى العالم وبالخطية الموت وهكذا اجتاز الموت إلى جميع الناس))  وفي 1 كو 15: 45 يدعو الرسول المسيح (( آدم الأخير))  قائلاً (( صار آدم الإنسان الأول نفساً حية وآدم الأخير روحاً محييا)) .'),
(15, 'آدم وحواء', 'أسفار آدم وحواء- كتبة هذه الأسفار الغير القانونية يهود كتبوها في الآرامية قبل سقوط أورشليم في سنة 70 ميلادية  ولا زالت أجزاء من هذه الأسفار باقية إلى اليوم في ترجمات مختلفة. وتسمى هذه الأسفار في الترجمة اليونانية (( رؤيا موسى))  وهذا خطأ. ومن يدرس هذه الترجمات سوف يرى أن فيها إضافات وضعها كتاب مسيحيون ونجد فيها وصفاً خيالياً مفصلاً لما حدث لآدم وحواء بعد السقوط.'),
(16, 'آرح', 'اسم عبري ومعناه ((رحالة))  وقد جاء في الكتاب المقدس:(1) اسم رئيس من أشير وهو أحد أبناء علا( 1 اخبار 7: 39).(2) أب لفريق من الراجعين من سبي بابل مع زربابل(عزرا 2: 5) وربما أنه هو نفس آرح الذي تزوجت ابنة ابنه بطوبيا العموني(نحميا6: 18?(3) 7: 10).'),
(17, 'آس', 'وهو نبات جميل المنظر عطري الرائحة. أوراقه دائمة الخضرة. اسمه بالعبرية ((هدس))  وهذا الاسم الذي يطلقه عليه عرب اليمن أيضاً. واسم استير بالعبرية ((هدسة))  وهو مأخوذ من اسم النبات. ويسمى أيضاً (( ريحان ))  واسمه باللاتينية Myrtus Comununis وتنمو شجيراته إلى ثلاثة أو أربعة أقدام في الارتفاع وتصل أحياناً إلى ثمانية أقدام. ويكثر على الجبال ومجاري المياه. وقد ذكر في نحميا8: 15 أن اليهود كانوا يجمعون أغصانه مع غيرها من الأغصان لاستخدامها في مظالهم في عيد المظال. وقد ذكر الآس أيضاً في اش 41: 19 ?55: 33? وزك 1: 8-11.'),
(18, 'آسا', 'اسم عبري ومعناه (( الآسي))  أي (( الطبيب))  وربما كان الاسم اختصار (( يهوه آسا))  أي (( الرب داوى وشفى))  (1) وهو ملك من ملوك يهوذا حكم من سنة 912 إلى سنة 871 ?.?. وقد ارتقى العرش في السنة العشرين من ملك يربعام الأول ملك اسرائيل. وآسا هو ابن ابيام وحفيد رحبعام. وكانت معكة ابنة ابشالوم أمه أو على الأصح جدته(1 ملوك 15: 9-10 قارنه مع عدد 2). وكانت العشر سنوات  الأولى من ملكه سني نجاح وازدهار وسلام(2اخبار 14: 1). وقد قام باصلاحات كثيرة كما يظهر من 1 ملوك15: 12 حيث يقول (( وأزال المأبونين من الأرض ونزع جميع الأصنام التي عملها آباؤه))  وقد نزع المذابح الغربية والمرتفعات وكسر التماثيل وقطع السواري. وخلع (( معكة))  من الملك بسبب عبادتها الوثنية وعملها تمثالاً (( لسارية))  أو الآلهة (( أشيرة))  (1 ملوك 15: 13?2 اخبار 14: 3). ومع أنه كان مصلحاً غيوراً إلا أن الشعب لم يسايره في جميع إصلاحاته فبقيت المرتفعات ولم تنزع( 1 ملوك15: 14? 2 اخبار 15: 17). وقد أغار عليه زارح الكوشي وغزا أملاكه بجيوش جرارة ولكن آسا انتصر عليهم في مريشة وهزمهم وطردهم بمعونة الرب( 2 اخبار 14: 9-15).وفي السنة الخامسة عشرة من ملكه أرسل الرب إليه النبي عزريا الذي شجعه بكلمات الرب على اتمام إصلاحاته التي بدأها فجدّد مذبح المحرقة في الهيكل وحثَّ الشعب على تجديد عهدهم مع الرب(2 اخبار 15: 1-15).وفي السنة السادسة والثلاثين( التي يظن بعض المفسرين أنها السادسة عشرة) من ملكه صعد بعشا ملك اسرائيل على يهوذا وبني رامة على التخوم بين يهوذا واسرائيل في الطريق من أورشليم إلى الشمال وحصّنها. ولما لم يستطع آسا أن يأخذ رامة ليفتح الطريق، أخذ خزائن الهيكل ليستأجر بها بنهدد ملك آرام ليستعين به على بعشا. فغزا بنهدد الجزء الشمالي من مملكة اسرائيل وبذلك اضطر بعشا إلى الانسحاب من رامة. فأخذ آسا مواد البناء التي كان قد جمعها بعشا في رامة وحصّن بها جبع والمصفاة.فجاء حناني الرائي إلى آسا ووبخه لاستناده على بنهدد ملك آرام بدلاًمن استناده على الرب الهه بعد أن اختبر معونة الرب في حربه ضد الغزاة من الكوشيين واللوبيين فقاوم آسا دخول الرائي وغضب عليه ووضعه في السجن( 1 ملوك 15: 16-22? 2 اخبار 16: 1-10).وفي السنة التاسعة والثلاثين من ملكه مرض في رجليه فلم يطلب الرب بل طلب الأطباء( 1 ملوك 15: 23?2 اخبار 16: 12). ولم يكن أميناً للرب في أواخر أيامه كما كان في أوائلها. ومات في السنة الحادية والأربعين من ملكه ودفن باحتفال عظيم في قبره الذي حفره لنفسه في مدينة داود. (1 ملوك 15: 24?2 اخبار 16: 14).(4)     آسا اسم للاوي هو ابن القانة الساكن في قرى النطوفاتيين بعد الرجوع من سبي بابل( 1 اخبار 9: 16).'),
(19, 'آساف', 'اسم عبري ومعناه (( الجامع))  أو ربما هو اختصار (( يهوه ساف))  أي  (( الرب جمع))  وهو:(1) اسم للاوي هو ابن برخيا من عشيرة الجرشوميين( 1 اخبار 6: 39?43). وكان يقف مع المغنين بآلات غناء ? رباب وصنوج مستمعين برفع الصوت بفرح،(2)  هو وهيمان بن يوئيل وايثان بن قوشيا( 1 اخبار 15: 16-19) ثم بعد ذلك عين في وظيفة دائمة في ضرب الصنوج في الخدمة في الهيكل(1 اخبار 16: 4?5?7). ويدعى آساف،(3) بالرائي،(4) كغيره من رؤساء المغنين(2 اخبار 29: 30 وقارنه مع 2 اخبار 35: 15? 1 اخبار 25: 5). ولما حان الوقت لوضع ترتيب كامل نهائي للخدمة،(5)  عهد،(6) بصفة دائمة،(7) إلى عشيرته،(8) وآساف على رأسها،(9)  بالجزء الموسيقي لأجل غناء بيت الرب بالصنوج والرباب والعيدان لخدمة بيت الله(1 اخبار 25: 1-9). وكانوا يقفون على اليمين في أثناء القيام بالخدمة(1 اخبار 6: 39). وقد رجع من السبي من عشيرة آساف مائة وثمانية وعشرون كلهم من المغنين( عزرا 2: 41 وقارنه مع نحميا 7: 44). ولما أسس البانون هيكل الرب في أيام زربابل أقاموا... اللاويين بني آساف بالصنوج لتسبيح الرب( عزرا 3: 10). وينسب إلى بني آساف اثنا عشر مزمورا كما يظهر ذلك من عنواناتها وهي  مزمور 50?73-83 ثم قارن هذه مع 2 اخبار29: 30. ويجيء مزمور 50 في القسم الثاني من سفر المزامير. أما المزامير الأخرى( 73-83) فتشمل الجزء الأكبر من القسم الثالث من السفر وفيه نجد أن الاسم الذي يطلق على الرب هو (( الوهيم ))  بدل  ((يهوه)) .(10) (( آساف))  اسم لأبي يوآخ كاتب حزقيا(2 ملوك 18: 18?37 واش 36: 3?22).(11) وكذلك  (( آساف))  اسم لحارس فردوس الملك ارتحشستا( ارتزر كسيس لونحمانوس) ملك الفرس( نحميا 2: 8).'),
(20, 'آسر حدون', 'وهي عبارة آشورية معناها ((آشور أعطى أخاً))  وهو ابن سنحاريب المفضل لديه مع أنه لم يكن ابنه الأكبر. وقد أثار سنحاريب لهذا الابن غضب اثنين آخرين من اخوته وهما أدرملك ? شرآصر ? فتآمرا على أبيهما وقتلاه غيلةفي سنة 681 ?.? وهو ساجد في بيت نسروخ الهه وهربا إلى أرمينية- أي أرض أراراط (2 ملوك 19: 36?37 ?2 اخبار 32: 21 واش 37: 37?38). وقد ارتكب هذا الجرم الشنيع عندما كان آسرحدون يقوم بحملة في الشمال الغربي، وأغلب الظن أنها كانت ضد أرمينية. وقد قتل سنحاريب في شهر طبيت(الشهر العاشر من السنة) فقفل آسرحدون راجعاً إلى نينوى في شهر شباط( الشهر الحادي عشر) وانتهت الحرب الأهلية في آشور في شهر آذار (الشهر الثاني عشر) سنة 681 قبل الميلاد.وقد برز آسرحدون في ميدان القيادة الحربية كما برز في ميدان الحكم والسياسة، ففي السنة الأولى من حكمه هزم ابن مردوخ بلادان جنوب بابل. ثم بعد ذلك بدأ إعادة بناء بابل التي كان قد أخربها سنحاريب إذ أثارت سخطه بعصيانها المتكرر ضد سلطة آشور. وقام آسرحدون أيضاً بحرب ضد الكمرين البرابرة( وربما كان هؤلاء أبناء جومر) انظر تحت (( جومر))  وقد نزلوا على آشور من وراء جبال القوقاز في الشمال. وحارب كذلك رجال الجبال في كيليكية وكذلك حارب بني عدن الذين في تلسار قارن أشعياء 37: 12. وفي السنة الرابعة من ملكه أخذ صيدون ونهبها وأجلى أهلها منها وخرّبها ودكها إلى الأرض وبنى عوضاً عنها مدينة جديدة في البقعة الأصلية. وخضعت فيما بعد لحكم آشور اثنتا عشر قبيلة في أرض فلسطين وسوريا وعشر قبائل في قبرص. وكان من ضمن الذين أخضعهم لسلطان آشور منسى ملك واشقلون، وملوك آدوم، وموآب، وعمون وغزة، وبلاد مادي. وقام بحملات على مصر من سنة 675 إلى سنة 674 ?.?. ومرّ في طريقه بصور وترك المدينة محاصرة. ثم دخل مصر وأخذ منف(منفيس أو نوف) وتقدم فأخضع البلاد بجملتها وهرب ملكها ترهاقة( واسمه بالمصرية القديمة تهرقا) وقد ورد ذكره في 2 ملوك 19: 9 وأشعياء37: 9. وقد مات آسرحدون في سنة 669 ?.?. وخلفه في الحكم ابنه الأكبر آشوربانيبال.'),
(21, 'آصَلُ', 'اسم عبري ومعناه (( الربط))  أو  ((الوصل))  قارن الفعل  (( وَصَل)) . وهو اسم مكان بالقرب من أورشليم(زك 14: 5) وربما هو وادي يصول إلى يمين عين اللوز في وادي النار.'),
(22, 'آصيل', 'اسم عبري ومعناه ((شريف))  أو ((أصيل))  وهو اسم رجل من نسل يوناثان بن شاول(1 اخبار8: 37?38?9: 43?44).'),
(23, 'آطير', 'اسم عبري ومعناه ((مغلق))  أو ((الذي يغلق))  انظر (( آطَر))  أي ((ثنى))  وكذلك  ((إِطار))  وهو:(1)  اسم رجل من بني حزقيا ورد هكذا (( اطير لحزقيا))  أو (( اطير من يحزقيا))  تمييزاًً له عن غيره. وقد عاد ثمانية وتسعون من نسله من السبي مع زربابل ( عزرا 2: 16 ونحميا7: 21).(2) اسم رجل كان رأس أسرة من بوابي الهيكل وقد رجعوا من بابل إلى أورشليم(عزرا 2: 42?(3) ونحميا 7: 45).'),
(24, 'آلَف', 'كلمة عبرية ومعناها ((ثور))  أو ((الف))  اسم قرية في بنْيامين يش 18: 28 وربما بقي الاسم في لفتة الحديثة التي تقع قريبة من أورشليم إلى الشمال الغربي.'),
(25, 'آموص', 'اسم عبري ومعناه ((قوي))  وهو أبو أشعياء النبي (2 ملوك 19: 2?20 واش 1: 1 الخ). ويقول التقليد اليهودي أنه كان نبياً وأخاً لأمصيا ملك يهوذا.'),
(26, 'آمون', 'اسم عبري ومعناه (( أمين))  أو (( صانع))  وهو اسم:(1)  ملك يهوذا وقد خلف أباه منسى وهو في الثانية والعشرين من العمر. وربما يظهر من الاسم (( آمون))  وثنية أبيه في اختيار اسم وثني لابنه وقد سار آمون على مثال أبيه في عبادته الأوثان وبعده عن الاله الحق. وكانت مدة حكمه سنتين ثم بعد ذلك قام عبيده ضده بغتة وقتلوه في بيته. فقتل شعب الأرض الفاتنين وملكوا يوشيا ابنه عوضاً عنه( 2 ملوك 21: 19-26?2 اخبار 33: 21?25).(2)  رئيس مدينة السامرة الذي سلم إليه اخاب الملك ميخا النبي لكي يسجنه(1 ملوك 22: 26).(3)  كان رئيس جماعة أطلق عليها (( بنو عبيد سليمان))  وذكروا مع (( النثينيم))  أو (( عبيد الهيكل))  ( نحميا 7: 57-59) وقد جاء ذكره في عزرا 2: 57 باسم ((آمي)) .(4)  آمون باللغة المصرية القديمة معناه (( المحتجب))  أو (( المختفي))  وكان في الأصل اله طيبة أو (( آمون نو))  كما في أرميا 46: 25 أو (( نو آمون))  كما في ناحوم 3: 8 التي كانت عاصمة مصر العليا. ولما ارتفعت مكانة هذه المدينة في عصر المملكة الوسطى ارتفعت معها مكانة آمون وصار أعظم آلهة مصر وكثيراً ما كان يذكر كصنو للإله (( رع))  باسم (( آمون رع)) .'),
(27, 'آمي', 'اختصار (( آمون))  وهو أحد رؤساء عشائر (( عبيد سليمان))  الذين رجعوا من السبي( عزرا 2: 57). ويدعى أيضاً آمون( نحميا 7: 59).'),
(28, 'آمين', 'كلمة عبرية معناها (( ثابت))  أو  ((راسخ))  أو (( صادق))  أو (( أمين))  وهي تستعمل:(1)  لتفيد التحقق أو التأكيد في قسم أو عهد كما في تثنية 27: 15-26 حيث وردت اثنتي عشر مرة. وكذلك وردت بهذا المعنى في أرميا 11: 5 ونحميا 5: 13 وغيره.(2)  في ختام الصلاة بمعنى (( ليكن هكذا))  أو (( ليتم هذا الأمر))  أو بمعنى (( استجب))  (1 كو 14: 16 وغيره.(3)  وقد استعملها المسيح كثيراً في فاتحة كلامه وترجمت (( الحق))  فوردت هذه العبارة (( الحق أقول لكم))  مرات كثيرة في الأناجيل وهي في الأصل ((آمين أقول لكم))  أو (( آمين آمين أقول لكم)) .(4)  استعملت ((اسماً))  ليسوع المسيح كما في رؤيا 3: 14 وصفة له كما في 2 كو 1: 20 واستعملت أيضاً اسماً لله أو صفة له وترجمت ((الحق))  (أشعيا 65: 16).'),
(29, 'آون', '(1) بلدة أطلق اسمها على سهل في سوريا (عاموس1: 5) وربما هي أويقة بالقرب من يبرود على الطريق إلى تدمر أو كما يعتقد البعض،(2)   أنها بقعة لبنان(يشوع 11: 17). ولذلك فالسهل هو سهل البقاع الذي تقع فيه مدينة بعلبك.(3) أطلق هوشع هذا الاسم على ((بيت ايل))  للدلالة على أنها لم تعد بعد بيت الله بل قد صارت بيت الصنم(هوشع 10: 8). وانظر أيضاً ((بيت آون)) .(4) مدينة ((اون))  المصرية التي أطلق عليها اليونان اسم هليوبوليس أي ((مدينة الشمس))   (حزقيال 30: 17). ومع أن هناك اختلافاًفي النطق بين ((اون))  المصرية ? ((آون))  العبرية التي معناها صنم إلا أن النبي أطلق عليها اسم ((آون))  للدلالة على ما فيها من عبادة الأصنام.'),
(30, 'آبا', 'كلمة آرامية ومعناها ((أب))  وهي اسم مستعار من لغة البنوة للتعبير عن ثقة الأبناء القصوى ومحبتهم العميقة في مخاطبتهم  للآب السماوي(مرقس 14: 36? رومية 8: 15? غلاطية 4: 6). ونجد أنه قد أضيف لها في هذه المواضع الثلاثة في اليونانية ترجمتها أي ((اب))  وكان اليهود والمسيحيون الأولون يستخدمونها في صلواتهم. ولم يكن يسمح للخدم أو العبيد أن يقولوا ((ابا))  في مخاطبتهم لرب البيت.'),
(31, 'أبانة', 'اسم آرامي ومعناه ((صخري))  وقد ورد في بعض النسخ بلفظ ((امانة))  التي تعني ((دائم)) ،    ((مستمر الجريان))  وهو أحد نهري دمشق ((ابانة))  ? ((فرفر))  اللذين ورد ذكرهما في قصة نعمان السرياني(2 ملوك 5: 12) وهو على الأرجح نهر بردى في سوريا الذي أطلق غليه اليونانيون اسم  ((خرُّيسورواس))  وينبع من عين بردى بالقرب من قرية زبداني على بعد ثلاثة وعشرين ميلاً من دمشق ويجري من الجبل الشرقي ماراً في السور الشمالي لمدينة دمشق القديمة، ويمد ينابيع دمشق وحدائقها الكثيرة بالمياه ويستمر في جريانه إلى أن يصب مياهه في بحيرة اجمية في بقعة تسمى المرج على بعد عشرين ميلاً من المدينة تقريباً. وإلى هذا النهر يعزى جمال وخصوبة السهل الذي تقع فيه دمشق ومياه نهر بردى صافية  بخلاف مياه نهر الأردن العكرة وهو يختلف عن أنهر فلسطين،فإنها، ما عدا الأردن، تجف معظم أيام السنة إلا أن هذا النهر يجري باستمرار وهذا ما حدا نعمان السرياني  أن يقول ((أليس ابانة وفرفر نهرا دمشق أحسن من جميع مياه اسرائيل)) .'),
(32, 'أَبَدِيٌّ-ألاء بَدُ', 'يستخدم العهد القديم كلمتين عبرانيتين للتعبير عن فكرة الأبد وهما ((عولام))  ? ((عاد)) . أما العهد الجديد فيستخدم هذه الكلمات اليونانية:  ((ايون))  ? ((ايونيوس))  ? ((ايديوس))  للدلالة على فكرة الأبد. ويستخلص من دراسة هذه الكلمات أن أبد وأبدي تشير إلى فكرة البقاء والدوام إلى ما لا نهاية. وعندما تستخدم عن الله فإنها تدل على أنه لا بداية له ولا نهاية. ولكن عندما تشير إلى المخلوقات الخالدة  فإنه يقصد بها تلك الخلائق التي لها الرسوخ والثبات وطول البقاء فقيلت عن الجبال والتلال والآكام وغيرها وترجمت في العربية  ((بالدهرية))  أو ((القدم))  (تكوين 49: 26 وحبقوق 3: 6).أما كلمة ((عولام))  العبرية فإنها تقال للإشارة إلى أبدية الله(تثنية 32: 40). وعهده أو ميثاقه(تكوين9: 16)? وكلامه(أشعياء 40: 8) ? وفرائضه(خروج 29: 28) ومواعيده(2 صموئيل 7: 13?16?25) وكهنوت المسيح وملكه وملكوته(مز 110: 4?أشعياء9: 6?7).وكلمة ((ايونيوس))  اليونانية تستخدم عن أزلية الله وأبديته وقد ترجمت في العربية بكلمة  ((أزلي))  (رومية16: 26) وروحه(عب9: 14)? وكذلك تشير إلى أبدية الإنجيل(رؤيا14: 6) والحياة الأبدية(يوحنا 3: 16?36) وملكوت المسيح(2 بط 1: 11) وبالمظال الأبدية في السماء(لوقا 16: 9) وكذلك تشير إلى النار والعذاب الأبديين(مت 25: 41?46).'),
(33, 'أَبَدُّونُ', 'كلمة عبرية معناها ((هلاك))  أو ((خراب))  أو ((ابادة))  وقد وردت في كتب العهد القديم في العبرية في عدة مواضع وترجمت ((الهلاك))  (أيوب 31: 12). وكذلك ذكرت اسماً لمكان الموتى كمرادف لقبر(مز88: 11)? وكمرادف للهاوية(أيوب26: 6وأمثال15: 11)? وكمرادف للموت نفسه(أيوب28: 22). ? ((أبدون))  في رؤيا9: 11 اسم ملاك الهاوية واسمه باليونانية ((ابوليون)) .'),
(34, 'أَبْرَامُ-إِبْرَاهِيمُ', 'ومعنى أبرام ((الأب الرفيع))  أو ((الأب المكرّم))  ومعنى ابراهيم ((ابورهام))  أي ((أبو جمهور))  (تك17: 5). وتدرس تحت هذا العنوان المواضيع التالية:أولاً: تاريخ حياته.1-  حياته وهو فيما بين النهرين،2-   ومدتهما خمسة وسبعون عاماً. وهو ابن تارح من نسل سام بن نوح وقد عاش ابراهيم الجزء الأول من حياته مع أبيه واخوته في أور الكلدانيين  وقد تزوج من ساري وكانت أخته بنت أبيه وليست بنت أمه كما نعرف ذلك من تك 20: 12. وبعد موت أخيه هاران،3- رحل هو وزوجته وتارح ابوه ولوط ابن أخيه من أور ليذهبوا إلى أرض كنعان(تك11: 27-31) بناء على أمر الرب كما اشار على ذلك استيفانوس انظر أعمال7: 2-4 فأتوا وأقاموا في حاران حيث مات تارح(تك 11: 31?4-    32) ولما كان ابراهيم في الخامسة والسبعين من عمره رحل هو وزوجته ولوط من حاران إلى أرض كنعان بناء على أمر الرب(تك12: 1) ويحتمل أنهم ذهبوا عن طريق دمشق لأن أليعازر الدمشقي الموكل على بيتِه كان من هناك(تك15:2).2-تنقلاته في كنعان ومصر.أقام ابراهيم أولاً في شكيم( تك 12: 6) ثم ذهب إلى بيت إِيل (تك12: 8) وارتحل منها إلى أرض الجنوب(تك 12: 9) وحدث جوع في الأرض فارتحل من هناك إلى مصر(تك12: 10) وهناك، خوفاً على حياته، ذكر لفرعون أن ساراي أخته دون أن يذكر أنها زوجته(تك12: 11-20) ثم من هناك عاد إلى أرض الجنوب في فلسطين(تك13: 1) وذهب من هناك إلى بيت إِيل(تك13: 3) ثم افترقا هو ولوط بسبب كثرة أملاكهما. فاختار لوط أن يذهب إلى أرض دائرة الأردن(تك13: 5-12) أما ابراهيم فسكن في أرض كنعان ونقل خيامه وأتى وأقام عند بلوطات ممرا وبقي هناك سنوات عديدة(تك13: 12?13?18).وأثناء إقامته عند بلوطات ممرا عمل عهداً مع ملوك الأموريين(تك14:13). وشن كدرلعومر ملك عيلام وحلفاؤه حرباً على ملوك الأموريين فانتصر عليهم وسبى لوطاً وأملاكه، ولكن ابراهيم كسرهم واسترجع لوطاً والنساء وكل الأملاك(تك14: 1-16) وعند عودته استقبله ملكي صادق ملك شاليم، فأعطاه ابراهيم عشراً من كل شيء وبارك ملكي صادق ابراهيم(تك14: 17-24) وقد وعده الرب حينئذ بوارث فصدق وعد الرب وآمن به فحسبه له براً وقد وعده الرب بميراث ارض كنعان وايد له هذا الوعد بعهد(تكص15) وأخذ ابراهيم هاجر جاريته المصرية زوجة فولدت له اسماعيل(تك ? 16) ولما كان ابرام ابن تسع وتسعين سنة ظهر له الرب وغير اسمه من أبرام إلى ابراهيم ووضع له الختان علامة للعهد، وغير اسم ساراي امرأته إلى سارة، وكشف له مضمون العهد أن النسل الوارث سيكون من سارة وسيدعى اسمه اسحاق ويقيم الرب معه العهد(تك ? 17). ثم أعلن الرب لإبراهيم خراب سدوم وعمورة بسبب شرهما فتشفع ابراهيم لأجل الأبرار هناك فأنقذ الرب لوطاً بيد ملاكين(تك ? 18?19).ومن عند بلوطات ممرا انتقل ابراهيم على أرض الجنوب وهناك أرسل أبيمالك ملك جرار وأخذ سارة لأن ابراهيم قال انها أختي ولكن الرب ظهر لأبيمالك في حلم ولم يدعه يمسها ولما عاقبه الرب على أخذه سارة ردها إلى ابراهيم. وصلى ابراهيم لأجله ولأجل بيته فرفع الرب العقاب عنه(تك ? 20).وافتقد الرب سارة فحبلت وولدت لابراهيم ابناً في شيخوخته لما كان ابن مئة سنة.ودعا اسمه اسحاق، وختن ابراهيم اسحاق ابنه(تك21:1-8). وقد ألحت عليه سارة من جهة هاجر وابنها فسمح له الرب فأبعدهما بابعادهما(تك21: 9-21) وبعد ذلك عمل ابراهيم عهدا‌ مع ابيمالك عند بئر دعيت فيما بعد بئر سبع(تك21: 22-34).ولما كبر اسحاق أراد الرب أن يمتحن ابراهيم فأمره بان يذهب إلى أرض المريا ويصعد ابنه محرقة هناك. وإذ كان على وشك تقديمه ذبيحة ناداه ملاك الرب قائلاً ((لا تمد يدك إلى الغلام ولا تفعل شيئاً))  فرفع ابراهيم عينيه ونظر ? إذا كبش وراءه ممسكاًً في الغابة بقرنيه  فأخذ ابراهيم الكبش وأصعده محرقة عوضاً عن ابنه وبعد ذلك ذهبا معاً إلى بئر سبع(تك22: 1-9).ثم رجعوا إلى حبرون وهناك ماتت سارة وكانت سنو حياتها مئة وسبعة وعشرين ودفنها ابراهيم في قبر في مغارة المكفيلة التي اشتراها من بني حث(تك ?23).وبعد ذلك أرسل ابراهيم أليعازر الدمشقي إلى ما بين النهرين لكي يحضر لابنه زوجة من عشيرته فأحضر له رفقه بنت بتوئيل. وقابلها اسحاق عند بئر لحي رئي، فاتخذها اسحاق لنفسه زوجة، وكان حينئذ ابن أربعين سنة(تك ?24?25: 20).وبعد موت سارة أخذ ابراهيم لنفسه زوجة اسمها قطورة(تك25: 1-5) ومات ابراهيم لما كانت أيام سني حياته مئة وخمسا وسبعين سنة ودفن في مغارة المكفيلة( تك25: 7-10).ثانياً: إيمان ابراهيمكان آباء ابراهيم يعبدون آلهة غير الرب(يشوع 24: 2?14) فكانوا في اور الكلدانيين يعبدون آلهة كثيرة وبنوع خاص ((نانار))  إله القمر وزوجته ((ننجال))  وكان في أور على مرتفعة عالية بناء يشبه الهرم يسمى باللغة البابلية ((زجوراة))  وفوق ((الزجوراة))  معبد للإله  ((نانار)) . أما ابراهيم فقد آمن بالإله الواحد مالك السماء والأرض وإلههما (تك 14: 22? 24: 3) وديان الأمم وكل الأرض(تك 15: 14?18: 25) والذي كل قوات الطبيعة طوع أمره ولا يستحيل عليه شيء(تك 18: 14?19: 24?20: 17?18) وهو الإله العلي المرتفع( تك14: 22) وهو سرمدي أبدي (تك21: 33) ولم يكن الله لإبراهيم الإله الواحد فحسب بل كانت لإبراهيم معه علاقة شخصية وشركة روحية قوية(تك24: 14) ولذلك نال ابراهيم لقب ((خليل الله)) الذي ذكر في الكتاب ثلاث مرات(2 اخبار 20: 7 واش 41: 8 ويع2: 23) أما صفات الله التي نسبها ابراهيم إليه فهي: العدل(تك 18: 25)? البر(تك 18: 19)? الأمانة واللطف والحق(تك 24: 27)? الحكمة والرحمة(قارن تك 20: 6) وقد آمن ابراهيم ان الله يطلب من البشر أن يتصفوا بالصفات الخلقية التي لله(تك 18: 19) وقد أعلن الله ذاته لإبراهيم في الرؤى والأحلام(تك 15: 1?20: 3) والظهور في شكل إنسان أو في شخص ملاك الرب(تك18: 1?22: 11) وحيثما سكن ابراهيم كان يقيم مذبحاً للرب ويدعو باسمه(تك 12: 7?8) وقد قدم صلوات تشفعية لأجل الآخرين ففي تك 17: 20 صلى لأجل اسماعيل وفي تك 18: 23-32 تشفع لأجل لوط، قارن هذا مع تك19: 20? وفي تك 20: 17 صلى لأجل أبيمالك وذلك لأنه عرف بأنه نبي. وقد عمل ابراهيم عهوده ومواثيقه واقسامه باسم الرب(تك14: 22? 21: 23? 24: 3) وقد قدم عشوره لملكي صادق كاهن الله العلي(تك14: 20) وقد مارس الختان كعلامة للعهد مع الرب(17: 10-14) وكان إيمان ابراهيم عظيماً إلى الحد الذي عنده كان مستعداً أن يقدم ابنه وحيده ذبيحة للرب ولكن الرب منعه من ذلك(تك 22: 2?12).وقد كانت حياة ابراهيم مع الناس مظهراً لإيمانه بالله وقد ظهر هذا في كرمه(تك13: 9? 14: 23 الخ.) وإضافة الغرباء(تك 18: 2-8)? وإخلاصه ووفائه وأمانته، وحنوه ورقة عاطفته(تك 14: 14? 24?18: 23-32? 23: 29 وشجاعته(تك14: 14-16) إلا أنه أظهر ضعفاً مرتين عندما لم يقل الحق كله في ذكر علاقة سارة زوجته به(تك12: 18?20: 11).ثالثاً : مكانته في الكتاب المقدس:1- في العهد القديم:فإنه من زمن اسحاق وما بعده كان الرب(يهوه) يلقب بأنه ابراهيم(خروج3: 15) ويذكر الكتاب المقدس ان الرب ظهر لإبراهيم(خروج6: 3) واختاره(نحميا9: 7) وفداه(اش29: 22) وباركه هو ونسله واسطة بركة لجميع أمم الأرض(تك12: 3? 17: 18? 22: 17?18) ودعي ابراهيم خليل الله(2 اخبار20: 7? اش 41: 8).2- في العهد الجديد:يدعى ابراهيم في العهد الجديد أباً لبني اسرائيل(أعمال 13: 26) والكهنوت اللاوي(عب7: 5) وأباً للمسيح(مت 1: 1)? وغلاطية(3: 16) وأباً لكل المسيحيين كمؤمنين(غلاطية 3: 29? ورومية 4: 11) أما البركات التي بورك بها فقد وردت في العهد الجديد بأسماء متنوعة منها ((الوعد)) (رومية4: 13)  ((وبركة))  (غلاطية3: 14)  ((ورحمة))  (لوقا 1: 54?55) ? ((القسم))  (لوقا 1: 73)  ((والعهد))  (أعمال 3: 25) وقد قال المسيح ان ابراهيم رأى يومه وفرح(يوحنا 8: 56) ويذكر العهد الجديد ابراهيم كمثال للتبرير بالإيمان (رومية4: 3?11? 18) وكذلك ذكره كمثال للأعمال الصالحة التي بها أكمل الإيمان(يع 2: 21-23) وطاعة الإيمان(عب 11: 8?17) وقد أشار المسيح إلى مكانته السامية بين القديسين في السماء(مت 8: 11 ولو 13: 28? 16: 23-31).رابعاً: ابراهيم والكشوف التاريخية الحديثة:لا يمكن أن نعين على وجه التحديد التاريخ الذي عاش فيه ابراهيم ولكنه ولد، وفقاً للتاريخ الذي حسبه الأسقف اشر، حوالي سنة 1996?.?. وقد اكتشفت آثار ونقوش في بابل ترجع إلى ذلك العصر ووجد عليها اسم ابراهيم في هذه الصيغ ((ابرامو)) .  ((ابمرام))  ? ((ابمراما)) . وقد أظهرت الكشوف التاريخية الحديثة الحالة التي كانت عليها مدينة أور التي خرج منها ابراهيم كما كانت حينئذ.ويمكننا الآن أن نعرف من تلك الكشوف مقدار ما كانت عليه هذه المدنية من تقدم في المدينة، وكذلك يمكننا ان نعرف نوع الوثنية التي نشأ فيها ابراهيم في أور والتي خرج منها بناء على دعوة إلهية. ويمكننا أن نعرف العلاقة التي كانت بين أور وحاران لأن المدينتين كانتا تعبدان إلهاً واحداً هو إله القمر. وكذلك أظهرت الكشوف أن بعض المدن القديمة القريبة من حاران كانت تحمل أسماء أفراد أسرة ابراهيم كما ورد ذكرها في الكتاب المقدس، فمن ضمن هذه مدن فالح وسروج وناحور وتارح (قارن هذه مع تك 11: 16-26) وقد أظهرت عقود الزواج التي اكتشفت في مدينة نوزي في شمال ما بين النهرين أن العلائق التي كانت بين ابراهيم وسارة وهاجر كانت وفقاً للنظم ? القوانين التي كانت سائدة في ذلك الحين في تلك البلاد. ومع أن أسماء الملوك المذكورين في تك ?14 لم تكتشف بعد إلا أن الكشوف التي وجدت دلت على أن كثيرين من ملوك بابل كانوا يقومون بحملات على كنعان في ذلك الحين. وكذلك دلت الكشوف والبحوث التاريخية على أن الأقاليم المجاورة للبحر الميت أي (( أرض دائرة الأردن))  كانت عامرة آهلة بالسكان إلى حوالي سنة 2000 قبل الميلاد وبعد ذلك وقعت كارثة مروعة وصفها بعضهم بأنها شبيهة بانقلاب بركاني أو بانفجار ذريع في جوف الأرض اندلعت منه نيران ولهب ارتفعت في الجو ثم نزلت على الناس نزول المطر ونتيجة لذلك خربت تلك البقاع وبقيت بلقعاً خالياً خاوياً مدة قرون عديدة.حضن ابراهيم((انظر حضن))'),
(35, 'أَبْشَالُومُ', 'ومعنى الاسم العبري ((ابي سلام))  أو ((الأب سلام))  أو ((أبو السلام))  وهو ثالث أبناء داود ولد في حبرون واسم أمه معكة بنت تلماي ملك جشور في آرام وهي بقعة صغيرة واقعة بين حرمون وباشان(2صم 3: 3). وعندما نقل داود عاصمة ملكه إلى أورشليم انتقل معه أبشالوم وهو لا يزال بعد صبياً صغيراً. وقد كان أبشالوم حسن المنظر جميل الصورة طويل الشعر محبوباً من أبيه ومن جميع الشعب.نفيه:عندما أذل امنون ثامار أخت أبشالوم الشقيقة، وكان امنون أخاه من أبيه، توانى داود عن إيقاع العقاب بأمنون فاغتاظ أبشالوم جداً. وبعد سنتين أقام أبشالوم وليمة في بعل حاصور ودعا إليها جميع بني الملك ومن ضمنهم أمنون ولكنه أوصى عبيده بأنه متى طاب قلب أمنون أن يوقعوا به ويضربوه ويقتلوه. فلما قتل أمنون غضب داود جداً . وهرب أبشالوم من أمام وجهه إلى ملك جشور أبي أمه وبقي هناك ثلاث سنوات(2صم ?13) ولما عفا داود عنه, عاد إلى أورشليم وبقي فيها سنتين لم ير فيهما وجه الملك(2صم ?14).عصيانه على أبيه:فلما عاد أبشالوم إلى أورشليم بدأ يجذب قلوب الشعب إليه وسلبها من أبيه، ولما أكمل وضع خطة مؤامرته على أبيه ? ادعى أنه يريد الذهاب إلى حبرون وفاءً لنذر كان قد نذره وهو في جشور فإذن له الملك بذلك فذهب إلى حبرون. ومن هناك أرسل جواسيسه إلى جميع أسباط اسرائيل فاشتدت الفتنة واجتمع إليه جمع كبير من الشعب ومن ضمنهم أخيتوفل وكان أحد مشيري داود الأذكياء(2صم 15: 1-12).هروب داود أمامه:فهرب داود وجميع المخلصين له من بنيه وشعبه، من أورشليم. ولكنه أرسل صادوق وأبيثار الكاهنين إلى أورشليم ثانية مع تابوت العهد وأرسل أيضاً حوشاي أحد مشيريه لكي يعمل هؤلاء على إبطال مشورة أخيتوفل(2 صم 15: 13-37). فعندما أشارأخيتوفل بأن يهاجم أبشالوم داود مباشرة أشار حوشاي بتأخير الهجوم. ثم تشاور مع  صادوق وأبيثار وأرسلوا إلى داود لكي لا يبيت تلك الليلة في سهول البرية وبذلك أتاحت هذه المشورة فرصة كاملة لداود ليعبر الأردن ويهرب إلى محنايم في جلعاد(2 صم 16-17: 24).انهزامه وموته:أقام أبشالوم على جيشه عماسا قائداً بدلاً من يوآب. ونزل أبشالوم وجيشه إلى جلعاد(2صم 17: 25-29) وفي هذه الأثناء قسم داود جيشه إلى ثلاثة أقسام تحت قيادة يةآب وأبيشاي وأتاي(2صم 18: 1?2) وفي المعركة التي وقعت في غابة أفرايم قتل ما يقرب من عشرين ألف جندي من جيش أبشالوم وقد لك بين أشجار الغابة الكثيفة عدد يزيد على هذا العدد ومن ضمن هؤلاء أبشالوم نفسه وقد كان راكباً على بغل فدخل البغل تحت أغصان شجرة بطم فتعلق رأسه بغصن يرجح أنه كان منخفضاً متشعباً فعلق بين السماء والأرض ومر البغل الذي كان تحته(2صم 18: 6-9) فوجده جندي وأخبر يوآب فأخذ يوآب ثلاثة سهام وصوبها إلى قلب أبشالوم وهو لا يزال بعد حياً غير عابىء بوصية داود أن يحترزوا من أن يمسوا أبشالوم بسوء.وللتحقق من موته أحاط به عشرة من فتيان يوآب وضربوه وقتلوه(2صم 18: 10-15) ودفنوه في حفرة عظيمة بالقرب من المكان الذي قتل فيه واقاموا عليه رجمة عظيمة من الحجارة وفقاً لعادة اليهود في تحقير الثوار والمجرمين والتشهير بهم في دفنهم(2صم18: 17) قارنه مع يشوع 7: 26?8: 29.حزن داود عليه:لما بلغ داود خبر موته استسلم لحزن شديد- وفي بكاء ورثاء ابن عن شعور العطف الجميل والحنو الشديد نحو ابنه العاق(2صم 18: 33) ويظهر من عنوان المزمور الثالث انه كتب أثناء عصيان أبشالوم على أبيه داود.نَصَب أَبْشَالُوم:أقام أبشالوم قبل موته نصباً في وادي الملك لتخليد ذكره لأنه قال ((ليس لي ابن لجل تذكير اسمي))  2صم 18: 18 ومع أننا نقرأ في 2صم 14: 27 أنه كان له ثلاثة أبناء وابنة إلا أنه يفهم من قوله هذا أن أبناءه ماتوا في سن مبكرة. ودعي هذا النصب ((يد أبشالوم))  ولا يعرف موضعه الآن على وجه التحقيق ويوجد في وادي قدرون اليوم قبر يعرف بقبر أبشالوم ولكن يظهر من هندسة البناء أنه أقيم في عصور متأخرة ربما ترجع إلى العصر الروماني أو الإغريقي الروماني.'),
(36, 'أَبْشَاي', 'انظر ((ابيشاي))'),
(37, 'إِبْصَان', 'وهو أحد قضاة اسرائيل والعاشر في عددهم. جاء بعد يفتاح وقضى لشعبه مدة سبع سنين وكان له عدد عظيم من البنين والبنات. وكان من بلدة تدعى ((بيت لحم))  ويرجح أنها بيت لحم التي في زبولون والتي كانت مقر قضائه. وقد مات ودفن فيها(قضاة12: 8-10) ويوجد تقليد يهودي يقول أن إِبصان هذا هو نفس بوعز الذي كان في بيت لحم يهوذا.'),
(38, 'أَبَغْثَا', 'ويرجح أن الاسم فارسي وربما معناه (( السعيد الحظ))  وكان أحد الخصيان الذي في خدمة احشويروش(استير 1: 10).'),
(39, 'أَبَفْرَاس', 'اسم يوناني اختصار ابفرودتس وربما معناه ((الحسن المنظر)) . وكان خادماً غيوراً في كنائس كولوسي ولاودكية وهيرابوليس كما كان العامل الرئيسي في تأسيس هذه الكنائس. وهوالذي حمل إلى بولس الرسول في السجن أخباراً طيبة عن كنيسة كولوسي(كو1: 7?8? 4: 12?13) ثم صار بعد ذلك رفيق الرسول في السجن(فليمون23). ويظهر تقدير الرسول له من الألقاب التي يطلقها عليه مثل ((العبد الحبيب معنا))   ((خادم أمين للمسيح))   ((وعبد للمسيح))  وهذا اللقب الأخير أطلقه  الرسول على نفسه عدة مرات ولم يطلقه على آخر غير أبفراس إلا مرة واحدة عندما لقب به تيموثاوس (فيلبي 1: 1).ومع أن الاسم ابفراس هو اختصار (( ابفرودتس))  إلا أنه يرجح أن هذين اسمان لشخصين مختلفين. فابفراس هذا كان من كنيسة كولوسي أما ابفرودتس فكان من كنيسة فيلبي ويظهر أن ابفراس كان يتمتع بمكانة في كنيسة كولوسي أعظم من التي كان يتمتع بها ابفرودتس في كنيسة فيلبي. انظر ابفرودتس.'),
(40, 'أَبَفْرُودِتُس', 'اسم يوناني وربما معناه ((الحسن المنظر)) . انتدبته كنيسة فيلبي ليحمل عطايا تقدير الأخوة هناك للرسول بولس ومحبتهم له وقد كان بولس في ذلك الحين مأسوراً في رومية( فيلبي4: 18) وبعد وصول ابفرودتس إلى رومية أصيب بمرض خطير وحزن عندما علم أن أخبار مرضه قد وصلت إلى فيلبي وأحدثت قلقاً للأخوة هناك ولذلك فإنه حالما استرد صحته أرسله بولس بسرعة إلى فيلبي مرة ثانية(فيلبي2: 25-30) وقد حمل ابفرودتس رسالة الرسول إلى المؤمنين هناك.'),
(41, 'أَبْفِيَّة', 'يظهر أنه اسم فيريجي يحمل معنى  ((التعزيز)) . وابفية امرأة مسيحية في كنيسة كولوسي وكانت على ما يظهر فرداً من أفراد أسرة فليمون ولذلك ظن البعض أنها كانت زوجة فليمون نفسه انظر فليمون عدد2.'),
(42, 'أَبَلِّس', 'اسم لرجل مسيحي في رومية أرسل إليه الرسول تحياته في رسالته(رومية 16: 10) ويصفه بولس بالقول ((المزكى في المسيح)) .'),
(43, 'أَبُلُّوس', 'اسم يوناني اختصار  ((ابولونيوس))  أو  (( ابولودورس)) . وهو رجل يهودي ولد في مدينة الاسكندرية وكان فصيحاً ملماً بما جاء في كتب العهد القديم وكان يتبع تعاليم يوحنا المعمدان ويكرز بغيرة عن المسيا المنتظر مع أنه لم يكن يعرف إلا معمودية التوبة التي كرز بها يوحنا المعمدان. وقد قام برحلة تبشيرية في آسيا الصغرى والتقى باكيلا وبرسكلا في مدينة أفسس. وقد علمه هذان بأكثر وضوح عن المسيح ويرجح أنه بعد ذلك مباشرة ذهب إلى أخائية حيث واصل عمله التبشيري هناك وشجع المؤمنين كثيراً وكان يحاج اليهود بقوة مثبتاً لهم أن يسوع هو المسيح( أعمال18: 24-28) وقد ذهب بولس إلى أفسس حالاً بعدما غادرها أبلوس ووجد هناك جماعة لا تعرف إلا معمودية يوحنا فقط ولم تسمع عن الروح القدس فكرز لهم بالمسيح وأقام بينهم مدة سنتين( أعمال 19: 1-10) وقد لقيت كرازة أبلوس في كورونثوس نجاحاً، إلا أنه وُجد بسببها انشقاق وتفرقة في الكنيسة إذ كان هناك اختلاف ضئيل بين كرازة بولس وبطرس وأبلوس مع أن أبلوس نفسه لم يقصد هذه التفرقة البتة(1 كو 1: 12?3: 4-6?22?4: 6) ويظهر أن الرسالة الأولى إلى أهل كورونثوس كتبت لبحث هذا الموضوع بين بولس وأبلوس. فالرسول بولس لم يكتب هذه الرسالة بقصد انتقاد زميله في الخدمة أو معارضته بل كتبها مقاوماً روح التفرقة والشقاق. وقد بقيت ثقة الرسول بأبلوس قوية إلى النهاية وقد حثه على زيارة كورونثوس مرة أجرى(1 كو 16: 12) وآخر ذكر لأبلوس نجده في رسالة الرسول إلى تيطس حيث يطلب إليه فيها أن يساعد أبلوس( تيطس 3: 13).'),
(44, 'إِبْلِيس', 'أصل الاسم في اللغة اليونانية (( ديابولس))  ومعناه (( المشتكي زوراً))  أو ((الثالب)) . والكلمة ((ديابوليس))  في العهد الجديد باللغة اليونانية ترجمت في العربية في معظم الأماكن بكلمة ((ابليس))  وفي مواضع قليلة ترجمت ((بالشيطان))  او ((الثالب)) . وهو ((روح شرير))  أو ((شيطان)) . وقد استخدمت هذه الكلمات كمرادفات انظر مت 4: 1-11 وبحسب دراسة ما وردت في الكتاب المقدس عنه نجد أنه:(1) أكثر الأرواح الساقطة شراً(رؤيا 12: 9) كما جاء ذكره في التجربة فيما سبق. أما الخطية التي سقط فيها فهي الكبرياء كما يظهر ذلك من أتيمو 3: 6.(2) وهو أكبر عدو لله( 1 يو 3: 8) والإنسان(1 بط 5: 8) وهو الذي جرب المسيح،(3) وهو الذي يغري الإنسان على ارتكاب الشر( يوحنا 13: 2).(4) وهو الحية القديمة التي أوقعت حواء في التجربة(2كو 11: 3) ولذلك دعي ((قتالاً للناس من البدء))   ((وكذاباً))   ((وأبا الكذاب)) .(5) وهو الذي ينزع الزرع الجيد متى زرع(لوقا8: 12) أو يزرع في وسطه زواناً (مت13: 39).(6) وهو كأسد زائر يجول دائماً ملتمساً من يبتلعه هو(1 بط 5: 8).(7) وهو الذي يضع فخاخاً ويطرح شباكاً بقصد إيقاع الضرر والاذى بابناء الله(افسس 6: 11?2تيمو2: 26) ويسببهم بخداعه(2 كو 11: 3) ولكن على الشخص المجرب ان لا يستسلم لتجربة ابليس بل عليه ان يقاومه فيهرب منه(افسس 4: 27 ويعقوب 4: 7).(8) ولابليس قوة على اعطاء الارواح النجسة سلطة على البشر(اعمال 10: 38).(9) وهو الذي يغري على اضطهاد الشهداء وسجنهم(رؤيا 2: 10).(10) وسيطرح في النهاية في بحيرة متقدة بالنار والكبريت قد اعدت له ولجنوده(مت 25: 41ويهوذا6). وقد سمي المتأصلون في الشر والكذب والقتل اولاد ابليس (يوحنا 8: 44) ?1 يو 3: 8?10). وقد دعا المسيح يهوذا مسلمه ابليس كما في الاصل اليوناني في(يوحنا 6: 70) ةقد جاء المسيح الى العالم ليهدم عمل ابليس( 1 يوحنا 3: 8) وقد اشار يهوذا في عدد9 من رسالته الى الخصام بين ابليس وميخائيل على جسد موسى. وقد ارتأى بعضهم ان في زكريا 3: 1?2 اشارة الى هذا الامر(انظر شيطان).'),
(45, 'أبِلِيَّة أو أبِيلِينَة', 'اسم لمقاطعة في سوريا اخذته من اسم عاصمتها ((ابيلا))  ويرجح ان هذا الاسم ((ابيلا))  مأخوذ من الكلمة العبرية آبل التي تعني ((مرج))  وتقع العاصمة على نهر بردى على مسافة ثمانية عشر او عشرين ميلاً من دمشق ويرجح ان مكانها اليوم قرية ((سوق وادي بردى))  او بالقرب منها ويذكر في لوقا 3: 1 انها كانت في السنة الخامسة عشرة من سلطنة طيباريوس قيصر، ولاية يحكمها ليسلنيوس رئيس الربع، وكان هذا في ايام كرازة يوحنا المعمدان.'),
(46, 'أبِنَ', 'انظر مأبون'),
(47, 'أبْنيْر', 'ومعنى الاسم ((ابي نور))  او ((الاب نور))  وكان رئيساً لجيش شاول الملك وهو ابن نير عم شاول(1صم 14: 50) واول مرة التقى فيها بداود كانت عندما قتل داود جليات الفلسطيني( 1صم 17: 55-58) وقد رافق ابنير شاول في مطاردته لداود(1 صم 26: 5وما بعده) ولما مات شاول اخذ ابنير ايشبوشت بن شاول ونادى به ملكاً في محنايم(2 صم 2: 8) وكان من نتيجة ذلك ان اشتعلت الحرب بين بيت شاول وبيت داود وانكسر ابنير ورجال اسرائيل امام عبيد داود فسعى عسائيل اخو يوآب رئيس جيش داود وراء ابنير فقتله ابنير. واستمرت العداوة قوية بين بيت شاول وبيت داود(2 صم 2: 12-3: 6). وكلنت لشاول سرية اسمها رصفة بنت أية اخذها ابنير لنفسه ولما سأله ايشبوشت عن سبب ذلك اغتاظ جداً وأخذ البنيامينيين الى جانبه. وذهب الى داود الى حبرون ومعه عشرون رجلاً منهم ثم اتفق معه على ان يجمع جميع اسرائيل الى جانبه. واذ ذهب لاتمام هذا الاتفاق علم يوآب بما حدث، ومن دون ان يست\nاذن داود ارسل واعاد ابنير بزعم التحدث اليه ولكنه قتله غيلة انتقاماً لاخيه عسائيل. ولما سمع داود الخبر حزن عليه حزناً عميقاً وبكاه مع جميع الشعب ورثاه وقال (( الا تعلمون ان رئيساً وعظيماً سقط اليوم في اسرائيل))  (2صم 3: 6-38). ومع ان داود لم يكن له شأن البتة في موت ابنير الا ان موت ابنير سلب المعارضين لداود قوتهم فانحاز اليه كل الشعب ونودي به ملكاً على كل اسرائيل.'),
(48, 'أبٌ', 'كلمة سامية وردت بهذا اللفظ في العبرية والفينيقية والاشورية والارامية والسريانية والسبئية والحبشية كما في العربية. وقد وردت في الكتاب المقدس بمعان كثيرة منها:(1)  السلف المباشر للانسان أي والده(تك 2: 24?(2) 42: 13) وكان للأب في الاسرة العبرانية وكذلك في الاسرة الرومانية سلطة مطلقة على اولاده،(3)  فكان مِنْ حقه مثلاً ان يتصرف كما يشاء في زواج ابنته(تك 29: 1-29) وفي تدبير زواج ابنه(تك ? 24) وكان له ان يبيع اولاده ان اراد ذلك(خروج 21: 7) كما كان له عليهم حكم الموت او الحياة كما نرى في تقدمة ابراهيم اسحاق ابنه(تك ?22) وكذلك ابنة يفتاح ( قض 11: 34 وما بعده) وكان الآباء قديماً يقدمون اولادهم ذبيحة لمولك( لاويين 18: 21) واحترام الآباء واكرامهم،(4) وكذلك الامهات،(5) كان واجباً لزاماً على الاولاد (خروج 20: 12?(6) لاويين 19: 3?(7) تثنية 5: 16).(8) الجد او الاسلاف على وجه عام(تك 28: 13?(9) 1 ملو 22: 50?(10) ارميا 35: 6) وقد ورد ذكر آباء بمعنى الاسلاف عامة ما يزيد على خمسمائة مرة في العهد القديم.وكذلك اطلق اللفظ ((آباء))  على الاصول الاولى في الامة والقبيلة والديانة كما في رومية 9: 5  ((ولهم الآباء))  وخروج 6: 14  (( هؤلاء رؤساء بيوت آبائهم)) .(11) اطلق هذا اللفظ رمزياً:  أ .على الاب الروحي الذي ينفث من روحه في غيره سواء كان تأثيره طيباً او على النقيض من ذلك. فقد دعي ابراهيم ((ابو المؤمنين))  (رومية 4: 11) كما دعي ابليس أب الاشرار ((انتم من أب واحد وهو ابليس))  (يوحنا 8: 44).ب . وللدلالة على التشابه والتقارب والتماثل ((وقلت للقبر انت أبي))  (ايوب 17: 14).ج . وعلى مصدر الشيء كما في افسس 1: 17  ((ابو المجد))  ? ايوب 38: 28  ((هل للمطر أب)) .د .وعلى الخالق كما في يعقوب 1: 17  ((ابو الانوار)) .هـ.وعلى مبتدع فن ما او عمل ما او مبتكر اسلوب خاص للحياة كما في تكوين 4: 20  ((أب ساكني الخيام ورعاة المواشي)) .و . على الشخص الذي تظهر فيه خاصيات الابوة كما في مز 68: 5  ((ابو اليتامى)) .ز . على من يقوم بعمل المرشد والمشير والمهتم بامر من الامور كما في تك 45: 8  ((وهو قد جعلني اباً لفرعون ))  وقض 17: 10  (( وكن لي اباً وكاهناً)) .ح . على رئيس محترم مكرم كما في 2 ملوك 5: 13  ((فتقدم اليه عبيده وكلموه وقالوا:  ((يا ابانا))  واطلق بخاصة على الانبياء كما في 2 ملوك 2: 12  ((وكان اليشع يركض وهو يصرخ:  ((يا ابي يا ابي مركبة اسرائيل وفرسانها؛))  كما اطلق على المتقدمين في السن والمقام( 1 يوحنا 2: 14)  (( اكتب اليكم ايها الآباء))  وعلى المسيحيين الاولين كما في 2 بط 3: 4.  ((من حين رقد الاباء)) .(12) يعتبر الله في الديانة المسيحية اباً فيقال ((ابانا الذي في السماوات))  وهكذا(مت 6: 9?14?26) ويدعى الله ((ابو ربنا يسوع المسيح))  (2 كو 11: 31) وان قوة العلاقة وغنى المجبة والنعمة المتضمنة في هذا التعبير العميق والخاصة بانجيل المسيح تبدو واضحة. وقد اعلن الله في العهد القديم كأب للشعب المختار (خروج 4: 22)?(13) وللملك الذي كان الممثل الخاص للشعب (2صم 7: 14) وتظهر ابوته في ترأفه ((كما يترأف الأب على البنين يترأف الرب على خائفيه))  (مز 103: 13). ولكن ابوة الله هذه اعلنت بانها نفس جوهر الذات الالهية وبانها وثيقة الصلة بالانسان،(14) في انجيل المسيح فقط. فاننا نستخلص من كلمات وحياة يسوع انه دعا الله  ((اباً))  ليس لانه الخالق او الحاكم او بسبب عهده مع ابراهيم ولكن لانه يحبنا. وقد وردت كلمة أب تسعين مرة في انجيل يوحنا وخمس مرات في مرقس وسبع عشرة مرة في لوقا وخمساً واربعين مرة في متى. وفي كل مرة من هذه المرات،(15) ما عدا اربع،(16) ورد هذا القول على فم يسوع.وابوة الله تسير في اتجاهين الاتجاه الاول: ابوته للبشر بالخلق والثاني: ابوته للمؤمنين بالنعمة. فقد خلق الله الانسان مشابهاً طبيعته حتى تتحقق بنوته لله ولكن الخطيئة وقفت حائلاً دون تحقيق هذه الغاية التي لا يمكن تحقيقها الآن الا بالفداء ولذا فتعتبر بنوة المؤمنين لله امتيازاً لا يُنْطق به ومجيد ( 1 يوحنا 3: 1). وهي تنال بالنعمة بالميلاد الثاني ( يوحنا 1: 12?13) والتبني(رومية 8: 14?19) وفي هذه العلاقة الممتازة في القرب من الآب يعتبر المؤمنون ابناء الله بمعنى خاص بهم دون غيرهم (كولوسي 1: 13?14) وهذه العلاقة ليست بحسب الطبيعة ولكنها بالنعمة.'),
(49, 'أَبُو عَلْبُون', 'ربما كان معنى الاسم العبري ((ابو القوة))  او ((القوي)) . وهو احد ابطال داود (2 صم 23: 31) ويسمى ابيئيل العَرَباتي في( 1 اخبار 11: 32). وربما يمكن ان نستنتجمن اللقب  ((العرباتي))  انه كان في بيت العربة(يشوع 15: 6?61) انظر بيت العربة.'),
(50, 'أَبُو كْرِيفَا', 'كلمة يونانية معناها ((مخفي))  او ((مُخبأ))  او ((سري)) . وقد وردت في سفر دانيال في الترجمة السبعينية(وهي ترجمة يونانية للعهد القديم) في ? 11: 43 للتعبير عن الكنوز المخفية. كما وردت في دانيال 2: 19 للدلالة على معرفة الاسرار المخفية عن علم البشر وقد وردت الكلمة في اليونانية في العهد الجديد ثلاث مرات: مر 4: 22  ((لانه ليس خفي لا يظهر))  ولوقا 8: 17 وكولوسي 2: 3  ((المذخر فيه جميع كنوز الحكمة والعلم)) .وقد كان هناك نوعان من المعرفة الدينية عند اليونان في ذلك الحين. النوع الاول كان يشمل عقائد وطقوساً عامة يمكن لجميع طبقات الشعب معرفتها وممارستها، اما النوع الثاني فقد كان يشمل حقائق عميقة غامضة لايمكن ان يفهمها او يدرك كنهها الا قلة من الخاصة ولذلك بقيت  ((مخفية))  او ((ابو كريفية))  عن العامة.وقد اطلقت في العصور المسيحسة الاولى على بعض الكتب غير القانونية في العهد القديم وكذلك في العهد الجديد. ويمكن ان ندرك معنى الخفاء والسرّيّة في ذلك لان بعض هذه الكتب ((رؤَوي))   Apocalyptic تحدث عن امور مستقبلة كانت بطبيعتها ((مخفية))  وكتبت في اوقات محنة لتشجيع الشعب.ومع ان هذه الاسفار قد وضعت ضمن النسخة السبعينية للعهد القديم الا ان علماء اليهود لم يضعوها ضمن كتبهم القانونية للاسباب التالية:1 . ان هذه الكتب قد نسبت الى اناس لم يكتبوها اصلاً.2 . ولان معظم هذه الاسفار قد كُتب باللغة اليونانية.3 . ةلانها لا ترتفع الى المستوى الروحي الذي في الاسفار القانونية ولذلك لا يمكن اعتبارها وحياً.4 . كتبت هذه الاسفار في عصور متأخرة بعد ان ختم العهد الجديد.وقد سار الآباء المسيحيون الاولون، على نهج علماء اليهود في نظرتهم الى هذه الاسفار. ومع انهم اقتبسوا بعض الاقوال الواردة فيها الا انهم لم يضعوها في منزلة الكتب القانونية. وقد اجازوا قراءتها للاستنارة والتعليم، لما فيها من معلومات تاريخية ولكنها لم تكن في عرفهم صالحة كأساس لعقيدة دينية او تعليم كنسي او طقس ما. وقد دافع عن هذه الكتب افراد بين حين وآخر وقاموا بمحاولات لادخالها او ادخال بعض منها ضمن الكتب القانونية الا انه عندما قررت مجامع الكنيسة الاولى الكتب التي تدخل ضمن الاسفار القانونية اعتبرت كتب الابو كريفا كتباً اضافية او ((محذوفة))  او غير قانونية .ولكن بما ان هذه الكتب موضوعة ضمن اسفار العهد القديم في الترجمات السبعينية واللاتينية فقد اقر مجمع ترنت في القرن السادس عشر اعتبارها قانونية وقد وضعت ضمن التوراة الكاثوليكية ما عدا كتابي اسدراس وصلاة منّسى.ويمكن ترتيب اسفار الابو كريفا في العهد القديم على النحو التالي:(1) اسفار تاريخية وتشمل: اسدراس الاول والمكابيين الاول والثاني واضافات الى سفر دانيال وهي (أ) نشيد الثلاثة الفتية المقدسين وتتمة سفر دانيال و(ب) تاريخ سوسنة و(ج)تاريخ انقلاب بيل. وبقية سفر استير، ورسالة ارميا وصلاة منسى.(2) اسفار قصصية تحوي اساطير وهي: سفر باروخ وسفر طوبيت وسفر يهوديت.(3) اسفار رؤوية- اسدراس الثاني.(4) سفران تعليميان وهما: سفر حكمة سليمان وسفر حكمة يشوع بن سيراخ.وقد كتبت هذه الاسفار في ازمنة مختلفة الا اننا يمكن ان نقول ان معظمها كتب ما بين سنة 200 قبل الميلاد و100 بعد الميلاد.ام ابو كريفا العهد الجديد فتحوي عدة كتب فيها تواريخ واناجيل موضوعة مصنوعة، منسوبة الى اناس لم يكتبوها، وتراجم ورسائل- ولم تدخل هذه الكتابات ضمن الكتاب المقدس على الاطلاق وهي تقل كثيراً في مرتبتها الروحية عن الكتب الحقيقية، وقد انشأها على هذا المنوال بعض ذوي الاغراض لمآرب ذاتية او لاثبات وجهات نظر معينة ولكن عرفت مطامحهم ولم تعبأ الكنيسة بما وضعوه أو انشأوه.'),
(51, 'أبُو لُونِيَّة', 'اسم يوناني نسبة الى أبولو. وهي بلدة في مكدونية وكانت واقعة على طريق أغناطية أحدى الطرق الرومانية الحربية والتجارية. ويظن أنها أسست في وقت سابق للقرن الرابع قبل الميلاد. وقد اجتاز فيها بولس و سيلا في طريقهما من فيلبي إلى تسالونيكي(أعمال 17: 1).'),
(52, 'أبُو ليُّون', 'اسم يوناني معناه ((المهلك))  أو ((المبيد)) . والكلمة من صوغ كاتب سفر الرؤيا انظر ص 9: 11 وقد استخدمها كترجمة لكلمة ((أبدون))  العبرية التي تعني الهاوية أو مكان الموتى ولكن الكاتب هنا وضعها للدلالة على شخص أو ملاك يحكم على الهاوية. وقد جعله يوحنا بنيان في كتابه ((سياحة المسيحي)) أحد الشخصيات التي يلتقي بها المسيحي في طريقه(انظر أبدون).'),
(53, 'أبِي', 'ويغلب على الظن أن الاسم العبري في هذا الوضع اختصار ((أبية))  أو ((أبيا))  التي معناها ((الربُّ أبٌ))  أو ((الرب أبي)) : وهي أم الملك حزقيا وبنت زكريا(2 ملوك 18: 2).وورد ذكرها باسم ((أبية))  في 2 أخبار 29: 1.'),
(54, 'أبِيَاثَار', 'اسم عبري ومعناه ((أبو الفضل))  أو ((أبو التفوق))  أو ((الأبُ فاضل))  وكان كاهناً وهو ابن أخيمالك من نسل عالي. ولما قتل شاول أباه أخيمالك والكهنة في نوب لأنهم أعطوا الخبز المقدس وسيف جليات لداود عندما كان فاراً من وجه شاول، هرب أبيثار إلى داود(1صم 22: 20-23) ويبدو أنه عندما أخذ داود الملك اشترك أبيثار مع صادوق في رئاسة الكهنوت(قارن 1 أخبار 15: 11) وقد بقي أبيثار أميناً لداود أثناء عصيان ابشالوم عليه ولكن لما أراد ادونيا أن يخلف داود في الملك اشترك أبياثار مع يوآب ابن صروية في مساعدته على تنفيذ مطمعه، ولكن هذه المحاولة باءت بالفشل وجلس سليمان على كرسي أبيه(1ملوك 1: 5-31).وفي بداية ملك سليمان قام أدونيا بمحاولة أخرى فغضب سليمان عليه وأرسل وقتله وطرد أبيثار من الكهنوت(1ملوك 2: 12-28).وبطرده حرم ابناه أخيمالك ويوناثان من الكهنوت وبذلك تمت النبوة على بيت عالي من حيث نهاية كهنوته(1 صم 2: 31-35).وقد أشار يسوع المسيح إلى أبيثار في مرقس 2: 26.'),
(55, 'أبِيأسَاف', 'اسم عبري ومعناه- ((أبي جمع أو زاد))  وهو من نسل لاوي من بني قورح ورئيس إحدى عشائر القورحيين( خروج 6: 24و1 أخبار 6: 23و37 وغيره).'),
(56, 'أبِيَام', 'اسم عبري ومعناه ((ابو اليم))  أو ((أبو البحر))  أو ((ابو الغرب)) . وهو ابن رحبعام ملك يهوذا الذي خلف أباه على العرش(1ملوك 14: 31و15: 1و7و8) وقد قرىء الاسم ((أبيا))  قارن مت 1: 7.'),
(57, 'أبِيئِيل', 'ومعنى الاسم العبري ((أيل أبي))  أو ((الله أبي)) .(1)  وقد ورد اسماً لرجل بنياميني كان أب قيس ونير وجد شاول الملك وأبنير( 1 صم 9: 1و14: 51).(2)  اسم أحد أبطال داود( 1 أخبار11: 32) ويدعى أبو علبون في (2 صم 23: 31).'),
(58, 'أبِيب', 'اسم عبري ومعناه ((سنبلة خضراءمن الشعير أو غيره من الحبوب))  وهو أول شهر في السنة العبرية. وقد غير اسم هذا الشهر إلى (( نيسان))  بعد السبي(نحميا 2: 1 واستير 3: 7) وأبيب اسم وصفي معناه شهر سنابل الحبوب الخضراء او شهر الحصاد. والسنة التي كانت تبدأ بشهر أبيب تشبه السنة البابلية القديمة ويرجح بأن إبراهيم هو الذي أدخلهافي التقويم العبراني. وكان عيد الفطير او عيد الفصح يقع في هذا الشهر. (خروج 12: 2و13: 4 و23: 15 وتثنية 16: 1). وهو يقابل شهر آذار( مارس) وأوائل نيسان(أبريل) في التقويم الميلادي.'),
(59, 'أبِيجَايل', 'اسم عبري ومعناه ((أبي فرح))  وهو اسم:(1) امرأة نابال وكانت امرأة ذكية الفهم جميلة المنظر. ولما مات نابال زوجها أخذها داود امرأةله(1 صم 25: 3و14-44) ولما أخذ العمالقة صقلغ، أخذوها أسيرة.ولكن داود أنقذها بعد أن هزم العمالقة(1 صم 30: 5 و18) وولدت له ابناً يدعى كيلآب(2صم 3: 3) أو دانيئيل(1 أخبار 3: 1).(2) أخت داود ويظن أنها أخته من أمه وليست من أبيه لأنها تدعى في 2 صم 17: 25. بنت ناحاش. وكانت أم عماسا(1 أخبار 2: 17).'),
(60, 'أبيَحائِل أو أبيحايِل', 'اسم عبري ومعناه ((أبو القوة))  أو ((الأب قوة))  وهو:(1) اسم لأحد اللاويين في عشيرة مراري(عدد 3: 35).(2) اسم امرأة أبيشور(1 أخبار 2: 29).(3) اسم أحد رؤساء سبط جاد(1 أخبار 5: 14).(4) اسم امرأة الملك رحبعام ومن نسل الياب أخي داود(2 أخبار 11: 18).(5) اسم أبي أستير الملكة(أستير 2: 15).'),
(61, 'أبِيدَاع', 'اسم عبري ومعناه ((أبو المعرفة))  أو (( ابي يعرف))  وهو ابنمديان وحفيد إبراهيم من قطورة (تك 25: 4 وأخبار 1: 33).'),
(62, 'أبِيِدَنِ', 'اسم عبري ومعناه ((الأب قاضٍ))  وهو ابن جدعوني( عدد2: 22) احد رؤساء بني بنيامين وقد اختير ليُمَثِّلَ سبطه في التعداد الذي تم في برية سيناء(عدد1: 11).'),
(63, 'أبِيرَام', 'اسم عبري ومعناه ((الأب الرفيع))  أو ((أبي رفيع))  وقد ورد:(1) اسم ابن الياب من نسل رأوبين(عدد 26: 5-9) وقد اشترك مع أخيه داثان وقورح وغيرهم في مخاصمة موسى وهرون(عدد ص16).(2) اسم لبكر حيئل البتئيلي الذي أعاد بناء أريحا في أيلم أخاب فبا بيرام بكره وضع أساسها وبسجوب صغيرة نصب أبوابها(1ملوك 16: 34).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(64, 'أبِيشَاي أوأبْشَاي', 'اسم عبري وربما معناه ((ابي يسى)) . وكان ابن فروية أخت داود وكان هو وأخوله يوآب وعسائيل من ضمن أبطال داود البارزين(2 صم 2: 18).ولما كان مع داود وهو هارب من وجه شاول ووجد شاول نائماً ليلاً أشار على داود بقتله  فأبى داود قائلاً ((لا تهلكه فمن الذي يمد يده إلى مسيح الرب ويتبرأ ؟))  (1صم 26: 9). وكلن يعمل مع يوآب في جيش داود( 2 صم 2: 18) ولما هرب ابنير بعد معركة جبعون تبعه عسائيل ولم يشأ أن يتحول عنه، وضربه أبنير فمات، فتبع يوآب وأبيشاي ابنير ولكنهما لم يستطيعا اللحاق به( 2 صم 2: 18-24). ولكن أبيشاي ساعد أخاه يوىب فقتلا أبينير غيلة فيما بعد( 2صم 3: 30).وفي حرب داود مع العمونيين والآراميين وضع أبيشاي على رأس الجيش الذي حارب العمونيين ويوآب أخاه على رأس الجيش الذي حارب الآراميين وانتصر اسرائيل في تلك المعركة( 2 صم 10: 10-14).وكان أبيشاي دائماً أميناً مخلصاً لداود ورافقه عند هروبه من وجه أبشالوم. ولما سب شمعي بن جيرا، من عشيرة بيت شاول، داود ، أراد أبيشاي أن يقتله في الحال ولكن داود منعه من ذلك(2صم 16: 5-14).ولما عاد الملك منتصراص نصح أبيشاي داود بأن يرفض ندامة شمعي وتوبته ويقتله فلم يسمع له داود في ذلك( 2صم 19: 21-23).وفي الحرب ضد جيش أبشالوم كان أبيشاي على رأس إحدى فرق جيش داود(2صم 18: 12). وقد ساعد داود عندما ثار عليه شبع ابن بكري البنياميني(2صم 20: 6و7) ولما تقدمت السن بداود وكان في حرب ضد الفلسطينيين أراد يشبى بنوب ان يقتله فأنجده أبيشاي فضرب الفلسطيني وقتله(2صم 21: 15-17).وكان أبيشاي شجاعاً شديد الاندفاع إلا أنه كان اقل مكراً من أخيه يوآب ومع ذلك فقد كان مثله في قسوته وعناده نحو أعدائه. وقد أدرك داود هذه القسوة وهذا العناد وكان يخشاهما. ولكنه كان يقدر إخلاص أبيشاي له وأمانته في خدمته.'),
(65, 'أبِيشَجْ', 'اسم عبري ومعناه ((أبي تائه))  وهي المرأة الشونمية التي اختيرت أمة لداود للعناية به وخدمته في شيخوخته وضعفه بسبب جمالها وحداثة سنها وحيويتها (1 ملوك 1: 1-4).وبعد موت داود أراد أدونيا ابنه أن يتزوجها وطلب من سليمان أن يسمح له بذلك فاعتبر سليمان هذا الطلب دسيسة لأخذ الملك منه فرفض الطلب وقتل أدونيا(1 ملوك 2: 13-25).'),
(66, 'اِبيشُور', 'اسم عبري معناه ((أبي حائط أو حصن)) . وهو رجل من يهوذا من عشيرة حصرون ةمن بيت يَرْحمئيل(1 أخبار 2: 28و29).'),
(67, 'اِبيشُوع', 'اسم عبري ومعناه ((أبو الخلاص))  وقد ورد:(1) اسماً لرجل بنياميني من بيت بالع( 1 أخبار 8: 4).(2) اسم ابن فينحاس الكاهن( 1 أخبار 6: 4و5و50).'),
(68, 'اِبيطَال', 'اسم عبري ومعناه ((أبي طِل أو ندى)) . وهو اسم إحدى نساء داود وهي أم شفطيا(2صم 3: 4و1 أخبار 3: 3).'),
(69, 'اِبيطُوب', 'اسم عبري ومعناه ((أبي طيبة أو صلاح)) . وهو اسم رجل بنياميني ابن شحرايم واسم أمة حوشيم (1أخبار 8: 11).'),
(70, 'اِبيعَزَر', 'اسم عبري ومعناه ((أبو المساعدة أو أبي معونة أو الأب عون)) . وهو اسم :(1) رجل جلعادي من نسل منسى. وكان جدعون القاضي من أبنائه( قض 6: 11). وعندما شكى الإفرايميون أن جدعون ذهب لمحاربة المديانيين والثلاثمائة رجل جلهم من بيت أبيعزر دون أن   يستعين بالافرايميين تلطف جدعون في القول معهم وقال لهم ان ما فعله هو والرجال الذين معه لا يحسب شيئاً في مقابل ما فعله الإفرايميون عندما اسروا اميري المديانيين غراباً وذئباً ثم قال لهم ((أليس خصاصة أفرايم خيراً من قطاف أبيعزر ؟))  فرضوا عن قوله( قض 8: 1-3).(2) أحد رجال داود الذين كان يقيمهم على جيشه أحياناص وكان يدعى العناثوثي(2صم 23: 27).ابيعزري:نسبة إلى ابيعزر.'),
(71, 'الأَبيكُوريُّون', 'اسم لأتباع أبيكورس الفيلسوف اليوناني، الذي أسس مذهباً تسمَّى باسمه. وكان ابيكورس (ابيقور) من أصل اثيني وولد في جزيرة ساموس عام 341 ق.م.  ومات عام 270ق.م. وفحوى مذهبه الفلسفي في الأخلاق إن اللذة غاية ما يريده الإنسان ،فعليه أن يبحث عنها ويتجنب الألم 0وقد أسيء فهم قصده فانه لم يعنِِ إشباع اللذة الشهوانية كما ظن معارضوه ولكنه أراد باللذة امتناع الألم الجسماني وانتزاع القلق العقلي والروحي في الانسا ن 0ولقد عاش هو في  طهر حتى ظن البغض انه خلواً من كل ميل شهواني 0 أما أتباعه فمنهم من اتخذ من مبادئه سبيلاً الى الاباحية واطلاق العنان للشهوات 0 ومع ان ابيكورس لم يذكر فكرة وجود اله الا انه رأى أن الآلهة يعيشون في عالم بعيد كل البعد ، غريب كل الغرابة ، عن عالم البشر ولا توجد ثمة علاقة بين هؤلاء وأولئك ولا يتأثرون  بمجريات الامور في عالم البشر 0وقد التقى الرسول بولس بالأبيكوريين في اثينا وكان طبيعياً ان تتناقض تعاليم الرسول مع تعاليمهم وظنوه متخلفاً عن ركب الفكر يريد أن يعود الى الخرافات والشعوذات القديمة فيعلم عن إله صار إنساناً لخلاص البشر وفدائهم(أعمال 17 : 18)0'),
(72, 'اِبيمالِك', 'اسم عبري ومعناه ((ابو ملك))  او ((الأب ملك))  وقد ورد:(1)  اسماً لملك في فلسطين عاش في عصر إبراهيم. وقد جاء إبراهيم إلى بلاده ومعه سارة زوجته ولكنه قال عنها انها اخته(تك ص 20). وفيما بعد ذلك دخل إبراهيم مع ابيمالك في عهد بشأن آبار المياه التي تخاصم عليها رجالهما مع بعضهم البعض، وبناء على هذا العهد سمي ذلك المكان بئر سبع(تك ص 21). انظر إبراهيم.(2)  اسماً لملك آخر في فلسطين عاش في عصر اسحاق وتكرر معه امر مشابه لما حدث لأبيه إبراهيم وأمه سارة فادعى اسحاق أن رفقة زوجته هي أخته ولما كشفت حقيقة الأمر أنبه ابيمالك لذلك ، ولكن كان كريماً لطيفاً في معاملته اسحاق. وكذلك حدث له مع ابيمالك مثلما حدث لإبراهيم مع ابيمالك الأول في شأن الآبار فدخلا معاً في عهد عند بئر سبع(تك ص 26).(3)  اسماً آخر لأخيس ملك جت الذي هرب إليه داود من وجه شاول(1 صم 21: 10-22: 1 قارنه مع عنوان مز 34). ويغلب على الظن أن الاسم ((ابيمالك))  كان لقباً لملوك الفلسطينيين.(4)  اسماً لابن جدعون أحد القضاة. وكانت أمه سرية جدعون من شكيم وكان لعشيرتها شيء كثير من النفوذ هناك. وقد استطاع ابيمالك بتأييد هذه العشيرة، أن يكتسب رضا الشعب عند ملكه. ومن النتائج الطبيعية من تعدد الزوجات أن يتشاجر ويتشاحن أبناء الزوجة الواحدة مع أبناء الأخرى، وهذا ما عمله ابيمالك فإنه قتل جميع أبناء أبيه. وكان عددهم سبعين ولم ينج إلا الابن الأصغر واسمه يوثام لأنه اختبأ. وبالرغم عن تأييد أهل شكيم له إلا أنه بعد ثلاث سنوات من ملكه قامت ضده فتنةفي شكيم فقام ابيمالك لمحاربتها وأخمد الفتنة. ونجح في أخذ المدينة ولكنه وهو يطارد الثائرين الذين هزموا احتموا في برج قوي في وسط مدينة تاباص. وإذ كان يهاجم البرج طرحت امرأة قطعة رحى على رأسه فشجت جمجمته. ولما رأى أنه قد جرح جرحاً مميتاً، فلئلا يقال أن امرأة قتلته، أمر حامل سلاحه أن يقتله بسيفه فطعنه الغلام فمات(قضاة ص 9).(5)  اسماً لكاهن في أيام داود وهو ابن أبياثار من نسل عالي( 1 أخبار 18: 16) وابيمالك هذا هو نفس لخيمالك المذكور في 1 أخبار 24: 6.'),
(73, 'اِبِيمايِل', 'ومعناه في اللغة العربية الجنوبية ((الله أب))  وهو التاسع في العدد من ابناء يقطان الثلاثة عشر ومن نسل سام(تك 10: 28، 1 أخبار 1: 22).'),
(74, 'اِبيَنَادَاب', 'اسم عبري معناه ((الأب كريم أو منتدب))  وقد جاء هذا:(1)  اسماً لرجل من قرية يعاريم وهو الذي قبل أن يكون تابوت العهد في بيته عندما أرجعه الفلسطينيون وتقدس اليعازار ابنه لأجل حراسة التابوت(1 صم 7: 1و2) وبعد مضي قرن تقريباًمن هذا، قام داود بإرجاع التابوت وعهد إلى اثنين من نسل ابيناداب بسوق المركبة التي أخذ عليها التابوت( 2صم 6: 3و4و1 أخبار 13: 7).(2)  اسماً للابن الثاني بين أبناء يسى الثمانية وأحد أخوة داود الثلاثة الذين كانوا يعملون في جيش شاول عندما عبر جليات الفلسطيني صفوف اسرائيل( 1صم 16: 8و17: 13).(3)  اسماً لأحد أبناء شاول الذين قتلوا مع أبيهم في معركة جلبوع( 1 صم 31: 2).(4)  اسماً لرجل تزوج ابنة طافة بنت سليمان. وكان ابن ابيناداب هذا أحد وكلاء سليمان الاثني عشر لتموين قصره الملكي. (1 ملوك 4: 7و11).'),
(75, 'اِبينِتُوس', 'اسم يوناني ومعناه ((الممدوح))  وكان من المسيحين الذين ارسل إليهم الرسول بولس تحياته في رومية ويشير إليه في رومية 16: 5 بأَنه حبيبه وبأَنه باكورةإخائية للمسيح ويجب ان تكون  ((باكورة آسيا))  لأن هذا هو النص الصحيح استناداً إلى أحدث دراسة للنصوص القديمة. وربما كان من مدينة افسس.'),
(76, 'اِبينُوعَم', 'اسم عبري ومعناه ((أبي نعيم))  أو ((أبو النعم))  وهو أبو باراق(ض 4: 6و12و5: 1و12).'),
(77, 'إِبْينيِر', 'اسم عبري ومعناه ((أبو النور))  (1 صم 14: 50). انظر إِبنير'),
(78, 'اِبيهُو', 'اسم عبري ومعناه ((الأب هو))  أو ((ابي هو)) . وهو الابن الثاني لهرون رئيس الكهنة(خروج 28: 1)، وقد مات هو وأخوه الأكبر ناداب عندما قدما ناراً غريبة أمام الرب(لاويين 10: 1و2). وبما أنه مات دون أن يكون له نسل فلذلك لم يكن له نصيب في سلسلة الكهنوت( 1 أخبار 24: 2).'),
(79, 'اِبيهْود', 'اسم عبري ومعناه ((أبي جلال))  أو ((الأب جلال))  وكان ابن بالع بكر بنيامين( 1 أخبار 8: 3).'),
(80, 'اِبيا', 'اسم عبري ومعناه (( أبي يهوه))  أو ((يهوه أب))  وقد ورد هذا الاسم لمن يلي:(1)  رجل من نسل بنيامين، وهو ابن باكر ( 1 أخبار 7: 8).(2)  الابن الأصغر لصموئيل. وقد عينه أبوه قاضياً في بئر سبع ولكنه افسد الحكم وعوج القضاء (1صم 8: 2و3و1 أخبار 6: 28).(3)  رجل من نسل هرون عينه داود رئيس فرقة من فرق الكهنوت ( 1 أخبار 24: 10) انظر فرقة ابيا.(4)  ابن يربعام الأول ملك اسرائيل. وقد مرض وهو لا يزال غلاماً فأرسل أبوه أمه متخفية إلى النبي أخيا الذي تنبأ ليربعام بالملك. ولكن النبي عرفها رغم تخفيها وأنبأها بموت الغلام وبانتهاء ملك بيت يربعام بسبب شره. وكذلك تنبأ بأن هذا الغلام وحده من بيت يربعام يدخل القبر لأنه وجد أمر صالح نحو الرب. أما الآخرون فتأكلهم الكلاب أو طيور السماء( 1ملوك 14: 1-18).(5)  ابن رحبعام ملك يهوذا واسم أمه معكة( وقد ورد ذكرها باسم ميخايا في بعض الأماكن) من نسل أبشالوم. وقد خلف أباه في الملك على يهوذا. وقد سار في شر أبيه ولم يكن مرضياً لدى الرب. ولما كانت الحرب لا تزال قائمة بين اسرائيل ويهوذا، قاد ابيا جيوش يهوذا ضد اسرائيل وانتصر عليه( 1 أخبار 3: 10و2 أخبار 11: 20-14: 1). وقد ورد ذكره باسم أبيام في 1 ملوك 14: 31 و15: 1و7و8.(6)  أحد الكهنة في عصر نحميا ومن ضمن الذين ختموا العهد(نحميا 10: 7) ويحتمل أن يكون نفس الكاهن المذكور في 12: 4 و17 والذي رجع من السبي مع زربابل.فِرَقة أَبِيّا:ورد ذكر هذه الفرقة في لو 1: 5 وفي هذا إشارة إلى تنظيم الكهنة واللاويين وعملهم في أيام داود الملك. كما جاء في 1 أخبار ص 24 الذي يذكر أن داود قسم الكهنة إلى أربع وعشرين فرقة وكان على كل فرقة من هذه أن تقوم بالخدمة في الهيكل مدة أسبوع ما عدا ايام الأعياد الكبرى التي كانت تشترك فيها كل الفرق. ويظهر أنه لم يرجع من سبي بابل إلا أربع من هذه الفرق، ولكن فيما بعد أعيد تنظيم الكهنة وتقسيمهم إلى أربع وعشرين فرقة كم اكانوا قبلاً.وقد كان زكريا أبو يوحنا المعمدان من فرقة ابيا التي كانت الفرقة الثامنة.'),
(81, 'أَبِياه', 'اسم عبري  ومعناه ((أبي يهوه))  أو ((يهوه أبٌ))  وقد ورد في 1 اخبار 2: 24 اسماً لزوجة حصرونوقد ورد في بعض الترجمات كالعلم فحسب وليس اسم علم وكأنها تقول ((امرأة حصرون أبيه)) .'),
(82, 'أبيَّة', 'ورد هذا اسماً لأم حزقيا في 2 أخبار 29: 1. أما في 2 ملوك 18: 2 فقد ورد بصورة ((أبي))  المختصرة.'),
(83, 'أتَاريِم', 'اسم عبري ومعناه ((آثار القدم))  وهو اسم لطريق غير بعيد من عرادفي  ((النقب))  في جنوب فلسطين التي حارب ملكها اسرائيل وهم في طريقهم غلى كنعان(عدد21: 1).'),
(84, 'أَتَّالِيَة', 'هي مدينة تنسب إلى اتالس فيلادلفس ملك برغامس الذي حكم من عام 159-138ق.م. وكانت المدينة ميناء في مقاطعة بمفيلية في جنوب آسيا الصغرى. وقد نزل منها بولس إلى أنطاكية في رحلته التبشيرية الأولى زكان برنابا معه. (أعمال 14: 25).'),
(85, 'ِتّاي', 'إاسم عبري وربما معناه ((الرب معي))  وهو:(1)  اسم ابن ريباي من جبعة بني بنيامين وكان واحداً من أبطال داود(2 صموئيل 23: 29و1 أخبار 11: 31).(2)  اسم واحد من رجال جتّ كان رئيساً لستمئة رجل من الذين تبعوا داود من تلك المدينة الفلسطينية. وكان أميناً لداود وفياً له في كل الظروف. وكان يقود ثلث جيش داود في المعركة التي قتل فيها ابشالوم(2 صموئيل 15: 18-22و18: 2و5).'),
(86, 'أَثْبَعَل', 'اسم فينيقي ومعناه ((مع بعل))  أو ((معه بعل))  وهو ملك صيدون وأبو ايزابل امرأة الملك آخاب( 1ملوك 16: 31).'),
(87, 'أثاث', 'وردت هذه الكلمة في أعمال 27: 19 ترجمة لكلمة ((سكيوي))  اليونانية. ومعناها كل معدات السفينة التي يمكن الإستغناء عنها لجعلها خفيفة في حالة هياج البحر الشديد.'),
(88, 'أثْلٌ', 'نوع من الشجر تنموا أنواع عديدة منه بكثرة في فلسطين. وقد غرس إبراهيم أثلاً في بئر سبع(تك 21: 33) وكان شاول يقيم تحت الأثلة في الرامة( 1 صموئيل 22: 6) وقد دفنت عظام شاول وعظام بنيه تحت الأثلة في يابيش(1 صموئيل 31: 13). ويقال أن في سيناء نوعاً من الأثل يخرج شيئاً يعرف بين الأهلين بالمن.'),
(89, 'أَثْنان', 'ومعنى الاسم عبري ((هدية أو أجرة))  وهو يطلق غالباً على الأجرة التي تدفع عنا لارتكاب فاحشة. وهو أيضاً اسم رجل من نسل يهوذا(1 اخبار 4: 7).'),
(90, 'أثْنَاي', 'اسم عبري ةمعناه ((هدية))  وهو لاوي من نسل جرشوم ومن أسلاف اساف(1 أخبار 6: 41) وربما هو نفس الشخص المذكور في 1 أخبار 6: 21 باسم يأثراي.'),
(91, 'أثِينَا', 'اسم يوناني وهو أصلاً اسم الآلهة ((أثينا))  ثم أطلق على المدينة التي كانت عاصمة أتيكا( وهي قسم من أقسام اليونان في القديم)، وقد اصبحت المدينة فيما بعد مركز الثقافة في العلوم والآداب في العالم القديم.ولا يعرف شيء بالتحقيق عن التاريخ الذي أسست فيه، إلا أن الأساطير تشير إلى أن كيكرويس (أول ملوكها ويظن أنه جاء من مصر) أسسها حوالي سنة 1581 ق.م. وقد استمرت يحكمها  ملوك إلى عام 1068 ق.م تقريباً. حين انتقلت السلطة إلى ايدي أراخنة( أو رؤساء سياسيون).وقد اشتهر من بين هؤلاء مشرعان هما دراكو الذي عاش حوالي سنة 620 ق.م وعرف بقسوته وجبروته، وصولون وعاش حوالي سنة 594 ق. م وقد وضع الدستور الأول لدولته. وقد انتصر الأثينيون في عام 490 ضد قواد داريوس هستاسبس ملك الفرس، بمعونة البلاتيين. أما في عام 480 ق.م فقد سلمت المدينة لزركسيس بن داريوس. ولكن اليونان اضطروا الغزاة إلى الانسحاب بعد معركة بحرية في سلاميس في نفس تلك السنة وقد ساعد هذا الانتصار الأثينيين على تأسيس امبراطورية صغرى عاصمتها أثينا كانت تعتمد في كيانها على قوة الأسطول البحري لا غلى الجيش. وقد برز في عام 459-431 ق. م. قائد ديمقراطي قدير وهو بركليس. وقد كان من خصائص حكمه الخيّر أن أقيم في أثينا عدد كبير من الأبنية العامة الجميلة، وكذلك ازدهرت الآداب أثناء حكمه. وفي أواخر حكمه اندلعت نيران الحرب البلبونيسية ودامت إلى عام 404ق.م حيث انتهت بتسليم أثينا للأسبرطيين. ولما جاء فيليب المقدوني وتداخل في أمور اليونان لم تكن الأمور مستقرة ولكن بعد أن قتل فيليب وجاء ابنه الاسكندر وتسلم مقاليد الحكم استتب له الأمر ووصلت أثينا في عصره إلى الذروة من القوة والنفوذ. وبعد أن خضعت بلاد اليونان للرومان أصبحت أثينا تحت نطاق حاكم مقدونية، ولكنها منحت استقلالاً ذاتياً تقديراً لتاريخها الماضي المجيد.ومع أنها كانت خاضعة للحكم الروماني فإنها صارت المدينة الجامعية في العالم الروماني ومنها شعّ نور روحي وحيوية فكرية إلى مدن أخرى مثل طرطوس وأنطاكية والاسكندرية، وقد ازدهت فيها أربع مدارس فلسفية: وهي الأفلاطونية، والمشائية، والأبيكورية والرواقية. وقد أقبل طلاب كثيرون إلى المدارس من بلاد اليونان ومن روما أيضاً وقد استمرت مركزاً للعلم إلى أن حرم جستنيان دراسة الفلسفة في عام 529 ميلادية.وفي أثناء رحلته التبشيرية الثانية نزل بولس في بيرية وهي ميناء أثينا على البحر. وبينهما طريق طوله ميلان. ويرجح أنه وهو في هذا الطريق رأى المذابح المخصصة للإله المجهول- وقد أشار مؤرِّخو القرن الثاني الميلادي إلى وجود هذه المذابح على نفس هذا الطريق، وفي أماكن أخرى في المدينة. ويرجح أنه عند اقترابه من العاصمة رآها كما كانت حينئذ ولا زالت إلى اليوم مبنية حول تل الأكروبوليس الذي ارتفاعه حوالي 500 قدم فوق سطح البحر، وقد كان هذا التل قبلاً حصناً ثم أصبح فيما بعد مركزاً دينياً، حيث أقيمت أشهر وأعظم المذابح والهياكل وربما قضى بولس وقتاً ما وهو يسير في شوارع المدينة ينظر غلى الأبنية العامة والهياكل العديدة والاستاديوم( ساحة الألعاب) والسوق وقاعات الاجتماعات حيث كانت تعقد المجالس أو يلقن الطلاب العلم. وربما اكتشف حينئذ أن الأثينيين يحبون الجلوس في السوق أثناء النهار للتحدث في أنباء اليوم أو للتناقش في الآراء الفلسفية والدينية أو للمناظرة. وقد رأى بولس في ذلك فرصة سانحة لينادي بالانجيل، ولكن لم تجد رسالته إذناً صاغية لدى جميع السامعين فطلبوا منه أن يذهب معهم غلى أريوس باغوس ليدافع عن تعليمه. وقد ظهر دفاعه أمام أعينهم كأنه فلسفة جديدة تنادي بإله واحد. وقد وجدت رسالته أذناً صاغية لدى دايونسيوس الأريوباغي. كما قد آمن بالرسالة عدد من المدينة ومن مجمع اليهود كما ورد في(أعمال 17: 15-34). ولا نعرف شيئاً عن كنيسة أسست في أثينا في زمنه أو أنه كتب رسالة لها كما كتب لغيرها من الكنائس، انظر أريوس باغوس.'),
(92, 'ألاثينُّيون', 'اسم أطلق على أهل مدينة أثينا. ةقد أشار لوقا إلى حبهم للاستطلاع وشغفهم بكل شيء جديد، انظر أعمال 17: 21.'),
(93, 'أجاج', 'اسم عماليقي وربما معناه ((متأجج أو عنيف))  ويظن البعض أن اسم ((أجاج))  كان لقباً لملوك العماليق كما كان يطلق اسم فرعون على كل ملك في مصر، ويشير الكتاب إلى شخصين بهذا الاسم وهما:(1)  أجاج ملك العماليق ذكره بلعام في بركته لاسرائيل، انظر سفر العدد 24: 7.(2)  أجاج ملك العماليق الذي قتله صموئيل بعد أن عفى عنه شاول 1 صموئيل 15: 8-33.'),
(94, 'أَجاجِي', 'ومعناه الأصلي ((شخص ينسب إلىأجاج))  ولكن اليهود فيما بعد استعملوه للتحقير لأن العمالقة كانوا أعداء اسرائيل الألداء، ولذا فللسخرية من هامان ولتحقيره قالوا ((هامان الأجاجي))  (1 أستير 3: 1و10و8: 5و9: 24).'),
(95, 'أُجْرَة', 'وهي ما يدفع مقابل القيام بعمل ما وكانت قديماً تدفع من محاصيل العمل ذاته كما حدث في أمر يعقوب ولابان(انظر تك 29: 15) وقد دفعت ابنة فرعون لأم موسى عندما عهدت إليها بارضاعه (انظر خروج 2: 9) وقد نصت الشريعة الموسوية على أن تدفع أجرة يوم ما في ختام ذلك اليوم (انظر لاويين 19: 13 وتثنية 24: 14و15). وقد استنكر الكتاب المقدس الامتناع عن دفع أجرة العامل( انظر أرميا22: 13 وملاخي3: 5 ويعقوب5: 4). وفي أيام وجود الرب يسوع المسيح على الأرض كانت أجرة العامل في الكرم ديناراً في اليوم(مت 20: 2). وقد أشار السيد المسيح إلى الأجرة التي يستحقها من يعملون في الخدمة الروحية فقال ((والفاعل مستحق أجرته))  (لوقا 10: 7). وكذلك أشار الرسول بولس إلى وجوب توفير ما يسد الحاجات المادية لمن يقومون بخدمة روحية (انظر 1 كو 9: 8-14).'),
(96, 'أَجِير', 'عامل يستخدم للقيام بعمل ما يقابل أجرة معينة تدفع له(انظر أيوب 7: 1و2). وقد حرصت الشريعة الموسوية على أن تحفظ للأجير حقه في أجرته(انظر أجرة)  وإننا ندرك من قول السيد المسيح في مثل الابن الضال ان السيد الصالح كان يعطي أجرائه طعاماً كافياً ويزيد على حاجتهم(لوقا 15: 17). وكانت مدة عمل الأجير محدودة(انظر أشعياء 16: 14). ويمكننا أن نفهم من حياة السيد المسيح عن الراعي الصالح بأن الأجير لم يكن يعمل بنفس روح التضحية والبذل اللتين يعمل بهما صاحب الغنم نفسه(انظر يوحنا 50: 12و13).'),
(97, 'َجلاَيِم', 'أاسم عبري وربما معناه ((بركتان))  وهي قريبة من الاشتقاق ((مأجل الماء))  أي مستنقع الماء. وهي اسم لبلدة في موآب(انظر أشعياء 15: 8) وربما مكانها اليوم ((رجم الجلمة))  أو ((خربة الجلمة))  بالقرب من الكرك.'),
(98, 'أَجَام', 'جمع أجمة وقد أطلقت الكلمة على مجتمعات المياه المتخلفة من فيضان النيل وبنوع خاص في الدلتا حيث ينمو فيها الأعشاب وعيدان البردي(انظر خروج 7: 19و8: 5) وكذلك تطلق على الأماكن التي توجد فيها بالمقارنة مع الصحراء(انظر أشعياء 14: 23و35: 7).'),
(99, 'أَجُور', 'اسم عبري ربما معناه ((جامع)) . وهو اسم لرجل حكيم، وربما اطلق عليه لأنه جمع أقوال الحكماء في أمثال ص 30 ويذكر في الترجمة العربية بأنه ((ابن ياقة))  (انظر أمثال 30: 1).'),
(100, 'أَجِي', 'اسم عبري وربما معناه ((شارد))  أو ربما الاسم مشتق من الفعل العربي ((أجا))  أي ((هرب))  أو  ((أرغم)) . وهو أبو شمة. أحد قواد جيش داود(انظر 2صم 23: 11).أحاز انظر آحاز(1)'),
(101, 'أَحْبَان', 'اسم عبري وربما معناه ((الأخ فهيم أو ذكي)) . وهو ابن أبي شور من سبط يهوذا(انظر 1أخبار 2: 29).'),
(102, 'أَحُذَّات', 'اسم عبري وربما معناه ((المأخوذ))  أو ((المملوك)) . زكان من ضمن أصحاب ومشيري أبيمالك ملك جرار وقد رافق أبيمالك عندما التقى باسحاق وعقد معه عهداً في بئر سبع(انظر تك 26: 26).'),
(103, 'أحَسْبَاي', 'اسم عبري ربما معناه ((إِلجأ إلى الرب)) . وهو أبو اليفلط أحد قادة جيش داود(2صم 23: 34).'),
(104, 'أَحشْويِروُش', 'اسم فارسي قديم ربما معناه ((رئيس الحكام)) . وقد ورد هذا الاسم في الكتاب المقدس :(1)  اسم أبي داريوس المادي(دانيال 9: 1) انظر داريوس.(2)  اسم ملك فارسي تزوج استير(انظر أستير1: 2و19و2: 16و17) وهو المعروف في اللغة اليونانية باسم  ((زركسيس)) . ويمكننا أن نعرف شيئاً عن اخلاقه وطباعه من سفر أستير فقد كان صاحب نزوات متقلقلاً، قصير النظر طاغية، قاسياً. وهذه هي الصورة التي نراها لزركسيس في التاريخ اليوناني. وهو ابن داريوس هستاسيس وقد خلف أباه على عرش الفرس حوالي سنة 486ق.م. وكانت أمه ابنة كورش. وبعد استعداد وتجهز لمدة أربع سنوات قام على رأس جيش جرار لغزو بلاد اليونان. وربما نجد إشارة إلى هذا الاستعداد في الإصحاح الأول من سفر أستير. ولكنه لما رأى انهزام الأبطال في سلاميس في سنة 480 ق.م عاد ثانية إلى بلاد الفرس.وفي السنة التالية(479ق.م) انهزم جيشه الذي كان يقوده ماردونيوس أمام اليونان في معركة بلاتيا. وفي سنة 465 قتل  ((زركسيس))  إذ اغتاله أحد رجال حاشيته وخلفه على العرش ابنه أرتزكسيس لونجمانوس(انظر سفر أستير).'),
(105, 'َحْلاَي', 'أاسم عبري وربما كان معناه ((أخوالاهي))  أو ((آه ياليت))  وقد ورد:(1)  اسم شخص من نسل شيشان(انظر أخبار2: 31و34).(2)  اسم أبي زاباد أحد قواد جيش داود(1 أخبار 11: 41).'),
(106, 'أَحْلَب', 'اسم عبري معناه ((سمين))  أو ((مثمر)) . اسم بلدة في نصيب أشير، ولم يطرد رجال هذا السبط الكنعانيين الذين كانوا يسكنون هذه البلدة(قضاة 1: 31). ويظن بعضالباحثين أنها وحلبة مكان واحد ويرجح، بعض العلماء بأن مكانها اليوم  ((خربة المحالب))  على بعد أربعة أميال شمال غربي صور. كما يظن آخرون أنها نفس  ((جوش))  حلب، المذكورة في التلمود أو بلدة ((الجش))  الحديثة بالقرب من صفد.'),
(107, 'أَحْمَثَا', 'اسم آرامي من الاسم الفارسي القديم ((هجمتانا))  وهي عاصمة ميديا. وقد وجد بها درج مكتوب فيه أمر كورش ببناء الهيكل في أورشليم(عزرا 6: 2). واسمها باليونانية ((أكبتانا))  واسمها الحديث ((همدان)) .'),
(108, 'أَحِير', 'اسم عبري معناه ((آخر)) . وهو اسم لرجل بنياميني(1 أخبار 7: 12) وربما كان هو نفس الشخص المذكور باسم أحيرام في عدد 26: 38.'),
(109, 'أَحيرَام', 'اسم عبري معناه ((الأخ رفيع)) . وهو اسم لرجل بنياميني أب عشيرة في سبط بنيامين(عدد 26: 38) وربما كان هو نفس الشخص الذي ذكر باسم أحير في 1 أخبار 7: 12 أو ايحي في تك 46: 21، أو اخرج في 1 أخبار 8: 1.'),
(110, 'أَخْآب', 'اسم عبري معناه ((أخو الأب))  وقد ورد اسماً لشخصين:(1)- ملك اسرائيل وهو ابن عمري الذي خلفه على العرش. وقد بدأ حكمه حوالي 875ق.م في السنة الثامنة والثلاثين من ملك آسا مَلِك يهوذا(1 ملوك 16: 29). وقد تزوج من إيزابل ابنة أثبعل ملك صيدون وكانت امرأة وثنية تعبد الإله بعل. وكان زوجها ضعيف الإرادة قليل العزيمة، فأثرت عليه وانقاد وراءها في عبادة بعل(16: 33-30). وقد أرسل الرب إيليا إلى آخاب فتنبأ بمجيء جفاف وقحط عقاباً لخطية آَخآب(1 مل17: 1) وقد دام القحط ثلاث سنين تقريباً( 1 مل 18: 1، ولو 4: 25، يع 5: 17). وقد تقدم إيليا إلى عوبديا وكيل بيت أخآب، وكان رجلاً يعبد الرب باخلاص، لكي يمهد له مقابلة مع أخآب. وعند التقاء إيليا بأخآب طلب منه أن يجمع كل اسرائيل وأنبياء الأوثان إلى جبل الكرمل حيث أيد الرب رسالة إيليا بارسال نار التهمت الذبيحة. فاختار الشعب عبادة الرب وقتلوا أنبياء الأوثان. ومن بعد هذا تنبا إيليا لأخآب بمجيء المطر، وفعلاً نزل المطر بغزارة وانتهى الجفاف. وإذ كان إيليا على جبل حوريب أمره الرب أن يذهب ويمسح ياهو بن نمشي ملكاً على اسرائيل عوضاً عن أَخآب وليعاقب بيت أخآب على خطيئته(1 مل 19: 16).وقد حاصر بنهدد ملك آرام السامرة عاصمة اسرائيل فانتصر أخآب عليه، ولكنه عمل مع بنهدد عهداً وأطلقه ولم يكن هذا وفقاً لإرادة الرب فجاء إنذار الرب له على فم أحد الأنبياء بأنه ما دام قد اطلق هذا العدو من يده فإن الشر سيأتي عليه وعلى مملكته على يد شعب هذا الرجل الذي أطلقه(1 ملوك 20: 42).وقد استولى أخآب وايزابل على كرم نابوت اليزرعيلي بعد أن دبرا له مكيدة لقتله فقتل ظلماً بناء على شهادة شهود زور. فأرسل الرب إيليا فتنبأ بموت أخآب وايزابل، وبأن الكلاب التي لحست دم نابوت سوف تلحس دم أخآب أيضاً في نفس المكان( 1مل 21: 19). وقد ندم أَخآب وتاب واتضع أمام الرب فجاءت كلمة الرب إلى إيليا بأن الشر الذي سيأتي على بيت أخآب لا ياتي في أيامه بل في أيام ابنه(1 مل 21: 29). وقد ورد في النقوش الاشورية أن أخآب أرسل ألفي مركبة وعشرة آلاف من المشاة ليشتركوا مع جيش آرام في حربهم ضد اشور. ويقول شلمناسر الثالث ملك اشور أنه انتصر عليهم في معركة ((قرقر))  بالقرب من حماة وكان هذا حوالي سنة 853ق.م.وقد أراد أخآب أن يسترد راموث جلعاد من الآراميين فطلب معونة يهوشافاط في حربه ضدهم. ومع أن بعض الأنبياء شجعوه على الدخول في هذه الحرب إلا أن ميخا النبي تنبأ بانهزامه وموته في راموث جلعاد وذهب يهوشافاط مع أخآب ولم يلبس أخآب ثيابه الملكية حتى لا يعرف، ولكن أصابه سهم غير مقصود بجرح مميت. فسال دمه في مركبته ومات وحمل إلى السامرة ولحست الكلاب الدم من مركبته وفقاً لقول الرب(1 مل 22: 38).وقد اكتشف المنقبون ألواحاً من العاج في السامرة وربما كانت بعض هذه الألواح من بقايا قصر العاج الذي بناه أخآب(انظر 1 مل 12: 39).(2)  اسم لنبي كذّاب وهو ابن قولايا وكان يتنبأ بالكذب لبني اسرائيل وقت أن كانوا في سبي بابل وقد تنبا أرميا بأن نبوخذ نصر ملك بابل سيقليه في النار( أر 29: 21-23).'),
(111, 'َخَائِيكُوس', 'أاسم يوناني(نسبة إلى أخائية) وهو اسم أحد القادة في كنيسة كورنثوس وقد جاء إلى أفسس لزيارة الرسول بولس (1 كو 16: 17).'),
(112, 'أَخَائِيَة', 'اقليم كان في الأصل جزءاً من بلاد اليونان في الجنوب في شبه جزيرة البلبونيس. وفي زمن العهد الجديد كانت أخائية ولاية رومانية تشمل بلاد اليونان الواقعة جنوبي مقدونية وكانت عاصمتها كورنثوس. وكان بيت استفانوس أول باكورة المسيحيين في أخائية أي في عاصمتها كورنثوس(1 كو 16: 15).  وقد هاجم اليهود في كورنثوس بولس وعارضوا مناداته بالإنجيل وأحضر بولس إلى غاليون حاكم تلك الولاية في ذلك الحين (أي في سنة 51أو52ميلادية) وقد أذن غاليون لبولس أن يستمر في مناداته. (اع 18: 17-12). وقد ارسل المسيحيون في اخائية عطايا لفقراء القديسين في أورشليم على يد بولس الرسول(رومية 15: 26). وقد زار بولس أخائية مراراً.'),
(113, 'أَخَرْحيل', 'اسم عبري وربما معناه ((أخو راحيل))  أو ((آخر القوة))  وهو بن هارم وكان رئيس عشيرة في يهوذا(1 أخبار 4: 8).'),
(114, 'أخْرَخ', 'اسم عبري وربما معناه ((تابع الأخ)) . وهو الابن الثالث لبنيامين(1 أخبار 8: 1). ويعتقد البعض انه نفس ايحي المذكور في عدد 26: 38.'),
(115, 'أَخُزَّام', 'اسم عبري معناه ((الملك))  او  ((المالك))  رجل من سبط يهوذا وهو ابن أشحور من عشيرة حصرون(1 أخبار 4: 6 وانظر أيضاً 1 أخبار 2: 24).'),
(116, 'أخْزَاي', 'اختصار أخزيا. وهو كاهن كان يقيم في أورشليم. وكان ابن مشليموث بن أمير(نحميا 11: 13). ويرجح أنه نفس ((يحزيرة))  المذكور في 1 أخبار 9: 12.'),
(117, 'أَخَزْيا', 'اسم عبري معناه ((الرب يمسك))  أو ((الرب يسند))  وقد ورد:(1)  اسم ملك من ملوك اسرائيل وهو ابن أخآب وايزابل. خلف أباه على العرش فكان ثامن ملوك اسرائيل. وقد حكم سنتين فقط من 850-852ق.م.(1 ملوك 22: 40و51). وقد اتحد أخزيا مع يهوشافاط في عمل سفن ترشيش لتذهب إلى أوفير لأجل الذهب ولكن السفن تحطمت في عصيون جابر فاقترح أخزيا على يهوشافاط أن يحاولا القيام بهذا العمل مرة أخرى ولكن يهوشافاط رفض ذلك بناء على تحذير واحد من الأنبياء(1 ملوك 22: 48و49و2 أخبار 20: 37-35).وقد ثار شعب موآب على اسرائيل بعد موت أخآب ولكن أخزيا لم يتخذ أية خطوة لاخضاعه(2 مل 1: 1و3: 5) يرجع حجر موآب الشهير إلى ذلك الحين. وسقط أخزيا من نافذة عليته التي في السامرة ومرض فأرسل رسلاً إلى بعل زبوب إله عقرون ليسأل هل يبرأ من مرضه، فقابل إيليا الرسل واخبرهم بأن مرض أخزيا هذا للموت وقد مات ولم يعقب ابناً فأخذ العرش من بعده أخوه يهورام(2 مل 1: 17-2).(2)  اسم الملك السادس من ملوك يهوذا. وهو ابن يورام او يهورام وكانت أمه عثليا ابنة أخآب ملك اسرائيل ويرجح أنه بدا حكمه أثناء مرض أبيه(2 أخبار  21: 18و19). وبعد سنة مات أبوه فخلفه على العرش(2 مل 8: 25) وكان ذلك حوالي عام 843ق.م. وقد سلك في طريق أخآب لأن أمه كانت تشير عليه بفعل الشر(2 أخبار 22: 3). وقد ذهب مع يورام ملك اسرائيل ليحارب الآراميين في راموث جلعاد فَجُرحَ يورام وذهب إلى يزرعيل ليبرأ فذهب أخزيا لزيارته(2 مل 8: 28و29). وفي تلك الأثناء ثار ياهو على يورام وقتله، وكذلك أمر رجاله بقتل أخزيا فقتلوه( 2مل 9: 16- 28). ويسمى أخزيا في 2 أخبار 21: 17 يهوآحاز وكذلك يدعى عزريا في 2 أخبار 22: 6 ولكن بعض المخطوطات العبرية تذكره باسم أخزيا في هذا الموضع عينه.'),
(118, 'أَخَشْتَارِيّ', 'ربما من الفارسية ومعناه ((تابع للملكة))  وهو ابن أشحور من عشيرة حصرون في سبط يهوذا(1 أخبار 4: 6).'),
(119, 'أخْنُوخ', 'اسم عبري ومعناه ((مكرس))  أو ((محنك))  ولفظ الاسم في الأصل العبري هو نفس الاسم حنوك في الترجمة العربية. وهو ابن يارد وابو متوشالح(تك 5: 18و21) وهو السابع من آدم(يهوذا عدد 14) من نسل شيث. ويخبرنا الكتاب المقدس أن أخنوخ سار مع الله اي أنه عاش في طاعة الله وشركة معه(تك 5: 22و24). وعاش ثلاثمائة وخمساً وستين سنة(تك 5: 23) ويخبرنا الكتاب المقدس أنه لم يوجد بعد ذلك لأن الله أخذه(تك 5: 24) وقد فسر كاتب الرسالة إلى العبرانيين هذا القول بأن الله نقله لكي لا يرى الموت(عب 11: 5) ويذكر يهوذا في رسالته عدد 14و15 أن أخنوخ تنبأ عن القضاء الذي يحل بالأشرار. ويمكن أن نرى هذه النبوة في سفر أخنوخ (ص 91) - وهو من الأسفار غير القانونية.كتاب أخنوخ:سفر من الأسفار غير القانونية ويسمى أيضاً ((نسخة أخنوخ الأثيوبية))  أو ((الحبشية))  ويسمى أيضاً أخنوخ الأول. وينسب خطأ إلى أخنوخ المذكور في تك 5: 23و24. والكتاب عبارة عن مجموعة من الأسفار اليهودية كتبت اصلاً في اللغة الآرامية على وجه الترجيح. وقد فقد الأصل الآرامي ولكن وجدت أجزاء من هذا الكتاب في الترجمة اليونانية. وكذلك توجد نسخة حبشية ترجمت عن النسخة اليونانية التي بدورها ترجمت عن الأصل الآرامي الذي يرجح أنه كتب بين سنة 163و80 قبل الميلاد.والكتاب مليء بأخبار الرؤى عن المسيا المنتظر والدينونة الأخيرة وملكوت المجد. ولعقيدة المسيا في هذا الكتاب  أهمية خاصة لأنها تمهد الطريق للعهد الجديد وكذلك تعتبر إعداداً لمجيء المسيا. ويدعى المسيا في هذا الكتاب  ((مسيح الله))  انظر ص 48: 10. وكذلك يدعى ((البار))  انظر 38: 2 وقارنه مع أعمال 3: 14  ((والمختار))  انظر ص 40: 5 وقارنه مع لوقا 9: 35 في الأصل اليوناني وكثيراً ما يدعى المسيا ((ابن الإنسان))  ص 46: 2 الخ. ويقول كاتب سفر أخنوخ أن ((ابن الإنسان))  كان موجوداً قبل خلق العالم انظر ص 48: 2و3 وأنه سيدين العالم انظر ص 69: 27 وأنه سيملك على الشعب البار انظر ص 62: 1- 6.ويقتبس كاتب رسالة يهوذا في عددي 14و15 سفر أخنوخ ص 1: 9. وكذلك يوجد لبعض الأقوال الخاصة بأواخر الأيام في العهد الجديد ما يقابلها في سفر أخنوخ. وقد اقتبس بعض الآباء في العصور المسيحية الأولى بعض أقوال هذا السفر. ومن بين هؤلاء جاستين الشهيد وأرينيوس وأكليمندوس الاسكندري وأوريجانوس. ولكن قادة المسيحيين فيما بعد أنكروا هذا الكتاب ورفضوه. ومن بين هؤلاء يوحنا فم الذهب وأغسطينوس وجيروم أو أورينيموس. ولم يعتبر اليهود او المسيحيون هذا الكتاب ضمن الأسفار القانونية.توجد نسخة سلافية تختلف في محتوياتها عن النسخة السابقة ويسمى هذا السفر غير القانوني ((أخنوخ الثاني))  أو ((كتاب أسرار أخنوخ))  وقد كتب هذا السفر اليهودي أولاً في اللغة اليونانية في مدينة الاسكندرية في النصف الأول من القرن الأول الميلادي. وقد فقد الأصل اليوناني أما النسخة الموجودة الآن فهي ترجمة سلافية.ويحتوي على رحلة أخنوخ في السماوات السبع وغعلانات الله لأخنوخ حسبما يزعمون وكذلك يحتوي على ما يقولون أنه تحذيرات أخنوخ لأبنائه.'),
(120, 'أَخٌ', 'جمعه إِخْوَةٌ(1)  لفظ أطلق على الابن في علاقته بأبناء أو بنات نفس الوالدين(تك 27: 6) أو نفس الأب فقط(تك 28: 2) أو نفس الأم فقط(قض 8: 19).(2)  كذلك أطلق على قريب من الأسرة الواحدة، ابن الأخ مثلاً،(تك 14: 16) أو من نفس الجنس(نح 5: 7) أو من أمّة قريبة(تث 23: 7) أو من أمة حليفة(عا 1: 9).(3)  وأطلق أيضاً على إنسان من نفس الدين الواحد(أعمال 9: 17) وكثيراً ما دعى المسيحيون أخوة(مت 23: 8).(4)  كما أطلق أيضاً على الصديق المحبوب فقد دعى داود يوناثان اخاً(2صم 1: 26) وكذلك أطلق على إنسان غريب كنوع من حسن الخطاب فقد دعى أخآب بنهدد أخاً(1 ملو 20: 32).(5)  وكذلك أطلق على أي إنسان من الجنس البشري مراعاة لأخوة البشر(تك9: 5).'),
(121, 'إِخْوَةُ الرَّب', 'ذكر العهد الجديد أسماء أربعة وهم: يعقوب و يوسي وسمعان ويهوذا. وقد ذكر أنهم أخوة الرب(مت 13: 55) وقد ذكر أيضاً انتقالهم إلى كفر ناحوم مع مريم ويسوعفي بدء كرازته(يوحنا 2: 12). ومرة فيما كان يسوع يكلم الجمع جاءت أمه وأمه طالبين أن يكلموه(مت 12: 47). وحتى نهاية خدمته لم يكونوا قد آمنوا به بعد(يوحنا 7: 3و5). ولكن بعد القيامة نقرأ أنهم كانوا يجتمعون مع التلاميذ(اع 1: 14).ويذكرهم الرسول بولس كقادة في الكنيسة المسيحية( 1 كو 9: 5) وقد أصبح أحدهم وهو يعقوب قائداً ممتازاً في كنيسة أورشليم(اع 15: 13 وغلا 1: 19) وهو كاتب الرسالة التي تحمل اسمه(يعقوب 1: 1).وقد تشعبت الآراء في نسبهم إلى المسيح ودرجة قرابتهم له:(1)  فقال قوم أنهم أخوته بالجسد من مريم. أي أن مريم بعد أن ولدت المسيح الذي حبل به فيها من الروح القدس وولدته وهي عذراء، ولدت هؤلاء الأخوة من يوسف ويقولون ان هذا هو التفسير البسيط الطبيعي للأقوال الواردة في (مت 1: 25، 13: 55) قد قال بهذا الرأي ترتليانوس في القرن الثاني ، هلفديوس في القرن الرابع ومعظم رجال الطوائف الإنجيلية ولكن هناك من يعارضون هذا الرأي ويقولون، لو أنه كان لمريم أولاد لما عهد المسيح بها إلى يوحنا تلميذه كما نجد هذا في يوحنا 19: 26و27، ويرد عليهم اصحاب الرأي بالقول أن أخوة المسيح لم يكونوا بعد قد آمنوا به ولذلك فضّل المسيح ان يضعها في عهدة يوحنا تلميذه ويرجح أنه كان أيضاً قريبها.(2)  أما الرأي الثاني فيقول أنهم كانوا أولاد يوسف من زوجة سابقة . ومن بعدها اتخذ مريم العذراء زوجة ثانية. ويستدلون على ذلك من أن الكتاب المقدس لا يذكر شيئاً عن حياة يوسف بعد ان بلغ يسوع السنة الثانية عشرة من العمر ويقولون لا بد أن يوسف مات بعد ذلك  ويرجحون أنه تزوج العذراء وهو متقدم في السن. وقد ورد هذا الرأي في بعض الأسفار غير القانونية وقال به أوريجانوس في القرن الثالث وأبيفانيوس في القرن الرابع الميلادي وكذلك تعتنق كنيسة الروم الأرثوذكس هذا الرأي.(3)  وهناك رأي ثالث يقول أن هؤلاء الأخوة هم أولاد كلوبا وكانت أمهم أخت أم المسيح، فهم أولاد خالته(انظر يوحنا 19: 25) ويقول أصحاب هذا الرأي أن مت 27: 56ومرقس 15: 40 يذكر وجود مريم أم يعقوب ويوسي عند الصليب. ويقول أن مريم هذه كانت أخت مريم أم يسوع وأن يعقوب ويوسي هما اللذان ذكر عنهما أنهما أخوان.وأول من قال لهذا الرأي هو ايرونيموس في القرن الرابع وأتبعته كنيسة رومية ولوثر وبعض أتباعه. أما معارضو هذا الرأي فيقولون - أنه من المستبعد أن يكون لأختين اسم واحد. كما يقولون أن الكتاب المقدس يفرِّق بين التلاميذ وأخوة الرب ويجعلهما فريقين يختلف أحدهما عن الآخر قارن أعمال1: 13و14.'),
(122, 'أَخُوخ', 'اسم عبري وربما معناه ((أخوي))  وهو اسم لابن بالع من سبط بنيامين(1 أخبار 8: 4) ويحتمل أنه هو نفس الشخص المذكور باسم أخيا في عدد 7 من نفس الاصحاح.'),
(123, 'أَخُوخِي', 'نسبة إلى أخوخ وأطلقت على بعض القادة في جيش داود وسليمان(2صم 23: 9و28و1 أخبار 11: 12و29).'),
(124, 'أَخُومَاي', 'اسم عبري وربما معناه ((أخو ماء))  وهو اسم رجل من رؤساء عشائر يهوذا(1 أخبار4: 2).'),
(125, 'أَخِي', 'اسم عبري وربما معناه ((أخي))  او ربما تكون اختصار ((أخيا))  وقد ورد:(1)  اسم واحد من رؤساء سبط جاد الذين كانوا يسكنون جلعاد في باشان( 1 أخبار 5: 15).(2)  اسم رجل من سبط أشير( 1 أخبار 7: 34).'),
(126, 'آخيام', 'اسم عبري ومعناه (( اخو الام )) وهو احد قادة جيش داود (2 صم 23: 33. و 1 اخبار 11: 35).'),
(127, 'أخيان', 'اسم عبري ومعناه ((أخي او اخ صغير)) رجل من سبط منسى من عشيرة شيمداع( 1 اخبار 7: 19).'),
(128, 'أخيتوفل', 'اسم عبري معناه ((أخو الجهل))  أو ((أخو الغباء)) . رجل من جيلوه في أرض يهوذا وأحد مشيري داود (2صم 15: 12) وكان أب أحد أبطال داود(2 صم 23: 34) ويحتمل أنه كان جداً لبثشبع( 2صم 11: 3و23: 34) وكان رجل احتيال ومكر ولم يكن مخلصاً في مشورته. وكان من أكبر مشيري أبشالوم وأعوانه في أثناء عصيانه على أبيه داود. ولما اتبع أبشالوم مشورة حوشاي وكان هذا العمل لصالح داود سراً، قتل أخيتوفل نفسه(2 صم 15: 31- 34و16: 15و17: 23) وهذه من حالات الانتحار النادرة في تاريخ اسرائيل في العهد القديم.'),
(129, 'أَخِيحُود', 'اسم عبري ومعناه ((أخو الاتحاد)) . وكان من نسل أهود من سبط بنيامين من بلدة جبعة( 1 أخبار 8: 7) وقد ورد ذكر هذا الرجل في بعض المخطوطات باسم أخيهود.'),
(130, 'أَخِيرَع', 'اسم عبري ومعناه ((أخو الاثم))  أو ((أخورع))  وهو ابن عينن ورئيس سبط نفتالي في زمن ارتحال اسرائيل في البرية( عدد1: 15، 2: 29، 7: 78، 10: 27).'),
(131, 'أَخِيسَامَاك', 'اسم عبري ومعناه ((أخو السند))  أو ((أخو العضد))  وهو رجل من سبط دان وأبو أهولياب( خر 31: 6).'),
(132, 'أَخِيش', 'يغلب على الظن أن هذا اسم فلسطيني ولذلك فمعناه غير معروف. وهو ملك جت وابن معوك ملكها قبله (1صم 27: 2) وقد هرب إليه داود مرتين ففي المرة الأولى تظاهر داود بالجنون لخوفه على نفسه منه(1 صم21: 10- 15)أما في المرة الثانية فقد لجأ داود إلى أخيش فرحب به وذلك بسبب العداوة بينه وبين شاول ,وأعطاه مدينة صقلغليقيم فيها هو و رجاله(1صم 27: 6).ولما اجتمع الفلسطينيون لمحاربة اسرائيل أراد أخيش أن يأخذ داود معه للحرب ولكن رؤساء الفلسطينيين عارضوا في ذهاب داود معهم فأذن له أخيش أن يعود إلى صقلغ( 1صم 28: 1و2 و ص29) ويرجح أن هذا هو نفس أخيش ملك جت الذي نقرأ عنه في بدء ملك سليمان. كما نقرأ في مل 2: 39و40 عن هروب عبدين من عبيد شمعي إليه وذهاب شمعي إليه لإستعادتهما. ويذكر أن أخيش في عنوان مز 34 باسم أبيمالك وربما كان هذا هو لقب ملوك الفلسطينيين.'),
(133, 'أخيشاحو', 'اسم عبري ومعناه ((اخو السحر او الفجر )) وهو ابن بلهان ابن يدعئيل ابن بنيامين (1 اخبار 7: 10).'),
(134, 'أَخِيشَار', 'اسم عبري ومعناه ((أخو الرجل المستقيم أو أخو المرنم))  وكان موظفاً موكلاً بشؤون بيت سليمان(1 ملوك 4: 6).'),
(135, 'أَخِيطُوب', 'اسم عبري ومعناه ((أخو الطيبة))  وقد جاء هذا اسماً لثلاثة أشخاص في الكتاب المقدس:(1)  ابن فينحاس بن عالي(1 صم 14: 3) وهو أبو أخيمالك الكاهن(1 صم 22: 9).(2)  ابن أمريا وأبو صادوق الكاهن( 1 أخبار 6: 7و8).(3)  ورد هذا اسماً لأخي طوب آخر ولكن من نقس العشيرة واسم أبيه امريا أيضاً واسم حفيده صادوق( انظر 1 أخبار 6: 11و12 وقارنه مع نحميا 11: 11).'),
(136, 'أَخيعَزَر', 'اسم عبري ومعناه ((أخو المعونة))  أو ((الأخ عون))  وقد ورد هذا اسماً لشخصين:(1)  ابن عميشداي من رؤساء بيوت سبط دان. وقد ناب عن سبط دان في التعداد الذي عمل في البرية( عدد 1: 12و2: 25و7: 66).(2)  رجل بنياميني رافق داود لما كان في صقلغ( 1أخبار 12: 3).'),
(137, 'أَخِيقَام', 'اسم عبري ومعناه (( أخي قام))  وهو ابن شافان أحد رؤساء يهوذا وقد أرسله يوشيا الملك مع آخرين إلى خلدة النبية ليسأل عن سفر الشريعة( 2 ملوك 22: 12و14) وقد استخدم نفوذه فيما بعد لحماية أرميا ضد هجمات الكهنة والأنبياء الكذبة أثناء حكم يهوياقيم. (أرميا 26: 24) وهو أبو جدليا(2 ملوك 25: 22).'),
(138, 'أَخِيلُود', 'اسم عبري ومعناه ((اخو الولد))  وهو ابو يهوشافاط المسجل في عصر داود وسليمان( 2صم 8: 16و1 ملوك 4: 3). ويرجح أنه هو أيضاً أبو بعنى أحد وكلاء سليمان(1 ملوك 4: 12).'),
(139, 'أَخِيم', 'هذه هي الصيغة اليونانية للاسم العبراني ((ياكين))  أو ((ياقيم))  الذي هو اختصار يهوياقيم أي ((الرب يقيم))  وقد ورد اسمه في مت 1: 14 في سلسلة نسب المسيح.'),
(140, 'أَخِيمالك', 'اسم عبري ومعناه ((أخو الملك))  ورد هذا اسماً:(1)  لابن أخيطوب ورئيس كهنة نوب، فلما كان داود هارباً من وجه شاول وكان في حاجة شديدة إلى الطعام أعطاه أخيمالك خبز الوجوه الذي لا يحل أكله إلا للكهنة، وأعطاه أيضاً سيف جليات( 1صم 21: 1- 9 وقارنه مع مر 2: 26) وانظر ((أبياثار)) . وقد نقل دواغ إلى شاول خبر هذا الأمر. وقد فسّره شاول بأنه خيانة من أخيمالك وفرية من كهنة نوب فأمر بقتلهم. ولكن أبياثار ابن أخيمالك نجا بنفسه( 1صم 21: 7، 22: 7- 23 وانظر عنوان مز 52).(2)  اسم لابن ابياثار وحفيد أخيمالك المذكور آنفاً. وقد كان واحداً من رؤساء الكهنة الذين عاصروا داود(2 صم 8: 17، 1 أخبار 24: 3و6و31) ويرجح أن أخيمالك هذا هو المذكور في ا أخبار 18: 16 مع أن اسمه ورد  ((أبيمالك))  في بعض المخطوطات العبرية.(3)  اسم رجل من أتباع داود( 1صم 26: 6).'),
(141, 'أَخِيمَان', 'اسم عبري معناه ((أخو منَّة أو هبة))  وهو اسم:(1)  أحد أولاد عناق الحبروني الثلاثة(عدد 13: 22) وكانوا يدعون ((النفيليم))  أو ((الجبابرة))  (عدد 13: 33) وقد طردهم كالب من حبرون ( يش 15: 14 وقض 1: 10).(2)  أحد اللاويين حراس أبواب الهيكل وكان في عهدته حراسة الباب الشرقي الذي كان يدخل منه الملك(1 أخبار 9: 17).'),
(142, 'أخيمَعَص', 'اسم عبري ومعناه ((أخو الامتعاض أو الغضب)) .(1)  اسم أبي أخينوعم امرأة الملك شاول (1 صم 14: 50).(2)  اسم ابن صادوق رئيس الكهنة في أيام داود. وقد بقي أخيمعص ويوناثان بن أبياثار في أورشليم أثناء عصيان أبشالوم على أبيه وكانا يعملان على إبلاغ داود بخطط أبشالوم(2 صم 15: 27و36و17: 15- 21) وكان أخيمعص أول من أخبر داود بانهزام أبشالوم( 2صم 18: 19- 30).(3)  اسم وكيل سليمان في نفتالي وأخذ باسمة بنت سليمان امرأة(1 ملوك 4: 15). وقد ظن بعض الباحثين أن هذا هو نفس أخيمعص المذكور في عصر داود.'),
(143, 'أَخِيمُوت', 'اسم عبري معناه ((أخو الموت))  وهو لاوي ابن القانة من عشيرة قورح(1 أخبار 9: 17).'),
(144, 'أَخِينَادَاب', 'اسم عبري معناه (( أخي نبيل أو كريم))  وهو ابن عِدُّو وكان وكيلاً لسليمان في محنايم( 1 ملوك 4: 14).'),
(145, 'أَخِينُوعَم', 'اسم عبري معناه ((أخي نعيم أو بهجة))  وهو اسم:(1)  ابنة أخيمعص وزوجة شاول(1 صم 14: 50).(2)  امرأة من يزرعيل تزوجها داود بعد أخذت منه ميكال (1 صم 25: 43و27: 3) وكانت أم أمنون بكر داود (2 صم 3: 2) وقد أخذها العمالقة في الأسر من صقلغ مع أبيجايل امرأته الأخرى ولكن داود أنقذها. (1 صم 30: 5و18).'),
(146, 'أَخِيهُود', 'اسم عبري ومعناه ((أخو العظمة))  وهو ابن شلومي ورئيس في سبط أشير وقد مثل هذا السبط في تقسيم أرض كنعان(عدد 34: 27).'),
(147, 'أخِيُو', 'اسم عبري ومعناه ((أخوي أو أخو يهوه))  وكان هذا اسم:(1)  ابن أبيناداب وأخو عزَّة وقد عهد إليه مع أخيه بنقل تابوت الرب من يعاريم إلى أورشليم في عصر داود (2صم 6: 3و4، 1 أخبار 13: 7).(2)  رجل من بنيامين من بني الفَعَل(1 أخبار 8: 14).(3)  رجل آخر من بنيامين وهو ابن يعوئيل (يعيئيل) من امرأته معكة(1 أخبار 8: 29و9: 35و37).'),
(148, 'أَخِيَّا', 'اسم عبري معناه ((أخو يهوة))  وهو اسم:(1)  رجل من يهوذا وهو ابن يرحمئيل(1 أخبار 2: 25).(2)  رجل من بنيامين من الذين نقلوا من جبع إلى مناحة(1 أخبار 8: 6و7) وربما هو نفس أخوخ المذكور في عدد 4 من نفس الاصحاح.(3)  ابن أخيطوب وكان رئيس كهنة في جبعة وربما هو نفس أخيمالك أو أخوه( 1صم 14: 3و18).(4)  أحد أبطال جيش داود المذكور باسم أخيا الفلونيّ(1 أخبار 11: 36).(5)  أحد اللاويين الذين عاشوا في عصر داود وكان على خزائن بيت الله وخزائن الأقداس( 1 أخبار 26: 20).(6)  ابن شيشا وكان كاتباً في عصر سليمان(1 ملوك 4: 3).(7)  نبي ويلقب بالشيلوني نسبة إلى شيلوه. وفي عصر سليمان التقى أخيا بيربعام وكان أخيا لابساً رداء جديداً فمزّق رداءه إلى اثنتي عشرة قطعة وأعطى يربعام عشر قطع دلالة على أنه سيملك على عشرة أسباط وقد تنبأ على مملكة سليمان بالانقسام بسبب عبادة سليمان الوثنية(1 ملوك 11: 29- 39). وبعد أن ملك يربعام مرض ابنه فأرسل امرأته متخفية لتسأل أخيا فيخبرها بأمر الغلام. وفعلاً قامت امرأة يربعام وذهبت إلى أخيا متخفية ولكنه عرفها وأخبرها بأن الغلام سيموت عقاباً لعبادة يربعام الوثنية(1 ملوك 14: 1- 18) ونعلم من 2 أخبار 9: 29) أن نبوات أخيا سجلت في كتاب.(8)  أبو بعشا ملك اسرائيل (1 ملوك 15: 27و33).(9)  أحد رؤساء الشعب الذين ختموا الميثاق في أيام نحميا(نحميا 10: 26).'),
(149, 'أدَام', 'اسم عبري معناه  ((أحمر ))  وهو اسم مدينة بجانب نهر الأردن بالقرب من صرتان , ويرجح أن مكانها اليوم تل الدامية على الشاطىء الشرقي للاردن على بعد مسافة تقل عن الميل جنوبي مكان اتصال نهر يبّوق بالأردن. وقد فاضت مياه هذا النهر عدة مرات في العصور المتأخرة وعملت على سد نهر الأردن فأوقفت مياهه كما نرى شيئاً من مثل ذلك في يشوع 3: 16 .'),
(150, 'أَدَامَة', 'اسم عبري معناه  ((أرض))  أو  ((أديم))  مدينة محصنة في نفتالي (يشوع 19: 36) ويحتمل أن مكانها اليوم خربة تقع على مسافة عشرة أميال شمالي بيسان وتدعى أدمة , أو حجر الدم عند التقاء نهر الأردن ببحر الجليل.'),
(151, 'أَدَامي النَاقِب:  ((الموضع الأحمر في المعبر))  .', 'بلدة على حدود نفتالي (يش 19: 33) ويحتمل أن مكانها اليوم هو خربة الداميّة التي تقع على مسافة خمسة أميال جنوبي غرب مدينة طبرية وهي في ممر طريق القوافل من جلعاد إلى عكا . وربما يحسن اعتبارها مع الكلمة التي تليها اسماً واحداً  ((أدامي الناقب))  أي أدامي العبر .'),
(152, 'مُؤَدِّب', 'هذه ترجمة للكلمة اليونانية  (( بيدجوجوس ))  الشخص الذي يرشد التلميذ . وكان المؤدب عبداً يوثق به ويعتمد عليه في عنايته بأولاد أثرياء اليونانيين . وكان عليه أن يحرص على الأولاد ضد أي خطر جسمي أو خلقي . وكان له أن يؤدبهم إن أساءوا وكان يرافقهم في ذهابهم إلى المدرسة وعند عودتهم منها .وفي غلاطية 3: 24 و25 يشبه الناموس بالمؤدب لأن الناموس يمنعنا من السير في طريق الخطأ ويظهر أننا خطأة وبذلك يقودنا إلى الإيمان بالمسيح وفي رسالة كورنثوس الأولى ص 4: 15 نجد نفس الكلمة اليونانية مستعملة في الجمع وقد ترجمت (( مرشدون ))  .'),
(153, 'أَدَبْئِيل', 'اسم عبري معناه ((أدب الله )) وهو الابن الثالث لاسماعيل (تك 25: 13و1 أخبار 1:29 ) وربما كان هذا الأب الأول لقبيلة أدبئيل العربية .وكان أفرادها يعيشون في القرن الثامن قبل الميلاد في شمالي غرب بلاد العرب كما نعلم ذلك من سجلات تغلث فلاسر ملك آشور .'),
(154, 'أَدَّار', 'كلمة عبرية وربما معناها ((الرحب أو العظمة ))  وقد وردت :(1) اسم مدينة على حدود يهوذا الجنوبية (يشوع 15: 3 ) وفي سفر العدد 34: 4 تسمى حصد أدّار .(2)اسم رجل بنياميني هو ابن بالع(1 أخبار 8: 3) وقد ورد هذا الاسم في بعض المخطوطات العبرية واليونانية باسم ((أرد))  ويرجح أن هذا هو الصواب. انظر ((أرد)) .'),
(155, 'َدَّان', 'أمعناها غير معروف وهي اسم مدينة في بابل، وبعض من اليهود الذين عادوا منها إلى فلسطين مع زربابل لم يقدروا أن يثبتوا سلسلة نسبهم إلى اسرائيل (عزرا 2: 59) وفي نحميا 7: 61 نجد أن البلدة نفسها تدعى أدُّون.'),
(156, 'أَدِّي', 'هذه هي الصيغة اليونانية للاسم العبري عدّو وهو أحد أسلاف يوسف زوج مريم أم يسوع المسيح(لو 3: 28).'),
(157, 'أدْرَامِيتِنِيّة', 'ميناء في ميسيا في شمال غربي آسيا الصغرى. وتوجد قرية تدعى بالتركية ((أدرمية))  في موقع بالقرب من مكان هذا الميناء. ولما كان الرسول بولس ذاهباً إلى رومة أقلع من قيصرية في سفينة أدراميتينية(أعمال 27: 2).'),
(158, 'أَدْرَمَّلَك', 'اسم اكدي معناه ((ملك مقتدر))  أو ((الإله ملك مقتدر))  وقد ورد اسماً:(1) لإله سفراويم. فبعد أن أخذ الآشوريون اسرائيل في السبي أسكنوا بعض أهل سفراويم في السامرة مكانهم. وكان هؤلاء الناس من أهل سفراويم يقدمون أولاد هم محرقات لأدرَملَك(2 ملوك 17: 31). وقد ظن بعضهم أن هذا الإله هو نفس ((أددملك))  الذي كان يعبد في بابل.(2) الابن سنحاريب ملك آشور من 705- 681 ق.م. وقد اشترك هو وأخوه شرآصر في قتل أبيهما وفرَّا إلى أرض أراراط(2 ملوك 19: 37واش 37: 38).'),
(159, 'أدْرِيَا', 'اسم بحر أبحرت فيه سفينة كان بولس الرسول عليها. وقد غرقت السفينة عند جزيرة كريت(أعمال 27: 27) والاسم مأخوذ من اسم بلدة تجارية تقع عند مصب نهر البو.ويظن البعض أن البحر الأدرياتيكي قد سمي أيضاً كذلك بالنسبة لهذه البلدة. ويشمل الاسم خليج ترنتين والبحر الصقلي والبحر الأيوني وخليج كورنثوس ويمتد استعمال هذا الاسم ليشمل المياه الواقعة بين كريت ومالطة. ويطلقون على هذا البحر الآن اسم البحر الأدرياتيكي.'),
(160, 'أَدَلْيَا', 'ويرجح أن الاسم من أصل فارسي. وهو اسم أحد أبناء هامان العشرة(أس 9: 8).'),
(161, 'أدْمَاثا', 'اسم فارسي ويرجح أن معناه ((الذي لا يقهر))  وكان واحداً من أمراء مادي وفارس أثناء حكم أحشويروش (أس 1: 14).'),
(162, 'أَدْمَة', 'ربما كان معنى الاسم ((احمرار))  وهو اسم لمدينة من مدن السهل(تك 10: 19و14: 2و8) وكانت من ضمن المدن التي أهلكت مع سدوم وعمورة (تك 19: 25و28و29، تثنية 29: 23، هو 11: 8).'),
(163, 'أَدُمّيم', 'اسم عبري ومعناه ((الأشياء الحمراء))  وهو اسم لطريق يسير من وادي الأردن إلى الأرض الجبلية بالقرب من الحدود بين بنيامين ويهوذا. وهو أقصر الطرق من أورشليم إلى أريحا. وكان يمر في برية اليهودية(يش 15: 7و18: 17). كان الذي وقعت فيه حوادث مثل السامري الصالح(لو 10: 30). وهناك تل قريب من هذا المكان يطلقون عليه اسم:  ((طلعة الدم)) .'),
(164, 'أَدُورَام', 'انظر أدونيرام.'),
(165, 'أَدُرَايِم', 'اسم عبري ربما كان معناه معنى ((التلان أو المرتفعان))  وقد أطلق على إحدى مدن يهوذا التي حصنها رحبعام (2 أخبار 11: 9) وربما كانت هي نفس أدورا المذكورة في المكابيين الأول 13: 20. ويظن أيضاً أنها دورا التي تبعد مسافة خمسة أميال جنوبي حبرون.'),
(166, 'أَدُوم', 'معنى الاسم (( أحمر)) . وقد ورد هذا الاسم فيما يأتي:1- لقب عيسو ابن اسحاق لأنه كان أحمر عند ولادته(تك 25: 25) ولأنه باع بكوريته لأجل طعام أحمر اللون(تك 25: 30).2- شعب الآدوميين (عدد 20: 18 و20 و21 ) .3- الإقليم الذي كان يسكنه أبناء عيسو أو آدوم . وكان يطلق على هذا الإقليم اسم أرض سعير(تك 32: 3) وهو اقليم جبلي وعر .وجبل هارون الذي يقع في تلك البقعة يرتفع إلى 4780 قدماً فوق سطح البحر. ويمتد الإقليم مسافة مائة ميل بين البحر الميت وخليج العقبة، على جانبي غور العربة (تك 14: 6) والجزء السفلي من سلسلة الجبال مكوَّن من صخور حمراء رملية ممتزجة ببعض حجر الجرانيت الأحمر مع بعض الأحجار الرخامية الحمراء. ولذا فلون الصخور في البتراء(بيترا) جميل للغاية. وليست أرض أدوم خصبة كأرض فلسطين. ولكن وجد فيها في عصر موسى حقول وكروم وآبار وطريق رئيسي يسير شمالاً وجنوباً( عدد 20: 17و19) وقد وجد المنقبون مناجم للنحاس والحديد هناك. وكانت سالع عاصمة ادوم قديماً ثم تغير اسمها فيما بعد إلى البتراء. ومن ضمن المدن المهمة في آدوم: بصرة من الشمال وتيمان بالقرب من البتراء، وعصيون جابر في الجنوب. واسم البلاد في اليونانية، أدوميا. وبرية آدوم هي العربة الواقعة جنوبي البحر الميت(2 ملوك 3: 8و11). والجزء الشرقي من آدوم يقع الآن في المملكة الأردنية الهاشمية.'),
(167, 'أَلادُومِيُّون', 'هم نسل عيسو أو آدوم(تك 36: 1- 19) وقد ذكر الأدوميون في سجلات مصر في عصر الأسرة الثانية عشرة التي حكمت من سنة 2000 إلى سنة 1788ق.م. وقد طرد عيسو ونسله الحوريين من أرض ىدوم وسكنوا في موضعهم (تثنية 2: 12) وكان حكام آدوم، في البداية، أمراء يشبهون رؤساء القبائل(انظر تك 36: 15- 19) ولكنهم أصبحوا فيما بعد، وقبل قيام مملكة اسرائيل، يلقبون بالملوك (تك 36: 31- 39). وعندما كان شعب اسرائيل ذاهباً من البريودة إلى كنعان طلب من آدوم أن يأذن له بالمرور في أرضه ولكن ملك آدوم أبى عليه ذلك (عدد 20: 14- 21) وقد اكتشف المنقبون حصوناً منيعة على حدود آدوم، ترجع إلى ذلك العصر.وقد كان الأدومي يعتبر أخاً للعبراني حتى أن الجيل الثالث من نسل الأدومي المقيم في اسرائيل كان يعد عبرانياً (تثنية 23: 7و8). وقد حارب الأدوميين(1 صم 14: 47). وكذلك غزا داود آدوم وأقام عليها حراساً( أخبار 18: 13) وقد تنبأ بلعام بغزو اسرائيل لآدوم(عدد 24: 18) وبعد أن غزا داود آدوم هرب أحد أمراء الأسرة الحاكمة في آدوم، واسمه هدد، إلى مصر وصار فيما بعد خصماً لسليمان(1 ملوك 11: 14- 22). وفي أثناء حكم يهوشافاط، وبعد موت أخاب، غزا الآدوميون والعمونيون والموآبيون يهوذا. إلا أنهم أفنوا بعضهم بعضاً في حرب أثارها عليهم الرب (2 أخبار 20: 1و22و23) وكان حاكم آدوم في ذلك الحين يحمل لقب ((ملك)) ، وقد عاون الآدوميون اسرائيل ويهوذا في حربهم ضد ميشع ملك موآب(2 ملوك 3: 4- 27) ولكنهم ثاروا في أيام حكم يورام ابن يهوشافاط فقهرهم يورام ولكنه لم يتمكن من إخضاعهم(2 ملوك 8: 20، 2 أخبار 21: 8- 10). وقد قتل أمصيا عشرة آلاف من الادوميون بأن طوّح بهم من فوق قمة الصخرة، فقتلهم في وادي الملح وأخذ سالع عاصمة بلادهم(2 ملوك 14: 7، 2 أخبار 25: 11و12). وقد غزا الآدوميون سبط يهوذا وأخذوا منه أسرى، في أثناء حكم آحاز، عندما قام عليه فقح ابن رمليا ورصين ملك آرام وأخذا منه بعض الأسرى. وقد بقيت آدوم مدة من الزمن خاضعة لحكم آشور وكان هذا أثناء حكم تغلث فلاسر الثالث وسرجون وسنحاريب وأسرحدون وآشور بانيبال. ولكنها اشتركت في ثورات عامي 711و 701ق.م. ضد آشور. وقد ابتهج الآدوميون عندما خرَّب نبوخذ نصر أورشليم( مز 137: 7) وقد تنبأ الأنبياء بالكوارث التي حلت بآدوم بسبب عدائها الكبير لإسرائيل. ولكنهم تنبأوا أيضاً باندماجها النهائي ضمن ملكوت الله(أرميا 49: 7- 22، مراثي 4: 21و22، حزقيال 25: 12- 14، يوئيل 3: 19 الخ) وبعد سبي السبطين بقيت أرض يهوذا خراباً يبابا، فاستولى الآدوميون عليها حتى مدينة حبرون. وفي القرن الخامس قبل المسيح طرد الأنباط الآدوميين من جبل سعير. وفي القرن الثاني قبل الميلاد أخذ يهوذا المكابي واليهود حبرون وغيرها من المدن التي كان قد استولى عليها الادوميون، وقد أرغم يوحنا هركارلوس الآدوميين على الاختتان وأدخلهم ضمن جماعة اليهود. وقد كان هيرودس ونسله آدوميين.ويستفاد من الكشوف الأثرية أنمدينة الآدوميين ازدهرت من القرن الثالث عشر إلى القرن السادس قبل الميلاد.واللغة الآدومية شبيهة باللغة العبرية. ولا يعرف شيء عن ديانة الآدوميين إلا أسماء بعض الآلهة مثل ((قوس))  و ((هدد)) . وقد اشتهرت آدوم بحكمائها(أرميا 49: 7 وعوبديا عدد8). وقد كان اليفاز التيماني أكثر أصحاب أيوب حكمة، آدومياً.'),
(168, 'أدون', 'انظر أدّان.'),
(169, 'أَدُونِي بَازَق', 'اسم كنعاني معناه ((سيد بازق))  وهو ملك بازق الذي حاربه جيش يهوذا وانتصر عليه ففرّ هارباً غير أنه أُمسك، وقطعت أباهم يديه وقدميه. وقد اعتبر هذا جزاء وفاقاً لما ارتكب من قسوة، إذ كان قد قطع أباهم أيدي وأقدام سبعين ملكاً. وجيء بهإلى أورشليم حيث مات هناك   ( قض 1: 4- 7).'),
(170, 'أدُوني صادَق', 'اسم كنعاني ومعناه ((سيد البر))  أو ((سيد العدل)) . وكان ملكاً على أورشليم قبلما افتتحها الإسرائيليون. وعندما سمع أن يشوع قد أخذ عاي، وكان قد أخذ أريحا من قبل، وأن الجبعونيين قد حالفوا يشوع، عمل هو حلفاً مع أربعة من ملوك الأموريين لمعاقبة جبعون. فانهزم هو وحلفاؤه، وأسروا وقتلوا(يش 10: 1- 27).'),
(171, 'أَدُونِيرَام', 'اسم عبري ومعناه ((السيد رفيع)) ، رجل أقامه داود على الجزية(2 صم 20: 24) وقد بقي على الجزية طوال مدة حكم سليمان وإلى عصر رحبعام. وهو ابن عبدا، ويدعى أدورام في 2 صم 20: 24، ويدعى هدورام في 2 أخبار 10: 18، وقد أرسله رحبعام إلى العصاة الذين تمردوا عليه فرجموه بالحجارة ومات(1ملو4: 6و12: 18و2أخبار10: 18) وقد ظن البعض أن أدورام وأدونيرام اسمان لشخصين مختلفين إنما على الأرجح أنهما اسمان لشخص واحد.'),
(172, 'أدُونِيقَام', 'اسم عبري معناه ((السيد قام))  وكان رأس أسرة عاد بعض أفرادها من سبي بابل مع زربابل والبعض مع عزرا (عز 2: 13، 8: 13، نحم 7: 18) ويظهر أن رأس هذه الأسرة كان من ضمن من ختموا العهد في عصر نحميا(نحم 10: 16) حيث ورد هناك ذكره باسم ((أدونيا)) .'),
(173, 'أَدُونِيَّا', 'اسم عبري معناه ((يهوه هو السيد))  أو ((الرب هو السيد)) . وقد ورد هذا اسماً لهؤلاء:(1)  أحد أبناء داود واسم أمه حجّيث، إحدى نساء داود. وكان الرابع بين أبناء الملك الذين ولدوا في حبرون(2 صم 3: 2و4) وكان محبوباً مدللاً لدى أبيه، وربما كان أحب أبناء داود إليه بعد أبشالوم. ويظهر أن محبة أبيه له أعمته عن أخطائه فلم يؤنبه على إساءة البتة. ولما تقدمت بداود السن حاول أدونيا أن يغتصب العرش لنفسه. ومن المحقق أنه كان قد علم بما اعتزمه أبوه من أن سليمان هو الذي يخلفه على العرش(1 ملوك 1: 13، 1 أخبار 23: 1، 28: 5)، ولكن بما أن الأول والثالث من أبناء داود قد ماتا، ويرجح أن الابن الثاني كان قد مات أيضاً، فقد رأى أدونيا أن من حقه، بما أنه أكبر أبناء داود الأحياء أن يخلف أباه على العرشمن دون أن يستند في ذلك على قانون أو عرفٍ يحصران وراثة العرش في أكبر الأبناء. وقد تمكن من أن يضم يوآب قائد الجيش إلى صفه، وكذلك أيده أبياثار الكاهن، وكان يرجو أن ينحاز إليه الكهنة واللاويون. ولكنه فشل في استمالة صادوق الكاهن وبنايا رئيس حرس الملك، وناثان النبي إلى صفه. وقد دعا أتباعه إلى وليمة عظيمة في عين روجل، ونودي به ملكاً في تلك الوليمة. ولكن داود، وهو في أورشليم، أعلن أن سليمان ابن بثشبع هو الذي يخلفه على العرش، فخاف أدونيا وذهب وأمسك بقرون الذبح وطلب وعداً من سليمان أن لا يقتله، فوعده سليمان بذلك على شرط أن لا يوجد فيه شر أو خيانة(1 ملوك 1: 5- 53). ولكن بعد موت داود طلب أدونيا أن تعطى له أبيشج، آخر زوجة أخذها داود. فظن سليمان أنه بطلبه هذا يطلب الملك فغضب وأمر بقتله فقتل(1 ملوك 2: 13- 25).(2)  أحد اللاويين الذين أرسلهم الملك يهوشافاط لتعليم شعب يهوذا( 2 أخبار 17: 8).(3)  أحد رؤوس الشعب الذين ختموا العهد في عصر نحميا(نحم 10: 16) وربما هو نفس ((أدونيقام)) .');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(174, 'أَذَار', 'من أصل آكادي ومعناه ((مظلم))  أو ((مليء بالسحب))  وهو الشهر الثاني عشر في السنة البابلية. وقد استعار اليهود الاسم من البابليين أثناء السبي(عز 6: 15 وأستير 3: 7و13، 9: 15). وهو يمتد من وقت ظهور الهلال في شباط(فبراير) إلى ظهوره ثانية في آذار(مارس). وكان عيد الفوريم يقع في هذا الشهر. وبالنسبة إلى اختلاف الزمن بين السنة الهلالية والسنة الشمسية فقد كانوا يضيفون شهراً واحداً إلى السنة يسمونه ((آذار الثاني)) .'),
(175, 'إِذْرَعِي', 'اسم عبري وربما معناه ((ذراع))  أو ((قوة))  وهي:(1)  عاصمة باشان(تثنية 3: 10، يش 12: 4و13: 12و31) وقد هزم اسرائيل عوج ملك باشان وقتلوه في هذا المكان (عدد 21: 33- 35، تثنية 1: 4، 3: 1و10). واسمها الحالي ((درعة))  ، وتقع في وادي زيدة على مسافة تسعة وعشرين ميلاً شرقي الطرف الجنوبي لبحر طبرية. وعلى الحدود بين الأردن وسوريا ويوجد، تحت البلدة الحالية، كهوف منقورة في الصخر ترجع إلى العصور القديمة.(2)  إحدى مدن نفتالي المحصنة(يش 19: 37) وربما كان مكانها اليوم ((تل خربيبة))  الذي يقع جنوبي قادش.'),
(176, 'أَرا', 'اسم عبري ربما معناه ((أسد))  وهو اسم رجل من الرؤساء في سبط أشير 1 أخبار 7: 38.'),
(177, 'أَرَاب', 'اسم عبري ومعناه ((كمين))  وهي قرية في تلال يهوذا(يش 15: 52) ومكانها الحالي يدعى ((الرابية))  وهي خربة جنوبي حبرون وشرقي دومة.'),
(178, 'أَرَارَاط', 'هذا اللفظ العبري مأخوذ من الأصل الآكادي(أورارطو) وقد أطلق هذا الاسم على بلاد جبلية تقع شمالي آشور، على أحد جبالها استقر فلك نوح(تك 8: 4). والقمة التي يطلق عليها اليوم ((جبل أراراط))  ترتفع إلى 16916 قدماً فوق سطح البحر واسمها في التركية ((أغرى داغ)) . ولما قتل ابنا سنحاريب أباهما هربا إلى أرض أراراط أي أرمينية(2 ملوك 19: 37). ويظهر أنه كانت هناك مملكة في أرض أراراط في عصر أرميا دعاها النبي مع غيرها للاشتراك في حرب ضد بابل(أرميا 51: 27).'),
(179, 'أَرَاسْتُس أو أَرَسْطُوس', 'اسم يوناني معناه ((محبوب))  وهاتان الصورتان المختلفتان هما لاسم واحد في الأصل اليوناني. وكان أرسطوس واحداً من رفاق بولس الذين كانوا يخدمونه، وقد أرسله بولس في رفقة تيموثاوس من أفسس إلى مكدونية(أعمال 19: 22) ويغلب على الظن أنه هو نفس أراستس الذي بقي في كورنثوس بعد أن تركها بولس للمرة الأخيرة(2تيموثاوس : 20) ويظن البعض أنه نفس أراستس خازن المدينة في كورنثوس. وكان مسيحياً وقد أرسل سلامه إلى بولس في رومية(رومية 16: 23). وقد وجد في خرائب كورنثوس نقش على البلاط يذكر أن أراستس هو الذي رصف المدينة على نفقته. ويظن أنه هو نفس الشخص المذكور آنفاً.'),
(180, 'أًرام', 'ولفظه في الآكادية ((أرامو))  وربما كان معناه ((الأرض المرتفعة))  وقد ورد اللفظ فيما يلي:(1)  اسم أحد أبناء سام(تك 10: 22و23، 1 أخبار 1: 17) ونسله الآراميون الذين سكنوا أرض أرام.(2)  أرض أرام وقد سكنها الأراميون وكانت تمتد من جبال لبنان في الغرب إلى ما وراء الفرات في الشرق، ومن جبال طوروس في الشمال إلى دمشق وما وراءها في الجنوب.وقد أطلق على هذا الإقليم اسم ((سوريا))  في الترجمة اليونانية للكتاب المقدس(السبعينية). وقد وصل تارح وأسرته من أور الكلدانيين إلى حاران وهي إحدى مدن أرام (تك 11: 31) وقد دعي إبراهيم ((أرامياً تائهاً)) لأنه خرج من حاران إلى كنعان(تثنية 26: 5) وقد ظهرت عدة ولايات أرامية في نفس الوقت الذي نشأت فيه مملكة في أرض إسرائيل. وهذه الدويلات الأرامية التي ظهرت في ذلك الحين:(1) أرام النهرين(تك 24: 10) والنهران هما الدجلة والفرات. ويظن البعض أنهما نهرا خابور والفرات. وكان فدَّان أرام يقع في هذا الإقليم(تك 28: 2و5) وقد سكن ناحور بن تارح ونسله في مدينة حاران في فدَّان أرام(تك 29: 4و5) وقد دعا العبرانيون هذه البقعة ((أرام التي في عبر النهر))  (2صم 10: 16) وفي هذا الإقليم كانت تقع مدينتا  ((نصيبين))  و ((الرها))  اللتين اشتهرتا كمركزين للثقافة والآداب السريانية.(2)  أرام دمشق: وأهم مدن هذا الإقليم هي مدينة دمشق نفسها التي كانت العاصمة. وكان الآراميون يملكون هذه المدينة في عصر قيام مملكة في اسرائيل(2 صم 8: 5، 1 ملوك15: 18) وقد صارت المدينة في النهاية مركزاً لنفوذ الآراميين في المناطق الواقعة غربي الفرات. وكثيراً ما اشتعلت نيران الحرب بين دمشق والإسرائيليين واستغرقت اماداً طويلة. وقد غزا الآشوريون دمشق وامتلكوها عام 732 قبل الميلاد.(3)  أرام صوبة أو صوبا وقد ازدهرت هذه الدولة في عصر شاول الملك وداود وسليمان وكانت تقع غربي الفرات وقد امتدت في عصور ازدهارها إلى حدود حماة في الشمال الغربي(1 صم 14:/ 47و2صم 8: 3و10: 6و 1 أخبار 18: 3).(4)  أرام معكة: كانت دويلة تقع شرقي الأردن بالقرب من جبل حرمون في نصيب منسى(يش 12: 5و 13: 11 و1 أخبار 19: 6).(5)  جشور وكانت دويلة أرامية تقع بالقرب من معكة شرقي الأردن، وكذلك كانت تقع في نصيب منسى(تثنية 3: 14) وقد هرب أبشالوم إليها بعدما قتل أخاه أمنون(2 صم 13: 37و 15: 8).(6)  أرام بيت رحوب. وهذه أيضاً دويلة أرامية يرجح أنها كانت تقع بالقرب من مدخل حماة(عدد 13: 21ويش 19: 28و2صم 10: 6).(7)  رجل من نسل أشير وكان اسم أبيه شامر(1 أخبار 7: 34).(8)  وردت أرام كالصيغة اليونانية لاسم ((رام))  في العبرية(مت 1: 3و4 ولو 3: 33) وهو ابن حصرون وأبو عميناداب.'),
(181, 'أَرَامِي', 'النسبة إلى أرام. وقد دعي بتوئيل ((أراميَّاً))  وكذلك لابان(تك 25: 20) ويرجح أن يعقوب أيضاً دعي ((أرامياً تائهاً))  (تثنية 26: 5) ودعي نعمان  ((الأرامي))  في 2 ملوك 5: 20 ولكنه يدعى نعمان السرياني في لوقا 4: 27 وذلك لأن الترجمة السبعينية استخدمت اللفظ ((سرياني))  بدلاً من ((أرامي)) . ويذكر في 1 أخبار 7: 14 أنه كان لمنسى سريَّة أرامية.'),
(182, 'أَلارَامِيَّة', 'إحدى اللغات السامية الشمالية. وتسمى أحياناً الكلدانية لأن الكلمات التي نطق بها الكلدانيون المذكورون في سفر دانيال 2: 4 كانت بهذه اللغة، ولكن العلماء يتجهون اليومإلى الرأي القائل بأن لغة الكلدانيين القومية لم تكن الآرامية بل كانت الآكادية، ولذا فتسمية هذه اللغة في الكتاب المقدس باللغة الآرامية هو عين الصواب.واقدم الكتابات الآرامية فيما عدا الكتاب المقدس تنحصر في نقوش ترجع إلى ثمانمائة سنة قبل الميلاد. وقد اكتشفت في سوريا وآسيا الصغرى وكذلك وجدت نقوش آرامية على النقود والأوزان في آشور وبابل. وكذلك وجدت كتابات آرامية على أوراق البردي والرقوق اكتشفت في مصر وترجع إلى خمسمائة عام قبل الميلاد. ويظهر من الكتابات أن الآرامية كانت اللغة السائدة في ميادين التجارة والسياسة، ليس في الدول الآرامية فحسب بل في مناطقأخرى في الشرق الأوسط القديم.ويمكن تقسيم اللهجات الآرامية إلى قسمين: الآرامية الشرقية والآرامية الغربية. أما اللهجات الآرامية الشرقية فكانت تشمل:(1)  آرامية التلمود البابلي.(2)  المندعية. وكان المندعيون شيعة غنوسية.(3)  السريانية: وهي لهجة الرها التي أصبحت فيما بعد لغة الكنائس المسيحية التي تتكلم الآرامية في سوريا وفيما بين النهرين.أما الآرامية الغربية فكانت تشمل اللهجات الآتية:(1)  الآرامية الكتابية- وهي لغة الأجزاء الآرامية الموجودة في العهد القديم وسيرد ذكرها فيما بعد.(2)  الآرامية اليهودية التي وجدت بعد تمام العهد القديم وهذه تشمل:ا:  كلمات آرامية وردت في العهد الجديد في كتابات يوسيفوس المؤرخ اليهود.ب: آرامية الترجوم أو الترجمات وهي عبارة عن ترجمات وتفسيرات لأسفار العهد القديم من العبرانية إلى الآرامية.ج: فصول موجودة في كتب التقليد اليهودية وهي ((المشنا))  و ((الجمارا))  و ((المدراشيم)) .(3)  الآرامية السامرية.(4)  الآرامية النبطية.(5) آرامية بلميرا- أي تدمر.(6) الآرامية المسيحية الفلسطينية.'),
(183, 'أَرامية الكتاب المقدس', 'وقد وجد في الكتاب المقدس أجزاء كتبت باللغة الآرامية وهي هذه:كلمتان آراميتان نطق بهما لابان: وردتا في تت 31: 47.وقد طلب ممثلوا الملك حزقيا من الآشوريين الذين كانوا يحاصرون أورشليم أن يتكلموا بالآرامية(2 ملوك 18: 26و أشعيا 36: 11) وكذلك يوجد عدد في أرميا وهو أرميا 10: 11.وتوجد أجزاء آرامية مطولة في عزرا 4: 8- 6: 18و7: 12- 26، وهذه عبارة عن قرارات أصدرها الملك الفارسي. وكذلك ورد في دانيال جزء كبير بالآرامية في ص 2: 4ب لإلى ص 7: 28 ويظن البعض أن هناك بعض آثار للآرامية في غير هذه من أسفار العهد القديم.ولما حُمل اليهود إلى السبي أخذوا في استعمال اللغة الآرامية التي حلت محل اللغة العبرية، كلغة للتخاطب في شؤون الحياة اليومية كما نجد في نحميا 8: 8 إشارة إلى هذا، فقد وجد الشعب أنه لا بد له من تفسير الكتاب في الآرامية حتى يمكن فهمه. واستتبع ذلك استخدام اليهود للحروف الآرامية المربعة بدل الكتابة الفينيقية القديمة.وقد تكلم السيد المسيح اللغة الآرامية. ووردت بعض أقواله في العهد الجديد في هذه اللغة مثلاً مرقس 5: 41  ((طليشا قومي)) ، مرقس 7: 34  ((أفثا)) ، مرقس 15: 34  ((الوي الوي لما شبقتني)) .'),
(184, 'أَرَان', 'اسم كنعاني معناه ((جدي عنز وحشي)) ، وهو اسم لابن ديشان الحوري(تك 36: 28 و 1 أخبار 1: 42).'),
(185, 'أَربئيل', 'انظر ((بيت أربئيل)) .'),
(186, 'أَربع', 'اسم كنعاني معناه ((أربعة))  وهو أبو عناق، وأعظم العناقيين. وقد تسمت مدينة قرية أربع باسمه(يش 14: 15و15: 13).'),
(187, 'أَرابع', 'انظر ((ربع)) .'),
(188, 'أَرُبُوت', 'كلمة عبرية معناها ((طاقات))  وهي اسم مكان بالقرب من سوكوه وحافر(1 ملوك 4: 10) ويظن أن مكانها اليوم  ((عرابة))  بالقرب من دوثان وبلدة جنين الأردنية.'),
(189, 'أَلأَرَبيُّ', 'نسبة إلى آراب، وهي مدينة في إقليم اليهودية الجبلي. وقد لقب فعراي أحد أبطال داود بالأربي(2 صم 23: 35).'),
(190, 'أَرْتَحْشَشْتَا أو أَرْتَحْشَسْتَا', 'وهما صورتان لاسم واحد في الأصل العبري ولفظه في الفارسية القديمة ((أرتكسشثرا))  ومعناه ((الذي يعطي القانون المقدس السيادة))  وقد ورد اسماً لملكين: أولهما هو الابن الثالث للملك زركسيس وقد خلف أباه على عرش الفرس وحكم من عام 465 إلى عام 424ق.م. ويدعى ((لونجمانوس))  أي ((طويل الباع))  وقد أمر في أول عهده بمنع الاستمرار في بناء الهيكل في أورشليم(عزرا 4: 7) ولكنه عاد فيما بعد وأذن بالاستمرار في البناء(عزرا 6: 14). وفي السنة السابعة من ملكه أذن لعزرا أن يعود ومعه عدد كبير من المسبيين إلى أورشليم(عزرا 7: 1و11و12و21 و8: 1). وقد يظن بعض العلماء أن عزرا قام بنشاطه في زمن حكم أرتحشستا الثاني(404- 358ق. م.) ولكن هذا الأمر يعوزه الدليل و لا يستقيم مع النصوص الكتابية.وفي السنة العشرين من ملكه(445ق.م) أذن لنحميا، الذي كان يعمل ساقياً له، أن يذهب إلى أورشليم، وأن يبني سور المدينة(نح 2: 1) وفي السنة الثانية والثلاثين من ملكه أيضاً(433ق.م.) أذن لنحميا أن يذهب إلى أورشليم مرة ثانية. وعينه حاكماً على المدينة(نح 13: 6) وقد ذكر المؤرخون اليونان أن أرتحشستا أخمد ثورة قامت في مصرعام 460ق.م. وأخرى في سوريا عام 448ق.م.'),
(191, 'أَرْتِيماس', 'اختصار الاسم اليوناني أرتيمادورس ومعناه ((عطية الآلهة أرطاميس)) . وهو أحد رفقاء بولس في الجزء الأخير من حياته. وقد فكر الرسول في إرسال رسالة معه إلى تيطس(تيطس 3: 12).'),
(192, 'أُرْجُوان', 'لون صباغة يشمل البنفسجي والقرمزي أو الأحمر(قارن مرقس 15: 17 مع مت 27: 28) وكانت ثياب الأرجوان غالية الثمن يلبسها الأغنياء و ذوو المكانة الرفيعة وكبار موظفي الدولة(أستير 8: 2و15، دانيال 5: 7، لو 16: 19 ورؤ 17: 4) وكان يلبسه الملوك بنوع خاص(قض 8: 26، 1 مكابيين 8: 14) وعندما ألبس الجند المسيح ثوب الأرجوان قصدوا بذلك السخرية والاستهزاء من قوله أنه ملك(مر 15: 17) وكانت تستخدم ثياب الأرجوان في أماكن العبادة كلباس لآلهة الأوثان(أرميا 10: 9) وقد استخدم الأرجوان في صنع ستائر خيمة الاجتماع والحجاب(خر 25: 4 و26: 1و31و36) وكذلك استخدم في صنع ثياب رئيس الكهنة(خر 28: 5و6و15و33و39: 29) وقد صنع حورام، وهو رجل صوري، حجاب هيكل سليمان من الأرجوان(2 أخبار 2: 14و3: 14) ويقول يوسيفوس أن البنفسجي أو الأزرق الذي كان في الحجاب يشير إلى زرقة السماء(يوسيفوس: حروب اليهود، الكتاب الخامس، الفصل الخامس، والفقرة الرابعة) وكانوا يصنعون لون الأرجوان من بعض أصداف السمك. وكان الصوريون يصنعون الأرجوان الذي اشتهروا به من نوعين من محار السمك اسمهما باللاتينية Murex brandaris, Murex trunculus .وقد   وجد صدف كثير في منية البيضا وكانت ميناء أوجاريت في العصور القديمة. ومن هذا يتضح أن لون الأرجوان كان يصنع هناك حوالي عام 1400 ق.م. وقد وجدت أكوام من هذه الأصداف في صيدون. وكان الفينيقيون يرسلون ألوان الأرجوان إلى أماكن بعيدة في العالم القديم(حز27: 7- 16). وكانت ليديا , أول من قبل رسالة المسيح على فم بولس الرسول , بياعة أرجوان(أع 16: 14).'),
(193, 'أَرْجُوب', 'اسم عبراني معناه  ((كتلة من الطين))  وهو اسم:(1) إقليم في باشان يقع على حدود جشور ومعكة وكان كذلك ضمن ممتلكات عوج أثناء دخول إسرائيل أرض كنعان، وكان هذا الإقليم يشمل ستين مدينة حصينة في ذلك الحين. وقد استولى عليه يائير الذي من سبط منسي ولذا فقد دعي المكان ((حؤوث يائير))  (تثنية 3: 4و13و14ويش 13: 30و1 ملوك 4: 13).(2) اسم رجل قتله فقح ابن رمليا، لما قتل فقحيا ملك إسرائيل في السامرة ليحل محله(2 ملوك 15: 5).'),
(194, 'أرجيم', 'انظر ((يعري أرجيم)) .'),
(195, 'أَرْخِبسُّ', 'معنله في اليونانية ((المتسلط على الفَرَس))  وهو مسيحي كان في كولوسي وربما كان ابن فليمون. وكان خادم الكنيسة في تلك المدينة. وقد أرسل بولس تحياته إليه(كولوسي 4: 17 وفليمون عدد 2).'),
(196, 'أرْخيلاَوُس', 'اسم يوناني معناه ((حاكم الشعب))  وهو الكبير بين ولدين ولدتهما ملثاس زوجة هيرودس الكبير السامرية. وقد تعلم في رومية هو وأخوه أنتيباس وأخواهما الآخران من أبيهما وهما هيرودس وفيلبس. وبعد موت هيرودس الكبير سنة 4ق.م أخذ أرخيلاوس الجزء الكبر من مملكته بما في ذلك اليهودية والسامرة(مت 2: 22). وقد أخمد أرخيلاوس ثورة لليهود اشتعلت في أورشليم في وقت عيد الفصح عقب ارتقائه العرش. وقد قتل حينئذ ثلاثة آلاف رجل. وبالرغم من المعارضة التي لاقاها من قادة اليهود ومن أخيه الصغير أنتيباس فإنه تمكن من أن ينال موافقة رومية على توليه العرش كحاكم لا كملك. ويظهر من (( مثل الأمناء))  أن الرب يسوع يشير إلى ما حدث عندما تسلم أرخيلاوس مقاليد الحكم. ويحتمل أن القول ((إنسان شريف الجنس))  يشير إلى أرخيلاوس. وأن القول ((ذهب إلى كورة بعيدة ليأخذ لنفسه الملك))  يشير إلى رومية  ((وأهل مدينته كانوا يبغضونه))  ربما يشير إلى زعماء اليهود( لو 19: 12- 14) وأن القول ((اما أعدائي الذين لم يريدوا أن أملك عليهم فأتوا بهم إلى هنا واذبحوهم قدامي(لوقا 19: 27) فربما يشير إلى معاملته القاسية لليهود.وقد خلعه أغسطس قيصر من ملكه بسبب سوء حكمه، في سنة 6 ميلادية، ونفاه إلى فينّ في بلاد الغال. وبعد ذلك تولى الحكم في اليهودية والسامرة ولاة رومانيون من أمثال بيلاطس البنطي وغيره.'),
(197, 'أرد', 'انظر ((أدّار)) .'),
(198, 'أَلأُرْدُنّ', 'اسم عبري معناه ((الوارد المنحدر))  وهو أهم أنهار فلسطين وله أربعة منابع: المنبع  الشرقي في بانياس(التي هي قيصرية فيلبس قديماً) وينبع هذا النبع من كهف في صخرة عالية. أما النبع الأوسط وهو أكبر المنابع جميعاً فهو في تل القاضي. ويرجح أن هذا المكان هو ((دان))  قديماً. وهناك مجرى لدان الوافر الفيضان الذي يتكون من اتحاد نبعين. أما منابعه شمالاً وأكثرها ارتفاعاً فوق سطح البحر فهو المنبع الذي ينبع من أسفل حاصبيا ويتكون منه نهر الحاصباني. وأصغر هذه المنابع هو نبع براغيت وهو أقصاها غرباً. والحاصباني هو أطول هذه النهيرات إذ يبلغ طوله أربعة وأربعين ميلاً، واللدّان أكبرهما حجماً وأغزرها ماء. وبانياس أجملها وتختلط مياه نهر البراغيث بمياه الحاصباني. وكذلك تختلط مياه نهر بانياس بمياه اللدّان. ويتكون من هذه النهيرات الأربعة نهران. وهذان بدورهما يمتزجان معاً في بقعة من المستنقعات على بعد خمسة أميال شمالي بحيرة الحولة ليكونا نهراً واحداً وهو نهر الأردن- وبعد ان يمر هذا النهر في بحيرة الحولة التي طولها أربعة أميال تقريباً يجري النهر إلى مسافة عشرة أميال ونصف ثم يصل إلى بحر الجليل ويبلغ طول بحر الجليل اثني عشر ميلاً ونصف ميل. والمسافة من الطرف الجنوبي لبحر الجليل إلى البحر الميت تبلغ خمسة وستين ميلاً. ولكن النهر يتعرّج في سيره بحيث تصل المسافة التي يقطعها إلى ما يزيد على مائتي ميل. أما المسافة من نهر بانياس إلى البحر الميت إذا قيست في خط مستقيم فتبلغ مئة ميل وأربعة أميال فقط.ويدعى الأردن ((الغور المنحدر))  فإن ارتفاع النبع في حاصبيا يبلغ 1700 قدم فوق سطح البحر. أما بحيرة الحولة فتقع على ارتفاع تسعة أقدام فوق سطح البحر. ويبلغ انخفاض بحر الجليل عن سطح البحر 685 قدماً. أمل البحر الميت فيبلغ انخفاضه 1275 قدماً تحت سطح البحر ، فالأردن هو النهر الوحيد في العالم الذي يجري في انخفاض عن سطح البحر في الجزء الأكبر من مجراه. وأهم النهيرات التي تصب مياهها في الأردن أثناء سيره فهي:من الشرق: نهر اليرموك والزرقا. ويتصل نهر اليرموك بالأردن على مسافة أربعة أميال جنوبي بحر الجليل- أما نهر الزرقاء فينبع من مدينة الزرقاء الأردنية ثم يتصل على مسافة أربعين ميلاً تقريباً جنوبي بحر الجليل.ويوجد في الغرب بعض الوديان التي تفيض فيها المياه في الشتاء ولكنها تجف في الصيف. ومن ضمن هذه الوديان ((وادي البيرة))  الذي يصل إلى الناصرة، وكذلك ((وادي الجلود))  الذي يسير في وادي يزراعيل إلى أن يمر بباشان و ((وادي فرة))  ويسير إلى شكيم ثم وادي نديمة الذي يسير إلى عاي. ثم ((وادي القلت))  وهو يسر إلى أورشليم. أما أهم قسم في مجرى هذا النهر فهو القسم الواقع بين بحر الجليل والبحر الميت. ويحيط بالنهر في هذا القسم أشجار وشجيرات كثيرة وكثيفة وتدعى ((كبرياء الأردن))  أو ((غابة الأردن))  (أرميا 49: 19) وكانت الأسود تسكنها في أزمنة العهد القديم.ويسمى هذا الجزء الضيق الذي تملأه الغابات الكثيفة التي تحيط بانهر الآن ((الزور))  أما الوادي المتسع الذي يحيط بالنهر ويرتفع عن (( الزور))  فيسمى ((الغور)) .وفي الجزء الجنوبي من الغور حيث يمكن ري الأراضي رياً منتظماً، تنمو الفاكهة بوفرة وبكثرة فتنمو مثلاً أشجار النخيل والبرتقال في أريحا. وهذا هو القسم الذي اختاره لوط لسكنه بسبب خصوبة أرضه وكثرة إنتاجه(تك 13: 8- 13).وقد كان وادي الأردن في عصور الكتاب المقدس مزدحماً بالسكان أكثر مما هو الآن. وقد وجد على الجانب الشرقي من نهر الأردن ما لا يقل عن سبعين تلاً لمدن كانت آهلة بالسكان في العصور القديمة. ويرجع العهد ببعض هذه الأماكن التي في وادي الردن إلى أربعة آلاف سنة قبل الميلاد أو إلى ثلاثة آلاف عام على الأقل.وتوجد معابر في النهر حيث يمكن عبوره وهي تكثر في الشمال وتصل جنوباً إلى ((مخاضة يبوق))  حيث عبر يعقوب النهر(تك 32: 22) أما جنوبي مخاضة يبوق فتندر هذه المعابر ولا يمكن استخدامها إلا عندما ينقضي فيضان النهر.وقد عبر إسرائيل النهر بمعجزة بقيادة يشوع ونعلم من يش 3: 16 أن المياه المنحدرة من فوق وقفت وقامت سداً واحداً بعيداً جداً عن آدام. واسم آدام الآن هو دامية قرب جسر دامية، وهي قريبة من المطان الذي يتصل فيه نهر اليبوق بالأردن.وقد سجل التاريخ عدة مرات فيها سقطت قطع كبيرة من الأرض وأوقفت فيضان النهر إلى حين. وقد حدث مثل هذا عام 1267 ميلادية و1906و1927 ميلادية. ويظن بعض العلماء أن الرب جعل قطعة كبيرة من الأرض تسقط في مجراه فتمكن إسرائيل من عبوره.أما امتلاء النهر إلى شطوطه في وقت الحصاد فسببه ذوبان الثلوج التي على جبل حرمون.بعض الحوادث الكتابية المرتبطة بالأردن:يضاف إلى ما سبق من سكن لوط في سهل الأردن وعبور يعقوب للأردن وعبور بني اسرائيل النهر في أيام يشوع فقد عبر المديانيون الذين كان يطاردهم جدعون في معابر قريبة من مخاضة يبوق(قض 7: 24). ولما كان داود هارباً من أبشالوم ابنه في خروجه من أورشليم وكذلك في عودته إلى أورشليم عبر الأردن فيما بين أريحا ويبوق(2 صم 17 : 22 و24 و19 : 15- 18). وعبر إيليا وأليشع الأردن إذ ضرباه بطرف رداء إيليا(2 ملوك 2: 5- 8 و13- 15). وقد أوصى أليشع نعمان السرياني أن يغتسل في الأردن سبع مرات ففعل واغتسل بقرب بحر الجليل فشفي من برصه(2 ملوك 5: 14). وكان يوحنا المعمدان يعمّد في الأردن وقد ذهب إليه يسوع هناك واعتمد منه(مت 3: 6 و13 - 17).وفي بانياس أو قيصرية فيلبّس , أي بالقرب من ينابيع الأردن , نطق بطرس باعترافه أن يسوع المسيح هو ابن الله الحي(مت 16: 13- 16).'),
(199, 'أَرْدُون', 'اسم عبري معناه ((سنامي الظهر))  وهو اسم ابن كالب بن حصرون من سبط يهوذا(1 أخبار 2: 18).'),
(200, 'أَرْز', 'اسم سامي قديم من أصل ربما كان معناه ((ثابت))  أو قوي , قارنه مع الفعل ((أرز))  في العربية , وهو شجر لبنان دائم الخضرة وينتمي إلى الفصيلة الصنوبرية وثمره يشبه ثمر الصنوبر(1 ملو 5: 6 ) واسمه باللاتينية  cedrus  libani . وقد يصل ارتفاع الشجرة منه إلى ثمانين قدماً وقد يبلغ محيط جذعها أربعين قدماً. وربما امتدت أغصانها إلى محيط قدره ثلاثمائة قدم(اش 2: 13 , حز 17: 22 و31: 3) وخشبه جيد قابل للدهان وله رائحة عطرية(نش 4: 11) ويعمر أمداً طويلاً. وقد استخدم في بناء القصور والهياكل وصواري السفن(حز 27: 5) وكان يستخدم أيضاً في صنع التماثيل والصناديق(حز 27: 4و5) والآلات الموسيقية والتوابيت.وقد جلب داود وسليمان خشب الأرز من حيرام ملك صور(2 صم 5: 11و1 ملو5: 8) وكان يؤتى به طافياً إلى يافا. وقداستخدم خشب الأرز في بناء قصر داود(2 صم5: 11و7: 2) وقصرسليمان وبخاصة في بناء  ((بيت غاب لبنان))  (1ملو 7: 2و3) وكان الجزء الداخلي في هيكل سليمان من خشب الأرز(1ملو6: 20) ولما أصلح بناء الهيكل في أيام عزرا استخدم خشب الأرز في إصلاحه(عز 3: 7) وقد صدّر خشب الأرز في العصور القديمة من لبنان إلى مصر وبابل وآشور واليونان. ويظن البعض الأرز الذي استخدم في طقوس التطهير(لاويين14: 4 وعدد 19: 6) إنما كان نوعاً من الرتم الذي ينمو في البرية. وأكبر غابات الأرز في لبنان اليوم تحتوي على ما يقرب من أربعمائة شجرة وهي في الجبال قرب بشرّي فوق نبع قاديشة إلى الشرق من طرابلس في لبنان. ويظهر رسم شجرة الأرز على العلم اللبناني وكذلك يظهر على بعض أنواع طوابع بريد الجمهورية اللبنانية.'),
(201, 'أَرِسْتَرْخُس', 'ومعناه في الينونانية  ((خير حاكم))  وكان رجلاً مكدونياً من تسالونيكي , وأحد رفاق بولس الرسول. وقد خطفه الغوغاء إلى المسرح أثناء اضطراب أحدثوه من أجل الإِلاهة أرطاميس(أعمال 19: 29) وقد رافق بولس من اليونان إلى مكدونية. وتقدم الرسول إلى تراوس(أعمال20: 4و6) وقد سافر مع بولس إلى رومية(أعمال27: 2) وسجن مع الرسول فإنّه يذكره بولس في كو 4: 10 وفي رسالة فليمون عدد 24.'),
(202, 'أَرِسْتُوبُولس', 'كلمة يونانية معناها ((خير مشير))  وكان هذا الرجل يقطن رومية، وقد أرسل بولس تحياته إلى أهل بيته(رو16: 10) وقد ارتأى بعضهم أنه حفيد هيرودس الكبير، الذي يعرف عنه أنه عاش في رومية وأن بولس يرسل تحياته إلى خدم هذا الرجل.'),
(203, 'أَرسْطوس', 'انظر أراستس.'),
(204, 'أَرْصَا', 'معناه في العبرية  ((الرضا))  وكان هذا الرجل وكيلاً على بيت أيلة ملك إسرائيل. وقد قتل زمري أيلة بينما كان يحتسي الخمر في بيت أرصا في ترصة(1 ملو 16: 9).'),
(205, 'أَرْطَامِيس', 'إِلهة القمر عند اليونان. وهي العذراء أخت أبلو وكثيراً ما ترسم في شكل صياد. وقد أعطي هذا الاسم في أفسس لإلهة مختلفة جداً عن هذه وهي الإلهة الأم التي تشبه سيبيل. وكانوا يعتقدون ان تمثالها سقط من السماء(أعمال 19: 35) ويرجح أنه كان واحداً من النيازك المحترقة التي تسقط من الجو. ويظهر رسم التمثال على النقود التي وجدت في أفسس. ويظهر شكلها في الرسم بثدِيّ كثيرة وعلى رأسها تاج ويرتكز ذراعاها على دعائم. وكان هيكل أرطاميس الذي رآه بولس في أفسس خامس هيكل بني على هذا الموقع. أما الهيكل الرابع فكان يعتبر واحداً من عجائب الدنيا السبع. وقد خرب الهيكل الذي رآه بولس وبقي موقعه غير معروف غلى أن اكتشف في عام 1870 وقد وجد المنقبون في أساسات الهيكل هدايا ثمينة من فضة وذهب. وكان ديمتريوس وغيره من الصناع يصوغون نماذج فضية لهذا الهيكل(أعمال 19: 24) ويبيعون هذه لعباد هذه الآلهة.وقد اشتكي ضد بولس بأنه ينادي بأن أرطاميس وغيرها من الآلهة المماثلة لها ليست بآلهة حقاً. وكان هتاف الشعب في المسرح هو ((عظيمة هي أرطاميس الأفسسيين))  وقد وجدت صيغة هذا الهتاف مكتوبة في بعض النقوش التي اكتشفت في أفسس.'),
(206, 'أَرْفاد', 'وهي مدينة في آرام يرجح أن موضعها اليوم هو ((تل أرفاد))  على مسافة 13 ميلاً شمالي حماة، وتذكر عادة مع حماة في العهد القديم، ونقرأ في 2 ملو 18: 34، 19: 13 واش 10: 9 أن الآشوريين يفتخرون بأنهم أخذوا أرفاد. كذلك يذكر أرميا(ص 49: 23) الاضطراب الذي شاع فيها بسبب الأخبار السيئة التي وصلت إليها. ونجد في سجلات الآشوريين أنهم أخذوها في القرن التاسع قبل الميلاد وأنها ثارت ضدهم ولكنهم عادوا وأخذوها عدة مرات.'),
(207, 'أَرْفَكْشاد', 'ولا يعرف معنى الاسم بالتحقيق.(1)  كان هذا الرجل ابناً لسام(تك 10: 22و24) وقد ولد قبل الطوفان بسنتين. ولما صار عمره 35 سنة ولد له شالح. وقد مات بعد هذا بأربعمائة وثلاث سنين، وكانت سنه حينئذ أربعمائة وثمان وثلاثين سنة(تك 11: 10- 13) وقد ظن بعضهم أن قسماً من نهر الزاب شمالي شرقي نينوى قد سمي باسمه وقد ورد هذا الاسم في الآشورية بصور ((أرباخا)) .(2) وقد ورد اسم أرفكشاد اسماً لأحد ملوك مادي كما جاء في سفر يهوديت 1: 1.'),
(208, 'أَرَك', 'وردت صيغة الاسم في الآكادية هكذا ((أورك))  و ((أركو)) . وكانت مدينة في شنعار بناها نمرود كما نجد هذا في تك 10: 10.وتسمى في العربية ((وركة))  وهي مكان يبعد بمقدار 80 ميلاً شمالي غربي ((أور))  في العراق وقد كشف التنقيب في هذا المكان عن بقايا وآثار ترجع إلى عصور سحيقة، يعود بعضها إلى أربعة آلاف سنة قبل المسيح.وبين هذه الآثار أقدم منارة عالية لهيكل، وأقدم ختم اسطواني وأقدم بناء من الأحجار. وهذه أقدم آثار من نوعها اكتشفت في بابل. ونجد في الأسطورة البابلية أن ((أرك))  كانت موطن البطل ((جلجاميش))  الذي كان كنمرود صياداً ماهراً.'),
(209, 'أَرَكْوِيُّون', 'وهم سكان أرك وقد سباهم أسنفر ونقلهم إلى السامرة وأسكنهم هناك بعدما سبي العشرة الأسباط(عز 4: 9).'),
(210, 'أَرَكيّ وأركيون', 'وكان الأركيون قبيلة كنعانية تملك مدينة عطاروت بالقرب من بيت أيل على الحدود بين أفرايم وبنيامين(يش 16: 2) وقد ظن بعضهم أن هذا الاسم نسبة إلى بلدة غير معروفة كانت تدعى أرك. وكان حوشاي، مشير داود الأمين، أركياً(2 صم 15: 32).'),
(211, 'أَرْموني', 'الكلمة العبرية معناها ((متعلق بالقصر))  وكان اسم أحد أبناء شاول من سريته رصفة، وقد سلمه داود مع ستة آخرين من ابناء شاول إلى الجبعونيين فصلبوهم انتقاماً للهجوم الذي قام به شاول على جبعون(2 صم 21: 8).'),
(212, 'إِرْميا', 'ومعناه ((الرب يؤسس))  أو  ((الرب يثبّت)) وقد ورد في الكتاب المقدس في المواضع الآتية:(1)  اسم رجل بنياميني من الذين انضموا إلى داود(1 أخبار 12: 4).(2)  اسم رجل من سبط جاد من الذين انضموا إلى داود(1 أخبار 12: 10).(3)  اسم رجل آخر من سبط جاد انضم أيضاً إلى داود(1 أخبار 12: 13).(4)  اسم رجل كان رئيس بيت في سبط منسى من الذين سكنوا شرقي الأردن(2 أخبار 5: 24). وقد ورد اسم هؤلاء الأربعة بصيغة ((يرميا)) .(5)  اسم رجل من سكان لبنة، وكان أبا حمّوطل زوجة يوشيا الملك وأم يهواحاز(2 ملو 23: 30و31).(6)  أرميا بن حبصينيا وكان من الركابيين(أرميا 35: 3).(7)  رئيس كهنة رجع من بابل مع زربابل(نح 12: 1و7) وكان رئيس بيت سمي باسمه في الجيل الذي أعقبه(نح 12: 12) وقد ورد اسمه بصيغة ((يرميا)) .(8)  كاهن كان رئيس بيت من الذين وضعوا الختم على العهد ليبقوا في معزل عن الغرباء ويحفظوا شريعة الله (نح10: 2) وقد ورد اسمه أيضاً بصيغة ((يرميا)) .(9)  أرميا النبي العظيم. وهو ابن حلقيا الكاهن من عناثوث في أرض بنيامين(أرميا 1: 1) وقد دعاه الرب للقيام بالعمل النبوي في رؤية رآها وهو بعد حدث، فأحس بأنه لم يكتمل النضوج بعد، وبأنه قليل الخبرة وغير كفوء للقيام بهذا العمل العظيم ومخاطبة الرجال الذين يكبرونه سناً وخبرة ومركزاً فمدّ الرب يده ولمس فمه وقال له ((ها أنذا قد جعلت كلامي في فمك. انظر! قد أقمتك اليوم على الشعوب وعلى الممالك لتقلع وتهدم، وتهلك وتنقض، وتبني وتغرس)) . و أخبره الرب أيضاً بأنه سوف يلقى مقاومة من الحكام والكهنة والشعب ولكنهم سوف لا ينتصرون عليه (أرميا 1: 4- 10) وقد بدأ عمله النبوي في السنة الثالثة عشرة من ملك يوشيا وبقي يقوم بهذا العمل إلى أن أخذت أورشليم في الشهر الخامس من السنة الحادية عشرة من ملك صدقيا(أرميا 1: 2و3). لذا فقد دامت خدمته مدة الثماني عشرة سنة التي حكم فيها يوشيا، والثلاثة شهور التي حكم فيها يواحاز والاحدى عشرة سنة والخمسة شهور التي حكم فيها صدقيا. إذاً فجملة مدة خدمته كانت إحدى وأربعين سنة. وحتى ذلك الحين لم يكن قد توقف بعد عن القيام بعمله النبوي(أرميا ص 42- 44).وكان رجال عناثوث مواطنوه، في مقدمة من قاوموه، وهددوه إن لم يمتنع عن الاستمرار في عمله النبوي. ولكنه ثابر على القيام برسالته بالرغم من الاضطهاد. إلا أنه شعر بقوة وطأة هذه المقاومة لعمل الله والتي شنها عليه مواطنوه لذا فقد صرخ إلى الرب لكي يُنزل بهم قضاءه(ارميا 11: 18-21، 12: 3). أما العداء الذي ظهرت بوادره في عناثوث فقد ذاع وانتشر بعد حين  حتى أصبح عداءاً عاماً  مما ألجأه إلى أن يصرخ أيضاً طالباً من الرب أن ينزل قضاءه بالمقاومين(أرميا 18: 18- 23، قارنه أيضاً مع ص 20: 12) ولكنه بقي أميناً لمهمته بالرغم من كل مقاومة واضطهاد. وفي السنة الرابعة من ملك يهوياقيم أملى أرميا نبواته التي نطق بها مدة العشرين سنة السابقة، وكتبها باروخ الكاتب في درج. وأخبر أرميا باروخ أن يأخذ السفر إلى بيت الرب وأن يقرأه على من يأتون من الشعب إلى الهيكل في يوم الصوم. ووصل الدرج في النهاية إلى الملك الذي بعد أن استمع إلى بعض فقرات منه مزق الدرج قطعاً، ورماه في النار حتى احترق كله(أرميا 36: 1- 26) ولكن الرب أرشد ارميا أن يكتب درجاً ثانياً كالدرج الأول وزيدت عليه إضافات أخرى(ص 36: 27- 32). وقام واحد من أعدائه وهو الكاهن فشحور الناظر الأول للهيكل وضرب أرميا وجعله في المقطرة. ولكنه أطلقه في اليوم التالي(ص 20: 1- 3). وعندما كانت أورشليم محاصرة تدارست السلطات اليهودية نبوات أرميا الخاصة بتقدم الكلدانيين وسبي يهوذا الذي يعقب ذلك، ونظروا في هذه النبوات من الناحيتين السياسية والحربية بدلاً من أن ينظروا فيها من الناحية الدينية وعلى الصعيد الروحي. وادعوا عليه أن نبواته ضد يهوذا وأورشليم ثبّطت همم المدافعين عن المدينة. ولما رفع الكلدانيون الحصار إلى حين، وأوشك أرميا أن ينتهز هذه الفرصة للذهاب إلى عناثوث لبعض شأنه، اتهم بأنه فارّ ليذهب إلى الكلدانيين، وألقي في الجب (ص 37: 1- 15) وبعد أيام كثيرة أطلقه الملك صدقيا من حبسه وأخذه وسأله سراً عن كلمة الرب بشأنه فأخبره أرميا بأنه يدفع إلى ملك بابل. وأمر صدقيا أن يضعوا أرميا في دار السجن وأن يحسنوا معاملته بعض الشيء ولكن الرؤساء أخذوه ورموه في الجب ليموت جوعاً(ص 37: 16- 21و38: 1- 6) فأشفق عليه خصيّ حبشي واستأذن الملك في أن يرفع أرميا من وحل الجب فأذن له ورفعه وأخذه ووضعه في دار السجن. وكان هناك إلى أن أخذت أورشليم(ص 38: 7- 28) وقد علم الكلدانيون بما عاناه، واعتقدوا أنه قاسى كثيراً من أجلهم لذلك فقد أصدر نبوخذ نصر أوامر صريحة بأن يحسنوا معاملة أرميا ووفقاً لذلك أرسل نبوزردان الكلداني رئيس الشرطة إلى دار السجن وأخذوه وأحضروه إليه مع غيره من الأسرى إلى الرامة فأطلق سراحه ومنحه حق الاختيار في أن يذهب إلى بابل أو يبقى في وطنه فآثر أن يبقى في وطنه وأعطاه رئيس الشرطة زاداً وهدية وأطلقه فأتى إلى جدليا بن أخيقام إلى المصفاة وأقام عنده في وسط الشعب الباقين في الأرض(ص 39: 11- 14، 40: 1- 6) ولما قتل جدليا حثّ أرميا الشعب أن لا يهربوا إلى مصر ولكن عبثاً حاول إن يثنيهم عن عزمهم، ولم يذهبوا إلى مصر فحسب بل أرغموا أرميا على مرافقتهم في رحلتهم(ص 41: 1و43: 7) وقد نطق بنبواته الأخيرة في تحفنحيس في مصر(ص 43: 8-ص44: 30) ولا يعرف شيء عن موته ولا كيف كان ولا متى حدث ذلك.نبوات أرميا:تتجلى حياة أرميا الروحية في سفره بوضوح. ولقد كانت رسالته رسالة قضاة على شعبه ولذا فقد جلبت على رأسه مقت مواطنيه وبغضهم. واضطره ثقل حملها أن يتوجع بمرارة من أنه ولد(ص 15: 1، 20: 14- 18) ولكنه بقي أميناً لرسالته وللمهمة التي ألقيت على عاتقه. لقد كان رجلاً وحيداً، أُسيء فهمه وافتري عليه واضطهد وكان مصير الجهود التي بذلها لأجل مواطنيه الفشل، وكثيراً ما قاسى عذاب السجن ولم يكن له عزاء سوى في الله وحده. لقد اضطرته ظروف حياته أن يلقي نفسه على الله، لذا فأمكنه أن يقدر عن بصيرة ومقدرة الشعور بالمسؤولية لله(ص 17: 9، 31: 29و30) ولذا فإننا نجد في سفر أرميا قوة الشعور بمسؤولية الفرد لله وحقيقة الشركة والاتصال بين النفس البشرية والله.والديانة بحسب مناداة أرميا هي ديانة القلب والحياة. لقد دعي للاضطلاع بعمله النبوي لخمس سنوات قبل اكتشاف سفر الشريعة في الهيكل أثناء إجراء بعض الإصلاحات في البناء . وكان لكلمات السفر أثر قوي في قلب الملك يوشيا. فقام بحرب شعواء على العبادة الوثنية وأجرى إصلاحات دينية كثيرة. فسرت في الشعب نهضة مباركة وعاد إلى عبادة الرب. وكان أرميا في تلك الأثناء يقوم بعمله النبوي على خير وجه فكان يحث الشعب على الطاعة مذكراً إياه بالعهد الذي عمله الرب معه وبأن الشر الذي أصاب الشعب لم يأت جزافاً بل حلّ بالشعب نتيجة عصيانه. وأبان لهم أن الطاعة هي أولى مطالب هذا العهد(أرميا 11: 1- 8) ولقد حذر أرميا قومه من أن يقتصر الإصلاح على الأمور الخارجية، بل ينبغي أن يصل إلى أعماق الحياة الداخلية، أي إلى القلب نفسه. وأعلن لهم في صراحة بأن الرب لم يطلب منهم الذبائح فحسب بل إنه يتطلب من الإنسان الاستماع والطاعة. وإرادة الله هي أن يحيا الناس حياة خُلُقِيّة رفيعة(ص 7: 21- 28 قارنه مع ص 6: 20و14: 20) والذبائح التي يرضى الله عنها هي ذبائح المستمع المطيع(ص 17: 24- 26، 27: 19- 22، 33: 10و11و18) أما صوم الذين يميلون إلى الزيغ عنه، وذبائحهم فغير مقبولة لديه(ص 14: 10- 12) وتواكل الشعب واستهتاره، وهم يزعمون أن الرب حاضر في وسط الهيكل وبين شعبه فبطل وبهتان. وكذلك الارتكان على أن شريعة الرب في حوزة الشعب. فلا نفع إلا في الطاعة(ص 7: 4- 7و8: 7- 9) وينتج عن ذلك أن سيأتي وقت لا يذكر التابوت فيه(ص 3: 16) والله إنما ينظر إلى القلب فحسب(ص 11: 2و17: 10و20: 12) فعلى الإنسان والحالة هذه أن ينتزع من قلبه الشهوات الجسدية إن أراد أن يعبد الرب بالحق وأن يخدمه الخدمة التي تليق به وعليه أن يغتسل من شره وأن يرجع إلى الرب من كل القلب(ص 3: 10و4: 4و14و17: 5). وقد أنبأ أرميا بالعهد الجديد حين يكون للشعب قلب جديد وتكتب شريعة الرب في هذا القلب(ص 24: 7و31: 33و32: 39و40). ولقد وصف في رؤياه مجد المملكة العتيدة. ولذ فللحقائق التي أدلى بها مكانة راسخة، وقيمة أدبية في قلوب شعب الله.وقد دونت بعض نبؤات أرميا أثناء حكم يهوياقيم ولكن الملك مزّق الدرج وأحرقه(ص36: 1و23) ولكن لم يمض وقت قصير حتى دونت مرة ثانية وزيد عليها نبوءات أخرى كثيرة(ص36:32) والسفر في وضعه الراهن يشمل تلك النبوءات , وكذلك ما نطق به النبي من نبوات بعد ذلك. وقد أعيد ترتيبها وتمّ إعدادها قرب ختام خدمة النبي .محتويات السفر:يحتوي سفر أرميا على مقدمة تسرد دعوة النبي للاضطلاع بعمله. وكيف كانت(ص1) ويشمل السفر أيضاً ثلاثة أقسام نبوية مرتبة بحسب الحوادث التي دعت إلى النطق بهذه النبوات(ص2: 51) ويختتم السفر بخاتمة تاريخية(ص52).أما الأقسام النبوية الثلاثة فهي :(1) إِنباء بالقضاء الوشيك أن يحل بيهوذا والوعد بالرجوع من السبي(ص2- 33) ويشمل هذا القسم :أ: إعلان القضاء على يهوذا بوجه عام , بسبب شروره(ص2- 20).ب: إعلان القضاء على الحكام المدنيين والرؤساء الدينيين(ص21- 23).ج: إعلان الخطة التي سيتَّبعها القضاء والزمن الذي يستغّرقه(ص24- 29).د: نبوة بالبركات التي تتبع القضاء (ص30- 33).(2) تاريخ وقوع القضاء(ص 34- 44) ويشمل :أ: إعلان القضاء على الفساد الذي كان متفشياً قبل خراب المدينة مباشرة( ص 34- 38).ب: بيان بالخراب الذي حلَّ بأورشليم وكيف كان وقوعه عليها. (ص 39) .ج:  حالة الشقاء التي كان عليها من بقي من السكان في البلاد والنبوات التي نطق بها النبي بشأنهم(ص 40- 44).(3)  نبوات على الأمم الغربية(ص 46- 51) ويمهد النبي لهذه النبوات بخطاب يوجهه إلى باروخ(ص 45).وقد تحدث النبي عن المسيا في(ص 23: 5- 8و30: 4- 11و33: 14- 26) وكذلك تحدث عن عهد الرب الراسخ والثابت بين الرب وشعبه في (ص 3: 31- 40و32: 36- 44وص 33).ويمكن ترتيب نبوات أرميا تاريخياً كالآتي:(أ) نبوات نطق بها في أثناء حكم يوشيا الملك وقد حكم يوشيا إحدى وثلاثين سنة. وبدأ النبي الاضطلاع بمهمته النبوية في السنة الثالثة عشرة لملك يوشيا وهذه النبوات مدونة في الإصحاحات 1- 12و14- 20.  يذكر الوحي أن النبي نطق بأية نبوة في أثناء حكم يهوآحاز ومدته ثلاثة أشهر.(ب) نبوات نطق بها أثناء حكم يهو ياقيم ومدته إحدى عشرة سنة. وهي مدونة في الإصحاحات22: 1- 19و25و26و35و36 وجزء من ص 45 وجزء من ص 46.(ج) نبوات نطق بها أثناء حكم يهوياكين ومدته ثلاثة أشهر وهي مدونة في ص 13 والجزء الأخير من ص 22.(د) نبوات نطق بها أثناء حكم صدقيا ومدته أحد عشر عاماً وهي مدونة في الإصحاحات 21و24و27و28و29 و32و34و37و39 وجزء من ص 49 وجزء من ص 51.(هـ) نبوات نطق بها في يهوذا بعد سقوط أورشليم وهي مدونة في جزء من ص 39 وفي ص 40: 1- 43: 7.(و) نبوات نطق بها في مصر وهي تشمل جزءاً من ص 43 وكل ص 44.(ز) نبوات لم يذكر لها تاريخ ولكن فيها ما يمكن أن يستدل منه على الزمن الذي قيلت فيه على وجه التقريب وهي مدونة في الإصحاحات 23و30و31 وجزء من ص 45 وجزء من ص 46 وص 47- 50 وجزء من ص 51.(ح) خاتمة، ص 52.والسفر هو الرابع والعشرون بين أسفار العهد القديم. وهو أهم مرجع لدينا عن تاريخ الربع الأخير من القرن السابع وأوائل القرن السادس قبل الميلاد. وأسلوب أرميا سهل العبارة سهل الفهم دقيق اللفظ يمثل لنا عصره والظروف التي جاز فيها فيذكر المنطقة لشدّ الحقوين(ص 13: 1- 11) وزق الجمر(ص 13: 12 وما بعده) والفخاري(ص 18: 1- 11) وابريق الفخار الذي تكسر(ص 19: 1- 3) وسلال التين(ص 24: 1- 10) وفي أسلوبه الكثير من فحص النفس وامتحانها ممّا يدل على تقدم عظيم في الفكر الديني وانتقاله من النظر إليه من الناحية القومية إلى اعتباره شيئاً يتعلق بالفرد وعلاقته بالله وأصبح للفرد قيمته وشخصيته المستقلة. فربما يزول الهيكل وتنتهي العبادة الرسمية للأمة كأمة ولكن يستطيع الفرد في أي مكان وفي أي وقت أن يرتفع إلى السموات العلى في الشركة مع الله. وقد حطمت نبواته صنم وحدة وتماسك ونجاح وانتصار إسرائيل بزعمه أنه شعب الله المختار. فشعب الله في كل أمة تتقيه. فبذلك ارتفع الفكر الديني من ميدان القومية الضيقة المحدودة إلى آفاق السمو الروحي ، فكل فرد يعبد الرب العبادة الحقة ويسلك السلوك الذي يرضيه يقبل لديه بغض النظر عن جنسه وقومه وأمته ولونه. هذه الديانة التي يعتنقها الفرد بعد أن يتوب إلى الرب ويرجع إليه تصبح الأساس الذي يبنى عليه العهد الجديد الذي يكتب على القلب بين الإنسان البشري والرب في السماء. (انظر أرميا 31: 31 وما بعده وص 34 ومواضع أخرى كثيرة في السفر).مراثي أرميا:اسم هذا السفر بالعبرية ((أيكا))  ومعناه ((كيف))  وهي أول كلمة في السفر وهو عبارة عن مجموعة خطابات رثاء تشبه الرثاء الذي نطق به داود توجعاً على شاول الملك، وابنه يوناثان لما سقطا على جبل جلبوع(2 صم 1: 17-27). و ((مراثي أرميا))  أحد أسفار العهد القديم وقد ورد في الكتاب المقدس بعد سفر أرميا ولكن نجده في الأصل العبراني في القسم الثالث من أسفار العهد القديم المسمى  ((كتوبيم))  أو ((الكتب))  وقد ورد بعد الجامعة وقبل أستير.ومما يجدر ملاحظته أن عدد أعداد كل من الإصحاحات 1 و 2 و4 و5 هو 22 عدداً أما الإصحاح الثالث ففيه 22 عدداً * 3 أي 66 عدداً. ونعلم أن في اللغة العبرية اثنين وعشرين حرفاً. وأعداد الإصحاحات 1 و 2 و4 تسير مرتبة بحسب حروف الأبجدية العبرية فالعدد الأول من الإصحاح يبدأ بكلمة أوله ((أَليف))  والثاني يبدأ بكلمة أولها حرف ((بيت))  والثالث يبدأ بكلمة أولها حرف ((جيمل))  وهلم جرا.أما في الإصحاح الثالث فالثلاثة الأعداد الأولى تبدأ بكلمات أولها حرف ((أَليف))  والثلاثة الأعداد الثانية تبدأ بكلمات أولها حرف ((بيت))  والثلاثة الأعداد الثالثة تبدأ بكلمات أولها حرف ((جيمل))  وهلم جرا. أما الإصحاح الخامس فلا يسير في ترتيب أعداده على هذا النظام الأبجدي.وموضوع هذا الرثاء هو غزو أورشليم وخرابها والآلام المروعة المرعبة التي قاساها المدافعون عنها في وقت الحصار من جوع وسيف. ويعلن الرثاء في صراحة أن خطايا الشعب كانت سبب الكارثة الدهماء التي حلت به. فما نزل بأورشليم وما أصاب شعبها كان نتيجة حتمية للتمرد على الله وعصيانه. وقد أبدع الكاتب في وصف الحوادث أيّما إبداع بحيث يخيَّل للقارىء أنه يرى هذه الكوارث تقع بأورشليم أمام عينه. وقد اتفق النقاد على أن مراثي أرميا هي من أبدع وأروع ما كتب في العالم من رثاء. وقد يخيّل للقارىء أنها كلمات دبجتها أقلام من نار بمداد من دموع.ولم يذكر في الكتاب المقدس اسم مؤلف هذا السفر غير أن التقليد جرى على أن أرميا هو مؤلف هذه المراثي. فقد جاء في الترجمة السبعينية، وفي فاتحة هذا السفر هذا القول:  ((وكان بعد سبي إسرائيل وخراب أورشليم أن جلس أرميا يبكي ورثا أورشليم بهذا الرثاء وقال))  لذا فقد نسب السفر إلى أرميا من زمن بعيد جداً. وقد أخذت الترجمات القديمة وكتب التقليد كالفلجات والترجوم والتلمود وغيرها بهذا الرأي. وقد اتفق رأي العلماء على أن السفر كتب بعد سنة 586 قبل الميلاد. أي بعد خراب أورشليم مباشرة. ومن يدرس سفر أرميا وهذا السفر لا يمكنه إلا أن يرى بوضوح التشابه العظيم في الروح والأحاسيس والعبارات والاصطلاحات.وقد ورد في 2 أخبار 35: 25 أن أرميا  رثى يوشيا الملك وكن من الواضح انه لا توجد علاقة بين ذلك الرثاء وهذه المراثي.رسالة أرميا:هذه الرسالة من ضمن الأسفار المحذوفة  ((الأبو كريفا))  وقد وردت في الترجمة السبعينية رسالة منفصلة قائمة بذاتها. أما في الترجمة اللاتينية الفلجات، والترجمة العربية اليسوعية فتجعلان الرسالة الاصحاح السادس في باروخ. وبما أن هذه الرسالة لم ترد في الكتب القانونية في العبرية، ولا صلة لها بأرميا ولم يكتبها هذا النبي العظيم لذا فلم تحسبها المذاهب المصلحة ضمن الأسفار القانونية. وقد كتبت أصلاً بالآرامية، كتبها واحد من اليهود في القرن الثاني أو الثالث ق.م. وقد فقد الأصل الآرامي وبقيت الترجمة اليونانية. وهذا الكتيب الصغير عبارة عن رسالة يدحض فيها الكاتب العبادة الوثنية ويسخر من الغباء الذي فيها. وهي في هذا تشبه الإصحاح العاشر من سفر أرميا.'),
(213, 'أُرْنان', '(1 أخبار 21: 15) انظر أرونة.'),
(214, 'أرنب', 'واسمه في العبرية ((ارنبث))  وهو حيوان معروف اسمه باللاتينية lepus ويعتبر أكله محرماً بحسب شريعة الأطعمة عند العبرانيين لأنه يجترّ ولكنه غير مشقوق الظلف(لاويين 11: 6 وتثنية 14: 7) على أن الأرنب ليس حيواناً مجترَّاً عبد التحقيق . إنما لأنه يحرك قواطعه وشفته العليا حركة خاصة نسبيّة بحركة شفتي الحيوانات المجترة، لذا فقد ظنه البعض أنه من ضمنها. والاسم اللاتيني للأرنب الذي موطنه فلسطين هو Lepus Syriacus على أنه قد لُوحظ أنه يوجد أربعة أنواع أخرى من الأرانب في فلسطين وسيناء. ويعتبر لحم الأرنب من الأطعمة المفضلة عند العرب .'),
(215, 'أَرْنُون', 'كلمة عبرانية معناها  ((الزئير))  , وهو اسم لنهر يدعى اليوم ((وادي الموجب))  في المملكة الأردنية الهاشمية. ويتكون من وادي  ((وله))  الذي يأتي من الشمال الشرقي ، ووادي  ((عنقيلة))  الآتي من الشرق ((وسيل الصعدة))  الآتي من الجنوب. هذه هي أودية أرنون(عدد 21: 14) ويجري نهر أرنون في غور عميق حتى يصل إلى البحر الميت في نقطة تقع إلى مسافة قصيرة من منتصف الشاطىء الشرقي. وكان الأرنون في عصر موسى الحد الفاصل بين الموآبيين في الجنوب والأموريين في الشمال (عدد 21: 13و26) . وكان الأرنون في عصر القضاة الحد الجنوبي لسبط روابين ، الذي يفصل بينهم وبين موآب(تثنية 3: 8و16 ويشوع 13: 16). ويخبرنا الحجر الموآبي أن ميشع ملك موآب في القرن التاسع قبل الميلاد وسع تخومه شمالي الأرنون. وتقع مدينة عروعير على الشاطىء الشمالي للنهر. ويقع بالقرب منها مكان قديم للعبور وربما كان هذا هو المكان الذي أطلق عليه  ((معابر أرنون))  (أشعياء 16: 2).'),
(216, 'أرْوَاد', 'وقد يكون معناه ((تيه))  وربما هي أرفاد وتسمى الآن رواد، وتقع على جزيرة صغيرة تبعد مسافة ميلين من الشاطىء السوري على بعد 30 ميلاً تقريباً شمالي طرابلس. وقد ورد في تك 10: 18 أن الأرواديين من نسل كنعان . وكانت أرواد مدينة فينيقية للسفن والتجارة مثل صور وصيدا. ويخبرنا سفر حزقيال(ص 27: 8و11) أن أرواد أرسلت ملاحين ومحاربين للدفاع عن صور. وتحدثنا السجلات الآشورية أن أرواد اشتركت مع دمشق وإسرائيل في حرب ضد آشور في معركة قرقر سنة 854 قبل الميلاد.'),
(217, 'أرْوادي', 'أي ساكن جزيرة أرواد(تك 10: 18).'),
(218, 'أَرُود أو أَرُودي', 'الفظ العبري معناه ((أحدب)) . وكان أرود ابناً لجاد وأباً للأروديين(عدد 26: 17) وقد ورد ذكر هذا الرجل نفسه في تك 46: 16 باسم  ((أرودي)) .'),
(219, 'أرُومة أو رومة', 'واللفظ العبري ربما كان معناه ((ارتفاع)) . وهي بلدة أقام فيها أبيمالك وهو يتأهب لغزو شكيم المدينة المجاورة لها (قض 9: 41) وقد ظن بعضهم أن موضعها هو ((الأُرمة))  الحديثة وهي على بعد ستة أميال شمالي شرقي شكيم.'),
(220, 'أرُوَنة وأُرْنَان', 'ولا يعرف معنى اللفظ العبري على وجه التحقيق. وهو اسم لرجل يبوسي اشترى داود بيدره على جبل الموريا. وعلى أرض البيدر بنى داود مذبحاً للرب. وقد عمل ذبيحة حتى يقف الوباء(2 صم 24: 18- 25) (1 أخبار 21: 15- 28). وفي نفس هذا الموقع بنى سليمان الهيكل(2 أخبار 3: 1) ويحتمل أن هذا البيدر كان على الصخرة التي تقوم عليها قبة الصخرة. وقد ورد الاسم بصيغة (( أرونة))  في صموئيل وبصيغة ((أرنان))  في أخبار الأيام، وهما من أصل واحد.'),
(221, 'أَرِيئِيل', 'وقد اختلفت الآراء في معنى اللفظ العبري لهذه الكلمة فمن قائل أنها تعني ((موقد مذبح الله))  أو  ((موقد مذبح))  أو ((أسد الله))  أو ((جبل الله))  وقد ورد هذا الاسم في الكتاب المقدس:(1)  اسم رجل من اليهود الذين رجعوا من سبي بابل مع عزرا. وقد أرسله عزرا مع آخرين غلى كسيفا ليأتوا بخدام للهيكل في أورشليم (عزرا 8: 15- 17).(2)  اسم أطلق على أورشليم (أش 29: 1و2و7).'),
(222, 'أرْيَة', 'كلمة عبرية معناها  ((أسد ))  اسم رجل اغتيل مع فقحيا بن منحيم ملك إسرائيل اغتالهما فقح بن رمليا(2 ملوك 15: 25).'),
(223, 'أريِحا', 'معناها ((مدينة القمر))  أو ((مكان الروائح العطرية)) . وهي مدينة ذات أهمية عظمى، تقع على مسافة خمسة أميال غربي نهر الأردن وعلى مسافة سبعة عشر ميلاً شمال شرقي أورشليم. أما أريحا التي ورد ذكرها في العهد القديم فموضعها تل السلطان، الذي يقع على بعد مسافة ميل من مدينة أريحا الحديثة التي تدعى الآن ((الريحا))  وتلول أبو العليق التي تقع على مسافة ميل غربي أريحا الحديثة هي بقايا الحي الراقي الغني من أريحا في عصر العهد الجديد. وتقع أريحا في منخفض يبلغ 825 قدماً تحت مستوى سطح البحر ولذا فجوها حار. وقد ساعدت المياه الجارية من نبع السلطان ومن وادي القلت على جعل الراضي المحيطة بأريحا خصبة. وقد اشتهرت منذ عصور قديمة بزراعة شجر النخيل(تثنية 34: 1و3 وقض 3: 13). وفي العصور الحديثة الموز والبرتقال والورد(سيراخ 24: 14) وأشجار الجميز(لوقا 9: 14) والبلسم وكثير من أشجار الفاكهة.وقد ورد ذكر هذه المدينة كثيراً في الكتاب المقدس لأنها كانت تتحكم في الوديان الذاهبة إلى عاي وأورشليم. وكانت أول مدينة هاجمها الإسرائيليون، غربي الأردن. وقد أرسل يشوع جواسيس إلى هذه المدينة وقد أخفتهم راحاب الزانية(يشوع 2: 1- 24) ووفقاً لأمر الرب سار المحاربون من إسرائيل صحبة سبعة من الكهنة حاملين أبواقاً وتابوت العهد، وقد طاف هؤلاء بالمدينة مرة في اليوم لمدة ستة أيام. وفي اليوم السابع طافوا حولها سبع مرات وضربوا بالأبواق وهتفوا هتافاً عالياً فسقطت أسوار المدينة. ويظن البعض أن الرب استخدم زلزلة من الزلازل التي كثيراً ما تحدث في تلك البقعة. وقد ذبح الإسرائيليون جميع سكان أريحا، ما عدا راحاب وأسرتها وخصصوا الأشياء الثمينة فيها للرب.(يشوع ص 6) وقد أخذ عاخان بعض هذه الأشياء الثمينة من أريحا لنفسه وكلن من نتيجة هذاأن انهزم الإسرائيليون أما هو وبيته فرجموا(يش ص 6). وقد أعطيت أريحا ضمن نصيب بنيامين وكانت على الحدود بين بنيامين وأفرايم(يش 16: 1و7، 18: 12 و21) وكان عجلون ملك موآب يسكن قصراً في أريحا لما أذل الإسرائيليين (قض 3: 13) . وقد أقام رسل داود الذين حلق ملك عمون لحاهم، في أريحا إلى أن نمت لحاهم(2 صم 10: 5 و1 أخبار 19: 5) وفي أيام آخاب حصّن حيئيل البتئيلي أريحا ولكنه فقد ابنيه وفقاً لنبوة يشوع(1 ملوك 16: 34 قارنه مع يش 6: 26) وقد زار إيليا وأليشع جماعة الأنبياء في أريحا قبل انتقال إيليا، ورجع أليشع إلى هؤلاء الأنبياء . والنبع الذي أبرأه أليشع هو ما يرجّح عين السلطان(2 ملو ص2) وقد أطلق سراح أسرى يهوذا الذين أخذهم جيش إسرائيل ، بقيادة فقح بن رمليا في أريحا(2 أخبار 28: 15) . وبالقرب من أريحا قبض البابليون على صدقيا الملك (2 ملو 25: 5 وأرميا 39: 5، 52: 8) وقد رجع مع زربابل من السبي 345 من سكان أريحا السابقين ونسلهم (عزرا 2: 34 ونح 7: 36) وقد ساعد بعض من هؤلاء في بناء سور أورشليم(نح 3: 2).وقد بنى هيرودس الكبير قلعة بالقرب من أريحا، وفي النهاية مات هناك. وفي عصر العهد الجديد كانت فرقة من الكهنة تسكن أريحا. ولا بد أنهم كثيراً ما كانوا يسافرون في الطريق الموصل من أورشليم إلى أريحا كما ذكر في مثل السامري الصالح(لو 10: 30و31) وقد أعاد يسوع البصر لبارتيماوس الأعمى ورفيقه في أريحا(مت 20: 29 ومر 10: 46 ولو 18: 35). وقد زار المسيح بيت زكا جابي الضرائب في أريحا وقد تاب زكا ورجع إلى الرب بعد ما زار يسوع بيته(لو 19: 1- 10).وقد أثبتت الكشوف التي أجريت في تل السلطان على أن أريحا من أقدم مدن العالم وترجع إلى العصر الحجري في الألف سنة السادسة قبل الميلاد. وقد اكتشف هنا أقدم فخار وأقدم نحت في العالم. وقد اكتشفت أيضاً أسوار أريحا التي سقطت في أيام يشوع وقد اسودت جدرانها من الحريق. وقد قدر بعض العلماء على أن ذلك الخراب الذي حلّ بأريحا حدث عام 1400 ق.م. ويقول آخرون أنه حدث في عام 1350 ق. م. وقد كشف المنقبون في تلول العليق عن قصر هيرودس الكبير الذي يظهر واضحاً من جبل التجربة(مت 4: 8) وقد وجدت هنا منازل مترفة شبيهة بالمنزل الذي كان يملكه زكا( لو 19: 1- 9).عربات أريحا أو  ((سهول أريحا)) :وقد أطلق هذا الاسم على الأراضي الواقعة بين أريحا ونهر الأردن(يش 4: 13) وكانت مدينة الجلجال ، حيث حلّ الإسرائيليون في دخولهم أرض كنعان ، في هذه البقعة(يش 5: 10).'),
(224, 'أَرِيداثا', 'اسم فارسي وربما كان معناه ((عطية هاري))  وهو الابن السادس بين أبناء هامان(أس 9: 8).'),
(225, 'أَرِيدَاي', 'اسم فارسي ربما كان معناه ((بهجة هاري))  وهو الابن التاسع بين أبناء هامان(أس 9: 9).'),
(226, 'أَرِيساي', 'اسم فارسي لا يعرف معناه . وهو اسم الابن الثامن بين أبناء هامان(اس 9: 9).'),
(227, 'أرْيوباغي', 'النسبة لأريوس باغوس، وهو لقب يعطى للقاضي في محكمة أثينا وكان ديونيسيوس الذي آمن بواسطة كرازة بولس يحمل هذا اللقب(أعمال 17: 34).'),
(228, 'أرْيوخ', 'اسم ربما كان من السومرية ولفظه فيها ((أري أكو))  ومعناه ((عبد اله القمر))  وهو رئيس شرطة الملك نبوخذ نصر ملك بابل(دانيال 2: 14) وقد كلف بإبادة حكماء بابل الذين لم يستطيعوا أن يفسروا حلم الملك(دانيال 2: 24) وهو الذي أحضر دانيال إلى حضرة نبوخذ نصر ليخبره بالحلم وبتفسيره(دا 2: 25). ولفظ هذا الاسم في العبرية هو نفس لفظ الاسم ((أريوك)) .'),
(229, 'أريوس باغوس', 'معناه في اليونانية ((تل س))  وهو اله الحرب أو ((تل اللعنات))  وهو تل في أثينا قريب من التل الكبير الذي يسمى الأكربول، من جهة الغرب. وكان يسمى تل اللعنات بسبب الأقسام التي كانوا يقسمون بها أمام المحكمة التي كانت تعقد على هذا التل . وكانت محكمة أثينا العليا تسمى أريوس باغوس لأنها كانت تلتئم على هذا التل لتصدر أحكامها. وكانت هذه المحكمة تنعقد في أيام بولس لإجراء المحاكمات في  ((ستوا باسليوس))  في  ((الأجورا))  بجانب تل أريوس باغوس. وربما يشير اسم أريوس باغوس المذكور في أعمال 17: 19و22 إلى هذه المحكمة التي كانت تشرف على الشؤون الدينية. وربما يستفاد من هذه الأعداد أن بولس ألقى خطابه على تل أريوس باغوس . وهذا ما سار عليه التقليد المسيحي في أثينا.'),
(230, 'أرْيُوك', 'معناه نفس معنى أريوخ فانظره.وهذا اسم ملك الأسار وهو واحد من الملوك الذين تحالفوا مع كدرلعومر ملك عيلام لغزو وادي الأردن في عصر إبراهيم(تك 14: 1). والأسار هذه هي لارسا في بابل ويعتقد البعض أن أريوك هذا هو نفس ((وراد سن))  الذي حكم تلك المدينة من سنة 1836- 1824ق. م. أو أنه هو نفس رمسن، الذي حكم من سنة 1824- 1763 ق. م.'),
(231, 'أَزْباي', 'ومعناه غير معروف. وهو اسم أبي نعراي أحد أبطال جيش داود(1 أخبار 11: 37) ويحتمل أن هذا هو نفس الرجل المذكور باسم  ((الأربي))  في 2 صم 23: 35.'),
(232, 'أزْرَاحي', 'النسبة إلى زارح وهو اسم أطلق على على اثنين من أبناء زارح، ايثان(1 ملوك 4: 31، وعنوان مز 89 قارنه مع أخبار 2: 6) وهيمان (عنوان مز 88 قارنه مع 1 أخبار 2: 6).'),
(233, 'أَزَّيْن شيرَة', 'اسم عبي معناه ((نصيب شيرة))  وهو اسم لقرية بنتها شيرة ابنة رجل من أفرايم اسمه أفرايم أيضاً (1 أخبار 7: 24) وقد ورد ذكرها مع بيت حورون السفلى والعليا. ومكانها اليوم  ((بيت سيرة))  على بعد ثلاثة أميال جنوبي غرب بيت حورون السفلى.'),
(234, 'أَزْنُوت تابور', 'عرارة وهي عبرية ومعناها  ((أذان))  أو  ((منحدرات تابور))  وهي موضع يقع على حدود نفتالي بالقرب من جبل تابور(يش 19: 34) ويعتقد البعض أن مكانها اليوم هي قرية ((أم جبيل))  الواقعة بالقرب من جبل تابور.'),
(235, 'أزْني', 'اختصار أزنيا ومعناه ((الرب يسمع))  وهو اسم أحد أبناء جاد وأبو الأزنين (عدد 26: 16) ويدعى  ((أصبون))  في تك 46: 16.'),
(236, 'أَزَنْيا', 'اسم عبري ومعناه ((الرب يسمع))  وهو لاوي، وأبو يشوع الذي كان واحداً ممن ضمنوا العهد في أيام نحميا(نح 10: 9).'),
(237, 'إِسْبانيا', 'أطلق هذا الاسم في العصور القديمة على كل شبه جزيرة إيبيرية التي تشمل في العصور الحديثة اسبانيا والبرتغال. ويرجح أن ترشيش التي كان يونان يقصد الذهاب إليها (يونان 1: 3) هي طرطوسة، وكانت مستعمرة فينيقية في إسبانيا بالقرب من جبل طارق. وكانت إسبانيا مشهورة بمناجمها التي يستخرج منها الذهب والفضة(1 مكابيين 8: 3). وقد أراد الرسول بولس أن يزور إسبانيا (رومية 15: 24و28) ويحدثنا التقليد المسيحي الذي جاءنا من العصور المسيحية الأولى أن الرسول تمم هذه الزيارة بعدما أطلق من سجنه في رومية. فآخر ما يسجله لنا سفر الأعمال عن بولس أنه أقام سنتين كاملتين في بيت استأجره لنفسه.'),
(238, 'أُسْبُوع', 'الكلمة العبرية هي ((شَبُوَع))  وهي من أصل معناه سبعة وقد استخدم العبرانيون تقسيم الوقت إلى أسابيع في عصر مبكر جداً في تاريخهم فالخلق تمّ في أسبوع من سبعة أيام(تك 1: 1- 2: 3) وكذلك ورد ذكر الأسبوع كثيراً في قصة الطوفان(تك 8: 8و10و12). وكانت حفلات الزواج تستغرق سبعة أيام(تك 29: 27و28). وكانت المآتم كذلك تقام لمدة سبعة أيام(تك 50: 10). وكان الأسبوع ذا أهمية بالغة في الشريعة العبرية الطقسية(خروج 12: 15و13: 6و7و22: 30و29: 30و35و37 ولاويين 12: 2و13: 5و14: 8 الخ) ولم يطلق العبرانيون أسماء على أيام الأسبوع فيما عدا اليوم السابع الذي أطلقوا عليه اسم السبت (انظر سبت).وكان العبرانيون يقدسون مدة من الزمن تقسم إلى سبعات من السنين وتختتم بالسنة التي هي سبت السنين(لاويين 25: 3و4) ويعتقد أغلب العلماء أن الكلمة أسبوع المستعملة في دانيال 9: 24- 27 تعني مدة من سبع سنوات.أما الكلمة اليونانية في العهد الجديد فهي كلمة ((سبّاتون))  وهي كما هو واضح مشتقة من  ((سبت))  وتعني مدة الزمن الممتدة من سبت إلى السبت الآخر (مت 28: 1) وفي القرن الأول الميلادي كان العبرانيون يطلقون أسماء الأعداد على أيام الأسبوع كأول الأسبوع وما شابه ذلك (مت 28: 1وأعمال 20: 7) وكانوا أحياناً يلقبون اليوم السابق ليوم السبت بيوم الاستعداد(مر 15: 42).وقد سار البابليون أيضاً على اعتبار الأسبوع وحدة الزمن عندهم، وذلك على حسابهم الزمن لبعض فروضهم وطقوسهم الدينية. وتشير قصة الطوفان عند البابليين عدة مرات إلى دورة من الزمن قوامها سبعة أيام، كما ورد في قصة الطوفانفي العهد القديم. أما المصريون فكانوا يحسبون الدورة عشرة أيام. وقد قسم اليونان الشهر إلى ثلاثة أقسام. أما الرومان فكانت دورة الزمن عندهم ثمانية أيام ولكنهم في القرن الثاني الميلادي أخذوا باستخدام أسبوع مكوَّن من سبعة أيام.'),
(239, 'إِسْتَاخِيْس', 'اسم يوناني معناه ((سنبلة قمح))  وهو اسم لمسيحي في روما أرسل بولس إليه تحياته وكان من أحباء بولس(رومية 16: 10).'),
(240, 'إِستِفَاناس', 'اسم يوناني معناه ((متوّج))  وهو اسم لمسيحي في كورونثوس وكان هو واهل بيته أول الثمار الروحية لكرازة بولس في أخائيا (1 كو 16: 15) وقد عمدهم الرسول بنفسه(1 كو 1: 16) وقد خدموا حاجات القديسين (1 كو 16: 16) وقد ذهب إستيفاناس مع فورتوناتوس لزيارة بولس في أفسس(1 كو 16: 17) ويحتمل أنهما حملا معهما ريالة الرسول الأولى إلى الكورنثويين.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(241, 'إِسْتِفَانُوس', 'اسم يوناني معناه ((تاج))  أو  ((إكليل من الزهور))  وهو اسم أول شهداء المسيحية. وبما أن اسمه يوناني فيرجّح أنه كان هيلينياً(أي أنه لم يكن يوناني الجنس بل يوناني اللغة والثقافة) أو أنه كان يهودياً يتكلم اليونانية. ولما اشتكا الهيلينيون المسيحيون في أورشليم من أن أراملهم كن يهملن (أعمال 6: 1) انتخب سبعة رجال من ضمنهم إستفانوس ليقوموا بأمر الخدمة اليومية وتوزيع التقدمات على الفقراء من المسيحيين(أعمال 6: 2- 6) وهؤلاء الرجال السبعة يعرفون بأول شمامسة في الكنيسة المسيحية. ويصف الكتاب المقدس استفانوس بأنه رجل ممتلىء من الإيمان والروح القدس(أعمال 6: 5) وأنه كان يصنع قوات وعجائب(أعمال 6: 8) وكان ينادي بالرسالة بحكمة(أعمال 6: 10). ولما لم يتمكن بعض من هؤلاء اليهود الهيلينيين أن يجاوبوا استفانوس أو يقاوموا قوة الحكمة والروح التي كانت فيه اخترعوا ضده شكايات زور ، فدسّوا رجالاً مأجورين يقولون أننا سمعناه يجدف على الله وعلى موسى وأنه تكلم ضد الشريعة وضد الهيكل . وقدمت هذه الشكاوى إلى مجمع السنهدريم(أعمال 6: 9- 14).وقد سجل لنا سفر الأعمال ملخصاً للدفاع المجيد الذي قدمه استفانوس (أع ص 7: 1- 53) فأبان أولاً أنه يعطي المجد كله لله(عدد 2) وأنه يكرم موسى (عدد 20- 43) والناموس(عدد 38و 53) والهيكل(عدد 47) ثم أبان ثلنية أنه لم يكن لموسى الكلمة النهائية ولا كان الهيكل نهائياً أيضاً. فقد اتبع موسى إعلانات سابقة. وقد وعد نفسه بمجيء نبي بعده وهو المسيح(عدد 37) . وكذلك الهيكل فقد جاء في أثر خيمة الاجتماع. ولم يكن المسكن النهائي لله رب الكون بجملته(عدد 48- 50) ثم ثالثة وبخ استفانوس اليهود على مقاومتهم لله المتكررة طوال حقب تاريخهم ، فقد قاوموا يوسف في أول نشأتهم(عدد 9) وموسى في دور تكوينهم كأمة(أعداد 39- 42) والأنبياء لما استقر بهم الأمر في كنعان (عدد 52)؛ وفي النهاية صبّ عليهم أعنف اللوم وأشده لأنهم رفضوا المسيح نفسه وقد قتلوه(عدد52) .وقد رفض المجلس أن يستمع لاستفانوس بعد هذا، أما هو فقال أنه يرى السماوات مفتوحة وابن الإنسان قائماً عن يمين الله(أعمال 7: 54- 56) عندئذ أخرجوه خارج المدينة، ربما من الباب اليوم باب استفانوس، ورجموه. وكان وهم يرجمونه يقول ((أيها الرب يسوع اقبل روحي))  ثم طلب من الرب غفران خطيتهم بسبب رجمه. وشاول الذي أصبح فيما بعد بولس، رسول يسوع المسيح العظيم، كان راضياً برجم استفانوس(أعمال 8: 1) وكان يحرس ثياب الذين رجموه(أعمال 7: 58) ولقد كانت شهادة استفانوس المجيدة حقاً من أكبر عوامل النعمة لإعداد شاول لكي يقبل المسيح (أعمال 22: 20) . وبعد موت استفانوس لاقى المسيحيون من العذاب أشده فتشتتوا من أورشليم إلى اليهودية والسامرة(أعمال 8: 1).'),
(242, 'إِسْتِير', 'يغلب على الظن أن هذا الاسم من أصل هندي قديم ومعناه  ((سيدة صغيرة)) ، ثم انتقل إلى الفارسية وأصبح معناه  ((كوكب))  ويظن بعض العلماء أنه يرجع إلى أصل أكادي لفظة ((أشتار))  ويقابل في العبرية  ((عشتاروت))  . وكانت استير فتاة جميلة، وهي ابنة أبيجائل الذي يرجّح أنه من سبط بنيامين(اس 2 : 15 وقارن عدد 5 مع عدد 7) أما اسمها في العبرية فهو هدَّسة أي ((شجرة الآس))  وقد تركت يتيمة وهي بعد صغيرة فأحضرها ابن عمها مردخاي الذي تبناها، إلى شوشن العاصمة الفارسية. وقد أقام الملك أحشويروش( وهو المعروف باسم زركسيس عند اليونان) وليمة لعظمائه وإذ كانوا يحتسون الخمر لعبت الخمر برأسه فأمر أن يحضر امرأته الملكة وشتي كي يرىعظماؤه جمالها الرائع. ولكن وشتي رفضت أن تمتهن كرامتها. وأهاج رفضها ثائرة الملك فأصدر وفقاً لنصيحة مشيريه قراراً بحرمان الملكة وشتي من المثول لديه. وأصدر أمراً بأن تعطي مكانتها لأخرى. كذلك أمر بأن يبحثوا بين الفتيات في كل مملكته عن فتاة جميلة لتأخذ مكانة وشتي . فاختيرت استير في السنة السابعة لملك أحشويروش ونصبت ملكة في القصر. ولم يكن معروفاً حينئذ أنها يهودية الجنس. ولقد اعتلت استير العرش في ظروف دقيقة ومتحرجة أيضاً . فقد كان ((هامان))  أقرب المقربين من الملك أحشويروش، وحدث بعد أن اعتلت استير العرش بخمس سنوات(اس 2: 16و3: 7) أن ثار غضب هامان على مردخاي لأنه رفض أن يقدم له الخضوع والاجلال. وقصد هامان أن ينتقم لنفسه لا بقتله مردخاي فحسب بل بابادة كل اليهود في كل أنحاء الامبراطورية. وقد تمكن هامان من أن يحوز رضا الملك ويأخذ موافقته ، فقد قدم رشوة باهظة وشكا اليهود بأنهم قوم صلاب عنيدون يتمسكون أين كانوا بشرائعهم وعاداتهم . ولكي يعضده الرعاع في فعلته بابادة اليهود زيَّن لهم النهب وإشباع أطماعهم(ص 2: 5و3: 15) وقد حثَّ مردخاي استير أن تتدخل في الأمر لحماية بني جنسها. ولكنها خافت ولم تجرؤ على الإقدام على مثل هذا الأمر فخاطبها مردخاي في عزم وقوة، فما كان منها إلا أن صامت وصلت وخاطرت بحياتها إذ مثلت في حضرة الملك دون ان يصدر لها أمر بذلك. وفي فطنة فائقة وحكمة نادرة انتهزت الفرصة لتوجيه التفات الملك إلى أن مؤامرة هامان غزت قصر الملك وامتدت إلى شخصها هي وبما أنه لم يكن من الممكن إلغاء أمر الإبادة فقد أمكنها أن تحصل لليهود على إذن بأن يدافعوا عن أنفسهم وأن يردوا كيد أعدائهم إلى نحورهم إن أرادوا.ولا يعرف شيء عن موت استير أو كيفية موتها أو تاريخه ومتى كان ذلك ،  فإذا كان أحشويروش هو زركسيس كما هو الاعتقاد السائد تكون استير إذاً واحدة من زوجاته. وأول زوجة لزركسيس ذكرها التاريخ على وجه التحقيق هي ((أمسترس)) حفيدة ((أوثانوس)) . فيذكر هيرودتس أنها كانت زوجته في سنة 479 ق. م. وهي السنة السابعة والثامنة من ملكه، وكانت وشتي زوجة لزركسيس في السنة الثالثة من ملكه، وكانت هي الملكة(اس 1: 3و9) وقد حلت استير محلها في الشهر العاشر من السنة السابعة لملكه(اس 2: 16و17) وفي السنة الثانية عشرة من ملكه كانت لا تزال استير ملكة(اس 3: 7و5: 3).سِفْرُ إِسْتِير:هذا آخر الأسفار التاريخية بحسب ترتيب الأسفار في الكتاب المقدس باللغة العربية وفي بعض اللغات الأخرى. أما في العهد القديم باللغة العبرية فيقع في القسم الذي يسمى كتوبيم أي ((الكتب))  وكان هو وأربعة أدراج أخرى أو ((مجلوث))  تقرأ في المناسبات الهامة. كان يقرأ كل واحد من هذه الأدراج أو المجلات في واحدة من هذه المناسبات. وآخر هذه المناسبات هو عيد ((البوريم)) . ولذا فمكان استير في التوراة العبرية في آخر الأدراج المعروفة في العبرية باسم ((مجلوث)) .ولسفر استير مكانة خاصة ممتازة عند اليهود. أما مكانه ضمن أسفار الوحي القانونية فقد كان موضع نقاش كثير. فقد حذفه مليتو الساردسي وجرجوري النزيانزي من سجلات الأسفار القانونية ورفض لوثر اعتباره ضمن الأسفار الموحى بها. وقد اتخذ هؤلاء هذا الموقف من ناحية هذا السفر العظيم لأن اسم الله لم يذكر فيه، صراحة ولا مرة واحدة. ولكن روح السفر كله ينم عن اعتقاد راسخ بوجود الله وإيمان قوي فيه وفي قدرته وعنايته(ص 4: 14) وتأمل مكانة الصوم كعمل ديني معترف به(ص 4: 16) وكذلك الصراخ والابتهال والتضرّع في الصلاة مع الصوم وكلها يعتبرها السفر أموراً لازمة واجبة حتمية ولها قوتها وفاعليتها(ص 9: 31) والدرس الرئيسي الذي يعلمه هذا الكتاب هو حقيقة العناية الربانية التي تسود في الكون وتحوّل اعمال البشر بحيث تتمم قصد السماء.أما أسماء بعض الشخصيات الرئيسة المذكورة في السفر فهي أسماء بابلية أو عيلامية. فاسم استير بطلة القصة شبيه باسم  ((أشتار))  إِلهة البابليين واسم ((هدَّسة))  قريب من الكلمة البابلية ((حدشتو))  أي عروس وكان أصلاً يطلق على شجرة الآس، ومردخاي اسم الإله البابلي الرئيسي ((مردوخ))  و ((هامان))  عدو مردخاي هو نفس ((هِمان))  أو ((هُمَّان))  (هميان) احد الآلهة الرئيسية في عيلام. و ((شوش))  عاصمة عيلام هي المكان الذي جرت فيه حوادث هذه القصة. واسم وشتي كان اسماً لأحد الآلهة في عيلام. ولهذا فقد ظنّ بعض النقاد أن السفر لا يستند إلى أساس تاريخي. ولكن الرأي المعقول في هذا السفر والذي يسهل تصديقه أنه سفر تاريخي بكل معنى الكلمة. فالسفر يشير إلى تاريخية الحوادث التي يتحدث عنها ويؤيدها بتواريخ واضحة بحسب التقويم الفارسي(ص 2: 23و6: 1و10: 2) وهو يعطينا التفسير المعقول الطبيعي لنشأة عيد البوريم الذي كان يمارس في عصر يوسيفوس في كل أنحاء العالم المعروف حينئذ. وعلاقة السفر بهذا العيد تدل دلالة واضحة على تاريخيته. ويقدِّم السفر وصفاً دقيقاً حياً للعوائد الفارسية والأحوال السائدة حينئذ وبخاصة كما كانت في شوشن(ص 1: 5و10و14و2: 9و21و23و3: 7و12و13و4: 6و11و5: 4و8: 8). ثم أن الوصف الذي يصوره الكتاب للأخلاق التي اتصف بها أحشويروش ينطبق تماماً على ما نعرفه من التاريخ عن أخلاق زركسيس. ويظهر أن زركسيس عقد مجلساً حربياً في السنة الثالثة من حكمه قبل أن يقوم بحملته على اليونان وأنه عاد إلى شوشن في ربيع السنة السابعة كما نفهم ذلك من تاريخ هيرودتس. وهذه التواريخ تتفق تماماً مع تاريخ الوليمة العظيمة التي أقامها ، وتاريخ اختيار من خلفت وشتي على العرش (ص 1: 3و2: 16).وقد اكتشفت حديثاً نقوش فارسية ذكرت اسم مردخاي كواحد من رجال البلاط الفارسي أثناء حكم أحشويروش وهذا ما يؤيد تاريخية السفر.ويسرد السفر حوادث القصة بدقة فائقة مبيناً كل الظروف المحيطة بها ذاكراً أسماء رجال البلاط الفارسي وأمرائه (ص 1: 10و14). فمن يتصفح سفر استير يجد ذكراً مفصلاً لهامان. فهو ابن همداثا الأجاجي (ص 3: 1و10و8: 5و9: 24) وأسماء أبنائه العشرة(ص 9: 7- 10).أما العبرانيون الذين ورد ذكرهم في هذا السفر فهم استير ابنة أبيجائل والملكة زوجة أحشويروش، ومردخاي ويحتمل أن الاسم نسبة إلى ((مردوخ)) . أما الاسم في صيغته هذه فيدل على أنه اسم إنسان لا اسم اله. ولم تكن مثل هذه الأسماء ممنوعة عند العبرانيين أو محرمة لديهم(قارن أسماء يهود مثل ((أبولس))  و ((هناداد))  و ((شنآصر))  وغيرها). وكان أحد الرجال الذين رجعوا من السبي يدعى مردخاي (عزرا 2: 2) ويعطينا سفر استير سلسلة نسب مردخاي الذي ينتمي إلى سبط بنيامين(اس ص 2: 5).وقد كتب سفر استير في اللغة العبرية المتأخرة وتتخلله كلمات فارسية ويظهر من ص 10: 2 ان زركسيس كان قد مات لما كتب السفر. ويعتقد بعض العلماء أن السفر كتب أثناء حكم أرتزركسيس لونجمانوس(465- 425 ق. م.) ولا توجد لدينا المعلومات الكافية لتقدير تاريخ كتابة السفر على وجه التحقيق. ويميل معظم النقاد إلى القول بأنه كتب في العصر الإغريقي الذي بدأ بفتوح الإسكندر عام 332 ق. م. ،فيظنون أنه كتب حوالي عام 300 ق. م. ولا يوجد اقتباس واحد من هذا السفر في العهد الجديد ولا يشير إليه كتاب العهد الجديد.تقسيم السفر:(1)  الملكة وشتي تتحدى أحشويروش الملك ص1(2)  استير تصبح ملكة ص 2: 1- 18.(3)  مردخاي يظهر مؤامرة ضد الملك ص2: 19- 23.(4)  مكيدة هامان لإهلاك اليهود ص3: 1- 15.(5)  استير تعد بأن تتوسط لدى الملك لأجل شعبها ص4: 1- 17.(6)  استير تدعو الملك و هامان إلى وليمة ص 5: 1- 14.(7)  هامان يدعم الملك على تكريم مردخاي ص 6: 1- 14.(8)  صلب هامان ص 7: 1- 10.(9)  اليهود يدافعون عن أنفسهم ص8: 2- 9: 15.(10)  عيد الفوريم ص5: 16- 32.(11)  تكريم مردخاي ص 10.وقد زيدت إضافات إلى سفر استير كما جاء في الترجمة السبعينية. وقد فصل أرنيموس(جيروم) هذه الزيادات من السفر ووضعها في آخره وقد أعطيت هذه الزيادات مكاناً في الأبو كريفا (أي الأسفار غير القانونية).الإضافات الأبوكريفية (غير القانونية) لسفر استيرورد في الترجمة اليونانية السبعينية لسفر استير خمسة أجزاء تبلغ في مجموعها 107 من الأعداد ولا يوجد لهذه نصوص في الأصل العبري فوضعت في نهاية السفر في الترجمة اللاتينية(الفولجاتا) وكذلك في ترجمة اليسوعيين العربية للكتاب المقدس. وقد كتبت هذه الإضافات اليونانية في عام 114 ق. م. كما نقرأ هذا في ص 11: 10 وهي تحتوي وصفاً لبعض النواحي القومية والدينية عند اليهود، وبعض الرسائل التي تدعى أنها للملك أرتزركسيس. ولا يوجد تناسق أو انسجام بين السفر في العبرية وبين هذه الزيادات. بل أن هناك تناقضاً بينهما. فتذكر هذه الإضافات أن ملك الفرس في ذلك الحين هو أرتزركسيس بدلاً من زركسيس . وتذكر أن هامان كان مقدونياً بدلاً من كونه فارسياً. فلهذه الأسباب، ولأن هذه الإضافات لم ترد في الأصل العبري، فلا تعتبرها المذاهب الإنجيلية ضمن الأسفار القانونية.'),
(243, 'إِسْحاق', 'ومعناه بالعبرية  ((يضحك))  وهو ابن إبراهيم وسارة؛ وقد ولد في النقب، وفي بئر سبع على الأرجح(تك 21: 14و31). وقد ولد حين كان أبوه قد بلغ السنة المائة من العمر تقريباً، وحين كانت أمه قد بلغت 90 عاماً من العمر تقريباً(تك 17: 17و21: 5). ولما وعد الله سارة بأنها تلد ابناً ضحك إبراهيم لأن أمراً مثل هذا يبعد تصديقه(تك 17: 17- 19). ولما سمعت سارة الوعد نفسه ينطق به أحد الضيفين السماويين ضحكت هي أيضاً لأنها لم تصدق شيئاً من مثل هذا(تك 18: 9- 15). ولما ولد الطفل قالت سارة أن الرب صنع إليّ ضحكاً وأن جيرانها سيضحكون معها (تك 21: 6). فقد لازم الضحك هذا الصبي من وقت الوعد به إلى ما بعد ولادته ولذا فقد دعا إبراهيم اسم ابنه ((اسحاق))  أي ((يضحك)) .ولقد ختن ابراهيم اسحاق في اليوم الثامن كم أمره الرب (تك 21: 4) وفي مناسبة الإحتفال بفطام اسحاق، وبناء على إثارة سارة وإشارتها، طرد ابراهيم الأمة هاجر وابنها اسماعيل(تك 21: 8- 21) . وفي نقطة ما على جبل المربا، ويرجَّح أنها البقعة التي بني عليها المذبح في هيكل سليمان فيما بعد، كاد ابراهيم يقدم ابنه ذبيحة، ولكن الله منعه من تقديم هذه الذبيحة، فقدم ابراهيم كبشاً عوض اسحاق ابنه(تك 22: 1- 13). وبهذا أظهر الله لإبراهيم في وضوح وإبانة أنه لا يطلب تقديم الأولاد ذبائح كما كان يفعل الكنعانيون في ذلك الحين.وعندما أراد إبراهيم أن يأتي بزوجة لاسحاق أرسل إلى قومه في حاران ليحضر زوجة له من أهله ومن عشيرته. وكان اسحاق وقت زواجه يسكن ((بئر لحي رُئي))  في النقب(تك 24: 62) وكان قد بلغ الأربعين من العمر(تك 25: 20).  ثم من بعد عشرين سنة من زواجه، استجاب الرب صلاة اسحاق وأعطاه وزوجته رفقة توأمين عيسو ويعقوب، وجاءت النبوة بأن الكبير يستعبد الصغير(تك 25: 21- 26).وانتقل اسحاق إلى جرار بسبب الجوع ، وهناك ظهر الله له في حلم وحذره من الذهاب إلى مصر، وجدد له العهود التي وعد بها ابراهيم أباه(تك 26: 1- 5). وقد تشبه بأبيه في اظهار زوجته بأنها أخته وذلك لكي ينجي نفسه. ولكن أبيمالك ملك جرار اكتشف الخدعة، وأكَّد له حمايته(تك 26: 6- 11) وقد بارك الله اسحاق في زرعه، وفي ما شيده حتى أن الفلسطينيين حسدوه وغاروا منه(تك 26: 12- 16). ولذلك انتقل اسحاق من جرار وحفر عدة آبار على التوالي ولكنه تركها بسلام للفلسطينيين الذين كانوا يتنازعونها(تك 26: 22). وظهر الله لاسحاق في بئر سبع ووعد بأن يباركه وبنى اسحاق مذبحاً للرب هناك(تك 26: 23- 25). وقد جاء إليه أبيمالك ملك جرار وعمل معه عهد سلام كما كان قد عمل مع أبيه من قبل(تك 26: 26- 33، قارنه مع تك 21: 22- 32). لذلك فقد اتصلت بئر سبع مرة أخرى بالقسم والحلف.وقد أحب اسحاق عيسو وأحبت رفقة يعقوب(تك  25: 28) . ولما أراد اسحاق أن يعطي البركة والجزء الأكبر من ميراثه لابنه عيسو، احتالت رفقة واستغلت عمى اسحاق وأشارت على يعقوب أن يتقدم لنوال البركة بدل عيسو. ولم يتمكن اسحاق من بعد هذا أن يمنح عيسو سوى بركة نانوية ضئيلة(تك 27: 1- 40). وبناء على إشارة رفقة أرسل اسحاق يعقوب إلى فدان آرام ليهرب من أمام غضب أخيه عيسو وليتخذ لنفسه زوجة من قومه وأهل عشيرته(تك 27: 41- 28: 6).ولما عاد يعقوب من فدّان آرام بعد فوات عشرين عاماً كان اسحاق لايزال ساكناً في حبرون(تك 35: 27). وهناك مات اسحاق فدفنه ابناه في مغارة المكفيلة إلى جوار والديه وزوجته(تك 49: 31).وقد اتصف اسحاق بالخضوع لأبيه(تك 22: 6- 9) وبالتأمل(تك 24: 63) وبالمحبة لأمه وزوجته(تك 24: 67) وبمسالمته للغير( تك 26: 20- 22) وبالصلاة(تك 25: 21و26: 25). ولكنه تمثل بأبيه في أنه لم يقل الحق من جهة زوجته خوفاً على حياته (تك 26: 7). ويرد اسمه كثيراً مقترناً مع ابراهيم ويعقوب بصفتهم آباء شعب الله(1 ملو 18: 36، مت 22: 32 الخ). ويتخذ بولس من اسحاق رمزاً للمتحررين من عبودية الناموس والذين نالوا الموعد وقد ولدوا حسب الروح (غلاطية 4: 21- 31) ويرى كاتب الرسالة إلى العبرانيين (ص 11: 20) في بركة يعقوب وعيسو دليلاً على الإيمان.'),
(244, 'إِسْخَرْيُوطِي', 'انظر  ((يهوذا الإسخريوطي)) .'),
(245, 'أَسَد', 'وهو حيوان ضار كبير الحجم من أكلة اللحوم. اسمه باللاتينية Fe is leo وكان الأسد معروفاً تمام المعرفة في فلسطين في العصور التي جرت فيها الحوادث المدونة في الكتاب المقدس ولكنه لا يوجد فيها الآن. وفي الكتاب المقدس ست كلمات عبرية للدلالة على الأسد وهي:  ((أرى))  (قض 14: 5) و ((أريه))  (قض 14: 8و9) وكلتاهما من أصل واحد وتدلان على الأسد المكتمل النمو، و ((لبي))  (عدد 23: 24) أي لبوءة، و ((كفير))  (مز 104: 21) وهو الشبل، و ((شحل))  (اي 28: 8)وهو الأسد الشديد الزئير، و ((ليش))  وهو ((الليث))  فقد ورد اللفظ العبري في أمثال 30: 30. وجميع هذه الأسماء وردت في أيوب4: 10و11. ويشير الكتاب المقدس إلى كثير من خصائص الأسد وأعماله وقوته(2 صم 1: 23) وشجاعته(2 صم 17: 10) وتحفزه قبل وثوبه وهجومه على فريسته(تك 49: 9) وزئيره(اي 4: 10) وافتراسه الغنم(1 صم 17: 34) والبشر(1 ملو 13: 24) وكان من أحب الأمكنة لسكن السود في فلسطين الغابات والأحراش القائمة على ضفتي الأردن(أر 49: 19) وكذلك وجدت الأسود على جبل حرمون(نشيد 4: 8) وفي السامرة(2 ملو 17: 25) وفي البرية الواقعة جنوبي يهوذا(أش 30: 6) وقد نال شمشون(قض 14: 5) وداود (1 صم 17: 34- 36) وبناياهو(2 صم 23: 20) الفخر لشجاعتهم في قتل الأسود . ونجد على الآثار المصرية والآشورية الرسوم التي تمثل صيد الأسد. وقد استعملت أشكال الأسد وتمثيلاته في هيكل سليمان(1 ملو 7: 29و36) وفي عرشه(1 ملو 10: 19و 20) كما نرى تماثيل أسود على جانبي عرش توت عنخ آمون ملك مصر. وقد أنقذ الرب دانيال من الأسود في بابل(دانيال 6: 16- 23) وكثيراً ما نرى رسم الأسود على جدران باب اشتار في بابل القديمة.وكثيراً ما استخدم الكتاب المقدس الأسد في مقارناته وتشبيهاته وأمثاله. فنجد مقارنة بين الأسد وبين الأعداء (أمثال 28: 15) والأنبياء الكذبة(حز 22: 25) والملك الغاضب(امثال 19: 12) والله في غضبه وغيضه(أرميا 25: 30) والشيطان(1 بطر 5: 8). ويقارن يهوذا بالأسد في (تك 49: 9) وكذلك يقول سفر الرؤيا 5: 5 عن المسيح:  ((الأسد الذي من سبط يهوذا)) . وشبه أيضاً ((دان))  (تثنية 33: 20) وكل اسرائيل(عدد 23: 24) وشاول ويوناثان(2 صم 1: 23) بالأسد ومن علامات السلام الذي يسود الكون أن الأسد والحمل يربضان معاً(أش 11: 7).والكائنات الحيَّة التي يستند عليها عرش الله في رؤيا حزقيال لكل واحد منها أربعة وجوه، وأحد هذه الوجوه وجه الأسد (حز 1: 10) وأول الكائنات الحيَّة حول عرش الله في رؤيا يوحنا يشبه الأسد (رؤ 4: 7) وقد ظن البعض أن الأسد في هذه الرؤى رمز لقوة الله.'),
(246, 'أَسِّير', 'كلمة عبرانية معناها ((أسير))  وقد ورد هذا الاسم في الكتاب المقدس لما يأتي:(1)  لرجل لاوي ولد لقورح في مصر (حز 6: 24و1 أخبار 6: 22).(2)  اسم لأحد أحفاد الرجل المذكور آنفاً وابن انياساف. وكان اِسّير هذا من أسلاف صموئيل(1 أخبار 6: 23).(3)  اسم ابن الملك يكينا(1 أخبار 3: 17). وقد ظن بعضهم أن هذا الاسم يعني وصف يكينا  ((بالأسير))  ولكنه يتضح من النص العبري ومن الترجمات القديمة على أنه لا يدل على وصف بل على اسم علم.'),
(248, 'أَسَرْئِيل', 'اسم عبري ومعناه ((من أسرة الله))  وهو ابن يهللئيل من سبط يهوذا(1 أخبار 4: 16).'),
(249, 'إِسْرَائِيل', 'معنى هذا الاسم العبري  ((يجاهد مع الله))  أو  ((الله يصارع))  وقد أطلق هذا الاسم في الكتاب المقدس على ما يأتي:(1) يعقوب إذ أطلق عليه الملاك الذي صارعه حتى مطلع الفجر في فنوئيل في مخاضة يبوق (تك 32: 28) أنظر  ((يعقوب )) .(2)  نسل يعقوب جميعاً , فاستعمل كمرادف لبني اسرائيل. ففي الشعر العبري كثيراً ما نجد يعقوب في صدر البيت , ويقابلها اسرائيل في عجزه أو العكس بالعكس (عدد 23: 7 ومز 14: 7). وقد بدأ بتسمية نسل يعقوب  ((اسرائيل))  حتى وهو بعد حي (تك 34: 7) وأحياناً كان بنو اسرائيل أثناء تيهانهم في البرية يلقبون بإسرائيل (خر 32: 4 وتثنية 4: 1). وقد أصبح اسم اسرائيل يطلق على كل الإثني عشر سبطاً كأمة واستمر كذلك من وقت افتتاح أرض كنعان على يد يشوع إلى موت سليمان . وأول إشارة إلى اسرائيل  كأمة خارج التاب المقدس نجدها في نقش لمنفتاح فرعون مصر الذي حكم مصر من سنة 1227- 1234 ق.م. وقد حكم مملكة اسرائيل المتحدة ثلاثة ملوك , شاول ,و داود ,وسليمان أي من سنة 1050 تقريباً إلى سنة 930 ق.م. تقريباً. وبعد الرجوع من السبي , يتحدث الكتاب عن الإسرائيليين الذين رجعوا من بابل وسكنوا في فلسطين ويطلق عليهم اسم اسرائيل كمرادف لشعب اسرائيل (عزرا   10: 5 قارنه مع 9: 1) أو بدل بني اسرائيل (نح 9: 2 قارنه مع عدد 1).(3)  يطلق هذا الاسم على الأسباط الذين انشقوا وانفصلوا عن يهوذا وبنيامين وأصبحوا مملكة اسرائيل . وقد أطلق الاسم على الأسباط التي تسكن الشمال لتمييزها عن سبط يهوذا ( 1 صم 11: 8) وبعد موت شاول اعترفت الأسباط الشمالية بابن شاول  (( ايشبوشت ))  ملكاً عليها ومن ذلك الوقت فصاعداً أصبح الاسم  ((اسرائيل ))  يدل على الأسباط التي كانت تسكن في الشمال (2 صم 2: 9). ومع أن المملكة اتحدت بعد ذلك أي أثناء حكم داود وسليمان فإن هذه الأسباط نفسها ثارت على رحبعم وكونت مملكة اسرائيل التي حكمها يربعام . وهذه الأسباط العشرة الثائرة هي:رأوبين , جاد ,نصف سبط منسّى (وكان هؤلاء يسكنون شرقي الأردن ) ونصف سبط منسى و افرايم ويسّاكر وزبلون واشيرودان ومعظم بنيامين كانوا يسكنون غربي الأردن أما أسباب هذا الإنقسام فبعضها يرجع إلى:(1)   الغيرة القديمة بين سبطي يهوذا وافرايم التي ترجع إلى وقت دخولهم أرض كنعان.(2)  التحيز الذي أظهره داود وسليمان بنوع خاص ليهوذا وأورشليم مما أثار حفيظة الأسباط التي كانت تسكن في الشمال .(3)  فداحة الضرائب وقسوة أعمال التسخير التي فرضها سليمان على الشعب في أورشليم.(4)  رفض رحبعام أن يخفف حمل هذه الضرائب وأعمال التسخير.ولقد كانت مملكة اسرائيل تختلف عن مملكة يهوذا من عدة نواح:(1) كانت أرض مملكة اسرائيل أكبر من يهوذا كثيراً فقد كانت مساحة أرضها ضعف مساحة أرض يهوذا وكان عدد سكانها ثلاثة أمثال عدد سكان يهوذا .(2) كانت أرض مملكة اسرائيل كثيرة السهول صالحة للزراعة ولشق طرق التجارة بين آسيا و إفريقيا .(3) هذه السهول  وهذه الطرق التجارية جعلت اسرائيل أكثر عرضةً من يهوذا للغزو وللمؤثرات الأجنبية .(4) كان في يهوذا بيت ملكي واحد له مواعيد الله التي أعطاها لداود أما في اسرائيل فقد وجد تسع أسر تداولت الحكم على التوالي ووجد فيه ثمانية ملوك ماتوا اغتيالاً أو انتحروا.(5) كان في يهوذا عاصمة واحدة فقط وهي أورشليم وكان فيها هيكل الرب أما قي اسرائيل فقد انتقلت العاصمة من شكيم إلى ترصة ثم إلى السامرة .(6) ولم يكن اسرائيل في أمر الدين مستقراً مثلما كان يهوذا بل كان عرضة لتأثير الوثنية وعبادة الأصنام .وقد دامت المملكة الشمالية مدة 210 سنوات تقريباً أي من سنة 930 إلى سنة 722 قبل الميلاد وحكمها 19 ملكاً( انظر ملوك اسرائيل ). ستة من هؤلاء الملوك يستحقون الذكر بنوع خاص وهم :(1) يربعام الأول  ابن ناباط(910- 931) وكان قائد الثورة ضد رحبعام , وهو الذي نصب عجول الذهب للعبادة في بيت ايل ودان , وهو الذي دخل في حرب لا طائل تحتها مع يهوذا . (2) عمري (885- 874) وقد نقل عاصمة املك إلى السامرة وأخضع موآب. وترك أثراً قوياً في الأمم الغريبة فبعد مضي حكمه بقرن تقريباً كان الآشوريون لا يزالون يدعون اسرائيل باسم  ((بيت عمري)) . (3) آخاب بن عمري(874- 852 ق.م.) وقد تزوج إيزابل التي شجعت على عبادة البعل في إسرائيل. وقد عقد حلفاً مع يهوذا وآخر مع آرام إنما إلى حين، وكان قد أعان آرام ضد آشور في معركة قرقر سنة 854 ق. م. ولكنه مات فيما بعد في حربه ضد آرام.  (4) ياهو (842- 814 ق. م.) وقد أسقط أسره عمري ووضع حداً لعبادة البعل. وقد أخذ آرام منه معظم شرقي الأردن وفرضت آشور الجزية عليه.  (5) يربعام الثاني وهو ابن يواش(785- 745 ق. م.) وفي أثناء حكمه الطويل بلغت المملكة الشمالية أقصى عظمتها وتقدمت أكثر من أي عصر آخر. فقد استعاد شرقي الأردن من آرام وعمل صلحاً مع يهوذا.  (6) هوشع(730- 722 ق. م.) وهو آخر ملوك المملكة الشمالية وعندما حاول العصيان على آشور ثارت آشور ضده وأخذت السامرة وكانت هذه نهاية مملكة إسرائيل.وكثيراً ما كانت العلاقات بين إسرائيل ويهوذا علاقات عداوة. فإننا نجد ذكر حروب طاحنة بين إسرائيل ويهوذا أثناء حكم يربعام الأول وبعشا ويواش وفقح. إنما تحسنت العلاقات أثناء حكم أسرة عمري وأصبحت علاقات مودة وصداقة وقد تزوج يهورام ملك يهوذا عثليا ابنة آخاب ملك إسرائيل.وقد دارت حروب طاحنة بين آرام وإسرائيل. فقد حارب بعشا ملك إسرائيل بنهدد ملك آرام. وقد قتل آخاب أثناء حربه ضد آرام. وقد أخذ الآراميون السامرة أثناء حكم يهورام . وأذل حزائيل ملك آرام إسرائيل أيما إذلال أثناء حكم ياهو ويهوآخاز. ولم تتحالف إسرائيل مع آرام سوى مرتين، وكان التحالف في هاتين المرتين للحرب ضد العدو المشترك آشور، مرة في معركة قرقر في سنة 854 ق. م. والمرة الأخرى أثناء حكم فقح ملك إسرائيل(734- 730 ق. م.)وقد حكم الآشوريون  المملكة الشمالية ولكنهم في النهاية أفنوها . وقد قاوم آخاب آشور ولكن ياهو دفع لآشور جزية. وإذ ضعفت آشور فترة من الزمن تمكنت إسرائيل من النهوض والازدهار وكان هذا أثناء حكم يربعام الثاني . ولكن منحايم(744- 735 ) دفع لآشور جزية فادحة. وقد أراد فقح (734- 730) أن يقاوم آشور فغزا ملك آشور إقليم الجليل من أملاك إسرائيل وأخذ في الأسر عدداً كبيراً من الشعب. وقد حاول هوشع(730- 722) أن يقاوم بثورة على آشور فما كان من شلمناسر الخامس ملك آشور إلا أن جاء وحاصر السامرة، ومن بعده جاء سرجون الثاني وافتتح المدينة وأخذ كثيرين من الشعب أسرى إلى آشور ( 2 ملو 18: 10و11). ويرى كاتب الإصحاح السابع عشر من سفر الملوك الثاني في هذا الدمار الذي حلّ بمملكة إسرائيل قضاء الله العادل جزاء خطيتهم وعبادتهم الوثنية. وقد جاء ملك آشور بقوم من آشور وأسكنهم مع الباقين في البلاد من شعب إسرائيل فاختلطوا بهم وجاء السامريون نتيجة لهذا الاختلاط.ومع أن الكتاب المقدس يوقع القضاء الشديد على عبادة إسرائيل الوثنية وانتشار عبادة الأصنام بينهم إلا أننا نرى عدداً من الأنبياء العظام الذين قاموا برسالتهم في ربوع إسرائيل والذين كان لهم نصيب فذّ في إحقاق الحق وفي رفع منار الدين الحق. ومع أن أخيَّا النبي هو الذي شجع يربعام على العصيان على رحبعام إلا أنه وبخه أشد التوبيخ لأنه أقام عجلي الذهب (1 ملو 11: 26- 40و14: 1- 16). وقد استخدم الرب إيليا في إرجاع الشعب عن عبادة البعليم إلى الرب(1 ملو ص 18) وقد وبخ إيليا آخاب أيما توبخ لأجل ظلمه وجوره(1 ملو ص 21). وحذّر ميخا بن يملة من قضاء الله(1 ملو 22: 5- 33).وقد أرسل أليشع رجلاً من قبله لكي يمسح ياهو ولكي يسقط بيت عمري ويبطل عبادة البعل(2 ملو 9: 1- 10) وشجع أليشع أيضاً يهورام ويوآش على مقاومة الآراميين(2 ملو 6و7و13: 14- 19). وقد اشتهر يونان ابن امتاي بمناداته لنينوى ولكنه أيضاً شجع يربعام الثاني فحاز انتصارات على آرام(2 ملو 14: 25). وكان عاموس من أرض يهوذا ولكنه قام بعمله النبوي في إسرائيل. وقد أعلن قضاء الله على ظلمهم وعلى العبادة الطقسية الفرضية التي يمارسونها وحذرهم من الأسر والسبي العتيدين(عاموس ص 5). وقد اضطلع هوشع بمهمته النبوية في أشد الأيام قتاماً في تاريخ إسرائيل، وقد أعلن قضاء الله على الشعب بسبب خيانتهم لله والفقراء ودعاهم للتوبة وحذرهم من القضاء الآتي.(4) يستعمل الاسم  ((إسرائيل))  و ((إسرائيليون))  بمعنى روحي ففي أشعياء ص 49: 3 يدعو الله إسرائيل  ((عبدي))  والإشارة هنا إلى شعب الله الروحي الأمين وفي الرسالة إلى رومية9: 6 يفرق بولس بين إسرائيل حسب الجسد وإسرائيل حسب الروح. وفي رومية 9: 4و5 يذكر الرسول امتيازات الإسرائيليين وفي 2 كورنثوس 11: 22 يتكلم عن نفسه بأنه ليس عبرانياً فحسب بل انه إسرائيلي وفرد من شعب العهد. ويقول يسوع المسيح عن نثنائيل أنه إسرائيلي حقاً لا غش فيه (يوحنا 1: 41) ولا يشير إلى قوميته فحسب بل يشير إلى إيمانه وإخلاصه.أرض إِسْراَئيل:انظر ((كنعان وفلسطين))'),
(250, 'إِسْمَاعِيلِيُّون', 'وهم نسل اسماعيل بن ابراهيم من أمته المصرية هاجر، وقد ورد في تك 17: 20 و25: 12- 16 أنه كان لاسماعيل اثنا عشر ابناً صاروا أمراء ورؤساء قبائل. وقد كانت هذه القبائل تسكن الجزء الشمالي من شبه جزيرة العرب على حدود فلسطين وأرض ما بين النهرين(تك 25: 18). وقد عرف الاسماعيليون بأهم تجار رحّل ينتقلون من مكان إلى آخر(تك 37: 25- 28) وكذلك عرفوا بمهارتهم في قيادة الجمال(1 أخبار 27: 30) وسكنهم الخيام(مز 83: 6) وبأنهم حاذقون في استعمال القوس(أش 21: 17).وأحياناً يستعمل الاسم ((اسماعيليون))  للدلالة على القبائل البدوية التي كانت تسكن شمال الجزيرة العربية ولذا فيدعى المديانيون اسماعيليين(تك 37: 25و28) وقد كانت غالبية هذه القبائل من البدو ولكن بعضاً منهم استقر بهم الأمر ، وأسست ممالك مستقلة كالنبطيين والتدمريين(سكان تدمر أو بلميرا) والغساسنة أو (بنو غسان) واللخميين أو (بنو لخم). ويرجع جميع العرب اليوم إلى اسماعيل فيعتبرونه جدهم الأكبر.'),
(251, 'أَسْنَات', 'اسم مصري لفظه في اللغة المصرية القديمة(نس- نيت) وهي نسبة إلى الآلهة(نيت) وكانت ابنة فوطيفار كاهن أون وزوجة يوسف وأم منسى وأفرايم(تك 41: 45و50و46: 20).'),
(252, 'أَسْنَة', 'اسم عبري ومعناه ((عليق))  أو ((شجيرة شائكة))  وكان اسماً لواحداً من النثينيم أو خدام الهيكل. وقد رجع بعض من نسله من بابل إلى فلسطين مع زربابل(عزرا 2: 50).'),
(253, 'أَسْنَفَّر أو أُسنَفَّر', 'وهذا رجل آشوري يلقب في (عزرا 4: 10) بالعظيم الشريف. وقد أسكن قبائل أجنبية متنوعة في السامرة. ويقول العلماء الآن أنه هو نفس آشور بانيبال(  ((آشور خالق ابن))  ) ملك آشور من سنة 669 إلى سنة 626 ق. م. وكان ابن أسرحدون وخليفته، وقد أخمد جيشه ثورة قامت في مصر، ونهب مدينة ((طيبة))  أو  ((نوآمون))  كما جاء ذكرها في ناحوم 3: 8- 10. وقد عفا آشور بانيبال فيما بعد عن واحد من الثوار واسمه نخو وأعاده إلى ملكه في  ((صان الحجر))  في الدلتا. وقد عفا عن ثائر آخر وهو منسى ملك يهوذا وأذن له أن يرجع إلى عرشه في أورشليم(2 أخبار 33: 13) وقد أخمد ثورة أخرى في عيلام وأخذ كثيرين أسرى من عاصمة بلادهم سوسا أو شوشن (قارن هذا مع عزرا 4: 9). وكذلك أخمد ثورة ثالثة قام بها أخوة شماش- شم-اكن. وكان نائباً للملك في بابل. وكان آشور بانيبال من أعظم من تعهدوا الفن والأدب برعايتهم في العالم القديم. وقد جمع الكتَّاب في عصره آلافاً عديدة من الكتب ونسخوها في الخط المسماري على اللوحات الفخارية وأضحت هذه اللوحات في العصور الحديثة المرجع الرئيسي لما نعرفه من تاريخ البابليين والآشوريين وآدابهم.'),
(254, 'أَسْوَان', 'مدينة من مدن صعيد مصر في الجنوب(حز 29: 10و30: 6) وهي نفس أسوان الحديثة. اسمها في المصرية القديمة سوان وتقع على الشلال الأول في النيل. وقد استخدم الفراعنة حجر الجرانيت المستخرج من أسوان في عمل التماثيل والنصب وبناء القصور والمعابد القديمة، وكانت عاصمة المنطقة(Nome) الأولى في مصر القديمة. وقد اشتهرت أسوان والفنتين بتجارتهما مع الحبشة، ومكانتهما في الدفاع عن مصر. ويتضح من أوراق البردي المكتوبة باللغة الآرامية والتي اكتشفت في جزيرة الفنتين أنه كان في مصر في القرن الخامس قبل الميلاد حامية جنود يهودية وهيكل يهودي هناك.'),
(255, 'أسِينْكِرِيتُس', 'اسم يوناني ومعناه ((لا نظير له))  وهو أحد المسيحيين في رومية ومن ضمن الذين أرسل إليهم بولس تحياته (رومية 16: 14).'),
(256, 'آسِيا', 'عندما يتحدث العهد الجديد عن آسيا يقصد بها دائماً المقاطعة الرومانية التي كانت تحمل هذا الاسم والتي كانت تقع في غرب آسيا الصغرى(أعمال 19: 10و1 كو 16: 19 و2 تيمو 1: 15و 1: 1 ورؤيا 1: 4) وكانت هذه المقاطعة تشمل ميسيا وليديا وكارية وجزءاً من فريجية وبعض الموانيء البحرية المستقلة وترواس وبعض الجزر الساحلية. وكانت أفسس عاصمة هذه المقاطعة في أزمنة العهد الجديد. وقد قضى الرسول بولس بها عدة سنوات ، ومن هذه المدينة انتشر الإنجيل إلى كل بقاع المقاطعة(أعمال 19: 10) وكان الحاكم الروماني للمقاطعة من رتبة ((نائب قنصل))  أو ((والٍ))  وكان يعين كل سنة من قبل مجلس الشيوخ الروماني. وقد بعث المسيح المقام على لسان يوحنا رسائل إلى السبع الكنائس التي في آسيا(رؤ 1: 3).وُجوه آسِيَّا:الاسم اليوناني المفرد ((اسيارخيس))  ومعناه ((رئيس آسيا)) . وكان هؤلاء الموظفون يعينون بواسطة المدن الواقعة  في المقاطعة الرومانية آسيا، وكانت مهمتهم الاشراف على الأعياد والمواسم السنوية وكذلك كانوا ينظمون الألعاب الرياضية العامة تكريماً للامبراطور الروماني. وكان أناس من وجوه آسيا أصدقاء لبولس(اعمال 19: 31).'),
(257, 'أشْبَان', 'اسم عبري ومعناه ((رجل ذو فطنة))  وهو ابن ديشان بن سعير الحوري من أرض آدوم(تك 36: 26و1 أخبار 1: 38و41).'),
(258, 'إِشْبَعَل', 'اسم عبري ومعناه  ((رجل البعل))  وهو الاسم الأول لإيشبوشت بن شاول(1 أخبار 8: 33و9: 39).'),
(259, 'أشبَيْع', 'اسم عبري وربما كان معناه ((كثرة))  أو ((شِبَع))  وهو واحد من نسل شيلة من سبط يهوذا (1 أخبار 4: 21) وكانت أسرته تنسج نوعاً دقيقاً ممتازاً من الكتان يعرف بالبر. ويظن البعض أن بيت أشبيع اسم لمكان ما.'),
(260, 'أشْبِيل', 'اسم عبري وربما كان معناه صورة من صور الاسم  ((أشبعل))  وهو ابن بنيامين ومؤسس أسرة(تك 46: 21 وعدد 26: 38و 1 أخبار 8: 1) ويرجّح أن يديعئيل المذكور في (1 أخبار 7: 6) اسم آخر لهذا الرجل نفسه.'),
(261, 'أشْتَأول', 'اسم عبري وربما معناه ((السؤال))  وهو اسم لمدينة في الطرف الجنوبي من يهوذا(يشوع 15: 20و33) وكانت من نصيب الدانيين (يشوع 19: 40 و41) وربما هي نفس أشوع الحديثة التي تبعد مسافة ميل ونصف الميل شرقي صرعة إلى الشمال وعلى مسافة تقرب من ثلاثة عشر ميلاً غربي أورشليم. ومن أشتأول ذهب الدانيون شمالاً لأخذ مدينة لايش (قض ص18).'),
(262, 'أشْتَمُوع', 'اسم عبري ومعناه ((الطاعة))  وقد جاء ذكره في الكتاب المقدس:(1)  اسم لمدينة في أرض يهوذا الجبلية وكانت قد أعطيت للكهنة(1 أخبار 6: 57) وتدعى هذه المدينة في يشوع 15: 50  ((اشتموه)) . وقد أرسل داود غلى هذه المدينة جزءاً من الغنيمة الت يأخذها من العمالقة(1 صم 30: 28) ويرجّح أن مكانها اليوم بلدة ((السموعة او سموع))  على بعد تسعة أميال جنوبي حبرون.(2)  أشتموع رجل معكي ابن يشبح من سبط يهوذا(1 أخبار 4: 17و19).'),
(263, 'أَشْتِمُوه', 'انظر أشتموع.'),
(264, 'أشْتُون', 'ولا يعرف معنى هذا الاسم على وجه التحقيق. وهو اسم أحد أبناء محير من سبط يهوذا(1 أخبار 4: 11و21).'),
(265, 'أشْحُور', 'اسم عبري وربما كان معناه ((سواد))  وهو ابن حصرون من سبط يهوذا واسم أمه أبيّاه (1 أخبار 2: 24) ويدعى  ((أبا تقوع))  وربما كان سكان بلدة تقوع من نسله.'),
(266, 'أشْدُود', 'ربما كان معناها ((قوة))  أو ((حصن)) . وهي إحدى مدن الفلسطينيين الخمس الرئيسية(يش 13: 3و1 صم 6: 17). وكان الإله الرئيسي فيها هو داجون(1 صم 5). وقد بقي العناقيون فيها بعد ما فتح العبرانيون كنعان. (يش 11: 22) وقد كانت من نصيب يهوذا (يش 15: 47) ولكن لم يتمكن هذا السبط من أخذها . وبعد أن انتصر الفلسطينيون على إسرائيل حملوا معهم تابوت الرب إلى أشدود ووضعوه في هيكل داجون(1 صم 5: 1- 2) فسقط تمثال داجون وأصيب الأشدوديون بالمرض فأرسلوا تابوت الرب إلى جت(1 صم 5: 3- 8) وقد هدم عزيا ملك يهوذا أسوار أشدود(2 أخبار 26: 6) .وقد حاصر ترتان، القائد الآشوري أثناء حكم سرجون ، أشدود وأخذها(أش 20: 1). وقد وردت أسماء الملوك الذين حكموا في أشدود تحت حكم آشور في السجلات التي جاءتنا من عصر سرجون وسنحاريب وأسرحدون ملك آشور. ويقول المؤرخ الإغريقي هيرودت أن سماتيك ملك مصر من 663- 609 ق. م. حاصر أشدود لمدة 29 سنة. ويظن بعضهم أن  ((بقية أشدود))  المذكورة في أرميا 25: 20 تشير إلى من بقي في أشدود بعد هذا الحصار الطويل. وقد قاوم الأشدوديون إعادة بناء أسوار أورشليم في أيام نحميا(نح 4: 7). وقد أخذ بعض اليهود الراجعين من السبي زوجات أشدوديات فلقوا من نحميا قصاصاً صارماً وتأنيباً مريراً(نح 13: 23و24). وقد أخذ المكابيون المدينة مرتين في القرن الثاني قبل الميلاد، وقد خربوا هيكل داجون في المرة الثانية(1 مكا 5: 68 و10: 84) وقد نادى فيلبس بالإنجيل في أشدود (أعمال 8: 40). والاسم العربي الحديث لأشدود هو ((أسدود))  وتبعد مسافة 18 ميلاً تقريباً، إلى الشمال الشرقي لغزة وهي في منتصف المسافة تقريباً بين غزة ويافا.'),
(267, 'أشَرْئِيلَة', 'والمعنى غير معروف على وجه التحقيق ، وهو اسم لابن آساف، وقد عينه داود لكي يقود العبادة في أورشليم(1 أخبار 25: 2) ويدعى ((يشرئيلة))  في (1 أخبار 25: 14) وأدق المخطوطات العبرية الموجودة بين أيدينا الآن تذكره باسم ((أسرئيلة))  و ((يسرئيلة)) .'),
(268, 'إِشْرِيئِيل', 'انظر اسريئيل.'),
(269, 'أَفُودِيَة ', 'كلمة يونانية معناها ((رحلة موفقة)) وهي اسم امرأة مسيحية في فيلبي وقد ساعدت الرسول بولس كثيراً في عمل الإنجيل (فيلبي 4: 3) ونرى الرسول بولس في فيلبي 4: 2 يحثها وزميلتها في العمل المسيحي أن تفتكرا فكراً واحداً في الرب.'),
(270, 'أَفِيح ', 'كلمة عبرية وربما معناها ((نافوخ)) أو الجزء اللين في رأس الرضيع. وهي اسم رجل بنياميني من أسلاف الملك شاول (1 صم 9: 1).'),
(271, 'أَفِيق', 'كلمة عبرانية معناها ((قوة أو حصن)) وهي :(1)  اسم مدينة في سهل شارون. وكانت مدينة للكنعانيين قتل الإسرائيليون بقيادة يشوع، ملكها (يش 12: 18). وقد اجتمع الفلسطينيون في هذا المكان ليحاربوا اسرائيل في زمن عالي الكاهن(1 صم 4: 1) ويرجَّح أيضاً أنهم اجتمعوا كذلك هنا لمحاربة اسرائيل في زمن شاول الملك (1 صم 29: 1). ومكانها اليوم بلدة رأس العين الحديثة وهي عند منبع نهر العوجة بالقرب من انتيباتريس (أعمال 23: 31).(2)  مدينة فينيقية لم يتمكن الإسرائيليون من أخذها (يش 13: 4) وبما أنها في شمال صيدون فقد ورد ذكرها مع أرض الجبليين ويرجَّح أنها أفقة الحديثة عند نبع نهر ابراهيم شرقي بيبلس ، أي جبيل الحديثة.(3)  مدينة في نصيب أشير (يش 19: 30) ولكن لم يتمكن الأشيريون من طرد الكنعانيين منها (قض 1: 31) ويرجَّح أنها تل الكردانة الحديثة'),
(272, 'أَفِيقَة', 'كلمة عبرية معناها ((ممقل)) أو ((حصن)) وهي مدينة في يهوذا، يرجح أنها كانت بالقرب من حبرون (يش 15: 53) وربما كانت هي خربة الصرامة الحديثة التي تقع جنوب غرب حبرون.'),
(273, 'أَكزِيب', 'كلمة عبرية معناها ((خادع)) وكانت أسم:(1) مدينة في غربي يهوذا (يش 15: 44) وتسمى في تك 38: 5 ((كزيب)) حيث كان يهوذا لما ولد ابنه شيلة. وتسمى نفس المدينة في سجل للرجال والبلدان في سبط يهوذا ((كزيبا)) (1 اخبار 4: 22) وفي ميخا 1: 14 توجد تورية لفظية بين الاسم ومعناه ((خادع)) أو ((كاذب)). وربما أخذت البلدة اسمها من نبع متقطِعّ الفيضان. وقد ظن بعضهم أنها نفس ((عين كزيبة)) الحديثة الواقعة في وادي إِله شمالي عدلام. وظن آخرون أنها ((تل البيضا)) جنوبي غربي عدلام. ويذكر احد خطابات  لخيش التي ترجع إلى عصر ارميا أنها كانت إحدى  مدن يهوذا  الحصينة.(2) مدينة عينت من نصيب الاشيرين (يش 19: 29) ولكنهم لم يتمكنوا من طرد الكنعانيين منها (قص 1: 31) وتقوم مكانها اليوم بلدة الزيب الحديثة، التي تبعد مسافة ثمانية أميال ونصف شمالي عكا.'),
(274, 'أَكشاف', 'كلمة عبرية معناها ((سحر)) أو ((عرافة)) وهي بلدة في اشير (يش 19: 25) وقد هزم يشوع ملكها (يش 11: 1 و 12: 20) وربما هي نفس ((تل كيسان))  الحديثة بالقرب من جنين.'),
(275, 'أُكَّال', 'كلمة عبرية معناها ((أنا قوي)) وهي اسم رجل وجَّه اجور إليه وإلى إِيثئيل كلامه في أمثال 30: 1. ونلاحظ أن كل الأمثال في الإصحاح الثلاثين موجهة إلى شخص ما، ففسر رجال ((المازوره)) أو كتبة اليهود هذا الاسم كاسم علم. أما الترجمة السبعينية وغيرها من الترجمات اليونانية القديمة فقد فسرت هذا الاسم كفعل معناه ((لقد فنيت)) ويرجح أنها مخطئة في هذا التفسير.'),
(276, 'أَكَّد', 'مدينة قديمة في أرض شنعار، وفي مملكة بابل. وتذكر في تك 10: 10 جنباً إلى جنب مع بابل كجزء من مملكة نمرود بن كوش. وقد جعل سرجون الأول اكد عاصمة إمبراطورية حوالي عام 2400 ق.م. وربما كان موقع هذه المدينة بقرب ((ابي هبة)) على نهر الفرات شمالي بابل.وقد امتد اسم اكد من المدينة إلى كل المقاطعة حيث يفيض نهرا دجلة والفرات بالقرب من بعضهما في العراق الأوسط. وقد وسعت أسرة من حوالي (2400-2200 ق.م)التي أسسها سرجون الأول حكمها بحيث كان يشمل معظم العراق، وعيلام من فارس والجزء الشرقي من سوريا. وقد قام أعضاء هذه الأسرة بكثير من الأبنية في مدن بابل القديمة.  ونصب النصر الذي أقامه ((نرام سن)) حفيد سرجون مثل مشهور للفن الاكادي، وقد كان الاكاديون يتكلمون لغة سامية قريبة من اللغتين العربية والعبرية. ولقد استعاروا الرموز المسمارية من السومريين ليتمكنوا من تدوين لغتهم. وكان هذا الخط المسماري. وتشمل اللغة الاكادية الأشورية والبابلية والكتابات الاكادية التي حفظت لنا تضم عقوداً تجارية وقوانين وسجلات تاريخية وأقاصيص دينية، بعض منها يساعد على تثبيت وتوضيح بعض أجزاء العهد القديم.'),
(277, 'أكل، وجبات الأكل', '1) أوقات تناول الطعام: كان طعام الإفطار في في الصباح الباكر جداً طعاماً خفيفاً يخلو من الرسميات. وكان يسميه التلمود ((لقمة الصباح)) ومن الواضح أنه لا يذكر في الكتاب المقدس سوى فطور واحد. فبعد ليل طويل انقضى في الصيد أكل التلاميذ سمكاً مع المسيح بعد قيامته (يو 21: 12 و 15). وينطق كاتب سفر الجامعة بالويل على الولائم الصباحية (جامعة 10: 16) ولم يكن من المألوف شرب الخمر في الصباح، (أعمال 2: 15).أما تناول الغداء في الظهر فيظهر أنه كان من عادات المصريين الشائعة (تك 43: 16) وكذا كان من عادات السوريين أو الآراميين (1ملو 20: 16) وفي وقت الظهيرة كان العمال في فلسطين يتوقفون عن العمل ليستريحوا ويتناولوا الطعام الذي أحضروه معهم (راعوث 2: 14). وكان بطرس على أهبة تناول طعام الغداء في الظهر عندما وصل إليه الرجال الذين أرسلهم كرنيليوس وقد دعاهم بطرس لتناول الغداء معه (أعمال 10: 9 و 10 و 23).أما الوجبة الرئيسية في اليوم فكانت طعام العشاء، وكانوا يتناولونها وقت غروب الشمس عندما كان يتوقف المسافرون عن الاستمرار في رحلتهم ليقضوا الليل (تك 19: 1-3، لو 24: 29 و 30) أو عندما كان العمال يعودون من الحقل (لو 17: 7). هذا هو الوقت الذي أطعم فيه المسيح الخمسة الآف (مر 6: 35) وفي طعام العشاء تتناول الأسرة عادة طبقاً واحداً رئيسياً، كثيراً ما يكون من اللحم والخضراوات المطبوخة. ويعتقد بعض المفسرين أنه عندما قال المسيح لمرثا: ((ولكن الحاجة إلى واحد)) (لو 10: 42) أنه كان يشير إلى هذه العادة في تناول طبق واحد وإن كل الترتيبات الأخرى التي كانت تقوم بها مرثا لم تكن لازمة.(2) العادات المتبعة في تناول الطعام: وضع الجسم عند الجلوس لتناول الطعام: في العصور الأولى كان العبرانيون يتناولون طعامهم وهم جالسون على الأرض أو على حصر كما يفعل البدو في الوقت الحاضر (تك 37: 25). أما استخدام المقاعد فقد جاء تبعاً للانتقال من حياة البدو إلى الحياة الزراعية بعد فتح أرض كنعان. فكان الملك شاول ورفاقه يجلسون على المقاعد (1صم 20: 5) وكذلك الملك سليمان ورجال بلاطه فإنهم كانوا يجلسون على المقاعد (1ملو 10: 5). ولما ازدادت الثروة وكثر الترف في عصر المملكة أصبحت عادة الآتكاء عند تناول الطعام، تدريجياً، وهي العادة المتبعة. وكانت في أيام عاموس تعتبر بدعة المترفين (عا 3: 12 و 6: 4) ولكن بعد ذلك الحين بقرنين نجد حزقيال يتكلم عن الجلوس على السرير الفاخر أمام مائدة منضضة وكأن ذلك لا بدع فيه (حز 23: 41).وكان الأغنياء بين الفرس (استير 1: 6) واليونان والرومان يتكئون وهم يتناولون طعامهم. وفي القرن الثاني قبل الميلاد أصبح الآتكاء عند تناول الطعام أمراً عادياً لدى اليهود (سيراخ 41: 24 في الترجمة اليسوعية). وكان يوضع عادة ثلاثة متكآت حول المائدة ويترك الجانب الرابع خالياً كي يقدم منه الخدم الأطباق للآكلين. وكانت ترتب المتكآت هكذا: العليا فالوسطى. وكانت العليا على يمين الخدم وهم يتقدمون لتقديم الطعام. وكان يجلس على المتكأ الواحد ثلاثة أشخاص عادة؛ ولكن أحياناً ما، كان يجلس أربعة أو خمسة. وكان يتكئ من يأكل ورأسه بجوار المائدة ومرتفع ليتناول الطعام من فوق المائدة، أما جسمه فيمتد بحيث تكون أقدامه في مؤخر المتكأ. وكان الذي يأكل يسند جسمه على مرفقه الأيسر ليترك الزراع الأيمن حراً يتناول به الطعام. كان رأس المتكأ في المقدمة يوضع قريباً من صدر المتكئ الذي يليه ولذا فيظهر أنه أثناء تناول العشاء الرباني كان يوحنا متكئاً في المقدمة أمام المسيح. وكانت المكانة في الجلوس على المتكأ بحسب هذا الترتيب الأعلى فالأوسط فالسفلى. فمن كان يجلس في المكان الأعلى لم يكن هناك إنسان وراء ظهره.ز ومرتبة الشرف والتكريم هي المكانة العليا في المتكأ، وهي التي جلس فيها المسيح عند العشاء الرباني.الاغتسال عند تناول الطعام: وكان العبرانيون يغسلون أيديهم قبل تناول الطعام كما يفعل اليوم وقد أصبحت هذه العادة طقساً وفريضة في ذاتها كما كانت الحال عند الفريسيين. ولقد أوضح السيد المسيح أن نظافة الداخل هي الأمر الجوهري الذي يعتد به (مر 7: 1013) وقد كان أيضاً من عادتهم غسل الأقدام عند تناول الطعام (تك 18: 4 و 19: 2 ولو 7: 44 ويو 13: 54).الصلاة عند تناول الطعام: قبل تناول الطعام طلب صموئيل البركة (1صم 9: 13) وبارك المسيح قبل تناول الطعام (مت 14: 19 و 15: 36 و 26: 26). وطلب المسيحيون الأولون البركة على الطعام (أعمال 27: 35) أما الصلاة بعد الانتهاء من الطعام فقد نشأت عند اليهود مما جاء في تثنية 8: 10.واعتادوا في تناول طعام العشاء أن يغمس كل واحد من الآكلين الخبز في طبق الطعام الواحد (مت 26: 23). وكانت النساء العبرانيات يأكلن مع الرجال (خر 12: 1-11 وراعوث 2: 14 و1 صم 1: 5 وأيوب 1: 4) ويوبخ أمثال 23: 1 و2 النهم عند تناول الطعام.(3) تناول الطعام عند الحكام: وكانت موائد الملوك والحكام كثيراً ما تجمع عدداً كبيراً من الموظفين. وقد غضب الملك شاول لما أمتنع داود أن يأكل في قصره (1صم 20: 5 و6). وكانت موائد سليمان فاخرة جداً حتى أنه عين موظفين مهمتهم الخاصة جمع المؤن من كل أجزاء البلاد (1ملو 4: 7-27). وكانت الملكة إيزابيل في السامرة تطعم بانتظام 450 نبياً من أنبياء البعل و 400 نبي من أنبياء أشيرة (1 ملو 18: 19). وكان نحميا كحاكم يضيف بانتظام 150 من الموظفين ومن الفقراء (نحم 5: 17-19).(4) يسوع وتناول الطعام: إن بعضاً من أهم تعاليم يسوع قد نطق بها أثناء تناول الطعام. ففي عرس قانا الجليل أجرى أول آية وهي تحويل الماء إلى خمر. (يوحنا 2: 1-11). وكضيف في الوليمة التي أقامها له متى أكل يسوع مع جباة الضرائب والخطاة وقال إنه جاء ليخلص مثل هؤلاء عندما أنتقده الفريسييون لأنه كان يأكل مع الخطاة (مت 9: 10-13). وعند تناوله الطعام في بيت سمعان الفريسي مدح يسوع المرأة الخاطئة التي غسلت رجليه ودهنتهما بالطيب توبة منها وإيماناً به (لو 7: 36 50). وقد أظهر فادينا قوته وتحننه في إطعام الخمسة آلاف (مت 14: 13-21) ثم أيضاً في إطعامه الأربعة آلاف (مت 15: 32-38) وعند تناول الطعام في بيت عنيا علم المسيح مرثا أن إعداد الطعام الفاخر غير مهم وهو دون التعليم الروحي الذي كانت تسمعه مريم بكثير (لو 10: 38-42). وعند تناول الغداء في يوم سبت في بيت فريسي شفى يسوع إنساناً كان مريضاً بمرض الاستسقاء وحذر ضد طلب الأماكن الأولى على المائدة وضد دعوة الأغنياء فقط إلى تناول الطعام، وقدم مثل العشاء العظيم (لو 14: 1-24) وعند تناول عشاء في بيت عنيا دهنت مريم قدميه بالطيب وامتدح يسوع وفائها وولائها (يو 12: 2-11). وفي عشاء الفصح الأخير مع تلاميذه تقدم يسوع وغسل أرجلهم وأنشأ فريضة العشاء الرباني ونطق على مسمع تلاميذه بتعاليم عن الروح القدس على جانب عظيم من الأهمية (مت 26: 6-29 يو 13-17). وأثناء تناول طعام العشاء في عمواس عرف التلميذان الرب المقام لما كسر الخبز وأعطاهما (لو 24: 28-31). وبعد هذا أكل يسوع بعض من سمك مشوي مع تلاميذه ليظهر لهم صدق وحقيقة جسده بعد القيامة من الأموات (لو 24: 41 و42). وعند تناول طعام الإفطار على شواطئ بحر الجليل عهد يسوع ثلاث مرات إلى بطرس بأن يرعى خرافه الذين هم جماعة المؤمنين (يو 21: 9-23).(5) الأكل المقدس: كان يسترجع الفصح إلى ذاكرة العبرانيين ذكرى نجاة الأبكار وقت أن كانوا في مصر (حر 12: 1-27). وكان الاشتراك في تناول الطعام المقدس يرتبط مع كثير من الذبائح العبرانية وبخاصة ذبيحة السلامة (لاويين ص3). وبعدما كانت تقدم أجزاء من بعض الذبائح للرب على المذبح كان الكهنة ومعشر العابدين يأكلون الباقي منها (لاويين 2: 1 و7: 6). ونرى في 1 صم 9: 11-24 مثلاً لتناول الطعام بعد تقديم الذبيحة في أيام صموئيل. وكان الوثنيون يشتركون في موائد مقدسة لديهم (1ش 66: 17) ومع هذه الموائد أحياناً ما كانوا يسكرون (عا 2: 8).ويستعيد عشاء الرب المسيحي إلى الذاكرة ذبيحة المسيح عنا (مت 26: 26-29 و 1 كو 11: 23-26). وفي بعض الأحيان كان يسبق عشاء الرب في الكنيسة الأولى وليمة تسمى وليمة المحبة (يهوذا عدد 12 و1 كو 11: 17-22).ويشبه انتصار المسيح وشركة المفديين معه بعشاء عرس الحمل في رؤيا 19: 9.انظر أيضاً:\n((وليمة)) ((وضيافة)) ((وطعام)).مأكََََلُ للنَََار:\nالكلمة العبرية للوقود هي ((مأكلت ايش)) (1ش 9: 5 و19) و ((اكله)) وبعض أنواع الوقود المذكورة في الكتاب المقدس هي: الشوك الذي يحدث شقشقة عند احتراقه (جا 7: 6) وأغصان الكروم (حز 15: 4 و 6) والجل المجفف (حز 4: 15) وعشب الحقل (مت 6: 30) والحطب أكثر أنواع الوقود شيوعاً (عدد 15: 32 وأعمال 28: 3 وغيره) وكان يصنع الفحم الخشبي من نبات الرتم (مز 120: 4) وكذلك من البلوط. وربما كان يشعل موقد الملك يهويا قيم بالفحم الخشبي (ارميا 36: 22). وقد استعمل خدام رئيس الكهنة الفحم الخشبي للتدفئة (يو 18: 18) ومنه أيضاً الجمر الذي استخدمه يسوع في شي السمك للتلاميذ (يو 21: 9).ويستخدم الوقود في الكتاب المقدس في عدة نواح رمزية. فإسرائيل (حز 15: 6) وعمون (حز 21: 32) يأكلهما القضاة كما تأكل النار الوقود. وعندما ينقذ إسرائيل يكون كشعلة منشَلة من النار (زكريا 3: 2). وترمز نار الكبريت إلى عذاب جهنم (رؤيا 20: 10).'),
(278, 'أكْلِيمَنّدُس', 'اسم لاتيني معناه ((رقيق أو رحيم)) وهو رجل مسيحي في فيليبي وكان عاملاً مع بولس الرسول (فيليبي 4: 3) ويرَجح أنه يختلف عن اكليمدس أحد مسيحي روما الذي كتب رسالة إلى الكنيسة في كورنثوس حوالي عام 90 ميلادي.'),
(279, 'أكمَة', 'أنظر ((عوفل)).'),
(280, 'أكمة جَارب', 'أنظر ((جارب)).'),
(281, 'أكِيلا', 'اسم لاتيني معناه ((نسر)) وهو يهودي ولد في بنتس من أعمال آسيا الصغرى.وقد قبل الإيمان المسيحي وأصبح زميلاًَ لبولس في الخدمة. وقد أقام أكيلا وزوجته بريسكلا مدة من الزمن في رومية ولكنهما اضطرا إلى ترك المدينة عندما أصدر الإمبراطور كلوديوس أمراً إلى كل سكانها من اليهود بتركها. فانتقل إلى كورنثوس حيث أخذ معه صناعته وهي صناعة الخيام. وقد أقام معه بولس هناك لأنه كان من أبناء صناعته ويحتمل أنه عن طريق شهادة الرسول قبل أكيلا وامرأته بريسكلا الإيمان المسيحي (أعمال 18: 1-3) وقد رافقه في السفر من كورنثوس إلى افسس (أعمال 18: 18 و19) ونقرأ في 1 كو 16: 19 أنهما اشتركا مع الرسول في إرسال تحياتهما من آسيا، ويرجح أنهما كانا في ذلك الحين في افسس حيث كانت الكنيسة تجتمع في بيتهما. وقد قابلا ابلوس في افسس وعلماه بأكثر وضوح عن التعاليم المسيحية (أعمال 18: 26) وقد رجعا فيما بعد إلى رومية لأننا نجد بولس يرسل إليهما تحياته إلى رومية (رو 16: 3) ويرَجح أنهما كانا في افسس مرة ثانية عندما أرسل إليهم الرسول تحياته في 2 تيمو 4: 19.'),
(282, 'ألإِسْكَندر', 'اسم يوناني معناه ((حامي البشر)) وقد ورد هذا:(1) اسماً للاسكندر الكبير ملك مكدونيا (336-323 ق. م.) وفاتح إمبراطورية الفرس. ويقول يوسيفوس المؤرخ اليهودي الذي عاش في القرن الأول الميلادي أن الاسكندر بعد أن أخذ غزة ذهب إلى أورشليم وهناك تقابل مع رئيس الكهنة جدوع الذي أخبر الاسكندر بالنبوات الخاصة به في سفر نبوات دانيال. وتمثل الرؤيا الواردة في دانيال ص 8 بلاد اليونان في شكل تيس من الماعز قرنة الأول العظيم رمز للاسكندر الكبير (عدد 21) وأنه هو الملك الجبار المذكور في دانيال 11: 3. ويقول يوسيفوس أن الاسكندر منح اليهود عدة أمتيازات. ولما ذهب الاسكندر إلى مصر زار هيكل زيوس-امون في واحة سيوة، وقد أعلنت النبوة أنه ابن لذلك الإله. ولذا فنراه يظهر بعد ذلك على قطع النقود وله قرنا كبش الإله المصري آمون، وجلد الأسد الذي للإله اليوناني هيركليز الذي كان ابناً لزيوس وبسبب قطع العملة هذه دعا القرآن الاسكندر بذي القرنين (سورة 18:ك 83 و 86 و 89). وقد مات الاسكندر في بابل في سن الثالثة والثلاثين- وهي نفس السن التي صلب فيها المسيح. وكما أشار سفر دانيال (ص 8: 22 و 11: 4) أنه بعد موت الاسكندر ستقتسم مملكته بين قواده الأربعة هكذا حدث. أما الأثر الباقي لفتوحاته فهو انتشار اللغة اليونانية في كل ربوع الشرق الأدنى. ولذا فقد مهدت الطريق للترجمة السبعينية اليونانية للعهد القديم. وقد كانت هذه اللغة شائعة في كل الأقطار ما حول شرقي البحر الأبيض المتوسط وكان يفهمها معظم شعوب تلك البقاع.(2) الكسندرس ابن سمعان القيرواني الذي حمل صليب المسيح (مر 15: 21) ويظن الكثيرون أن الكسندرس ذكر في هذه المناسبة لأنه كان مسيحياً.(3) الاسكندر أحد أقرباء رئيس الكهنة حنان، وعضو في المجلس الذي فحص بطرس ويوحنا (أعمال 4: 6).(4) اسكندر رجل يهودي من افسس حاول أن يدافع عن اليهود أمام الشعب في المسرح (أعمال 19: 33).(5) اسكندر رجل من افسس صار مسيحياً ولكنه ترك الإيمان الصحيح والأخلاق القويمة ولذلك يوبخه الرسول بولس أشد التوبيخ ويوقع عليه دينونة (1 تيمو 1: 20).(6) اسكندر رجل نحاس في افسس قاوم تعاليم الرسول بولس ورفاقه (2تيمو 4: 14 و 15) ويحتمل أن اسكندر هذا والمذكور في (1 تيمو 1: 20) هما شخص واحد.'),
(283, 'ألاسكندرية', 'مدينة على شاطئ مصر الشمالي، أسسها الاسكندر الكبير في سنة 332 ق. م. وقد سميت باسمه. وكانت الإسكندرية تشتمل البلدة المصرية القديمة ركوتس التي قبل تأسيس الإسكندرية، وتبعد 14 ميلاً غربي مصب فرع كانوب (رشيد)، أحد فرعي النيل الرئيسيين، وكانت تقع على قطعة من الأرض بين بحيرة مريوط والبحر الأبيض المتوسط. وقد بني حاجز في وسط المياه طوله ميل يوصل بين الشاطئ وجزيرة فاروس، وقد ساعد هذا الحاجز على جعل ميناء الإسكندرية أحسن ميناء في مصر.وأقيم على جزيرة فاروس فنار ارتفاعه أربعمائة قدم وكان يعتبر احدى عجائب الدنيا القديمة السبع. وأصبحت الإسكندرية بعد تأسيسها مركزاً للتجارة بين الشرق والغرب وقد زاد عدد سكانها إلى أن بلغ 600000 نسمة. وقد كانت مدينة الإسكندرية عاصمة البلاد المصرية طوال البطالسة والرومان والبيزنطيين إلى الفتح العربي في عام 640 ميلادي. وكان الميوزيوم (المتحف) مركزاً للدراسة العلمية وكانت مكتبة الإسكندرية أكبر مكتبة في العالم القديم إذ كانت تضم ما يزيد على 000 ،500 مجلد. وكان من أهم معابدها السرابيوم، هيكل الإله سرابيس وهو إله مكون من امتزاج الإله اوزيوس بالإله ابيس. ويظن بعضهم أنه خليط من زيوس الإله اليوناني وأوزيرس إله العالم الأخر عند عند المصرين القدماء. وأعظم ما تبقى من آثار الإسكندرية هو ما يسمى خطأ عمود بمباي.ولكنه في الحقيقة عمود دقلديانوس الذي أقامه حوالي سنة 303 ميلادية، ويسمى عمود السواري، ويحتمل أنه كان منصوباً في السرابيوم. وسراديب المقابر التي في كوم الشقافة تظهر امتزاجاً بين الفن الإغريقي والفن المصري والديانة الإغريقية والديانة المصرية القديمة.وقد منح البطالسة اليهود امتيازات خاصة في الإسكندرية فأقبلوا إليها بكثرة، حتى كانوا يسكنون قسمين من أقسام المدينة الخمسة. وقد ترجم العهد القديم من العبرانية إلى اليونانية في مدينة الإسكندرية وقد بدأت ترجمته حوالي عام 285ق. م في عصر الملك بطليموس فيلادلفس.وهذه هي الترجمة المعروفة بالترجمة السبعينية. وقد حاول الفيلسوف اليهودي فيلو أن يوفق بين الفلسفة الإغريقية والوحي عند العبرانيين، وقد فسر العهد القديم تفسيراً مجازياً وعاش فيلو في القرن الأول الميلادي.ويشير دانيال ص 11 إلى البطالسة في الإسكندرية كملوك الجنوب. وقد حاور استفانوس مجمعاً في أورشليم كان بعض أعضائه من يهود الإسكندرية (أعمال 6: 9). وكان ابلوس مثلاً للثقافة اليهودية في الإسكندرية (أعمال 18: 24). وتظهر أهمية المدينة التجارية من أن الرسول بولس في ميرا في ليكية بآسيا الصغرى نزل في سفينة إسكندرية محملة بالحبوب إلى إيطاليا في رحلته إلى روما (أعمال 27: 6) وأتم رحلته من مالطة إلى إيطاليا على سفينة إسكندرية أخرى (أعمال 28: 11). ويقول التقليد أن مرقس البشير حمل رسالة الإنجيل إلى الإسكندرية وأنه استشهد هناك. وأصبحت المدينة فيما بعد مركزاً للثقافة والتعليم المسيحيين وبخاصة في عصر اكليمندس الإسكندري واوريجانوس. وأهم المخطوطات اليونانية للكتاب المقدس كتبت على الأرجح في مدينة الإسكندرية، وخاصة تلك المسماة بنسخة الإسكندرية، بنسخة الفاتيكان، والنسخة السينائية.'),
(284, 'إِسكندريون', 'كان هناك اسكندريون أعضاء مجمع التحررين (الليبرتينيين) في أورشليم (أعمال 6: 9) وقد حاور أعضاء من هذا المجمع استفانوس واتهموه بتهم باطلة (أعمال 6: 9-14).'),
(285, 'ألْتَقُون', 'كلمة عبرية ربما معناها ((الله هو الأساس)) وهي اسم قرية في أرض يهوذا الجبلية (يش 15: 59) ويرَجح أن مكانها اليوم ((خربة الدير)) الحديثة التي تبعد مسافة أربعة أميال غربي بيت لحم وهي على بعد ميلين جنوبي ((حوسان)).'),
(286, 'إِلْتَقَى إِلْتَقَيْه', 'كلمتان عبرانيتان بمعنى ((الله خوفها)) وهما اسم مدينة في دان تعينت لللاويين (يش 19: 40 و44 و 21: 20 و 23) وفي سنة 701 ق.م هزم سنحاريب ملك آشور المصريين بالقرب من التقى وأخرب البلدة.ويرجّح أنها نفس ((خربة المقنع)) الحديثة التي تقع على بعد 6 أميال جنوبي عقير (عقرون) و7 أميال شمالي تبنة (تمنة).'),
(287, 'أْلتُولَد', 'كلمة عبرية ربما معناها ((إله الميلاد)) وهي بلدة في الطرف الجنوبي من يهوذا (يش 15: 21 و 30) وقد تعينت ضمن نصيب الشمعونيين (يش 19: 4) وتدعى في 1 أخبار 4: 29 ((تولاد)) ويرّجح أنها نفس ((خربة عرقة سقرة)) الحديثة التي تبعد مسافة 13 ميلاً جنوبي شرقي بئر سبع.'),
(288, 'ألْحانان', 'اسم عبري معناه ((اللَّه الحنان)) وهو اسم:(1) ابن ياعير وهو الذي قتل أخا جليات الجتي (1 أخبار 20: 5) وقد ورد في 2 صم 21: 19 أن أباه بيتلحمي،وأن الحانان نفسه نفسه هو الذي قتل جليات ولكن من الواضح أنه يقصد ((قتل أخا جليات)).(2) رجل بيت لحمي هو أبن دودو وأحد أبطال داود الثلاثين (2 صم 23: 24 و 1 أخبار 11: 26).'),
(289, 'ألْدَاد', 'اسم عبري معناه ((من أحبه اللَّه)) وهو واحد من الشيوخ السبعين الذين انتخبوا لمعاونة موسى في القضاء لبني إسرائيل في البرية. ولم يذهب الداد وميداد إلى خيمة الاجتماع مع الشيوخ الآخرين ولكن روح الرب حل عليهما أيضاً وتنبأ كلاهما، وقد أراد يشوع أن يمنح الداد من أن يتنبأ ولكن موسى أنب يشوع وقال: ((يا ليت كل شعب الرب كانوا أنبياء)) (عدد 11: 24-29).'),
(290, 'ألْدَعه', 'اسم عبراني معناه ((من دعاة اللَّه)) وهو ابن مديان (تك 25: 4 و 1 أخبار 1: 33).'),
(291, 'ألْزَاباد', 'اسم عبراني معناه ((قد أنعم اللَّه)) وهو:(1)رجل جادي محارب آتى إلى داود (1 أخبار 12: 8 و 12).(2) لاوي من أسرة عوبيد ادوم وكان من حراس الأبواب في بيت الرب في أورشليم (1 أخبار 26: 7).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(292, 'ألْصافَان أو اليصافان', 'اسم عبراني معناه ((اللَّه قد أخفى)) وهو ابن عزيئيل ورئيس القهاتين في البرية (خر 6: 18 و 22 وعدد 3: 30) وقد ساعد في نقل جسمي ناداب وابيهو إلى خارج المحلة (لا 10: 4) وقد اتخذ أحد بيوت الآباء اسمه منه (1 أخبار 15: 8 و2 أخبار 29: 13) وقد ورد اسمه في سفر العدد وسفر أخبار الأيام بصورة اليصافان ومعناه مثل الصافان تماماً.'),
(293, 'ألِعَاد', 'لسم عبراني معناه ((اللَّه قد شهد)) وهو ابن افرايم. وقد قتل هو وأخوه عزر عندما حاولا سرقة ماشية رجال جت، وقد ناح عليهما افرايم أبوهما أيام كثيرة (1 أخبار 7: 21 و22).'),
(294, 'ألِعَادا', 'اسم عبراني معناه ((من جمَّله اللَّه)) وهو ابن تحث من سبط افرايم (1 أخبار 7: 20).'),
(295, 'ألِعَازَار', 'اسم عبري معناه ((اللَّه قد أعان)) وقد ورد:(1) اسم ابن هارون الثالث (خر 6: 23) وقد كرس كاهناً مع أبيه وأخوته (خر 28: 1) وقد منع موسى العازار من النوح والظهور بمظهر الحزن عند موت أخويه ناداب وابيهو فقد قتلا عندما قربا ناراً غريبة (لا 10: 1-7) وقد أصبح حينئذ العازار رئيس اللاويين، وثانياً لهارون في السلطة الكهنوتية (عدد 3: 32) وقبل موت هارون مباشرة نقل موسى ثياب الكهنوت ووظيفته (عدد 20: 25-28 وتثنية 10: 6) وظلّ في هذه الوظيفة بقية حياة موسى ومدة قيادة يشوع. وقد كان له نصيب كبير في توزيع أرض كنعان بالقرعة على أسباط إسرائيل (يش 14: 1) وقد  خلفه في رئاسة الكهنوت ابنه فينحاس (قض 20: 28).(2) اسم لاوي من عشيرة مراري من بيت محلي. وقد مات دون أن يعقب ابناً وقد تزوجت بناته أبناء عمهن (1 أخبار 23: 21 و 22 و 24: 28 قارنه مع عدد 36: 6-9)(3) اسم ابن ابيناداب. وقد كرس رجال قرية يعاريم العازار حراسة التابوت عندما ارسل إليهم من بيتشمس (1 صم 7: 1)(4) اسم أحد أبطال داود بن اخوخي (2 صم 23: و 1 أخبار 11: 12).(5) اسم كاهن وهو ابن فينحاس وقد ساعد رئيس الكهنة في عصر عزرا (عزرا 8: 33).(6) اسم كاهن كان أحد الذين خدموا كعازفين على الآلات الموسيقية عند تدشين سور أورشليم في عصر نحميا (نحم 12: 27 و42). ويظن بعضهم أنه نفس الرجل المذكور في عزرا 8: 33.(7) اسم ابن فرعوش. وقد أقنعه عزرا أن يخرج امرأته الغريبة (عزرا 10: 18 و25).(8)اسم ابن متتيا الكاهن وأخي يهوذا المكابي الابوكريفا (1 مكا 2: 5) وفي المعركة التي حدثت بين يهوذا والسوريين في بيت زكريا ذهب إلى أحد فيلة السوريين ظاناً أن الملك على هذا الفيل ونزل تحته وقتله فسقط عليه الفيل ومات في مكانه (مكا 6: 43-46).(9) اسم ابن اليهود وأحد أسلاف يسوع المسيح (مت 1: 15) وقد ورد بصورة اليعازار وورد في الترجمة اليسوعية للكتاب المقدس بصورة العازار.'),
(296, 'إِلِعَاسَة', 'اسم عبراني معناه ((قد صنع اللَّه)) وهو:(1) اسم رجل من نسل يهوذا ومن نسل يرحميثيل (1 أخبار 2: 39).(2) اسم رجل من نسل شاول ويوناثان ابنه (1 أخبار 8: 33-37 و 9: 43) وقد ورد ذكره في هذا النص الأخير بصورة ((العسة)).(3) اسم ابن لشافان وقد حمل هو وجمريا بن حلقيا رسالة من ارميا في أورشليم إلى المسبيين في بابل (ارميا 29: 1-3).(4) اسم ابن فشحور وقد اقنع عزرا العاسة أن يخرج امرأته الغريبة (عزرا 10: 19 و22).'),
(297, 'ألِعَالَة', 'كلمة عبرانية ربما كان معناها ((اللَّه قد صعد)) وهي اسم بلدة في شرقي الأردن أخذها الرؤبينيون وأعادوا بنائها (عدد 32: 3 و37) ثم فيما بعد أخذها المؤابيون (1 ش 15: 4 و 16: 9 وارميا 48: 34) وخرب هذه البلدة تسمى الان ((العال)) وهي على قمة تل على بعد ميلين شمالي حشبون.'),
(298, 'إِلِعُوزَاي', 'اسم عبري معناه ((اللَّه قوتي)) وهو احد الأبطال الذين جاءوا إلى داود في صقلغ (1 أخبار 12: 1 و 5).'),
(299, 'ألف', 'اسم الحرف الأول في الأبجدية العربية، والفا اسم الحرف الأول في الأبجدية اليونانية التي أخذت عن الأصل السامي. والألف أحد ألقاب المسيح التي وردت في سفر الرؤيا ويقصد بهذا اللقب أن المسيح هو قبل كل الأشياء وأنه بدء كل شيء (رؤيا 1: 8 و11 و 21: 6 و 22: 13 قارنه مع اش 44: 6).'),
(300, 'ألِفَالَط', 'اسم عبري معناه ((اللَّه نجاة)) وهو ابن داود (1 أخبار 14: 5) وقد ورد ذكره في أخبار 3: 6 بصورة ((اليفالط)).'),
(301, 'ألْفَعَل', 'اسم عبري معناه ((اللَّه قد فعل)) وهو رجل بنياميني ابن شحرايم وكان رئيس بيت من بيوت الآباء (1 أخبار 8: 1 و 8 و 11 و 12 و 18).'),
(302, 'الألقوُشِي', 'وكان النبي ناحوم القوشيا. ويذكر تقليد أن القوش كانت من ضمن بلدان الجليل. ويقول تقليد آخر أنها كانت تقع جنوبي بيت جبرين في منحدرات يهوذا. أما التقليد المتأخر الذي يقول أن موطن ناحوم كان في أرض أشور على مسافة سفر يومين شمالي الموصل فلا قيمة له.'),
(303, 'الكِسَنْدُرس', 'انظر الاسكندر.'),
(304, 'أَلاَّسار', 'مكان في بابل. ويرّجح أنه نفس لارسا القديمة. ومكانه اليوم سنكرة جنوبي شرقي ارك. وكان اريوك ملك الاسار من الأربعة ملوك الذين أتوا من الشرق وهاجموا شرقي فلسطين وأخذوا لوطاً أسيراً (تك 14: 1 و9) وكانت لارسا إحدى المدن الرئيسية في بابل حوالي سنة 2000 ق. م.'),
(305, 'الليِّرِيكُون', 'مقاطعة رومانية على الشاطئ الشرقي للبحر الادرياتيكي. وقد سميت هذه المقاطعة فيما بعد باسم دلماطية وهي الآن جزء من يوغسلافيا ويقول بولس الرسول في الرسالة إلى أهل رومية ص 15: 19 أنه نادى بانجيل المسيح من أورشليم وما حولها إلى الليركيون. وأنه لسبب خدمة بولس التبشيرية وغيره من المسيحيين تثبتت رسالة المسيح وتأصلت المسيحية في الليريكيون أثناء القرن الأول والثاني الميلاديين. والعالم المسيحي جيروم (340-420 ميلادية) الذي ترجم الكتاب المقدس إلى اللاتينية تلك الترجمة المعروفة باسم ((الفلجات)) ولد في الليريكون.'),
(306, 'أَلُّون', 'اسم عبري معناه ((بلوطة)) وهو اسم ابن يدايا من سبط شمعون (1 أخبار 4: 37).'),
(307, 'ألُّون بَاكوت', 'اسم عبري معناه ((بلوطة البكاء)) وهو اسم بلوطة بالقرب من بيت ايل وقد دفنت تحتها دبورة مرضعة رفقة (تك 35: 8).'),
(308, 'ألَّمَّلَك', 'كلمة عبرانية معناها ((بلوطة الملك)) وهي اسم قرية في اشير (يش 19: 26) ويحتمل أن يكون في اسم المكان الحديث المسمى ((وادي الملك)) والذي يفرغ مياه سهل البطّوف في نهر قيشون مقابل جبل الكرمل، بقية في الاسم القديم.'),
(309, 'ألم. آلام. تألم', 'تشير الكلمة تألم كما وردت في أعمال 1: 3 إلى آلام المسيح ابتداء من ألمه في بستان جثسيماني إلى موته على الصليب. ويعلمنا الكتاب المقدس أن آلام المسيح كانت لأجل فداء الجنس البشري (1 بط 3: 18). وفي أعمال 14: 15 ويعقوب 5: 17 تستخدم الكلمة ((ألم)) عن المشاعر والعواطف الإنسانية.'),
(310, 'الموداد', 'اسم عبري معناه ((اللَّه صديق)) وهو اسم ابن يقطان من نسل سام (تك 10: 26 و 1 أخبار 1: 20) وربما قطن هو ونسله جنوبي بلاد العرب.'),
(311, 'المودام', 'وهو ابن عير في سلسلة نسب يسوع المسيح (لو 3: 28).'),
(312, 'الناثان', 'اسم عبري معناه ((اللَّه أعطى)) وهو اسم:\n(1) أبي نحوشتا أم الملك يهوياكين (2 ملو 24: 8) وقد سكن في أورشليم ويرّجح أنه نفس الأمير الناثان الذي أرسله الملك يهوياقيم إلى مصر (ارميا 36: 12 و25).(2و 3 و 4) ثلاثة لاوين. الاولان رئيسان والثالث معلم دعاه عزرا إلى نهر اهوا في بابل ليعدّوا العدّة للرجوع إلى أورشليم (عزرا 8: 15 و 16).'),
(313, 'النعم', 'اسم عبراني معناه ((اللَّه سرور)) وهو أبو يريباي ويوشويا وهما بطلان من أبطال جيش داود (1 أخبار 11: 46).'),
(314, 'آلهة', 'أكثر معاني هذا الاسم الجمع شيوعاً وهو يطبق على الآلهة الوثنية الباطلة كما نجد هذا في خر 20: 3 و 1 اكو 8: 5. وتستعمل هذه الكلمة للقضاة بما أنهم يمثلون اللَّه ويعملون نيابة عنه (مز 82: 1 و138: 1 ويوحنا 10: 34).'),
(315, 'أللَّه', 'هذا اسم الإله خالق جميع الكائنات والحاكم الأعظم لجميع العوالم، والواهب كل المواهب الحسنة. واللَّه ((روح غير محدود، أزلي غير متغير في وجوده وحكمته وقدرته وقداسته وعدله وجودته وحقه)) وهو يعلن لنا نفسه بطرق متنوعة وفي أحوال مختلفة متباينة فيظهر لنا في أعماله، وتدبير عنايته (رو 1: 20) ولكنه يتجلى غاية التجلي ويظهر ذاته في الكتاب المقدس. وقد أعلن لنا نفسه بأجلى بيان وعلى أكمل كيفية في شخص ابنه الوحيد مخلصنا يسوع المسيح وعن طريق حياته وأعماله.\nوفي تأملنا في اللَّه نتدبر الأمور الآتية:(1) أسْماء اللَّه:ويوجد في العهد القديم باللغة العبرية ثلاث مترادفات رئيسية لاسم الجلالة وهي ((الوهيم)) و ((يهوه)) و ((ادوناي)). فالاسم الأول مستعمل كثيراً في الإصحاح الأول من سفر التكوين. ويكثر استعماله في مز 43-72 تلك المزامير التي سميت بمزامير الوهيم. ويستعمل على التبادل مع الاسمين الآخرين فيما بقي من أسفار العهد القديم. ويدل هذا الاسم على صفة اللَّه كالخالق العظيم. وعلى علاقته مع جميع شعوب العالم من أمم ويهود. أما الاسم الثاني فيدل على علاقة اللَّه مع بني إسرائيل وهو إله تابوت العهد وإله الرؤيا والإعلان وإله الفداء. أما ادوناي فتستعمل في مخاطبة اللَّه بخشوع ووقار وهيبة. وكان اليهود يستعملون ادوناي عوضاً عن يهوه وهي كلمة لم يكونوا يلفظونها على الإطلاق.غير أن هذه الكلمات الثلاث لا ترد في الترجمة العربية بصيغها العبرانية، إنما تستعمل بدلا منها ألفاظ اللَّه ويهوه والرب أو السيد.(2) طبيعة اللَّه: \nاللَّه واحد وهو ثلاثة اقانيم متساوية في الجوهر (مت 28: 19 و 3 كو 13: 14)، اللَّه الآب، واللَّه الابن، واللَّه الروح القدس، فالآب هو الذي خلق العالمين بواسطة الابن (مز 33: 6 وكو 1: 16 وعب 1: 2) والابن هو الذي أتم الفداء وقام به، والروح القدس هو الذي يطهر القلب والحياة، غير أن الاقانيم الثلاثة يشتركون معاً في جميع الأعمال الإلهية على السواء. ولا تظهر عقيدة التثليث واضحة كل الوضوح في العهد القديم كما تظهر في العهد الجديد. وقد أشير إلى التثليث في تك ص 1 حيث ذكر ((اللَّه)) و ((روح اللَّه)) الخ (قابل مز 33: 6 ويو 1: 1و 3) و ((الحكمة الإلهية)) المتجلية في أمثال ص 8 تقابل ((الكلمة)) في يوحنا ص 1. وهي تشير إلى الاقنوم الثاني في اللاهوت، وتطلق الصفات الإلهية على كل اقنوم من هذه الاقانيم الثلاثة على حدته.(3) وحدة اللَّه:وهذه ظاهرة بوضوح وجلاء في العهد القديم كما تظهر أيضاً في العهد الجديد. غير أن التثليث أمر بيّن واضح في العهد الجديد ويشار إليه، ويفهم ضمناً في العهد القديم. وأعظم داع لابراز وحدة اللَّه هو إظهار خطأ إشراك آلهة أخرى معه؛ ومنع عبادة الأوثان التي كانت كثيرة الشيوع في الأزمنة الأولى قديماً. ففي تثنية 6: 4 يدعى اللَّه ((رباً واحداً)) ويدعى العهد القديم أيضاً ((الإله الحي)) تميزاً له عن آلهة الوثنيين الباطلة.والاعتقاد بأن اللَّه واحد بيّن جداً وجلّي في الديانة اليهودية كما أنه بيّن جداً في الديانة المسيحية.(4) صفات اللَّه:وتظهر صفات اللَّه وأعماله، فقد أكمل جميع الكائنات فهو قدوس (يش 24: 19) لا يفنى (1تي 1: 17) حاضر في كل مكان (مز 139: 7 و1 ع 17: 24) قدير (تك 17: 1) غير متغير (مز 102: 26) عادل (ار 9: 24) رحوم (مز 136) حكيم (أي 12: 13) وهو محبة (1 يو 4: 16) وهناك كثير من الصفات غير هذه التي تظهر في أعمالِه التي تفوق إدراك العقل البشري وتزيد عن الحصر.ابن اللَّه:اطلق هذا اللقب على المسيا (مز 2: 7 ويو 1: 49) وهو يدل على العلاقة القوية المكينة بين ألآب السماوي والابن الأزلي. وقد استعمل هذا اللقب في العهد الجديد ما يقارب من 44 مرة عن يسوع المسيح. واللَّه ألآب يحب ابنه (يو 3: 35 و10: 17) ألآب هو الذي أرسل الابن ويعمل به (يو 3: 16 و 17 و 8: 42) وغلا 4: 4 وعب 1: 2) والمسيح بما أنه ابن الله فهو إله بكل الكالات غير المحدودة التي للجوهر الإلهي (يو 1: 1-14 و10: 30-38 وفيليبي 2: 6) والابن مساو لله في الطبيعة (يو 5: 17-25) ومن هذه الاعتبارات فالمسيح فريد في هذا وهو ((ابن الله)) ليس من وجهة النظر الجسدية كما يفهم من الكلمة ((ولد)) إنما يفهم به كتشبيه ليعبر عن مقدار المحبة والتعاون والتساوي في الطبيعة بين الاقنوم الأول والاقنوم الثاني في الثالوث الأقدس.وقد قال المسيح عن نفسه أنه ابن الله (يو 5: 17-47 و 10: 36 و 11: 4) وقد اتهمه كهنة اليهود وحاكموه وحكموا عليه لأنه قال أنه المسيح ابن الله (مت 26: 63-66) وكثيراً ما أطلق الرسل هذا اللقب على المسيح (أعمال 9: 20 وغلا 2: 20 و 1 يو 3: 8 و 5: 5 و 10 و13 و 20).والبراهين على أن المسيح هو ابن الله الأزلي كثيرة في العهد الجديد، فعند معمودية المسيح جاء صوت من السماء قائلاً: ((هذا هو ابني الحبيب)) (مت 3: 17) وقد جاء الصوت بنفس هذه الكلمات عند تجلي المسيح (مت 17: 5) وإن أخلاق الرسل وأعمال المسيح المعجزية لبراهين قوية على أنه ابن الله بواسطة القيامة من الأموات (رو 1: 4) وبصعوده إلى السماء (عب 1: 3).واستخدم هذا اللقب ((ابن الله)) عن المسيح بنوع خاص في التحدث عن عمل الفداء العظيم الذي أجراه فهو النبي الأعظم (عب 1: 2) وهو الكاهن الأعظم (عب 5: 5) وهو الملك العظم (عب 1: 8).أبْناء الله:وقد ورد هذا اللقب:(1) للملائكة لأن الله هو خالقهم وضابطهم (أي 1: 6 و 2: 1 و38: 7 ومز 29: 1 و89: 6 ود 3: 25).(2) وقد دعي آدم ابن الله لأن الله خلقه مباشرة (لو 3: 38).(3) دعي شعب إسرائيل ابن الله أو أبناء الله بما أنهم كانوا موضوع محبته الخاصة وعنايته (خر 4: 22 و 23 وتثنية 14: 1 و32: 5 و 6 و 19 واش 43: 6 و 7 وهو 1: 10).(4) استعمل هذا التعبير ((أبناء الله)) في العهد الجديد عن المؤمنين بالله بنوع خاص. فيصبح المؤمنون أبناء الله بالميلاد الجديد (يو 3: 3و 5 و 6 و 8).إنهم مولودون من الله بالمعنى الروحي (يو 1: 12 و 13 و 5: 21 وافس 2: 5 ويعق 1: 18 و 1 بط 1: 23) وعليهم أن ينموا في مشابهتهم الله في القداسة والمحبة (1 يو 3: 9 و4: 7 و5: 4) وقد صار المؤمنون أبناء الله بالتبني (غلا 4: 5) ويعلمهم الروح القدس أن يقول (( أبا أي ألآب)) (غلا 4: 6 ورو 8: 15) وروح الله القدوس هو الذي يرشدهم ويقودهم (رو 8: 14).(5) أما معنى التعبير ((أبناء الله)) الوارد في تك 6: 2 فقد حدث كثير من النقاش حول تفسيره:ا- فمنهم من قال أن ((أبناء الله)) المقصودين في هذا الموضع هم الملائكة (انظر رقم 1 أعلاه) الذين تركوا حالتهم السماوية واتخذوا لأنفسهم زوجات من بنات الناس. وقد ورد هذا التفسير في الترجمة السبعينية في سفر اخنوخ. وكذا قال به فيلو ويوسيفوس وجستن مارتر واكليمندس الإسكندري وترتليان.ب- واعتقد الآخرون أن العبارة أبناء الله الواردة في تكوين 6: 2 تعني الناس الأتقياء، عباد الله وبنوع خاص نسل شيث الصالح وقد تزوج هؤلاء نساء لم يكن من النسل الصالح ولذا فقد حلّ بهم القصاص. ويؤيد هذا الرأي من النصوص الواردة في رقم 3 و 4 من هذا البند. وقد قال به أيضاً يوليوس افريكانوس وكريستم (يوحنا فم الذهب) وكيرلس الكبير بطريرك الإسكندرية وأغسطينوس وجيروم.'),
(316, 'ألُوش', 'اسم مكان حل فيه الإسرائيليون أثناء ترحالهم (عدد 33: 13 و 14) وكان بالقرب من رفيديم التي كانت المكان الثاني الذي حلوا فيه.'),
(317, 'ألُوي ألُوِى لِمَا شَبقْتَنِي؟', 'هذه كلمات آرامية نطق بها المسيح وهو على الصليب (مر 15: 34) ومعناها: ((إلهي إلهي لماذا تركتني؟)) وقد وردت هذه العبارة في متى 27: 46 على هذه الصورة ((إيلي إيلي لما شبقتني؟)) وهي في معناها كالعبارة السابقة. وقد وردت هذه العبارة في الترجوم. وهي الترجمة الآرامية لمزمور 22: 1 وتظهر هذه العبارة مقدار الألم الروحي الذي عاناه المسيح على الصليب وقد قال بعضهم بأن المسيح كان في تلك اللحظة يحمل خطية العالم بأسره، ولذا فقد شعر بالانفصال الوقتي عن ألآب السماوي.'),
(318, 'ألْيَاب', 'اسم عبراني معناه ((الله اب)) وهو اسم:(1) ابن حيلون ورئيس سبط زبولون في البرية (عدد 1: 9 و2: 7 و 7: 24 و 29 و 10: 16).(2) اسم رجل راؤوبيني، وهو ابن فالت وابي داثان وابيرام (عدد 16: 1 و 12).(3) اسم رجل لاوي من أسلاف صمؤيل(1 أخبار 6: 27) ويدعى في 1 صم 1: 1 ((اليهو)) وفي 1 أخبار 6: 34 يدعى ((إِيليئيل)).(4) اسم أخي داود الأكبر. وكان طويل القامة ولكن لم تكن روحه كروح داود (1 صم 16: 6 و 7 و 17: 13) وقد استهان بداود قبل مبارزته لجليات (1 صم 17: 28 و 29) وقد تزوجت ابنة ايجابيل أحد أبناء داود في صقلغ (1 أخبار 12: 1 و 8 و 9 و 2 أخبار 11: 18).(5) أحد الرجال المحاربين في جاد وقد جاء إلى داود في صقلغ (1 أخبار 12: 1 و 8 و 9).(6) لاوي كان عازفاً على الآلات الموسيقية وحارساً للباب في مقدس الرب في زمان داود (1 أخبار 15: 12 و 16 و 20).'),
(319, 'ألِياداع', 'انظر اليداع.'),
(320, 'ألِياسَاف', 'اسم عبري معناه ((من زاده الله)):(1)وقد كان رئيساً لبني جاد عند الإحصاء الثاني (عدد 1: 14 و 2: 14 و 7: 42 و 47 و10 : 20).(2) اسم لاوي كان رئيساً لبيت الجرشونيين (عدد 3: 24).'),
(321, 'ألِيَاشِيب', 'اسم عبري معناه ((من يرده الله)) وهو اسم:(1) كاهن في عصر داود وأحد أسلاف الفرقة الحادية عشرة في الكهنوت (1 أخبار 24: 1و 12).(2) لاوي ومرنم من ضمن الذين اقنعهم عزرا بترك زوجاتهم الغريبات (عزرا 10: 10 و19 و 24).(3 و 4) اسم لرجلين احدهما ابن زنو والآخر ابن باني وقد اقنعهما عزرا أن يتركا زوجتيهما الغريبتين (عزرا 10: 27 و36).(5) ابن يوياقيم وقد خلف أباه كرئيس للكهنة (نحم 12: 10) وقد عاش نحميا وأعاد هو والكهنة بناء باب الضأن في أورشليم (نحم 3: 1 و 20 و 21) وقد دخل في علاقة قرابة عن طريق الزواج، مع طوبيا العموني وتزوج حفيده من ابنه سنبلط الحوروني (نحم 13: 4 و 28) وبما أنه لم يكن متمسكاً في أمر الانفصال بين اليهود والأمم فقد عين مخدعاً في الهيكل لطوبيا بسبب قرابته له (نحم 13: 5).(6) ابن اليوعيني وواحد من نسل زربابل (1 أخبار 3: 24).'),
(322, 'ألْيَاقِيم', 'اسم عبري معناه ((من يثبته الله)) وهو اسم:(1) ابن مليا أحد أسلاف يسوع المسيح (لو 3: 30).(2) ابن حلقيا وكان على بيت الملك حزقيا. وكان واحداً من ثلاثة أرسلهم حزقيا ليجتمعوا مع ربشاقي القائد الآشوري الذي كان يحاصر أورشليم (2 ملو 18: 18 و 26 و 37 واش 36: 3 و11 و 12) ثم من بعد هذا أرسل حزقيا الياقيم والرسولين الآخرين معه إلى اشعيا النبي ليخبروه بجواب ربشاقي (2 ملو 19: 2 واش 37: 2) وقد امتدح اشعيا الياقيم جداً (1 ش 22: 20-25).(3) ابن يوشيا وخليفته على عرش يهوذا وقد ملَّكه نخو فرعون مصر على يهوذا وغير اسمه إلى يهوياقيم (2ملو 23: 34 و 2 أخبار 36: 4).(4) واحد من الكهنة الذين اشتركوا في خدمة تدشين سور أورشليم في عصر نحميا (نحم 13: 27 و41).(5) ابن ابيهود وأحد أسلاف يسوع المسيح (مت 1: 13).'),
(323, 'إِلِيئيل وايليئيل', 'وهما اسم واحد في الأصل العبري ومعناه ((ايل هو الله)) وهو اسم:(1) لاوي ابن توح ومن القهاتيين وأحد أسلاف صموئيل النبي، ويدعى أيضاً اليآب واليهو (1 أخبار 6: 1 و34).(2) رجل من محويم وأحد أبطال جيش داود (1 أخبار 11: 26 و 46).(3) اسم رجل آخر من أبطال جيش داود (1 أخبار 11: 47).(4) اسم رجل من جاد وقد جاء إلى داود إلى صقلغ (1 أخبار 12: 11).(5) اسم لاوي، وهو ابن جبرون. وقد عاش في زمان داود (1 أخبار 15: 9و 11).(6) اسم رجل بنياميني وهو ابن شمعي (1 أخبار 8: 1 و 20 و21).(7) اسم رجل بنياميني وهو ابن شاشق (1 أخبار 8: 22 و25).(8) اسم رئيس في نصف سبط منسى الذي سكن شرقي الأردن (1 أخبار 5: 24).(9) اسم احد المشرفين على العشور والتقدمات في زمان حزقيا (2 أخبار 31: 13).'),
(324, 'أليَحْيَا', 'اسم عبري معناه ((من يخبئه الله)) وهو شعبلوني وأحد أبطال داود (2صم 23: 32 و 1 أخبار 11: 33).'),
(325, 'ألِحُورَف', 'اسم عبري وربما كان معناه ((الله يعطي الخريف الثمر)) وهو كاتب سليمان (1 ملو 4: 3).'),
(326, 'أَلِيَداد', 'اسم عبري معناه ((من يحبه الله)) وكان أحد رؤساء سبط بنيامين عندما كان بنو إسرائيل يحلّون في شطيم قبل دخولهم أرض كنعان. وقد يعين كواحد ممن عهد إليهم بتقسيم الأرض بين الأسباط (عدد 24: 17 و 21).'),
(327, 'ألِيدَاع', 'اسم عبري معناه ((من يعرف الله)) وهو:(1) ابن لداود ولد في أورشليم (2 صم 5: 16) وقد ورد أيضاً بصورة ((الياداع)) (1 أخبار 3: 8) ويدعى في 1 أخبار 14: 7 ((بعليا داع)) حيث عوض بلفظة ((بعل)) بدلاً من لفظة ((ايل)) أي الله.(2) أبو رزون (1 ملو 11: 23).(3) جبار من بنيامين كان مع يهوشافاط (2 أخبار 17: 17).'),
(328, 'ألِيشابَع', 'اسم عبري معناه ((الله يُقسم)) وهو اسم ابنه عميناداب وأخت نحشون. وقد صارت زوجة هارون وأم ناداب وابيهو والعازار وإِيثامار (خر 6: 23).'),
(329, 'ألِيشَافاط', 'اسم عبري معناه ((الله قضى)) وكان احد رؤساء المئات وقد ساعد يهوياداع في خلع عثليا ووضع يوشيا على عرش يهوذا (2 أخبار 23: 1-15).'),
(330, 'ألِيشَة', 'ابن ياوان (تك 10: 4) ويظهر من تك 10: 5 أن نسله من ضمن أولئك اليونان الذين استقروا في الجزائر وعلى سواحل البحر الأبيض المتوسط.سواحل اليشة:\nوتسمى أيضاً جزر اليشة وكان يؤتى بالأرجوان من هذه الجزر إلى صور (حز 27: 7) ونعلم الآن من لوحات تلّ العمارنة ومن الكتابات الاوجريتية أن اليشة كانت جزءاً من جزيرة قبرص.'),
(331, 'ألِيشَع', 'اسم عبراني معناه ((الله خلاص)) وهو خليفة إيليا في العمل النبوي في المملكة الشمالية. وكان أليشع ابن شافاط ومن سبط يساكر. وقد أقام في آبل محولة في وادي الأردن. وكان ينتسب إلى أسرة ثرية، لأن حقل أبيه كان يستلزم أثني عشر زوجاً من الثيران لحرثه. وقد وجده إيليا يحرث فدعاه للعمل النبوي إذ طرح رداءه عليه (1 ملو 19: 16 و19-21) وعندما ذهب إيليا إلى ما وراء الأردن ليُنقل إلى السماء ذهب اليشع رداء إيليا وضرب بالرداء مياه الأردن فانفلق الأردن وانشطر وعبر اليشع إلى الجانب الغربي من النهر (2 ملو 2: 1-18).وكان اليشع يختلف عن إيليا من نواح عدة. فقد كان اليشع أصلع الرأس (2 ملو 2: 23 قارنه مع 2 ملو 1: 8) وكان يرتدي ملابس عادية كبقية الناس (2 ملو 2: 12 قارنة مع 2 ملو 2: 8) وكان يحمل معه عكازاً (2 ملو 4: 29). ويختلف عن إيليا في أنه كثيراً ما كان يوجد في البلدان والمدن (2 ملو 6: 32) وقد خصصت أسرة شونمية غرفة معدة في بيتها كان يأتي إليها في رحلاته للقيام بعمله النبوي (2 ملو 4: 8-13) وأحياناً ما كانت الموسيقى تحركه للتنبوء (2 ملو 3: 15).ويسجل لنا العهد القديم معجزات قام بها اليشع أكثر من أي نبي آخر. وقد أظهرت بعض هذه المعجزات كما أظهرت معجزات إيليا أن الرب هو الإله الواحد الحقيقي. وقد ساعد بعض هذه المعجزات شعبه ضد أعدائه. وكانت بعض هذه المعجزات أعمال رحمة ورأفة وشفقة شبيهة بالمعجزات التي قام بها المسيح، وكانت هذه أعظم بكثير مما قام به اليشع. ولقد أبرأ اليشع المياه في نبع عند أريحا بوضع ملح فيه (2 ملو 2: 19-22) وقد نطق بلعنة الرب على الأحداث الذين سخروا منه كنبي ( 2 ملو 2: 23-25) ولقد أنبأ بنجاح الحملة على موآب (2 ملو 3: 11-27) وقد زاد زيت الأرملة على يديه (2 ملو 4: 1-7) وبصلاته عادت الحياة إلى ابن المرأة الشونمية (2 ملو 4: 8-37) وذكر ترياقا للسم الذي تناوله بعض الأنبياء في الطعام (2 ملو 4: 38-41) وأطعم مئة رجل بعشرين رغيف شعير وبعض السويق ( 2 ملو 4: 42-44) وأخبر نعمان أن يغتسل في الأردن فيبرأ من برصه (2 ملو 5: 1-19) وقد أنبأ بأن جيحزي سيصير أبرص بسبب طمعه وشهوته (2 ملو 5: 20-27) وجعل حديد الفأس الذي سقط في الماء، يطفو إلى السطح (2 ملو 6: 1-7) وأنبأ ملك إسرائيل بتحركات الجيوش الآرامية (2 ملو 6: 8-12) وقد أنبأ في وقت حصار السامرة وتفشي الجوع فيها بأن الحصار سيرفع عنها وأن الطعام سيكون متوفراً (2 ملو ص 7) وقد أنبأ بنهدد ملك آرام أنه سيموت قريباً (2 ملو 8: 7-15).وقد أرسل نبياً شاباً ليمسح ياهو وأن يأتي بالقضاء على بيت آخاب (2 ملو 9: 1-10: 28)  وقد تنبأ بثلاث انتصارات على الآراميين. وأخيراً بعد أن مات اليشع أوتي بميت ووضع في نفس القبر مع اليشع فعادت الحياة إلى جسم ذلك الميت حالما مسَّ جثمانه عظام النبي (2 ملو 13: 20 و 21).'),
(332, 'ألِيشَاماع، وأليشامع، وألِيشَمَع', 'اسم عبري معناه ((قد سمع الله) وهو:(1) ابن عميهود وأحد رؤساء افريم في بدء ارتحالهم في البرية (عدد 1: 10 و 2: 18) وهو أحد أسلاف يشوع (1 أخبار 7: 26).(2)ابن يقيمة من سبط يهوذا (1 أخبار 2: 41).(3) ابن داود. وقد ولد في أورشليم (1 أخبار 3: 1 و 5 و 6) انظر اليشوع.(4) ابن آخر لداود (2 صم 5: 16 و 1 أخبار 3: 8).(5) كاهن وهو أحد أولئك الذين أرسلهم يهوشافاط ليعلموا في مدن يهوذا (2 أخبار 17: 7 و 8).(6) أحد الرؤساء وكان كاتباً في حكم يهوياقيم (ارميا 36: و12 و 20 و 21) ويرَجح أنه نفس جد إسماعيل  الذي قتل جدليا (2 ملو 25: 25 وارميا 41: 1).أَلِيشُوع: اسم عبري معناه ((الله خلاص)) وهو ابن داود وقد ولد في أورشليم (2 صم 5: 15 و1 أخبار 14: 5). وقد ورد في سجل أسماء أبناء داود اسم اليشامع مقابل اسم اليشوع (1 أخبار 3: 6) وربما هذان اسمان لشخص واحد. وقد ورد الاسم في هذا العدد في بعض المخطوطات العبرية واليونانية ((اليشوع)) مما يرَجح أنه نفس الشخص.'),
(333, 'أَلِيشُوع', 'اسم عبري معناه ((الله خلاص)) وهو ابن داود وقد ولد في أورشليم (2 صم 5: 15 و1 أخبار 14: 5). وقد ورد في سجل أسماء أبناء داود اسم اليشامع مقابل اسم اليشوع (1 أخبار 3: 6) وربما هذان اسمان لشخص واحد. وقد ورد الاسم في هذا العدد في بعض المخطوطات العبرية واليونانية ((اليشوع)) مما يرَجح أنه نفس الشخص.'),
(334, 'أَلِيصَابَات', 'هذه هي الصيغة اليونانية لاسم لفظه في اللغة العبرية ((اليشبع)) أي ((الله قسم)) وهو اسم امرأة تقية من سبط لاوي ومن بيت هارون. واسمها في العبرية هو نفس اسم امرأة هارون ((اليشبع)). وكانت اليصابات هذه زوجة زكريا وصارت فيما بعد أم يوحنا المعمدان الذي ولدته بعد أن كانت قد تقدمت بها السن. ومع أنها كانت من سبط يختلف عن السبط الذي جاءت منه مريم في الناصرة إلا أنهما كانتا قريبتين. وقد زارت العذراء مريم اليصابات في أرض يهوذا الجبلية. وقد أوحي إلى اليصابات بالروح القدس فرحبت بمريم داعية إياها ((أم ربي)) (لو 1: 5-45).'),
(335, 'أَلِيصَافَان', 'اسم عبري معناه ((الله اخفي)) وقد ورد:(1) اسم ابن عزيئيل ورئيس القهاتيين اللاويين في البرية (خر 6: 18 و 22 وعدد 3: 30) وقد ساعد في رفع جثتي ناداب وأبيهو من قدام القدس إلى خارج المحلة (لا 10: 4) وقد أصبح أباً لبيت ضمن عشائر إسرائيل (1 أخبار 15: 8 و 2 أخبار 29: 13).(2)اسم ابن فرناخ ورئيس في سبط زبولون وقد مثَّل هذا السبط عند تقسيم الأرض (عدد 34: 25).'),
(336, 'أَلِيَصُور', 'اسم عبري معناه ((الله صخرة)) وهو ابن شديثور. وكان رئيس سبط رأوبين أثناء عمل الإحصاء في البرية (عدد 1: 5 و 2: 10).'),
(337, 'أَلِيعازَر و أَلِيعَزَر', 'اسم عبري معناه (( الله عون)) وقد ورد اسماً لعدة أشخاص وهم:(1) وكيل بيت إبراهيم وخادمه الأمين (تك 15: 2). وقد أرسله إبراهيم لإحضار زوجة لإسحاق فذهب إلى فدان آرام وأحضر رفقة من هناك (تك ص 24).(2) ثاني أبناء موسى وصفورة (خر 18: 4 و 1 أخبار 23: 15 و 17 و 26: 25).(3) رئيس من بني بنيامين (1 أخبار 7: 8).(4) كاهن من الذين يضربون كانوا البوق أمام التابوت أثناء حكم داود (1 أخبار 15: 24).(5) رئيس للراؤبينيين في أيام داود (1 أخبار 27: 16).(6) وهو نبي وابن داود أو هو من مريشه. وقد تنبأ بغرق سفينة يهوشافاط لأنه اتحد مع أخزيا من بيت آخاب (2 أخبار 20: 37).(7) أحد اليهود البارزين في أيام عزرا، وقد أرسله عزرا مع غيره ليبحثوا عن اللاويين (عز 8: 16).(8 و 9 و10) ثلاثة رجال أحدهم كاهن والثاني لاوي. والثالث ابن حاريم. وقد أقنعهم عزرا بترك نسائهم الغريبات (عز 10: 10 و 18 و 23 و31).(11) ابن يوريم وواحد من أسلاف المسيح (لو 3: 29).'),
(338, 'أَلِيعَام', 'اسم عبري معناه (( الله قريب)) وقد تسمى بهذا الاسم:(1) أبو بثشبع التي كانت امرأة أوريا وأخذها داود (2 صم 11: 3) وقد ورد ذكره في 1 أخبار 3: 5 باسم ((عميئيل)).(2) ابن اخيتوفل وأحد أبطال جيش داود (2 صم 23: 8 و34). وقد ظن بعضهم أمه هو نفس الشخص الذي ورد تحت رقم (1).'),
(339, 'أَلِيعِينَاي', 'اسم عبري ويرّجح أنه اختصار ((اليهوعيناي)) ومعناه ((عيناي نحو يهوه)) وهو ابن شمعي وأحد رؤساء بني بنيامين (1 أخبار 8: 20).'),
(340, 'أَلِيفَاز', 'اسم عبري وربما كان معناه ((الله ذهب نقي)) وقد ورد اسم لشخصين وهما:(1) ابن عيسو من امرأته عدا، وأبو تيمان (تك 36: 4 و 11 و 12 و 15 و 16 و 1 أخبار 1: 35 و 36).(2) أحد أصحاب أيوب الثلاثة، وكان من تيمان في أدوم وربما كان من نسل اليفاز المذكور في (1) (أيوب 2: 11) وكان اليفاز أول من تكلم بين أصحاب أيوب الثلاثةز وقد وردت أقواله في أيوب ص 3-8 و 15 و 22-14 وقد ذكر في هذه الأقوال أن كل ألم هو نتيجة الخطية، وأن خطايا أيوب لا بدّ وأن تكون جسيمة لأنه قاسى آلاماً مبرحة. ولكن في النهاية وبخ الله اليفاز ورفيقيه الآخرين لأنهم اتهموا أيوب بما لم يصدر عنه (أيوب 42: 7-9).'),
(341, 'أَلِيفَال', 'اسم عبري معناه ((الله قد قضى)) وهو أحد أبطال جيش داود وأبن أور (1 أخبار 11: 26 و 35) وقد ورد ذكره في 2 صم 23: 34 باسم أليفلط بن احسباي.'),
(342, 'أَلِيفَالط وأَلِيفَلَط', 'اسم عبري معناه ((الله نجاه)) وهو اسم:(1) ابن ابيشاي، وأحد أبطال جيش داود (2 صم 23: 34) وقد ورد ذكره في 1 أخبار 11: 35 باسم اليفال.(2) ابن لداود ولد في أورشليم (1 أخبار 3: 5 و 6 و 14: 5).(3) ابن آخر لداود ولد في أورشليم أيضاً، وربما بعد موت الابن المذكور في رقم (2) (2 صم 5: 16 و 1 أخبار 3: 8 و 14 : 7).(4) ابن عاشق من سبط بنيامين (1 أخبار 8: 39).(5) أحد أبناء ادونيقام. وقد رجع مع عزرا من بابل (عز 8: 13).(6) احد أبناء حشوم، وكان من ضمن من أقنعهم عزرا بترك نسائهم الغريبات (عز 10: 23).'),
(343, 'أَلِيفَلْيا', 'اسم عبري معناه ((ليميْزه الله)) وهو لاوي مغن، وضارب بالعود، وكان كذلك أحدْ حراس أبواب المقدس في عصر داود (1 أخبار 15: 15-21).'),
(344, 'أَلِيفَا', 'اسم عبري معناه ((من تقيأه الله أو من رفضه الله)) وهو حرودي وكان واحداً من أبطال جيش داود (2 صم 23: 25).'),
(345, 'أَلِيمَاس', 'أنظر ((بار يشوع)).'),
(346, 'أَلِيمَالِك', 'اسم عبري معناه ((الله ملك)) رجل من بيت لحم وهو زوج نعمي حماة راعوث (را 1: 2 و 3 و 2: 1 و3 و4: 3 و9).'),
(347, 'ألِيهَو', 'اسم عبري معناه ((هو الله)) وقد ورد:(1) اسم ابن توحو وأحد أسلاف صموئيل (1 صم 1: 1) وقد ذكر في 1 أخبار 6: 27 باسم الياب وذكر باسم ايليئيل في 1 أخبار 6: 34.(2) اسم اكبر أخوة داود (1 أخبار 27: 18) ويدعى أيضاً اليآب (1 صم 16: 6).(3) أحد الرؤساء في سبط منسى وقد تبع داود إلى صقلغ (1 أخبار 12: 20).(4) لاوي من القورحيين من بني عوبيد أدوم وكان واحداً من حرّاس أبواب المقدس في عصر داود (1 أخبار 26: 1 و 8).(5) ابن برخئيل البوزي وهو واحد من أصحاب أيوب وقد أدلى بحديثه (أيوب 32-37) بعد أن فرغ أصحاب أيوب الثلاثة المتقدمون في السن من كلامهم.وقد أنّب أيوب لأنه يبرر نفسه من دون الله، وقد وبّخ أصحاب أيوب الثلاثة لأنهم أوقعوا اللوم على أيوب.وقد أشار اليهو إلى أنه يمكن أن يكون الألم واسطة الله في تأديب الأبرار وتعليمهم. وقد مهدت عباراته التي نطق بها من عظمة الله، لكلمات الله ذاته.'),
(348, 'أَلِيهُو عِينَاي', 'اسم عبري معناه ((عيناي نحو يهوه)) وقد ورد:(1) اسم لاوي من عشيرة قورح، وهو ابن مسلميا وكان أحد حرّاس أبواب المقدس في أورشليم (1 أخبار 26: 2 و 3).(2) اسم ابن زرحيا وقد رجع من السبي من بابل إلى أورشليم مع عزرا وكان معه مئتان من أتباعه (عز 8: 1 و 4).'),
(349, 'أَلِيُود', 'هذه هي الصيغة اليونانية لاسم عبري لفظه اليهود ومعناه ((الله جلال)) وهو ابن أخيم وهو واحد من الأسلاف الذين ورد ذكرهم في سلسلة نسب المسيح (مت 1: 14 و 15).'),
(350, 'أَلِيُوعِينَاي', 'اسم عبري معناه ((عيناي نحو يهوه)) وقد ورد:(1) اسم رئيس بيت في سبط شمعون (1 أخبار 4: 24 و 36).(2) اسم رئيس بيت في سبط بنيامين (1 أخبار 7: 8) وهو ابن باكر.(3 و 4) اسم كاهنين اقنع عزرا كلاً منهما بترك زوجته الغريبة (عزرا 10: 23 ونحميا 12: 41 وعزرا 10: 27).'),
(351, 'أَلْيُوعِيني', 'هذا الاسم في العبرية يشبه سابقِه وهو ابن نعريا من سبط يهوذا (1 أخبار 3: 23 و 24).'),
(352, 'أَمَام', 'اسم قرية في جنوب يهوذا (يشوع 15: 26) وربما كانت تقع في وادي الصيني.'),
(353, 'أَمَانَة', 'كلمة عبرية معناها ((الثبات)) وهي اسم لجبل لبنان الصغير. وربما يقع نبع نهر ابانة، الذي يسّمى أيضاً أمانة، في هذا الجبل (نشيد 4: 8).'),
(354, 'أَمْبِليَاس أو أَمْبِلياتِس', 'اسم لاتيني معناه ((متسع)) وهو اسم أحد المسيحيين في رومية الذين أرسلهم إليهم الرسول بولس سلامه (رو 16: 8).'),
(355, 'أمَّة', 'كلمة عبرية معناها ((ام)) وتلّ امّة تلّ تجاه جيح قرب جبعون. وقد سعى يوآب وراءه ابنير إلى هذا المكان (2 صم 2: 24) انظر ((زمام القصبة)) (2 صم 8: 1).'),
(356, 'إِمَّير', 'كلمة عبرية معناها ((غنم)) وقد ورد:(1) اسم كاهن في عصر داود وقد كانت الفرقة السادسة عشر من الكهنة من نسله (1 أخبار 24: 14) وربما كان هو سلف الكهنة المذكورين في (1 أخبار 9: 12 ونحم 11: 13 وعز 2: 37 و10: 20 ونحم 7: 40).(2) اسم أب فشحور كاهن في عصر ارميا (1ر 20: 1).(3) اسم أب صادوق الكاهن الذي ساعد في بناء الهيكل في زمن نحميا (نحم 3: 29) وقد وضع بعضهم (2) و (3) تحت (1).(4) اسم مكان في بابل رجع منه المسبيون العبرانيون إلى أورشليم مع زر بابل ولكنهم لم يمكنهم ان يثبتوا نسبهم العبراني (1 عز 2: 59 ونحم 7: 61) ويظن بعضهم أنَّ هذا كان اسماً لشخص عبراني ولم يكن اسم مكان.'),
(358, 'إِيمان', 'قد وردت هذه الكلمة مراراً عديدة في العهد الجديد وهي تفيد أنها:(1) ديانة المسيح وملكوت الله (1 ع 6: 7 ورو 1: 5 وغل 1: 23 و1 تي 3: 9 ويه 3) وهي المسلمة مرة للقديسين.(2) وهو العمل الذي يمكننا من التمسك بصحة الإنجيل ويسوع المسيح وقوتهما فينا ولنا. والثقة بالخلاص الذي تممه المسيح نيابة عنا. وهذا المعنى أكثر شيوعاً من غيره وخاصة في رسائل بولس الرسول وإنجيل يوحنا وكذلك في مت 8: 10 ويو 3: 16 ورو 1: 16. ولم ترد كلمة ((آمن)) في العهد القديم إلا مرّات قليلة إلا أن معناها يفهم ضمناً في عبارات متنوعة مثل قوله: ((التفتوا إلى الرب)) (اش 45: 22) و (انتظر الرب) (مز 27: 14)) و ((المتكلين عليه)) (نا 1: 7). ويعتبر إبراهيم ((أباً للمؤمنين)) إذ كان جلّ اعتماده على الله (رو 4: 11). وقد اتخذ الرسول بولس موضوع رسالته إلى رومية من آية في حبقّوق ((أمّا البار فبالإيمان يحيا)) (رو 1: 17 وحب 2: 4) ونجد في عب ص 11 مثالاً مفصلاً تفصيلاً تاماً عن إيمان الأبطال الاقدمين.ويتطلب الإيمان ثلاثة أمور- أولاً: اقتناع الفهم. ثانياً: تسليم الإرادة. ثالثاً: ثقة القلب. إنما الثقة هي عمدة الإيمان وملاكه، ولاسيما حينما يكون مخلصنا يسوع المسيح موضوع الإيمان. وينبغي أن تقترن ثقتنا بالإقناع التامّ بصحة أقواله وتعاليمه وإلاّ كان إيماننا باطلاً. ويؤهلنا الإيمان بالمسيح لإدراكه والتمسك به وبكل الفوائد الناجمة عن هذا ولذا فقد ورد في يو 3: 36 ((الذي يؤمن بالابن له حياة أبدية)) والإيمان على طرف نقيض من الشك (مت 21: 21) ولا يلازم العيان (2 كو 5: 7) فإنّ ما نؤمن به لا نراه (عب 11: 1) ولا يمكننا أن نشارك المسيح في بره واستحقاقاته ما لم يكن لنا إيمان به. فبالإيمان ((نلبس المسيح)) وبالإيمان نتبرر لا بالأعمال. أما خلاصنا فقد تممه لنا المسيح حين قال ((قد أكمل)) على أنه شذا رائحة الورد تفوح منه وكما أن الشجرة الجّيدة تأتي بأثمار حسنة كذلك الإيمان الحيّ فإنه ينبغي أن يكون مصحوباً بالأعمال الصالحة، وغليك بعض النصوص: قال مخلصنا ((إيمانك قد شفاك)) وقال الرسول بولس ((لأنكم بالنعمة مخلصون بالإيمان وذلك ليس منكم بل هو عطية الله (1ف 2: 8) غير أنه لا بد لنا من أن نقول أيضاً أن ((الإيمان بدون أعمال ميت)) (يع 2: 26) ونقول أيضاً بلزوم ((الإيمان العامل بالمحبة)) (غل 5: 6).'),
(359, 'أمْنُون', 'اسم عبري معناه ((أمين)) وقد ورد اسماً لما يأتي:(1) لابن داود من أخينوعم اليزرعيلية. وقد ولد في حبرون لما كانت هذه المدينة عاصمة ملك أبيه. وقد أذل ثامار أخته من أبيه فقتله أبشالوم شقيقها (2 صم ص 13 و 1 أخبار 3 : 1).(2) لابن شيمون من سبط يهوذا (1 أخبار 4 : 20)'),
(360, 'الأمُورِّيون', 'شعب كان يتكلم لغة سامية. وقد حكموا أجزاء من فلسطين وسوريا وبابل بعض الزمن. وكان البابليون من قبل سنة 2000 ق.م يدعون سوريا وفلسطين، أرض الأموريين. وكان حمورابي الذي عمل الشرائع والقوانين، أشهر ملوك هذه الأسرة. وكانت ماري، وهي واقعة على نهر الفرات وتدعى الآن، تل الحريري، عاصمة الأموريين في أوائل الألف الثانية قبل الميلاد. وقد اكتشف قصر كبير وما يقرب من 20.000 (عشرين ألف) لوحة فخارية مكتوبة بالخط المسماري.ويذكر تك 10 : 16 أن سلسلة نسب الأموريين ترجع إلى كنعان. وكان الأموريون في عصر ابراهيم أهم قبيلة في الأرض الجبلية في جنوب فلسطين (تك 14 : 7 و 13) وفي وثت الخروج كان الأموريون ما زالوا يقطنون ذلك الأقليم (عدد 13 : 29 وتث 1 : 7 و 19 و 20 و 44). وكانوا قبل الخروج قد افتتحوا ما وراء الأردن من نهر أرنون في الجنوب إلى جبل حرمون في الشمال (عدد 21 : 26 - 30 وتث 3 : 8 و 4 : 48 ويش 2 : 10 و 9 : 10 وقض 11 : 22) وكان سيحون (عدد 21 : 21) ملك الأرض الواقعة بين أرنون واليبوق، وعوج ملك باشان (عدد 21 : 33). أموريين وقد هزم العبرانيون هذين الملكين واحتلوا أرضهما، وقد غزا يشوع الأموريين الذين كانون يقطنون الأرض الجبلية في غرب فلسطين (يش 10 : 5 و 6) وينبئ تك 15 : 16 بانهزام الأموريين قضاء عليهم بسبب شرهم ولكن بقي الأموريون في أرض كنعان بعد أن افتتحها العبرانيون (قض 1 : 35 و 3 : 5) وقد عقد العبرانيون صلحاً معهم في زمن صموئيل (1 صم 7 : 14) وقد استعبد سليمان جميع الأموريين الذين بقوا إلى عصره (1 مل 9 : 20 و 21 و 2 أخبار 8 : 7) وبما أن الأموريين كانوا من أهم القبائل في فلسطين فيظهر أن اسم الأمريين قد أطلق في بعض الأحيان على كل شعب فلسطين (يش 7 : 7 وقض 6 : 10 وعا 2 : 10).'),
(361, 'أَموُن', 'انظر كلمة ((آمون)).'),
(362, 'أَنَا ثِيمَا', 'كلمة يونانية معناها ((مفرز، أو واقع تحت لعنة)) وقد وردت هذه الكلمة اليونانية بمعنى، واقع تحت لعنة، كما هي في نطقها اليوناني في ترجمة فانديك العربية للكتاب المقدس للدلالة على من توّقع عليه اللعنة (1 كو 12 : 3 و 16 : 22 وغل 1 : 8 و 9) وقد ترجمت نفس الكلمة اليونانية في رو 9 : 3 بلفظ ((محروم)) وقد وردت هذه الكلمة اليونانية في الترجمة السبعينية للكتاب المقدس ترجمة للكلمة العبرية ((حرم)) التي تعني أن شخصاً ما أو شيئاً ما، قد أفرز أو خصص للهلاك (عدد 21 : 3 ويش 6 : 17) أو هي تعني في بعض الأحيان أنه قد كُرِّس لله (لا 27 : 28).'),
(363, 'أَنا حَرَة', 'كلمة عبرية ربما تعني ((منخر أو ممر)) وهي مدينة قديمة على حدود يساكر (يش 19 : 19) وربما حلّ مكانها اليوم بلدة الناعورة على بعد خمسة أميال شمالي شرق يزرعيل.'),
(364, 'أْنتِيبا ترِس', 'كلمة يونانية معناها ما يخص أنتيباتير، وقد أخذ الجنود الرومانيون الرسول بولس أسيراً، ليلاً من أورشليم إلى هذه البلدة في طريقهم إلى قيصرية ( 1 ع 23 : 31) وكانت هذه البلدة تدعى ((أفيق)) في العهد القديم (يش 12 : 18) وقد أعاد هيرودوس الكبير بناء هذه البلدة وأطلق عليها اسم أبيه أنتيباتير تكريماً له. ويُرجَّح أن مكانها الآن هو رأس العين التي تقع على طريق روماني قديم بين أورشليم وقيصرية. ويوجد نبع كبير فيها ومنه ينبع نهر العوجة.'),
(365, 'أْنتِيبَاس', 'اختصار الاسم اليوناني ((أنتيباتير)) ومعناه ((من يحل عوضاً عن أبيه)) وقد ورد:(1) اسم أحد المسيحيين وقد مات شهيداً في برغامس في القرن الأول المسيحي (رؤ 2 : 12 و 13).(2) اسم آخر لهيرودوس حاكم الجليل وابن هيرودوس الكبير. فيدعوه يوسيفوس المؤرخ باسمي هيرودوس وأنتيباس. أما العهد الجديد فيدعوه فقط باسم هيرودوس. انظر كلمة ((هيرودوس)).'),
(366, 'إِنْجيل', 'من اللفظ اليوناني أونجليون ومعناه ((خبر طيب)) وقد أوجز الإنجيل في يو 3 : 16 في أن الله أرسل ابنه الوحيد لخلاص المؤمنين. والنقط الرئيسية في الإنجيل كما بشَّر به بولس هي: أن المسيح مات لأجل خطايانا، وأنه قام من بين الأموات (1 كو 15 : 1 - 4). ويدعى في العهد الجديد ((إنجيل الله) (رو 1 : 1 و 1 تسا 2 : 2 و 9 و 1 تيم 1 : 11)، و ((إنجيل المسيح)) (مر 1 : 1 ورو 1 : 16 و 15 : 19 و 1 كو 9 : 12 و 18 وغل 1 : 7) ((وإنجيل نعمة الله)) (ا ع 20 : 24) ((وإنجيل السلام)) (آفس 6 : 15) ((وإنجيل خلاصكم\"\" (آفس 1 : 13) ((وإنجيل مجد المسيح)) (2 كو 4 : 4) ((وإنجيل الملكوت أو بشارة الملكوت)) (مت 4 : 23) وقد بشر يسوع المسيح نفسه بهذا الإنجيل (مر 1 : 14) وبشر به الرسل (1 ع 16 : 10) والمبشرون (1 ع 8 : 25).وقد استعمل جستن مارتر (الشهيد) كلمة إنجيل عن الكتابات التي تتضمن الشهادة الرسولية ليسوع، في عصر مبكر أو في سنة 150 ميلادية تقريباً. والكلمة العربية للإنجيل، وهي بشارة، تشمل هذا المعنى أيضاً أي أنها كتاب رسولي يختص بحياة المسيح على الأرض.'),
(367, 'الأناجيل الأربعة القانونية', 'نسب الكتاب المسيحيون في القرن الثاني الميلادي، الأربعة الأناجيل إلى متى ومرقس ولوقا ويوحنا. وقد تسلمت الكنيسة هذه الكتابات كسجلات يوثق بها وذات سلطان إذ تحتوي على شهادة الرسل عن حياة المسيح وتعاليمه. وبد الكتاب المسيحيون من القرن الثاني الميلادي باقتباس هذه الأناجيل وشرحها وقاموا بعمل ترجمات منها إلى لغات متنوعة كالسريانية والقبطية واللاتينية، ولذا فما من شك في أن هذه الأناجيل سجّلات رسولية صحيحة وصادقة. وتؤدي الرسائل في العهد الجديد صورة المسيح وتعاليمه وأعماله وشخصه كإنسان وإله كما وردت في هذه الأناجيل.ولكل من الأناجيل الأربعة خاصياته المميزة له التي تفرد بها بسبب غرض الكاتب في كتابته والأشخاص الذين كتب إليهم كما كانت ماثله في ذهنه. فقد كتب متى وجهة النظر اليهودية، وهو يقدم لنا يسوع كالمسيا الملك الذي تمت فيه نبوات العهد القديم. ومرقس يكتب للأمم وربما كان يقصد الرومانيين منهم بوجه خاص، وهو يقدم لنا فوق كل شيء قوة المسيح للخلاص كما تظهر في معجزاته. أما لوقا، وهو يكتب للمثقفين من اليونان، يكتب لهم في لغة بأسلوب أكثر روعة مما كتب غيره من كتبة الأناجيل، ويُظهر لنا تأثير الرسول بولس في إبراز نعمة المسيح التي تشمل الساقطين والمنبوذين والفقراء والمساكين بعطفه. أما قصد يوحنا الخاص فهو في أن يُظهر يسوع كالكلمة المتجسدة الذي يعلن الآب للذين يقبلونه (يو 20 : 30 و 31).ويوجد بين الأناجيل الثلاثة الأولى كثير من التشابه ولكنها تختلف عن أنجيل يوحنا من عدة أوجه. وبما أن متى ومرقس ولوقا يقدمون حياة المسيح من وجهات نظر متشابهة على وجه العموم لذا فقد أطلق على هذه الأناجيل الثلاثة اسم ((الأناجيل المتشابهة)) أو Synoptic وهي مأخوذة من كلمة Synopsis اليونانية التي تعني ((النظر معاً)) وهؤلاء يركزون كتاباتهم حول تبشير المسيح ومناداته في الجليل بينما يركز يوحنا أنجيله حول عمل المسيح في اليهودية. ويقدم الثلاثة الأول تعليم المسيح عن الملكوت، وأمثاله وتعليمه للشعب، أما فيسجل لنا تعليم المسيح عن نفسه في أحاديث مستفيضة، وتشترك الأناجيل الأربعة في الشيء الكثير بحيث يؤيدون الواحد الآخر، ويختلفون عن بعضهم البعض بحيث يكمل الواحد منهم الآخر ويتممه.أما المصادر التي استقى منا البشيرون الأربعة المعلومات التي ضمنوها في أناجيلهم في مصادر موثوق بها. فقد كان متى ويوحنا رسولين اتبعا يسوع ولذا فمعرفتهما بالحوادث التي سجلاها هي معرفة شخصية. أما مرقس فقد كان رفيقاً لبطرس وقد ذكر بياس حوالي سنة 140 ميلادية أم مرقش ضمَّن في أنجيله ما وعظ به بطرس عن يسوع. ويحقق لنا لوقا نفسه بأنه استقى معلوماته من شهود عيان (لو 1 : 1 - 4) ولذا فإننا نجد في الأناجيل شهادة الرسل.ويظن بعض العلماء أن مرقس هو أول الأناجيل التي دونت، وأن متى ولوقا استخدما على وجه العموم نفس النقاط الرئيسية التي وضعها مرقس. وهنا فريق من النقاد، ويسمون ((نقاد الشكل)) يؤكدون أن مادة الأناجيل حفظت في أحاديث شفهية. وقد أشار غيرهم من العلماء إلى أساس آرامي يرى في خلال لغة الأناجيل اليونانية كما هي بين أيدينا، وأن هذه البقايا الآرامية في الأناجيل دليل على زمن كتابتها المبكر، ودليل أيضاً على صحتها وصدقها ولا سيما وأن المسيح كان يتكلم الآرامية في أحاديثه.\nوأخير أن وعد المسيح لتلاميذه ليؤيد صدق هذه الأناجيل وصحة رسوليتها. فقد قال ((أما المعزّي، الروح القدس الذي سيرسله الآب باسمي فهو يعلمكم كل شيء ويذكركم بكل ما قلته لكم)) (يو 14 : 26).'),
(368, 'الأناجيل غير القانونية', 'يدخل تخت هذا العنوان كل ما كتبه بعض الكتاب في العصر المسيحي المبكر بعد العصر الرسولي، بما يختص بأخبار سيرة مخلصنا، ونسبوه إلى غيرهم كأنجيل يعقوب وأنجيل نيقوديموس وأنجيل الأبيونيين وأنجيل المصريين وأنجيل العبرانيين وأنجيل الناسيين وإنجيل بطرس وإنجيل توما وإنجيل الطفولية وهو إنجيل عربي. والمظنون أن إنجيل يعقوب كتب في القرن الثاني. وأما موضوع هذه الأناجيل فوصف لحالة يوسف والعذراء مريم، والعجائب التي عملها المسيح في حداثته، وما شاهده في الهاوية وغير هذه مما يرضي عقول السذّج ومن شابههم من العامة الذين يرتاحون إلى مثل هذه الأساطير وأخبار القصصيين. أما نقص هذه الأناجيل فظاهر لأنها تناقض روح المخلص وحياته على أنها دليل على صحة الأسفار القانونية دلالة النقود الزائفة على وجود النقود الصحيحة الحقيقية الخالصة.'),
(369, 'إِتفاق البشيرين', 'ويراد به جمع الأناجيل القانونية معاً وترتيبها على نسق يظهر أوجه التشابه وأوجه الاختلاف بينها فتوضع المادة التي فيكل إنجيل على حدة في عمود يقابل المادة المشابهة لها أو المختلفة عنها في غيره من الأناجيل الأخرى مع إظهار تاريخ الحوادث المذكورة في كل من الأناجيل (انظر ((متى)) و ((مرقس)) و ((لوقا)) و ((يوحنا)) ).\nوأقدم اتفاق للبشيرين بقي لدينا هو الذي أعده تاشيان في سنة 170 ميلادية. وقد ترجم أو الفرج المدعو بابن العبري اتفاق البشيرين هذا إلى العربية في القرن الثالث عشر الميلادي.'),
(370, 'أنْدرَاوُس', 'اسم يوناني معناه ((رجل حقاً)) وهو اسم أحد تلاميد المسيح، وأخ سمعان بطرس وكان موطنه بيت صيدا (يوحنا 1 : 44) وكان صياداً كبطرس (مر 1 : 16 - 18) وكان لأندراوس بيت مع بطرس في كفرناحول (مر 1 : 29) وكان تلميذاً ليوحنا المعمدان الذي أرشده إلى يسوع حمل الله وبعد ما اقتنع أندراوس بأن يسوع هو المسيا أحضر بطرس أخاه إلى يسوع (يو 1 35 - 42) وقد يسوع أندراوس ليتبعه (مر 1 : 16) وقد جاء ذكره في سجل الرسل كما ورد في مر 3 : 18. وأندراوس هو الذي أخبر يسوع عن الصبي الذي كان معه خمسة أرغفة وسمكتان عند إطعام الخمسة آلاف (يو 6 : 8 و 9) وقد سأل هو وبطرس ويعقوب ويوحنا عن خراب أورشليم ومجيء المسيح الثاني (مر 13 : 3 و 4) وأخبر هو وفيلبس يسوع برغبة بعض اليونانيين في رؤيته (يو 12 : 22).ويقول التقليد أن أندراوس استشهد في باتريا في أخائيا في القسم الجنوبي من بلاد اليونان وأنه صلب على صليب بشكل × وهذا النوع من الصلبان يسمى الآن صليب القديس أندراوس. ويقول تقليد آخر أن سفينة كانت تحمل شيئين من بقايا أندراوس غرقت بالقرب من المكان من المعرف الآن باسم مدينة القديس أندراوس في اسكتلندا. وتقام ذكرى استشهاده في 30 من نوفمبر من كل عام وتلقى العظات في ذلك اليوم في بعض الكنائس عن الإرساليات التبشيرية بما أن أندراوس يبرز في الأناجيل كالشخص الذي يُحضر الآخرين إلى يسوع. ومما هو محقق أن الرسول أندراوس لم يكتب سفر الأبوكريفا المعروف باسم ((أعمال القديس أندراوس)).'),
(371, 'أنْدرُونِكوُس', 'اسم يوناني ومعناه ((قاهر الرجال)) وكان اسم أحد أقرباء الرسول بولس، وكان في السجن معه. وقد قبل المسيحية قبل بولس وذهب إلى روما وقد أرسل الرسول تحياته إليه في رو 16 : 7.'),
(372, 'إِنْسان', 'تك 11 : 26) وهو رأس المخلوقات الحيّة وسيدها (تك 1 26 - 28) ويعلمنا الكتاب المقدس أن الناس في الأصل من دم واحد غير أنهم تفرقوا بعدئذ إلى أمم وقبائل عديدة يتميز بعضها من البعض في اللون والقامة والهيئة واللغة والعادات. وقد قطنت كل أمة من بقاع الأرض ما خصتها به العناية الإلهية. وخلق الله الإنسان من التراب وخلقه على صورته تعالى مميزاً إياه عن سائل الكائنات الحية بما أودع فيه من روح حيّة خلقية تؤهله ليكون مشابهاً صورة خالقه جّل شأنه. وقد أوجد الله فيه العواطف الخلقية والميول الروحية القوى العقلية. وقد ورد في اللغة العبرية مترادفات كثيرة بمعنى الإنسان. وقد اشتق معظمها للدلالة على أصله وهيئته وعناصره وما أشبه هذا. وقد جاء في تك 2 : 7 أن الله نفخ في أنفه نسمة حياه، ولا يراد بنسمة الحياة هذه عملية التنفس الطبيعي فحسب وإنما المراد منها هو أن الله أعطاه تلك القوى العقلية والروحية مقترنة بالنفس الحية (انظر كلمة ((آدم))).وبعد ما خلق الله الإنسان على صورته وضع له من الشرائع الإلهية ما ينبغي عليه أن يسير وفقاً له. على أنه غلب على أمره وانقاد إلى مخالفة تلك الشرائع وعصيان الأمر السماوي فاستحق غضب الله عليه. وفقد بسبب ذلك جميع أمانيه وآماله. ومنذ ذلك الحين انحرفت طبائعه عما كانت قد فطرت عليه من البراءة والبُعد عن شبه الخالق فصار ميالاً إلى الشر والفساد. وأخذت المفاسد تستحوذ عليه إلى أن تحكمت في طباعه وانتقلت عنه بحكم الوراثة إلى نسله (انظر كلمة ((خطية))) واستولى الموت على جميع نسل آدم. وقد شملت العناية الإلهية الإنسان كيما ترفعه وترده إلى مكانته من الله، فجعلت عليه أن يحب الرب إلهه من كل قلبه ونفسه وفكره وقدرته. وأن يحب قريبه كنفسه ولكن الإنسان ضعيف بطبعه كثير النزوع إلى الآثام والشرور ولو أنه قدّر الشريعة السماوية حق قدرها وسلك بموجبها من بداءَة أمره لظهر له في جلاء قبح المعصية ولارتدّ عن ارتكاب الشرور والمعاصي.وقد أرسل الله ابنه الوحيد إلى العالم لينقذ الإنسان وينتشله من تلك الوهدة التي تردى فيها فجاء المسيح وأطاع الشريعة الإلهية واحتمل عقاب التعدّي عليها وهكذا هو صالحنا مع الله وفتح الطريق ثانية أمام كل مؤمن يروم الاقتراب من الله لنوال السعادة الأبدية. ولم يكفِّر مخلصنا عن خطايانا فحسب بل أرسل لنا الروح المعزّي ليوجه قلوبنا إليه مجدداً إياها ويثبت نفوسنا فيه مقوّماً لها. وهو لا يزال يشفع فينا لدى الآب في السماوات. ومع أن بني الإنسان قد فقدوا الصورة الإلهية التي خلقوا عليها، ومع أنهم وقعوا تحت طائلة العقاب الإلهي التي خلقوا عليها، ومع أنهم وقعوا تحت طائلة العقاب الإلهي الرهيب إلا أهم أصبحوا بسبب عمل الفداء. أهلاً لأن ينالوا غفران خطاياهم غفراناً تاماً كاملاً إذا آمنوا بالرب يسوم المسيح ((الشفيع الوحيد بين الله والناس، وندموا على خطاياهم ندامة صحيحة حقيقية، وأصبحوا أهلاً للتحرر من عبودية الخطية ورقها، والانتقال إلى حرية أبناء الله بالنعمة المجانية التي لا تحدّ قط. بل يصبحون ((ورثة الله ووارثين مع المسيح)) ودليل الإيمان الطاعة لأوامر الله والخضوع التام عن اختيار لسلطته الإلهية.والبشر كانوا معرضين في الحياة الحاضرة لصنوف من الضيقات والتجارب والموت، أما بعد انتقالهم من هذه الدار الدنيا سيحاكمون كل بحسب ما عمل الجسد خيراً كان أم شراً. أما الأبرار الذين قد غفرت آثامهم وثبتوا في محبة الله فإنهم سيدخلون إلى أفراح الملكوت السماوي الأبدية. وأما الأشرار الذين أهملوا وسائط النعمة ولم يكترثوا بالخلاص المقدم لهم بل رفضوه مستهينين أو متهاونين فإنهم سيذهبون إلى عذاب أبدي. (انظر كلمة ((المسيح))).'),
(373, 'ابنُ إنسان - ابنُ الإنسان', '((ابن الإنسان)) عبارة وردت في عدد 23 : 19 وهي ترجمة لعبارة عبارنية ترجم إلى العربية في أماكن أخرى ((بابن آدم)) فمثلاً في حز 2 : 1 (وقد وردت هذه العبارة ((ابن آدم)) إشارة إلى النبي حزقيال في سفره سبعاً وثمانين مرة). وتشير هذه العبارة في دانيال 7 : 13 إلى شخص يختلف عن الأربعة الحيوانات التي ورد وصفها في الجزء الأول من الإصحاح في أنه شبيه بالإنسان في المنظر. وهذا الشخص الشبيه بابن إنسان قد أعطي سلطاناً أبدياً وملكوتاً لا ينقرض. وقد وردت هذه العبارة ((شبه ابن إنسان)) في رؤيا 1 : 13 و 14 : 14 للدلالة على المسيح القائم من الأموات والممجّد.وقد استعملت عبارة ((ابن الإنسان)) في السفر غير القانوني المنسوب إلى أخنوخ (ص 46 : 2 و 3 و 48 : 2 و 62 : 7 و 9 و 14 و 63 : 11 و 69 : 26 و 27 و 70 : 1 و 71 : 17) للدلالة على المسيا كما يأتي في يوم في يوم القضاء والانتصار.ويوجد في الأناجيل الأربعة ثمانية وسبعون مثلاً يستخدم فيها يسوع المسيح هذه العبارة ((ابن الإنسان)) عن نفسه. ويستخدم هذا اللقب في مرقس 2 : 28 عن نفسه وصفته كرأس الجنس البشري وممثله. ولذا فإن العبارة تدل على الإنسانية الحقّة، وتدل في مواضع أخرى على أنه المسيا عندما يتنبأ بمجيئه الثاني وبمجده (مت 26 : 64 ومر 14 : 62) ودينونته لجميع البشر (مت 19 : 28) وربما استخدم المسيح هذه العبارة كثيراً لأن فيها دلالة على أنه المسيا، وهي في نفس الوقت تصلح في الإشارة إلى حياته المتواضعة على الأرض كالإنسان الكامل. ومما يستحق الملاحظة هو أن هذا التعبير ((ابن الإنسان)) لم يستخدم عن المسيح بعد القيامة سوى مرة واحدة ( 1 ع 7 : 56) ويستخدم الكتاب المقدس ألقاباً أكثر تمجيداً كالرب وغيرها في الإشارة إلى المخلص بعد الصمود.'),
(374, 'أنسِيمُس', 'اسم يوناني معناه ((نافع)) وهو اسم عبد فليمون، الذي كان من المسيحيين البارزين في كولوسي. ويظهر من الرسالة إلى فليمون أن أنسيس سرق سيده وهرب إلى روما. وفي روما أصبح مسيحياً عن طريق مناداة بولس وخدمته. فأرسله بولس ثانية إلى كولوسي ومعه رسالة إلى فليمون يطلب الرسول فيها إلى فليمون أن يقبل أنسيس لا كعبد بل كأخ. وقد رافق أنسيس تيخيكس في رحلته من روما إلى كولوسي (كو 4 : 9). ويقول التقليد أن أنسيس أصبح فيما بعد أسقف بيرية، وأنه مات شهيداً.'),
(375, 'أنْطَاكية', '1) وكانت مدينة على نهر العاصي على مسافة خمسة عشر ميلاً من البحر الأبيض يض المتوسط. وقد أسس هذه المدينة سلوقس نيكاتور أحد قواد جيش الأسكندر الأكبر أسسها عام 300 ق.م ودعاها أنطاكية نسبة إلى أبيه أنطيوخس. وقد أسس سلوقس أيضاً سلوقية على مصب نهر العاصي لكي تكون ميناء لأنطاكية، وقد صارت أنطاكية عاصمة السلوقيين وهم نسل سلوقس وأتباعه الذين صاروا حكام سوريا من بعده (1 مك 3 : 37). وفي عام 64 ق.م أخذ المدينة بومباي القائد الروماني وأصبحت عاصمة إقليم سوريا الروماني. وكانت أنطاكية مركزاً مهماً للتجارة والتبادل الثقافي بين الشرق والغرب. وكانت ثالث مدينة في الإمبراطورية الرومانية (بعد روما والإسكندرية) وكانت الآلهة ((تيخي)) أو ((الحظ)) هي آلهة أنطاكية الخاصة وكانت تقوم عبادة ((أبولو)) في ((دفني)) بالقرب من أنطاكية على كثير من الرجس والنجاسة والممارسات الجنسية الجامحة.وكان في أنطاكية جماعة كبيرة من اليهود ومن بينهم ظهر المسيحيون الأوّل في المدينة.وقد أصبحت أنطاكية أهم مركز للمسيحية بعد أورشليم، وانتشرت المسيحية من هذه المدينة إلى الغرب. وقد دعي التلاميذ مسيحيين في أنطاكية أولاً (1 ع 11 : 26) ومن أوائل الشمامسة في المسيحية في أورشليم رجل يدعى نيقولاوس من أنطاكية وقد اهتدى من الوثنية إلى اليهودية ثم صار شماساً مسيحياً (1 ع 6 : 5). وبعد موت استفانوس الشهيد هرب المسيحيون من أورشليم إلى أنطاكية وبشروا بالأنجيل لليهود واليونانيين هناك (1 ع 11 : 19 - 21). وقد أرسلت الكنسية في أورشليم برنابا ليقود العمل التبشيري في أنطاكية ودعا بولس معه ليعاونه في الوعظ والتعليم (1 ع 11 : 22 - 25). وقد أرسل المسيحيون في أنطاكية عطايا وتقدمات إلى المسيحيين في أورشليم أثناء المجاعة (1 ع 11 : 29 و 30). وأرسلت كنيسة أنطاكية الرسول بولس في ثلاث رحلات تبشيرية (1 ع 13 : 1 - 3 و 15 : 40 و 18 : 23) وقد عاد إلى الكنيسة هناك بعد الرحلتين التبشيريتين الأوليين ليقدم لها تقريراً عن خدمته (1 ع 14 : 26 - 28 و 18 : 22).وقد رأت الكنيسة في أنطاكية أن المسيحيين من الأمم غير ملزمين أن يحفظوا الشريعة الطقسية. ولذا فقد أرسلت الكنيسة في أنطاكية بولس وبرنابا إلى مجمع للقادة المسيحيين في أورشليم، وقرر المجمع أن المسيحيين الداخلين إلى المسيحية من الأمم غير مرتبطين بمطالب الشريعة الفرضية والطقسية (1 ع 15 : 1 - 29) وقد وبخ بولس في أنطاكية بطرس لرفضه أن يأكل مع المسيحيين من الأمم (غل 2 : 11 و 12) وقد جعل مبدأ التحرر من الشريعة الطقسية والفرضية، التبشير بالإنجيل ممكناً على نطاق واسع بين الأمم.وقد ظهر في أنطاكية بعد أزمنة العهد الجديد اثنان من أعظم قادة الكنيسة المسيحية شهرة وهما: أغناطيوس أسقف أنطاكية الذي استشهد في روما، ويوحنا كرسستم ((فم الذهب)) الواعظ المسيحي الشهير الذي ذهب إلى القسطنطينية. وقد أظهرت الكشوف التي أجريت في أنطاكية خرائب كنائس كثيرة أكثرها قديمة يرجع إلى القرن الرابع الميلادي. وقد زينت بعض هذه الكنائس برسوم جميلة كانت أنطاكية قد اشتهرت بها. وقد اكتشفت بالقرب من أنطاكية كأس مسيحية فضية ترجع إلى القرن الثالث أو الرابع بعد الميلاد ولا يمكن أن نجزم، كما يدعي البعض، بأن كأساً فضية أخرى أكثر قدماً من هذه وجدت داخل هذه الكأس، وهي بذاتها الكأس التي استخدمها يسوع المسيح عندما وضع فريضة العشاء الرباني. وأنطاكية الآن بلدة قليلة الأهمية، وقد أصبحت بعد الحرب العالمية الثانية تحت حكم تركيا.(2) وكانت أيضاً مدينة في وسط آسيا الصغرى، في فريجية بالقرب من حدود بيسيدية ولذا فتدعى أحياناً أنطاكية بيسيدية (ا ع 13 : 14) أو أنطاكية التي في اتجاه بيسيدية، وقد أسس سلوقس الأول نيكاتور، الذي كان واحداً من قواد الأسكندر الأكبر، هذه المدينة في أوائل القرن الثالث قبل الميلاد ودعاها أنطاكية تكريماً لأبيه أنطيوخس كما دعى أنطاكية على نهر العاصي بهذا الاسم أيضاً تكريماً لأبيه. وكانت تقع هذه المدينة على طرق تجارية مهمة وجعلها الرومانيون المركز الإداري للجزء الجنوبي من إقليم غلاطية. وقد أسكن سلوقس مؤسس المدينة جماعة من اليهود هناك.وقد زار بولس وبرنانا أنطاكية بيسيدية في رحلتهما التبشيرية الأولى ( ا ع 13 : 14) وقد ألقى الرسول بولس عظمة في المجمع اليهودي هناك وقد ورد في ا ع 13 : 16 - 41 جزء من هذه العظة وقد قبل رسالة الخلاص بالمسيح كثيرون من اليهود والأمم، ولكن أهاج بعض اليهود المقاومين قادة المدينة ضد بولس وبرنابا فجروهما خارج المدينة (ا ع 13 : 42 - 50). وقد نزل بولس وبرنابا في أنطاكية عند عودتهما من رحلتهما التبشيرية، ونظّما الكنيسة هناك بإقامة شيوخ في آخر رسائله (2 تيم 3 : 11) ما قاساه من ألم وعذاب في أنطاكية. ويظن بعضهم أن رسالة بولس إلى الغلاطيين أرسلت إلى الكنائس الواقعة في جنوب غلاطية ومن ضمنها كنسية أنطاكية.ولم يبقَ من أنطاكية هذه سوى خرائب بالقرب من بلدة يلفتش على المنحدر الجنوبي لسلسلة جبال تدعى سلطان دغلاري في أواسط تركيا.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(376, 'أنطيوخس أو أنطيوكس', 'اسم يوناني معناه ((مقاوم)) وكان اسم:(1) أنطيوخس الثالث الملقب ((الكبير)) ملك سوريا من 223 - 187 ق.م. وبعد أن قام بعدة محاولات لأخذ فلسطين استولى عليها في النهاية وانتزعها من البطالسة في مصر في معركة بانياس عام 198 ق.م. ويشير 1 مكابيين 8 : 6 - 8 إلى انهزامه على يد الرومان في عام 190 ق.م. ويظن كثيرون من المفسرين أنه ملك الشمال المذكور في دانيال 11 : 13 - 19.(2) أنطيوخس الرابع أو ((أبيفانيس)) ملك سوريا من 175 - 163 ق.م. وقد أراد أن يحق الديانة اليهودية فثار المكابيون ضده (1 مكا 1 : 41 - 53). ويظن كثير من المفسرين أنه هو القرن الصغير المذكور في دانيال 7 : 8 و 8 : 9 - 14 وأن الإشارة الواردة في دانيال 11 : 7 -45 والتي تذكر المحتقر الذي ينجس الهيكل في أورشليم إنما تعني أنطيوخس أبيفانيس هذا.(3) أنطيوخس الخامس أو ((أيوباتور)) ملك سوريا من 163 - 162 ق.م وقد قتله ابن عمّه دمتريوس وخلفه على العرش (1 مكا 6 : 17 - 7 : 3).(4) أنطيوخس السادس أو ((ثيوس)) ملك سوريا من 145 - 142 ق.م. وقد أجلسه على العرش قائد يدعى تريفون ثم قتله من بعد ذلك (1 مكا 11 : 39 و 40 و 54 و 13 : 31 و 32).(5) أنطيوخس السابع أو ((سيديتس)) ملك سوريا من 138 - 129 ق.م. وقد قبل في بادئ الأمر أن يتنازل عن بعض الامتيازات لسمعان المكابي ولكنه فيما بعد قام بحرب ضد سمعان وضد ابنه هيروكانوس (ا مكا 15 : 1 - 16 : 10).\n(6) اسم أبي رجل يهودي اسمه نومينيوس وكان هذا مشيراًَ ليوناثان المكابي ولأخيه سمعان ( ا مكا 12 : 16 و 14 : 22).'),
(377, 'أنطونيا', 'اسم برج في الزاوية الشمالية الغربية من منطقة الهيكل، وقد بنى هذا البرج هيرودس الكبير وسماه أنطونيا تكريماً لماركوس أنطونيوس ويرجّح أنه كان في مكان قلعة بارس التي ذكرها يوسيفوس والتي كانت مقامة على صخرة مرتفعة شمالي غربي الهيكل، ويرجح أن نحميا أصلح القصر الذي كانت فيه هذه القلعة في عصره (نجم 2 : 8). ولما هاجم الغوغاء والرعاع بولس، جاء جنود رومانيون من هذا البرج لينقذوا بولس. ومن درج هذا البرج خاطب بولس الشعب (ا ع 21 : 31 - 22 : 24) ويشغل الآن الموقع الذي كان فيه هذا البرج ثكنات للجيش الأردني.'),
(378, 'أنُوق', 'ومعناه ((كاسر)) وقد ذكرت الشريعة الموسوية في لا 11 : 13 وثثنية 14 : 12 هذا الطائر بين الطيور النجسة. وقد حرّم أكله على العبرانيين. واسمه في العبرية ((بيرص)) ومعناه ((كاسر) لأنه يحمل الرمم والعظام السلاحف في الهواء ثم يتركها لتسقط فتتكسر ثم يأكل النخاع الموجود داخل هذه العظام ويأكل لحم السلاحف ولذا فقد سمي ((كاسر العظام)). واسمه في اللاتينية Gypactus barbatus أي ((العقاب الملتحي)) أو ((أبو ذقن)) وقد سمي هكذا لوجود ريش أسود تحت ذقنه. وهو طائر كبير الحجم يبلغ طوله ثلاثة أقدام ونصف عندما يبسط جناحين يبلغ طوله عشرة أقدام وهو يعيش في الوديان المنعزلة.'),
(379, 'أُنوش', 'اسم عبري معناه ((رجل)) وهو ابن شيث (تك 4 : 26 و 5 : 6 - 11 و 1 أخبار 1 : 1 ولو 3 : 38).'),
(380, 'أنْيِسيفُورُس\"', 'اسم رئيسي ومعناه ((من يأتي بالنفع)) وكان اسم رجل مسيحي خدم في كنيسة أفسس. ولما كان بولس سجيناً في روما زاره أنيسيفورس وساعده. ويصلي بولس الرسول طالباً بركه الله عليه وعلى أهل بيته (2 تيم 1 : 16 - 18) ويرسل سلامه وتحته إلى أهل بيته (2 تيم 4 : 19) ويظن البعض أن أنيسيفورس كان قد توفي لما كتب الرسول هذه الرسالة ولكن هذا غير محقق.'),
(381, 'أنِيعَام', 'اسم عبري ربما كان معناه ((أنا عم)) أو ((مرثاة الشعب)) وكان اسم ابن شميداع من سبط منسّى (1 أخبار 7 : 19).'),
(382, 'أهْوَا', 'اسم لنهر ولمنطقة في بابل وقد جمع عزرا اليهود الراجعين إلى أورشليم إلى هذا النهر ونادى فيهم بصوم وصلاة (عز 8 : 15 و 21 و 31) ولا يعرف مكانه اليوم على وجه التحقيق.'),
(383, 'أُهود', 'اسم عبري وربما كان اختصار الاسم أبيهود وكان:(1) قاضياً لبني إسرائيل وهو ابن جيرا من سبط بنيامين. وكان أهود أعسراً. وقد قتل عجلون ملك موآب الذي أذل بني إسرائيل، وقاد شعبه إلى النصر على الموآبيين (قض 3 : 15 - 30).(2) ابن بلهان من سبط بنيامين (1 أخبار 7 : 10).'),
(384, 'أهُولَة', 'اسم عبري معناه ((خيمتها)) وقد أطلق النبي حزقيال هذا الاسم على السامرة التي يشبهها بامرأة شريرة أغواها الآشوريون (حز 23 : 4 - 44) وربما يشير الاسم إلى مقدس كان في خيمة.'),
(385, 'أهُوِليآب', 'اسم عبري معناه ((خيمة آب)) وكان ابن أخيساماك من سبط دان وقد عاون بصلئيل في عمل الخيمة وأثاثها (خر 31 : 6 و 35 : 34 و 35).'),
(386, 'أهْولِيبَامَة', 'اسم عبري معناه ((خيمتي مكان مرتفع)) وكان اسم:(1) زوجة عيسو بني عنى الحوي (تك 36 : 2) وكانت تدعى أيضاً يهوديت (تك 26 : 34).(2) رئيس أدومي (تك 36 : 41 و 1 أخبار 1 : 52).'),
(387, 'أهُولِيبَة', 'اسم عبري معناه ((خيمتي فيها)) وقد أطلق النبي حزقيال هذا الاسم على أورشليم مشبهاً إياها بامرأة شريرة أغواها البابليون (حز 23 : 4 - 44) ويشير الاسم إلى خيمة الرب في أورشليم.'),
(388, 'أهْيَة', 'اسم عبري معناه ((الكائن)) هذه كلمة عبرية في حروف عربية وهي لفظ اسم الرب الذي أطلقه الرب على نفسه عندما كان يتكلم إلى موسى (خر 3 : 14) ويعبر هذا الاسم عن أبديته ووجوب ووجوده، والكلمة العبرية في صيغة المضارع المفرد للمتكلم. والاسم العبري الذي يطلق عادة الرب هو اسم ((يهوه)) ويترجم عادة بالرب، وهو في العبرية في صيغة المضارع المفر للغائب، من ذات الفعل.'),
(389, 'أُنِيل', 'اسم عبري معناه ((إرادة الله)) وهو ابن باني وقد أقنعه عزراً بترك زوجته الغريبة (عز 10 : 34).'),
(390, 'أوبُوت', 'اسم عبري معناه ((قرب الماء)) وهو اسم مكان حل فيه بنو إسرائيل. وهو بالقرب من حدود موآب الجنوبية الشرقية (عدد 21 : 10 و 11 و 33 : 43 و 44) ومكانها اليوم عين الويبة.'),
(391, 'أوْبِيل', 'اسم عبري ربما كان معناه ((سائق إبل)) وهو اسم رجل إسماعيلي ممن وضع داود إبله في عهدتهم (1 أخبار 27 : 30).'),
(392, 'أُور', 'اسم عبري معناه ((نور أو لهب)) وهو أبو أليفال أحد قواد جيش دواد (1 أخبار 11 : 35) ويدعى أحسباي في 2 صم 23 : 34.'),
(393, 'أوُر الكلدانيين', 'وهي مسقط رأس إبراهيم التي ولد ونشأ فيها ولكنه خرج منها إطاعة لدعوة الرب وذهب إلى حاران ومنها ذهب إلى كنعان (تك 11 : 28 و 31 و 15 : 7 ونحم 9 : 7). ومكان أور اليوم خرائب تدعى المغبّر في منتصف المسافة بين بغداد والخليج الفارس، وعلى مسافة عشرية أميال شرقي مجرى نهر الفرات في الزمن الحاضر. وقد احتل المدينة السومريون والعيلاميون والبابليون والكلدانيون على التوالي.وقد أثبتت الكشوف الحديثة أن مدينة أور وجدت ما يقرب من ألف عام قبل عصر إبراهيم وكانت في ذلك الزمن السحيق مركزاً لمدينة راقية. وتقول سجلاتها القديمة التي اكتشفت فيها أن بعض ملوكها حكموا آلافاً من السنين، وتدل طبقة من رواسب الطمي اكتشفت فيها على أن طوفاناً عظيماً حدث في أرض ما بين النهرين. ولكننا لا يمكن أن نجزم بأن رواسب الطمي هذه باقية من الطوفان الذي حدث في أيام نوح كما يدعي البعض ذلك. وقد اكتشفت في المقبرة الملكية التي يرجع تاريخها إلى سنة 2500 ق.م تقريباً, جواهر جميلة وأشياء أخرى من الفضة والذهب. ولكن يظهر أن الحياة البشرية لم تكن ذات قيمة تذكر عند أولئك القوم, فقد دلت الكشوف على أن ثمانية وستسن من الخدم قد قتلوا ليقوموا بخدمة الملكة في الحياة الأخرى. وقد امتد سلطان أور في عصر أورنمَو حوالي عام 2350 ق.م, على معظم أرض ما بين النهرين، التي هي العراق الآن. وقد شيَد هذا الملك ((زيجورات)) أو برج هيكل عظيم, وكان نانار إله القمر يعبد على قمته. وقد وجد هناك كثير من اللوحات الطينية وقد كتبت عليها وثائق معاملات تجارية مما يدلّ على أن أور كانت في ذلك الحين مركزاً عظيماً للتجارة.'),
(394, 'أُربْانَوس', 'اسم لاتيني معناه ((ظريف أو مؤدب)) وكان اسم رجل مسيحي عاون بولس في عمله التبشيري ثم ذهب إلى روما. ويرسل إليه بولس تحياته وسلامه إلى روما (رو 16: 9).'),
(395, 'أُورشُلَيم', 'وكانت عاصمة يهوذا وفلسطين السياسية لزمن طويل. كما أنها مدينة مقدسة عند اليهود والمسيحيين والمسلمين.(1) أسماؤها وأول مرة ورد فيها اسم أورشليم هو في نقش مصري قديم يرجع إلى القرن التاسع عشر قبل الميلاد ، وفيه تصب اللعنة على أمير هذه المدينة.وربما أن معنى هذا الاسم هو ((اساس السلام)) أو ((اساسس الإله شاليم)) وتدعى هذه المدينة في مز 76: 2 ((ساليم)) ولذا فيرجح أن شاليم التي كان ملكي صادق ملكاً لها هي نفس. أورشليم (تك 14: 18). أما اسماء أورشليم الآخرى فهي: -يبوس (قض 19: 10 و 11) اريئيل (1ش 29: 1) مدينة العدل (1ش 1: 26) والمدينة (مز 72: 16) ومدينة القدس أو المدينة المقدسة (1ش 48: 2 ومت 4: 5) أما اسماؤها في العربية فبالإضافة إلى أورشليم فهي تسمّى أيضاً بيت المقدس والمقدس والقدس الشريف أما الاسم الغالب فهو القدس.(2) جغرافيتها (أ) موقعها. تقع أورشليم على مسافة أربعة عشر ميلاً غربي الطرف الشمالي للبحر الميت، وعلى بعد ثلاثة وثلاثين ميلاً جنوبي شرقي يافا الواقعة على البحر الأبيض المتوسط، ةعلى مسافة ستة أميال شمال شرقي بيت لحم، وعلى بعد مئة وثلاثة وثلاثين ميلاً جنوبي غربي دمشق، ويتفاوت ارتفاع المدينة فوق سطح البحر بين 2350 قدماً إلى 2850 قدماً. ولذا فمناخها معتدل كتوسط درجة حرارته على مدار السنة 63 درجة فرنهايت، ومتوسط سقوط الأمطار فيها في السنة يصل إلى 26 بوصة تقريباً. وتسقط معظم الأمطار بين تشرين الثاني (نوفمبر) ونيسان (ابريل).(ب) تلالها. بنيت أورشليم على خمسة تلال تكون في مجموعها نتوءاً صخرياً يبرز في وسط أرض يهوذا الجبلية في الشمال، وتحيط بها الوديان والتلال من الجهات الثلاث الأخرى. وكانت المدينة اليبوسية الأصلية على التل الجنوبي الشرقي، وهو الآن غير آهل بالسكان كثيراً. وقد دعي هذا التل باسم ((صهيون)) ومدينة داود (2صم 5: 7) وكان اسم عوفل أو ((الأكمة)) يطلق على الطرف الشمالي من هذا التلّ على الأقل (2 أخبار 27: 3 ونحم 3: 26) ويدعو يوسيفوس المؤرخ هذا التل باسم اكرا أو المدينة السفلى. أما التل الشرقي الأوسط فقد كان المكان الذي أقيم عليه الهيكل. ويدعى في تك 22: 2 ((المرّيا)) وقد كان موضع بيدر ارونة أو ارنان، ومنه اشتراه داود ليكون الموضع الذي يبني فيه الهيكل (2 أخبار 3: 1) ويطلق الأنبياء اسم صهيون على التل المقام عليه الهيكل أيضاً (انظر 1ش 4: 5) ويدعو يوسيفوس المؤرخ التل الجنوبي الغربي المدينة العليا. ومنذ القرن الرابع الميلادي واسم صهيون يطلق خطأ على هذا التل. أما التلّ الشمالي الغربي فيرجح أنح لم يكن واقعاً ضمن نطاق المدينة في أزمنة العهد القديم. ويدعوه يوسيفوس الحي الشمالي وهو الآن الحيّ المسيحي في المدينة. وأما التلّّّّ الخامس وهو الشمالي الشرقي فلم يكن جزءاً من المدينة في أزمنة العهد القديم ويدعوه يوسيفوس بيزيثا أو المدينة الجديدة.وتحيط التلال بأورشليم من ثلاثة جوانب (مز 125: 2) فإلى الشمال الشرقي منها جبل سكوبس، وجبل الزيتون في الشرق وجبل دير أبو طور في الجنوب، ويسمى أيضاً تلّ المشورة الشريرة، ويقول عنه التقليد أن يهوذا خنق نفسه هناك، وسلسلة تلال اليهودية الرئيسية في الغرب.(ج) الوديان: وادي قدرون وهو يقع بين المدينة وجبل الزيتون إلى الشرق. ويسمى أيضاً وادي يهوشافاط (يؤ 3: 12) ويدعى في العربية وادي سيدتي مريم، وإلى الغرب من المدينة يقع واد يدعى وادي الميس ويتجه شرقاً إلى بركة السلطان ويسير إلى جنوب المدينة ويسمى هذا الجزء منه وادي الربابة. ويرّجح أن وادي الربابة هو وادي ابن هنوم واسمه في العبرية جي هنوم (يش 18: 16) وهو نفس اسم ((جهنم)) في العربية. ويوجد بين التلال الشرقية والتلال الغربية في المدينة واد، وقد امتلأ الآن بقطع الأحجار والطوب وغيرها التي ألقيت فيه على مدى القرون ويسميه يوسيفوس وادي ((تيروبيون)) ومعناه ((صانعوا الجبن)) ويسمى في العربية بالوادي. ويتصل وادي الربابة بوادي سيدتي مريم جنوبي شرقي المدينة ويتكّون منها وادي النار الذي يسير في الجنوب الشرقي إلى البحر الميت.(د) منابع المياه: 1. الينابيع. توجد في منطقة أورشليم أربعة ينابيع معروفة وهي:النبع المسمى جيحون في 1 مل 1: 38 ويدعى الآن عين سيدتي مريم أو عين أم الدرج في وادي قدرون شرقي التل الجنوبي الشرقي مباشرة. وقد حفر الكنعانيون سرداباً في الصخر، يصل بين المدينة وبين هذا النبع ليستخدم في وقت الحصار، ويرجح أن يوآب ورجاله دخلوا المدينة من هذا السرداب ليأخذوها لداود (2صم 5: 8 و 1 أخبار 11: 6). وتوجد بئر بالقرب من التقاء وادي الربابة بوادي سيدتي مريم تعرف ببئر أيوب، ويرجح أنها عين روجل التي ورد ذكرها في 1 مل 1: 9. وشمالي الهيكل وبالقرب من كنيسة القديسة آن الموجودة في الوقت الحاضر، توجد بركة بيت حسدا التي يمدها بالمياه نبع متقطع (يوحنا 5: 2-4) وتوجد غرب الهيكل ينابيع تسمى حمّام الشفاء.\n2. مستودعات المياه بركة الحمرا في الطرف الجنوبي من التل الجنوبي الشرقي ويرّجح أنها نفس البركة السفلى والعتيقة (1ش 22: 9 11)، وإلى الشمال قليلاً من بركة الحمرا توجد عين سلوان التي هي البركة العليا المذكورة في 2مل 18: 17 ويرّجح أنها نفس بركة الملك المذكورة في نحم 2: 14، وبركة سلوام المذكورة في يوحنا 9: 7 وقد عمل هذه البركة الملك حزقيا (2مل 20: 20)، وإلى شمالي منطقة الهيكل توجد بركة إسرائيل الكبيرة وبركة بيت حسدا (يو 5: 2-4)، خارج الباب الشرقي الذي يدعى باب سيدتي مريم يوجد مستودع مياه يسمى بركة سيدتي مريم، وفي الجزء الغربي من سور المدينة توجد بركة حمّام البطريق، وقد دعاها البعض خطأ، بركة حزقيا. وخارج السور الحالي، وغربي باب يافا أو باب الخليل توجد بركة السلطان، وربما سميت كذلك نسبة إلى السلطان سليمان القانوني. وكذلك يوجد كثير من خزّانات المياه المغطاة والتي تستخدم لخزن المياه فيها وتشمل هذه خزّانات المياه الكبيرة الموجودة تحت منطقة الهيكل. وأقيمت مستودعات حديثة جلبت إليها المياه من عيون قارة.3. القنوات اكتشف مجرى قديم يصل بين عين سيدتي مريم وبركة الحمرا، ويحتمل أنه نفس,مجرى مياه شلهوب الذي ورد ذكره في اش 8: 6 وربما أن مجرى هذه القناة كان يسير خارج أسوار المدينة فقد كان من السهل على المحاصرين أن يوقفوا سير المياه فيها ولذلك فقد حفر الملك حزقيا سرداباً تحت الأرض في الصخر طوله ألف وسبعمائة قدم ليوصل مياه جيحون أو عين سيدتي مريم، إلى بركة سلوام أو عين سلوان (2مل 20: و 2 أخبار 32: 30) وقد اكتشف نقش عبري يصف هذا العمل في السرداب. وتوجد قناة تصل بين بركة ماملا وبركة حمّام البطريق. ويرّجح أن هيرودس الملك الكبير هو الذي حفر هذه القناة من برك البراق، والتي تدعى أيضاً برك سليمان بالقرب من بيت لحم، إلى الهيكل في أورشليم وقد مدّ بيلاطس البنطي هذه القناة جنوباً إلى بركة أيوب. وتوجد قناة أخرى يرّجح أن الإمبراطور سفيرس الذي حكم أثناء القرن الثالث الميلادي هو الذي حفرها وكانت توصل المياه بين وادي بيار إلى نكان بالقرب من باب الخليل، أو باب يافا. وترفع المياه في الوقت الحاضر بالمضخات من رأس العين بالقرب من يافا إلى أورشليم.(3) الآثار القديمة:1 الأسوار. ويرّجح أن الأسوار اليبوسية كانت تحيط بالتل الجنوبي الشرقي الذي يقع إلى جنوبي منطقة الهيكل وبعد أن أخذ الملك داود المدينة حصّن هذا السور (2صم 5: 9 و 1 أخبار 11: 8) بما في ذلك ((ملو)) التي كانت على ما يرّجح قلعة للطرف الشمالي للتلّ. وبنى سليمان سوراً حول أورشليم بما في ذلك الهيكل والقصر المشيدان على التل الأوسط الشرقي (1مل 3: 1) ولكن لا يعرف مكان سور سليمان على وجه التحقيق. وأما باب الزاوية المذكور في عصر اميصا (2مل 14: 13) فيحتمل أنه كان بالقرب من مكان باب يافا أو باب الخليل، ويدل هذا على أن المدينة كانت تشمل التلّ الجنوبي الغربي. وكان السور الجنوبي الغربي للمدينة في عصر ارميا يصل إلى طرف وادي ابن هنّوم الذي هو الآن وادي الربابة (ار 19: 2) وكان السور الذي إلى الشمال في ذلك الحين، آي في القرن السابع قبل الميلاد، وكان يقع إلى شمال غربي الهيكل (2مل 22: 14). ويصف نحميا في نحم 2: 12-15 كيف أنه شاهد الأسوار والأبواب التي هدمها البابليون ويخبرنا في نحم ص 3 كيف أصلح الشعب هذه الأسوار وهذه الأبواب ورمموها. وقد هدم انطيوخس ابيفانيس اسوار أورشليم في القرن الثاني قبل الميلاد (1 مكا 4: 60 و 12: 36 و 13: 10). وقد حصن هيرودس الكبير سور أورشليم وبنى فيها أبراجاً وبعض ما عمله هو أساس القلعة الموجودة في الوقت الحاضر في الزاوية الجنوبية الشرقية للمدينة وعند المكان الذي كان مبكى اليهود فيما قبل. أما مكان السور على وجه التحقيق كما كان في أيام يسوع المسيح فغير يقيني. ويظن كثيرون من العلماء أن السور كان يتجه بحيث يجعل موقع كنيسة القيامة خارج المدينة وهذه هي وجهة النظر التقليدية. أما غيرهم فيظنون أن الأسوار كانت تشمل داخلها موقع هذه الكنيسة، وفي هذه الحال لا يكون هذا هو مكان الصلب الذي كان خارج المدينة (عب 13: 12). وقد بنى هيرودس اغريباس الأول السور الشمالي الثالث وقد اكتشفت بعض بقاياه شمالي السور الحالي. وفي سنة 132 ميلادية بنى الإمبراطور الروماني هدريان سوراً كان في الغالب مبنياً في مكان الأسوار الحالية وقد أقامها السلطان التركي سليمان في القرن السادس عشر بعد الميلاد. ويوجد في الأسوار الحالية 34  برجاً وثمانية أبواب.\nب. المواقع الكتابية: مما لاشك فيه أن الحرم الشريف يقوم في مكان ساحة هيكل سليمان وأن قبة الصخرة قائمة على الأرجح في مكان هيكل سليمان، والصخرة التي تحت هذه القبة هي على الأرجح في موضع المحرقة كما كان في هيكل سليمان وكان هيكل زر بابل وهيكل هيرودس الذي زاره يسوع المسيح في هذا المكان أيضاً. ويظهر من 2 أخبار 3: 1 أنه على هذه البقعة ذاتها والتي تسمى أيضاً بجبل المريا شرع إبراهيم بتقديم ابنه اسحاق (تك 22: 2) ويعتقد المسلمون أن النبي محمد ارتقى إلى السماء بمعجزة من مكان ما في الحرم يدعى قبّة المعراج.وتوجد في أورشليم أماكن كثيرة يربطها التقليد بحوادث في حياة يسوع المسيح. ولكن لا يمكن التثبت إلاّ من القليل منها على وجه التحقيق، فالبركة السفلية التي تحت الأرض وبالقرب من كنيسة القديسة آن (حنة) هي على الأرجح بركة بيت حسدا حيث شفا يسوع المقعد (يو 5: 2-9). وعين سلوان هي بالتحقيق بركة ساوام حيث اغتسل المولود من أعمى فاستعاد بصره وفقاً لأمر يسوع (يو 9: 7). وكان بستان جثسيماني على وجه التحقيق في المكان التقليدي المعروف عند سفح جبل الزيتون. وتوجد أرضية مرصوفة وعليها نقوش رومانية تحت كنيسة أخوات صهيون ويحتمل أن هذا البلاط في مكان هو البلاط الذي كان يدعى بالآرامية ((جبّاثا)) حيث كان يتخذ بعض الجنود الرومانيين مركزهم بالقرب من ساحة المحاكمة التي حاكم فيها بيلاطس المسيح (يو 19: 13). ويقول التقليد أن كنيسة القيامة مقامة فوق مكان الصلب ومكان قبر يسوع المسيح ولكن يظن بعض العلماء أن موضع هذهين المكانين غير معروف، ويقول بعض العلماء، أن موقعها يقع إلى الشمال من الأسوار الحالية.ج. نقوش قديمة لها صلة بالكتاب المقدس: لقد ذكر قبلاً نقش سرداب سلوام الذي يرجع إلى عصر الملك حزقيا. وقد وجد نقش على حجر كان يغطي عظام عزيّا الملك. وكذلك اكتشف نقشان من هيكل هيرودس وفيهما تحذير للأمم بالابتعاد عن ساحة العبرانيين (افسس 2: 14).(4) تاريخ أورشليم: يعكس تاريخ أورشليم في العصور الكتابية تاريخ الشرق الأدنى. وأول إشارة إلى أورشليم خارج الكتاب المقدس وردت في نصوص مصرية ترجع إلى القرن التاسع عشر قبل الميلاد وفيها تصب اللعنة على الأعداء الأجانب ومن ضمنهم ذكرت أورشليم. وتوجد رسائل بين لوحات تل العمارنة مرسلة من ملك أورشليم إلى اخناتون ملك مصر في القرن الرابع عشر قبل القرن الرابع عشر قبل الميلاد. وكان ملكي صادق كاهناً ملكاً على ساليم التي ي أورشليم (تك 14: 18) ويذكر قضاة 1: 8 أن رجال يهوذا أخذوا أورشليم ولكن أخذها من اليبوسيون، ومنهم أخذها داود (2صم 5: 6-9) وجعلها عاصمة ملكة. وقد كانت هذه خطة حكيمة منم جانبه إلى المدينة لم تكن تتصل بأي سبط من الأسباط ولذا فقد كانت صالحة كعاصمة لكل الأسباط. ولقد نهب شيشق ملك مصر أورشليم (1مل 14: 25 و26) وكذلك نهبها الفلسطينيون والعرب معاً في عصر يهورام (2 أخبار 21: 16 و 17) وكذلك نهبها يهوآش ملك إسرائيل (2 مل 14: 13 و 14). وقد فشل سنحاريب ملك أشور في أخذ المدينة (2 مل 19: 36). وأما نبوخذ نصر ملك بابل فقد أخذ المدينة مرتين (2 مل 24: 10-16 و 25: 1-11). وقد أذن الملك كورش الفارسي وشجع كثيرين من اليهود للرجوع إلى أورشليم (عزرا ص 1) وكذلك تمكن نحميا بمساعدة احشويروش (اتزركسيس) ملك فارس من العودة إلى أورشليم وإعادة بناء أسوار المدينة. وقد ضّم الاسكندر الأكبر أورشليم ضمن إمبراطوريته وبعد موته صارت أولاً تحت حكم البطالسة في مصر ثم انتقلت إلى السلوقيين في سوريا. وفي 165 قبل الميلاد ثار المكابيون اليهود وأقاموا في النهاية مملكة يهودية وكانت عاصمتها أورشليم. وبعد أن أخذ القائد الروماني بومباي أورشليم عام 63 ق.م أصبحت المدينة تحت حكم الرومان، أما عن طريق غير مباشرة كما كانت الحال في أيام هيرودس الكبير الذي كان يحكم بأذن روما، أو عن طريق مباشرة كما كانت الحال في حكم بنطيوس بيلاطس. وبعد ما ثار اليهود على روما أخذ القائد تيطس الروماني المدينة وأحرق الهيكل وباع كثيرين من شعبها في السبي وكان ذلك سنة 70 ميلادية وقد تّم هذا وفقاً لتحذير السيد المسيح (مت 24: 2).(5) أورشليم رمزياً: يستخدم الأنبياء أورشليم وصهيون كرمز إلى ملكوت الله فمثلاً انظر 1ش 65: 17-25 ورؤيا 21: 2 وقد أطلق اسم أورشليم على الكنيسة الممجدة.'),
(396, 'أّوْرَن', 'اسم عبري معناه ((شجرة الغار)) أو ربما ((شجرة الكافور)) وتعرف هذه الشجرة باللاتينية باسم Laurus nobilis وكان اسم ابن يرحميئيل من سبط يهوذا (1 أخبار 2: 25).'),
(397, 'أُورُو كْلِيدُون', 'كلمة يونانية معناها ((رياح جنوبية شرقية تثير الأمواج)) هذا اسم الرياح الجنوبية الشرقية التي هاجت على السفينة التي كان بولس قد أبحر عليها وجذبتها بعيداً عن طريق سيرها (1ع 27: 14) وخير المخطوطات اليونانية تذكر في هذا النص اسم ((يورقلون)) ومعناها ((رياح شمالية شرقية)).'),
(398, 'أورِي', 'اسم عبري وهو اختصار أوريا وقد ورد:(1) اسم رجل من سبط يهوذا وهو أبو بصلئيل الذي كان بين من عملوا في بناء المقدس وترصيعه (خر 31: 2 و 35: 30 و38: 22 و 1 أخبار 2: 20 و2 أخبار 1: 5).(2) اسم أبي جابر الذي كان وكيلاً لسليمان في جلعاد (1 مل 4: 19).(3) اسم أحد حرّاس أبواب الهيكل. وكان ممن اقنعهم عزرا بترك نسائهم الغريبات (عز 10: 24).'),
(399, 'أُورِيئِيل', 'اسم عبري ومعناه ((الله نوري)) وقد ورد اسم:(1) ابن تحث من عشيرة قهات من سبط لاوي (1 أخبار 6: 24).(2)  رئيس لعشيرة قهات من عشائر اللاويين في عصر داود (1 أخبار 15: 5 و11) ويحتمل أنه نفس الشخص المذكور في (1).(3) اسم رجل من جبعة وكان أب ميخايا أم ابيّا ملك يهوذا (2 أخبار 13: 2).(4) وقد ورد هذا الاسم كاسم ملاك في بعض الأسفار غير القانونية أو الأسفار المنسوبة إلى أسماء مستعارة مثل سفر اخنون 20: 2 واسدراس الثاني 4: 1.'),
(400, 'أُورِيم وُتّمِيم', 'كلمتان عبرانيتان معناهما (( أنوار وكمالات)) ويوجد هذان الاسمان معاً عادة، مع ان اوريم ذكرت مرتين بمفردها (عدد 27: و21 و1 صم 28: 6) ويرّجح أنهما كانا شيئين صغيرين أو ربما حجرين، وكانا يحفظان في صدرة رئيس الكهنة (خر 28: 30 و لا 8: 8) وكان رئيس الكهنة يستخدم الأوريم والتميم في معرفة إرادة الله في الأمور الكهنوتية أو السياسة القومية (عدد 27: 21 و1 صم 28: 6 وعز 2: 63 ونحم 7: 65) وقد وردت في الترجمتين السبعينية واليونانية، والفلجاتا اللاتينية إشارة إلى الاوريم والتميم في 1 صم 14: 41 وقد أبطل استخدام هذين القرعتين المقدستين في أزمنة ما بين العهدين ويحتمل أن الاسمين يدلان على نور وكمال الإرشاد الذي يأتي من الله.'),
(401, 'أُورِيَا', 'اسم عبري معناه ((يهوه نوري)) وكان:(1) اسم رجل من أصل حثي ولكنه كان من ضمن من يعبدون الرب إله العبرانيين كما يظهر من اسمه. وكان قائداً في جيش داود (2 صم 23: 39 و 1 أخبار 11: 41) وقد اتصل الملك داود اتصالاً شريراً ببثشبع زوجة اوريا، ثم أمر داود أن يوضع اوريا في المقدمة في مكان من الجيش يعرّض فيه للموت، عندما كان الجيش يحاصر ربّة عمون فقتل اوريّا وقد وبّخ النبي ناثان الملك داود على هذه الخطايا وأعلنه بحلول دينونة الله عليه بسببها (2صم ص 11 و12).(2) اسم رئيس كهنة أطاع أمر الملك آحاز وبنى مذبحاً في الهيكل في أورشليم مثيلاً لمذبح في دمشق (2 مل 16: 10-16) ويرّجح أنه هو نفس الكاهن الذي شاهد اللوح الذي كتب عليه أشعياء اسم ((مهير شلال حاش بز)) (اش 8: 1 و 2).(3) اسم نبي هو ابن شمعيا من قرية يعاريم. وقد تنبأ ضد يهوذا ثم هرب إلى مصر وقد أرسل الملك يهوياقيم رسلاً إلى مصر وأحضروه فقتله الملك (ارميا 27: 20-23).(4) اسم كاهن وكان أب مريموث (عز 8: 33 ونحم 3: 4 و 21) ويحتمل أنه نفس الرجل، الذي يرّجح أنه كان كاهناً، والذي وقف بجانب عزرا وقرأ الشريعة (نحم 8: 4).'),
(402, 'أُوزَال', 'وهو ابن يقطان ويظهر أنه استقر في جنوب بلاد العرب (تك 10: 27 و 1 أخبار 1: 21) وقد ورد اسم اوزال في كثير من المخطوطات العبرية وكذلك في الترجمة السبعينية لحزقيال 27: 19 كاسم مكان مع غيره من الأماكن في بلاد العرب. وهذا هو المكان الذي تسميه المصادر العربية ازل وهو الاسم القديم لعاصمة اليمن السابقة والتي صار اسمها فيما بعد صنعاء. ويحتمل أن اوزال المذكور في تك 10: 27 هو الذي اقام هذه المدينة.'),
(403, 'أُوزَاي', 'اسم عبري يحتمل أن يكون معناه ((يهوه قد سمع)) وهو ابو فالال الذي ساعد نحميا في ترميم اسوار أورشليم (نحم 3: 25).'),
(404, 'أُوصُم، أَوصْم', 'اسم عبري ويحتمل أن معناه ((غضبان)) وقد ورد:(1) اسم ابن يسى وأخي داود من سبط يهوذا (1 أخبار 2: 15).(2) اسم ابن يرحميئيل من سبط يهوذا (1 أخبار 2: 25) وقد وردت صيغة هذا الاسم في العبرية السابقة في (1).'),
(405, 'أُضنَّا', 'يشبه اللفظ العربي في اللفظ اليوناني للكلمة العبرية ((هوشعنا)) الواردة في مز 118: 25 ومعناها ((خلّص)). وقد هتف الجمهور بهذه الكلمة تحية ليسوع المسيح عند دخوله الانتصاري إلى أورشليم يوم أحد الشعانين أو أحد السعف. وقد وردت هذه الكلمة يمفردها مرتين (مز 11: 9 ويو 12: 13)ووردت مرتين وجاء بعدها ((لابن داود)) (مت21: 9 و15) ووردت مرتين أيضاً وجاء بعدها ((في الأعالي)) (مت 12: 9 ومر 11: 10). ويقول التلمود أن مز 118: 25الذي يشمل هذه الكلمة كان ينطلق من أفواه الشعب ضمن هتافهم وهم يهزون الأغصان في أيديهم أثناء عيد المظال. وقد ورد بعد هذه الكلمة في الأناجيل اقتباس من مز 118: 26 وكان هذا اللفظ في المبدا صلاة، وقد أصبح يستعمل فيما بعد كتعبير عن الفرح أو للترحيب بقادم.'),
(406, 'أُوغُسطُس أو أوْغُسطُس قيصر', 'أوغسطس لقب لاتيني ومعناه ((المبجّل))وقيصر اسم أسرة رومانية وهو أول امبراطور روماني (31 ق. م-14م) وكان اسمه أصلاً أوكتافيوس كايبياس, ولكنه أخذ اسم الأسرة قيصر عن يوليوس قيصر الذي كان أخاً لجدته لأمه. وقد أرسى قواعد السلام في كل أنحاء الإمبراطورية وفي الأراضي المحيطة بالبحر الأبيض المتوسط. وقد حكم هيرودس في فلسطين بسماح من أوغسطس وكن يرسل الجزية إلى روما. وقد جاءت مريم ويوسف إلى بيت لحم في وقت ولادة يسوع بناء على قرار أوغسطس بإجراء إحصاء (لو 2: 1) ولما أثبت أرخيلاوس ابن هيرودس بأنه غير كفء للحكم عزله أوغسطس وأرسل حكاماً رومانيين ليحكموا اليهودية. وقد أطلق هيرودس الكبير اسم قيصرية تكريماً لأوغسطس وتخليداً له. ولا تزال قيصرية باقية إلى اليوم. وكذلك اسم سبسطي وهي الترجمة اليونانية في المؤنث لاسم أوغسطس, أطلقه على مدينة  السامراة القديمة وتدعى الآن سبسطية. وقد أطلق هيرودوس فيليبس اسم قيصرية على مدينة أخرى تكريماً له وتخليداً لذكره وهذه المدينة تدعى الآن بانياس, وقد كان في هذه المدينة هيكل للإله أوغسطس. ولذا فقد كانت مناسبة جميلة أن يعلن بطرس في هذا المكان اعترافه المجيد بأن يسوع هو ابن الله الحقيقي (مت 16: 16) وقد سمي الشهر أغسطس كذلك لذكرى هذا الإمبراطور. وقد أصبح اسم أوغسطس واسم قيصر من ألقاب الأباطرة فيما بعد ذلك الحين, فنجد مثلاً في أعمال 25: 21 و26 أن لقب أوغسطس يستعمل لنيرون.\nكتيبة أوغسطس: \nهذا لقب شرف أعطي لكتيبة رومانية(1ع 27: 1) ويني كتيبة الإمبراطور وكان الاسم اوغسطس أحد ألقابها ولا يعرف إذا كان هذا اللقب أطلق على هذه الكتيبة في زمن الإمبراطور الأول أو فيما بعد.'),
(407, 'أُوفاز', 'اسم مكان كان مصدراً للذهب (1 رم 10: 9 ودا 10: 5)ولا يعرف هذا الكان إلا بهذه الصفة فحسب. وقد غيّر التلمود والترجمة السريانية الاسم إلى أوفير في هذين النصين. ولكن يرجّح أن أوفاز هي القراءءة الصحيحة في هذين الموضعين لأنها أكثر صعوبة في قرائتها من أوفير.'),
(408, 'أُوفير: وقد ورد', '1) اس اسم ابن يقطان ويظهر أنه استقر في جنوب بلاد العرب لأن اسمه ورد مع اسم شبا وحويلة (تك 10: 29 و1 أخبار 1: 23).(2)اسم أرض سميت باسم ابن يقطان الذي ذكر سابقاً في ورجّح أن هذه الأرض كانت في جنوب بلاد العرب, أو اليمن في الوقت الحاضر. وقد اشتهر هذا المكان بذهبه (1 أخبار 29: واي 22: 24 و28: 16 ومز 45: 9 واش 13: 12) وقد أرسل سليمان ملك بني اسرائيلوحيرام ملك صور اسطرلاً تجارياً من عصيون جابر على خليج العقبة إلى أوفير وقد أحضر هذا الأسطول ذهباً وخشب صندل وحجارة كريمة (1 مل 9: 26-28 و10: 11). وقد قال بعضهم أن أوفير تقع على شاطيء أفريقيا الشرقي أو أنها في بلاد الهند ولكن يرجح أن موقعها هو في بلاد اليمن.'),
(409, 'أُولام', 'اسم عبري معناه ((أول)) وهو:(1) ابن فرش من سبط منسّى (1 أخبار 7: 16 و 17).(2) ابن عاشق من سبط بنيامين (1 أخبار 8: 39) وكان بنو أولام أبطال حرب (1 أخبار 8: 40).'),
(410, 'أولاي', 'لما كان دانيال النبي في مدينة شوشان, وهي شوش في الوقت الحاضر رأى رؤيا ورأى في الرؤيا أنه على نهر أولاي (دا 8: 2 و 16) ورأى كبشاً وتيساً, ويحتمل أن نهر أولاي هو نفس نهر كرخة غرب شوشان أو أن فرع صغير منه أو من قناة خارجة من هذا النهر أو أنه القناة التي تقع شرقي شوشان.'),
(411, 'أولُمبَاس', 'اسم يوناني وهو اختصار أولمبيادوروس أي عطية زيوس ((أولمبيوس)) وهو مسيحي كان في روما وقد أرسل بولس إليه (رو 16: 15) ويقول تقليد متأخر يشك في صحته, أنه أصبح فيما بعد أسقف فيليبي وأنه استشهد في روما.'),
(412, 'أوْمار', 'اسم عبري ويحتمل أن معناه ((فصيح)) وكان ابن اليفاز وحفيد عيسو (تك 36: 11 و 1 و1 أخبار 1: 36).'),
(413, 'أون', '(1) اسم عبري معناه ((قوة)) وهو ابن فالت من سبط يهوذا وقد اشترك في عصيان قورح على موسى (عدد 16: 1).(2) اسم من اللغة المصرية القديمة لفظه أونو وقد تزوج يوسف ابنة كاهن أون (تك 41: 45 و 50 و46: 30) وقد ورد في الترجمة السبعينيةفي خروج 1: 11 اسم أون مضافاً إلى أسماء المدن التي بناها العبرانيون في مصر. والقراءة العبرية في النسخة المتداولة في حز 30: 17 هي آون أي ((شر)) ولكن القراءة بحسب الترجمة السبعينية والفلجاتا هي أون ويحتمل أن هذه هي القراءة الأصلية. وتدعى المدينة في 1ر 43: 13 بيت شمش, وهو نفس الاسم اليوناني لمدينة ((هليوبوليس)) مدينة الشمس, وتدعى المدينة في 1 ش 19: 18 في بعض المخطوطات العبرية ((مدينة الشمس)) (حرس) وتدعى في مخطوطات أخرى((مدينة الهلاك)9 (هرس). وكانت أون في مصر قديماً عاصمة النوم وهو الأقليم الثالث عشر في مصر السفلى وكانت مركز عبادة رع إله الشمس وموقع أون بالقرب من ضاحية المطرية الحالية على مسافة عشرة أميال شمالي شرقي القاهرة. وأهم ما تبقى من الآثار من هذه المدينة القديمة مسلة أتقنت نقوشها, وهي مسلة سنوسرت الأول من الأسرة الثانية عشرة الذي حكم من 1971- 1926 ق.م. ومما لاشك فيه أن يوسف شاهد هذه المسلة, وهي مذكورة في 1ر 43: 13 وقد اشتهرت أون قديماً بجامعتها. ويقول التقليد أن العائلة المقدسة جلست لتستريح تحت شجرة جميز في أون التي هي المطرية حالياً, وأن مريم غسلت ثياب الصبي يسوع في مياه عين شمس.'),
(414, 'أُونَام', 'اسم عبري معناه ((قوي)) وهو اسم:(1) أحد أبناء شوبال, وهو حوري من آدوم (تك 36: 23 و 1 أخبار 1: 40).(2)أحد أبناء يرحميئيل من سبط يهوذا (1 أخبار 2: 26 و 28).'),
(415, 'أُونَان', 'اسم عبري معناه ((قوي وهو أحد أبناء يهوذا من امرأة كنعانية. وبعد موت عير أخيه, رفض أونان أن يخلف نسلاً لأخيه كما كان متبعاً في ذلك الحين ووفقاً لأمر أبيه. وقد أمات الله أونان بسبب رفضه (تك 38: 4 و46: 13).'),
(416, 'أُونُو', 'اسم عبري معناه ((قوي)) وهو اسم مدينة في بنيامين (نحم 6: 2و 11: 35) وقد بناها بنياميني يدعى شامر (1 أخبار 8 : 12) وقد عاد بعض ساكنيها من سبي بابل (عز 2: 23 ونحم 7: 37) وتدعى الآن كفرعانا وهي على مسافة سبعة أميال شرقي مدينة يافا.'),
(417, 'أُوهَد', 'اسم لايعرف معناه على وجه التحقيق وهو أحد أبناء شمعون (تك 46: 10 وخر 6: 15).'),
(418, 'أُوهَل', 'اسم عبري بمعنى ((خيمة)) وهو واحد من بني زر بابل (1 أخبار 3: 20).'),
(419, 'أُوِي', 'اسم عبري معناه ((رغبة)) وهو واحد من ملوك مديان الذين قتلهم بنو اسرائيل في أيام موسى (عدد 31: و8 ويش 13: 21).'),
(420, 'إِبْنُ آوَى', 'الاسم العبري هو ((تان)) وقد ترجم صواباً بابن آوى أو بنات آوى بحسب الأصل سواء أكان مفرداً أوجمعاً في 1 ش 13: 22 وار 9: 11 و 10: 22 و14: 6 و 49: 33 و51: 37 ومراثي 4: 3 وميخا 1: 8. وقد وردت نفس الكلمة وكان ينبغي أن تترجم بابن آوى أو ببنات آوى حسب صيغتها في المفرد أو في الجمع, في هذه الأماكن 1ش 34: 13 و35: 7 و43: 20 وملا 1: 3 ولكنها ترجمت في ترجمة فانديك العربية للكتاب المقدس بكلمة ((ذئب)) أو ((ذئاب)) وكذلك ترجت في ترجمة فانديك بكلمة ((التنانين)) في مز 44: 19 وهذا مجانب للصواب. ونعلم من هذه النصوص أن ابن آوي يعيش في الخرب مثلاً، اش 13: 22 وأنه كثير العواء (ميخا 1: 8).واسم ابن آوي في اللاتينية هو Cauis aureus ومعناه ((الكلب الذهبي)) وفي هذا إشارة إلى لون فرائه السفلي الأصفر. وهو أكبر من الثعلب وأصغر من الذئب. وتصطاد جماعات ابن آوى جماعات وغالبية ما تقتتات به الرمم أو الجيف.وقد ورد في ترجمة فانديك العربية للكتاب المقدس اسم ابن آوى في قضاة 15: 4 ومز 63: 10 ترجمة لكلمة تعرب عادة بكلمة ((ثعلب)) وكذلك وردت أيضاً في اش 13: 22 و 34: 14 ترجمة تعني على الأرجح ((ضبع)).'),
(421, 'أَوِيل مَرُدَخ', 'من البابلي أومل مردوك ومعناه ((إنسان الإله مردوخ)) وهو ابن نبوخذنصر وخليفته في ملك بابل (562-560 ق.م). وقد أخرج يهوياكين الملك الأسير من السجن وكان يعطيه نصيباً يومياً من الطعام (2 مل 25: 27-30 وار 52: 31-34) وقد ذكر هذا النصيب اليومي في بعض السجلات البابلية التي اكتشفت حديثاً. ونرجل شراصر المذكور في ارميا 39: 13 هو زوج أخت اويل مردوخ الذي قتله وخلفه على العرش.'),
(422, 'إِيثَام', 'مكان حل فيه العبرانيون في خروجهم من أرض مصر ويظهر من خروج 13: 20 وعدد 23: 6-8 أنه كان شرقي سكوت التي يرجح أن مكانها في الوقت الحاضر هو تل المسخوطة، والتي كانت على طرف الصحراء. ولذا فيظن أن ايثام كانت بالقرب من مدينة الإسماعلية الحالية. وبعد أن عبروا البحر سار العبرانيون ثلاثة أيام في البرية إلى أن وصلوا مارة.'),
(423, 'إِيثَامَار', 'كلمة عبرية معناها ((ساحل النخيل)) وهو اسم أصغر أولاد هارون (خر 6: 23) وقد كرّس لوظيفة الكهنوت مع أبيه وأخوته الثلاثة الكبار (خر 28: 1) وقد عهد إليه بمسؤولية إحصاء المواد التي جمعت لبناء خيمة الاجتماع (خر 38: 21) وقد رأس الجرشونيين والمراريين في خدمة خيمة الاجتماع (عدد 4: 21-33) وقد أسس أسرة كهنوتية (1 أخبار 24: 4-6) وقد استمرت إلى ما بعد السبي (عز 8: 2). وكان رئيس الكهنة عالي من اسرة ايثامار وقد استمر نسل عالي في رئاسة الكهنوت إلى أن أخذ صادوق من أسرة العازار وظيفة الكهنوت في عصر سليمان (1 مل 2: 27 و35) ولم يكن نسل ايثامار كثيرين كما كان نسل العازار (1 أخبار 24: 4).'),
(424, 'أَيثَان', 'اسم عبري معناه ((ثابت)) وقد ورد:(1) اسم ابن زارح من سبط يهوذا (1 أخبار 2: 6) وقد أشتهر بحكمته (1 مل 4: 31) ويظهر من عنوان مزمور 89 أنه كان كاتب ذلك المزمور.(2) اسم أبن قيشي أو قوشيا من سبط لاوي وكان أحد المغنين في خيمة الاجتماع في عصر داود (1 أخبار 6: 44 و 47 و 15: 17 و 19) وكان يدعى أيضاً يدوثون (1 أخبار 16: 41).(3) اسم ابن زّمة من سبط لاوي ( 1 أخبار 6: 42).'),
(425, 'أَيْثَانِيم', 'اسم عبري ومعناه ((مطر دائم)) وهو الشهر السابع في التقويم العبري (1 مل 8: 2) وهو يدعى أيضاً تشرى ويقابل شهر أكتوبر على وجه التقريب. وكان عيد الأبواق ويوم الكفارة وعيد المظلات تقع في هذا الشهر (انظر ((شهر)) ).'),
(426, 'إِيثِيئيل', 'اسم عبري معناه ((الله معي)) وقد ورد:(1) اسم ابن يشعيا من سبط بنيامين (نحم 11: 7).(2) اسم واحد من اثنين وجّه اليهما آجور خطابه في أمثاله (أمثال 30: 1).'),
(427, 'إِيْحِي', 'اسم عبري وهو اختصار احيرام، وأحد أولاد بنيامين (تك 46: 21) ويدعى احيرام في عدد 26: 38، واحير في 1 أخبار 7: 12 واخرخ في 1 أخبار 8: 1.'),
(428, 'إِيخَابُود', 'اسم عبري معناه ((أين المجد؟ أو لا مجد)) وهو ابن فينحاس وحفيد عالي الكاهن الأعظم (1 صم 4: 21) وقد ولد بعد وصول الأخبار السيئة إلى أمّه تحمل إليها بعد موت زوجها وحميها وأخذ تابوت الرب.'),
(429, 'إِيزَابَل', 'يحتمل أن معنى الاسم ((غير مرتفع)) وقد ورد:(1) اسم ابنة اثبعل ملك الصيدونيين وكاهن عشترتى (1مل 16: 31) وقد تزوجت آخاب ملك إسرائيل عبادة البعل اشيرة (1 مل 16: 32 و 33) وكانت تعول كثيرين من كهنة البعل واشيرة (1 مل 18: 19) وقد حاولت أن تقتل أنبياء الرب (1 مل 18: 4) وبعد انتصار إيليا على أنبياء البعل على جبل الكرمل عملت ايزابل نذراً بأن تقتل إيليا (1 مل 19: 2) وايزابل هي التي دبّرت قتل نابوت لكي تحصل على كرمه ليأخذه آخاب الملك (1 مل 21: 8-14) وقد تنبأ إيليا بأن الكلاب ستأكل ايزابل (1مل 21: 23) وأثناء ثورة ياهو على بيت آخاب كحلت ايزابل عينيها وتطلعت من النافذة إلى ياهو ولكنه أمر فقذف بها رجال إلى أسفل وتمت فيها نبوة إيليا (2 مل 9: 30-37).(2) اسم امرأة في ثياتيرا كانت تدعو نفسها نبية، وقد أغوت المسيحيين هناك أن يتكبوا فاحشة وأن يأكوا مما ذبح للأوثان. وقد أطلق عليها الاسم إشارة إلى شرها (رؤ 2: 18-23).'),
(430, 'إِيشْبُوشَث', 'اسم عبري معناه ((رجل الخزي)) وهو ابن الملك شاول وخليفته كملك على إسرائيل وكان اسمه أصلاً اشبعل ومعناه ((رجل السيّد)) ولكن تغير اسمه عندما صار اسم بعل بغيضاً لأنه كان كذلك اسم إله من آلهة الأوثان (2 صم 2: 8 و 1 أخبار 8: 33 و 9: 39). وبعد موت شاول نادى ابنير بايشبوشث ملكاً على إسرائيل ولكن يهوذا قبل داود ملكاً عليه. وكانت سن ايثبوشث عندما بدأ حكمه أربعين سنة تقريباً وحكم سنتين وكانت عاصمة ملكة محنايم شرقي الأردن (2 صم 2: 8-12)وقد انهزمت قوات ايشبوشث في حربها ضد داود (2 صم 2: 12-3: 1) وقد اتهم ايشبوشث كل شجاعته (2 صم 4: 1) وقد قتل ركاب وبعنة ايشبوشث واتيا برأسه إلى داود ولكن داود قتلهما ودفن رأس ايشبوشث بكرامة في حبرون (2 صم 4: 5-12) من ثم أصبح داود ملكاً على إسرائيل كلّه.'),
(431, 'إِيشْهُود', 'اسم عبري ومعناه ((رجل جلال)) وهو رجل من سبط منتسى وكانت أمّه همّولكة (1 أخبار 7: 18).'),
(432, 'إِيصر', 'اسم عبري معناه ((كنز)) وهو ابن سعير الحوريّ من أرض ادوم (تك 36: 21 و 30 و 1 أخبار 1: 38).'),
(433, 'إيطَالِيَا وإِيطَالِيَة', 'أطلق الاسم إيطاليا في الأصل على الجزء الجنوبي من شبه الجزيرة الإيطالية ولكن في أزمنة العهد الجديد أصبح الاسم يشمل كل شبه الجزيرة بين البحر الإدرياتيكي والبحر الترياني. وقد عاش أكيلا وبرسكلا في إيطاليا قبل مجيئهما إلى كورنثوس (1 ع 18: 2) وقد ذكرت إيطاليا أيضاً في الكلام عن رحلة بولس إلى روما ليمثل أمام قيصر لنظر شكواه (1 ع 27: 1 و 6) ويشمل كاتب الرسالة إلى العبرانيين ضمن من يرسل إليه سلامه، أولئك الذين من إيطاليا. ويدل هذا على أنه كان هناك مسيحيون فإنَّ أجزاء متفرقة من شبه الجزيرة الإيطالية. وتشمل البلدان الإيطالية المذكورة في العهد الجديد، روما عاصمة إيطاليا والأقاليم المحيطة بالبحر الأبيض المتوسط، وريغيون وبوطيولي (1 ع 28: 13) وفورن أبيوس والثلاثة الحوانيت (1 ع 28: 15).وكانت الكتيبة الإيطالية تدعى رسمياً ((الكتيبة الإيطالية الثانية للرعية الرومانية)). وكانت تتكون من عبيد محررين من إيطاليا. وقد صار كرنيليوس قائد هذه الكتيبة مسيحياً.'),
(434, 'أيعَزَر', 'ختصار أبيعزر وهو ابن جلعاد من سبط منسّي (عدد 26 : 30) وصيغة الاسم لاسم المألوفة الكاملة هي أبيعزر (يش 17 : 2).'),
(435, 'إِيْفَة', 'مكيال عبري يستعمل في كيل الدقيق (قض 6 : 19). أو الشعير (راعوث 2 : 17) انظر ((مكاييل وموازين)).'),
(436, 'إيِفُود', 'اسم عبري معناه ((غطاء)) وهو رجل منبسط منسّي وقد ناب ابنه حنيئيل عن سبط منسّي عند تقسيم الأرض (عدد 34 : 23).'),
(437, 'إيْقُونِيَة', 'وهي مدينة في جنوبي الجزء الأوسط من آسيا الصغرى. وكانت أصلاً مدينة في فريجية ولكن ضمها الرومان إلى ليكأونية، وكانت على الطريق التجاري بين أفسس وسوريا. وقد نادى بولس وبرنابا بالإنجيل في أيقونية في رحلتهما التبشيرية الأولى وقد آمن كثيرون من اليهود واليونانيين ولكن لما سمع الرسولان بأن هناك خطة للهجوم والاعتداء عليها ذهبا إلى لسترى (1 ع 14 : 1 - 6) وقد رجع بولس وبرنابا فيما بعد إلى أيقونية وشجعا المسيحيين هناك وأقاموا هناك قسوساً (1 ع 14 : 21 - 23) ويظن بعض العلماء أن بولس زار أيقونية مرة أخرى أثناء رحلته التبشيرية الثانية (1 ع 16 : 6). وكذلك يظنون أنه زارها مرة ثالثة أثناء رحلته التبشيرية الثالثة ( 1 ع 18 : 23) وقد امتدح المسيحيون في أيقونية تيموثاوس (1 ع 16 : 2) ويذكر بولس في نهاية حياته الاضطهادات التي عانات في أيقونية (2 تيمو 3 : 11) ويقول التقليد أن أكيلا استشهد هناك. والاسم التركي لهذه المدينة هو قونيا.'),
(438, 'إِيل', 'اسم من أسماء الله في العبرية. وتستعمل أيل بمفردها للدلالة على الإله الواحد الحقيقي فمثلاً عدد 12 : 13 وكثيراً ما يستعمل أيل مع لقب من ألقاب الله مثل أيل عليون ((الله العلي)) وأيل شداي (الله القدير)) تك 35 : 11. وتستعمل كلمة أيل كجزء من أسماء عديدة كثيرة مثل العاد ((الله قد شهد)) وأليعازر ((الله قد أعان)). وأحياناً ما تستخدم أيل عن إله من آلهة الأوثان مثلاً خر 34 : 14 وتوجد كلمات قريبة من كلمة أيل في اللغات السامية الأخرى وتدل على معنى ((إله)) فمثلاً أيل في الأكادية تدل على آله على وجه العموم، وأيل في الأوجريتية اسم أبي إلهه.'),
(439, 'إِيل إله إسرائيل', 'ومعناه أيل إله إسرائيل وقد أطلق يعقوب هذا الاسم على مذبح بناه بالقرب من شكيم وأطلقه عليه لأن اسمه هو كان قد تغير منذ زمن قصير من يعقوب إلى إسرائيل (تك 32 : 28).'),
(440, 'إِيل بيت أيل', 'ومعناه ((إله بيت إيل)) فقد بنى يعقوب مذبحاً في بيت إيل ودعى اسم المكان إيل بيت إيل لأن الله كان قد أعلن نفسه له قبلاً هناك (تك 28 : 11 - 22).'),
(441, 'إيل فَارَان', 'اسم مدينة أدومية على حدود برية فاران وقد أحذ كدرلعومر هذه المدينة (تك 14 : 6) ولا يعرف موقعها على وجه التحقيق. وقد ورد هذا الاسم في ترجمة فانديك العربية للكتاب المقدس عند ذكر ((بطمة فاران)).'),
(442, 'أََيْلاث أو أيْلَة', 'اسم عبري معناه ((شجرة البلوط)) وقد ورد:(1) اسم بلدة في الطرف الشمالي من خليج العقبة بالقرب من عصيون جابر (تث 2 : 8 و 1 مل 9 : 26) وكانت ميناءً بحرياً مهماً كما كانت مركزاً للقوافل ذات الأهمية. وقد عبر بنو إسرائيل بايلة في طريق مرورهم في آدوم (تث 2 : 8 ) ويرّجح أن داود الملك أخذ هذه البلدة من الأدوميين (2 صم 8 : 14) وقد ذكرت أيله في 2 أخبار 8 : 17 مع عصيون جابر كميناء كان قد أعده سليمان لأسطوله التجاري. وقد استعاد الأودمييون المدينة ولكن أخذها منهم عزّيا ملك يهوذا ثانية وأعاد بناءها (2 مل 14 : 22) وبعد ما أخذ رصين ملك آرام المدينة رجع إليها الأدوميون (2 مل 16 : 6) وكانت المدينة النبطية التي بنيت على هذا الموقع تدعي أيلا. انظر ((عصيون جابر)).(2) اسم رئيس أدومي (تك 36 : 41 و 1 أخبار 1 : 52).(3) اسم أبي شمعي أحد ضباط سليمان (1 مل 4 : 18).(4) اسم ابن بعشا ملك إسرائيل وهو الذي خلفه على العرش. (1 مل 16 : 6) ولما كان أيلة في السنة الثانية من حكمه كان يشرب ويسكر في بيت أرصا وكيلة في ترصة وكانت حينئذ عاصمة إسرائيل. فدخل عليه زمري رئيس نصف المركبات وقتله وملك عوضاً عنه (1 مل 16 : 8 - 10).(5) اسم أبي هوشع آخر ملوك إسرائيل (2 مل 15 : 30 و 17 : 1 و 18 : 1 و 9).(6) اسم ابن كالب من سبط يهوذا (1 أخبار 4 : 15).(7) اسم ابن عزّي من سبط بنيامين (1 أخبار 9 : 8).'),
(443, 'أَيْلُول', 'سم الشهر السادس بين الشهور العبرية (نحم  7 : 15) وهو يقابل شهر سبتمر على وجه التقريب انظر ((شهر)).'),
(444, 'إيْلُون', 'اسم عبري معناه ((بلوطة)) وهو اسم:(1) رجل حثي وأبي أحد نساء عيسو (تك 26 : 34 و 36 : 2).(2) أحد أبناء زبولون (تك 46 : 14 وعدد 26 : 26).(3) قاضي في إسرائيل ويلقب بالزبولوني وقد قضى مدة عشر سنوات ودفن في أيلون في زبولون (قض 12 : 11 و 12).(4) قرية في دان (يش 19 : 43) ويرجح أن مكانها حالياً هو قرية عليان أو يحتمل أنها خربة وادي علين الحالية.'),
(445, 'أَيْلون بيت حانان', 'وهي بلدة، ويرجح أنها كانت في دان وكان يحكمها ابن دقر أحد ضباط سليمان (1 مل 4 : 9) ويرجّح أنها نفس أيلون المذكورة في (4) السابقة.'),
(446, 'إِيلِيآثَة', 'اسم عبري ومعناه ((الله قد جاء)) وهو ابن هيمان من سبط لاوي وكان يضرب على الآلات الموسيقية في عبادة الرب في زمن داود (1 أخبار 25 : 4 و 27).'),
(447, 'إِيْليَئِيل', 'اسم عبري ومعناه ((أشجار)) وهو اسم المكان الثاني الذي حلّ فيه بنو إسرائيل بعد عبورهم البحر الأحمر. وكان هذا المكان يقع بين مارة وبرية سين، وكان فيه اثنا عشر عيناً وسبعون شجرة من أشجار النخيل (خر 15 : 27). ويرجَّح أن مكانه الحالي هو واحدة وادي غرندل حيث توجد ينابيع وأشجار ويبعد هذا المكان مسافة ثلاثة وستين ميلاً جنوبي شرقي السويس.'),
(448, 'إِيلِيِّا', 'اسم عبري ومعناه ((إلهي يهوه)) والصيغة اليونانية لهذا الاسم هي اليأس وتستعمل أحياناً في العربية. وهو:(1) نبي عظيم عاش في المملكة الشمالية. وبما أنه يدعى التشبي فيرجّح أنه ولد في تشبة ولكنه عاش في جلعاد (1 مل 17 : 1) وكان عادة يلبس ثوباً من الشعر (مسوحاً) ومنطقة من الجلد (2 مل 1 : 8) وكان يقضي الكثير من وقته في البرية (1 مل : 17 : 5 و ص 19) وبما أن إيزابل ساقت زوجها وشعب بني إسرائيل إلى عبادة البعل فقد تنبأ إيليا بأن الله سيمنع المطر عن بني إسرائيل واعتزل النبي إلى نهر كريت وكانت الغربان تعوله وتأتي إليه بالطعام وبعد أن جفّ النهر ذهب إلى صرفة وبقي في بيت امرأة أرملة، ووفقاً لوعد إيليا لها لم يفرغ من بيتها الدقيق والزيت طوال مدة الجفاف. ولما مات ابن الأرملة صلى إيليا فأعاد الله الحياة إلى الصبي (1 مل ص 17). وفي السنة الثالثة من الجفاف قابل إيليا عوبديا وكيل آخاب وكان مؤمناً بالله واتفق معه على مقابلة الملك. وطلب النبي من الملك أن يجمع الشعب إلى جبل الكرمل وأن يحضر معه أنبياء البعل وأشيرة ليرى أيهما يرسل ناراً تلتهم المحرقة، الرب أم البعل. فصلّى أنبياء البعل ولكن لم يكن من مجيب لصلاتهم. ولكن دعا إيليا الرب فاستجاب له ونزلت نار من هذه السماء والتهمت المحرقة. ويشير التقليد إلى أن هذه المعجزة تمت على جبل الكرمل في مكان يدعى حالياً ((المحرقة)) فاقّر الشعب بأن الرب هو الله الإله الحقيقي. وبناء على أمر إيليا قتل أنبياء البعل. عندئذ أعلن إيليا بأن المطر سوف ينزل وجرى قدام مركبة الملك إلى مدخل يزرعيل (1 مل ص 18).ولما توعدت إيزابل بقتل إيليا لأنه قتل أنبياء البعل هرب إلى الجنوب إلى بئر سبع وطلب إلى الله أن يأخذ حياته، ولكن الله أرسل إليه ملاكاً ليشجعه وليعطيه طعاماً وماء. وبقوة هذه الأكلة أمكنه أن يسافر مدة أربعين يوماً إلى جبل حوريب الذي يدعى أيضاً جبل سيناء ويقول التقليد أن المغارة التي على جبل موسى هي المكان الذي أقام فيه إيليا، ثم هناك أتى الرب بالريح والزلزلة والنار ولكنه في النهاية تكلم إلى إيليا في صوت منخفض خفيف. ثم بعث الله إيليا ليمسح ياهو ملكاً على إسرائيل وليمحو شر بيت آخاب وعبّاد البعل، وليمسح حزائيل ملكاً على آرام وليمسح اليشع نبياً ليخلفه (1 مل ص 19).وقد دبرت إيزابل قتل نابوت ليرث زوجها آخاب كرم نابوت. ولما دخل آخاب ليأخذ الكرم قابله إيليا وتبنأ بالموت الشنيع الذي سيموته آخاب وإيزابل وكذلك أنبأ بمحو بين آخاب (1 مل ص 21).وسقط احزيا ابن آخاب وخليفته على العرش من النافذة فمرض، وأرسل رسلاً ليسألوا بعل زبوب إله عقرون عن شفائه فقابل إيليا الرسل وأرجعهم إلى السامرة فأرسل اخزيا ضابطاً مع خمسين رجلاً ليأخذوا إيليا ولكنه صلى فأتت النار من السماء والتهمت الضابط والخمسين رجلاً معه. وحدث ذات الأمر مع ضابط ثاني وخمسين رجل آخرين. أما الضابط الثالث الذي أُرسل إليه لأخذه فإنه تضرع لأجل حياته وحياة رجاله الخمسين فذهب معه إيليا إلى الملك اخزيا وأنبأه بأنه مادام قد حاول أن يستشير إلهاً وثنياً فإنه سيموت حالاً. وهكذا حدث وتمت هذه النبؤة (2 مل ص 1). ويسجل لنا 2 أخبار 21 - 15 رسالة من إيليا إلى الملك يهورام ملك يهوذا، فيها ينتقد إيليا سلوك الملك وشروره وينذره بمرض يأتي عليه وبموته.وفي نهاية أيامه ذهب إلى الأردن مع اليشع وضرب إيليا الأردن بردائه فانشق الماء وسار النبيان على اليابسة ثم جاءت مركبة وفرسان نارية وحملت إيليا إلى السماء وترك ردائه لأليشع (2 مل 2 : 1 - 18).وقد وردت آخر إشارة إلى إيليا في العهد القديم في ملا 4 : 5 و 6 والتي فحواها أن الرب سيرسل إيليا النبي قبل يوم الرب العظيم. ويترك بعض اليهود مقعداً خالياً على مائدة عيد الفصح لإيليا.أما في العهد الجديد فقد وعد الملاك أن يوحنا المعمدان سيتقدم المسيح برؤيا إيليا وقوته (لو 1 : 17) وفي هذا المعنى قال المسيح أن إيليا قد جاء في شخص يوحنا المعمدان (مت 11 : 14 و 17 : 10 - 12) وقد ظن بعض الناس خطأ أن يسوع نفسه هو إيليا (مت 16 : 14) وفي عظته التي ألقاها في الناصرة أشار يسوع إلى إقامة إيليا في بيت أرملة صرفة (لو 4 : 26 و 27) وقد ظهر إيليا وموسى مع يسوع عند التجلي (لو 9 : 28 - 36 وغيره من الأناجيل). وكان يعقوب ويوحنا يفكران فيما حدث لجنود اخزيا (2 مل ص 1) عندما طلبا من يسوع إذا ما كانا يدعوان أن تنزل نار على السامريين ولكن يسوع وبخهما على ذلك (لو 9 : 54 و 55) ويشير بولس إلى تشجيع الرب لإيليا بأن مؤمنين كثيرين كانوا بين بني إسرائيل في أيام إيزابل وأخاب (رو 11 : 2 - 4) ويذكر يعقوب (ص 5 : 17 و 18) وصلاة إيليا لأجل امتناع المطر وصلاته لأجل نزول المطر كمثال لقوة صلاة البار.\n(2) اسم ابن يروحام من سبط بنيامين (1 أخبار 8 : 27).\n(3) اسم كاهن وهو ابن حاريم وقد أقنعه عزرا بترك زوجته الغريبة (عز 10 : 21).\n(4) اسم رجل من بني إسرائيل وهو ابن عيلام وقد أقنعه عزرا بترك زوجته الغريبة (عز 10 : 26).'),
(449, 'آلإِيمِيُّون', 'وهم السكان الأقدمون للمنطقة التي سكنها الموآبيون فيما بعد، وهي تقع إلى شرقي الأردن وقد هزمهم كدرلعومر في سهل قريتايم (تك 14 : 5) وكانوا طوال القامة كالعناقيين. وكانوا في وقت ما شعباً كبير العدد وقوياً وكانوا يدعون أيضاً بالرفائيين (تث 2 : 9 - 11).'),
(450, 'أَيَّة', 'اسم عبري معناه ((صقر)) وقد ورد:(1) اسم رجل حوري هو ابن صعون (تك 36 : 24 و 1 أخبار 1 : 40).(2) اسم أبي رصفة سرية شاول (2 صم 3 : 7 و 21 : 8 و 10 و 11).'),
(451, 'أُيَّل وأُيَّلة وأيائل', 'يوان يأكل العشب ويدعى باللاتينية  Cervus وهو شديد السرعة وكان يحسب بين الحيوانات الطاهرة حسب الشريعة (تث 12 : 15 و 14 : 5) ويشير نش 2 : 9 واش 35 : 6 إلى خفته. وهو كثير الظمأ أثناء ركضه، وإذا ما جاع هزل وضعفت قوته (ار 14 : 5 ومراثي 1 : 6) ومن عادات الأيل القفز على الصخور (2 صم 22 : 34 ومز 18: 33 وحب 3 : 19) ويشير الكتاب المقدس إلى محبة الأيل (نش 2 : 7 و 3 : 5) ويشبه نفتالي بأيلة مسيّبة (تك 49 : 21).'),
(452, 'أُيَّلَة الصبح', '(أيلة الفجر) وتوجد هذه العبارة في عنوان مز 22. وعلى الأرجح هي لا تشير إلى لى مضوع الشعر إنما تشير إلى النغمة التي يوقع عليها المزمور.'),
(453, 'أيّلون', 'اسم عبري معناه (مكان الأيائل) وقد ورد:(1) اسم مدينة للاويين في سبط بني دان (يش 19 : 42) وقد أعطيت لبني قهات (يش 21 : 24) وكان يقطنها الأموريون (قض 1 : 35) وقد ورد ذكرها في حروب بني إسرائيل مع الفلسطينيين (1 صم 14 : 31 و 2 أخبار 28 : 18) وقد حصّنها رحبعام (2 أخبار 11 : 10) ثم صارت ضمن منطقة بني بنيامين لأن بني دان وسعوا منطقتهم إلى الشمال (قض ص 18) وبما أنها كانت على الحدود الفاصلة بين المملكتين فإنها تذكر أحياناً في قسم أفرايم (1 أخبار 6 : 66 و 69) وأحياناً في مقسم يهوذا وبنيامين (2 أخبار 11 : 10 و 28 : 18) واسمها الحديث ((يالو)) وهي قرية صغيرة على بعد 14 ميلاً إلى الغرب من أورشليم شمالي طريق يافا.(2) اسم واد بالقرب من البلدة التي سبق ذكرها، وفي هذا الوادي هزم بنو إسرائيل بقيادة يشوع الأموريين (يش 10 : 12) والاسم الحديث لهذا الوادي هو ((وادي سليمان)).(3) اسم مكان في زبولون حيث دفن القاضي أيلون (قض 12 : 12) ولا يعرف موضع هذا المكان الآن على وجه التحقيق ويحتمل أنه مكان خربة اللون الحديثة أو يحتمل أن مكان تل البطمة.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(454, 'أيُّوب', 'اسم عبري. ولا يعرف معناه على وجه التحقيق، ويقول بعضهم أنه قريب من اللفظ العربي آيب فربنا يعني الراجع إلى الله أو التائب، ويقول آخرون أنه يعني المبتلي من الشيطان ومن أصدقائه ومن الكوارث التي حلّت به. ويقول هؤلاء أن الاسم في هذه الحالة مأخوذ من إيثاب أي ((المعادي)). وهي أحد رجال العهد القديم الأبرار وكان يقطن أرض عوص (أي 1 : 1) وأول من ذكره هو حزقيال (حز 14 : 14 و 16 و 20) وكان يعيش في بيئة شبيه ببيئة الآباء الأولين وفي ظروف مماثلة لظروفهم، وكان يقيم بالقرب من الصحراء في زمن كان يقوم فيه الكلدانيون بغزوات في الغرب (أي 1: 17). ولا يوجد مسوغ للشك في حقيقة الاختبارات العجيبة التي جاز فيها وقد ورد ذكرها في سفره. وقد أبرزت هذه الاختبارات مسألة من أهم المسائل وهي: لماذا يسمح الله بأن يتألم البار؟ ثم يسير السفر في معالجة هذه المشكلة في قصيدة شعرية فلسفية رائعة. وقد كتب سفر أيوب الذي يعتبر أحد أسفار الحكمة شعراً في الأصل. ويرسم لنا السفر صورة حّية قوية للآلام التي عانها أيوب والنقاش الذي دار بينه وبين أصحابه بشأن الأسباب التي لأجلها قاسى ما قاساه من ألم، وبشأن إيجاد حلّ لهذه المشكلة وتذكر المقدمة (ص 1: و 3: 2) ومقدمات الخطابات الأخرى وبخاصة خطاب اليهو (ص 32: 1-5) والخاتمة عظمة أيوب واتساع ثرائه في أوائل أيامه ثم في أواخر أيامه لما باركه الرب (أي 42: 7-17) وقد كتبت هذه الأجزاء التي ذكرناها، في الأصل نثراً أما مشكلة السفر التي أشير إليها أنفاً فهي:لماذا يتألم البار؟ والغرض الرئيسي هو دحض النظرية التي تقول أن الألم علامة على غضب الله وعدم رضاه، وأنه لا بدّ أنه صادر كنتيجة لخطيئة ارتكبها من يقاسي هذا الألم. ومن يدرس العهد القديم يلاحظ أن النجاح كثيراً ما يأتي نتيجة لحياة البر، وأن الشر نذير الفشل والخيبة (قارن خر 23: 20 وتث 28  ومز 37 و 63 واش 58: 7-13 وار 7: 5-7 و 17: 5-8 و 19-27 وص 31: 29 و 30 حز ص 18) ولذا فعندما يكون هناك استثناء لقانون الثواب والعقاب يصبح سبب حيرة عظيمة وارتياب بالغ، أما في حالة الأبرار فقد كان هناك اتجاه إلى البحث عن الخطيئة التي هي سبب ما يقاسون من ألم بما ان الألم ينتج عن الخطيئة لذا فكل ألم دليل على أنه كانت هناك خطيئة سببت هذا الألم. ومن الواضح أن هذا الاستنتاج مجانب للمنطق السليم. وأيوب في نقاشه لا يدعي أنه بريء كل البراءة من الخطيئة ولكنه يعتقد اعتقاداً راسخاً أن عقابه، إن كان هناك شيء موجب للعقاب، فإنه لا يتناسب في قسوته مع خطيئة. وتصور فاتحة الكتاب أيوب كرجل أصحاب نجاحاً كبيراً في حياته ويمتلك الكثير من القطعان والمواشي وله عدد كبير من الخدم وله أسرة كبيرة. وقد سمح للشيطان أن يختبر إيمان أيوب ففقد في الأول مقتنياته وحرم من أسرته ولما فشلت هذه الوسيلة في إخماد إيمان أيوب سمح للشيطان فيما بعد أن يصيب جسده بالألم ولكن إيمان أيوب ينتصر في النهاية ويعود إلى نجاح فاق نجاحه الأول ويمكن أن يقسّم السفر إلى:أولاً:مقدمة ص 1و 2.ثانياً:محاورات شعرية ص 3: 1-42 : 6 ويلي ذلك.(1) أيوب ينعي يوم مولده ويتمنى الموت ص 3.(2) ثلاثة سلاسل محاورات بين أيوب وأصحابه ص 4-27.(ا) السلسلة الأولى من خطابات أصحاب أيوب وجوابه لهم ص 4-14.1- خطابات اليفاز ص 4و 5.2- رد أيوب ص 6 و7.3- خطابات بلدد ص 8.4- رد أيوب ص 9 و 10.5- خطاب صوفر ص 11.6- رد أيوب ص 12-14.(ب) السلسلة الثانية من خطابات أصحاب أيوب وجوابه لهم ص 15-21.1- خطاب اليفاز ص 15.2- رد أيوب ص 16و 17.3- خطاب بلدد ص 18.4- رد أيوب ص 9.5- خطاب صوفر ص 20.6- رد أيوب ص 21.(ج) السلسلة الثانية من خطابات أصحاب أيوب وجوابه لهم ص 22-27.1-  خطاب اليفاز ص 22.2-  رد أيوب ص 23 و 24.3-  خطاب بلدد ص 25.4- رد أيوب ص 26 و 27.(3) قصيدة في الحكمة ص 28.(4) أيوب يراجع تاريخ حياته ص 29-31.(5) خطابات اليهو ص 32-37.(6) الرب يتكلم ص 38-41.(7) خضوع أيوب ص 42: 1-6.ثالثاً:خاتمة ص 42: 7-17.ويظهر من خلال المحاورات التي دارت بين أيوب وأصحابه أنه يشعر شعوراً قوياً باستقامته، ومع ذلك فإنه لا يستطيع أن يدرك سر اليد التي جاءت عليه بقوة وبقسوة. ويزداد التنازع الداخلي القلبي كلما ازداد اليأس من حالته الخارجية الظاهرة، ولكنه في كل هذه يبقى ثابتاً على عزمه راسخاً في اعتقاده أنه مهما يقع عليه من سؤ ومهما يصيبه من شر، فإنه سيبقى على ثقته بالله واتكاله عليه. ثم يرى بريقاً من النور عندما يجول بخاطره أنه في وقت ما ووفقاً لمسرة الله ورضاه سيظهر بر أيوب وتعلم براءته. وربما لا يحدث هذا في هذه الحياة ولكنه سيحدث يقيناً وأنه لا بد آت.وفي هذا اقتناع قوي بالخلود. عندما ينطق أيوب بهذا القول الرائع ((أما أنا فقد علمت أن ولييّ حيّ والآخر على الأرض يقوم وبعد أن يفني جلدي هذا وبدون جسدي أرى الله)) وبهذا يصل أيوب إلى الأساس الراسخ الذي لا يمكن أن يتزعزع عنه البتة (أي 19: 25 و26).ثم في النهاية يتقدم اليهو أحد أصحاب أيوب الذي كان صامتاً إلى الآن ويقدم أساساً آخراً، للحوار فبدلاً من أن نعتبر الألم كعقاب خطيئة يضع هو اعتباراً آخراً وهو أن الألم كثيراً ما يكون وسيلة إلى تشجيع أولاد الله وتنقيتهم وتطهيرهم وفي هذه الحالة لا يعبّر الألم عن غضب الله بله يكون كمجرد تأديب صادر من أب محبّ. وفي هذا يظهر اليهو وكأنه يمهد الطريق لمجيء الرب المخلص. ويظهر من أي 32-37 أن أيوب قبل هذا الرأي. عندئذ يتكلم الرب ويظهر لأيوب معرفة الإنسان ضئيلة قليلة لا تمكنه من أن يدرك كيف يفسر أسرار الله وأحكامه. فيتضع أيوب أمام الرب (أي 38: 1-42: 6). وفي الختام يرجع أيوب ثراؤه ونجاحه وتعود أسرة أيوب إلى ما كانت عليه من قبل (ص 42: 7-17).وقد قال بعض النقاد بأن أجزاء من سفر أيوب مثل المقدمة والخاتمة ترجع إلى مصادر قديمة. كما قال آخرون أن هناك أجزاء أدخلت إلى السفر بعد كتابته. ولكنَّ من يدرس السفر درساً مدققاً ويسير مع اتساق الحوار وتسلسله وسير الفكر المنطقي فيه يقتنع بوحدة السفر، وأن الاتجاه إلى قبول وحدته أقوى كثيراً من الاتجاه إلى تجزئته.أما المنظر الذي تمت فيه حوادث هذه القصيدة الرائعة فهو الهضبة الواقعة شرقي أو جنوبي شرقي فلسطين حيث تقع عوص وتيمان وشوة ونعمة. ويعتقد أن الكاتب كان من أهل فلسطين. ولا يمكن تعيين تاريخ كتابة السفر على وجه التحقيق. وقد ظن بعض النقاد أنه كتب في عصر ارميا. وظن آخرون أنه كتب بعد السبي بسبب الصراع الواضح فيه بشأن الثواب والعقاب. أما لغة السفر ففيها بعض من مؤثرات ارامية أو عربية آرامية، وربما تشير إلى تاريخ متأخر لكتابة السفر. ومع أن بعض العلماء يقولون أنه كتب في القرن الرابع قبل الميلاد تقريباً كما أن هناك علماء يعتقدون أنه بما أن البيئة والظروف التي يتحدث عنها السفر تشبه البيئة والظروف التي عاش فيها الآباء الأولون فلذلك يحتمل أنه يرجع إلى الألف الثانية قبل الميلاد.'),
(455, 'أَسْرِيِئيل', 'اسم عبري وربما كان معناه ((الله يجاهد))  وهو ابن جلعاد من سبط منسى كان رأس بيت الإسرائيليين(عدد 26: 31 ويش 17: 2و1 أخبار 7: 14) وفي هذا العدد الأخير ورد اسمه في الترجمة العربية إِشريئيل ولكنه في العبراني اسريئيل.'),
(456, 'أَسُّوس', 'وهي ميناء بحري في ميسيا في آسيا الصغرى تجاه جزيرة ميتيليني. وقد ترك الرسول بولس في رسالته الثالثة السفينة في ترواس ومشى إلى أسوس حيث أخذ السفينة مرة أخرى(أعمال 20: 3و4) ويسمّى مكانها اليوم في التركية  ((بهرمكوي))  وفيها خرائب كثيرة ترجع إلى أزمنة قديمة.'),
(457, 'أَسْفَاثَا', 'وهي كلمة فارسية معناها  ((عطية الفرس المقدس))  وهو ثالث أبناء هامان(اس 9: 7).'),
(458, 'إِسفِنْجَة', 'الاسفنج هيكل لين ليفي لحيوان بحري من المجموعة التي تسمى باللاتينية Cornacuspongiae ويوجد كثير من الاسفنج على الصخور قرب الشواطىء الشرقية للبحر الأبيض المتوسط. ومن أكثر أصناف الاسفنج المعروفة صنف يطلقون عليه في اللاتينية اسم Euspongia officinalis ويستخدم في غسل الجسم. وعندما كان المسيح على الصليب أخذ واحد من الواقفين هناك اسفنجة وملأها خلاًّ ووضعها على قصبة وأمسك بها لكي يسقي المسيح لتخفف حدَّة الشعور بالألم (مت 27: 48و مر15: 36 ويو 19: 29).'),
(459, 'أسْقُف', 'الكلمة العربية مقتبسة عن اللفظ اليوناني ((أبسكوبوس))  التي معناها ((مشرف))  . وهذه الكلمة اليونانية استعملت في الترجمة السبعينية عن العازر الكاهن (عدد 4: 16) وعن قواد الجيش(عدد 31: 14) وقد استعملت هذه الكلمة خمس مرات في العهد الجديد. ففي أعمال 20: 28 يدعو الرسول شيوخ(عدد 17) كنيسة أفسس ((أساقفة))  ويقول أن واجبهم أن يرعوا كنيسة الرب . وكان في كنيسة فيلبي عدد من الأساقفة(فيلبي 1: 1). وفي 1 تيمو 3: 2- 7 يعدد الرسول المؤهلات التي ينبغي أن تتوفر في الأسقف. أمّا الموظفون الآخرون الذين يذكرهم الرسول فهم الشمامسة(عدد 8- 13). وفي تيطس 1: 5- 9 يذكر الرسول مرة ثانية المؤهلات التي ينبغي أن تتوفر في الأساقفة (عدد 7) الذين يذكرهم أيضاً باسم الشيوخ (عدد 5) ويمكن أن تلخص هذه المؤهلات فيما يأتي:(1)  ينبغي أن يكون الأسقف زوج امرأة واحدة وله أولاد في الخضوع.(2)  وينبغي أن يكون طاهر الخلق كريماً جواداً.(3)  وينبغي أن يكون له صيت حسن لدى الذين هم في الخارج.(4)  وينبغي أن يكون صحيح الإيمان سليم المعتقد.(5)  ومن جهة المقدرة الشخصية ينبغي أن يكون كفؤاً، قادراً على أن يعلم الآخرين. وقد اكتمل نضوجه من حيث السن.ومن الواضح أن الرسول يتكلم عن نفس الوظيفة الواحدة، مرة باسم شيخ ومرة باسم أسقف. وفي 1 بط 2: 25 يدعى المسيح راعي النفوس وأسقفها.'),
(460, 'أُسْقُفِيَّة', 'هذه هي وظيفة الأسقف أو المشرف. وقد استعملت هذه الكلمة مرة واحدة في الكتاب المقدس باللغة العربية وذلك في 1 تيمو 3: 1 والأعداد التي تلي هذا العدد تصف الأمور التي يتطلب وجودها والمؤهلات التي يجب أن تتوفر في الأسقف . والكلمة اليونانية المستعملة هنا وردت أيضاً في أعمال 1: 20 وجاءت وصفاً لوظيفة الرسول التي كان يشغلها يهوذا الإسخريوطي وقد ترجمت في الكتاب المقدس باللغة العربية ((وظيفة)) .'),
(461, 'إِسْكَنْدَر', 'انظر  ((الاسكندر))'),
(462, 'أَسَل', 'الأسل نبات مائي. كحشائش المستنقعات ويرجح أنه هو نفس النبات المعروف باللاتينية باسمArundo donax. وكان يستعمل الأسل في صنع الحبال (أيوب 41: 2) أو في الوقود(أيوب 41: 20) في هامش الترجمة العربية للكتاب المقدس. وقد ورد الأسل في أش 9: 14 و19: 15 رمزاً للشعب الوضيع. ويشبه إحناء الرأس في العبادة بانحناء الأسلة أمام الريح أو تيار المياه(أش 58: 5).'),
(463, 'أَسْمَانْجُونِي', 'حجر كريم يشبه الياقوت، لونه أزرق ضارب إلى الحمرة. وهو الحجر الحادي عشر في أساسات أورشليم الجديدة(رؤ 21: 20).لَوْن أَسْمَانْجُونِي:وهو لون أزرق شديد الزرقة يشبه لون الياقوت الأزرق. وقد أمرت الشريعة الموسوية الإسرائيليين باستعمال أهداب اسمانجونية في أذيال ثيابهم(عدد 15: 38) وكانت الحلل الملكية تصنع من اللون الاسمانجوني والأرجواني (اس 8: 15) وكذلك حلل أصحاب المناصب الرفيعة(حز23 : 6) . وكانت الثياب الزرقاء والأرجوانية ضمن البضائع التي كانت تبيعها صور وتتجر فيها(حز 27: 24). وبعض الدروع التي يلبسها الفرسان المذكورين في رؤيا 9: 17 اسمانجونية تتناسب مع لون الكبريت المذكور في العدد نفسه.'),
(464, 'إِسْمَاعِيل', 'اسم عبري ومعناه ((يسمع الله))  وقد ورد اسماً لعدة أشخاص:(1)  اسماعيل بن ابراهيم من هاجر المصرية أمة سارة. وقد حثت سارة ابراهيم أن يأخذ أمتها زوجة لكي يعقب منها نسلاً لأن سارة كانت عاقراً(تك 16: 1- 4) وكان هذا النظام في الزواج معمولاً به في تلك الأزمنة. وقد دلت الاكتشافات على أنه كان موجوداً في  ((نوزي))  بالقرب من كركوك في العراق. أما هذا العمل من ناحية سارة فمعناه ضعف الإيمان بمواعيد الرب لإبراهيم وسارة بأن يكون لهما ابنٌ. وبعد أن حملت سارة نظرت إلى سيدتها باحتقار لأنها كانت عاقراً فطردتها سيدتها ، ولاقاها ملاك الرب في الطريق وأمرها أن ترجع إلى سيدتها وإلى بيت ابراهيم ، ووعدها بأنها ستلد ابناً تسميه اسماعيل وأنه يكون أباً لجمهور من الناس، وأنه سيسكن البرية كحمار وحشي(تك 16: 5- 14) وبعد أن رجعت هاجر ولدت اسماعيل لما كان ابراهيم ابن ست وثمانين سنة، وبعد أن كان له في أرض كنعان عشر سنين(تك 16: 3- 16).وقد ختن اسماعيل في الثالثة عشرة من عمره(تك 17: 25) وهي السن التي يختن فيها الأولاد العرب في الوقت الحاضر. وفي الوليمة التي أقيمت بمناسبة فطام اسحاق سخر اسماعيل من أخيه الصغير وكان اسماعيل حينئذ قد بلغ السادسة عشرة من عمره. فألحت سارة على ابراهيم أن يطرد هاجر وابنها فطردهما (تك 21: 8- 14). فتاهت الأم وابنها في برية بئر سبع في جنوب فلسطين وكانا على وشك الهلاك من الظمأ. فأَرى الله هاجر بئر ماء ووعدها ثانية بأن ابنها اسماعيل سيصير مصدر أمة عظيمة. ومنذ ذلك الحين سكن اسماعيل في برية فاران في جنوب فلسطين على حدود شبه جزيرة سيناء وأصبح ماهراً في استعمال القوس. وأخذت له أمه زوجة من بلادها، من مصر(تك 21: 15- 21) وولد له اثنا عشر ابناً الذين أصبحوا آباء القبائل العربية(انظر ((اسماعيليين))  ) وولد له أيضاً ابنة اسمها محلة (تك 28: 9) أو بسمة (تك 36: 3) وقد تزوجها عيسو. وقد اشترك اسماعيل مع اسحاق في دفن أبيهما ابراهيم في ممر بالقرب من حبرون(تك 25: 9). وقد مات اسماعيل بعد أن بلغ من العمر 137 سنة(تك 25: 17).ويستخدم الرسول بولس اسماعيل في العهد الجديد رمزاً لأولاد ابراهيم حسب الجسد وليس حسب الروح، وهم تحت عبودية الناموس وكانوا يضطهدون أبناء الموعد أي المسيحيين(غلا 4: 22- 31).ويقول العرب أنهم من نسل اسماعيل. والتقاليد العربية تقول أن الابن الذي أراد ابراهيم أن يقدمه ذبيحة هو اسماعيل لا اسحاق حسبما نعرف من الكتاب المقدس. وتنتقل هذه التقاليد بمشاهد حياة ابراهيم واسماعيل من جنوب فلسطين إلى مكة التي يقولون أن هذه الحوادث جرت فيها.(2)  اسماعيل بن نثنيا من الأسرة المالكة في يهوذا. فبعد خراب أورشليم في سنة 587 ق. م. جاء اسماعيل وبعض من ضباط جيش يهوذا المشتت وأعلنوا خضوعهم لجدليا، الحاكم اليهودي الذي عينه نبوخذ نصر ملك بابل حاكماً على أرض يهوذا. ولكن بعليس ملك عمون شجّع اسماعيل فقصد سراً أن يخرج الكلدانيين من بلاده، وربما كان يقصد أيضاً أن ينتزع العرش لنفسه. وكان اسماعيل وعشرة من رجاله في ضيافة جدليا في المصفاة فقتلوا جدليا وقتلوا جنوده من اليهود والكلدانيين. وحتى يبقى جرمهم سراً قتلوا سبعين حاجاً يهودياً كانوا مارين بالمصفاة في طريقهم إلى أورشليم وأسروا جميع سكان المصفاة ومن ضمنهم بنات الأسرة اليهودية المالكة، وكن من أقرباء اسماعيل. وانطلقوا في طريقهم إلى عمون فأدركهم يوحانان بن قاريح ورجاله وأنقذوا جميع من سباهم اسماعيل من المصفاة وهرب اسماعيل مع ثمانية من رجاله إلى عمون(2 ملو 25: 25 وأرميا 40: 7و41: 18).وكان من نتيجة اغتيال جدليا الحاكم الذي عينه البابليون على يد اسماعيل، أن اضطر جماعة من اليهود إلى الهرب إلى مصر خوفاً من انتقام البابليين(أرميا 42: 1- 43:7) وقد تركت جريمة اسماعيل أثراً لا يمحى في أذهان اليهود حتى أنهم أصبحوا بعد رجوعهم من السبي يصومون اليوم السابع من شهر تشرين كذكرى لذلك الحادث.(3)  رجل بنياميني يدعى اسماعيل بن أصيل (1 أخبار 8: 38و9: 44).(4)   أبو زيديا كان أحد قواد جيش يهوشافاط ملك يهوذا وقد جاء ذكره باسم ((يشمعيل))  وهو اللفظ العبري الذي يقابله في العربية لفظ  ((اسماعيل)) .(5)  اسماعيل بن يهوحانان وكان قائد مئة في جيش يهوذا(2 أخبار 23: 1) وقد ساعد يهوياداع الكاهن على التخلص من عثليا وتنصيب يوآش ملكاً على يهوذا.(6) اسماعيل بن فشحور وكان كاهناً  وكان أحد الذين اقنعهم عزرا أن يتركوا زوجاتهم الأجنبيات(عزرا 10: 22).'),
(465, 'أَمِتَّاي', 'اسم عبري معناه ((حقيقي)) وهو أبو يونان (2 مل 14: 25 ويون 1: 1).'),
(466, 'أَمِيْو', 'تستخدم كلمة أمير في الترجمة العربية للكتاب المقدس للدلالة على رئيس قبيلة أو قائدها، أو رئيس أسرة فمثلاً أطلق على أدوم (تك 36: 5-43) ويطلق اللقب أمير على قائد حربي في جيش إسرائيل (1 مل 9: 22) أو رومية (أعمال 21: 32) وقد ورد اسم أمير في زكريا 9: 7 ترجمة لكلمة عبرية يحسن أن تترجم ((أسرة)) أو ((عشيرة)).'),
(467, 'أَمْرَافَل', 'ملك شنعار الإقليم الواقع حول بابل، وقد أغار هو وثلاثة ملوك غيره معه على خمس مدن بالقرب من البحر الميت وأخذ لوطاً وأسرته أسرى. وقد استرد إبراهيم لوطاً وأسرته والغنائم التي أخذها امرافل وغيره من ملوك الشرق (تك 14: 1 و 9) وقد ذكر بعض العلماء بأنه هو نفس حامورابي صاحب القوانين المعروفة باسمه ولكن كثرة العلماء الآن تضع هذا الرأي موضع الشك.'),
(468, 'أِمْرِي', 'اسم عبري اختصار امريا وقد ورد:(1) اسم ابن باني من سبط يهوذا (1 أخبار 9: 4).(2) اسم أبي ذكور الذي بنى جزءاً من سور أورشليم في زمن نحميا (نحم 3: 2).'),
(469, 'أَمَرْيَا', 'اسم عبري معناه ((قال يهوه)) وقد ورد:(1) اسم كاهن هو ابن مرايوث (1 أخبار 6: 7).(2) اسم رئيس كهنة هو ابن عزريا (1 أخبار 6: 11 وعز 7: 3).(3) اسم رئيس بين الكهنة الذين رجعوا من بابل إلى أورشليم مع زر بابل ورئيس أحد بيوت الآباء (نحم 12: 2 و 7 و13).(4) اسم كاهن ختم العهد في زمن نحميا (نحم 10: 3).(5) اسم واحد ممن أقنعهم عزرا بترك زوجاتهم الغريبات (عز 10: 42).(6) اسم ابن شفطيا من سبط يهوذا (نحم (11: 4).(7) اسم ابن حزقيا وواحد من أسلاف النبي صفنيا (صف 1: 1).(8) كاهن رئيس في زمن يهوشافاط (2 أخبار 19: 11) وقد عينه الملك ليعطي رأياً بشأن شريعة الرب في المنازعات في الأمور القانونية.(9) اسم ابن حبرون من سبط لاوي (1 أخبار 23: 19 و 24: 23).(10) اسم لاوي عاون في توزيع التقدمات للكهنة في زمن حزقيا (2 أخبار 31: 14 و15).'),
(470, 'أَمْصِي', 'اسم عبري وريما كان اختصار ((امصيا)) وقد ورد:(1) اسم لاوي وهو ابن باني (1 أخبار 6: 46).(2) اسم ابن زكريا وكان كاهناً في زمن نحميا (نحم 11: 12).'),
(471, 'أَمَصْيَا', '1) اسم أحد ملوك يهوذا، وهو ابن يوآش واسم أمه مه ((يهوعدّان)) وقد تسلم مهام الحكم في الخامسة والعشرين من عمره بالنيابة عن أبيه الذي كان مريضاً. ثم اعتلى العرش بعد اغتيال أبيه، وقد اغتال من قتلوا أباه ولكنه عفا عن أولادهم وقد أستأجر 100000 (مائة ألف) من جنود إسرائيل ليذهبوا معه في حملة على آدوم ولكنه صرفهم بناء على أمر واحد من رجال الله، وأخذ معه قوات يهوذا فقط. وهزم الادوميين في وادي الملح وأخذ سالع عاصمتهم. ولكنه أحضر معه في عودته اوثان آدوم وأقامها آلهة له. أما الإسرائيليون الذين كان صرفهم فنهبوا مدن يهوذا الواقعة شمال بيت حورون. فحارب اميصا إسرائيل ولكنه انهزم في بيت شمس وأخذ أسيراً. وهدم يهوآش ملك إسرائيل جزءاً من سور أورشليم وأخذ الخزائن وبعض الرهائن معه إلى السامرة. ثم لما حدثت موآمرة  على امصيا في أورشليم هرب إلى لخيش وقتلوا امصيا هناك، وقد حكم مدة 29 تسعة وعشرين عاماً من حوالي 799-771 ق. م. (2 مل 14: 1-20 و 2 أخبار 25: 1-28).(2) اسم رجل من سبط شمعون (1 أخبار 4: 34).(3) اسم لاوي وهو ابن حلقيا (1 أخبار 6: 45).(4) اسم كاهن في بيت ايل وقد منع النبي عاموس من التكلم ضد يربعام بن يوآش ملك إسرائيل، وضد المقدس في بيت ايل. وقد تنبأ عاموس بأن امصيا سوف يؤخذ في السبي (عاموس 7: 10-17).'),
(472, 'أمْفِيبُولِيس', 'اسم يوناني معناه ((حول المدينة)) وكانت امفيبوليس مدينة بالقرب من نهر ستريمون، وقد سميت امفيبوليس لأنها كانت محاطة تقريباً بانحناء النهر. وكانت على الطريق المعروف بقيا اغناطيا Via Egnatia على مسافة 33 ميلاً جنوب غربي فيليبي. وقد مرّ فيها الرسول بولس وسيلا عندما سافر من فيلبي إلى تسالونيكية (أعمال 17: 1) وفي مكانها الآن قرية تسمى نيو خوريو.'),
(473, 'أُمّ', 'لم يطلق هذا اللفظ في العهد القديم على الامّ الحقيقية فحسب، بل كان يطلق أيضاً على الجدّة (1 مل 15: 10) وكذلك أطلق على الرابّة أو زوجة الأب (تك 37: 10) وعلى القائدة (قض 5: 7) وأم البلدان والشعوب (ار 50: 12 وحز 19: 2). وبما أن مقام المرأة في الهيئة الاجتماعية يتخذ دليلاً على تمدن الشعوب وانحطاطها فيحسن بنا أن تراجع ما ورد بشأن الأمهات من الآيات والنصوص. فقد ورد في أمثال 10: 1 و 15: 20 و 17: 25 و 29: 15 و31: 1 وكذلك في أسفار موسى الخمسة (خر 20: 12 وتث 5: 16 و 21: 18-21 ولا 19: 3) الكثير من الأمهات.وكانت أم الملك تكرّم وتحترم جداً (1 مل 2: 19) انظر ((ملكة)). وكانت مريم أمّ الرب يسوع المسيح المباركة مثالاً نبيلاً للأمومة في إيمانها (لو 1: 38) وفي محبتها لأبنها (لو 2: 48) وقد عهد المخلص وهو على الصليب بأمّه إلى الرسول يوحنا (يو 19: 26 و27). وقد أخذ تيموثاوس الإيمان ومعرفة الكتاب المقدس عن أمه افينكي التي نشأت بدورها في معرفة الرب على يدي أمها لوئيس (2 تيم 1: 5).'),
(474, 'أمَم', 'يطلق هذا الاسم في الكتاب المقدس على الشعوب غير العبرانيين (اش 49: 6 ورو 2: 14) وقد رأى أنبياء العهد القديم بأن المسيح سيكون نوراً للأمم (اش 2: 2-4 وعا 9: 12 وزكر 9: 7).وقد أعلن سمعان الشيخ أن يسوع المسيح يكون نور إعلان للأمم (لو 2: 32) وقد أعلن يسوع المسيح في متى 12: 12 بأن نبوة اشعيا (ص 42: 4) بأنه ((على اسمه يكون رجاء الأمم)) قد تمت فيه. وقد أشار المسيح في تعليمه إلى بعض ضعفات الأمم كاهتمامهم بالأمور المادية مثلاً (مت 6: 32) وإلى سيادة وتسلّط رؤساء الأمم عليهم (مت 20: 25) وفي إرساليته العظمى أمر المسيح رسله أن يتلمذوا جميع الأمم (مت 28: 19).وقد علم الله بطرس في رؤيا أن الأمم ليسوا بنجسين (أعمال 10: 9-16) وقد أرسل الروح القدس إلى قائد المئة الروماني كرنيليوس وأهل بيته بعد ما آمنوا ولذا فقد اعتمد هؤلاء الأمم (1 ع 10: 44-48) وقد وصلت الكنيسة في أورشليم إلى هذه النتيجة وهي أن الله قد وهب التوبة للأمم أيضاً (1 ع 11: 18) وقد قرر مجمع أورشليم أن الشريعة الموسوية غير ملزمة للمؤمنين من الأمم (أعمال ص 15).وقد دعا المخلص القائم من الأموات شاول الطرسوسي وأرسله ليبشر بالإنجيل وبخاصة بين الأمم (1 ع 26: 17 و 18) ويدعو بولس نفسه رسول الأمم (رو 11: 13) ويذّكر بولس الأمم بأنهم يقبلون الخلاص عن طريق شعب الله  (رو 11: 13-24).جزائر الأمم:\nيظهر أنه قصد بهذا الاسم كما ورد في تك 10: 5 وصف 2: 11 جزائر وسواحل الجزء الشمالي الشرقي من البحر الأبيض المتوسط حيث كانت هناك جماعات من اليونان.دار الأمم:\nانظر كلمة ((هيكل)).'),
(475, 'بئر آبَار', 'البئر حفرة في الأرض تصل إلى نبع مياه. وكان حفر الآبار أمراً ضرورياً في برية اليهودية، لجفاف جوها. وكانت أكثر هذه الآبار، إن لم تكن كله، تخص اناساً معلومين (عدد 21: 22) إلا أن بعضها كان قليل العمق يخرج منها في الحال ينبوع ماء غزير (انظر عين) وذلك نادر. وبعضها على عمق عظيم (يو 4: 11) وكان ينفق الاهلون في حفرها بالغ جسيمة.وكان بعض هذه الآبار ملكاً لأفراد، والبعض الآخر تملكه الجماعة. (تك 29: 2 و 3).وكانوا يردمونها إذا وقع خصام بين من حفروها وغيرهم (تك 26: 15) وكانوا يستقون الماء منها بواسطة دلو (يو 4: 11) وقد يستعملون بكرة لرفع الدلو (جا 12: 6) أو يستقون عن طريق الرافعة (الشادوف) كما يفعل بعض الإعراب إلى يومنا هذا.ومن الآبار المشهورة بئر بيت لحم (2صم 23: 15 و 1 أي 11: 17 و 18) وبءر عسق وسطنه ورحوبوت (تك 26: 20-22) التي حفرها اسحق في أرض جرار. وبئر هاجر (تك 21: 19) وبئر حاران (تك 29: 3 و 4) التي عندها قابل يعقوب رفقة لأول مرة. وبئر يعقوب (يو 4: 6) التي عندها قابل يسوع المرأة السامرية.وكان أكثر اليهود يجمعون مياه المطر أو غيرها في صهاريج أو أحواض، يُبَطِّنونها بخشب أو يملطون جوانبها بطين ثم يسقفونها ليجتمع فيها الماء ويبقى نظيفاً. وقد ينحتونها في الصخر بعناية عند سفوح التلال.وكانوا ستعملون هذه الصهاريج الفارغة التي يبقى في قاعها الطين اللزج، لحبس من يريدون حبسهم كما جرى مع يوسف (تك 37 : 24-29) ومع ارميا (ار 38: 6) وإلى ذلك أشار المرنم في الكلام عن جب الهلاك وطين الحمأة في مز 40: 2. وكان اولئك المنكودو الحظ يقاسون في تلك الورطة الوخيمة اشد العذاب.ولم تزل للان في أرض فلسطين آبار وصهاريج كبيرة وكثيرة العدد باقية من أيام القدم، أكثرها مشقة وغير نافعة، وإلى هذه يشير النبي بقوله، ((آباراً آباراً مشققة لا تضبط ماءً لا فائدة منها)) (ارميا 2: 13).وكان أهالي أورشليم يعتمدون الاعتماد كله على مياه تلك الصهاريج لجمع الماء أثناء فصل الشتاء، فكانت تكفي سكان المدينة في وقت الحصار مدة ليست بقليلة بعد انقطاع الماء الجاري \'ليها عن طريق القنوات. ولما كان الصخر تحت مدينة أورشليم كلسياً، كان من السهل على أهلها حفر الآبار. فكان لكل أسرة بئر أو اثنتان أو أكثر، خاصة بها. وهناك اقيسة أربعة منها مختصة ببيت واحد: (1) 15 قدماً طول في 8 عرض في 12 عمق (2) 8 4 15 (3) 10 10 15 (4) 30 30 20 .إذا قابلنا فلسطين مع غيرها من البلدان كالبلاد الأوربية مثلاً، نرى أن الأنهر والينابيع فيها قليلة جداً بالنسبة إلى أنهر وينابيع أوربا. فلا عجب إذا بذل سكانها الجد والاجتهاد والدرهم والدينار في حفر الآبار والصهاريج وبنائها لتقوم مقام الأنهر والينابيع. وكانوا يعتنون الاعتناء كله بنظافتها، فيبنون على أفواهها أرصفة من الحجارة النظيفة، ويغطونها بحجر كبير يمنع الأوساخ عنها. (تك 29: 2 و 3). وكانت تقام إلى جانب الآبار الحياض والمساقي والأجران لتشرب منها الماشية والقطعان. ولم تصنع هذه الآبار في المدينة فقط بل كان منها مئات وألوف في الحقول والطرقات لأجل فائدة المواشي والفعلة والحصادين والمسافرين.وأكثر هذه الآبار الموجودة في الأرض المقدسة الآن إنما هي من صنع الاقدمين كما تشاهد ذلك في الأطلال القديمة والطرق المهجورة كالطريق التي بين أريحا وبيت لحم، وعدة أمكان أخرى. ومركز هذه الآبار على الغالب في الأودية والأماكن المنخفضة حيث تنحدر إليها مياه الأمطار بسهولة، أو تترشح بين طبقات الأرض وتتجمع فيها.ولقلة الماء في فلسطين، كانت هذه الآبار والصهاريج عند الأهل أفضل من جميع مقتنياتهم. فكثيراً ما كانت تقع بينهم الخصومات من أجلها حتى كان يؤول ذلك أحياناً، إلى حروب هائلة بين القبائل المتخاصمة عليها. انظر كلمة ((ماء)).وكانت موارد الماء هذه، أمكنة تجمع الناس ومواقع مشهورة كما نقول اليوم ((ساحة المدينة)) وهذا يوضح القول ((وعلق داود ركاب وبعنة أخاه، ابنى رمون اللذين قتلا ايشبوشث على بركة حبرون)) (2 صم 4: 12).'),
(476, 'بئر', 'اسم عبري وقد جاء:(1) اسم المحطة في موآب حيث أعطى الله الماء لبني إسرائيل من بئر حفرها رؤساء الشعب (عد 21: 16-18).(2) مكان بين أورشليم وشكيم (قض 9: 21) هرب إليه يوثام من وجه أخيه ابيمالك بعد أن ألقى أمثولته عن العوسج. وربما كان هذا المكان هو نفس بئيروث.'),
(477, 'بئر ايليم', 'اسم عبري ومعناه ((بئر البطم)) وهو موضع في موآب (1 شع 15: 8) يرجح أنها المكان المشار إليه في بئر (1) وربما كان مكانه اليوم هو ((المدينة)) في واد ثمد.'),
(478, 'بئر سَبْع', 'كلمة عبرية معناها ((بئر السبع)) أو ((بئر القَسَم)) دعيت هكذا بسبب إعطاء إبراهيم سبع نعاج لابيمالك شهادة على حفره إياها (تك 21: 31). وهناك مكث إبراهيم مدّة طويلة (تك 21: 33). ومن بعده رجع اسحاق إلى نفس الموضع وجدّد البئر (تك 26: 25) وأطلق اسم البئر على المدينة التي نشأت حولها (تك 26: 33). وهي تبعد عم حبرون نحو ثمانية وعشرين ميلاً إلى الجهة الجنوبية.ولما كانت هذه المدينة على الحد الجنوبي من أرض كنعان، ودان على الشمالي منها، شاع عنده القول ((من دان إلى بئر السبع))، ويريدون به طوال البلاد (قض 20: 1) وكذلك قولهم من ((بئر سبع إلى جبل افرايم)) يريدون به طول مملكة يهوذا (2 أي 19: 4).وكانت بئر سبع في نصيب يهوذا ولو أنها أعطيت عند التقسيم ضمن نصيب سبط شمعون (يش 15: 28 و 19: 2) ومن الذين سكنوا فيها أبناء صموئيل النبي (1 صم 8: 2) ثم صارت أخيراً، كما يخبرنا الكتاب، مركزاً لعبادة الأصنام (عا 5: 5 و 8:14) وقد شاهد بعض السيّاح في خراباتها عدة آبار قديمة العهد جداً، عمق البعض منها نحو خمسين قدماً. وبالقرب من هذه الآبار أحواض تملأ عند الحاجة فتستقي منها الغنم والبقر والجمال. وهذا مما يدلنا على أن تلك الهضاب المجاورة للمدينة كانت مرعى للمواشي.'),
(479, 'بئر لَحَى رؤي', 'وهذه عبارة معناها ((بئر الحي الذي يراني)) عين ماء بين قادش وبارَد (تك 16: 14 و 24: 62 و 25: 11) في الطريق من أشور إلى مصر اتجهت هاجر المصرية عند هربها من سيدتها. ويدل تك 25: 11 على أن هذه البئر لا تبعد كثيراً عن جيرار. ويقول رولاند أنه وجد البئر عند عين مُوَيْلح 50 ميلاً جنوب بئر سبع ونحو 11 ميلاً غرب عين قادش.'),
(480, 'أبار بَني يَعْقان', '(تث 10: 6) نفس المكان في عدد 33: 31 باسم ((بني يعقان)) وهو على حدود ادوم. ربما تكون هذه ((البيرين)) نحو ستة أميال جنوبي العوجة.'),
(481, 'بئر يعقوب', 'بئر في قطعة الأرض التي ابتاعها يعقوب ونصب فيها خيمته (تك 33: 19) وهي البئر التي جلس يسوع المسيح بجانبها عندما تكلم مع المرأة السامرية (يو 4: 5 و 6) وهي في فم الوادي بقرب شكيم (قابل تك 33: 19 ويش 24: 32). وهنا أعلن الرب للمرأة حقيقة عبادة الله بالروح والحق. وهي على بعد ميل ونصف إلى الجنوب الشرقي من نابلس عند سفح جبل جرزيم (جبل الطور) بقرب الدرب الموصل من أورشليم إلى الجليل.ويحيط بالبئر حائط قديم وطول الفسحة المحيطة بها 192 قدماً وعرضها 151 قدماً. وفي هذه الفسحة أثار. وعمق البئر نحو 75 قدماً وقطرها نحو سبعة أقدام. ويظن العلماء أن عمقها كان نحو 150 قدماً وأنها قد ارتفعت بسبب سقوط الحجارة فيها. وكانت كنيسة مبنية فوقها في القرن الرابع بعد الميلاد.'),
(482, 'بئيرَا', 'اسم عبري معناه ((بئر)) رجل من سبط اشير (1 اي 7: 37).'),
(483, 'بئيرة', 'اسم عبري معناه ((بئر)) رئيس سبط راؤبين سباه تِلْعْث فلاسر (1 اي 5: 6).'),
(484, 'بئيروت', 'كلمة عبرية معناها ((أبار)) وهي مدينة جبعونية في نصيب سبط بنيامين (يش 9: 17) وهي مبنية في سفح الأكمة التي كانت جبعون مبنية عليها وتبعد 9 أميال إلى الشمال من أورشليم وتدعى الآن ((البيرة)).'),
(485, 'بئيروتيون', 'أهل بئيروت (2صم 4: 3) وكان منهم الاثنان اللذان قتلا ايشبوشث. وكذلك كانا حاملا سلاح يوآب من نفس المكان (2 صم 4: 2 و 23: 37) وقد رجع رجال من هذه المدينة من السبي (عزرا 2: 25).'),
(486, 'بئِيِرِي', 'اسم عبري معناه ((بئريّ)) وقد ورد:(1) اسم رجل حثي. وهو ايد يهوديت إحدى نساء عيسو (تك 26: 34). وقد ورد الاسم في ترجمة فانديك العبرية بصورة ((بيري)).(2) اسم ابي النبي هوشع (هو 1: 1).'),
(487, 'باباي', 'اسم اكادي معناه ((طفل)) هو جُدّ بعض المسبين الذين رجعوا مع زربابل (عز 2: 11 ونح 7: 16) وقد رجع مع عزرا أيضاً نحو ثمانية وعشرين من بني باباي (عز 8: 11) وصعد منهم أربعة إلى أورشليم في أيام عزرا بسبب اتخاذ النساء الأجنبيات (عز 10: 9 و 28).'),
(488, 'بابل', 'ما يقصد بها في الكتاب المقدس:(1) المدينة (1ش 13: 19 و 21: 9 و 48: 20) انظر ((مدينة بابل)).(2) أهل المدينة وولاية بابل (حز 23: 15 و 17) انظر ((بابل)).(3) ولاية بابل نفسها (2مل 24: 1 و 25: 27 مز 137: 1). انظر مدينة ((بابل))(4) بابل التي ورد ذكرها في رؤ 14: 8 و 16: 19 و 17: 5 و 18: 2 و 21 اسم رمزي يشير إلى روما. فقد اسهبت روما بابل في بذخها وفي امتداد إمبراطوريتها وفي زناها وفي اضطهادها لشعب الله وقد قصد بذلك النطق بالدينونة وإيقاع القضاة على روما تحت اسم مستعار هو ((بابل)).(5) ويرّجح أن بابل التي كتب منها الرسول بطرس رسالته الأولى (1 بط 5: 12) هي روما. وقد ظن بعضهم أنها بابل الواقعة على نهر الفرات. أو أنها بابليون الواقعة في مصر القديمة. ولكن لا يوجد لدينا دليل قاطع على أن بطرس زار هذين الموضعين.\nمدينة بابل:1) الاسم:جاء اسم بابل من لفظ ((باب ايلو)) من اللغة الاكدية ومعناه ((باب الله)) ونفس اللفظ ترجمة الكلمة السومرية ((كادنجرا)) وتظهر أهمية بابل في العصور القديمة من ورود ذكرها في الكتاب المقدس أكثر من مائتي مرة. وهو اسم العاصمة العظيمة لمملكة بابل القديمة ((شنعار)) المذكورة في تك 10: 10 و 14: 1 والأسماء الأخرى التي أطلقت على المدينة كثيرة، منها ((تندير)) مركز الحياة و ((ايريدوكي)) المدينة الطيبة آي الفردوس، إذ كان البابليون يعتقدون أن جنة عدن في بقعتها و ((سو-انا)) اليد العالية، ويظن أن المعنى ((ذات الأسوار العالية)).2) تاريخ تأسيسها:جاء في تك 10: 9 أن مؤسس ((بابلي)) هو ((نمرود))على أن البابليين ينسبونها إلى ((مردوخ)) إلههم الأكبر وقد بناها مع ((ارك واكد وكلنة)) وهيكلها الشهير. ولا يعرف بالضبط تاريخ تأسيسها ولكنه من المؤكد أنه يرجع إلى الأزمنة البدائية (يذكر رجال الحفريات أن الطبقة السفلى لبابل ترجع إلى نحو 40000 سنة ق. م.).وصار إلههم الأعظم ((مردوخ)) رأس مجموعة الآلهة البابلية. بسبب نفوذ بابل كعاصمة، إذ كانت محجّ عبادته وبسبب مركز برج بابل العظيم الذي كانوا يتناقلون عنه أموراً عديدة (انظر ((برج بابل)) ).وقد بلغت بابل ذروة مجدها في القرن الثامن عشر قبل الميلاد في عصر حامورابي المشترع العظيم من الأسرة البابيلة الأولى، وكذلك في القرن السادس قبل الميلاد في عصر الملك نبوخذ نصر في الأسرة الكلدانية.3) وصف أسوارها وبوّاباتها:وصف ((هيرودتس)) المدينة فبيّن أنها تقع في سهل عظيم خصيب مربع الشكل ضلعه نحو 14 ميلاً فيكون محيطها نحو 56 ميلاً ومساحتها نحو 196 ميلاً مربعاً.وبما أن هذه المساحة هائلة وبما أن أثار الأسوار لم يعثر عليها في بعض الأماكن فقد تطرق الشك إلى هذا التقدير.ومما ذكره ((هيرودتس)) أن المدينة كانت محاطة بخندق عميق عريض مملوء بالماء. ووراء ذلك سور علوه نحو 300 قدم وعرضه نحو 87 قدماً. ومع أن بابل بقيت محجراً يقتلع منه البناؤون مواد البناء لمدة ألفي سنة، إلا أنه لا زال من الغريب أن تختفي كمية الطوب الهائلة هذه بدون أن يترك أي أثر يدل عليها.4) مركزها وأقسامها وشوارعها وهيكلها:كانت المدينة مبنية على جانبي نهر الفرات. وفي نقطة تقابل السور بالنهر كان ينثني مسافة على الجانبين ليكون حاجزاً تعلوه الاستحكامات. وكانت بيوتها ترتفع إلى دورين أو ثلاثة أو أربعة. وشوارعها كانت مستقيمة ويظهر أنها كانت تتقاطع في زوايا قائمة كشوارع يصل إلى النهر بوابة نحاسية كبيرة تحميه. وفي داخل السور الخارجي بنوا سوراً آخر لا يقل عنه قوة إلا أنه يحصر مساحة أقل. وكانت الأرض بين السورين تستخدم كمزرعة للفواكه أو الخضراوات أو كانت تستخدم كمنتزهات. وكل قسم من أقسام المدينة كان يحوي بنائين عظيمين: احدهما قصر الملك تحيط به استحكامات قوية والآخر هيكل ((بيل)) وهو بناء له بوابات نحاسية ويمتد فرسخين لكل جانب (الفرسخ 8/1 ميل أي نحو 220 ياردة).داخل هذا المزار المقدس برج صلد في مربع ضلعه نحو فرسخ تعلوه أبراج مدرّجة تصل إلى ثمانية. وحول الأبراج مصاعد من التراب في نقطة الوسط منها مسطح يرتاح فيه الصاعد. وعلى أعلى برج منها خلوة (حجرة) كبيرة فيها مقعد وطاولة مذهبة. ليس فيها تمثال ولا يبيت فيها أحد، إلا امرأة من الشعب تختارها الآلهة. وفي خلوة أخرى سفلية تجد تمثالاً ذهبياً ((لبيل)) جالساً وكان كرسيه وموطئ قدميه أيضاً من الذهب وبقربه طاولة مذهّبة. وكان وزن المعدن الثمين في كل هذه نحو 800 وزنة. إن إصلاحات ومنشآت نبوخذ نصر الذي كان يتمشى على سطح قصره وانتفخ بالكبرياء فقال ((أليست هي بابل العظيمة التي بنيتها)) (دانيال 4: 30)هذه الإصلاحات كانت حقاً ضخمة وبديعة. والسجل الذي تركه هذا الملك يشير إلىهذه العظمة, كما ويثبتها ما كتبه المؤرخون اليونان بعد ذلك.9) القوَّة الحربية:كانت هذه المملكة مؤلفة من أمم وشعوب مختلفة من ساميين وطورانيين وكوشيين وغيرهم. أما أصحاب السلطة فكانوا الساميين. واشتهر شعب هذه المملكة بشدة اليأس والإقدام. وفي أيام نبوخذ نصر هاجموا جميع البلدان الواقعة مابين دجلة والنيل. وكان صدى صليل سيوفهم يملأ أعدائهم رعباً. وأما أصوات مركباتهم فكانت كرعد قاصف (1ر 4: 29وحز 26: 10).وقلما حاقت بهم هزيمة أو أخفقوا في غزوة من غزواتهم. واشتهرت فرسانهم بالبسالة والنجدة. وقد جاء في النبي حبقوق 1: 8 شيء من وصف سرعة خيولهم وحدتها. وامتازت جنودهم بجودة رمي السهام وطعن الرماح وضرب السيوف - فحيثما توجهوا توجه معهم الظفر, وحيثما ساروا, سار معهم الرعب الذي امتلأت منه قلوب أعدائهم. وهابتهم من أجله الأمم المجاورة, ولاسيما الشعب اليهودي الذي كان يرى شرب كأس الحمام أهون من ملاقاة تلك الجيوش الجرارة. فكانوا يمثلون بالقتلى ويرتكبون في معاملة المسبيين والأسرى ما لم يسبقهم أحد\\ إلى شرّ منه أو إلى مثله سوى الأشوريين.10) صناعة بابل:واشتهرت هذه الأمة بجودة الصناعة, كحفر الحجارة الثمينة ورسم الصور على الصخور والآجر (حز 23: 14). وقد وجد في آثارهم آنية من زجاج وخزف على هيئات مختلفة بالغة الجمال. وأما منسوجات بابل فكانت على غاية الإتقان منذ القدم ومما يثبت ذلك ذكر رداء شنعاري نفيس في سفر يشوع 7: 21 وقد أتقنوا صنع الأقمشة حتى اشتهرت بضاعتهمهذه عند الرومانيين الذين كانوا يتفاخرون بها ويشترونها بأثمان غالية. قيل أنه كان معلّقاً في مقصورة الإمبراطور نيرون نسيج بابلي موشّى بصور تيلغ قيمته 32300 جنيه انكليزي. وكاموا, فضلاً عن أحكام نسجها, يلونونها بألوان غاية في الحسن والإبداع. ويرسمون عليها الأصداف والحيوانات من مفترسة وغير مفترسة. وبالاختصار فقد كانت أقمشتهم فائقة الحسن والجمال, فكانت الأمم المعاصرة لهم ترغب فيها كل الرغبة كما هو الحال في أيامنا الحاضرة بالنظر إلى جودة الأقمشة الشرقية كالبسط الكردية والفارسية, فإن منها ما لا تقلّ قيمته عمّا كانت تصنعه في بابل. ولا عجب إذا حذا أهل المشرق في هذه الصناعة حذو أسلافهم البابليين, وانتقل إليهم ما كان فيهم من الدراية والحذق والمهارة.11) لباس أهلها:وأما لباس الطبقات العليا فكان قميصاً من الكتان إلى القدمين, وفوقه حلة من الصوف - وكانت أحذيتهم خفّا نعله من الخشب. وأما شعورهم فكانوا يلفونها, وبعد دهنها بجميع أنواع الطيب والعطر, بعمامة بيضاء. وأما العامة فكان لباسهم رداء واحداً فقط.12) علومهم:ومن جملة العلوم التي امتاز أهل هذه المملكة علم الفلك ولهم فيه أبحاث دقيقة. فإنهم كانوا يعينون أوقات الخسوف والكسوف قبل حلولها. وقد وصف ((هيرودس)) خمس كسوفات ذكرها أولئك الفلكيون القدماء. ومما يستحق الاعتبار في هذا الفن معرفتهم السيارات الخمس, ووضعهم جدولاً للثوابت (النجوم) وتعيينهم الأبراج, حتى أنهم توصلوا إلى تحقيق طول السنة الشمسية, واخترعوا المزولة أيضاً. ولا ينكر عليهم إلاّ خلطهم علم الهيئة (الفلك) بعلم التنجيم. فكان علماؤهم عرّافين ومنجمين وسحرة معاً. وكان البابليون يكتبون الخط المسماري علو لوحات فخارية, وكانت كتابتهم تشمل أناشيد ينية وشرائع وبعض القصص عن الخليقة أو الطوفان مثلاً, ولذلك شملت كتاباتهم شيئاً من التاريخ ومن علوم الفلك والتنجيم وشملت بعض الخطابات والرسائل والوثائق التجارية.13) ثروتهم وأخلاقهم:كانت بابل, فضلاً عن شهرتها العلمية, مدينة ذات تجارة واسعة براً وبحراً. فكان تجارها يجلبون من المدن المجاورة لهم, الذهب والعاج والقرمز واللؤلؤ, وهذا ما كانوا يجلبونه من خليج العجم. وهكذا كثرت ثروتهم وزاد مجدهم وغناهم, فصارت نساؤهم تتزين بجميع أنواع الحلي والمجوهرات, وعاشوا بالتنعم والترفه.على أن كثرة تنعمهم عادت عليهم بالدمار والخراب. وحطّ التأنق في المعيشة من منزلتهم مادياً وأدبياً. فصارت بناتهم نحيفات ضعيفات البنية. وأخذ الجهل من البابليين كل مأخذ فأدمنوا شرب المسكرات فزادوا تعجرفاً وتكبراً, وكثر الفجور بين سكان هذه المدينة حتى كانت العذارى تباع في الأسواق, والنساء يرتكبن الفحشاء, خالعات العذار, سدّاً لعوزهن, بعد أن كنَّ على أعظم جانب من الغنى واليسر. وكن يستعملن من ضروب الحيل وأنواع التدليس ما يفوق الوصف لإغراء الرجال, وإيقاعهم في اشراكهن إرواء لميولهن الفاسدة.14) آلهة بابل واضمحلال مملكتها:كان حكام هذه الدولة من النوع المطلق. وديانتها وثنية كما سبق, ومن آلهتهم بيل- ونبو- ومردوخ. وصنعوا لها أصناماً عديدة وشادوا لها هياكل على غاية من الفخامة كهيكل بيل وبرج نمرود وغيرهما من الهياكل المزخرفة التي أغاظوا بها الله تعالى. فغضب عليهم وسلّمهم إلى أيدي غيرهم من الأمم الذين تغلبوا عليهم ودمّروا بلادهم وسبوا عيالهم.وحالتها الحاضرة مطابقة لما قاله الله بفم أنبيائه القديسين, إذ يقول: ((1 مر على مياهها فتنشف أنهرها فيدخلها العدو على حين غفلة)). وقد تم كلامه تعالى في بابل العظيمة حين دخلها كورش الفارسي في سنة 539 ق.م. وذكر هيرودتس أن العدو دخل المدينة وأكثر أهلها غافلون فلم يشعروا بالخطر الآتي عليهم, فنهب الأعداء جميع أمتعتهم وأموالهم إذ لم تكن لهم فرصة أن يخفوا منها شيئاً. فجاء ذلك طبقاً لما قال ارميا النبي (50: 37) وأرسل سيفاً على خزائنها فتنهب. وقد وردت نبوات أخرى بالقضاء على بابل مثل 1 ش 13 و14: 1-23 و21: 1-10 و46: 1 و2 و47: 1- 3 وارميا ص 50 وص 51 ورؤ 18: 10 انظر أيضاً تحت كلمات ((كلديا)) و ((نبو)) و ((نبوخذ نصر)).'),
(489, 'برج بابل', '(تك 11: 4: 4-9) بعد نهاية الطوفان شرع نسل نوح في بناء برج بابل في سهل شنعار بغية أن يجمعهم مكان واحد من الأرض فلا يتبددون على وجه البسيطة الواسعة. وكان في قصدهم جعل العالم كله مملكة واحدة عاصمتها هذا المكان الذي اختاروه في أرض شنعار وسمي بابل. وليقيموا لأنفسهم اسماً ومجداً دلالة على كبريائهم وتشامخ نفوسهم (تك 11: 4).ولعدم توفر الحجر استعملوا اللبن أي صبُّوا الطين في قوالب وأحرقوا القوالب حتى لا تتأثر بالماء. واستعملوا الحمر بدل الطين والحمر هو المزيج اللزج الذي كان يكثر في بعض هذه البقاع بسبب وجود البترول. والحمر هو القار أو المادة الإسفلتية وعندما تيبس تثبت القوالب.إلا أن الرب لم يكن في قصده تجمّع الناس بعد الطوفان بل انتشارهم لتعمير الأرض. ثم لم يكن من صالح الناس أن يلجأوا إلى طرقهم وكبريائهم في تحدّي الرب. فبلبل الرب ألسنتهم، فكفوا عن العمل وتفرقوا فعمّروا الأرض وصارت البقعة اسمها بابل من الفعل ((بلبل)) العربي، والعبري القريب منه ((بلل)) وبسبب هذا التشتت والطقس والتربة واختلاف طرق المعيشة نشأت أجناس الناس وتكونّت لغاتهم المختلفة.ويظن أن ((اي-تيمن-انا-كي)) التي معناها ((بيت الأساس الصخري للسماء والأرض)) ويتركب من ثماني مصاطب يعلوها هيكل بيل الذي أعاد ترميمه نبوخذ نصر وهو هذا البرج المعنيّ، انظر ((بابل)) بند 4 و 9.سَبي بابل:انظر ((سبي)).ولاية بابل:هي البلاد الواقعة بين نهري الفرات والدجلة وعاصمتها بابل (دا 2: 49 و 3: 1 و 12 و 30) وكانت تعرف قديماً بأرض شنعار (تك 10: 10) انظر بابل.'),
(490, 'بابليُّون', 'أهل بابل.'),
(491, 'باتَرَا', 'مرفأ جميل في ليكية قرب مصب نهر اكسانثوس تجاه جزيرة رودس. وكانت مدينة ذات شأن في القديم. وقد سمّاها بطليموس فيلادلفس ((ارسنوي)) باسم امرأته. وهي المدينة التي منها ذهب الرسول بولس إلى فينيقية في سفره من فيلبي إلى أورشليم (1ع 21: 1). وكان بها هيكل مشهور لأبولو-إله الوحي عند اليونان.'),
(492, 'بَارَابَاس', 'اسم آرامي معناه ((ابن الاب)) وهو رجل اشتهر بسفك الدماء وفعل المنكرات. ولما كان اليهود يحاكمون مخلصنا يسوع كان باراباس هذا  ملقى في السجن لعلة قتل وتحريك فتنة بين الشعب. وكان من عادة الحكومة الرومانية أن تطلق لليهود أسيراً كل سنة في عيد الفصح من أرادوا. فبلغ من انحطاط تلك الأمة في ذلك الحين أنهم طلبوا من الحاكم الروماني إطلاق باراباس المجرم وتسليم مسيحها إلى الموت على الصلب (مت 27: 16-26).'),
(493, 'بَارَاق', 'سم عبري معناه  ((برق)) (قض 4: 6 و 5: 1 و 12 عب 11: 32) هو ابن ابينوعم من قادش نفتالي، دعته دبّورة النبية فخلّص بني إسرائيل من يد يابين ملك كنعان. جمع عشرة ألاف من رجال نفتالي وزوبولون وبهم انتصر على سيسرا قائد جيش يابين في يزرعيل.'),
(494, 'بَارْتِيمَاوُس', 'اسم آرامي معناه ((ابن تيماوس)) انسان أعمى شفاه مخلصنا يسوع بقرب أريحا (مر 10: 46).'),
(495, 'بَارَد', 'اسم عبري معناه ((برد)) مكان في جنوب فلسطين قرب بئر لحي رئي (تك 16: 14) وقد ظن البعض أنه جبل ام البارد بقرب عين قديس.'),
(496, 'بَارْ سَابَا أو بَرْ سَابَا', 'اسم آرامي ومعناه ((ابن سابا)) ويظن البعض أن معناه قد يكون ((ابن السبت)) أي ولد في سبت. ويوجد شخصان يعرفان بهذا الاسم. الأول يوسف بار سابا الملقب يوستس وهو الذي ألقى الرسل القرعة بينه وبين متياس عندما أرادوا تعين تلميذ بدلاً من يهوذا الاسخريوطي الذي اسلم ربه (1ع 1: 23) والثاني يهوذا بار سابا رجل اشتهر بالتقوى بين الأخوة ورافق بولس وبرنابا في سفرهما من أورشليم إلى إنطاكية لإبلاغ قرارات المجمع الأول (1ع 15: 22).'),
(497, 'بَارَع', 'يرجح انه اسم كنعاني ربما كان معناه ((عطية)) أو ربما كان معناه ((صاعداً)) أو ((فائقاً)) انظر الكلمة العبرية ((بارع)) وهو اسم ملك سدوم (تك 14: 2) وقد هزمه كدر لعومر.'),
(498, 'بَارُوخ', '(1) اسم عبري معناه ((مبارك)) وكان كاتباً محباً ومخلصاً للنبي ارميا (ار 32: 12). حيث سلمه صك الحقل الذي اشتراه.وفي ار 36 استدعاء ارميا النبي فكتب كلام الله الذي تنبأ به ارميا في درج وقرأه على مسامع الشعب في بيت الرب ثم قرأه بعد ذلك في أذان رؤساء اليهود فاضطربوا اضطراباً عظيماً. وأشار بعضهم على باروخ أن يذهب ويختبئ هو وارميا من وجه الملك يهوقايم، لأن هذا الملك احتدم غيظاً ومزق السفر وألقاه في النار عند استماعه جزءاً صغيراً منه. ثم بعد ذلك أوحى إلى ارميا أن يكتب السفر ثانيةً فاحضر صديقه باروخ وأملى عليه ما كان مكتوباً في السفر السابق وبعض الزيادة أيضاً.ومن أعمال باروخ التي تستحق الذكر، ذهابه إلى بابل حاملاً رسالة من النبي ارميا تنبئ بما كان مزمعاً أن يحلّ بتلك المدينة العظيمة من القصاص الإلهي والعقوبة. وما لبث أن يرجع ارميا إلى أورشليم حتى القي الحصار على المدينة وسجنا كلاهما. فلما فتحت المدينة أُخرجا من السجن وكان باروخ من جملة من أخذوا إلى مصر (ار 43: 1-7). أما عن السفر المسمى باسمه ((سفر باروخ)) فانظر ما بعده.(2) باروخ بن زباي (نح 3: 20) رمم قسماً ثانياً من سور أورشليم. ويرّجح أنه كان ضمن الذين ختموا الميثاق (نح 10: 6).(3) ابن كلحوزة من يهوذا من عشيرة الشيلوني (نح 11: 5).سفر باروخ:احد أسفار الابوكريفة ويزعم السفر أن باروخ كاتب ارميا كتب الاصحاحات الخمسة الأولى منه. ويشمل هذا السفر مقدمة ص 1: 1-14 وثلاثة أقسام:(1) اعتراف إسرائيل بخطيئة وصلاته طالباً الغفران ص 1: 15-3: 8 ويظهر من دراسة هذا القسم أنه كتب أولاً باللغة العبرية أما أقدم المخطوطات التي لدينا الآن فهي في اللغة اليونانية. ويحتمل أن تاريخ هذا القسم يرجع إلى القرن الثاني قبل الميلاد إلا أن البعض يظنون أنه يرجع إلى العصر الفارسي.(2) حثّ على الرجوع إلى نبع الحكمة ص 3: 9-ص 4: 4.(3) تشجيع ووعد بالنجاة ص 4: 5-ص5: 9.وهناك اختلاف في الرأي من جهة هذين القسمين إذا ما كانا كتبا أصلاً باللغة العبرانية أم اللغة اليونانية. ويشبه الإصحاح الخامس، الإصحاح الحادي عشر من ((قصائد سليمان)) أحد الأسفار غير القانونية، وربما يرجع تاريخه إلى القرن الأول قبل الميلاد. وقد اتفق غالبية العلماء على أن باروخ لم يكتب هذا السفر والإصحاح السادس من هذا السفر كما ورد في ترجمة الفلجات والترجمة اليسوعية يكوّن سفراً قائماً بنفسه باسم رسالة ارميا كما وردت في الترجمة السبعينية. انظر ((رسالة ارميا)) و ((الابوكريفا)).'),
(499, 'بَارِيج', 'اسم عبري ومعناه ((هارب أو شارد)) وهو اسم رجل هو ابن شمعيا من ذرية داود (1 اي 3: 22).'),
(500, 'بَارْيَشُوع', 'اسم ارامي معناه ((ابن يشوع)) وهو نبي كذاب يعرف بعليم الساحر (اع 13: 6 و 8). وكان مع الوالي سرجيوس بولس في بافوس وهي مدينة في جزيرة قبرص. وقد قاوم بولس وبرنابا طالباً أن يفسد الوالي عن الإيمان احتفاظاً بسلطان سحره، فوبخه بولس، ففي الحال سقط عليه ضباب وظلمة وكان أعمى إلى حين. انظر ((سرجيوس بولس)).'),
(501, 'بَاز أو البازي', 'طائر من كواسر الطير ومن فصيلة الصقر والشاهين وهو نوع يكثر في لبنان وفلسطين. وكان طائراً مقدساً عند المصرين القدماء، حتى كان قتله ولو سهواً يعدّ عندهم من أعظم الجرائم. وأما اليهود فيعدونه من جملة الطيور النجسة حسب ما جاء في الشريعة (لا 11: 16 وتث 14: 15) وقد ورد الاسم العبري لهذا الطائر في سفر أيوب 39: 26. وجاء في ترجمة فانديك العربية ذكره باسم العقاب. ويرجح أن الاسم العبري يشمل الطيور التي تسمى في اللاتينية Accipitev nisus و .Falco tinnuculus'),
(502, 'بَازَق', 'اسم عبري معناه ((بذر البذار)) وهي:(1) مدينة كانت في نصيب يهوذا (قض 1: 4) وحدثت عندها موقعة ظفر فيها شعب الرب بالكنعانيين وأسروا ملكهم (انظر ((ادوني بازق)) ) ويظن البعض أنها خربة بزقة بقرب جازر أو أنها نفس المدينة التالية.(2) مقاطعة أو موضع (1 صم 11: 8 و 9) فيه عد شاول جنوده قبلما أنقذ يابيش جلعاد وظن بعضهم أنها بقرب وأن اسمها الحالي إِبرق.'),
(503, 'بَاسِمَة', 'اسم عبري ومعناه ((رائحة ذكية)) وهو اسم ابنة سليمان وامرأة اخميعص أحد قواده (1 مل 4: 15). وقد ورد هذا الاسم والاسم ((بسمة)) بصورة واحدة في اللغة العبرية.'),
(504, 'بَاشَان', 'اسم عبري ومعناه ((أرض مستوية أو ممهدة)) وهي مقاطعة في أرض كنعان واقعة شرقي الأردن بين جبلي حرمون وجلعاد (عد 21: 33). وسميت باشان من جبل في تلك البلاد (مز 68: 15). وكانت باشان تشمل حوران والجولان واللجاه، وكلها مؤلفة من صخور وأتربة بركانية. وتربتها خصبة للغاية وماؤها غزير. ويزرع فيها الحنطة والشعير والسمسم والذرة والعدس والكرسنة. ويحدها شمالاً أراضي دمشق، وشرقاً بادية سورية، وجنوباً أرض جلعاد، وغرباً غور الأردن. ويخترق جانبها الشرقي جبل الدروز وهو جبل باشان القديم. ويمر بالجولان سلسلة تلال من الشمال إلى الجنوب هي براكين قديمة خامدة. أما مقاطعة اللجاه فهي حقل من ((اللافا)) أي الصخر البركاني قد انسكبت من تل سيحان وهو فم بركان قديم بقرب شحبه. وذكرت باشان نحو ستين مرة في الكتاب المقدس.وكان سكان باشان القدماء من الرفائيين (تك 14: 5 وتث 3: 11) وأصابت الهزيمة عوجاً ملكها، فقتله الإسرائيليون (عد 21: 33 و 32: 33) واقتسموا أرضه. وقد اشتهرت بمراعيها ومواشيها وأشجارها (تث 32: 14 ومز 22: 12 واش 2: 13 وار 50: 19 وحز 39: 18) وفيها من الآثار ما يؤيد صدق الكتاب المقدس (تث 3: 3-13 ويش 13: 30).ومن أبنيتها أربعة أنواع (1) مغاور للسكن (2) مناجم تحت الأرض يبلغ طولها 150 قدماً وتتفرع منها أزقة سراديب بشكل تحت الأرض، بجانبها بيوت تنفتح كواها في سقفها. (3) بيوت منقورة في الصخر (4) بيوت معمرة من حجارة منحوتة وأبوابها وكواها من الحجر أيضاً.'),
(505, 'بَاشَان حَوُّوتِ يَائِير', 'اسم عبري معناه ((مخيمات يائير أو قرى يائير في باشان)) وهو اسم أقليم ارجوب أو تراخونيتس في باشان وهي اللجاه (تث 3: 14) ويدعى أيضاً حوّوث يائير (عد 32: 41).'),
(506, 'بَاشِق', '(اطلب ب ش ق).'),
(507, 'بَاصِر أو بَاصَر', 'سم عبري معناه  ((حصن)) وهو: (1) اسم رجل هو ابن موفح من سبط اشير (2) اسم مدينة في نصيب رأوبين. وأعطيت للاويين وكانت من مدن الملجأ. وتقع شرقي الأردن (تث 4: 43 ويش 20: 8 و21: 36 و1 اي 6: 78). ثم أخذها الموآبيون وحصنها ميشع ملك موآب (انظر حجر موآب سطر 27) ويظن أنها ام العمد التي تقع على بعد خمسة أميال ونصف شرقي حشبون، وثمانية أميال ونصف شمالي شرقي مأدبا.'),
(508, 'بَاطِح', 'اسم عبري معناه ((بطن أو وادي)) وهو اسم مدينة في نصيب اشير (يش 19: 25) وقد ذكر المؤرخ يوسابيوس أنها نفس بلدة بيت بطن الواقعة على مسافة سبعة أميال ونصف شرقي عكا.'),
(509, 'باطن', 'اسم عربي معناه ((بطن أو وادي)) وهو اسم مدينة في نصيب اشير(يش 19: 25) وقد ذكر المؤرخ يوسابيوس انها نفس بلدة بيت بطن الواقعة على مسافة سبعة اميال ونصف شرقي عكا.'),
(510, 'بَافُوس', '(1ع 13: 6) ميناء على التخم الغربي من جزيرة قبرص كانت في أيام الرومانيين، انيين، وكان هناك هيكل للاله افروديت في بافوس القديمة وتدعى الآن بافو، حيثما قبل سرجيوس بولس بشرى الخلاص وضرب الله عليم الساحر بالعمى.'),
(511, 'بَاكَر', 'اسم عبري معناه ((البكر آي الأول والمبكر)) وردت هذه اللفظة في (تك 46: 21 و 1 اي 7: 6و 8 اسماً لأحد أولاد بنيامين وفي عدد 26: 35 اسماً لأحد بني افرايم وهذا الأخير يعرف أيضاً باسم برد (1 اي 7: 20).'),
(512, 'البَاكَرِيُّون', 'عشيرة باكر من نسل افرايم (عدد 26: 35).'),
(513, 'بَالاَّق', 'اسم موآبي ربما كان معناه ((المتلف أو المخرب)) وهو ملك الموآبيين وقد طلب من بلعام أن يلعن بني إسرائيل في أيام موسى (عد 22: 2 و 4 يش 24: 9 وميخا 6: 5) انظر ((بلعام)).'),
(514, 'بَالَة', 'اسم عبري معناه ((سيدة)) وربما كان اختصار ((بعلة)).'),
(515, 'بَالَع', 'اسم عبري معناه ((البلع)) أو ((الفلك)) وهو اسم:(1) مدينة من مدن الدائرة الخمسة (تك 14: 2 و 8) كانت مبنية على الشاطئ الشرقي من البحر الميت على طريق مصر. (انظر ((صوغر)) ).(2) ملك ادوم وهو ابن بعور (تك 36: 32).(3) ابن بنيامين الأكبر (عد 26: 38).(4) رأوبيني وهو ابن عزرا (1 أخبار 5: 8).'),
(516, 'البَالعِيُّون', 'هم نسل بالع (عد 26: 38).'),
(517, 'بَاموُت', 'وهو نفس ((باموت بعل)) (عد 21: 19).'),
(518, 'بَامُوت بَعل', 'اسم عبري معناه ((مرتفعات البعل)) وهو مكان يقع شمالي ارنون ويحتمل أنه أحد الأماكن التي ضرب فيها بنوا إسرائيل خيامهم، وإليه أخذ بالاق بلعام، ومنه تظهر سهولة مآب (عد 21: 19 و 22: 41) وقد ورد ذكره في حجر موآب باسم ((بيت باموت)) (انظر سطر 27) وكان يقع في حدود رأوبين. وقد ورد ذكره في يش 13: 17 بين ديبون وبيت بعل ماعون ويحتمل أن مكانه اليوم هو ((خربة القويقية)) على بعد ميلين ونصف جنوبي جبل نبو.'),
(519, 'بَانِي', 'اسم عبري معناه ((بناء)) وربما كان اختصار ((بنايا)) وقد ورد:(1) اسم رجل من نسل جاد وكان أحد أبطال داود (2 صم 23: 36).(2) اسم رجل من نسل يهوذا (1 أخبار 9: 4).(3) اسم مؤسس أسرة عاد بعض أفرادها من السبي من زر بابل (عز 2: 10) وقد تزوج بعض منهم بنساء أجنبيات (عز 10: 29) وقد ختم ممثلو الأسرة العهد (نحم 10: 14) ويدعى هذا الشخص في نحميا 7: 15 باسم ((بنوي)).(4) اسم لاوي من عشيرة مراري ( 1 أخبار 6: 46).(5) اسم لاوي كان يعيش قبل الرجوع من السبي وكان أحد أبناء اساف ومن عشيرة جرشوم (نحم 11: 22).(6) اسم لاوي وهو أبو رجوم (نحم 3: 17) وربما كان هو الذي اشترك بنصيب وافر في عيد المظال في زمن عزرا (نحم 8: 7 و 9 : 4 و 5) وختم العهد نيابة عن أسرته (نحم 10: 13).(7) اسم لاوي (نحم 9: 4).(8) مؤسس بيت أو عشيرة وجاء من نسله شخص يدعى باني أيضاً (عز 10: 34 و 38).'),
(520, 'ببغاء', 'هذا هو الاسم الذي ورد لهذا الطائر في الترجمات العربية التي بين أيدينا حالياً ترجمة لكلمة عبرية لفظها ((انفاه)) (لا 11: 19 وتث 14: 18).ولكن الاسم العبري لا يعني ببغاء البتة بل هو اسم لطائر يمثّل فصيلة من الطيور واسمه باللاتينية Ardeidae وتشمل هذه الفصيلة مالك الحزين، و ((ايبس)) أو أبو منجل والكركي وللقلق وغيرها. وهي عادة طيور كبيرة الحجم ذات منقار طويل وأرجل طويلة عارية، وهي بطيئة في طيرانها وتعيش على الأسماك والزواحف وكانت تعتبر نجسة في الشريعة الموسوية، وتتوالد هذه الطيور كلها وتكثر عند بحيرة الحولة وتعيش مع الماشية في المراعي القريبة من البحيرة.'),
(521, 'بَتْرُوبَاس', 'اسم يوناني وهو اختصار بتروبياس ومعناه ((حياة أبيه)) وهو رجل مسيحي من مدينة رومية، أرسل إليه بولس تحيته (رو 16: 14) وقد ورد في بعض التقاليد أنه كان واحداً من السبعين تلميذاً. ثم صار فيما بعد اسقفاً على بوطيولي حيث استشهد، ويقيم له أهل مدينته عيداً سنوياً.'),
(522, 'بَتُوئِيل', 'اسم عبري ربما كان معناه ((بيت الله)) وقد ورد:(1) اسم ابن ناحور أخي إبراهيم وأبي لابان ورفقة (تك 22: 22 و 23 و 24: 15 و24 و 27 و 28: 2 ((انظر لابان)) ).(2) اسم موضع كان في نصيب شمعون (1 أخبار 4: 30) ويسمى أيضاً ((بيت ايل)) (يش 12: 16) وبتول (يش 19: 4) وكسيل (يش 15: 30). وقد أرسل داود هدية لهذه المدينة (1 صم 30: 27).'),
(523, 'بَتُول', 'انظر ((بْتوئيل)).'),
(524, 'بَتُولِمَايِس', 'مدينة أطلق عليها هذا الاسم احد البطالسة، وهي عكَا الحديثة شمالي حيفا على ساحل البحر المتوسط وكانت قديماً تدعى ((عكَو)) (قض 1: 31) وقد سلم الرسول بولس على الأخوة في بتولمايس وهو في طريقه إلى أورشليم لآخر مرة وبقي معهم يوماً هناك (1 ع 21: 7) (انظر ((عكَو)) ).'),
(525, 'بَثّ', '(انظر ((مكاييل)) ).'),
(526, 'بَثّ رَبِّيم', 'اسم عبري معناه ((ابنة كثيرين)) ويرجح أنه كان باب حبشون. كانت بقربه البرك التي تكلّم عنها سليمان في نشيد الانشاد 7: 4.'),
(527, 'بُثُور', 'قروح أو دمامل ظهرت في أجسام الناس والحيوانات في أرض مصر من رماد ذرّاه موسى أمام فرعون (خر 9: 9) فحملته الرياح وبثته في جميع أنحاء البلاد فكان إذا سقط شيء منه على إنسان أو حيوان أصيب بالدمامل وما ذلك إلا ليظهر لهم تعالى أن جورهم على بني إسرائيل جلب عليهم هذه الضربة الهائلة. فأذاقتهم اشدّ العذاب جزاءً لهم على معاملتهم السيئة لشعب الله. وربما يشار إلى مثل ذلك بقرحة مصر(تث 28: 27).'),
(528, 'بَثْشَبَع', 'اسم عبري معناه ((ابنة القسم)) أو ((ابنة اليوم السابع)) ابنة اليعام وامرأة اوريا الحثي (2صم 11) شغف بها داود الملك واحتال على زوجها فقُتل، فتزوجها وجعل ابنها سليمان وريث ملكه (1 مل 1: 11-53).'),
(529, 'بَثْشُوع', 'تحريف بثشبع (1 أخبار 3: 5).'),
(530, 'بِثَينة', 'اسم عبري معناه ((ابنة يهوه)) أو ((عابدة يهوه)) وهي ابنة فرعون وامرأة مرد رجل من يهوذا (1 أخبار 4: 18) ويرّجح أنها دخلت في عبادة يهوه من الأديان.'),
(531, 'بِثِينِيئة', '(1ع 16: 7) مقاطعة في الشمال الغربي من آسيا الصغرى. يحدها شرقاً يافلاغونيا اغونيا وشمالاً البحر الأسود وجنوباً فريجة وغلاطية وغرباً بحر مرمرة. حاول بولس وسيلا دخولها فمنعهما الروح. ولكن الإنجيل دخلها عن طرق أخرى. ولما كتب بطرس رسالته الأولى كان أهل بثينية ضمن من وجهها إليهم (1 بط 1: 1) وقد اشتهر أهلها بالقوى والثبات كما يشهد بذلك التاريخ الكنسي للقرن الأول من التاريخ المسيحي. وكما تشهد بذلك أيضاً الرسالة التي أرسلها بليني الأصغر إلى الإمبراطور تراجان في طليعة القرن الثاني وذكر فيها كيف اعتنق الدين المسيحي كثيرون فسجن منهم من سجن وعذّب من عذّب، فارتد البعض وقدّموا خمراً ولباناً لتماثيل الالهة وسجدوا لصور الإمبراطور. إلا أن ذلك لم يجدِ في تخويف الاخرين بل إنهم يكثرون. ويطلب الأذن باستعمال طرق أشدّ.وبعد ذلك عقد مجمعان في أثنين من مدنها، نيقية 325 ب.م. وخالقيدونية 451 ب.م. وكانا من أكبر المجامع المسيحية.'),
(532, 'بَجَع', 'طائر جميل يغب في المياه وهو نجس حسب الشريعة اليهودية (لا \n11: 18 وتث 14: 16). والاسم في الأصل العبري ربما يعني ((فرخة الماء)) (Porphyrio \ncaeuleus) أو نوعاً من البوم.'),
(533, 'بَحْر', 'أطلقت هذه الكلمة في الكتاب المقدس على الأنهر والبحيرات \nوحيثما اجتمعت المياه بكثرة (1ش 21: 22 وار 51: 36) منها:(1) الاقيانوس (تك 1: 2 و10 تث 30: 13).(2) البحر المتوسط، فيسمّى البحر الغربي (تث 11: 24) وبحر \nفلسطين (خر 23: 31) والبحر الكبير (عد 34: 6 و 7) وحديثاً البحر الأبيض المتوسط.(3) البحر الأحمر وهو بحر سوف (خر 15: 4).(4) بحر الملح أو بحر العربة (تث 3: 17).(5) لنهر كبير كالنيل (1ش 19: 5) والفرات (ار 51: 36).(6) لمستنقع (حز 32: 2) وكان العبرانيون يريدون بهّا الجهة \nالغربية لوقوع بحر الروم غربي اليهودية.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(534, 'اَلبَحْر اْلأحْمَر', 'وهو بحر يفصل آسيا عن أفريقيا وكان العبرانيون مدة إقامتهم \nفي مصر يسّمونه البحر (خر 14: 2 و 9 و 16 و 21 و 28 و 15: 1 و 4 و 8 و 10 و 19 يش \n24: 6 و 7) وسمّي أيضاً بحر مصر (1ش 11: 15) وبحر سوف ربما اتخذ هذا الاسم من \nالنبات الذي يكثر على شواطئه (خر 10: 19 و 13: 18 و 15: 4 و 22 و23: 31).وأطلق اليونانيون اسم البحر الأحمر على هذا البحر والخليج \nالعجمي. وظنّ بأن اسم البحر الأحمر أخذ من المرجان الأحمر النامي فيه. وقد سمّاه \nالمصريون بحر بنط آي بحر العربية ويسميه العرب بحر الحجاز.وطول البحر الأحمر نحو 1490 ميلاً، يوصله بالمحيط الهندي مضيق \nباب المندب وعرضه 18 ميلاً فقط.ومعدّل عرض البحر الأحمر 150 ميلاً ويضيق إلى جهتي \nطرفيه. وفي الشمال ينقسم إلى خليجين، خليج العقبة إلى الشرق، وخليج السويس إلى \nالغرب. ويبعد طرف الأخير عن البحر الأبيض المتوسط نحو 70 ميلاً. وتنحصر شبه جزيرة \nسيناء بين الخليجين. ويختلف عمق البحر الأحمر بين 400 و600 قامة. وعلى شواطئه ألياف \nمن المرجان وجزائر عديدة تجعل الملاحة فيه عسيرة ولاسيما في أجزائه الضيقة.وطول خليج السويس نحو 180 ميلاً ومعدّل عرضه 30 ميلاً. وقد \nأوصلت ترعة قديمة هذا الخليج بالنيل حفرها بعض الفراعنة، فكانت تمر بها المراكب في \nالقرن الرابع عشر قبل المسيح وكان طولها 62 ميلاً. وأعاد حفرها العرب مرتين وفي سنة \n1869 تّم حفر قناة السويس فاتصل البحر الأحمر بالبحر الأبيض.أما خليج العقبة فطوله نحو 100 ميل ومعدّل عرضه 15 ميلاً \nوعليه ميناء ايلات وكيناء عيصون جابر.والبلدان المهمة على \nشاطئ البحر الأحمر الإفريقي هي: السويس، وسواكن، ومصوّع، وأما على شاطئه الآسيوي \nفالبلدان قليلة وأهمها جدّة.والحادثة المهمة المقرونة \nبالبحر الأحمر هي مرور بني إسرائيل فيه وغرق المصريين (خر 14 و15) وكثيراً ما تشير \nالكتب المقدّسة إلى هذه الحادثة (عد 33: 8 وتث 11: 4 ويش 2: 10 و2 صم 22: 16 ونح 9: \n9-11 ومز 66: 6 واش 10: 26 واع 7: 39 و1 كو 10: 1 و2 عب 11: 29 الخ).وقد اختلفت الآراء في مكان عبور بني إسرائيل والترجيح أنهم \nعبروا خليج السويس بالقرب من طرفه الشمالي. وقد اثبت العلامة روبنسون أن ريحاً \nشرقية شمالية تهب على هذا الجزء تكفي لطرد الماء من بعض الأماكن. وعلى كل حال \nتغيّرت المعالم في العصور الغابرة بحيث يتعسّر معرفة الموضع بالضبط.وبعد مرورهم ارتحلوا على شاطئ خليج السويس (عد 33: 10) ومن \nالبحر الأحمر آتى الجراد (خر 10: 12-19) والسلوى (عد11: 13). وقد داروا حول خليج \nالعقبة ليطوفوا بأرض ادوم. وفي ملك سليمان بني مراكب في عيصون جابر وأيلة عند رأس \nخليج العقبة (1 مل 9: 26 و 10: 22 و 2 اي 8: 17 و 18).'),
(535, 'بَحْر طَبَرِيَّة', 'اطلب ((طبرية)).'),
(536, 'اَلْبَحْر الْكَبِير', '(عد 34: 6) أو بحر فلسطين. هو مجتمع عظيم من الماء يتوسط \nبين الثلاث القارّات رّات آسيا وأوربا وأفريقيا، ولذلك دعي، البحر الأبيض المتوسط، \nطوله من الشرق إلى الغرب نحو 2000 ميل وعرضه من 400 إلى 800 ميل وعلى منتصف شاطئه \nالشرقي موقع أرض كنعان. ويشير غليه الكتاب المقدس باسم البحر (عدد 13: 29 و1ع 10: \n6) أو البحر العظيم (يش 23: 4) أو البحر الغربي (تثنية 11: 24) وكان الفينيقيون في \nأزمنة العهد القديم يلمون بمعرفة هذا البحر وبالملاحة فيه من شواطئ سوريا على مضيق \nجبل طارق أو أعمدة هرقل.'),
(537, 'بَحْرِ كنَّارَة وَ كِنَّرُوت', '(عد 34: 11 وتث 3: 17 ويش 12: 3) (اطلب ((بحر الجليل)) في \n((جليل)) ).'),
(538, 'بَحْر الْمِلْح', '(البحر الميت) ويدعى بحر العربة (تث 3: 17 و 4: 49 و 2مل 14: 25) وبحر الملح (تث 3: 17 ويش 3: 16 و 12: 3) والبحر الشرقي (حز 47: 18 ويؤ 2: 20 وزك 14: 8) والبحر (حز 47: 8) وعمق السّديْم (تك 14: 3) وبحر سدوم وبحر الملح \n(في التلمود) والبحيرة الزفتية والبحيرة السدومية (في يوسيفوس). ولم يرد اسم البحر \nالميت في مؤلفاته العبرية وإنما اخترع الاسم المؤلفون اليونانيون. ويسمى بحر \nلوط.يبعد بحر الملح 16 ميلاً عن أورشليم شرقاً ويرى جلياً \nمن جبل الزيتون وهو في أعمق جزء من الغور الممتد من خليج العقبة إلى الحولة. وطوله \n46 ميلاً وأقصى عرضه عشرة أميال ونصف الميل. مساحته 300 ميل مربع تقريباً وإن كان \nذلك يتغير حسب فصول السنة وارتفاع الماء. وهيئته مستطيلة تحيط به جبال تعلو عنه نحو \n1500 قدم، وتنحدر في أكثر الأماكن إلى شاطئه غير أنها تبتعد عنه قليلاً في قسمه \nالجنوبي عند جبل اصدم.ويخترق جزئه الجنوبي الشرقي اللسان، \nوهو سهل طوله عشرة أميال وعرضه نحو 5 أميال وسطحه مؤلف من طباشير طيني مغطى بملح \nوفيه خرابات برج صغير وصهريج وبعض الأعمدة وبقايا خزف غير أنه لم يتحقق تاريخ شيء \nمن ذلك.قعر هذا البحر مفلطح تقريباً ومؤلف من طين أزرق \nورمل ممزوجين ببلورات ملح. وأقصى عمقه 1310 أقدام وجنوبي اللسان 11 قدماً إلا أن \nالمشاهد أن هذا العمق يزيد سنوياً فإن جزءاً كان عمقه 3 أقدام صار نحو 9 في 25 \nسنة.وينقذف زفت من قعر طرف هذا البحر الجنوبي عند حدوث \nالزلازل. ويصب في البحر الميت الأردن، والزرقاء معين، ونهر الموجب، وعدة أودية أخرى \nتجري مياهها في الشتاء فقط كوادي كرك ووادي نميرة ووادي سديّة ووادي زويرة ووادي \nغوير ووادي النار.أما ماؤه فلونه صاف وينصب منه من نهر \nالأردن نحو ستة ملايين طن ماء كل يوم، ويتبخّر كله إذ لا مخرج لهذه البحيرة ويحتوي \nهذا الماء على 25 في المئة من المادة الجامدة نصفها ملح اعتيادي ومن جملتها كلوريد \nالمغنيسيوم الذي يكسبه الطعم المرّ، وكلوريد الكالسيوم الذي يكسبه خاصته اللزجة \nالزيتية. وفيه كمية كبيرة من البروم وقليل من العناصر الأخرى. ويختلف ثقله النوعي \nمن 021 و 1 إلى 256 و 1 ومقدار المادة الجامدة فيه نحو ثمانية أضعاف ما في ماء \nالبحار. وإذا أصاب شيء من هذا الماء الثياب أو الأيادي أو الوجه، ترك غطاء من الملح \nعليها. وإذا أصاب اللسان احسّ بلذع وحرارة لشدة ملوحته ولا يغرق الإنسان في هذا \nالماء لكثافته. وزد عليه فانه لا يعيش فيه شيء من النبات أو الحيوان. فإذا دفعت \nعليه مياه الأنهر بشيء من ذلك مات وانقذف إلى الشاطئ. هذا وإن كان قد روى بعض \nالروّاد أن نباتات قليلة تنمو على أفواه الوديان العديدة بعد فصل الشتاء وقال بعضهم \nأن أنواعاً معدودة من الأسماك تعيش فيه إلاّ أن المعروف عنه أن لا أحياء تعيش \nفيه.وبجانب هذا البحر ينابيع ساخنة من جملتها عين غوير \nدرجة حرارة مائها في شهر يناير 596 ف. واقليم البحر الميت حارّ جداً وشواطئه قاحلة \nوفي بعض الأماكن محاطة بجذوع الأشجار وفروعها المحمولة إليه من الأنهر التي تصب فيه \nوخشبها متشبع بالملح بحيث لا يكاد يشتعل. وفي بعض المواضع ينمو القصب بجانبه كما هي \nالحال بقرب عين الفشخة.ذكر هذا البحر أولاً في الكتاب \nالمقدس في تك 14: 3 وظن الاكثرون أن الموضع المشار إليه هناك هو الجزء الواقع جنوبي \nاللسان. وظن غيرهم أنه القسم من الغور الواقع جنوبي اريحا.أما مدن الدائرة (تك 19: 25) فكانت قريبة من البحر. وظن \nالكثيرون أنها تغطّت بالمياه بعد هدمها. (ويذكر حزقيال أن من علامات الحياة في \nملكوت الله الجديدة شفاء مياه البحر الميت وتكاثر أنواع الأسماك فيه بحيث (حز 47: \n6-12).لفائف البحر الميت أو ادراج البحر الميت.في عام 1947اكتشف بالقرب من خربة قمران في الشمال الغربي من \nالبحر الميت احدا عشر أو اثنا عشر درجاً ترجع إلى القرن الأول قبل الميلاد والقرن \nالأول الميلادي، وهذه اللفائف أو الأدراج على أعظم جانب من الأهمية لدارسي الكتاب \nالمقدس. ومن ذلك الحين اكتشف في تلك البقعة والبقاع القريبة منها ألاف القطع من \nمئات المخطوطات. ومن ضمن هذه اللفائف التي وجدت أجزاء من كل أسفار العهد القديم ما \nعدا سفر استير وكذلك وجدت أجزاء من كتب الابوكريفا. وقد وجد سفر اشعياء بكامله \nتقريباً وبعض التفسيرات على سفر حبقوق مثلاً. كذلك وجد كتاب يتكلم عن نهج ونظام \nالجماعة التي تسكن هذه البقعة وهي جماعة الاسينيين، وبعض الأناشيد الدينية والأشعار \nالتعبدية. وما اكتشف من العهد القديم يعتبر أقدم ما وصلنا من أجزاء العهد \nالقديم.وبعض هذه القطع المخطوطة يؤيد نصّ النسخة العبرية الماسورية أو المسلّمة، \nوالبعض الآخر يؤيد نصّ الترجمة اليونانية أو السبعينية. ولكن الكثير من هذه الأجزاء \nمن اللفائف أهمية لدارسي العهد الجديد في أنها توضح البيئة والأفكار التي كانت \nسائدة قبل أزمنة العهد الجديد وبخاصة روح التطلع وتوقع مجيء المسيا المنتظرة مما \nمهد الطريق في القلوب لمجيء المسيح وقبوله.'),
(539, 'الْبَحْر الْمْسبُوك', 'و حوض كبير صنعه سليمان من نحاس قد أخذه داود غنيمة (1 \nأخبار 18: 8) وجعله وجعله سليمان لخدمة الهيكل. وكان موضعه في الدار الداخلية بين \nمذبح المحرقة والقدس جهة الجنوب قليلاً. وقد وضع ليغسل فيه الكهنة أيديهم وأقدامهم \nقبل دخول القدس أو التقدم على المذبح (1 مل 7: 39 و 2 أخبار 4: 6) وكان مستدير \nالشكل وكان طول قطره 15 قدماً ومحيطه 45 قدماً وعلوه سبعة أقدام ونصف وكان يسع 2000 \nألفي بث (1 مل 7: 23 و 26 قارنه مع 2 أخبار 4: 5). وكانت حافته مقوسة إلى الخارج \nكحافة الكأس وكانت الجوانب مزينة بصفين من القثاء وقد نصب اثني عشر ثوراً وقد \nاستخدم في الأول الجبعونيون ليملأوه.ثم بعد ذلك كان الماء يجلب إليه في قناة من برك \nسليمان. وقد أنزل احاز البحر عن الثيران وجعله على رصيف من حجارة (2 مل 16: 17) \nولما فتح نبوخذ نصر أورشليم كسّر هذا الحوض (2 مل 25: 113 وار 27: 19-22) ونقله \nالكلدانيون إلى بابل.'),
(540, 'بَحْرُومِي', 'أحد أهل بحوريم (1 أخبار 11: 33).'),
(541, 'بَحْر يَعرْير', '(ار 48: 32) انظر ((يعزير)).'),
(542, 'بَحُورِيم', 'اسم عبري معناه ((الشباب)) وهو اسم قرية بالقرب من جبل \nالزيتون تقع على الطريق من أورشليم إلى الردن (2 صم 16: 5) وقد ورد ذكرها مراراً في \nتاريخ حياة داود. وإليها رجع زوج ميكال عندما أحضروها لداود، وعندها سب شمعي داود \nأثناء هروبه من وجه اباشلوم أبنه الثائر عليه، وفيها اختبأ يوناثان واخميعص (2 صم \n3: 16 و16: 5 و17: 18) انظر ((اخميعص)) ومكانها الآن ((رأس التيم)).'),
(543, 'بُحِيْرَة', 'طلق اسم بحيرة في لو 5: 1 و 2 و 8: 22 و23 و33 على بحيرة \nجنيسارت أو بحر جنيسارت أو بحر الجليل. وأطلق في رؤيا 19: 20 و 20: 10 و 14 و15 و \n21: 8 على بحيرة النار والكبريت. والبحيرات قليلة الوجود في سوريا وفلسطين. والبحر \nالميت الذي يصح أن يسمى بحيرة يطلق عليه في أغلب الحالات اسم ((بحر الملح)) ويحتمل \nأن مياه ميروم الوارد ذكرها في يش 11: 75 هي نفس بحيرة الحولة.'),
(544, 'بَحُْور', '(خر 30: 8) مركَب يصنع من لبان ونوع آخر من الصمغ أو بعض \nالعطورات. وأما وأما كيفية تركيبه فموجودة في خر 30: 34-36. وكان استعماله ممنوعاً \nفي غير بيت الرب حيث كان يحرقه الكهنة فقط (2 أخبار 26: 16-21) على مذبح من شجر \nاللبخ مغشَى بالذهب يسمّى مذبح البخور (انظر ((مجمرة)) و ((مذبح)) ) وكان البخور \nالمتصاعد رمزاً للصلاة انظر مز 141: 2 ولو 1: 10. وفي الرؤيا منظر الملاك الذي يوقد \nالبخور على المذبح الذهبي فيصعد دخانه مع صلوات القديسين (رؤيا 8: 3-6) على أن \nالوثنين أيضاً كانوا يوقدون بخوراً للآلهة الكاذبة (ار 11: 12 و 17).'),
(545, 'بَدَاد', 'اسم عبري معناه ((انفصال)) وهو أبو هداد ملك ادوم (تك 36: 35) ويسمّى بدد أو هدد (1 أخبار 1: 46).'),
(546, 'بَدَان', 'اسم عبري ربما كان معناه ((ابن الدينونة))(1) اسم قاضي ذكر بين يرّبعل (جدعون) ويفتاح (1 صم 12: 11) والأرجح أنه باراق كما ورد في الترجمة السبعينية والترجمة السريانية.(2) اسم رجل ابن اولام من سبط منسى (1 أخبار 7: 17).'),
(547, 'بَدَد', '(انظر ((بداد)) أعلاه).'),
(548, 'بَيْدر', '(انظر ((درس)) ).'),
(549, 'بِدْقَر', 'اسم عبري ربما كان معناه ((ابن داقر)) وهو اختصار ((بعل دقر)) أحد قواد ياهو. كان ضابطاً من الرتبة الأولى (2 مل 9: 25).'),
(550, 'برَايا', 'اسم عبري معناه ((من خلقه يهوه)) وهو رئيس بنياميني وابن شمعي (1 أخبار 8: 21).'),
(551, 'بَربري برابرة', 'كانت هذه الكلمة تطلق على كل فرد من أمم الأرض ما عدا اليوناني والروماني. فيقال يوناني وبربري ولا تتضمن هذه اللفظة حينما تستعمل في الكتاب المقدس شيئاً من معنى الخشونة والتوحّش الذي قد يفهم في وقتنا الحاضر (1ع 28: 2 و 4 ورو 1: 14 وكو 3: 11).'),
(552, 'بَرْثُولَمَاوْس', 'اسم يوناني عن الآرامية ومعناه ((ابن تولماي)) كنية أو لقب لنثنائيل والبرهان على ذلك هو ذكر فيلبس ونثنائيل معاً في انجيل يوحنا (يو 1: 45-51) وذكر فيلبس وبرثولماوس في الأناجيل الاخر (مت 10: 3 ومر 3: 18 ولو 6: 14) وأيضاً عدم ذكر الاسمين آي برثولماوس في جدول يوحنا ونثنائيل في جداول الإنجيلين الآخرين. فلذلك يرّجح أنه كان ذا اسمين كغيره من الرسل. ولم يذكر سوى في الآية المشار إليها وفي يو 21: 2 ويرّجح أن اسم برثولماوس ورد في الكتاب المقدس مع اسم فيلبس لأن فيلبس هو الذي آتى به إلى المسيح. ويقول التقليد أنه بشر في الشرق وأنه مات شهيداً بانتزاع جلده.'),
(553, 'بُرْج', '(مت 21: 33) كانت الأبراج في قديم الزمان تبنى على أسوار المدن لدفع العدو عنها عنها عند هجومه على المدينة. وكان البعض ينون برجاً (عليّة) في الكروم أو البساتين لأجل التنزه أو لتكون ملجأ للناطور أو (الحارس) (2 مل 26: 10) يأوي إليها إذا هطلت الأمطار وكان علو هذه الأبراج أحياناً ستين قدماً وعرضها 30 قدماً.ووردت لفظة برج مراراً عديدة في الكتاب المقدس مقرونة باعلام كما يأتي:برج بابل (تك 11: 4و 9) برج تاباص (قض 9: 51-53) برج التنانير (نح 3: 11) برج حننئيل (نح 3: 1 وار 31: 38 وزك 14: 10) برج داود (نش 4: 4) برج سلوام (لو 13: 4) برج شكيم وهو عبارة عن قلعة حصينة قرب قرية شكيم التجأ إليها أهل تلك المدينة هندما هاجمهم ابيمالك (قض 9: 46-49). وبرج القطيع (مي 4: 8) وبرج لبنان (نش 7: 4) وبرج المئة (نح 3: 1) وبرج يزرعيل (2 مل 9: 17).'),
(554, 'بَرْجَة', 'اسم يوناني لا يعرف معناه.عاصمة بمفيلية وهي مستعمرة رومانية في آسيا الصغرى على بعد سبعة أميال ونصف من البحر على ضفة نهر كسترس. تصل غليها المراكب الصغيرة. وكان سكانها يونانيون وفيها هيكل وحلبة سبق ومراسح. وعلى قمة الصخر هيكل لارطاميس وقد وجدت بعض النقود المسكوكة فيها مصوّر عليها هذه الآلهة الشهيرة. زارها بولس وبرنابا ومرقس في الربيع. وهناك فارقهما مرقس وعاد إلى أورشليم (1ع 13: 13 و 14: 25) واسمها الآن اسكى قلصّى. وفيها أثار قديمة كثيرة.'),
(555, 'بَرْحومي', 'نسبة غير قياسية لبحوريم (2 صم 23: 31) وربما كان هو نفس الاسم البحرومي المذكور في 2 أخبار 11: 33.'),
(556, 'برَخئيل', 'اسم عبراني معناه ((المبارك من الله)) أبو اليهو أحد أصحاب أيوب (اي 32: 2 و 6).'),
(557, 'بَرَخيا وبَرِخيّا', 'اسم عبري معناه ((المبارك من الله)) (1) لاوي ابو ساف (1 أخبار 6: 39 و15: 17). (2) رجل ابن زربال من ذرية داود (1 أخبار 3: 20). (3) لاوي ابن آسيا (1 أخبار 9: 16). (4) لاوي كان بوّاباً لتابوت العهد (1 أخبار 15: 23). (5) افرايمي ابن مشليموت عاش في أيام فقح الملك ( 2 أخبار 28: 12). (6) ابو مشلام أحد العاملين في بناء أسوار أورشليم (نح 3: 4 و30 و 6: 18). (7) ابو زكريا (زك 1: 1 و 7 ومت 23: 35).'),
(558, 'بَرَد', 'عندما تمر الغيوم في تيار بارد جداً فجأة تتحول قطعها إلى جليد متجمّد. فأحياناً يكون البرد رذاذاً لأن التجمد يلحق بحبات الماء النازلة مطراً. وأحياناً تكبر القطع فتضر الإنسان والحيوان والزرع. وكان البرد إحدى الضربات التي أصابت مصر أيام موسى (خر 9: 23 و 33 ومز 78: 47). واستخدم الله البرد في مساعدة يشوع على إهلاك أعدائه فقد جاء أنه رمى الاموريين ((بحجارة البرد)) من السماء فأهلك منهم أكثر مما قتل بحد السيف (يش 10: 11). وكانت ضربة البرد تعد عند الاقدمين من الضربات العظيمة، ولذلك استعملت مجازاً في الكتاب المقدس للتعبير عن عقابات هائلة (1 ش 28: 2 ورؤ 16: 21).'),
(559, 'البَرْدِي', 'هو البابيروس الشهير. نوع من الفصيلة السعدية cyperus Papyrus (1 ي 8: 11) ينمو في مستنقعات النيل والحولة. وكان المصريون يصنعون منه سلالاً وأسفاطاً كالسفط الذي وضع فيه موسى النبي (خر 2: 3 و5) ولاعجب إذا قيل أنهم صنعوا منه قوارب (1 ش 18: 2) لأن سيقانه كانت في غاية المتانة والليونة, تنثني على بعضها البعض بكل سهولة. واصطنع المصريون من البرديّ الرق المعروف بورق البابيروس. وكيفية صنع الورق منه هو أن يؤخذ القسم الداخلي من القصب المذكور فيشق إلى سيور صغيرة ثم تلتصق هذه السيور بعضها إلى بعض بالغراء وتترك في الشمس إلى أن تجف جيداً.ومما تجدر ملاحظته أن الكلمة المترجمو ((ورق)) في 2 يوحنا 12 تشير إلى ورق من البردي.وقد وجد في مصر كميات كبير من أوراق البردي التي هي على جانب عظيم من الأهمية في دراسة الكتاب المقدس. فمن بين هذه بردي الألفنتين أو الجزيرة وهو مكتوب بالغة الآرامية وقد كتب قبل عام 400 قبل الميلاد في منطقة أسوان.وبردي ((ناش)) وهو مكتوب بالغة العبرية ويحوي بعض الأجزاء من الوصايا العشر ويرجع إلى القرن الثاني قبل الميلاد.وبردي جون ريلندز ويحوي بعض الأجزاء من العهد القديم بالغة اليونانية جاءت من القرن الثاني قبل الميلاد. ومن ضمن هذه المجموعة أقد جزء معروف إلى وقتنا هذا من العهد الجديد وهو جزء من انجيل يوحنا ويرجع إلى أوائل القرن الثاني الميلادي.وأوراق بردي تشستر بيتي وتحتوي على أجزاء من العهد القديم والعهد الجديد بالغة اليونانية. وبردي بودمار (2) ويشمل معظم انجيل يوحنا ويرجع إلى عام 200 ميلادي تقريباً.والبردي الغنوسي الذي اكتشف بالقرب من نجع حماد, ويرجع إلى القرنين الثالث والرابع الميلاديين.'),
(560, 'بَرّر يبرّر تبريرا', '(رو 3: 24 و 25 و 4: 25) تتضمن كلمة التبرير القانون الأساسينون الأساسي للأيمان المسيحي. وهي عكس الدينونة. والتبرير خلاف البراءة، أي الحكم بعد ارتكاب الجريمة. وحيث أن الجميع اخطأوا وأعوزهم مجد الله، وحيث أن أجرة الخطيئة هي موت، فكل الجنس البشري صار تحت حكم الموت. وهنا المعضلة التي لم تحلّها جميع الأديان الوثنية وغيرها: كيف أن الله القدوس يغفر خطايا الناس بدون تنفيذ حكم الموت في الخاطي؟ كيف يكون الله باراً، أي عادلاً، بينما هو لا ينفذ حكمه؟ كانت الذبائح في العهد القديم رمز موت الفادي. ولما جاء المسيح على الأرض، مات هو نفسه، أي قدّم نفسه ذبيحة عن الخطيئة. نفّذ الحكم فمات وصار موته يحسب لكل من يؤمن به. هذا معنى القول (رو 3: 25) ((لإظهار برّه في الزمان الحاضر ليكون بارّا ويبرّر من هو من الإيمان بيسوع)) هذا هو السبب (رو 3: 25) ((الذي قدمه الله كفارة بالإيمان بدمه من أجل الصفح عن الخطايا السالفة بإمهال الله)). وهذا التبرير مجّاني أي بنعمة الله، وليس للمؤمن فضل فيه. على أن المؤمن، حتى تغفر خطاياه السالفة، والمستقبلة، ينبغي أن يخضع لفعل روح الله القدوس، الذي يغير قلبه ويجعله يولد من فوق، فيصير، كما قال بطرس (2 بط 1: 3 و 4) ((لكي تصيروا بها شركاء الطبيعة الإلهية)).'),
(561, 'البرّية', 'د تطلق هذه الكلمة على أرض خربة غير صالحة لشيء (1ش 21: 1) وقد تطلق أيضاً على أرض غير محروثة تصلح أن تكون مرعى جيداً للمواشي، وأشهرها برية سيناء التي تاه فيها بنو إسرائيل، وبرية سين، وبرية فاران، وبرية يهوذا، وعين جدي، وأريحا، وزيف، وبئر سبع، وجبعون، وبيت آون وبرية معون. (أنظرها في أبوابها انظر أيضاً ((قفر)) ).'),
(562, 'برزاوَت أو برذايث', 'اسم عبري معناه ((نبع الزيتون)) وهو اسم رجل اسيري ابن ملكيئيل (1 أخبار 7: 31).'),
(563, 'بَرزِلاّي', 'سم عبري معناه ((مصنوع من حديد)) (1) رجل جلعادي (2 صم 19: 31) كان صديقاً لداود وقد أحسن معاملته أيام كان هارباً خوفاً من ابشالوم ابنه. فلما قتل ابشالوم ورجع داود إلى أورشليم كان برزلاّي هذا في صحبته إلى أن عبروا الأردن. وعندئذ استأذن من الملك أن يرجع إلى وطنه، أما الملك فعرض عليه، جزاء لمعروفه، أن يصحبه إلى أورشليم، ويصرف باقي أيامه في دار الملك. فلم يقبل برزلاّي تلك الدعوة لأنه كان متقدماً جداً في السن. إلا أنه أرسل ابنه عوضاً عنه، وجاء نص في الكتاب، أن الملك داود أحسن إلى عائلة برزلاّي كل مدة حياته، وأوصى سليمان أبنه من بعده أن يحسن إلى أولاد برزلاّي ويجعلهم من الآكلين على مائدته (1 مل 2: 7).(2) المحوليّ، حمو ميكال ابنه شاول (2 صم 21: 8).(3) زوج ابنة برزلاّي الجلعادي الذي عاد خلفاؤه من بابل، \nوطلبوا أن يدخلوا سلك الكهنة، ولكنهم لم ينجحوا في ذلك (عز 2: 61 و 62 ونح 7: 63 و 64).'),
(564, 'بَرْسَابا', '(انظر ((بار سابا)) ).'),
(565, 'بَرْسِيس', 'اسم يوناني معناه ((فارسية)) وكانت امرأة مسيحية من مدينة رومية ((تعبت كثيراً في الرب)) أرسل إليها بولس الرسول تحية (رو 16: 12).'),
(566, 'بِرْشَاع', 'اسم عبري ربما كان معناه ((مع الشر)) أو ((بالشر)) وكان ملك عمورة (تك 14: 2).'),
(567, 'بَرَص أَبَرص', 'البرص مرض عضال وهو غير الجذام المعروف. لأن البرص مرض جلدي وإن كان يؤثر في الأعضاء التي يصيبها، فتتساقط عقد الصابع والأنف وسقف الحلق الخ.. وقد يعيش به المريض طويلاً. وكثيراً ما يبدأ كنتوء (ورم) أو بياض كالقوباء. إلا أنها بعد قليل تتأكّل حوافيها فتصير أعمق من الجلد ويبيض الشعر النابت فيها (لا 13: 2 و 3 و 7 و 8 الخ) وإذا كان في الجلد ناتئ ابيض قد صيّر الشعر ابيض وفي الناتئ وضح من لحم حي (لا 13: 10 و 14-16 و24) فيحكم على الأبرص أنه نجس ويجب أن ينعزل عن الناس. أما إذا غطّى البرص كل الجسم ولا يرى فيه لحم حي، فيكون الشخص طاهراً (عد 13) وقد يصيب المرْض الذقن أو الأنف أو يعقب حرقاً بالنار أو دمّلا (لا 13) وقد ذكر هذا المرض في غير اللاوين في عد 12: 10 و 2 اي 26: 16-23 و 2 مل 5: 27. وقد ورد في لا 14: 55 برص البيوت وبرص الثياب، وما ذلك إلا عبارة عن الفطر الذي قد ينمو على جدران البيوت فيحدث فيها بقعاً كما يحدث البرص بقعاً في بدن صاحبه. والرأي السائد أن البرص هو الذي أسمه الآن (بسورياس).وكان البرص داء مكروهاً جداً عند اليهود فكان يسطو على البعض منهم باعتباره قصاصاً لهم من الله كما حصل لمريم أخت موسى، وجيحزي. وقد يصيب المرض أناساً من أي طبقة حتى المتنعمين كنعمان السرياني.'),
(568, 'بَرْغَامُس', 'اسم يوناني لا يعرف معناه على وجه التحقيق. وهو اسم مدينة في ميسيا بآسيا الصغرى كانت فيها إحدى الكنائس السبع (رؤ 1: 11 و 2: 12-17) وقد لقبها القديس يوحنا بكرسي الشيطان لكثرة المعلمين الكذبة فيها الذين أضلّوا الناس وأسقطوهم في وهدة الخطية. (وكثرت هياكلها الوثنية ومن ضمنها هيكل للآله زفس) وعدد سكانها الآن 20 ألفاً تقريباً وبعضهم مسيحيون واسمها حالياً في اللغة التركية هو برجما. وكان فيها مكتبة تحتوي على مائتي ألف مجلد أضافتها كليوباترا إلى مكتبة الإسكندرية. وكانت برغامس مركزاً كبيراً لصناعة الرقوق والورق من الجلود، ولذا فاسم الرقوق في اللغات الأوربية Parchment مأخوذ من اسمها.ولم تزل أثار هذه المدينة القديمة باقية إلى الآن تشهد بعظمتها وغناها، كالأعمدة الرخامية التي ظنّ أنها بقايا هيكل اسكولابيوس. ويدّعي أهاليها الآن أنهم يعرفون قبر الشهيد انتيباس، ومحل الكنيسة التي اجتمع فيها التلاميذ لقراءة رسالة يوحنا.وبالاختصار نقول أن هذه المدينة كانت قديماً، عامرة متمدنة، وكفاها فخراً أنها مسقط رأس جالينس العالم الشهير الذي كان أول من قال أن الأوعية الدموية تحمل دماً لا هواء حسبما زعم من سبقه من الأقدمين.'),
(569, 'بَرْق', 'لمعان الضوء الناتج عن انفجار كهرباء الجو (السالب والموجب) وردت هذه الكلمة في الكتاب المقدس للتعبير عن هول الغضب الإلهي على بني البشر (2 صم 22: 15). وقد لازم البرق ظهور الرب في جبل سيناء (خر 19: 16) وكذلك يلازم البرق رؤى السماوات (رؤ 8: 5 ومت 24: 27) ويشبه مجيء ابن الإنسان بالبرق.'),
(570, 'بُرقع بَراقع', 'غطاء للوجه كانت تلبسه نساء أورشليم في أيام اشعياء ولا زالت تلبسه بعض النساء إلى يومنا هذا (اش 3: 19).'),
(571, 'بَرْقُوس', 'يرّجح أنه اسم ارامي معناه ((ابن قوس)) كان أباً لبعض النثينيم (خدم الهيكل) الذين رجعوا من سبي بابل (عز 2: 53 ونح 7: 55).'),
(572, 'بِرْكة، بُرك', 'حوض تتجمّع فيه مياه المطر أو نبع ماء من الأرض، وبعضها تتصرّف مياهها في قنوات الحدائق. وأشهر البرك المذكورة في الكتاب المقدّس، بركة بيت حسدا (يو 5: 2)وبركة حبرون (2 صم 4: 12) وبركة السامرة (1 مل 22: 38) وبركة سلوام (يو 9: 7) والبركة العليا (2 مل 18: 17) والبركة السفلى (1ش 22: 9) وبركة الملك (نح 2: 14) والبركة العتيقة (1ش 22: 11) وبركة سليمان (جا 2: 6). \nوادي بَركة:اد احتفل يهوشافاط بعد أن غلب الموآبيين (2 اي 20: 26) يسّمى الآن وادي ن وادي بريكوت وهو على بعد 8 أميال إلى الجنوب من بيت لحم.'),
(573, 'بارك يبارك بركة مبارك الخ', 'ترد لفظة ((بارك)) ومشتقّاتها كثيراً في الكتاب المقدس. فقد تشير إلى مباركة الناس الله (مز 103: 1 و 134: 1) ((باركي يا نفسي الرب)) أي احمدي أعماله وأثنى عليها ومجدّي الرب من أجلها. وقد تشير إلى مباركة الناس (تك ص 49 وتث 33) ومعناها نقل البركة إليهم وطلبها من الرب لهم. ومن هذا القبيل بركة هرون وبنيه لبني إسرائيل (عد 6: 23-27). وعماد البركة أن الرب يبارك الناس، أي يمطر عليهم من نعمة، ويزيد غلاتهم ويلطف بعائلاتهم (تك 12: 2 و 3). وقد بارك المسيح تلاميذه قبلما صعد (لو 24: 50 و51). \nكأس البركة:ي 1كو?كو 10: 16 إشارة على الكأس المقدسة رمزاً لدم الفادي وتحمل جميع نعم وبركات الفداء بدمه. وتشير إليها كأس الخلاص (مز 116: 13). وكان من التقاليد القديمة أن رئيس الوليمة يأخذ كأساً ويبارك الله لأجلها ولأجل جميع المراحم للمجتمعين ثم ثم يديرها على الضيوف فيشرب كل منهم كما جرت العادة في العشاء الربّاني.'),
(574, 'بَرْمِينَاس', 'اسم يوناني يرّجح أن معناه ((ثابت))وهو أحد الشمامسة الذين كانوا يفرّقون الحسنات على فقراء الكنيسة وأراملها في أورشليم 01ع 6: 5).'),
(575, 'بَرْنَابَا', 'اسم آرامي معناه ((ابن الوعظ)) وهو لاوي قبرصي الجنس. اعتنق المسيحية في زمان الرسل. فترك علاقاته العالمية وابتدأ يجاهد في نشر بشرى الخلاص في العالم، ويحث الناس على اعتناق المسيحية، ويعزّيهم في مصائبهم. ولذلك سماه الرسل برنابا آي ابن الوعظ بعدما كان اسمه أولاً يوسف (1 ع 4: 36).وبرنابا ضمن من استجابوا للشركة المسيحية الأولى فحقّق المبدأ. ((ولم يكن أحد يقول أن شيئاً من أمواله له، بل كان عندهم كل شيء مشتركاً)) (1ع 4: 32)، ففي عدد 37 نقرأ ((إذ كان له حقل باعه وأتى بالدراهم ووضعها عند أرجل الرسل))وكان كبير القلب كريماً فهو الذي رحّب ببولس بعد ما قبل المسيح وعرّف التلاميذ عليه لما رجع من دمشق إلى اورشليم (1ع 9: 27). ثم بعد ذلك أخذ بولس من طرسوس إلى إنطاكية وبشّرا هنالك باسم المسيح فنجح نجاحاً عظيماً ( 1ع 11: 25 و 26). ثم سافرا للتبشير في الخارج في السفرة التبشيرية الأولى 01ع ص 13). وحضرا مجمع أورشليم (1ع 15: 22 وغل 2: 1). وذهبا مع يهوذا الملقب برسابا وسيلا إلى إنطاكية (1ع 15: 22-34) ثم ذهب برنايا ومرقس إلى قبرص (1ع 15: 39).والبعض ينسبون إليه الرسالة إلى العبرانيين. وتنسب إليه رسالة معنونة باسمه. إلا أنه لا يعرف كاتبها من هو. أما انجيل برنابا الذي يزعم البعض أن برنابا كاتبه فهو مؤلف وضع في القرون الوسطى وانتحل اسم برنابا باطلاً. ويشير التقليد إلى مكان بالقرب من فاما غوستا في قبرص على أنه مقبرة برنابا.'),
(576, 'بَرْنِيع', '(انظر ((قادش)) ).'),
(577, 'بَرْنِيكي', 'اسم يوناني معناه ((حاملة النصر)) وهي ابنة اغريباس الكبير وأخت اغريباس الصغير ملك اليهود، امرأة اشتهرت بسوء السيرة والمفاسد. فقد قال بعضهم أنها كانت على علاقة غير شريفة مع أخيها، قال التاريخ أنها كانت عشيقة فسباشيان وتيطس. وذكر عنها أنها حضرت، باحتفال عظيم، محاكمة بولس أمام الوالي فستوس في قيصرية (1ع 25: 13).'),
(578, 'بُرُوخُورُس', 'اسم يوناني معناه ((قائد في جوقة المرتلين)) وهو أحد الشمامسة السبعة الذين انتخبوا للعناية بأرامل اليونانيين ويرّجح أن العناية بفقراء المسيحيين في أورشليم كانت ضمن مهمتهم (1ع 6: 5).'),
(579, 'بَرُودَخ بَلادَان', '(2 مل 20: 12) انظر ((مردوخ بلادان)).'),
(580, 'مِبراة', '(ار 36: 23) هي السكينة الصغيرة المستعملة لبري القلم.'),
(581, 'بَرِيث', 'كلمة عبرية معناه ((عهد)) (قض 9: 46) اطلب ((بعل بريث)).'),
(582, 'بَرِيسكّلا', 'اسم لاتيني معناه ((امرأة عجوز صغيرة)) وهي امرأة اكيلا رجل يهودي، اشتهرت بالتقوى والفضيلة فكانت تساعد رجلها في التبشير والمناداة بالإنجيل وفي أعماله الخيرية التي كان يعملها مع أبناء الكنيسة في ضيافاته الكثيرة التي كان يصنعها في بيته (1ع 18: 2 و 18 و 26 ورو 16: 3 و 1 كو 16: 19).'),
(583, 'بَرِيعة', 'اسم عبري معناه ((بارع)) وقد ورد اسم:(1) أحد أقرباء افرايم الذي قتل أخوته رجال جتّ حينما نزلوا ليسرقوا ماشيتهم. وإذا ما حُوّر اسمه قليلاً كان معناه بلية ولذلك سماه أبوه بريعة \n((لأن بلية كانت في بيته)) ( 1 اي 7: 23).(2) أحد أولاد اشير (تك 46: 17 وعد 26: 44 و45 و1 اي 7: 30 و 31).(3) رئيس بنيامين (1 اي 8: 13).(4) لاوي ابن شمعي (1 أخبار 23: 10 و11).'),
(584, 'البَريعيّون', 'نسل بريعة (عد 26: 44).'),
(585, 'بِزْثا', 'اسم فارسي معناه ((الهدية المضاعفة)) أحد خصيان احشويرش السبعة (1س 1: 10).'),
(586, 'بِزْيوتِية', 'اسم عبري معناه ((محتقر يهوه)) مكان في جنوبي شرقي اليهودية (يش 15: 28) ولا يعرف موضعه الآن على وجه التحقيق.'),
(587, 'بُسْتان بساتين', 'ارض مسورّة معدة لغرس الشجر المثمر أو المزهر وأحياناً \nلغرض الانشراح ((اطلب جنّة)) (1مل 21: 2 و 2 مل 9: 27 و 21: 18 واش 10: 18 و 29: 17 \nو 32 : 15 وار 2: 7 و 48: 33 ولو 13: 19 ويو 18: 1 و 26 و 19: 41).'),
(588, 'بُستَاني', 'حارس البستان ومن يشتغل فيه (يو 20: 15).'),
(589, 'بَسْمة', 'اسم عبري معناه ((عطرة)) وهي إحدى نساء عيسو (تك 26: 34 و 36: 3 و 17).'),
(590, 'بَسوديا', 'اسم عبري معناه ((من هو موضع ثقة يهوه)) وهو أب مشلاّم أحد اللذين رمّما الباب العتيق في سور أورشليم (نح 3: 6).'),
(591, 'بَواسير', 'رض أصاب الفلسطينيين (1 صم 5: 6 و 9) وكان من دأب الوثنيين \nتقديم تمثال يم تمثال الجزء المصاب بمرض للإله عند البرء منه (1 صم 6: 5).'),
(592, 'بَسُور', 'وادي البسور جنوبي اليهودية. عبره والجدول الذي كان فيه داود مع 400 من رجاله (1 صم 30: 9-12) وكان هذا الوادي يقع جنوبي صقلغ. وربما كان هو وادي غزة الذي تجري مياهه بالقرب من بئر سبع وتسير إلى البحر الأبيض المتوسط جنوبي غزة.'),
(593, 'بَشّر تبشيراً', 'ابلغ الخبر الطيب-البشارة. وكان مخلصنا يبشّر ويعلم في الهيكل وخارجه. وكثيراً ما كان يعظ الجموع من البحر أو على الجبل. وأوصى تلاميذه من بعده أن يذهبوا ويبشّروا جميع الأمم في أقطار المسكونة. فالتبشير بدأ في المسيحية. وأما في زمان العبرانيين القدماء فلم يكن ثمة تبشير. إلا أنهم بعد السبي، على ما يخبرنا الكتاب المقدس 1 أخبار ابتدأوا أن يفسروا الشريعة للشعب. \nمبشّر:تطلق هذه الكلمة في العهد الجديد على من يعظ ببشارة الخلاص، متنقّلاّ من مكان إلى آخر، لا يستقر في مكان مخصوص، إنما همه التجوّل يعظ بالإنجيل ويؤسس الكنائس باسم المسيح (1ع 21: 8 واف 4: 11 و 2 تي 4: 5).وكان المبشرون مساعدين للرسل في أشغالهم وكانوا رفاقهم في أسفارهم فكان الرسول بولس يأخذهم في معيته حيث كان يذهب ليزور الكنائس (1ع 20: 4 و 5) ويرسلهم حاملين أخباراً مختلفة بخصوص بشرى الخلاص ليعلونها للأخوة البعيدين عنه (في 2: 19-23) حتى أنه كان يسلمهم أشغالاً مهمة ليتمّموها مدة غيابه (1 تي 1: 3 و 3: 14 و 15 و 4: 13).وقد أشير بالرموز الآتية إلى أصحاب \nالبشائر الأربع: فرمز إلى متى بوجه الإنسان لأنه بيّن تسلسل المسيح من آدم، وإلى \nمرقس بأسد لأنه كتب عن المسيح من حيث أنه أسد يهوذا المنتصر، وإلى لوقا بثور لأنه \nأشار إلى أن المسيح قدّم نفسه ذبيحة لأجل خطايا العالم، وإلى يوحنا بنسر لارتفاعه \nفي أفاق لاهوت المسيح العليا.'),
(595, 'بِشلاّم', 'اسم عبري معناه ((بسلام)) وكان حاكماً من قبل الدولة الفارسية على فلسطين في مدة رجوع اليهود من سبي بابل. ولما ابتدأ الراعون من السبي في ترميم هيكلهم، أرسل كتابة ضدّهم إلى الملك ارتحشستا ليمنعهم عما شرعوا فيه (عز 4: 7).'),
(596, 'بُصْرة', 'اسم عبري معناه ((قلعة)) أو ((حظيرة)) ويطلق هذا الاسم على مدينتين: الأولى في بلاد ادوم (اش 34: 6 و 63: 1) وقد خربت كما تنبأ عنها ارميا النبي (ص 49: 13 وعا 1: 12). وأما بصيرة الحديثة فعلى بعد 20 ميلاً إلى الجنوب الشرقي من بحر الميت. والثانية في بلاد موآب (ار 48: 24) وظن بعضهم أنها باصر.'),
(597, 'بُصْقَة', 'اسم عبري معناه ((مرتفع)) أو ((أرض صخرية)) وهي مكان في سهول اليهودية في أقصى الجنوب وكان جد يشوع لأمه من هذا المكان. وكانت بالقرب من لخيش (يش 15: 39 و 2 مل 22: 1). وربما كان مكانها اليوم بلدة دوائمة.'),
(598, 'بَصَل', 'نبات من الفصيلة الزنبقية، ينمو بكثرة في مصر. والبصل المصري مشهور لكبره وحسن طعمه. وقد اولع الإسرائيليون بأكله حتى أنهم فضّلوه على المنّ والسلوى (عد 11: 5).'),
(599, 'بَصَلْئيل', 'اسم عبري معناه ((في ظل الله)) وقد ورد هذا الاسم:(1) لرجل من سبط يهوذا اشتهرا بالحذق في الصناعات الدقيقة \nفصنع الأدوات اللازمة للهيكل (خر 31: 1).(2) لرجل ذكر عنه عزرا النبي أنه تزوّج بامرأة غريبة (عز 10: 30).'),
(600, 'بَصْلوت أو بَصْلِيت', 'اسم عبري معناه ((انتزاع)) وهو رجل رجع نسله من السبي مع \nخدم الهيكل ((النثينم)) الذين رجعوا مع زر بابل (عز 2: 52 ونح 7: 54).'),
(601, 'بَصُور', 'أبو بلعام ويسمّى في العهد القديم بعور وهو اسم عبري معناه \n((احتراق)) (عد 22: 5 الخ) ولكن في 2 بط 2: 15 يدعى بصور حسب الصيغة اليونانية.'),
(602, 'بَضائع', '(نح 10: 31) اطلب ((تجارة)).'),
(603, 'بَطِّيح', 'تنمو كل أنواع البطيخ بكثرة في الديار المصرية. وفي ذلك \nحكمة إلهية، لأنه مهما كان الظمآن في تلك البلاد الحارة فقيراً، يقدر أن يروي عطشه \nبأكله شيئاً من البطيخ رخيص الثمن. والظاهر أن الإسرائيليين اعتادوا عليه واولعوا \nبأكله حتى أنهم لما خرجوا إلى البرية واشتدّ عليهم الحرّ، وهم سائرون في صحراء \nسيناء، تذكّروا بطيخ مصر وقالوا يا ليتنا بقينا هناك فنروي عطشنا بتلك الفاكهة \nالرطبة (عد 11: 5).'),
(604, 'بُطْرُس', 'اسم عبري معناه ((صخرة أو حجر)) وكان هذا الرسول يسمى أولاً سمعان واسم أبيه يونا (مت 16: 17) واسم أخيه اندراوس، واسم مدينته بيت صيدا. فلما تبع يسوع سمي ((كيفا)) وهي كلمة آرامية معناها صخرة، يقابلها في العربية صفا أي صخرة وقد سّماه المسيح بهذا الاسم. والصخرة باليونانية بيتروس ومنها بطرس (يو 1: 42 ومت 16: 18) وكانت مهنة بطرس (يو 1: 42 ومت 16: 18) وكانت مهنة بطرس صيد السمك التي كان بواسطتها يحصل على ما يكفي عائلته المقيمة في كفر ناحوم كما يستدل من عيادة يسوع لحماته وشفائها من الحمّى. (مت 8: 14 و 15 ومر 1: 29-31 ولو 4: 38-40).ويرجح أن بطرس كان تلميذاً ليوحنا المعمدان قبل مجيئه إلى المسيح. وقد جاء به إلى يسوع أخوه اندراوس واحد من تلميذي يوحنا المعمدان المقربين إليه. وقد أشار يوحنا في حضورهما إلى يسوع بعد رجوعه من التجربة في البرية (يوحنا 1: 35-42). وقد دعا يسوع بطرس ثلاث مرات فأولاً: دعاه ليكون تلميذاً، ودعاه ثانية: لكي يكون رفيقاً له ملازماً إياه باستمرار (مت 4: 19 ومر 1: 17 ولو 5: 10) ثم دعاه ثالثة: لكي يكون رسولاً له (مت 10: 2 ومر 3: 14 و 16 ولو 6: 13 و 14) وقد ساعد حماس بطرس ونشاطه وغيرته على أن يبرز كالمتقدم بين التلاميذ من البداية. فيذكر اسمه دائماً أولاً عند ذكر أسماء الرسل (مت 10: 2 ومر 3: 16 ولو 6: 14 واع 1: 13). وكذلك عند ذكر أسماء التلاميذ الثلاثة المقربين جداً إلى يسوع كان اسمه يذكر أولاً فمثلاً في التجلي، وعند إقامة ابنه بايرس، وفي بستان جثسيماني وهلم جرا (مت 17: 1 ومر 5: 37 و 9: 2 و 13: 3 و 14: 33 ولو 8: 51 و 9: 28).ولا يدل سقوطه السريع على شيء من الشك، فإن ما أظهره من المحبة لسيدة بعد ذلك كفيل بالبرهنة على أن ما حصل من إنكار سيده، كما تركه جميع التلاميذ في ليلة المحاكمة، إنما كان ضعفاً بشرياً، لم يستمر طويلاً بل أن نظرة العتاب من سيده الذي عرفه جيداً جعلته يخرج إلى خارج ويبكي بكاءً مرّاً (لو 22: 62).وفي الكتاب المقدّس أمور تذكر مختصة بهذا الرسول، تظهر صفاته الحسنة، كقوله ليسوع ((اخرج من سفينتي يا رب لأني رجل خاطيء)) (لو 5: 8 و 9)وما ذلك إلا لتأّثره السريع بالعجيبة التي صنعها المسيح. وهكذا إذا تتبّعنا سيرة هذا الرسول نرى أموراً تبرهن على سرعة إيمانه وثقته بابن الله، منها مشية على الماء (مت 14: 29) ومنها أنه أوّل من أدرك حقيقة شخصية يسوع فاقرّ جهاراً بأنه المسيح ابن الله (مت 16: 16).هذا ولا يخلو أن فكرة كان متّجهاً نحو الأشياء الزمنية كما يظهر من قوله ليسوع بعد ذلك ((حاشاك يا رب، لا يكون لك هذا)). وذلك إذ سمعه يقول، أنه ينبغي أن يذهب إلى أورشليم ويتألم الخ (مت 16: 22 و 23) إلا انه مع كل ذلك كان متمسّكاً بكل ثبات بسده كما ظهر من قوله، ((يا رب إلى من نذهب؟ كلام الحياة الأبدية عندك)) (يو 6: 67 و 68).وحينما أراد يسوع أن يغسل أرجل التلاميذ أبي عليه ذلك أولاً إلاّ أنه لم يلبث أن اقتنع بكلام سيده وصرخ قائلاً ((ليس رجليّ فقط بل يديّ وراسي)). وإذا قال يسوع لتلاميذه ((حيث اذهب أنا لا تقدرون انتم أن تأتوا))، قال له بطرس: ((يا سيد، لماذا لا اقدر أن اتبعك الآن؟ إني أضع نفسي عنك)) (يو 13: 37 و 38).وبعد القيامة، يخبرنا الجزء الأول من سفر الأعمال أن بطرس حقق ما أنبأ المسيح عنه ((وعلى هذه الصخرة ابني كنيستي)) فسواء أكان المقصود بالصخرة الإيمان الذي صرّح به لبطرس، ((إنه المسيح بن الله الحي)) أم إن لفظة صخرة قصد بها الاستعمال المزدوج أي أن الإيمان هذا كان الأساس، أو أن بطرس واسمه معناه ((صخرة)) كما قدّمنا يعبّر عن الحقيقة أن كل من يؤمن بأن المسيح هو ابن الله الحي ومخلص العالم يكوّن الكنيسة، على كلا الحالين نشط بطرس لقيادة أعضاء الكنيسة الأولى.فكان هو الذي قاد التلاميذ إلى سدّ الفراغ في عدد الرسل (1ع 1: 15) بانتخاب بديل ليهوذا. وكان هو الذي أوضح معنى حلول الروح، وكيف انه من الآن يكون الخلاص بالإيمان بابن الله لمغفرة الخطايا (1ع 2: 14-36). فانضم \nعند ذاك للكنيسة أول ثلاثة ألاف عضو. وكان الواسطة في شفاء الأعرج، وكان الكليم المدافع، والشارح للمسيحية (1ع 3: 4 و 12 4: 8) وكان هو الذي وبّخ حنانياً وسفيرة لتطهير أغراض ودوافع العطاء (1ع 5: 3 و 8) وكان هو الفم الذي أعلن فتح باب الخلاص لليهود (1ع 2: 10 و 38) وللأمم في موضوع كرنيليوس (1ع 10).وبعد ما وُضعت أساسات الكنيسة، بدأ بطرس يختفي آخذاً مكاناً متواضعاً برضى وقبول. ففي الكنيسة في أورشليم أخذ القيادة يعقوب، أخو الرب (1ع 12: 17 و 15: 13 و 21: 18 وغل 2: 9 و12) وكان الباب للأمم قد فتح على مصراعيه وتولىّ \nبولس الرسول القيادة في توصيل بشرى الخلاص لهم (غل 2: 7). وأما بطرس كرسول انجيل الختان، تاركاً أورشليم لعقوب والأمم لبولس.وينهي سفر الأعمال ذكر نشاط بطرس في ص 15 عندما قوبل رأيه عن تبشير الأمم بالترحيب من الجميع. وبعد ذلك نسمع أنه كان في أنطاكية (غل 2: 11) وربما في كورنثوس (1 كو 1: 12) وأنه واصل رحلاته التبشيرية وزوجته معه من مكان لآخر (1 كو 9: 5) وأخيراً استشهد كما سبق الرب وأخبره (يو 21: 19).بخلاف ذلك لايخبرنا الكتاب المقدس شيئاً عن حظ هذا الرسول وعن أتعابه وآلامه أو نجاحه وتوفيقهغير ما نستطيع استنتاجه من رسالتيه. ففيهما يبرز بطرس أمامنا مثالاً للوداعة والثبات في الإيمان \nوأنموذجاً للرجاء الذي لايفنى ولايضمحل.ووصف المؤرخون كيفية سجنه وصلبه بالتفصيل. غير أنه لا يستطيع أحد تأكيد أين ومتى كان ذلك بالضبط. وقيل أن المسيحيين في رومية نصحوا له بأن يهرب غير أنه, كما يقولون, رأى السيد داخلاً ررومية وهو يحمل الصليب. فقال له إلى أين يا سيد؟ فأجابه إلى رومية حيث أصلب ثانية. قيل فتوبخ بطرس ورجع واستشهد مصلوباً, وطلب أن ينكس الرأس إمعاناً في تأديب نفسه وفي الشهادة لسيده.غير أنه يكفينا الترجيح أن بطرس ذهب إلى رومية واستشهد فيها حسبما ذكر بابياس وإيرونيموس وإكليمندس الإسكندري وترتوليانوس وكايوس وأوريجانوس ويوسابيوس. فإن هؤلاء لم يزيدوا على قولهم أن الرسول ذهب إلى رومية حيث استشهد.وقد ذهب بعض الطوائف إلى جعل بطرس رئيساً على الكنيسة وجعلوا من أنفسهم خلفاء له.وأما دعوى تغيير الإسم والتصريح الذي نطق به السيد المسيح قائلاً: ((على هذه الصخرة أبني كنيستي)) فقد سبق شرحهما بما فيه الكفاية. وشبيه بذلك, الإدعاء أن رومية, تبعاً لذلك ينبغي أن تكون الكرسي الوحيد للرئاسة في الكنيسة. ويكفي أن نقول أن الروح لايحصر في مكان أو كرسي وأنه حيث يحل الروح يكون كرسي المسيح ولو تعددت الكراسي. وكأني بالرسول الذي حنّكته الأيام قد ترك اندفاعه الطبيعي الذي جعله أوّل من كان ينطق عندما يوجّه إليهم السؤال, كأني به في أخريات حياته خشي ما وقع فيه أولئك, فبدأ رسالته الثانية بالتصريح, ((سمعان بطرس عبد يسوع المسيح ورسوله إلى الذين نالوا معنا إيماناً ثميناً, مساوياً لنا ببرّ إلهنا والمخلص يسوع المسيح)). فساوى المسيحيين بنفسيه فكم بالحري القادة بينهم وبين غيرهم.وقد كتب الرسول رسالتين النبرة العظمى والفكرة الواضحة فيهما هي: ((الرجاء الحي)) (1 بط 1: 3 و4 و2بط 3: 13).رسالة بطرس الأولى: وقد كتبت على الأرجح من رومية فإن بابل المشار إليها في 5: 13 وإن كان يمكن أن تكون بابل الكلدانية- وكان فيها مسيحيون. أو يمكن أن تكون بابل المصرية (بابلون التي هي مصر القديمة الآن) إلا أن المرجح هو الرمز لرومية ببابل لأنها كانت حينئذ نبع أشد الاضطهادات على المسيحية والمسيحيين. وكتبها بين سنتي 63و 67 للمذكورين في آسيا الصغرى حيث سبق بولس فبشّر وأرسل رسائل غلاطية وأفسس وكولوسي. وكان غرضه تشديد إيمانهم وسط التجارب المحرقة وإنعاش روح الرجاء والانتظار فيهم.ويذكر كاتب هذه الرسالة أن بطرس الرسول (ص 1 : 1) ويؤيد نص الرسالة بجملته هذه الحقيقة كما تشهد بها كل الأدلة التاريخية التي لدينا. وهي موجهة ((إلى المتغربين من شتات بنتس وغلاطية وكبدوكية وآسيا وبيثينية)) (ص 1: 1) وتشير في الغالب إلى جميع المسيحيين الذين كانوا يقطنون هذا الإقليم الذي يشمل في العصور الحديثة إقليم آسيا الصغرى.محتويات رسالة بطرس الأولىيمكن أن تقسم محتويات الرسالة كما يأتي:مقدمة: ص 1: 1 و2.(1) الرجاء المسيحي بقيامة المسيح من بين الأموات 1: 3-2: 10.أ الأمن والإطمئنان بالرغم من الاضطهاد 1: 4- 12.ب حثّ على أن يحيوا حياة تليق بالرجاء 1: 13- 2: 3.ج طبيعة الكنيسة 2: 4- 2.(2) توجيهات بقصد الوصول إلى الخلق المسيحي السامي 2: 13- 4: 6.أ بالنسبة للدولة 2: 13- 17.ببالنسبة للعلائق الإنسانية 2: 18- 25.ج السلوك في البيت 3: 1- 7.ع السلوك تحت الاضطهاد 3: 8- 4: 6.(3) تعليمات بشأن الحاجات الراهنة 4: 7- 5: 11.أ الصبر والاحتمال في زمن الاضطهاد 4: 7- 19.ب واجبات الشيوخ والشعب 5: 1- 11.تحيات ختامية 5: 12- 14.رسالة بطرس الثانية: هذه رسالة رعوية موجهة إلى المسيحيينفي كل مكان لتحذرهم ضد التعاليم الزائفة ولتحثهم أن يتمسكوا بالإيمان ويثقوا فيه.ويقول كاتب هذه الرسالة عن نفسه أنه ((سمعان بطرس عبد يسوع المسيح ورسوله)) (1: 1و 3: 1) وأنه كان مع المسيح فوق جبل التجلي (1: 17 قارنه مع مت 17: 1- 13 ومر 9: 2- 13 ولو 9: 28- 36)والذي أنبأ الرب يسوع باستشهاده (2 بط 1: 14 قارنه مع يو 21: 18 وما بعده)إلا أنه مع هذه كلها فإن النقاد غير متفقين من جهة كاتبها ومن جهة تاريخ كتابتها فيقولون أن أسلوب الرسالة ليس بالأسلوب البسيط وهي تخالف الرسالة الأولى في أسلوبها وقد بدأ النقاد من عصر جيروم يأخذون اختلاف الأسلوب هذا دليلاً على اختلاف الكاتب. ثم يقولون أيضاً بشكهم في أن رسائل الرسول بولس كانت متداولة قبل موت بطرس الرسول إلى الحد الذي يفهم من نص هذه الرسالة, ثم يقولون أن الكنيسة الأولى في بعض أجزاء العالم القديم لم تكن متثبتة متحققة من جهة كاتب هذه الرسالة (قارن ما جاء في تاريخ يوسابيوس الكنسي بخصوص هذا) ولم تدخل الرسالة ضمن مجموعة أسفار العهد الجديد في الكنيسة السريانية إلا في القرن السادس).إلا أن صدق نسبة هذه الرسالة إلى كاتبها بطرس الرسول ثابت من أن بعض العلماء اليوم يعتقدون اعتقاداً راسخاً أن أسلوب الرسالة الثانية هو نفس أسلوب بطرس الصياد ذاته وأسلوب الرسالة الأولى هو أسلوبه الذي جاءنا على يد كاتب كما يظهر هذا من 1 بط 5: 12 ويشير هؤلاء العلماء إلى التشابه الكبير بين الرسالتين في الأسلوب والمادة والتعبيرات والتراكيب. ثم أن كون الكنيسة الأولى قبلتها ضمن مجموعة أسفار العهد الجديد, ففي هذه جميعها دليل على أن كاتبها هو بطرس الرسول.أما من جهة تاريخ كتابتها فيظهر أن في ص1: 14 تلميحاً إلى أنها كتبت قبل استشهاد الرسول في روما مباشرة سنة 68 ميلادية, وأن في ذكر الأخطار التي يعالجها الرسول فيها بذاتها وفي استخدام الكاتب لرسالة يهوذا وفيما يفهم فيها من إشارة إلى الرسالة الأولى ما \nيؤيد أنها كتبت في هذا التاريخ.محتويات رسالة بطرس الثانية:(1) تحية 1: 1و 2.(2) حث على النمو في النعمة والمعرفة 1: 3-11.(3) التثبت من الخلاص المسيحي 1: 12-21.أ من التعليم الرسولي 1: 12-18.ب من كلمة الوحي 1: 19-21.(4) إدانة المعلمين الزائفين وبغض تعاليمهم 2: 1-22.(5) التحقق من مجيء المسيح ثانية 3: 1-13.(6) واجب المسيحيين 3: 14-18.أ أن يعيشوا حياة طاهرة 3: 14.ب أن يفسروا التعاليم تفسيراً صائباً 3: 15 و 16.ج أن يقاوموا المعلمين الزائفين 3: 17.د أن ينموا في النعمة ومعرفة المسيح 3: 18.'),
(605, 'بَطل-أبطال', 'ستعملت هذه الكلمة في الكتاب المقدس بمعنى المدح كما نرى \nفي حز 27: 11 لا لا صفة خاصة من الناس كما ظن البعض.'),
(606, 'بَطّالة', 'ردت هذه الكلمة في مت 12: 36. وهي من البطالة آي الخلو من \nالعمل. آي كلمة لا عمل لها لها -لا لزوم لها. ومعناها كلمة غير نافعة ومهملة.. الخ. \nويقصد بها كلمة لا تليق كالقسم المتكرّر ( ((لا تنطق باسم الرب إلهك باطلاً)) ) أو \nنميمة. وقد تكون الكلمة رديّة في ذاتها وقد لا تكون، مما يحتّم على المؤمن أن يحاسب \nعلى لفظه حتى لا يحاسب عليه.'),
(607, 'بّطْليموس', 'وجمعه بطالسة-لقب خلفاء الاسكندر المكدوني في مصر. \nوالمذكورون من البطالسة في الكتاب المقدس هم:(1) بطليموس \nالأول ولقّب ((سوتر)) آي المخلّص (323-285 ق.م) وهو أول سلسلة البطالسة. يرّجح انه \nابن غير شرعي لفليبس. خدم في جيش الاسكندر وفتح مصر سنة 323 ق.م. وتغلب على بردكّس \nسنة 321 ق.م. وزحف إلى فلسطين سنة 320 ق.م. وأخذ أورشليم يوم السبت. وسبى عدداً من \nاليهود فأخذهم إلى مصر، إلا انه عاملهم بلطف فأقاموا في مملكته. ويظن أنه ملك \nالجنوب (دا 11: 5).(2) بطليموس الثاني الملقّب \n((فبلادلفوس)) (285-247 ق.م.) ابن المتقدّم ذكره. وملك بسلام بعدما اقترنت برنيكي \nابنته بانطيوخس الثاني ملك سوريا (دا 11: 6) وهر الذي أّسس المكتبة الشهيرة في \nالإسكندرية. وجذب إلى تلك المدينة أناساً شهيرين كالشاعر ثيوكريتوس، والمهندس \nاقليدس، والفلكي اراتس وغيرهم. وقيل أنه أول من أمر بالترجمة السبعينية. وجمع بين \nالشرق والغرب وبين حكمة اليهود وفلسفة اليونانين. وكان لمساعيه تأثيراً عظيم في \nتاريخ الديانتين اليهودية والمسيحية.(3) بطليموس الثالث \nالملقّب ((اورجيتس)) (247-222 ق.م.) ابن المتقدم ذكره. زحف إلى سوريا لأخذ ثأر أخته \nالتي رفضها زوجها وقتلها. ففتح سوريا إلى إنطاكية شمالاً وبابل شرقاً، وقدّم ذبائح \nفي أورشليم حسب الشريعة. واسترجع التماثيل التي نقلها قمبيز إلى مصر (دا 11: \n7-9).(4) بطليموس الرابع الملقّب ((فيلوباتور)) (222-205 \nق.م.) ابن المتقدم ذكره وهو الذي غلب جيش انطيوخس الكبير عند ((رفح)) بقرب غزّة سنة \n205 ق.م. (دا 11: 10-12) وقدّم ذبائح الحمد في هيكل أورشليم إلاّ أنه حاول أن يدخل \nإلى قدس الأقداس فضرب بالفالج.(5) بطليموس الخامس الملقّب \n((ابفانيس)) (205-181 ق.م.) ابن المتقدّم ذكره. وكان عمره خمس سنين عندما مات أبوه. \nوفي مدة صغره فتح انطيوخس الكبير البقاع وفينيقية واليهودية. وهرب عدد غفيرمن \nاليهود الذين بقوا أمناء لسلسلة البطالسة إلى مصر حيث أسّس رئيس الكهنة هيكل \nليونتوبولس. ثم بواسطة الرومانيين تصالح بطليموس وانطيوخس إلاّ أن قوة مصر تقلّصت \nبعد ذلك الافتتاح (دا 11: 13-17).(6) بطليموس السادس \nالملّقب ((فيلومتر)) (181-146 ق.م.) ابن الأخير. وكان طفلاً عندما توفي أبوه. وكانت \nأمه كليوباترا ملكة إلى حين وفاتها 173 ق.م. وكانت مسالمة لسورية إلا انه في سنة \n171 ق.م. هجم انطيوخس ابيفانيس على مصر وتغلّب على يطليموس وأخذه أسيراً. غير أن \nالرومانيين استرجعوا مصر سنة 168 ق.م. ثم صارت تلك البلاد تدريجياً، ولاية رومانية \n(دا 11: 25-30) وفي مدة بطليموس السادس كمل بناء الهيكل في ليونتوبولس، فصار لليهود \nمركزاً غير اورشليم. وكانوا يخالطون فلاسفة اليونانين والمصريين ونتج من ذلك تأثير \nعظيم في استعداد الشعب اليهودي لقبول الديانة المسيحية.'),
(608, 'بُطْمة', 'جرة من الففصيلة السماقية Pistacra Terebinthus ينمو بكثرة \nفي فلسطين وسورية ويعمر سنين عديدة، حتى إذا ماتت الشجرة الأصلية تفرّخ من أسفلها \nفروع جديدة تخلفها. وإلى ذلك أشار النبي (اش 6: 13) وقد تكبر أشجار البطم كثيراً \nوتلتفّ أغصانها كما ورد في وصف الشجرة العظيمة (2 صم 18: 9) الملتّفة التي علق بها \nابشالوم بينما كان هارباً على بغله. \nوادي البطم:واد حلّت فيه جيوش العبرانيين حينما قتل داود جليات بطل \nالفلسطينيين (1 صم 17: 19). ويظن انه وادي السنط على بعد احد عشر ميلاً إلى الجنوب \nالغربي من أورشليم، وعرضه ربع ميل وجوانبه شديدة التصاعد. ويوجد إلى الآن بعض أشجار \nالبطم الكبيرة فيه.'),
(609, 'بَطمْس', 'زيرة في الأرخبيل الرومي تسمّى الآن ((بطمو)) على بعد نجو \n30 ميلاً جنوب ساموس على شاطئ آسيا الصغرى الجنوبي للغرب. كان من عادة الدولة \nالرومانية أن تنفي إليه المذنبين والمجرمين. والرجح ان يوحنا الانجيلي نفي إليها في \nسنة 94 ب.م. في زمان دوميتيانس (رؤ 1: 9).وتربتها مجدبة \nلأن أكثر أراضيها صخور قاحلة مغطاة بقليل من التراب، قيل أنها بقايا بركانية. وعلى \nمسافة قليلة من الشاطئ صومعة داخلها كهف يظنّ ان يوحنا كتب فيها سفر الرؤيا.'),
(610, 'بُطونيم', 'اسم عبري معناه ((حبات الفسدق)) وهي مدينة من أملاك سبط \nبني جاد (يش 13: 26) اسمها الآن خربة بطنة ثلاثة أميال جنوب غربي السّلط.'),
(611, 'بَعْرا', 'اسم عبري معناه ((محترقة)) وهي امرأة شجرايم من سبط بنيامين (1 اي 8: 8).'),
(612, 'بَعَيسا', 'اسم عبري ربما كان معناه ((عمل يهوه)) وهو لاوي جرشوني من \nسلفاء آساف (1 اي 6: 40) وقد ورد هذا الاسم في بعض المخطوطات والترجمات القديمة \nبصورة ((معسيا)).'),
(613, 'بَعْشا', 'اسم عبري ربما كان اختصاراً لاسم بعل شمس ومعناه ((الشمس \nبعل أو رب)) وهو ابن اخيّا من سبط يساكر. تآمر على ناداب بن يربعام وضربه في جبثّون \nالتي للفلسطينيين (1 مل 15: 27) وملك عوضا عنه عشرين سنة. وحذرا من ان يعارضه معارض \nفي الملك قتل كل عائلة يربعام إتماماً لما قاله نبي الله (1 مل 14: 10) وسلك بعشا \nفي طريق يربعام وعمل الشر في عيني الرب وكانت الحروب والأضطرابات وكل أيام ملكه (1 \nمل 15: 16-21) ولما مات تولى أبنه أيله ففتن عليه عبده زمري رئيس نصف المركبات \nوقتله وكل أهل بيته (1 مل 16: 9-11).'),
(614, 'بَعَشْترة', 'اسم كنعاني معناه ((بيت عشتاروت)) مدينة في باشان (يش 21: \n27) وتدعى أيضاً عشتاروت (1 اي 6: 71).'),
(615, 'بَعُوضَة-بعوض', 'نوع من الحشرات الصغيرة يقع في اللبن أو السمن فيصفى عنه. \nوبعض أنواع البعوض ينقل جراثيم الملاريا وقول المسيح (مت 23: 24) يشير إلى كون \nالفريسيين يلتفتون إلى الأشياء الصغيرة ويتركون الكبيرة.'),
(616, 'البعل وجمعه بعليم', 'اسم سامي معناه ((رب أو سيد أو زوج)). وهو إله كنعاني وكان \nابن الإله ايل وزوج الآلهة بعلة أو عشيرة أو عنات أو عشتاروت ويعرف كالإله هدد. \nوكان غله المزارع ورب الخصب في الحقول وفي الحيونات والمواشي.وقد اولع أهل المشرق جدّاّ بعبادة البعل حتى أنهم كانوا يضحون \nالذبائح البشرية على مذابح (ار 19: 5). وكانوا يختارون الأماكن المرتفعة كالجبال \nوالتلال ذات المناظر الجميلة فيبنون عليها الأبنية الفاخرة المزخرفة ويكّرسونها \nلهذا الإله العظيم عندهم.وقد صار البعل بعد ذلك عثرة \nللإسرائيليين الذين كسروا شريعة الله بإدخالهم عبادة هذا الإله إلى بلادهم (1 مل \n18: 17-40 ويش 22: 17 وعد 25: 3 و 5 و 18 ومز 106: 28 وتث 4: 3).وبالاختصار نقول أن عبادة البعل كانت عمومية بين أهالي المشرق \nفي الزمان القديم ولذلك ترى له أسماء عديدة، وما ذلك إلا لأن كل أمة كانت تسميه \nباسم يعرف به عند قومها. وكان الاسم من أسمائه يبتدي غالباً ببعل وينتهي باسم تلك \nالبلاد أو المدينة الموجود هو فيها، أو بشيء ينسب إليه نحو بعل فغور، وبعل زبوب - \nأي إله الذبان وهو إله عقرون.وكان للبعل كهنة كثيرون \nيخدعون الناس بسحرهم وشعوذتهم وأعمال أخرى ينسبونها لإلههم، كما نرى من قصة إيليا \nوأنبياء البعل، فإنه قتل منهم نحو 450 نفساً فاظهر بذلك للناس كذبهم وعدم قدرة \nآلهتهم على عمل العجائب.'),
(617, 'بّعْل', '(1) رجل من نسل رأوبين (1 أخبار 5: 5).(2) اسم رجل من نسل بنيامين (1 أخبار 8: 30).(3) اسم مدينة من سبط شمعون وتسمّى أيضاً بعلوت وبعلة بئر (1 \nاي 4: 33). ولا يعرف موضعها الآن على وجه التحقيق.'),
(618, 'بَعَلة', 'سم سامي معناه ((سيدة)) (1) مدينة في دان (يش 19: 44) \nوالأرجح أنها المدينة التي حصّنها الملك سليمان (1 مل 9: 18 و 2 اي 8: 6). ولا يعرف \nموضعها الآن على وجه التحقيق.(2) اسم آخر لقرية يعاريم \n(يش 15: 9) ولبعل يهوذا (2 صم 6: 2) ولقرية بعل في يهوذا (يش 15: 60 و 18: 14) اطلب \n((قرية يعاريم)).(3) موضع في يهوذا (يش 15: 29) وهي \n((بالة)) (يش 19: 3) وبلهة (1 اي 4: 29) ويرّجح أن موضعها الآن هو ((تلول المريخ)). \nجَبَل البعلة:جبل في يهوذا بين عقرون ويبنئيل (يش 15: 11).'),
(619, 'بَعْلة بئر', 'اسم عبري معناه ((صاحبة البئر)) (يش 19: 8) انظر بعل (3)'),
(620, 'بَعْل بَريث', 'اسم كنعاني معناه ((رب العهد)) وهو الاسم الذي كان يعبد به \nالبعل في شكيم في زمن القضاة (قض 8: 33 و 9: 4) وكان له هيكل هناك، وأحياناً كان \nيعرف باسم ايل بريث أي إله العهد (قض 9: 46).'),
(621, 'بَعْلَبَك', 'هي مدينة واقعة في سهل البقاع عند سفح الجبل الشرقي. \nوسمّاها اليونانيون هليوبوليس آي مدينة الشمس وهي على بعد 42 ميلاً إلى الشمال \nالغربي من دمشق وعى علو 3800 قدم فوق سطح البحر. ولا يعرف إلاّ القليل عن تاريخ هذه \nالمدينة القديمة. ولكنها اشتهرت بعظمة أبنيتها الفاخرة حتى أن هيكلها الكبير كان \nيعدّ من عجائب الدنيا.وفي بعلبك هيكلان كبيران، طول \nاصغرهما 225 قدماً وعرضه 120 قدماً. وكان محاطاً بأعمدة كبيرة الحجم طول الواحد \nمنها 45 قدماً والمسافة بين كل عمودين من 9-12 قدماً. وأما هيكل الشمس وهو أكبرها \nفطوله 324 قدماً وكان محاطاً بأربعة وخمسين عموداً يبلغ قطر الواحد منها 7 أقدام \nوعلوه من قاعدته إلى قمته 89 قدماً. وأما الحجارة المبني منها هذا الهيكل \nفكلسيّة.وبالاختصار نقول أن هذه المدينة العظيمة عدّت من \nأعظم مدن العالم، حتى أن هياكلها كانت تضاهي هياكل اليونان بعظمة بنائها وإن كانت \nدونها في الترتيب والزخرفة. وقلّما يعرف المؤرخون عن مؤّسسها الأصلي. وأما هيكل \nالشمس فقد بناه انطونينس بيوس سنة 150 ب.م. وقد ظنّ البعض أنها بعل جاد المذكورة في \nيش 11: 17 و 12: 7 و13: 5.'),
(622, 'بَعْل تامار', 'سم عبري معناه ((إله النخل)) مكان قريب من جبعة (قض 20: \n33). وربما هو المسمّى نخلة دبورة (قض 4: 5) ولا يعرف مكانه الآن على وجه التحقيق. \nوقد قال بعضهم بأن مكانه ((الرأس الطويل)) وقال آخرون إنه ((خربة ارحا)).'),
(623, 'بَعْل جاد', 'اسم عبري معناه ((معسكر البعل)) كان بعل جاد منتهى فتوحات \nيشوع شمالاً (يش 11: 17 و 12: 7 و 13: 5). ولا يعرف مكانه على وجه التحقيق وقد قال \nبعضهم أن مكانه الآن ((حاصبيا)) وقال آخرون ((بعلبك)).'),
(624, 'بَعْل حاصُور', 'اسم عبري معناه ((بعل الساحة)) اسم لمكان قتل فيه اباشالوم \nأخاه امنون (2 صم 13: 23). وربما كان مكانه الآن جبل القصور على بعد مسافة أربعة \nأميال ونصف شمالي شرق بيت آيل.'),
(625, 'بَعْل حرمون', 'اسم عبري معناه ((بعل جبل حرمون)) مكان بالقرب من جبل \nحرمون (قض 3: 3 و 1 أخبار 5: 23) ولا يعرف الآن موضعه على وجه التحقيق.'),
(626, 'بَعْلَزَبوب', 'اسم كنعاني وفي العهد الجديد بعلزبول. أصل هذا الاسم بعلزبوب فغيّر اليهود لفظه إلى بعلزبول (مت 10 25 و 12: 24 و 27 ومر 3: 22 ولو 11: 15 و 18 و19) ومعناه ((بعل الأقذار)) لأنهم كانوا يحتقرون آلهة الوثنيين، ويعتبرونهم كشياطين (ا كو 10: 20) وأما بعلزبوب ومعناه إله الذباب فكان إله عقرون. والأرجح أنه كان إله الطب عندهم (2 مل 1: 3) وهو أكبر جميع آلهتهم ولذلك دعي رئيس الشياطين كما ورد في مت 12: 24 ولو 11: 15 وقد ظنّ البعض أنه بعل المساكن لأنه رئيس الأرواح النجسة التي تدخل بعض الناس وتسبب الجنون كالروح النجس الذي أخرجه يسوع من الإنسان المجنون حينما دعاه الفريسيون بعلزبول رئيس الشياطين (مت 12: 24).'),
(627, 'بَعْل شليشة', 'سم عبري معناه ((بعل الثلث)) (2 مل 4: 42) وربما كان كفر \nالثلث على مسافة 16 ميلاً شمالي شرقي ((اللد)).'),
(628, 'بَعْل صَفون', 'اسم كنعامي معناه ((بعل الشمال)) أو ((بعل برج المراقبة)) \n(برج مجدل) الذي كان قريباً منها. وهو مكان قرب خليج السويس على الشاطئ الغربي من \nالسويس حيث عبر العبرانيون البحر الأحمر (خر 14: 2 و 9 وعد 33: 7).'),
(629, 'بَعْل فراصيم', 'اسم كنعاني معناه ((بعل الانفجارات)) مكان في وادي \nالرفائيين (2 صم 5: 20 1 أخبار 14: 11). وهو جبل فراصيم قرب وادي جبعون (اش 28: 21) \nوربما كان مكانه اليوم ((رأس السنادر)).'),
(630, 'بَعْل فغور', 'اسم موآبي لإله كان يعبد في جبل فغور (عدد 25: 1-9).'),
(631, 'بَعْل هامون', 'اسم كنعاني معناه ((بعل الجمهور)) وهو مكان كان لسليمان \nفيه كرم (نش 8: 11) ولا يعرف موضعه الآن على وجه التحقيق.'),
(632, 'بَعْلوت', 'اسم عبراني معناه ((سيدات-جمع بعلة)).(1) مدينة واقعة في الجهة الجنوبية من اليهودية (يش 15: 24) \nوالأرجح أنها بعلة بئر _يش 19: 8).(2) موضع كان في نصيب \nأشير مكان يحكمه بعنة أحد ضباط سليمان (1 مل 4: 16).'),
(633, 'بَعْلي', 'سم عبري معناه ((سيدي أو ربّي)) وردت هذه الكلمة في هو 2: \n16 وهي تحتمل معنيين الأول إله الكنعانيين المشهور. والثاني بعل امرأة، آي زوجها \nوفحوى عبادة طاهرة خالية من التقاليد الوثنية.'),
(634, 'بَعْلِيا', 'اسم عبري معناه ((يهوه بعل)) رجل بنياميني جاء إلى داود \nإلى صقلغ (1 أخبار 12: 5).'),
(635, 'بَعَلْيَا داع', 'اسم عبري معناه ((البعل يعلم)) أحد أبناء داود (1 أخبار \n14: 7) ويدعى أيضاً اليداع والياداع (2 صم 5: 16 و 1 أخبار 3: 8).'),
(636, 'بَعْلبس', 'اسم عموني معناه ((ابن عليس)) أو ((ابن السرور)) وكان ملك \nالعمونيين في أيام نبوخذ نصر (1ر 40: 14).'),
(637, 'البَعْلِيم', 'جمع بعل (اطلب ((بعل)) ).'),
(638, 'بَعْل يهوذا', 'انظر ((قرية يعاريم)).'),
(639, 'بَعْنَا', 'اسم عبري معناه ((ابن الضيق)) وهو اسم:(1) وكيل سليمان في المقاطعة الجنوبية في سهل يزرعيل من مجدو \nإلى الأردن، وكان ابن اخيلود وربما كان أخاً ليهوشافاط المسجل (1 مل 4: 12).(2) وكيل سليمان في اشير وما جاورها وكان ابن حوشاي وربما كان \nحوشاي هذا هو نفس صديق داود ومشيره (1 مل 4: 16).(3) رجل \nهو أبو صادوق (نحم 3: 4).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(640, 'بَعْنَة', 'اسم عبري معناه ((ابن الضيق)).(1) أحد أبني رمون من رجال ايشبوشث ابن يوناثان. ذهب مع أخيه \nركاب فدخلا في حر النهار بيت ايشبوشث المذكور وقطعا رأسه وأخذاه إلى داود في حبرون \nظانين أنهما يسرّانه بهذا العمل الفظيع. وأما هو فقتلهما جزاء لهما على هذه القسوة \nالبربرية وأمر بأن تعلق أيديهما وارجلهما على البركة في حبرون (2 صم 4: 2-12).(2) اسم أبي خالد أحد ضباط جيش داود (1 أخبار 11: 30).(3) رجل عاد مع زربابل من سبي بابل (عز 2: 2).'),
(641, 'بَعُور', 'اسم كنعاني معناه ((احتراق)) وهو اسم:(1) أبو بالع ملك ادوم (تك 36: 32 و 1 أخبار 1: 43).(2) أبو بلعام (عد 22: 5 الخ) ويدعى بصور (2 بط 2: 15).'),
(642, 'بعُولة', 'كلمة عبرية معناه ((متزوجة)) ذكر هذه الكلمة النبي اشعيا \nليظهر تمسك الأرض اليهودية بالله، تعالى، كتمسك المرأة ببعلها (اش 62: 4).'),
(643, 'بَعُون', '(عد 22: 3) (اطلب ((بعل معون)) ).'),
(644, 'بِغْثا', 'اسم فارسي قديم معناه ((عطية الله)) وهو أحد خصيان الملك \nاحشويروش السبعة (اس 1: 10).'),
(645, 'بِغثان', 'وهو أحد خصيان الملك احشوريوش وقد تآمر هو وترش على الملك \n(اس 2: 21).'),
(646, 'بُغْض', 'لبغضة عكس المحبة (مت 5: 43 43- 48) وقد أوصانا المسيح أن \nلانبغض إلا الخطيئة. وأن نحبه أكثر من الجميع. وأما البغضة فسبب جميع الانشقاقات \nوالخصومات التي تؤدي إلى أعظم الأضرار (اطلب ((محبة))).'),
(647, 'بَغُل', 'حيوان من ذوات الحوافر, يتولد من الحمار والفرس, وهو أكبر \nمن الحمار وأصغر من الفرس ولكنه يعمر أكثر منه. ومن أوصافه العناد (مز 32: 9) \nوالصبر على المشقّات. وهو يقوم مقام الفرس في الجبال الوعرة والأراضي المحجرة حيث \nيصعب على الفرس أن يسير في تلك المسالك الضيقة التي يعبرها البغل بكل سهولة, كأنه \nوجد لينوب منابه فيها. ولذلك يرغب سكان الجبل في اقتنائه كما هو مشاهد في جبل لبنان \nوغيره من البلدان الجبلية. وكان البغل معروفاً عند اليهود. ورغب فيه ملوكهم \nوأمراؤهم كما نقرأ عن بغل الأمير ابشالوم وغيره (2 صم 18: 9 و2 أخبار 9: 24 و1 مل \n1: 33 و10: 25 و18: 5). وقد نهى الناموس الموسوي عن تربية البغال (لا 19: 19).'),
(648, 'بِغْواي', 'اسم فارسي معناه ((حسن الحظ)) وقد ورد اسم:(1)رجل رجع بعض أولاد بغواي مع زربابل (نح 7: 19 وعز 2: 14) \nوبعضهم مع عزرا (عز 8: 14).(2) رجل يدعى بهذا الاسم ختم \nالميثاق الذي صنعه بنو اسرائيل (عز 2: 2 ونح 7: 7 و10: 16).'),
(649, 'بَقْبَقّر', 'اسم عبري معناه ((باحث)) وهو رجل لاوي (1 أخبار 9: 15).'),
(650, 'بَقْبُوق', 'اسم عبري معناه ((قارورة)) وكان أولاد بقبوق مع النثينيم \n(خدام الهيكل ) الذين رجعوا مع زر بابل (عز 2: 51 ونح 7: 53).'),
(651, 'بَقْبُقيا', 'اسم عبري معناه ((قارورة)) أو ربما كان معناه ((الرب سكب)) \nوهو رجل من بني لاوي (نح 11: 17 و12: 9 و25).'),
(652, 'البَقَر', 'من الحيوانات المجترّة المشقوقة الظلف, ولذلك عدّها \nالعبرانيون من البهائم الطاهرة (لا 22: 19).'),
(653, 'بَقَرة', 'ال النبي (اش 7: 21 و22) و22) ((ويكون في ذلك اليوم أن \nالإنسان يربي عجلة بقر وشاتين. ويكون أنه من كثرة صنعهما اللبن. يأكل زبداً. ((فإن \nكل من أبقى في الأرض يأكل زبداً وعسلاً)). ومعنى كلام النبي في هذه النبوة هو أن \nأرض يهوذا ستتغير هيئتها ويقلّ عدد سكانها. فينبت في كرومها الشوك والحسك, حتى أن \nلبن بقرة وشاتين يكفي لإعالة عائلة واحدة. وحرّم على اليهود حسب الشريعة الموسوية \nأن يذبحوا بقرة وعجلها في يوم واحد, وأما القصد من ذلك فغير معروف. وظنّ بعضهم أن \nسبب التحريم لإنما كان لأنها عادة وثنية أو لمجرد الشفقة والحنو. وقد ورد في سفر \nالعدد ص 19: 2 أن يرش رماد بقرة حمراء... الخ للتطهير. وكان المصريون القدماء \nيمثلون الآلهة هاثور في شكل بقرة.'),
(654, 'البقَاع', 'جمع بقعة وهي الأرض المستنقعةوهو السهل الواقع بين لبنان \nوالجبل الشرقي (اطلب لبنان وسورية).'),
(655, 'بُقي', 'اسم عبري وهو اختصار بقيا.(1) \nرجل من سبط دان وكان أحد الرؤساء الذين عيّنوا ليقسموا أرض كنعان (عد 34: 22).(2) رجل من نسل رؤساء الكهنة (1 أخبار 6: 5 و51 وعز 7: 4) \nويرّجح أنه لم يصل لهذا المنصب.'),
(656, 'بُقَيّا', 'اسم عبري وربما كان معناه ((الرب قد امتحن)) وهو رئيس \nالقسم السادس من المغنيين (1 أخبار 25: 4 و13).'),
(657, 'بِكْر', 'كانت العادة عند اليهود أن يكرّسوا كل بكر ذكر لخدمة الرب (خر 13: 12 و34: 19) وهناك سبب آخر وهو أنهم أمروا بذلك ليتذكروا قتل الله لأبكار المصريين وإبقائه على أبكارهم (خر 13: 12) فنسخ حكم هذه العادة منذ تعيين اللاويين زاد عدد الأبكار 273 ذكراً عن عددهم فداهم العبرانيون بدراهم عن كل رأس خمسة شواقل من الفضة (نحو نصف جنيه انكليزي) وأما البكر من البهائم فكان مكرّساً أيضاً لخدمة الرب. لايفكّ ولايبدل إلا إذا كان من الحيوانات النجسة حسب الشريعة, وأراد صاحبه فكّه. وإذا لم يرد بيع أو قتل أو بدل (خر 13: 13 لا 27: 27). وكان للبكر امتيازات عن باقي إخوته, يرد ذكرها في الكلام عن حقوق البكورية, كأن يأخذ نصيب اثنين وغير ذلك (تث 21: 17)هذا إذا كان يستحق هذه الامتيازات وإلا فيسقط حقه في جميع امتيازات البكورية كما حدث لعيسو ورأوبين (تك 27: 29 و1 أخبار 5: 1 و2). وقد اكتشف في النقوش التي وجدت في نوزي في العراق والتي ترجع إلى العصر الذي عاش فيه عيسو بأن أحدهم باع بكوريته لأجل نعجة. وأما ما ورد في الكتاب المقدس من ذكر أبكار المساكين (اش 14: 30) فيراد به شدة الفقر. وكذلك بكر الموت أي الموت العظيم (اي 18: 13) وبكر كل خليقة أي أول ورأس الخليقة (كو 1: 15)وأما البكر في عب 1: 6 فالمراد به الدلالة على عظمة المسيح ورئاسته.'),
(658, 'أبكار وباكورة', 'كانت فريضة على العبرانيين أن يقدموا لله من باكورة حصادهم \nوكرمهم وأبكار زيتهم وأول مخبوز من غلاّتهم الجديدة وأول الصوف من ماشيتهم. وكانت \nكل هذه العطايا تعطى لكهنة الرب الذين كانوا يستعملونها في احتياجاتهم المختلفة (خر \n23: 19 وعد 15: 19- 21 و18: 11- 13).وأما مقدار ما يقدّم \nمن هذه المواسم فغير معروف تماماً ولكنه يظن أنه لايقل عن 1 من 60 منها. وأما كيفية \nتقديم هذه الباكورات والأبكار فمشروحة بالتفصيل في لا 23: 10- 14.ومما يستحق الاعتبار كيفية جلب حزمة من أول حصاد الشعير إلى الهيكل التي كان يردّدها الكاهن أمام الرب في اليوم الثاني بعد عيد الفصح. وكان كذلك أنه بعد أن كان ينقّيها يأخذ حفنة من الشعير, ويشويها ويلتّها بالزيت ويقدمها للرضى عن الشعب. وأما القمح فكانوا يقدمون منه خبزاً جديداً شكراً لله على مواسمهم الجديدة.وأما حكم الأشجار فكان إذا زرع أحدهم شجرة, لايقطف ثمرها إلا بعد نهاية ثلاث سنوات. أما أثمارالسنة الرلبعة فيقدّسها للرب, وأما السنة الخامسة فلصاحب الملك. وكان بعدها أن يتصرّف بملكه كما يشاء (عد 18: 12 ولا 19: 23 و24) ولم يكن مسموحاً لهم أن يمسّوا الحصاد إلا بعد تقديم هذه التقدمة المذكورة آنفاً. ومازالوا على ذلك إلى أن توفي سليمان الملك, ثم أهملوها بعده, إلى أن استنهضهم حزقيا الملك للعمل بها(2 أخبار 31: 4 و5) وكذلك نحميا بعد رجوعهم من السبي (نح 10: 35 و12: 44) ويشدّد الأنبياء بتقديمها (حز 20: 40 و44: 30 و48: 14, قابل رؤيا 14: 4).'),
(659, 'البَكُورية', '(تك 25: 31) كان للبكورية عند اليهود امتيازات يمتاز بها \nالبكر عن غيره من ه من أخوته. منها نيابة البكر عن أبيه في البيت حين غيابه. ومنها \nاختصاصه بالبركة على شرط أن يكون مستحقاً لها, وإلاّ فتعطى لغيره كما حدث لعيسو \nورأوبين. ومنها أنه يعطى نصيباً واحداً زائداً عن أخوته (تث 21: 17) ومنها, وهو \nأهمها وأعظمها اعتباراً, أن البكر كان مكرساً للرب (خر 22: 29).وبناء على ذلك اختار الله اللاويين من الشعب ليخدموه عوضاً عن \nأبكار الشعب وفرض عليهم إزاء ذلك فدية البكر خمسة شواقل من الفضة كما تقدم الكلام \nعن ذلك.وكان للبكر من أولاد الملوك الحق أن يتبّوا أريكة \nالملك بعد أبيه (2 أخبار 21: 3 و4). على أن ذلك لم يكن مطّرداً فإن سليمان ويهوآحاز \nوابيّا خلفوا آباءهم في الملك ولم يكونوا أبكاراً (2 مل 23: 31 و1 أخبار 3: 14 و2 \nأخبار 11: 18- 22).ولما كانت البكورية أمراً ذا شأن \nواعتبار عند اليهود, فإنهم كانوا يلقبون كل ما كان كبير الأهمية بالبكر. ولذلك جاء \nذكر البكر, وبكر من الأموات والابن الوحيد في ألقاب سيدنا المسيح, له المجد (كو 1: \n18 ورو 8: 29).'),
(660, 'بُكْران', '(جمع بَكْر وهو ابن الناقة) ولعل الجمال المشار إليها في \nاش 60: 6 هي الهجن وهي نوع من الجمال مشهور بخفته وسرعته يقطع في النهار من 60 إلى \n90 ميلاً. وهو أعلى من الجمل المعروف وأطول منه, ولكنه لايحتمل الحر والبرد مثله \n(اطلب جمل).'),
(661, 'بُكْرو', 'اسم عبري معناه ((بكر)) وهو ابن آصيل ( 1 أخبار 8: 38 و9: 44).'),
(662, 'بِكْرِي', 'اسم عبري ومعناه ((بكري)) وهو أبو شبع أو أحد أجداده (2 صم \n20: 1).'),
(663, 'بَكورَة', 'اسم عبي معناه ((جد)) أو ((سلف)) وهو أحد أسلاف شاول (1 صم 9: 1).'),
(664, 'بُكاء', 'وادي البكاء, طريق تؤدي إلى أورشليم ولم يكن فيها آبار \nأولاً إلا أنهم حفروا فيها آبار بعد ذلك, فكان يشرب منها المارّ قاصداً المدينة \nالمقدسة. وقال بعضهم أنها وردت في مز 84: 6 على سبيل الاستعارة. وقال آخرون أنها \nوادي جهنم أي وادي ابن هنّوم. وكلمة BACA (بكا) قد تعني ((بلسان)).'),
(665, 'بَلاَدان', 'اسم أكادي ومعناه ((أعطى ابناً)) وهو أبو مردوخ بلادان ملك \nبابل (2 مل 20: 12 واش 39: 1).'),
(666, 'بَلاَستُس', 'اسم يوناني معناه ((فرخ نبات)) وكان الناظر على مخدع \nهيرودس اغريباس (اع 12: 20).'),
(667, 'بِاءجَاي', 'اسم عبري معناه ((سرور)) وهو أحد الذين ختموا الميثاق (نح 10: 8).'),
(668, 'بِلجة', 'اسم عبري معناه ((بهجة)) وهو:(1) \nرئيس الفرقة الخامسة عشرة من الكهنة في أيام داود (1 أخبار 24: 14).(2) كاهن عاد مع زربابل (نح 12: 5 و18).'),
(669, 'بِلْدَد', 'اسم عبري ربما كان معناه ((بيل قد أحب))وهو أحد أصحاب أيوب \nالثلاثة, الذي تباحث مع أيوب عن عدالة الله تعالى في ما صنعه معه(اي 8: 18 و25). \nوكان يكنى بالشوحي- وذلك إما نسبة إلى بلاده أو إلى شوح ابن ابراهيم من أمرأته \nقطورة (تك 25: 2). وربما كان شوح هذا جدّ بلدد المذكور هنا (اي 2: 11).'),
(670, 'بَلَسان', '(تك 37: 25) وطن البلسان بلاد الحبش. وهو شجر يبلغ علوّه 14 قدماً ذو ساق ذو ساق ناعمة وأوراق صغيرة خضراء يستخرج منه بلسان جلعاد المشهور برائحته العطرة الذي طالما أطنب الشعراء والمؤرخون القدماء في مدحه. وقد ذكر الأطباء في القديم له منافع عظيمة في شفاء الأمراض والجروح (ار 8: 22) وهكذا شاع استعماله بين الأمم الشرقية في ذلك الزمان. فكان التجار يحملونه إلى مصر ويبيعونه لسكان البلاد الذين كانوا يحنطون موتاهم به. وذكر في الكتاب أن الإسماعيليين الذين اشتروا يوسف كانوا حاملين بلساناً معهم إلى مصر. ثم عزّ وجوده أخيراً, وراجت سوقه فكان يباع بضعف ثقلة من الفضة. وقيل أن تيطس وبومبيوس أخذا منه كميات معهم إلى رومية, علامة على انتصارهما العظيم. وأما كطيفية استخراج هذا البلسم المشهور فهي أن تجرح شجرة البلسان بفأس فيخرج العصير من قشرتها فيُتلقى في أوعية خزفية معدة لذلك.أما البلسان فلا ينبت الآن في جلعاد غير أنه لايستنتج من ذلك أنه لم ينبت في الأعصر الماضية. ولاسيما أن لفظة جلعاد كانت تطلق على أرض واسعة ممتدة إلى شرقي جلعاد الحالية. ويظن الرهبان في أريحا أن الزَقوم Balanites Egyptiaca وهو بلسم جلعاد, فيستخرجون من ثمره نوعاً من العصير يبيعونه كبلسم جلعاد.'),
(671, 'بِلشَان', 'اسم عبري معناه ((السائل)) أو ربما كان من الأكادي ومعناه \n((سيدهم)) وهو رجل رافق زربابل في رجوعه من بابل (عز 2: 2 ونح 7: 7).'),
(672, 'البَلاط', '(يو 19: 13) كانت فسحة دار الفضاء التي لبيلاطس مبلطة بالرخام أو نوع آخر من الحجارة (قابل اش 1: 6) (واطلب جبّاثا).'),
(673, 'بَلْطشاصّر', 'اسم كلداني ومعناه ((ليحفظ بيل حياته)) وهو اسم أعطي \nلدانيال في بلاط نبوخذ نصر (دا 1: 7) (اطلب دانيال).'),
(674, 'بَلّوط', 'ذكر البلوط في أماكن شتى من الكتاب المقدس. ومن جملتها, تك \n12: 6 و13: 18 و14: 13 و18: 1 و35: 8 وتث 11: 30 ويش 24: 26 وقض 4: 11 و9: 6 و37 و1 \nصم 10: 3 و1 مل 13: 14 واش 6: 13 و44: 14 وحز 6: 13 وعا 2: 9 وزك 11: 2.والبلوط حسب اصطلاح لبنان هو الملّول واسمه النباتي السنديان \nالبرتغالي. Quercus Lusitanica Lam ورقه يسقط في الشتاء. وتبلغ شجرته علو 15 متراً. \nوخشبه أقل قوة من أكثر أنواع السنديان, ينشقّ بسهولة. إنما حطبه جيد كثير الاستعمال \n(اطلب سنديان).'),
(675, 'البَلّوطة', '(يش 19: 33) (اطلب صَنَعَنَنّيم).'),
(676, 'بَلّوطة تابور', 'رد هذا الاسم في 1 صم 10: 3 ولم يذكر في محل آخر من الكتاب \nالمقدس. وأما معنى ا معنى لفظة تابور فغير معروف, وقال البعص أن بلوطة تابور هي ذات \nألوان باكوت التي دفنت تحتها دبورة مرضعة رفقة (تك 35: 8) وقال كوندر أنها السهل \nالواقع جنوب أورشليم المسمى الآن البقعة.'),
(677, 'بَلّوطة صَعنَايم', 'سم المكان حيث خيّم جابر القيني (قض4: 11) وقال آخرون أنه \nمن السهل الواقع على على بعد ميلين أو ثلاثة أميال غربي بحيرة الحولة طوله ميلان \nوعرضه ميل واحد. تحيط به تلال من الجهة الغربية من هذا السهل موقع قادش نفتالي أو \nقادش. وربما كانت صنعايم هي بَسّوم الحديثة الواقعة شرقي تابور أو خان البحار (اطلب \nصعننّيم).'),
(678, 'بَلّوطة العائفين', 'بلوطة بالقرب من شكيم وقد ترجمها بعضهم بلوطة المعوننيم \n(قض 9: 37 قارنه مع تث 18: 10- 14).'),
(679, 'بَلّوطات ممرا', '(انظر ممرا).'),
(680, 'بَلّوطة مورة', '(انظر مورة).'),
(681, 'بَلْعام', 'اسم عبري ربما كان معناه ((الملتهم)) وهو ابن بعور من فتور \nوهي قرية فيما بين النهرين. وكان نبياً مشهوراً في جيله. والظاهر أنه كان موحّداً \nيعبد الله, وليس ذلك بعجيب لأنه من وطن ابراهيم الخليل حيث يظن أن أصول تلك العبادة \nكانت لم تزل معروفة عند أهل تلك البلاد, ما بين النهرين في أيام هذا الرجل.وقد ذاع صيت هذا النبي بين أهل ذلك الزمان فعلا شأنه وصارت \nتقصده الناس من جميع أنحاء البلاد ليتنبأ لهم عن أمور متعلقة بهم، أو ليباركهم \nويبارك مقتنياتهم وماأشبه. ومما هو جدير بالذكر أن بالاق ملك موآب استدعاه إليه \nليعلن شعب اسرائيل, وأما هو فسأل ربه ليلة قدمت عليه رسل موآب, فلم يأذن له. فلما \nكان الصباح رفض طلب بالاق وإن كان قد ذهب وبارك بني إسرائيل (عد 22: 9- 24: 25). \nولكنه دبر وسيلة للإيقاع بهم في شرك عبادة الأصنام. وقد حارب بنو إسرائيل \nالمديانيين وقتل بلعام عدد 31: 8 و16 ويهوذا 11).'),
(683, 'بَلِهَان', 'اسم عبري معناه ((أبله أو غرّ)) وهو اسم:(1) رئيس حوري وهو ابن إِبصر (تك 36: 27 و1 أخبار 1: 42).(2) رئيس بنياميني من عشيرة يديعئيل (1 أخبار 7: 10).'),
(684, 'بِلْهة', 'اسم عبري معناه ((بلهاء أو غرّة)) وهو اسم:(1) جارية راحيل (تك 29: 29 و30: 3) وقد ولدت ليعقوب دان \nونفتالي (تك 35: 25).(2) موضع في شمعون (1 أخبار 4: 29) \nيسمّى أيضاً بالة (يش 19: 3)وبعلة (يش 15: 29).'),
(685, 'بَلِيعال وبليّعال', 'اسم عبري معناه ((عديم الفائدة أو شرير)) وهو اسم كان \nكتّاب الأسفار المقدسة يلقّبون به كل من كان ذميماً ولئيماً لا يخاف الله ولا يهاب \nإنساناً (قض 19: 22 و 1صم 2: 12) وبهذا المعنى يقول الرسول ((وآي اتفاق بين المسيح \nوبليعال)) (2 كو 6: 15).'),
(686, 'بَمْفِيلية', 'مقاطعة في آسيا الصغرى واقعة إلى الجهة الشمالية من بحر \nالروم بين كيليكية وليكية. وكانت عاصمتها تسمى برجة، حينما زارها بولس الرسول (1ع \n13: 13 و 14: 24 و 27: 5) وأضاف إليها كلوديوس بيسيدية وليكية اللّتان لم تكونا قبل \nذلك فيها. وهي ساحل طوله 80 ميلاً وعرضه 30 ميلاً. وتخترقها ثلاثة أنهار هي \nالكتاركتس وألسترس والاورينيدون. وكانت برجة عاصمتها واتالية (اضالية) ميناها (1ع \n24: 25). وكانت بمفيلية بلاداً صغيرة مدة الحرب الفارسية، فأرسلت ثلاثين مركباً \nفقط، بينما أرسلت كيليكية مائة. وفي الأول أضافها الرومانيون إلى مستعمرة آسيا ثم \nفصلوها. وكان الحاكم عليها حينئذ شيشرون الشهير. وكانت برجة الموضع الأول الذي زاره \nبولس وبرنابا في السفرة التبشيرية الأولى. وهناك فارقهما مرقس (1ع 13: 13) ثم بعد \nرجوعه من بيسيدية بشّر في برجة ثم سافر من اتالية إلى إنطاكية (1ع 14: 24-26) وكان \nفي أورشليم يوم الخمسين بعض سكان بمفيلية.'),
(687, 'بِمْهال', 'اسم عبري ربما كان معناه ((على مهل)) وهو رجل من سبط اشير \n(1 أخبار 7: 33).'),
(688, 'بّنْ اونى', '(ابن حزني) (اطلب بنيامين).'),
(689, 'بَنَايَاهو', 'اسم عبري معناه ((من بناه يهوه)) وهو ابن رئيس الكهنة في \nأيام داود الملك. وقد اشتهر بشجاعته وإقدامه مراراً عديدة (2 صم 23: 20-23) وكان \nبناياهو هذا صديقاً مخلصاً لسليمان واتفق معه ضد ادونيّا (1 مل 1: 36-39) وبعد ما \nقتل يوآب أخذ محله في قيادة الجيش (1 مل 2: 25 و 29: 34).'),
(690, 'بُنْتُس', 'سم يوناني ولاتينني ومعناه ((البحر)) (1 بط 1: 1) اسم المقاطعة الشمالية الشرقية من آسيا الصغرى. وكانت واقعة على شطوط البحر الأسود، يحدها شرقاً كولخس وجنوباً كبدوكية وغرباً غلاطية. وكان يسكن هذه المقاطعة في أيام مخلصنا عدد ليس بقليل من اليهود (1ع 2: 9). وقد دخلت إليها بشارة الخلاص في القرن الأول المسيحي فاعتنق عدد غفير من أهلها الديانة المسيحية. وقد أرسل إليهم بطرس الرسول رسالته الأولى. وبنتس هذه هي وطن اكيلا رفيق بولس (1ع 18: 2) وكانت مملكة مستقلّة ومن ملوكها مثرادتس الشهير. إلا أنها أضيفت إلى املاك الرومانيين في زمن بومبيوس.'),
(691, 'بِنْحَائل', 'اسم عبري معناه ((ابن القوة)) وهو أحد الأمراء الذين \nأرسلهم يهوشافاط ليعلموا الشعب الشريعة (2 أخبار 17: 7).'),
(692, 'بَن حَانَان', 'اسم عبري معناه ((ابن المنعم)) وهو رجل من سبط يهوذا (1 \nأخبار 4: 20).'),
(693, 'بَنْزوحَيْت', 'اسم عبري معناه ((ابن زوحيت)) وهو رجل من نسل يهوذا (1 \nأخبار 4: 20).'),
(694, 'بِنْعَا وبِنْعَة', 'وهو ابن موصى من نسل يوناثان بن شاول (1 أخبار 8: 37 و 9 43).'),
(695, 'بَنْ عمَي', 'اسم عبري معناه ((ابن شعبي)) وهو ابن لوط من ابنته الصغرى \nومنه تسلسل العمّونيّون (تك 19: 38).'),
(696, 'بِنّوى', 'اسم عبري معناه ((مبني)) وهو اسم:(1) رجل لاوي (عز 8: 33).(2 و 3) \nاثنان من الذين أخذوا نساء غريبة (عز 10: 30 و 38).(4) \nلاوي بنى قسماً من السور (نح 3: 24).(5) أب لقوم عادوا مع \nزربابل (نح 7: 15) ويرّجح أنه باني (عز 3: 10).'),
(697, 'بُنِّي', 'اسم عبري ربما كان معناه ((مبني)) أو ((مقام)) وهو اسم:(1) لاوي (نح 9: 4).(2) أحد الذين \nختموا الميثاق (نح 10: 15).'),
(698, 'بَنْهَدَد', 'اسم عبري صيغته الآرامية بار هدد ومعناه ((ابن هدد)).(1) كان ملكاً على دمشق في زمن آسا ملك يهوذا. وهو الذي عقد \nمعه آسا عهداً ضد بعشا ملك إسرائيل (1 مل 15: 18) (اطلب آسا وبعشا).(2) ابن بنهدد المذكور أعلاه. خلف أباه في الملك وقد أثار هذا \nالملك، أخذ أسيراً في إحداها (1مل 20) وكان من بعد آخاب أنه أثار حرباً على يهورام \nملك إسرائيل، ولكن كان النبي اليشع يخبر ملك إسرائيل بتدابيره التي كان يدبرها \nعليهم فلم يصادف نجاحاً. وقد حاقت به الهزيمة، مرة، بأعجوبة إلهية (2 مل 6: \n8-33).ومن أمره أنه حاصر، مرة، السامرة فحدث ضيق شديد في \nالمدينة لأن جنوده كانوا محيطين بها، لا يمكنون أحداً من الدخول إليها ولا الخروج \nمنها. وكانت نجاتها من يده، أن الله ألقى الرعب ذات ليلة في قلب جنوده، ففروا \nهاربين، تاركين أمتعتهم وسلاحهم ومؤنتهم فنهبها الإسرائيليون. وأكلوا وأطعموا \nمساكينهم وأراملهم بعد ما أوشكوا أن يموتوا جوعاً (2 مل 7). وحدث بعد ذلك أن بنهدد \nمرض مرضاً شديداً فأرسل حزائيل عبده إلى النبي اليشع ليسأله أيشفى من مرضه أم لا. \nفكان جواب النبي أن الملك لا يشفى، وأن حزائيل هذا يملك عوضاً عنه. فرجع حزائيل إلى \nالملك بجواب كاذب أنه يشفى. ثم عمل على قتله فخنقه في فراشه وملك مكانه (اطلب \nحزائيل). ومما حدث لبنهدد هذا مع اليشع النبي أنه أرسل إليه عبده نعمان السرياني، \nوكان أبرص، فشفاه النبي من برصه (2 مل 5) (اطلب اليشع).(3) اين حزائيل في زمن ملك يهوآحاز وقد اضطهد بنهدد العشرة \nالأسباط أو المملكة الشمالية (2 مل 13: 3-13) ولكن يوآش بن يهوآحاز هزم بنهدد ثلاث \nمرات واستعاد المدن التي أخذها ملك دمشق من بني إسرائيل (2 مل 13: 22-25 قارنه مع ص 10: 32-33).'),
(699, 'بَنُو', 'اسم عبري معناه ((ابنه)) وهو لاوي (1 أخبار 24: 26 و 27).'),
(700, 'ابن', 'توّسع العبرانيون في إطلاقهم لفظة ابن على القرابة، \nفاستعملوها تارة بمعنى حفيد (تك 29: 5)وأطلقوها تارة على القرابة البعيدة جداً (مت \n22: 42) وتستخدم أيضاً للدلالة على صفة أو خاصيةٍ ما، كابن السلام (لو 10: 6).'),
(701, 'ابن آوى', 'جمعه بنات آوى (أطلب آوى).'),
(702, 'بِنْت، ابنة', 'قد تستعمل هذه الكلمة في اللغة العبرانية في غير معناها \nالأصلي، فيقال مثلاً ((يا ابنتي)) ويراد بها يا بنت أخي، أو يا أختي أو يا حفيدتي، \nإلى غير ذلك من الاصطلاحات. فاستير مثلاً، قيل عنها أنها ابنة مردخاي وكانت ابنة \nعمّ له. وقد يراد بابنة فلان أنها من نسله. كقولهم بنات موآب وبنات حث. ومن هذا \nالقبيل قيل عن اليصابات امرأة ذكريا أنها إحدى بنات هرون، مع أن هرون كان قد مات \nقبل وجودها بقرون عديدة. وجاء في تك 6: 2 ابناء الله وبنات الناس، وأريد بذلك أن \nرجالاً أتقياء أطهاراً تزوّجوا ببنات متدنّسات بالشرور والآثام.'),
(703, 'ابناء الله', '(تك 6: 2) ظن البعض أنه يراد بلفظة أبناء الله هنا، إما \nملائكة أو أرواح طاهرة. ولكن ولكن يرّجح أنه يقصد به الأبرار (انظر الله).'),
(704, 'تَبنَّى', 'جرت العادة عند الاقدمين، ولا تزال إلى الآن، أن يتبنّوا \nأولاداً لأنفسهم. فيتمتع هؤلاء البنون بجميع الحقوق البنوية من وراثة وغيرها كأنهم \nأبناء حقيقيون (خر 2: 10 واش 2: 7). وكانت المرأة قديماً، إذا لم يكن لها ولد تعطي \nجاريتها زوجة لبعلها، فإذا ولد له منها بنون، تبنتّهم سيدة الجارية وحسبتهم بنين \nلها كما صنعت سارة وراحيل. وإذا حدث أن رجلاً لم يكن له إلاّ ابنة وحيدة فقد يكون \nمنه أن يعطيها زوجة لعبد معتوق، ويتبنّى أولادها ليرثوه ويحيوا اسمه بعد موته.والتبنّي أمر مشهور عند الرومانيين واليونانيين. وقد سنّ لهم \nحكامهم في شرائع مخصوصة. وأما التبني فيراد به في الديانة المسيحية أن نؤمن بالمسيح \nمخلّصاً ورّباً لنا، فيغير روح الله القدوس قلوبنا ونولد الولادة الثانية فنصير أبناء الله (يو 1: 12 و 13) وورثاء لملكوته.'),
(705, 'بَنّاء', 'إنه لأمر محقق أن العبرانيين تعلموا صناعة البناء في مصر \nمدة إقامتهم بين ربوعها الديار (خر 1: 11 14) وأما استخدام داود وسليمان البنّائين \nمن الفينيقيين فلا ينافي أن الإسرائيليين عرفوا هذه الصناعة (1 مل 5: 17 و 18 و 1 \nأخبار 14: 1) واتقنوها جيداً، لأنم انفسهم كانوا يشتغلون في بناء الهيكل بمساعدة \nبنائي الفينيقيين. وأما بناء هيكل سليمان فأمر يحير العقول ويدهش الألباب. وكل من \nتأمل في ترتيبه وكيفية تركيب حجارته المربعة يعترف بحذق أولئك الرجال الذين كان لهم \nاليد الطولى في هذه الصناعة. وأعجب ما في ذلك أنهم لم يستعملوا في بنائه الطين أو \nالطفال المذكور في حز 13: 10 وإنما كانوا يسكبون الرصاص أحياناً بين تلك الحجارة \nالكبيرة لتعديل ضغطها. وأما الطين فكانوا يستعملونه في الأبنية الاعتيادية داخلاً \nوخارجاً (لا 14: 4-42 ومت 23: 27).'),
(706, 'بَنيامين', 'سم عبري معناه ((ابن اليد اليمنى، أو ابن اليُمن)) (1) تك 35: 18 وهو ابن يعقوب من امرأته راحيل، وكان أصغر أخوته. ولدته أمه في طريق بيت لحم وهم راجعون من فدان ارام إلى أرض كنعان. ولكنها ماتت عقب ولادته، لذلك دعته بن اوني، آي ابن حزني. وأما أبوه فدعاه بنيامين آي ابن يميني. وكان بنيامين ولداً لطيفاً مطيعاً ومحبوباً من أبيه، ومغرياً له في شيخوخته ولا سيما مدة غياب أخيه يوسف. وكل من قرأ قصة يوسف يرى محبته الغريبة لأخيه الصغير. فإنه ألح على أخوته وهم لم يعرفوه بعد، أن يحضروه معهم إلى مصر. فلما أحضروه ونظره، بكى وقبّله باشتياق لا مزيد عليه.وأما سبط بنيامين فكان نصيبه من أرض كنعان الأراضي الواقعة بين افرايم ويهوذا التي بلغت مساحتها نحو 26 ميلاً طولاً و12 عرضاً، وهي تعلو نحو 2000 قدم فوق سطح بحر الروم. وكلها أراض خصبة التربة, جيدة الهواء. ومن مدنها أورشليم عاصمة الأراضي المقدسة و25 مدينة أخرى غيرها.وقد اشتهر بنو بنيامين بالصفات الحسنة, كالثبات والوفاء. فإنه لما عصت الأسبات العشرة وانفصلت عن مملكة يهوذا لم ينقادوا إليهم, بل بقوا على اتحادهم وعهدهم مع إخوتهم بني يهوذا (امل 12: 21- 23) وقد تنبأعنهم يعقوب بقوله, ((بنيامين ذئب مفترس)) وكانوا أشداء البأس, أقوياء البنية, اشتهروا جداً بضرب المقلاع. وقام منهم شاول, أول ملك على بني إسرائيل.وجاء في قض ص 20 أن بقية الأسباط تحالفوا مرة ضد هذا السبط واحتالوا بمكيدة كادت تفنيه عن آخره. وكان بولس الرسول من هذا السبط.(2) رئيس بنياميني (1 أخبار 7: 10).(3) احد الذين أخذوا نساء غريبات (عز 10: 32). أرض بنيامين:اطلب بنيامين).'),
(707, 'بَني بَرَق', 'سم عبري معناه ((بنو البرق)) اسم مدينة في دان (يش 19: 45) \nوالأرجح أنها ابن ابراق أو بني براق الحالية على مسافة أربعة أميال من يافا.'),
(708, 'بَنِينو', 'اسم عبري ربما كان معناه ((ابننا)) وهو رجل لاوي ختم \nالميثاق (نح 10: 13).'),
(709, 'بَنات النعش', '(اي 38: 32) نجوم لامعة كوكبة الدب الأكبر.'),
(710, 'بَني يَعْقان', 'اسم عبري معناه ((أبناء يعقان)) وهو اسم قبيلة يرجح أنها \nمن نسل سعير الحوريّ وقد أطلق هذا الاسم على الآبار التي حلّ قربها بنو إسرائيل. \nوكانت تسمى أيضاً بئيروت. وقيل أنها البئرين الحالية على بعد 6 أميال إلى الجهة \nالجنوبية من العوجا (عد 33: 31 و 32).'),
(711, 'بَهْرَمان', 'جر كريم لونه أحمر (خر 28: 18 وحز 27: 16 و 28: 13) وبعض?: \n13) وبعضهم يقولون أن الكلمة العبرية تعني الزمرُّد.'),
(712, 'بَهيموت', '(اي 40: 15) قال البعض أنها جمع بهيمة في العبرانية. وقد \nترجمت في بعض بعض المواضع ((وحوش)) (اي 35: 11 ومز 73: 22). وزعم آخرون أنها كلمة \nمصرية قديمة معناها ((ثور الماء)). وبناء على ذلك يصح الحكم بأن بهيموث هو فرس \nالبحر الموجود قديماً في أرض مصر والآن في النيل الأعلى حيث يقضي معظم نهاره في \nالمياه وبين الأشجار، فإذا جاء الليل خرج إلى الحقول المجاورة في طلب المرعى، فأتلف \nمزروعاتها وأشجارها لما هو عليه من شدة النهم.ويصدق قول \nأيوب على هذا الحيوان أكثر مما يصدق على سواه، لأنه عظيم الحجم ضخم الجسم طوله نحو \n16 قدماً وعلوه 7 أقدام. وأما علماء اليهود فيزعمون أن بهيموث حيوان كبير الحجم ذو \nقدرة عظيمة ومنظره هائل، ومن شأنه أنه كان ولا يزال يسمّن منذ ابتداء الخليقة إلى \nمجيء المسيح، فإذا جاء قدّم عندها وليمة للمؤمنين.'),
(713, 'تانَة شيلوه', 'اسم عبري معناه (( قريبة من شيلوه )) وهي واقعة على التخوم ما بين افرايم ومنسى ( يش 16: 6 ) وهي الآن خربة تعنة، تل على بعد سبعى أميال جنوب شرقي شكيم ( نابلس ) تقريباً.'),
(714, 'تاباص', 'اسم عبري معناه (( ضياء أو بهاء )) وهي مدينة قريبة من شكيم داخلة ضمن تخوم افرايم وفي وسطها برج قوي قتل أمامه ابيمالك ( قض 9: 50-55 و 2صم 11: 21 ) ولا يزال باقياً في مدينة توباص الكبيرة وتبعد 10 أميال شمال شرقي شكيم ( نابلس ) على طريق بيسان ( انظر ابيمالك ) وتعرف الآن (( بطوباس )).'),
(715, 'تابِعَة', '( لا 20: 27 ) ( اطلب عرافة ).'),
(716, 'تابوُت', '( تك 50: 26 ) ( اطلب دفن ).'),
(717, 'تابوت العهد', '( خر 25: 10 ) صندوق صنعه موسى بأمره تعالى، طوله ذراعان ونصف وعرضه ذراع ونصف وارتفاعه ذراع ونصف. وكان مصنوعاً من خشب السنط ومغشى بصفائح ذهب نقي من داخل ومن خارج، ويحيط برأسه أكليل من ذهب فوقه غطاء من ذهب نقي. وفوق كل طرف من الغطاء كروب من ذهب يظلّل الغطاء. وعلى كل من جانبي التابوت حلقتان من ذهب لعصوي التابوت المصفحتين بالذهب لحمل التابوت. وكان المنوط بحراسته وحمله بنو فهات من اللاويين ( عد 3: 29-31 ).ويمثل الكروبان حضور الرب الذي لا يدنى منه وسكناه بين الكروبين وإعلان صوته من بينهما وهناك يجتمع مع ممثلي الشعب ( خر 25: 10-22 و 30: 6 وعد 7: 89 و 1صم 4: 4 ).وكان في التابوت الوعاء الذي يحتوي على المن، وعصا هارون التي أفرخت، ولوحا العهد، وكان عليهما وصايا الله العشر المكتوبة بإصبع الله ( خر 25: 16 و 21 وعب 9: 3 و 4 ) ثم وضع بجانبه كتاب التوراة ( تث 31: 26 ) ومن ثم يسمى التابوت أحياناً تابوت الشهادة ( خر 25: 16 و 40: 21 ) ولم يكن وعاء المن وعصا هارون في ملك سليمان ( 1مل 8: 9 ) وفوق الغطاء ظهر السحاب حيث تراءى الله ( لا 16: 2 وعد 7: 89 ) وكان في أيام التيه إذا ما رحل العبرانيون في البرية أن التابوت يحمل أمام الشعب ويتقدمه عمود السحاب نهاراً وعمود النار ليلاً. وكان إذا حمل التابوت يقول (( قم يا رب فلتتبدد أعداؤك ويهرب مبغضوك من أمامك )) وإذا حل التابوت يقول أيضاًَ (( ارجع يا رب إلى ربوات ألوف إسرائيل )) ( عد 10: 33-36 ). وعندما عبر العبرانيون الأردن حمل التابوت أمامهم إلى السماء فانشق تيار النهر فوقفت المياه المنحدرة من فوق وعبر الشعب على اليابسة ( يش 3: 14-17 ). ثم بقي مدة في الخيمة في الجلجال وبعد ذلك نقل إلى شيلوه حيث بقي بين 300 و 400 سنة ( ار 7: 12-15 ) ثم أخذ من الخيمة وحمل أمام الجيش فوقع في أيدي الفلسطينيون إلى اشدود ووضعوه بجانب صنم داجون ( 1صم 5: 2 ) غير أن الله أنزل عليهم بلايا وأمراضاً حتى اضطروا إلى إرجاعه إلى أرض العبرانيين فوضع في قرية يعاريم ( 1صم 6 و 7 ) ثم عندما سكن داود أورشليم نقل التابوت إليها على غاية من التجلة والمظاهر الدينية المناسبة، فبقس هناك إلى أن بني الهيكل ( 2صم 6: 1-15 و 1 أخبار 15: 25-29 ) ويظن أنه في أثناء ذلك كتب المزمور المائة والثاني والثلاثون ثم وضع التابوت في الهيكل ( 2 أخبار 5: 2-10 ) ووضع منسى تمثالاً منحوتاً في بيت الله وربما أزال التابوت من مكانه حتى يجد له مكاناً ( 2 أخبار 33: 7 ) غير أن يوشيا أرجعه ( 2 أخبار 35: 3 ) وسماه تابوت القدس.ولم يكن التابوت في الهيكل الثاني غير أننا لا نعلم هل أخذ عندما نهب البابليون أورشليم أو اختفى ثم فقد. ويوجد تقليد عند الاثيوبيين يفتقر إلى اثبات وهو أن تابوت العهد موجود بأكسوم بأثيوبيا.'),
(718, 'تابور', 'جبل في أرض الجليل يسمى الآن الطور يشرف على مرج ابن عمير ( عامر ) ويقع في تخوم يساكر ( يوسيفوس جزء 5: 1، 22 ) وهذا يؤيد ما جاء في يشوع 19: 22. وهو جبل شهير مع أنه أصغر من جبل حرمون ( مز 89: 12 ) يقع على بعد خمسة أميال ونصف نحو الجنوب الشرقي من الناصرة و12 ميلاً شمالاً من جبل جلبوع و12 ميلاً جنوب غربي بحيرة جنيسارات.( 1 ) تاريخه:يصلح موقع تابور لاجتماع الجيوش للقتال لكونه على مرج ابن عمير ( عامر ) وهناك جمع باراق جيشه نحو 10000 رجل لمحاربة سيسرا ( قض 4: 6-14 ) وهناك قتل المديانيون بعض مقاتلي إسرائيل قبل نصرة جدعون ( قض 8: 18 ).ذكر مع تابور كرمل ( ار 46: 18 ) وعبدوا عليه الأوثان ( هو 5: 1 ) ولم يذكر في العهد الجديد غير أنه في الجيل الثاني للميلاد تشير بعض التقاليد إلى أن هذا الجبل هو جبل التجلي ( مر 9: 2-10 ) وهذا التقليد يفتقر إلى إثبات. ووجدت على قمة هذا الجبل مدينة كان قد حاصرها انطيوخس الكبير سنة 218 قبل الميلاد واستولى عليها ثم حصنها ( بولبيوس جزء 5: 7 و 6 ) وظلت قائمة إلى عصر المسيح ثم أن يوسيفوس ضرب سوراً حولها لتحصينها ( يوسيفوس 37 وجزء 4: 1، 8 ).وبالنسبة للاعتقاد الذي ساد أن هذا الجبل هو جيل التجلي أقيمت عليه عدة كنائس منذ القرون الأولى للمسيحية. وقبل نهاية القرن السادس بنيت عليه ثلاث كنائس تذكاراً للمظال الثلاث التي طلب بطرس أن تقام هناك. وحديثاً أقيمت كنيسة فخمة للاتين على قمته.( 2 ) منظره:أن جبل تابور أحد الجبال الرائعة المنظر بين جبال فلسطين لأنه مرتفع عن السهل على هيئة ثدوية، وهو منفرد عن بقية جبال الجليل وعلوه 1375 قدماً فوق السهل و 1843 قدماً فوق سطح البحر الأبيض المتوسط. والطريق إلى قمته عسرة المسالك وقد أصبحت واجهته الجنوبية مقفرة وأما بقية جوابه فمكسوة بشجر السنديان والبطم والجوز وغيرها. وتربته مخصبة توافق المرعى ويصطاد من بين أشجاره الحجال والأرانب والثعالب وغيرها من الحيوانات والطيور حتى وبعض الضواري كالذئب والنمر. وقمة الجبل أقل من ربع ميل طولاً وثُمنه عرضاً وعليها ديران احدهما للروم والآخر للاتين.وعليها أيضاً خرب أبراج وحصون وأقبية وصهاريج وغيرها من الأبنية اليهودية واليونانية والشرقية والغربية والعثمانية. أما المنظر من رأس تابور فهو أجمل جميع مناظر فلسطين الوسطى فيظهر منه إلى الشمال والشرق جبل الشيخ وبحر الجليل وجبال حوران وجلعاد، وإلى الجنوب والغرب مرج ابن عمير ( أو عامر كما يدعى الآن ) وجلبوع والكرمل والبحر الأبيض المتوسط.( 2 ) مدينة في زبولون خصصت لبني مراري من اللاويين ( 1 أخبار 6: 77 ) ( اطلب كسلوت تابور ) وربما كان مكانها في الوقت الحاضر خربة دبورة عند سفح جبل تابور وتدعى اليوم دبورية.'),
(719, 'تاحَت', 'اسم عبري معناه (( ما هو تحت )). أحد محطات بني إسرائيل في البرية ( عد 33: 26 و 27 ) ويظن أنها عند جبل التيه بين العرب والتياهة.'),
(720, 'تاحَش', 'اسم عبري معناه (( تخس أو دلفين )). ابن لناحور من سريته رؤومة ( تك 22: 24 ).'),
(721, 'تاحَن', 'اسم عبري معناه (( معسكر )). رجل من بني افرايم ومؤسس قبيلة ( عد 26: 35 و 1 أخبار 7: 25 ).'),
(722, 'تاحِينِيُون', 'ذرية تاحن المذكور انفاً ( عد 26: 35 ).'),
(723, 'تارَح', 'اسم عبري معناه (( عنزة جبلية )) ابن ناحور وأبو إبراهيم كان يقطن اور الكلدانيين وقضى أغلب حياته فيها مؤثراً عبادة الأوثان على عبادة الله ( يش 24: 2 ). ومن بين الآلهة التي عبدها القمر حيث كان عيكل مكرساً له في اور. وقد رافق إبراهيم إلى حاران ما بين النهرين حيث توفي، وله من العمر مئتان وخمس سنين وكان عمر إبراهيم وقتئذ خمساً وسبعين سنة ( تك 11: 31 و 32 ).'),
(725, 'تاريع', '( اطلب تحريع ).'),
(726, 'تامَح', 'مؤسس أسرة النثينيم الذين رجعوا مع زربابل من السبي ( عز 2: 53 ونح 7: 55 ) ويسمّى أيضاً ثامح.'),
(727, 'تَبعِيرة', 'اسم عبري معناه (( اشتعال )) موضع في برية فران حيث تذمر بنو إسرائيل واشتعلت نار الرب وأحرقت طرف المحلة في قبروت هتاؤة، وأخمدت بتوسلات موسى ( عد 11: 1-3 وتث 9: 22 ).'),
(728, 'تِبن', 'اسم عبري معناه (( من التبن )) ابن جينة ادعى الملك وحارب عمري وقد انحاز إليه نصف الشعب مدة أربع سنوات غير أنه انهزم أخيراً ( 1مل 16: 21 و 22 ).'),
(729, 'تَتنْاي', 'احد ولاة الفرس في جنوب نهر الفرات عارض في إعادة بناء الهيكل ( عز 5: 3 و 6 و 6: 6 و 13 ).'),
(730, 'تُجّار تجارة', '( اش 23: 2 ) كانت البضائع في الاعصر الخالية تنقل من مكان إلى آخر بواسطة القوافل كما هو جار في وقتنا الحاضر، وقد جاء في الكتاب أن يوسف بيع إلى قافلة تجار. وأول تجارة سمع عنها مع سبا كانت بواسطة قوافل ترسل منها وإليها من سوريا وفلسطين ومصر غير أنه كانت هناك علاقات كثيرة أيضاً بين أمم مختلفة بواسطة الأنهر والبحار. وأول أمة اشتهرت بالتجارة كانت فينيقية، وكانت صيدون عاصمة فينيقية أولاً ثم صارت صور عاصمة البلاد. ومن رام زيادة المعرفة عن تجار فينيقية فعليه بمراجعة حز 27 و 28 وكانت تجارة المصرين أيضاً متسعة النطاق فكانوا يأتون إلى بلادهم ببضائع من الصومال وسبا والهند ويرسلون بضائعهم إلى عدة أماكن مجاورة للبحر المتوسط. وقد أشار يعقوب الرسول إلى تجار تلك الأيام الذين كانوا يتجولون من مدينة إلى أخرى طمعاً في الأرباح ( يع 4: 13 ) ( اطلب فينيقية ). وقد نشأت التجارة على هيئة ما كانت عليه في الأزمنة القديمة لما انقسم الناس إلى جماهير مختلفة وصارت معيشة أهالي المدن متوقفة على محاصيل الفلاحين وبضائع الأمم الأجنبية كما هو معلوم في زمن إبراهيم وما ورد في قصة يوسف حينما حدثت المجاعة العظيمة في مصر. ولم يكن اليهود يعتنون كثيراً بتنظيم تجارتهم مع الأمم الأجنبية، وذلك لعدم نجاحهم في عمل السفن فقد خابت مشروعات يهوشافاط التجارية ( 1 مل 22: 48 و 49 ) غير أننا نعلم أيضاً أنهم كانوا يبتاعون البضائع الأجنبية ( عز 3: 7 ونح 13: 20 و 21 ) ويرسلون بضائعهم إلى الممالك الأجنبية أيضاً كفينيقية وغيرها ( 1مل 5: 11 وحز 27: 17 واع 12: 20 ) وقد اشتهرت يافا ميناء أورشليم بتجارتها، فإن السفن كانت تقلع منها إلى موانئ عديدة ( يون 1: 3 ) أما تجارة اليهود الداخلية فكان معظمها أيام المواسم والأعياد فإنهم في تلك الأوقات كانوا يبيعون المواشي للذبيحة ويصرفون الدراهم في الهيكل وهذا ما جعل المخلص يسوع أن يقلب موائد الصيارفة ويطرد الباعة من الهيكل ( مت 21: 12 ويو 2: 14 ).'),
(731, 'تَحْتيم إلى حُدْشي', 'هي المدينة التي زارها من وكلهم داود على إحصاء بني إسرائيل قبلما أتوا إلى دان يعن واستداروا إلى صيدون ( 2صم 24: 6 ) ولا يعرف مكانها بالضبط، وربما تكون واقعة في أرض الحثيين نحو قادش، كما وردت في الترجمة اليونانية السبعينية.'),
(732, 'تحت أو تاحت', 'اسم عبري معناه (( ما هو تحت )).( 1 ) لاوي من قبيلة قورح من بيت يصهار ( 1 أخبار 6: 24 و 27 ).( 2 ) افرائمي وهو ابن برد قبيلة شوتالح ( 1 أخبار 7: 20 ).( 3 ) ابن العادا من نسل تحث بن برد ( 1 أخبار 7: 20 ).'),
(733, 'تحْريع', 'اسم عبري معناه (( مكار )) رجل من نسل شاول من جهة يوناثان ( 1 أخبار 9: 41 ) ويدعى أيضاً تاريع ( 1 أخبار 8: 35 ).'),
(734, 'تَحفنْحيس', '( حز 30: 18 ) اطلب (( تحفنيس )).'),
(735, 'تَحْفنيس', '( 1 ) امرأة فرعون ملك مصر في عصر سليمان وهي التي اقترن باختها هدد ( 1 مل 11: 18-20 ) وكان زوجها من الأسرة الثانية ةالعشرين لملوك مصر.\n( 2 ) مدينة في مصر السفلى وتدعى تحفنيس ( ار 2: 16 ) أو تحفنحيس ( ار 43: 7 و 8 و 9 و 44: 1 و 46: 14 وحز 30: 18 ) التجأ إليها اليهود هرباً من انتقام البابليين بعد قتل جدليا ( ار 43: 7-13 ) ويظهر أن اليهود اتخذوها مسكناً دائماً لهم ( ار 44: 1 و 46: 14 ) وقرئت هذه الكلمة في النسخة السبعينية تفنيس وهي بذاتها دفنة المدينة المحصنة الواقعة على الفرع البلوذي من النيل ( هيرودوت جزء 2: 30، 107 ) وربما يقصد بها تل دفنة الواقع على بعد 10 أميال غربي القنيطرة.'),
(736, 'تَحكَمُوني', 'لقب عبري بمعنى (( حكيم )) وهو لقب ليشبعام وهي محرف من حكموني ( 2 صم 23: 8 و 1 أخبار 11: 11 ) مؤسس أسرة حكموني.'),
(737, 'تَحِنَّة', 'اسم عبري معناه (( نعمة أو استغاثة )) وهو رجل من سبط يهوذا مؤسس مدينة ناحاش ويسمّى أباها وهو ابن اشتون ( 1 أخبار 4: 12 ).'),
(738, 'تَخت', '( اطلب سرير ).'),
(739, 'تُخَس', 'ترجمة للكلمة العبرية (( تحش )) وهو حيوان استعمل جلده للغطاء الخارجي لخيمة الاجتماع ( خر 25: 5 ) وتتخذ منه نعال الأحذية الراقية التي تلبسها السيدات ( حز 16: 10 ) والجلود المشار إليها في الغالب جلود الحيوانات البحرية كالدلفين والدوكونج وعجل البحر.'),
(740, 'تَحْم', 'كان بنوا إسرائيل إذا تغلبوا على أرض يقتسمونها بالقرعة جرياً على الشريعة الموسوية لكل سبط أو بيت أو فرد حسب نصيبه. وكان يفصل بين الحقل الواحد والآخر صف من الأشجار أو كومة من الحجارة توضع على زوايا الحقل. وعليه كان نقل هذه التخوم أو نزعها سهلاً للغاية ولهذا السبب كان القصاص صارماً على كل من يتعدى على تخم صاحبه ( تث 19: 14 و 27: 17 وام 23: 10 ).'),
(741, 'تَدَّاوُس', '( مت 10: 3 ) ( اطلب يهوذا ).'),
(742, 'تّدْعال', 'ملك جوييم أحد الملوك الذين تعاهدوا أن يحاربوا مع كدر لعومر ( تك 14: 1-9 ).'),
(743, 'تَدمر', 'مدينة في الصحراء ( 2 أخبار 8: 4 ) وهي قديمة جداً، وكانت من أجمل مدن العالم حصنها سليمان الحكيم لضبط طرق القوافل المارة بها. وفي 1 مل 9: 18 ورد الاسم في النص العبري بصورة (( تامار )) وفي الهامش بصورة تدمر وهي واقعة على بعد 140 ميلاً من الشمال الشرقي من مدينة دمشق و120 ميلاً من الفرات وهي خربة الآن تمتد نحو ميل ونصف. وهي واحة يحيط بها القفر من كل الجهات فيفصلها عن المعمورة من الأرض حولها، ومن سنة 251م إلى 273م  كانت تدمر مستقلة جزئياً ثم بعد وقت استقلت استقلالاً تاماً. ولما تغلب عليها اسكندر أطلق عليها اسم (( بالميرا )) أي مدينة النخل وذلك لما يكتنفها من غابات النخل العظيمة. وأطلال هذه المدينة اليوم تستحق النظر والتأمل لما هي عليه من العظمة والجمال ولما فيها من الآثار القديمة والأعمدة. ويخترق المدينة شرقاً وغرباً صف من الأعمدة ويقاطع هذا الصف صفاً آخر. وأكثر هذه العمدة قائمة إلى الآن ولم يزل كثير من أعتابها كما كان أولاً وعند طرف أطول الصفين قوس مبينة من الحجارة المنقوشة، وعند ملتقاهما أربعة أعمدة من الصخر، اثنان منهما قائمان واثنان ساقطان. وفيها عدا هذين الصفين أعمدة كثيرة وآثار هياكل وقبور مزخرفة وهيكل الشمس العظيم. وقرية تدمر الحديثة داخل أسوار هذا الهيكل وأثارها لا يضاهيها في الرونق والاتساع في كل سورية إلا بعلبك. ومياهها غزيرة تجري من تحتها في اقنية قديمة وأعظم ينابيعها يجري في قناة طبيعية تحت الجبل جنوبي المدينة. ودرجة حرارة مائها الكبريتي 880 ف. أما لقبور فأكثرها خارج المدينة وهي غاية في الإتقان بعضها محفور في الصخر تحت الأرض وبعضها مبني على هيئة أبراج. وكانت المدينة ملآنة بالتماثيل المنحوتة ونواويس فيها مومياء شبيه بما في قبور مصر. ونظراً لموقع هذه المدينة الكائن بين سورية وما بين النهرين يزعم بأنها كانت ذات أهمية تجارية قل زمن سليمان وأن سليمان استخدمها لمقاصد تجارية فقط وفي أيام الملكة زنوبيا ( زينب أو الزباء ) جعلتها حاضرة مملكتها غير أن اورليانس دمرها سنة 273 ق.م. وآثار المدينة تدل على قدم عهدها.'),
(744, 'تَراخُونِيتِس', 'اسم يوناني معناه (( أرض محجرة )) إقليم وعر كثير الصخور واقع جنوب دمشق وشرقي الأردن، ويشتمل على الصخور البركانية المسماة الآن اللجاة مع جزء من جبل الدروز ( اطلب ارجوب ). وقد أخذ هذا الإقليم هيرودوس الكبير من الإمبراطور اوغسطس بشرط أن يبيد جميع اللصوص الذين كانوا فيه فإنه كان ملآناً منهم، وبعد موته في أيام ظهور يوحنا المعمدان استولى عليه مع ايطورية ابنه فيلبس رئيس الربع أخو هيرودس رئيس ربع الجليل ( لو 3: 1 ).'),
(745, 'تَرافَيم', 'كلمة عبرية معناها (( مسعدات )) وقد وردت بدون ترجمة ( قض 17: 5 و 1 صم 15: 23 و 19: 16 ) وقد تترجم أصناماً ( تك 31: 19 و 34 و 35 ) وهي أصنام أو آلهة رب البيت وتكون صغيرة جداً لسهولة حملها في الهروب بسرعة ويمكن إخفاؤها تحت حداجة الجمل ( ثك 31: 19 و 30 و 34 ) وأكبر ما الترافيم يكون على هيئة الادميين ( 1 صم 19: 13 ) وفي الحفريات الأثرية التي أجريت لم يكتشف أية ترافيم كبيرة، وكان الناس يعتقدون أنها مجلبة للفأل وكانت تستشار في كل المقترحات ( حز 21: 21 وزك 10: 2 ). وبحسب القانون البابلي كان لمن عنده آلهة الأسرة الحق في أن يرث نصيب البكر وقد استعملها لابان في حاران وسرقت ابنته راحيل الترافيم وحملتها إلى كنعان ( تك 31: 19 و 34 ) ولم يكن ليعقوب علم بها ( تك 31: 32 ) ولما وصل يعقوب إلى شكيم أمر أهل بيته وكان من كان معه أن يعزلوا الترافيم التي بينهم ( 35: 2-4 ) وفي أيام القضاة كان لميخا الذي من جبل افرايم مذبح خاص وكاهن بافود وترافيم وتمثال منحوت وتمثال مسبوك ( قض 17: 4 و 5 و 18: 4 ) وبواسطتها كان ميخا يستشير الرب ( قض 18: 5 و 6 ) وقد حمل هذه الترافيم والأصنام جماعة من الدانين ( 18: 17-20 ) وقد أشار صموئيل أن التمرد كخطية العرافة والعناد كالوثن والترافيم ( 1 صم 15: 23 ) ومع ذلك فكان يوجد في بيت داود ترافيم لزوجته ( 1 صم 19: 13 ). ويستفاد من إشارة النبي إلى حالة اليهود ( هو 3: 4 ) بقوله سيقعدون أياماً كثيرة بلا ملك وبلا رئيس وبلا ذبيحة وبلا تمثال وبلا افود وترافيم، إن اليهود سيقعون في حالة الكفر التام بحيث لا يفقدون عبادة الله فقط بل كل دين وكل عبادة. وقد أباد يوشيا الترافيم مع غيرها من الأصنام ( 2 مل 23: 24 ) ومع ذلك فإنه وجد بين الشعب بعد رجوعه من السبي من يسأل الترافيم ( زك 10: 2 ).'),
(746, 'تَرالة', 'مدينة لسبط بنيامين واقعة بين يرفئيل وصيلع ( يش 18: 27 ).'),
(747, 'تَرتاق', 'اسم إله من آلهة العويين الذين آتى بهم شلمناصر إلى السامرة ليسكنوها عوضاً عن بني إسرائيل ( 2 مل 17: 31 ).'),
(748, 'تَرتان', 'اسم آشوري وهو لقب كان يطلق على رئيس جيش الآشوريين ( 2 مل 18: 17 و اش 20: 1 ) ويلفظ الآشوريين هذا الاسم تَرتَانو أو تُرتْانو.'),
(749, 'تَرتُلُّس', 'اسم لاتيني وهو (( تصغير ترتيوس )) محام روماني دعي من قبل اليهود للشكاية على بولس أمام فيلكس، وذلك لجهل اليهود نظام الشريعة الرومانية. وكان خطابه حقاً متأثراً بأسلوب محامِي الرومان في ذلك الوقت ( 1 ع 24: 1-28 ).'),
(750, 'تَرتِيُوس', 'اسم لاتيني معناه (( الثالث )) وهو الشخص الذي استكتبه بولس الرسول رسالته إلى أهل رومية ( رو 16: 22 ).'),
(751, 'تُرجُمان', '( اطلب نبي ).'),
(752, 'تَرْحَنَة', 'اسم عبري معناه (( انعام )) ابن كالب ابن حصرون وهو اخو برحيئيل من معكة سريته ( 1 أخبار 2: 48 ).'),
(753, 'تُرس', '( اطلب سلاح ).'),
(754, 'تَرَش', 'اسم فارسي معناه (( ثابت أو يابس )) وهو أحد الخصيين اللذين كانا يحرسان باب قصر الملك وقد نفذ فيها حكم الإعدام ( اس 2: 21 و 6: 2 ).'),
(755, 'تِرْشاثا', 'اسم فارسي معناه (( مخوف أو محترم )) وهو لقب فارسي لحاكم يهوذا في أيام الاحتلال الفارسي وقد جاء مع زربابل ( عز 2: 63 ونح 7: 65 و 70 و 8: 9 و 10: 1 ).'),
(756, 'تَرشِيش', 'اسم فينيقي معناه (( معمل للتكرير )).\n( 1 ) شعب متناسل من ياوان ( تك 10: 4 ) ويتضح من دخول يونان إلى السفينة ليهرب إلى ترشيش من يافا ( يون 1: 3 ) إنها مدينة وإن الطريق إليها كانت عبر مياه البحر الأبيض المتوسط. وكذلك كانت سفن سليمان وحيرام تأتي كل ثلاث سنوات مرة إلى ترشيش حاملة ذهباً وفضة وعاجاً وقروداً وطواويس ( 1 مل 10: 22 و2 أخبار 9: 21 ) ولما كان يقتضي لتلك السفن ثلاث سنين لكي تعود من سفرها يستنتج أن تكون ترشيش بلداً بعيداً ( اش 66: 19 ). ويعتقد أن تكون ترشيش هي ترتيوس وهي واقعة في جنوب اسبانيا قرب جبل طارق ( هيرودت جزء 4: 152 ) ولعل ترتيوس هذه هي قرطجنة المدينة الواقعة شمالي افريقيا وكانت قائمة إلى أوائل العصر المسيحي. وكانت ترشيش غنية جداً في الثروة المعدنية كالفضة المطرقة المصنوعة ألواحاً ( ار 10: 9 ) والحديد والقصدير ( حز 27: 12 ) وقد كانت سفن ترشيش مخصصة للتجارة منها وإليها. ثم بعد ذلك أصبحت سفن ترشيش من العظمة بمكان بحيث تسافر إلى كل جهة. وكانت الرياح تصدم سفن ترشيش المحملة بأثمن البضائع وتكسرها في مياه بحر الروم ( الأبيض المتوسط ) ( مز 48: 7 واش 2: 16 و23: 1 و14 و60: 9 وحز 27: 25 ) وإن يهوشافاط بنى بعضاً من هذه السفن العظيمة فكانت تسافر إلى أوفير الواقعة على فرضة عصيون جابر ( إيلات ) على خليج العقبة في البحر الأحمر ( 2 أخبار 9: 21 و1 مل 10: 22 ). أما القول سفن ترشيش يقصد منه السفن المسافرة إلى ترشيش ( 2 أخبار 20: 36 و1 مل 9: 28 و2 أخبار 9: 21 ) وبمعنى آخر ربما يقصد بسفن ترشيش سفن التكرير على شبه السفن التي تمخر عباب البحر الأبيض المتوسط ولها اتصال بالمناجم ومعامل التكرير في سردينية وفي عهد متأخر سفن جنوبي اسبانيا مع فينيقية أو ربما يكون المقصود بسفن ترشيش السفن الكبيرة ( اطلب سفينة ). \n( 2 ) رجل من بني بنيامين بن بلهان ( 1 أخبار 7: 10 ).\n( 3 ) أحد رؤساءمادي وفارس السبعة ( اس 1: 14 ).'),
(757, 'تَرشيشة', 'انظر ترشيش( 1 ).'),
(758, 'تِرْصة', 'اسم عبري معناه (( فرح وانشراح )) مدينة جميلة للغاية ( نش 6: 4 ) وكانت أصلاً للكنعانيين وهي إحدى مدنهم التي خربها يشوع بن نون وأعطاها لأسباط بني إسرائيل ( يش 12: 24 ) وكانت مركزاً لمملكة أسباط إسرائيل العشرة نحو خمسين سنة ( 1 ملو 14: 17 و15: 21 و33 و16: 6 و23 ) حتى بنى عمري السامرة ( 23 و24 ).\nويمكن أن يكون موقعها رابية التل الفارغة الكبيرة على بعد 7 أميال شمالي شرقي مدينة نابلس ( شكيم ).\n( 2 ) أصغر بنات صلحفاد الخمس ( عد 26: 33 و27: 1 ويش 17: 3 ).'),
(759, 'تَرْعاتيم', 'إحدى عشائر الكتبة اللاويين الثلاثة من القينيين من سكان يعبيص ( 1 أخبار 2: 55 ).'),
(760, 'تَرْمَة', 'وهي مدينة وجد فيها مرة أبيمالك ( قض 9: 31 ) وربما كانت نفس أرومة ( قض 9: 41 ).'),
(761, 'تُرْهاقة', '( بالمصرية تَهَرْقا ) الملك الثالث والأخير من الأسرة الخامسة والعشرين أو الأسرة الإثيوبية, لما كان ابن عشرين سنة توجه شمالاً من ناباتا لما غزا الأخير مصر وأصبح بعد ذلك ترهاقة على رأس الجيش الإثيوبي والمصري متقدماً لمحاربة الأشوريين. ولما سمع سنحاريب ملك الأشوريين سنة 701 قبل الميلاد أن ترهاقة ملك الحبشة ( إثيوبية ) قادم لمحاربته تقابل معه في معركة التكة ( 2 مل 19: 9 ) وحوالي سنة 688 قبل الميلاد أصبح ترهاقة ملكاً على مصر ( فرعون ) وفي سنة 671 قبل الميلاد اندفع ابن سنحاريب أسر حدون داخل القطر المصري وهزم ترهاقة الذي كان يدعوه ملك كوش أي ملك الحبشة ( إثيوبية ) ( 2 مل 19: 9 واش 37: 9 ) وأخذ منه مدينة منفيس وأسر ابن ترهاقة.'),
(762, 'تِرواس', 'ميناء بحرية من أعمال ميسية ( اع 16: 11 ) وفيها رأى بولس رجلاً مقدونياً في رؤيا يدعوه لزيارة أوربا ( اع 16: 8- 10 و2 كو 2: 12 ) وأقام فيها الرسول بولس أيضاً أسبوعاً حينما كان راجعاً من سفرته الثالثة ( اع 20: 6 ) وقد ترك مرة ردائه والرقوق هناك (  2 تي 4: 13 ). أسسها أنتيجنوس أحد خلفاء الإسكندر الذي سماها أنتجونا وبعد موته غيّر اسمها منافسه ليسماخوس ملك تراقية باسم الإسكندرية, وأضاف إليها ترواس تمييزاً لها من إسكندرية مصر. وهي واقعة على بعد مسافة جنوبي تروياهوبيروس التي منها أطلق اسم ترواس لهذه المقاطعة. واسمها الآن أسكي استامبول.'),
(763, 'ترُوجِيليُون', 'مدينة ورأس عال واقعة غربي شاطيء آسيا الصغرى مقابل جزيرة ساموس, أقام بولس هناك عند رجوعه من سفرته الثالثة ( اع 20: 15 ) ويسمى المرفأ في هذا المكان ميناء القديس بولس.'),
(764, 'تُروفيمُس', 'اسم يوناني معناه (( مُغذٍ )) وهو مسيحي من الأمم من أفسس وكان رفيقاً لبولس في سفراته التبشيرية, وهو الذي اتهم باطلاً أنه حضر إلى الهيكل للأزدراء بالشريعة ( اع 20: 4 و21: 26 ) وآخر ما نسمع عنه أن الرسول تركه مريضاً في مليتس ( 2 تي 4: 20 ).'),
(765, 'تَرِيفَيْنا', 'اسم يوناني معناه (( ظريفة أو لطيفة )) وهي امرأة من رومية أرسل إليها بولس تحياته موصياً إياها أن تتعب في الرب (  رو 16: 12 ).'),
(766, 'ترِيفُوسَا', 'اسم يوناني معناه (( ظريفة أو أنيقة )) وهي امرأة من رومية يلازم اسمها تريفينا في قائمة أولئك الرومانيين الذين يرسل لهم بولس الرسول تحياته وربما تكونان أختين أو شماستين وذكر أنهما تعبتا في الرب ( رو 16: 12 ).'),
(767, 'تَسالونِيكي', 'مدينة كانت حاضرة إحدى مقاطعات مكدونية وتدعى الآن سالونيك واقعة على سالونيكا, وكان اسمها أولاً ثرما ومعناه (( ينبوع ساخن )) جعلها الإسكندر الأول ابن انتيباتير مقراً لسكناه وسماها تسالونيكا باسم امرأته تسالونيك أخت إسكندر الأكبر, وكانت تسالونيكي المدينة الثانية بعد مدينة القسطنطينية بين المدن التجارية وتحررت المدينة 42 قبل الميلاد.وكان حكامها يسمون بوليترخس (( حكام المدينة )) ( اع 17: 6 )) وكان بها عدد ليس بقليل من اليهود الذين كان يقصدونها للتجارة. وكان لهم مجمع هناك وبعدما طرد الرسول بولس من فيلبي أتى إليها ليبشر بالإنجيل وجذب بولس إلى الإيمان ممن أصبحوا نواة الكنيسة المسيحية ( اع 17: 1- 13 ) وأسس هناك سنة 52 ميلادية كنيسة بمساعدة رفيقيه تيموثاوس وسلوانس ( اتس 1: 1 و2: 1 و2 ) وكتب الرسول لهذه المدينة رسالتين, وكان مساعداه ارسترخس وسكوندس وهما من تسالونيكي وآمنا على يده ( اع 20: 4 و27: 2 ).الرسالتان إلى أهل تسالونيكي:هما السفران الثالث عشر والرابع عشر حسب ترتيب أسفار العهد الجديد وكاتبها الرسول بولس. والأرجح أن رسالته الأولى هي أول رسالة كتبها قرب نهاية سنة 52 م أو في بداية سنة 52 م وهو في كورنثوس بالاشتراك مع سلوانس وتيموثاوس إلى كنيسة التسالونيكيين. والغاية منها تثبيت تابعي المسيح هناك في النعمة والقداس وتشجيعهم وحثهم على التمسك بِعُرَى الفضيلة، وطلب الحصول على الأفراح الأبدية. والإصحاح الرابع من هذه الرسالة يصف القيامة وصفاً دقيقاً جلياً يؤكد للمؤمنين أن الراقدين لم يهلكوا إنما سبقوا غيرهم وسوف يقومون في اليوم الأخير. ويأمرهم أن لا يحزنوا من جهة الراقدين كما يفعل الأمم الذين لا رجاء لهم فأنه كما المسيح مات ثم قام كذلك سيقوم جميع المؤمنين، غير أن البعض لا يموتون لأنهم يكونون أحياء عند مجيء المسيح ثانية ولكنهم لا يسبقون الراقدين لأن الراقدين في المسيح سيقومون أولاً أي قبل تغير الأحياء. وفي يوم الدينونة العظيم سينزل الرب نفسه مع جنوده المقدسين بهتاف وصوت عظيم يعقبه صوت بوق ينبه الراقدين ويدعوهم لملاقاة ربهم فيقوم هؤلاء من قبورهم ويتغير الأحياء عن شكل جسدهم.ويمكن أن تقسم محتويات الرسالة الأولى إلى أهالي تسالونيكي كما يأتي:( 1 ) تحية ص 1: 1.( 2 ) شكر لأجل نموهم الروحي ص 1: 2-10.( 3 ) دفاع الرسول عن رسالته وإرساليته ضد الهجمات اليهودية ص 2: 1-16.( 4 ) سرد بعض الحوادث ص 2: 17-ص 3: 10.أ: غياب بولس عنهم ص 2: 17-20.ب: إرسالية تيموثاوس ص 3: 1-5.ج: تقرير تيموثاوس ص 3: 11-13.( 5 ) صلاة بولس لأجلهم ص 3: 11-13.( 6 ) معالجة مشاكل التسالونيكيين ص 4: 1-5: 22.أ: بعض التوصيات الخلقية ص 4: 1-12.ب: المجيء الثاني 4: 13-5: 11.ج: حياة الكنيسة وتصرفاتها 5: 12-22.( 7 ) صلاة بولس الختامية، وخاتمة الرسالة 5: 23-28.وأما الرسالة الثانية فكتبت بعد الأولى ويظهر أن الغاية منها هو إيضاح بعض عبارات مهمة وردت في الرسالة الأولى خشي الرسول من تأويلها على غير ما يقصد بها وتحذيراً للكنيسة التسالونيكية من الرسالة المزورة باسم بولس. ولما كانت كنيسة تسالونيكي قد توهمت من الرسالة الأولى أن يقوم الرب كان قريباً جداً، بيّن لهم الرسول في الرسالة الثانية أن مجيء الرب سيكون بعد الارتداد، وأن سر الاثم سيعمل أولاً إلى أن يأتي الرب ليبيد الاثيم (( الذي مجيئه بعمل الشيطان بكل قوة وبآيات. وعجائب كاذبة وبكل خديعة الاثم )) ثم يوصيهم الرسول أن يثبتوا في الإيمان والمحبة وأن يتجنبوا جميع الذين يسلكون (( بلا ترتيب وليس حسب التعليم المعطى لهم )).ويمكن ان تقسم محتويات الرسالة الثانية إلى أهالي تسالونيكي كما ياتي:( 1 ) تحية 1: 1-2.( 2 ) شكر لأجل نموهم 1: 3-10.( 3 ) صلاة لأجلهم 1: 11-12.( 4 ) تعاليم من جهة المجيء الثاني 2: 1-12.أ: إنه ليس عاجلاً 2: 1-4.ب: الحوادث التي تسبقه 2: 5-12.( 5 ) قصد الله الأزلي 2: 13-17.( 6 ) نصائح متنوعة 3: 1-15.أ: طلب لأجل الصلاة 3: 1-2.ب: كلمة تشجيع 3: 3-5.ج: حث على العمل 3: 6-15.( 7 ) صلاة ختامية، التوقيع، والبركة 3: 16-18.'),
(768, 'تَشْبِي', 'أحد أفراد أسرة أو أهالي قرية تسمّى (( تشبه )) ( 1 مل 17: 1 ) ولا نعرف أسرة بهذا الاسم وفي كتاب طوبيا ( 1: 2 ) تشبي هي في جنوبي كيديوس من نفتاليم في الجليل فوق اشير وكديوس في قادش. ويلاحظ من النسخة السبعينية ومن تاريخ يوسيفوس أن النص الوارد في 1 مل 17: 1 يبين أن تشبه واقعة في شرقي الأردن في جلعاد. وهي القرية التي ولد فيها إيليا المعروف بالتشبي.'),
(769, 'تَعْنَك', 'اسم كنعاني معناه (( أرض رملية )) مدينة للكنعانيين ذكرت نحو عام 1600 قبل الميلاد عندما تقدم تحتمس الثالث ضد مجدو. انهزم ملكها وذبحه يشوع بن نون ( يش 12: 21 ) وهي تقع في حدود يساكر ولكنها أعطيت لمنسى ( يش 17: 11 و 1 أخبار 7: 29 ) ثم للاويين ( يش  21: 15 ) ولم يقو المنسيون على طرد الكنعانيين الساكنين فيها ولكنهم فرضوا أخيراً عليهم ضريبة ( قض 5: 19 ) وكانت هذه المدينة جزءاً من دوائر سليمان ( 1 مل 4: 12 ) المعدة لمؤازرة مائدة الملك وربما هي عانير ( 1 أخبار 6: 70 ) وتل تعنك هو موضع المدينة القديمة ويقع بين التلال المنخفضة على الطرف الجنوبي من سهل يزرعيل 5 أميال نحو الجنوب الشرقي لمجدو القديمة.'),
(770, 'تَغْلَث فَلاَسِر', 'بلأشورية تُكلتي بل إيشارا معناه (( ثقتي ابن إشارة )) هو ملك أشور ولم يكن إلا اسم آخر لغول, والمرجح أن فول هو اسمه الأصلي ولما ارتقى العرش اتخذ له لقب تغلث فلاسر العظيم. ففي حوالي سنة 743 قبل الميلاد وجد فرصة ليرسل أو يقود حملات ليوقع الرعب في الأقاليم ثم احتل أرض إسرائيل دفع الضريبة له فثبت ملكه له وترك أرض إسرائيل ورجع إلى بلاده. وفي سنة 734 قبل الميلاد صار تحالف مع فقح ورصين ضد أحاز ملك يهوذا فدعاه أحاز ليساعده ضدهما رغم تحذير أشعياء النبي له وقدم لتغلث فلاسر مالاً وفيراً ( 2 مل 16 : 7 و 8 ) وانتهز فرصة امتداد سلطانه ليكون مع أحاز وسار معه ضد فلسطين وحاصر مدنها على الساحل الفينيقي. \nوسقطت غزة سنة 734 قبل الميلاد وعاقب الفلسطينيين الذين اغتنموا فرصة مضايقة يهوذا لينبهوا ( 2 أخبار 28: 18 ) وفي أثناء تقدمه نحو الجنوب وهو راجع من الشمال أخذ عيون وابل بيت معكة ويانوح وقادش وحاصور وجلعاد والجليل وكل أرض نفتالي وحمل الأسرة إلى أشور ( 2 مل 15: 29 ). وفي ذلك الزمان حول الرب روح فول ملك الآشوريين أو تغلث فلاسر ضريبة على ملوك كثيرين كان من بينهم ملوك آمون وموآب ويهوآحاز ملك يهوذا وتتفق هذه الحوادث مع ما جاء في الكتاب فان أحاز الملك ذهب إلى دمشق ليقابل تغلث فلاسر ( 2 مل 16: 10 ). وفي سنة 730 قبل الميلاد سقطت دمشق في يديه ومات في العاشر من شهر طيبت سنة 728 قبل الميلاد بعد أن أقام مملكة عظيمة ومجيدة لم يعرف مثلها في حكم الملوك السابقين.'),
(771, 'تُفْتَة', '( اطلب توفة ).'),
(772, 'تُفّاح', 'اسم هذه الفاكهة في العبرانية (( تبوح )) ( نش 2: 3 و 5 و 7: 8 و 8 : 5 وام 25: 11 ويؤ 1: 12 ) وهذا الاسم يفيد معنى الرائحة الذكية وهو من أخص صفات التفاح فإنها منعشة للمعيي والمريض ومفرح للجميع، ولا يعتد برأي من أراد أن يترجم اللفظة العبرانية بالمشمش أو الرمان لزعمهم أن التفاح قليل الوجود في المشرق وأنواعه غير معتبرة. ويُذكر شجر التفاح بين فواكه فلسطين في يؤ 1: 12 وتوجد بلاد تُسمّى تفوح في الأراضي المنفخضة ( يش 15: 34 ) وقرب حبرون ( 35 ) وعلى حدود افرايم ومنسى ( يش 17: 8 ) حيث تنمو أشجار التفاح هناك. واسم التفاح باللاتينية Pyrus malus.'),
(773, 'تَفْسح أو تفساح', 'اسم عبري معناه (( مخاضة أو ممر )) وهي مدينة كانت آخر حدود أملاك سليمان في اتجاه الفرات ( 1 مل 4: 24 ) وهي بذاتها ثبتكس الواقعة على الضفة الغربية للفرات فوق مصب بليخ، وكانت من أهم الممرات في المجرى الأوسط للفرات. وكانت تُعرَف تحت حكم السلوقين حيث كانت جيوش كورش الصغير واسكندر الأكبر تستعمل هذه المخاضة. واسمها الآن دبسة.'),
(774, 'تَفْصَح أو تِفْساح', 'مكان ضرب سكانه منحيم بوحشية قاسية ( 2 مل 15: 16 ) وذكر مع ترصة التي يظهر أنها ليست بعيدة عنه ويظن العالم كوندر أنها موضع تفسح الحديثة الواقعة على بعد نحو ستة أميال وتصف جنوبي غربي نابلس ( شكيم ).'),
(775, 'تَفُّوح', 'اسم عبري معناه (( تفاحة )) وهي اسم:( 1 ) مدينة في الأرض السفلى من يهوذا ( يش 15: 34 ) ويرجّح أن تكون بيت نتيف.( 2 ) مدينة لافرايم غربي شكيم على حدود افرايم اش 16: 8 و 17: 7 و 8 وربما تكون هي بذاتها شيخ أو زرد قرب ياسوف الجديدة وتبعد نحو 8 أميال جنوبي شكيم ( نابلس ).( 3 ) ابن حبرون ( 1 أخبار 2: 43 ) من نسل يهوذا.'),
(776, 'تِقْوَة', 'اسم عبري معناه (( انتظار أو أمل )).( 1 ) أبو شالوم زوج خلدة النبية ( 2 مل 22: 14 ) ويدعى أيضاً توقهة ( 2 أخبار 34: 22 ).( 2 ) أو يحزيا ( عز 10: 15 ).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(777, 'تقُوع', 'اسم عبري ربما يكون معناه (( نصب الخيام )) مدينة في يهوذا ( 1 أخبار 2: 24 و 4: 5 ) في البرية في اتجاه عين جدي ( 2 أخبار 20: 2 و 10 ) ويظهر أن الرعاة كانت تقصد هذه البقعة بدليل أنها كانت موطن عاموس النبي الذي كان بين الرعاة. وإلى تقوع هذه أرسل يوآب وأخذ امرأة حكيمة وجعل في فمها كلاماً لتكلم به داود بشأن أبشالوم ( 2 صم 14: 2 - 20 ) وقد بناها رحبعام ( 2 أخبار 11: 6 ) وفي عصر نحميا ساعد شعب تقوع في إعادة بناء سور أورشليم، بينما كان أعيان تلك الجهة يظهرون عدة مبالاة بالعمل ( نح 3: 5 و 27 ) ولا يزال اسم تقوع باقياً للآن وعي عبارة عن قرية خربة تبعد نحو 6 أميال جنوب شرقي بيت لحم تحتوي على تل عريض القمة حيث توجد بقايا أساسات منازل من حجارة مربعة وخرائب تشغل مساحة أربعة أو خمسة فدادين.'),
(778, 'بِرية تَقوع', 'كانت مجاورة لتقوع وهي قسم من برية يهوذا وفيها هزم يهوشافاط بني عمون والموآبيين ( 2 أخبار 20: 20 - 30 ).'),
(779, 'تقو عيّون', 'هم سكان تقوع المذكورة آنفاً ( 2 صم 23: 26 و 1 أخبار 11: 28 و 27 : 9 ونح 3: 27 ).'),
(780, 'تَقَيْل', '( دا 5: 25 ) ( اطلب منا ).'),
(781, 'تَلاسَّار تَلَسَّار', 'اسم آشوري معناه (( تل آشور )) اسم مكان كان يقطنه بنو عدن وتغلب عليه الآشوريون ( 2 مل 19: 12 واش 37: 12 ) يكّون إحدى مدن بيت اديني، مملكة صغيرة على الجزء الأعلى من الفرات.'),
(782, 'تَلَح', 'اسم عبري معناه (( شجة أو كسرة )) ورجل من بني افرايم ويرجّح أن يكون من جهة بريعة ( 1 أخبار 7: 25 ).'),
(783, 'تِلَسَّار', '( اطلب تلاسار ).'),
(784, 'تَل أبيب', 'اسم بابلي معناه (( كومة أو تل سنابل القمح )) قرية في بابل عند نهر خابور أو كبار سكنها حزقيال النبي مع اليهود المسبيين ( حز 3: 15 ) ويظن أن موقعها كان في موقع تل أبان الحديثة.'),
(785, 'تَل حخيلة أو اكمة الحكيلة', 'اسم عبري معناه (( التل المظلم )). تل في نصيب يهوذا في ربية زيف اختفى فيه داود هارباً من شاول ومعه 600 من رجاله، وهناك عسكر شاول بعد ذلك حينما عزم في نفسه على مطارة داود ( 1 صم 26: 1 - 3 ) وهو واقع جنوبي شرقي حبرون وعلى الجنوب من الصحراء ليس بعيد من معون ( 1 صم 23: 19 و 24 - 26 ).'),
(786, 'تَل حوشا', 'اسم عبري معناه (( تل السكوت أو السحر أو تل الصانع )). اسم مكان في بابل صعد منه قسم من اليهود المنفيين هناك مع زربابل إلى أورشليم ( عز 2: 59 وتح 7: 61 ).'),
(787, 'تَل القُلَف', 'في وادي يزرعيل لجهة الشمال من ينبوع حرود ( قض 7: 1 ) ومن المحتمل أن يكون جبل داحي أو حرمون الصغير نحو 8 أميال إلى الشمال الغربي من جبل جلبوع وميل واحد جنوبي نايين ( انظر أيضاً مورة ).'),
(788, 'تَل مِلْح', 'اسم عبري معناه (( تل الملح )) مكان في بابل رجع منه اليهود المسبيين هناك مع زر بابل ( عز 2: 59 نح 7: 61 ).'),
(789, 'تَلْم، أتلام', 'الأخاديد الباقية بعد مرور المحراث في الأرض ( 1 صم 14: 14 واي 39: 10 ومز 65: 10 و 129: 3 ) وقول أيوب تباكت أتلامها ( أي 31: 38 ) وهذا كلام مجازي يدل على جرح الاحساسات من الظلم.'),
(790, 'تَلْماي', 'اسم سامي معناه (( خاص بالأخاديد أو الحرَّاث )) ويرجّح أن تكون مشتقة من الكلمة الحورية (( تلماً )) ومعناها (( كبير )).( 1 ) ابن عناق ويرجح أن يكون مؤسس قبيلة العناقيين ( عد 13: 22 ) طرده كالب من حبرون ( يش 15: 14 وقض 1: 10 ).( 2 ) ملك جشور وابنته معكة كانت إحدى زوجات داود وهي أم أبشالوم ( 2 صم 3: 3 و 13: 37 ).'),
(791, 'تِلمِيذ', 'تشير هذه الكلمة في الكتاب المقدس إل كل من اتبع معلماً مثل أشعياء النبي ( اش 8: 16 ) ويوحنا المعمدان ( مت 9: 14 ). وتستعمل لكل المؤمنين الذين قبلوا تعاليم المسيح ( مت 10: 42 ولو 14: 26 و 27 و 33 ويو 4: 1 و 6: 66 ) وبنوع أخص من الرسل الاثني عشر ( مت 5: 1 و 8: 23 و 10: 1 و 12: 1 إلخ ) ( اطلب مدرسة ).'),
(792, 'التَلمُود', 'اسم عبري معناه (( تعليم )). يقسم هذا الكتاب إلى قسمين (( المشنة )) وهو الموضوع (( والجمارة )) وهي التفسير. فالمشنة (( التكرار )) عبارة عن مجموعة من تقاليد اليهود المختلفة مع بعض الآيات من الكتاب المقدس. واليهود يزعمون بأن هذه التقاليد أعطيت لموسى حين كان على الجبل ثم تداولها هرون واليعازر ويشوع وسلموها للأنبياء، ثم انتقلت عن الأنبياء إلى أعضاء المجمع العظيم وخلفائهم حتى القرن الثاني بعد المسيح حينما جمعها الحاخام يهوذا وكتبها. ومن ثم صار هذا الشخص يعتبر عندهم جامعاً للمشنة والجمارة (( التعليم )) وهي مجموعة من المناظرات والتعاليم والتفاسير التي جرت في المدارس العالية بعد انتهاء المشنة. والتفاسير المسطرة مع المشنة نوعان يعرف أولهما بلتمود أورشليم وقد كتب بين القرن الثالث والخامس والذين كتبوه هم حاخمو طبرية، ويعرف الثاني بتلمود بابل وقد كتب في القرن الخامس. والتلمود يساعدنا كثيراً في درس تعاليم المسيح فإنه يفسر بعض الإشارات والاستعارات الموجودة فيها، مثلاً غسل الأيدي وقال المسيح للفريسيين أنهم يبطلون كلام الله بتقليدهم ( مر 7: 1 - 13 ).'),
(793, 'تَمور', 'إله الباليين الذين يسمونه دوموزي وكان اسمه الكامل بالشمومرية دوموزيد ابزو ( الابن الأمين لمياه المحيط الجنوفي ) ويسم أيضاً في كتب العبادة الشومرية ساتاران ( رب الشفاء ).\nوكان البابليون والآشورين والفينيقيون والفلسطينيون يعبدون تموز وأطلق اسمه على الشهر الرابع من السنة السامية ( اطلب سنة ).\nوكان تموز زوجاً لأخته الإلهة عشتر ( عشتاروت ) وملكاً في الأرض السفلية كما أنه كان إله المرعى وحامي القطعان وحارسها، ومن ثم لقب بالراعي وكانوا يتصورون أنه يموت سنوياً ثم يعود إلى الحياة مع السنة الجديدة. وتقول الأسطورة أنه بينما كان يموت في الأرض السفلية توقفت الحياة على ظهر الأرض ثم على ظهر الأرض ثم اخترقت عشتر الدنيا السفلية ومنحته الشفاء. وترمز هذه الأسطورة إلى موت النباتات في حرارة الصيف وعودتها إلى الحياة في الربيع. ورأى حزقيال في رؤياه أن النساء اليهوديات كن جالسات على الباب الشمالي من الهيكل نائحات على الإله تموز ( حز 8: 14 ). وأشار كيرلس الأسكندري وايرونيموس أن السوريين كانوا يحتفلون سنوياً بتقديم العبادة في شهر يونية لأدونيس، وكانت النساء تنوح على موته وهم يحتفلون بعودته إلى الحياة ويدعى الشهر البابلي الرابع ( يونيو ويوليو ) تموز باسم هذا الإله.'),
(794, 'تُميم', 'خر 28: 30 ( اطلب أوريم ).'),
(795, 'تِمنَاع', 'اسم عبري معناه (( صدٌّ )).( 1 ) ابنة سعير وأخت لوطان أصبحت سرية لاليفا ابن عيسو عمليق ( تلك 36: 12 و 22 و 1 أخبار 1: 39 ).( 2 ) اسم أمير من أمراء أدوم ( تك 36: 40 و 1 أخبار 1: 16 و 15 ).'),
(796, 'تِمْنة', 'اسم عبري معناه (( القسم المعين )).( 1 ) مدينة في جبال يهوذا إلى جنوبي حبرون ( يش 15: 57 ) وربما يكون هذا هو المكان الذي التقى فيه يهوذا بكنته ثامار ( تك 38: 12 - 14 ) وكانت قريبة من جبعة واسمها الحديث تبنة وتتصل بعدلام وعينايم على بعد 4 أميال شرقي بيت نتيف.( 2 ) مدينة على حدود الأراضي التي خضعت ليهوذا وليست بعيدة عن بيت شمس ( يش 15: 10 ) وأعطيت بعد ذلك لسبط دان ( يش 19: 43 ) وكان يقطنها الفلسطينيون وهي بلدة امرأة شمشون ( قض 14: 2 ) وأخذها أيضاً الفلسطينيون في حكم أحاز ( 2 أخبار 28: 18 ) وهي خربة تدعى تبنة وتقع على بعد نحو 3 أميال جنوب غربي بيت شمس.( 3 ) مدينة قريبة من بيت آيل، وبنى السوريون حصناً فيها في أيام يوناثان المكابي ( 1 مكا 9: 50 ) وربما هي نفس تمنة سارح.'),
(797, 'تِمْنَة حارس', '( اطلب تمنة سارح ).'),
(798, 'تِمْنة سارح', 'اسم عبري معناه (( نصيب وافر )) وتمنة حارس (( نصيب من الشمس )) ( قض 2: 9 ) وهي مدينة على جبل أفرايم في الجانب الشمالي من جبل جاعش أعطيت ليشوع فبناها ودفن فيها ( يش 19: 50 و 24: 30 ) ويرجح أن تكون هي تبنة التي تقع على مسافة 12 ميلاً شمال شرقي اللد.'),
(799, 'تِمْني', 'أحد سكان تمنة حمو شمشون ( قض 15: 6 ).'),
(800, 'تَنْحُومَث', 'اسم عبري معناه (( تعزية )) تنحومث النطوفاني أبو أحد الرؤساء الذي كانوا تحت وكالة جدليا ( 2 مل 25: 23 وار 40: 8 ).'),
(801, 'تَنّور', 'اللفظ العبري لتنور شبيه باللفظ العربي. والتنور ( جمعه تنانير ) عبارة عن بناء من الحجر أو الفخار أو اللبن يخبز فيه الخبز ( خر 8: 3 ولا 2: 4 ) وأحياناً ما كان من الممكن أن يحمل تنور صغير يرجح أنه كان مصنوعاً من الفخار من مكان إلى آخر ( تك 15: 17 ) وأحياناً ما كانت النساء من عدة أسر يستخدمن تنوراً واحداً كبيراً ( لا 26: 26 ).وكان التنور يوقد بعيدان من الحطب أو العشب المجفف ( مت 6: 30 ) أما داخل التنور فأسود بسبب الدخان الصاعد منه ( انظر مراثي 5: 10 ) وتشير حرارة التنور واشتعال اللهب فيه إلى الغضب ( هو 7: 6 ) وإلى الشهوة ( هو 7: 4 ) وإلى هلاك الناس ( هو 7: 7 ) وإلى دينونة الله ( مز 21: 9 واش 31: 9 وملا 4: 1 ).'),
(802, 'تَنّين', '( اي 7: 12 ) جاء في الكتاب المقدس أن الله خلق في اليوم الخامس التنانين ( تك 1: 21 ) وهذه الكلمة لا تشير إلى جنس مخصوص من الحيوانات إنما يقصد بها أكبر الزحافات سواء أكانت برية أم بحرية ( اطلب يونان ) ويعني بالتنين في العهد الجديد (( الحية القديمة المدعوة الشيطان )) ( رؤ 12: 3 و 20: 2 ) ويصور التنين رمزياً بلون أحمر له سبعة رؤوس وعشرة قرون وعلى رؤوسه سبعة تيجان وذنبه طويل وله فم كبير قادر أن يلقي منه ماء نهر وراء من يريد إهلاكه ( رؤ 12: 13 - 16 وص 16: 13 ) وطرح من السماء إلى الأرض حيث اضطهد الكنسية وأخيراً قبض عليه وسجن في الهاوية ( 12: 7 - 17 و 20: 3 ) ويشبه التنين في بعض الوجوه الوحش الوارد ذكره في ص 13 وهذا الوحش مركب من أربعة وحوش كالتي ذكرها دانيال وتمثل الأربعة اتحاد أربع قوات على الأرض ضد مملكة الله ( دا 7: 7 ).'),
(803, 'تاب يَتوب توبة', '( حز 14: 6 ومت 9: 13 ) أول التوبة تغيير في الفكر يصحبه أسف وندامة على عمل شيء ما كان يتمنى عامله عدم وقوعه ولكنه يمكن وقوع الندامة لسبب نتائج الخطية بدون قصد تركها كما جاء عن يهوذا أنه ندم على ما عمل ( مت 27: 3 ) وكما ورد أيضاً في عب 12: 17 أن عيسو لم يجد مكاناً للتوبة مع أنه طلبها بدموع وذلك بعد حادثة مباركة اسحق ليعقوب دونه ( تك 27: 24 - 40 ) وأما التوبة للحياة فهي الحزن والندامة على ارتكاب الشر والابتعاد عن الخطية وبغضها وبذل الجهد في الاتكال التام على نعمة الله ومساعدة الروح القدس للابتعاد عنها والانقياد إلى مشيئة الله والخضوع لأوامره الطاهرة ( مت 3: 8 واع 5: 31 و 11: 18 و 2 كو 7: 8 - 10 و 2 تي 2: 25 ) وهذه التوبة التي تنال مغفرة الخطايا باستحقاق يسوع المسيح.'),
(804, 'تُوبال', 'هو خامس أولاد يافث ( تك 10: 2 ) وقد ذكر مع ياوان ( اش 66: 19 ) ومع ماشك في تجارة الرقيق والأوعية النحاسية في أسواق صور ( حز 27: 13 و 32: 26 ) وكان جوج أسيراً لماش وتوبال ( حز 38: 2 و 3 و 39: 1 ) ويرجح أن ذريته كانت تقطن البلاد الواقعة في شرق آسيا الصغرى.'),
(805, 'تُوبال قايين', 'اسم معناه (( ضرب مطرقة الحداد )) ابن لاماك من امرأته صلَّة، وكان حداداً ضارب كل آلة من نحاس وحديد ( تك 4: 22 ).'),
(806, 'تَاج', 'أو إكليل كان التاج في الأزمنة الغابرة لباساً للرأس مزخرفاً منسوجاً علامة السلطان أو الاحترام وكان غالباً مخصصاً للملوك والأمراء يضعونه على رؤوسهم في أوقات الاحتفالات ( 2 أخبار 23: 11 ) وربما صنع الملك الواحد لنفسه تيجاناً بقدر عدد الممالك الخاضعة له ( رؤ 19: 12 ) وقد استعملت كلمة تاج أو إكليل على سبيل الاستعارة بمعنى الشرف ( ام 12: 4 ) والظفر ( مراثي 5: 16 ) والحياة الأبدية والمجد ( 1 بط 5: 4 ).(1) التاج الملوكي عبارة عن دائرة من الذهب (مز 21: 3) وكان يلصق به الجواهر (2 صم 12: 30 وزك 9: 16) وأحياناً كانت تجمع التيجان جملة مع بعضها البعض (زك 6 : 11 و 1 مكا 11 : 13). ولما حارب داود في ربة بني عمون أخذ تاج ملكهم وكان  خاصاً بالضم مولوك لأنهم كانوا يخرجون به إلى ساحة القتال (2 صم 12 : 30). وأما لفظة وزن في هذه الآية فتشير إلى قيمة التاج وليس إلى ثقله, ثم تفننوا في عمل التيجان واشكالها وصاروا يزينونها بالحجارة الكريمة, وكانت الملكات تتوج بها ايضاً(اس 2 : 17). وكان غطاء الرأس العادي لملوك الفرس (اس 1 : 11) عبارة عن قبعة جامدة ربما تكون من القماش يحيط بها شريط أزرق وأبيض. وكان تاج الملك الآشوري مخروطي الشكل يشبه الطربوش تقريباً مزين بأشرطة مشغولة من ذهب, ومرصع بالاحجار الكريمة. وكان في مصر تاجان : احدهما لمصر العليا وكان عالياً مستديراً أبيض دقيق الطرف ينتهي بقبيبة مستديرة والثاني كان تاج مصر السفلى وهو مثل قبعة حمراء مسطحة ترتفع من الخلف إلى نقطة عالية وحافته مموجة ذات نتوء تنزل على الجبهة من الامام. ولما اتحدت المملكتان تحت حكم واحد كان الملك يرتدي التاجين متحدين, وكان تاج مصر السفلى يعلو تاج مصر العليا. وكان اللباس الملوكي الذي يلبس فوق الجبهة يحمل الحية المقدسة اوراوس عند قدماء المصريين والتي ترمز إلى الملك والسلطان. وكان شكل التاج المألوف عند اليونانيين والرومان والشعوب الخاضعة لسلطانهم عبارة عن تاج متألق, وقد وضع جيش الرومان تاجاً من الشوك على هامة المسيح بقصد مضاعفة تعذيبه والاستهزاء به لادعائه الملك (مت 27 : 29).(2) تاج رئيس الكهنة ويحتوي على لوحة من الذهب (لا 8 : 9) نقش عليها ((قدس يهوه))  ومربوطة بخيط من القماش بلون أزرق تكون قدام العمامة (خر 28 : 36 و 37 و 29 : 6).(3) تاج الأنتصار (2 تي 2 : 5 و 4 : 8 وعب 2 : 9) ويكون اكليلاً من الأوراق أو المعدن وكان ينقش على تيجان الأمراء والأبطال ما يدل على أعمالهم العظيمة وخدماتهم الصادقة, وكان الأكليل يعطى لمن يحوز قصب السبق في الألعاب العامة أو لمن يجاهد الجهاد الحسن في الحروب (2 تي 4 : 7 و 8).(4) وجرت العادة قديماً أن يتوج العروسان (نش 3 : 11 وحز 16 : 12) ولا تزال هذه العادة مرعية في الكنائس الشرقية القديمة إذ يلبس العريس والعروس اكليلاً أثناء حفلة الزفاف.'),
(807, 'تُوجَرْمة ', 'إقليم في أقاصي الشمال (حز 38 : 6) سكنه قوم من بني يافث من جهة جومر (تك 10 : 3) وكانت تجارتهم مع صور الخيول والبغال (حز 27 : 14) وكانت توجرمة تقع في الغرب الجنوبي من بلاد أرمينيا .'),
(808, 'تُوح ', 'وكان لاوياً من بني قهات (1 أخبار 6 : 34) ويدعى أيضاً توحو (1 صم 1 : 1).'),
(809, 'تُوعو توعي ', 'ملك حماه وربما كان حثياً وقد اشتبك في الحرب مع هدد عزر ملك صوبة وأرسل ليهنىء داود على إنتصاره على عدوه (2 صم 8 : 9 و 10) ويدعى أيضاً توعي (1 أخبار 18 : 9 - 12).'),
(810, 'تُوفة ', 'وكتبت أيضاً تفتة (اش 30 : 33) (تحليلها غير مؤكد ربما تعني هذه الكلمة البصاق أو مكان البغضة أو تكون مأخوذة من الآرامية ومعناها ((مكان الحريق)) مكان في وادي ابن هنوم وهو وادي الربابة الحالي, وكان قد اعتاد العبرانيون في أيام اشعياء واراميا أن يحرقوا هناك أولادهم وبناتهم في النار (ار 7 : 31 و 32) تقدمة للاله مولوك (2 مل 23 : 10) وكان في توفة جحر عميق واسع يجمع فيه الخشب وتشعل فيه النيران ودنسه يوشيا (2 مل 23 : 10) وتنبأ ارميا أن عدداً من الشعب سيقتل هناك وإن اسم توفة سيختفي والوادي الذي تقع فيه يسمّى وادي الذبح (ار 7 : 32 و 33 و 19 : 6) (اطلب وادي هنوم).'),
(811, 'تُوفل ', 'اسم عبري معناه ((كلسي)) مكان ربما كان ذات مرة محطة لبني اسرائيل في البرية (تث 1 : 1) ويقول بعضهم أنه طفيلة التي تقع على مسافة 14 ميلاً جنوب شرقي البحر الميت .'),
(812, 'تُوقهة ', 'وهو نفس تقوه (1).'),
(813, 'تُوكَن ', 'اسم عبري معناه ((مقباس أو وزن)) مدينة في ملك بني شمعون (1 أخبار 4 : 32) ولا يعرف مكانها الآن على التحقيق .'),
(814, 'تُولاد ', 'اسم عبري معناه ((ولادة)) مدينة في جنوبي يهوذا (1 أخبار 4 : 29) اطلب كلمة التولد .'),
(815, 'تُولاع ', 'اسم عبري معناه ((دودة أو قماش قرمزي)) بكر يساكر جد التولاعيين (تك 46 : 13 وعد 26 : 23 و 1 أخبار 7 : 1).'),
(816, 'تُولع ', 'في العبري نفس الاسم المذكور آنفاً وهو ابن فواة من سبط يساكر وكان قاضياً لبني إسرائيل خلفاً لابيما لك وقضى 23 سنة وعاش ومات ودفن في شام على جبل افرايم (قض 10 : 1 و 2).'),
(817, 'تُوما ', 'اسم آرامي معناه ((توأم)) احد ألاثني عشر رسولاً  (مت 10 : 3) وكان يسمى التوأم والظاهر انه كان ذا مزاج سوداوي وبعد ما ذهب المسيح إلى اليهودية لما هددوه برجمه بالحجارة (يو 11 : 7 و 8) فلمحبة توما له قال ((للتلاميذ لنذهب لنموت معه)) (عدد 16) وعندما قال المسيح ((أنا ذاهب لأُعد مكاناً)) وعندما قال أيضاً لهم ((أنتم تعلمون إلى أين أذهب وتعرفون الطريق)) قال توما ((نحن لا نعلم إلى أين تذهب فكيف نقدر أن نعرف الطريق .))   فأجاب يسوع بكلماته الحلوة المعروفة ((أنا هو الطريق والحق والحياة))  (يو 14 : 1 - 6) ولم يكن توما في الاجتماع الأول لما حظي التلاميذ برؤية الرب وبعد قيامته من الأموات فقال توما ((إن لم أبصر في يديه أثر المسامير وأضع إصبعي في أثر المسامير وأضع يدي في جنبه لا أومن)) (يو 20 : 24 و 25) وقد أطلق عليه بعد هذه الحادثة توما المتشكك . ويقول اغسطينوس أنه شك على أنه لا يجب أن نشك نحن . وبعد ثمانية أيام أراه المسيح الجروح التي في يده وجنبه فقال ((ربي و إلهي)) (يو 20 : 29) وكان على بحر الجليل مع ستة آخرين من التلاميذ لما أصلح شباكهم يسوع (يو 21 : 1 - 8) وكان مع البقية في العلية في أورشليم بعد الصعود (أع 1 : 13) ويفيد التقليد أن توما كان بعد ذلك عاملاً في برثيا والفرس, ويظن أن الرسول توما بشر في الهند إلى أن مات شهيداً . ويوجد مكان قرب مدراس يسمّى الآن جبل القديس توما وإلى الآن لا يزال كثيرون في الشرق يدعون أنهم من مسيحي الكنائس التي اسسها هذا الرسول ولا سيما سكان الملبار بالهند, وهم مسيحيون يتبعون طقس الكنيسة السريانية . وقد اكتشف في نج حمادي بصعيد مصر مخطوطات غنوسية مكتوبة باللغة القبطية وجدت سنة 1945 ومن ضمنها نسخة من انجيل ابو كريفا يدعى ((انجيل توما)) و الاعتقاد العام عند العلماء أن نسبته إلى الرسول غير صحيحة وأنه من كتابات الغنوسيين, وهذه المخطوطة ترجع إلى القرن الخامس ميلادي .'),
(818, 'تِيخيو كُس ', 'اسم يوناني معناه ((محصن)) وهو مسيحي من ولاية آسيا وسافر مع آخرين لما تقدم بولس من مقدونية إلى تر واس وكان اخاً محبوباً, وخادماً أميناً للرب . وأرسله بولس ليحمل الرسائل إلى افسس و كولوسي (أف 6 : 21 و كو 4 : 7) واقترح بولس أيضاً أن يرسله إلى كريت (تي 3 : 12) ثم أخيراً أرسله بولس إلى افسس (2 تي 4 : 12).'),
(819, 'تِيراس ', 'ابن يافس (نك 10 : و أخبار 1 : 5) وربما كان نسله الترسنيون و كانوا يحتلون جزيرة وأراضي واقعة على ساحل بحر ايجية (راجع تاريخ هيرودوت جزء 1 : 57 و 94) ولعلهم قراصنة تروشا الذين غزوا مصر وسوريا في القرن الثالث عشر قبل الميلاد .'),
(820, 'تِيرا ُّنس ', 'اسم لاتيني معناه ((حاكم مطلق أو طاغية)) وهو رجل من افسس يرّجح أنه كان معلماً للفلسفة أو الخطابة أو ربما كاتباً يهودياً ويعلم القانون في مدرسته . وكان بولس يحتاج كل يوم لمدة سنتين لنشر المسيحية بعد أن أخفق أن يتم ذلك في مجمع اليهود (أع 19 : 9).'),
(821, 'تِيريا ', 'ابن يهللئيل وهو رجل من نسل يهوذا (1 أخبار 4 : 16).'),
(822, 'التيصي ', 'لقب ليوحا أحد أبطال داود (1 أخبار 11 : 45) ولعل نسبته راجعة إلى اسم البلد التي هو من أهلها.'),
(823, 'تِيطس ', 'رفيق مؤتمن لبولس وعامل معه (2 كو 8 : 6 و 16 و 23) ولد من أبوين أميين (غلا 2 : 3) لم يذكر اسمه في لأعمال الرسل ولكن الرسول أشار إليه في رسائله و كان أحد المندوبين من إنطاكية (أع 15 : 3) الذين رافقوا بولس وبر نابا إلى أورشليموقت انعقاد المجمع (غلا 2 : 1 و 3) ويحتمل أن يكون من أهل إنطاكية ويرجّح أنه تجدد على يد بولس اذ أن الرسول يدعوه ابني الصريح حسب الإيمان المشترك (تي 1 : 4) ومن الواضح أنه أصغر سناً من بولس ولم يضطر تيطس أن يختتن (غل 2 : 3 - 5) وقد أرسه بولس إلى كورنثوس واناط به ترتيب أمور خطيرة وتدبير مسائل ذات شأن (2 كو 8 : 6 و 2 تي 1 : 5) ولما ترك الرسول افسس كان يأمل أن يقابل تيطس في ترواس (2 كو 2 : 12 و 13) ولما لم يأت ذهب إلى مقدونية وهناك أتصل به تيطس وأخبر بولس أخباراً سارة (2 كو 7 : 6 و 13 و 14) ولم نقرأ شيئاً عن تيطس إلا بعد أن أفرج عن بولس من سجنه الأول في رومية . وتدلنا رسالة بولس إلى تيطس على أنه ترك كريت ليتولى تنظيم الكنائس في تلك الجزيرة وظهر أنه مثل تيموثاوس في افسس كان مندوباً رسولياً أخيراً . ورافق الرسول إلى نيكوبوليس وذهب أخيراً إلى دلماطية(2 تي 4 : 10).رسالة بولس إلى تيطس :هي السفر السابع من اسفار العهد القديم وقد كتبت إلى تيطس بقصد ارشاده في تصرفه مع سكان كريت اذ كان يبشرهم ويكرز فيما بينهم بأمر الخلاص ومعرفة الرب يسوع . ويرجح انها كتبت بعد الافراج عن بولس من سجنه الاول في رومية واستئنافه العمل التبشيري وربما كتبت في سنة 65 أو 66. والرسالة فصيحة العبارة بليغة المعنى، والمبادىء. والتعاليم المعلنة فيها انما هي من اعظم التعاليم واوسعها مجالاً للبحث. وفي هذه الرسالة يحث الرسول تيطس على اتباع التعليم الصحيح والمحافظة على حسن السيرة وطهارتها لان سكان كريت كانوا قوماً مستعبدين للشهوات واللذات الدنيوية ويحذره الرسول من التعاليم الكذبة والهرطقات، وأوضح الصفات التي يتجلى بها الشيوخ والاساقفة.محتويات الرسالة إلى تيطس(1) تحية 1 : 1 - 4 .(2) مؤهلات الشيوخ 1 : 5 - 16 .(3) تصرف المسيحيين 2 : 1 - 10 .(4) الانجيل الحافز الاعظم للعيشة الصالحة 2 : 11 - 15 .(5) تصرف المسيحيين في العالم 3 : 1 - 11 .(6) امور متفرقة شخصية وتحيات 3 : 12 - 15 .'),
(824, 'تِيلون ', 'اسم عبري معناه ((مرتفع)) وكان ابن شيمون وهو رجل من نسل يهوذا (1 أخبار 4 : 20).'),
(825, 'تَيْما و تيماء ', 'اسم عبري ربما كان معناه ((الجنوبي)) وهي قبيلة اسماعيلية تسلسلت من تيما فكانت تقطن بلاد العرب (تك 25 : 15 و 1 أخبار 1 :30) وتسمّى ايضاً الجهة التي يسكنون فيها تيماء (اش 21 : 14) وكانت القوافل معروفة جيداً في هذه البقعة (اي 6 : 19) وتيماء في بلاد العرب في منتصف الطريق بين دمشق ومكة وعلى مسافة متساوية من بابل إلى مصر وقد ذكرت تيما مع سباء (اي 6 : 19) ومع دَدَان (اش 21 : 13 و 14 وار 25 :23).'),
(826, 'تَيْمان ', 'اسم عبري معناه  ((اليميني او الجنوبي))(1) بكر اليفاز بن عيسو (نك 36 :11).(2) قبيلة تسميت باسم بكر اليفاز بن عيسو والاقليم الذي تسكنه (تك 36 : 11 و 15 و 34) وواضح ان الاقليم الذي كانت تسكنه واقع في الجزء الشمالي من ادوم. ويسمى ارض ابناء الشرق ويدعى ايضاً تيْمن (حز 25 : 13) وقد اشتهر اهله بالحكمة (أر 49 : 7 وعو 9). وربما كان مكانه الان طويلان شرقي البتراء.'),
(827, 'تِيماني ', 'بالنسبة إلى تيمان (تك 36 : 34 وأي 2 : 11) وكان اليفاز صديق ايوب من تيمان (أي 2 : 11) وهو اسم احد ابناء اشحور (1 أخبار 4 : 6).'),
(828, 'تِيمن ', 'الصحراء الجنوبية (اطلب تيمان).'),
(829, 'تِيماوس ', 'اسم يوناني معناه ((معتبر)) هو ابو الرجل الاعمى الذي شفاه المسيح (مر 10 : 46).'),
(830, 'تِيمُوثاوس ', 'اسم يوناني معناه ((عابد الله)) رفيق بولس و مساعده، ومن تسمية الرسول إِياه إِبني والابن الصريح والابن الحبيب والامين (1 تي 1 : 18 و 1 :2 و 1 كو 4 : 17 و تي 1 :2) يرّجح انه آمن على يده. وواضح انه في اول ارسالية تبشيرية زار بولس لسترا في لكاؤنية فوجد هناك تيموثاوس الذي نشأ منذ الطفولة على مبادىء الديانة القويمة بعناية جدته وامه (2 تي 3 : 15) ومع ان امه كانت يهودية الا ان اباه كان رجلاً يونانياً ولهذا فانه لم يكن مختتناً فاخذه بولس وختنه لئلا يثير غضب اليهود عليه، ولما زار بولس لسترا في رحلته الثانية وجد الشاب قد اشتهر بين الاخوة في لسترا وايكونية (1 ع 16 : 2) وجعله بولس رفيقاً في اسفاره وصحبه إلى غلاطية ثم إلى ترواس وفيلبي وإلى تسالوكنيكي وذكر في اع 17 : 14 أنه بقي مع سيلا في بيرية لما ذهب بولس إلى اثينا ارسل لهما ان ياتيا باسرع ما يمكن (اع 17 : 15) ولكن من 1 تس 3 : 1 و 2 يتضح انه ارسل تيموثاوس إلى تسالونيكي وان سيلا وتيموثاوس لم يصحباه حتى وصل إلى كورنثوس (اع 18 : 5 و 1 تس 3 : 6) ومكث تيموثاوس مع بولس في كورنثوس (1 تس 1 : 1 و 2 تس 1 : 1). ويخبرنا بولس في 1 كو 4 : 17 أنه قبل كتابة الرسالة ارسل نيوثاوس إلى كورنثوس ليصلح العيوب هناك، ولسبب ما يظهر انه رجع إلى افسس لانه قبل ما يترك بولس تلك المدينة بوقت قليل تقدمه تيوثاوس وارسطوس إلى مقدونية (اع 19 : 22) حيث رافق بولس هذا الشاب الصديق (2 كو 1 : 1) وذهب الاثنان معاً إلى كورنثوس وقد شهد له مرة بقوله ((لانه يعمل عمل الرب كما انا ايضاً)) (1 كو 16 : 10) وجاء عنه في محل آخر انه يكرز معه يسوع المسيح ابن الله (2 كو 1 : 19). في الرسالة إلى اهل فيلبي يشير الرسول بقوله ((لان ليس لي احد آخر نظير نفسي يهتم باحوالكم باخلاص)) (في 2 : 19 و 20) وهذا دليل قاطع على ائتلاف الخواطر الذي كان بين الاثنين. ومن مراجعة الرسائل نرى آيات عديدة تشير إلى جهد تيموثاوس في كنيسة افسس حينما كان حديث السن (1 تي 4 : 12). ونعلم من 2 تيمو 4 : 9 و 21 أن بولس قد طلب من تيموثاوس ان يذهب إلى رومية ونعلم من عب 13 : 23 أنه ذهب إلى رومية وسجن ثم اطلق من السجن وهذا آخر مانسمعه عن تيموثاوس في الكتاب المقدس.رسالتا بولس الرسول إلى تيموثاوس هما الخامسة عشرة والسادسة عشرة من اسفار العهد الجديد، و كتبت الاولى بعد اطلاق سراح بولس من سجنه واستئناف عمله التبشيري. وكان تيموثاوس قد تُرك في كنيسة افسس لما ذهب بولس إلى مقدونية (1 تي 1 : 3) ويرجح ان تكون الرسالة قد كتبت في مقدونية حوالي سنة 64 و 65، وتعالج الرسالة الصعوبات التي واجهت تيموثاوس. وهي تتضمن ايضاً تعاليم خصوصية بشأن صفات معلمي الكنيسة وكل خدمة الانجيل وواجباتهم بجرأة ومحبة على الصدق والامانة في اعماهم.محتويات الرسالة الاولى إلى تيموثاوس(1) مقدمة - 1.أ : تحية 1 : 1 و 2.ب : المسيحية ضد الخطأ 1 : 3 - 11.ج : بولس خادم للانجي 1 : 12 - 17.د : وصية بولس لتيموثاوس 1 : 18 - 20.(2) حياة الكنيسة ونظامها 2 و 3.أ : ارشادات من جهة العبادة العامة 2 : 1 - 7.ب : التصرف في العبادة العامة 2 : 8 - 15.ج : مؤهلات موظفي الكنيسة 3 : 1 - 16.1 : الاساقفة 3 : 1 - 7.2 : الشمامسة 3 : 8 - 13.3 : الغرض من هذه الارشادات 3 : 14 - 16.د : دحض للتعاليم الكاذبة 4 : 1 - 11.ه : ارشادات خاصة 4 : 12 - 16.و : العلاقات المتنوعة 5 : 1 - 6 : 2.1 : بالنسبة للسن والجنس 5 : 1 و 2.2 : بالنسبة للأرامل 5 : 3 - 16.3 : بالنسبة للشيوخ 5 : 17 - 21.4 : تحذيرات اضافية 5 : 22 - 25.5 : واجبات العبيد 6 : 1 و 2.ز : التعاليم الكاذبة 6 : 3 - 10.(3) خاتمة، وصية ختامية للمسيحيين 6 : 11 - 21.أما الرسالة الثانية فقد كتبت من رومية بعد القبض على الرسول للمرة الثانية نحو سنة 67 وفيها يتكلم عن نفسه انه اسير (2 تي 1 : 8 و 16 و 2 : 9) وتوقع ان يستشهد (4 : 6) واصدقاء كثيرون تركوه ولوقا وحده صديقه الاول كان معه (4 : 11) مع ان كثيرين اجتمعوا حوله (21) اما رسالته فكان جزء منها لتشجيع تيموثاوس على عمله ليبشر، وجزء منها خاصاً في الاسراع إلى رومية وليتم خدمة الرسول. وكتب بولس هذه الرسالة لما كان ينتظر وقت انحلاله وموته (2 تي 4 : 6 - 8) وتعتبر كوصية الموت الاخيرة من ذلك الاب الرسول الموقر إلى ابنه في الرب، وهي تتضمن عدة امور بشأن واجبات كل مسيحي وما عليه ان يعمله في التجارب والضيقات وفيها يُّصرح الرسول بإيمانه القويم بالرب يسوع المسيح وبكل مواعيده العظيمة التي سبق فاعدها للمؤمن به.محتويات الرسالة الثانية إلى تيموثاوس(1) تحيات وشكر 1 : 1 - 5.(2) نصيحة ليتموثاوس من جهة حاجاته الروحية 1 : 6 - 2 : 13.(3) توصيات من جهة عمل تيموثاوس كمعلم 2 : 14 - 4 : 5.أ : معلم ومرشد إلى التعليم الصحيح 2 : 14 - 19.ب : توصيات شخصية 2 : 20 - 26.ج : التنبؤ بفترة انحلال خلقي 3 : 1 - 9.د : مثال بولس الرسول 3 : 10 - 13.ه : تأييد الكنيسة المقدسة 3 : 14 - 17.و : حث على الثبات .(4) كلمات بولس الوداعية 4 : 6 - 22.أ : موته العتيد 4 : 6 - 8.ب : توصيات لتيموثاوس 4 : 9 - 15.ج : اتكال بولس على الله 4 : 16 - 18.د : تحيات والبركة 4 : 19 - 22.'),
(831, 'تِيمُون ', 'اسم يوناني معناه ((مكرَّم)) وكان احد الشمامسة السبعة الذين اقامهم الرسل ليحلوا محلهم في الامور الدنيوية في الكنيسة الاولى (اع 6 : 5).'),
(832, 'تِين ', 'ورد اسم التين مراراً عديدة في الكتاب المقدس وهو شجر مشهور في فلسطين وسورية وثمره اجاصي الشكل وقد تعلو شجرة التين عن الارض من عشرة اقدام إلى عشرين قدماً وتتفرع اغصانها إلى انحاء مختلفة وتنتج التينة ثمراً طيباً (قض 9 : 11) وشجر التين الجديد لا يثمر ما لم يفلح (لو 13 : 6 - 9 وام 27 : 18) وكان القدماء يعتبرون جلوس كل انسان تحت تينته من دلائل السلام والفلاح (1 مل 4 : 25 و 2 مل 18 : 31 واش 36 : 16 ومي 4 : 4 وزك 3 : 10 ويو 1 : 48) ومن خواص التين الغريبة ظهور ثمره قبل اوراقه فاذا ظهرت الاوراق ولم يظهر الثمر لا يؤمل اثمارها في تلك السنة (مت 21 : 19) واوراق التين في فلسطين من اكبر الادلة على اقتراب الصيف (نش 2 : 13 ومت 24 : 32 ولو 21 : 29 و 30). وكانت محاصيل التين مهمة فيما سلف فاذا نقص حمله او اصابته آفة عدّ ذلك ضربة عظيمة علىالبلاد (ار 5 : 17 و 8 : 13 ويؤ 1 : 7 و 12 وحب 3 : 17).التين الباكوري (نش 2 : 13) وينضج في حزيران (يونيو) وهو جميل ولذيذ جداً (ار 24 : 2) وقد شبه النبي هوشع علائق الرب مع بني اسرائيل بهذا النوع اذ يقول ((رأيت اباكم كباكورة على التينة في اولها)) (هو 9 : 10) والتين اذا نضج يسقط حالاً من مجرد هز اشجاره، ومن ذلك اتخذ النبي اشارته إلى نينوى حيث يقول ((جميع قلاعك اشجار تين بالبواكير اذا انهزت تسقط في فم الآكل)) (نا 3 : 12).التين الصيفي ويظهر في اواخر حزيران (يونيو) اي بعد نضوج النوع السابق ويتم نضجهفي آب وايلول فوجاً بعد فوج اثناء ذلك يجمع ويوضع في الشمس إلى ان ييبس ثم يحفظ للشتاء .التين الشتوي قد يبقى على الاشجار بعض التينات إلى فصل الشتاء واحياناً إلى الربيع الا ان بعض انواعه لاتنضج اثماره الا في اواخر الخريف واوائل الشتاء فيعرف بالشتوي .واما القول بانه لم يكن وقت التين ... الخ (مر 11 : 13 و 21) فالمراد وقت جمع التين الاعتيادي لا وقت ظهوره ومقاربة نضجه . فقد وجد المؤلف شجرة تين عند عيون موسى قرب آخر نيسان وكانت اثمارها تكاد تنضج . وفي بعض الظروف قد ينضج قليل من الثمر في الوقت الذي طلب فيه المسيح ثمراً ولم يجده ويظهر من القصة ان المخلص لم يقطع بوجود ثمر صالح للاكل اذ يقال في انجيل مرقس ((فنظر شجرة تين من بعيد عليها ورق وجاء لعله يجد فيها شيئاً)) فان قوله يدل على الشك بوجود شيءٍ يسد به جوعه غير انه لما اتى إلى الشجرة وجدها حاملة ورقاً فقط فلعنها ليس لان ثمرها لم ينضج في غير اوانه، بل لانها عقيمة ليس عليها شيء من الثمر لا ناضج ولا غير ناضج ويقصد السيد المسيح بذلك الامة اليهودية .وكان التين ييّبس ويذخر مؤونه منذ القديم إلى الان (1 اخبار 12 : 40) فقد جاء عن ابيجايل انها لاقت داود بمئتي قرص من التين (1 صم 25 : 18) وقد يستعمل علاجاً ايضاً فان اشعياء عالج دمل حزقيا بوضع قرص التين عليه وهكذا نجّاه من خطر الموت (2 مل 20 : 7 واش 38 : 21).'),
(833, 'الثالوث الاقدس (تثليث) ', 'عرّف قانون الايمان هذه العقيدة بالقول ((نؤمن باله واحد الآب والابن والروح القدس اله واحد جوهر واحد متساوين في القدرة والمجد)) .في طبيعة هذا الاله الواحد تظهر ثلاثة خواص ازلية، يعلنها الكتاب في صورة شخصيات (اقانيم) متساوية . ومعرفتنا بهذه الشخصية المثلثة الاقانيم ليست الا حقاً سماوياً اعلنه لنا الكتاب في العهد القديم بصورة غير واضحة المعالم، لكنه قدّمه في العهد الجديد واضحاً، ويمكن ان نلخص العقيدة في هذه النقاط الست التالية :1 - الكتاب المقدس يقدم لنا ثلاث شخصيات يعتبرهم شخص الله .2 - هؤلاء الثلاثة يصفهم الكتاب بطريقة تجعلهم شخصيات متميزة الواحدة عن الاخرى .3 - هذا التثليث في طبيعة الله ليس مؤقتاً او ظاهرياً بل ابدي وحقيقي .4 - هذا التثليث لا يعني ثلاثة آلهة بل ان هذه الشخصيات الثلاث جوهر واحد .5 - الشخصيات الثلاث الآب والابن والروح القدس متساوون .6 - ولايوجد تناقض في هذه العقيدة، بل بالاحرى انها تقدم انا المفتاح لفهم باقي العقائد المسيحية . ولقد كانت هذه الحقيقة متضمنة في تعاليم المسيح (يو 14 : 9 - 11 و يو 14 : 26 و يو 15 : 26) وقد تمسكت الكنيسة بما جاء واضحاً في مت 28 : 19، وتحدث الرسل مقدمين هذه الحقيقة في كو 13 : 14 و 1 بط 1 : 2 و 1 يو 5 : 7 ولا نستطيع ان نغفل منظر معمودية المسيح وفيه يسمع صوت الآب واضحاً موجهاً إلى المسيح، ويستقر الروح القدس على رأس المسيح الابن في شكل حمامة (مت 3 : 16 و 17 و مر 1 : 10 و 11 و لو 3 : 21 و 22 و يو 1 : 32 و 33).ولقد كان يقين الكنيسة وايمانها بلاهوت المسيح هو الدافع الحتمي لها لتصوغ حقيقة التثليث في قالب يجعلها المحور الذي تدور حوله كل معرفة المسيحيين بالله في تلك البيئة اليهودية او الوثنية وتقوم عليه .والكلمة نفسها ((التثليث او الثالوث)) لم ترد في الكتاب المقدس، ويظن ان اول من صاغها واخترعها واستعملها هو ترتليان في القرن الثاني للميلاد . ثم ظهر سبيليوس ببدعته في منتصف القرن الثالث وحاول ان يفسر العقيدة بالقول ((ان التثليث ليس امراً حقيقياً في الله لكنه مجرد اعلان خارجي، فهو حادث مؤقت وليس ابدياً)) . ثم ظهرت بدعة اريوس الذي نادى بان الآب وحده هو الازلي بينما الابن والروح القدس مخلوقان متميزان عن سائر الخلقة، واخيراً ظهر اثناسيوس داحضاً هذه النظريات و واضعاً اساس العقيدة السليمة التي قبلها واعتمدها مجمع نيقية في عام 325 ميلادية .ولقد تبلور قانون الايمان الاثناسيوسي على يد اغسطينوس في القرن الخامس، وصار القانون عقيدة الكنيسة الفعلية من ذلك التاريخ إلى يومنا هذا.ولا يستطيع دارس هذه العقيدة ان ينسى المصلح جون كلفن، الذي عاش في القرن السادس عشر، ونبرّ على التساوي التام بين الاقانيم الثلاثة في هذه العقيدة، التي يلزمها مثل هذا التنبير من وقت إلى آخر على مر الزمن.واخيراً نود ان نشير إلى ان عقيدة التثليث عقيدة سامية ترتفع فوق الادراك البشري ولا يدركها العقل مجرداً، لانها ليست وليدة التفكير البشري بل هي اعلان سماوي يقدمه الوحي المقدس، ويدعمه الاختبار المسيحي. وهكذا تصير كل ديانة يبتدعها البشر خالية من عقيدة التثليث. وفي سبيل قبول هذه العقيدة واعتناقها لا بد من الاختبار العميق للحياة المسيحية.'),
(834, 'ثَامار ', 'اسم عبري معناه ((نخلة)) وجاء في العهد القديم كاسم لثلاثة من النساء واسم لمكان.(1) اسم زوجة ((عير)) بكر يهوذا (تك 38 : 6 - 30) فلما توفي ((عير)) اعطيت زوجة لاخيه ((اونان)) الذي مات ايضاً عاجلاً لشره، فوعدها اليهود ان يعطيها لابنه الصغير ((شيله)) متى كبر، حسب العادة، وانتظرت ثامار تحقيق الوعد وطال انتظارها حتى رتبت ان تلاقي يهوذا في شكل بغي على الطريق وقت جزّ غنمه. وقد اخذها فصارت بسببه اماً لفارص وزارح (1 اخبار 2 : 4) و لما اتهمت بالزنا بررت نفسها مظهرة خطيئة يهوذا، فلم تقتل. ونلاحظ ان اسم ثامار وابنها فارص قد ذكرا في راعوث 4 : 12 وفي نسل يسوع المسيح حسب الجسد في مت 1 : 3 بدون اية اشارة شائنة.والقصة تصور لنا عوائد الزوج عند العبرانيين في ذلك الوقت. فالاب يختار للابن زوجته، واخ المتوفي يتزوج بامرأة اخيه. والزانية تغطي وجهها وتجلس على الطريق في موسم جز الغنم، وتأخذ رهناً لاجرتها.(2) اسم اخت ابشالوم الجميلـة التي اذلـها امنون اخوها باضطجاعـه معها رغمــاً عنــها (2 صم 13 و 1 اخبار 3 : 9).(3) اسم ابنة ابشالوم الجميلة (2 صم 14 : 27).(4) اسم مكان في الجنوب الغربي من البحر الميت (حز 47 : 19 و 48 : 28).ويظن البعض ان ما جاء في 1 مل 9 : 18 باسم تدمر انما هو ثامار حسب بعض النسخ العبرية القديمة (انظر  ((تدمر))).'),
(835, 'ثَامِح ', 'اسم عبري معناه ((ضحك)) (عز 2 : 53) اطلب ((تامح)).'),
(836, 'ثَاوفِيلـُس ', 'اسم يوناني معناه ((محبوب من الله)) او ((صديق الله)) وهو الشخص الذي وجّه اليه لوقا انجيله وسفر الاعمال (لو 1 : 3 واع 1 : 1). والصفة المطلقة على ثاوفيلس هي ((العزيز)) انما تشير إلى شخص معين بالذات. وليست للمسيحيين عامة كما ظن بعضهم، ولعله كان رومانياً وصاحب منصب كبير تتطلب مخاطبته بهذا التعبير الذي لم يتبعه المسيحيون عادة مع بعضهم البعض. ونلاحظ ان اللقب ((العزيز)) لم يرد في ديباجة سفر الاعمال، ولذلك يعتقد بعضهم انه اعتنق المسيحية فيما بين كتابة الانجيل، وكتابة سفر الاعمال.ويعتقد آخرون انه كان محامياً تدخّل للدفاع عن بولس في روما. وان لوقا ارسل اليه هذين السفرين ليكسبه اولاً للمسيح كما ويعطيه مادة للدفاع. واعتقد بعضهم انه شيخ اشترك في ارسال رسالة من الكورنثيين إلى بولس. ولكن هذه النظريات كلها تفتقر إلى الدليل.'),
(837, 'الثُرَيّا ', 'اسم مجموعة من النجوم جاء ذكرها في اي 9 : 9 و 38 : 31 و عا 5 : 8 وموقعها في عنق برج الثور وتطهر في اوائل الربيع. كما انه يمكن رؤية ستة او سبعة من نجومها بالعين المجردة لكن استعمال التلسكوب يكشف عن اكثر من مائة نجم. ولقد كان العبرانيون الاول والساميون عامة يعنون عناية خاصة بدراسة الفلك. (انظر ((الجبار))).'),
(838, 'ثُعبَان سام طيار ', '(اش 14 : 29 و 30 : 6) يرّجح ان التشبيه هنا مجازي حقيقته، افاعي صحاري الشرق المشهورة بسرعة الوثب (انظر ((حية)))والكلمة العبرية التي تلفت نظرنا في هذه التسمية هي كلمة ((سيراف)) التي تحمل معنى اللذعة النارية وبريق النار المتوهجة، ومن الغريب ان صيغة الجمع التي وردت في اشعياء 6 : 2 و 6 قد ترجمت إلى سيرافيم اي الكائنات السماوية التي ابصرها النبي في رؤياه.'),
(839, 'ثَعْلَب ', 'حيوان مشهور بالدهاء والمكر والشراهة يكثر وجوده في كل الشرق، وهو يقتات بالطيور والزحافات الصغيرة، ويضرب به المثل لشدة مكره ودهائه (حز 13 : 4) ولقد جاء ذكره في كلام السيد المسيح عن هيرودس   (لو 13 : 32). والثعالب المعروفة في فلسطين نوعان : النوع المصري، والنوع الاسمر. ويعيش الثعلب عادة في جحور (مت 8 : 20) ويكثر وسط الخرب والقبور (مراثي 5 : 18)، وهو شديد الحرص على حياته (حز 13 : 4 و لو 13 : 32) وهو مشهور بنشاطه المدمر وخصوصاً للكروم (نش 2 : 15).'),
(840, 'الثلاثة الحوانيت ', '(1 ع 28 : 13 - 15) كان هذا الاسم يطلق على ثلاثة منتديات عامة على بعد 33 ميلاً نحو جنوبي روما. وقد كانت بمثابة استراحة على الطريق المشهور باسم الطريق الابياني Appian Way . في ايطاليا وجاء في اخبار الرسول بولس انه لما قدم إلى روما ونزل في بوطيولي مسافراً على قدميه لاقاه الاخوة في الثلاثة الحوانيت ورافقوه عائدين معاً إلى رومية.'),
(841, 'مُثَلَثات ', 'آلة موسيقية يظن انها شريط معدني مثني على هيئة مثلث يدق عليه بقضيب من حديد او معدن آخر فتعطي صوتاً رناناً، وربما تكون هي الآلة المستعملة حالياً في الكنيسة القبطية الارثوذكسية إلى جانب الناقوس، ويظن آخرون انها آلة موسيقية ذات ثلاثة اوتار.وقد جاء ذكرها في الكتاب المقدس بمناسبة انتصار داود على جليات (1 صم 18 : 6).مُثلثات الاسنان :(1 صم 13 : 21) الارجح انه يشار بها إلى بعض انواع ادوات الحراثة وربما هي شوكات ذات ثلاثة اسنان.'),
(842, 'ثلج (ثليج . جليد . بَرَدْ) ', 'ورد ذكر الثلج كثيراً في الكتاب المقدس بالنسبة لتساقطه بصورة متكررة على سفوح جبال فلسطين ومرتفعاتها، وقدكان ذلك مصدر المياه والحياة للسكان والحيوان، ومنظر الثلج في نزوله يشبه بالصوف الابيض (مز 147 : 16) وقد قيل ((كبرد الثلج في يوم الحصاد)) (ام 25 : 13) اشارة إلى ما يؤتى به من الثلج ايام الحصاد لتبريد الماء للحاصدين، ولما كان ماء الثلج ألين من المياه العادية وانسب منها للغسل جاء في اي 9 : 30 ((ولو اغتسلت في الثلج ونظفت يدّي بالاشنان)).ولم يشر الكتاب إلى الثلج بمعناه الحرفي الا قليلاً، كما في اي 6 : 16 و ار 18 : 14 وان كان يذكره في الغالب بطريقة مجازية، كما عندما يصف بياض الابرص (خر 4 : 6 و عد 12 : 10 و 2 مل 5 : 27)، او عندما يصف لباس الله او لباس المسيح او لباس الملائكة (دا 7 : 9 و مت 28 : 3 و مر 9 : 3)، اويصف شدة البرد (2 صم 23 : 20 و 1 اخبار 11 : 22 و ام 31 : 21)، لكن المعنى المجازي المألوف هو النقاوة التي تحدثها مغفرة الله وقوته المطهرة من نجاسة الخطيئة (مز 51 : 7 و اش 1 : 18).'),
(843, 'ثمنِيَة ', '(رؤ 6 : 6) الكلمة اليونانية المستعملة هنا تعني كيلاً يعادل لتراً وثمانية من المائة.'),
(844, 'تَثْنِيَة ', 'هذا هو الاسم الذي يطلق على السفر الخامس من الاسفار الموسوية كما ورد في الترجمة السبعينية، ويعني تكرار الشريعة الموسوية مرة ثانية، والقصد من هذا التكرار هو التوضيح والتعبير، اما اسم السفر الوارد في العبرية فهو ((إِلهِ هد باريم)) اي ((هذا هو الكلام)) وهي الكلمات الثلاث الاولى في هذا السفر.وينتسب السفر إلى موسى المشرع العظيم، وقدكتبه قبل تسليم مهام قيادة الشعب ليشوع، فيما عدا بعض الاجزاء القصيرة، كالاصحاح الاخير منه، ويظن البعض ان هذه الاجزاء كتبت بعده. ويعتقد جيروم وهو دارس الكتاب العظيم في القرن الرابع للميلاد ان هذا السفر هو المشار إليه في 2 مل 22 : 8 وعلى هذه النظرية الاخيرة بنى بعض الباحثين في العصر الحاضر رأيهم الذي ينادي بأن السفر قد كتب سراً بيد اتقياء اليهود في عهد منسى ملك يهوذا 693 - 639 ق . م. واكتشف في عصر يوشيا (638 - 608 ق . م). غير ان الكشوف الحديثة والابحاث التي قام بها العلماء في النصف الاول من القرن العشرين من مقابلات تاريخية ولغوية تشير إلى ان السفر يرجع إلى عصر موسى.وسفر التثنية نبوي يفيض بالحض على البر بغيرة وحرارة متناهيين فهو غني بالمادة الوعظية، وبالاجمال فإنه تفصيلي للوصية العظمى ((تحب الرب إلهك من كل قلبك ومن كل نفسك ومن كل قوتك (تث 6: 5).وقد اشار المسيح في تعليمه إلى آيات هذا السفر عدة مرات (قانون متى 4: 4 مع تث 8: 3 ومت 4: 7 مع تث 6: 16 ومت 4: 10 مع تث 6: 13 ومت 5: 31 مع تث 24: 1 ومر 12: 30 مع تث 6: 5).كذلك اشار لوقا وبولس إلى هذا السفر (قارن 1 ع 3: 22) مع تث 18: 15 و 18 و 1 كو 9: 9 مع 25: 4 و 2 كو 13: 1 مع تث 19: 5 وغل 3: 13 مع تث 21: 23 ورو 10: 6 - 8 مع تث 30: 12 و 14).تبرز في هذا السفر ثلاثة تعاليم:1 - الرب اله لا مثيل له، فليس هناك اله ىخر أعلن نفسه كشخصية روحية متميزة بالامانة والرأفة للذين يحبونه ويطيعونه غير الرب.2 - شعب الله شعب لا مثيل له. باعتبارهم جنس مختار وكهنوت ملوكي وأمة مقدسة ورثة لكل وعود الله للآباء، وهم محبوبون لذلك يؤدبهم الله ليصيرهم كاملين ومقدسين.3 - الصلة القائمة بين الله وشعبه صلة لا مثيل لها. إن علاقة الشعوب بألهتها علاقة خوف أما شعب الرب فعلاقته بالهة علاقة حب وتقدير وتبعيّة وتمسك تقوم على عهد مبارك بين الله والشعب. وينقض العهد وتنفصم الصلة إذا عصى الشعب وصية الرب.ويشتمل هذا السفر على ثلاثة خطابات ألقاها موسى على بني اسرائيل في أرض موآب في الشهر الحادي عشر من السنة الأربعين لخروجهم من مصر.الخطاب الأول:(ص 1- 4) ويحتوي على استعراض لرحلات بني اسرائيل منذ قطع العهد مع الله في جبل سيناء، وذكر الحوادث المتعلقة بشأن كنعان وفي هذا الخطاب تحريض على الطاعة لأوامر الله وشرائعه.الخطاب الثاني:(ص 5 - 27) وينقسم إلى ثلاثة أقسام:1 - مقدمة تستعرض الوصايا المذكورة في القسم الأول وتنّبر على ضرورة حفظها مع ذكر البركات الناتجة عن الطاعة والعقوبات التي تنتج عن العصيان (ص 5 - 11).2 - تفسير مطول لمطالب الله (ص 12 - 26).3 - خاتمة تنتهي بالوعد بالبركة في طريق الامانة وباللعنة في طريق العصيان (ص 27 - 28).الخطاب الثالث:(ص 29 - 30) وهو خطاب قصير ويدور حول تأييد العهد المذكور بالبركة وباللعنة وتأكيده. أما الاصحاحات ص 31 - 33 فهي عبارة عن كلمات موسى الوداعية، وتحتوي على تشجيعاته كما تحتوي على قصة تسليم كتاب الشريعة للكهنة بني لاوي (31: 9)، ثم نشيد موسى (32) وبركته (33).أما الاصحاح الأخير ص 34 فيدور حول موت موسى رجل الله.'),
(845, 'ثوب. ثياب', 'وردت في الكتاب المقدس كلمات وأسماء كثيرة لما كان يلبسه الناس في أوقات مختلفة، وان لم ترد أوصاف كافية لهذه الملابس يمكن أن توضحها لنا، لكننا نستطيع أن نعرف بعض الايضاحات بخصوصها مما صوّره القدماء من المصريين والبابليين والاشورين واليونانيين في كتاباتهم وفي فنونهم، إلى جانب ما نراه اليوم فيما يلبسه البسطاء في فلسطين وفي بعض بلاد الشرق الأوسط.1 - ثياب الرجال:وتنقسم إلى المنطقة - الثوب الداخلي - الثوب الخارجي - الأحذية - العصائب.أ - المنطقة:يظن أنها أقدم أنواع الثياب التي استعملها الإنسان. وكانت تعني في الايام الولى ((ازارا)) يلبس مباشرة حول الحقوين ويغطي العورة، ويشار إليه ((بالمِسح)) الذي كان يقوم مقامها في أوقات الحزن (2 مل 1: 30 واش 20:2)، وكانت تصنع من الجلد أو من القماش الخشن أو من الكتان (2مل 1: 8 وار 13: 1).وتطور استعمال المنطقة مع الزمن فصارت تعني الزنار أو الحزام (خر 29: واع 21: 11) وغايتها إحكام القميص وكانت تصنع عادة من حبل أو صوف أو بوص أو جلد، أما الكهنة فكانوا يصنعونها من ذهب واسمانجوني وفرمز وبوص مبروم (خر 28: 8). وكانت المنطقة تستخدم في حمل السلاح (2 صم 20: 8) وكذلك الفضة وما نحمله عادة في الجيوب.ب ـ الثوب الداخلي:كان في الأصل قميصاً بدون أكمام يمتد إلى الركبة فقط، ثم زيد طوله بعد ذلك وأضيفت إليه الأكمام وبدأوا يمنطقونه. وكان يصنع عادة من صوف أو كتان يختلف قماشه حسب غنى الشخص وذوقه وقد كان الرجل اليهودي يؤدي كل أعماله اليومية مرتدياً هذا القميص، ولكن الكتاب أطلق كلنة عريان في بعض الأماكن على من تجرد من كل شيء إلا القميص (1 صم 19: 24 وميخا 1: 8 ومر 14: 51 و 52 ويو 21: 7).جـ - الثوب الخارجي:أو الرداء (مت 5: 40 و 21: 8) كان قطعة مربعة أو مستطيلة من القماش طولها من ستة أقدام تلف حول الجسد، وإذا مسّت الحاجة تطرح فوق المنكب أو تحت الابط. أما في الليل فكانت تستعمل نظير غطاء (خر 22: 26 و 27 وتث 24: 13) ويظن أن الأهداب كانت على أطراف هذا الثوب وحوافيه (عد 15: 38 ومت 23: 5) وهذا هو الشيء الذي كانت الشريعة تحميه لصاحبه لينام فيه حتى وان كان مديناً، رغم أنها أباحت أخذ الثوب الداخلي (القميص) استيفاء للدين. ولكن المسيح في تسامحه العجيب علّم تلاميذه في مت 5: 40 أن يخطو في تبعيته إلى ما بعد الشريعة فيقدمون الرداء الذي يتمتع بحمايتها، علاوة على الثوب، اعلاناً لرغبة الانسان الخيّرة، ورفضه التام لكل خصام وشر، وتسامحه البعيد المدى.د ـ الأحذية:(مت 3: 11) أو النعال (تث 25: 9 ومر 6: 9). كان اليهود عادة كباقي الشعوب الشرقية لا يلبسون شيئاً في أقدامهم في داخل بيوتهم، لكنهم متى خرجوا إلى خارج فإنهم يلبسون نعالاً. وقد كانت النعال تصنع إما من خشب أو جلد، وتربط بسيور من جلد (شراك) أو خيط (تك 14: 23 واش 5: 27). وكانت النعال تخلع عند دخول البيوت أو أمكنة العبادة احتراماً لها، كما أنها كانت تخلع في أوقات الحزن (2 صم 15: 30) ولما كانت هذه الأحذية لا تقي القدم من الأقذار والغبار التزم صاحبها أن يغسل قدميه بعد المشي في الخارج، لذلك وجب على المضيف أن يقدم ماءً لهذه الغاية لضيفه (تك 24: 32 ولو 7: 44). وكان حل سيورر الحذاء منوطاً بالخدم وكذلك غسل القدمين (مر 1: 7 ويو 13: 1 - 16).وكانت النعال تستعمل مجازاً عند البيع والشراء. فقد كان المشتري اليهودي يخلع نعله ويعطيه للبائع اشارة إلى المبادلة (را 4: 7 و 8).كما كانت الشريعة الموسوية توصي أخا الزوج المتوفى أن يقيم نسلاً لأخيه، وفي حال رفضه واصراره على الرفض تتقدم الأرملة أمام الشيوخ وتخلع نعله من رجله الخ .. ويسمى عند العبرانيين بيت مخلوع النعل (تث 25: 5 - 10).هـ - العصائب:لباس الرأس وقد اتخذ شكل قطعة من القماش ذات طبقتين (كوفية) ويلبسه الانسان للوقاية من الشمس. أو تلف حول الرأس كعمامة.2 - ثياب المسيح:كانت ثيابه بسيطة للغاية وتتكون حسب عادة عصره من (1) قميص داخلي يقابل ولا شك ما نلبسه اليوم من ملابس داخلية (2) قميص خارجي، ولعل هذا هو الذي قيل فيه ((قميص بغير خياطة منسوجاً كله من فوق)) (يو 19: 23). ويقول فيه يوسيفوس ((عن مثل هذا القميص الذي بغير خياطة منسوج كله من فوق لم يَحُل لبسه إلا لرؤساء الكهنة)). وقد أخذه العسكر بالقرعة (3) منْطقة لربط القميص (4) الرداء الخارجي الذي نعتقد أنه كان من الصوف الأبيض بناء على ما جاء في وصف ملابسه على جبل التجلي (مر 9: 3). وقد اقتسمه العسكر مع باقي الملابس عند صلب المسيح. (5) الأحذية أو النعال. (6) وأخيراً العمامة أو غطاء الرأس، لأنه كان من الضروري على معلمي اليهود أن يلبسوا غطاء للرأس.3 - بدلات ثياب:(مل 5: 5 و 22) كان الملوك والعظماء يهدون بدلات ثياب للضيوف. وبما أن هيئة الثياب كانت متساوية عند الجميع صار ابدال ثياب شخص واحد بثياب ىخر أمراً سهلاً (تك 27: 15 و 1 صم 18: 4 وتث 22: 5 ولو 15: 22).4 - ثياب الكهنة:تتكون بالنسبة لهرون من صدرة ورداء وجبة وقميص مخرم وعمامة ومِنْطقة. (خر 28: 4) وبالنسبة لبنيه من اقمصة ومناطق وقلانس للمجد والبهاء وسراويل من كتان (خر 28: 40 - 43).5 - ثياب النساء:أما ثياب النساء فلم يكن يختلف عن لباس الرجال إلا في الثوب الخارجي الذي كان أكثر اتساعاً من رداء الرجال وهو الذي يسمّى الآن مئزراً أو ازارا (مر 14: 51) وأضيف إلى ذلك البرقع أو اللثام (تك 24: 65). أما المناديل فكانت تحمل في اليد أو توضع على الوجه (1 ع 19: 12).وكانت ثياب النساء تزين بالجواهر أو أهداب الذهب والفضة والتطريز. وكانت النساء من عامة الشعب يلبسن الاقراط في آذانهن والخزامة في أنوفهن ويتحلين بالاساور والخلاخل (2 صم 1: 10 واش 3: 16 و 19 و 20) ويحملن المرائي المصنوعة من النحاس المصقول في أيديهن أو يعلقنها على مناطقهن أو في أعناقهن (خر 38: واش 3: 23). وكانت النساء اليونانيات والرومانيات يتركن الشعر يطول ثم يضفرنه ويزينه بالحلي والجواهر وضروب من الزينة أيضاً (1 تي 2: 9 و 10 و 1 بط 3: 3).'),
(846, 'ثُودَاس', 'وهو اختصار للاسم اليوناني ثيودورس ويعني ((عطية الله)) وقد ورد ذكره في حديث غمالائيل أمام السنهدريم في وقت القاء القبض على الرسل (1 ع 5: 18 و 36). وفي ذلك الحديث ذكر غمالائيل أمر ثبات كل حركة من الله وتبدد كل حركة ليست من الله. وقدّم مثلين لهذا الحق، أحدهما ثوداس الذي ادعى أنه شيء، وانقاد وراءه أربع مائة رجل قُتلوا جميعاً وتبددوا، ثم مثل يهوذا الجليلي الذي ازاع وراءه شعباً غفيراً ثم هلك هو وتشتت الشعب. وانهى غمالائيل حديثه طالباً عدم التعرض للرسل في حركتهم، فإن كانوا من أنفسهم فسيتبدون، وغن كانوا من الله فسيثبتون، ويكون أعضاء السنهدريم مقاومين لله.وقد ورد اسم ثوداس في كتابات يوسيفوس عن بعض حركات اليهود العصيانية ضد الرومان لكن الآراء اختلفت بخصوص شخصية ثوداس هذا وصلته بثوداس المذكور على لسان غنالائيل.'),
(847, 'ثور', '(عجل) ذكر البقر وهو حيوان معروف في الشرق بقوته وجَلَده على العمل. وهو حيوان استأنسه الإنسان وروّضه منذ القديم. وامتلكه ابراهيم وابيماليك في ايامهما (تك 12: 16 و 20: 14 و 21: 27) كما استخدمه المصريون القدماء وقت الضربات العشر (تك 9: 3). وكان يُكَوّن الجانب الأعظم في مقتنيات الآباء (تك 24: 35 أي 1: 3). وقد استخدمت الثيران في الحراثة (تث 22: 10 و 1 مل 19: 19 واي 1: 14 وام 14: 4 و 2 صم 6: 6) وفي الدراسة (تث 25: 4 وهو 10: 11 و 1 كو 9: 9). وكانت تذبح وتؤكل أيضاً (1 مل 1: 25 و 19: 25 و 19: 21 ومت 22: و 1 اخبار 12: 39). كما كانت تقدم كذبائح لا سيما كمحروقات (تث 7: 87 و 2 صم 24: 22 و 1 مل 8: 63 و 2 اخبار 5: 6 و 7: 5). وفي الدراسة كانت الثيران تترك بلا كمامة حسب الشريعة (تث 25: 4) وفي وقت حراثة الأرض كانت تعلف علفاً جيداً (اش 30: 24). كما كانت تربط كل اثنين معاً بالنير ويسمّى كل اثنين منهما فدان بقر (1 مل 19: 19).أما الثور غير المروض الذي اشار إليه اراميا 31: 18 فقد سمي الوعل (تث 14: 5 واش 51: 20). أما ثيران باشان (مز 22: 12) فقد كانت شهيرة بالنسبة لانها كانت تُربي في الأرض الغنية بالخير شرقي بحر الجليل.وللثور تاريخ وأهمية عظمى في عبادات الشرق الأوسط القديمة، وقد كانت له أهمية كبرى في مصر تركزت في ممفيس تحت اسم عبادة ((آبيس))، وكانوا يعتقدون أنه ولد نتيجة نزول شعاع من أشعة الشمس من السماء على بقرة انجبت عجلاً ذا لونين أبيض مع أسود مع مثلث أبيض فوق جبهته وهلال قمري على جانبه الأيمن، خدمة الكهنة في وقت الدولة القديمة حوالي 2700 - 2200 ق.م، ولقد تغلغلت هذه العبادة بصورة فعالة في مصر. ويعتقد بعضهم أن وقفة موسى ضد العجل الذي صنعه هارون في البرية كانت بمثابة محاولة لتخليص العبرانيين من ديانة مصر الوثنية (خر 32: 1 - 24). أما الثور المجنّح فقد تميزت به الرسوم البابلية.وكان الثور أيضاً رمزاً محبباً للخصب وللعبادة في الديانة الكنعانية، وكانت الكلمة ((ايل)) تشير إلى ((الاب الثور)) في عبادتهم.وفي التقاويم الكنعانية والعبرانية القديمة والعربية أيضاً، يشار إلى الفصول الأربعة باثني عشر ثوراً منتظمة في أربعة أقسام وفي كل قسم ثلاثة ثيران.وعلى الرغم من الوصية الثانية (خر 20: 4) فقد وَجَدَتْ صور الثور وتماثيله طريقاً لها في الفنون العبرانية ففي أيام سليمان وقت بناء الهيكل وربما تحت تأثير فنان فينيقي دخلت الوثنية القائمة على الثور في الحياة العبرانية، إذ صنع البحر المسبوك (1 مل 7: 23 - 26) الذي يصور المعاني الوثنية عن الكون، لأنه يرتكز على 12 ثوراً منقسمين إلى أربعة أقسام وفي كل قسم ثلاثة ثيران تتجه بوجوهها إلى جهات الأرض الأربع.أما محاولة يربعام أن يفصل بين اسرائيل ويهوذا باقامته عجلين وفي دان وبيت ايل (1 مل 12: 26 - 33) فقد جلبت لعنات كثيرة على رأس يربعام وبالتالي على اسرائيل.'),
(848, 'ثوم', 'نبات يزرع بكثرة في كل الشرق يشبه البصل وله رائحة قوية. يظن أنه عرف أولاً في وسط آسيا ثم انتقل إلى مصر حيث صار طعاماً شائعاً. وكان اليهود مولعين باكله حتى أنهم اشتهوه في البرية وافتقدوه (عد 11: 5).'),
(849, 'ثِياتيرا', '(1 ع 16: 14) مدينة في آسيا الصغرى في مقاطعة ليديا قرب حدود ميسيا. وربما يعني الاسم ((مدينة أو قلعة ثيا)) وقد كانت على الطريق من برغاموس إلى ساردس. وبين سنة 301 وسنة 281 ق.م. جاء سلوقس نيكاتور واسكن فيها الكثير من الاغريق واطلق عليها الاسم ثياتيرا. وكانت تعرف باسم بيلوبيا أو ايوهيبيا، كما يقول  بليني الكبير، وربما كانت هذه الأسماء وصفية فقط، ولا تزال بقايا هذه المدينة موجودة في شكل بقايا أعمدة مثبتة في شوارع وعمارات المدينة التي قامت مكانها باسم إق حصار، وهي إلى الجنوب الشرقي من ازمير.اشتهر أهلها بمهارتهم في صناعة الارجوان وصبغه. وقد كانت ليديا بائعة الارجوان من هذه المدينة (1 ع 16: 14) وفي هذه المدينة قامت احدى الكنائس السبع التي تحدث عنها سفر الرؤيا (رؤ 1: 11 و 2: 18 - 29).ويظن أنه كان فيها قديماً معبد لسمبث وهو خارج المدينة، وربما كانت سميت هي النبية المشار أليها في رؤ 2: 20 - 22.'),
(850, 'ثِيني', '(رؤ 18: 12) نوع من الشجر يمتاز خشبه برائحة زكية علاوة على لونه الوردي الجميل وصلابة أعواده لذلك قدّره الرومان وطلبوه فارتفع سعره واعتبر من الكماليات، يذكره سفر الرؤيا بصفة مجازية ضمن الأشياء التي تباع في أسواق بابل الشريرة.'),
(851, 'جَابَِر', 'اسم عبري معناه ((رجل أو بطل)) ورد مرتين في العهد القديم. أولاً جابَر بفتح الباء (1 مل 4: 13) أبو أحد وكلاء سليمان في راموت جلعاد وكورة ارجوب وتسمى جلعاد الشمالية. والثاني ابن اوري بكسر الباء جابِر وكيل القسم الجنوبي من جلعاد (1 مل 4: 19). واللفظ بصورتيه اسم وتحد في الاصل وربما تشيران إلى رجل واحد.'),
(852, 'جَابي', '(دا 11: 20) و (زك 9: 8) اطلب ((جباية)).'),
(853, 'جَاثَر', 'اسم ارامي معناه غير معروف على وجه التحقيق، وهو من اولاد ارام واخو عوض (تك 10: 23)، وقد ذكرت الكلمة في 1 اخبار 1: 17 ولم يعرف مكانهم.'),
(854, 'جَاحَر', 'اسم عبري معناه ((ضعيف)) وهو رأس لعشيرة النثينيم وأحد الراجعين من السبي (عزرا 2: 47) وذكر أيضاً في نح 7: 49. وقد تكون الكلمة العربية جُحر بضم الجيم مشتقة منها، ولذلك قيل أن معنى الكلمة كمين.'),
(855, 'جَاحَم', 'اسم عبري معناه ((محترق من الشمس)) وهو اسم أحد أولاد ناحور أخي ابراهيم (تك 22: 24) وكان ولد له من سريته رؤومة.'),
(856, 'جَاد', 'اسم عبري معناه ((طالع حسن)) وهو:(1) أحد ابناء يعقوب من زلفة جارية ليئة (تك 30: 11) في ميلاده قالت ليئة ((بسعد)) وكانت بركة يعقوب له ((جاديزحمه جيش. ولكنه يزحم مؤخرة)) (تك 49: 19) وموسى بارك الله لأنه وسع جاد (تث 33: 20).كان لجاد سبعة بنين كل منهم اسس عشيرة من قبائل الجاديين (تك 46: 16 وعد 26: 15 - 18).(2) جاد، رائي أو نبي، وهو صديق لدواود جاء إليه لما كان داود في مغارة عدلام. ونصحه أن يترك ذلك المكان، إلى غيره أكثر اماناً (1 صم 22: 5) صار لداود مشيراً وراثياً ثم جاء إلى داود بعد ذلك ليختار، بناءً على امر الله، واحداً من ثلاثة أنواع من القصاص لما احصى داود الشعب (2 صم 24: 11 - 14).ساعد في ترتيب الخدمة الموسيقية في بيت الرب وتنظيمها (2 اخبار 29: 25) وكتب تاريخ ملك داود (1 اخبار 29: 29).(3) سبط جاد المتناسل من جاد بن يعقوب (عد 1: 14 وتث 27: 13 وحز 48: 27 و 28). في الاحصاء الأول الذي تم في البرية كان عدد الجاديين كافياً لحمل السلاح وهو 45650 (عدد 1: 24 و 25) وفي الاحصاء الثاني 40500 (عدد 26: 15 - 18) وكان جبابرة سبط جاد مرافقين لداود في صقلغ (1 اخبار 12: 8 - 15) واشتهر من هذا السبط رجلان هما: برزلاّي الجلعاجي (2 صم 17: 27) وايليا التشبي من مستوطني جلعاد (1 مل 17: 1).وكان نصيب سبط جاد شرق الاردن. عينه لهم موسى واشترط عليهم أن يعبروا النهر إلى اخوتهم وان يساعدوهم في غزو ارض كنعان (عد 32: 30 - 32) ويمتد نصيبهم بين تخم رأوبين من الجنوب ونصف سبط منسى من الشمال. واشتمل تخمهم على الجزء الجنوبي من جبل جلعاد من اليبوق جنوباً إلى حشبون ومن حشبون إلى ربة عمون غرب وادي الاردن، وشملت من الوادي، الشاطيء الشرقي من بيت نمرة بالقرب من الطرف الشمالي للبحر الميت وإلى بحيرة جنيسارت (يش 13: 24 - 28 وتث 3: 12 و 16 و 17) وكان الاقليم أيضاً كانت تقع راموت جلعاد التي تعنيت مدينة ملجأ (يش 20: 8) وكان تخمهم ساحة قتال للحروب التي جرت بين آرام واسرائيل (2 مل 10: 33).ولما احصى داود اسرائيل ويهوذا خرج رؤساء الجيش وعبروا الاردن ونزلوا في وسط وادي جاد تجاه يعزير (2 صم 24: 5).'),
(857, 'جَادَر', 'اسم عبري معناه ((جدار أي حائط أو سور)) وهي اسم مدينة في اقصى جنوب أرض يهوذا (يش 12: 13) ومكانها الآن غير معروف ويحتمل أن تكون بيت جادر أو جدور.'),
(858, 'جَادِي', 'ابو منحيم ملك اسرائيل (2 مل 15: 14 و 17).'),
(859, 'جَارَب', 'اسم عبري معناه ((جراب)) وهو اناء مصنوع من الجلد. وجاءت بمعنى برص أو مرض الجرب. جارب اليثري وهو أحد ابطال داود (2 صم 23: 38 و 1 اخبار 11: 40).'),
(861, 'جَازَر', 'اسم عبري معناه ((نصيب أو مهر العروس)). وهو اسم مدينة كنعانية قديمة يرجع تاريخها إلى ثلاثة آلاف سنة ق. م. قريبة من لخيش وبيت حورون السفلي تتاخم ارض افرايم (يش 10: 33 و 16: 3 و 1 أي 7: 28) وتجاور نصيب افرايم وقد اعطيت مع مسارحها لبني قهات اللاويين (يش 21: 21 و 1 اخبار 6: 67) وقد عجز الافرايميون عن طرد الكنعانيين منها فسكنوا معهم مدة من الزمن (يش 16: 10) وكان الكتعانيون عبيداً تحت الجزية (قض 1: 29).ذكرت جازر في حروب داود في اكثر من موقعة حصلت فيها أو بالقرب منها (2 صم 5: 25 و 1 اخبار 20: 4).وقد غزاها فرعون ملك مصر واحرقها واعطاها مهراً لابنته امرأة سليمان، وبعد ذلك اعاد سليمان بناءها (1 مل 9: 15 - 17) وكانت أيضاً ذات أهمية في حروب المكابيين الذين قوّوا تحصيناتها (1 مك 9: 52) وقد أخذها سمعان المكابي بعد حصار. أما اسمها الحالي فهو تل الجزر وهي تقع على بعد 18 ميلاً شمالاً غرب اورشليم وعلى بعد خمسة أميال وثلثي الميل شرقي عقرون، وقد وجدت نقوش باليوناني والعبراني في خرائبها تشير إلى اسم المدينة.'),
(862, 'جَازِيز', 'اسم عبري معناه ((جزاز)) وهو اسم احد أولاد كالب واسم احد احفاده (1 أي 2: 46).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(863, 'جَاسَان', '(تك 45: 10) وهي منطقة خصيبة في مصر كثيرة المرعى للقطعان والمواشي، واقعة شرق الدلتا. وهي المعروفة الآن بالشرقية الممتدة من جوار ابي زعبل إلى البحر ومن برية جعفر إلى وادي توميلات وقد اعطاها يوسف لابيه واخوته فسكنوا فيها هم وذريتهم من بعدهم نحو مائتي سنة. وكانت تعد من ((أفضل الأرض)) (تك 46: 34 و 47: 6) وهي تكون جزءاً من أرض رعمسيس، وهناك استقبل يوسف أباه واخوته لما حضروا من ارض كنعان (تك 46: 27 و 29) وفي وقت اضطهاهم كان الشعب مقيماً هناك (خر 8: 22).'),
(864, 'جَاعَش', 'اسم عبري معناه ((رعد أو زلزلة)) (قض 2: 9) هضبة تابعة لسبط افرايم جنوب تمنة سارح (يش 24: 30) هناك دفِن يشوع. ويقال إن أودية جاعش المذكورة في 2 صم 23: 30 و ا اخبار 11: 32 كانت في تلك النواحي.'),
(865, 'الجَامِعَة', 'السفر الحادي والعشرون من العهد القديم، ومعنى اسمه الكارز، ودعي بسفر الجامعة في التي معناها من يجلس في محفل أو يتكلم في مجتمع أو كنيسة. والاسم الجامعة يشير إلى سليمان بن داود الملك في اورشليم (جا 1: 1) وقد فاق. السابقيين جميعاً في اورشليم في الحكمة والغنى (جا 1: 16 و 2: 7 و 9).كتبه سليمان في شيخوخته أو في كمال اختباره، وفيه يلقى درساً عظيماً من حياته ويعبّر عن عواطفه ونظراته إلى الحياة التي اختبرها فما جاء في (1: 12 - 14) يشير إلى اختباره الفعلي وهو ملك، ويشير إلى انّ كل شيء باطل والادلة على أن كاتبه سليمان، بخلاف ما ذكر سابقاً، ما ياتي:(1) تب عن سليمان أنه اتخذ نساءً كثيرات بينهن كثير من الاجنبيات وعابدات الاوثان اللواتي أملن قلبه عن اتباع الرب (1 مل 11: 3 و 4) ويقول الكاتب (ص 7: 26 و 28) ((وجدت أمراً من الموت المرأة التي هي شباك وقلبها اشراك ويداها قيود)) ويقول ((اما امرأة فبين كل اولئك لم اجد)).(2) يقال أن سليمان ألف أمثالاً عديدة ولا شك أنه كتب أكثر سفر الامثال، ويقال أنه تكلم بثلاثة آلاف مثل (1 مل 4: 32) أما الكاتب فيقول (ص 12: 9) ((واتقن امثالاً كثيرة)) وقد قال بعض المنتقدين أن في هذا الكتاب كلمات غير عبرانية محضة، فلذلك يجب أن يكون الكاتب من جيل آخر وأنه شخص يشبه سليمان. غير أن إدخال بعض الكلمات الغربية في تأليف إنسان امتدت صلاته التجارية إلى أمم والسنة كثيرة لا يستغرب.إن رسالة هذا السفر خاصة بالحياة الحاضرة على الارض. ولسؤال هل من نفع للانسان من كل تعبه (ص 1: 3) فيجيب عن هذا السؤال من اختباره المتنوع. ويجد الجامعة أن المصدر الوحيد لكفاية الانسان هو في ذاته، وفي استعمال قواه العقلية والجسدية في اتفاق مع نواميس الكون الطبيعية والادبية التي وضع فيها (ص 2: 24) ثم ينتقل من ذاته إلى العالم الخارجي (ص 3) فيبحث في حال الانسان في الزمان فيجد أن الله قد رتب كل شيء حسناً في وقته وهو وقت ثابت لا يتغير. وكل جميل في وقته غير أن الناس يعملون الشر ويظلمون بعضهم بعضاً بحيث أن البار يقع تحت القصاص، والشرير ينجو والتقى لا يجازي خيراً هنا، والشفي لا ينال جزاء أفعاله الرديئة. ويستنتج على من ذلك ان الله سيدين الطرفين (ص 3: 16 إلى 4: 3) فينصف الواحد ويقاص الآخر. ويستدل من ذلك على اعتقاده بالدينونة والحياة الآتية.والثروة أقل نفعاً من الصحة (ص 5: 1 إلى 6: 9) ثم يتحدث الجامعة عن الصيت الذي هو أفضل من الدهن الطيب وكيفية الحصول عليه (ص 7: 1 إلى 10) الحكمة صالحة (ص 7: 12) وهي تحبي أصحابها وتفيد في التعامل مع الملوك (ص 8: 1 - 8) ويذكر الجامعة هذه الحقيقة العامة ((انة التقوى هي افضل سياسة)) (ص 8: 10 - 15) وان الموت يأتي للجميع على السواء. فليبحث الانسان إذاً عن اللذة في افراح الحياة المتواضعة العادية لأنها نصيبه تحت سياسة الله الادبية. فليذكر إذاً الشاب خالقه. وتختم النصائح بهذه: ((اتقِ الله واحفظ وصاياه لان هذا هو الانسان كله لان الله يحضر كل عمل إلى الدينونة)) (ص 11: 9 إلى 12: 14) ويستعمل الكاتب في حججه فلسفة كانت سائدة في وقته وهي: ان يكيف الانسان نفسه للظروف والاختبار المحيطين به. وقد شك بعض اليهود في قانونية هذا السفر ولكنهم اقتنعوا أخيراً بمكانته بين الاسفار القانونية. ويلاحظ أنه لا توجد اقتباسات مباشرة أو تلميحات منه في كتب العهد الجديد.'),
(866, 'جَامُول', 'اسم عبري معناه ((مفطوم))  من نسل هرون رئيس الفرقة الثانية والعشرين من الكهنة مدة ملك داود (1 اخبار 24: 17).'),
(867, 'جَأوئِيل', 'اسم عبري معناه ((عظمة الله أو مفدي من الله)) ابن ماكي، وهو أحد الجواسيس من سبط جاد (عد 13: 15).'),
(868, 'جَبَّاي', 'اسم عبري معناه ((جابي الجزية)) بنياميني كان ساكناً في اورشليم (نح 11: 8).'),
(869, 'جُبّ', 'حفرة كبيرة عميقة تحفر في الأرض لجمع المياه أو قد تكون موجودة من قبل. والجب يسمى أيضاً بئراً قد يفرغ ملؤه (زك 9: 11) ويبقى فيه الطين نظير الجب الذي انزلوا فيه يوسف (تك 37: 22 - 34) وارميا النبي (ار 38: 6) ويستعمل مجازياً للقبر والموت ((كحفرة)) (أي 33: 18 - 30) وسمي ((حب الهلاك)) (مز 40: 2) وموضع الموتى (مز 28: 1 و 30: 3 و 88 : 4).'),
(870, 'جُبَّة', '(1 صم 24: 4) كان يرتديها شاول وقطع داود طرف جبته (اطلب ((ثوب))).'),
(871, 'جَبَّاثا', 'لفظة ارامية معناها ((تل مرتفع أو جبهة)) ولما ترجمت لليونانية (ليثو ستروتن) فهمت بأنها موضع مستوي مرصوف ببلاط يشبه مقعد القضاة. ومنه حكم بيلاطس على يسوع واسلمه للموت (يو 19: 13) وكان يوجد أمام قصر هيردوس فضاً. فسيح فيه شيء من الارتفاع والاستدارة. وقد اكتشف شمالي الحرم الشريف حديثاً ساحة على بلاطها نقوش حفرها الجنود الرومان ترجع إلى عصر السيد المسيح.'),
(872, 'جِبَّثُون', 'اسم عبري معناه ((جبل او ارتفاع)) اسم بلدة لبني ىدان اعطيت لبني قهات اللاويين (يش 19: 44 و 21: 10 - 23) امتلكها الفلسطيون وهناك قتل ناداب وهو يضرب عليها حصاراً (1 مل 15: 27) وحاصرها عمري قبل أن يعتلى العرش (1 مل 16: 15 -17) ودام حصارها سبعاً وعشرين سنة. وربما تكون هي المعروفة اليوم بتل الميلاط وتقع على بعد خمسة أثمان الميل جنوب نمنة وشرق عقرون مباشرة.'),
(873, 'جَبَّار', '(الرجل الذي يقتل في الغضب) ((مولود الأرض)) (تك 10: 8 و 9) يقصد بهذه الكلمة أحياناً ذو البأس والجور والتعدي والأثم واحياناً يقصد بها طول القامة وذو البنية الغريبة الهائلة. وكان الجبابرة اشداء البأس معطلين (تك 6: 4) نظير التيتانيين المذكورين في ميثولوجية اليونان واساطيرهم. وكان بنو عناق سكان حبرون قديماً من اشهر الجبابرة المذكورين في الكتاب المقدس فخافهم بنو اسرائيل جدَّاً حتى كان من جواسيسهم أنهم قالوا فيهم ((اننا كنا في عيوننا كالجراد وكذلك كنا في اعينهم)) وهنا دليل قاطع على ما بلغه بنو عناق من طول القامة وشدة البطش والقوة. ومن الابطال المعدودين والجبابرة المشهورين قديماً عوج ملك باشان (تث 3: 1) الذي كان سريره من حديد طوله تسع اذرع وعرضه اربع اذرع، وجليات (1 صم 17: 4) الذي كان طوله ست اذرع وشبر، والمصري الذي قتله بنايا الذي كانت قامته خمس اذرع (1 اخبار 11: 23). وقد قام افراد كثيرون اتصفوا بطول القامة وعظمة البنية مثل الايميين والرفائيين والزمزميين وامم قديمة من كانوا كنعان وغيرها في شرق الاردن (تث 1: 28 و 2: 10 و 11 و 20 و 21 و 9: 2) ولما اخذ العبرانيون حبرون لجأ العناقيون الهاربون من الهلاك إلى المدن الفلسطينة (يش 11: 21 - 22). ومن هؤلاء يشبي بنوب الذي من أولاد رافا.واطلقت لفظة جبار كاسم من اسماء الله (مز 24: 8) نظراً لقوته، والجبار مضاف إلى ((ال)) التعريف اسم لأحد الابراج ((اوريون)) وهو مجموعة من الكواكب تحتوي على 1000 كوكب ويرى بالمنظار المقرب (تلسكوب) (عاموس 5: 8). والكلمة في هذا الموضع هي ترجمة للكلمة العبرية ((كسيل)) وفي الميثولوجية الكلاسيكية يشبه الجبار بانسان عظيم القوة اشتهر بصنع الحديد وبالصيد، ولما قتله الالهة ديانا انتقل للسموات وربط بها (ايوب 38: 31) ويكنى عنه هنا بربط لا يمكن للبشر حلها، وترى هذه المجموعة بقرب (الدب الاكبر) في خطوط طولية وعرضية.'),
(875, 'جِبْرَائِيل', 'اسم عبري معناه ((رجل الله أو اظهر الله ذاته جباراً)) اسم لملاك ذي رتبة رفيعة ارسل ليفسر رؤيا لدانيال النبي (دانيال 8: 16 - 27) وبعث مرة في زيارة لنفس النبي ليعطيه فهماً وليعلن له نبوة السبعين اسبوعاً (دانيال 9: 21 - 27).وقد ارسل إلى اورشليم ليحمل البشارة لزكريا في شأن ولادة يوحنا المعمدان (لوقا 1: 11 - 22).وارسل أيضاً إلى الناصرة ليبشر العذرلء مريم بأنها ستكون أمَّا للمسيح (لوقا 1: 26 - 38) وقد وصفت جبرائيل نفسه بانه واقف أمام الله (لوقا 1: 19).'),
(876, 'جَبَع جِبْع جَبْع', 'اسم عبري معناه ((تل)) مدينة في بنامين (يشوع 21: 17) وكانت تقع على الطرف الشمالي لمملكة يهوذا (2 ماوك 23: 8 وزكريا 14: 10) وتوجد الآن قرية تحمل الاسم القديم واقعة على قمة هضبة وتبعد مسافة 6 اميال شرق اورشليم.'),
(877, 'جَبَعَا', 'اسم عبري معناه ((تل)) واحد من نسل كالب 1 اخبار 2: 49.'),
(879, 'جِبْعَة', 'اسم عبري معناه ((تل)) اسم لعدة قرى منها:(1) حبعة الله: (1 صموئيل 10: 5).(2) جبعة بنامين: (يشوع 18: 28 وقضاة 20: 10 و 1 صموئيل 13: 2) وهي تل الفول الحالية على بعد 4 اميال شمال اورشليم شرقي الطريق من اورشليم إلى نابلس.(3) جبعة في حبال يهوذا: (يشوع 15: 57) على بعد 10 اميال إلى الشمال من حبرون.(4) جبعة شاول: (1 صموئيل 10: 26 و 11: 4 و 15: 34 و 22: 6 و 23: 19 اشعياء 10: 29) وهي نفس جبعة بنيامين وكانت عاصمة ملك شاول.'),
(880, 'جِبْعُون', 'اسم عبري معناه ((تل)) المدينة الرئيسية للحويين من أهل كنعان (يشوع 9: 3 و 17 و 11: 19) وينتمون أيضاً إلى الاموريين (2 صموئيل 21: 2) وقد امتلكوا أيضاً كفيرة وبئيروت وقرية يعاريم (يشوع 9: 17) ونبعد جبعون عن اورشليم بنحو 50 غلوة أو 5 اميال إلى الشمال كما يقول يوسيفوس. أما موقعها الحالي فيعرف بقرية الجيب الواقعة على قمة هضبة شمال غربي اورشليم. عمل سكانها صلحئأ مع يشوع وبدهائهم قطعوا عهداً (يشوع 9: 4 - 15) ولما اكتشف خداعهم صاروا لبني اسرائيل عبيداً وجامعي حطب، لكن المعاهدة احترمت فلما اجتمع ملوك الاموريين الخمسة عليهم وحاربوهم اتى بنو اسرائيل لمعونتهم وانقذوهم من اديهم (يشوع 10: 1 - 11 واشعياء 28: 21).وبعد عدجة قرون لما قتل شاول الجبعونيين اقتص العدل من بنيه السبعة وصلبوا (2 صموئيل 21: 1 - 9) وقد صارت  جبعون من نصيب بنيامين (يشوع 18: 25) كما اعطيت مع مسارحها لبني هرون (يشوع 21: 17) سكنها اسلاف شاول وقتاً من الزمن وكان لهم نفوذ (1 اخبار 8: 29 و 9: 35) وفي الصراع الذي قام بين ايشبوست وداود شبت معركة في جبعون (2 صموئيل 2: 8 - 17 و 24 و 3: 30) الكسر فيها رجال اسرائيل امام عبيد داود في بركة جبعون وهناك سقط الرجال الاربعة والعشرون.وفي جبعون انتصر داود على الفلسطينيين (1 اخبار 14: 16) وعند صخرة جبعون العظيمة قتل يوىب عماساً بالسيف (2 صموئيل 20: 8 - 10) وفي جبعون حكم داود في نهاية ايامه وحكم سليمان في بداية ملكه. وقبل يبنى الهيكل قدم سليمان هناك ذبائح، وهناك أخذ في الحلم رسالة الله (1 ملوك 3: 4 - 15 و ا اخبار 16: 39 - 40 و 21: 29 و 2 اخبار 1: 3 و 6 - 3) ولما رجع الجبعونيون من السبي عاونوا في بناء سور اورشليم (نحميا 7: 25 و 3: 7) وفي الجنوب الشرقي من قرية الجيب الحالية يوجد نبع ماء يتصل بخزان صناعي ويسمى هذا في العهد القديم بركة جبعون (2 صموئيل 2: 13) حيث التقى يوآب بانبير وقتل من عبيد داود 19 رجلاً وعسائيل ومن رجال ابنير وبنيامين مات 360 رجلاً، وفي برية جبعون انتصر يشوع على الملوك الخمسة المتعاهدين. وهناك حدثت معجزة وقوف الشمس عن المغيب (يشوع 10: 12 -14) وقد اكتشف اخيراً درج عظيم يوصل إلى بئر عميقة تصل إلى نبع الماء في جبعون.'),
(881, 'الجِبْعُونيُّون', 'هم سكان جبعون المذكورة آنفاً أو سكان القرى الثلاث المتصلة بها (2 صموئيل 21: 1 - 4 ويشوع 9: 3 و 7 و 17).'),
(882, 'الجِبْعِي', 'من جبعة (1 اخبار 12: 3) أحد الابطال الذين جاءوا إلى داود إلى صقلغ.'),
(883, 'جَبَل و جِبَال', '(1) ذكرها موسى وكتبة الوحي على سبيل الالقاب والاعلام. فلقب موسى فلسطين ببلاد الهضاب والاكام نظراً لكثرة جبالها التي توسع مساحتها. وكان اعلاها في فلسطين جبل حرمون ثم جبال لبنان وجبل صهيون وجبل المريا وجبل الكرمل وجبل الزيتون وجبل تايور (اطلب ((لبنان وكرمل وتابور)) ... وغيرها في مواضعها).(2) هذه الجبال لا تحيط بالبلاد على هيئة سور لوقايتها بل تخترقها في أنحاء مختلفة وتتشعب في جميع جهاتها. وأما جبال اسرائيل (حزقيال 36: 1) فيقصد بها البلاد بأسرها لكونها بلاداً جبلية وكثيرة الهضاب . وكانت أكثر جبال فلسطين تحرث من اسافلها إلى قننها ولم تزل ىثار الاجلال على جوانبها حتى اليوم.(3) يذكر كتبة الوحي الجبال على سبيل الاستعارة في اشعارهم ونبواتهم فالجيل استعارة طبيعية للبقاء والاستمرار الازلي الابدي (تثنية 33: 15 وحبقوق 3: 6) وهي استعارة للثبات (مزمور 30: 7 واشعيا 54: 10) وللصعوبات ومخاطر الحياة المتعبة (ارميا 13: 16) وللعقبات التي يصعب تسلقها أو يقوى عليها (زكريا 4: 7 ومتى 21: 21) وشبهت وقاية الله وحمايته شعبه بالجبال (مزمور 125: 2) وقد ورد تشبيه ملكوت المسيح بجبل (اشعياء 2: 2 و 11 : 9 ودانيال 2: 35) اطلب ((اكمة)).'),
(884, 'جِبّال', '(1) مذكورة في (مزمور 83: 7) هي تعني الجزء الشمالي من جبال ادوم وهو موضع معروف أيضاً بتيمان.\n(2) البلاد المجاورة لاورشليم (الجبال) ذهبت إليها مريم العذراء لتزور اليصابات (لوقا 1: 39).'),
(885, 'الجَبَل الأقْرَع', 'وهو جبل املس جنوب فلسطين في طريق جبل سعير (يشوع 11: 17 و 12: 7) في شمال شرقيعبرة في وادي المارة. واسمه في الاصل جبل حلق.'),
(886, 'جَبل يَعَاريِم', 'انظر ((يعاريم)).'),
(887, 'الجِبَال المُشَعَّبَة', '(نش 2: 17) هي جبال ليست ملساء بل خشنة وهي مجاورة لسلسلة جبال لبنان.'),
(888, 'الجِبْليُّون', 'أهل مدينة جبيل (يشوع 13: 5) ومشهورون بكونهم قاطعي الاحجار.'),
(889, 'جُبْن', 'روب اللبن وتخثيره وتجميده بعزل المصل منه ثم يضغط على هيئة اقراص (1 صم 17: 18 و 2 صم 17: 29 واي 10: 10) وقد ورد ذكر الجبن في الكتاب المقدس اربع مرات. وطريقة التخثير قديماً كانت بازهار الارضي، وحديثاً بمواد كيماوية أو منفحة. ثم يحفظ الجبن في سلال أو اواني خزفية إلى حين الحاجة (ام 30: 33) ((لأن عصر اللبن يخرج جبناً). ويعرف قسم من اورشليم بوادي الجبانيين كما يقول يوسيفوس.'),
(890, 'جَبْهَة', 'يقال أن العبد كانوا قيماً يوسمون على جباههم. وجاء في رؤيا 7: 3 ان ملاك الله امر بختم بعض الرجال على جباههم لانقاذهم من الخراب وفي رؤيا 14: 9 ان عبيد الوحش وسموا على جباههم، وفي رؤيا 17: 5 كان مكتوباً على جبهة المرأة الزانية ((سر)) وبما أن العادة جرت بأن تغطي المرأة جبهتها بالبرقع كانت المكشوفة الجبهية تحسب زانية (ارميا 3: 3) والجمع جباه (حزقيال 3: 7 ورؤيا 7: 3).'),
(891, 'جِبَايَة', '(جابي) (مت 17: 25 ورو 13: 7) مال يدفع على البضائع الصادرة والواردة (اطلب ((جزية))).'),
(892, 'مكان الجِبَاية', 'اطلب ((عشار)).'),
(893, 'جُبَيل', 'مدينة فينيقية على ساحل البحر الابيض المتوسط شمال بيروت على بعد 20 ميلاً منها وعلى بعد 42 ميلاً من صيدون وسكانها هم الجبيليون.وكانت تسمى قديماً عند اليونان ببلوس وأما عند العرب الآن تسمّى جبيل وقد تكون تصغير كلمة جبل. كانت تابعة لمصر في القرن الخامس عشر قبل الميلاد وقد حفظت لنا المراسلات المتبادلة بين حاكمها رب عجاي مع فرعون في الرسائل المعروفة بالواح تل العمارنة. وقد اشتهرت أيضاً في أيام عظمة صور بعمل السفن (حز 27: 9). وفيها الآن خراب عديدة، تشهد بعظمتها القديمة. وقد اظهرت الكشوف الحديثة اثاراً ترجع إلى عهد الفراعنة والفينيقيين والرومان والاتراك. ووجدت فيها مدافن قديمة وضعت فيها رفات الاموات في جرار من الفخار.'),
(894, 'جَت', 'اسم عبري معناه ((معصرة)) وهي إحدى مدن الفلسطينيين الخمس العظمى (1 صم 6: 17 و 7: 14) اشتهرت بكونها مسكناً لبقية من العناقيين وهم اناس طوال القامة (يش 11: 22 وعد 13: 33 وتث 2: 10 و 11). وهي في تخوم دان وولد فيها جليات الجبار وغيره من رجال الجبابرة الفلسطينيين (1 صم 17: 4 و 2 صم 21: 15 - 22 و 1 اخبار 20: 4 - 8) وكانت حصناً من حصونهم (يش 11: 22). وكان اسم ملكها اخيش (1 صم 21: 10 - 15 و 27: 1 - 7).ثم اخذها داود من ايديهم (2 صم 15: 18 و 1 اخبار 18: 1) ثم دارت عليها نكبات الزمان فاخذها الفلسطينيون (1 مل 2: 39) ثم عادت ليهوذا فعززها رحبعام (2 اخبار 11: 8) ثم أخذها حزائيل ملك ارام (2 مل 12: 17) ثم استرجعها يهوآش (2 مل 13: 25) وبعد رجوعها إلى يد الفلسطينيين هدم عزيا سورها (2 اخبار 26: 6).يذكرها ميخا في اقتباس تاريخي عفا عليها الزمن (مي 1: 10 و 2 صم 1: 20). ويظن أن موقعها الحالي ما يعرف بتل عراك المنشية وهي على بعد ستة أميال ونصف غرب بيت جبرين.'),
(895, 'جِتَّايِم', 'سم عبري معناه ((معصرتان)) قرية لبنيامين (نح 11: 33) هرب إليها البئيروتيون من قسوة شاول (2 صم 4: 3) وقد ذكر اسم جامتيتي في الواح تل العمارنة وموقعها قرب مدينة الرملة.'),
(896, 'جَتّ حَافِر', 'اسم عبري معناه ((معصرة الحفرة أو البئر)) (2 مل 14: 25) وهي مدينة من مدن زبولون في أرض رأس النبي يونان (2 مل 14: 25).وكانت في أيام جيروم قرية صغيرة على بعد ميلين شرقي صفورية موضعها مزرعة المشهد على بعد 3 اميال شرقي الناصرة. أما الموقع الأصلي فكان جنوب خربة الزرة، وفيها أحد قبور يونان.'),
(897, 'جَتّ رِمُّون', 'اسم عبري معناه ((معصرة الرمان)) وهو اسم:(1) مدينة لسبط دان (يش 19: 45) تعينت نصيباً للاويين من بني قهات (يش 21: 24 و 1 اخبار 6: 69) ويظن أنها تل ابو زيتون شرقي جريشة في سهل شارون بالقرب من يافا.(2) مدينة في سبط منسى غرب الاردن اعطيت للاويين من بني قهات (يش 21: 25) ويحتمل أنها سميت خطأ بلعام (1 اخبار 6: 70).'),
(898, 'جَتِّي', 'وجيتون وهم سكان جت اطلب ((جت)).'),
(899, 'جَتِّيَّة', 'تسمية لآلة موسيقية وقد ذكرت الكلمة عنواناً لمزمور 8 و 81 و 84. مؤنث الصفة جتي وهي آلة موسيقية كانت تستعمل في جت. أو هي أغنية قطاف الكرم أو نشيد للحرس الجتي (2 صم 15: 18).'),
(900, 'جَثْسَيْمَانِي', 'كلمة ارامية معناها ((معصرة الزيت)) (مت 26: 36) وكان بستاناً فيه اشجار الزيتون ومعصرة لعصره وهو يقع شرق اورشليم فيما وراء وادي قدرون قرب سفح جبل الزيتون (مت 26: 30 ومر 14: 26 و 32 ويو 18: 1) كان المسيح يتردد إليه كثيراً طلباً للعزلة وترويح النفس (لو 22: 39 ويو 18: 2).وهو الآن مكان مقدس. لأنه كان مكان المه وتسليمه والقبض عليه (مت 26: 36 - 56 ومر 14: 32 - 52 ولو 22 : 39 - 53 ويو 18: 1 - 12).ويرجح أن البستان الذي ذهب إليه المخلص يقع قليلاً إلى شرق الجسر الذي يوصل ما بين باب استفانوس ووادي قدرون وهو مفترق طريقين. والبستان مربع الشكل 150× 140 قدماً. وفي عام 1848 احاطه الرهبان الفرنسيسكان بسور، فيه الآن ثماني شجرات من الزيتون متناهية في الكبر يشاهد عندها آثار اوائل العهد العربي في لاورشليم في القرن السابع فهي بالطبع لم تشاهد آلام المسيح لأن تيطس قطع جنيع الاشجار حول اورشليم وقت حصارها كما يذكر المؤرخ اليهودي يوسيفوس.وقد اشار الرحالة روبنسون وجيروم وغيرهما إلى أن المكلن يقع في سفح جبل الزيتون وأن كان طمسون يعتقد ان المكان الاصلي لا بد ان يكون بعيداً عن اسواق اورشليم وضوضائها عدة مئات من الياردات. والرهبان يجمعون نوى زيتونة فيعملون منها خرزاً للمسابح. ويقصد السياح البستان للزيارة.'),
(901, 'جَحَر', 'اسم عبري معناه ((ضعف)) وهو رأس عائلة من النثينيم (عز 2: 47) رجع من السبي وهو أيضاً جاحر (نح 7: 49).'),
(902, 'جَحْش', 'يطلق هذا الاسم على صغار الحمر وقد ورد ذكره في بركة يعقوب لابنه (تك 49: 11) كان يركبه الشرفاء (قض 10: 4).ومنه ما هو بري كجحش الفرا (أي 11: 12). وقد اظهر المسيح تواضعه كما اظهر روح الملوك الاولين لما رفض ان يركب فرساً وركب جحشاً ابن اتان في دخوله الانتصاري إلى اورشليم (زك 9: 9 ومت 21: 5).'),
(903, 'الجَحِيم', '(1) الهاوية مقر الموتى. وهي ترجمة للكلمة العبرية شئول والكلمة اليونانية هاديس وقد فهم العبرانيون هذه الكلمة تارة كأنها قبر أو موت. وقد صورّ كتّاب الاسفار الجحيم كأنه مكان تحت الأرض (عد 16: 20 - 33 وحز 31: 14 - 17 وعا 9: 2) وله ابواب (اش 38: 10) وهو مكان مظلم مخيف سكانه يشعرون ولكنهم في وجود بليد جامد (2 صم 22: 6 ومز 6: 5) تذهب إليه نفوس الجميع (تك 37: 35) فيه القصاص وفيه الثواب، ولا يمكن العودة منه إلى الأرض (1 صم 28: 8 - 19 وتث 11: 6) وهو مكان عريان امام الله (أي 26: 6).ويقول المرنم ان الله هناك (مز 139: 8) وان ارواح شعبه وحالتهم في ذلك المكان كانت تحت عينيه الساهرة. وهذا التعليم عن معرفة الله لشعبه بعد الموت وحضوره معهم ومحبته الدائمة لهم اشتمل على الغبطة للابرار والويل للاشرار بعد الموت. واصبح لهم مقران: الابرار يكونون مع الرب، والاشرار يبعدون عن وجهه. وهذا التعليم أيضاً يتصل بتعليم قيامة الجسد والحياة الابدية والمجد العتيد. وهذه كلها يذكرها العهد القديم كما في ايوب 19: 25 - 27 ومز 16: 8 -11 ... الخ ودا 12: 2 و 3 ويوجد اساس أيضاً لهذه التعاليم في انتقال اخنوخ واختطاف ايليا أما المسيح فقد اوضح كل الغموض حول هذه التعاليم لانه انار الخلود (2 تي 1: 10 ويو 14: 1 - 3 وفيلبي 1: 23).(2) ويوجد معنى ثان مأخوذ من اللفظ اليوناني جيئنه (متى 5: 22 و 29 و 30 ومرقس 9: 47 ولوقا 12: 5 ويعقوب 3: 6) وهذا بدوره مأخوذ من الكلمة العبرية جيهنوم أو وادي هنوم حيث كان يحرق الاطفال لمولك. ومن هذا العمل كان يشار إليه كرمز للخطية والويل حتى صار الاسم اشارة إلى مكان القصاص الابدي (متى 18: 8 و 9 ومرقس 9: 43) واشار إليه بطرس في رسالته الثانية 2: 4 بالطرح في جهنم. كما أن للكلمة جهنم اسماً آخر عند الرومان والاغريق وهو كلمة ترتاروس مقر الويل وهو اسفل الهاوية.'),
(904, 'جِدَار', '(مزمور 62: 3 وأمثال 24: 31 وجامعة 10: 8 واشعياء 30: 13 وحزقيال 13: 5) وهي نفس الكلمة العبرانية في المعنى الحقيقي ما يحيط بالبيت، والمعنى المجازي ما يفيد الحمى والحراسة (انظر ((جدران))).'),
(905, 'جِدْ جُود', '(تث 10: 7) (اطلب حور الجد جاد).'),
(906, 'تَجْدِيد', '(متى 19: 28 وتيطس 3: 5) الكمة اليونانية في الموضوعيين المشار إليهما واحدة وهي ((بالنجاسيا)). وكثرت المباحثة بخصوصها وأول معنى تحمله الكلمة هو الوارد في تيطس 3: 5 بمعنى ميلاد جديد وأما معناها في متى 19: 28 وهو رد الامور إلى حالتها الكاملة في العصر الجديد. ومن الخطأ ان تترجم بكلمة ((قيامة)). وكان مخلصنا يقصد بها العالم بما هو جديد في مجيئه ثانياً إلى العالم، إذ تصير السماء والأرض جديدتين.أما المعنى المراد في تيطس 3: 5 فهو يتبع غسل الميلاد الثاني لما يخلع الانسان العتيق ويلبس الجديد المخلوق حسب صورة الله في البر وقداسة الحق وهو ما يعرف بالولادة من فوق (يوحنا 1: 12 و 13) كما يدعى أيضاً الولادة الثانية وتجديد الروح القدس والقيامة الروحية من موت الخطيئة كما انه يعني الحياة مع المسيح.والتجديد في معناه اللاهوتي هو غرس الحياة الروحية، بمعونة الروح القدس وكلمة الله، في نفسِ قج تلطخت بالآثام والمعاصي لتكون قادرة على ادراك الامور الروحية والايمان بها، والتعلق الشديد بتلك الحالة الجديدة فيشرع الانسان بالاتيان بالاعمال الطيبة والخدمات المبررة والمقاصد الخيرية.والحاصل على التجديد يشعر بتوبة وتواضع، ويفرح بمعرفة الله والشركة معه، ويسر بنعمة المسيح لان تلك العواطف صارت ملكاً لرب المحبة.'),
(907, 'عِيد التَّجْدِيد', 'احد الاعياد اليهودية السنوية انشا الاحتفال به يهوذا المكابي سنة 165 ق. م. تذكاراً لتطهير الهيكل به وتجديده. وبعد ثلاث سنوات دنسه اليونانيين بامر انطيوخس ابيفانيس سنة 163 ق.م. كما جاء في تاريخ المكابيين الاول 4: 52 - 59.ويسمى هذا العيد أيضاً عيد الانوار كما ورد في تاريخ يوسيفس (اثار 12: 7) وكان الاحتفال بهذا العيد يشبه الاحتفال بعيد المظال ويدوم ثلاثة أيام ابتداءً من 25 كانون الاول (ديسمبر). ويقع عادة في الشتاء وقد حضره المسيح مرة على الاقل وفيه القى خطاباً على الجمهور المعيج (يوحنا 10: 22) وما زال اليهود يحتفلون به للأن.'),
(908, 'جِدَّلْتي', 'اسم عبري معناه ((عظّمت الله)) هو لاوي واحد المغنين للرب ابن هيمان، وكان رئيساً على الفرقة الثانية والعشرين من الـ 24 فرقة التي عينها داود لخدمة الغناء في بيت الرب. وقد بلغ مجموعهم الكلي 288 مغنياً (1 اخبار 25: 4 و 7 و 29).'),
(909, 'جَدّي', 'اسم عبري معناه ((سعيد)) جاسوس من سبط منسى استخدم في استكشاف أرض كنعان (عدد 13: 11).'),
(910, 'جَديِئِيل', 'اسم عبري معناه ((نصيبي الله)) احد الجواسيس من سبط زبولون في اكتشاف أرض كنعان (عدد 13: 10).'),
(911, 'جدّيل', '(اطلب ((جديل))).'),
(912, 'جُدران', '((حوائط)) كانوا قديماً يبنون الجدران من اللبن وهو مزيج من التراب والتبن يجبلان معاً بالماء على نسب معلومة ثم يجفف في الشمس، ولذلك كانوا يبنون البناء سميكاً ليقاوم السيول الجارفة التي كثيراً ما كانت تهدم الجدران المذكورة (مزمور 62: 3 واشعياء 30: 13) ذلك لأنه واهي التركيب كما نرى في البيوت الموجودة في القرى سريعة الاندثار. وهذا مما يؤيد قول النبي اشعياء ((قد هبط اللبن فنبني بحجارة منحوتة)) (اشعياء 9: 10) ويتفق هذا مع قول عاموس أن النار تأكل القصور لأن جزءاً عظيماً من اللبن المبينة منه كان من التبن والتراب الكثير المسام (عاموس 1: 7 و 10 و 14).قد استعملت كلمة جدران مجازاً لمّا يتخلى الله عن امة أو عن انسان (اشعياء 5: 5) واستعملها ارميا لعضلات القلب (ارميا 4: 19).كما تستعمل الجدران الواطئة أيضاً أسواراً تقام حول الكروم لتمتد عليها دولي العنب كما يقصد بها وقاية العنب من بنات آوى التي كانت تسطو على حقول القثاء والكروم فتلحق بها ضرراً جسيماً ولا سيما في أيام قطاف العنب. وجاء في نحميا 4: 3 ان طوبيا العموني هزأ باليهود اذ كانوا يبنون السور ((عن ما يبنونه إذا صعد ثعلب فإنه يهدم حجارة حائطهم)) (نحميا 4: 3).'),
(913, 'جَدَرِيُّون', '(متى 8: 28 ومرقس 5: 1 ولوقا 8: 26) هم أهل جدرة المعروفة الآن باسم ام قيس الواقعة جنوب شرقي بحر طبرية ويصفها يوسيفوس المؤرخ اليهودي بأنها مدينة عظيمة. ويصفها يوسابيوس بأنها واقعة شرق الاردن مقابل طبرية. تقع على بعد 3 ساعات غربي اربد على رأس الجبل المشرف على وادي نهر اليرموك وعلى بعد 5 أميال من الشاطيء الجنوبي. اشتهرت أيام المسيح كمدينة تاريخية ذات آثار رومانية، وتوجد فيها ينابيع معدنية حارة تتراوح درجة حرارة الماء فيها بين 108 درجة -  119 درجة ف. ويقول يوسيفوس أيضاً باحتمال وجود مدينتين بهذا الاسم ثانيهما عاصمة بيرية - وهي مكان حصين يسكنه أُناس أثرياء ويعرف هذا المكان اليوم بتل جادور.أما كورة الجدريين فهي المقاطعة كلها. (اطلب ((جرجسيون))).'),
(914, 'جِدْعُوم', 'اسم عبري معناه ((ابادة أو خراب)) اسم لقرية في بنيامين تقع بين جبعة وصخرة رمون (قضاة 20: 45).'),
(915, 'جِدعُون', 'اسم عبري معناه ((حاطب أو قاطع بشدة)) هو ابن يوآس الابيعزري كان يسكن في عفرة (قضاة 6: 11) بينما كان يخبط الحنطة في المعصرة لكي يهربها من الناهبين المديانيين دعاه ملاك الرب ليخلص شعبه من المديانيين (قضاة 6: 12 - 24) وفي الحال قدم ذبيحة، وفي تلك الليلة هدم مذبح البعل الذي لابيه، وبنى مذبحاً للرب (قضاة 6: 25 - 27) . وطلب أهل المدينة موته لكن أباه ألح على أن البعل يدافع عن نفسه ومن هنا صار اسم جدعون يربعل (ليقاتله البعل). جمع معه منسى واشير وزبولون للقتال ولكنه تأنى ريثما تتحقق دعوته بمعجزة جزة الصوف (قضاة 6: 240).قام مع رجاله بعد أن انقص العدد إلى 300 رجل ليكون مجد الانتصار للرب وليس لانسان، قام بهجوم ليلي على محلة المديانيين الذين كانوا حالين في وادي يرزعيل (قضاة 6: 33) عند تل مورة (قضاة 7: 1) وشاع الاضطراب في صفوف المديانيين حتى حارب بعضهم بعضاً وهربوا إلى الاردن حتى حدود بلادهم، ثم ارسل جدعون رسله إلى الافرايميين للقاء المديانيين وقطع خطوط الرجعة عليهم عند مخاوض الاردن، فقام هؤلاء بالقبض على الهاربين وقتلوا اميري المديانين غراباً وذئباً. واتوا إلى جدعون عبر الاردن (قضاة 7: 24 إلى 8: 3). وواصل جدعون ورجاله مطاردة المديانيين إلى حدود الصحراء واسر ملكي مديان وقتلهما (قضاة 8: 4 - 21) كانت هذه النصرة يوماً مشهوراً واصبح يسمّى يوم مديان (اشعياء 9: 4 و 10: 26 ومزمور 83: 11).قامت حركة بعد ذلك لجعل جدعون وبيته بيتاً ملكاً فرفض جدعون قائلاً ((الرب يتسلط عليكم)) (قضاة 8: 22 - 23). على لأأن جدعون أخذ اقراط الذهب التي غنمها الاسرائيليون من المديانيين وصنع منها افوداً وجعل الافود في مدينته في عفرة، ولقد برهن هذا الفعل على عدم حكمة لأنه جعل لنفسه ولبيته ولاسرائيل فخاً (قضاة 8: 27 وانظر لاويين 20: 6).وكان لجدعون زوجات كثيرات وكان له 70 ولداً. ومن سريته ولد له ابيمالك الذي جعل نفسه ملكاً بعد موت أبيه.ومات جدعون بشيبة صالحة بعد ان قضى لاشرائيل نحو 50 سنة. وقد ذكر اسمه في رسالة العبرانيين في العهد الجديد في قائمة ابطال الايمان.'),
(916, 'جِدْعُوِني', 'اسم عبري معناه ((قاطع)) ابو رئيس الكهنة سبط بنيامين في البرية زمن موسى (عدد 1: 11 و 2: 22 و 7: 60 و 65 و 10: 24).'),
(917, 'تَجْدِيف', 'شتيمة ونميمة (كولوسي 3: 8) ويقصد بها في الكتاب المقدس كلام غير لائق في شأن الله وصفاته (مزمور 74: 10 - 18 واشعياء 52: 5 ورؤيا 16: 9 و 11 و 21) وفي شريعة موسى كان عقاب التجديف الرجم (لاويين 24: 10 - 16) وقد اتهم بها نابوت اليزرعيلي زوراً (ملوك الاول 21: 9 - 13) واستفانوس (اعمال 6: 11) وربنا يسوع المسيح نفسه (متى 9: 3 و 26: 65 - 66 ويوحنا 10: 33 و 36).ومن أنواع التجديف على الروح القدس الطعن في معجزات المسيح كقول الفريسيين أن شفاء الأعمى والأخرس الذي تمّ بروح الله إنما هو من اعنال بعلزبول (متى 12: 22 - 32 ومرقس 3: 22 - 30) فمثل هذا التجديف غير قابل للمغفرة واما الذين يشعرون بيأس ظناً منهم أنهم قد جدفوا على الروح القدس، فيأسهم بهذا السبب يظهر أنهم قابلون للتجديد والمغفرة بخلاف الذين قد رفضوا التوبة عن تصلف فقاوموا الروح القدس وتجاوزوا في معارضته وتكذيب انذاره إلى ان صارت التوبة عندهم من المحال. فامثال هؤلاء لا ينالون المغفرة أصلاً.'),
(918, 'جَدائِل', '(تثنية 22: 12) هي أهداب الأثواب عند العبرانيين (اطلب ((هدب))) وعليه فالهدب الذي لمسته المرأة النازفة الدم على ثوب المسيح (متى 9: 20) ربما كان من هذا القبيل، لأن المسيح اطاع الشريعة واكملها (اطلب ((ثياب))) .'),
(919, 'جِدَال ومُجَادَلة', '(1 تي 2: 8) مناقشة مصحوبة بالغضب والدمدمة (فيلبي 2: 14).'),
(920, 'جَدَلْيَا', 'اسم عبري معناه ((يهوه عظيم)).(1) أحد المتنبئين بالعود والرباب وهو ابن يدوثون (1 اخبار 25: 3) وكان رأس الفرقة الثانية من الأربع والعشرين فرقة المكونة من اثني عشر موسيقياً الذين عينهم داود لخدمة بيت الرب (1 اخبار 25: 9).(2) احد اسلاف النبي صفينيا (صفينيا 1: 1).(3) ابن فشحور في زمن ارميا (ارميا 38: 1).(4) رجل من يهوذا عالي النسب وهو ابن اخيقام بن شافان اقامه نبوخذ نصر وكيلاً وحاكماً على فلسطين (يهوذا) بعد غزو اورشليم (ارميا 40: 5 وملوك الثاني 25: 22) أقام في المصفاة ومن هناك اغتاله غدراً اسمعيل بن نثنيا من النسل الملكي بمساعدة بعض رجاله (ارميا 41: 2 و 18).(5) أحد الكهنة من الذين أمالهم عزرا ان يطلقوا نساءهم الغريبة (عزرا 10: 18).'),
(921, 'جَدُور', 'اسم عبري معناه ((حصن أو مكان مسور)) وهو:(1) ابن يعوئيل واخو نير وقيس من اسلاف شاول (1 اخبار 8: 30 - 31 و 9: 35 - 37).(2) ابن فنوئيل (1 اخبار 4: 4).(3) ابن يارد من امرأته اليهودية (1 اخبار 4: 18).(4) مدينة في جبال يهوذا واقعة بين بيت لحم وحبرون (يشوع 15: 58).(5) موضع بين سعير ويهوذا ربما كان في تخوم سبط شمعون بالقرب من الحدود الشمالية الغربية لفلسطين (1 اخبار 4: 39) وقد وردت في الترجمة السبعينية جيرار.(6) قرية في سبط بنيامين (1 اخبار 12: 7) جاء منها اثنان من الابطال الذين يرمون الحجارة والسهام من القسي باليمين واليسار ورافقوا دتود وهو محجوز عن وجه شاول. وموضعها الآن خربة الجديرة بين بيت عنان وبيت لقيا شمال غرب اورشليم.'),
(923, 'جُدْيرَة الجُدْيرِيّ', 'اسم عبري معناه ((حظيرة الغنم)) (يشوع 15: 36) وهي:(1) قرية في سهول يهوذا تقع على بعد أربعة اميال ونصف شمال غربي صرعة واشتأُول (يشوع 15: 36).(2) قرية في تخوم بني بنيامين (1 اخبار 12: 4) وتقع على بعد 6 أميال إلى الشمال الغربي من اورشليم.'),
(924, 'الجَدِيرِي', 'بعل حانان الجديري (1 اخبار 27: 28) أحد رؤساء الاملاك التي للملك داود إقامة على الزيتون والجميز اللذين في السهل انظر ((جديرة)) رقم (1).'),
(925, 'جُدَيْرُوت وجَدِيِرُوت', 'اسم عبري معناه ((حظائر الغنم)) وهي مدينة في سهل يهوا (يشوع 15: 41) وفي مدة حكم آحاز أخذها الفلسطينيون (2 اخبار 28: 18) والمعتقد أنها هي ((كثرة)) الحالية وقد ذكرت في سفر المكابيين الأول باسم قدرون (مكابيين الأول 15: 39) التي هي قرية في وادي سورق على بعد 3 اميال من الجنوب الغربي من عفيرْ و 4 أميال جنوب غربي عقرون.'),
(926, 'جُدَيْرُوَتايِم', 'وهي صيغة المثنى لجديرة في اللغة العبرية ومعناها ((حظيرتان لغنم)) اسم لقرية في سهل يهوذا (يشوع 15: 36) ويرجح أن الاسم هنا ليس لقرية بل لحظيرتين، ولذلك ترجمتها السبعينية بالقول في يشوع 15: 36 ((الجديرة وحظائر غنمها)) لأن المدن أربع عشرة مدينة فقط، ولكن الاسماء خمسة عشر اسماً.'),
(927, 'جَدِيِل وجَدّيِل', 'اسم عبري معناه ((الله زاد وعظم)) الاسم الأول أبو أحد النثينيم (عزرا 2: 47) والثاني من عبيد سليمان (عزرا 2: 56) وكلاهما رجعا من السبي مع زربابل (نحميا 7: 49 و 58).'),
(928, 'جَرَار', 'اسم عبري معناه ((جرة)) وهي ىنية خزفية، وهي مدينة قديمة شهيرة في جنوب شرقي غزة (تكوين 10: 19 و 2 اخبار 14: 13) وقديماً احتلها الفلسطينيون (تكوين 26: 1) وربما كانت هي المكان المعروف الآن بخربة ام جرار. وهذه المدينة اتى إليها كل من ابراهيم واسحق بسبب الجوع، وتعرض كل منهما إلى الكذب على ابيمالك بشأن امرأتيهما. والكلمة كانت لقباً لجميع ملوكها (تكوين 20: 1 و 26: 1 و 6 و 17 و 20) والمعروف عنها أيضاً أن آسا ساق الكوشيين المتقهقرين إليها (2 اخبار 14: 13). ويعين بعض العلماء موقعها الآن على بعد 19 ميلاً من الجنوب الغربي لبيت جبرين.'),
(929, 'أَجْرَب', '(لاويين 21: 20 و 22: 22) هو الشخص المصاب بالجرب وهذا مرض جلدي معروف كان ممنوعاً أن يدخل مصاب به في تقريب الوقائد للرب أو تقديم خبر الهة من نسل هرون.وذكر الجرب من ضمن اللعنات لمن لا يطيع وصايا الرب (تث 28: 27).'),
(930, 'جِرَاب', '(صموئيل الأول 17: 40) وعاء مَصنوع من الجلد المدبوغ أو القماش الخشن يعلق في الكتف بواسطة سير ويوضع فيه زاد السفر ولا غنى لكل راع أو مزارع عن هذا الجراب. ولما كان داود راعياً علق واحداً على كتفه لما قتل جليات.'),
(931, 'جَرَّب - يُجَرّبّ - تَجْرِبَة', '(متى 22: 18 ولوقا 4: 13) وردت الكلمة في صيغة الفغل في الكتاب المقدس أكثر من 50 مرة. وكلمة تجربة 16 مرة، وكلمة تجارب 7 مرات، وكلمة مجرب مجرب 4 مرات وهي تفيد معاني مختلفة.(1) المعنى الغالب يقصد بالتجربة الاغراء على الخطيئة وارتكاب الاثم لذلك دعي الشيطان عدو الجنس البشري الآن ((مجرباً)) (متى 4: 3).(2) وقد يقصد بها امتحان المرء وطاعته (يعقوب 1: 2 و 3).(3) تجربة صبر الله واحتماله (خروج 17: 2 وكورنثوس الأولى 10: 9).(4) اختبار أمانة الله في مواعيده (ملاخى 3: 10).(5) قد يقصد منها بإزاء المسيح أن يوقفوه موقف مذنب (متى 16: 1 و 19: 3 ولوقا 10: 25).أما تجارب المسيح من ابليس في بدء خدمته على الأرض فكانت إغراءً له وتعزيزاً بالتقوى أو ايعازاً له انتصر على قوات الجحيم واسترد الفردوس الذي فقده آدم الول. والمكان التقليدي للمكان الذي جرب ابليس المسيح هو جبل قرنطل قرب اريحا.'),
(932, 'جِرْجَاش وجِرْجَاشِيُّون', 'وردت في صيغة المفرد (تكوين 10: 16) وبصيغة الجمع كإحدى قبائل كنعان (تكوين 15: 21 وتثنية 7: 1 ويشوع 3: 10 و 24: 11 ونحميا 9: 8).'),
(933, 'جِرْجَسِيُّون', 'أهل جرجسة (مت 8: 28 - 33) أو هم المذكورون في انجيل مرقس 5: 1 وانجيل لوقا 8: 26 بالجدريين. والسبب فس هذا التباين الظاهر هو أن متى لما كتب انجيله خاصة لليهود الذين عرفوا تلك الأرض جيداً ذكر موضع المعجزة بالضبط، بينما مرقس ولوقا اللذان كتبا لاجل الأمم لم يذكرا القرية التي بقربها حدثت تلك المعجزة، بل ذكرا كورة الجدريين التي كانت تلك القرية فيها. أما كورة الجدريين (فاطلب جدرة). وقد عزا البعض هذا التباين لاختلاف النسخ القديمة والمخطوطات في ذكر كلمة جرسة وجرجسة وجدرة. ولا تزال خرائب على بحر الجليل تعرف اليوم بالكلمة كرسة على الشاطىء الشرقي من البحر المذكور مقابل مجدلة على مسافة خمسة اميال من دخول الاردن إلى البحيرة وهناك موضع بين وادي سمك ووادي فيق حيث تقترب الهضاب إلى البحر مما يسهل لقطيع أن يندفع مهرولاً إلى البحر.'),
(934, 'جَارِحَة: وجَوَارِح', '(طيور كاسرة) وردت في الكتاب المقدس حقيقة ومجازاً - المعنى الاول ذكر في تكوين 15: 11 عندما قطع الرب مع ابراهيم ميثاقاً وامره أن يقدم ذبائح متعددة وشقها، نزلت الجوارح فكان ابراهيم يزجرها.المعنى الثاني جاء في اشعياء 18: 6 وايوب 5: 7 وارميا 12: 9 حيث استعملت مجازاً للقسوة والخطف والنهب ود ذكرها ارميا هناك بالقول: ((جارحة ضبع ميراثي لي. الجوارح حواليه عليه)).'),
(935, 'جُرْح: وجُرُوح - وجِرَاحَات', 'جاء في نواهي الشريعة الموسوية لجماعة بني اسرائيل بألا يجرحوا أجسادهم لميت وكتابة وسم لا يجعلوا فيهم (لاويين 19: 28). وقد ورد ذكر الجروح في الكتاب المقدس فيما يأتي:(1) حرمت الشريعة على الكهنة أن يجرحوا جراحة في اجسادهم بل يكونوا مقدسين لالههم (لا21: 5) ويقول روبرت جاميسون في شرح هذه العبارات أن هذه العادات كانت سائدة بين الوثنيين في أحزانهم وخرافاتهم مثل الموآبيين والكنعانيين وبعض الهنود.(2) أما جروح أيوب التي سببت شكواه فهي معروفة (ايوب 9: 17).(3) جروح بلا سبب من نتائج الخمر والسكر (امثال 23: 29).(4) جروح المحب وهي امينة للنفع في التوبيخ (امثال 27: 6).(5) جروح المسيح لاجل معاصينا (اشعياء 53: 5).(6) جروح النبي الكاذب (زكريا 13: 6).(7) جراحات بولس وهي سمات لتلمذته للمسيح (اعمال 16: 33).'),
(936, 'جَرَاد', 'ترجمة الكلمة العبرانية ((اربة)) واليونانية ((اكردس)) وهو نوع من الحشرات من فصيلة الجندب مشهور بكثرة عدده وشدة شراهته. والنوع المذكور بكثرة في الكتاب المقدس هو النوع الاعتيادي المسمّى باللاتينية ايديبودا الراحلة Oedipoda Migratoria.وصفه: طول الواحدة منها بوصتان أو أكثر لها أربعة أجنحة، الاماميان أضيق والخلفيان واسعان شفافان، وللواحدة ست أرجل تمشي على أربع وتقفز باثنين. وللجراد فم قارض يقرض أوراق الشجر والأعشاب والبراعم والزهور والثمار، وكان الجراد يعتبر في الشريعة من الحشرات الطائرة (لاويين 11: 21 و 22). كان يوحنا المعمدان يأكله ولا يزال يؤكل في بعض بلدان الشرق (مت 3: 4) وغالباً كان الاسينيون يأكلونه، وكيفية أكله هو أن تقطع أرجله وأجنحته ورأسه وتنزع أمعاؤه ويسوى اللحم الباقي على نار هادئة، وقد يقلى في الزيت أو يجفف في الشمس ويسحق، ثم يحفظ إلى حين الحاجة فيستعاض به عن الدقيق.وهو من اشد الضربات المدمرة المخربة في الشرق، وكان الجراد الضربة الثامنة التي ضرب بها الرب فرعون في أرض مصر (خروج 10: 4-15 و مزمور 78: 46 و 105: 34) لذلك أشير إليه مراراً عديدة كعنوان السخط الإلهي (تث 28: 38 و 1 ملو 8: 37 و 2 أخبار 6: 28 و ناحوم 3: 15) كما أن الأمم قديماً غير اليهود كانت تعتبره ضربة من السماء فقد قال بلينيوس ما معناه ((هذه الضربة دليل سخط الآلهة)).وهو يحجب نور الشمس بعدده، والشعوب ترتعد من ظهوره فإنه يعبر البحار الواسعة ويقطع الفلوات الفسيحة ويغطي الحصاد بغيومه المظلمة ويهلك الأثمار ويفني كل نبت.وعندما تحمله ريح شرقية على وادي النيل يحدث ضرراً بالغاً بالحاصلات الزراعية، لذلك تستعد الحكومات في مصر وفي غيرها من بلدان الشرق لمقاومته ومكافحته وإبادة بيضه بكل وسيلة علمية حديثة.وكثيراً ما شبهت الجيوش بالجراد لكثرة عددها (قضاة 7: 12 و ارميا 46: 23) وشدة تخريبها.والأنواع المذكورة في سفر يوئيل (1: 4) هي: القمص وهو الجراد القارض، الزحاف وهو الجراد الزاحم في زحفه، الغوغاء وهو الجراد النطاط، والطيار وهو الجراد المخرب. \nويوئيل هنا يصف أضراره وهي معلومة للقارئ الشرقي، حيث تمشي الصغار كجيش عرمرم مدة النهار وتستريح في الليل أو يخدرها البرد (ناحوم 3: 17) وليس الجراد ملك إلا أنه يتقدم بإتقان وانتظام ولا يلتفت يميناً ولا يساراً (أمثال 30: 27) فإذا وجد بيتاً أو جداراً في دربه مشى فوقه ويدخل الأبواب والكوى (خروج 10: 6 ويوئيل 2: 9) وإذا صادف ماء دخل فيه، فإذا كان الماء نهراً ملأه ومشى بعضه فوق جثث البعض، وإذا كان الماء بحراً غرق فيه الجميع ويحدث أنه لكثرة الجثث على الشاطئ يتولد وباء خطير كما حدث قبل المسيح بمئة وخمس وعشرين سنة وأمات 80000 شخص في ليبية وقيروان ومصر.وإذا كبر الجراد أخذ يطير ويخيم في الهواء (يوئيل 2: 10) ويسقط بعضه على الأرض فإذا ذاك يجتهد الأهالي أن يمنعوه عن الزوال بكل وسائلهم (ارميا 51: 14) ومنها أصوات دق الطبول وقرع الصفائح.والجراد على أنواعه يضع بيضه في شهر نيسان (ابريل) أو أيار (مايو) في حفرة اسطوانية الشكل تحفرها الأنثى ويفقس في شهر حزيران (يونية). وتجتاز الحشرة منها أطوار النمو من عذراء إلى شرنقة ثم مجنحة فجرادة كاملة النمو.'),
(937, 'جَرْدَم', 'ترجمة الكلمة العبرية ((حاسيل)) أي المتلف أو المخرب وهو نوع من الحشرات النهمة. وقد ورد في سفر الرؤيا ص 9: 1-11 ذكر نوع من الجراد له صفات خارقة وهو واحدة من الضربات التي تحل بالأرض ويكثر كثرة بالغة وهو من فصيلة الجراد ويذكر في الكتاب معه (ملوك الأول 8: 37 و مزمور 78: 46) وجاء في الترجمة السبعينية باليونانية (بروخوس) ويرّجح أنه الجراد في طور تكوينه، وكان الله يستخدم هذه الحشرات كوسيلة لتنفيذ عقابه (اطلب جراد، غوغاء).'),
(938, 'جَرَّة', '(تكوين 24: 14) إناء من الفخار أو الخزف، وأحياناً يصنع من مادة أخرى كالحجر أو الخشب (يو 2: 6) وكانت الجرّة تستخدم في الشرق لاستقاء الماء ولا تزال حتى عصرنا الحاضر تستخدم لحمل الماء من البئر أو العين (جمعة 12: 6).وتستعمل الجرة للسوائل أو المواد الجافة. ولها مقبضان وتحملها النساء عادة (تكوين 24: 13 و 15 و 16 و يوحنا 4: 28) وأحياناً يحملها الرجال (مرقس 14: 13) وكان يخزن فيها الخمر أو الزيت كما يخزن فيها الطعام في البيوت (1 ملو 17: 12). (طلب كوار).'),
(939, 'جِرزِيِّم', 'جبل صخري منحدر يكوّن الحد الجنوبي للوادي الذي يقع فيه شكيم (نابلس) وهو يواجه جبل عيبال في الجانب الشمالي من الوادي.ويرتفع جبل جرزيم 2849 قدماً فوق سطح البحر الأبيض المتوسط و 700 قدم فوق مدينة نابلس. ولما فتح الإسرائيليون الجزء الأوسط من فلسطين حمل يشوع التوجيه الذي أعطى لموسى، وأوقف نصف الأسباط على جبل جرزيم لينطقوا بالبركات وأوقف النصف الآخر إلى جبل عيبال لينطقوا باللعنات (تثنية 11: 29 و 27: 11-13 و يشوع 8: 33-35).ومن على جبل جرزيم نطق يوثام بن جدعون بمثله لرجال شكيم وهو مثل العوسج والأشجار (قضاة 9: 7-21). ويقول يوسيفون أن منسى أخا يدوع رئيس الكهنة تزوج من ابنه شخص أجنبي يسمى سنبلط وأمره شيوخ أورشليم إما أن يطلق امرأته أو أنه لن يقترب من المذبح، وفكر منسى في طلاقها رغم أنها كانت عزيزة عنده، لكن أباها سنبلط وعد صهره أنه إذا احتفظ بزوجته ولم يطلقها فسيبني له هيكلاً ضد هيكل أورشليم. ولقد وفى بوعده وبنى هيكل على جبل جرزيم, وكان هدا أصل الهيكل السامري. وإذا كان سنبلط هذا هو السامري الذي ذكر في سفر نحميا (4: 1 و 13: 28) والذي قاوم اليهود، فيكون تاريخ جرزيم السامري في عام 432 ق.م.ولقد هدم الهيكل بواسطة يوحنا هركانوس سنة 128 ق.م. وقد صار جبل جرزيم مقدساً عند السامرين بسبب تشييد الهيكل عليه ولذلك أشارت المرأة السامرية وردد إشارتها يسوع بقولهما ((هذا الجبل)) (يوحنا 4: 20 و 21) وكانت بئر يعقوب التي كانا يتحدثان عنها في سفح جبل جرزيم الذي يسمى الآن جبل الطور. والجبل مركب من الحجر الكلسي وعلى أكمته آثار صهاريج وبلاط وقلعة للسكن.وبين جبل جرزيم وجبل عيبال وادٍ عميق ضيق والمكان على وجه العموم يناسب الاحتفال الذي صار أيام يشوع حيث يسهل تبادل القراءة وسمعها بين الجبلين ويتسع الموضع لجمهور كبير كبني اسرائيل (اطلب: عيبال، شكيم، سامريون).'),
(940, 'الجَرِزِّيُّون', 'هم شعب استوطنوا جنوب فلسطين وذكروا مع العمالقيين والجشوريين زمن شاول (1 صم 27: 8).'),
(941, 'جِرْشُوم', 'اسم عبري معناه ((غريب)) وقد تعني ((تقي)) من الفعل العبري ((جرش)) أي ((طرد)) ((ونفي)) وهو:(1) بكر أولاد موسى (خروج 2: 22 و 18: 3) ولد له في أرض مديان.(2) دعي جرشون ابن لاوي في 1 أخبار 6: 1 وجرشوم في 1 أخبار 6: 16 و 17 و 15: 7 وقد صار رأس بيت أبيه وحسب من اللاويين وليس بين الكهنة (1 أخبار 23: 14-16).(3) كاهن، من نسل فينخاس ورأس لبيت أبيه في ذلك الفرع في زمن عزا (عورا 8: 2).'),
(942, 'جِرْشُون', 'اسم عبري معناه ((نفي)) أو ((جرس)) بكر لاوي ومؤسس أسرة الجرشونيين (تكوين 46: 11 و خروج 6: 16 و 17 و 1 أخبار 6: 1 و عد 3: 17) وقد كتب جرشوم (1 أخبار 6: 16 و 17 و 15: 7) وأما ابناه لبني وشمعي فقد أسسا أسرتين فرعيتين (خروج 6: 17).'),
(943, 'الجَرْشُونيَّون', '(من نسل جرشون) كانوا يؤلفون قسماً من أقسام اللاويين الثلاثة كانوا حالين في البرية في الجانب الغربي من خيمة الاجتماع وعهد إليهم بحراسة الخيمة ذاتها (وهي المسكن والغطاء وسجف باب خيمة الاجتماع واستار الدار والأطناب وحول المذبح) (عدد 3: 23-26 و 4: 21-28) ولمساعدتهم تعن لهم عجلتان وأربعة ثيران.ومن الجرشونيين تفرعت قبيلتان وهما اللبنيون والشمعيون وكان تعدادهما في البرية 7500 من الذكور (عد 3: 21 و 22) أعطيت لهم ثلاث عشرة مدينة منها اثنتان في نصف سبط منسى وأربع في يساكر وأربع في سبط أشير وثلاث مدن في نفتالي (يشوع 21: 27-33) وقد نظمهم داود للخدمة (1 أخبار 23: 7-11).'),
(944, 'جُرْمُوق', '(اطلب سلاح).'),
(945, 'الجَرْمِي', 'اسم عبري معناه ((اقوي أو العظمي)) لقب لابي قعيلة من الذين أقاموا مع الملك داود لشغله (1 أخبار 4: 19).'),
(946, 'جُرْن-أَجْران', '(يوحنا 2: 6) هي أوان حجرية لحفظ الماء لغسل أرجل المدعوين في الأعراس والولائم، يسع الواحد منها مطرين أو ثلاثة. والمطر يعادل حول 80 رطلاً أو 35 لتراً وربما كانت موضوعة أيضاً لأجل غسل الأباريق والأواني (مرقس 7: 3 و 4).'),
(947, 'جَارِيَة', '(اطلب عبد).'),
(948, 'جَزَام', 'اسم عبري معناه ((مفترس أو مهلك)) من أصل عبري ((جزم)) أي ((قطع)). اسم رجل أسس عشيرة من النثينيم، رجع نبوه مع زربابل (نحميا 7: 51) ويدعى أيضاً جزام (عزرا 2: 48).'),
(949, 'جُزَاز', 'المراعي بعد حشها (مزمور 72: 6).'),
(950, 'جِزَاز المَلِك', '(عاموس 7: 1) ((وإذا خلف عشب بعد جزار الملك))، وخلف العشب هو ما تبقى من العشب بعد قطعه أي النمو الثاني إذ كان سكان اليهودية قديماً لم يكن يسمح لهم أن يسرحوا مواشيهم في المراعي العمومية إلا بعد أن تكون قد رعتها خيول الملك لا سيما المعد منها للحرب وكانت تلك الخيول تسرح في المراعي بعض شهور السنة وتأكل خير ما في العشب. هذا هو معنى جزار الملك والعبارة هنا نبوة من عاموس لسكان اليهودية أبان فيها دينونة للشعب وذلك بإرساله جراداً ليأكل ما تبقى من العشب فتحرم منه مواشيهم.'),
(952, 'جِزْع', 'حجر كريم كان يوجد في أرض الحويلة (تكوين 2: 12) كما تدل عليه الكلمة العبرية ((شوهام)) ومما ورد عنه في الكتاب المقدس أنه كان ثميناً جدَّاً (خروج 28: 9-12 و 20 و 39: 6 و 13 و ايوب 28: 16) كان من ضمن تقدمات بني اسرائيل لإعداد مواد المسكن بناء على طلب الرب.على حجري جزع نقشت أسماء بني اسرائيل ستة أسباط على كل حجر ووضعا على كتفي الرداء لهرون للتذكار (خروج 28: 9-12) وكان الجزع هو الحجر الثاني في الصف الرابع الذي رصعت به صدرة القضاء (قض 28: 20) وقد كان من بين الأحجار الكريمة التي جمعها داود لبناء الهيكل (1 أخبار 29: 2) والجزع الشفاف بلّوري ترى فيه عدة ألوان مرتبة في خطوط متوازية وتشبه إلى حد ما لون اللحم تحت الظفر.'),
(953, 'جَزَع عَقِيقِي', '(رؤيا 21: 20) حجر كريم جامع بين صفات العميق والجزع، فهو يشبه الجزع في تركيبه ويشبه العقيق في لونه ويكون الأساس الخامس في سور أورشليم الجديدة. والجزع والعقيق موجودان في بلاد العرب واليهودية وبلاد الهند ولونه مزيج بين الأبيض والأحمر وأحياناً يميل إلى السمرة.'),
(954, 'جَزُونِي', 'لقب رجل اسمه هاشم وهو أبو بعض أبطال داود (1 أخبار 11: 34) ويجوز أن يكون لقب الأسرة أو المكان.'),
(955, 'جِزْيَة', '(1) مال أو بضاعة أو خدمة تقدم من أمة أو من فرد لأمة أو الملك علامة الخضوع وقياماً بالنفقة (تكوين 49: 15 و قضاة 1: 28 و عزرا 4: 13 و اشعياء 31: 8 و متى 17: 25) ولما أراد الفريسيون أن يصطادوا المسيح ليجربوه فسألوه عن جواز دفع الجزية لقيصر فأجابهم بقوله المشهور ((أعطوا ما لقيصر لقيصر وما لله لله)) مبيناً بهذه الإجابة التمييز بين واجبين في دائرتين مختلفتين.(2) جزية حسب الشريعة الموسوية عبارة عن درهمين كانت تفرض على كل نفس فوق سن العشرين ومقدارها نصف شاقل ينفق في سبيل خدمة خيمة الاجتماع (خروج 30: 13) ثم في أيام نحميا كان كل اسرائيلي يدفع جزية اختيارياً هو (ثلث) شاقل لنفقة الخدمة في الهيكل (نحميا 10: 32 و 33) ثم صار فيما بعد نصف شاقل كضريبة سنوية تجمع من كل يهودي جاوز العشرين من عمره في كل أنحاء العالم، وأما محاورة المسيح وبطرس في أمر الجزية التي دفعها المسيح في كفرناحوم فكان المقصود بها أن يوضح المسيح  لبطرس أنه كان ممكناً أن يعفي (المسيح) من دفع الجزية لو شاء، لأنه ابن الله الذي كانت تدفع تلك الضرائب لخدمة بيته لكنه دفع الاستار لكي لا يعثر الشعب (متى 17: 24-27) وقد جعل الملك سليمان على الشعب جزية ثقيلة (1 ملو 12: 4) ويقول يوسيفوس المؤرخ أنه بعد خراب أورشليم ألزم الامبراطور فاسيسيان جميع اليهود في أنحاء الامبراطورية أن يدفعوا لهيكل جوبيتر في رومية، الدرهمين اللذين كانوا يدفعونهما سابقاً للهيكل.'),
(956, 'جَسَد', 'يراد بهذه الكلمة:(1) عضلات الجسم الحيواني إن كان إنساناً أو حيواناً أو طيراً أو سمكاً (تكوين 6: 17 و 19 ولاويين 21: 5 و 1 كو 15: 39). وهذه الكلمة هي ترجمة الكلمة العبرانية ((بسر)) والكلمة اليونانية ((ساركس)).(2) نقيض الروح (اشعياء 10: 18 و عب 13: 3 و متى 26: 41) والكلمة العبرانية أيضاً هنا هي ((بسر)) لكن الكلمة اليونانية هي ((سوما)) و ((ساركس)) في الموضعين المذكورين في العهد الجديد في الكلمتين بين جسد وجسم كما جاء في كولوسي 1: 24.(3) الإنسان الطبيعي المجرد من روح الله الذي تسيطر عليه الشهوات ويطيع الذهن (رومية 7: 5 و 8: 5-7 و 2 كو 7: 1 و غلاطية 5: 16-21 و افسس 2: 3 و 2 بط 2: 10) ولذا فهو يشمل الطبيعة البشرية غير المجددة وغير المقدسة (رومية 8: 8 و يوحنا 3: 6).(4) أطلقت كلمة الجسد على الكنيسة وهي جماعة المؤمنين رمزاً لتنوع المواهب بين أعضائها وتعاونهم معاً وارتباطهم بالرأس الذي هو المسيح (رومية 12: 5 و افسس 3: 6).'),
(957, 'جِسْم رُوحَانِي', 'هو الذي سيسلبه المؤمنون بعد القيامة كما لبسوا صورة الحيواني يلبسون صورة السماوي وهو الجسم المجرد عن الشهوات الحيوانية والأميال العالمية. شكله لا يتغير وإنما يكون طاهراً نقياً قابلاً للتمتع بجميع الأفراح السماوية والمباهج الروحية مع النفس المطهرة والمفتداة بدم المسيح وسيكون تحت سلطة الروح (1 كو 15: 44) (اطلب النفس).'),
(958, 'جِشْفَا', 'اسم عبري معناه ((معانقة)) كان من رؤساء النثينيم بعد السبي في زمن نحميا (نحميا 11: 21).'),
(959, 'جَشَم', 'اسم عبري معناه ((جسيم)) أو ((مطر)) وهو رجل عربي اتفق مع سنبلط وطوبيا على مقاومة اليهود بعد رجوعهم من السبي، وقد هزأ بنحميا لما فكر في إعادة بناء سور اورشليم واتهمه بالتمرد (نحميا 2: 19) ولما لم يفلح في تعطيل اليهود انضم مع آخرين في مؤامرة اغتيال ولما فشل في هذا الغرض أيضاً أشاع بين الأمم أن نحميا يحصن المدينة تمهيداً للتمرد على مملكة فارس ليقيم نفسه ملكاً على يهوذا (نحميا 6: 1-9). وقد اكتشفت مؤخراً نقوش في الجهة الشمالية من مصر على وعاء فضي ويذكر أن جشم كان ملكاً على قبيلة قيدار.'),
(960, 'جَشُور', 'اسم عبري معناه ((جسر)) مقاطعة واقعة بين حرمون وباشان تتاخم ارجوب (تثنية 3: 14 و يشوع 12: 5 و 13: 11 و 13 و 1 اخبار 2: 23) وتقع شرقي معكة داخل نصيب منسى.وكانت جشور مستقلة فهرب إليها ابشالوم بعد أن قتل أخاه امنون (2 صم 13: 37) وعلى حدود هذه المقاطعة جسر على نهر الأردن بين طبرية والحولة يعرف بجسر بنات يعقوب.'),
(961, 'الجَشُوريُّون', '(1) سكان جشور (تثنية 3: 14 و يشوع 12: 5 و 13: 11 و 13).(2) قبيلة كانت تقطن جنوب فلسطين في اتجاه مصر (يشوع 13: 2 و 1 صم 27: 8).'),
(962, 'جَعْبَة', 'وعاء توضع فيه السهام (اشعياء 49: 2) حيث ترجمت إلى كنانة. وذكرت الجعبة على سبيل الاستعارة في (مراثي 3: 13 و ارميا 5: 16) وكان رماة السهام يحملون الجعبة على ظهورهم وفتحها على الكتف اليمنى متى كانوا مشاة، أما وهم في مركباتهم فكانوا يعلقونها بجانب المركبة وكان المصريون يضعونها أفقية على ظهورهم ويخرجون السهام من تحت الذراع اليسرى. وقد استخدمت جعبة كتشبيه للاعتزاز بالنسل في مز 127: 5.'),
(963, 'جَعْثَام', 'اسم عبري معناه ((ضعيف)) حفيد عيسو وأحد أمراء ادوم (تكوين 36: 11 و 1 اخبار 1: 36) ويظهر أن إمارة سميت باسمه (تكوين 36: 16).'),
(964, 'جَعَل', 'اسم عبري معناه ((كراهه)) أو ((جعل)) أو ((خنفساء)) هو ابن عابد جاء مع عصابة من الاتباع إلى شكيم وأهاج أهلها ضد الملك ابيمالك في غيابه قاصداً بذلك اغتصاب الملك منه فأرسل زبول كلمة إلى ابيمالك يعلمه بفتنة جعل، وأشار عليه أن ينتظر إلى أن ينقضي الليل ويهجم عند الفجر ويأخذ المدينة. وفعل الملك بهذه المشورة ولما حاربه تغلب عليه ابيمالك فهرب (قضاة 9: 26-41).'),
(965, 'جَفْنَة سَدُوم', '(نبات) وهي شجيرة بقرب سدوم، وتحمل عناقيد أثمار مرّة كالحنظل (تثنية 32: 32) وارتأرى البعض أنها الشجرة المعروفة بالعشر عند العرب يبلغ علوها 15 قدماً وثمرها كروي أصفر اللون يشبه البرتقال في الحجم والشكل ويتدلى منها على هيئة عناقيد في كل منها ثلاث أو اربع ثمرات. ويبلغ قطر جزع هذه الشجرة 8 قراريط وأثمارها شهية للنظر ناعمة الملمس غير أنه إذا ضغط عليها أو عصرت انفجرت كانفجار الزق المملوء هواءً ويبقى في يد من يضغط عليها بقايا قشورها الرقيقة مع بعض أليافها. ويقول يوسيفوس عن أثمار جفنة سدوم أنها إذا قبضت باليد انحلت إلى دخان ورماد. وموطن هذه الشجرة في مصر العليا وبلاد العرب والهند كما أنها تنمو في عين جدي وأنحاء أخرى من وادي البحر الميت الحار المناخ.وقد قصد موسى بالآية المشار إليها في تثنية 32: 32 أن يصف بصورة مجازية دناءة أعداء الله ونجاستهم فإنهم يتظاهرون بالتقوى والعفة ولكنهم بالحقيقة هم مثل أثمار جفنة سدوم.'),
(966, 'جَلاَل', 'اسم عبري معناه ((الرب دحرج))(1) لاوي (1 اخبار 9: 15).(2) لاوي ثانٍ (1 اخبار 9: 16 و نحميا 11: 17) ابن يدثون.'),
(967, 'جِلْبُوع', 'اسم عبري ربما كان معناه ((عير متفجرة)) (1 صم 28: 4) الجبل الذي هزم فيه شاول في المعركة مع الفلسطينيين وعليه لقي حتفه هو وبنوه الثلاثة (1 صم 31: 1 و 8 و 2 صم 1: 6 و 21 و 21: 12 و 1 اخبار 10: 1 و 8). وهو يكّون الجزء البارز في الشمال الشرقي من جبل افرايم كما يكّون مساقط المياه بين حوض نهر قيشون ووادي نهر الأردن ويطلق هذا الاسم على سلسلة جبال مرتفعة تكون قوسا شرقي وادي يزرعيل طول هذه السلسلة ثمانية أميال تقسمها الأودية العميقة الضيقة إلى عدة هضاب أعلى نقطة فيها هي الواقعة عند ((الشيخ برقان)) وهي تعلو فوق سطح البحر 1696 قدماً.أما المنحدرات الغربية لهذه السلسلة فهي متدرجة لكن المنحدرات التي تواجه الجنوب فهي شديدة الانحدار وعرة تكثر فيها الحفر والشقوق العميقة في أماكن عديدة ومثلها المنحدرات الشرقية وتوجد على بعض هذه المنحدرات لا سيما من جهة الغرب أراضي للمراعي وينمو القمح والشعير ويزرع الزيتون، وما عدا ذلك فالمنحدرات صخرية جرداء أو مغطاة بالحشائش والأعشاب البرية.ويطلق عليه اسم جبل فقوع وتقع بالقرب منه قرية جلبون التي تشبه الاسم القديم.'),
(968, 'جِلْجَال', 'كلمة عبرية معناها ((متدحرج)) (تث 11: 30) وقد تعني ((دائرة)) ولها عدة معانٍ مذكورة:(1) هي أول معسكر للإسرائيليين بعد عبور الأردن ودخولهم أرض كنعان. وهنا أقيم نصب تذكاري من اثني عشر حجراً أخذت من قعر النهر (يشوع 4: 2-24) وبتغيير طفيف معناها دحرجة عارهم عقب ختانهم الذي أهملوه في البرية كفريضة مدة طويلة (يشوع 5: 7-9).نصبت الخيمة بين الأردن واريحا وعلى موقعها يثبت التخم الشمالي ليهوذا (يشوع 15: 7) ولم يؤكد علماء الكتاب أي جلجال كان يمر عليه صموئيل في سفراته الدورية السنوية (1 صم 7: 16) ويستفاد من 1 صم 11: 15 أن في الجلجال نصب شاول ملكاً وتجددت المملكة، وهناك تجمع الشعب حول شاول لمواجهة أعدائهم الفلسطينيين، وهناك انتظروا مع شاول أو يأتي صموئيل ليقدم المحرقة وعندما تأخر صموئيل قام شاول وقدم المحرقة بنفسه (1 صم 13: 4-15) واعتبر هذا اقتحاماً لوظيفة صموئيل. وبهذا العصيان أضاع شاول امتياز تأسيس أسرة ملوكية (1 صم 13: 14) وفي الجلجال عصا شاول أيضاً لما عفى عن أجاج الذي طلب منه أن يحرمه ومن ثم رفض من الملك وفاقه روح الله (1 صم 15: 20-23 و 16: 14).إلى الجلجال أتى وفد من سبط يهوذا ليرحب برجوع داود إلى الملك بعد موت ابنه ابشالوم (2 صم 19: 15 و 40) ولكن هذا المكان المليء بالذكريات المقدسة صار مركزاً للأصنام أيام الملوك الذين تولوا الحكم بعد يربعام ونتج عن ذلك أن هجاه الانبياء ولعنوه (هوشع 4: 15 و عاموس 4: 4 و 5: 5) ويرّجح أن ما ذكر في نحميا 12/ 29 بعد السبي إشارة إلى بين الجلجال والآن هي بقعة تسمى خرابة الاثلة. وبالقرب منها بركة تسمى الجلوجولية على بعد ميل وثلث شرقي اريحا وهذا يتفق والوصف الكتابي للجلجال.(2) قرية خرج منها إيليا واليشع ونزلا إلى بيت ايل (تثنية 11: 30 2 ملو 2: 1-4 و 4: 38) وهي جلجلية الحديثة 8 أميال شمال بيت ايل.(3) عاصمة ملك جوييم الذي انتصر عليه يشوع (يشوع 12: 23) تقع على مسافة 5 أميال شرق انتيباتريس في سهل شارون.(4) مكان مقابل عقبة ادميم شمال وادي عخور (يشوع 15: 7).'),
(969, 'جُلْجُثَة', '(اطلب جمجمة).'),
(970, 'جُلْجُل جَلاَجِل', 'وردت في العهد القديم بصيغة المفرد والجمع، كانت تصنع من ذهب مع حلية اخرى على هيئة رمانة من ذهب أيضاً تعلق على أذيال رداء رئيس الكهنة الأزرق اللون ويسمع صوت الجلاجل عند دخول رئيس الكهنة إلى القدس وعند خروجه أمام الرب (خروج 28: 33 و 34 و 39: 25 و 26) وأما كلمة أجراس المذكورة في زكريا 14: 20 فلا تعني جلاجل لاختلاف الكلمتين في الأصل العبري.'),
(971, 'جَلَد', 'الكلمة العبرية ((رقيع)) ومعناها شيء ممتد مطروق (تك 1: 6 و 7 و 8 و 14 و 15 و 17 و 20) وظهر جلد السماء في نبوة حزقيال كأنه مقبب منتشر (حزقيال 1: 22) قسمت المياه في وقت الخلق إلى مياه فوق الجلد ومياه تحت الجلد (تكوين 1: 7 و مزامير 148: 4) حيث قيل ((المياه التي فوق السموات)). والجلد مرصع بالكواكب والنجوم المضيئة (دانيال 12: 3) وشبه الجلد بما شبهت به السموات:(1) خيمة منتشرة فوق الأرض (مزامير 104: 2 و اشعياء 40: 22) ((ينشر السموات كسرادق)).(2) المرآة المسبوكة (ايوب 37: 18).(3) للجلد طاقات (تكوين 7: 11) و كوى (2 ملو 7: 2) ومصاريع (مزامير 78: 23) وقد سادت هذه الآراء والأوصاف عند اليهود والجنس السامي عموماً، وهذه العبارات على سبيل التشبيه وليست حرفية مادية.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(972, 'جِلْد-جُلُود', 'صنع الله أقمصة من جلد لآدم وحواء قبل طردهما من الجنة (تكوين 3: 21) ولما تعلم الناس صناعة دبغ الجلود استعملت لأغراض كثيرة كالثياب والأمتعة وذكرت في الكتاب لهذه الأغراض وغيرها:(1) استعملت لباساً (عبرانيين 11: 37 و عدد 31: 20).(2) واستعملت أغطية للخيمة (خروج 26: 14) ((جلود كباش محمرة)) و ((جلود تخس)) أي حيوان الدلفين البحري أو عجل البحر.(3) يصنع منه الزق للخمر متى 9: 17.(4) يصنع منه المجن للقتال وكان يلمع ويلين بالزيت أو يمسح بالدهن (2 صم 1: 21 و اشعياء 21: 5).(5) للنعال (حزقيال 16: 10).(6) مناطق من جلد كالمناطق التي كان يلبسها إيليا ويوحنا المعمدان (2 ملو 1: 8 و متى 3: 4).'),
(973, 'جَلاَّدُون', 'يشبهون رجال البوليس في يومنا الحاضر وكانوا قديماً مستخدمين عند الحكومة الرومانية ليجلدوا المذنبين أو ليضربوا أعناقهم، وكانوا يحملون أما الولاة رزمة عصي بينها بلطة، أما الجلادون والسعاة الذين كانوا حرس داود فيرجح أنهم كانوا من أهل اليهودية الأصليين فإن اسم الجلادين في العبرانية الكريتيين (1 صم 30: 14) والسعاة فيها فليثيين (اطلب ((كريتييون)) ).'),
(974, 'جِلْعَاد', 'اسم عبري معناه ((صلب أو خشن)).(1) ابن ماكير وحفيد منسى وهو رأس عشيرة الجلعاديين (عدد 26: 29 و 30 و يشوع 17: 1).(2) أبو يفتاح (قضاة 11: 1 و 2).(3) جادي (1 اخبار 5: 14).(4) مدينة في سهول جلعاد (هوشع 6: 8 و 12: 11).(5) قطر جبلي شرق الأردن يمتد إلى بلاد العرب وهو يشتمل البلقاء الحديثة، أرضه صخرية وعرة (تثنية 34: 1 و 2 صم 2: 9) وجاء في يشوع 13: 25 أن تخم جاد كان يشمل كل مدن جلعاد، (يشوع 13: 30 و 31) وجاء في تثنية 3: 12 و 13 و 16 أن نصف جبل جلعاد أعطي لرأوبين وجاد وبقية جلعاد أعطيت لنصف سبط منسى. وكان ينبت في جلعاد نوع من الشجر يخرج منه مادة صمغية تدعى بلسان جلعاد ذات خواص طيبة (ارميا 8: 22 و 46: 1) وله أهمية كبرى بين مواد التجارة (تكوين 37: 25). وذكر استرابو الجغرافي الشهير أنه يوجد حقل في فلسطين قرب اريحا مملوء من هذه الأشجار وأما عصير البلسان فيشبه الحليب اللزج ويتجمد بسرعة وكان يستعمل علاجاً  في الالتهابات. وفي زمن الاسكندر كانت قيمته تعادل ضعفي وزنه فضة (اطلب بلسان).'),
(975, 'جَبَل جِلْعَاد', 'جبل غرب الأردن يشرف على وادي يزرعيل انصرف منه قسم من جيش جدعون (قضاة 7: 3) وبالقرب منه عين حرود المسماة اليوم عين جلعود كما أن هناك نهراً يدعى نهر جلعود وكل هذ اصداء لكلمة جلعاد. كانت أيضاً جلعاد من نصيب نفتالي (2 ملو 15: 29) وربما امتد تخم نفتالي إلى الرشق نحو الأردن.'),
(976, 'الجِلْعَادِيُّون', 'قسم من سبط منسى من نسل جلعاد (عدد 26: 39).'),
(977, 'جَلْعِيد', 'كلمة عبرية معناها ((رجمة الشهادة)) أقيمت كنصب تذكاري بواسطة يعقوب في جبال جلعاد شمال نهر يبوق. مكانها اليوم غير معروف. أقيمت هذه الرجمة بين مساكن لابان ومساكن يعقوب شهادة على العهد المبرم بينهما ألا يقرب أحدهما الآخر ليسيء إليه (تكوين 31: 45-45).'),
(978, 'جلَلاَي', 'اسم عبري معناه ((الرب دحرج)) أحد اللاويين المغنين وكان له نصيب في تدشين سور اورشليم في أيام نحميا (نحميا 12: 36).'),
(979, 'جَليِّم', 'كلمة عبرية معناها ((اكوام))(1) قرية بالقرب من جبعة شاول وعناثوث وهي لبني بنيامين (اشعياء 10: 29 و 30) ولا تبعد كثيراً عن بحوريم (1 صم 25: 44) وهي مسكن فلطي الذي أخذ ميكال امرأة داود (2 صم 3: 13-16).(2) بلدة في يهوذا ذكرت في الترجمة السبعينية في مجموعة مع تقوع وبيت لحم وايثام ولا سيما بالبُلدان الواقعة غرب اورشليم ويحتمل أن تكون بيت جالا الواقعة بالقرب من بيت لحم.'),
(980, 'جُلْيَات', 'ربما كان اسماً كنعانياُ معناه (سبي أو نفي)) رجل من جت الفسطينيين. كان من جبابرتهم بلغ طوله أكثر من 9 أقدام وكانت أدوات حربه مناسبة لطول قامته وقوته، قتله داود كما هو مذكور بالتفصيل في (1 صم 17 و 21: 9 و 10 و 1 اخبار 20: 5) وربما كان أحد بني عناق (عدد 13: 23 و يشوع 11: 22) وذكر جليات آخر غير الذي قتله داود (2 صم 21: 19 و 1 اخبار 20: 5) وهذا الأخير قد قتله بن يعري ارحيم البيتلحمي وقد ذكر عدد من الجبابرة في المعارك الحربية من أولاد رافا حتى ظن بعض الكتاب أن رافا هو من أنسباء جليات.'),
(981, 'الجَلِيل', 'اسم عبري معناه ((دائرة)) أو ((مقاطعة)) كانت في الأصل في القطر الجبلي لنفتالي (2 ملو 15: 29 و 1 اخبار 6: 76) وكانت قاديش إحدى مدنها (يشوع 20: 7 و 21: 32) وكانت المدن العشرون غير المهمة الموهوبة من سليمان لحيرام واقعة في أرض الجليل (1 ملو 9: 11) وفي هذا القسم كان يقيم كثيرون من الكنعانيين (قضاة 1: 30-33 و 4: 2) وأما عبارة جليل الأمم فتفيد أن هذا القسم كانت تقطنه غالبية من الأمم (مت 4: 15) وامتد اسم الجليل حتى شمل كل منطقة يزرعيل وقد أخذ كثيرون من أهل الجليل إلى اشور أثناء الحروب (2 ملو 15: 29). واليهود القلائل الذين استوطنوا الجليل بعد الرجوع نقوا إلى اليهودية بواسطة سمعان المكابي حوالي عام 164 ق.م. (2 مكا 5: 23) لكن الجليل بعد قليل صارت كلها يهودية فكونت جزءاً من مملكة هيرودس الكبير. وبعد موته صارت إلى هيرودس رئيس الربع وكانت في القسم الشمالي من بين الثلاثة الأقسام التي قسمت إليها فلسطين في زمن المسيح في عصر الدولة الرومانية.وفي الحروب اليهودية عام 70 للميلاد كانت الجليل مقسمة إلى قسمين وهما: الجليل العليا والجليل السفلى:-العليا ويحدها من الشمال صور ومن الجنوب السامرة ومن الغرب فينيقية ومن الشرق الأردن، والسفلى تقع جنوب العليا وتمتد من بحيرة طبرية إلى قرب بطوليماس والتي اسمها الآن عكا على البحر الأبيض المتوسط. وكانت هذه المنطقة خصبة جدّاً وكثيرة السكان. ويذكر يوسيفوس في تاريخه أن سكانها بلغوا في أيامه ثلاثة ملايين نسمة وكان لها جيش قوامه مئة ألف مقاتل وبها 240 مدينة وقرية بين حدود القسمين وأصغر هذه القرى سكانها 15000 نسمة وأكبر المدن سيفوريس (صفورية) كان بها خليط من الأجناس أدى إلى نبرات خاصة في لغتهم كما هو واضح من مرقس 14: 70 و لوقا 22: 59 و اعمال 2: 7، سكنها قديماً أربعة أسباط وهم يساكر، زبولون، نفتالي، واشير. وكان الاعتقاد أن شعب الجليل لا يمكن أن يكون منه نبي (يوحنا 7: 41-52) غير أن معظم رسل المسيح كانوا من الجليل. وكان يسوع يعرف بأنه الجليلي (متى 26: 69) ففيها نشأ وتهذب وخدم في حدودها الشرقية عند بحر الجليل وداخل منطقتها في كورزين وبيت صيدا وكفرناحوم ونايين وقانا والناصرة. وقيل عن بطرس أنه جليلي ولغته تظهره (متى 26: 69 و 73 و مرقس 14: 70). \nطول مقاطعة الجليل 19 ميلاً وعرضها 25 ميلاً، على العموم هي جبلية خصبة تنمو فيها الحبوب وتكثر فيها الجبال مثل الكرمل وجلبوع وتابور ويبلغ ارتفاع بعضها إلى 4000 قدمٍ.'),
(982, 'بَحْر الجَلِيل', 'بحيرة عذبة تستمد مياهها من نهر الأردن واسم بحر الجليل القديم بحر كنّازة (عدد 34: 11) ثم بحيرة جنيسارت (لوقا 5: 1) وبحر الجليل أو بحرية طبرية (يوحنا 6: 1 و 21: 1) وهو الاسم المشهور به بين العرب. ولهذا البحر شأن عظيم في الأناجيل من حيث نسبته إلى تاريخ حياة المخلص في بدء حياته فإن كفرناحوم التي كثيراً ما وطئتها أقدام المسيح تقع على شاطئه. ومنها اختار أربعة من تلاميذه الصيادين الذين جعلهم صيادي الناس. وبعد صلب المسيح تفرق شمل تلاميذه غير أنه بعد قيامته اجتمع بهم على شاطيء ذلك البحر لأنهم كانوا قد رجعوا إلى هناك يشتغلون بحرفتهم القديمة. وكان الصيد مهنة مريحة (مرقس 1: 20) ويعد بعض السياح 22 نوعاً من السمك هناك من الأنواع الصغيرة والكبيرة الشهية الطعم ويشبهها ما في الأردن وفروعه. وهذا البحر محاط بتلال وهضاب إلا في سهول البطيحة من الشمال والغوير وطبرية الغوير من الجنوب حيث يلتقي به نهر الأردن في دخوله وخروجه وتلك الهضاب من الغرب كلسية بركانية وأما من الشرق فبركانية ويبلغ علوها أكثر من 1000 قدم.أما طول البحر من الشمال إلى الجنوب فيبلغ نحو اثني عشر ميلاً ونصف وطرفه العريض المقابل لقرية المجدل يبلغ سبعة أميال ونصف سطحه يقل ارتفاعاً بنحو 682 قدماً عن سطح البحر الأبيض المتوسط. وبسبب انخفاض سطحه فمناخه نصف حار. وبالقرب منه جبل حرمون المتوج بالثلوج ولذلك كثيراً ما تثور الأرياح وتعصف الأنواء بغتةً مارةً بمنحدر الجبل وتنتهي في سطح البحر. وقد جاء في الإنجيل أن زوبعة من هذا النوع فاجأت السفينة التي كانت تتقل التلاميذ دون سيدهم وإن يسوع جاء إليهم ماشياً على تلك الأمواج العجاجة ولما دنا من السفينة نزل بطرس لملاقاته على الماء ولم يصب بأدنى أذى (مت 14: 24-36). وورد أيضاً في الأناجيل أن النوء هاج على السفينة بينما كان نائماً فيها فارتعب التلاميذ من شدته فذهبوا وأيقظوه فقام وانتهر الريح فسكنت (مت 8: 23-27) (انظر طبرية).'),
(983, 'جَلِيل', '(1) صفة من صفات الله الأعظم المهاب (تثنية 28: 58).(2) وصف للفضائل المسيحية التي ينبغي على كل مؤمن أن يفتكر فيها ويتصف بها (فيلبي 4: 8).'),
(984, 'جَلاَء', '(1) أطلقت قديماً على سبي كوش بواسطة ملك آشور (اشعياء 20: 4).(2) اهبة جلاء أو استعداد للرحيل (ارميا 46: 19) وهي لغة مجازية لسبي.'),
(985, 'جَلِيلُوت', 'اسم عبري معناه ((دوائر)) أو ((سهول)) اسم مكان على تخوم بنيامين (يشوع 18: 17) وغالباً هي الجلجال مقابل عقبة (مطلع) ادميم (يشوع 15: 7) (اطلب جلجال).'),
(986, 'جَليلي', 'مواطن من الجليل ( مرقس 14: 70 ولوقا 13: 1 واعمال 5: 37) قيلت عن بطرس (لوقا 22: 59) وعن المسيح (لوقا 23: 6).'),
(987, 'جَلِيلِيُّون', '(1) فئة من اليهود كانوا يعرفون بالجليليين لأنهم انقادوا إلى يهوذا الجليلي. ويزعم البعض أن غايتهم كانت مقاومة الحكومة الرومانية لضريبة وضعتها على كاهل اليهود سنة 10-12 ق.م. وقد عصى يهوذا وأتباعه الحكومة الرومانية (اعمال 5: 37) واستمروا على ذلك إلى خراب اورشليم والهيكل. ويخبرنا التاريخ أن الجليليين كانوا على وفاق مع الفريسيين.(2) وأما الجليليون الذين قتلهم بيلاطس حينما كانوا يقدمون ذبائحهم في اورشليم خالطاً دمائهم بذبائحهم (لوقا 13: 1 و 2) فكانوا من رعايا هيرودس الذي كان عدواً لبيلاطس (لوقا 23: 12) وزعن بعضهم أن هلاكهم على هذه الصورة وهم يقدمون فرائض العبادة والسجود كان دليلاً على عدم رضى الله عنهم وسخطه عليهم.'),
(988, 'جُمْجُمَة', 'هي موضع الجلجثة حيث يصلب يسوع هناك. وهو موضع بالقرب من اورشليم لكنه خارج أسوار المدين وفي حدودها دفن (يوحنا 19: 17 و 41 و عبرانيين 13: 11-13) ويظهر أنها كانت بقعة منظورة (مرقس 15: 40 و لوقا 23: 49) وبالقرب من طريق سلطاني عام (مت 27: 32 و 33).والكلمة المأخوذة عن اليونانية ((كرانيون)) جمجمة. وأما الجلجثة فهي الكلمة الأرامية لكلمة جمجمة وبالعبرانة (جولجوليث): (مت 27: 33 و مرقس 15: 22 و يوحنا 19: 17).ويظن جيروم أن الاسم أطلق على الموضع بسبب وجود جماجم مكشوفة غير مدفونة. وافتكر غيره أن المكان كان ساحة للإعدام. والتفسير العادي الشائع أن المكان كان تلاً على شكل جمجمة. والمعتقد الآ، أن كلمة جبل الجلجثة تسمية حديثة. والسؤال اليوم: أين هو موضع الصلب وموضع القبر? يوجد موضعان قابلان للبحث بين العلماء.(1) أولهما كنيسة القيامة داخل أسوار المدينة الحديثة.(2) والموضع الثاني التل الأخضر أو الهضبة الخضراء وجلجلثة غوردن حيث مغارة أرميا وهي على مسافة 250 ياردة شمال شرقي باب الشام (باب العامود).أما كنيسة القيامة فلها تقليد قديم يؤيدها، وهذا رأي يوسابيوس المولود في قيصرية حوالي سنة 264 م. وهو أقدم مؤرخ يقدم معلومات أو بيانات عن هذا الموضوع. يقول يوسابيوس أن بعض الأشقياء غطوا القبر بالأرض وبنوا فوقه معبداً للآلهة فينوس، ثم ترك المكان للنسيان والإهمال وقتاً طويلاً. وجاء قسطنطين وبنى مكان المعبد كنيسة، ومكان هذه الكنيسة الآن مشغول بكنيسة القيامة. بيد أن هذا الموضع المؤيد بالتقليد، اعتقد البعض أنه غير صحيح ذلك لأنه لا أحد يعلم اتجاه السور الثاني شمال أو جنوب تلك البقعة التي بنيت فوقها كنيسة القيامة. ويقال أن بوسيفوس المؤرخ اليهودي شرح هذا بقوله أن السور الشمالي كان يسير جنوب هذا الموقع، والعبرة الآن أن كل هذه الأماكن أصبحت تحت المدينة الحديثة حيث لا يمكن القيام بعملية الحفر والتنقيب. أما الرأي القائل أن الموضع هو بالقرب من مغارة أرميا فقد نادى به عالم اوتوثينوس عام 1849 واتفق معه علماء آخرون.أما بحسب التقليد اليهودي الحديث ففي هذا المكان كان المجرمون يلقون حتفهم رجماً بالحجارة.أما التل الذي تقع فيه مغارة أرميا فهو بعد السور الثاني. وقمة التل المستديرة ومدخلا المغارة الأجوفان تحت التل يجعلها منظورة من على مسافة بعيدة كأنها جمجمة. وبالقرب منها الحدائق العظيمة والقبور المنحوتة في الصخر، لكن لا تقليد قديم يربط الصليب بهذا المكان، وما زال تحقيق المسألة قيد البحث وربما في إخفاء موضع صلب يسوع حكمة إلهية لئلا يجعله الناس موضعاُ لعبادة هيولية (جسمية) مغايرة لروع الديانة الصحيحة.'),
(989, 'مِجْمَرِة', '(لاويين 10: 1) وتسمى أيضاً مبحرة (عب 9: 4) وهي إناء يستعمل في الهياكل لوضع النار والبخور فيه (عد 16: 6 و 7 و 39) كانت المجمرة تملأ ناراً من النار المضرمة دائماً على مذبح التقدمة وتحمل في اليد الواحدة ويحمل البخور في الأخرى. كانت محامر الخيمة من النحاس (خروج 27: 3 و خروج 38: 3).أما تلك التي كانت في الهيكل فكانت من الذهب (ملوك الأول 7: 50 و 2 اخبار 4: 22 و عبرانيين 9: 4) وكذلك المباخر المذكورة في سفر الرؤيا كانت من ذهب (رؤيا 8: 3 و 5).كانت المجامر التي استعملها قورح ورفاقه من النحاس. وكانت المجمرة التي تستعمل يوم الكفارة من الذهب الخالص، يدخل بها رئيس الكهنة إلى قدس الأقداس مرة في السنة حسب عادته ويرش البخور على النار فتتصاعد منه رائحة عطرية. وتدعى الجمرة صحناً (عد 7: 14) وجاماً (رؤيا 5: 8).'),
(990, 'جَمَرْيَا', 'اسم عبري معناه ((الرب قد أكمل))(1) هو ابن حلقيا، واحد الاثنين اللذين أرسلهما الله صدقيا إلى نبوخذ نصر ملك بابل، وانتهز أرميا الفرصة وحمّله رسالة إلى الذين كانوا في السبي محذراً إياهم من النبوات الكاذبة بشأن عتقهم (أرميا 29: 3).(2) ابن شافان الكاتب وأخو اخيقام، كان يشغل مخادع الهيكل وانضم مع اللذين طلبوا إلى يهوياقيم بألاّ يحرق كتابات النبي أرميا (أرميا 36: 10 و 11 و 12 و 25).'),
(991, 'جِمْزُو', 'اسم عبري معناه ((كثير الجميز)). بلدة لها قرى مستقلة في أرض يهوذا، استولى عليها الفلسطينيون أيام آحاز (2 اخبار 28: 18).وجمزة الحاضرة هي قرية صغيرة على بعد 3 أميال جنوب شرقي لدة أو اللد.'),
(992, 'جُمَّيْز', 'شجر معروف Ficus Sycomorus أخضر الورق اسمه بالعبري ((شقماه)) وباليونانية سيكومورس ويشبه التين وهو نوع منه، كبير الحجم كثيف الظل ممتد الأغصان وكثيرها، فلذلك صعد زكا إليه لما أراد أن يرى المسيح (لو 19: 4) وكان قديماً كثير الوجود في سهل يهوذا (2 ملو 10: 27 و 1 اخبار 27: 28 و 2 اخبار 1: 15 و 9: 27). كان ينمو في وادي الأردن (لوقا 19: 4) ويزرع في أرض مصر (مزمور 78: 47) و لا يزال موجوداً. وكان خشبه يستعمل كثيراً، لكنه أقل قيمة من خشب الأزر (اشعياء 9: 10 و 1 ملو 10: 27).يبلغ طول الشجرة من 25 إلى 50 قدماً، وتشغل مساحة تزيد على 60 قدماً، شكل أوراقها على شكل القلب. تزرع على جانب الطريق لكثرة ظلمها وقد وجد البعض نعوشاً مصرية مصنوعة من خشب الجميز لم تزل علة حالتها الأصلية مع أنها ترجع إلى 3000 سنة. أما الثمر فينمو على هيئة عنقود ويطلع من الجزع مباشرة أو الأغصان الكبرى. وكانوا في القديم يعتنون جدَّاً بجمعه حتى أنهم كانوا يعينون أناساً لقطعه بالمشرط لكي ينضج ولجنيه (1 اخبار 27: 28 و عاموس  7: 14) ولا يؤكل ثمره إلا بعد تنقيته من الحشرات العالقة به. ويقال أنه لا يحتمل البرد.أما الجميزة المذكورة في لوقا 17: 6 فيظهر أنها كانت من نوع التوت الشامي Morus Nigra.'),
(993, 'جَمَشْت وجَمَسْت', 'هو حجر كريم ذو ألوان كثيرة مختلفة أخصها اللون الأرجواني (خروج 28: 19 و 39: 12) وكان من جملة الحجارة التي كانت في صدرة رئيس الكهنة وهو السور الثاني عشر لاورشليم الجديدة (رؤ 21: 20) وهو لؤلؤة بلورية نقية ذات لون بنفسجي أزرق فاتح كما يعتقد من المنغنيز.'),
(994, 'جَمْع', 'عيد الجمع (خروج 23: 16 و 34: 22 و لاويين 23: 39) عيد يقع في آخر الحصاد 15-22 من تسري لأي في أواخر ايلول وأوائل تشرين الأول وهو عيد المظال (اطلب مظال) (تث 16: 13-15).'),
(995, 'جَمَاعَة', 'كانت هذه الكلمة تختص غالباً بأمة اسرائيل مع الغرباء النازلين عندهم (مز 22: 22) وكان يسود الجماعة رؤساء الاسباط والبيوت وقد انتخب منهم سبعون شيخاً لقضاء الأشغال وإجراء الأحكام (عد 11: 16) وكانت الجماعة تجتمع في محل خصوصي عند استماع صوت بوقين. وإذا صوت بوق واحد اجتمع رؤساء الألوف (عدد 10: 3 و 4 و 7) وفي أيام اليهود المتأخرة أُلف مجمع شيوخ سمي السنهدريم، وفي العهد الجديد يراد بهذه الكلمة جمهور من الناس فقط (اع 13: 43 و رؤيا 18: 17).'),
(996, 'مَجْمَع', '(1) هو مكان الاجتماع وعند اليهود مركز للعبادة ودار للقضاء العالي وكان يستعمل كمدرسة.كانت العبادة قبل السبي تؤدي في هيكل اورشليم فقط وطبعاً كان الكتاب يقرأ في أي مكان (أرميا 36: 6 و 10 و 12-15).وكان الناس يذهبون إلى الانبياء في أي مكان للإرشاد الديني (2 ملو 4: 38) وكانت العبادة في اورشليم مستحيلة وغير ممكنة لما كان الشعب في سبي بابل. ويظهر أنه في ذلك الوقت وفي بابل نشأت فكرة المجامع.ولقد أقيمت لا لتكون مكاناً للذبائح وإنما للتعليم الكتابي والصلاة وزعم البعض أن المجمع نظمه موسى (عدد 11: 16) وقد جاءت كلمة معاهد الله في مزمور 74: 8 بمعنى مجامع الله أو محافل أو مقادس من أيام يهوشافاط (2 اخبار 19: 8-11) وأما السنهدريم فقد انتظم في أيام المكابيين وبقي إلى زمن مخلصنا (متى 5: 22)، وهو المجمع الذي حكم على يسوع (مت 26: 59 و مرقس 15: 1) ومن ذلك الوقت أخذت سلطته تضعف وتتناقص إلى وقت خراب اورشليم ثم نقل إلى تبنة وبعد ذلك إلى طبرية حيث انتهى سنة 425 م. وكان لهذا المجلس سلطة عظيمة في الأمور الدينية والمدنية. وكان مؤلفاً من واحد وسبعين عضواً ينتخبون من الشيوخ والكهنة والكتبة ويرئسه رئيس الكهنة. وأما زمن اجتماعه ففي الصباح قرب الهيكل وحسب تقليد التلمود أخذ من السنهدريم الحكم بالموت قبل موت المسيح بثلاث سنين فلما جاء اليهود بيسوع إلى بيلاطس ليحاكمه قالوا له: ((لا يجوز لنا أن نقتل أحداً)) (يو 18: 31).(2) كان لليهود مجامع أخرى عديدة يختلف نظامها فيما بينها غير أنها كانت كلها خاضعة لمجمع السنهدريم في اورشليم.وكان المجمع في قرية عدد أصحاب بيوتها 120 فصاعداً مؤلفاً من ثلاثة وعشرين شخصاً، وفي القرى الصغيرة من سبعة قضاة ولاويين أو ثلاثة أعضاء فقط. وقد أشير إلى هذه المجامع وسلطتها في متى 5: 21 و 22 و مرقس 13: 9. وأما أرباب المشورة الذين خاطبهم فستوس (اع 25: 12) فكانوا مجمعاً قائماً بذاته يتعلق أمره بالحكومة الرومانية. وكان لليهود مجامع خارج أرض فلسطين في المدن الصغرى كما كان في سلاميس وفي قبرص (اع 13: 5) وفي ايقونية (اع 14: 1) وفي بيرية (اع 17: 10). وكانت هذه المجامع منفصلة عن الحكومة وتدير شؤونها الدينية والمدنية وهي خاضعة بالطبع لقانون البلاد.(3) كان ترتيب تلك المجامع أشبه بترتيب خيمة الاجتماع فإن الهيكل كان في الوسط وفيه تابوت لحفظ نسخة من الشريعة وقدامه المنبر وأما المقاعد فيها فكان بعضها أعلى من البعض الآخر أحياناً وكانت العليا مخصصة للشيوخ وتعرف ((بالمجالس الأولى)) (متى 23: 6 و يع 2: 2 و 3) وكان في الاجتماع الرجال يجلسون في جانب والسيدات في جانب آخر.وكان لكل مجمع خدمة مخصوصون(أ) رئيس المجمع (مر 5: 35 و اع 18: 8) وفي بعض المجامع كان يوجد عدد من الرؤساء (مرقس 5: 22 و اع 13: 15) وعمل الرئيس أن يرئس الخدمة ويعين أو يأذن لعدد مناسب أن يصلي ويقرأ الكتب ويعظ وكان مسؤولاً عن الممتلكات (لو 13: 14).(ب) المجلس ذاته وهو مؤلف من الشيوخ وذوي الرفعة والاقتدار (مر 5: 22).(جـ) خادم المجمع (لو 4: 30) وكان يعد البناء للعبادة ويعلم في المدرسة الملحقة بالمجمع.(د) كان لكل مجمع شمامسة لتوزيع الصدقات من أصل مال المجمع (مت 6: 2).(هـ) مختار الجماعة كان يقرأ الكتاب ويصلي، ونقرأ عن يسوع أنه اختير لقراءة الكتاب في مجمع الناصرة (لو 4: 16) وغالباً علّم في المجامع (مت 4: 23) وقد دعي بولس وبرنابا رؤساءُ المجمع في انطاكية بيسيدية لإلقاء كلمات الوعظ (اع 13: 15).وقد تألفت لجنة من ثلاثة أعضاء أغنياء من المجلس وثلاثة شمامسة والخدام والمختار ومعلم اللاهوت لإدارة العبادة. وكان يحق لمدبري المجمع أن يخرجوا منه المجرمين ويجلدوهم (مت 10: 17) على أن الإخراج من المجمع (يو 16: 2) كان شرّاً من الجلد عند اليهود، وربما كانت تجري محاكمة المجرمين في المجمع. وأما جلدهم فيوكل بالمجلس، والمجلس ينفذ حكم الجلد على يد رجل معين لذلك (اع 22: 19 و 26: 11) وبعد أن أخرج الرسل من مجامع اليهود أخذوا يجتمعون معاً للصلاة في بيوت خاصة (اع 2: 36 و 5: 42 و رو 5 و 1 كو 6: 19 و كو 4: 15).(4) أما ترتيب الصلاة فكان كما يأتي: كان الواعظ بعد اجتماع الشعب يعلو المنبر ويتلو الصلاة العمومية فيقف عندها كل الشعب في أماكنهم على غاية من الخشوع والوقار (مت 6: 5 و مر 11: 25 و لو 18: 11 و 13) ويرددون متحدين كلمة ((آمين)). وكانت الصلوات تسع عشرة طلبة يعقبها تلاوة بعض الآيات (تث 6: 4-9 و 11: 13-21 و عدد 15: 37-41) ثم كانت تكرر بعض الصلوات ثم يقرأ الناموس والانبياء. وأما الناموس فكان مقسماً إلى أربعة وخمسين فصلاً مع بعض إضافات من أقوال الانبياء يقرأ منها فصل كل سبت إلى أن تقرأ بجملتها على مدار السنة (اع 13: 27 و 15: 21).ومن غريب الاتفاق حسب قول البعض أن التلاميذ كانوا يقرأون في اليوم المقابل ليوم الخمسين فصلاً من يوئيل فيه إشارة إلى حلول الروح القدس عليهم. وبعد رجوع الشعب من السبي عينوا أشخاصاً لقراءة الناموس والانبياء وتفسيرها لأنهم كانوا قد نسوا أثناء لغتهم العبرانية واستبدلوها في الغالب باللغة الآرامية (نح 8: 2-8) فكان التفسير والوعظ يأتيان أخيراً ويقوم يهما أحد خدمة المجمع أو يكلف بهما شخص آخر اشتهر في التعليم إذا اتفق وجوده هناك، كما اتفق ذلك لمخلصنا وتلاميذه مراراً عديدة. ثم تختم الصلاة بإعطاء البركة.وكان اليهود يسمون المجمع بلغتهم ((بيت هكنست)) ولا تزال بقايا مباني المجامع هذه موجودة في الجليل في تل حوم التي هي كفر ناحوم قديماً وفي اربيد وكفر بيريم وفي أماكن أخرى. وشكل هذه المباني مربع ممتدة من الشمال إلى الجنوب يتوسطها فناء كبير وبابان صغيران على الجانب الجنوبي، وفي الداخل أربعة صفوف من الأعمدة يعلو كل منها تاج من الفن الإغريقي والمباني مزينة برسوم من ورق الكرمة وأغصان العنب. وقد دلت الاكتشافات الحديثة عام 1934 على وجود مجامع على الضفة اليمنى لنهر الفرات على طريق من حلب إلى بغداد يرجع تاريخها إلى عام 245 ميلادية ووجدت على جدرانها نقوش كثيرة توضح مناظر كتابية وتبين عصر أصول الفن المسيحي.وتدل المجامع العظمى على أن نحميا نظمها منذ عام 410 ق.م. وكان يشتمل المجمع على 120 عضواً (سفر المجلات 17 و 18) وقد ترأس عزرا على هذا المجمع. وكان سمعان العادل الذي مات حوالي 275 ق.م. آخر أعضاء هذا المجمع، وإن كانت فكرة وجود هذا المجمع غير مذكورة في مؤلفات يوسيفوس وفيلو وفي الكتاب المقدس إلا أن بعض التقاليد قد تصدق. وربما كان هذا المجمع العظيم مجلساً من الكتبة لتقرير المسائل اللاهوتية.'),
(997, 'جَمَل', '(مت 23: 24) والكلمة اللاتينية (كاملوس) وكذلك اليونانية وهي مشتقة من الكلمة الفينيقية والعبرية ((جامال)). ويوجد نوع من الجمال يسمى البكتري يكثر في سهول أواسط آسيا أما المذكور في الكتاب المقدس فهو من ذي السنام الواحد وأمّا الهجين فأصغر جسماً من الجمل العادي وأسرع منه (اش 66: 20) إذ أن العادي بطيء السير (2 مل 8: 9).في معدة الجمل تجويف قسم مقسّم إلى غرف أو حويصلات تمتلئ عند شربه ماء يكفيه مدة تختلف بين العشرين والثلاثين يوماً. والجمل يستمر نحو ربع ساعة يشرب. وأما طعامه فأغصان الأشجار والشوك والعشب الخ. وله من الصفات والأطوار ما يؤهله لسكني البرية والوعر. وهو حيوان مجتر ولكنه لا يشق ظلفاً ولذا فيعتبر من الحيوانات غير الطاهرة بحسب الناموس الموسوي (لا 11: 4 و تث 14: 7) وهو صبور على التعب، وأخمص قدمه مفلطح ويشبه الوسادة لكي لا يغرق في الرمال. وطأته أشد وأثبت من وطأة الحمار وهو أسهل منه انقياداً وأقدر على حمل الأثقال، ولهذا يسميه أهل الشرق بسفينة الصحراء. وقد أهلته العناية لأعمال شاقة كنقل البضائع في الصحاري المقفرة. وقد علم أن يركع إذا أريد تحميله فتظهر عليه عندئذ سمات الغضب وقلة الصبر ولكنه كثير الجلد حتى أنه قد يبقى مدة من الزمان سائراً بحمله بدون أن يكلّ، ومن طباعه أنه يأكل مرة في اليوم ويستعين بعنقه الطويل على تناول الأغصان أو الأعشاب التي يمر بها في طريقه. وهو يحمل من 100 إلى 160 رطلاً، ومعدل سيره بها ثلاثون ميلاً في اليوم وقد يحمل أكثر من ذلك إذا كانت المسافة قصيرة والعرب عادة يزينون عنق الجمل بالحلى والسلاسل (قض 8: 21 و 22-26).لحم الجمل ولبنه وشعره وجلده وزبله نافعة. فلحمه ولبنه للأكل وشعره لنسج الثياب (مت 3: 4) (انظر ((وبر)) ) وجلده لعمل الأحذية والسيور والأجربة وقرب الماء، وزبله للوقود. وهو يعمر من الثلاثين إلى الأربعين سنة ولهذه الأسباب المار ذكرها عم استخدام الجمل في الشرق من قديم الزمان، فاستخدمه التجار في تلك البلدان لنقل البضائع، واستخدمه الأفراد للركوب ولحمل الأثقال (تك 24: 64) وذكر الكتاب عن ملكه سبا أنها أتت إلى اورشليم بموكب عظيم جداً بجما حاملة أطياباً (1 مل 10: 2) وأخذ حزائيل حمل أربعين جملاً (2 مل 8: 9) وكان اقتناء الجمل يعد من الغنى والثروة، ومن ذلك ما ذكر عن أيوب أن كان له 2000 جمل، وعن قبيلة من العرب تدعى المديانيين أنها كانت هي وجمالها لا تحصى (قض 7: 12) وكانت الجمال تستخدم في الحرب لجر الأثقال ويركبها المحاربون (1 صم 30: 17) وكان العرب في الحرب يركب فارسان ظهراً لظهر على جمل واحد فيحمي أحدهما ظهر الآخر كرّاً وفرّاً. وذكر هيرودوتس أيضاً أن العرب في جيش زركسيس كانوا يركبون الجمال.ويستعمل لبن الجمل طعاماً. فقد جاء في تك 32: 15 أن يعقوب أفرز هدية لعيسو ((ثلاثين ناقة مرضعة وأولادها)) وللنياق المرضعات قيمة عند العرب. وللجمل صبر غريب عن الماء ومثله الغنم فهي تصبر عن المياه ولكن شتان ما بين صبرها وصبر الجمل فإنه في الأحوال الاعتيادية قد يكتفي بالندى وعصير اكلأ والعشب ومن عادة العرب أو تورد أغنامها الماء كل يومين مرة وأما جمالها فكل ثلاثة أيام مرة وذلك عند يبس الكلأ. ومن صفات الجمل أيضاً إنه قليل الأكل، غبي، ثقيل الحركة، سيء الطبع، منتقم أحياناً. وإذا ضرب أو نخس فلا يحس بالألم ما لم تكن الضربات شديدة. وليس في الجمل شيء جميل، وصغاره وكباره سواءٌ في ذلك، فإن الصغيرة قلما تمرح أو تلعب إنما هي رزينة في كل حركاتها كالكبيرة فمنظرها ومنظر الكبار واحد وأعمالها واحدة.ومع أنه مخلوق لسفر االصحاري فركوبه متعب جداً حتى أن السفر عليه 25 ميلاً يتعب أكثر من السفر على الخيل 50 ميلاً على أنه لولاه لكاد يتعذر قطع تلك المسافات الشاسعة في الصاري و القفار لأن الخيل لا تحمّل ما يحتمله الجمل من شظف المعيشة والاستعباد الطويل المستمر حتى لقد صدق العرب في ما قالوه من أن ((هذا الحيوان إنما هو مرحمة من مراحم الله)). ولا يزال العرب البدو يستخدمونه في جنوب فلسطين (1 صم 27: 9 و 2 اخبار 14: 15) ووطنه الأصلي الصحراء العربية والصحاري الافريقية (خر 9: 3 و قض 6: 5 و 1 مل 10: 2 و 1 اخبار 5: 18-21).وللجمل صفة غريزية تؤهله لأن يكون من الحيوانات الحاملة، وعندما يراد تحميله يبرك مستنداً على الحدبة المبطنة على صدره كقاعدة لجسمه الضخم. كما أنه يوجد له مخدات في أسفل قدميه نوافق سيره على الرمال والحصى حيث قُسم له نصيبه من الوجود.أما المسافرون فقد يضعون على ظهره فوق الرحل صندوقاً على كل جانب يجلسون فيهما. وقد يستعملون الهودج فيفرشون فيه سجادات ويحملون المؤونة للسفر. وقد أخفت راحيل تحت الحداجة تماثيل أبيها (تك 31: 34). أما العبارة الواردة في مت 19: 24 ((أن مرور جمل من ثقب إبرة أيسر من أن يدخل غني ملكوت الله)) فالمراد بها تصوير أمر خارق العادة. وأهل ملبار يستعملون هذا المثل للفيل ويميل بعض المفسرين لهذا القول أن عبارة ثقب إبرة هي اصطلاح للباب الصغير المثقوب في باب كبير لدخول الأغنام فقط منه. ويرجح أن الرأي الغالب هو أن المسيح قصد فيها المعنى البسيط الواضح الذي يدل على استحالة هذا الأمر.'),
(998, 'جَمَلِيّ', 'اسم عبري معناه ((سائق ظعن أي جمل)) أو ربما الكلمة الدارجة ((جمال)). هو أبو الجاسوس الذي من سبط دان (عد 13: 12).'),
(999, 'جمْلِئِيل', 'اسم عبري معناه ((الله كافأه)) ابن فدهصور رئيس من سبط منسى (عد 1: 10 و 2: 20 و 7: 54 و 59) والاسم القريب الشبه من غمالائيل معلم الناموس في سفر الأعمال 5: 34-39.'),
(1000, 'الجَمِيل', '(اع 3: 2) أحد أبواب الهيكل (اطلب ((هيكل)) ).'),
(1001, 'أَجْنَبِي', 'ليس كاهناً من نسل هرون (عد 3: 10 و 16: 40).'),
(1002, 'أجَانِب', '(مت 17: 25 و 26) أناس آخرون (من غير النسل الملكي).'),
(1003, 'أجْنَبِيَّات', 'نعت للنساء اللواتي أوقعن سليمان في الخطيئة (نح 13: 26).'),
(1004, 'جِنْتُوى', 'اسم عبري معناه ((بستاني)) رئيس من الكهنة اللذين رجعوا مع زربابل من بابل (نح 12: 4).'),
(1005, 'جِنْثُون', 'اسم عبري معناه ((بستاني)) أحد الكهنة اللذين ختموا العهد ليعبدوا الرب (نحميا 10: 6).'),
(1006, 'جَنَاح', 'جزء من الهيكل كانت حافته على ارتفاع عظيم من الأرض (مت 4: 5) فوق سطح رواق سليمان وكان يصعد إليه بدرج. وهذا المكان كان يشرف على واد عميق شرقيه من علو 400 قدم. ويزعم بعض المؤرخين أنه كان يوجد جدار في هذه الناحية يمتد من أسفل الوادي إلى مساواة سطح أرض الهيكل.'),
(1007, 'جُنْد', 'اطلب ((صفوف)) و ((حرب)).'),
(1008, 'جُنُود', 'تدل عبارة ((رب الجنود)) على أن الرب هو إله القوات في السماوات وعلى الأرض كما قال داود لجليات ((أنت تأتي إليّ بسيف وبرمح وبترس. وأنا آتي إليك باسم رب الجنود)) (1 صم 17: 45) والنبي أشعياء يقول ((هكذا ينزل رب الجنود للمحاربة)) (اش 31: 4) كما تدل العبارة ((رب الجنود)) على أن الله إله مهوب وكل قوي الكون جنود له بأقسامها الروحية والمادية التي لها صفوف وتنظيمات مرتبة بأمره.(1) أحد هذه التنظيمات الملائكة، أنه الرب إِله الجنود والذي ظهر ليعقوب في بيت إِيل لما رأى السلم وملائكة الله يصعدون وينزلون (تك 28: 12 و 13 و هوشع 12: 4 و 5). ((لأنه من في السماء يعادل الرب. من يشبه الرب بين أبناء الله. إِله مهوب جداً في مؤامرة القديسين ومخوف عند جميع الذين حوله. يا رب إِله الجنود من مثلك قويٌّ ربٌّ وحقك من حولك)) (مز 89: 6-8).(2) وتنظيم آخر هو النجوم والكواكب في ترتيبها الجميل وهيئتها العجيبة والرب قائدها. ويدعو النبي أشعياء الذين يريدون معرفة الله أن يرفعوا إلى العلاء عيونهم وينظروا جند السماء (اش 40: 26 و 45: 12).(3) وتنظيم ثالث تشتمل عليه عبارة ((رب الجنود)) هو جميع قوى الطبيعة وهي خاضعة لأمر الرب. هو الذي صنعها ويحييها كلها وجند السماء له تسجد (نح 9: 6) ويدخل ضمن هذا القسم أن رب الجنود يرسل السيف والجوع والوباء (ار 29: 17) وهو ((الجاعل الشمس للإضاءة نهاراً وفرائض القمر والنجوم للإضاءة  ليلاً الزاجر البحر حين تعج أمواجه رب الجنود اسمه)) (ار 31: 35). ومجمل القول أن قدماء الإغريق نظروا إلى السماوات فوقهم وإلى الأرض تحتهم ودعوا ما أبصروه الكون أو ((كوزموس)) وهذه كلمة تدل على جمال الموازنة، والرومان لما اكتشفوا ذات الحركات والعلاقات المتوازنة دعوا كل الخليقة ((يونيفوس)) والكلمة فيها وحدة وارتباط. وأما العبرانيون فبقوة التصوير الشعري ومعرفتهم بالإله القدير المتسلط. وعلمهم بالترتيب والنظام الظاهرين في كل مكان رأوا جيشاً عظيماً في العُدة وفي العدد ورتب متنوعة تعمل تحت مشيئة واحدة هي مشيئة رب الجنود الملك الذي بيده كل الأمور.'),
(1009, 'جُنْدُب', 'دويبة صغيرة من جنس الجراد وهي ترجمة الكلمة العبرية ((حاجاب)) ورد ذكرها عدة مرات في الكتاب المقدس. وحاجاب كلمة تدل على كثرة الجراد أو الجندب إذ يغطي الأرض أو يحجب الشمس. وردت كلمة جندب بين الطيور التي تؤكل بحسب شريعة موسى (لا 11: 22) وفي اش 40: 22 جاءت كدليل على الصغر لأن الجندب صغير الحجم جداّ. وكذا في سفر الجامعة دليل على أن أكله يستشقل عندما تبطل الشهوة رغم صغر حجمه (جا 12: 5) أما في سفر العدد 13: 33 فقد ترجمت الكلمة ((كالجراد)) لكثرة العدد وفي 2 اخبار 7: 13 ترحمت ((جراد)) ).'),
(1010, 'مَنْجَانِيق مَجَانِق', '(حز 4: 2 و 21: 22) المنجنيق آلة حرب كانت تستعمل لهدم الأسوار والحصون للمدينة المحاصرة، وهي عبارة عن جذع من الخشب المتين كالسنديان، أحد طرفيه كرأس الكبش ينتهي بحديد مدبب يعلق بمنتصفه في برج من خشب مركب على دواليب يقرب إلى الأسوار المقصود هدمها وكثيراً ما كان في البرج رامون بالقوس والمقلاع يطلقون السهام والحصى على المدافعين بينما يقرب المنجانيق ويطرق به السور.'),
(1011, 'جُنُون (مجنون)', 'اضطراب عقلي يجعل الإنسان خالياً من الفكر الصائب (تث 28: 28) وهو حالة يتخيل فيها المختل بأنه شخص آخر وإنه محاط بظروف غير الواقعة. ويحصل الجنون لأسباب شتى كالحزن والإجهاد العقلي والغضب والتصورات الدينية غير الحقيقية، وأحياناً من مرض في الدماغ. ويظن كثيرون أن المجنون هو مَن سلب الله عقله ويعامل غالباً بالشفقة. تظاهر داود بالجنون وأخذ يخربش على مصاريع الباب ويسيل ريقه على لحيته ونجا أمام اخيش ملك جت (1 صم 21: 13-15).ومن أعراض الجنون الهذيان (اع 12: 15 و 1 كو 14: 23) مثل المجنون الذي يلقي ناراً وسهاماً وموتاً. وقد ينتج السكر أعراضاً وقتية للجنون فيشرب السكارى ويترنحون ويجنون (ار 25: 16 و 51: 7) والجنون أيضا هو حالة للعواطف الثائرة غير المضبوطة ومن أعراضه الافتتان (ارميا 50: 38) ((بالأصنام تجن)) والحنق (مز 102: 8 و اع 26: 11) والحمق (لو 6: 11). ويذكر العهد الجديد بعض حالات فيها تنسب بعض الأمراض العقلية والجسمية إلى عمل الأرواح النجسة التي تتكلم (مر 1: 23) وتقر بلاهوت المسيح (مر 1: 24) وتتميز باختلافها عن شخصية الإنسان الذي تسكنه (مت 8: 31) ويعلمنا المسيح أن هذا الجنس لا يخرج إلا بالصلاة (مر 9: 29).'),
(1012, 'الجَان', '(اطلب ((عرافة)) ).'),
(1013, 'جَنَّة', 'الفردوس الأصلي الذي رتبه الله للإنسان قبل سقوطه (تك 2: 10 و 13: 10) ووضع في وسطه شجرة الحياة، وأطلقت الكلمة على كل بستان في قصور الملوك.'),
(1014, 'جَنَّات', 'بساتين معدة للإنشراح واللذات ومنها جنات الملك سليمان (جا 2: 5 ونشيد الإنشاد 4: 13) وفيها سواقي وينابيع (عدد 24: 6 و جا 2: 5 و اش 58: 11 و 65: 3) وكانت هذه الجنات مصونة لكي لا يدخلها الغريب (نش 4: 12-16).'),
(1015, 'مِجَن', '(اطلب ((ترس)) ).'),
(1016, 'مَجْنُون', '(انظر ((جنون)) ((وشيطان)) ).'),
(1017, 'أرْض جَنِيّسَارَت', 'قطعة من الأرض هلالية الشكل غرب بحر الجليل ذكرت في الكتاب المقدس مرتين فقط (مت 14: 23 و مر 6: 53 قابل لو 5: 1) ويزعم البعض أنها السهل المعروف بالغوير. وهي تمتد نحو ثلاثة أميال إلى أربعة طولاً بجانب البحيرة وأكثر من ميل عرضاً من شاطئها. ويحدها غرباً هضاب الجليل شمال مجدلة حيث تسقي أراضيها عدة مجاري مياه، والقسم الشمالي الممتد إلى خان منية ليس فيه مياه البتة ويظن أن أراضيه كانت تسقى بأقنية تأتيه من نبع كفرناحوم ويرجح أنها عين الطابغة.'),
(1018, 'بُحَيْرَة جَنّيسَارت', '(لو 5: 1) اسم شائع لبحر الجليل (يطلب في مكانه).'),
(1019, 'عَيْن جَنّيِم', 'اسم عبري معناه ((ينبوع الجنات)) وهي:(1) قرية في منخفضات يهوذا (يشوع 15: 34) ويقال أنها المعروفة بأم حنة في الجنوب الغربي من بيت شمس لكن عين فطير شرق بيت الجمال في نفس المجاورة تقع في موقع أقرب إلى عين جنيم القديمة.(2) مدينة في تخم يساكر يظن أنها جنين الحديثة وكان يمر فيها مجرى من الماء فيسقي الحدائق الجميلة المحيطة بها (يشوع 19: 21) وكانت من نصيب الجرشيون من بني لاوي (يشوع 21: 29) وهي على بعد نحو 5 أميال شمال شرقي دوثان و 7 أميال جنوب غربي جبل جلبوع وعلى بعد 78 ميلاً شمال أورشليم.'),
(1020, 'جَنُوبَت', 'اسم عبري معناه ((سرقة)) وهو ابن هدد عدو سليمان وقد ولد وتربى في بيت فرعون بعد أن فطمته تحفنيس الملكة أخت أمه (1 مل 11: 20).'),
(1021, 'جِهَاد', '(عب 12: 1 اطلب ((ألعاب)) ).'),
(1022, 'جَوَاهِر', '(اطلب ((حجارة كريمة)) ).'),
(1023, 'جُهًنَم', '(اطلب ((هنّوم)) ).'),
(1024, 'جُوب', 'اسم عبري معناه ((خندق أو صهريج)) (2 صم 21: 18 و 19) اسم السهل الذي شبت فيه نار الحرب مرتين بين الفلسطينيين وبني اسرائيل في حكم داود. ويظن البعض أنه جازر الوارد في 1 اخبار 20: 4 اطلب ((جازر)).'),
(1025, 'جُوج و مَاجُوج', '(1) رأُوبيني (1 اخبار 5: 4).(2) جوج كان رئيساً على ماشك وتوبال (حز 38: 39) اطلب ((ماشك و توبال)) وهو يوصف بأنه سيقوم ويغزو أرض اسرائيل في أخر الأيام وسيقتل على الجبال في مذبحة هائلة (حز 38 و 39) وهو وشعبه وحلفاؤه رمز إلى الوثنية في النبوات لأنهم يعاكسون ويقاومون ملكوت الله. وربما أخذ الاسم من جيجيس أحد رؤساء العائلات الليدية واشوربانبيال يدعوه جوجو كان في الحرس الملكي وموضع ثقة الملك. وفي عام 700 ق.م. قتل سيده من البيت المنافس للأسرة الهرقلية واغتصب عرش مملكة ليدية. كان غنياً جداً وقدم هدايا عظيمة لهيكل ابولو في دلفي وحارب ضد المدن الاغريقية في آسيا الصغرى كما يقول هيرودتس في تاريخه. وفي شيخوخته أخذت مملكته من الكميرين فهزمهم في القتال وأسر عدداً من رؤسائهم وخشي أن يتجدد الغزو فأرسل هدايا إلى الملك آشور بانيبال ملك آشور. ولمدة طويلة لم يستطع أحد من آشور أن يفهم لغة سفراء ليدية وأخيراً حصلوا على رجل يدرك اللغة وصارت صداقة بين جيجيس والآشوريين وبعد برهة قصيرة أعان جيجيس مصر لتثور ضد آشور فأثار عمله هذا انتقام ملك آشور الذي سلط عليه الكاميريين، وغزوا مملكة ليدية من جديد، وفي هذه الفترة حوالي عام 662 ق.م. قُتل جيجيس تاركاً العرش لابنه ارديس ليخلفه عليه انظر ((جومر وماجوج)).(3) شخصير سرية شبية بجوج المذكور في نبوة حزقيال ستظهر قبل نهاية العصر الحاضر، وربما تعمل ضد الكنيسة وتشدد النكير عليها  (رؤ 20: 8) أما ماجوج فقد كان ثاني أبناء يافت (تك 10: 2) ويقصد بهم قبائل السكيثيين المتوحشة الذين كانوا يأتون من الشمال بقواتهم العظيمة فرساناً ومشاة متسلحين بالقسي.'),
(1026, 'جُمْهُور جُوج', 'اسم الوادي الذي قبرت فيه قتلى جوج (حز 39: 11 و 15) ويظن أصحاب الترجوم أنه كان بقرب بحر الجليل، والأرجح، أنه كان على الطريق العظمى بين سوريَّا ومصر.'),
(1027, 'جُور أو عَقَبَة جُور', 'اسم عبري معناه ((جرو أو أسد أو أي حيوان)) (2 مل 9: 27) ويقع بالقرب من بيلعام وهو مكان على الطريق بين يزرعيل وجنين الحديثة حيث قتل اخزيا ملك يهوذا بأمر ياهو ومات.'),
(1028, 'جُور بَعْل', 'اسم كنعاني معناه ((مسكن بعل)) مقاطعة كان يقطنها العرب (2 اخبار 26: 7) يحتمل أن تكون في الصحراء جنوب بئر سبع عند حولة الغوري.'),
(1029, 'الجَوْزَاء', 'كان يطلق هذا الاسم في الميثولوجية اليونانية على توأمي زفس وهما- كاستور ويولكس اللذين تعلق بهما حظ نوتية تلك الأيام ولذلك كانوا يسمون السفن بعلامة الجوزاء تيمنا به (اع 28: 11).'),
(1030, 'جُوزَان', 'بلدة ومقاطعة في بلاد مادي على نهر خابور (2 مل 17: 6 و 18: 11 و 19: 12 و 1 اخبار 5: 26 و اش 37: 12) كان يسكنها بعض من بني اسرائيل دعاها بطلمايس جوزنيتيس (اطلب ((خابور)) ).'),
(1031, 'جُوشَن', '(1) مدينة في جبال يهوذا (يش 15: 51) وهي قرية الضاهرية الحديثة.(2) مقاطعة في جنوب يهوذا بين غزة وجبعون (يش 10: 41 و 11: 16).'),
(1032, 'جُوع', '((قلة الطعام)) أما بسبب انقطاع المطر وذبول المحصولات، أو منع دخول الطعام إلى مدينة محاصرة. وقد أخبر مراراً في الكتاب المقدس بجوع في مصر وفلسطين وبلاد العرب سببه في الأولى عدم فيضان النيل، وفي الثانية عدم سقوط المطر وفي الأخيرة إتيان الجراد. وكان من جملة أنواع قصاص الله لأجل الخطايا وكان الأنبياء يتنبأون عنه وجاء في تكوين 12: 10 أن جوعاً ألزم ابراهيم أن يترك كنعان ويتغرب في مصر كما تغرب اسحق في فلسطين (تك 26: 1) وفي أيام حكم يوسف حدث جوع شديد في مصر دام سنين وعم البلاد المجاورة (تك ص 41-46).وحدث جوع في أيام القضاة جعل اليمالك يتغرب في موآب (را 1: 1) وفي حكم داود (2 صم 21: 1) وفي أيام إيليا واخاب حدث جوع شديد (1 مل ص 17 و 18) وفي أيام اليشع حدث جوع أشد في السامرة لسبب الحصار (2 مل 4: 38 و 6: 24 و 25) فيه أكل الشعب لحم الحمير وزبل الحمام (2 مل 8: 1). وقد حدث جوع بسبب حصار أورشليم على يد نبوخذ ناصر (2 مل 25: 1-3 و ار 52: 4-6).وفي حكم كلوديوس عام 41-54 ميلادية حدث الجوع في مدة متعاقبة عم اليهودية واليونان وإيطاليا (اع 11: 28) كما يكتب عن هذا الجوع يوسيفوس وتاسيتوس.'),
(1033, 'جَوْعَة', 'وهي من أصل عبري ((جاعاة)) بمعنى ((خفض)) وهو مكان بالقرب من أورشليم من جهة الجنوب الغربي، تنبأ ارميا عن بناء المدينة ويمد خيط القياس إلى جوعة (ار 31: 39).'),
(1034, 'جُولاَن', 'ربما كان هذا الاسم كنعانياً وربما كان معناه ((شاطئ)) أو ((جانب)) أو ((جولان)) من الفعل جال. وهو اسم مدينة شهيرة للملجأ في باشان كانت تخص نصف سبط منسى شرق الأردن (تث 4: 43 و يش 20: 8 و 21: 27 و 1 اخبار 6: 71) ومنها تسمت تلك الناحية المعروفة باسم جولانيتس جولان الآن. ويذكر يوسيفوس في تاريخه عن الأمة اليهودية أن الكسندر جانيسوس مني بشر هزيمة قرب هذا المكان ثم أعاد عليها حملة أخرى فيما بعد ودمرها. وتقع هذه المنطقة بين حرمون واليرموك وتنقسم إلى جزئين: القسم الجنوبي أرض لينة قابلة للحرث، والشمالي نصف صخري وكان الجزء المنبسط منها يروى بمجاري من حرمون وينابيع عديدة، وبها مراعي من أغنى نوع في سوريا أما الآن فإنها خربة.'),
(1035, 'جوُلِيَا', 'اسم لاتيني مونث من يوليوس، وكانت سيدة مسيحية من رومية، يرجح أنها زوجة فيلولوغس الذي بعث إليه بولس سلامه (رو 16: 15).'),
(1036, 'جُومَر', '(1) هو بكر يافث (تك 10: 2 و 3) وقد قطنت ذريته جانباً عظيماً من آسيا الصغرى وفريجية (حز 38: 6 و 1 اخبار 1: 5 و 6) ويذكر هيرودتس أن هوميرس الشاعر الإغريقي لقبهم بأهل الشمال الأقصى (في كتابه الاوديسا) ثم أتوا إلى آسيا من المناطق التي وراء القفقاس واستوطنوا كبادوكية وهددوا الامبراطورية الآشورية ولكن آسرحدون هزمهم ثم اتجهوا إلى الغرب واحتلوا آسيا الصغرى واشتبكوا في عدة معارك مع جايجنيس (جوج) ملك ليدية وقتلوه. وقد طردهم الياتيس من ليدية فيما بعد.(2) جومر اسم عبري وربما كان معناه ((إله يكمل)) وهو اسم لابنة دبلايم وزوجة هوشع (هو 1: 3).'),
(1037, 'جُونِي', 'اسم عبري معناه ((مدهون أو أحمر فاتح))(1) ابن نفتالي ومؤسس عشيرة الجونيين (تك 46: 24 و عد 26: 48 و 1 اخبار 7: 13).(2) رجل من ذرية جاد (1 اخبار 7: 13).'),
(1038, 'جُونِيُّون', 'ذرية جوني (عد 26: 48).'),
(1039, 'جِوَاء', 'الكلمة العبرية لواد وهو بطن من الأرض أو واد واسع فيه دفن موسى في أرض موآب (تث 34: 6).'),
(1040, 'جِيبيِم', 'اسم عبري معناه ((أحواض محفورة لخزن الماء)) وهي قرية شمال أورشليم (اش 10: 31) اختلف العلماء على تحديد موقعها حالياً فمنهم من يدعوها خربة الدوير وآخرون باطن البطش. ويطن البعض أن المكان يدعى الآن الجيب، ويظن آخرون أنها العيسوية.'),
(1041, 'جِيح', 'اسم عبري معناه ((متدفق)) أو ((شلال)) وهي قرية تقع بالقرب من تل أمة بين جبعون في بنيامين ومخاضة الأردن بالقرب من مخاضة يبوق (2 صم 2: 24) وصل إليها يوآب وابيشاي في سعيها وراء ابنير بن نير.'),
(1042, 'جِيحزي', 'اسم عبري معناه ((وادي الرؤية)) هو غلام النبي اليشع ورفيقه (2 مل 4: 12) اخبر النبي عن رغبة مضيفتهم الشونمية في ان يكون لها ابن ولما مات الولد ذهبت الى اليشع وارتمت عند قدميه (2 مل 4: 12 و27) وحاول جيحزي ان يدفعها. اراد النبي ان يلقى عليه درسا في ان الأمر لا يتطلب سحرا بل ايمانا وصلاة لذلك ارسل الغلام بعصاه ليضعها على الصبي الميت ولم تأتي بنتيجة (2 ملو 4 :29 -37) لكن لما صلى قام الولد. واتفق ايضا انه لما شفى اليشع نعمان السرياني من داء البرص بأمر الله ان نعمان هذا قدم له تقدمة نفيسة فآبى النبي ان يأخذها فاغتاظ جيحزي من سيده لانه رد التقدمة ولحق نعمان خفية منه واخذ قسما من التقدمة لنفسه واخفاه فلصق به وبنسله برص نعمان عقاباً لما كان منه من طمع وكذب، كما تسبب في جلب الاحتقار للوظيفة النبوية.'),
(1043, 'جِيحُون', 'اسم عبري معناه ((نبع متدفق)).(1) اسم نهر من أنهار جنة عدن الأربعة (تك 2: 13) يظن أنه نهر اركيس الذي يصب في بحر قزوين. ويظن بعضهم أنه من أكبر الأنهار في بابل.(2) اسم ينبوع أو مجرى في ضواحي مدينة أورشليم وبقربه مسح سليمان ملكاً على اسرائيل (1 مل 1: 33-45) ويقال أن اليبوسيين حفروه عام 2000 ق.م. وكان ينبوع بهذا الاسم خارج المدينة في الغرب منها طمه حزقيا وجر مياهه بأقنية تحت الأرض إلى أورشليم خوفاً من أن يأتي الأعداء فيجدون مياهاً غزيرة (2 اخبار 32: 3 و 4 و 30) واتصلت إحدى هذه الأقنية ببركة سلوام داخل أسوار أورشليم (يو 9: 7).'),
(1044, 'جِيرا', '(اطلب ((مكاييل وأوزان)) ).'),
(1046, 'جَيْرُوت كِمْهَام', 'اسم عبري معناه ((مرقب كمهام)) ربما كان هو خان أو نُزُل (ار 41: 17) اطلب ((كمهام)).'),
(1047, 'جَيْش', '(مت 26: 53) إن الكلمة المترجمة جيش هنا هي الكلمة اليونانية لجئون- وهي الفرقة الرئيسية في الجيش الروماني. كانت في الأصل مكونة من 3000 عسكري من المشاة ومن 300 من الفرسان. ومن عام 100 ق.م. إلى سقوط الإمبراطورية اختلف العدد ما بين 5000 إلى 6000. ومن أيام اوغسطس إلى هادريان في زمان العهد الجديد كان العدد 6000 هو الاعتيادي ما عدا الفرسان. وكانت هذه الفرقة الرئيسية أو اللجئون (legion) يحتوي على 10 كتائب كل كتيبة فيها 3 أوراط وكل أورطة فيها 200 جندي. غير أن الكلمة تفيد أيضاً عدداً غير معين (مرقس 5: 9 و 15) وأما كلمة جيش في معناها المطلق فإنها لفظة تدل على عدد من الجنود ا, الغزاة كبيراً كان أو صغيراً (تك 49: 19 و 2 صم 22: 30).'),
(1048, 'جِيشان', 'اسم عبري معناه ((قذر)) رجل من يهوذا. ابن يهداي (1 اخبار 2: 47).'),
(1049, 'جِيل: أجْيَال', '(دور) كما هو لفظة الكلمة العبرية ولها عدة معان.(1) كل فوج من الأشخاص المتسلسلين بالتعاقب من سلف مشترك (تك 50: 23 و خر 20: 5 و تث 23: 2).(2) عصر يعيش فيه أناس في وقت واحد كمعاصرين (تك 7: 1 و عد 32: 13).(3) طور من حياة العائلة أو الجنس البشري في مدة من الزمان تساوي مئة سنة (تك 15: 16 و مت 1: 17 و خر 1: 6).(4) صنف من الناس (جيل أعوج ملتو) (تث 32: 5).(5) وقت من الأوقات (جا 1: 4 و لو 1: 50).'),
(1050, 'أجْيَال', '(تشترك بالمعاني المشار إليها في جيل وتأتي أيضاً بمعنى التوالد) (تك 6: 9) وهي ترجمة ((دوروث)) العبرية.'),
(1051, 'جِيلُونِي', 'أحد سكان مدينة جيلوه (2 صم 15: 12 و 23: 34).'),
(1052, 'جِيلُوه', 'قرية في تلال يهوذا (يشوع 15: 51 و 2 صم 15: 12) ومنها كان اخيتوفل.'),
(1053, 'الجيلُونِي', 'وحالياً هي خربة جعلا على مسافة ميل وربع غرب ادمار وعلى مسافة 5 أميال شمال غرب حبرون.'),
(1054, 'جِينَة', 'اسم عبري معناه ((حماية)) أبو تبني وهو الذي نازع عمري عرش الملك على إسرائيل (1 مل 17: 21 و 22).'),
(1055, 'حَابَر', 'اسم عبري معناه ((شركة، شريك)).(1) ابن بريعة، حفيد اشير، ومؤسس أسرة (تكوين 46: 17 و عدد 26: 45).(2) القيني، من بني حوباب حمو موسى، وزوج ياعيل التي قتلت سيسرا (قضاة 4: 11-24).(3) رجل من سبط يهوذا، من نسل عزرا، وأبو سوكو (1 اخبار 4: 18).(4) بنياميني، ابن شحرايم من ألْفَعَل (1 اخبار 8: 17).'),
(1056, 'حَابَريُّون', 'ذرية حابر (عدد 26: 45).'),
(1057, 'حَاجَات', 'اسم عبري معناه ((جرادة، جندب)) رجل رجع بنوه من بابل مع زربابل، مؤسس أسرة من النثينم (عزرا 2: 46).'),
(1058, 'حَاديِد', 'اسم عبري معناه ((حاد، مسن، سن)) وهي قرية في بنيامين، تقع على بعد ثلاثة أميال شرقاً في الشمال شرقي من لود، أي لدّ (عزرا 2: 33 و نحميا 11: 33). وكانت قرية مبنية على تل تشرف على السهل، وتدعى الآن ((حديثة)).'),
(1059, 'حَارَان', 'اسم ربما كان من أصل اكادي معناه ((طريق، قافلة)) وهو اسم:(1) مدينة بين النهرين، على نهر بليخ وهو فرع للفرات وتقع على مسافة 280 ميلاً إلى الشمال الشرقي من دمشق. وكانت المدينة مركزاً تجارياً، لكونها على أحد الطرق التجارية الرئيسية بين بابل والبحر المتوسط، وقد اتخذت إِله القمر إلهاً لها وتغرّب فيها تارح وابراهيم مدة من الزمن، ومات تارح هناك (تكوين 11: 31 و 32 و 12: 4 و 5). وسكنت فيها أسرة ناحور، ولابان أخو رفقة، ويعقوب (تكوين 27: 43 و 28: 10 و 29: 4 و 5). وقد استولى عليها الاشوريون كما ورد ذلك في 2 ملوك 19: 12 و اشعياء 37: 12. وفي 53 ق.م. انهزم القائد الروماني كرسوس، رفيق بومباي ويوليوس قيصر بالقرب من حاران أمام القائد الفارسي سورينا، وذُبح بطريقة وحشية حالاً بعد ذلك. والمدينة الآن قرية صغيرة لا تزال محتفظة بالاسم حران.(2) ابن كالب وعيفة، من أسرة حصرون (1 اخبار 2: 46).'),
(1060, 'حَارِث', 'اسم عبري معناه ((وعر)) وهو اسم غابة في يهوذا، اختبأ فيها داود مدة من الزمن بينما كان شاول يهدد حياته (1 صم 22: 5). ويعتقد البعض أن موقعها كان عند قرية خرس الحديثة إلى الجهة الشمالية من وادي ارنبة قرب قعيلة.'),
(1061, 'الحَارِث', 'اسم كثيرين من ملوك المملكة النبطية العربية ومن منهم:(1) ملك كان معاصراً لرئيس الكهنة ياسون، نحو 170 ق.م. (2 مكا 5: 8).(2) الحارث الرابع (9 ق.م. -40 ق.م.) وهو حمو هيرودس انتيباس رئيس الربع وعندما طلق هيرودس ابنته لكي يتزوج هيروديا أهلن الحارث عليه حرباً وهزم جيشه هزيمة ساحقة في 36 م. وانجاز الرومان إلى هيرودس، وأرسلوا فايتيليوس لتأديب الحارث، لكن موت الإمبراطور طيباريوس انهى الحملة. وكان نحو 39 أو 40 م. أثناء حكم كاليجولا، أو ربما في 36 م. إن الحارث استولى على دمشق، وأقام فيها نائباً من قبله (2 كورنثوس 11: 32). وقد حاول حاكم دمشق أن يقبض على بولس (اعمال 9: 24 و 25). (انظر نبطيون، وسالع).'),
(1062, 'حَارَس', 'اسم عبري معناه ((شمس)) وهو اسم:(1) مرتفع في عجلون (قضاة 1: 35). وبالمقارنة مع يشوع 19: 41 و 42 يصبح الرأي القائل بأنّ جبل حارس هو مدينة عين شمس، أي بيت شمس محتملاً.(2) عقبة حارس (قضاة 8: 13)، بقعة شرقي الأردن، رجع منها جدعون بعد هزيمة زبح وصلمناع.(3) مدينة مصرية، ورد اسمها في بعض المخطوطات في اشعياء 19: 18 وهي مدينة الشمس، أي هيليوبوليس انظر اون (2).'),
(1063, 'حارُوص', 'اسم عبري معناه ((ذهب)) وهو حمو الملك منّسى، وجد الملك آمون لامه (2 ملوك 21: 19).'),
(1064, 'حَاريِف', 'اسم عبري معناه ((خريف، خريفي، حصاد)).(1) رب أسرة، رجع أفرادها من بابل مع زربابل (نحميا 7: 24).(2) رئيس ختم العهد، كممثل لأسرته (نحميا 10: 19). وهو نفسه يورة (عزرا 2: 18)، الذي معناه أيضاً مطر خريفي.(3) رجل من بني كالب (1 اخبار 2: 51).'),
(1065, 'حَاريِم', 'اسم عبري معناه ((حريم، حرم، مكرس، افطس)).(1) رجل من نسل هارون. كونت أسرته الفرقة الثالثة من الكهنة في أيام داود (1 اخبار 24: 1 و 6 و 8). وربما كانوا أفراد الأسرة التي رجعت من بابل (عزرا 2: 39 و نحميا 7: 42). وفي الجيل التالي بعد السبي حمل هذا الاسم بيت أب بين الكهنة (نحميا 12: 15) حيث دعي أيضاً حريم. انظر رحوم. وفي عصر متأخر كان البعض من هذه الأسرة بين الذين تزوجوا نساء غريبات (عزرا 10: 21). وبعد ذلك أيضاً كان كاهن بهذا الاسم بين الذين ختموا العهد لكي يحفظوا شريعة الله ولكي يسعوا لينموا التزوج بالنساء الغريبات (نحميا 10: 5).   \n(2) مؤسس أسرة غير كهنوتية، رجع أفرادها من بابل مع زربابل، وطردوا نساؤهم الغريبات، ورمم أحد أبنائه قسماً من سور أورشليم (عزرا 2: 32 و 10: 31 و نحميا 3: 11 و 7: 35).'),
(1066, 'حَاصُور', 'اسم عبري معناه ((حظيرة)) وكان اسم:(1) عاصمة مملكة الكنعانيين في شمال فلسطين، كان يحكمها يابين في زمن يشوع، ويعتقد البعض أنها كانت تقع فوق مياه ميروم، أخضعها يشوع وأحرقها (يشوع 11: 1-13 و 12: 19). وأعيد بناؤها وأعطيت لسبط نفتالي (يشوع 19: 36). وفي أيام دبورة وباراق استولى عليها ملك آخر كنعاني اسمه أيضاً يابين (ولذلك يعتقد البعض أن يابين كان لقباً لملوك كنعان، كفرعون لملوك فلسطين، والحارث لملوك شمالي بلاد العرب). وعند هزيمة قائده سيسرا، حاول أو يواصل حربه ضد العبرانيين، لكنه انهزم أخيراً وقتل (قضاة 4: 1-24 و 1 صموئيل 12: 9).(2) حاصور في أقصى جنوب يهوذا قرب قاديش (يشوع 15: 23). وربما كانت تقع مكانها اليوم الجابرية قرب بئر الحافر، التي على نحو تسعة أميال جنوب شرق العوجة.(3) قرية حصرون (يشوع 15: 25) ويقال أنها غالباً خربة القريتين نحو أربعة أميال ونصف جنوب تل ماعين.(4) قرية بنيامين (نحميا 11: 33). ويقول البعض أنها خربة حاصور، على بعد أربعة أميال إلى الشمال الغربي من أورشليم، وبين بيت حنينة والنبي صموئيل.(5) مقاطعة في الصحراء العربية شرق فلسطين. ومما يدل على عظمة هذه المدينة سابقاً ما ورد عنها في أرميا 49: 28-33، فقد تنبأ ارميا بنهب نبوخذ نصر لها. ويذكر بيروسوس أن نبوخذ نصر هزم العربية. وربما يكون الاسم اسم مجموعة ويشير إلى حياة الجماعة القروية المستقرة إذا ما قورنت مع البدو والرّحل. انظر (حدّته).'),
(1067, 'حَاصُور حَدَتَّه', '(أو حاصور وحدتة، كما وردت في بعض النسخ كمكانين). \nفإذا كانت الكلمة الثانية أرامية فالاسم معناه ((قرية جديدة)). في جنوب يهوذا (يشوع 15: 25)، حسب الظاهر قرب صور. وربما كانت في الحظيرة جنوب شرق طواني نحو البحر الميت. وهي غير حاصور الثانية المذكورة في 15: 23. ويعتقد البعض أن مكانها هي خرب قصر الحدادة.'),
(1068, 'حَافَر', 'اسم عبري معناه ((حفرة أو بئر)) وهي:(1) مدينة غرب الأردن (يشوع 12: 17). والاسم لمقاطعة، ربما كانت قرب سوكوه (1 ملوك 4: 10). ويقول البعض أنها تل بيشار على وادي الحوارث في سهل شارون، والأرجح أنها في يهوذا استولى عليها يشوع وربما كانت هي المشهد الحالية.(2) ابن جلعاد مؤسس أسرة في منّسى (عدد 26: 32 و 33 و 27: 1 و يشوع 17: 2 و 3).(3) رجل من يهوذا، ابن اشحور من تقرع (1 اخبار 4: 6).(4) المكيراتي من أبطال داود (1 اخبار 11: 36). انظر مع ذلك الملاحظات على اور (2).'),
(1069, 'حَافَريُّون', 'ذرية من سبط منّسى (عدد 26: 32).'),
(1070, 'حَالَص', 'اسم عبري ربما كان معناه ((قوة أو (الله) قد خلّص، خالص)).(1) الفيلوني من بني افرايم، أحد الرؤساء الثلاثين الذين كانوا مع داود، رئيس فرقة الشهر السابع (2 صموئيل 23: 26 و 1 اخبار 11: 27 و 27: 10).(2) رجل من بني يهوذا من نسل حصرون (1 اخبار 2: 39).'),
(1071, 'حَالَف', 'اسم عبري معناه ((حلف، محالفة، مبادلة)) وربما كان معناه ((قصبة)) مدينة على حدود نفتالي (يشوع 19: 33). ويظن آخرون أنها الرأس. ويرجح أنها عرباتة شرقي جبل تابور.'),
(1072, 'حَالَق', 'اسم عبري معناه ((قسم، نصيب)) ابن جلعاد جد الحالقيين، أسرة منّسى (عدد 26: 30 و يشوع 17: 2).'),
(1073, 'حَالَقيُون', '((ذرية حالق)).'),
(1074, 'حَالِم', 'اسم عبري معناه ((صحة، قوة)). وهو حلداي (زكريا 6: 10 و 14).'),
(1075, 'حَام', 'اسم عبري معناه ((حامي أي ساخن أو حمي حماية)). أصغر أبناء نوح، ولد بعد ما كان عمره 500 سنة (تكوين 5: 32 و 6: 10 و 9: 24). في وقت الطوفان كان متزوجاً لكن يظهر أنه لم يكن له بنون بعدُ (تكوين 7: 7 و 1 بطرس 3: 20). في حادث سكر أبيه تصرف تصرفاً عاقاً فجلب على نفسه وعلى نسله كنعان اللعنة (تكوين 9: 22-27). وقائمة شعوب العربية الجنوبية، وكوش، أي الحبشة، ومصر، وكنعان (تكوين 10: 6-14). تشتمل كلا ذراريه وأولئك الذين أخذوا عن طريق الغزو وغيره. \nوقد أطلق الاسم على مصر واستخدم في الكتاب المقدس في الشعر فقط (مزمور 78: 51 و 105: 23 و 27 و 106: 22).'),
(1076, 'حامُول', 'اسم عبري معناه ((محمول، يرثى له، يبقى)). أصغر أبناء فارض، ومؤسس أسرة في يهوذا (تكوين 46: 12 و عدد 26: 21 و 1 اخبار 2: 5).'),
(1077, 'حامُوليُون', 'نسل حامول.'),
(1078, 'حانان', 'اسم عبري معناه ((حنان، رحيم، كريم)).(1) بطل من أبطال داود (1 اخبار 11: 43).(2) رجل شهير من بنيامين، ابن شاشاق (1 اخبار 8: 23).(3) ابن آصيل، من نسل يوناثان ابن شاول (1 اخبار 8: 38 و 9: 44).(4) نبي ابن يجدليا، كان لأولاده مخدع في الهيكل (ارميا 35: 4).(5) مؤسس أسرة من النثينيم رجع أفردها من بابل مع زربابل (عزرا 2: 46 و نحميا 7: 49).(6) رجل ربما كان لاوياً استخدمه عزرا مع آخرين لكي يفهم الشريعة للشعب (نحميا 8: 7). ويظهر أنه ختم العهد (نحميا 10: 10) وسمي حنان. (7 و 8) رئيسان للشعب، ختما العهد أيضاً (نحميا 10: 22 و 26).(9) ابن زكور، أقامه نحميا جامع أعشار أو مساعداً للخزانة، نائباً عن الشعب (نحميا 13: 13) والخزنة الأربعة المشار إليهم هنا انتخبهم نحميا من طبقات الشعب الأربع هم: الكهنة والكتبة واللاويون والشعب.'),
(1079, 'حانُون', 'اسم عبري معناه ((حنون، منعِم، منعَمْ عليه)).(1) ملك العمونيين، ابن ناحاش وخلفه وكان ناحاش، صديق داود وقد صنع مع داود معروفاً. أرسل الملك العبري عبيده إلى حانون ليعزوه على موت أبيه، وليهنئوه بجلوسه على العرش. لكن المشيرين الأشرار أو عزوا إليه بأن الغرض الحقيقي للبعثة كان التجسس على عاصمة العمونيين، ولذلك أساء حانون معاملة السفراء وعاملهم بفظاظة وبطريقة مخجلة، إذ حلق أنصاف لحاهم وقصر ثيابهم من الوسط. وإذ علم بأن هذه الإهانة ستستنكر، استعد للحرب، وتحالف مع الاراميين أي السوريين، لكنه انهزم وخسر الحرب (2 صموئيل 10: 1-11: 1 و 1 اخبار 19: 1- ص 20: 3). (2 و 3) يهوديان رممّا قسماً في سور أورشليم في عهد نحميا (نحميا 3: 13 و 30).'),
(1080, 'حانِيس', 'مدينة في مصر (اشعياء 30: 4)، تبعد نحو 50 ميلاً جنوب ممفيس على الضفة الغربية للنيل، وهي لا تزال معروفة بأهناس، أي اهناسيا، وفي العصر اليوناني الروماني كانت معروفة بهيرا كليوبوليس العظمى.'),
(1081, 'حبايا', 'اسم عبري معناه ((يهوه قد خبأ، خبأة يهوه)). أبو بعض اليهود الذين ادّعوا بأنهم نسل مقدس. ولمْا لم توجد أسماؤهم في السجل، طردوا من الكهنوت (عزرا 2: 61). أما النطق الصحيح لهذا الاسم كما ورد في نحميا 7: 63 فهو حبايا وليس حبابا.'),
(1082, 'حَبَّة', 'انظر حنطة، قمح وشعير.'),
(1083, 'مَحَّبة', 'المحبة سواء استخدمت عن الله أو الإنسان، هي الرغبة الحارة الملتهفة لأجل خير المحبوب، والاهتمام العظيم برفاهته. والمحبة لكلا الله والإنسان أساسية للديانة الحقيقية، سواء كما هي موضحة في العهد القديم أم العهد الجديد. وقد صرح الرب يسوع نفسه أن كل الناموس والأنبياء يتوقف على المحبة (متى 22: 4 و مرقس 12: 28-34). والرسول بولس، في أنشودته الفريدة عن المحبة (1 كورنثوس 13)، يجعلها أعظم الفضائل أو النعم في الحياة المسيحية، أعظم من التكلم بالألسنة، أو موهبة النبوة، أو اقتناء إيمان فائق السمو، لأنه بدون المحبة، كل هذه المواهب والنعم، مهما تكن مرغوبة ونافعة في حد ذاتها، تكون كلا شيء، وبلا قيمة دائمة في نظر الله. ولا يعني هذا أن الرب يسوع أو الرسول بولس يقلل من قيمة الإيمان الذي منه تنبع كل النعم، فنعمة الإيمان هذه معترف بها كنعمة أساسية في جميع معاملات الله مع الإنسان. ومعاملات الإنسان مع الله (يوحنا 6: 28 و 29 و عبرانيين 11: 6)، لكنَّ كليهما يحسبان أن الإيمان ما هو إلا عقيدة عديمة الأثر وباطلة ما لم يظهر نفسه في المحبة لكلا الله والإنسان. وكما أن المحبة هي أسمى تعبير عن الله وعلاقته بالجنس البشري، كذلك يجب أن تكون أسمى تعبير عن علاقة لإنسان بخالقه وبأخيه الإنسان.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1084, 'ولائم محبّية', 'ترجمة كلمة يونانية ((اجاباي)) في يهوذا 12 ((ولائم محبية)) كما توجد أيضاً في بعض المخطوطات في2 بطرس 2: 13 بدلاً من كلمة ((اباتاي)) المترجمة ((غرورهم)). كانت الولائم المحبية ولائم اجتماعية تقام في الكنائس متصلة بعشاء الرب (قارن اعمال 2: 46 و 6: 1 و 20: 7 و 11). ويظهر أن فريضة عشاء الرب أو الافخارستيا (الشكر). كانت في الأصل تقع في ختام وليمة المحبة (قارن متى 26: 26-29 و مرقس 14: 22-25 و لوقا 22: 14-20 و 1 كورنثوس 11: 25). ويبدو أنه كانت هناك حالات في كورنثوس حيث دُنِّست الوليمة، فبدلاً من أن تكون تعبيراً عن الأخوة، صارت وليمة عادية وفرصة للتفاخر والشراهة (1 كورنثوس 11: 17-34). ويقرر ذهبي الفم أنه بعدما توقفت شركة المقتنيات الأولى، كان أغنى الأعضاء يحضرون تبرعاتهم من الطعام والشراب إلى الكنيسة، التي فيها، عند ختام الاجتماعات وبعد ممارسة فرضية عشاء الرب، كان يشترك الجميع، ولا يستثنى الفقراء. وبهذه الوسيلة هم يساعدون على تقدم مبدأ المحبة بين المسيحيين. وقد حرمت بعض المجامع الكنسية الأولى إقامة مثل هذه الولائم، لكن جميع هذه المجامع معاً لم تنجح تماماً في إطفاء التمسك الزائد بحياة ولائم المحبة في الكنيسة الغربية، بينما لا تزال موجودة في الكنيسة اليونانية. ومن الطوائف الدينية الأكثر حداثة، والتي أنعشت الولائم المحبية، المورافيون والدانكريون. وهي موجودة أيضاً بين السانديمانيين.'),
(1085, 'حُبُر', 'انظر: جلد، وجلدة، وجلادون.'),
(1087, 'حَبْرُون', 'اسم عبري معناه ((عصبة، صحبة، رباط، اتحاد)). وهي:(1) لاوي ابن قهات ومؤسس أسرة (خروج 6: 18 و عدد 3: 19 و 1 اخبار 6: 2 و 18 و 23: 12).(2) مدينة في أرض يهوذا الجبلية (يشوع 15: 48 و 54)، ودعيت أصلاً قرية أربع (مدينة رباعية، تيترابوليس) (تكوين 23: 2 و يشوع 20: 7)، انظر أربع. وقد بنيت سبع سنين قبل صوعن، في مصر (عدد 13: 22)، وكانت موجودة من وقت مبكر في أيام ابراهيم، الذي سكن بعض الزمن في جوارها، تحت بلوطات أو بطمات ممرا (تكوين 13: 18 و 35: 27). وماتت سارة هناك، واشترى ابراهيم مغارة المكفيلة لتكون قبراً، وقد اشتراها من الحثيين الذين كانوا يملكون المدينة حينئذ (تكوين 23: 2-20). وتغرب اسحق ويعقوب مدة من الزمن في حبرون (تكوين 35: 27 و 37: 14). زارها الجواسيس، ووجدوا العناقيين ساكنين فيها (عدد 13: 22) وكان ملكها هوهام، أحد أربعة ملوك تحالفوا مع ادوني صادق ضد يشوع، لكنهم انهزموا، وأسروا، وقتلوا (يشوع 10: 1-27). وأُخِذَت حبرون نفسها فيما بعد وأهلك سكانها (الآيات 36-39). هذه القصة لها تكملة في يشوع 11: 21 و 2، حيث سجل أن يشوع في ذلك الوقت قطع العناقيين من حبرون، ودبير، وعناب، وكل البلاد الجبلية، وأهلك مدنهم تماماً. لكن بعد هذه الحملة الأولى العامة، رجع المتبقون بالتدريج من مخابئهم وملاجئهم. وفي مدى سنوات قليلة أعادوا بناء كثير من المدن المخربة. وكان بين أولئك الراجعين بقايا القبائل الثلاث من العناقيين الذين سكنوا في حبرون. فلقد وجدوا مستوطنين هنا مرة أخرى بعد غزو كنعان (يشوع 14: 12). وقد طالب كالب بهذه المقاطعة ملكاً له، عندما امتلك سبط يهوذا إقليمه المخصص له بعد موت يشوع، عاد كالب فأخذ حبرون (قضاة 1: 10 و 19 و 20 و يشوع 15: 13-19). وكان لحبرون قرى تابعة لها (يشوع 15: 54). وقد أعطيت للكهنة، وكانت إحدى مدن الملجأ (يشوع 20: 7 و 21: 10-13 و 1 اخبار 6: 54-57). وأرسل داود إلى هناك جزءاً من غنيمة صقلع التي استردها (1 صموئيل 1: 1-3 و 11 و 32 و 5: 1-5 و 1 ملوك 2: 11 و 1 اخبار 29: 27)، وولد هناك عدد من أولاده (2 صموئيل 3: 2-5 و 1 اخبار 3: 1-4). ودفن هناك ابنير (2 صموئيل 3: 32) ووضع رأس ايشبوشث في القبر نفسه (2 صموئيل 4). وفي حبرون رفع ابشالوم راية العصيان (2 صموئيل 15: 17-10). وحصنها رحبعام (2 اخبار 11: 5 و 10). وأثناء السبي، عندما احتل الأدوميون جنوب يهوذا، وقعت حبرون، ضمن أماكن أخرى، في أيديهم. وقد استرجعها منهم يهوذا المكابي. في ذلك الوقت كان لها قلعة ذات أبراج وكانت رأس المدن الأخرى. ولم تذكر في العهد الجديد. وحبرون هي الآن مدينة الخليل. لأنها مدينة ابراهيم خليل الله (يع 2: 23). وهي من أقدم المدن في العالم التي لا تزال آهلة بالسكان، وحبرون واقعة في الوادي وعلى منحدر، وتعلو 3040 قدماً فوق مستوى البحر. وهي على بعد 19 ميلاً إلى الجنوب الغربي من أورشليم، وثىثة عشر ميلاً ونصف ميل إلى الجنوب الغربي من بيت لحم. ويوجد 25 ينبوعاً من الماء وعشرة آبار كبيرة قرب حبرون، مع كروم وغابات زيتون. وفي المكان الذي قيل أن فيه قبر ابراهيم وسارة واسحق ويعقوب أقيمت كنيسة في عصر الإمبراطور جستنان. وفي ذلك المكان يقوم اليوم جامع كبير.(3) اسم رجل ذكر في جداول الأنساب لسبط يهوذا (1 اخبار 2: 42 و 43).'),
(1088, 'حبرونيون', 'عشيرة اللاويين القهاتيين من نسل حبرون (عدد 3: 27 و 26: 58 و 1 اخبار 26: 23 و 30 و 31).'),
(1089, 'حَبْس', 'انظر: قصاص.'),
(1090, 'حَبَشَة', 'انظر: كوش.'),
(1091, 'حَبْصينيا', 'اسم عبري معناه ((نور أو مصباح يهوه)) رجل من الركابيين عاش قبل ارميا بزمن طويل (ارميا 35: 3).'),
(1092, 'حَبقوق', 'اسم عبري معناه ((يعانق، أو ربما اسم نبات حدقة)). نبي في يهوذا. ويستنتج من مزموره في الإصحاح الثالث ومن الإرشادات لإمام المغنين في الآية 19 أنه كان من سبط لاوي وأنه أحد المغنين في الهيكل. وسفر حبقوق هو الثامن في النبوات الصغيرة ويتكون من:(1) شكوى أولى: صرخة إلى الله ضد العنف والعسف والإثم والظلم لا تُسمع (حبقوق 1: 2-4)، الشر منشر ومنتصر. جواب الرب: الله يقيم الكلدانيين (الآيات 5: 10) لكنهم كمذنبين سيعاقبون (الآية 11).(2) شكوى ثانية: ملكوت الله لن يتلاشى فعلاً، والكلدانيون سيفتقدون بالحكم والتأديب (الآية 12) لكن لا تزال هناك مشكلة أدبية باقية: الله يسمح للكلدانيين أن يتلفوا ويخربوا أولئك الذين هم أبر منهم. هل يستمر هذا? (الآيات 13-17) جواب الرب: الكلدانيون منتفخون وغير مستقيمين هذه الحقيقة كافية لعين الإيمان، أنها تحكم عليهم بالهلاك (قارن حبقوق 1: 1 واشعياء 10: 12-16) وتحكم على الجميع الذين هم مثلهم، أما البار فبإيمانه يحيا (حبقوق 2: 1-4). هذا الحق يفهم فهماً صحيحاً، يحل المشكلة فضلاً عن ذلك، فإن الإيمان بيقينية أن الله سيعاقب الإثم يجعل النبي قادراً على أن ينطق بخمس ويلات على الكلدانيين بسبب شرهم (الآيات 5-20).(3) صلاة تسبيح (الإصحاح الثالث) التي فيها بعد دعاء وطلبة أن الله في الغضب يذكر الرحمة (الآية 2)، يصف النبي ظهور الله في جلاله، وما يلحق ذلك من رعب لأعدائه (الآيات 3-15)، كما يعبر عن الثقة الهادئة التي لإيمانه بالله (الآيات 16-19).ومن الواضح أن السفر كتب في عصر الكلدانيين لأن:(1) الهيكل كان لا يزال قائماً (2: 10) والخدمة الموسيقية تمارس فيه (3: 19).(2) الكلدانيون يصبحون قوة مخفية بين الشعوب أثناء ذلك الجبل (1: 5 و 6)، وقد بدأوا فعلاً في قتل الأمم. (الآيات 6 و 17).ولقد كان الكلدانيون معروفين منذ زمن طويل لدى العبرانيين. قد استرعوا كثيراً ما الالتفات في ثوراتهم ضد الآشوريين في 626 ق.م. أو بانتصارهم على المصريين في كركميش 605 ق.م. ومن السفر، يبدو أن حبقوق تنبأ أثناء حكم يهوياقيم (607-597 ق.م.) لكن من الصعب تعيين العصر بدقة. ويعتقد غالبية النقاد أن النبوة ترجع إلى زمن وقوع معركة كركميش. ويعتقد آخرون أن تاريخ النبوة كان قبل تلك المعركة بزمن وجيز.وقد وجد بين اللفائف أو الادراج التي اكتشفت في وادي قمران في عام 1947 نسخة لتفسير نبوات حبقوق يرجع تاريخ كتابتها إلى نفس الزمن الذي كتب فيه درج نبوات اشعياء الذي اكتشف أيضاً بين هذه اللفائف حوالي القرن الأول قبل الميلاد.'),
(1093, 'حَبل، حِبال', 'كلمة مستخدمة في العهد القديم عن ما ((يربط))، وعن ((ما ينسج)). ولا تدل في أية صيغة من الصيغتين على سمك أو قوة معينة (انظر  قضاة 15: 13 و2 صموئيل 8: 2 و 17: 13 و يشوع 2: 15). وفي العهد الجديد وردت كلمة حبل عن ((ما يصنع من سمار أو حلفاء)) وقد تعني الحبل الذي تربط به السفينة (اعمال 27: 32). أو الحبال الصغيرة التي تصلح كسوط (يوحنا 2: 15). وكانت الحبال عادة تصنع من الكتان، أو القنب أو الليف، لكنّ المصريين، وكذلك أيضاً العبرانيون كانوا يصنعون الحبال من سيور جلدية (انظر خيط ورباط وسفن وقوارب). وكان وضع الحبال على الأعناق علامة من علامات الشدة والضيق (1 ملوك 20: 31). وربما يشار بحبال الخطيئة (امثال 5: 22) إلى قوة العادة. كما يظن أن ((حبل الفضة)) (جامعة 12: 6) إنما يشير إلى النخاع الشوكي لتشابههما في الشكل واللون. وكذلك يقصد بالحبال في مزمور 16: 6 الحبل المستخدم لمسح أو لقياس الأراضي، وتخطيط تخومها، فالمراد ليس الحبل نفسه، بل الميراث الذي قيس به (انظر عاموس 7: 17 و زكريا 2: 1 و 2 و حزقيال 47: 3).'),
(1094, 'حَثَاث', 'اسم عبري معناه ((رعب)) ابن عثيئيل (1 اخبار 4: 13).'),
(1095, 'حِثّ، حِثّيين، أرض الحثّيين', 'ظل المستشرقون عدداً من السنين منشغلين بمركزين لإمبراطوريتين عظيمتين هما: وادي النيل ووادي الدجلة والفرات، ومن سنة 1871، عندما اكتشفت نقوش في كركميش تمثل أمامنا عنصر جديد إذ بدأت معرفتنا بالحثيين واكتشفت إمبراطورية شرقية عظيمة ثالثة، ازدهرت في آسيا الصغرى بين 1900 و 1200 ق.م. تقريباً. ولا يعرف على وجه التحقيق العنصر الذي ينتمي إليه الحثيون. فهناك جنس منهم يشاهد في آثار مصر له أنف كبير، ويظهر أن الأرمن الحديثين هم من سلالة هذه الأمة. وكان الحثيون قصاراً ممتلئي الجسم، ذوي شفاه غليظة. وأنوف كبيرة وجبهة مائلة للوراء، وتظهر هذه العلامات على آثار الحثيين. ولم يكن الحثيين ساميين. ويظهر من الآثار أنهم كانوا يرتدون ثياباً ثقيلة، فكانت تصل معاطفهم إلى الركبتين، وزي الرأس مرتفع مصنوع من الصوف. وكانوا يلبسون أحذية منحنية إلى فوق عند الأصابع، مما يبين أنهم جاءوا من جبال ثلجية.وتاريخياً ينبغي أن نميز تمييزاً دقيقاً بين الإمبراطورية الحثية، والولايات الحثية في شمال سوريا وجنوب شرق آسيا الصغرى. وبحسب علم الحفريات، تنطبق كلمة حثيين على بقايا ثقافة شهيرة فريدة موجودة في آسيا الصغرى، شمال سوريا وشمال العراق. وعلى الرغم من كل المشابهة بين آثار حثي آسيا الصغرى، وتلك التي في شمال العراق وشمال سوريا (بما في ذلك منطقة طورس)، فهناك بعض العوامل التي تبين انفصالاً عاماً بين الجماعتين. ويظهر أن ثقافة الحثيين الأناضوليين كانت متركزة في كبادوكية، التي تبين أوانيها الفخارية صلات نسب كثيرة بطروادة.وقد جاء الحثيون أي (الناسيون) إلى آسيا الصغرى في وقت مبكر، حوالي سنة 2500 ق.م. والاسم حثيين مشتق من حاتي (أي أناضوليا)، التي كانت عاصمتها حتّوشاش.ومن المتفق عليه الآن بوجه عام أن اللغة الحثية هي متصلة بكيفية ما باللغات الهندية الأوروبية (الآرية).والمفتاح الحقيقي للمشكلة الحثية اكتشفه هوجو فينكلر الألماني، الذي اكتشف 1906-1907 و 1911-1912 في بوغازي (موقع حتوشاش القديمة) نحو 10.000 لوحة طينية مجفّفة منقوشة بحروف آشورية اسفينية أو مسمارية. وهي تمثل عدداً من اللغات: السومرية، والاكادية، والحثية وغيرها. والنقوش على الآثار مكتوبة بالحروف الحثية الهيروغليفية.والكلمة حثي وحثيون (مفرد وجمع) وردت 47 مرة في العهد القديم، بينما وردت كلمة حث 14 مرة أخرى. وكثيراً ما يذكر الحثيون في قائمة الأمم الساكنة كنعان قبل دخول العبرانيين (قارن تكوين 15: 20 و خروج 3: 8 وتثنية 7: 1 و 20: 17 و يشوع 3: 10 و 11: 3 و 24: 11). وهم ذرية حث ثاني أبناء كنعان. وتقرأ عن ابراهيم أنه اشترى مغارة المكفيلة من عفرون الحثي (تكوين 23: 10-18). واتخذ عيسو امرأتين حثيتين (تكوين 26: 34)، وتزاوج العبرانيون فيما بعد مع الحثيين (قضاة 3: 5 و 6). وعندما خاطب حزقيال أورشليم الخائنة، قال في ص 16: 3: ((...مخرجك ومولدك من أرض كنعان. أبوك أموري وأمك حثية)) (انظر 16: 45). وكان لداود أصدقاء حثيون (1 صموئيل 26: 6). وتزوج بثشبع، امرأة اوريا الحثي (2 صموئيل 11: 2-27). وكان لسليمان نساء حثيات بين نسائه (1 ملوك 11: 1). وسخر سليمان الحثيين مع غيرهم من الشعوب في أعمال مملكته، (1 ملوك 9: 20-22 و 2 اخبار 8: 7-9).وقد اعتبر العبرانيون الحثيين شعباً قوياً معروفاً، فقد اعترفوا بأرض الحثيين (يشوع 1: 4). ويذكر ملوك الحثيين في جملة واحدة مع ملوك آرام (1 ملوك 10: 29 و 2 اخبار 1: 17). ويوضعون في مرتبة واحدة مع المصريين كدليل على عظمتهم (2 ملوك 7: 6).وكانت مملكة الحثيين في الأصل أرستقراطية إقطاعية، وفي أثناء المملكة القديمة أو الإمبراطورية الأولى (نحو 1900-1650 ق.م.) كان النبلاء يتوجون الملك بتخاناس (نحو 1900 ق.م.) في كوششارا أو كوساراس (جيور كاليسي الحديثة، جنوب غرب أنقرة، وغزا نيساس (نيسا) إلى جنوب نهر هاليس. وهزم ابنة انيتاس، كل آسيا الصغرى. وكانت نيساس عاصمته.ونقل مورشيليش الأول (1620) العاصمة إلى حتوشاش وهزم حلب. وفي غارة نهب بابل وضع نهاية لأسرة حمورابي الأمورية (نحو 1595).وفي سنة 1482 هزم تحتمس الثالث المصري الحثيين في مجدو، وكان عليهم أن يدفعوا له جزية. وقد توغل حتى حلب وعبر الفرات. وكانت جبال طوروس الحد الجنوبي للبلاد الحثية عندئذ.وامتد حكم المملكة الجديدة أو الإمبراطورية الثانية من نحو 1450 إلى نحو 1200 ق.م. لكنّ شوبيلوليوماش (نحو 1395-1355) أعاد فتح الأناضول وهزم الميتانيين وجعل شمال سوريا بين الفرات والبحر المتوسط إلى جبال لبنان مرة أخرى جزءاً من الإمبراطورية.وأثناء الأسرة التاسعة عشر اصطدم المصريون تحت حكم سيتي الأول مع الحثيين في سوريا. وخاض موراتاليش معركة قاديش مع رمسيس الثاني نحو 1295، حيث تم جلاء المصريين عن شمال سوريا.وحوالي سنة 1279 عقد حتوشيليش الثالث معاهدة مع رمسيس الثاني على شروط المساواة. وزار الملك الحثي مصر ليحتفل بزواج ابنته الكبرى برمسيس الثاني.وقد قاوم الأخائيون في اليونان الحكم الحثي في آسيا الصغرى. وحوالي سنة 1200 ق.م. سقطت الإمبراطورية الحثية في أيدي شعب ايجي وربما كان هؤلاء من التراقيين والفريجيين.وقد تجمع الحثيون عندئذ، حول كركميش وجعلوها عاصمة لهم، وهناك اتصلوا بالآشوريين. وأخيراً في سنة 717 ق.م. سقطت كركميش في أيدي سرجون الثاني. وهكذا ابتلعت آشور بالتدريج شمال سوريا وشرق آسيا الصغرى. وقد كان الحثيون حلقة الثقافة بيت\\ن وادي الدجلة والفرات وأوروبا. (انظر ((كنعان))).'),
(1096, 'حَثْلون', 'مكان على الحدود الشمالية لفلسطين، كما تنبأ حزقيال، قرب مدخل حماة (حزقيال 47: 15 و 48: 1 و انظر عدد 34: 8). وربما هي حيتلة، شمال شرقي طرابلس.'),
(1097, 'حَجابا أو حجابة', 'اسم آرامي معناه ((جرادة)) مؤسس عشية من النثينيم، رجع بنوه من بابل مع زربابل، وهو غير حاجاب (عزرا 2: 45 و نحميا 7: 48).'),
(1098, 'حَجَّي', 'اسم عبري معناه ((عيد، أي مولود في يوم عيد)).(1) ابن جاد، ومؤسس عشيرة (تكوين 46: 16 و عدد 26: 15).(2) نبي معاصر لزكريا (قارن حجي 1: 1 مع زكريا 1: 1). تنبأ بعد الرجوع من بابل. كان العمل في إعادة بناء الهيكل قد توقف لمدة 15 سنة، وكان حجي أداة كبرى في إنهاض الشعب للشروع في البناء (عزرا 5: 1 و 2 و 6: 14).سِفر حَجّي:هو العاشر في النبوات الصغيرة. وهو يتكون من أربع نبوات نطق بها في مدى أربعة شهور من السنة الثانية لداريوس هستاسبيس، 520 ق.م.(1) في اليوم الأول من الشهر السادس يوبخ النبي أولئك الذين تركوا الهيكل خراباً، وبنوا لأنفسهم بيوتاً مسقوفة، ويبين أن الله سوف لا يبارك أعمالهم الخاصة. وكنتيجة لهذا الإنذار، استؤنف العمل في الهيكل في اليوم الرابع والعشرين من الشهر نفسه (الإصحاح الأول).(2) في الشهر السابع، وفي اليوم 21، يشجع أولئك الذين يبكون على تواضع هذا البناء الجديد بالمقارنة مع بهاء البناء القديم. ويتنبأ بأن مجد هذا البيت الأخير سيكون أعظم من مجد الأول، لأن الله سيهز الأمم، وسيأتي مشتهى كل الأمم، فيعيد لشعبه فضتهم وذهبهم، ويملأ الله هذا البيت مجداً، وسيعطي الله السلام في هذا المكان (حجي 2: 1-9 وعبرانيين 12: 26-28).في الشهر التاسع، في اليوم 24، يضيف ملحقاً للنبوة الأولى، كما أن مسّ النجس للمقدس ينجسه، هكذا نسيانهم الأول لله دنس عملهم، فلم يمنح الله بركته. لكن غيرتهم التي انتعشت لأجل الله ستقترن بأوقات ناجحة من الرب (2: 10-19).(4) في اليوم نفسه يضيف ملحقاً للنبوة الثانية عندما يهز الرب الأمم، سيثبت زربابل، الذي يمثل نسل داود الملكي (2: 20-23).'),
(1099, 'حَجِّيث', 'اسم عبري مؤنث حجي ومعناه ((عيدية، أي مولودة في يوم عيد))، وهي إحدى نساء داود أدونيا (2 صموئيل 3: 4 و 1 ملوك 1: 5).'),
(1100, 'حَجَر', 'فلسطين بلاد حجرية، وكثيراً ما كان من الضروري تنقية الحقل من الحجارة تمهيداً لزرعه (اشعياء 5: 2). وكانت حقول العدو تشوه برمي الحجارة عليها، كما تسد بالأحجار (2 ملوك 3: 19 و 25). وكانت الأحجار تستخدم في أغراض متعددة: \n(1) لأجل الأرصفة والحواجز والمراسي والمواني، وأسوار المدن (نحميا 4: 3)، لأجل المساكن (لاويين 14: 45 و عاموس 5: 11)، والقصور (1 ملوك 7: 1 و 9)، والحصون ، والهياكل (1 ملوك 6: 7)، ولأجل تبليط الأفنية ولأجل الأعمدة (استير 1: 6)، وفي عصور الهيرودسيين استخدمت الحجارة لتعبيد الشوارع (انظر ((شارع)))، ولأجل مجاري المياه فوق القناطر، وخزانات الماء، والجسور، والجدران حول الكروم (أمثال 24: 30 و31). وفي بناء المذابح استخدم الإسرائيليون أحجاراً غير منحوتة (خروج 20: 25)، وكذلك في بناء الجدران العالية، وعندما كانوا يعملون أكواماً من الحجارة لإحياء ذكر حادث (تكوين 31: 46) أو لتمييز قبر مجرم مشهور (يشوع 7: 26 و 8: 29 و 2 صموئيل 18: 17)، ولا تزال هذه العادة منتشرة في سوريا وبلاد العرب، لكنها لا تقصر على قبور الأشرار. وكانت الأحجار تقطع وتنحت لعدة أغراض (1 ملوك 7: 9-11)، وأحياناً كانت تستخدم أحجار ذات حجم ضخم، كما كانت الحال في بناء أسوار الهيكل وأدعمة الميناء في قيصرية.(2) كانت تستخدم أحجار لغلق فم الأحواض والآبار، ومداخل القبور (تكوين 29: 2 و متى 27: 66 و يوحنا 11: 38). وتوضيح التخوم أو الحدود (تثنية 19: 14). وربما استخدمت كعلامات على الطريق (أرميا 31: 21). وفي العصور الرومانية كانت تقام الأميال الحجرية على الطرق العامة الرئيسية، كما على الطريق مثلاً بين صور وصيداء، وبين بيلا وجيراسا، حيث لا تزال هذه ترى هناك. وكانت تقام الأحجار لتمييز القبور ولإحياء ذكر الحوادث (تكوين 31: 45  و 35: 14 و 20 و 2 صموئيل 18: 18)، وكانت أحياناً تنقش عليها سجلات الأعمال (انظر ((حجر موآب))). وقد استخدمت الأحجار في حالتها الطبيعية كما وهي منحوتة على السواء، كأصنام (لاويين 26: 1 و تثنية 29: 17 و 2 ملوك 19: 18 و قارن اشعياء 57: 6). وهناك أحجار صغيرة معيّنة، تسمّى في اليونانية ((بيتولي)) و ((بيتوليا)). وأحياناً كانت هذه، إن لم تكن دائماً، نيازك، لذلك كانت تعتبر مقدّسة لأنها سقطت من السماء، وكثيراً ما حيكت الخرافات الوثنية على مثل هذه الأحجار (أعمال 19: 35). وقيل أنها تحمي من الشر. والاسم اليوناني الذي هو بلا شك من أصل سامي يشبه ((بيت إيل)). وقد يبين أن الحجر كان معتبراً كمسكن قوة فائقة الطبيعة، أو روح، أو إِله. وفي بعض الأحيان كرس العبرانيون حجراً مفرداً كتذكار لله (تكوين 28: 18-22 و 1 صموئيل 7: 12 و اشعياء 19: 19). وكانوا يعطون اسماً دينياً للمكان (تكوين 35: 7) أو حتى للحجر، تماماً كما كانوا أحياناً يسمّون المذبح باسم الله (تكوين 33: 20 و خروج 17: 15 و قارن تكوين 35: 7). لكن في مثل هذه الحالات لم يكونوا يعتبرون اللاهوت أو القوّة ساكنة في الحجر أو المذبح ولم يقدموا لها إكراماً إلهياً. بل عبدوا الله دون الحجر التذكاري (تكوين 31: 54 و 35: 1 و 7 و 1 صموئيل 7: 9).(3) كانت ترمي الأحجار بالمقلاع والمنجنيق (قضاة 20: 16 و 1 صموئيل 17: 40 و 2 اخبار 26: 15). وكانت وسيلة لإعدام المجرمين (انظر ((رَجْم))). وكانت تستخدم قطع من حجر الصوان لكي تقدح ناراً، كما كانت تصنع بشكل خشن لكي يستخدم كسكاكين (يشوع 5: 2). وكثيراً ما كانت تقطع من الأحجار أوزان للموازين (تثنية 25: 13 و انظر ((موازين)))، واستخدمت الألواح الحجرية لكتابة الوثائق (خروج 24: 12). وكانت تنحت الأواني من الأحجار لحفظ الماء (خروج 7: 19 و يوحنا 2: 6)، والموائد أيضاً (حزقيال 40: 42). وكان حجر مدور وزنه 30 رطلاً أو نحو ذلك، يدحرج ذهاباً وإياباً على الحنطة لكي لا يطحنها إلى دقيق، أو كانت تصنع مطحنة أو رحى للطحن من حجرين (تثنية 24: 6).يشير الحجر مجازياً إلى القساوة أو فقدان الشعور (1 صموئيل 25: 37 و حزقيال 36: 26)، والمتانة أو القوة (أيوب 6: 12 و 41: 24). واتباع المسيح هم ((حجارة حية مبنيون هيكلاً روحياً، وحجر الزاوية الرئيسية هو المسيح نفسه)) (افسس 2: 20-22 و 1 بطرس 2: 4-8).'),
(1101, 'حَجَر بوهن', 'انظر بوهن.'),
(1102, 'حَجَر الزاحفة', 'انظر الزاحفة.'),
(1103, 'حَجَر المعونة', 'حجر تذكاري نصبه صموئيل، كما يظهر قرب المصفاة، في مكان بين المصفاة والسن، حيث هزم الرب الفلسطينيين (1 صم 7: 10 و 12).'),
(1104, 'حَجَر الافتراق', '((حجر العزل)). المكان الذي اختبأ فيه داود إلى أن استطاع يوناثان أن يخبره بموقف شاول منه، وهناك افترقا (1 صموئيل 20: 19).'),
(1105, 'الحَجر الكبير', '(1 صم 6: 18) كان في حقل يهوشع البيتشمسي حيث وضع تابوت الرب بعد ما أرجعه الفلسطينيون إلى قرية يعاريم. وبعض المخطوطات تقول المرج الكبير.'),
(1106, 'حِجَارة كريمة', 'جميع الحجارة الكريمة المشار إليها في الأسفار القانونية، ما عدا ثلاثة، مذكورة في خروج 28: 17-20 و رؤيا 21: 11 و 19: 21، في النص والهامش والثلاثة الباقية هي الألماس (حزقيال 3: 9)، وعين المهر والعقيق (خروج 28: 19 و رؤيا 4: 3)، ومن هذه اثنان على الأقل هما فقط اسمان آخران لحجرين كريمين مما ذكر من قبل. والحجارة الكريمة المذكورة في الكتاب هي: الألماس، يشم، عين الهر، جمشت، جزع (أو زمرد سلقي)، زمرد (أو بهرمان)، بهرمان (أو زمرد)، عقيق أبيض، زبرجد، عقيق أخضر، بللّور، اسمانجوني (أو عين الهر) يشب، ياقوت أزرق، عين الهر، جزع، لؤلؤ، عقيق أحمر، ياقوت أزرق (أو اسمانجوني) جزع عقيق، ياقوت أصفر (انظر كل حجر كريم في بابه).'),
(1107, 'حَجَل', 'طائر بري، يسمى في العبرية قوري أي الصارخ أو المنادي، ويقتنص على جبال فلسطين (1 صموئيل 26: 20). وكان الحجل المحبوس في قفص يستخدم كطعم (سيراخ 11: 30). ويقارن أرميا محصّل الغني بغير حق، بالحجلة التي تحضن ما لم تبض، أو تجمع صغاراً لم تبضهم (أرميا 17: 11). ويوجد نوعان منه في فلسطين: حجل الصحراء، أو حجل هاي الرملي (Amoperdix heyi)، وهو النوع الوحيد في عين جدي، في البرية حيث كان داود عندما قارن نفسه بحجل مطارد (1 صموئيل 26: 20) والنوع الثاني هو حجل الشوكار واسمه باللاتينية Caccadis Chukar . ويعد لحم هذا الطائر من المآكل الفاخرة، ويطارده الناس إلى أن يكلّ من التعب و الطيران فيلتقطونه حينئذ بالأيادي. وعش الحجل المبني على الأرض معرض لأن يداس ويخرب، ولذلك كثيراً ما تطرد منه الحجلة بغتة. وحجل شوكار موجودة بكثرة في جميع أجراء فلسطين الجبلية. وله ريش ملوّن بألوان زاهية على طرفي جناحيه. وساقان ومنقار أحمر داكن، وعنق أسود قاتم وهو طائر كبير لطيف، من نوع الحجل اليوناني، لكنه أكبر منه، ويفوق حجل الشوكار الهندي في الحجم.'),
(1108, 'حُجْلَة', 'اسم عبري معناه ((حَجَلة)) إحدى بنات صلفحاد (عدد 26: 33 و 27: 1 و 36: 11 و يشوع 17: 3).'),
(1109, 'حَجِيَّا', 'اسم عبري معناه ((عيد يهوه)) لاوي من نسل مراري (1 اخبار 6: 30).'),
(1110, 'أحْجِيَة', 'تستخدم في الكتاب بمعنى لغز أي شيء غير واضح ويجب أن يكتشف بواسطة فكر ثاقب ويعرض فقط لكي يثير الالتفات ويحرّك البحث ولكي يجعل الحق أكثر وضوحاً وتأثيراً، ويطلب من مقدمه أن يعطي شرحاً مباشراً (حزقيال 17: 2-24). أو قد يطرح اللغز أما الناس ليكتشفوا معناه كلغز شمشون (قضاة 14: 12-19). وقد ورد هذا اللغز في قالب شعري، أما الغاز سليمان وحيرام فقد وردت في كتاب تاريخ يوسيفوس (كتاب 8 فصل 5 و فترة 3).'),
(1111, 'حَجيلَة', 'اسم عبري معناه ((مظلم وملتبس وكئيب)) تل في برية زيف (1 صموئيل 26: 1-3)، جنوب شرقي حبرون وإلى جنوب الصحراء، غير بعيد من معون (1 صموئيل 23: 19 و 24-26). أخفى داود نفسه هناك. وهناك نزل شاول فيما بعد عندما كان يطارده انظر ((تل مخيلة)).'),
(1112, 'حِدأة', 'طائر من الجوارح من فصيلة الباشق أو الباز أو الصقر، يسمّى علمياً الحدأة الملكية، وللحدأة جناحان طويلان مسننّان، ولها عادة ذيل طويل متشعب. والاسم ترجمة لثلاث كلمات في العبرية. والحدأة والباشق كلاهما نجسان حسب الشريعة (لاويين 11: 14 و تثنية 14: 13). والحدأة عدة أنواع. الحدأة السوداء (المهاجرة) (Milvus migrans) موجودة في وسط أوروبا، وشمال أفريقيا، وغرب آسيا. وتظهر فلسطين في شهر آذار، حيث تنزل بلا ضوضاء في الهواء، وتنطلق إلى أسفل لأجل طعامها، الذي يتكون من الرمم لأنها طائر جبان، لا تريد أن تزعج الماشية. وهي تتوالد على الأشجار، وتزين عشّها بخرق من ألوان مختلفة. والحدأة السوداء الجناحين (Elanus cseruleus) توجد أيضاً في فلسطين، لكنها نادرة. انظر ((نسر وباشق))).'),
(1113, 'حَدَاجَة', '(هودج) رحل الجمل الذي تركب فيه النساء وفي الحداجة أخفت راحيل أصنام أبيها (تكوين 31: 34).'),
(1114, 'حَدَار', '(تكوين 25: 15) انظر ((حدد)).'),
(1115, 'حَدَاشَة', 'اسم عبري معناه ((حديثة)) قرية في سهل يهوذا (يشوع 15: 37). وربما يكون موقعها في خربة الحُدَيدة بين عراق المنشية وخربة عجلان.'),
(1116, 'حَدَتَّة', 'اسم عبري معناه ((حديث)) (انظر ((حاصور حدتة))).'),
(1117, 'حَدَد', 'اسم عبري معناه ((حدّة وشراسة))، ابن اسماعيل (1 اخبار 1: 30) ويدعى أيضاً حدار (تكوين 25: 15).'),
(1118, 'حِدّاقَل', 'اسم عبري من أصل سومري وهو نهر دجلة (تكوين 2: 14 و دانيال 10: 4). وينابيعه الرئيسية في وسط ارمينيا حيث تنبع من المنحدر الجنوبي للجبال المقابلة لجبال طورس. والنبع الغربي يجري بجوار دياربكر، متعرجاً لمسافة تزيد على 150 ميلاً. والنبعان الشرقيان المعروفان بـ ((بيتليس تشاي)) و ((بهتان تشاي))،ينبعان جنوب بحيرة فان، وطولهما نحو 100 ميل. وبعد ملتقى هذه الجداول يتجه النهر إلى الشرق للجنوب الشرقي تقريباً، خلال جبال كردستان، وتصب فيه أنهر متعددة، نخصّ منها الزاب الأكبر والأصغر والديالة، وأخيراً يلتقي بالفرات. وقديماً كان يصب في الخليج الفارسي. ويمر في جريانه بخرائب نينوى، التي تقوم على الضفة اليسرى أو الشرقية، تقريباً مقابل الموصل على ضفته اليمنى. ثم بعد ذلك يقسم النهر بغداد إلى قسمين، ومن بعد ذلك أيضاً يمر بالخرائب التالية: أولاً خرائب استاسيفون أو المدائن، عاصمة البرثيين، ثم خرائب سلوقية عاصمة الدولة اليونانية، وطول مجرى الدجلة إلى ملتقاه مع الفرات عند شط العرب هو 1146 ميلاً أي أكثر قليلاً من نصف طول النهر الشقيق. أما النهر المتحد فطوله 120 ميلاً.'),
(1119, 'حَديد', '(معدن معروف) توبال قايين، من نسل قايين، اشتغل في النحاس والحديد (تكوين  4: 22). وفي العصر الموسوي المبكر كانت هناك فؤوس وأدوات أخرى من الحديد (عدد 35: 16 و تثنية 19: 5). كان سرير عوج من الحديد (تثنية 3: 11). وفي زمن يشوع كانت الأواني تصنع من هذا المعدن (يشوع 6: 19 و 24)، وكانت تستخدم المركبات الحديدية في الأغراض الحربية (يشوع 17: 16)، واستمر ذلك طوال عصر القضاة، وإلى أزمنة متأخرة (قضاة 1: 19 و 4: 3 و 13).ومن الحديد كانت تصنع الدروع والأسلحة، كأسنة الرماح والتروس (1 صموئيل 17: 7 و رؤيا 9: 9)، والآلات الزراعية كالفؤوس والنوارج (2 صموئيل 12: 13 و عاموس 1: 3)، وأدوات البنائين والمسامير (1 ملوك 6: 7 و 1 اخبار 22: 3)، وأدوات النقر أو الحفر (أيوب 19: 24 و ارميا 17: 1)، والشصوص الشائكة لصيد السمك (أيوب 41: 7)، والأبواب والقضبان والقيود (مزمور 105: 18 و 107: 10 و 16 و 149: 8 و اشعياء 45: 2 و أعمال 12: 10)، والأصنام (دانيال 5: 4). وكان يستورد الحديد من ترشيش، واليونان، والشمال، وبلا شك من بلدان مجاورة للبحر الأسود (ارميا 15: 12 و حزقيال 27: 12 و 19). وكان يمكن الحصول عليه في فلسطين (تثنية 8: 9)، لكونه متوفراً في جبال لبنان. وقد حدث أول صهر الحديد نحو 1400 ق.م. وربما كان ذلك في هضاب آسيا الصغرى الحثية. كانت خاماته تصهر في أفران (تثنية 4: 20 و 1 ملوك 8: 51)، وكانت مبنية من حجارة، نحو 10 أقدام في الارتفاع، وأما قطرها فطوله 3 أقدام ويوجد ما يشبه هذا في لبنان في الوقت الحاضر. وكان يستخدم الفحم فيها، وتنفخ النار بمنفاخ (حزقيال 22: 20 و قارن ارميا 6: 29). ويظهر أن الفلسطينيين قد تعلّموا استخدام الحديد في الشمال وحافظوا بتدقيق على أساليب صنعه (1 صموئيل 13: 19-22). وانتشر استخدام هذا المعدن بين العبرانيين بعد ما هزم شاول وداود الفلسطينيين وكسرا قوتهم والحّداد هو الصانع الذي يصنع الحديد آلات وأسلحة (1 صموئيل 13: 19 و اشعياء 44: 12 و 54: 16)، مثل توبال قايين. ويستخدم الحداد كور الفحم، والمنفاخ، والملقط، والمطقة، والسندان انظر ((عصيون)) ((جابر)) ((منفاخ)).'),
(1120, 'أرض حِدراخ', 'مقاطعة في سوريا بالقرب من دمشق (زك 9: 1).'),
(1121, 'حَدشى', '(انظر ((تحتيم))).'),
(1122, 'حِدْلاي', 'اسم عبري معناه ((منتهى))، وهو رجل من افرايم (2 اخبار 28: 12).'),
(1123, 'حَرَادَة', 'اسم عبري معناه ((رعب وخوف))، وعي محلة للعبرانيين في البرية (عدد 33: 24 و 25). وربما كانت جبل حرادة في وادي العين التي تبعد مسيرة يوم عن عين حضيرة.'),
(1124, 'حَرْب', 'قبل الاشتباك في حرب عدوانية، استشار العبرانيون الله ليعرفوا أرادته في الأمر (قضاة 20: 23 و 27 و 28 و 1 صموئيل 14: 37 و 23: 2 و 1 ملوك 22: 6). أو عندما لم يكن ممكناً تجنب القتال، كانوا يطلبون معرفة معونة الله بالصلاة وأحياناً بالذبائح (1 صموئيل 7: 8 و 9 و 13: 12 و 2 اخبار 20: 5-12). وكان الوثنيون يلجأون إلى العرافة للأغراض نفسها (حزقيال 21: 12)، وكانوا يهتمون بأن يبدأوا الحرب في يوم حسن الطالع حسب زعمهم. وعادة كان الجواسيس يرسلون إلى الأمام للحصول على معلومات خاصة بالبلاد، ولأخذ الاستعداد للمقاومة قبل دخول بلاد معاوية أو الاشتباك في معركة (عدد 13: 7 و يشوع 2: 1 و قضاة 7: 9-11 و 1 صموئيل 26: 4). وعندما كان الأسرى يؤخذون كانوا يستجوبون للغرض نفسه (قضاة 8: 14 و 1 صموئيل 30: 11-15). وعندما كان العدو يقترب جداً إلى المعركة، كان الكاهن أو القائد يشجع الشعب بأن يذكرهم بحضور الله ومعونته، وكان الضباط يعفون من الخدمة الخائفين والذين بنوا بيوتاً ولم يسكنوها، أو غرسوا كرماً ولم يتمتعوا بثمره، أو خطبوا نساء ولم يتزوجوهن بعد (تثنية 20: 2-9 و 2 اخبار 14: 20). وكانت تستخدم خطط حربية متعددة، مثل المباغتة، والكمين، والتظاهر بالفرار، والمخادعة (تكوين 14: 15 و يشوع 8: 2-7 و قضاة 7: 16-22 و 2 صموئيل 5: 23). وأحيانا عندما كانت الجيوش المتعادية تصطف للمعركة، كان يقع الاختيار على بطل من كل معسكر (1 صموئيل 17). وفيما عدا ذلك كان يلتحم القتال. وكان البوق يضرب للهجوم، وكان نفخ البوق إشارة للزحف واستغاثة بالله (عدد 10: 9 و يشوع 6: 5 و قضاة 7: 20 و 2 اخبار 13: 12). وكان الجيش يهجم إلى الأمام بهتاف (يشوع 6: 5 و 1 صموئيل 17: 52 و ارميا 50: 42 و حزقيال 21: 22 عاموس 1: 14)، ثم يشتبك الجنود في القتال يداً ليد. وكانت المطاردة دموية. وكان العبرانيون عندما ينتصرون، ينهبون معسكر العدو، ويسلبون الموتى، مثلهم في ذلك مثل غيرهم من الأمم في عصرهم (قضاة 8: 24-26 و 1 صموئيل 31: 9 و 2 اخبار 20: 25)، وأحياناً كانوا يقتلون أو يشوّهون الأسرى (يشوع 8: 23 و 29 و 10: 22-27 و قضاة 1: 6 و 8: 21 و 2 صموئيل 8: 2)، وفي أكثر الأحيان كانوا يصيّرونهم عبيداً.وعندما كانت تحاصر مدينة ما، كان المحاصرون يحصنّون معسكرهم ضد الهجوم، وإذا أمكن، فإنهم كانوا يقطعون الماء عن المدينة. ولكي يدفعوا آلاتهم الحربية إلى العمل كانوا يقيمون المتاريس- أي كوم التراب في اتجاه المدينة (2 صموئيل 20: 15 و حزقيال 4: 2). وكان المتراس يزداد في الارتفاع بالتدريج حتى كان أحياناً يصل إلى نصف ارتفاع سور المدينة. فوق هذا المستوى المائل كان يسير الكبش، وهو آلة حربية لهدم الأسوار، حتى يصل إلى موقع مناسب، ثم من أعلاه ومن المتراس، كان رماة النبال والمسلحون بالمقاليع يطلقون قذائفهم. وكانت توضع سلالم التسلق على قمة المتراس للتسلق على السور. وأحياناً كان يوضع الوقود على الأبواب وتشعل فيه النار لكي تحترق ثم تفتح ثغرة للدخول للمدينة (قضاة 9: 52)، وأحياناً ما كان رماة النبال يهاجمون المدافعين عن السور، وكان هؤلاء الرماة يقفون عند قاعدة السور، وليس على المتراس. وكان المحاصرون يستعدون للمحاصرة بحماية مصادر مياههم، وترميم تحصيناتهم وتقويتها (2 اخبار 32: 3-5). وكانوا يزعجون العدو ويحاولون أن يضطرونه للخروج لمهاجمتهم. وكانوا يصدون الهجوم ويقرقون المحاصرين في عملياتهم العدوانية بإطلاق الرماح والحجارة ورميهم بالسهام من الأسوار، وكانوا يتلفون أو يحاولون أن يتلفوا الآلات الحربية بواسطة رمي شعلات محترقة عليها وبواسطة تقويض الأكوام التي كانت تقوم عليها الكباش (2 صموئيل 11: 21 و 24 و 2 اخبار 26: 15). وكثيراً ما كانت المدن المسبية تخرَّب ويذبح سكانها، ولا يستبقى منها أحد لا بالنسبة لسنه ولا بالنسبة لجنسه (يشوع 6: 21 و 24 و 8: 24-29 و 10: 22-27 و 2 ملوك 15: 16). وكان يُحتَفَل بالنصر بالغناء والرقص (خروج 15: 1-21 و قضاة مل و 1 صموئيل 18: 6 و 2 اخبار 20: 26-28). (انظر كلمة جيش).'),
(1125, 'مِحْراب', 'مؤخر الهيكل أو قدس الأقداس. هذه الكلمة ترجمة للكلمة العبرية ((دبير)) التي تعني مؤخر وقد وردت هذه الكلمة في الترجمة العربية في 1 ملو 6: 5 و 19 و 8: 6 و مز 28: 2 ومن دراسة هذه الشواهد يتضح أنها استعملت كناية عن قدس الأقداس أو الهيكل كله (انظر كلمة هيكل).'),
(1126, 'حِرْباء', 'نوع من الزواحف كالوزع ((تنشمث)) (لاويين 11: 30). ورئتا الحرباء كبيرتان جدّاً، وعندما تتمدّدان تجعلانها شبه شفافة، وعيناها بارزتان من رأسها، وهما مستقلتان في حركتهما، حتى أنها تقدر أن تحول إحدى عينيها إلى أعلى والأخرى إلى أسفل، أو تنظر في اتجاهات مختلفة إلى أشياء متنوعة في وقت واحد. ولها أيضاً قدرة على تغيير لونها طبقاًَ للون الأشياء التي حولها أو تبعاً لمزاجها عندما تنزعج. وهذه القوة ترجع إلى وجود خلايا صافية أو حاملة لمادة ملوّنة في الجلد، يضبط الجهاز العصبي انقباضها وانبساطها. والحرباء تعيش في الأشجار. وأقدامها تصلح للاستخدام كالأيدي، وذيلها طويل يصلح للقبض، حتى أنها تقدر أن تتعلق بالأغصان. وهي تقتات على الحشرات التي تقتنصها بواسطة لسانها الطويل المغطى في طرفه بمادة لزجة. (انظر حرذون، عظاية، وزغة، ورل).'),
(1127, 'حَرْبونا', 'اسم فارسي معناه ((خرب، اصلع، اجرد، سائق حمار)). وهو خصي وساقي احشويرش (استير 1: 1 و 7: 9).'),
(1128, 'حَرْث', 'انظر فلاحة.'),
(1129, 'مَحْرَاث', 'المحراث في فلسطين بدائي. ويتكون من قائمة خشبية أو غصن شجرة، يعلّق النير في أحد طرفيها، بينما يبرز من الطرف الآخر غصن أو ترشق فيه دعامة مغلقة بصفيحة رقيقة بصفيحة رقيقة من الحديد تشكل سكة المحراث (اشعياء 2: 4). وفي العصر البرونزي كانت سكة المحراث من خشب, ومن المحتمل أن الفلسطينيين هم الذين أدخلوا سكة المحراث الحديدية (1 صموئيل 13: 20). ويجر المحراث بواسطة ثيران أو أبقار، ويوجه باليد (أيوب 1: 14 و لوقا 9: 62). ومثل هذه الأداة لا تقدر أن تفعل أكثر من أن تخدش سطح الأرض. عندما كان اليشع يحرث باثني عشر فداناً من الثيران ربما كان هناك اثنا عشر محراثاً، كل محراث منها بزوج ثيران ورجله، وكان اليشع آخر الاثني عشر (1 ملوك 19: 19 و 20). (انظر نير).'),
(1130, 'حَرْاجوان', 'من فصيلة الجراد (لاويين 11: 22).'),
(1131, 'حَرْحَس', '((لمعان بهاء)) جد شلّوم زوج خلدة لنيبة (2 ملوك 22: 12). دعي حسرة في 2 اخبار 34: 22.'),
(1132, 'حَرْحُور', 'اسم عبري معناه ((حرارة شديدة، حمو اشتعال)). مؤسس عشيرة من النثينيم، رجع بعضهم من بابل مع زربابل (عزرا 2: 51 و نحميا 7: 53).'),
(1133, 'حِرْذَون', 'حيوان نجس حسب الشريعة، من الزواحف والاسم ترجمة عبرية ((أناقة)) (لاويين 11: 30). والحرذون هو عظاية الحائط، له بقع بيضاء على ظهره، يخرج منه أنين حزين. والحرذون العادي أو المروحي القدم كثير جداً في فلسطين. وهو مألوف في البيوت، ويجري فوق جدرانها وسقوفها. وهو يقدر على القيام هذا بسبب التركيب العجيب لأصابعه المجهزة بأطباق يتكون تحتها فراغ عندما يمشي الحيوان وهكذا يلتصق هذا الحيوان بالجدران والسقوف بطريقة ماصة، ويظن بعضهم أن الكلمة في الأصل تعني ((البرص)). ولذلك يدعوه أهل البلاد ((أبو بريص)).'),
(1134, 'حُرّان', '(حزقيال 27: 23) هذا هو الاسم كما ورد في الترجمة العربية التي بين أيدينا أما الاسم في العبرية فهو حاران (انظر حاران).'),
(1135, 'حَرير', 'خيط دقيق، ناعم تنتجه أنواع متعدّدة من دود القز، والحرير قماش منسوج من خيط الحرير. وقد وصل الحرير إلى أسواق الغرب حالاً بعد غزوات الاسكندر الأكبر. وكان معروفاً عند اليونانيين بالسيريكون، نسبة إلى السيريين، الذين يعرفون عامة بالصينيين. وكان الحرير سلعة تجارية مختارة (رؤيا 8: 12). وهو يليق لثياب الأباطرة الرومان والملوك. وفي حكم الامبراطور اوريليان، 270-275 م، كانت السلع الحريرية الخالصة تباع بوزنها ذهباً. والملابس الجميلة المشار إليها في حزقيال 16: 10 و 13 بالبز ربما كانت من الحرير كما فهم المفسّرون الربيون، وكما ترجمت في اللغة الانكليزية.'),
(1136, 'حَرَس حَارس', '(نشيد 5: 7 و اشعياء 21: 11) كان يطلب من الحراس في بلاد فارس التعويض هما سُلب من الناس في الشوارع، ولذلك كانوا متيقظين جداً ليحذروا سكان المدينة ويحموهم من البطش والغدر (حزقيال 33: 2-9). وكان يطلب منهم أيضاً أن ينادوا في أوقات الليل متجولين في الأزقة. ولم تزل هذه العادة جارية في بعض المدن الكبيرة إلى هذا اليوم. وفي وقت خطر كان الحراس يقفون في أبراج فوق أبواب المدينة (اشعياء 21: 8 و 62: 6).'),
(1137, 'حَرَش', 'اسم عبري معناه ((أبكم، أصم)) رأس عشيرة لاوية، ملحق بموظفي الخيمة 445 ق.م. (1 اخبار 9: 15).'),
(1138, 'حَرْشا', 'اسم عبري معناه ((أبكم، أصم)) مؤسس عشيرة من النثينيم، رجع بعضهم إلى بابل مع زربابل (عزرا 2: 52 و نحميا 7: 54).'),
(1139, 'حُرْمَة', 'اسم عبري معناه ((موضع مقدس، خراب)) وقد دعيت مدينة صفاة، بعد خرابها. كانت تقع في البلاد الجنوبية نحو أقدام ادوم، قرب صقلع، حين تجاسر العبرانيون بعدما فقدوا أيمانهم، أن يتقدموا من قادش نحو كنعان. نزل العماليقيون والكنعانيون، وضربوهم وطاردوهم حتى إلى حرمة، كما دعيت المدينة بعد خرابها بنحو 38 سنة بعد ذلك (عدد 14: 45 و تثنية 1: 44). وفي الارتحال الثاني للعبرانيين من قادش بعد انقضاء هذه الـ 38 سنة، بينما كانوا معسكرين في جبل حور، عاد الكنعانيون تحت ملك عراد، وهجموا عليهم، وأخذوا بعض الأسرى، فنذر بنو اسرائيل بإهلاكهم لو ساعدهم يهوه، ودمّروا مدينتهم، ودعوا المنطقة المخربة حرمة، أي خراباً (عدد 21: 1-3).وكانت حرمة من نصيب يهوذا، لكنها نقلت فيما بعد إلى شمعون (يشوع 15: 30 و 19: 4). وبعد موت يشوع، ساعد يهوذا شمعون على أن يأخذ المدينة، وكان يسكنها الكنعانيون، وأما أنها نجت من الخراب عندما دُمِرّت المنطقة أولاً فذاك إتماما للعهد (عدد 21: 2). وقد أفرزت المدينة فيما بعد للخراب فذبح فيها الإنسان والحيوان، ودعيت المدينة منذئذٍ حرمة (قضاة 1: 17). وكان يشوع قد هزم ملكها من قبل. وربما كان غائباً عن مدينته عند وقوع المعركة، لأنه كان يساعد قوماً من بني جنسه في حبرون عندما حدثت هزيمة قومه (يشوع 12: 14). وبعدما أخربت المدينة سكنها الشمعونيون (1 اخبار 4: 30). وكانت كريمة على داود عندما كان طريداً، وأرسل داود إلى أصدقائه هناك جزءاً من غنائم صقلع (1 صموئيل 30: 30). وربما كان موقعها في تل السبع (ويدعى أيضاً تل المشاش) نحو 3 أميال شرق بئر سبع.'),
(1140, 'حَرْمون', 'اسم عبري معناه ((جبل مقدّس)) جبل دعاه الصيدونيون سريون أي ((المتلالئ)) والأموريون سنير أو شنير (تثنية 3: 8 و 9 و نشيد 4: 8). وكان اسم آخر عند العبرانيين وهو سيئون (تثنية 4: 48). وكان هو الحد الشمالي الشرقي لغزوات العبرانيين بقيادة موسى ويشوع (تثنية 3: 8 و 9 و يشوع 11: 3 و 17 و 12: 1 و 13: 5 و 11 و 1 اخبار 5: 23). ويذكر في الكتاب المقدس في الشعر العبري مقروناً مع تابور (مزمور 89: 12)، وصهيون (مزمور 133: 3)، ولبنان (نشيد 4: 8)، وهو يعلو عليها جميعاً. ويكوّن الطرف الشرقي لسلسلة جبل لبنان الشرقي، ويعلو إلى ارتفاع 166 قدماً فوق البحر. ويمكن أن يشاهد من أجزاء كثيرة في فلسطين. والمنظر من القمة بهي بديع، ومنه يقدر الإنسان أن يرى لبنان، والسهل حول دمشق، وصور، والكرمل، وجبال الجليل الأعلى، وسهول الجليل الأدنى، وبحيرة حولة وبحر الجليل، ولذروته 3 قممم، والقمة الواقعة إلى الجنوب الشرقي أعلاها جميعاً. وإذا له هذه القمم يوصف يوصف بأنه جبال حرمون (مزمور 42: 6)، لكن الكلمة في هذا النص يمكن أن تشير إلى سلسلة حرمون عامة. وذروة الجبل مغطاة بالثلج على مدار السنة. والمنبع الرئيسي للأردن في جبل حرمون. ويقال أن تجلّي الرب يسوع حدث فوقه، لكن هذا موضوع جدال. ويدعى حرمون الآن ((جبل الشيخ)).'),
(1141, 'حَرْمونِيْون', 'سكان جبل حرمون (مزمور 42: 6).'),
(1142, 'حَرَنفَر', 'ربما كان اسم مصري معناه ((هورس صالح)) اشيري ابن صوفح (1 اخبار 7: 36).'),
(1143, 'حَرياهايا', 'اسم عبري معناه ((يهوه حامي)) أبو الصائغ عزّليئيل الذي رمم جزءاً من سور أورشليم بعد ما رجع نحميا من شوشن 445 ق.م. (نحميا 3: 8).'),
(1144, 'حَرُود', 'اسم عبري معناه ((ارتعاد، رعب)) وهو ينبوع بالقرب منه نصب جدعون خيامه بينما كان اعداؤه المديانيون عند تل موره، في الوادي (قضاة 7: 1). ويعتقد البعض أنها عين جالود على الجانب الشمالي الغربي من جبل جلبوع، نحو ميل شرقاً للجنوب من يزرعيل وبالقرب من بيسان.'),
(1145, 'حَرُودي', 'أحد سكان مدينة حرود (2 صموئيل 23: 25)، ربما هي خربة خريدان نحو ثلاثة أميال ونصف ميل جنوب شرقي أورشليم.'),
(1146, 'حَرُوشة الأمم', 'اسم عبري معناه ((نحت الأمم)). وهي مدينة سكنتها أجناس مختلفة من الأمم. أقام فيها سيسرا (قضاة 4: 2 و 13 و 16). وهي تل عمار تحت الحارثية، وفي موقعها قرية صغيرة على الضفة الشمالية لقيشون، عند النقطة التي يمر فيها المجرى من مضيق ليدخل إلى سهل عكا، وهي على بعد 16 ميلاً إلى الشمال الغربي من مجدو.'),
(1147, 'حَرًوفي', 'لقب شفطيا القورحي الذي انضم إلى داود في صقلع (1 اخبار 12: 5). أما قراءة بعض النسخ فهي خريفي.'),
(1148, 'حَرُوماف', 'اسم عبري معناه ((اشرم الأنف)) أبو يدايا الذي عمل في ترميم سور أورشليم (نحميا 3: 10).'),
(1149, 'حَريِم', 'اسم عبري معناه ((مخروم الأنف، مكرّس، لا ينتهك)) وهو اسم:(1) أحد رؤساء الشعب الذين خيموا العهد (نحميا 10: 27).(2) رئيس فرقة من الكهنة (نحميا 12: 15).وقد دعي في غير هذا الموضع باسم حاريم (اطلب حاريم).'),
(1150, 'حَزَائِيل', 'اسم آرامي معناه ((قد رأى الله)) وهو آرامي من البلاط الملكي أمر الرب إيليا بأن يمسحه ملكاً على آرام (1 ملوك 19: 15). وبعد بضعة سنوات أي بين 845 و 843 ق.م. سمع بنهدد الذي ملك حينئذٍ على البلاد أن اليشع كان في دمشق، فأرسل إليه حزائيل ليسأل النبي عما إذا كان سيشفى من مرضه الخطير. فأخبر اليشع حزائيل أن سيده لن يشفى وإنه هو نفسه ملك آرام وسيرتكب فظائع مخيفة في شعب إسرائيل. وعندما رجع حزائيل إلى بنهدد، أخبره بأن النبي تنبأ بأنه سيشفى، وفي اليوم الثاني قتله وملك عوضاً عنه (2 ملوك 8: 7-15). وفي 843 حارب الملك شلمناصر الآشوري حزائيل وفرض عليه جزية. وفي 838 حاربه شلمناصر مرة أخرى. ونحو ختام ملك ياهو على إسرائيل، ضرب حزائيل أرض العبرانيين شرق الأردن (2 ملوك 10: 32)، وفي حكم الملك التالي عبر النهر، وأذل إسرائيل إذلالاً شديداً (ص 13: 4-7)، وغزا أرض الفلسطينيين، وأخذ جت، وأعاقته فقط عن مهاجمة أورشليم هدية ثمينة مكوّنة من كنوز الهيكل المكرّسة (ص 12: 17 و 18). وبيت حزائيل (عاموس 1: 4) في دمشق.'),
(1151, 'حَزَايا', 'اسم عبري معناه ((قد رأى يهوه)). وهو رجل من يهوذا، من عشيرة شيلة (نحميا 11: 5).'),
(1152, 'حَزْقِي', 'اسم عبري معناه ((قوتي، أو اختصار حزقيا)) وهو بنياميني، ابن الفَعَلَ (1 اخبار 8: 17).'),
(1153, 'حَزْقيَِال', 'اسم عبري معناه ((الله يقوي)). وهو أحد أبناء الأنبياء الكبار، ابن بوزي، ومن عشيرة كهنوتية (حزقيال 1: 3). ولد وكبر ونشأ في فلسطين، وربما في أورشليم في بيئة الهيكل، أثناء خدمة النبي أرميا. ثم حُمِل مسبياً من يهوذا مع يهوياكين (579 ق.م.) ثماني سنوات بعد نفي دانيال (حز 33: 21 و 40: 1 و قارنه مع 2 ملوك 24: 11-16). وكان شاباً في ذلك الوقت، ولم يكن طفلاً لكنه كان تحت السن عندما نهض اللاويون بواجباتهم وحسبوا في الإحصاء كرجال. وعاش مع المسبيين اليهود على نهر خابور أو ((كبار)) وهو قناة في أرض بابل، وربما كانت في تل أبيب (حزقيال 1: 1 و 3 و 3: 15)، تزوّج ربما مبكراً في السنة السادسة، أو على الأكثر في السنة التاسعة من السبي، وكان له بيت (ص 8: 1 و 24: 1 و 18).بدأت خدمته النبوية في السنة الخامسة لسبي يهوياكين، أي بزمن مدته 7 سنين قبل خراب الهيكل في أورشليم، بينما كان ساكناً على ضفاف الخيبر (ص 1: 1 و 2). وكان في السنة الثلاثين من عمره وهي السن التي فيها يدخل اللاويون الخدمة (عدد 4: 3). والنظرية أن السنة الثلاثين لا تشير إلى سن حزقيال، لكنها تاريخ محسوب أما من اعتلاء نبوخذ نصر، العرش، أو من اصلاحات يوشيا، تسقط بالنظر إلى ارميا 25: 1 و 3 و 2 ملوك 23: 36 و 25: 2-6 و حزقيال 1: 2. ومع أنه كان مسبياً في أرض غريبة. إلاّ أنه كانت له الحرية أن ينطق بنبواته. وكان يرجع إليه شيوخ الشعب لأجل النصيحة (ص 8: 1 و 14: 1 و 20: 1). لكن كلماته لم تتبع بأمانة (33: 30-33). وواضح من المشابهات في الفكر واللغة أنه كان على دراية تامة بتعليم ارميا. فهو يتناول ملاحظات ارميا التعليمية، أو استعارته الإيضاحية، أو خطاباته القصيرة، ويوضحها ويوسعها، وكثيراً ما يعطيها صيغة أدبية نهائية: مثل القدر (ارميا 1: 13-15 و حزقيال 11: 2-11 و 24: 3-14)، والأختين (ارميا 3: 6-11 و حزقيال 23)، والغفران للمذنبين عندما يتوبون (الأمة، ارميا 18: 5-12 و الفرد، حزقيال 18: 21-32)، والرعاة يستعاض عنهم بالملك الدادوي (ارميا 23: 1-6 و حزقيال 34: 1-24)، المسئولية الفردية بالنظر إلى المثل عن الآباء الذين يأكلون الحصرم (ارميا 31: 29 و 30 و حزقيال 18: 2-31)، الخليقة الروحية الجديدة (ارميا 31: 33 و 34 و حزقيال 11: 19 و 20 و 36: 25-29). وللمسببين من دون اليهود الذين بقوا مقيمين، رجاء رجاء المستقبل (ارميا 24 و حزقيال 111: 15-21 و 37: 1-14). وقد امتد نشاط حزقيال النبوي فترة تزيد عن 22 سنة على الأقل (قارن ص 1: 2 مع 29: 17). و لا يعرف وقت موته ولا الطريقة التي مات بها.وسفر حزقيال يقوم في الكتاب العربي بين مراثي ارميا ودانيال. وبما أن هذين السفرين موضوعان بين الكتابات في الأسفار العبرية القانونية، فإن مكان حزقيال في الكتب المقدسة العبرية يأتي مباشرة بعد ارميا. والنبوات مرتبة بنظامها التاريخي تقريباً، وهي مؤرخة بحسب سني سبي يهوياكين التي ألقيت فيها. والسفر ينقسم إلى ثلاثة أقسام:أولاً- نبوات ألقيت قيل غزو أورشليم، وهي تنبئ عن سقوطها بسبب خطاياها. في السنة الخامسة يدعى الكاهن للوظيفة النبوية ويعد لعمله بواسطة رؤيا (1: 1-3: 21)، ثم يؤمر بأن يتنبأ، بواسطة أفعال رمزية وتفسيرها، عن خراب المدينة (3: 22-7: 27). في السنة السادسة إنذارات ليهوذا بسبب وثنيتها (ص 8)، ارتحال يهوه الرمزي من الهيكل بسبب تدنيسه (9: 1-11:13)، تعزية، فإِن المسببين ما زالوا شعب الله، وهو سيكون مقدساً لهم (آية 16)، ويردهم إلى أرض آبائهم (آية 17)، ويعطي الذين يصلحون حياتهم قلباً جديداً (الآيات 18-21). وعدم الإيمان والالتصاق بالأنبياء الكذبة هما سبب ترك يهوه مدينته (ص 12-14)، وإن خراب أورشليم مؤكد (ص 15-17)، لكن التائبين سيتمتعون برضى الله (ص 18). رثاء على رؤساء إسرائيل (ص 19). في السنة السابعة، يتنبأ بأن يهوه سيعاقب الشعب لأن اسمه قد تدنس في نظر الأمم. لكنه سيردهم فيما بعد لأجل اسمه (20: 1-44)، وقوع القضاء مؤكد، وقد ذكرت التعديات أمام الله (20: 45-23: 49). في السنة التاسعة، يرمز إل حصار أورشليم وتشتت الشعب بقدر (ص24).ثانياً- نبوات الحكم على الأمم: في السنة التاسعة، على امون وموآب وادوم وفلسطين (ص 25) في السنة الحادية عشرة، علة صور وصيداء (ص 26-28). وفي السنوات العاشرة، والسابعة والعشرين، والحادية عشرة، على مصر (ص 29-32).ثالثاً- نبوات متعلقة بالرجوع من السبي: ألقيت بعد غزو نبوخذ نصر لأورشليم وخرابها. في السنة الثانية عشرة، في المساء قبلما وصلت أخبار سقوط المدينة إلى النبي، كانت يد الرب عليه، وعندما تلقى النبي هذه الأخبار بدأ نشاطه من جديد (33: 1-22). ويتعلم أن الشعب بعد الدينونة سيدرك أن يهوه هو الله، وأن نبياً صادقاً كان بينهم (33: 23-33)، وسيقوم راعٍ صالح هو داود (ص 34)، وسيعاقب أعداءهم (ص 35)، وسيتقدس الشعب وسيرجعون إلى أرضهم (ص 36)، وسينتعش الأسباط الاثنا كما من الموت، ويعودون فيجتمعون (ص 37)، وسيقهر أعداؤهم أخيراً (ص 38 و 39). في السنة الخامسة والعشرين، تكشف إعادة تأسيس كنيسة الله، إذا تعرض رمزياً في رؤيا الهيكل مكبراً ومقدساً كله، والشعب مطهراً ومقبولاً لدى يهوه (ص 40-43)، وخدماته المقدسة (ص 44-46)، ونهر الحياة يخرج منه ويجعل الحياة بهجة (ص 47)، وتوزيع الأرض بين الأسباط، وتعرف مدينتهم بأنها المكان الذي يحل فيه يهوه (ص 48).في هذه الرؤيا يتغير الهيكل الذي قد عرفه حزقيال جيداً في أيام صباه تغييراً تاماً في مظهره. فعوضاً عمدن جيل صهيون الصغير، يرى جبلاً عالياً متوجاً بمباني مقدساً جديداً أعظم من ذي قبل. وملاكاً، بقصبة وحبل قياس، واقفاً في الباب. والهيكل الجديد مصمّم فعلاً حسب القديم في ترتيباته العامة، لكنه قائم بالنسبة إلى مساكن الناس، وأفنيته وغرفه منسّقة حتى تحمي قداسة يهوه، الذي سيسكن هناك حالاً، وتطبع على العابدين انفصال يهوه عن كلا النجاسة الأدبية والطقسية. منذ سنوات كثيرة مضت قد رأى حزقيال في رؤيا يهوه تاركاً الهيكل القديم المدنس (10: 18 و 19 و 11: 22-24). والآن يرى النبي يهوه راجعاً من الباب نفسه إلى الهيكل ومجد يهوه مالئاً البيت، ويسمع صوتاً من الداخل قائلاً: ((هذا مكان كرسيّ...حيث أسكن في وسط إسرائيل إلى الأبد ولا ينجس بعد بيت إسرائيل اسمي القدوس...) (43: 1-6). وفي الدار الداخلية بعد أمام بيت المقدس، يرى حزقيال مذبح الكفّارة معدّاً لإسرائيل الجديد ويسمع التصريح: ((فأرضى عنهم يقول السيد الرب)) (الآيات 13-27).وتوصف الآن للنبي عبادة إسرائيل المقبول وفي الثيوقراطية الجديدة ستطابق الحالة الحقيقية المثل الأعلى الإلهي. ولن يدخل إلى المقدس غير المختونين بالقلب أو الجسد. والعشائر اللاوية التي دلّت على أنها غير أمينة قديماً لن يسمح لها بأن تخدم المذبح، لكنهم سيعطون مكاناً، مهما يكن متواضعاً، في الهيكل. والكهنة بنو صادق الذين اسمهم نفسه يعني بار، الذين ظلوا أمناء هم وحدهم سيشغلون وظيفة الكاهن العليا قدام يهوه (ص 44). وستقدم للرب تقدمة، قسم من الأرض لإعانة الخدمات ولخدمة المقدس (45: 1-6). وللرئيس أيضاً ستعطى إعانة مناسبة من تقدمة الأرض، تمكنه من تدعيم الخدمات العامة باسم الشعب، وهو لن يسيء استغلال سلطته باضطهاد الشعب أو التعدي على حقوق الكهنة (45: 7-12 و 46: 2 و 16-18). والشعب كذلك سيجتمع في أماكنه المعيّنة في الهيكل عندما يعبدونه (آية 9). وكل أعضاء الثيوقراطية، الرسميين والشعبيين يعرفون الواجبات المختصة بهم في أماكنهم وعلاقاتهم المتعددة بالنسبة إلى الكفّارة ويؤدون هذه الواجبات. ويرى حزقيال بعد ذلك نهراً خارجاً من الهيكل، متزايداً إلى مجرى عظيم، يهب الحياة والصحة للمناطق التي يعمها الجدب والموت (47: 1-12). وحدود الأرض التي ستحتلها الجماعة الراجعة من السبي موصوفة (47: 13: 20)، وموقع الأسباط (47: 21-48: 29)، وتقدمة الأرض تقسّم بين الكهنة واللاويين والرئيس والمدينة (48: 8-22). خص جميع الشعب مشتركاً (انظر الآيات 15-20 و 30-34 و قارن آية 19و 45: 6)، واسمها يهوه شمّه أيّ يهوه هناك (48: 35)، إشارة إلى الفكر المركزي للنبوة كلها.فما هو النصيب الذي قام به حزقيال في تقدم الفكر الديني? يعتبر نقاد ((مدرسة ويلهاوزين)) أن حزقيال هو أبو اليهودية الشكلية الأخيرة. ويزعمون أن وصف أورشليم الجديدة في الإصحاحات 40-48 هو البرامج لذي بناءً عليه أقام الترتيبات الكهنوتية الخاصة المتضمنة في لاويين وعدد. هذه النظرية مرفوضة من العلماء الذين يتخذون النظرة الكتابية عن صل القوانين العبرانية، ويتمسكون بأن تشريع اللاويين الخاص يسبق عصر حزقيال، وأيضاً أن النبي لم يقصد أن تكون هذه الإصحاحات كبرنامج. والصورة المعلنة في هذه الإصحاحات ليست مثلاً أعلى توقّع النبي أن يتحقق حرفياً، لكنها رمزية خالصة. فليس من الممكن ن نفهم جبل صهيون المرتفع الجديد، والمقاييس وتقسيمات الأرض الهندسية، والياه الشافية التي تخرج من المقدس وتصبح في الحال نهراً عظيماً، والأشجار التي تنتج ثمرها كل شهر وورقها للشفاء إلاّ لهذا الاعتبار. ومن بين الأشياء الأخرى التي قدمها:الفكرة عن الله. فهو يزيل فكرة تحول الله إلى إنسان ((منظر شبه مجد الرب)) (1: 28). بينما آخرون يتكلمون عن يهوه يرعى شعبه (تكوين 48: 15 و مزمور 28)، يجمع مشتتي قطيعه (ارميا 23: 3 و 31: 10)، ويعتني بهم بلطف (اشعياء 40: 11)، ويمثل حزقيال الله كمن يفتش عن خرافه الضالة (حزقيال 34ك 11-16 و قارن متى 18: 12-14 و لوقا 19: 10).رؤيا أورشليم الجديدة: الجبل المرتفع (حزقيال 40: 2 و رؤيا 21: 10)، المدينة المقدسة خيمة الله بين الناس (حزقيال 37: 27 و رؤيا 21: 3)، مجد الله فيها (حزقيال 43: 2-5 و رؤيا 21: 11)، المدينة المربعة (حزقيال 48: 16 و 30 و رؤيا 21: 16)، التي اثنا عشر باباً (حزقيال 48: 30-34 و رؤيا 22: 1)، الأشجار التي على جانبي النهر، التي أوراقها للشفاء (حزقيال 47: 7 و 12 و رؤيا 22: 2). وهي رمزية في حزقيال كما أنها رمزية في سفر الرؤيا.لكن فوق الكل ساعد حزقيال الفكرة الروحية لأورشليم المستقبل فهو يتناول تعاليم ارميا الأصلية، ويؤكد المسؤلية الفردية بأكثر وضوح (حزقيال 18 و 33: 1-20). وكذلك طبيعة الشعب التي تخلق من جديد، ومجد وقداسة الملكوت التي ستكون تاج الزمان الآتي (11: 19و 20 و 36: 24-29). إن جماعة الله الجديدة هي موضوع رجاء شعب الله عندما ينظرون إلى المستقبل المجيد.'),
(1154, 'حِزْقيَِّا', 'اسم عبري معناه ((الرب قد قوّى أو الرب قوّة)) (1) ابن آحاز ملك يهوذا. اشترك مع أبيه في الحكم في 728 ق.م. وبما أن آحاز كان عاجزاً عن المساهمة الفعلية في شئون الدولة، صار حزقيا الحاكم الفعلي. قيل أن حزقيا قد بدأ يحكم في سن الخامسة والعشرين (2 ملوك 18: 2 و 2 اخبار 29: 1). كان خادماً مكرساً ليهوه وافتتح حكمه بترميم الهيكل وتطهيره، وأعاد تنظيم خدماته الروحية وموظفيه، واحتفل بفصح عظيم دعا إليه، ليس فقط يهوذا وبنيامين، بل العشرة الأسباط الأخرى (2 اخبار 29: 1-30: 13). وأزال المرتفعات، وطرح التماثيل، وحطّم الحيّة النحاسية التي عملها موسى، لأنها صارت عبادة وثنية.انتصر حزقيا على الفلسطينيين وأصبح عظيماً وناجحاً. وفي السنة الرابعة من ملكه 724 ق.م. ابتدأ شلمناصر حصار السامرة، وفي 722 ق.م. أكمله سرجون الثاني، وحمل العشرة الأسباط إلى السبي (2 ملوك 18: 9 و 10). وفي 714 ق.م. وفقاً لحساب المؤرخين العبرانيين، بدأت سلسلة الغزوات الآشورية التي كونت صورة مميّزة لحكم حزقيا وانتهت بكارثة لآشور. ويقدم الكتاب هذه الوقائع كرواية متصلة. وتقع في ثلاثة أقسام: بداية الغزوات نحو 714 (2 ملوك 18: 13 و اشعياء 36: 1 وربما 2 اخبار 32: 1-8 و قارن غزو فلسطين في 712-711 و اشعياء 20: 1) الحملة الرئيسية في 701، في مرحلتها الأولى (2 ملوك 18: 14-16)، وفي مرحلتها النهائية (2 ملوك 18: 17 إلى 19: 35 و 2 اخبار 32: 9-21 و اشعياء 36: 2 إلى 37: 36)، ونهاية سنحاريب في 681 (2 ملوك 19: 36 و 37 و اشعياء 37: 37 و 38). كان سرجون لا يزال على عرش آشور في 714، لكنه كان قد وضع ابنه سنحاريب في مركز عسكري رفيع قبل ذلك التاريخ، وربما كان سنحاريب هو الذي قاد جيوش أبيه التي كانت في 720 أو 751 وبداية 714، أو ربما في تاريخ متأخر قد ((أخضعت يهوذا)) حسب التقرير الآشوري، عندما كان جيش آشور الرئيسي يشعل حرباً إلى شمال آشور وشرقها. كان حزقيا مريضاً، ربما من جمرة خبيثة، وقارب على الموت لكنه مُنح امتداداً جديداً لحياته مدته 15 سنة (2 ملوك 20: 1-11 و اشعياء 38). وكان الاستعلام عن الآية هو الغرض الظاهر لبعثة مرودخ بلادان، ملك بابل، أما الغرض الحقيقي فكان إقناع الملك يهوذا بأن ينضم إلى التحالف العظيم الذي كان يجري تكوينه سراً ضد القوة الآشورية. فانتفخ حزقيا بمجيء السفراء البابليين وبسط أمامهم مصادره المالية، لكن النبي أشعياء حذّره بأن شعب يهوذا يؤخذ أسيراً إلى ذلك المكان نفسه الذي جاء منه السفراء (2 ملوك 20: 12-18 و 2 اخبار 32: 31 و اشعياء 39). وانضم حزقيا إلى الحلف لكن سرجون الذي كان قائداً مقتدراً، هجم على الحلفاء قبلما نضجت خططهم. وتمت حملته على اشدود، بقيادة ترتان (اشعياء 20: 1)، في 712 وكان سببها رفض فلسطين، ويهوذا، وادوم، وموآب، أن يدفعوا الجزية وفي 710 خلع مرودخ بلادان عن العرش وجعل نفسه ملكاً على بابل بدلاً منه.وفي 705 قُتِل سرجون واعتلى ابنه سنحاريب عرش آشور. فكان تغيير الحكام إشارة لثورات جديدة. ولكي يقمع ثورة الغرب، تقدّم سنحاريب إلى بلاد فلسطين في عام 701، متغلباً على فينيقية في طريقه واستقبل وفوداً من اشدود، وعمون، وموآب، وادوم، ملتمسة السلام. وصمدت مدن كثيرة، وتقدم سنحاريب إلى يافا، وبيت داجون، واشقلون، وأماكن أخرى. واتجه شرقاً، واستولى على لخيش، ونصب خيامه هناك، وأخذ جزيا من حزقيا الذي كان يرتعب فرقاً. وكانت هذه الجزية مكونة من 30 وزنة من الذهب، و 300 وزنة من الفضة أو وفقاً للحساب الآشوري عن أنه كانت في الجزية أحجار كريمة، وأخشاب ثمينة، وأدوات من العاج، وبنات حزقيا، ونساء من القصر، وغير ذلك. ولكي يحصل حزقيا على المعادن الثمينة، قشر أبواب وأعمدة الهيكل ونزع عنها أغشيتها. لكن أخباراً وصلت إلى سنحاريب بينما كان لا يزال في لخيش عن تحالف بين المدن الفلسطينية ومصر وكوش (2 ملوك 18: 21 و 24)، ولأنه لم يكن يقبل أن تكون هناك قلعة قوية كأورشليم في مؤخرته، أرسل فصيلة من جيشه لمحاصرة المدينة. وكان حزقيا قد سمع عن زحف الجيش الجنوبي وعن مقاومة عقرون القوية للآشوريين. وانتعش أيضاً إيمانه بيهوه عن طريق تشجيعات أشعياء، فأبى أن يقبل الجيوش الآشورية في المدينة. وفي الوقت نفسه كان الملك الآشوري قد رفع الحصار عن لخيش وهجم على لبنة (2 ملوك 19: 8). وإذا سمع عن موقف التحدي الذي وقفه حزقيا، أرسل إليه رسلاً يحملون رسائل تهديد، متواعداً إياه بانتقام قادم، ولكن سنحاريب تراجع إلى التقية، حيث جرت معركة. وصُدَّ المصريون، لكن غنائم النصر كانت زهيدة. ثم حول سنحاريب التفاته إلى المدن المعادية في المنطقة المجاورة. ولم يتوقف تدميره للمدن وتقدمه إلى أورشليم إلا بواسطة الوبأ المفاجئ الذي ضرب جيشه، والذي أهلك في ليلة واحدة 185000 من جنوده ((سنحاريب)).ومن ضمن الأعمال التي قام بها حزقيا في أورشليم أنه حفر بركة وقناة للمياه وأقام سرداباً وفي سنة 18881 اكتشف في سرداب سلوام نقش عبري يرجع إلى عهد حزقيا ويصف إقامة ذلك السرداب (انظر 2 ملوك 20: 20).وفضلاً عن أشعياء، كان هوشع وميخا معاصرين لحزقيا (هوشع 1: 1 و ميخا 1: 1). ومات الملك نحو 693 ق.م. تاركاً ابنه منّسى ليعتلي العرش (2 ملوك 20: 21 و 2 اخبار 32: 33).(2) جد النبي صفنيا (صفنيا 1: 1)، ويعتقد أن حزقيا هذا هو نفس حزقيا الملك.(3) ابن نيريا، قريب لأسرة يهوذا الملكية (1 اخبار 3: 23).(4) رجل رجع بعض أولاده مع زربابل (نحميا 7: 21) ويكتب اسمه بحزقيا في عزرا 2: 16.(5) أحد رؤساء الشعب الذين ختموا العهد في زمن نحميا (نحميا 10: 17).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1155, 'حازِمون', '(أعمال 27: 17). الذين قاموا بربط حبال حول السفينة لمنع انفصال أخشابها.'),
(1156, 'حَزو', 'اسم عبري معناه ((رؤية)) ابن ناحور وملكة (تكوين 22: 22). وهذا الاسم وربما لا يزال باقياً في المنطقة الجبلية، باسم حازو التي عبرها اسرحدون، وهي ليست بعيدة من حوران.'),
(1157, 'حَزييِيل', 'اسم عبري معناه ((بصر الله)) ربما اختصار يحزيئيل. لاوي جرشوني، ابن شمعي (1 اخبار 23: 9).'),
(1158, 'حَريِز', 'اسم عبري معناه ((خنزير)) رئيس من رؤساء الشعب ختم العهد مع نحميا (نحميا 10: 20).'),
(1159, 'حَزيُون', 'اسم آرامي معناه ((رويا)) أب طبريمون، وجد بنهدد ملك آرام (1 ملوك 15: 18).'),
(1160, 'حَسَد', 'اسم عبري معناه ((رحمة، نعمة)) أبو أحد وكلاء سليمان (1 ملوك 4: 10).'),
(1161, 'حَسَديا', 'اسم عبري معناه ((قد كان الرب منعماً)) ابن زربابل من نسل داود الملكي (1 اخبار 3: 20).'),
(1162, 'حَسْرَة', 'اسم عبري ربما كان معناه ((عوزة)) انظر ((حرحس)).'),
(1163, 'حَسَك', 'انظر ((شوك)).'),
(1164, 'حَسْلي', 'ربما من العبرية ((يهوه قد حفظ)) وهو جد المسيح عاش بعد السبي (لوقا 3: 25).'),
(1165, 'مُحْسِنون', 'لوقا 22: 25. ولفظ الاسم المفرد في اليونانية يورجتس أي محسن. وقد لقب بعض الملوك بهذا اللقب تعظيماً لهم. ومن جملة من لقب به اثنان من البطالسة.'),
(1166, 'حَسُوفا', 'اسم عبري معناه ((معرّى)) مؤسس أسرة من النثينيم، رجع أفرادها من سبي بابل مع زربابل (عزرا 2: 23 و نحميا 7: 46).'),
(1167, 'حَشْبَدّانَة', 'اسم عبري ربما كان معناه ((قاض مسئول)) وهو أحد الذين وقفوا عن يسار عزرا عندما كان يقرأ الشريعة للعشب الراجع من السبي (نحميا 8: 4).'),
(1168, 'حشبنا', 'اسم عبري معناه ((من يحاسبه يهوه)). احد الذين ختموا العهد مع نحميا ( نحميا 10: 25 ).'),
(1169, 'حَشَبْنيا', 'اسم عبري معناه ((من يحاسبه يهوه)).(1) أبو حطوش، أحد الذين اشتركوا في ترميم نحميا (نحميا 9: 5).(2) أحد اللاويين، الذين بواسطته حضوا المسبيين الراجعين، واعدّوهم لختم العهد مع يهوه في أيام نحميا (نحميا 9: 5).'),
(1170, 'حَشْبُون', 'اسم موآبي معناه ((حسبان، تدبير)) وهي مدينة سيحون، ملك الاموريين لكن يظهر أنها مأخوذة أصلاً من الموآبيين (عدد 21: 25-30 و 34). فقد عينها موسى للموآبينيين، وبعد الغزو أعاد بناءها رجال ذلك السبط ((عدد 32: 37 و يشوع 13: 17). لكنها كانت قائمة على خط الحدود بين رأوبين وجاد (يشوع 13: 26)، وحدث أن امتلكها سبط جاد، للاويين (يشوع 21: 39 و 1 اخبار 6: 81). وأخذها الموآبيون في أيام أشعياء وارميا (اشعياء 15: 4 و 16: 8 و 9 و ارميا 48: 2 و 33 و 34). وبعد ذلك كانت في حوزة اسكندر جانايوس وهيرودس الكبير. ولا تزال تعرف باسم حسبان وهي مدينة خربة، قاتمة على تل منعزل، بين ارنون ويبوق، وتقع نحو سبعة أميال ونصف شمال مادبا. وهناك خزان مياه عظيم، شرق خرائب المدينة، ربما هو أحد البرك التي كانت خارج أسوار المدينة (نشيد الإنشاد 7: 4).'),
(1171, 'حَشَبيا', 'اسم عبري معناه ((يهوه دبّر أو حاسب)).(1) لاوي مراري، من نسل امصيا، وجد يدوثون أو ايثان (1 اخبار 6: 44 و 45). وربما كان من نسل بوني المذكور كجد شمعيا (1 اخبار 9: 14 و نحميا 11: 15).(2) لاوي مراري، ابن يدوثون ورئيس الفرقة الثانية من العازفين الذين عينّهم داود للمقدس (1 اخبار 25: 3 و 19).(3) لاوي قهاتي من عشيرة حبرون، وكيل على البلاد الواقعة غرب الأردن (1 اخبار 26: 30 و قارن 23: 12).لاوي ابن قموئيل، رئيس من سبط لاوي، عاش في عصر داود (1 اخبار 27: 17).(5) رئيس من اللاويين عاش أثناء ملك يوشيا (2 اخبار 35: 9).(6) لاوي مراري، انضم إلى عزرا عند نهر اهوا، ويظهر أنه كان أحد الاثني عشر الذين استودعهم عزرا الكنز الذي نقل إل أورشليم (عزرا 8: 19 و 24). وربما كان هو الذي ختم العهد (نحميا 10: 11)، وكان رئيساً لاوياً وأحد عازفي الهيكل (نحميا 12: 24).(7) رئيس نصف دائرة قعيلة، في عصر نحميا. رمم جزءاً من سور أورشليم (نحميا 3: 17).(8) لاوي من نسل آساف (نحميا 11: 22).(9) كاهن، رئيس بيت أب لحلفيا في زمن يهوياقيم رئيس الكهنة (نحميا 12: 21).'),
(1172, 'حَشّوب', 'اسم عبري ربما كان معناه ((من يفكر فيه، أو الذي يتجه فكر الله إليه)).(1) ابن فخث موآب، أحد الذين رممّوا سور أورشليم (نحميا 3: 11).(2) يهودي رمم جزءاً من سور أورشليم مقابل بيته (نحميا 3: 23). وربما كان هو، أو سابقة ضمن من ختموا العهد (نحميا 10: 23).(3) لاوي مراري، أب شمعيا (1 اخبار 9: 14 و نحميا 11: 15).'),
(1173, 'حَشْمون', 'اسم عبري معناه ((خصب)) مدينة في أقصى جنوب يهوذا (يشوع 15: 27).'),
(1174, 'حَشْمونة', 'اسم عبري معناه ((خصبة)) محط للعبرانيين في ترحالهم في البرية (عدد 33: 29 و 30).'),
(1175, 'أحْشَاء', '(ما في البطن، امعاؤه) ويقصد بها احساسات الرأفة والحنان (كولوسي 3: 12 و 1 يوحنا 3: 17).'),
(1177, 'حَشُوبَة', 'اسم عبري معناه ((محسوب، مقدّر)) ابن زربابل (1 اخبار 3: 20).'),
(1178, 'حَشُوم', 'اسم عبري ربما كان معناه ((أنف مفرطحة)). وهو مؤسس أسرة، رجع أعضاؤها من بابل مع زربابل (عزرا 2: 19 و 10: 33 و نحميا 7: 22). وممثل الأسرة، أو شخص بهذا الاسم، وقف بجانب عزرا بينما كان هذا يخاطب الشعب (نحميا 8: 4)، ثم ختم العهد (نحميا 10: 18).'),
(1179, 'حصَاد', 'يمكن أن تنقسم فترة الحصاد في فلسطين القديمة إلى قسمين: حصاد الشعير وحصاد القمح، والأول يسبق الأخير بنحو أسبوعين (راعوث 2: 23). وتكرس بداية الحصاد بإحضار حزمة الباكورات، أول الحصيد، للتردد (لاويين 23: 10). وكان الحصاد يبدأ في الوديان والأراضي المنخفضة قبلما تنضج الغلال على التلال. وكان حصاد الشعير يبدأ في وادي الأردن في أبريل، عندما يكون الأردن ممتلئاً (يشوع 3: 15 و قارنه مع 5: 10)، فكان يحل مباشرة بعد فصل الأمطار (1 صموئيل 12: 17 و 18 و أمثال 26: 1). وعندما كان يتم حصاد القمح في الأراضي المرتفعة يتأخر إلى شهر يونيه.ونجد في سفر راعوث وصفاً مطولاً للحصاد (راعوث 1: 22). وكانت الحنطة تقطع بالمناجل (ارميا 50: 16). وتجمع شمائل (راعوث 2: 16)، وتحزم حزماً (مزمور 129: 7)، ثم تنتقل إلى البيادر أو الاهراء، وكانت تنقل أحياناً على العجلات (عاموس 2: 13)، ثم تدرس وتذرّى. وكانوا يستخدمون الثيران للدراس ولم يكن يجوز تكميمها (تثنية 25: 4 و 1 كورنثوس 9:).ويرمز الرب يسوع مخلصنا بالحصاد إلى خلاص النفوس، وبالحاصد أو العامل إلى الكارز بالإنجيل (لوقا 10: 2 و يوحنا 4: 35-38). وكذلك يرمز بالحصاد إلى انقضاء العالم (متى 13: 39). وبالحصّادين إلى الملائكة في ذلك الوقت سيرسل الملاك منجله ((إذ قد يبس حصيد الأرض)) (رؤيا 14: 15). (انظر سنة، فلاحة).'),
(1180, 'حصَار', '(تثنية 20: 19). انظر ((حرب)).'),
(1181, 'حَصْرَاي', 'اسم عبري معناه ((محصور، محاصر)) كرملي، أحد أبطال داود (2 صموئيل 23: 35). وفي اخبار خربة غزة، جنوب غرب رأس زويرة.'),
(1182, 'حصر اذار', 'انظر ((ادار))'),
(1183, 'حصر جدة', 'اسم عبري معناه (( قرية السعد)) مدينة في اقصى جنوب يهوذا (يشوع 15: 27)، ربما خربة غزة، جنوب غرب رأس زويرة.'),
(1184, 'حَصَرُ سوسة', 'اسم عبري معناه ((قرية الخيل)) وهي قرية للشمعيونيين (يشوع 19: 5 و 1 اخبار 4: 31). وظن البعض أن مكانها اسطبلات أبو سوسين، قرب تل الفارغة، ولكن يحتمل أنها سوسية التي تقع على مسافة ميلين شمالي اشتموع.'),
(1185, 'حَصَر سوسيم', 'جمع حصر سوسة.'),
(1186, 'حَصَر شوعال', 'اسم عبري معناه ((قرية الثعلب)) قرية في أقصى جنوب يهوذا، أعطيت للشمعونيين (يشوع 15: 28 و 19: 3 و 1 اخبار 4: 28). وقد سكنها اليهود بعد السبي (نحميا 11: 27)، وربما كانت ((الوطن))، بين بئر سبع وتل السبع، وإلى الجنوب الشرقي من بئر سبع.'),
(1187, 'حَصَرَ عينان', 'اسم عبري معناه ((قرية العيون، قرية الينابيع)). قرية على حدود فلسطين الشمالية كانت ((القريتين))، على الطريق من دمشق إلى تدمر.'),
(1188, 'حَصَرُ الوسطى', 'اسم عبري معناه ((القرية المتوسطة)). قرية على حدود حوران (حزقيال 47: 16).'),
(1189, 'حصرو', 'انظر ((حصراي))'),
(1190, 'حَصْرُون', 'اسم عبري معناه ((حصار، حظيرة)).مكان على الحد الجنوبي ليهوذا، لا يبعد كثيراً عن صين وقاديش برنيع (يشوع 15: 3 و عدد 34: 4). وكانت قريبة من ادار، أو هي نفس ادار. وربما كان موقعها خرابة القديرات على وادي القديرات. وقد كانت محطاً بين أورشليم وعصيون جابر. وكذلك كانت محطاً قوياً لقرية حصرون (يشوع 15: 25) انظر ((حاصور)) (3).(2) ابن رأوبين، ومؤسس أسرة الحصرونيين (تكوين 46: 9 و خروج 6: 14 و عدد 26: 6 و 1 اخبار 5: 3).(3) ابن فارض، من سبط هوذا، ومؤسس أسرة (تكوين 46: 12 و عدد 26: 21 و راعوث 4: 18 و 1 اخبار 2: 5 و متى 1: 3).'),
(1191, 'حِصّة، حِصَص', '(مقدار من الطعام). كانت عادة القدماء أن يفرزوا لكل ضيف حصة، وكانوا يكرمون الضيف الممتاز بأن يعطوه أكثر من غيره (تكوين 43: 34)، وفي 1 صموئيل 1: 5 سميّت نصيبا.'),
(1192, 'حَصُّّّون تامار', 'اسم عبري معناه ((تقطيع النخيل، رملة النخيل)). انظر عين جدي (2 اخبار 20: 2). ربما هي ثامار (حزقيال 47: 19 و 48: 28)، أو ((تمارا))، التي ربما كان موقعها مكان ((كرنوب))، على مسافة 20 ميلاً إلى الجنوب الغربي من الطرف الجنوبي للبحر الميت على الطريق من حبرون إلى إيلات.'),
(1193, 'حَصَاة بَيْضاء', '(رؤيا 2: 17). يعتقد البعض أنها تشير إلى الحصى البيض التي كانت تعطى للمنتصرين في الألعاب الأوليمبية محفوراً عليها أسماؤهم وقيمة الجوائز التي كانوا يكافأون بها.'),
(1194, 'حَضْرَموت', 'اسم عبري معناه ((قرية الموت)) وهو اسم جماعة من اليقطانيين سكنوا مقاطعة في الجزيرة العربية (تكوين 10: 26 و 1 اخبار 1: 20). واسم منطقة في جنوب شبه الجزيرة لا تزال تسمّى بهذا الاسم، يكثر فيها اللبان والمر، لكن هواءها ليس صحيحاً، لذلك سُمِيّت بهذا الاسم.'),
(1195, 'حِضْن', 'تستعمل كلمة الحضن في الكتاب المقدس للدلالة على الحفظ (اشعياء 40: 11 و لوقا 16: 23)، أو شدة المحبة (يوحنا 13: 23)، أو الاتحاد الكامل (يوحنا 1: 18).'),
(1196, 'حَضَيْرموت', 'اسم عبري معناه ((حظائر، ديار، قرى)). وهي محط العبرانيين في وقد جاء ذكره بعد قبروت هتأوه (عدد 1: 35)، وعلى هذا الجانب من فاران (عدد 12: 16 و 3: 17 و تثنية 1: 1). وهناك تذمرت مريم وهارون على موسى (عدد ص 12)، وربما كانت ((عين خضرة))، الواقعة على مسافة 36 ميلاً شرقي جبل سيناء.'),
(1197, 'حَطُّوش', 'اسم عبري معناه ((مجتمع)).(1) رجل من يهوذا، ابن شمعيا، وأسرة شكنيا (1 اخبار 3: 22).(2) رئيس كهنة رجع من بابل  مع زربابل (نحميا 12: 2 و 7).(3) رأس بيت أب، من بني داود، رجع مع عزرا أورشليم (عزرا 8: 2).(4) ابن حشبنيا، رمّم جزءاً من سور أورشليم (نحميا 3: 10).(5) كاهن ختم العهد مع نحميا (نحميا 10: 4).'),
(1198, 'حَطِّيل', 'اسم عبري ربما كان معناه ((خطل، ثرثار، متقلقل)). أحد عبيد سليمان، أسس أسرة رجع أعضاؤها من باابل ممع زربابل (عزرا 2: 55 و 57 و نحميا 7: 57-59).'),
(1199, 'حَطِيطا', 'اسم آرامي معناه ((مخطط)) أحد بوابي الهيكل، أسس أسرة رجع أعضاؤها من بابل مع زربابل (عزرا 2: 42 و نحميا 7: 45).'),
(1200, 'حَطِيفا', 'اسم آرامي معناه ((مخطوف، أسير)). مؤسس أسرة من النثينيم، رجع أعضاؤها من بابل مع زربابل (عزرا 2: 54 و نحميا 7: 56).'),
(1201, 'حَفارايم', 'اسم عبري معناه ((حفرتان)) مدينة على حدود يساكر (يشوع 19: 19)، يقول البعض أنها خرابة الفرّية، على مسافة خمسة أميال ونصف شمال غربي مجدّو، وربما كانت ((الطيبة)) الواقعة شمال غربي بيت شان، في قلب نصيب يساكر.'),
(1202, 'حُفْرَة', 'حجر كبير عميق في الأرض، قد يكون طبيعياً أو صناعياً فتكون بئراً (تكوين 14: 10 و 37: 20 و 24). وقد تستخدم الحفرة لصيد الحيوان، بأن يغطى فمها بأغصان يربط حيوان اليف كخروف أو جدي، فيثب عليه المفترس فيقع في الحفرة، أو يدوس عليها الحيوان الآكل العشب الذي على غطائها فيقع في الفخ. ويُكنى بذلك عن حل الناس والشيطان (مزمور 119: 85 و أمثال 26: 27 و حزقيال 19: 4). وتستخدم الحفرة مجازياً للقبر أو الموت (أيوب 33: 18 و 24)، انظر ((جحيم)) و ((الهاوية)).'),
(1203, 'حَفْصيبة', 'اسم عبري معناه ((سروري بها)).(1) أم الملك منّسى (2 ملوك 21: 1).(2) اسم رمزي سيعطى لصهيون أو لأورشليم (اشعياء 62: 4).'),
(1204, 'حُفَّة', 'اسم عبري ربما كان معناه ((غطاء)).رجل من نسل هارون، أسرته صارت لفرقة 13 من الكهنة (1 اخبار 24: 13).'),
(1205, 'حُفِّيم', 'انظر ((حوفام)).'),
(1206, 'مَحْفَل', 'عيد احتفالي فيه يدعى العبرانيون ليجتمعوا معاً، ولا يسمح حينئذ بعمل من أعمال التسخير. وكانت الأعياد المقدسة تشمل كل سبت (لاويين 23: 1-3)، واليوم الأول واليوم السابع من عيد الفطير (خروج 12: 16 و لاويين 23: 6 و 7 و عدد 28: 18 و 25)، يوم الخمسين (لاويين 23: 15-21)، اليوم الأول واليوم السابع من الشهر السابع، والأخير هو الكفارة العظيم (لاويين 23: 24-28 و عدد 29: 1)، واليوم الأول واليوم السابع من عيد المظال، الذي كان يبدأ في اليوم الخامس عشر من الشهر السابع (لاويين 23: 34-36 و نحميا 8: 18). وتستخدم هذه الكلمة للدلالة على اجتماع الشعب للعبادة (خروج 12: 16). كما استخدمت مرة للدلالة على اجتماع الأشرار (أرميا 15: 17). وفي العهد الجديد يقصد بها (1) الاجتماع سواء أكان شرعياً (أعمال 19: 39) أم غير شرعي (أعمال 19: 32 و 41). (2) اجتماع الملائكة في السماء (عبرانيين 12: 22).'),
(1207, 'حُفْني', 'اسم مصري معناه ((أبو ذنيبة، أو فرخ الضفدع)). ابن عالي، وقد قام هو وأخوه فينحاس بوظيفة الكهنوت في شيخوخة عالي، لكنهما أظهرا أنهما غير جديرين بهذه الوظيفة المقدسة بسبب أخلاقهما الفاضحة، واعترض عالي على تصرفهما اعتراضاً ليناً ولم يوبخها توبيخاً صارماً، ونتيجة لذلك صدر القضاء الإلهي ضده وضد بيته. ولما تقرر أن يؤخذ تابوت الله إلى ميدان المعركة ضد الفلسطينيين، رافقه حفني وفينحاس اللذان كانا حارسين له لكونهما الكاهنين اللذين كانا يقومان بالخدمة، وقد قتل كلاهما في المعركة التي وقعت بعد ذاك (1 صموئيل 2: 22-4: 22). وهما مثل رديء لتراخي رجال الله والوالدين المؤمنين في تربية أولادهم التربية البيتية الصحيحة. انظر ((عالي)).'),
(1208, 'حُقُّوق', 'اسم عبري معناه ((محفور أو مقضي به)) مدينة على حدود نفتالي (يشوع 19: 34). وهي ياقوق على مسافة ما يقرب من ستة أميال إلى الشمال الغربي من تل حوم.'),
(1209, 'حَقْل', 'استخدم في الكتاب المقدس للدلالة على أرض غير مسوّرة، سواء أكانت مرعى أم فلاحة (تكوين 37: 7 و 14-16)، مهما كانت مساحتها (تكوين 23: 9 و متى 13: 24). وقد ترجمت نفس الكلمة العبرية بكلمة بلاد في تكوين 14: 7 و راعوث 1: 6. وعندما كانت توضع الحدود صناعياً كانت تبين بواسطة حجارة (تثنية 19: 14).'),
(1210, 'حَقْل دَما', 'اسم آرامي معناه ((حقل الدم)) قطعة من الأرض معروفة بحقل الفخّاري، اشتراها الكهنة بالثلاثين قطعة من الفضة التي طرحها يهوذا الخائن في الهيكل، وقد خصصّوها لكي تكون مقبرة للغرباء (متى 27: 7). هذه القطعة من الأرض التي اشتريت بثمن الدم، والتي كانت المكان الذي فيه شنق يهوذا نفسه، سميت بحق بناء على ذلك حقل الدم (متى 27: 8 و أعمال 1: 19). ويشير بطرس إلى يهوذا كأنه اقتنى الحقل (أعمال 1: 18 و 19). وربما هو لا يعني أنه اشتراه شخصياً، بل أن الكهنة اشتروه بمال يهوذا الخائن، ذلك المال السحت المقتنى بالحرام. والموقع التقليدي لهذا الحقل، الذي يرجع تاريخه إلى عصر جيروم في القرن الرابع، يقع على الجانب الجنوبي لوادي هنّوم. وربما كان هو نفس المكان أو قريباً منه، لأن المنطقة فيها طين الفخاري، وقد استخدمت كمكان لدفن الموتى منذ وقت طويل، وقد دفن فيها فيما بعد كثيرون من الصليبيين. وبقي الاسم القديم إلى الآن إذ يطلق على هذا المكان اسم ((حقّ الدّم)).'),
(1211, 'حَقْل الفخاري', 'انظر ((حقل الدم)).'),
(1212, 'حَقْل القصَّار', '(حقل مبيض الثياب) وحقل القصار يقع خارج أورشليم وكان موضعاً مشهوراً في عصر الملكية. وكان فيه طريق سلطاني وقناة البركة العليا (اشعياء 7: 3 و 36: 2)، وكان قريباً من المدينة حتى أن رسل الآشوريين عندما وقفوا في الحقل وتكلموا سمعهم الشعب الذين كانوا على سور المدينة وفهموهم (2 ملوك 18: 17). وهناك رأي قديم يضع هذه الحوادث في الغرب، قرب يافا الحالي، حيث تجري هناك قناة من بركة ماميلا في وادي هنّوم الأعلى، إلى المدينة. وهناك رأي آخر يرجّح أن موقعه كان إلى الشمال، حيث أن الشمال كان الجانب الطبيعي الذي يقع الهجوم منه. لكن من المحتمل جداً أن القناة المشار إليها هي النفق الذي يوصل ينبوع العذراء ببركة سلوام، وفي هذه الحالة يكون حقل القصار في وادي قدرون.'),
(1213, 'حَقُوقا', 'اسم عبري معناه ((مائل، منحني، معوج)) وهو مؤسس عشيرة من النثينيم (عزرا 2: 51 و نحميا 7: 53).'),
(1215, 'حَكَليْا', 'اسم عبري ربما كان معناه ((ينتظر يهوه، أو يهوه محتجب)) أبو نحميا (نحميا 1: 1 و 10: 1).'),
(1216, 'حَكْمُوني', 'اسم عبري معناه ((حكيم)) وهو أبو يشعبان ويحيئيل (1 اخبار 11: 11 و 27: 32) انظر ((تحكموني)).'),
(1217, 'حَلاَة', 'اسم عبري معناه ((حلية، زينة، عقد، قلادة)). إحدى امرأتي اشحور جد رجال تقوّع (1 اخبار 4: 5 و 7).'),
(1218, 'حَلْبَة', 'اسم عبري معناه ((حبلى، خصبة، دسمة)). مدينة داخل نصيب أشير، لم يطرد منها الكنعانيون، وربما كانت هي نفس أحلب (قضاة 1: 31). وهي تقابل ((محلبة)) عند الآشوريين والتي مكانها اليوم ((خربة المحالب))، على مسافة تقرب من أربعة أميال شمال شرقي صور.'),
(1219, 'حَلْبُون', '((حبل، سمين، خصب)) مدينة في سوريا مشهورة بخمورها (حزقيال 27: 18)، ويعتقد أنها حلبون الواقعة على مسافة 13 ميلاً إلى الشمال الغربي من دمشق. والقرية الواقعة في واد ضيق محصور بين بقاع جبلية جرداء شديدة الانحدار، وشواطئ طويلة مائلة بارتفاع يصل من 2000 إلى 3000 قدم. وقاع الوادي ملآن بالبساتين، وفي أعلى منحدرات الجبال كروم متدرجة المصاطب. وعلى طول تلك المصاطب وفي الوادي من أسفل خرائب واسعة. وكان خمرها مشهوراً في آشور وبابل وفارس.'),
(1220, 'حِلْداي', 'اسم عبري معناه ((خالد أو باق)) وهو اسم أحد الراجعين من السبي (زك 6: 10) واسمه في الأصل هو نفس الاسم الوارد في الترجمة العربية بلفظ خلداي ويدعى أيضاً حالم (زك 6: 14).'),
(1221, 'حَلَح', 'يرّجح أنه اسم آشوري لا يعرف معناه وهو اسم مقاطعة في إمبراطورية آشور حمل إليها المسبيون من الأسباط العشرة (2 ملوك 17: 6 و 18: 11 و 1 اخبار 5: 26). وربما كانت هي المقاطعة التي عرفت فيما بعد باسم خلكيتيس، فيما بين النهرين، قرب جوزان، في حوض نهري الخابور والساكوراس.'),
(1222, 'حَلْحُول', 'قرية في جبال يهوذا (يشوع 15: 58). ولا تزال القرية تسمّى حلحول، وهي تقع على مسافة نحو 4 أميال شمال حبرون. ويقول التقليد أن قبر يونان (يونس) وقبر النبي جاد في هذا المكان.'),
(1223, 'حَلَف', 'انظر ((قسم)).'),
(1224, 'حَلْفَى', '(1) زوج إحدى المريمات، وأبو يعقوب الصغير ويوسي (متى 10: 3 و مرقس 15: 40)، ويقول البعض أنه كلوبا (يوحنا 19: 25 و مرقس 15: 40). انظر ((يعقوب)).(2) لأبو لاوي أو متى (مرقس 2: 14 و قارن متى 9: 9).'),
(1225, 'حَلْفاء', 'نبات قليل الارتفاع، واشتقاق اسمه بالعبرية يدل على أنه ينمو في المستنقعات، ويحني رأسه، ويُفتل إلى حبال. ويكثر في صعيد مصر وفي وادي حلفا (خروج 2: 3 و 5 و أيوب 8: 11).'),
(1226, 'حِلْقاي', 'اسم عبري ربما كان اختصار حلقيا معناه ((يهوه قسمي، نصيبي، حلقي)). وهو كاهن، رئيس بيت أب مرايوث، في أيام يهوياقيم رئيس الكهنة (نحميا 12: 15).'),
(1227, 'حَلْقَة', 'اسم عبري معناه ((قسم، نصيب)) مدينة على حدود أشير (يشوع 19: 25)، أعطيت بضواحيها للاويين الجرشونيين (يشوع 21: 31). تسمى في أخبار 6: 75 حقوق. ويقول البعض أنها يرقة أو يركا الواقعة على مسافة ثمانية أميال ونصف شمال شرقي عكا. ويرجح آخرون أنها ((تل الهربج)).'),
(1228, 'حِلْقَث هصّوريم', 'اسم عبري معناه ((حقل الصوان الحاد أو حدود السيوف)) اسم أعطي لمشهد المصارعة عند بركة جبعون، بين 12 بنياميني من رجال ايشبوشث ومثلهم من رجال داود (2 صموئيل 2: 16).'),
(1229, 'حِلْقِيا', 'اسم عبري معناه ((يهوه قسمي، نصيبي)) (قارن مزمور 16: 5).(1) لاوي، ابن امصي، من بني مراري (1 اخباري 6: 45 و 46).(2) لاوي مراري آخر، ابن حوسة (1 اخبار 26: 11).(3) أبو الياقيم، الذي كان على بيت الملك في حكم حزقيا (2 ملوك 18: 18 و 26 و اشعياء 22 20 و 36: 3).(4) كاهن، أو أرميا (أرميا 1: 1).(5) أب جمريا المعاصر لأرميا (أرميا 29: 3).(6) رئيس الكهنة المعاصر ليوشيا، الذي ساعد الملك في إصلاحه الديني ووجد سفر الشريعة بينما كان يحسب الفضة المدخلة إلى الهيكل (2 ملوك 22: 4-14 و 23: 4 و 1 اخبار 6: 13 و 2 اخبار 34: 9-22). انظر ((يوشيا)).(7) رئيس كهنة رجع من بابل مع زربابل (نحميا 12: 7). وفي الجيل التالي حمل هذا الاسم بيت أب (عدد 21).(8) أحد الذين وقفوا بجانب عزرا عندما قرأ الشريعة للشعب (نحميا 8: 4).'),
(1230, 'المَحَلة', 'محط جيش أو جماعة من الناس، حيث تقام ترتيبات وقتية لإراحتهم (خروج 14: 19 و 1 صموئيل 4: 5 و 17 و 2 ملوك 7: 7). وكانت تقرر ترتيبات صارمة لجيش العبرانيين لضمان النظافة في محلتهم (تثنية 23: 9-14). وترتيب محلة الأمة العبرانية المتنقلة أثناء ترحالهم في البرية، موصوف في عدد 1: 47-2: 34 و 3: 14-39 و قارن 10: 11-28، وانظر ((شطيم)). وكانت خيمة الاجتماع في الوسط، وحولها خيام اللاويين، والشعب أربعة من أربع جهات الخيمة ولكل قسم منها راية كما كان لكل سبط وكل بيت كبير، وكان لكل سبط رئيس انتخبه الله ففي شرق الخيمة عند بابها موسى وهرون وبنو هرون، ثم محلة يهوذا وتشمل أسباط يهوذا ويساكر وزبولون، وفي جنوبها القهاتيون ثم محلة رأوبين وتشمل أسباط رأوبين وشمعون وجاد، وفي غربها الجرشيونيون ثم محلة أفرايم وتشمل أفرايم ومنّسى وبنيامين، وفي شمالها المراريون ثم محلة دان وأشير ونفتالي. وكان منظر المحلة بهجاً (عدد 24: 2 و 5).'),
(1231, 'حُلْم', 'أفكار تخطر للعقل أثناء النوم. ويمكن أن تنقسم الأحلام إلى ما يأتي:(1) أحلام باطلة (أيوب 20: 8 و مزمور 73: 20 و اشعياء 29: 8).(2) أحلام يستخدمها الله لمقاصد ملكوته.وفي استخدام هذه الأحلام يعمل الله طبقاً لنواميس العقل، وهي أحلام أولاً يقصد بها أن تؤثر على الحياة الروحية للأفراد. فحلم المدياني ثبّط العدو، وشجع جدعون، الذي سمعه بتدبير العناية الإلهية (قضاة 7: 13). وربما كان كذلك حلم امرأة بيلاطس (متى 27: 19). وقد أرسلت العناية الإلهية من هذه الأحلام في العصور الحديثة. فيوحنا نيوتون، وهو مهتم بخلاص نفسه، أتى له حلم أَوضح له طريق الخلاص. ثانياً أحلام توجيهية نبوّية. فقد أرسلت الإعلانات الإلهية في أحلام إلى ابيمالك (تكوين 20: 3)، وإلى يعقوب (تكوين 28: 12 و 31: 10)، وإلى لابان (تكوين 31: 24)، وإلى يوسف (تكوين 37: 5 و 9 و 10 و 20)، وإلى ساقي فرعون وخبازه (تكوين 40: 5)، وإلى فرعون (تكوين 41: 7 و 15 و 25 و 26)، وإلى سليمان (1 ملوك 3: 5)، وإلى نبوخد نصر (دانيال 2: 1 و 4 و 36 و 4: 1 و 2)، وإلى دانيال (دانيال 7: 1 و 2)، وإلى يوسف خطيب مريم (متى 1: 20)، وإلى المجوس (متى 2: 12). وموهبة التفسير المضبوط للأحلام النبوية أعطيت لبعض الناس المحبوبين عند الله المقربين لديه، مثل يوسف (تكوين 41: 16 و 25)، ودانيال (دانيال 2: 25-28 و 47). والأحلام المعطاة كإعلانات للكنيسة كانت تعرض لامتحانات لتقرير طبيعتها. فإذا كانت تحرّض على تصرف فاسد، كانت بسبب هذه الحقيقة نفسها تعلن بأنها كاذبة، وأي إنسان كان يسعى بواسطتها أن يقود الشعب من عبادة يهوه إلى عبادة آلهة باطلة كان يحكم عليها بالموت (تثنية 13: 1-5 وقارن أرميا 23: 25-32 و 29: 8 و زكريا 10: 2). وقد أعطى الله البعض أن يحلموا والبعض الآخر أن يفسروا الأحلام (تكوين 20: 3-6 و 28: 12-15 و 1 صموئيل 28: 6 و دانيال 2 و يوئيل 2: 28). وكان من يرى حلماً يذهب إلى رئيس الكهنة بطريقه معينة ويستلم منه عن تفسير الحلم، غير أنه لم يكن يسوغ اعتبار الأحلام الاعتيادية ولا الاستفسار من العرافين، أو المجوس، أو السحرة (تثنية 13: 1-5 و 18: 9-14). وقد تستخدم كلمة الرؤى بمعنى الحلم (تكوين 46: 2 و أيوب 33: 15 و دانيال 2: 28 و 7: 1)، على أنها غالباً تشير إلى ما يعلن للرائي وهو يقظان (قارن 2 ملوك 6: 17 و أعمال 18: 9 و 23: 11 و 27: 23 و 2 كورنثوس 12: 1 و 2 و 4 لوقا 1: 22 و 1 صموئيل 3: 15).'),
(1232, 'حَلاَوى', 'حلوى من إنتاج فلسطين كان الصوريون يشترونها (حزقيال 27: 17) اللفظ العبري هو يناج والترجمة العربية تتبع الترجوم وتشير بأنه نوع من المربى أو الحلوى، وفي الأكادية يانيجو هو نوع من الكعك.'),
(1233, 'حُلِيّ', 'يزين الشرقيون أنفسهم بزينات متنوعة. ولقد طالما كان الحال كذلك. فالعبرانيون، والمصريون، والمديانيون، والسوريون، كلا الرجال والنساء، كانوا مغرمين بلبس الزينات (تكوين 24: 22 و خروج 3: 22 و 11: 2 و 32: 2 و عدد 31: 50). وكانت النساء تلبس عقوداً من الخرز واللآلئ، وأدوات من الذهب، والفضة، والنحاس (نشيد الأناشيد 1: 10 و 11 و 1 تيموثاوس 2: 9)، والأقراط والخزائم، والأنواط، والقلائد والأطواق، والسلاسل، والمرايا النحاسية، وأساور الذراع والساعد والمعصم، والخواتم، والخلاخيل (تكوين 24: 22 و 47 و 35: 4 وخروج 35: 22 و عدد 31: 15 و اشعياء 3: 18-23). والرجال من كل الطبقات ما عدا الفقراء كانوا يلبسون الخواتم التي هي أختام (تكوين 38: 18)، التي كانت تستخدم في العمل كما للزينة. وكذلك لم يعتبروا الأساور للأذرع كأنها تخّص النساء فقط. لقد لبس شاول، مثل ملوك آشور، سواراً حول الذراع أو المعصم (2 صموئيل 1: 10). وكانت عادة قومية عند الاسماعليين أن يلبس الرجال أقراطاً (قضاة 8: 25 و 26)، وأحياناً كان بعض الرجال من العبرانيين يفعلون هذا (خروج 32: 2). وكان الرجال أصحاب الجاه يلبسون سلسلة ذهبية كعلامة على الوظيفة (تكوين 41: 42 و دانيال 5: 29). وكانت تنزع الزينات في وقت الحزن (خروج 33: 4-6). وقد ذكرت الحلي أول مرة في تكوين 24: 22 عندما قام خادم إبراهيم إلى رفقة الأقراط والأساور. والعذراء ترغب في الحلي (أرميا 2: 32)، وكانت حلي العبرانيين من الأساور والأطواق والأقراط والخزائم (حزقيال 16: 11 و 12). وفي اشعياء 3: 16-25 وصف دقيق لنساء ذلك العصر اللواتي كن يتحلين ويلبسن ثياباً حسب أزياء تلك الأيام. وكان الرسل يحثون النساء المؤمنات بأن يتزّين بالأعمال الصالحة (1 تيموثاوس 2: 10)، وبالروح الوديع الهادئ الذي هو قدام الله كثير الثمن، ولا يعتمدن على الزينة الخارجية (1 بطرس 3: 4).'),
(1235, 'حَمَاة', 'اسم آرامي معناه ((حمى، حصن، قلعة)).مدينة على نهر العاصي، شمال حرمون (يشوع 13: 5) تقع على مسافة نحو 120 ميلاً شمال دمشق. كانت مدينة حثية كما يظهر من عدد كبير من الكتابات الحثية هنأ ملكها، توعي، داود على نصرته على هدد عزر عدوهما المشترك (2 صموئيل 8: 8 و 10 و 1 اخبار 18: 3 و 9 و 10). أخذ سليمان حماة وبنى مدن المخازن في منطقها (2 اخبار 8: 3 و 4). إنما عادت حالاً، إلى سكانها الأصليين، لكن يربعام الثاني، ملك إسرائيل، استولى على دمشق وحماة (2 ملوك 14: 28). نحو هذا الوقت سمى عاموس هذه المدينة حماة العظيمة (عاموس 6: 2). وفي معركة قرقر، تحالفت حماة مع بنهدد الدمشقي وآخاب، ضد شلمناصر الثالث، ملك آشور، لكن الآشوريين هزموها فيما بعد (2 ملوك 18: 34  و 19: 13). وبعد هزيمة الآشوريين للسامرة، انضمت السامرة مع بقية سكان حماة في ثورة على الآشوريين في سنة 720 ق.م. لكن سرجون قمع سريعاً. وقد نقل الآشوريين مستعمرين من حماة، احضروا معهم اشيما، إلى السامرة (2 ملوك 17: 24 و 30)، بينما وضع بعض المسبيين من إسرائيل، كما يبدو، في حماة (اشعياء 11: 11). بعد ذلك أصبح تاريخها مدمجاً في تاريخ آرام أو سوريا، ويظهر أنها قد صارت تابعة لدمشق (أرميا 49: 23). وتنبأ حزقيال أن حماة ستكون ضمن نطاق مملكة الله (حزقيال 47: 16 و 17 و 20 و 48: 1). وكانت حماة معروفة باسم ابيفانيا أثناء عصر السادة اليونانية في أيام انطيوخوس ابيفانيس. ولا تزال المدينة الحديثة تحتفظ بالاسم القديم، حماة.(2) المقاطعة التي تحكمها المدينة، وكانت ربلة إحدى مدنها (2 ملوك 33: 33).مدخل حماة:كان معتبراً الحد الشمالي لإسرائيل (عدد 13: 21 و 34: 18 و 1 ملوك 8: 65). وربما كان لهذا التعبير عند أهل الجنوب معنى خاص ويدل على وادي البقاع الطويل، بين سلسلتي جبال لبنان الغربية والشرقية والذي فيه يمتد الطريق إلى حماة. والبعض يعتبرونه كالممر بين جبال لبنان والنصيرية، ممتداً من حمص إلى طرابلس، وهكذا يربطه إقليم سوريا الداخلي بساحل البحر المتوسط. وهناك رأي آخر بأن الكلمة العبرية المترجمة مدخل، ومعناها في أو عند الدخول، هي اسم علم. وبناء على ذلك يعتقد أن هناك مكاناً اسمه ليبو حماة (لبوة الحديثة)، يقع على بعد 14 ميلاً إلى الشمال الشرقي من بعلبك وهكذا هو يشرف على الأرض المرتفعة بين نهري العاصي والليطاني، وقد زاره الجواسيس (عدد 13: 20). وهو حد فلسطين الشمالي (يشوع 13: 5).'),
(1236, 'حَمَاتِيون', 'سكان حماة (تكوين 10: 18).'),
(1237, 'حَماة صُوبة', 'مملكتا حماة وصوبة المتجاورتان، أو مكان صغير يدعى حماة، ملك لمملكة صوبة آرام أو سوريا. استولى عليها سليمان (2 اخبار 8: 3).'),
(1238, 'حَمْدَان', 'اسم عبري معناه ((سار، بهيج، مشتهى)). وهو حوري، بكر ديشون من نسل سعير (تكوين 36: 26)، ويدعى أيضاً حمران (1 اخبار 1: 41).'),
(1239, 'حِمَار', 'ذكرت في الكتاب المقدس ثلاثة أنواع من هذا الحيوان المعروف:(1) الحمار الوحشي، أو حمار الوحش، ويسمى في العبرية ((عرود))، أي الشارد. وهو موصوف شعرياً في أيوب 39: 5-8، حيث يذكر أيضاً حمار سوريا الوحشي العادي، وهو مذكور أيضاً في دانيال 5: 21. وهو ربما النوع الذي يسمى باللاتينية Asinus orager الموجود في الصحراء وفي الجزيرة العربية، حيث كان يوجد بكثرة، ولكنه الآن نادراً الوجود جداً، وكان يرحل أحياناً إلى حوران والحمار الأليف من سلالته.(2) حمار سوريا الوحشي، وفي العبرية ((فرا)) أي الوثّاب، واسمه باللاتينية Asinus hemippus وهو مذكور في أيوب 24: 5 و 39: 5 و مزمور 104: 11 واشعياء 32: 14 و أرميا 14: 6 وهو أصغر حجماً ممن السابق. ومن المعروف أن قطعاناً ضخمة منه كثيراً ما تدخل جبال ارمينيا في الصيف. وهي موجودة في كل الأوقات في شمال الجزيرة العربية، وما بين النهرين، وسوريا، وأحياناً ما تدخل شمال فلسطين. وهذا هو النوع الذي يظهر رسمه في نقوش نينوى.(3) الحمار الأليف، وفي العبرية ((حامور)) واسمه باللاتينية Equus asinus. وهو متناسل من النوع الأول. وهو عنيد وبليد. لكنه من الناحية الأخرى قوي، ويطعم بسهولة، وصبور، وصفوح. وقد تدجن الحمار من عهد طويل. وكان لإبراهيم حمير (تكوين 12: 16). ركب عليها (تكوين 22: 3). وكذلك كان ليعقوب (تكوين 30: 43). وكانت الحمير تستخدم لحمل الأثقال (تكوين 49: 14 و اشعياء 30: 6). وللحرث وغيره (تثنية 22: 10). وكانت الحمير البيضاء تعتبر مناسبة الأشخاص ذوي المراكز العليا (قضاة 5: 10)، كما لا تزال فلسطين. وكثيراً ما كان لرجل واحد عدد كبير من الحمير يستخدم لها راعياً خاصاً (تكوين 36: 24 و 1 اخبار 27: 30). ويشار بذكر الحمار مع الجفنة إلى الجاه والخصب. والأتان أفضل من الحمار لأنها سلسلة القياد في الركوب ولجودة لبنها للطعام وللأطفال وللمرضى. وقد نُهي عن الحرث على حمار وثور إشارة إلى كراهة الله للاختلاط بين المؤمنين وغير المؤمنين (تثنية 22: 10). وكان أخذ حمار اليتامى إثماً فظيعاً (أيوب 24: 3). والحمار محب لصاحبه (اشعياء 1: 3). ويستدل على شدة في السامرة مدة حصارها من الثمن الغالي الذي كانوا يدفعونه لأجل رأس الحيوان كثير الوجود كالحمار (2 ملوك 6: 25). وعند موت الحمار كاننت تسحب جثته إلى البرية حيث تأكلها الضباع وبنات آوى والكلاب والعقبان، ويعبر عن شدة الإهانة التي يمكن حدوثها للإنسان بتشبيه موته بموت الحمار (أرميا 22: 19 قارن 36: 30). وقد أظهر الرب يسوع مخلصنا بأنه ملك السلام، بل ركب على حمار، لإشارة إلى كونه محباً للسلام، وظهر هذا جلياً في دخوله الانتصاري إلى أورشليم (زكريا 9: 9 و متى 21: 5).'),
(1240, 'حُمَر', 'وهو نوع من القار المعدني شبيه بالقطران الشديد ويتحول إلى زفت إذا ما جمد تماماً وما يستعمل ولا زال يستعمل أيضاً إلى اليوم في طلاء المراكب ويوجد في البحر الميت وبالقرب منه ولذلك فقد دعاه اليونان Asphaltitis أي إسفلت، وكذلك يوجد في مدينة ((حت)) على الفرات وكان يستخدم في بابل عوضاً عن الإسمنت ليَمسك لبنات الطوب الواحدة بالأخرى كما حدث في بناء برج بابل (تك 11: 3). وقد سقط ملكا سدوم وعامورة في حفر من الحمر عند هروبهما (تك 14: 10). وقد طلت أم موسى السفط الذي وضعته فيه بالحمر أو الزفت (خروج 2: 3).'),
(1241, 'يَحْمُور', 'تعريب للكلمة العبرية ((يحمور)). وهو حيوان طاهر حسب الشريعة الطقسية ويستخدم للطعام (تثنية 14: 5). كانوا يقدمونه يومياً على مائدة سليمان (1 ملوك 4: 23). واشتقاقه يبين أن لونه محمر. وبناء على مصادر غريبة، يطرح قرونه كل سنة، وهذه صفة الغزال أو الأيل، واسمه باللاتينية Caperolus capraea. وهو في أوروبا حيوان صغير ارتفاعه نحو قدمين عند الكتف، لكنه في آسيا يبلغ إلى حجم أكبر. وفي الصيف يكون لونه بنياً محمراً قاتماً، أما في الشتاء فيكون رمادياً مصفراً. وله رقعة بيضاء على كفله. وطول قرنيه نحو قدم وفيها ثلاث شعب أو نقط. وهو يرى على مساحة كبيرة في أوروبا وآسيا. وهو موجود في فلسطين على جبل الكرمل وجبل لبنان.لكن الكثيرين يقولون أن اليحمور هو التيتل أو بقر الوحش، وهو بحجم الوعل الكبير، وله رأس طويل وجبهة ضيقة، وشعر محمر أو بني شاحب. ولحمه لذيذ جداً.'),
(1242, 'حَمْرَان', 'اسم عبري معناه ((أحمر)) رجل من نسل سعير (1 اخبار 1: 41). انظر ((حمدان)).'),
(1243, 'حِمِّص', 'نوع معروف من النبات أو بذوره التي تؤكل، وهي مغذية، مثل البسلة والفول، وربما كان من القطاني الذي أراد دانيال ورفقاؤه أن يتغذوا به (دانيال 1: 12 و 16). والحمص المشوي مذكور في 2 صموئيل 17: 28.'),
(1244, 'حُمْطَة', 'اسم عبري معناه ((مكان العظاية)) أو ربما ((قلعة)) وهي مدينة في تلال يهوذا مذكورة في حبرون (يشوع 15: 54).'),
(1245, 'حَمَامَة', 'طائر (مزمور 55: 6) له صوت حزين (اشعياء 38: 14)، له طبع لطيف ودود (نشيد الأناشيد 2: 14 و 5 و 6: 9). لكنه ليس كثير الحكمة (هوشع 7: 11). وهو هياب خجول، عندما يخوف يرتجف. وإذا كان من النوع فإنه أحياناً يتردد بكثرة على الوديان (حزقيال 7: 16)، ويبني عشه في جانب الجحور أو الشقوق (أرميا 48: 28). وإذا كان أليفاً يطير عندما ينزعج إلى النوافذ أو الأبراج (اشعياء 60: 8 وقارن تكوين 8: 8-12). وقد أشار إليه الرب يسوع وذكره كناية عن بساطته وبرآته وعدم إيذائه (متى 10: 16). وكان يشتري ويباع داخل ساحة الهيكل (متى 21: 12 ومرقس 11: 15 و يوحنا 2: 14)، لأنه كان يستخدم في الذبائح (تكوين 15: 9 و لاويين 12: 6-8 و مرقس 11: 15 و لوقا 2: 24 و يوحنا 1: 32). والحمامة رمز للروح القدس (متى 3: 16 و مرقس 1: 10 و لوقا 3: 22 و يوحنا 1: 32). والحمامة ما هي إلا اسم آخر لليمامة، مع أن اليمامة أصغر. واشتهر وادي الحمام قرب قرية المجدل في الجليل بكثرة حمامه ويمامه.والحمامة نوع من الطيور، يذكر منه بعضهم أربع فصائل موجود في فلسطين: الحمام المطوق أو حمام الغاب Columba Palvmbus، والقمري، Cobumba oenas وحمام الصخر Colmba livia، وحمام الصخر الرمادي المؤخر Columba schimpevi. والحمام المطوق يزور فلسطين في أسراب ضخمة في الربيع والخريف أثناء رحلاته السنوية. والقمري أو الحمام البري موجود على الغالب في شرق الأردن، أو في وادي هذا النهر، وحمام الصخر يكثر على الساحل وفي الأراضي المرتفعة غرب الأردن. وحمام الصخر الرمادي المؤخرة يكثر بوفرة كبيرة في داخل البلاد وفي وادي الأردن، ويلجأ إلى الكهوف والمغاور والشقوق. وهو نوع الموصوف في نشيد الأنشاد 2: 14 و أرميا 48: 28.ويراد بالقول ((عيناك حمامتان)) الوارد في نشيد الأناشيد 4: 1 رقة تعبير العيون عن العواطف (قارن نشيد الأناشيد 5: 12). وشبه رجوع شعب الله من السبي إلى أرضهم ((كحمامة من أرض آشور)) (هوشع 11: 11). وتمنى داود أن يطير من همومه كالحمامة (مزمور 55: 6-8). وأرسل نوح حمامة من الفلك ليعرف إذا ما كانت قد ظهرت اليابسة أم لا (تكوين 8: 6-12). وأشار الأنبياء إلى هدر الحمام (اشعياء 38: 14 و 59: 11 و حزقيال 7: 16 و ناحوم 2: 7).أما الآية الواردة في مزمور 68: 13 ففيها إشارة إلى نوع من الحمام في دمشق لون جناحيه كالذهب. ويقصد المرنم بالآية أن يشبه الشعب في زمن الرخاء بأجمل الطيور الطاهرة بالمقارنة مع حالهم في زمن البؤس. وفي اشعياء 60: 8 إشارة إلى أسراب الحمام الراجعة إلى بيوتها كالغيوم. انظر ((يمامة)).زبل الحمام:مادة ارتفع سعرها في زمن المجاعة أثناء حصار السامرة الذي قام به بنهدد (2 ملوك 6: 25). وكانت تؤكل أثناء حصار أورشليم.وربما هو فرث الحمام استخدم لإخصاب البقول لإمداد أهل السامرة المحاصرين، أو أكلة الجائعون، وربما هو نوع من البقول شبيه بالعدس أو الحمص كان يسميه العبرانيون، وكذلك كانوا يسمون المكيال الذي يكال به بهذا الاسم.'),
(1246, 'الحمامة البكماء بين الغرباء', 'جزء من عنوان مزمور 56، والأرجح أنه اسم اللحن الذي كان يرنم عليه هذا المزمور.'),
(1247, 'حَمَّام', 'في جو الشرق الحار يجد الناس أنه من الضروري الأكثار من الاستحمام وقد استحمت ابنة فرعون في النيل (خروج 2: 5). وكان المصريون يلبسون ثياباً من الكتان، تغسل باستمرار، وكان كهنتهم يستحمون في ماء بارد مرتين في النهار ومرتين في الليل، والمصريون والعبرانيون والسوريون كانوا يغسلون تراب الطريق عن أقدامهم عندما كانوا ينزلون فيبيت بعد رحلة (تكوين 18: 4 و 19: 2 و 24: 32 و 43: 24 و يوحنا 13: 10). وإذا مس العبرانيون نجاسة، كانوا يغسلون أجسادهم وثيابهم (لاويين 14: 8 و 15: 5 و 17: 15 و عدد 19: 7 و 8). أو في نهر (2 ملوك 5: 10)، أو في بيت، في الدار أو في الحديقة (2 صموئيل 11: 2 و 4). وكانوا يستحمون ويدهنون أنفسهم ويلبسون أبهى ملابس العيد وثياب فاخرة حالما ينزعون ثياب الحزن (خروج 40: 12 و 13 و راعوث 3: 3 و 2 صموئيل 12: 20 و متى 6: 17). وكان الكهنة يغسلون أيديهم وأقدامهم قبل دخول المكان المقدس أو تقديم محرقة على المذبح (خروج 30: 19-21). وكان رئيس الكهنة يستحم عند تنصيبه وفي الكفّارة قبل كل عمل من أعمال التكفير (لاويين 6: 8 و 16: 4 و 24). وفي زمن المسيح، كان اليهود يغسلون أيديهم قبل الأكل، ويغسلون أو يرشون أنفسهم حال عودتهم من السوق (مرقس 7: 3 و 4). في هذا الوقت أيضاً، وعندما دخلت عادات اليونان والرومان إلى اليهود، أقيمت الحمامات العامة. والينابيع الحارة في طبرية، وجدارة أو الحمّة، وكالليروي، قرب الشاطئ الشرقي للبحر الميت، كان يؤمها الناس لأجل الصحة. وكانت برك الماء المتصلة بالقصر في أريحا تستخدم للاستحمام والسباحة.'),
(1248, 'حَمّة', 'اسم عبري معناه ((ينبوع ساخن)).(1) مدينة مسورة لنفتالي (يشوع 19: 35). ربما هي نفسها حمّوت دور وحمّون، والتي أعطيت اللاويين (يشوع 21: 32 و 1 اخبار 6: 76)، وهي عمواس، التي هي القرية فيها حمامات ساخنة على مسافة صغيرة من طبرية، وتسمّى الآن الحمة وهي قرب الشاطئ الغربي من بحر الجليل نحو ميل وربع جنوب طبري. والماء كبريتي وطبي.(2) مؤسس بيت ركاب، أسرة من القينين (1 اخبار 2: 55).'),
(1249, 'حَمّوت دور', 'اسم عبري معناه ((ينابيع دور الساخنة)). مدينة لاوية ومدينة ملجأ لبني نفتالي (يشوع 21: 32). انظر ((حمّة وحمّون)).'),
(1250, 'حَمّون', 'اسم عبري معناه ((ينابيع حارّة)).(1) مكان في تخم بني أشير قرب صيدون (يشوع 19: 28). ويظن أنها عين حامول التي تبعد 10 أميال جنوب صور.(2) مدينة لاوية لبني نفتالي (1 اخبار 6: 76). والأرجح أنها حمّة وحمّوت دور وتدعى الآن. حمامات الحمّة والأرض التي حولها تدعى أرض الحمّة. انظر ((حمّة وحمّوت دور)).'),
(1251, 'حَمُوئيل', 'اسم عبري معناه ((حمية الله، غضب الله)). ابن مشماع، ربما من أسرة شاؤول (1 اخبار 4: 26).'),
(1252, 'حَمُور', 'اسم كنعاني معناه ((حمار)). أمير شكيم (تكوين 34: 20 و يشوع 24: 32 و قضاة 9: 27)، حوّي (تكوين 24: 2). ابنه شكيم أفسد دينه، وكلا الأب والابن سقطا فريسة لانتقام أخويهما شمعون ولاوي (تكوين 34: 1-31).'),
(1253, 'حَمُوطَل', 'اسم عبري معناه (حمو الطل, نسيب الندى, و ربما, الحمو هو طل) .وهي ابنة ارميا من لبنة و زوجة الملك يوشيا,و أم الملكين يهو آحاز و صدقيا(2ملوك23: 31و24 : 18 و ارميا 1: 52) , و سميت حمَيطل و حميطل.'),
(1254, 'حَنَان', 'انظر(حانان) (6) .'),
(1255, 'حَنَاني', 'اسم عبري معناه(منعم, كريم, رحيم, أو اختصار حننيا) .(1) ابن هيمان و رئيس الفرقة الثامنة عشرة من الفرق الاربع و العشرين من العازفين الذين عينّهم داود للمقدس(1 اخبار4: 25و 25) .(2) ابو النبي ياهو(1 ملوك1: 16) , و كان هو نفسه رائياً. و بخ آسا, و وضع في السجن وفقاً لامر الملك(2 اخبار7: 16ـ 10) .(3) اخو نحميا الذي اتى اليه باخبار اورشليم (نحميا2: 1) . و هو و حننيا رئيس القصر اقيما على المدينة فيما بعد  (نحميا2: 7) .(4) كاهن, ابن امَير, اقنعه عزرا بطرد زوجته الغريبة (عزرا20: 10) .(5) لاوي عزف على آلة في تدشين سور اورشليم في عصر نحميا (نحميا36: 12) .'),
(1256, 'حَنانيا', 'اسم عبري معناه (يهوه تحنن) و هذا الاسم هو نفس كلمة حننيا او حننيّا في الاصل العربي.(1) احد الذين انضموا الى الكنيسة في اورشليم في اول عهد المسيحية و انضمت معه زوجته سفيرة, و قد باع قطعة ارض, و اخذ جزءاً من الثمن, و وضعه عند اقدام الرسل (اعمال 1: 5ـ 10) . كان كل شيء مشتركاً عند الجماعة المسيحية. (اذ لم يكن فيهم احد محتاجاً لان كل الذين كانوا اصحاب حقول او بيوت كانوا يبيعونها و يأتون بأثمان المبيعات و يضعونها عند ارجل الرسل. فكان يوزع على كل واحد كما يكون له احتياج) . (اعمال34:   4 و35) . و لم يكن احد ملزما بأن يفعل هذا (اعمال 4: 5) . و الغرض المقصود لم يتطلب ان كل مقتني يباع, لكنه مع ذلك كانت المقتنيات تباع عندما تتطلب الحاجة. احضر حنانيا جزءاً من الثمن, و وضعه عند ارجل الرسل متظاهرا بأنه اكل. فوبخه بطرس لانه كذب على الروح القدس, فسقط و مات في الحال, كما حدث لامرأته سفيرة, التي جاءت بعد ثلاث ساعات, واذ لم تكن تدري بما قد جرى, كرّرت كذب زوجها, فوقع لها المصير نفسه الذي تنبأ به بطرس.(2) مسيحي في دمشق,اخبر في رؤيا بتجديد شاول, و ارسل اليه لكي يرد له بصره و يدخله الى الكنيسة المسيحية بواسطة المعمودية (اعمال10: 9ـ 18) .(3) رئيس كهنة, عيّنه هيرودس. ملك خالكيس, نحو عام 48م. و بعد اربع سنوات ارسله والي سوريا الى روما ليستجوَبعن هجوم اليهود العنيف على السامرين, لكن اطلق سراحه لنفوذ اغريباس, و رجع الى اورشليم, و كان يوناثان, و هو رئيس كهنة سابق, مرتبطاً به سياسياً. و في سنة 58م. استدعى بولس امام حنانيا لاستجوابه, و ظهر ضد بولس امام فيلكس الوالي (اعمال2: 23 و1: 24) . و في ذلك الوقت قُتِل زميله يوناثان. و نحو سنة 59م. قرب ختام حكم فيلكس, خلع اغريباس حنانيا نفسه من وظيفته. و يظهر انه قام على تل جنوب غربي اورشليم, في المدينة العليا, قرب قصر الحشمونيين. و قتل سنة 67م.'),
(1257, 'الحوانيت الثلاثة', 'محط صغير, على طريق أبيوس على مسافة نحو10 أميال من فورن أبيوس, و30 ميلاً من روما حيث قابل عدد من المسيحيين بولس و هو في طريقه إلى روما (أعمال15: 28) .'),
(1258, 'حَنْث', 'انظر(( قسم)).'),
(1259, 'حِنْطة', 'أهم جميع الحبوب و تزرع بكثرة في أرض مابين النهرين (تك14: 30) و في مصر (حز32: 9) و في فلسطين (خر22: 34 و تثنية 8: 8 وقض11: 6) و في غير هذه الأماكن.و ترجع ممارسة زراعتها إلى عصور مبكرة في التاريخ.و كانت الحنطة تزرع في فلسطين في شهر تشرين الثاني(نوفمبر) أو في شهركانون الأول (ديسمبر) بعد ابتداء فصل الأمطار و كانت تحصد إما في نيسان(أبريل) أو أيار (مايو) أو حزيران (يونيو) بحسب الموقع الذي زرعت فيه و التربة و حالة الطقس في ذلك الحين. و كان خبز العبرانيين يصنع عادة من دقيق الحنطة (خر 2: 29) . و كانوا يشوون سنابل الحنطة و يفركونها و يأكلون القمح المشوي(لا14: 2 و16 و راعوث14: 2) . و قد ورد في الكتاب المقدس أنهم كانوا يأكلون الحنطة الجديدة على هذه الكيفية (لا14: 23 و 2 ملوك42: 4) .و كانت مصر تعتبر مصدر غلال إقليم البحر الأبيض المتوسط بجملته و كانت تشحن منها كميات هائلة من الحنطة كل سنة من الإسكندرية إلى روما (أعمال6: 27 و38) و قد ذكر المؤرخون الأقدمون و منهم بليني, مثلاً: أن أفضل أنواع الحنطة كانت تزرع في مصر. و الحنطة المصرية كثيرة السنابل حيث يطلع على ساق واحد عدد كبير من السنابل, و قد ذكرت في تك22: 41 و ترى نقوش تبين سنابل الحنطة في العصور القديمة تظهر في الآثار المصرية. و لا تزال هذه الأنواع من الحنطة تزر في مصر إلى اليوم. أما الحنطة التي تزرع في فلسطين فهي من النوع العادي ذي السنبلة الواحدة على الأغلب.'),
(1260, 'تَحْنِيط', 'قلما حنط العبرانيين موتاهم (تكوين2: 50و 26 و قارن2 أخبار14: 16 و يوحنا 39: 19), أما المصريون فكانوا يمارسونه منذ أقدم العصور. و كان المحنطون طائفة كبيرة العدد, و كانوا يسكنون في المدافن, و كان المحنطون و الأطباء يحسبون من ضمن الكهنة. و كانوا ينقسمون الى ثلاث فرق: الفرقة الأولى كانت تقوم بفتح الجثة, و الثانية كانت تهتم بالأطياب و العطور و العقاقير, و الثالثة كانت تقود الطقوس الدينية عند وضع الجثة في المدفن. و في عصر الأسرة الثامنة عشرة وصلوا بفنهم إلى حد الكمال الأقصى. فكان المخ يسحب من الأنف بخطاف حديدي, و يملأ مكانه بالتوابل. و كانت الأحشاء تنزع, و تجويف البطن يغسل بواسطة حقنة بخمر البلح, ثم يملأ بمسحوق من المر و القثاء الهندي, و القرفة, و التوابل الأخرى. ثم تنقع الجثة كلها في النطرون (محلول ملح البارود) و تترك فيه نحو 70 يوماً. ثم تلف في لفافات أو عصائب عرضها 3 أو 4 بوصات فقط, لكنها كانت تصل في الطول إلى 700, أو ربما الى 1000 ياردة. و كان يستخدم الصمغ العربي لحفظ اللفافات في مكانها, وأخيراً بعد أن تحنط الجثة, كانت توضع في تابوت من خشب الجميز أو الحجر أو الفخار, مصنوع على شكل إنسان, و منقوش و ملون ليمثل الشخص الراحل. و كثيراً ما كانت توضع في صندوق آخر خارجي من الخشب أو تابوت حجري. و الطريقة الأقل نفقة لم تكن تتطلب فتح الجثة, بل كانت تذاب الأعشاب بواسطة حقنها بزيت الأرز. و في حالة الفقراء, كان التجويف البطني يغسل فقط و ينقع في النطرون. و بحسب المؤرخ ديودوروس سيكولوس, كان دفن إنسان غني يكلف نحو خمسمائة جنيهاً, و كان هناك نوع من التحنيط أرخص جداً اعامة الشعب. و توقف التحنيط نحو 500م. و يوجد كثير من الموميات في المتاحف. و عندما تنزع اللفافات عن إحداها و تتعرض للهواء تتفتت.و حنط بنو إسرائيل جثتي يعقوب و يوسف ليحافظوا عليهما لينقلوهما إلى أرض كنعان (تك2: 50 و26) .ووضع آسا في سرير ملآن بالأطياب و العطور (2 أخبار14: 16) .و أعدَّت النساء حنوطاً, كما أعد نيقوديموس مراً و عوداً لتكفين جسد المسيح. انظر كلمتي ((  دفن, و مصر)).'),
(1261, 'حَنَمْئيل', 'اسم عبري ربما كان معناه(( الله قد تحنّن)). هو ابن شلّوم, و ابن عم النبي ارميا (ارميا 7: 32) .'),
(1262, 'حَنّاتون', 'اسم عبري معناه(( المنعم عليه)) مدينة تقع على حدود زبولون  (يشوع14: 19) , على الطريق من مجدّو إلى عكّا, و ربما تقع على تل الهربج في وادي الملك. أو ربما هي تل البُديوية.'),
(1263, 'حَنَنْئيل', 'اسم عبري معناه((الله قد أنعم , قد تحنن)) قارن في العربية: حنا الله, و هو برج في أورشليم(نحميا1: 3 و 39: 12 وارميا38: 31 وزكريا10: 14) . قرب باب الضأن و برج المئة. انظر(( أورشليم)).'),
(1265, 'حَنَّة', 'اسم عبري معناه (( حنان, حنون, نعمة)).(1) إحدى زوجتي القانة كانت محبوبة لدى زوجها, و لذلك تعرضت لمضايقات ضرتها. و لما كانت عاقراً نذرت أنها إذا ولدت طفلاً ذكراً تخصصه لخدمة الرب. و أجاب الله صلاتها الحارة, و أعطاها سؤالها, و أصبحت أم صموئيل النبي, و نفذت نذرها (1 صموئيل1) . و أغنية انتصارها شعرية رائعة, و ربما كانت في فكر مريم العذراء عندما عبّرت عن شكرها بأنغام شعرية مماثلة لدى سماعها أنها ستلد ابناً اسمه يسوع و يدعى ابن الله (1 صموئيل1: 2 ـ 10 و لوقا 46: 1 ـ 55) .(2) بنت فنوئيل من سبط اشير, نبية, أرملة, دامت حياتها الزوجية 7 سنوات فقط. و في سن84 كانت لا تفارق الهيكل عابدة بأصوام و طلبات ليلاً و نهاراً, و كانت هناك عندما أُحضر الطفل ليُكرس. و سمعت ما تنبأ به عنه سمعان الشيخ عندما أخذه على ذراعيه و بارك الرب و طلب إليه أن يطلقه بسلام بعد ما رأى المخلّص بعينيه. و هذه النبية عرفت الطفل القدوس و أعلنت أنه هو المسيا (المسيح المنتظر)  (لوقا 36: 2ـ 38) .'),
(1266, 'حَنَنْيا', 'اسم عبري معناه (( يهوه قد أنعم, قد تحنن)).(1) بنيامني, ابن شاشق(1 أخبار24: 8) .(2)  ابن هيمان و رئيس الفرقة 18 من الفرق الـ 24 من العازفين التي كوَّنها داود للمقدس (1 أخبار4: 25 و 23) .(3) واحد من رؤساء جيش الملك عزيا (2 أخبار 11: 26) .(4) أبو الأمير صدقيا, المعاصر لأرميا (أرميا 12: 36) .(5) ابن عزور من جبعون. في السنة 4 من ملك صدقيا تنبأ برجوع المسببين بعد سنتين من السبي. لكن أرميا كان قد أعطى نبوة مختلفة. و كقصاص للنبي الكذّاب حُكِمَ عليه بالموت, ومات بعد ذلك بشهرين (أرميا28) .(6) جد, أو سلف بعيد, ليرئيا, ناظر الحراس الذي قبض على أرميا بتهمة أنه جاسوس للكلدانيين و سيهرب إليهم (أرميا 13: 37ـ 15) .(7) الاسم العبري للأمير الأسير, أحد الفتية الثلاثة رفقاء دانيال في السبي, الذي سماه الكلدانيون شدرخ (دانيال6: 1 و 7) .(8) ابن زربابل, و أبو فلطيا و يشعيا (1 أخبار19: 3 و 21) .(9) ابن باباي, أقنعه عزرا بأن يطرد زوجته الغريبة (عزرا28: 10) .(10) عطَار (صيدلي) ساعد في ترميم سور أورشليم (نحميا8: 3) .(11) كاهن ضُرِبَ بالبوق عند تدشين السور (نحميا41: 12) .(12) رئيس للشعب, ختم العهد مع نحميا  (نحميا23: 10) .(13) رئيس القصر و حاكم شريك مع حناني, أخي نحميا, على أورشليم (نحميا2: 7) .(14) كاهن, رئيس بيت رب أرميا في أيام رئيس الكهنة يوياقيم, بعد السبي بجيل (نحميا12: 12) .(15) ابن شلميا, عمل في ترميم السور (نحميا30,3) .'),
(1267, 'حَنُوك', 'اسم عبري معناه (( دارس, مدرب, مكرِّس)).(1 و 2) ابن قايين و سمّاها باسمه (تكوين17: 4) .(3) ابن مديان, و من نسل ابراهيم من قطورة (تكوين4: 25 و 1 أخبار33: 1) .(4) بكر رأوبين, و مؤسس عشيرة سبطية, و هي عشيرة الحنوكيين (تكوين9: 46 وخروج14: 6 و عدد5: 26 و أخبار3: 5) .'),
(1268, 'حَنِيئِيل', 'اسم عبري معناه (( نعمة الله, حنان الله)).(1) رئيس من سبط منسّى, عندما كان العبرانيون على وشك أن يدخلوا كنعان, تعيَّن في لجنة لتقسيم الأرض (عدد23: 34) .(2) اشيري، ابن عُلاَّ (1 اخبار 7: 39) .'),
(1269, 'حُوباب', 'اسم مدياني معناه (( محبوب)). و هو حمو موسى, بحسب النسخة العبرانية الماسوريتية (قضاة11: 4) . بينما حمو موسى هو رعوئيل, أو يثرون (خروج1: 3 و 18: 4 و 1: 18 و 2 و 5 و 6) . و يقرر بالتحديد أن حوباب هو ابن رعوئيل  (عدد29: 10) , و إذا إلى الحروف الساكنة العبرية فقط, يمكن جداً ترجمة الكلمات العبرية إلى: (( حوباب صهر موسى))  (قضاة16: 1 و 11: 4) . و حمو موسى, رعوئيل أو يثرون, زار موسى في المحلة في رفيديم و رجع إلى أرضه  (خروج 18: 1 و 5 و 27 ) . و بعد سنة عندما كان العبرانيون على وشك أن يتقدموا من سيناء, حث موسى حوباب, ابن رعوئيل, على أن يرافقهم ويساعدهم بمعرفته للصحراء. ورافق حوباب أخيراً(عدد 29: 10 وقضاة 16: 1 و11: 4) .ولذلك يظهر أن حوباب ليس هو شخص رعوئيل نفسه. وبعد غزو كنعان, سكنت أسرته في يهوذا, جنوب عراد, وكانت لا تزال هناك في عصر شاول وداود (قضاة 16: 1 و1صموئيل 6: 15 و27: 10 و11: 4) , أسرة من الميدانيين.'),
(1270, 'حُوبَة', 'اسم ارامي ربما كان معناه ((قصب, حلفاء)). مدينة شمال دمشق, وهي أقصى حد وصل اليه ابراهيم في مطاردة ملوك الشرق المتحالفين المنهزمين(تكوين 15: 14) . ويذكر بعضهم مكاناً بهذا الاسم بين تدمر وحمص على مسافة نحو 50 ميلاً شمالي دمشق.'),
(1271, 'حوثام', 'اسم عبري معناه ((ختم, خاتم)).(1) اشيري, ابن حابر, من سلالة بريعة (1 أخبار 32: 7) .(2) عروعيري, كان اثنان من أبنائه من أبطال داود (1 أخبار 44: 11) .'),
(1272, 'حور', 'يرجّح أن هذا اسم مصري ماخوذ من اسم الاله ((حورس)).(1) رجل من يهوذا, من سلالة حصرون, من بيت كالب (1 أخبار 18: 2 و19) . جد بصلئيل (1 أخبار 20: 2 وخروج 1: 31 و2) . مع هرون دعم ذراعي موسى أثناء المعركة مع العمالقة (خروج 10: 17-12) . كان مشتركاً مع هرون في حكم اسرائيل بينما كان موسى غائباً في جبل سينا(خروج 14: 24) . ويقول يوسفيوس أن حور كان زوج مريم أخت موسى.(2) واحد من 5 ملوك مديان ذبحهم موسى(عدد 8: 31 ويشوع 21: 13) .(3) أبو وكيل سليمان في افرايم(1 ملوك 8: 4) .(4) أبو رفايا الذي عمل في ترميم سور أورشليم(نحميا 9: 3) .'),
(1273, 'حُورام', 'اسم فينيقي وعبري معناه ((الأخ رفيع)).(1) ملك صور(2 أخبار 3: 2) . انظر ((حيرام)).(2) صانع ماهر صوري(2 أخبار 11: 4 و16) . انظر ((حيرام)).(3) بنياميني, ربما ابن بالع (1 أخبار 5: 8) . انظر ((حوفام)).'),
(1274, 'حُوران', 'اسم سامي ربما كان معناه ((ارض سوداء, قارن في العربية حوَرَ واسود)) وهي منطقة أحجار بركانية سوداء تقع جنوب دمشق ومتاخمة لجلعاد(حزقيال 16: 47 و18) . وكانت في العصر اليوناني الروماني تطلق على اقليم صغير وكانت معروفة حينئذ باسم اورانيتيس, وكانت واحدة من اربع دوائر, تراخونيتيس في الشمال, وجولونيتيس, وعلى مايظهر باتانيا نحو الشمال الغربي. وهكذا كانت تتكون من السهل المنبسط بين جولونيتيس وجبل حوران الحالي, وربما تشمل الأخير. ونحو سنة 30 ق. م. وهب أوغسطس ليهيرودس الكبير اورانيتيس مع تراخونيتيس وباتانيا. وعندما انقسمت مملكة هيرودس كونت هذه المقاطعات القسم الأكبر من ربع فيلبس ابنه(لوقا 1: 3) . وكان سطحها منبسطا ًتتخلله صخور وروابي بركانية وكانت تربتها خصبة الى درجة أن حوران أصبحت مخزن غلال للدائرة كلها. وتقوم في حدودها مدن وقرى كثيرة مهجورة في الغالب, وبعضها ((مدن باشان الجبّارة)) الصخرية. انظر ((باشان)).'),
(1275, 'حُوراي', 'اسم عبري لا يعرف معناه على وجه التحقيق وهو أحد أبطال داود من أودية جاعش (1 أخبار 32: 11) . وقد سمّى هداي في 2 عموئيل 30: 23.'),
(1276, 'حُور الجدجاد', 'اسم عبري معناه ((كهف جدجاد)) محط لبني اسرائيل في البرية(عدد 32: 33) . وهي الجدجود(تثنية 6: 10 و7 وقارن عدد 31: 33-33) . ربما تقع على وادي غدغودة, أو غداغد, التابع لوادي جيرافي, شمال كونتيلة الجيرافي, شمالاً الى الشمال الغربي من خليج العقبة.'),
(1277, 'حُوروُنايِم', 'اسم موآبي معناه ((كهفان, وهدتان)) مدينة موآبية على سفح أُحدُود. ويظهر أنها غير بعيدة من صوعر (أشعياء5: 15 وأرميا 3: 48 و5 و34, انظر ((الحجر الموآبي))) . لا شك أنها مدينة اورناي, التي أخذها الإسكندر يانّاوس من العرب, وردها ابنه هيركانوس الى الحارث ربما هي العراق, التي تقع على نحو 1640 قدم تحت مستوى الهضبة الموآبية, والحافلة بالينابيع, والحدائق, والكهوف الكثيرة.'),
(1278, 'حُوروُني', 'من سكان حورونايم, أو ربما على الأرجح, من بيت حورون, سنبلط عدو نحميا الذي قاومه في ترميم أورشليم (نحميا 10: 2 و19 و28: 13) .'),
(1279, 'حُوري', 'اسم نسبة ربما كان معناه ((واح من الحوريين)).(1) ابن لوطان وابو عشيرة من بني سعير(تكوين 22: 36 و30 و1 أخبار 39: 1) .(2) ابو الجاسوس, شافاط, من بني شمعون(عدد 5: 13) .'),
(1280, '(3) حُوري', 'اسم عبري يطلق على فرد من عشيرة حور رجل من بني جاد(1 أخبار 14: 5) .'),
(1281, 'حُوريب', 'انظر ((سيناء)).'),
(1282, 'حُوريم', 'اسم عبري معناه ((منعزل, مقدس)) وهو اسم مدينة حصينة في نفتالي(يشوع 38: 19) وربما موقعها الآن خربة قتامون باقرب من جبل حرمون في الجليل.'),
(1284, 'حُوريُون', 'سكان جبل سعير الأصليون ولذلك يدعون بني سعير(تكوين 20: 36 و21) هزمهم كدر لعومر وحلفاؤه(تكوين 6: 14) . وكان يحكمهم امراء(29: 36 و30) . ثم أبادهم فيما بعد نسل عيسى وسكنوا مكانهم(تثنية 12: 2 و22) .وكان الإعتقاد سابقاً أن الكلمة مشتقة من كلمة عبرية((حور)) بمعنى شق, أو((كهف)) وفسر الاسم على أنه يعني سكان الكهوف إنما هذا الإشتقاق المتداول لا يقابل بعد بقبول عام منذ اكتشاف الحوريين(الخوريين) كعنصر سلالي في الشرق الأدنى. وكان الحوريون شعباً غير سامي من الجبال, هاجر بعد سنة 2000 ق. م. إلى شمالي وشمال شرقي مابين النهرين. وانتشروا بعد ذلك في أراضي ما بين النهرين وسوريا المنخفضة الخصبة, ووصلوا فعلاً إلى فلسطين وحدود مصر. وقد سبقوا العبرانيين في فلسطين, وكان الفرات الأوسط أحد مراكز ثقافتهم وكانت مملكة ميتانو أو هانيجالبات على الفرات الأعلى حوريّة حقاً, مع أن حكامها كانوا أولاً آريين, وفيما بعد حثيين. والثقافة الآشورية المبكرة ورثت الثقافة الحورية وخلفتها. وأباد الآشوريين النوزو الحوريين في نحو عام 1400 ق. م.'),
(1285, 'حُوسَة', 'اسم عبري معناه ((ملجأ)).(1) لاوي, من بني مراري, أقامه داود بواباً على باب شلكّة (1 أخبار 38: 16 و10: 26 و16) .'),
(1286, 'حُوشَة', 'اسم عبري معناه ((عَجَلة, انفعال)). مدينة ليهوذا (1 أخبار 4: 4 و11: 27) , ومن المحتمل أنها قرية حوسان, جنوب غربي بيت لحم.'),
(1287, 'حُوشاتي', 'ساكن حوشة, لقب سبكاي, وربما هو مبوناي, من أبطال داود (2 صم 27: 23 و 1 أخبار 29: 11 و 11: 27) . ويدعى أيضاً حوشي في 2 صم 18: 21.'),
(1288, 'حُوشام', 'اسم عبري معناه ((عجلة, انفعال)). رجل من بلاد التيمانيين, خاف يوباب كملك على ادوم (تكوين 34: 36 و35 و1 أخبار 45: 1 و46) .'),
(1289, 'حُوشاي', 'اسم عبري معناه ((متسرع, سريع)). ويلقب بالأركي, وهو أحد مستشاري داود الاثنين الرئيسين. بقي أميناً لملكه أثناء تمرد ابشالوم, وبالفعل هو الذي أحبط مشورة اخيتوفل (2 صموئيل 32: 15-37 و5: 17-16) . انظر ((اخيتوفل)) ((وبعنا)).'),
(1290, 'حُوشيم', 'اسم عبري معناه ((عجلة, انفعال)).(1) ابن دان (تكوين 23: 46) . ودعي أيضا ًشوحام (عدد 42: 26) .(2) بنياميني, رئيس عشيرة, ابن احير(1 أخبار12: 7) .(3) واحدة من ثلاث نساء شحرايم البنياميني (1 أخبار 8: 8 و11) .'),
(1291, 'حُوشي', 'انظر ((حوشاتي)).'),
(1292, 'حُوصَة', 'اسم عبري معناه ((ملجا)). قرية على حدود اشير, ويظهر أنها جنوب صور, وهي المدينة الثانية على الشاطئ (يشوع29: 19) . ربما كانت هي باليتايروس, أو صور على الأرض الرئيسية. وبعضهم يقول أنها (العزيّة) التي لاتزال موجودة.'),
(1293, 'حَائط السَطح', '(تثنية 8: 22) حاجز كان يبنى حسب أمر الشريعة حول سطح البيت ليمنع سقوط الناس.'),
(1294, 'حائط السياج المتوسط', '(افسس 14: 2) . حاجز في دار الهيكل, كان يفصل بين دار اليهود ودار الأمم, ويرمز به الى كل ما يفصل اليهود عن الأمم ويفضلهم عليهم سواء أكان من جهة نواميسهم أم عاداتهم. انظر((هيكل)). وقد وجد لوح من هذا الحائط, يرجع الى عهد الرسول بولس وقد كتب عليه تحذير بأن كل أممي يتجاوز هذا الحائط يقتل.'),
(1295, 'حُوفام', 'اسم عبري لا يعرف معناه وهو ابن, أو حفيد بعيد لبنيامين ومؤسس عشيرة في ذلك السبط (عدد 39: 26) . دعي أيضاً حفّيم (تكوين21: 46 وقارن 1 أخبار 12: 7 و15) , وربما حورام (1 أخبار 5: 8) .'),
(1296, 'حُوفاميون', 'نسل حوفام (عدد 39: 26) .'),
(1297, 'حُول', 'اسم ارامي معناه ((دائرة, دورة)). الإبن الثاني لأرام(تكوين23: 10 و1 أخبار 17: 1) . وكمنطقة جغرافية يمكن تحقيق الاسم بأنه حولية التي يذكرها اشورنازربال في صلتها بجبل ماسيوس.'),
(1298, 'حُولون', 'اسم عبري ربما كان معناه ((رملية)) وهي مدينة في جبال يهوذا (يشوع 51: 15) , اعطيت بضواحيها أو مسارحها للكهنة (يشوع 15: 21) . وقد اعطيت حيلين أيضاً (1 أخبار 58: 6) ولا يعرف مكانها اليوم.(2) مدينة موآبية (ارميا 21: 48) , ربما حورون.'),
(1299, 'حُومَر', '(هوشع 2: 3) . انظر ((مكاييل وموازين)).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1300, 'حَوَاء', 'اسم عبري معناه ((حياة)) الاسم الذي اعطاه آدم للمرأة الأولى لأنها أم كل حي (تكوين 20: 3) . حالاً بعد خلقه, أُحضرت إليه لكي تكون معيناً نظيره. وقد تكونت حوّاء من جنب آدم, وهذا يشير إلى وحدة المرأة مع الرجل في الطبيعة والحقوق والإمتيازات, مع أنه يسود عليها وهي تخضع له كما للرب في العلاقات الزوجية وشؤون الحياة العامة. وقد وضعهما الله في جنة عدن, ولكي يمتحن طاعتهما منعهما من أن يمسا أو يذوقا شجرة معينة. لكن الحية تحت تأثير شيطاني قادت حوّاء إلى الشك في صلاح الله, ثم إلى أكل الثمرة المحرمة. وبعد ذلك الحّت على آدم أن يأكل, ((فسمع لقولها)), وشاركها ذنبها. وكانت النتيجة سقوط الإنسان (تكوين 1: 3_24) . وبعد طرد الزوجين المذنبين من الجنة, صارت حوّاء على التوالي أم قايين, وهابيل, وشيث وبنين وبنات آخرين(تكوين 1: 4 و2 و25 و26 و1: 5_5) . ويشير الرسول بولس في 2 كو 3: 11 و1 تيمو 13: 2 و14 الى حواء والسقوط.'),
(1301, 'حُوروث يائير', 'اسم عبري معناه ((قرى أو مخيمات أو معسكرات يائير)). وهي مدن بدون أسوار في القسم الشمالي الغربي من باشان, في منطقة الأرجوب, حيث تقترب هذه البقعة من بلاد الجشوريين والمعكيين, وحيث تندمج الحدود بين الأرض الوعرة, وجلعاد, والأرض الرملية العراء, وباشان في بعضها البعض اندماجاً غير ملحوظ(تثنية 4: 3 و14) . استولى عليها يائير وهو من سبط منسّى.\nوكان عددها قابلاً للتغير لأنها واقعة في أرض متنازع عليها (1 أخبار 23: 2) . ويجب أن تميز بدقة من الستين مدينة المسورة في قلب باشان, وكذلك في أرجوب (تثنية 4: 3 و5 و1 ملوك 13: 4) . وبما أن الحدود بين جلعاد وباشان لم تكن محددة بوضوح بواسطة الطبيعة, فلم تتعين أيضاً بطريق العرف, ويشار إلى بعض الأماكن إلى هذه الحدود غير المقرّرة سواء أكانت في جلعاد أوفي باشان. وقد تضمن غزو العبرانيين لباشان الاستيلاء على مدن مسورة في الأرجوب على هذه الحدود غير المقررة وعندما يذكر استيلاء يائير هذا في علاقته بغزو مملكة عوج, الذي تمّ من اذرعي في باشان(تثنية 14: 3 ويشوع 30: 13) , لكن عندما تمثل البلاد الشرقية في مخيلة المسافر في وادي الأردن أو في كنعان غربي النهر, فإنه بالطبع يتكلم أولاً عن جلعاد, وأحياناً يستخدم هذا الاسم بتوسع الأرض المرتفعة كلها شرق النهر. وكثيراً مايكون للاسم عدة دلالات متنوعة الأرض(1 أخبار 21: 2_23 وعدد 40: 32 و41 و1 ملوك 13: 4) .'),
(1302, 'حوِيّون', 'اسم عبري معناه ((قرية مخيم)) قارن في العربية: خواء أو مجموعة خيام. وهم أحد أجناس كنعان قبل غزو العبرانيين لها (تكوين 17: 10 وخروج 17: 3 ويشوع 1: 9) . وقد تشتتوا إلى عدة جماعات. ففريق منهم سكن في شكيم في عصر يعقوب(تكوين 17: 10 وخروج 17: 3 ويشوع 1: 9) . وقد تشتتوا إلى عدة جماعات. ففريق منهم سكن في شكيم في عصر يعقوب (تكوين 18: 33 و2: 34) , وظل لسلالتهم تأثير في المدينة لعدة أجيال بعد الغزو(قضاة 28: 9) . وفريق منهم أيضاً سكن في جبعون وجوارها, وقد حصلوا على عهد سلام من يشوع, عن طريق حيلة احتالوها, لكن حالما انكشفت خدعتهم, استخدموا محطبين وسقاة(يشوع9) . وكان لهم مقر واسع, ربما هو مقرهم الرئيسي, في سفح جبل لبنان, من جبل حرمون الى مدخل حماة (يشوع 3: 11 وقضاة 3: 3) . في هذه المناطق الجبلية الشمالية كانت لهم قرى يملكونها حتى إلى وقت متأخر في عصر داود(2 صموئيل 7: 24) . وأولئك الذين كانوا في فلسطين الأصلية مع الكنعانيين الآخرين الذين بقوا في البلاد, طولبوا بأن يقدموا خدمة تسخير لسليمان في عمليات البناء الواسعة التي قام بها(1 ملوك 20: 9_22 و2 أخبار 7: 8 و8) .ونجد في النص العبري في تكوين 2: 36 ذكرأً لرجل اسمه صبعون وهو حوّي, لكننا نجد أنه ذكر في عددي 20 و29 بأنه حوري. وتكوين 2: 34 ويشوع 7: 9 وفي الترجمة السبعينية حوري مع أنه ذكر في العبرانية بأنه حويّ. ويحتمل أنه كان قسم من الحوريين يعرف بالحويين.'),
(1303, 'حَويلة', 'اسم سامي معناه ((رملية)) قارن العبرية, حول ((رمل)).(1) رجل من بني كوش (تك 7: 10) .(2) رجل من بني يقطان (تك 29: 10) .(3) مقاطعة في بلاد العرب, يسكن بعضها الكوشيون ويسكن البعض الآخر اليقطانيون, وهم شعب سامي (تكوين 7: 10 و1: 29 و1 أخبار 9: 1 و23) . والصلة بين حويلة وحضرموت وأماكن اخرى تشير إلى موقع في وسط البلاد العربية أو جنوبها. وفي حويلة نهر قيشون, والمنطقة غنية بالذهب والمقل_ وهو صمغ عطري طبي, والأحجار الكريمة(تكوين 11: 2 و12) . ويفضل البعض أن يحققها بمنطقة خولان, في القسم الغربي من بلاد العرب شمالي اليمن. ولا يعرف إلى أي حد كانت تمتد الحويلة شمالاً, ومن قصة محاربة شاول مع العمالقة كي نستنتج أن قسماً من الصحراء العربية, يمتد عدة مئات الأميال شمال اليمامة ويحمل اسم حويلة (1 صموئيل 7: 15 وقارن تكوين 18: 25) .'),
(1304, 'حِيئِِيل', 'اسم عبري معناه ((الله حي)) رجل من بيت ايل, حصّن اريحا في ملك آخاب فجلب على نفسه اتمام نقمة يشوع, فمات بكره, اذ ربما قدم ذبيحة, عندما وضع الأساس, وكذلك مات صغيره عندما نُصِبت أبوابها (يشوع 26: 6 و1 ملوك 34: 16) .'),
(1305, 'حِيرام', 'ورد اسمه في سفر أخبار الأيام ((حورام)), ماعدا في النص العبري في 1 اخبار 1: 14 و2 أخبار 11: 4ب و10: 9 حيث ورد في المقروء ((حورامْ)), وورد في المكتوب ((حيرام)). وقد ورد في العبرية مكتوبا أيضاً ((حيروم)) (1 ملوك 10: 5 و18 و40: 7) . اسم عبري وفينيقي اختصار اخيرام ومعناه ((الاخ يُرَفّع)).(1) ملك صور. بناء على الاقتباص الذي يقتبسه يوسفيوس من المؤرخ الفينيقي ديوس, ومن الأخبار التاريخية الصورية التي ترجمها ميناندر, فإن حيرام خلف أباه ابيبعل, وملك 34 سنة, ومات وهو في الثالثة والخمسين من عمره, وقد وسع مدينة صور ببناء رصيف على الجانب الشرقي, وبنى كذلك ممرّاً يربط المدينة بالجزيرة التي قام عليها هيكل جوبتر أو بعل سميم, وكرس عموداً ذهبياً في هذا الهيكل, ورمم المقادس القديمة, وسقفها بأرز قطعة من لبنان, وشيد هياكل لهرقل وعشتاروث. وكان صديقاً لداود وسليمان(1 ملوك 1: 5 و2 أخبار 3: 2) . وفي وقت ما, بعد أن استولى داود على قلعة صهيون, ارسل حيرام سفارة اليه, وعندما اشتهى داود قصراً, قدم له حيرام خشب الارز وبنائين ونجارين(2 صموئيل 11: 5) . وكان هذا على ما يتضح قبل مولد سليمان(2 صموئيل 2: 7 و 12 و 2: 11) . وعندما اعتلى سليمان العرش, ارسل تهنئاته. وقد قدم ارزاً وسرواً لبناء الهيكل, وصناعاً مهرة ليساعدوا في تجهيز الخشب والحجر, وفي مقابل هذا دفع له سليمان حنطة وزيتاً(1 ملوك 1: 5-12 و2 اخبار 3: 2-16) .ولذلك قدم حيرام 120 وزنة من الذهب (1 ملوك 14: 9) , وانضم مع سليمان في ارسال بعثة بحرية الى اوفير لأجل هذا المعدن الثمين(1 ملوك 26: 9-28 و2 اخبار 21: 9) . ولذلك قدم له سليمان مقابل بعض من خدماته 20 مدينة في الجليل, لكنه رفضها (1 ملوك 10: 9-12 و2 اخبار 1: 8 و2) , انظر ((كابول)). وقد اطلق اسم حيرام على غير هذا الملك من ملوك الفينيقيين. وقد اكتشف في جبيل تابوت لملك يدعى احيرام وقد ظن بعضهم انه هو نفس الملك الذي عاصر داود وسليمان.(2) صانع, ابوه صوري, وامه ارملة من نفتالي (1 ملوك 13: 7 و14) . لكنها من مواليد دان (2 اخبار 14: 2) . وقد قام بصناعة الأشياء النحاسية في هيكل سليمان, كالأعمدة, والمرضة, والمناضح والرفوش (1 ملوك 13: 7-46 و2 اخبار 13: 2 و14) . ولقب ((حورام ابي)) أو ((حيرام ابي)) في 2 اخبار 13: 2 و16: 4 وربما يعني هذا اللقب انه صانع ماهر أو مشير.'),
(1306, 'حِيرَة', 'اسم عبري بما كان معناه ((نبيل أو شريف)) وهو عد لامي صديق يهوذا (تكوين 1: 38 و12 و20) .'),
(1307, 'حِيزير', 'اسم عبري معناه ((خنزير)) من بني هرون, رئيس كهنة, كبرت اسرته الى بيت اب في عصر داود, واصبحت الفرقة الـ 17 من الكهنة (1 اخبار 15: 24) .'),
(1308, 'حَيّة النحاس', '(عدد 9: 21) . شكل حية محرقة صنعها موسى وأقامها على عمود في البرية حسب قول الرب, لكي ينظر اليها بنو اسرائيل, الذين لدغتهم الحياة المحرقة, بايمان في وعد الله ان يشفى الذين ينظرونها (عدد 8: 21 و9) . وفي السنين التالية بأ العبرانيون يستخدمونها كصنم, فحطمها حزقيا, وبازدراء دعاها نحشتان أي ((قطعة نحاس)) فقط كما في احدى الترجمات (2 ملوك 4: 18) .وعندما تنبأ الرب يسوع بصلبه, شرح معناه واهميته الروحية بمقارنته برفع الحية النحاسية(يوحنا 14: 3 و15) .'),
(1309, 'حَاك', 'مارس المصريون فن الحياكة قبل وصول العبرانيين إلى مصر, وكانوا ينتجون أقمشة منسوجة مثل ثياب البوص أو الكتان (تكوين 42: 41) . وكان الرجال عادة هم الذين يعملون هذا العمل, ولكنّه لم يكن مقتصراً على الرجال فقط, لأن النساء يظهرن على النول في النقوش المصرية القديمة. وفي وقت الخروج عرف العبرانيين كلاً من الحياكة البسيطة والمتقنة (خروج 35: 35) . وأنتجوا أقمشة متعددة على الأنوال. فالأصناف السميكة, مثل أقماش الخيام, والملابس الخام للفقراء كانت تصنع من شعر الماعز ووبر الإبل (خروج 7: 26 ومتى 4: 3) , والأقمشة الفاخرة كانت تنسج من الكتان والصوف (لاويين 47: 13) , وكانت تصنع نماذج مطرزة ومصورة,  وكذلك أنسجة ملونة باستخدام خيوط ملونة ألواناً (خروج 1: 26 وقارن 39: 28) , حتى الخيوط الذهبية كانت تنسج في الأقمشة (خروج 3: 29) , وكان القماش أيضاً يطرز بصور ونماذج(خروج 16: 27 و23: 38) . وكان التطريز يعمل عادة بالايدي ويعتقد كثيرون من المفسرين أن الكلمة العبرية ((روقيم)) الترجمة ((طراز)) تعني الشخص الذي ينسج التصميمات في النسيج, أي المدبج. وكان امتبع بين العبرانيين أن تقوم النساء بعمل النسيج والغزل عادة (2 ملوك 7: 23 وقارن 1 صموئيل 19: 2 وامثال 22: 31 واعمال 39: 9) . وكانت الاوشحة والاقمصة تخرج من النول جاهزة للاستعمال عندما كنت الاقمشة تنسج كاملة, لم تكن تتطلب خياطة. وكانت مثل هذه الاقمصة توصف للكهنة (خروج 6: 28 و8) , وقد لبس يسوع واحداً منها قبيل صلبه (يوحنا 23: 19) .وكان النول يوضع في اما عمودياً او افقياً وفي الصور المصرية يرتفع اطار النول ارتفاعاً ضئيلاً فوق الارض, ويجلس النساج القرفصاء في عمله ويبدو كأنه يدوس على الخيوط, وخيوط السداة (الطويلة) تجري متوازية وتمتد بين الدعامتين اللتين تتعلق عليهما هذه الخيوط. وفواصل من أي نوع بدائي, تفصل الخيوط السداة إلى مجموعتين وتشكل ممراً لمرور الوشيعة(المكوك) او أي حامل آخر لخيوط اللحمة, وتكون هذه قريبة منه أي بينه وبين القماش المنسوج. ويضرب بقصبة (او عارضة خشبية) الخيط الأخير الذي دفعه وسط السداة ليضغطه ضغطاً وثيقاً إلى اللحمة.وهكذا كان للنول العبري دعامته وشيعته (1 صموئيل 7: 17 و2 صموئيل 19: 21 و ايوب 6: 7) . ووتد النسيج, قد يكون القصبة التي بها يضرب خيط اللحمة إلى مقره (قضاة 13: 16 و14) وكان يقطع النسيج من النول (اشعياء 12: 38) .'),
(1310, 'حيلام', 'اسم عبري ربما كان معناه((حصن)) وهو مكان في شرق الأردن, حيث هزم داود هدر عزر, ملك ارام ((سوريا)) (2 صموئيل 16: 10_19) .ربما هو عليم (1 مكابيين 26: 5) أو علمة في سهل حوران.'),
(1311, 'حِيلون', 'اسم عبري مناه ((قوي, شجاع)) أبو الياب, رئيس لزبولون (عدد 9: 1 و7: 2 و 24: 7 و29 و16: 10) .'),
(1312, 'حيلِين', 'انظر ((حولون)) (1) .'),
(1313, 'حَين', 'اسم عبري معناه ((حنان, عطف, شفقة, لطف)). ابن صفنيا (زكريا 14: 6) .'),
(1314, 'حِيناداد', 'اسم عبري ربما كان معناه ((حنان هدد)) مؤسس اسرة لاوية, عضد ابناؤه زربابل في الوقت التي كانت توضع فيه اساسات الهيكل (عزرا 9: 3) , وساعد في ترميم سور اورشليم (نحميا 18: 3) .'),
(1315, 'حَيَوان', 'انظر تحت الاسماء المتعددة, قصة خلقها في تكوين 24: 1. اعطى لها الله الاسماء التي اطلقها عليها آدم (تكوين 20: 2) . سلّط الله الإنسان عليها (تكوين 24: 1_ 26 و2: 9 ومزمور 6: 8_ 8 وقارن مرقس 13: 1) . حفظت في فلك نوح (تكوين 19: 6_ 22) , قصاص الضرر الذي يقع منها(خروج 28: 21_ 36 و5: 22) , قصاص الاذى الذي يلحقه الانسان بها (خروج 33: 21) العناية بها (خروج 30: 22 و5: 23 و19 و26: 34 ولاويين 27: 22 و28 وتثنية 21: 14 و4: 22 و6 و7 و4: 25 وامثال 10: 12 و1 كورنثوس 9: 9 و1تيموثاوس 18: 5) , عناية الله بها (مزمور 6: 36 و10: 104_ 18) عاداتها(ايوب 5: 6 و39: 38_ 41 وص 39 و15: 40_ 24 وص 41 ومزمور 20: 104_ 22 واشعياء 3: 1 و13: 38 و14 وارميا24: 2 و7: 4 و8: 5 و7: 8 و5: 14 و6 و38: 25 وعاموس 4: 3 و12) تسبح الله (مزمور10: 148 واشعياء 20: 43) . طاهرة تؤكل(لاويين14) , ونجسة لا تؤكل (لاويين11) .'),
(1316, 'حَياة', 'أي نفس, روح وهي:(1) الحياة الطبيعية, وهي عكس الموت (تكوين 7: 2 وأعمال 25: 17) , أو عمر الإنسان على الأرض (تكوين 7: 25 ولوقا 25: 16) .(2) الحياة الروحية, وهو عكس كل ما هو جسدي وحيواني.(3) الحياة الابدية, وهي حياة الله والمسيح في المؤمن المولود ثانية(يوحنا 3: 3 و5 و7 و16 و24: 5 و3: 17) .ويشبه الخير بالحياة(تثنية 15: 30) والشر بالموت(رومية 32: 1) . انظر((شجرة الحياة)) (تكوين 9: 2 و22: 3 ورؤيا 7: 2 و2: 22) .و((خبز الحياة)) (يوحنا 35: 6 و51) .و((ماء الحياة)) (رؤيا 1: 22 و17) .'),
(1317, 'حَيّة', 'حيوان يزحف على بطنه (تكوين 1: 3 و14) لها رأس وذنب (تكوين15: 3 وخروج 4: 4) , لكن ليس لها اطراف. تسمى اسماً شاملاً في العبرية نحاش (قارن, العبرية, حنش) (قارن تكوين13: 3 مع 2 كورنثوش 3: 11 وعدد 9: 21 مع يوحنا 14: 3) واذ تتلوى في سيرها, يكون فمها معرضاً للاحتكاك بالتراب, الذي تلحسه(ميخا 17: 7 وقارن تكوين 14: 3) . ولغة بعض انواعها تصب سماً مميتاً في الجرح (عدد 6: 21 ومزمور 4: 58 وامثال 32: 23) . وبعضها يمكن ان يرقى(جامعة 11: 10) . والحية موجودة في البرية والمناطق المأهولة, وعلى الطرق, وفي السياجات, وعلى الصخور, وفي الجدران (تكوين 17: 49 وعدد 6: 21 وامثال 19: 30 وجامعة 8: 10 وعاموس 19: 5) .والحياة المحرقة التي لدغت بني اسرائيل في البرية وسببت الموت كانت نوعاً من الحياة الموجودة في الصحراء العربية وفي غيرها تحدث لدغته ألماً نارياً محرقاً من الالتهاب والعطش (عدد 6: 21 وتثنية 15: 8) . انظر ((حية النحاس)).وحية التجربة كانت في المظهر كحّية عادية ولكنها تفوق وحوش البرية في المكر والدهاء. وبعد ما تورطت في تجربة الإنسان, لعنت بين الوحوش (تكوين 1: 3 و14) . وربما لم تبصر حواء شيئاً اكثر من حّية, لكن الشيطان كان في هذه الحية, كما كانت الارواح النجسة فيما بعد في الناس وفي الخنازير, تقودها, وتعيرها دهاء خارقاً, وتستخدمها كوسيلة بها تقترب إلى حواء (رومية 20: 16 و2 كورنثوس 3: 11 ورؤيا 9: 12) , انظر ((شيطان)). وقد وقع عليه القصاص, كما وقع على الحيوان البريء عندما استخدم آلة للخطية (لاويين 15: 20 و16) . وقد جعلت طريقة زحفها على الارض ذكرى لانحطاطها وعلامة على دينونتها. وهي مكروهة جداً من الإنسان, الذي يهم بقتلها كلما رآها. وكلما ادرك الإنسان بوضوح أن الروح الشرير كان سيد الحية, حول عداوته إلى العدو الأكبر. انظر ((صل)) ((وافعوان)) ((وافعى)).'),
(1318, 'حَيّات مُحْرقة', '(عدد 6: 21) . انظر ((حية)).'),
(1319, 'خَابُور', 'اسم اكادي لا يعرف معناه ولفظه في العبرية ((كبار)) وهو نهر في ارض الكلدانيين استقر على ضفتيه بعض المسبيين اليهود بينهم النبي حزقيال. وهناك رأى النبي كثيراً من الرؤى (1: 1 و3 و15: 3 و23 و15: 10 و20) .وهو غير نهر ((خابور)) الذي اطلق عليه اليونان ((خابوراس)) والذي يجري على مقربة من نصيبين في أعالي بلاد مابين النهرين ويصب في نهر الفرات. أما خابور هذا فقد كان قناة كبيرة في جنوب شرق بابل وكان يطلق على اسم القنوات اسم ((نهر)).'),
(1320, 'خَابُور نهر جوزان', 'نهر في بلاد مابين النهرين أُخذ إليه مسبيو الأسباط العشرة(2 مل 6: 17 و11: 18 وأخبار 26: 5) . وقيل أنه هو نهر خابور الذي يجري جنوباً مخترقاً بلاد مابين النهرين, وبعد مائة وتسعين ميلاً يلتقي بالفرع الشرقي لنهر الفرات عند قرقيسيا.'),
(1321, 'خَالب أو خالد', 'اسمان عبريان معناهما ((سمن وخلود)). وهو خالب بن بعنة التطوفاني أحد أبطال جيش داود(2 صم 29: 23) وقد أطلق عليه أيضاً((خالد)) (1 أخبار 30: 11) ويظن أنه هو أيضاً (( خالداي التطوفاني)) (1 أخبار 15: 27) .'),
(1322, 'أخبار الأيام', 'السفران التاريخيان في العهد القديم.وهما من وضع مؤلف واحد سمي((المؤرخ)) وتجمعها وحدة الفكر ووحدة الهدف.وهما سفر واحد في المخطوطات العبرية القديمة. وقد قسمها مترجمو الترجمة السبعينية إلى سفرين. وقد قبل هذا التقسيم في النسخة المطبوعة لأسفار العهد القديم. واسم السفرين في العبرية معناه((أعمال الأيام)). ومعنى الاسم كما ورد في الترجمة السبعينية الأمور التي تركت وكان الغرض من وضع السفرين أن يكونا تكملة لأسفار صموئيل والملوك ولكنهما في الحقيقة يستهدفان غرضاً مستقلاً. وقد قال جيروم أنهما سجل لحوادث لتاريخ المقدس كله.وينقسم السفران من تلقاء ذاتهما إلى أربعة أقسام:(1) مقدمة وقد تضمنت تسلسل الإنسان(1 أخبار ص 1-9) .(2) موت شاول وحكم داود(ص 10-29) .(3) حكم سليمان(2 أخبار ص1-9) .(4) تقسيم المملكة ((ملوك يهوذا حتى السبي, امركورش الملك (ص 10-36) .ويتفق السفران اتفاقاً كاملاً مع الأجزاء المماثلة التي وردت في الأسفار التاريخية المتقدمة من التكوين إلى الملوك مما يدل على أن ((المؤرخ)) قد استعان بهذه المصادر. والظاهر أنه استعان بمظاهر أخرى أيضاً لم يُبقِ عليها التاريخ. كما أنه يقتبس من أصحاب الرؤى والأنبياء (1 أخبار 29: 29 و2 أخبار 29: 9 و15: 12 و22: 13 و34: 20 و22: 26 إلى آخره) .وقد كتبت أسفار صموئيل والملوك من وجهة نظر الأنبياء أما سفر الأخبار في قد كتبا من وجهة نظر الكهنة. وقد اهتم ((المؤرخ)) ايما اهتمام بتسلسل الأسماء. أما الحوادث فقد استخلص منها العلل والعبر الأدبية والدينية (1 أخبار 13: 10 و14 و2 أخبار 1: 12-12 و7: 16-12 و35: 20-37 إلى آخره) . وقد آمن المؤرخ بالتدخل الإلهي في الشؤون البشرية (2 أخبار 13: 13-20 و8: 14-14 و1: 20-30) . وفي معالجته تاريخ الملوك أغفل ((المؤرخ)) الكهنوتي تاريخ شاول والمملكة الشمالية كأنما حوادثهما لاتمت بصلة إلى الهدف الذي يرمي إليه. وذكر موت شاول وأبنائه كأنه انتقال من تسلسل الأسماء إلى حكم داود. ولم يجد في تاريخ المملكة الشمالية مادة تدل على تقدم عبادة يهوه الحقة في أورشليم وارتقائها - على أن((المؤرخ)) يدلي بيانات في المسائل المتعلقة بالعبادة والطقوس الدينية ومساهمة اللاويين والمرتلين وعلاقة ملوك أسرة داود بعبادة يهوه في الهيكل بأكثر تفصيل مما جاء في سفري الملوك. والصبغة الكهنوتية في السفرين لاتقلل إطلاقاً من قيمتهما التاريخية.وقد اتفق الرأي القديم على أن عزرا هو كاتب سفري الأخبار, على أنه لا يمكن إثبات هذا الرأي بالدليل القاطع إنما من الأدلة عليه أن 2 أخبار 22: 36 و23 يشبهان يشبهان أكبر الشبه عزرا 1: 1-3.ويكوّن السفران مع سفري عزرا ونحميا وحدة تاريخية احتوت احتوت تاريخ مملكة يهوذا ابتداء من آدم إلى عصر عودة الشعب على يد عزرا ونحميا. وقد ورد سفرا أخبار الأيام في العهد القديم في العبرية في القسم الثالث أو الأخير من الأسفار المقدسة. ويرددان بعد عزرا ونحميا لذلك فإننا نجد سفري الأخبار في آخر أسفار العهد القديم. وهذا هو الموضع الذي احتله هذان السفران في عهد السيد المسيح على الأرض لأنه ذكر أن آخر أنبياء العهد القديم الذي استشهد به هو زكريا (مت 35: 23 ولوقا 51: 11 وقارن هذا مع 2 أخبار 20: 24-22) أما ترتيب سفري الأخبار في الترجمة السبعينية فقد وضع السفران ضمن الأسفار التاريخية وقبل عزرا ونحميا. وقد اتبعت معظم الترجمات هذا الترتيب الأخير. أما تاريخ كتابة السفر فيرجح أنه كتب حوالي سنة 400ق.م إذ أن السفر في 1 أخبار 19: 3-24 يذكر ستة أجيال بعد زربابل.'),
(1323, 'خَبَز خُبز خبّاز', 'كان الخبز المستعمل عند العبرانيين يشبه كعكاً مسطحاً صغيراً مصنوعاً من دقيق القمح أما الفقراء فكانو يخبزونه من دقيق الشعير. وكانت الحنطة تطحن يومياً في مطحنة تدار باليد ويخبز الخبز الطازج يومياً. وحينما كان الخبز يؤكل على عجل كان يؤكل في أكثر الأحيان بدون خميرة (تك 3: 19 و 1 صم 24: 28) على أن صناعة الخبز المختمر لم تكن مجهولة لديهم. وكان الدقيق يصنع عجيناً بخلطه بالماء ويختمر بعد ذلك.على أنه في الفصح الأول كان العجين قد خلط بالماء في المعاجن ولم يكن قد اختمر بعد ساعة تلقوا الأمر بالرحيل(خر 34: 12) .وكان(التنور) الذي تستعمله الأسر الخاصة وعاء قابلاً للنقل, كان يحمى ثم توضع الأرغفة الرقاق في داخله. وعلاوة على الخبز المخبوز في التنور (لاو 2: 4) فإنهم كانوا يخبزون نوعاً من الكعك على صاج محمى (لاو 2: 5) كما كانوا يخبزونه أيضاً على حجارة محماة بعد إزالة الرماد عنها (1 مل 19: 6) . وكانوا يصنعون هذا النوع الأخير إذا اقتضت الظروف العجلة في إعداد الخبز (تك 18: 6) . وكانوا يسمونه خبز ملة، ينضج جانب منه ويبقى الآخر نيئاً رخواً. وكان إعداد الخبز من واجبات النساء (تك 18: 6 و 1 صم 8: 13 ولاويين 26: 26 وقض 6: 19) . وفي البيوت الكبيرة كان يقوم به العبيد، على أنه كان في المدن الكبرى خبازون يبيعون الخبز (ارميا 37: 21) . ونجد في سفر التثنية (ص 2) بياناً مفصلاً بأنواع الخبز الذي كان مقبولاً في التقدمات. وجاء الخبز على سبيل الاستعارة مثل (( خبز الاتعاب )) (مز 127: 2) (( وخبز الدموع )) (مز 80: 5) (( وخبز الكذب )) (ام 20: 17) (( وخبز الشر )) (ام 4: 17) . وقد دعا يسوع نفسه على سبيل المجاز (( خبز الحياة )) (يوحنا 6: 35) .'),
(1324, 'خِبْز الوجوه', '(خر 25: 30) هو خبز الفطير الذي كان يُصنع كل سبت ويُقدم على مائدة الذهب ساخناً، وكان يقدم منه اثنا عشر رغيفاً بقدر عدد اسباط بني اسرائيل. ويظن أن الأرغفة كانت تجعل صفين. وسميت خبز الوجوه لأنها كانت دائماً أمام الرب، وكانت تغير كل يوم سبت (لاويين 24: 8) ولم يكن يحل لاحد ان يأكل منها غلا الكهنة وخم في المقدس (1 صم 21: 1- 6 ومت 12: 4) . ولعل الارغفة الاثني عشر التي كانت تهيأ امام الرب كانت تومىء إلى صلة مستمرة بين يهوه وبين شعبه، فهو واهب المنح والخيرات التي يستمتعون بها في حضرته ويستخدمونها لخدمته. وكان بنو القهاتيين قوامين على صناعة هذا الخبز يهيئونه كل سبت (1 اخبار 9: 2) .وكانت مائدة الخبز تصنع من خشب اللبخ وتغطى بقشرة من ذهب، وكانت أطرافها تنتهي بغكليل ذهبي وكانت توضع حلقة في نهاية كل طرف من اطرافها، توضع فيها العصي التي تحمل بها. وكان طول المائدة ذراعين وعرضها ذراعاً واحداً وارتفاعها ذراعاً ونصف ذراع (خر 25: 23 - 299. ولمعرفة طريقة نقلها انظر عدد 4: 7 و 8. وكانت توضع في القدس إلى جوار الحائط الشمالي أي عن يمين الداخل إلى الخيمة (خر 40: 22) وكان في هيكل سليمان عشر موائد لخبز الوجوه كما كان فيه عشر مناثر. ولكن يظهر أنه لم يستخدم إلا مائدة واحدة في وقت واحد كما بم يستخدم أكثر من منارة واحدة في وقت واحد (2 اخبار 4: 8 و 13: 11) . وكذلك إذا راجعنا تاريخ يوسبفوس، الكتاب الثامن، والفصل الثالث والفقرة السابعة، فإننا نجد في 1 ملو 7: 48 و 2 اخبار 29: 18 ذكر الاستعمال مائدة واحدة. أما مائدة خبز الوجوه التي كانت في الهيكل الثاني فقد حملها معه انتيخوس ابيفانيس، ولكن يهوذا المكابي صنع مائدة جديدة (1 مكابيين 1: 22 و 4: 49) وقد حمل تيطس الروماني هذه أيضاً معه إلى روما (راجع حروب يوسيفوس الكتاب السابع الفصل الخامس والفقرة الخامسة).'),
(1325, 'خَتْم - خاتم - خواتم', 'كان يُنقش على الخاتم اسم صاحبه أو رسم معين أو كلاهما معاً (حز 28: 13) . وكانت الأختام في بابل القديمة اسطوانية الشكل. وقد عثر على نماذج منها يرجع تاريخها إلى حوالي عام 3300 ق.م. وتدل هذه على ارتقاء في الفن في ذلك العصر السحيق. وكانت الخواتم تلبس في الأصابع والآذان والمعاصم السحيق. أو تربط بحبل وتعلق في العنق (اش 3: 21 ولوقا 15: 22 وتك 38: 18 وترميا 22: 24) . وكان الناس يختمون باختامهم المكاتيب أو المستندات. وهذا ما يفعله الاميون في بعض البلدان حتى اليوم (1 مل 21: 8 ونحميا 9: 38 واستير 8: 8 وارميا 32: 10 ويوحنا 3: 33) . وقد وجد عدد كبير من الاختام وآثار الاختام في فلسطين يرجع تاريخها إلى المدة بين القرنين الثامن والخامس قبل الميلاد، وتحمل هذه اسماء أصحابها. وكانت الصناديق والقبور والاشياء التي لا يُراد فتحها تختم بخاتم الشخص الذي يملك سلطة منع الناس عنها (ايوب 14: 17 و 41: 15 ودانيال 6: 17 ومتى 27: 66 ورؤيا 5: 1) . وكان إذا أريد ختم باب أو صندوق أو قبر يوضع عليه قليل من الطين أو الشمع ثم يختم بالخاتم. والارجح أن قبر المسيح ختم على هذه الصورة، أي طلي رباط الحجر الذي كان على بابه بالطين أو الشمع ثم ختم بخاتم عمومي أو شخصي بحيث كان يعرف إذا كسر الختم.وقد وردت كلمة ((ختم)) على سبيل الاستعارة في الكتاب المقدس للدلالة على عمل أو علامة أو طريقة التثبيت والتمييز (5 تي 2: 19) والتأمين (رو 4: 11 واف 1: 13 ورؤيا 7: 3) .'),
(1326, 'خِتان', 'وهو التطهير (تك 17: 10 - 12 ويو 7: 22) والختان من الشعائر المعروفة في اليهودية، وهو قطع لحم غرلة كل ذكر ابن ثمانية ايام. وقد جعل هذا الطقس علامة عهد بين الله وابراهيم الذي اختتن هو وأهل بيته وعبيده الذكور. وكان الختان يقوم به عادة رب البيت أو أحد العبرانيين وأحياناً الام (خر 4: 25 ومكابيين الأول 1: 60) وقد ختن ابراهيم وهو في التاسعة والتسعين واسماعيل وهو في الثالثة عشرة (تك 17: 11 - 27) ثم تجددت سنة الختان لموسى (لا 12: 3) فقضي أن لا يأكل الفصح رجل اغرل. وكان اليهود يحافظون كل المحافظة على هذه السنة وقد اهملوها أثناء رحلتهم في البرية. على أنه عند دخول ارض كنعان صنع يشوع سكاكين من الصوان وختن الشعب كله (يشوع 5: 2 - 9) . وكان مفروضاً على كل الغرباء الذين يقبلون الدخول في اليهودية أن يخضعوا لهذا الفرض مهما تكن اعمارهم (تك 34: 14 - 17 و 22 وخر 12: 48) .على أن الختان كان شائعاً ومعروفاً بين المصريين القدماء وغيرهم من الشعوب، إلا أنه لم يكن معروفاً لدى الفلسطينيين . ولكنه في اليهودية كان فرضاً دينياً للتمييز بين نسل ابراهيم وباقي الناس (رو 4: 9- 12) ومعنى الختان الروحي لدى اليهود هو تكريس الجسد، ولذلك كان يدعون انفسهم (( أهل الختان )) ويدعون من عداهم ((أهل الغرلة)). وفي بكور العصر المسيحي زعم فريق من اليهود المتنصرين إن حفظ تلك السنة ضروري للخلاص، ولهذا قال بولس تلك السنة ضرورية للخلاص، ولهذا قال بولس في رسالته إلى غلاطية (( ها أنا بولس أقول لكم أنه إن اختتنتم لا ينفعكم المسيح شيئاً لكن اشهد أيضاً لكل انسان مختتن أنه ملتزم أن يعمل بكل الناموس)) (غلا 5: 2 و 3) . وأيضاً ((لأنه في المسيح يسوع ليس الختان ينفع شيئاً ولا الغرلة بل الخليقة الجديدة)) (غلا 6: 15) . ويتضح مما جاء في كولوسي 2: 11 و 12 أن الرسول يُعلّم بأن للمعمودية في العهد الجديد نفس المكانة التي كانت للختان في العهد القديم.ولا يزال اليهود المعاصرون يمارسون هذه السنة بكامل طقوسها، فيأتون بالولد إلى المجمع فيأخذه رجل يدعى ((سيد العهد)) ثم يأتي الخاتن ويجري عملية الختان مع بعض الطقوس والمراسيم.'),
(1327, 'مَخَدَّات', '(حز 13: 18 و 21) ربما كان المقصود بالكلمة الاصلية مناديل. ولعلَّ المراد بها نوع من الثياب والزينة كانت توضع على رؤوس نساء بني اسرائيل، اللاتي كن يعبدن الاصنام.'),
(1328, 'مُخْدَع', '(تك 43: 30) غرفة داخلية (متى 6: 6) وكثيراً ما كانت في العلاء. وكان المخدع أحياناً داخل مخدع ثان (2مل 9: 2) .'),
(1329, 'خادِم خُدّام', '(1) تابع شخصي أو معين. وهي ليست وظيفة دنيا، فيوسف العبد الرقيق لما ارتقى إلى مكانة الكرامة والمجد في بيت مولاه ((خدمة )) (تك 39: 4) وأنزلت أبيشج الشونمية منزلة الكرامة والاحترام في بيت داود ((وكانت تخدمه)) (1 مل 1: 4 و 15) . ووقف يشوع على خدمة موسى في وظيفته (خر 34: 13 و 33: 11 ويشوع 1: 1) . ووقف اليشع على خدمة ايليا، وصب الماء على يديه وخلفه بعد ذلك (1 مل 19: 21 و 2 مل 3: 11) . وخادم المجمع كان يعاون المعلم الذي كان يقوم بالخدمة (لو 4: 20) . وتلاميذ المسيح قاموا على خدمته وكانوا شهود عيان له (لو 1: 2 واعمال 26: 16) .(2) موظف عمومي في خدمة الدولة أو خدمة الله مثل الكهنة واللاويين في القيام بخدمة الاقداس (خر 28ث 18: 5 ولو 1: \" 23 وعب 9: 21) . ومثل المسيح ككاهن عظيم في خدمة الاقداس (عب 8: 2) . وبولس في خدمة الانجيل (رو 15: 16) . وتطلق الكلمة على ملحق في البلاط الملكي (1 مل 10: 5) . وعلى موظف في مرتبة عالية (2 اخبار 22: 8 واستير 1: 10) . كما تطلق على الملائكة أيضاً (مز 103: 21 و 104 4) .(3) شخص ملحق بخدمة آخر ومحسوب كأنه ممثل من يخدمه، كالحاكم مثلاً الذي يمثل الله ((فإن الحكام ليسوا خوفاً للاعمال الصالحة بل للشريرة)) (رو 13: 3) . وتطلق الكلمة خاصة على خادم الانجيل مثل تيموثاوس (1 تس 3: 2) . وبولس وابولس (1 كور 3: 5) وابفراس (كولوسي 1: 7) .'),
(1330, 'خادمة الكنيسة', '(رو 16: 1) أغلب الظن أنه كان في الكنائس نساء تقيات يخدمن النساء كما خدم الشمامسة الرجال. وكان من واجباتهن العناية بالمريضات والفقيرات والارامل وتربية اليتامى وإضافة الغرباء. وربما كنّ من الارامل (انظر 1 تي 5: 9 - 16) .'),
(1331, 'يَخدم موائد', '(1ع 6: 2) المراد بخدمة الموائد الاشتغال بجمع الطعام وتوزيعه على المساكين أو الاهتمام بتدبير شؤون الكنيسة المالية.'),
(1332, 'خدمة العين', '(اف 6: 6 وكو 3: 22) هي خدمة الاكراه والاجبار أو الخدمة المأجورة التي تصنع كأنها تحت نظر السيد.'),
(1333, 'خُرُوج', 'هو السفر الثاني من اسفار موسى، والثاني حسب ترتيب اسفار العهد القديم. واسم هذا السفر في الأصل العبري ((واله شيموت)) أي ((وهذه اسماء)) أما معنى اسمه في الترجمة السبعينية اليونانية وفي معظم الترجمات الاخرى فهو ((الخروج)) والاسم مطابق للمسمى، فهو سجل لتاريخ خروج بني اسرائيل من مصر متجهين إلى فلسطين. وقد اقتبس عنه المسيح وتلاميذه 25 آية بمعانيها. وقد تضمن تاريخ الحوادث التي جرت من وقت موت يوسف إلى وقت بناء خيمة الشهادة. ويمكن تقسيمه إلى ثلاثة أقسام.1- في مصر:(ص 1: 1 - 12: 36) ويمرُّ السفر مرَّ الكرام على القرون المتوالية التي اعقبت نزول يعقوب إلى مصر، ويلخصها في عبارة واحدة عن تكاثر الشعب بعد موت يوسف (1: 7) . ويتحدث بعد ذلك عن: المظالم التي وقعت على العبرانيين (1: 8 الخ) ، وولادة موسى وحياته الاولى ودعوته (ص 2 - 4) وكفاحه مع فرعون وانزال الضربات، ثم رسم فريضة الفصح بمناسبة الضربة الأخيرة (ص 5: 1 - 12 : 36) .2- من مصر إلى سيناء :(ص 12: 37 - 42 و 19: 2) . ويصف هذا القسم الرحيل من رعمسيس (ص 12: 37 - 42) . ويضع قواعد وتعليمات إذافية عن الفصح والشروط التي يخضع لها الغرباء للاشتراك في هذا العيد (12: 43 - 51) وفرض تقديس كل بكر (ص 13: 1 و 2) ، واعلان سبعة ايام من ذلك الحين فصاعداً تحفظ احتفاء بعيد الفصح، ووصية الله لتقديس البكر (اعداد 3- 16) وعبور البحر الأحمر (ص 14) ، وترنيمة الخلاص (ص 15: 1 - 19) ، والمياه المرَّة في مارة والمن والسلوى (ص 15: 20 - 16: 36) . وفي رفيديم: المياه تنفجر من الصخر في حوريب، والانتصار على عماليق، وزيادة يثرون (ص 17 و 18) .3- في سيناء:(ص 19: 3 - 40: 38 ثم إلى سفر العدد 10: 10) توطيد دعائم النظام الثيوقراطي الديني: العهد الثيوقراطي الذي اقترحه الله مشترطاً فيه الطاعة (19: 3 - 6) . موافقة شيوخ الشعب على الشروط (عدد 7 و 8) . الوصايا العشر والاحكام الإضافية الأخرى التي كتبت في كتاب العهد (ص 20 - 23 و 24: 4) . موافقة الشعب على هذا العهد (24: 1 - 8) . ووليمة العهد بين الطرفين المتعاقدين (عدد 9 - 11) . موسى فوق الجبل: الأوصاف المعمارية للخيمة واثاثاتها وموائد الحجر (ص 24: 12 - 31: 18) . العجل الذهبي (ص 32 و 33) . زيادة موسى الثانية للجبل وتلخيص سريع لشرائع العهد (ص 34) . بناء خيمة الاجتماع وإقامتها (ص 35 - 40) .تاريخ الخروج:اختلف العلماء في تحديد التاريخ الذي حدث فيه خروج بني اسرائيل من مصر وهاك ملخص لمجمل الأراء الخاصة به:(1) الرأي الأول وهو الذي يقول بأن الخروج حدث في القرن السادس عشر قبل الميلاد - وهذا هو الرأي الذي قال به مانيثو المؤرخ المصري الذي عاش نحو سنة 250 ق.م وقد استمر العلماء يأخذون بهذا الرأي منذ عصر مانيثو إلى أواخر القرن التاسع عشر الميلادي. ومجمل هذا الرأي هو أن العبرانيين طردوا من أرض مصر مع الهكسوس. ولكن هذا التاريخ لا يتفق مع النصوص الكتابية الواردة في خروج 1: 11 و 12: 40 و 1 ملوك 6: 1. ولا يتفق أيضاً مع الاكتشافات الحديثة التي أظهرتها الحفريات.(2) أما الرأي الثاني فيقول أن الخروج حدث في منتصف القرن الخامس عشر قبل الميلاد أو نحو سنة 1447 وأنه حدث في زمن تحتموس الثالث أو في زمن امنوفس الثاني. وهذا التاريخ هو أقرب التواريخ اتفاقاً مع قضاة 11: 26 فإن يفتاح الذي عاش حوالي سنة 1100 ق.م . يذكر أن ثلاث مئة سنة مضت منذ دخول العبرانيين الأرض أي أنهم دخلوها في نحو سنة 1400 ق.م. وعندما يضاف إليها الأربعون سنة التي قضوها في البرية يصل التاريخ إلى أواسط القرن الخامس عشر تقريباً.وكذلك بتفق هذا التاريخ مع النص الوارد في 1 ملوك 6: 1 حيث يقول: (( وكان في سنة الأربع مئة والثمانين لخروج بني اسرائيل من أرض مصر في السنة الرابعة لملك سليمان .... أنه بنى البيت للرب)). فإذا كان قد بدىء ببناء الهيكل في عام 967 ق.م. فيكون الخروج قد تم في عام 1447 بحسب هذا النص.وكذلك يتفق هذا التاريخ مع الاكتشافات التي أظهرها التنقيب في اريحا وحاصور حسبما يقول لنا بعض العلماء.ويتفق أيضاً مع ما ورد في لوحات تل العمارنة التي تتحدث عن شعب قادم إلى أرض فلسطين في هذا التاريخ تقريباً، أو بعده بزمن قصير. وتدعو اللوحات هذا الشعب باسم ((الخبيرو)) ويعتقد بعض العلماء أن هؤلاء هم العبرانيون الذين جاءوا إلى أرض فلسطين في نحو هذا التاريخ.(3) ويقول الرأي الثالث بأن تاريخ الخروج يقع في نحو عام 1290 ق.م أو في أوائل القرن الثالث عشر قبل الميلاد. وأنه قد حدث في أثناء حكم رمسيس الثاني إذ أن الكتاب يذكر في خروج 1: 11 أن بني اسرائيل بنوا مدينتي مخازن: فيثوم ورعمسيس. ويقولون أن هذا الاسم رعمسيس هو اسم فرعون الذي حدث الخروج في عصره. ولكن لا يمكن أن يتخذ اسم المدينة كدليل قاطع على اسم فرعون الذي تمّ الخروج في عصره لأنه من المحتمل جداً أن الاسم ((رعمسيس)) قد استخدم في عصر سابق لعصر رمسي الثاني بزمن طويل.كذلك يرى القائلون بهذا الرأي أن في فتح مدن مثل لخيس وغيرها في تاريخ يقرب من هذا التاريخ تأييداً لرأيهم. ولكننا نعلم أن التاريخ الذي  يستدل عليه من الحفريات لا يمكن إلا أن يكون تقريبياً.(4) يقول علماء آخرون أن الخروج حدث في عصر منفتاح أو حوالي عام 1230 ق.م. وقد بنى هذا الرأي على تفسير خاطىء للنصب التذكاري الذي أقامه منفتاح، وفيه يذكر انتصاره على اسرائيل وغيره من الأمم التي كانت تقطن فلسطين في ذلك الحين. فقد ظنّوا أن في ذكر اسرائيل اشارة إلى أن بني اسرائيل كانوا في ذلك الحين قد خرجوا إلى مصر، وقضوا زمن التيه في البرية ودخلوا فلسطين واستقروا في البلاد زمناً طويلاً قبل انتصار منفتاح عليهم واستقروا في البلاد زمناً طويلاً قبل انتصار منفتاح عليهم وهذا ما يناقض رأي هؤلاء العلماء.موضع العبور:اختلف العلماء في موضع العبور، والحق أن تعيينه بالدقة ليس من الأمور اليسيرة. فالمعجزات التي جرت على يد موسى تمت في صوعن أي تأنيس (مز 78: 12) . وكانت رعمسيس ضاحية لهذه العاصمة. ومنها ارتحل بنو اسرائيل إلى سكوت (خر 12: 37) وهي تل المسخوطة في واجي طميلات، وتبعد 32 ميلاً جنوب شرق تانيس و 11 ميلاً غرب الاسماعيلية. فهم لم يتخذوا أقصر الطرق إلى فلسطين، بل رحلوا عن طريق البرية بالقرب من البحر الأحمر (خر 13: 17 و 18) . وضربوا خيامهم لأول مرة بعد مغادرة سكوت في ((ايثام)). ولم يمكن تعيين هذا الموقع، على أنه كان طرف البرية عند حافة الصحراء (عدد 20) . ومن هناك رجعوا وضربوا خيامهم أمام فم الحيروت بين مجدل والبحر امام بعل صفون (خر 14: 2) . وليس من الميسور تحديد هذا الموقع، غلا أنّ المعروف أنه كان غرب البحر الأحمر. ومن هناك عبروا البحر الأحمر (بحر سوف) إلى برية شور (خر 15: 4 و 22) . ثم ساروا بمحاذاة البحر الحمر إلى جبل سيناء (خر 16: 1) . ويذهب كثيرون من العلماء إلى أن الخليج كان ممتداً في أيام موسى إلى منطقة البحيرات المرة على هيئة مستنقع. من مدينة الاسماعيلية الحديثة . ويقول آخرون أنه كان بالقرب من مدينة السويس. ويرى بعضهم أنّ في اسم ((جبل عتاقة )) بالقرب من السويس اشارة إلى عبور البحر.وللخروج معنى روحي عميق في حياة الشعب وفي حياة الكنيسة إذ أن في التحرر من العبودية رمز إلى عمل الله المجيد في الخلاص من عبودية الخطية والتحرر من سلطانها.'),
(1334, 'خَرْدل', 'نبات حباته صغيرة الحجم، ولكنها تنمو بحيث تصير شجرة أكبر من غيرها من الاعشاب (لو 13: 19 ومتى 13: 32 ومر 4: 32) ، تستقر الاطيار على أغصانها لتلقط حباتها. وهي أصغر البذور جميعاً (متى 13: 32) . وكبر النبتة التي تنمو من هذه البذرة يمثل تكاثر ملكوت السماوات من بداية ضئيلة . وقد استعمل اليهود هذه البذرة في أمثالهم كما استعملها يسوع (متى 17: 20 ولو 17: 6) للدلالة على الاشياء الصغيرة. والخردل المعروف في فلسطين هو الخردل الأسود Sinapis nigra  وهو نبات بري يبلغ في طوله علو الجواد وراكبه كما شاهده المسافرون وهو يزرع أيضاً في الحدائق طلباً في بذوره التي تستخدم كنوع من أنواع التوابل كالملح والفلفل.'),
(1335, 'خُرَافة', 'هي قصص وحكايات فارغة وآراء سخيفة (1 تي 1: 4) تستبدل حق الله بالكذب، وهي آفة كل الاديان والعقائد.'),
(1336, 'خرنوب', '(لو 15: 16) نوع من النبات اسمه باللاتينية Ceratonia siliqua  تؤكل قرونه بعد جفافها، وهو طعام لتسمين الخنازير والمواشي، ولكن كان يغرُّ على الابن الضال في المثل الذي ضربه المسيح. وشجرة الخرنوب جميلة المنظر دائمة الخضرة، تعلو إلى ثلاثين قدماً، وليس بها اشواك. وهي كثيرة القرون وتكبر قرونها أحياناً حتى تبلغ قدماً في طولها. ومع أنها طعام الخنازير والمواشي، إلا أن الناس ياكلونها مجففة إبان المجاعات، وتعصر القرون أحياناً وهي خضراء ويضاف عصيرها على لبن محَّلى بسكر فيجمد. وبداخل القرون بذور سمراء جافة أصغر من بذور الحمص الجاف. وإذا هزتها الرياح بعد الجفاف تحدث صوتاً أشبه بصليل بعض أنواع الافاعي. ويكثر الخرنوب في بعض بلدان الشرق، وفي جنوب أوربا مثل ايطاليا واسباني. وقال بعضهم ان يوحنا المعمدان كان يقتات منه وهو في البرية.'),
(1337, 'خَزَف', 'تحسب صناعة الخزف من أولى الحرف التي اتقنها الانسان في عصوره الأولى واغلب الظن ان بني اسرائيل تعلموا صناعة الخزف من المصريين. وقد استعملوا أواني خزفية أثناء رحلاتهم في البرية (لا 6: 28 و 11 : 33 وعدد 5: 17) . والطريقة التي كانت مستعملة عند بني اسرائيل، والتي اشار إليها الأنبياء مراراً، كانت تضاهي في أغلب الحالات الطريقة التي عرفها المصريون. فكان يُداس الطين اولاً بالأرجل على أن يصير الخليط متعادل الامتزاج (اش 41: 25) ثم يأخذ الخزَّاف كمية كافية ويضعها على قرص من الخشب في الدولاب الذي كان يُدار باليد أو بدواسة (ار 18: 3) . وكانوا بعد ذلك يطلون الوعاء بالدهان ثم يشوونه في الموقد. ويستدل من ار 32: 14 أن آنية الخزف لم تستعمل للطبخ فقط بل لحفظ بعض الأشياء فيها مثل الصكوك وغيرها. وقد وجد علماء الحفريات كثيراً من المستندات الخطيّة القديمة داخل أوان من الخزف. وقد اتخذت مقدرة الخزاف على صياغة أي شكل من الطين (اش 45:  9 وارميا 18: 5 - 12 ورومية 9: 20 - 25) تمثيلاً لقدرة الله وسلطانه على البشر. على أن الله، طبعاً، يمارس هذه السلطة وفق مقتضيات حكمته وعدله وصلاحه وحقه.'),
(1338, 'خِزامَة', '(2 مل 19: 28 وحز 29: 4) وهي حلقة كانت تدخل في انوف الحيوانات أو الناس لكي يجرها معذبوها. وما زالت تستعمل حتى اليوم للثيران الهائجة في بعض الخزائم تشبه الصنانير التي تستعمل لصيد الاسماك. وهذه كانت تمر بخياشيم السمك بعد صيده ويربط بها خيط بحيث تترك السمكة في الماء مربوطة بوتد على أن يستحسن الصياد أخذها (ايوب 41: 2 وحز 29: 4) . وكانت تستعمل أحياناً لجر الأسود وغيرها من الحيوانات المفترسة (2 مل 19: 28 وحز 19: 4) كما كانت توضع في شفاه الأسرى (2 اخبار 33: 11) .'),
(1339, 'خَزائم الأنف', '(اش 3: 21) وهي حلقات من الذهب او معدن آخر، تعلق عادة في المنخر الأيمن للزينة. وقد  استعمل عامة المصريين هذه الخزائم.'),
(1340, 'خَزانة - خزائن', '(يو 8: 20 و 1 اخبار 9: 26) مكان داخل الهيكل كانت تودع فيه العطايا. (انظر أيضاً ((هيكل)) ) .بَيت خزائن:(عز 5: 17) كانت بيوت خزائن ملوك يهوذا في المدن والقرى والحصون، والبرية (1 اخبار 27: 25) .'),
(1341, 'خَزائن الخمر', '(1 اخبار 27: 27) أماكن لحفظ الخمر، ربما كانت أقبية أو كهوفاً. وقد جرت العادة بين العبرانيين واليونانيين أن يطمروا جرار الخمر إلى العنق.'),
(1342, 'خَشَبَة', '(اطلب ((قصاص)) ) .'),
(1343, 'خُصَاصة', 'هي بقايا الحنطة بعد حصادها، أو العنب بعد قطفه. وقد أمر بنو اسرائيل أن يبقوا على الكرمة علالة، وعلى الزيتونة حصاصة، أي بعض الثمر للفقراء والغرباء واليتامى (راعوث 2: 2 و 16 واش 17: ولا 19: 9 و10 و23: 22 وتث 24: 19) . ولما أراد جدعون أن يُلجم الافراميين الذين تشكوا منه قال: ((أليست خصاصة أفرايم خير من قطاف ابيعزر)) (قض 8: 2) . وما تزال عادة ترك بقايا الحصاد للفقراء مرعية حتى اليوم في بعض بلدان الشرق الأدنى.'),
(1344, 'خَصيّ', 'تعني الكلمة الشخص الذي جرّد أو حرم من قواه الجنسية، وكانوا يستخدمون الخصيان في بلدان الشرق قديماً في الدور الداخلية (اش 56: 3 ومتى 19: 12) . وكثيراً ما كان أولئك الخصيان يحتلون المراكز الرفيعة ومراتب السلطان والجاه. ورئيس الخبازين كانوا كلهم خصياناً (تك 37: 36 و 40 : 2 و 7) . وقد خدم الخصيان بلاط ملوك بابل (دانيال 1: 3) وفارس وكانوا حرساً على أبواب قصورهم (استير 1: 10 و 2: 21) . وكان خصي يشرف على دار حريم الملك في فارس (استير 2: 3 و 14) . وانتدب خصي ليرافق الملكة (استير 4: 5) . كما خدم الخصيان في بلاط الملك آخاب وابنه وخدموا ايزابل الملكة أيضاً (1 مل 22: 9 و 2 مل 8: 6 و 9: 32) كما استخدمهم داود في بلاطه (1 أي 28: 1) وغيره من الملوك اللاحقين في مملكة يهوذا. وكان حامل كاس الملك هيرودس ومقدم طعامه، ورفيقه إلى غرفة النوم من الخصيان، كما كان خادم زوجته الحبيبة ماريمنا خصيّاً أيضاً، على ما يقول يوسيفوس المؤرخ اليهودي.وقد نصت الشريعة الموسوية على أن لا يدخل خصي أو مجبوب في جماعة الرب (تثنية 23: 1) . ولكن الله يعد في أشعياء 56: 4 و 5 بأن يكون لهؤلاء مكان في بيت الرب.'),
(1345, 'خصيّ حبسي', 'كان هذا الرجل وزيراً لكنداكة ملكة الحبشة، وقد أقيم على جميع خزائنها (اع 8: 27) ويرّجح أنه دخل اليهودية وقبل تلك الديانة. وعندما كان راجعاً من أورشليم التقى بفيلبس الذي بشره فقبل المسيح واعتمد على يديه (اع 8: 26 - 39) .'),
(1346, 'خَطيئة أو خطية', 'الخطية هي التعدي على شريعة الله واحكامه. وكل من يفعل الخطية يفعل التعدي أيضاً. وخطية الترك هي اهمال ما تفرضه شريعة الله. أما خطية الفعل فهي ارتكاب ما نهت عنه تلك الشريعة (تك 4: 7 ومتى 25: 45 و 1 يو 5: 17 وغلبا 3: 10 - 12 ورمية 3: 23 و 1 يو 3: 4) .'),
(1347, 'خَطِيب', '(اطلب ((ترتلوس)) ) .'),
(1348, 'خَفّاش', 'من الحيوانات النجسة التي حرم اكلها (لا 11: 19 وتث 14: 11 و 12 و 18) . والخفاش ليس طائراً، بل هو حيوان من ذوات الثدي واللبونة، وهو لا يشبه الطيور إلا من حيث قوة الطيران، وجسمه مغطى بالشعر لا بالريش، وله اسنان بدلاً من المنقار. وغعضاء الطيران تختلف فيه عن الطيور الأخرى، وهو يسكن غالباً في الكهوف والاماكن المقفرة القذرة (اش 2: 20) .'),
(1349, 'خَلخال', '(انظر أيضاً ((جُلجل )) (( ثياب )) ) ذكرت الخلاخيل في اش 3: 16 و 18 وهي حلي تلبس في الأرجل كما تلبس الاساور في الذراع، وتصنع من المواد التي تصنع منها الاساور كالذهب أو الفضة أو المعادن الرخيصة. ويقصد بلبسها الجلجلة اثناء المشي. وزعم بعضهم أن السلاسل التي كانت توضع في أرجل النساء بقصد الزينة، هي التي كانت تجعلهن يتخطرن في مشيتهن كما جاء في اش 3: 16 وما تزال هذه الحلي تستعمل في الشرق عند بعض الطبقات.'),
(1350, 'خَلْداي', 'اسم عبري معناه (( خالد أو باق)).(1) هو خلداي النطوفاني، وكان رئيس الفرقة الثانية عشرة في جيش داود (1 اخبار 27: 15) .(2) وهو ايضاً أحد الذين عادوا من السبي وقد ورد اسمه في بعض الترجمات باسم حلداي (زك 6: 10) . وفي عدد 14 ورد باسم حالم.'),
(1351, 'خَلْدة', 'اسم عبري معناه ((ابن عرس)) هي امرأة شالوم، وهي نبية شهيرة سكنت القسم الثاني من أورشليم (2 مل 22: 14) . في عهد الملك يوشيا وتنبأت عن خراب أورشليم، ولكنها أضافت أنه نظراً لتقوى الملك، فإنه سيموت قبل أن يشهد بعينيه هذه الكارثة (2 مل 22: 15 - 20 و 2 اخبار 34: 20 - 28) .'),
(1352, 'خَلاص', 'يراد بالخلاص في العهد القديم النجاة من الشر او الخطر (خر 14: 13 ومز 106: 8 - 10) . اما في العهد الجديد فقد خلع عليها معنى ىخر، هو انقاذ الخطاة بالايمان بيسوع المسيح. وهذا هو المراد عينه بعبارات ((يوم الخلاص)) (2 كو 6: 2) و ((انجيل الخلاص)) (اف 1: 13) وعبارات أخرى غيرها. وينطوي تحت معنى الخلاص في العهد الجديد غفران الخطيئة والخلاص من ربقتها ونتائجها وتطهير النفس وافراح الازلي (مت 1: 21 وعب 5: 9) .مخَلص:(انظر كلمة ((المسيح)) ) .'),
(1353, 'إِخلاص', '(2 كو 1: 12) ضد الرياء أو المكر، ومطابقة ما في القلب لما تنطق به الشفتان. وتشير الكلمة اليونانية في هذا الموضع إلى نور الشمس الساطع، وذلك لأن هذا النور الوهاج، إذا ما سطع على شيء ما، اظهر نقاوته خالية من كل زعل.'),
(1354, 'خَلق - خليقة', 'قد يكون معنى ((الخليقة)) كل المخلوقات (رو 8: 19) أو فعل الخلق ذاته (مر 10: 6) . ومعنى الخلق هو ابداع الاشياء التي لم يكن لها وجود. والفاعل في الفعل (( خلق )) هو الله دائماً. فالله خلق السموات والأرض (تك 1: 1) والحياة المائية والهوائية (عدد 21) والانسان (عدد 27) ، والكواكب (اش 40: 26) والريح (عاموس 4: 13) وهو الذي يخلق القلب النقي الطاهر (مز 51: 10) . والرب أمر فخلقت السموات بكل أجنادها وملائكتها، والشمس والقمر والنجوم والمياه التي فوق السموات (مز 148: 5) . لقد تكلم فصنع كل شيء، وهو القدير العزيز الحكيم. عليه تتوقف حياة كل المخلوقات، وبيده يرعاها ويصونها، واختفاء وجهه عنها يهلكها، ونسمته المبدعة تجدد الحياة على الأرض (مز 104: 27- 30) . وقد خلق الله العالمين ((بالكلمة)) الذي هو ((الابن)) (يو 1: 3 وافسس 3: 9 وكولو 1: 16 وعب 1: 2) .وتنقسم الخليقة إلى جزئين، يكمل أحدهما الآخر. ففي الجزء الأول (تك 1: 1 - 2 : 3) يستعمل اسم الجلالة ((الله)). وفي اليوم الثاني (تك 2: 4 - 25) ((الرب الإله)). يشير الجزء الأول إلى خليقة كل الكون، أما الثاني فإلى خليقة الإنسان، وهو فاتحة قصة سقوط الإنسان وفدائه. وفي سائر الأحوال ، الله هو المسيطر على شؤون العالم والبشر، وكل الأشياء مرتبة بحكمة، وتهدف إلى قصد حكيم في الكون ونحو الإنسان.أما الأيام الستة، فتشير إلى ستة أعمال في مدد إلهية تنتهي بالراحة الإلهية (تك 2: 2 و 3) . وكان العمل الأول خلق النور المنتشر، والعمل الثاني تنظيم للسموات، وفصلها عن سطح الأرض بواسطة الجلد. والعمل الثالث فصل المياه عن اليابسة وخلق النبات والعمل الرابع اظهار نور الشمس والكواكب عن طريق تكسير الابخرة. والعمل الخامس خلق الحياة الحيوانية الدنيا في الماء والهواء. والعمل السادس خلق الحيوانات البرية والانسان الذي خلق على صورة الله. وفي اليوم السابع استراح الله من عمله وبدأ يمارس وظيفة ((الحارس)) المدبر فبارك ما خلقه، وعين للانسان يوم راحة في الأسبوع لخير الجسد والنفس.ويعتقد بعض المفسرين أن لفظة ((يوم)) لا تعتبر بالضرورة مدة أربع وعشرين ساعة، ويقولون انها بالأرجح تشير إلى مدة جيولوجية طويلة الأمد. ولتاييد رأيهم يقولون أنه كثيراً ما استعملت لفظة ((يوم)) في الكتاب المقدس للدلالة على مدة أكثر من يوم شمسي، كيوم الاشرار، ويوم النقمة، ويوم الدينونة، ويوم الخلاص وألف سنة في عيني الرب كيوم واحد (مز 90: 4 و 2 بط 3: 8) .ومما هو جدير بالذكر في فهم قصة الخلق كما وردت في سفر التكوين هو أن الله خلق الكون ولم يتركه لذاته ولشأنه كما يزعم بعض الفلاسفة. إن قوته لا زالت عاملة في الكون خالقة مسيرة وحافظة.ثم إن الكتاب المقدس يعلمنا شيئاً آخر عن عمل الله في الخلق ، فمكانة الله في الخلق وفق تعليم الكتاب المقدس تختلف عن فكرة ارسطوطاليس الذي يتحدث عن العلة الأولى، وكأن لا اتصال بين الله وبين الخليقة سوى عن طريق سلسلة من العلل والمعلومات. فإن الكون وما فيه من صنع الباري اليوم كما يناقض تعليم ارسطوطاليس عن أزلية المادة إذ أن الكتاب المقدس يعلمنا بأن المادة بداية. ثم إن الكتاب المقدس في تعليمه عن الخلق يناقض الحلوليين الذين لا يفرقون بين الخالق وخليقته بل يمزجون بينهما فالله ليس الخليقة وليست الخليقة الله. وقد خلق العالم بمحض حريته لا كما يقول الغنوسيون بأن الخلق عبارة عن انبثاق من الله يشبه التوالد الذاتي، فصدر عنه كضرورة لا محيص منها. أنه مبدع الكائنات وهي في وجودها وسيرها وبقائها وانتظامها تعتمد عليه بما أنه الخالق والمسير والحافظ والمدبر لها ولكل ما يتصل بها.ومع أن مهمة الكتاب المقدس هو أن يعلمنا عن مكانة الله في الخلق والخليقة إلا أن ما يعلمنا إياه لا يتناقض مع العلم الصحيح الذي ثبتت صحته من غير شك.ويظهر لنا الله في سفر التكوين ((شخصاً)) لا مجرد قوة كما يزعم البعض، ويتمثل لنا عاملاً في خلق العالم و كل ما فيه من لا شيء. واسمى أعمال الله في الخلق هو الانسان ذروة الخليقة، وهو يعمل في الكون وفق نواميس وشرائع ثابتة، وإيماننا بالله يتسامى فوق كل الفروض والنظريات العلمية. وتؤيد لنا القصة أن الخليقة لم تكن وليدة الصدفة، بل من تدبير إله حكيم، مدبر عاقل، قادر على كل شيء يتكلم فيطيعه الخلق.وقد ثبت لدى العلماء أن بعض قصة الخليقة كما جاءت في سفر التكوين. وردت ايضاً في الآثار الأشورية في لوحات من الفخار. ولكن القصة الاشورية مضطربة ومفككة، حافلة باساطير الاقدمين يصعب فهم معانيها في كثير من المواضع. أما قصة سفر التكوين فمسلسلة ومرتبة ترتيباً محكماً. فضلاً عن هذا فإن القصة الاشورية تذكر عديداً من الالهة، أما قصة التكوين فتحدثنا عن إله واحد، هو خالق السموات والارض، ورب العالمين.'),
(1355, 'خَلّ', 'تصِير الخمر، أو أي شراب قوي حريفة بطريقة التخمير والتخليل (عدد 6: 3) فإذا زادت نسبة التخمير، أو حفظت الخمر زمناً طويلاً، صارت خلاً. والخل حامض يضرس الاسنان (ام 10: 26) ويقسي الصابون (امثال 25: 20) (( كخل على نطرون )). وهو في ذاته غير صالح للشرب (مز 69: 21) ولكنه إذا مزج بقليل من الزيت، صار صالحاً للشرب، وخاصة لإطفاء العطش متى غرَّ وجود الماء الصالح. وكان يستعمل مع الأطعمة الاخرى، ويغمس فيه الخبز (راعوث 2: 14) . وكان من عادة الجنود الرومان أن يشربوا في معسكراتهم نوعاً مخففاً من الخل ممزوجاً بالماء. ولعل شراباً من هذا النوع هو الذي قدمه الجندي الروماني ليسوع وهو على الصليب ليطفيء حرقة ظمائه (مر 15: 36 ويو 19: 29 و 30) . وهذه الجرعة التي شربها يسوع تختلف عن الخمر الحريف الذي سبق أن قدم له ورفضه، وكانت تلك الخمر ممزوجة بالمرّ (متى 27: 34 ومر 15: 23) .وما يزال الخل مستعملاً حتى اليوم في كثير من بلدان العالم، يضاف إلى بعض الأطعمة والتوابل ليكسبها مذلقاً حريفاً.'),
(1356, 'خُلُوِي', 'اسم يوناني معناه (( النبتة الأولى من العشب الاخضر )). وهي امرأة مسيحية، وأهلها هم الذين اخبروا بولس عن الانشقاق الذي حدث في كنيسة كورنثوس (1 كو 1: 11) .'),
(1357, 'خَمير', 'قطعة من العجين المختمر توضع في العجين لتخمره (خر 12: 15 و 19 و 13: 7) . وكان استعمال الخمير محظوراً في التقدمات للرب (لا 2: 11) ، وذلك لأن التخمير كان رمزاً إلى الفساد ويرمز إلى التعاليم الفاسدة (متى 16: 11 ومرقس 8: 15) وإلى الشر في قلب الانسان (1 كو 5: 6- 8 وغلا 5: 9) . ولم يكن يسمح لليهود باستعمال الخمير خلال ايام الفصح، وذلك رمز إلى الحياة عديمة الفساد التي تتطلبها خدمة الله الحقة. وكان هذا يذكَرِهم أيضاً بيوم خروجهم من مصر، حيث لم تمكنهم الظروف من أن يخمروا عجينهم. ولذلك أكلوا خبزاً فطيراً. ومن ذلك الوقت جعلوا أكل الفطير في الفصح تذكاراً لعَجلتهم. على أن الكلمة ((خمير)) وردت في المثل المذكورة في انجيل متى (متى 13: 33) بمعنى جيد، حيث شبه فعل الانجيل الخفي في قلب الانسان بالخميرة، وهو رمز إلى نمو ملكوت الله في العالم من بداية صغيرة.'),
(1358, 'خمسة', 'اسفار موسى الخمسة:هي الاسفار الخمسة الأولى من العهد القديم وهي: التكوين، والخروج، واللاويين، والعدد، والتثنية. وسميّت هذه الاسفار ((سفر شريعة الرب بيد موسى)) (2 اخبار 34: 14) و (( سفر شريعة موسى))  (2 اخبار 17: 9) و ((سفر الشريعة)) (2 مل 22: 8 و 2 مل 23: 21) و ((شريعة موسى )) (عز 7: 6) و ((سفر شريعة موسى)) (نح 8: 1) و ((سفر موسى)) (عز 6: 18 ونح 13: 1 و 2 اخبار 25: 4 و 13: 12) و ((التوراة)) (مت 12: 5) و ((الناموس)) (لو 10: 26 ويو 8: 5 و 17) .وقد اجمع جمهرة العلماء والباحثين على أن موسى هو كاتب هذه الاسفار أو أكثرها على الأقل، وإن يكن بعضهم قد شك في أن موسى هو كاتب الاسفار. وليس في الاسفار ذاتها آية واحدة تؤكد أن موسى هو كاتبها كلها. ومع ذلك فإنّا واجدون اقتباسين في القصة بقلم موسى ذاته: أحدهما قصة الانتصار على عماليق (خر 17: 14) . والثاني وصف رحلة بني اسرائيل من مصر إلى سهول موآب تجاه اريحا (عدد 33: 2) . وهناك أيضاً نشيد تهذيبي، يردد فضل الله على بني اسرائيل، ويقال أن موسى هو واضعه وملحنه (تث 31: 19 و 22 و 30 و 32: 44) . وكذلك نشيد الحمد والشكر على النجاة من يد فرعون والبحر الاحمر، وقد قيل أن موسى هو كاتبه ومنشده (خر 15: 1 - 18) .ويتألف القسم التشريعي القانوني في هذه الاسفار من ثلاث مجموعات: المجموعة الأولى تسمى ((كتاب العهد)) وتتضمن الوصايا العشر، وهي القانون الأساسي للأمة، وبعض اللوائح والقوانين المتعلقة بها (خر ص 20 - ص 23) . وقد قيل أن موسى هو كاتب هذه المجموعة  (خر 14: 4) . وتتألف المحموعة الثانية من شرائع وقوانين خاصة بالمقدس والخدمة (خر ص 25 - ص 31 ولاويين وجزء كبير من سفر العدد) . أما المجموعة الثالثة فقد قيل عنها صراحة أن موسى ألقاها ورددها في آذان الاجيال المقبلة عشية دخولهم أرض كنعان. وهي تشمل بياناً موجزاً للطريقة التي قاد بها الله الشعب، ثم تكراراً لبعض أجزاء الشريعة، وابراز النواحي الروحية والدينية فيها، والظروف المستخدمة التي ستطبق فيها هذه الاحكام. وقد ألقى موسى هذه العظات للشعب وكتبها وسلمها إلى اللاويين ليحتفظوا بها في عودتهم (تث 31: 9 و 24 - 26) . هذا هو الدليل الداخلي في الاسفار ذاتها التي تشير إلى أن موسى كاتبها.وهناك أدلة أخرى غير هذا الدليل الداخلي، فإن بقية اسفار العهد القديم تشير إلى أن موسى هو كاتب الشريعة (يشوع 1: 7 و عزرا 6: 18 ونحميا 8: 1 و 18) . وكذلك توجد نصوص كثيرة في العهد القديم فيها اشارات واضحة صريحة إلى ((شريعة موسى)) (يشوع 8: 31 - 35 وقض 3: 4 و 1 ملوك 2: 3 و 2 ملوك 8: 6 و 12 و 21: 7 و 8 ودانيال 9: 11 و 13 وعزرا 3: 2 و 7: 6 وملا 4: 4) .ثم اننا نجد في العهد الجديد اشارات كثيرة إلى أن موسى هو كاتب الاسفار الخمسة. فهذا هو رأي علماء اليهود وقادتهم في زمن السيد المسيح (مر 12: 19 ويو 8: 5) . وقد اشار السيد المسيح والبشيرون إلى هذه الاسفار الخمسة وإلى أن كاتبها هو موسى (مر 12: 26 ولو 16: 29 و 24: 27 و 44) . فيقولون (( جاء في موسى )) أو ((كتاب موسى )) ثم يشيرون أيضاً إلى أن موسى أعطاها أو جاءت على يديه أو أنه كاتبها (مر 10: 5 و 12: 19 ويو 1: 17 و 5 46 و 47 و 7: 19) .وقد زعم بعضهم أن هناك عبارات يصعب صدورها عن موسى، إلا اننا عند درس هذه العبارات لا نجد مشقة البتة في قبول صدورها عن موسى فمثلاً القول ((وكان الكنعانيون حينئذ في الارض)) (تك 12: 6) وهي عبارة تاريخية صادقة فإن الكنعانيين كانوا حقاً وفعلاً في الأرض في زمن ابراهيم. كذلك القول الوارد في (تثنية 1: 1) (( في عبر الأردن)) فيزعم بعضهم إن هذه العبارة لا تصدر إلا عن كاتب كان في كنعان فعلاً. ولكن ألا يعتبر هذا الاسم ((عبر الأردن)) اسم علم لشرق الأردن بدون أي نظر إلى المكان الذي يوجد فيه الكاتب.أما عن سجل موت موسى في تثنية 34: 5 - 12 فهل يصعب أن يكون هذا الجزء قد أضيف بعد موت موسى بارشاد الروح القدس. وهذا لا يعني البتة أن شخصاً آخر غير موسى كتب هذه الاسفار الخمسة.فكثرة علماء الكتاب المقدس مقتنعون بأن موسى هو كاتب تلك الاسفار، وذلك لأنه عرف شخصياً جانباً عظيماً من الحوادث التي جاءت بها، وخاصة ما يتعلق بمصر واحوال شعبها وحضارتها، مثال ذلك تلميحه إلى طريقة السقي (تث 11: 10) والحرب (تث 20: 5) واستخراج المعادن (تث 8: 9) والقصاص (تث 20: 5) واستخراج المعدن (تث 8: 9) والقصاص (تث 20: 5) الخ. ولا يقدر أحد أن يقدم البيان الوافي عن رحلة البرية ما لم يكن هو على رأس الراحلين. فضلاً عن هذا فإن أسلوب الاسفار الخمسة ولاهوتها وخاصة ما تعلق بالثواب والعقاب في الآخرة، يرجع تاريخها إلى عصر مبكر قبل عصر داود، يرجع تاريخها إلى عصر مبكر قبل عصر داود، وقبل السبي. وقد ايدت الحفريات تاريخية الاسفار الخمسة فاثبتت وجود برج بابل وكذلك وجود الحثيين، وكان زعم البعض قبل ذلك بعدم وجودهم. واثبتت الحفريات أيضاً ما حدث من اقلاب مدن الدائرة كسدوم وعامورة. وكذلك ايدت الحفريات ما تذكره هذه الاسفار عن حالة الآباء الأولين من حيث أنها توافق ما كان مألوفاً ومعروفاً في القرن العشرين قبل الميلاد، لا في عصر نتأخر عن هذا.'),
(1359, 'خَمسون - يوم الخمسين - عيد الخمسين', 'هو عيد الاسابيع (خر 34: 22 ولا 23: 15 وتث 16: 9) وسميّ يوم الباكورة (عد 28: 26) وكان يقع في اليوم الخمسين بعد اليوم الثاني من الفصح (لا 23: 15 و 16 وتث 16: 9 و 10) . وكان أحد الاعياد الثلاثة التي كان يتحتم على الذكور من الشعب أن يذهبوا فيها ليمثلوا أمام الرب (خر 34: 22 و 23) . وكان يعتبر سبتاً أي زمن راحة لا يقومون فيه باي عمل بل يجتمعون معاً للعبادة (لا 23: 21 وعد 28: 26) . وقد بدأ في الأول كيوم شكر لاجل الحصاد في البلاد المقدسة، وكانت مدته يوماً واحداً. وكانوا يقدمون فيه رغيفين من الدقيق الذي طحن من غلة الحصاد (لا 23: 17 و 20 و تث 16: 10) . وكذلك كانوا يقدمون عشر ذبائح في ذلك اليوم (لا 23: 18 و 19) وكانوا يحثون الشعب في هذا العيد أن يذكروا المحتاجين كاللاوي والعبد والامة واليتيم والارملة (تث 16: 11 و 12) .ويقول التقليد اليهودي إن الناموس اعطي في اليوم الخمسين بعد خروجهم من مصر، ولذا فحفظ اليهود اليوم تذكاراً لاعطاء الناموس أكثر مما يحفظونه كيوم عيد جمع الحصاد.وقد انسكب الروح القدس في يوم الخمسين حين كان كثيرون قد أتوا إلى اورشليم واجتمعوا بمناسبة هذا العيد (اع 2: 1 - 14) وقد أخذت الكنيسة المسيحية تحفظه ضمن أعيادها - وهو عيد العنصرة.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1360, 'خَنْزير', 'كان الخنزير من الحيوانات النجسة (لاويين 11: 7 وتث 14: 8) وذلك لأنه قذر وهو لا يجتز طعامه، ويولد لحمه بعض الأمراض إذا لم ينضج عند طبخه. وكان محرناً على العرب تربيته، وقد حرم القرآن أكله، كما حرمه التوراة. وقد حسبه الفينيقيون والاثيوبيون والمصريون نجساً مع أنهم في مصر كانوا يقدمون خنزيراً ذبيحة في العيد السنوي لاله القمر ولاوزبريس (باخوس) . ومع ذلك فإنه كان يتحتم على من يلمس خنزيراً ولو عرضاً أن يغتسل. ولم يكن يُسمح لراعي الخنزير أن يدخل الهيمل، ولم يكن يتزوج إلا من بنات الرعاة مثله، لأن أحداً لا يرضى أن يزوج ابنته من راعي الخنازير (راجع هيرودتس 2: 47) أما عند اليهود فكان لحم الخنزير محرماً بقذارته (امثال 11: 22 ومتى 7: 9 و 2 بط 2: 22) وكان رعي الخنازير من احط المهن وادناها، لا يقربها إلا الفقراء المعدمون (لو 15: 15) على أن اليهود المستبيحين (اش 65: : 4 و 66: 17) . وفي عصر انتيخوس ابيفانيس كانوا يأمرون اليهود بأكل لحم الخنزير للتأكد من عدم بقائهم على دينهم القديم، أو الولاء لدين غزاتهم وحكامهم (1 مكابيين 1: 47 و 50 و 2 مكابيين 6: 18 و 21 و 7: 1) وفي عصر المسيح كان بعضهم يرعون قطعاناً من الخنازير (مر 5: 11 - 13) في مستعمرة اغلب سكانها من اليونان. وما كانوا يربونها ليأكلوا لحومها، بل ليبيعوها إلى اليونان أو للجيوش الرومانية.'),
(1361, 'خُودَش', 'اسم عبري معناه ((الهلا)) وهو اسم زوجة شحرايم (1 اخبار 8: 9) .'),
(1362, 'خَوذَة\"', '(اطلب ((سلاح)) ) .'),
(1363, 'خَوزي', 'وربما هو اسم ارامي معناه ((ابريق)) وهو وكيل هيردوس انتيباس. وامرأته يوَّنا كانت بين النساء اللاتي خدمنَ يسوع في حياته ومماته (لو 8: 3 و 24 10) .'),
(1364, 'مَخاضة', 'يراد بها معبر أو المكان الذي يعبر فيه مجرى من الماء كالأردن (يش 2: 7 وقض 3: 28 و 12: 5 و 6) ويبوق (تك 32: 22) وارنون (اش 16: 2 معابر) والفرات (ار 51: 32 معابر) وتبلغ مخاوض الأردن الخمسين.'),
(1365, 'مخافة أو خوف', 'خوف الله أو مخافته. وخوف الله نعمة، وهو دليل الاكرام لاسمه القدوس، وخشية اغاظته عند تعدي شريعته الطاهرة. وخوف الله يقتضي السهر والتذلل والصلاة، ويقترن بالمحبة والطاعة لأنه اشبه بخوف الابن من أبيه. ان في خوف الله تشترك أقوى الحواس وأشد العواطف: المحبة والطاعة والثقة والشكر.'),
(1366, 'خُون أو كون', '(1 اخبار 18: 8) هي ((بيروثاي)) المذكورة في 2 صم 8: 8 (اطلب ((بيروثة)) ) .'),
(1367, 'يَختار - مختار - اختيار', 'اقترنت كلمة ((مختار)) ببعض الاشخاص في العهدين القديم والجديد (اش 65: 9 ومت 24: 22 ومر 13: 27 ولو 18: 7 ورو 8: 33 وكو 3: 12 وتي 1: 1) . وجاء في العهد الجديد عبارات تحمل معنى الاختيار كقوله ((المختارة معكم)) (ابط 5: 13) و ((قصد الله حسب الاختيار ))  (رو 9: 11) ((واختيار النعمة)) (رو 11: 5) و ((من الله اختياركم)) (1 تس 1: 4) و ((دعوتكم واختياركم)) (2 بط 1: 10) .وتدل هذه النصوص الكتابية وغيرها على أن الاختيار عمل من اعمال الله يقصد به أن يختار قوماً من البشر ويخلصهم لكي تكون لهم علاقة خاصة. وسر هذا الاختيار فوق ادراك البشر، أما أثره فيعرفه البشر ويدركونه، بل أن الكتاب يحثهم على التحقق منه.العنصر السري في الاختيار إذن ينحصر في أنه عمل الله ومن ذا الذي يعرف فكر الله؟ وهو ليس من ابتدع البشر وليس في مقدورهم (رومية 9: 11) وهو يتصل بقدرة الله وحريته في أن يعمل كما يشاء. وكذلك يتصل الاختيار باعمال عناية الله وبمعاملاته للبشر. انظر مثلاً يعقوب وعيسو (رومية ص 9) ((لكي يثبت قصد الله حسب الاختيار)).الاختيار في العهد القديم:ويظهر الاختيار في العهد القديم في أن الله اختار جماعة خاصة لعرض خاص (تثنية 4: 37 و 7: 6-8 و 10: 15 و 14: 2 و 1 ملوك 3: 8 و اش 14: 1 وحجي بأن اختياره له لا يتوقف على فضيلة فيه أو على استحقاق من جانبه إنما تمّ نعمة من الله وفضلاً (تثنية 7: 6-8) .'),
(1369, 'خيْمَة', '(انظر كلمات ((مساكن)) ((عيج المظال)) ((كرم)) ((بستان)) ) .الخَيْمَة:الخيمة الاصلية التي كان يجتمع فيها الرب بشعبه (خر 33: 7 - 10) ، ولذلك سميت (0خيمة الاجتماع)) وأطلقت (0الخيمة) 9 على بيت آخر وضع فيه داود التابوت (2 صم 6: 17 و 1 اخبار 16: 1) .أما الخيمة الأصلية فهي التي أمر الله موسى أن يقيمها في البرية لكي يسكن الله فيها بين شعبه (خر 25: 8 و 9) . ولذلك سميت ((المسكن)). وكانت تودع فيها ألواح الناموس والشهادة ولذلك سميت ((مسكن الشهادة)) (خر 38: 21) . وقد أطلق عليها اسم علم ((بيت الرب)). وقد صنعت الخيمة من المواد الأولية التي وجدت في النواحي المجاورة، شجر السنط الذي كان ينبت في البرية، وجلود الحيوانات، والذهب، والفضة، والنحاس وأدوات الزينة. وهذه كلها تبرع بها الشعب في سخاء وحماس (خر 35: 21- 29) . ولم يكن هذا النفاق السخي ضياعاً، فإن المعادن الثمينة في مثل هذا المسكن المؤقت المتنقل، كان مقدَّراً لها أن تنتقل فيما بعد إلى بناء فخم دائم. وكذلك الآنية المقدسة كانت ستنتقل إلى مسكن جديد مستديم.وانقسمت الخيمة إلى ثلاثة أجزاء: المسكن، والخيمة، والغطاء.أما ((المسكن)) فصنع من البوص المبروم المطرز بالكاروبيم، ومن ألواح للمقدس وقدس الأقداس.أما الخيمة فكانت فوق المسكن، وكانت مصنوعة من شعر المعزى.أما الغطاء فكان من جلود الكباش والتيوس، وكان يوضع فوق الخيمة والمسكن لوقايتهما من الشمس والمطر.وأحاط بدار المسكن شقق من بوص مبروم علوها نحو ثمانية أقدام معلقة بعواميد نحاس يبعد الواحد عن الآخر نحواً معامل ثمانية أقدام بواسطة عُرى من الفضة. وكان على كل طرف عشرة. فكان طول المساحة مائة وخمسين قدماً، وعرضها خمسة وسبعين قدماً. وكان عرض المدخل إلى شرقي المسكن ثلاثين قدماً ومغطى بشقة من بوص حبال وأوتاد من نحاس وقواعدها من نحاس، وكانت رزز الأعمدة وقضبانها من فضة.وبقرب الطرف الغربي من الدار كان طول الخيمة ذلتها 45 قدماً وعلوها 15 قدماً. وكان جانباها ومؤخرها مغلفة بألواح، وكان مقدمها مفتوحاً. وفي كل جانب عشرون لوحاً. وفي المؤخر ثمانية ألواح. ولكل لوح طرفان من الفضة يدخلان في قاعدتين من فضة. وكانت الألواح موصولة بعوارض من خشب السنط مصفحة بذهب، تنفذ بحلقات من ذهب.وكان مدخل الخيمة بشقة مزخرفة معلقة على خمسة عواميد. وانقسم داخلها إلى المقدس، وقدس الأقداس، يفصل بينها شقة مطرزة من أعلى المسكن إلى أسفله، وسِمّيت هذه الشقة بالحجاب.وكان في دار المسكن:(1) مذبح المحرقة بقرب مركز الدار.(2) المرحضة من نحاس (خر 30: 18) وسميت أيضاً بحر النحاس (1 مل 7: 23) وكانت بين المذبح والخيمة. وكان فيها ماء لغسل أيدي وأرجل الكهنة عند دخولهم إلى المقدس.أما أثاث الخيمة فكان. (1) منارة الذهب إلى اليسار. (2) مائدة خبز الوجوه قبالة المنارة. (3) مذبح البخور بين المنارة ومائدة خبز الوجوه وأمام التابوت. (4) تابوت العهد.وقد اشتغل الصناع تسعة أشهر في إقامة الخيمة، وكان من ضمن هؤلاء الصناع بصلئيل واهولياب وغيرهم ممن أعطاهم الله روح حكمة وبصيرة نيّرة ومهارة فائقة لإتمام هذا العمل (خر 31: 2-6). ودشنت بعد الانتهاء منها بشعائر دينية (خر ص 40 و عب 9: 21). وكانت تنصب مدة السفر في البرية في وسط المحلة تحيط بها خيام الكهنة واللاويين، ثم خيام بقية الأسباط حواليهم في أربعة أقسام (عدد 2: 2-34). وكان صنع الخيمة دقيقاً بحيث يمكن فكها وحملها ونصبها في مكان آخر. وكان موضع كل محلة، والنقل إلى محلة أخرى، وترتيب الارتحال، مرسومة من الله. وفي اليوم الذي أكملت فيه الخيمة أظهر الله ذاته في سحابة غطتها وملأتها. وبعد ذلك تحولت السحابة إلى عمود كان  مام الشعب في رحلاتهم. فكان إذا وقف العمود فوق الخيمة، ينزل الشعب. وإذا انتقل، نقلت الخيمة وتبع الجمهور السحابة. وفي الليل استحالت السحابة عامود نار سائر أمامهم (خر 40: 35-38 و عدد 9: 15-23).وعندما انتهت رحلات الشعب استقرت الخيمة في الجلجال (يش 4: 19). وبقيت هناك حتى تم افتتاح البلاد ثم نقلت إلى شيلوه مدة ثلاث مائة أو أربع مائة سنة. ومن هناك نقلت إلى نوب (1 صم 21: 1-9) وفي ملك داود نقلت إلى جبعون (1 اخبار 21: 29). وكانت هناك في مستهل حكم سليمان (2 اخبار 1: 3-13). وبعد إتمام بناء الهيكل نقلت مع كل أثاثها وآنيتها، وقد بنى الهيكل على نمط الخيمة، وإن يكن ضعفها في حجمها طولاً وعرضاً وعلواً.وقد كانت خيمة الاجتماع مركز عبادة شعب الله قبل بناء الهيكل. ثم أن نظامها وترتيب العبادة فيها علما الشعب أشياء كثيرة عن قداسة الله وحلوله بينهم وحضوره في وسطهم، كما وعلّمت أشياء عن الذبائح والكفارة. وتعلّم الشعب من تقديم البخور قدسية الاقتراب إلى الله في الصلاة. وتعتبر الخيمة أو المسكن في العهد الجديد رمزاً للمسيح (عب 9: 11) ثم يتحدث سفر الرؤيا عن سكن الله مع الناس وإنه سيسكن معهم. وفيه إشارة إلى دوام الشركة الروحية والتمتع الأبدي بالحضرة القدسية (رؤيا 21: 3).أما ((خيمة ملكوم)) (عا 5: 26) فيرجح أنها كانت صغيرة تُحمل على الأكتاف، ومناسبة لوضع التمثال فيها.'),
(1370, 'خِيوس', '(اع 20: 15) جزيرة في الارخبيل اليوناني، على تخوم آسيا الصغرى، بين لسبوس وساموس. يبلغ طولها نحو 32 ميلاً وعرضها 15 ميلاً تينها وعنبها ورخامها. وقد مر الرسول بولس بها أثناء سفره من ميتيليني إلى ساموس.'),
(1371, 'خَشَب جُفْر', 'هو الخشب الذي بني به فلك نوح (تك 6: 14) ولا بد أنه كان خشباً متيناً. ويرّجح بعضهم أنه صنف من أصناف السرو لصلاحيته لبناء السفن وعدم قابليته للنخر والسوس. ويعتقد أن الاسم العبري ((جوفر)) يقابل الاسم العربي ((كافور)).'),
(1372, 'خَمْر', 'كانت الخمر تصنع من العنب، فكانوا يجمعون العناقيد في سلال (ارمياء 6: 9) ثم يحملونها إلى المعصرة ويلقونها هناك. وكانت المعصرة مركبة من دنّ قليل العمق مبني على الأرض أو منقور في الصخر (اش 5: 2) ، ويتصل بثقوب في اسفله بدنّ آخر، منقور في الصخر ايضاً. وكان العنب يُسحق بطريق الدوس بالأقدام (نحميا 13: 15 وايوب 24: 11) ، وكانوا يستخدمون رجلاً أو أكثر حسب حجم الدن. وكان الائسون - في مصر وربما في فلسطين أيضاً - يمسكون بحبال معلقة حتى لا يسقطوا، ويغنون أثناء عملهم على وتيرة واحدة كنوع من التسلية والترفيه أثناء العمل (اش 16: 10 وارميا 25: 30 و 48: 33) وحولهم وثيابهم (اش 63: 1 - 3) . ومن الدنّ الأعلى ينساب العصير إلى الدنذ الأسفل، ويوضح بعد ذلك في أزقة أو قنينات من الجلد (ايوب 32: 19 ومتى 9: 17) أو في أوعية كبيرة من الفخار، حيث يترك طويلاً ليختمر. وبعد الاختمار تنقل إلى أوعية أخرى (ارمياء 48: 11 و 12) .وكان عصير العنب يُستمل بعد عصره بطرق مختلفة: كشراب فاكهة غير مختمر، أو كخمر بعد التخمير، أو كخل بعد زيادة تخميره. ولعلهم كانوا في الزمن القديم - كما في الوقت الحاضر - يصنعون من العصير شراباً حلواً بعد غليه.وقد ذُكرت الخمر مع الحنطة والزيت كعطية عظمى للإنسان، وكانت في كل بيت يقدمونها للضيوف لا سيما في الأعياد (تك 14: 18 ويو 2: 3) . غير أن اليهود، وسائر الأمم، كما وبخهم أيضاً العهد الجديد (ام 20: 1 و 23: 29 - 35 واش 5: 22 و 28: 1 - 7 و 56: 12 وهوشع 4: 11) . وفي الطقس الموسوي كانت السكيب من خمر مع المحرقة اليومية (خر 29: 40) وعند تقديم الباكورات (لا 23: 13) وعند تقديم بقية الذبائح (عدد 15: 5) . وكان يدفع العشر منه (تث 18: 4) ولم يكن يُسمح للنذير بأن يشرب منه مدة نذره (عدد 6: 3) ولذلك لم يكن يسمح للكاهن بأن يشرب منه عند دخوله لخدمة المقداس (لا 10: 9) ولم يكن لائقاً للقضاة أن يشربوا منه عند جلوسهم في مجالس القضاة. (امثال 31: 4 و 17 و 23: 20 و 21 و 29 - 35) . وقد اتخذت في العهد القديم احتياطات كثيرة لوقاية الناس من الافراط في شربها، كمزجها بالماء. ويذكر الكتاب أنهم كانوا يعينون رئيساً للوليمة ويعتقد أنه كان يعين لهذا الغرض (يو 2: 9 و 10) . وقد نهى الكتاب عن السكر بالخمر، وعلّم أن السكر بها خطيئة (1 صم 1: 14 - 16 واش 5: 11 -17 و 1 كو 5: 11 و 6: 10 وغلا 5: 21 وافسس 5: 18 و 1بط 4: 3) . والخمر الممزوجة (امثال 9: 2) والشراب الممزوج ليسا خمراً ممزوجة بماء لتخفيفها بل يشيران إلى مزيج الخمر بأنواع من العقاقير والتوابل التي تجعل طعمها يلذ بأنواع من العقاقير والتوابل التي تجعل طعمها يلذ ساربيها، وتجعل لونها شائقاً.'),
(1373, 'داثان', 'اسم عبري قريب من الاسم الاكادي ((داثنو)) الذي معناه ((قوي)) وهو اسم ابن الياب من بني راوبين. وقد اشترك وقد اشترك مع ابيرام أخيه واون في عصيان قورح (عدد ص 16 و 26: 7-11 و تث 11: 6 و مز 106: 17).'),
(1374, 'داجون', 'اسم كنعاني من ((داجان)) ومعنا ((حنطة)). ويظن بعضهم أنه من ((داج)) ومعناه ((سمكة)). وهو اسم صنم عند الفلسطينيين (1 صم 5: 1-3) ويذكر الكتاب المقدس ثلاث حوادث حدثت في هيكل للإله داجون: (1) ففي هيكل الفلسطينيين لداجون مات شمشون (قض 16: 23-30). (2) وقد قتل الفلسطينيون في اشدود عندما أخذ تابوت العهد إلى هيكل داجون إلههم (1 صم 5: 1-7) وسقط تمثال إلههم إلى الأرض. (3) بعد معركة جلبوع التي انتصر فيها الفلسطينيون على العبرانيين أخذ رأس شاول وعلق في هيكل داجون (1 اخبار 10: 10) في بيت شان. وقد كشف التنقيب عن هذا الهيكل. وقد كان هذا الإله يعبد في فينيقية. وتقول نصوص اكتشفت في رأس شمرا ((داجان)) أو ((داجون)) أب اليان وهو بعل كان يعبد كإله الحنطة. وقد كشف التنقيب عن هيكل له في أوجريت أو رأس شمرا. وقد ظن بعضهم أن التمثال الذي نصفه الأعلى إنسان ونصفه الآخر سمكة هو تمثال داجون. ويذكر ديدوروس الصقلي آلهة في اشقلون إحدى مدن فلسطين اسمها ((دركيتو)) نصفها السفلي على هيئة سمكة ونصفها العلوي على شكل امرأة. ويبدو أن عدم السير على عتبة هيكل داجون، من طقوس الفلسطينيين (قارن 1 صم 5: 5. ويظن بعض العلماء أن داجون كان يعبد في أكاد في القرن الخامس والعشرين قبل الميلاد.'),
(1375, 'دَارِيُّوس', 'اسم من الفارسية القديمة معناه ((مالك الخير)). يطلق الاسم على عدة ملوك جاء ذكرهم في التاريخ القديم:(1) داريوس المعروف الماديّ (دا 5: 31 و 11: 1) ابن احشويروش (دا 9: 1) تولى الملك على الكلدانيين بعد استيلاء جيش كورش على بابل وكان ابن اثنتين وستين سنة وقد ولّى على المملكة 120 مرزبانا (سطرباً) وأقام عليهم ثلاثة وزراء أحدهم دانيال. وكان له حق الكتابة إلى كل الشعوب (دا 6: 25) وملك بابل نيابة عن كورش. ويظن البعض أنه كياكسريز خال كورش. ويظن آخرون أنه جبرايس حاكم بابل وقائد جيوش كورش.(2) داريوس بن هستاسبس الذي حكم من سنة 521-486 ق.م. وفي أول حكمه وقف العمل في بناء الهيكل في أورشليم بسبب شكوى القبائل المجاورة، ولكنه بالبحث وجد في اخميثا عاصمة ماداي أمر الملك كورش بإعادة لهيكل. فأمر أن يرجع اليهود إلى أورشليم فعادوا إلى العمل في إقامة الهيكل (عز 6: 1-15 قابله مع زك 7: 1-3) وفي أيامه تنبأ حجي و زكريا (حج 1: 1 و 2: 1 و 10 و 18 و زك 1: 1 و 7 و 7: 1). وفي زمن حكمه أيضاً عصت بابل تحت إِمرة ((ندنتوبيل)) ولكن داريوس هزمه سنة 521 فتم فيها ما قيل في نبوة اشعياء ص 47. ويقول هيرودوت أن داريويس أمر بأن تنزع من بابل أبوابها النحاسية التي كان عددها مئة (ار 51: 58) وقد انهزم هذا الملك في موقعة ماراثون. وقد دون هذا الملك أعماله على حجر بهستون بالقرب من همدان في ثلاث لغات: الفارسية القديمة، والبابلية، والعيلامية. (اطلب ((احشويروش)) و ((فارس)) ).(3) آخر ملوك الفرس وقد حكم من 336-331 ق.م. وقد تسمى بهذا الاسم عندما تبوأ العرش، واسمه قُدُمانس. وقد تغلب عليه الاسكندر الأكبر (1 مكا 1: 1) وانقرضت بموته دولة الفرس الأولى وتم فيها ما جاء في سفر دانيال من النبوات عنها (دا 2: 39-40 و 7: 6 و 7 و 8: 5 و 6 و 20 و 21).'),
(1376, 'دَامَرِس', 'اسم يوناني يرجح أنه صيغة أخرى من الاسم داملس ومعناه ((عِجْلة)) (اع 17: 34) وهو اسم امرأة من اثينا آمنت بالإنجيل بسبب كرازة بولس.'),
(1378, 'دانيال', 'وهو اسم عبري معناه ((الله قضى)) وهو اسم:(1) أحد الأنبياء الأربعة الكبار وكان من عائلة شريفة ويظن أنه ولد في أورشليم (دا 1: 3 قابل يوسيفوس) وأُتي بأمر نبوخذ نصر \"إلى بابل مع ثلاثة فتيان من الأشراف: هم حننيا وميشائيل وعزريا سنة 605 ق.م. فتعلم هناك لغة الكلدانيين ورشح مع رفقائه الثلاثة للخدمة في القصر الملكي (دا 1: 1-4 و ار 25: 1) فغير رئيس الخصيان أسمائهم فسمى دانيال بلطشاصر ورفقاءه شدرخ وميشخ وعبدنغو. وأبى هؤلاء الأربعة أن يأكلوا من طعام الملك وأن يشربوا من خمره واختاروا القطاني والماء. ومع بساطة هذا المأكل والمشرب فإن مناظرهم ظهرت أحسن من بقية الفتيان الذين تناولوا من أطايب الملك وخمره.ثم بعد ما تعلم دانيال ثلاث سنين أعطاه الله فرصة لإظهار علمه وحكمته ففسر حلماً لنبوخذ نصر كان قد أزعجه (دا ص 2) ومكافأة له على هذه الخدمة نصبه حاكماً على بابل ورئيساً على جميع حكمائها. وفي هذا المنصب اشتهر بين أهل جيله (حز 14: 14 و 20 و 28: 3). مع أن بعض العلماء يظنون أن في هذه الكلمات إشارة إلى شخص آخر اسمه دانيال عاش في عصر مبكر قبل دانيال صاحب السفر. ثم أبان للملك في وقت آخر ما كان الله قد قصد أن يقاصه به لأجل عنفوانه وكبريائه (دا ص 4). ووقع له أيضاً مثل ذلك مع بيلشاصر ابن ابنة نبوخذ نصر وابن نيونيدس والذي حكم  في بابل نيابة عنه، وقد قرأ دانيال الكتابة على الحائط (دا ص 5).وفي أيام داريوس المادي جعل دانيال أول الوزراء الثلاثة في دولة مادي وفارس، فدسّ أعداء دانيال له وحملوا داريوس على أن يصدر أمراً بأن لا تقدم صلاة إلا للملك مدة 30 يوماً، أما دانيال فلم ينقطع عن الصلاة ولذلك طرح في جب الأسود حسب أمر الملك (ص 6: 3-23 ثم انظر 1 مكابيين 2: 60) وفي السنة الأولى لداريوس كان السبي يقرب من نهايته (ارميا 25: 11 و 12 و 29: 10 و دانيال 9: 1 و 2). وقد تذلّل دانيال أمام الرب واعترف بخطايا شعبه واتجه إلى الله في الصلاة ونتيجة لهذه كلها أعلنت له نبوة السبعين أسبوعاً (ص 9: 24). وفي السنة الثالثة لملك كورش ملك الفرس رأى دانيال في رؤيا النزاع الأخير بين قوات العالم وملكوت الله (ص 10-12). وقد أشار العهد الجديد إلى دانيال في مت 24: 15 و مرقس 13: 14 و عبرانيين 11: 33. وقد ورد ذكره في الابوكريفا في 2 اسدراس 12: 11 و 1 مكابيين 2: 60.سفر دانيال:يوضع سفر دانيال في الكتاب المقدس باللغة العربية وكذلك في اللغات الحديثة كالإنكليزية والفرنسية والألمانية، ضمن الأنبياء الكبار فيأتي في الترتيب بعد حزقيال. وفي هذا تتبع هذه الترجمات الترتيب الذي سارت عليه الترجمة السبعينية ومن بعدها ترجمة الفلجات اللاتينية. أما موضع السفر بحسب ترتيب الأسفار في العهد القديم كما جاء في الأصل العبري فيقع في القسم الذي يسمى يسمى ((الكتوبيم))س أي ((الكتب)). وهو القسم الثالث من العهد القديم في اللغة الأصلية.وينقسم السفر بالنسبة إلى محتوياته إلى قسمين رئيسين وهما:أولاً: الأجزاء التاريخية.ثانياً: الأجزاء الرؤوية أو النبوية.وفي القسم الأول نجد:(1) مقدمة السفر كله-وفيها نرى صورة لدانيال ورفاقه الثلاثة وكلهم من أبناء الأشراف في يهوذا الذين أخذوا في السبي إلى بابل وكيف أنهم رفضوا أن يتنجسوا بأطايب الملك وبخمر مشروبه (ص 1).(2) دانيال يفسر حلم نبوخذ نصر عن التمثال الهائل العجيب (ص 2).(3) رفض رفاق دانيال أن يسجدوا لتمثال الملك، وإلقاؤهم في اتون النار المتقدة، ونجاتهم العجيبة من الاحتراق بنيرانه الملتهبة (ص 3).(4) دانيال يفسر حلم الملك عن الشجرة العظيمة التي قطعت (ص 4).(5) دانيال يفسر الكتابة على الحائط في الوليمة التي أقامها بيلشاصر (ص 5).(6) داريوس الماديّ يرفع دانيال إلى أسمى المناصب في المملكة فيثير هذا حسد أعدائه فيكيدون له ويلقى في جب الأسود. ولكن الرب ينجيه من جب الأسود (ص 6).ونجد في القسم الثاني أربع رؤى عظيمة يراها دانيال وهي:(1) رؤيا تمثل قوى العالم الأربع العظمى في شكل أربعة حيوانات ثم تزول هذه القوى ومن بعدها تثبت مملكة شعب قدسي العلي وهي ملكوت أبدي (ص 7).(2) الرؤيا التي فيها يرى القوة التي يمثلها تيس المعز تتغلب على قوة أخرى هائلة يمثلها كبش، والقوة التي يمثلها تيس المعز تنقسم إلى أربعة أقسام، ملك أحد هذه الأقسام ينجس الهيكل (ص 8).(3) أما الرؤيا الثالثة فقد جاءت استجابة لصلاة التوبة التي قدمها دانيال. وهي عبارة عن رسالة حملها إليه الملاك جبرائيل تتعلق بمملكة المسيح العتيدة التي تأتي بعد سبعين أسبوعاً (ص 9).(4) في الرؤيا الرابعة تأتيه رسائل من الله تؤكد له محبة الله للمؤمنين الأمناء في شعبه. وفيها يرى صورة للمظالم التي يرتكبها ملك الشمال (ص 10-12).لغة السفر:ويجدر بنا أن نلاحظ أن سفر دانيال مكتوب في لغتين في الأصل، فقد كتب جزء منه باللغة الآرامية وهو يشمل ص 2: 4-ص 7: 28 أما بقية السفر فقد كتبت باللغة العبرانية.تاريخية السفر:كان من أثر الكشوف الحديثة أنها ساعدت على إثبات تاريخية السفر. فلقد زعم كثير من العلماء في القرن التاسع عشر وأوائل القرن العشرين بأنه من المشكوك فيه إن كان هناك ملك باسم بيلشاصر، ولكن اكتشاف سجلات معاصرة لغزو بابل أثبت أنه كان شخصية تاريخية. وقد فسرت لنا هذه السجلات السبب الذي لأجله جعل بيلشاصر دانيال ثالثاً في المملكة بدلاً من أن يكون ثانياً فيها وذلك لأن بيلشاصر وأباه الملك نبونيدس كانا يشغلان المكانيين الأولين في المملكة. ثم أظهرت لنا هذه السجلات السبب الذي لأجله ذكر أن ملك بابل في الليلة التي غزاها فيها الميديون كان بيلشاصر وليس نبونيدس. الملك كان قد ترك بابل وبنى لنفسه في تيماء في شمال الصحراء العربية قصوراًتشبه قصور بابل،وإنه عهد بالملك إلى ابنه وشريكه في الملك في الملك بيلشاصر الذي كان يحمل لقب ملك بابل. وقد كان الملك الفعلي في غيبة أبيه نبوبيدس. وكذلك أظهرت لنا هذه الكشوف أن كورش لما عين نائباً للملك في بابل كان يسير وفق سياسته التي عرفت عنه. ثم أظهرت لنا الإشارة إلى بيلشاصر كابن نبوخذ نصر تتمشى مع العرف المألوف حينئذ.ويقتبس السيد المسيح نبوة دانيال وذلك في مت 24: 15. ويتحدث يوسيفوس عن دانيال بأنه نبي عظيم (يوسيفوس، آثار-الكتاب العاشر والفقرة 11) وقد ذكر يوسيفوس أن نبوات دانيال كانت موجودة قبل عصر الاسكندر الأكبر أي قبل عام 330 ق.م. أو بالحري قبل عصر ارتزركسيس. وقد ذكر أمر نجاة شدرخ وميشخ وعبدنغو من اتون النار المتقدة، وكذلك أمر نجاة دانيال من جب الأسود في مكا 2: 59 و 60 وكذلك في ص 1: 54 بمقارنته بدانيال 9: 27 و 11: 31.تفسير دانيال في الكنيسة المسيحية:يتفق تفسير السفر مع ما حدث في التاريخ فمثلاً قرار ارتزركسيس الذي أعلنه في السنة السابعة من ملكه أي في سنة 458 وبعد مضي سبعة أسابيع من السنين أو 49 عاماً أي عام 408 ق.م. ويتفق هذا مع الإصلاح الذي تمّ على يد عزرا و نحميا الذي قاما به في أثناء هذا الزمن. ولا يعرف متى توقف هذا الإصلاح ولكننا نعلم أن الحاكم الذي حل محل نحميا سنة 411 كان فارسياً وقد جاء قبل ختام الأسبوع السابع. ثم من بعد ذلك جاء 62 أسبوعاً  أو 434 سنة وتصل هذه من ذلك الحين إلى عام 26 ميلادي أي إلى الزمن الذي بدأ فيه يسوع مناداته الجهارية بالإنجيل أو ربما في أوائل عام 27 ميلادي بعد هذه الأسابيع الاثنين والستين ((يقطع المسيح)) (دانيال 9: 26) متمماً. كفّارة الإثم وآتياً بالبر الأبدي الذي يتحدث عنه في دانيال 9: 24. وفي وسط هذا الأسبوع الأخير يأتي واحد يبطل الذبيحة ويضع حداً للفرائض الطقسية لا عن طريق منعها بل بتقديمه نفسه ذبيحة على صليب الجلجثة وبذلك جعل كل الذبائح الأخرى والطقوس الموسوية غير ذات موضوع. ويرى بعض المفسرين أنه مع أن الحوادث التاريخية التي حدثت في تاريخ ملكوت الله تتفق وما جاء في السفر عن هذا الملكوت الأبدي ومع ما لهذا الاتفاق من أهمية عظمى إلاّ أنهم يقولون أنه يرّجح أن هذه السبعين أسبوعاً، يضاف إليها السبعة الأسابيع الأخرى التي تجيء في الاتبداء على حدتها وكذلك الأسبوع الأخير الذي يجيء على حدته، كل هذه أن هي إلا أمور رمزية وتعبر تعبيراً رمزياً لا حسابياً عن آماد طويلة شاسعة في تاريخ ملكوت الله على الأرض.ومما هو جدير بالذكر أن الرب يسوع المسيح اتخذ لقب ((ابن الإنسان)) المذكور في دانيال 7: 13 و 14 لقباً، وتحدث عن مجيئه للدينونة محققاً في نفسه وشخصه ما تحدّث به دانيال عن عمل ابن الإنسان في الدينونة (مت 24: 30 و 26: 64).ثم أن رؤى دانيال والوحوش الصاعدة من البحر المذكورة في ص 7: 3-7 نجد صداها في صورة الوحش الذي رآه يوحنا صاعداً من البحر (رؤيا 13: 1).وكذلك يجدر بأن نذكر أن الغرض النهائي من نبوات السفر يتركز في ملكوت الله، فهف النبوات تأسيس هذا الملكوت وغايتها انتشار عمل هذا الملكوت وذيوعه، والنهاية المجيدة التي تهدف إليها هي مجيء هذا الملكوت في كماله. فالسفر والحالة هذه شبيه بسفر الرؤيا في أغراضه وأهدافه وغاياته.(2) كاهن من عائلة ايثامار رجع من عزرا وناب عن بيت أبيه وكان من ضمن الذين ختموا العهد في ومن نحميا (عزرا 8: 2 و نحم 10: 6).(3) دانيال المذكور في سفر حزقيال 14: 14 و 20 و 28: 3 يرّجح كثير من العلماء أنه شخصية تختلف عن دانيال صاحب السفر والذي كان معاصراً لحزقيال. فإنه يذكر في هذه النصوص مع نوح ومع أيوب مما يدل على أن دانيال هذا عاش في زمن مبكر جداً سابق لعصر حزقيال. ثم يلاحظ أن اسم دانيال هذا في كتابته تنقصه في الأصل ((ياء)) أما دانيال صاحب السفر فتوجد ((الياء)) في كتابة اسمه في الأصل. ويظن بعض العلماء أن دانيال هذا كان بطلاً عاش في القديم، وكان معروفاً بتقواه وإنصافه للأرامل والأيتام. وقد ورد ذكره في نصوص ((أوجريت)) من رأس الشمرا  في نحو القرن الخامس عشر أو الرابع عشر قبل الميلاد.'),
(1379, '(4) دانيئيل أو دانيال', 'أحد أبناء داود من ابيجايل وقد ولد في حبرون (1 اخبار 3: 1) ويدعى باسم كيلآب في 2 صم 3: 3.'),
(1380, 'دان يَعَن', 'اسم مكان بين جلعاد وصيدون (2 صم 24: 6) وربما كان هذا المكان هو نفس دان التي كانت في الطرف الشمالي من فلسطين (قارن الارتباط بينها وبين صيدون في قضاة 18: 28 و 29) ويرجح بعضهم أن موضعها اليوم دانيان وهي خربة شمال اكزيب.'),
(1381, 'داود', 'اسم عبري معناه ((محبوب)) وهو ابن يسى وثاني ملوك بني إسرائيل. ويمكن أن ننظر إلى حياته من عدة مراحل:(1) المرحلة الأولى: مرحلة حداثته وشبابه وقد قضى الشطر الأول من هذه المرحلة في بيت لحم يهوذا. وكان أصغر ابن بين ثمانية بنين (1 صم 16: 10 و 11 و 17: 12-14) ومع أننا نرى في سجل سبط يهوذا في 1 اخبار 2: 13-15 ذكر سبعة أبناء ليسى فقط إِلا أنه يرجح أن أحدهم مات دون أن يعقب نسلاً. وقد عرفت أمّ داود بالتقوى والصلاح (مز 86: 16 و 116: 16). وتاريخ أسلافه رائع وبديع ومجيد وباعث على الإلهام إلا أنه لم يخل من بعض لوثات الخطيئة في بعض الأحيان (تك 37: 26 و 27 و 38: 13-29 و 43: 8 و 9 و 44: 18-34 و يشوع 2: 1-21 و راعوث 4: 17-22). وكان داود أشقر مع حلاوة العينين وحسن المنظر (1 صم 16: 12) وبما أنه كان أصغر الأبناء فقد كلّف بمهمة العناية بأغنام أبيه. وقد أظهر في القيام بهذه المهمة إخلاصاً نادراً وشجاعة فائقة فقد قتل أسداً ودباً هاجما القطيع (1 صم 16: 11 و 17: 34-36) وقد تمتع بمواهب موسيقية من صنف ممتاز، فقد أجاد اللعب على القيثار ثم أنشأ فيما بعد المزامير والأناشيد. ولما رفض الرب الملك شاول، ولم بنادَ بداود ملكاً حينئذ لئلا تثار العداوة بينه وبين شاول. وقد احتفل بمسحه في وسط ضيق وربما كان ذلك بحضور نفر قليل من شيوخ قريته. ولم يذكر سبب مسحه (1 صم 16: 4 و 5 و 13) إِلاّ أنه يبدو أن يسى وداود عرفا الغرض الذي استهدفه مسحه. وقد كان هذا الحادث نقطة تحول في حياته فإن الروح الرب جاء بقوة، ومع هذا فلم يحتقر عمله الوضيع أو يستهن به.(2) تتضمن المرحلة الثانية خدمته لشاول. فإنه لما رفض الله شاول اعتراه روح شرير. وإذ ظهرت دلائل انحراف عقله عن جادة الصواب نصح أتباعه أن يلحق ضارباً بالقيثار بخدمته لكي يهدئ من روعه بموسيقاه عندما تضطرب حاله. وذُكر داود عندئذ كلاعب ماهر وبطل شجاع يعمل في الحرب ببأس لحداثته وشبابه (1 صم 16: 14-18). فدعا شاول داود، وقد أفاد كثيراً بموسيقاه ورضي كثيراً عن أخلاقه، وطلب إلى يسى أن يبقيه معه وعيّنه حامل سلاح له (1 صم 16: 19-23). وقد كانت خدمة داود لشاول خير مدرسة تدرب فيها داود، فقد تعلم أساليب الحرب والسياسة والحكم. وقد اتصل بحكم عمله بعدد من أقدار الرجال في ذلك الحين ورأى جوانب الملك المنيرة والمظلمة. ولكن لم يقم داود مع شاول باستمرار، إذ يبدو أن حالة الملك تحسنت. وقد أتيحت الفرصة لداود مراراً كثيرة أن يعود ليشرف على غنم أبيه (1 صم 17: 15). وبينما كان يقوم بزيارة قريته ويشرف على الغنم إذ نشبت الحرب بين العبرانيين والفلسطينيين فغزا الفلسطينيون يهوذا وأقاموا معسكرهم على مسافة تقرب من خمسة عشر ميلاً غرب بيت لحم. فقاد شاول الجيش لملاقاتهم. وكان أخوة داود الثلاثة الكبار مع الجيش. ثم بعد قضائهم مدة تقرب من ستة أسابيع بعيدين عن أهلهم وذويهم أرسل يسى داود لكي يسأل عن سلامتهم. وفي ذلك حين كان جليات قد تقدم متحدياً أي إنسان في جيش العبرانيين لكي يبرز لمبارزته فأثار هذا التحدي روح داود. وقد شعر يقيناً بأن الرب سيزيل على يديه العار الذي لحق بقومه. فسأل عمّن يكون هذا الفلسطيني الذي يعيّر صفوفاً الله الحي. وقد بلغت كلماته إلى شاول الذي إذ رأى قوة الروح الدافع بالفتى أن يتقدم عهد إليه بأمر مبارزة جليات. فخلع داود لباس الحرب الذي كان قد ألبسه إياه شاول بحجة أنه لم يجربه. وقد أظهر في تلك الآونة عبقرية عظمى فإن حركة جليات البطيئة بسبب ثقل السلاح الذي كان يلبسه يحتم عليه أن يكون قتاله عن قرب من عدوه فلا يستطيع أن يتمكن منه ما دام بعيداً عنه. أما داود فجاء إليه خفيف الحركة لا يعوقه ثقل سلاح أو ما أشبه ذلك. أتى ومعه مقلاع كان قد اتقن استخدامه وانتقى خمسة أحجار ملس يمكن قذفها عن بعد. جاء ويتملكه احساس رائع بعدل القضية التي يدافع عنها ويحارب لاجلها، ويسود على قليه وكل مشاعره شعور سماوي بالثقة بالله.ثم بدا المتحاربان يتبادل عبارات التعيير كما كان مألوفاً لدى المتبارزين في القديم. ثم قذف داود بمقلاعه حجراً سقط جليات على أثره فتقدم داود وقضى عليه بسلاحه. وبعد الانتصار ذهب داود مع شاول اما إلى جبعة بنيامين - المركز الذي كان شاول يقيم فيه ويحكم منه، أو انهما ذهبا إلى نوب حيث كانت خيمة الاجتماع منصوبة. ثم عرض داود رأس جليات في أورشليم ويبدو انه فعل ذلك تحدياً لليبوسيين الذين كانوا لا يزالون يملكون الحصن في ذلك الحين. ثم وضع سلاح جليات في خيمته (1 صم 17: 54) ووضع السيف في خيمة الاجتماع (1 صم 21: 9). ثم عن داود لما ذهب لمجابهة جليات انذهل شاول من بطولة روحه وسأل ابنير عن الفتى وابن من يكون. ولما عاد داود منتصراً وجه غليه شاول السؤال نفسه فرد عليه داود ببساطة قائلاً: (( ابن عبدك يسّى البيت لحمي )) ( 1 صم 17: 55 - 18 - 1) ولم يكن اسلاف داود قد عرفوا بالبطولة الحربية. ويبدو أن شاول في سؤاله أراد أن يعرف مركز أسرة المنتصر الذي وعد أن يعطيه ابنته زوجة، ولكي يعفي اسرة أبيه من الضرائب التي كانت مفروضة عليهم (1 صم 17: 25 و 18: 18) فاكتشف انه لا يوجد في أسرة داود ما يمكن أن يخجل منه إذا ما صار صهره.وقد كان انتصار داود على جليات مرحلة ذات أهمية عظمى في حياته. فقد أظهر من البطولة والشجاعة والتواضع والتقوى ما جعله محبباً إلى نفس يوناثان فتعلقت نفسه بداود وأحبه كنفسه (1 صم 18: 1) ولم يسمح له من بعد ذلك أن يعود إلى بيت أبيه بل بقي في بلاط شاول على الدوام (1 صم 18: 2). ولكن الاكرام الذي ناله داود لانتصاره على جليات أثار غيرة شاول الذي أصبح عدواً لدوداً منذ ذلك الحين (1 صم 18: 6 - 9) وقد رأى شاول أن تنبؤ صموئيل بانتقال الملك منه إلى من هو خير منه (1 صم 15: 17 - 29) قد قرب من تمامه في داود فحاول ان يمنع ذلك جهد المستطاع. ومن أجل ذلك حاول مرة أن يقتل داود بالحربة (1 صم 18: و 11) وإذا فشل في هذه المحاولة، حطّ من مكانة داود الحربية ومن سلطته (1 صم 18: 13). وقد اعطي ابنته التي وعد أن يزوجها لداود، زوجة لآخر (1 صم 18: 13). وقد حاول أن ينصب من محبته لميكال فخاً له لقتله (1 صم 18: 20 - 27) وكلما ازدادت شهرة داود كلما خاف شاول على ملكه منه ولم يخفِ قصده في قتله (1 صم 18: 29 و 30 و 19: 1) ولم يتخل اتباع شاول قطّ عن السعي في تنفيذ هذا القصد (1 صم 24: 9 وعنوان مز 7) ومع أن عداوته لداود أخمدت بعض الوقت إلاّ أن غيرته ثارت من جديد وحاول أن يضربه بالحرية (1 صم 19: 4 - 9) ثم أراد فيما بعد أن يقبض على داود لولا أنه هرب بحيلة دبرتها ميكال (1 صم 19: 10 - 17) وقد كتب داود مز 59 فبي ذلك الحين. ثم هرب إلى صموئيل في الرامة حيث ارسل شاول رسلاً للقبض عليه (1 صم 19: 18 - 24) ومن بعد ذلك هرب داود إلى يوناثان الذي بعد أن تحرّى الموقف اخبره أنه لا أمان له ما دام يظل باقياً في بلاط شاول (1 صم ص 20).(3) داود البطل الطريد: هذه الظروف القاسية التي حلّت بداود أظهرته بمظهر من فقد ثقته بالله أو كاد، وأغرقته في بحر من اليأس والقنوط. فهرب من شاول وجاء إلى نوب وكان إيمانه قد بلغ من الضعف أقصاه فلم ينطق بالصدق (1 صم 21: 1 - 9) ومن هناك أسرع إلى جتّ وطلب حماية أخيش ملكها عدو شاول (1 صم 21: 10) ولكن أقطاب الفلسطينيين لم يطمئنوا إلى وجود من هزمهم وأضاع هيبتهم في وسطهم فألقوا القبض عليه (1 صم 21: 14 وعنوان مز 56) غير أنه تظاهر بالجنون فطرده (قارن عنوان مز 34، ومن المرجح جداً أن ابيمالك هو نفس اخيش أو أنه لقب لقّب به اخيش ) ثم استعاد داود إيمانه بالله (مز 34) ورجع إلى يهوذا وأقام في مغارة عدلام (1 صم 22: 1) ولكنه اخذ أبويه ليقيما في موآب (1 صم 22: 3 و 4) واجتمعت إليه جماعة متعددة الأغراض متضاربة المشارب فمنهم من لا عمل له، ومنهم اليائس وغير هؤلاء ممن بلغ عددهم أربعمائة في البداية ثم ازدادوا إلى ستمائة. وكان ابياثار من ضمن هؤلاء - وهو الكاهن الذي بقي من بين كهنة نوب . جاء ومعه الافود. وكذلك النبي جاد الذي يرجح أن داود التقى به في الرامة (1 صم 22: 5 و 20 و 23: 6) ولذا توفرت لداود في ذلك الحين المعونة الدينية. ثم ذهب من عدلام إلى قعيلة وخلص البلدة من ايدي الفلسطينيين (1 صم 23: 1 - 5) وإذ تهيأ شاول لمهاجمته هرب إلى برية يهوذا (1 صم 23: 14 ومز 63) حيث طارده شاول بعد ما جاء إليه الزيفيون واخبروه أن داود مُختبىء عندهم غير ان شاول أضطر أن يتوقف عن المطاردة عندما علم أن الفلسطينيين قد اقتحموا البلاد (1 صم 23: 14 - 29) وبعدما رجع شاول من متابعة الفلسطينيين عاود مطاردته لداود فذهب إلى عين جدي إذ علم أن داود مختبىء هناك. ولكنه كاد يقع قي قبضة داود لولا أنه عفا عنه وأنقذ حياته (1 صم 24 ومز 57 و 142). ثم عمل على حماية أملاك قومه من الناهبين (1 صم 23: 1 و 25: 16 و 21 و 27: 8). وقد انتظر داود أن يرد له بعض جميله فيقدم الطعام لرجاله فانه لم يطلب ضريبته ولم يطلب مؤناً له ولرجاله في دفاعهم عن تلك الممتلكات غير أن نابال رفض طلبه بسخرية واحتقار فاثار هذا غيظ داود ولم ينقذ نابال من أن يسفك دمه سوى حكمة زوجته (1 صم ص 25) التي أخذها داود زوجة له بعد موت نابال. ثم عاد إلى مكان بالقرب من زيف فاخبر الزيفيون شاول مرة ثانية عن مكانه فنهض لمطاردة شاول وأظهر داود مروءة وشهامة وعفا عنه بعد أن وقع في يده فأخذ رمحه وكوز الماء من عند رأسه وأيقظه وأعلمه أنه كان قد وقع في يده غير أنه عفا عنه. وإذ يئس داود من كفّ شاول عن مطاردته ترك يهوذا واستأذن اخيش في ان يحتل صقلغ وهي مدينة في طرف الصحراء الجنوبية حيث بقي هناك سنة وأربعة أشهر يحمي الفلسطينيين ويحارب قبائل الصحراء (1 صم ص 27) ولما ذهب الفلسطينيون إلى جبل جلبوع لمحاربة شاول لم يسمح اقطاب الفلسطينيين لداود أن يذهب معهم (1 صم 28: 1 و 2 وص 29) وعند عودته وجّد ان صقلغ قد اخربت. فتعقب الغزاة واستخلص منهم الاسلاب (1 صم ص 30) ولما سمع بما حدث في معركة جلبوع حزن لمصرع شاول ويوناثان ورثاهما رثاءً حاراً (2 صم ص 1).(4) داود ملك يهوذا، بعد موت شاول اختار سبط يهوذا داود ملكاً عليه لأنه من هذا السبط. وبدا حكمه على سبط يهوذا في حبرون (2 صم 2: 1 - 10) وكانت سنه حينئذ ثلاثين عاماً (2 صم 5: 4) فقامت بقية الاسباط بقيادة ابنير، وكان من أقوى رجال عصره، وأقامو ايشبوشث ابن شاول في محنايم واشتعلت نار حرب أهلية لمدة سنتسن بين رجال داود ورجال ايشبوشث وانتهت باغتيال ابنير وايشبوشث على غير رغبة داود وعدم رضاه (2 صم 2: 12 - 4: 12) وقد دام ملك داود في حبرون مدة سبع سنين وستة أشهر (2 صم 5: 5) وكان قد صارت له عدة نساء وابناؤه الذين ولدوا في حبرون هم: امنون، وابشالوم، وادونيا (2 صم 3: 1 - 5).(5) داود ملك على كل بني اسرائيل: وعند موت ايشبوشث اختارت كل الاسباط داود ملكاً عليها. وقد بادر للحال بتأسيس المملكة (2 صم 5: 1 - 5) وكانت بعض الحاميات الفلسطينية في بعض بلاد المملكة، وكانت بعض البلدان الأخرى في يد الكنعانيين. فقام داود بأخذ حصن اليبوسيين في أورشليم. وكان السكان يعتبرون الحصن منيعاً لا يقهر ولكن داود اكتسحه عنوة. وبحكمة جعل المدينة عاصمة ملكه وأقام قصراً بناه له صناع صوريون. وكانت العاصمة الجديدة تقع على الحدود بين الاسباط التي في الشمال والجنوب. وقد ساعد أخذه البلاد التي كانت في أيدي الكنعانيين على فتح الطريق بين يهوذا والشمال ويسّرت التعامل بين الشمال والجنوب وعملت على أحكام أواصر مملكته. وقد غزا الفلسطينيون البلاد مرتين ولكن هزمهم داود في المرتين بالقرب من أورشليم (2 صم 5: 17 - 25 و 1 اخبار 14: 8 - 17) وقد تقدم بعد انتصاره الثاني على الفلسطينيين فغزا بلادهم واخذ جتّ. وفي هذه المرة ثم في مرات تالية قام فيها داود بمهاجمة الفلسطينيين تمكن من اخضاعهم تماماً (2 صم 21: 15 - 22) فكفوا عن اقلاف المملكة التي أسسها مدة عدة قرون وحالما اتم داود تأسيس المملكة وجه عنايته إلى الشؤون الدينية فاحضر التابوت باحتفالات دينية وذبائح وفرح عظيم فنقله من قرية يعاريم (يشوع 15: 9 و 2 اخبار 1: 4) ووضعه في خيمة الاجتماع التي نصبها له في مدينة داود (2 صم ص 6 و 1 اخبار ص 13 وص 15: 1 - 3) ثم من بعد ذلك وجه عنايته إلى تنظيم العبادة فأحسن تنظيمها (1 اخبار ص 15 وص 16) ووضع تصميماً لهيكل عظيم (2 صم ص 7 و 1 اخبار ص 17 وص 22: 7 -10). وزاد الله له نعمته فاصبح ناجحاً جداً. ولكي يضمن أمن مملكته ويحرص على ان لا تأتيها العدوى الوثنية من الامم المجاورة ولكي ينتقم لاجل هجمات وقعت على ملكه، قام داود بحرب ضد الامم المحيطة بهم فاخضع الموآبيين والاراميين في صعوبة ودمشق والعمونيين والادوميين والعمالقة (2 صم ص 8 و 10 و 12: 26- 31) وبهذا امتدت مملكته إلى أقصى حدود وصلت إليها في كل تاريخها. وقد ارتكب داود خطيئته الشنيعة ضد أوريا الحثي أثناء حربه مع العمونيين. وقد وبخه النبي ناثان على هذه الخطيئة واعلن له العقاب السماوي الذي يحلّ به: ان السيف لن يفارق بيته (2 صم 11: 1 إلى 12: 23) وقد تاب داود توبة صادقة (مز 51) ولكن الغلام الذي ولد نتيجة زواجه من امرأة اوريا مات (2 صم 12: 19) وقد ظهرت قوة الشهوة الجامحة وقوة الانتقام الغشوم في بيته (2 صم ص 13)، وفي عصيان ابنه عليه فاقضَ مضجعه واثار حرباً أهلية وشعواء في مملكته (2 صم ص 14 -19). وقد وجدت روح القلق والحسد والطمع التي اثارها ابشالوم في الاسباط، مكاناً في نفوس الشعب وبقيت حتى بعد ما اخمد عصيان ابشالوم، وظهرت مرة أخرى في عصيان شبع بن بكري (2 صم ص 20) وقد وفىّ داود العدل حقّه بحسب ما كان سائراً في عصره فانتقم لكسر شاول الحلف مع الجبعونيين (2 صم ص 21). وارتكب خطيئة كبرياء بقيامه باحصاء الشعب وقد عوقب لذلك بان اصيب الشعب بالوباء (2 صم ص 24 و 1 اخبار ص 21) ةقد انشغل داود كثيراً أثناء حكمه بشئون الدولة الداخلية وتنظيمها وكذلك بالحروب الكثيرة التي قام بها. وكذلك شغل باعداد المواد لبناء الهيكل. وختم حكمه بتثبيت سليمان على العرش وارثاً له (1 ملو ص 1) ثم أوصى بغيقاع الحكم العدل على بعض ممن ارتكبوا جرماً وافلتوا من العدالة في مدة حكمه (1 ملو 2: 1 - 11) ومات في السنة الحادية والسبعين من عمره بعد أن حكم أربعين سنة أو يزيد منها سبع سنين ونصف سنة في حبرون وثلاثة وثلاثين سنة في اورشليم (2 صم 2: 11 و 5: 4 و 5 و 1 اخبار 29: 27). وقد دفن داود في مدينة داود وتقع هذه جنوب الحرم الشريف. اما مكان قبره بحسب التقليد فهو المكان الذي يسمّى ((النبي داود )) وهو بالقرب من الباب المسمى بهذا الاسم.وقد اعتبر داود منذ حداثته مرنم اسرائيل الحلو (2 صم 23: 1) وقد نسبت إليه المزامير. وقد ورد في الكتب التاريخية ذكر لشغفه بالموسيقى. فقد كان يضرب على القيثارة بمهارة فائقة (1 صم 16: 18 - 23 و 2 صم 6: 5) وقد نظم خدمة التسبيح للمقدس (1 اخبار 6: 31 و 16: 7 و 41 و 42 و 25: 1) وقد انشأ رثاء لشاول ويوناثان وكذلك رثا ابنير. ثم انشد رثاء لشاول ويوناثان وكذلك رثا ابنير. ثم انشد انشودة النجاة والكلمات الختامية التي نطق بها (2 صم 1: 17 - 27 و 3: 33 و ص 22 وص 23: 1 - 7) وقد اشار عاموس وعزرا إلى نشاطه الموسيقي (عزرا 3: 10 ونحم 12: 24 و 36 و 45 و 46 و عا 6: 5) وابن سيراخ (ص 47: 8 و 9) ومثل هذا العمل الذي قام به داود كان قد نشأ وترعرع وتهذبت الملكات له عند المصريين القدماء والبابليين والعبرانيين (عدد 21: 14 وقضاة ص 5). وينسب على داود ثلاثة وسبعون مزموراً كما ذكر في عناوين هذه المزامير في الأصل العبري (قارن مز 3 و 34 و 51 الخ). وكثيراً ما تذكر المناسبة التي لاجلها أنشد هذا المزمور أو ذاك. مزمور 59 وعلى الأرجح مز 7 انشئا اثناء وجوده مع شاول ومزامير 34 و 52 و 54 و 56 و 57 و 63 و 142 انشئت في وقت حلّ به ضيق وعندما كان طريداً. ومزامير 3 و 18 و 30 و 51 و 60 كتبت في عدة مناسبات فيها جاز داود اختبارات منوعة عندما كان ملكاً.ومع أن داود ارتكب في بعض الاحيان خطايا يندى لها الجبين خجلاً إلا إننا غذ نظرنا على نسبة النضوج الروحي الضئيلة التي كانت سائدة في ذلك العصر وحالة الظلام التي كانت تعم العالم قبل انبلاج فجر النور، ثم اذا نظرنا إلى عمق توبته لرأينا في هذا شيئاً مما يخفف ذنبه على حدّ ما. ومن الناحية الاخرى إذا نظرنا إلى قوة تعلقه بالله وشدة اخلاصه له وروعة ايمانه به امكننا ان ندرك كيف انه دعي رجلاً حسب قلب الله (1 صم 13: 14) وعلى الاجمال فقد فعل المرضي في عيني الله ما عدا ما كان من خطيئته في حق اوربا الحثي (1 ملو15: 5) فقد خدم جيله بمشورة الله ورقد (اعمال 13: 36). وإن مقدار ماله من الأثر في الجنس البشري عظيم إلى حد كبير. فقد اسس ملكاً، وانشأ مزامير انشدت في كل بقاع العالم المسيحي طوال قرون وقرون. وكلما انشدت كلما بعثت في المرنمين حياة روحية قوية. وقد كان داود حلقة على غاية ما يكون من الأهمية في سلسلة انساب من هو ابن داود وفي نفس الوقت رب داود (مت 22: 41 - 45).مدينة داود:هذا هو الاسم الذي اطلق على :(1) الحصن اليبوسي في صهيون والذي أخذه رجال داود وسمي ((مدينة داود)) لأنه جعله مقر ملكه (2 صم 5: 6 - 9 و 1 اخبار 11: 5 و 7) وكانت مدينة داود تقع على الطرف الجنوبي من المكان الذي اقيم فيه الهيكل فيما بعد. وتاريخ هذا الحصن اليبوسي الذي صار مدينة داود يرجع على عصور مبكرة في التاريخ فقد كان هذا المكان مدينة ماهولة محاطة بالاسوار حوالي سنة 3000 ق.م. وفي ملك داود أخذ التابوت إلى هذه المدينة واستقر هناك على أن بني الهيكل ووضع فيه (2 صم 6: 12 و 16 و 1 ملو 8: 1 و 1 اخبار ص 15: و اخبار 5: 2).وفي أسوار مدينة اورشليم الحديثة ((القدس)) باب يدعى ((باب النبي داود)) وفي السور أيضاً ((برج)) بالقرب من (( باب يافا (أو الخليل) )) ويدعى ((برج داود)). ويعتقد معظم الباحثين ان داود لم يبن شيئاً في هذه البقعة ولكن نعلم يقيناً أنه اقام بناءَ في المكان الذي يقع الآن إلى الجنوب من الحرم الشريف. وقد اكتشفت بعض الاسوار القديمة في هذه البقعة ويحتمل أن داود هو الذي قام ببنائها.(2) يطلق الاسم ((مدينة داود)) على بيت لحم لان بيت داود كان فيها (لو 2: 4).'),
(1382, 'دبّ', 'لون الدبّ السوري رمادي تغشاه صفرة ويعيش الدبّ عادة على الاعشاب والخضراوات إلا إذا اضطرته الحاجة إلى الافتراس وخطر الدببة إذا ما أثيرت عظيم للغاية (اش 11: 7 وعا 5: 19) وبخاصة اذا ما انتزعت صغارها منها إذ أنها شديدة التعلق بصغارها (2 صم 17: 8 وامثال 17: 12 وهوشع 13: 8). ومع ان وجود الدببة السورية يقتصر الان على لبنان في غربي الاردن، وعلى حرمون وجلعاد وباشان شرقي النهر غلا ان الدببة في العصور القديمة كانت تجول وتهيم في البلاد كلها. وقد قتل داود دباً دبتان من الغابات القريبة من بيت إيل ومزّقت اثنين واربعين ولداً سخروا من اليشع النبي (2 ملو 2: 24).ويكنى بالدبة الثكول في الامثال الجارية إلى شدة الغيظ والرغبة في الانتقام.اما الدب المذكور في دانيال 7: 5 الذي اطلق لكي يأكل لحماً كثيراً فيرّجح انه يشير إلى الدولة المادية. ثم إننا نرى ان الأربعة الوحوش المذكورة في دانيال تمتزج معاً وتذكر كوحش واحد في سفر الرؤيا 13: 2 لتمثل كل قوة العالم، وأرجل هذا الوحش الواحد هي ارجل الدبّ.ومن البركات العظمى التي تحدث للبشر نتيجة لانتشار الإنجيل وأثره في القلوب ان اخلاق البشر تتغير ويتم ما جاء في اش 11: 7 من أن البقرة  والدبة ترعيان، وتربض اولادهما معاً.'),
(1383, 'دَبّاشة', 'اسم عبري ومعناه ((سنام الجمل)) وهو اسم بلدة كانت تقع على حدود زبولون (يش 19: 11) ويرّجح إن مكانها الآنسس ((تل الشمّام)) مقابل ((تل قيمون)) إلى جنوبي قيشون.'),
(1384, 'مُدّبر، تدبير', 'دعي المشرفون على العمل أو نظاره في العهد القديم ((مدبرين)) (خر 5: 6 و 14) وقد دعي المسيح مدبراً أي حاكماً يرتب شؤون أتباعه ويدبرها ويرعى شعبه والمؤمنين به (مت 2: 6) وقد دعي يوسف مدبراً على مصر وعلى بيت فرعون كله (اع 7: 10) وكانت مهمة تدبير شؤون الكنيسة وظيفة، لها مكانتها في الكنيسة الأولى (رو 12: 8 و 1 كو 12: 28 و 1 تي 5: 17) ويرّجح أنها كانت وظيفة تدبير المعونة والمساعدة اللازمة للجماعة ولأفرادها المحتاجين وتوزيع المعونة.'),
(1385, 'دَبَرَة أو دَبَرَت', 'اسم عبري وربما كان معناه ((مرعى)) وهو اسم لبلدة كانت تقع في نصيب يساكر. وقد أعطيت هي وضياعها للجرشيون (يش 19: 12 و 21: 28 و 1 اخبار 6: 72 و حروب يوسيفوس الكتاب الثاني والفصل الحادي والعشرين والفقرة الثالثة)ومكانها اليوم قرية دبورية في السفح الغربي لجبل تابور.'),
(1386, 'دِبْري', 'اسم عبري معناه ((كليم، أو محب لكثرة الكلام)) وهو اسم رجل من سبط دان وهو أبو شلومية وقد قتل حفيده ابن شلومية لأنه جدّف على الاسم وسبّ (لا 24: 11-14).'),
(1387, 'دَبّاغ', 'الدباغة هي العملية التي تمر فيها جلود الحيوانات من حالتها الأولى بعد أن تنتزع عن الذبائح إلى أن تصير صالحة للاستعمال للأحذية والحقائب والملبوسات الجلدية وغيرها مما يصنع من الجلد. ففي الدباغة يزال الشعر الذي على الجلد باستخدام محلول الجيرة وغيره. ويوضع الجلد في محلول من قشور وجذورها وبخاصة أشجار البلوط حتى يتشبع الجلد بحامض عصير النبات فيصبح متيناً طرياً يصلح للاستعمال ويبقى زمناً طويلاً. وهكذا تتحول جلود الحيوانات إلى جلد يستخدم لأغراض عدة. وقد أقام بطرس في يافا عند دباغ يعمل في هذه الصناعة (اع 10: 5 و 6).'),
(1388, 'دِبْلاَيِم', 'اسم عبري ومعناه ((كعكة مزدوجة من التين)) وهو اسم حمي هوشع، وأبو زوجته جومر (هو 1: 3).'),
(1389, 'دَبْلَة', 'اسم عبري معناه ((حلقة مستديرة، كعكة)) وهو اسم مكان في فلسطين حز 6: 14) ولم يعرف موضعه على وجه التحديد. ويظن بعض العلماء أن الاسم هو ربلة كما وردت صيغته في بعض المخطوطات، ولذا فيقولون أن العبارة التي تقول من القفر إلى دبلة، يجب أن تقرأ ((من القفر إلى ربلة)) أي من أقصى الجنوب إلى أقصى الشمال أو ((إلى مدخل حماة)) (قارن حز 47: 16 و 48: 1) ويكون في استعمال ربلة في هذه الحالة إشارة إلى الحوادث المؤسية المفجعة التي حدثت فيها (2 ملو 23: 33 و 25: 6 و ما بعده و 20 و ما بعده). ويظن آخرون أنها البلدة التي مكانها الآن دبل الحديثة وتقع في شمال الجليل (انظر ((دبلة)) ).'),
(1390, 'دَبُورَة', 'اسم عبري معناه ((نحلة)) وقد ورد اسماً:(1) لمرضعة رفقة التي رافقتها من أرض ما بين النهرين وعاشت إلى ما بعد عودة يعقوب من فدان آرام. وربما عادت إلى قومها أهل قرابتها في حاران بعد موت رفقة ورافقت يعقوب عندما عاد إلى كنعان أو أنها لحقت به لما كان في شكيم. وعلى أي حال فإننا نراها معه في بيت إيل حيث ماتت وكان لها من العمر 155 عاماً فإنها لم تكن أغر من اسحق بكثير. ودفنت عند سفح الجبل الذي كانت المدينة مقامة عليه تحت شجرة بلوط أطلق عليها اسم أَلُّون بكوث أي بلوطة البكاء (تك 24: 59 و 35: 8).(2) نبية هي زوجة لفيدوت. وفد كانت تقيم تحت شجرة نخيل سميت باسمها كانت تقع بين الرامة وبيت إيل في جبل افرايم، وهناك كانت تقضي لبني إسرائيل. وقد دعت باراق ليقوم بمحاربة سيسرا ورافقت باراق في هذه الحرب(قض 4: 9) فالتقى الجيشان-جيش باراق وفيه عشرة آلاف وجيش يابين ملك كنعان بقيادة سيسرا. وكان الذي يقوده سيسرا أكثر من جيش باراق عَدداً وعُدداً. وكان معه تسع مئة مركبة من حديد. ولكنه انهزم وهرب سيسرا وسقط جيشه بحد السيف وبعد النصر ترنمت دبورة بترنيمتها المشهورة الواردة في قض ص 5 (انظر ((باراق وياعيل)) ).'),
(1391, 'دَبير', 'اسم عبري ربما كان معناه ((مقدس)) وقد ورد:(1) اسماً لملك عجلون الذي تحالف مع ادوني صادق ولكنهما انهزما وقبض عبيهما وقتلهما يشوع (يش 10: 3 و 23 و 36).(2) اسم مدينة في أرض يهوذا الجبلية وهي في النقب أو في أرض الجنوب وكانت تدعى أيضاً قرية سفر أي مدينة الكتب، وكانت تدعى أيضاً قرية سنّة أي مدينة غصن النخيل (يش 15: 15 و 19 و 48 و 49). وكان يسكنها في عصر يشوع قوم من العناقيين. وكان لها ملك وكانت ترأس مدن أخرى تحيط بها. وقد فتحها يشوع وقتل سكانها (يش 10: 38 و 39 و 11: 21 و 12: 13) ولكن عاد الفارّون منها وسكنوها ثانية فغزاها عثنئيل (يش 15: 15-17 و قض 1: 11 و 12) وقد عينت نصيباً للكهنة (يش 21: 13 و 15 و 1 اخبار 6: 57 و 58) وقد ظن كثيرون أن مكانها الآن قرية الظهرية التي تبعد حوالي 12 ميلاً جنوبي غربي حبرون ولكن على الأرجح أن مكانها الآن هو تل بيت مرسيم الذي يبعد حوالي 13 ميلاً غرباً إلى الجنوب الغربي من حبرون، وعلى بعد ثلاثة أميال شمالاً إلى الشمال الغربي من شامير. وقد كشف التنقيب في دبير عن آثار ترجع إلى عهد الكنعانيين والمصريين والهكسوس والعبرانيين.(3) اسم مكان يقع شرقي الأردن بالقرب من محنايم وكانت تقع في نصيب سبط بني جاد (يش 13: 26) وربما كانت هي لودبار.(4) اسم بلدة تقع على حدود سبط يهوذا بقرب وادي عخور (يش 15: 7) وربما كان الاسم هو الذي ما زال باقياً في قرية تغرة الدبر الحالية في وادي دبير بالقرب من الدُّمّ (ادُمَيم) على الطريق بين أورشليم وأريحا.'),
(1392, 'دَجاجة', 'طائر اشتهر بحنوه على أفراخه وحدبه عليهم والتفاني في حمايتهم. وقد أشار السيد المسيح إلى حنو هذا الطائر مشبهاً نفسه به (مت 23: 37) والدجاجة تخاف الجوارح والكواسر خوفاً شديداً وقد علم مخلصنا أن النسر الروماني كان مزمعاً أن يظهر حالاً ويروّع الفراخ الخائفة في أورشليم ولذا فقد أراد أن يجمعهم ويخلصهم من جور البشر ومن وقوع غضب الله عليهم.'),
(1393, 'دَخِيل', 'تطلق هذه الكلمة في العهد الجديد على الشخص الذي يدخل الديانة اليهودية من بين الوثنيين. وكان للفريسيين غيرة شديدة على اكتساب الدخلاء (مت 23: 15). ويذكر الشاعر الروماني هوراس أن اكتساب الدخلاء إلى الديانة اليهودية كان من أبرز الخاصيات التي تميز بها اليهود في عصره. وقد أوصت الشريعة الموسوية بحسن معاملة الأجانب الساكنين في الأرض المقدسة (تث 10: 18 و 19) وكذلك أوصت بحماية مدن الملجأ شريطة أن يمتنع الأجانب عن التجديف وعبادة الأوثان (لا 20: 2 و 24: 16) وكان يباح للأجانب والغرباء بأن يشتركوا في شعائر يوم الكفّارة (لا 16: 29) وعيد الأسابيع (تث 16: 11) وعيد المظال (زك 14: 16-19) غير أنه لم يكن يباح لهم الاشتراك في شعائر عيد الفصح ما لم يختنوا (خر 12: 48 و عد 9: 14) كي يتّم دخولهم في الديانة اليهودية وعبادتها. وقد دخل عدد غفير ممن لم يكونوا يهوداً أصلاً إلى الديانة اليهودية. وقد كان عدد من هؤلاء موجوداً في يوم الخمسين عندما انسكب الروح القدس (أعمال 2: 10) وكان أحد الرجال الذين انتخبوا ليعنوا بأمر الفقراء في الكنيسة الأولى دخيلاً من إنطاكية واسمه نيقولاوس (أعمال 6: 5). ويذكر يوسيفوس (حروب: الكتاب الثاني والفصل العشرون والفقرة الثانية) بأنه كان في إنطاكية عدد كبير من الدخلاء، ويرّجح أن رئيس حاشية الملكة كنداكة ملكة الحبشة كان قد دخل الديانة اليهودية قبل أن يلقاه فيلبس في الطريق ويرشده إلى طريق الخلاص بالإيمان بالرب يسوع المسيح (أعمال 8: 27) ويحدثنا يوسيفوس أيضاً أن الأسرة المالكة في اديابيني شرقي الفرات كانت قد دخلت الديانة اليهودية (يوسيفوس: آثار، الفصل العشرون فقرات 2-4) وكان في إنطاكية بيسدية دخلاء كثيرون وقد ذكر الربيون نوعين من الدخلاء: (أ) النوع الأول وهو ما أطلق عليه اسم دخلاء البرّ. وهؤلاء الدخلاء هم الذين قبلوا أن يختتنوا وأن يعتمدوا وأن يقدموا الذبائح. وقد قبلوا الديانة اليهودية بفروضها وطقوسها ونواميسها. (ب) النوع الثاني وهو ما أطلق عليه اسم ((دخلاء الباب)) أو (الدخلاء النزلاء)) وكان هؤلاء أقل مرتبة من سابقيهم فقد قبل هؤلاء أن يحفظوا فرائض أو وصايا نوح السبع (انظر كلمة نوح) ولكنهم رفضوا أن يختتنوا أو يقبلوا الديانة اليهودية برمتها. وقد سمى هؤلاء في العهد الجديد المتعبدين (اع 13: 43 و 50 و 16: 14 و 17: 4 و 17 و 18: 7). وكانت نواة الكنائس التي أسسها بولس في الغالب من هؤلاء. ومن ضمن أفراد هذا الصنف كرنيليوس وليدية وتيموثاوس وتيطس.'),
(1394, 'دُخَن', 'هذا هو اللفظ العربي لنوع من الحبوب وهي لفظة في العبرية يشبه اسمه في العربية. أما الاسم اللاتيني فهو Panicum miliaceum. وقد أُمر حزقيال أن يستخدم هذا النوع من الحبوب كواحد من العناصر التي يصنع منها الخبز الذي يقوم بإعداده (حز 4: 9) ولا زال يستخدم هذا النوع من الحبوب بكثرة في غربي آسيا وجنوبها وفي شمال أفريقيا وجنوب أوربا. والعيدان التي تنبت عليها حبوب الدُخن تستخدم طعاماً مهماً للماشية، والحبوب نفسها تستخدم لإطعام الطيور وكذلك يجد فيها الإنسان طعاماً مغذياً مستساغاً.'),
(1395, 'دَدان', 'اسم لشعب كوشي وربما كان سكنهم في وقت ما بالقرب من رعمة في جنوب الجزيرة العربية (تك 10: 7) وهم من نسل إبراهيم من قطورة زوجته بعد موت سارة (تك 25: 3). وكان الددانيون شعباً تجارياً لم مكانة مرموقة في تجارة العالم القديم (حز 27: 15 و 20 و 38: 13) وكانوا من بلاد العرب (اش 21: 3) ويقطنون جنوبي الادوميين (ارميا 25: 23 و 49: 8 و حز 25: 13) وكانت طرق القوافل من الجنوب ومن وسط الجزيرة العربية تمر ببلادهم. ولا يزال الاسم باقياً في ديدان وهي مكان يقع إلى الجنوب الغربي من ((تيماء)). وكانت دَدان التي تقع بقرب تيماء مركزاً للتجارة في الجزيرة العربية. واسمها الحديث ((العُلا)) في وادي القرى في شمال الحجاز. وقد كانت محطاً للقوافل كما كانت مركزاً للتجارة من اليمن والهند إلى البحر الأبيض المتوسط.'),
(1396, 'دَرْبة', 'مدينة في القسم الجنوبي من ليكأونية في آسيا الصغرى. ولما رجم بولس في لسترا وطرد هو وبرنابا منها، ذهبا إلى دربة. وهناك بشرا بالمسيح وربحا عدداً كبيراً من التلاميذ للمسيح (اع 14: 6 و 21) ثم مرّ بها بولس في رحلته التبشيرية الثانية وتعرف بتيموثاوس فيها أو في لسترا. وكانت دربة مكان ميلاد غايوس (اع 20: 4) ويعتقد بعض العلماء أنها كانت تقع على تل جودلسين في سهل يبعد مسافة ثلاثة أميال شمالي غربي زوستا وعلى بعد خمسة وأربعين ميلاً جنوبي كونية أو ايقونية.'),
(1397, 'دَرْج', '(انظر ((كتاب)) ).'),
(1398, 'دَرَجة، درجات', 'تستعمل كلمة ((درجة)) في الترجمة العربية للكتاب المقدس للدلالة على عدة أشياء:(1) أنها تدل على قسم من الزمن، ففي الساعة الشمسية يُبين الوقت بالدرجات (2 ملو 20: 9 و اش 38: 8).(2) تستعمل للوحدة التي يتكون السلّم من مجموعها سواء أكان السلم من حجارة أم من غيرها (2 اخبار 9: 18).(3) تستعمل للدلالة على مكانة الإنسان بين الناس ومقامه في الجماعة التي ينتمي إليها (1 تيمو 3: 13).'),
(1399, 'دَرْدِي', 'وهو ما يرسب من الخمر أو أي نوع من الكحول ويطلق عليه أيضاً اسم ((الكدر)) وتشير عبارة ((خمر على دردي)) المذكورة في اش 25: 6 إلى الخمر التي طال عليها الأمد دون أن تحرك. وكانوا يتركون الخمر على كدرها ورواسبها أمداً طويلاً كيما يشتد لونها وتتأصل خواصها فيها، ويشار مجازياً بالاستقرار على الدردي إلى اكتفاء الإنسان بحالته الخلقية وظروفه الراهنة (ارميا 48: 11 و صف 1: 12) ويشير شُرب عكر كأس الغضب إلى وقوع القصاص على الأشرار بتمامه (مز 75: 8).'),
(1400, 'دُرّة، دُرر', '(مت 7: 6 انظر ((لؤلؤ)) ).'),
(1401, 'دَرَس، يدرس، دِرَاساً', 'هذه هي العملية التي بها تنفصل الحبوب عن القش. وكانوا في القديم يخبطون مقادير صغيرة من الحبوب بعصاً (قض 6: 11) وكانت هذه هي الوسيلة متَّبعة في استخراج الشبْث والكمون (اش 28: 27) أما عندما تكون المقادير كبيرة فكانوا يستخدمون لدرسها الثيران في البيادر. وكان البيدر يقام في العراء لصفاء الجو وجفافه في تلك البقاع (قض 6: 37) وكانوا يقيمون بيدراً واحداً للقرية كلها. ولكن أحياناً ما كان فرد بذاته يملك بيدراً لنفسه (2 صم 24: 16) وكانوا يحرصون أن يكون البيدر على سطح صخري مستوٍ ما أمكن وعلى قمة تل معرّض لهبوب الرياح. فكان بيدر أُرنان على جبل الموريا وقد بنى الهيكل عليه (1 اخبار 21: 15-28) أما إذا لم يتوفر مثل هذا البيدر الطبيعي فإنهم كانوا يسوون قطعة من الأرض ويمدونها على شكل دائرة يبلغ قطرها نحو خمسين قدماً. ويأتون بحزم الغلة التي يريدون درسها فتحل وتصف على شكل دائرة على أرضية البيدر. ويستخدمون الثيران أو البقر أو غير هذه من الحيوانات لجر النورج ولا يكمّمون الحيوانات التي تدرس (تث 25: 4). وتسير الحيوانات على الغلة على شكل دائرة فيساعد درس هذه الحيوانات مع النورج على تكسر السيقان إلى تبن وانفصال الحب عن التبن. وكانت النوارج البدائية تصنع من قطعة من الخشب وكانوا يضعون عليها أحجاراً لجعلها ثقيلة حتى تسهل عملية الدرس أو كان السائق يجلس عليها لكي يزيد من ثقلها للقصد نفسه. ثم تطورت النوارج فأصبحت عبارة عن عجلة مصنوعة من برواز مستطيل الشكل فيه ثلاث اسطوانات مجهزة بدواليب من حديد أطرافها مسننة كالمنشار (اش 41: 15) وكان يثبت فوق البرواز مقعد يسع شخصاً أو اثنين يجلس عليه لسائق ومعه آخر وتجره الحيوانات الدارسة. وعندما يمر النورج على العيدان والسنابل يقطع القش ويخرج الحب.أما النورج الذي يستعمل في معظم نواحي سورية وفلسطين فهو عبارة عن لوحين من خشب متين يثبت أحدهما بجانب الآخر بعارضتين. ويبلغ طول هذا النورج نحو ذراعين ونصف ذراع وعرضه نحو ذراع وربع وجزؤه الأمامي منحن قليلاً إلى أعلى وسطحه السفلي مثقوب بثقوب كثيرة يثبت فيها قطع من صوان أو صخر آخر صلب تبرز نحو نصف قيراط من السطح فعند مرور النورج على عيدان الغلة تكسّرها فتصير تبناً وينفصل الحب عن السنابل ويجلس السائق على سطح النورج العلوي أو يتكئ عليه. وتأكل الحيوانات التي تجر النورج من السنابل فإنها لا تُكمّ في أغلب الأحيان.وينام صاحب الغلة على البيدر أو بالقرب منه ليحرس غلته (را 3: 2-14). وبعدما تدرس الحبوب تذّرى فيجمع التين في كومة والحبوب في أخرى، أما العصافة أو ذات القش الصغيرة التي لا نفع منها فإن الريح تذريها (مز 1: 4) أو إن بقيت فإنها تحرقها النار (اش 5: 24 و مت 3: 12) ويعد الفراغ من عملية الدرس تخزن الحبوب في المنازل أو في آبار جافة أو في المغاور. وكذلك كان التبن يستخدم ليخزن في علف الحيوان أو ليستخدم وقوداً. أما الآبار التي كانوا يخزنون فيها الغلال على هيئة قنينة يزيد عمقها على قامة الإنسان وفمها مستدير يزيد قطره على نصف الذراع. ويكون بمستوى الأرض بحيث يسهل إخفاؤه. وأقيمت بعض هذه الآبار في الغرف الخاصة بالنساء لكي تخفى عن العيون (2 صم 4: 6 و 17: 18 و 19).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1402, 'مَدْرَسة', 'وجدت المدارس في بابل وفي مصر في عصور مبكرة في التاريخ ملحقة بالهياكل والمعابد ولم يكن للعبرانيين في الأزمنة القديمة مدارس بالمعنى المتداول الآن. أما التعليم فلم يكن منعدماً. فقد كان الوالدون يعلمون أولادهم تعليماً دينياً (تك 18: 19 و تث 6: 7) وتذكر الابوكريفا في سفر سوسنا 3 شيئاً عن هذا التعليم الديني.ونرى في العهد الجيد إشارة واضحة إلة وجود هذا النوع من التعليم (2 تيمو 3: 15) أما الكبار فقد كانوا يتلقون التهذيب الديني على أيدي الكهنة واللاويين الذين كانوا يطوفون في جميع أنحاء البلاد. بل كثيراً ما كان هؤلاء الكهنة واللاويون يطوفون في جميع أنحاء البلاد لكي يعلموا الشعب سنن الشريعة وفرائضها (لا 10: 11 و 2 اخبار 17: 7-9 و حجي 2: 11). وكانوا كل سبع سنين يقرأون الشريعة جهاراً في عيد المظال على مسامع جماعة الشعب (تث 31: 10-13)   وكانت الأعياد والأناشيد والترانيم التي كتبت لهذا الغرض (تث 31: 19 و 30 و 32: 1-43) تحي ذكرى هذه الحوادث التي حدثت في نشأة الأمة والتي جعلها تعبد الرب فاديها والذي أحسن إليها. وقد أذاع الأنبياء المعرفة الدينية بمناداتهم، وانهضوا الحياة الدينية في بلادهم وكانت المعاملات التجارية والنظم القضائية تجري في الشوارع والميادين والساحات على مرأى من الناس ولذا فكانت من أهم الوسائل لتثقيف الشعب وتعليمه بالنظر والسمع ويغلب على الظن أن القراءة والكتابة كانت مألوفة لدى الكثيرين من الشبان حتى في العصور المبكرة (قض 8: 14 و اش 10: 19).أما في العصر الإغريقي-الروماني فكانوا يوجهون عناية عظمى نحو تهذيب النشء وتعليمهم. وكانت تلحق بالمجامع مدارس أولية حيث كان يتعلم الأطفال أن يقرأوا الكتب المقدسة وأن يكتبوا. وكانوا يتعلمون أيضاً شيئاً من علم الحساب. وفي نحو عام 75 ق.م. صار التعليم الابتدائي إلزامياً. وقد حددت سن الالتحاق بهذه المدارس في عصر غمالائيل. فكان يلحق الأطفال بالمدارس في ذلك الحين عندما يبلغون السادسة من العمر. وكانوا يستخدمون الرقيق وغيرهم كمعلمين يقومون بتعليم أبناء الأغنياء. وكان الكتبة يقومون بالتعليم الذي يلي التعليم الابتدائي. وكانت الموضوعات التي يعلمونها تتناول الشريعة. وقد أقيمت قاعات للدرس ألحقت بالهيكل في أورشليم أما في المدن الأخرى فكانت تلحق بالمجمع قاعة وكانت تستخدم لإلقاء المحاضرات (لو 2: 46). وكان التعليم على الغالب، بالمجان ولكن يقال أنه في عصر هيرودس الكبير كان حارس الباب يجمع نقوداً كرسم للدخول. ولم يكن تعليم التلاميذ قاصراً على هذه الوسائل فإنه بالإضافة إلى هذه كان رجال العلم يقيمون منافسات ومناظرات علنية بين بعضهم البعض يحضرها الذين يرغبون في العلم ومن يريد التعليم.وكان المسيح يعلم في المجمع وفي رواق الهيكل (مرقس 6: 2 و يو 1: 23).ويرجح أن غمالائيل كان رئيساً لمدرسة الدراسات العليا وكان ذا شهرة بالغة كأستاذ (اع 22: 3) ويقال أن كرسي الأستاذ أو المعلم كانت مرتفعة عن سطح أرض حجرة الدرس حتى أن قدميه كانتا في موازاة رؤوس التلاميذ. ويرّجح بأن الآية المشار إليها سابقاً مجازية ويراد بها أن غمالائيل كان أستاذاً مشهوراً وإن له في التعليم مكانة ممتازة.(انظر ((ولد))، ((تلميذ))، ((مؤدِبّ)) ).'),
(1403, 'درْع', '(انظر ((ترس))، ((سلاح)) ).'),
(1404, 'دَرْقُون', 'اسم عبري ربما كان معناه ((سريع)) أو ((مستعجل)) قارنه بالفعل العربي ((دَرَق)) أي ((أسرع)) وهو اسم رئيس أسرة بين أبناء عبيد سليمان (عز 2: 56 و نحم 7: 58).'),
(1405, 'دِرْهَم', '(انظر ((مكاييل))، ((أوزان))، ((فضة)) ).'),
(1406, 'دَرُوِسَّلا', 'اسم لاتيني وهو مؤنث دروسوس وهي صغرى بنات هيرودس اغريباس الأول من زوجته كيبروس (ولدت حوالي عام 38 ميلادي)، أي قبل موت أبيها في عام 44 بعد الميلاد. ولما لم تبلغ السادسة من عمرها بعد أعطي الوعد لشخص يدعى ابيفانيس بأنه سيتزوجها، وكان ابيفانيس هذا ابن انتيخوس ملك ((كومجيني)) وقد وعد ابيفانيس أن يدخل الدين اليهودي ولكنه رفض فيما بعد ففشل التحالف ونقض العهد بينهما. من ثم تقدم للتزوج منها عزيز-ملك امسا (حمص) بنفس الشروط التي طلب من ابيفانيس تنفيذها. فقبل ملك حمص أن ينفذ هذه الشروط وتم الزواج وصارت دَرُوسلا زوجة له. وقد كانت دروسلا على جانب من الجمال الرائع ولذلك فكانت أختها الكبرى برنيكي تضطهدها وكانت برنيكي ذلت جمال عادي. وقد أغرى جمال دروسلا فيلكس حاكم اليهودية الروماني فأغرى قبرصياً يدعى سمعان أن يستميلها إليه لتكون زوجة له فاستجابت لإغرائه وبخاصة لأن اضطهاد برنيكي لها جعلها في شقاء وتحدت الشريعة اليهودية وتركت زوجها وتزوجت فيلكس-وهو أممي وعابد للأوثان. وكان ذلك في نحو سنة 53 ب.م. وولد لهما ابن سمياه اغريباس وكبر اغريباس هذا وتزوج إلا أنه هلك في انفجار بركان فيزوف (انظر: آثار يوسيفوس، الكتاب الثامن عشر والفصل الخامس والفقرة الرابعة). وكذلك الكتاب العشرين والفصل السابع والفقرتين الأولى والثانية) ومن هذه الحوادث يمكن أن ندرك لماذا تكلم بولس عن البرّ والتعفف والدينونة أمام فيلكس ودروسلا وكيف ارتعب فيلكس من كلامه (أعمال 24: 24 و 25).'),
(1407, 'دَعْوَى', 'كان رفع الدعاوى مبدأً مقرراً منذ القديم في الشريعة اليهودية (تث 17: 8 و 9). وكانت الشريعة الرومانية تبيح للرعايا أن يرفعوا دعواهم إلى الإمبراطور إذا لم يرضوا بحكم حكام الأقاليم والمقاطعات. ولذلك رفع الرسول بولس دعواه إلى قيصر (اع 25: 11).'),
(1408, 'دَعوئيل', 'اسم عبري معناه ((معرفة الله)) أو ((الدعاء لله)) وربما كان من أصل قريب من الفعل العربي ((دعا)). وهو رجل من سبط جاد وأبو الياساف (عدد 1: 14 و 7: 42 و 10: 20) ويدعى في عدد 2: 14 رعوئيل ومعنا ((صديق الله)).'),
(1409, 'دُفٌّ', 'اسم لآلة موسيقية وهي نوع من الطبول أو هي ما يسمى ((بطبل اليد)). وهي عبارة عن قطعة من الجلد الرقيق مشدود إلى إطار من الخشب شداً محكماً قوياً بحيث إذا ضربت اليد على الجلد المشدود أحدث صوتاً وإذا ما أتقن توقيع اليد عليها على نغم خاص أعطت صوتاً موسيقياً جميلاً. ويعلقون في أطراف الإطار الخشبي أجراساً صغيرة فتهز اليد الواحدة الآلة وتضرب أصابع اليد الأخرى على الجلد. وكان الدُف يستعمل كثيراً في الحفلات، وكانت تقوم النساء بالضرب عليه ويصحب ذلك الغناء. ومراراً كانت تضرب الدفوف ويرقص الراقصون على أنغامها. وأحياناً ما يستعمل الدُفّ بمفرده ولكنه في أحيان كثيرة يستعمل مع غيره من الآلات الموسيقية لمرافقة جوقات الترنيم في ترنيمها، أو تستخدم مع الفرق الموسيقية التي تشترك في عبادة الرب (تك 31: 26 و خر 15: 20 و قض 11: 34 و 1 صم 10: 5 و 18: 6 و 1 اخبار 13: 8 و أيوب 21: 12 و مز 81: 2 و اش 5: 12).'),
(1410, 'دَفّة', '(انظر ((سفينة)) ).'),
(1411, 'دُفقَة', 'اسم عبري ربما كان معناه (((سوق المواشي)) وهو اسم أحد الأماكن الذي حطّ فيه العبرانيون رحالهم أثناء ترحالهم في البرية. وكان على الطريق إلى سيناء بين البحر الأحمر ورفيديم (عدد 33: 12 و 13) وقد قال بعض العلماء أنه ربما كان مكانها اليوم ((سرابية الخادم)) أو مكان قريب من وادي المغارة.'),
(1412, 'دَقَر', 'اسم عبري معناه ((طعن)) أو ((مِعْوَل)) وهو أبو أحد الوكلاء الذين كانوا يعملون في مسح الأراضي في حكم سليمان (1 ملو 4: 9).'),
(1413, 'دِقْلَة', 'اسم سامي وربما كان معناه ((شجرة النخيل)) قارنه مع الاسم العربي ((دَقل)) أي نوع من نخيل البلح من مرتبة دنيا. وكان اسماً لقوم من نسل يقطان (تك 10: 27 و 1 اخبار 21) وكان سكن هؤلاء على الأغلب في الجزيرة العربية.'),
(1414, 'دَلايا', 'اسم عبري معناه ((من حرره الرب)) وقد ورد:(1) اسم رجل من نسل هارون وقد نمت أسرته إلى أن صارت عشيرة في عصر داود وكان الكهنة من هذه الأسرة يكونّون الفرقة الثالثة والعشرين من الكهنة (1 اخبار 24: 18).(2) اسم أمير وهو ابن شمعيا وأحد الذين طلبوا إلى الملك يهوياقيم أن لا يحرق سفر نبوات ارميا (36: 12 و 25).(3) رجل من ذرية داود وهو ابن اليوعيني (1 اخبار 3: 24).(4) أحد النيثيم وجدّ قوم صعدوا من تل ملح وتل حرشا (عز 2: 60 و نحم 7: 62).(5) اسم أبي رجل حاول أن يخيف نحميا وربما كان هو الرجل الذي ذكر سابقاً في (1) حفيده (نحم 6: 10).'),
(1415, 'دُلْب', 'نوع من الشجر الذي ينمو عند جداول المياه وينمو إلى ارتفاع يتراوح بين 70 و 90 قدماً وأوراق هذا النوع من الشجر تشبه كف اليد، أو تشبه أوراق شجر الجميز. وقد أطلقوا عليه في اللاتينية اسم Platanus Orientalis. وينمو في جنوبي أوربا وغرب آسيا. وينمو في فلسطين كشجر بريّ على جوانب الجبال. كما أنهم يزرعونه في أماكن كثيرة وقد ورد ذكره مرتين في الكتاب المقدس (تك 30: 37 و حز 31: 8).'),
(1416, 'دِلْعان', 'اسم عبري من أصل معناه ((اندلع أو امتدّ)). وهو اسم في البقاع السفلي من أرض يهوذا بقرب المصفاة (يش 15: 38) ومكانها في الوقت الحاضر تلّ النجيلة.'),
(1417, 'دَلْفون', 'اسم اكادي معناه ((من لا ينام)) وهو ابن هامان (اس 9: 7).'),
(1418, 'دَلماطيّة', 'إقليم على الشاطئ الشرقي من البحر الادرياتيكي ويشمل الإقليم عدد كبير من الجزر الصغيرة المجاورة له. وقد أخضع الرومان القبائل الجبلية في هذا الإقليم في عام 9 ب.م. بقيادة اغسطس قيصر وطيباريوس قيصر وأصبح الإقليم مقاطعة رومانية من ذلك الحين. وكانت تعتبر في الأصل جزءاً من الليريكون. وكان هذا أقصى حدود رحلات الرسول التبشيرية في هذه النواحي (رومية 15: 19) وكان رفيقه تيطس بعد أن بقي مدة من الزمن مع بولس في العاصمة الإيطالية قد ارتحل إلى دلماطية. ويرّجح أنه ذهب هناك ليذيع بشارة الإنجيل بين سكان ذلك الإقليم (2 تيمو 4: 10) وهي اليوم جزء من دولة يوغسلافيا.'),
(1419, 'دَلمانوثة', 'مكان يرّجح أنه كان بقع على الشاطئ الغربي من بحر الجليل بالقرب من مجدل. ويظن بعضهم أنها نفس مجدل، ويظن آخرون أن مكانها خان المنية بالقرب من طبغة (الطابغة). وقد أبحر إليها يسوع وتلاميذه بعد إطعام الأربعة الآلاف (مر 8: 10 قارنه مع مت 15: 39).'),
(1420, 'دَليلة', 'اسم عبري معناه ((معشوقة)) أو ((مدللة)) وهي امرأة فلسطينية من وادي سورق وقد أغوت شمشون فأحبها إلى أن وقع في حبائلها فانتزعت منه سر قوته فسلمته إلى أيدي أعدائه الذين قلعوا عينيه وسجنوه في غزّة إلى أن انتهت حياته بموته مع أعدائه (قض 16: 4-22) (انظر ((شمشون)) ).'),
(1421, 'دمشق', 'عاصمة سوريا وأكبر مدنها. تقع على مسافة نحو خمسة وستين ميلاً شرقي البحر المتوسط وعلى مسافة نحو 133 ميلاً إلى الشمال الشرقي من أورشليم. والمدينة مقامة في سهل عند السفح الشرقي لجبل لبنان الصغير. وينبع في هذه السلسلة من الجبال نهران يجريان شرقاً وهما: نهرا ابانة وفرفر المذكوران في الكتاب المقدس (2 ملو 5: 12). ويدعيان الآن بردى والأعوج. ويتفرع هذان النهران إلى جداول كثيرة تسقي مياهها الحدائق والبساتين التي تحيط بدمشق. وتقع دمشق على ارتفاع نحو 300 و 2 قدم فوق سطح البحر وجوها معتدل. وهي مركز هام لطرق التجارة. وهناك طريق يسير غرباُ وجنوباً إلى أن يصل إلى البحر الأبيض المتوسط ومصر. وطريق ثان يسير جنوباً إلى شرق الأردن والجزيرة العربية. وطريق ثالث يسير عبر الصحراء إلى بغداد. وطريق رابع يسير شمالاً إلى حلب. ودمشق مشهورة بأسواقها والأقمشة التي تروج تجارتها فيها وكذلك اشتهرت بالأواني المعدنية.تاريخها:مدينة دمشق عريقة في القدم. وقد ورد ذكرها في عصر إبراهيم (تك 14: 15). وكان اليعازر الدمشقي الموكل على بيت إبراهيم من هذه المدينة. وكانت دمشق في عصر داود إحدى الممالك التي قامت في الهلال الخصيب. وقد غزاها داود وأقام فيها حامية (2 صم 8: 5 و 6 و 1 اخبار 18: 5 و 6). وبعد أن محق المملكة السورية في صوبة، قام رجل يدعى رزون، وكان هذا من رعايا ملك صوبة سابقاً، وجمع حوله جماعة من الرجال وتمكن من أخذ دمشق حيث أسس المملكة السورية. ودامت الحرب بينها وبين المملكة الشمالية أو مملكة السامرة آماداً طويلة (1 ملو 11: 23 و 24) وكانت دمشق عاصمة حزيون وطبريمون وبنهدد (1 ملو 15: 18 و 20 و 20: 34 و 2 ملو 8: 7) وحزائيل (1 ملو 19: 15 و 17 و 2 ملو 8: 8-15) ورصين. وكان هناك حلف بين طبريمون وبنهدد الأول وبين ملوك يهوذا (1 ملو 15: 18 و 19 و 2 ملو 16: 2 و 3) ولقد اتفق آخاب على أن يبني أسواقاً في دمشق (1 ملو 20: 34) وقد تحالف ملوك دمشق مع ملوك الفينيقيين واخاب ملك السامرة وقاموا بحرب ضد شلمناصر ملك آشور واشتبكوا معه في الحرب في معركة قرقر في سنة 853 ق.م. ومع أنه هزم المتحالفين إلا أنهم تمكنوا من وقف تقدم الآشوريين. ولكن شلمناصر عاد وهزم حزائيل ملك دمشق سنة 734 ق.م. وأرادا مهاجمة أورشليم استنجد آحاز ملك يهوذا بتغلاثفاسر ملك آشور فجاء إلى دمشق وأخذها (سنة 732 ق.م) وحمل أهلها في السبي إلى قير وقتل رصين (2 ملوك 16: 5-6 و اش 7: 1-8: 6 وعا 1: 3-5) ولكم سرعان ما استعادت دمشق نجاحها ومكانتها وازدهارها (حز 27: 18). وانتقلت من الآشوريين إلى الكلدانيين ومنهم إلى الفرس ثم انتقلت إلى اليونان المقدونيين. وكانت دمشق إحدى المدن التي تكونت منها ديكابوليس أو العشر مدن. وقد أخذ القائد الروماني ميتللوس كدينة دمشق عام 64 ق.م. وفي العام التالي أي 63 ق.م. أصبحت سوريا مقاطعة رومانية وكان عدد كبير من اليهود يسكنون دمشق. وكانت هناك عدة مجامع (أعمال 9: 2 و حروب يوسيفوس الكتاب الثاني والفصل العشرون والفقرة الثانية) وبالقرب من دمشق ظهر المسيح لشاول الطرسوسي في الطريق إذ كان ذاهباً إليها ليضطهد المسيحيين فيها، فسقط إلى الأرض وسمع الصوت السماوي (أعمال 9: 2 و 3 و 8-10 و 22: 6 و 10-13 و 26: 12) وقد تدلّى من أسوارها ليهرب من غضب اليهود فيها (أعمال 9: 24 و 25 قارنه مع ص 26: 21 و غلاطية 1: 17). وكانت المدينة في عصر الرسول بولس في يدي الحارث ملك البلاد العربية التي عرفت باسم ((بيترايا)) أو البتراء ولكنها سرعان ما عادت إلى أيدي الرومان (2 كو 11: 32 و 33). \nآثارها:بنى جامع دمشق الكبير في مكان كنيسة يوحنا المعمدان التي كان قد بناها الإمبراطور قسطنطين ويرّجح أن هيكل رمون (2 ملو 5: 18) كان في نفس هذا المكان والشارع الذي يدعى ((المستقيم)) الذي أقام بولس بالقرب منه لما ذهب إلى دمشق (أعمال 9: 11) يدعى الآن سوق الطويلة وفيه أعمدة قديمة وربما ترجع إلى عصر الرسول بولس. ويقع بيت حنانيا (أعمال 9: 10) كما يذكر التقليد في الحي المسيحي والمكان الذي تدلى منه بولس من السور في زنبيل (أعمال 9: 25) مكانه الآن الكنيسة.'),
(1422, 'دموع في زق', 'وردت هذه العبارة في مز 56: 8 ويظن أنها تشير إلى عادة كانت شائعة عند المصريين القدماء والفنيقيين والرومانيين إذ كان أولئك القوم يجمعون الدموع التي تذرف من أعين النائحين في أوقات تقديم التعزية. وكانت هذه الدموع تحفظ في قارورة تدعى زق الدموع وتوضع على أضرحة الموتى لتكون شاهداً على حزن الأحياء على الميت، ومحبتهم له وشعورهم بفداحة الخسارة لفقده. وكان زق الدموع يصنع عادة إما من الزجاج أو من الفخار.'),
(1423, 'دُمَّل، دُمَّلة', 'الدمل هو قرحة ملتهبة. وقد ضُرب الناس والبهائم بالدمامل في الضربة السادسة التي حلت بآل فرعون في زمن موسى (خر 9: 8-11). ويوجد نوع خاص من الدمامل هو من ضمن علامات البرص (لا 13: 18-20) وستكون الدمامل جزءاً ن العقاب الذي يحل بالذين يعبدون الوحش (رؤيا 16: 2).'),
(1424, 'باب الدِّمْن', 'وهو أحد أبواب أورشليم في عصر نحميا (نحم 2: 13 و 3: 13 و 14 و 11: 31). وكان هذا الباب يقع على مسافة ألف ذراع شرقي باب الوادي حيث يلتقي وادي تيروبيون أو الوادي بوادي هنّوم جنوبي بركة سلوام الحالية. ويرّجح أن هذا الباب سمي باب الدمن أو باب الزبل لأن الأقذار والمهملات كانت تحمل من المدينة من هذا الباب وتطرح في وادي هنّوم.'),
(1425, 'دِمْنة', 'مدينة للاويين كانت تقع ضمن زبولون (يش 21: 35) ويرّجح أنها نفس رمون المذكورة في يشوع 19: 13 وهي رمّانة الحديثة التي تبعد مسافة ستة أميال شمالي الناصرة.'),
(1426, 'دَم', 'الدم هو السائل الحيوي الذي يسير في الجسم. وحياة الإنسان في دمه (لا 17: 11 و 14) أو أن الدم هو الحياة (تث 12: 23) أو أنه العنصر المادي فيها. فهناك آخر مادي كما يعلمنا الكتاب المقدس (مز 104: 29 و 30) بما أن الدم يمثل الحياة وبما أن الحياة مقدسة أمام الله فقد قيل عن دم هابيل أنه صرخ إلى الله من الأرض طالباً الانتقام له (تك 4: 10) وبعد الطوفان مباشرة حرّة أكل دم الحيوانات مع أنه صرح بذبحها وأكلها كطعام (تك 9: 3 و 4 و اع 15: 20 و 29). وقد نصّت الشريعة على أن سافك دم الإنسان بالإنسان يسفك دمه (تك 9: 6). وفقدان الحياة هو عقاب الخطيئة وكان من الضروري أن تسلم هذه الحياة رمزاً حتى تمحى الخطيئة (عب 9: 22) ولذا نصّت الشريعة الموسوية على أن دم الذبائح التي تذبح أو تصطاد لأكلها طعاماً ينبغي أن يغطي التراب لأن الله منع تناول الدم كطعام وخصصه للتكفير عن الإثم (لا 17: 10-14 و تث 12: 15 و 16). أما العبارات التي وردت في العهد الجديد مثل ((دم يسوع)) و ((دم المسيح)) و ((دم يسوع المسيح)) و ((دم الحمل)) فكلها عبارة رمزية مجازية يقصد بها الملكوت الموت الكفارى (1 كو 10: 16 و افسس 2: 13 و عب 9: 14 و 10: 19 و 1 بط 1: 2 و 19 و 1 يو 1: 7 و رؤيا 7: 14 و 12: 11).'),
(1427, 'وليّ الدم، أو منتقم الدم', 'وهو الشخص الذي يتولى إيقاع العقاب بالقاتل ولذا فيعطى القانون حقّه من الإجلال، ويسري على البشر قول الوحي أن سافك دم الإنسان بالإنسان يسفك دمه (تك 9: 5 و 6 و عدد 35: 31) وعندما تنتظم الحياة المدنية يصبح من واجب محاكم العدل أن تقتص من الآثم. غير أنه في العالم السامي كما كان عند غيرهم من الشعوب كالإغريق وقبائل الجرمان والسلاف كان العرف المتبع أن ينتقم الإنسان لنفسه. فكان عندما يقتل أحد أو يموت من جرّاء حادثة ما، كان على أقرب الأقرباء إلى الميت أن ينتقم له وكان يدعى ((منتقم الدم)) فيقتل القاتل أو من تسبب في موت قريبه ولو عن غير قصد بدون محاكمة تظهر ظروف القتل وبواعثه. وكان أقرب أقرباء من قتل انتقاماً للدم يأخذ على نفسه مهمة قتل من قتل قريبه. وهكذا تبدأ سلسلة من الثأر بين أسرتي القاتل والمقتول. وقد وضعت الشريعة الموسوية قواعد أزالت الكثير من سيئات نظام ((الثأر)). فتعينت مدن للملجأ يهرب إليها القاتل غير المعتمد. ويعطى فرصة للمحاكمة فتثبت برآته القتل عمداً وعن سابق قصد (عدد 35: 19 و 24 و 27 و 2 صم 14: 11) (انظر ((مدن الملجأ)) ).'),
(1428, 'دَنَس', 'تعتبر الشريعة الموسوية نجساً أي شخص أو أي شيء يمس شخصاً نجساً أو شيئاً نجساً مثل جسم ميت أو ما أشبه (لا 17: 15) وكانت تفرض بعض الفروض والطقوس من غسل وغيره لإزالة مثل هذه النجاسة. وتعبر بعض الخطايا في العهد القديم نجاسة إذ أنها تنجس الإنسان من ناحية روحية، كالزنى مثلاً (لا 18: 20) والقتل (اش 59: 3) وعبادة الأوثان (حز 37: 23).أما في العهد الجديد فقد علم الرب يسوع المسيح بأن النجاسة التي تمسّ النفس أو الروح أشد خطراً وأعظم ضرراً على الإنسان من النجاسة الطقسية (مت 15: 18 و مر 7: 2 و 19) وقد سار الرسل في هذا السبيل سيرة المخلص في إبراز الخطر الذريع الكامن في النجاسة الروحية وفي بيان الأهمية العظمى للطهارة الروحية (رومية 12: 15 و يع 3: 6). وقد أعطى الله بطرس درساً في الرؤيا بأن الفرائض الطقسية بحسب الشريعة الموسوية قد أصبحت في المسيح ولا أثر لها على المؤمن ولا سلطان لها عليه (أعمال 10: 11-15).'),
(1429, 'دَنّة', 'قرية في أرض يهوذا الجبلية ومكانها الآن دير الشمس أو سميا بين يوطة (يطة) والظاهرية.'),
(1430, 'دِنْهابة', 'اسم أدومي ربما كان معناه ((من يعطي حكماً)) وكانت عاصمة بالع ملك ادوم (تك 36: 32 و 1 اخبار 1: 43) وربما كان مكانها اليوم ((خربة الدن)) وهي بلدة في موآب جنوبي ارنون بقليل.'),
(1431, 'دَرْدَع، دارَع', 'اسم عبري ربما كان معناه ((شوك)) وهو ابن ماحول من عشيرة زارح من سبط يهوذا. وقد اشتهر بالحكمة. ولكن الكتاب يذكر أن سليمان فاقه في الحكمة (1 ملو 4: 31 و 1 اخبار 2: 6).'),
(1432, 'دَفَنَ', '(انظر ((قبر)) ).'),
(1433, 'دَهْر', 'تستعمل هذه الكلمة لعدّة معان:(1) تستعمل للدلالة على الزمن الطويل سواءً أمان في الماضي أم في المستقبل (تك 6: 4 و 9: 12 و غيره).(2) تستعمل للزمن الحاضر (لو 16: 8 و 20: 34 و رومية 12: 2 و 1 كو 1: 20) وغالباً ما تشير إلى الشر الذي في العالم.(3) تستعمل عن انقضاء العالم (2 بط 3: 18).(4) تستعمل للدلالة على الأزل والأبد (مز 45: 6 و 102: 24 وعب 1: 8).'),
(1434, 'دَهْوِيُّن', 'وردت هذه الكلمة في بعض الترجمات العربية ترجمة لكلمة آرامية في غزر 4: 9 فجاءت اسماً لشعب معيّن. وقد ذكر المؤرخ اليوناني هيرودتس شيئاً عن قبيلة الإمبراطورية الفارسية اسمها داي. ولا يعرف على وجه التحقيق إذا ما كانت هذه القبيلة هي نفس الدهوبين أم لا.وقد وردت قراءة أخرى للأصل الآرامي للكلمة تشير إلى أن معناها هو ((أي أنّه)) وهذا هو المعنى الوارد في بعض النسخ من الترجمة السبعينية ولذلك فهو يوجد في معظم الترجمات الحديثة. وهذا التفسير للكلمة بالنسبة إلى القرنية مستساغ ومعقول وتكون قراءة العبارة هكذا: ((والشوشنيون أي العيلاميون )) فقد كانت شوشن أو سوسة عاصمة عيلام.'),
(1435, 'دُوَاغ', 'اسم ادومي معناه ((شديد الخوف)) وكان رئيس رعاة لشاول (1 صم 21: 7) وكان في نوب في الخيمة وقد كان داود هارباً من امام شاول فوصل إلى نوب وتمكن من الحصول على طعام وسيف من اخيمالك الذي لم يعلم بأنه هارب من الملك (عنوان مز 52) فاخبر دواغ شاول بما حدث فثارت ثائرة الملك واستدعى اخيمالك والكهنة الذين كانوا معه وطلب منهم ايضاح ما حدث. فلم يكتف شاول بهذا الايضاح وطلب إلى الحراس أن يقتلوا الكهنة فامتنعوا عن ذلك فامر شاول داوغ أن يقوم بقتلهم فقتل خمسة وثمانين كاهناً. ثم هاجم قريتهم وقتل فقتل النساء والاولاد وأهلك الماشية أيضاً (1 صم 22: 7 - 23).'),
(1436, 'دُوَثان', 'كلمة عبرية ربما تعني ((آبار)) وهي بلدة لا تبعد كثيراً عن شكيم والسامرة ويمر بها طريق القوافل (تك 37: 14 و 17 و 25 و 2 ملو 6: 13). وقد القي يوسف في بئر بالقرب منها ثم أخرج من البئر وبيع للمديانيين (تك 37: 17 - 28). وقد حاصر الاراميون اليشع في تلك البلدة ولكن ضُرب جنود الجيش المهاجم بالعمى وأُخذوا إلى السامرة، ومن هناك ارسلوا إلى بلادهم دون أن يصابوا بأذى (2 ملو 6: 8 - 23) ومكانها اليوم تل لا يسكنه أحد ويدعى (( تل دوثان )) بالقرب من بئر تبعد مسافة تسعة اميال ونصف شمالي شرقي السامرة. وبالقرب من التل توجد طريق للقوافل وعلى أغلب الظن أنها الطريق التي سارت فيه القوافل منذ عصر يوسف. وبالقرب منها حُفَر تخزن فيها المياه شبيهة بالبئر التي القي فيها يوسف. وقد اثبتت الكشوف الحديثة أن تل دوثان كانت مدينة آهلة بالسكان في زمن يوسف وفي زمن اليشع.'),
(1437, 'دودة، ودود', 'يطلق هذا الاسم على أي حيوان صغير زاحف، لا عظام في جسمه ويتكون من عدة مفاصل أو حلقات متحركة ولا اطراف له أو أن كانت له اطراف فقصيرة جداً. ويطلق على الدودة في اللغة العبرية اسم ((تولع)) أو ((تولعث)) وفي اليونانية اسم ((سكولكس)). وقد ورد ذكرها بأنها متلفة للعنب والكروم ((تث 28: 39 ويونان 4: 7. وقد تولد الدود في المن الذي اخترته العبرانيون في البرية غذ ابقي اناس منهم بعضاً من المنّ إلى الصباح (خر 16: 20) كذلك يأكل الدود الجثث (اش 14: 11 وقارن اش 66: 24 مع مر 9: 48). وقد الدود سبباً في موت الاحياء (اع 12: 23) ويقارن ضعف الانسان وحقارته بالدودة (ايوب 25: 6 واش 41: 14).'),
(1438, 'دُودانيم', 'الرابع بين ابناء ياون (تك 10: 4) كما ذكر في بعض المخطوطات العبرية. أما بعض المخطوطات العبرية الاخرى وكذلك الترجمات اليونانية القديمة والسامرية فقد ذكرته باسم رودانيم. وكذلك ورد الاسم بهذه الصيغة في 1 اخبار 1: 7 في مثير من المخطوطات والترجمات. وقد ظن الكثيرون أن صيغة الاسم الصحيحة في تك 10: 4 هي رودانيم.'),
(1439, 'دوداواهو', 'اسم عبري معناه ((محبوب الرب)) أو ((الرب صديق)) وهو اسم رجل من مريشة، وهو ابو اليعزر الذي تنبأ عن تحطّم سفن يهوشافاط (2 اخبار 20: 37).'),
(1440, 'دُودَاي', '(انظر ((دودو)) (2) ).'),
(1441, 'دُودُو', 'اسم عبري اختصار دوداواهو وقد ورد اسم:(1) رجل من يساكر وهو من اسلاف القاضي تولع (قض 10: 1).(2) رجل اخوخي وهو ابو العازر أحد ابطال داود الثلاثة الذين كانوا في المرتبة الاولى (2 صم 23: 9) وقد ورد باسم دوداي في الاصل العبري في 1 اخبار 11: 12. وقد عين داود دوداي هذا قائداً حربياً لفرقة الشهر الثاني.(3) رجل من بيت لحم وهو ابو احد ابطال داود الثلاثة الذين كانوا في المرتبة الاولى (2 صم 23: 9) وقد ورد باسم دواداي في الاصل العبري في 1 اخبار 11: 12. وقد عين داود دوادي هذا قائداً حربياً لفرقة الشهر الثاني.(3) رجل من بيت لحم وهو ابو الحانان احد ابطال دتود (2 صم 23: 24 و 1 اخبار 11: 26).'),
(1442, 'دُوَر', 'اسم كنعاني معناه ((مسكن)) وقد ورد اسماً لمدينة كنعانية على البحر الابيض المتوسط (يش 11: 2 و 1 مكابيين 15: 11) تبعد مسافة ثمانية اميال شمالي قيصرية. وقد هزم يشوع ملكها (يش 12: 1 و 23) وكانت تقع في نصيب اشير ولكنها اعطيت لسبط منتسى (17: 11) ولكن لم يتمكن ذلك السبط من اخراج الكنعانيين منها (قض 1: 27) وقد ورد في نقش لرمسيس الثاني عشر بان ((زكّالو)) وهم شعب جاء عن طريق البحر، احتلوا تلك المدينة في القرن الثاني عشر قبل الميلاد. وقد كان الاقليم المحيط بدور من ضمن الاقاليم التي  كانت تدفع جزية لسليمان (1 ملو 4: 11). وفي القرن الرابع قبل الميلاد ضمت صيدون دور ويافا إليها. وفي سنة 217 ق.م. حاصرها انتيخوس الكبير. وحوالي سنة 138 ق. م. حاصرها انتيخوس السابع براً وبحراً (1 مكابيين 15: 11 - 14 ويوسيفوس: آثار، الكتاب الثالث عشر والفصل السابع والفقرة الثانية) وفي ايام الاسكندر جنّاوس اخذها مدة من الزمن الطاغية زويلوس (يوسيفوس: آثار الكتاب، الثالث عشر والفصل الثاني عشر والفقرة الثانية) وتوجد بقايا دور في البرج شمالي بلدة الطنطورة بقليل.'),
(1443, 'دَار', '(انظر ((مسكن)) ((هيكل)) ).'),
(1444, 'دار الولاية', 'الكلمة اليونانية المترجمة دار الولاية هي ((براتوريون Praitorion)) ومعناها أصلاً ((مكان البريتور Praetor)). وكان هذا موظفاً رومانياً يشغل مركزاً مهماً. فربما كان حاكم ولاية كبيلاطس البنطي الذي كان حاكم اليهودية.وقد ورد اسم دار الولاية في الاناجيل للدلالة على قصر الحاكم الروماني في اورشليم حيث حاكم بيلباطس البنطي يسوع المسيح وسمح بصلبه (مت 27: 27 ومر 15: 16 ويو 18: 28 و 33 و 19: 9). وقد ذكر المؤرخ اليهودي ((فيلو)) ان حكام اليهودية الرومانيين عندما كانوا يذهبون إلى اورشليم كانوا يشغلون قصر هيرودس الكبير في مكان القلعة الحديثة في القدس وهذا ما يعتقده أغلبية العلماء الآن. ويقول التقليد في اورشليم بان مكان قصر الحاكم كان إلى جانب برج انطونيا إلى شمال الحرم الشريف قليلاً ولذا فطريق الجلجثة ((او درب الصليب)) بحسب هذا التقليد يبدأ من هذا المكان. وقد اكتشفت بالقرب من هذا المكان احجار بلاط عليها رسوم منقوشة تظهر ان الجنود الرومانيين كانوا في هذا المكان في مدة وجود الرب يسوع على الارض. ولكن لا تثبت هذه الحقيقة ان قصر الحاكم الروماني كان بجوار برج انطونيا.'),
(1446, 'دُورا', 'اسم اكادي معناه ((دائرة أو أو حائط)) سهل واسع في اقليم بابل حيث نصب نبوخذ نصر تمثال الذهب (دا 3: 1) وقد اطلق اسم دورا على عدة اماكن في بابل. ويوجد هناك نهر يسمى نهر دورا وبقربه تلال تسمى ((تلال دورا)) وهي تقع في بلاد العراق الحديثة.'),
(1447, 'دومة', 'اسم عبري معناه ((سكوت)) وقد ورد:(1) اسم ابن اسماعيل (تك 25: 14 و 1 اخبار 1: 30).(2) اسم بلدة في أرض يهوذا الجبلية (يش 15: 52) ومكانها اليوم الدومة وهي تبعد مسافة عشرة أميال جنوبي غرب حبرون وعلى مسافة ميلين ونصف شمالي الظاهرية.(3) اسم مكان ذكر في أشعياء 21: 11 مع سعير أو ادوم. ويعتقد بعضهم أن هذا المكان هو الواحة التي تسمى ((دومة الجندل)). وتدعى الآن ((الجوف)) وهي في الشمال الغربي من حدود الأردن. وربما سكن نسل دومة ابن اسماعيل هذه البلاد. وربما اختار لها ويشير الاسم العبري إلى الخراب.'),
(1448, 'دَواء', 'من ضمن الأدوية المذكورة في الكتاب المقدس: الزيت (اش 1: 6 ويعقوب 5: 14). والزيت ممزوج مع الخمر (لو 10: 34)، اقراص التين (2 ملو 20: 7)، البلسان (ارميا 8: 22)، وأوراق بعض الأشجار (حز 47: 12) والخمر لأجل المعدة (1 تيمو 5: 23) والكحل للعينيين (رؤ 3: 18) ( انظر ((طب)) ).'),
(1449, 'ديبون', 'اسم موآب ربما كان معناه ((هزال)) أو (0انحلال)) وهو:(1) اسم مدينة في مواب شمالي ارنون وقد أخذها الاموريون من موآب ثم أخذها العبرانيون (عدد 21: 30 و 32: 3) وقد بناها الجاديون (عدد 32: 34) ومن ذلك الوقت دعيت ديبون جاد (عدد 33: 45 و 46) ومن بعد ذلك أُعطيت لسبط رأوبين (يش 13: 9 و 17) ثم احتلها الموآبيون قبل زمن الملك ميشع ملك موآب (انظر الحجر الموآبي سطري 21 و 28 واش 15: 2 وارميا 48: 18 و 22) واسمها في العربية ذيبان وهي خربة على بعد ثلاثة اميال شمالي نهر ارنون وشمالي غربي عراعير. وقد اكتشف الحجر الموآبي بين خرائب ديبون في عام 1868 (انظر ((الحجر الموآبي)) ) وقد كشف التنقيب في ديبون عن حائط المدينة الموآبي وعن بعض الخزف.(2) اسم قرية كانت تقع في أرض يهوذا (نحم 11: 25) وربما كانت هي ديمونة.'),
(1450, 'دبشان', 'اسم سامي معناه  ((ظبي)) وهو أحد أولاد سعير الحوري (تك 36: 21 و 28 و 30 و 1 اخبار 1: 38 و 42).'),
(1451, 'ديشون', 'اسم سامي معناه ((ظبي)) وقد ورد:(1) اسم أحد أولاد سعير الحوريّ (تك 36: 21 و 30 و 1 اخبار 1: 38 و 41).(2) اسم ابن عني وحفيد سعير (تك 36: 25 و 30 و 1 اخبار 1: 41) وقد ظن بعضهم أن (1) و (2) يشيران إلى نفس الشخص.'),
(1452, 'ديك ', '(انظر (( صياح الديك)) ).'),
(1453, 'ديماس ', 'اسم يوناني ربما كان اختصار ديمتريوس أحد زملاء بولس في خدمته. وقد أرسل سلامه من رومية إلى الكلوسيين وإلى فليمون (كلو 4: 14 وفليمون 24) ثم من بعد ذلك هجر الرسول لأنه لم يود أن يحتمل الألم والاضطهاد فذهب إلى تسالونيكي (2 تيمو 4: 10).'),
(1454, 'ديمتريوس', 'اسم يوناني معناه (( يخص ديميتر)) وكانت ديميتر هذه إلهة الزراعة وقد ورد :(1) اسم ديمتريوس الأول، الملقب سوتر وكان ملك سوريا السلوقي وقد ملك من عام 162 إلى 150 ق.م. وهو الذي أرسل جيوشاً ضد اليهود بقيادة ليساس نيكانور وقد هزم يهوذا المكابي في النهاية (1 مكا 7: 1 - 10 : 50 و 2 مكا 14: 1 - 40).(2) ديمتريوس الثاني الملقب نيكاتور ملك سوريا السلوقي الذي ملك في سوريا من عام 145 إلى 138 ق.م. ثم ملك أيضاً من عام 129 إلى عام 125 ق.م. وقد منح اليهود مقداراً من الاستقلال (1 مكا 10: 67 - 14: 3).(3) ديمتريوس صائغ وصانع هياكل فضة في افسس غذ كان يصنع النماذج لهيكل ديانا ((ارطاميس)) ولمّا رأى أن هناك خطراً على صناعته بسبب مناداة المسيحيين ضد الاصنام وانتشار الايمان المسيحي أثار زملاءه في الصناعة ضد الرسول لمدة ساعتين ((عظيمة هي ارطاميس الافسسيين)) (اع 19: 24 - 41) وقد أرغمت ثورة الجمهور بولس أن يذهب على مكدونية (اع 20: 1).(4) مسيحي يدعى ديمتريوس كتب عنه يوحنا قائلاً: ((ديمتريوس مشهود له من الجميع ومن الحق نفسه ونحن أيضاً نشهد وأنتم تعلمون أن شهادتنا هي صادقة)) (3 يو 12).'),
(1455, 'ديمون', 'اسم آخر لديبون في موآب (اش 15: 9) ويرّجح أن النبي اختار هذا الاسم لأن فيه في اللغة العبرية بعض القرابة اللفظية من كلمة ((دم)) المذكورة في نفس العدد. ومياه ديمون المذكورة في هذا العدد هي في الغالب مياه نهر ارون.'),
(1456, 'ديمونة', 'بلدة في جنوب يهوذا في النقب بالقرب من ادوم (يش 15: 22) ويرّجح أنها تفس ديبون المذكورة في نحميا 11: 25 ومكانها اليوم ((القباب أو القبيبة)) في الشمال من عرعرة وشرقي ((تل الملح)).'),
(1457, 'دَين', '(انظر ((قَرَض)) ((قِرْض)) ).'),
(1458, 'دَان، يدين، ديِن، دَينونة', 'تطلق هذه الكلمات على حكم الله على الناس بحسب أعمالهم (مت 10: 15 وجا 11: 9 ومت 12: 36 واع 17: 31 وعب 9: 27 و 2بط 2: 9 و 3: 7 و 1 يو 4: 17). وقد أعطيت الدينونة للرب يسوع المسيح فهو الديان الذي يقف أمامه جميع البشر لكي يعطوا حساباً عن أعمالهم في الجسد خيراً كانت أم شراً (مت 25: 31 و 32 و 26: 64 ويو 5: 22 واع 17: 31 ورو 2: 16 و 2 كو 5: 10) وهذه الدينونة عامة وشاملة (يو 5: 28 و 29 ورو 14: 10 و 22 و 2 كو 5: 10 ورو 20: 12 و 13) وحكم هذه الدينونة نهائي ولا يقبل النقص ولا الاسئناف. وبموجب هذا الحكم يدخل الابرار إلى أمجاد ملكوت المسيح وأفراحها، ويذهب الاشرار إلى الظلمة الخارجية واليأس الابدي (قابل ما تقدم مع مت 25: 14 - 46 و 1 كو 15: 52 - 57 و 1 تس 4: 14 -17 وعب 6: 2).'),
(1459, 'دينار', 'اللفظ العربي يرجع إلى أصل لاتيني هو ديناريوس ((denarius)) ومعناه ((عشرة)) لان هذه العملة كانت تساوي أصلاً عشرة ((آسات)) رومانية. وكان الدينار عملة رومانية من الفضة وكان وزنها في عصر العهد الجديد ثلاثة جرامات وكانت تساوي نصف ليرة لبنانية أو ستة أو سبعة قروش مصرية. وكان الدينار الجر العادي للعامل في اليوم (مت 20: 3) وكان على اليهود أن يتعاملوا بهذه العملة عندما كانوا يدفعون جزية لرومة ولذا فقد استخدم السيد المسيح هذه العملة في الجواب على السؤال الخاص بدفع الجزية لقيصر (مت 22: 19). وقد نقش على الدينار الذي كان متداولاً في أيام السيد المسيح على الأرض، صورة طيباريوس قيصر واسمه وألقابه.'),
(1460, 'دِينَة', 'اسم عبري معناه ((دينونة)) وهي ابنة يعقوب من ليئة ولم يكن ليعقوب بنات سواها. ثم أنه بينما كان يعقوب راجعاً من فدّان ارام إلى أرض كنعان مرّ على سالم - وهي بلدة في شكيم فخرجت ابنته دينة لترى بنات تلك البلاد فرآها شكيم بن حمور وكان امير البلاد هناك فأغواها واذلّها ثم طلب أن يتزوجها فابى اخوتها إلاّ إذا اختتن هو وكل الذكور في شكيم. فقبل حمور وابنه شكيم هذا الشرط وكلّما أهل مدينتهما واختين جميع الذكور في تلك المدينة. ولما تحقق شمعون ولاوي من أهل شكيم يتوجعون بسبب اختتانهم وأنهم لا يستطيعون أن يدافعوا عن أنفسهم هجما على المدينة وقتلا حمور وشكيم ونهبا المدينة وسبيا الأطفال والنساء. أما يعقوب فقد وبخهما على عملها هذا غير أنهما احتجا بغيظهما الشديد على شكيم لما اركب من اذلال اختهما (تك 30: 21 و 34: 1 - 31) وقد ذكرت دينة مع بقية أسرة يعقوب الذين ذهبوا إلى مصر (تك 46: 8 و 15).'),
(1461, 'دينيون', 'ورد هذا الاسم في الترجمات العربية للكتاب المقدس المتداولة بين، لقبيلة كانت تقطن السامرة بجل الاسرائيليين الذين ذهبوا في السبي (عزر 4: 9) ولكن بعض مخطوطات الترجمة اليونانية المعروفة بالترجمة السبعينية ومعظم الترجمات الحديثة تفسر هذه الكلمة على أنها تعني ((قضاة)).'),
(1462, 'ديوترفيس', 'اسم يوناني معناه ((من يعوله زفس)) وكان أحد أعضاء الكنيسة التي كان غايس الذي ارسل إليه يوحنا رسالته عضواً فيها. وقد كان يحب أن يكون بارزاً ظاهراً في الكنيسة وقد رفض أن يقبل الرسول يوحنا أو الأخوة الذين ذهبوا. وقد طرد الذين قبلوهم (3 يو 9 و 10).'),
(1463, 'دِيُونيسيوس', 'اسم يوناني معناه ((من يخص الاله ديونيسوس)) وكان هذا أحد الذين قبلوا الديانة المسيحية بناءً على مناداة بولس في اثينا (اع 17: 34) وقد سمي بالاريوباغي لأنه كان قاضياً في محكمة اثينا العليا التي كانت تسمى اريوس باغوس. ويقول مؤرخو الكنيسة أنه أصبح فيما بعد كارزاً شهيراً بالإنجيل وأنه مات شهيداً فس اثينا في ستة 95. وقد وجدت كتابات نسبت إليه ولكن يرّجح أنها ترجع إلى القرن الخامس الميلادي فقط.'),
(1464, 'ذِئب', 'اسم مدياني معناه ((ذنب)) وقد ورد اسم أمير للمديانيين، قتل على يد جدعون ورجاله، عند معصرة تسمت باسمه فيما بعد قض 7: 25. ولعل هذا الاسم ذئب أطلق على الأمير بسبب مهارته الحربية حسب العادة المتبعة في تلك الأيام في إطلاق الأسماء والألقاب.'),
(1466, 'ذبَاب - أو ذُبان - جمع ذبابة', 'وهي حشرة صغيرة طائرة تعيش في بلدان الشرق وفي البلاد الحارة بكثرة، وتوجد أنواع كثيرة منها تنتسب إلى الجنس الذي يسنى باللاتينية Diptera. بعضها مزعج فقط وبعضها الآخر ضار للغاية، وقد كان الذباب الضربة الرابعة على مصر ايام موسى (خر 8: 21). وفي لغة اشعياء يُظهر لنا الذباب كنوع من التأديب والقضاء الالهي (اش 7: 18). والكلمة العبرانية للذباب هي ((زبوب)) وهذا يوضح معنى بعل زبوب، أي رب الذباب، الذي يحمي من هذا الوباء، وقد كان هذا إله العقرونيين حسب ما جاء في 2 مل 1: 2 - 16 واشتهر بقدرته على كشف المستقبل. (انظر ((بعازبوب)) ).'),
(1467, 'ذَبِيحة', 'ترجع عادة تقديم الذبائح لله إلى فجر التاريخ البشري، فنجد في العهد القديم إشارات إلى مثل هذه الذبائح كطريقة للتقدم إلى الله (تك 8: 20 وخر 10: 25 ولا 4: 23 و 5: 7 و 17 : 11)، وقد وجدت طقوس متنوعة بخصوص الذبائح في بابل ومصر وسغيرهما قبل أيام موسى بكثير.أما المسيحيون الآن ففي غنى عن هذه الذبائح لأن المسيح رُفع على الصليب ذبيحة طاهرة كاملة لأجلهم (عب 13: 11 و 12) إلا انهم يقدمون ذبائح من صنف آخر تدل على شركتهم في المسيح، كالتسبيح وفعل الخير والتوزيع (عب 13: 15 و 16) (انظر ((قربان)) ).'),
(1468, 'مذبح', 'ويعني مكان مرتفع تقدم عليه الذبيحة او التقدمة او البخور اثناء العبادة، وقد وردت في العهد القديم اكثر من اربعمائة اشارة الى المذابح، الا ان اول اشارة جاءت عند خروج نوح من الفلك تك 8: 20 مع ان الذبائح كانت معروفة فبل الطوفان.وكان القدماء يعيرون المذابح اهتماماً عظيما ويعدونها من المستلزمات الضرورية للعبادة، فبنوها على اشكال تختلف باختلاف الامم التي كانت تقيمها، فمنهم من كان يبني المذبح مستديرا وغيرهم مربعاً. وعلى الغالب فان المذابح كانت تكرس لبعض الآلهة وتسمى بأسمائها. وبعضها لم يكن له اسم اطلاقاً، كما في اثينا فهناك المذبح (( لاله مجهول)). وكان الرومانيون كثيراً ما يزينون مذابحم بالاكاليل والازهار ويحفرون على جوانبها تماثيل الآلهة التي كانت الهياكل مكرسة لها.وكان القصد من بناء المذابح الاستغاثة بالله او تقديم الشكر له(1)	مذبح المحرقات او المذبح النحاسي:ونجد وقت عبادة بني اسرائيل في البرية'),
(1469, 'ذراع', '(انظر مكيال).'),
(1470, 'مذراة', 'وردت في اش 30: 24 ومت 12:3 وهي آلة زراعية تسميها العامة ((مذراية)) وهي ذات اصابع يُرمى بها خليط التين والحنطة في الهواء فيطير التبن الى جانب وتقع الحنطة وحدها على الارض، والرفش والمنسف الآت الزراعية اخرى تستعمل في تنقية القمح (انظر ((انظر درس)) ).'),
(1471, 'ذكري', 'اسم عبري معناه ((الذي يذكر)) وهو لاوي من عشيرة قهات من بيت يسهار (خروج 21:6) والاسم في الاصل هو نفس ذكري.'),
(1472, 'ذهب', 'من اثقل واثمن المعادن، ويقبل التطريق اكثر من غيره.كان يستورد قديماً من ارض الحويلة ( تك 11:2و12) .'),
(1473, 'ذوذهب', 'اسم عبري معناه ((من لديه ذهب)) وهو اسم مكان على مقربة منه كلم موسى بني اسرائيل بشأن معاملة الرب لهم (تث 1:1).'),
(1474, 'مذهبة', 'ترجمة لكلمة عبرية وهي كلمة غامضة المعنى.\nوردت في عناوين ستة من مزامير داود'),
(1475, 'رَآيا', 'اسم عبري معناه (( يهوه رأى )) أو (( يهوه اعتنى )).( 1 ) ابن شوبال وأحد أحفاد يهوذا من حصرون ( 1 أخبار 4: 2 ) ويدعى هراوه في 1 أخبار 2: 52.( 2 ) ابن ميخا من سبط رأوبين ( 1 أخبار 5: 5 ).( 3 ) اسم رجل رجع بنوه مع زربابل ( عز 2: 47 ونح 7: 50 ).'),
(1476, 'رَابَع', 'اسم مدياني معناه (( الرابع )) أحد ملوك مديان الخمسة الذين قتلهم بنو إسرائيل في الحرب التي شنّها موسى ضد المديانيين ( عد 31: 8 ويش 13: 21 ).'),
(1477, 'رَاحاب', 'اسم عبري معناه (( رحب )) أو (( متسع )). هي امرأة زانية من أريحا ( يش 2: 1 ) أضافت الجاسوسين اللذين ارسلهما يشوع ليتجسسا المدينة، وخبأتها لدى البحث عنهما، وأخيراً أنزلتهما بحبل من الكوة إذ كان بيتها ملاصقاً لسور المدينة. وبهذه الطريقة أنقذتهما فعادا سالمين إلى محلة العبرانيين وقبل أن أطلقتهما قطعت عليهما عهداً ليتوسطا في إنقاذ حياتها وكل بيت أبيها إذا ما دخل العبرانيون المدينة وخربوها، وأعطياها علامة أن تربط حبلاً من خيوط القرمز في الكوة التي أنزلتهما منها ( يش 2: 1024 ). \nوعندما أخذ يشوع أريحا نجت راحاب مع كل بيتها فسكنوا جميعاً في وسط بني إسرائيل ( يش 6: 17-25 وعب 11: 31 ويع 2: 25 ).وهي التي تزوجت سلمون من سبط يهوذا فصارت ضمن سلسلة نسب الملك داود وبالتالي ضمن سلسلة نسب الرب يسوع ( مت 1: 5 ).'),
(1478, 'رَاحيل', 'اسم عبري معناه (( شاة )) ( تك 29: 6 ). ابنة لابان الصغرى وكانت حسنة المنظر فأحبها يعقوب للنظرة الأولى عندما رآها عند البئر بالقرب من حاران إذ كانت تسقي غنم أبيها لابان. وقد خدمه يعقوب سبع سنين لأجل راحيل فخدعه لابان وأعطاه ليئة. ثم خدمة يعقوب لأجل راحيل سبع سنين أخرى. وراحيل هي أم يوسف وبنيامين وماتت عند ولادة بنيامين ( تك 29: 1-30 و 30: 22-25 و 35: 16-20 ) وهي التي أخفت أصنام أبيها عند ارتحال يعقوب إلى كنعان ( تك 31: 32-35 ) ( انظر (( يعقوب ))  ).\nوقد تحدث ارميا ( 31: 15 ) عن حزن راحل جدة سبطي افرايم ومنسى، وكيف أنها صارت (( تبكي على أولادها وتأبى أن تتعزى عنهم لأنهم ليسوا بموجودين )). ويظن أن هذه النبوة تمت مرتين، الأولى عندما سبي السبطان المذكوران إلى ما وراء الفرات ( ار 40: 1 )، والثانية عندما قتل هيرودس أطفال بيت لحم مؤملاً أن يتقل ضمنهم الطفل يسوع ( مت 2: 18 ).وقد ماتت راحيل ودفنت في (( طريق افراته )) أو بيت لحم. ولا يزال ضريحها قائماً على بعد ميل شمالي بيت لحم ويعرف ذلك المزار بقبة راحيل ( انظر (( الرامة ))  ).'),
(1479, 'راخال أو راكال', 'اسم عبري معناه (( تجارة )) مدينة في يهوذا، لا يعرف موقعها الآن، أرسل إليها داود قسماً من غنيمته ( 1صم 30: 29 ).'),
(1480, 'رأس شهر ورؤوس شهور', '( عد 10: 10 و28: 11 و 2مل 4: 23 وحز 46: 1 و 3 و 6 ) ( انظر (( قمر ))، (( مواسم ))، (( شهر ))، (( هلال ))  ).'),
(1481, 'رئيس الربع', '( مت 14: 1 ) لقب كان يطلق على من يحكم ربع مملكة. وفي الكتاب المقدس يقصد به كل من كان متولياً على مقاطعة في الإمبراطورية الرومانية كبيرة كانت أم صغيرة. وقد ذكر العهد الجديد ثلاث من هؤلاء الحكام وهم:( 1 ) هيرودس رئيس ربع على الجليل.( 2 ) فيلبس رئيس ربع على ايطورية وتراخونيتس.( 3 ) ليسانيوس رئيس ربع على الابلية ( لو 3: 1 ).ومن باب التعظيم كان رئيس الربع يدعى ملكاً في بعض الأحيان ( مت 14: 1 و 9 ومر 6: 14 ).'),
(1482, 'رئيس المجوس أو راب ماج', 'هذا لقب اكادي (( راب موجي )) ومعناه (( الرئيس الكبير )) ( ار 39: 3 و 13 ). وهي وظيفة في البلاط البابلي شغلها نرجل شراصر.'),
(1483, 'رِياسة', 'يراد بقوله (( رياسة وسلطان )) ( اف 1: 21 وكو 1: 16 و 2: 10 ) طغمة من الملائكة.'),
(1484, 'رِاعوث', 'اسم موآبي ربما كان معناه (( جميلة )) وهي فتاة موآبية تزوجت أولاً بمحلون بن اليمالك من سبط يهوذا. ولما مات زوجها لصقت بحماتها نعمي ورافقتها إلى بيت لحم تاركة شعبها وبيت أبيها في موآب. فكافأها الرب على صنيعها إذ وجدت نعمة في عيني بوعز الذي تزوجها. وبهذا صارت ضمن سلسلة نسب داود والمسيح.سفر راعوث:هو ثامن سفر في العهد القديم، وسمي بهذا الاسم نسبة إلى راعوث بطلة الرواية فيه. يوضع هذا السفر في العهد القديم باللغة العبرية ضمن الأسفار التي يسمونها (( مجلّوث )) أو الأدراج وهذه تقع في القسم الثالث من الأسفار المقدسة الذي يسمى (( كتوبيم )) أو الكتب، ويقرأ اليهود هذا السفر في عيد الخمسين أو عيد الحصاد.ولا يمكن الجزم بالوقت الذي دون فيه هذا السفر أو بتحديد شخصية كاتبه. لكن نظراً لن داود ذكر في ختامه فيستنتج أنه لا يمكن أن يكون قد كتب قبل أيام داود. ولهذا نسب بعضهم كتابته إلى صموئيل، وآخرون إلى حزقيا، وآخرون إلى عزرا.أما الوقت الذي تمت فيه هذه الرواية فلا يمكن الجزم به أيضاً، وعلى أي حال فما ورد في الإصحاح الأول والآية الأولى يفهم أنها تمت (( في أيام حكم القضاة )) ربما قبل ولادة داود بستين عاماً أو أكثر قليلاً ( ص 4: 21 و 22 ).وتلخص الرواية في أنه عندما حدث جوع في أرض العبرانيين ذهب رجل اسمه اليمالك إلة وآب واستوطن فيها مؤقتاً ثم مات هناك تاركاً أرملته نعمي وابنين تزوجا بفتاتين موآبيتين. وإذ مات الابنان وسمعت نعمي أن المجاعة في بلادها قد انتهت عزمت على العودة إليها، والحت على كنتيها بالعودة كل إلى بيت أبيها. فقبلت احداهما وهي عرفة، أما الثانية وهي راعوث فأظهرت ولاءً كاملاً لحماتها ولم تشأ أن تفارقها. وإذ وصلت إلى بيت لحم هي وحماتها نعمي كانتا في أشد حالات الفقر فخرجت راعوث إلى الحقول لتلتقط ما يتبقى وراء الحصادين. ودبر الرب أن تلتقط في حقل بوعز وهو نسيب غني لحميها اليمالك. وأخيراً اقترن بها ورزق منها بعوبيد ابي يسمى أبي داود.\nولقد كان قبول راعوث في كنيسة العهد القديم رمزاً غلة قبول الأمم في ملكوت الله وخلاص الإنجيل للشعوب.\nأما محتويات السفر فهي كالآتي:( 1 ) أسرة اليمالك في بلاد موآب 1: 1-5.( 2 ) نعمي وراعوث تذهبان إلى بيت لحم 1: 6-22.( 3 ) راعوث تلتقط الحب في حقل بوعز ص 2.( 4 ) بوعز وراعوث في البيدر ص 3.( 5 ) بوعز يتزوج راعوث، ونسلهما ص 4.'),
(1485, 'رَافا', 'اسم عبري معناه (( هو شفى أي الله شفى )) رجل من نسل بنيامين ( 1 أخبار 8: 2 ).'),
(1486, 'رافائيل', 'اسم عبري معناه (( الله قد شفى )) وهو اسم احد الملائكة السبعة الواقفين أمام الرب ( طوبيا 12: 15 ) ويذكر سفر طوبيا أن هذا الملاك يلازم طوبيا في ارتحاله.'),
(1487, 'رَافة', 'اسم عبري معناه (( شُفي )) وهو أبو فلطي الجاسوس المنتخب من سبط بنيامين ( عد 13: 13-19 ).'),
(1488, 'رَاقم', 'اسم عبري معناه (( رقش، تشكيل، تلوين )).( 1 ) مدينة لسبط بنيامين وربما كان موقعها الآن قلندية شمالي القدس وغربي الرام ( يش 18: 27 ).( 2 ) رجل من نسل منسى ( 1 أخبار 7: 16 ).( 3 ) أحد ملوك مديان الذين قتلهم بنو إسرائيل ( عد 31: 8 ويش 13: 21 ).( 4 ) رجل من نسل يهوذا من بني حبرون ( 1 أخبار 2: 43 ).'),
(1489, 'رِئم', '( عد 23: 22 و 24: 8 وتث 14: 5 و 33: 17 ) حيوان يرّجح بأنه هو (( الاوروخس )) وهو نوع من الثور وجد قديماً غير أنه انقرض من العالم وله قوة هائلة ( عد 23: 21 و 24: 8 ) ولا يمكن احناء عنقهللنير أو تسخيره لخدمة الإنسان في الأعمال الزراعية ( اي 39: 9-12 ).\nوالكلمة العبرانية المترجمة هنا بالرئم ترجمت في اي 39: 9 و 10 بالثور الوحشي، وفي مز 22: 21 و 29: 6 واش 34: 7 بالبقر الوحشي.'),
(1490, 'رام', 'اسم عبري معناه (( مرتفع، سام )).( 1 ) رجل من نسل يهوذا ومن أولاد حصرون ( 1 أخبار 2: 9 و 10 ) ويدعى أرام ( مت 1: 3 و 4 ولو 3: 33 ).( 2 ) رجل من نسل يهوذا ومن أولاد يرحمئيل ( 1 أخبار 2: 25 و 27 ).( 3 ) أحد أحفاد بوز، وجد اليهو ( اي 32: 2 ) ويزعم البعض أنه هو ارام الوراد ذكره في تك 22: 21 لكن لا أساس لهذا الزعم لأن ارام ليس من نسل بوز، ثم أن كلمتي رام وارام تختلفان في الأصل العبري.'),
(1491, 'راماتيم صوفيم', 'اسم عبري معناه (( مرتفعنا الصوفيين )) ((  1 صم 1: 1 ) 0انظر (( الرامة )) ( 2 ).'),
(1493, 'رامة الجنوب', 'مدينة في التخم الجنوبي لسبط شمعون ( يش 19: 8 ) وتسمى أيضاً (( راموت الجنوب )) ( 1 صم 30: 27 ) ( انظر (( الرامة )) ( 6 ).'),
(1494, 'رامة المصفاة', 'اسم عبري معناه (( مرتفعة برج المراقبة )) قرية على تخم سبط جاد ( يش 13: 26 ) وربما كانت هي نفس راموت جلعاد.'),
(1495, 'راموث', '( 1 صم 30: 27 ) انظر (( الرامة )) (( وراموث الجنوب )) و (( رَمة )).'),
(1496, 'راموت جلعاد', 'اسم عبري معناه (( مرتفعات جلعاد )) هذه كانت مدينة للاموريين ثم صارت للجاديين. وهي من أشهر مدنهم وموقعها شرقي الأردن. وقد أعطيت للاويين وعينت مدينة للملجأ ( تث 4: 43 ويش 20: 8 ). وتدعى أيضاً الرامة ( 2 أخبار 22: 6 ). وكانت مركز أحد الوكلاء الذين أقامهم سليمان على شؤون التموين 01 مل 4: 13 ).وبعد ذلك استولى عليها الاراميون فتحالف اخاب ويهوشافاط لإرجاعها، غير أن اخاب جرح جرحاً بليغاً ومات في الموقعة ( 1 مل 22: 2-36 و 2 أخبار ص 18 ).ومما يذكر عنها أيضاً أن يورام ( يهورام ) جرح فيها بعد استرجاعها من الاراميين. ومُسح فيها أيضاً ياهو القائد ملكاً على إسرائيل بأمر اليشع ( 2 مل 8: 28 و 2 أخبار 22: 5 و 6 ). ويرّجح أنها تل راميث الحالية.'),
(1497, 'راموت الجنوب', 'انظر (( رامة الجنوب )).'),
(1499, 'الراميّ', 'رجل ينسب إلى الرامة ( 1 أخبار 27: 27 ).'),
(1500, 'رأوبين', 'اسم عبري معناه (( هوذا ابن )) ( تك 29: 32 ). هو بكر يعقوب ولدته له ليئة، وكان نسله قليلاً وضعيفاً. عندما تآمر أخوته لقتل يوسف تقدم هو باقتراح أن يلقى أخوه في البئر آملاً أن يرده إلى أبيه حيّاً. ولم يكن معهم عندما باعوا يوسف إلى الإسماعيليين فاغتاظ جداً عندما رجع إلى البئر وإذا بيوسف ليس فيه ( تك 37: 21-29 ). وعندما وجد رأوبين أخوته أنفسهم في ضيقة شديدة في مصر بعد عشرين عاماً أسرع فذكرهم أنه لم يشترك معهم في المؤامرة التي قصدوا بها قتل يوسف ( تك 42: 22-24 ).وعندما تلكأ يعقوب في إرسال بنيامين إلى مصر عرض رأوبين على أبيه اثنين من أولاده كرهينة إن لم يردّ بنيامين إليه ( تك 42: 37 ).وقد كان لرأوبين أربعة أولاد هم: حنوك وفلّو وحصرون وكرمي ( تك 46: 8 ). وعندما كان يعقوب على فراش الموت أعلن بأن رأوبين سوف يكون فائراً ( غير ثابت ) كالماء. وأنه لا يتفضل أي ( لا تكون له الرئاسة ). وبسب خطيئته الشنيعة-التي بها دنس فراش أبيه-خسر امتياز البكورية ( تك 35: 22 و 49: 3 و 4 ).سبط رأوبين:أحد أسباط إسرائيل الاثني عشر، وكان مكوناً من نسل رأبين. وكان ينقسم إلى أربع عشائر كبرى تنتسب إلى أولاد رأوبين الأربعة ( عد 26: 5-11 ). كان عدد رجال الحرب ( من ابن عشرين سنة فصاعداً ) من السبط في الإحصاء الأول 500ر46 ( عد 1: 20 و 21 )، وفي الإحصاء الثاني ( بعد 38 سنة ) 730ر43.وقد كان داثان وابيرام واون الذين اشتركوا في فتنة قورح رأوبينيين ( عد 16: 1-50 ). وبعد انتهاء الحرب مع سيحون وعوج طلب سبط جاد وسبط رأوبين ونصف من سبط منسى أن يكون نصيبهم في امتلاك الأرض في شرقي الأردن لأن مواشيهم كانت كثيرة وكانت الأرض صالحة لرعاية المواشي. فأجابهم موسى إلى طلبهم على شرط أن يساعدوا أخوتهم باقي الأسباط في افتتاح أرض كنعان ( عد 32 ). وبعد أن عادوا إلى نصيبهم في شرقي الأردن أقاموا مذبحاً للدلالة على العهد الذي قطع بينهم وبين بقية أسباط إسرائيل ( يش 22: 1-34 ) وفي أيام شاول حارب الرأوبينييون الهاجريين وغلبوهم وسكنوا مكانهم ( 1 أخبار 5: 10 و 18 و 22 ). وبما أن نصيب رأوبين كان شرقي الأردن فقد صاروا هم والجاديون وال المسبيين إلى بابل ( 1 أخبار 5: 26 ).نصيب رأوبين:كان نصيب رأوبين يقع شرقي الأردن والبحر الميت. وكان حده الجنوبي وادي ارنون ( وهو وادي موجب الآن ) أما الحد الشمالي فكان إلى الشمال من وادي حسبان ويصل إلى حدود جاد وكان حدّه الغربي الأردن. أما إلى الشرق فلم تكن هناك حدود معينة بل كان يمتد إلى البرية ( يش 13: 15-21 قابله مع عدد ص 32: 37 و 38 ). وكانت هذه الرض قبلاً للموآبيين الذين طردهم العبرانيون ( عد ص 21: 24 وتث 3: 16 و 17 ويش 13: 15-23 ). وكان هذا النصيب ينقسم على ثلاثة أقسام ( 1 ) الغور ( 2 ) جبال موآب وجلعاد ( 3 ) سهل البلقاء. وكان فيه أربع عشرة مدينة مهمة ما عدا مدن العربة. وكانت الأرض الواقعة في نصيب رأوبين صالحة كمراعي للمواشي. وكان من ضمن المدن المهمة هناك ميدبا وحشبون وديبون وباموت بعل وبيت بعل وبيت فغور وباصر ويهصة وقديموت وميفعة.وقد أخذ الموأبيون الكثير من مدن الروبينيين ويظهر هذا من أسماء المدن الموآبية المذكورة في أشعياء في إصحاحي 15و 16 وفي ارميا ص 48 وفي (( الحجر الموآبي )).'),
(1501, 'رَأوبينيون', 'ذرية رأوبين ( عد 26: 7 يش 1: 12 ).'),
(1502, 'رَؤومة', 'اسم عبري معناه (( مرتفع )) وهي سرية ناحوم أخي إبراهيم ( تك 22: 24 ).'),
(1503, 'رَاءِ', '( 1 صم 9: 9 ) انظر (( نبيّ )).'),
(1504, 'رُؤيا', '( وجمعها رؤى ) تستعمل لفظة (( رؤيا )) في الكتاب المقدس لمعنيين:( 1 ) الحلم في المنام ( اي 33: 15 واش 29: 7 ).( 2 ) الاعلان ( مز 89: 19 وام 29: 18 واش 1: 1 ).والوقع أنها معنى واحد لأن الله يستخدم كليهما لإعلان إرادته وحكمه وذلك عن طريق أشخاص أتقياء تقدست حياتهم وصفت من ادناس العالم.وقد حذر الكتاب المقدس من الرؤى المزيفة التي يدعيها الأشرار ( ار 23: 16 و 21 و 22 و 27 ).والواقع أنهما معنى واحد لأن الله يستخدم كليهما لإعلان إرادته وحكمه وذلك عن طريق أشخاص أتقياء تقدست حياتهم وصفت من أدناس العالم.وقد حذر الكتاب المقدس من الرؤى المزيفة التي يدعيها الأشرار ( ار 23: 16 و 21 و 22 و27 ).'),
(1505, 'رؤيا يوحنا', '( وتسمى إعلاناً رؤ 1 : 1 ) هي السفر الأخير من العهد الجديد. ويتضمن هذا السفر, حسب تعبير كاتبه, (( إعلان يسوع المسيح الذي أعطاه إياه الله ليرى عبيده ما لابد أن يكون عن قريب )). وقد أرسل المسيح هذا الإعلان لعبده يوحنا بيد ملاكه لينقله هو بدوره إلى الكنيسة ويشهد بكل ما رآه ( رؤ 1 : 1 و 2 ).وقد وجّه الحديث إلى سبع كنائس في آسيا ( رؤ 1: 4 و 11 ). وإذ يشير العدد 7 في الكتاب المقدس إلى الكمال, فلعل القصد من ذلك أن السفر يوجه إلى كل الكنيسة. أما الغاية الرئيسية من السفر فهي تعزية الكنيسة وتحذيرها وسط صراع العالم وإعدادها لمجيء الرب الثاني ( ص 1 : 7 و 8 و 22 : 7 و 10 و 17 و 20 ).ولدى التأمل في السفر يتضح أنه بعد المقدمة ( ص 1 : 1 - 3 ) والتحية ( ص 1 : 4 - 8 ) ينقسم إلى سبعة أقسام رئيسية تنتهي في ص 22 : 7 يعقبها الخاتمة ( 22 : 8 - 21 ). وكل من هذه الأقسام يشمل رؤيا مستقلة أو سلسلة رؤى, وينقسم إلى سبعة أقسام فرعية.أما السبعة أقسام فهي كما يلي:( 1 ) رؤيا المسيح الممجد وسط كنيسته، ويتبعها سبع رسائل إلى السبع كنائس التي في آسيا ( رؤ 1: 9-3: 22 ). والغاية هنا لتعليم الكنيسة في حالتها الحاضرة وتحذيرها وتشجيعها.( 2 ) رؤيا الله يسيطر على مصير المسكونة مسبحاً من كل الخليقة، ورؤيا حمل الله بيده السفر المختوم بسبعة ختوم والمتضمن الأوامر الإلهية ( ص 4 و 5 )، ويتبع ذلك فتح الختوم في سبع رؤى تعلن قصد الله من خروج المسيح ليغلب إلى يوم الدينونة العظيمة ( ص 6-8: 1 ). وبين الختم السادس والختم السابع نجد رؤيا تبين سلامة شعب الله وسط الضيقة العظيمة التي تحل بالعالم ( ص 7 ).( 3 ) رؤيا السبعة ملائكة الذين أعطوا سبعة أبواق ( ص 8 : 2 - 11 : 19 ) وتبدأ برؤيا ملاك يقدم لله صلوات القديسين ( ص 8 : 2 - 6 ) ويتبع كل بوق رؤيا خراب يحل بالعالم الشرير, وينتهي الكل بالدينونة الأخيرة. وبين البوق السادس والبوق السابع تتوسط رؤيا أخرى أيضاً تعلن حفظ الكنيسة الشاهدة ( ص 10 : 1 - 11 : 14 ).( 4 ) رؤيا الكنيسة ترمز إليها بامرأة تلد المسيح ويشهر عليها التنين ( أي الشيطان ) حرباً ( ص 12 ) ويتبع ذلك رؤى الوحشين اللذين سيستخدمهما الشيطان لمعاونته ( ص 13 ), ورؤيا الكنيسة المجاهدة ( ص 14 : 1 - 5 ) ورؤيا الخطوات المضطردة لنصرة المسيح ( الأعداد 6 - 20 ).( 5 ) رؤيا الجامات المحتوية الضربات الأخيرة ( ص 15 و 16 ) وتمثل الرؤيا الأولى نصرة القديسين, أما السبعة الجامات فتمثل ضربات الله السبع على العالم الشرير ( ص 16 ).( 6 ) رؤيا المدينة الزانية, أي بابل ( ص 17 ) ويتبعها نصرة المسيح عليها وعلى أعدائه المتحالفين معها, وتختم أيضاً بالدينونة الأخيرة ( ص 18 و 19 و 20 ).( 7 ) رؤيا الكنيسة المثالية عروس المسيح, أو أورشليم الجديدة ( ص 21 : 1 - 8 ) ويتبعها وصف لأمجادها ( ص 21 : 9 - 22 : 7 ).والواضح من السفر أن كاتبه اسمه يوحنا ( ص 1 : 1 و 4 و9 و 22 : 8 ). وبالرغم مما زعمه بعض الكتاب الأوائل أنه ليس هو يوحنا الإنجيلي إلا أن الكنيسة تكاد تجمع بأنه هو, مستندة في هذا إلى أدلة خارجية وداخلية سيما إلى شهادات يوستينوس الشهيد وبابياس اللذين عاشا في بداية القرن الثاني وإيرينيوس وترتوليانس وإكليمندس الإسكندري وأوريجانوس.وقد كتب السفر في جزيرة بطمس إحدى جزر بحر اليونان, وهي تبعد نحو 24 ميلاً عن شاطيء آسيا الصغرى, وكان ذلك نحو سنة 95 م قرب نهاية حكم دومتيانس الذي نفى عدداً من المسيحيين إلى أقاليم بعيدة.'),
(1506, 'مِرآة', '( اي 37 : 18 و 1 كو 13 : 12 و 2 كو 3 : 18 ويع 1 : 23 ) يراد بها صفيحة من المعدن كانت تصقل جيداً حتى تصير صالحة لانعكاس النور عنها. وكان المصريون قديماً والفينيقيون وسائر الأمم القديمة يصنعون المرايا من النحاس أو من مزيج المعادن. وكانت تصنع إما مستديرة أو بيضاوية أو مربعة. وكثيراً ما كانوا يثبتون فيها يداً ليسهل حملها واستعمالها.مَرائي:جمع مرآة ( خر 38 : 8. انظر (( مرآة )) ).'),
(1507, 'رَب', 'يقصد بهذا اللفظ:( 1 ) اسم الجلالة, وفي هذه الحالة تطلق على الآب والابن بدون تمييز بينهما ( اع 10 : 36 و رؤ 19 : 16 ) سيما في رسائل بولس الرسول.( 2 ) وقد تستعمل بمعنى السيد أو مولى دلالة على الاعتبار و الإكرام.يوم الرب:ورد هذا التعبير مرة واحدة في العهد الجديد ويقصد به اليوم الذي تقام فيه العبادة ( رؤ 1 : 10 ), وورد أيضاً في 2 بطرس 3 : 10 لكن بمعنى آخر يقصد به (( يوم مجيء الرب )).\nكان يوم الراحة والعبادة في العهد القديم هو يوم السبت لكنه استبدل بيم الأحد في العهد الجديد منذ العصر الرسولي نظراً لأن المسيح قام فيه من بين الأموات. وزاده الرب إكراماً إذ ظهر فيه مرة أخرى لجميع تلاميذه لما كانوا مجتمعين معاً ( يو 20 : 26 ). يضاف إلى هذا أن الروح القدس حل على التلاميذ يوم الأحد.وقد حرص التلاميذ على الاجتماع معاً للعبادة في يوم الأحد ( اليوم الأول من الأسبوع ), ففي اع 20 : 7 نرى مسيحيي ترواس يجتمعون معاً في أول الأسبوع ليكسروا خبزاً في عصر الرسول بولس. وفي 1 كو 16 : 2 نرى مسيحيي كورنثوس يضع كل واحد عنده في كل أول أسبوع ما تيسر, أي ما قصد أن يقدمه لفقراء أورشليم حتى إذا وصلهم بولس لا يكون حينئذ جمع.ويبدو أن المتنصرين من اليهود كانوا في البداية يحفظون السبت والأحد عل أساس أنهم كانوا لا يزالون متمسكين ببعض وصايا الناموس وطقوسه, بينما كان المتنصرون من الأمم لا يحفظون إلا الأحد. ومع توالي الأيام صار الجميع يحفظون يوم الأحد فقط.عشاء الرب:ورد هذا التعبير مرة واحدة في 1 كو 11 : 20 ويقصد به الفريضة التي أسسها الرب يسوع ليلة آلامه قبيل ذهابه إلى بستان جشيماني حيث أسلم ليصلب . وتسمى هذه الفريضة بالافخارستيا وهي كلمة يونانية معناها (( شكراً )) أو الشكر, أو الشركة, أو شركة جسد الرب ودمه, أو العشاء الرباني, أو مائدة الرب, أو وليمة.ويعتقد البعض أن المخلص أراد أن يهيئ العقول لقبول هذه الفريضة قبل تأسيسها بوقت طويل. وذلك في حديثه الوارد في الإصحاح السادس من إنجيل يوحنا عن خبز الحياة. ولما حان الوقت أسسها كما يحدثنا الإنجيليون الثلاثة الأولون والقديس بولس الرسول.وقد رسم الرب يسوع نظام هذه الفريضة قبل الصلب فإنه بعد أن تناول عشاء الفصح أخذ الخبز وباركه وقدم الشكر لأجله وأعطاه للتلاميذ قائلاً: (( هذا هو جسدي المكسور لأجلكم اصنعوا هذا لذكري )) وكذلك الكأس أيضاً بعد العشاء قائلاً: (( هذه الكأس هي العهد الجديد بدمي الذي يسفك عن كثيرين لمغفرة الخطايا )) ( مت 26: 28 ) أما الغرض الذي لأجله رسمت هذه الفريضة فهو لنذكر الرب يسوع ( لو 22: 19 ) ولنخبر بموته مظهرين إياه إلى أن يجيء ( 1 كو 11: 25 و 26 ). ولم يكن العشاء مقصوراً على الرسل أو على اليهود من المسيحيين ولكنه كان يمارس في كنائس المسيحيين من الأمم أيضاً ككورنثوس مثلاً ( 1 كو 10: 15-21 ). وقد أدركت الكنيسة أن سيكون هذا العشاء امتيازاً لها تحظى بممارسته في كل العصور والأزمان. وقد دعيت المائدة التي يوضع عليها الخبز (( مائدة الرب )) ( 1 كو 10: 21 ).\nوالكأس التي تقدم احتفظت بالاسم القديم الذي كانت تسمى به في الفصح اليهودي وهو (( كأس البركة )) ( 1 كو 10: 16 ). وقد دعيت أيضاً (( كأس الرب )) ( 1 كو 10: 21 و 11: 27 ).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1508, 'رب الجنود', 'هذه العبارة ترجمة للعبارة العبرية (( يهوه صباؤُوث )). ويعني هذا التعبير أن الرب رئيس قوات العبرانيين ( 1 صم 17: 45 و اش 31: 4 ) أما إذا نظرنا إلى التعبير في معناه الأوسع مدى من هذا، نجده يعني أن الله يضبط جيوش الأمم، والمجاعات والأوبئة ( ار 29: 17 ) والشمس والقمر والنجوم ( اش 40: 26 و 45: 12 وار 31: 35 )، وكل قوات الطبيعة ( نحم 9: 6 ) والملائكة ( مز 89: 6-8 ). وقد وردت ترجمة هذه العبارة في العهد القديم باللغة اليونانية بلفظ (( بنتكراتور )) أي (( الحاكم على الجميع )).'),
(1509, 'ربابة، رباب', 'وردت هذه الكلمة في بعض الترجمات العربية ترجمة للفظ العبري (( نبل )). أما بعض الترجمات العربية الأخرى فقد ترجمت اللفظ العبري (( نبل )) بكلمة (( عود )) وكان يصنع جسم هذه الآلة الموسيقية من الخشب ( 2 صم 6: 5 ). وقد ارتأى بعضهم بأن جسم الآلة الخشبي الذي يردد النغم كان مصنوعاً على شكل زق من الجلد. وهذه الآلة من الآلات المعروفة بالوترية. وكان لأحد أنواع هذه الآلة عشرة أوتار ( مز 33: 2 ). وكان نغم هذه الآلة مرتفعاً وهو ما يسمونه (( السيرانو )) ( 1 أخبار 15: 20 ). ويقول يوسيفوس أنه كان لهذه الآلة الموسيقية عادة اثنا عشر وتراً وكان النغم يوقع عليها بالأصابع. ويقول اغسطينوس أن الأوتار كانت مشدودة بين جسم الآلة الذي يردد النغم والذراع المنحني المتصل بجسم الآلة. ومن الواضح أن هذا النوع من الآلات الموسيقية كان من بعض أنواع القيثار. وقد كان الأنبياء الذين التقى بهم شاول يوقعون على الرباب ( 1 صم 10: 5 ). وقد استخدم الرباب ضمن الآلات الموسيقية التي وقع عليها احتفاء بنقل تابوت العهد إلى أورشليم في زمن داود ( 2 صم 6: 5 ). وقد شمل داود الموقعين على الرباب ضمن الفرقة الموسيقية التي كانت توقع على الآلات الموسيقية في القدس في أورشليم ( 1 أخبار 15: 16 و 20 و 28 ). وقد ورد ذكرها كثيراً في المزامير كإحدى الآلات التي يوقع عليها التسبيح للرب والحمد له ( فمثلاً مز 57: 8 و 150: 3 ).'),
(1510, 'رَبّة', 'كلمة عبرية وعمّونية معناها (( كبيرة )) وهي:( 1 ) مدينة في جبال يهوذا ( يش 15: 60 ) والأرجح أنها كانت قريبة من أورشليم، ولعلها هي ربوتي الوارد ذكرها في ألواح تل العمارنة.( 2 ) (( ربة بني عمّون )) أو (( ربّة عمّون )) ( تث 3: 11 و 2صم 12: 26 وار 49: 2 وحز 21: 20 ). وهي مدينة تقع عند منبع يبّوق وتبعد عن الأردن بنحو 23 ميلاً شرقاً. وكانت عاصمة أرض بني عمّون وفيها مات اوريا عند محاصرة يوآب للمدينة ( 2 صم 11: 17 ). وبعد ذلك جمع داود جيوشه وذهب إلى المدينة وحاربها وأخذها ( 2 صم 12: 29 ) وقد انذر ارميا ( ص 49: 2-6 ) وحزقيال ( ص 21: 20 ) بأن القضاء سيقع على ربّة. وقد جملها بطليموس فلادلفوس ( سنة 285-246 ق.م ) فسميت فيلادلفيا تكريماً له.وكانت تقع في الحد الشرقي لإقليم بيرية وفي أقصى جنوب العشر المدن. وكان الطريق التجاري بين دمشق والجزيرة العربية التي يمر بها. واسمها الحديث عّمان وهي عاصمة شرق الأردن ( انظر (( عمّون ))  ).'),
(1511, 'ربّة موآب', 'انظر (( عار موآب )).'),
(1512, 'رَبّوني', 'كلمة آرامية معناها (( ربّي )) أو (( سيّدي )). وكان هذا اللقب يحمل اسمي عبارات التقدير والاحترام بين اليهود في مخاطبتهم معلماً دينياً. ويوحنا يترجمها بكلمة (( معلم )) ( يو 20: 16 ). وقد وردت نفس الكلمة في الأصل اليوناني في مرقس 10: 51 وقد ترجمت (( يا سيدي )).'),
(1513, 'ربَيّت', 'كلمة عبرية معناها (( جمهور )) وهي مدينة لبني يسّاكر على الحدود الجنوبية ( يش 19: 20 ) ومكانها اليوم قرية ربّا الحالية التي تبعد عن جنين بنحو سبعة أميال في اتجاه الجنوب الشرقي.'),
(1514, 'رَبْساريس', 'من اللقب الاكادي (( رب شا ريشو ))ومعناه (( الرئيس هو الرأس )).وهو لقب من ألقاب الدولة الآشورية. وقد انتدب سنحاريب ملك أشور رجلاً يحمل هذا اللقب لمرافقة جيشه الذي أرسله للهجوم على أورشليم ( 2 ملو 18: 17 وار 39: 3 ).ولعل رئيس خصيان الملك نبوخذ نصر ( دا 1: 3 ) كان يحمل نفس هذا اللقب.'),
(1515, 'رَبْشاقَى', 'من اللقب الاكادي (( رب شاقو )) ومعناه (( رئيس السقاة )) أو (( لواء )) وقد انتدب سنحاريب ملك أشور رجلاً يحمل هذا اللقب مع ربساريس وترتان لمرافقة جيشه الذي أوفده للهجوم على أورشليم ( 2 مل 18: 17 ) وهو الذي تولى مناقشة رجال حزقيا ( الاعداد 19 و 26 و 27 و 37 ) ولعله كان هو رئيس الحملة ( ص 19: 8 ).'),
(1516, 'ارابع', 'يراد بالقول أن بطرس كان مسلماً إلى أربعة أرابع من العسكر-أنه كان مسلماً إلى أربع جماعات، كل جماعة مكونة من أربعة عساكر فتكون الجملة ستة عشر جندياً. وكانت الجماعة تتناوب الحراسة كل ثلاث ساعات. وفي أثناء حراسة الليل كان جنديان ينامان مع بطرس في سجنه والاثنان الآخران يلبسان قدام الباب ( اع 12: 4 و 6 ).'),
(1517, 'رِبْلَة', 'اسم سامي ربما كان معناه (( جمهور )) أو (( كثرة )) وهي مدينة في أرض حماة ( 2 مل 23: 33 و 25: 21 ) كان المصريون مرابطين فيها عندما أُتي بيهواحاز أسيراً ( 2 مل 23: 33 ). وعندما ألقي القبض على صدقيا بعد هربه من أورشليم أُتي به إلى نبوخذ ناصر الذي كان في ربلة، وفي ربلة أيضاً قتل بنوه ورؤساء يهوذا ( 2 مل 25: 6 و 7 و21 وار 39: 5-7 و 52: 9-11 و 27 ).\nولا يعرف على وجه التحقيق إن كانت هي ذات (( ربلة )) التي ذكر عنها في عد 34: 11 إنها شرقي عين-آي العين الكبيرة التي يخرج منها نهر العاصي واسم البلد الحديث هرمل.'),
(1518, 'رِبا', '( مت 25: 27 ) الفائدة عن المال المقترض. كانت الشريعة الموسوية تنهي اليهود عن أخذ الربا من أخوتهم ( خر 22: 25 ولا 25: 35-37 ) وتسمح لهم بأخذه من الغرباء ( تث 23: 20 ).وكان الصيارفة يقرضون الأموال بربا زهيد ويقرضونها بربا فاحش فيربحون الفرق ( حز 22: 12 ).طالما ندّد الكتاب المقدس بالمرابين ( مز 15: 5 وام 28: 8 وحز 18: 8 ). وقد تراخى اليهود في حفظ الناموس بعد السبي ومن ضمنها الوصايا الخاصة بالربا فانتهرهم نحميا بشدة ( نح 5: 1-13 ).'),
(1519, 'رَتَم. رَتْمَة', 'نوع من الشيح اسمه اللاتيني Retama raetam وينمو في الصحاري وذكر في 1 مل 19: 5 أن إيليا نام تحت الرتمة. وقد يؤكل جذر الرتم وقت المجاعات ( 1 أخبار 30: 4 ). وقد يصنع فحم من جذوع الرتم وجذوره ( مز 120: 4 ).'),
(1520, 'رِثمة', 'اسم عبري معناه (( رتمة )) وهي محلة حط بنو إسرائيل فيها رحالهم في البرية ( عد 33: 18 و 19 ). ومن الرتم الذي ينمو في البرية. وربما كانت شمالي عين خضراء في شبه جزيرة سيناء.'),
(1521, 'رِجْس. رَجاسة', '( 1 ) ( تك 46: 34 ) يقال عن الشيء أنه رجس أي مكروه كحرفة الرعاة مثلاً فإنها كانت رجساً عند المصريين. ولم يكن بغض المصريين للرعاة يعزى لبغض الحرفة ذاتها بل لشدة بغضهم للاسم وذلك أن جماعة من الرعاة البدو المعروفين بالهكسوس غزوا مصر وأذاقوا المصريين مرّ العذاب أثناء حكمهم لها. وهذا ما جعل اسم الرعاة مكروهاً جداً عندهم.( 2 ) ( تث 23: 18 ) كانت بعض الحيوانات والأعمال المنهي عنها تعد رجساً حسب الناموس.( 3 ) ( ار 44: 4 و 2 مل 23: 13 ) كانت عبادة الأصنام على أشكالها وتنوعها تعد رجساً.رِجْسة الخراب. رِجْس المخرّب. جناح الارجاس:يراد في هذه العبارات في نبوات دانيال ( ص 9: 27 و 11: 31 و 12: 11 ) الإنذار بأن الأصنام ستقام في الهيكل في أورشليم. وقد رأى اليهود تحقيق النبوة الواردة في دانيال 11: 31 عندما أقيم هيكل للأوثان في الهيكل في أورشليم، وقد أقامه انتيخوس ابيفانيس في سنة 168 ق.م. وأمر بتقديم ذبيحة خنزير للإله زفس اولمبيوس فيه ( 1 مكا 1: 54 و 6: 7 و 2مكا 6: 2 ) وقد أنذر السيد المسيح بأنه متى رأى المؤمنون في اليهودية رجسة الخراب التي تكلم عنها دانيال قائمة في المكان المقدس أن يهربوا إلى الجبال. وعندما اقتربت الجيوش الرومانية بشارتها ورموزها الوثنية في سنة 70 ميلادية رأى المؤمنون المسيحيون في هذا تحذيراً لهم فهربوا إلى فحل في شرق الأردن قبل خراب أورشليم. وفي هذه النبوات انذر ضد العبادة الوثنية والخراب الذي تحدث نتيجة لممارستها.'),
(1522, 'رِجْسة الخراب. رِجْس المخرّب. جناح الارجاس', 'يراد في هذه العبارات في نبوات دانيال ( ص 9: 27 و 11: 31 و 12: 11 ) الإنذار بأن الأصنام ستقام في الهيكل في أورشليم. وقد رأى اليهود تحقيق النبوة الواردة في دانيال 11: 31 عندما أقيم هيكل للأوثان في الهيكل في أورشليم، وقد أقامه انتيخوس ابيفانيس في سنة 168 ق.م. وأمر بتقديم ذبيحة خنزير للإله زفس اولمبيوس فيه ( 1 مكا 1: 54 و 6: 7 و 2مكا 6: 2 ) وقد أنذر السيد المسيح بأنه متى رأى المؤمنون في اليهودية رجسة الخراب التي تكلم عنها دانيال قائمة في المكان المقدس أن يهربوا إلى الجبال. وعندما اقتربت الجيوش الرومانية بشارتها ورموزها الوثنية في سنة 70 ميلادية رأى المؤمنون المسيحيون في هذا تحذيراً لهم فهربوا إلى فحل في شرق الأردن قبل خراب أورشليم. وفي هذه النبوات انذر ضد العبادة الوثنية والخراب الذي تحدث نتيجة لممارستها.'),
(1523, 'رَجُل', 'يقصد بها زوج. وكانت الكلمة تطلق أيضاً على من كان خاطباً ولم يتزوج بعد لأن عهد الخطبة كان مقدساً لا يسوغ نقضه ( مت 1: 16 ). ولما كان (( الرجل رأس المرأة )) ( افسس 5: 23 ) ورب البيت فله الإكرام والإعتبار.'),
(1524, 'رِجْل. أَرْجُل', 'كانت الأحذية تخلع من الأرجل قبل أن يطأ المرء مكاناً مقدساً ( خر 3: 5 ويش 5: 15 ). وكانت تخلع أيضاً كعلامة للحزن ( 2صم 15: 30 وحز 24: 17 ).وكان الكهنة يخدمون في الهيكل وهم حفاة. ولا تزال هذه العادة مرعية في بعض الكنائس المسيحية الشرقية.وكانت العادة أنه إذا ما دخل الضيف بيت أحد الموسرين تقدم الخادم وخلع حذاءه وغسل قدميه ( 1 صم 25: 41 ومر 1: 7 ).واقتداءً بما صنع المسيح مع تلاميذه إذ غسل أقدامهم ( يو 13: 5 و 6 ) لا تزال الكنائس التقليدية وبعض الكنائس المصلحة تمارس طقس غسل الأرجل إذ يتقدم المطران أو كبير الكهنة أو القسيس فيغسل أرجل المؤمنين في الكنيسة ( انظر (( ثياب ))، (( غبار ))  ).'),
(1525, 'رَجَم', 'اسم عبري معناه (( صديق )) ابن يهداي من نسل يهوذا ( 1 أخبار 2: 47 ).'),
(1527, 'رَجَم مَلِك', 'اسم عبري معناه (( صديق الملك )) وهو رجل أرسله أهل بيت إِيل مع غيره ليسأل الكهنة سؤالاً عن الصوم ( زك 7: 2 ).'),
(1528, 'رُحامة', 'اسم عبري معناه (( رُحِمَتْ )) اسم مجازي أطلق على بني إسرائيل ( هو 2: 1 ) ثم أصبحوا فيما بعد يحيون بعضهم بعضاً.'),
(1529, 'رَحُبْعام', 'اسم عبري معناه (( اتسع الشعب )) ابن سليمان من نعمة العمونية ( 1 مل 14: 13 ) ومع أنه كان ابن رجل حكيم إلا أنه كان ضيق التفكير، فحالما مات سليمان حوالي سنة 931 ق.م. اجتمع في شكيم ممثلون للاثني عشر سبطاً ليجعلوه ملكاً إذ كان هو الوارث الشرعي. فطلبوا منه أن يخفف من النير الذي حمّلهم إياه أبوه. أما هو فقد أمهلهم ثلاثة أيام. وفي أثنائها طلب مشورة الشبان المتهورين-الأمر الذي أثار في الشعب روح الغضب والثورة وأدى إلى انقسام المملكة. وخرج عليه عشرة أسباط سميت باسم مملكة إسرائيل ولم يبق معه سوى سبطي يهوذا وبنيامين وقد سميا باسم مملكة يهوذا ( 1 مل 12 و 2 أخبار 10 )\nوفكر رحبعام أن يزحف علىّ العصاة بجيش عظيم ويخضعهم غير أنه امتنع بأمر إلهي ( 1 مل 12: 21-24 ). فأكتفى بتحصين بعض المدن في يهوذا وبنيامين وشدد الحصون ( 2 أخبار 11: 5-12 ). لكن الحرب قامت بينه وبين يربعام ملك مملكة إسرائيل فيما بعد واستمرت طويلاً ( 2 أخبار 12: 15 ).انتشرت لعبادة الوثنية في مملكة إسرائيل منذ بدايتها، وبعد ثلاث سنوات سارت ملكة يهوذا في ذات الطريق الذي سلكته إسرائيل ( 1 مل 14: 21-24 و 2 أخبار 11: 13-17 و 12: 1 ).وفي السنة الخامسة من ملك رحبعام صعد إليه شيشق ملك مصر وغزا مملكته واستولى على بعض المدن الحصينة وأخذ أورشليم ذاتها ونهب الهيكل والقصر الملكي ( 1 مل 14: 25-28 و 2 أخبار 12: 2-12 ).كان لرحبعام ثماني وعشرين زوجة وستون سرية، وأنجب منهن ثمانية وعشرين ابناً وستين ابنة ( 2 أخبار 11: 21 ).وقد ملك رحبعام سبع عشر سنة ومات حوالي سنة 915 ق.م. وخلفه ابنه ابيا ( 1 مل 14: 21 و 31 و 2 أخبار 12: 13 و 16 ).'),
(1530, 'رَحَبْينا', 'اسم عبري معناه (( الرب رحب أو وسع )) وهو رجل من نسل موسى ( 1 أخبار 23: 17 و 24: 21 و 26: 25 ).'),
(1531, 'مِرْحَضَة', '( خر 30: 18 ) إناء مستدير مصنوع من نحاس كان يستعمل في خيمة الشهادة ليغسل الكهنة فيه أيديهم وأقدامهم قبل الخدمة في الهيكل أو قبل الدخول إلى القدس ( خر 30: 17-21 ). وكان هذا يرمز إلى الطهارة الواجب توفرها في من يخدم الله. وكانت توضع المرحضة بين المذبح وباب خيمة الاجتماع.\nوفي هيكل سليمان كان هنالك (( بحر من النحاس المسبوك )) وعشر مراحض بدلاً من مرحضة واحدة ( 1 مل 7: 23-26 و 38-40 و 43 ). انظر كلمات (( هيكل، البحر المسبوك )).'),
(1532, 'رحلات إسرائيل', '( انظر (( خروج )) (( سيناء )) (( برية )) (( قفر ))  ).'),
(1533, 'رَحى', '( انظر (( طحن ))  ).'),
(1534, 'رَحُوب', 'اسم عبري معناه (( مكان رحب أي متسع ))، أو (( شارع متسع )).( 1 ) أبو هدر عزر ملك صوبة ( 2 صم 8: 3 و 12 ).( 2 ) لاوي ختم العهد مع نحميا ( نح 10: 11 ).( 3 ) مكان انتهى إليه الجواسيس ( عد 13: 21 ). ويدعى أيضاً بيت رحوب ( 2 صم 10: 6 و 8 ).( 4 ) مدينة في نصيب سبط اشير ( يش 19: 28 و 30 ). ويظن البعض أن ذكر الاسم في هذين العددين يشير إلى مدينتين لا مدينة واحدة في اشير.وقد أعطيت رحوب للاويين ( يش 21: 31 و 1 أخبار 6: 75 ). وربما كان مكانها (( تل البئر الغربي )) بالقرب من عكا وإلى جهة الشرق منها.'),
(1535, 'رَحُوبوت', 'اسم عبري معناه (( الأماكن الرحبة أي المتسعة ) أو (( الشوارع المتسعة )).( 1 ) مدينة كانت جزءاً من مدينة نينوى العظيمة، وتسمى أيضاً (( رحوبوت عير )) ( تك 10: 11 ).( 2 ) بئر حفرها اسحق في وادي جرار ( تك 26: 22 ). ويقرر روبنسون أن الوادي هو وادي الرُّحَيْبة الذي يبعد عن بئر سبع 19 ميلاً إلى الجنوب الغربي.'),
(1536, 'رَحوبوت النهر', '( تك 36: 37 و 1 أخبار 1: 48 ) مدينة جاء منها شاول ملك آدوم. وربما كانت هي نفس الرحابة التي تقع على نهر الفرات.'),
(1537, 'رَحُوم', 'اسم عبري معناه (( شفوق أو محبوب )).( 1 ) أحد الذين رجعوا مع زربابل من السبي البابلي ( عز 2: 2 ) ويدعى أيضاً نحوم ( نح 7: 7 ).( 2 ) صاحب القضاء الذي كتب رسالة إلى ارتحشستا الملك ليوقف إعادة بناء أسور أورشليم وهيكلها ( عز 4: 8 و 9 و 17 و 23 ).( 3 ) لاوي ساعد في ترميم سور أورشليم ( نح 3: 17 ).( 4 ) أحد الذين ختموا العهد مع نحميا ( نح 10: 25 ).( 5 ) كاهن رجع من بابل مع زربابل ( نح 12: 3 ) ويدعى أيضاً حريم ( نح 12: 15 ).'),
(1538, 'رَخْم', 'العقاب المصري، وهو من الطيور النجسة ( لا 11: 18 وتث 14: 17 ). ويسمى عند العامة دجاجة فرعون. اسمه باللاتينية Neophron percnopterus ولونه أبيض إلا ان لون الريش الذي في أطراف الأجنحة أسود.'),
(1539, 'تَرْديد', 'كانت عملية الترديد تتم في المناسبات التالية:( 1 ) ذبيحة السلامة، فكان الكاهن يردد الصدر ترديداً أمام الرب ثم يأكله ( لا 7: 30-34 ).( 2 ) الحصاد، إذ كان الكاهن يردد أول حزمة أمام الرب ( لا 23: 10 و 11 ).( 3 ) خبز الترديد أو خبز الباكورة، وهو عبارة عن رغيفين يصنعان من الحنطة الجديدة. وكانا يرددان يوم الخمسين أي بعد خمسين يوماً من ترديد حزمة أول الحصاد ( لا 23: 15-20 ).( 4 ) ذبيحة الاثم عن الأبرص بعد شفائه ( لا 14: 12 و 13 ) وبها كانا الأبرص يقدس ثانية لخدمة الله.( 5 ) تقدمة الغيرة ( عد 5: 25 ).كل هذه كان الكاهن يرددها نحو جهات السماء الأربع للدلالة على أن الله ليس إلهاً محلياً بل هو رب الكل ( انظر (( قربان ))  ).'),
(1540, 'رَدَّاي', 'اسم عبري ربما كان معناه (( يهوه أخضع )) أخ داود، وهو خامس أبناء يس ( 1 أخبار 2: 14 ).'),
(1541, 'رِداء', '( انظر (( ثوب ))  ).'),
(1542, 'رِداء شنعاري', '( أو رداء بابلي حسب بعض الترجمات ) هو الرداء الذي سرقه عخان عند خراب أريحا ( يش 7: 21 ) ويقرر يوسيفوس بأنه (( رداء ملكي موشى بالذهب )). وقد كانت بابل مشهورة بصناعة النسيج.'),
(1543, 'مَرَازِبة', 'والمفرد مرزبان. هي وظيفة سامية ذات سلطان واسع النفوذ، اسمى من وظيفة الوالي ( دا 3: 3 ). يماثلها (( ساتراب )) عند اليونانيين. وبعض الترجمات العربية تدعوهم (( أقطاب )) أو (( أمراء )).'),
(1544, 'رزة', 'كانت الشقق تعلق في خيمة الاجتماع بواسطة رزز مصنوعة من الذهب أو الفضة ( خر 26: 32 و 37 و 27: 10 ).'),
(1545, 'رَزُون', 'اسم ارامي معناه (( أمير )) ( 1 مل 11: 23 ) ابن اليداع،وهو الذي هرب من عند هدد عزر وجمع رجالاً من الغزاة ثم قام ببعض الغزوات بجوار دمشق وتبوأ عرش الملك في دمشق. وكان خصماً لإسرائيل كل أيام داود وسليمان.'),
(1546, 'رِسَّة', 'اسم عبري ربما كان معناه (( ندي )) وهو مكان في البرية حطّ فيه الإسرائيليون رحالهم ( عد 33: 21 و 22 ) ولعله كنتلّة الجرافي بين قسيمة والعقبة شمال غربي جبل رويسة النجيين.'),
(1547, 'رَسول', '( مبعوث ):( 1 ) أي شخص يرسل في مهمة خاصة ( 1 صم 11: 7 ويو 13: 16 ).( 2 ) ويطلق الاسم بصفة خاصة على تلاميذ الرب يسوع الاثني عشر الذين اختارهم ليعاينوا حوادث حياته على الأرض ويروه بعد قيامته ويشهدوا له أمام العالم بعد حلول الروح القدس عليهم ( مت 10: 2-42 واع 1: 21 و 22 ).وهؤلاء هم سمعان بطرس واندراوس ويعقوب بن زبدي ويوحنا أخوه وفيلبس وبرثولماوس وتوما ومتى العشار ويعقوب بن حلفى ولباوس الملقب تداوس ( ويسمى أيضاً يهوذا ابن حلفى ) وسمعان القانوني ( وهو الغيور ) ويهوذا الاسخريوطي.وقد دُوِنّ قانون إرساليتهم في متى 10: 5-42 ودونت سيرة كل واحد تحت اسم كل منهم. وكان الرسل من الطبقة المتوسطة وبعضهم من الفقراء ولم يكن بينهم أحد من الكهنة. وكان اغلبهم غير متعلمين، كما كان القليلون لهم المام بمبادئ التعليم. على أن يسوع عنى بتعليمهم تعليماً روحياً عميقاً مدة إقامته معهم. وكانوا أجمعين من الأتقياء-غير أن يهوذا الاسخريوطي انحرف وسلك مسلكاً شائناً أدى به إلى تسليم سيده ومعلمه. وبعد صعود المسيح ببضعة أيام انتخب التلاميذ متياس خلفاً ليهوذا الاسخريوطي. وبعد الصعود بسبع سنوات دعي بولس من المسيح إذ كان في الطريق بين أورشليم ودمشق. ومع أنه لم يكن ضمن الاثني عشر إلا انه جاهد وتعب أكثر من جميعهم وكرز في بلاد أكثر وكتب رسائل أكثر ( غل 1: 1 و 12 و 15 و 16 و 2 كو 11: 23-29 ).\nويخبرنا سفر الأعمال بخدماتهم الأولى لا سيما عن أعمال بطرس وبولس. ولو انه لم يتحدث مطلقاً عن خدمات الكثيرين منهم أمثال اندراوس وفيلبس وبرثولماوس وتوما ومتى ولباوس ومتياس. لكن ليس معنى ذلك أنهم لم يعملوا شيئاً.ويشترط في الرسول:أولاً:أن يكون قد اتصل بالمسيح وعاشره وتلقى تعاليمه منه مباشرة.ثانياً:أن يكون المسيح قد دعاه إلى هذه الخدمة. ولم يستثن من هذا الشرط سوى متياس. على أن الاحد عشر الذين اختاروه حرصوا على أن لا يختاروا أحداً من بين الذين عاشروا المسيح منذ معمودية يوحنا إلى قيامة المسيح ( اع 1: 21 و 22 ). ويروي التقليد أنه كان هناك حول السبعين تلميذاً.( 3 ) وأطلقت لفظة رسول أيضاً على برنابا الذي لازم بولس في خدماته الأولى في إنطاكية وفي آسيا الصغرى ( اع 14: 4 و 14 ).( 4 ) وأطلقت كذلك بمعنى أعم على المبشرين بالإنجيل ( 2 كو 8: 23 وفي 2: 25 ).( 5 ) وسمي مخلصنا رسولاً ( عب 3: 1 ) وهو خليق بهذا الاسم لأنه هو المرسل من الاب لخلاص البشرية. وفي 42 موضعاً من إنجيل يوحنا يتحدث المسيح عن نفسه بأنه مرسل من الاب.'),
(1548, 'رسالة- ورسائل', 'أطلق هذا الاسم على 21 سفراً في العهد الجديد، كتبها الرسل إلى كنائس معينة أو أشخاص معينين أو المسيحيين بصفة عامة. وإن كانت هذه الرسائل تتضمن نصائح أ و تعليمات لكنائس معينة أو لأشخاص معينين بسبب ظروف معينة إلا أنها تصلح للتعليم لكنيسة المسيح بصفة عامة في كل مكان وفي كل زمان لأن كل ما سبق فكتب لأجل تعليمنا ( رو 15: 4 ).وقد كتب بولس أربعة عشر رسالة أو ثلاث عشر. ويعقوب واحدة وبطرس رسالتين ويوحنا ثلاثا ويهوذا واحدة.ولا شك في أن العهد الجديد لا يتضمن جميع ما كتبه الرسل غير أن الكنيسة الأولى قررت أن هذه الرسائل الحالية هي القانونية التي كتبت بإلهام الروح القدس.وقد كتبت بعض الرسائل قبل كتابة الأناجيل. فرسالة يعقوب ( وربما كانت هي أسبق أسفار العهد الجديد ) كتبت حوالي سنة 45م ورسالتا تسالونيكي حوالي سنة 50م.وكل الرسائل عدا الرسالة إلى العبرانيين ورسالة يوحنا الأولى تبين في بدايتها اسم كاتبها واسم الكنيسة أو الشخص الموجهة إليه. أما رسالتا بطرس ورسالة يهوذا فإنها موجهة إلى جماهير المؤمنين. ويتلو العنوان التحية وتختم أغلب الرسائل كذلك بالتحية.أما التحية التي يستعملها بولس في بداية رسائله فتتضمن كلمتين (( نعمة وسلام )) والأرجح أن بولس استخدم كاتباً أملى عليه رسائله ( رو 16: 22 ). ويظن أن السبب في ذلك هو ضعف بصره. على أنه كان يدون في نهاية رسائله تحية بخطه بحروف كبيرة يقول عنها في إحدى المرات أنها (( علامة في كل رسالة )) ( 2 تس 3: 17 ).وقد صرّح الرسل أن هذه الرسائل جزء من كلمة الله ( 1 تس 2: 13 و 11 تس 2: 13 و 1بط 1: 12 ) وقد ذكر الرسول بطرس صراحة عن رسائل الرسول بولس أنها من ضمن الكتب المقدسة ( 2 بط 3: 15 و 16 ).بيان برسائل العهد الجديدالرسالة		موضع كتابتها		تاريخ كتابتها تقريباًيعقوب		أورشليم 		45م1تسالونيكي		كورنثوس		50م2تسالونيكي		كورنثوس		50مغلاطية		افسس			55 أو 56م1كورنثوس		افسس			57م2كورنثوس		مكدونية			57مرومية		كورنثوس		58مكولوسي		رومية			62مافسس		رومية			62مفليمون		رومية			62مفيلبي			رومية			63مإلى العبرانيين	إيطاليا			65-68م1بطرس		بابل أو روما		64 أو 65م1تيموثاوس		مكدونية			64 أو 65متيطس		مكدونية			65 أو 66م2تيموثاوس		رومية			67م2يطرس		رومية			68ميهوذا		؟			81م1و 2 و 3يوحنا	افسس			90-100مرَسَن:إحدى مدن أشور، وهي ضاحية لنينوى وإحدى مجموعة المدن المعروفة (( بالمدينة الكبرى )). وتقع بين نينوى وكالح ( تك 10: 12 ).'),
(1549, 'مرساة-مراسي', '( 1 ) الآلة المعروفة لتثبيت السفينة في موضعها ( 1ع 27: 13 و 29 ). وكانت تطرح في المياه غالباً من مؤخر السفينة وأحياناً مقدمتها. وظن بعضهم انه أشير بالآلة المذكورة إلى مرساة ذات أربع شعب كالمرساة المعروفة الآن المستعملة في المياه الضحلة.\n( 2 ) واستعملت الكلمة مجازاً للدلالة على الرجاء بمواعيد الله المثنتة على الصخر الأبدي ( عب 6: 19 ).'),
(1550, 'رَشف', 'اسم عبري معناه (( لُهب )) رجل من نسل افرايم ( 1 أخبار 7: 25 ).'),
(1551, 'رَصَاص', 'معدن ثقيل كان معروفاً منذ عهد بعيد. أُخذ غنيمة من المديانيين ( عد 31: 22 ). وُجد في مصر وكان يصدر إلى ترشيش ( حز 27: 12 ). وكان يستعمل في الأوزان ( زك 5: 7 ). وكألواح للكتابة عليها. ولعل ما ورد في اي 19: 23 و 24 يعني أن السفر الذي كان يتمنى أيوب كتابته خليق بأن ينقر في الصخر ويصب الرصاص في الحفر.'),
(1552, 'رَصَف', 'اسم سامي معناه (( حجر محمي أو فحم متوهج )) ( 2 مل 19: 12 واش 37: 12 ). وهي مدينة افتخر ربشاقي بأن الآشوريين دمروها. والأرجح أنها كانت في موقع رصافة الحالية التي تبعد نحو عشرين أو ثلاثين ميلاً غربي الفرات في اتجاه تدمر.'),
(1553, 'رِصْفَة', 'اسم سامي معناه (( حجر محمي أو فحم متوهج )) وهي سرية لشاول ( 2 صم 3: 7 ). بسببها قام نزاع بين ايشبوشث وابنير أدَّى إلى انضمام ابنير إلى داود (( 2صم 3: 6-8 ). وقد حرست جثتي ابنيها اللذين صلبهما الجبعونيون وتركوهما على خشبة الصليب عدة أشهر ليلاً ونهاراً ( 2 صم 21: 8-11 ).'),
(1554, 'رَصِيا', 'اسم عبري معناه (( بهيج )) وهو رئيس من بني اشير ( 1 أخبار 7: 39 ).'),
(1555, 'رَصِين', 'اسم ارامي معناه (( جدول ماء صغير ))( 1 ) أحد ملوك ارام حوالي سنة 738 ق.م. ( 2مل 15: 37 ). في أيام احاز ملك يهوذا انضم رصين إلى فقح مالك إسرائيل وصعد إلى أورشليم لمحاربتها ففشلا ( 2 مل 16: 5-9 ). وهذا ما سبق وتنبأ به اشعيا ( اش 7: 1-9: 12 ). وإذ استنجد احاز بتغلث فلاسر الثاني ملك أشور تمكن من قتل رصين ( 2 مل 16: 7-9 ). وفي الاثار الآشورية ما يشير إلى هذه الحرب.( 2 ) مؤسس أسرة من النثينيم عاد بنوه مع زربابل من السبي البابلي ( عز 2: 48 ونح 7: 50 ).'),
(1556, 'رَعْد', 'هو الصوت الذي يعقب البرق. إنه يسبب رعباً وفزعاً ( خر 9: 23 ). ويندر أن يحدث في الصيف الذي هو فصل الجفاف في فلسطين ( ام 26: 1 ) ولذلك فعندما حدث أن جاء مطر في هذا الفصل بعد الصلاة من أجله كان هذا علامة على استجابة الصلاة ( 1 صم 12: 17 و 18 ).ولقد طالما وصف مجازاً بأنه هو صوت الرب ( اي 37: 2-5 و 40: 9 ومز 29: 3-9 ) لأن الرب هو الذي يرسل العاصفة ويوجهها ( اي 28: 26 ).ولقد طالما رافق الرعد إعلانات حضور الله عندما آتى في جلال وعظمة ( خر 19: 16 ورؤ 4: 5 ).والرعد يعلن عظمة قدرة الله في عمله في الطبيعة.ولأنه كان ينظر إليه بأنه ينذر بعاصفة مدمرة فإنه يرمز إلى الانتقام الإلهي ( 1صم 2: 10 و2 صم 12: 14 و 15 ومز 77: 18 ).'),
(1557, 'رَعْلايا', 'اسم عبري معناه (( الرب ارعب )) أحد الرؤساء الذين رجعوا من السبي البابلي مع زربابل ( عز 2: 2 ) ودعي رَعَمْيا في ( نح 7: 7 ).'),
(1558, 'رَعَما. رَعْمة', 'اسم عبري معناه (( ارتعاش )):( 1 ) حفيد حام ( تك 10: 7 و 1 أخبار 1: 9 ).( 2 ) مقاطعة في الجنوب الغربي من بلاد العرب كانت تتجر مع صور بالطيب والحجارة الكريمة والذهب ( حز 27: 22 ). ويُظن بأن سكانها من ذرية رعما ( رعمة ) حفيد حام.'),
(1559, 'رَعمسيس', 'اسم مصري قديم معناه (( ابن إله الشمس )) وهي مدينة في مصر في أخصب منطقة في البلاد ( تك 47: 11 ) وهذه المنطقة ( اسمها جاسان ع6 ) هي التي سكنها بنو إسرائيل بأمر من فرعون. لقد بنى رمسيس ( رعمسيس ) الثاني هذه المدينة في حدود مصر الشرقية وسماها باسمه.\nوالأرجح أنها هي نفس مدينة المخازن التي بناها العبرانيون لفرعون ( خر 1: 11 ). وعند خروج بني إسرائيل من مصر تحركوا من رعمسيس إلى سكوت ( خر 12: 37 ). أما موقعها الآن فهو مدينة صالحجر أو صان الحجر.'),
(1560, 'رَعَمْيا', 'اسم عبري معناه (( الرب ارعب )) ( انظر رعلايا ).'),
(1561, 'رَعُو', 'اسم عبري معناه (( صديق )) أحد أسلاف مخلصنا ( لو 3: 35 وتك 11: 20 و 21 ).'),
(1562, 'رَعْوئيل', 'اسم عبري معناه (( صديق الله ))( 1 ) أحد أولاد عيسو ( تك 36: 4 ).( 2 ) حمو موسى ( خر 2: 18 وعد 10: 29 ) ويسمى أيضاً يثرون ( خر 3: 1 ) ويبدو انه أطلق عليه الاسم ومعناه (( سمو )) كلقب شرف ( انظر (( يثرون ))  ).( 3 ) رجل من سبط جاد، أبو الياساف ( عد 2: 14 ) ( انظر (( دعوئيل ))  ).'),
(1563, 'راعٍ', 'الشخص الذي يهتم برعاية المواشي سيما الغنم. وكان صاحب المواشي يكل أمر رعياتها لابنه أحياناً ( تك 37: 2 و 1 صم 17: 11 و 19 ) أو لابنته ( تك 29: 9 وخر 2: 16 و 17 ). أو لأجير ( تك 30: 31 و 32 وزك 11: 12 ويو 10: 12 ).يذهب الراعي إلى الحظيرة في الصباح ويدعو خرافه التي تعرف صوته وتتبعه. أما الصوت الغريب فلا تعرفه ( يو 10: 2-5 ) ويقودها إلى المرعى ويقضي معها هناك النهار كله وفي بعض الأحيان الليل أيضاً ( تك 31: 4 ونش 1: 7 ولو 2: 8 ) ويحرسها من الوحوش، واللصوص ( 1 صم 17: 34 و 35 واش 31: 4 ). ويمنعها من الاعداء على الأراضي المزروعة، ويرد الضال ( خر 34: 12 ولو 15: 4 ) ويعني بصفة خاصة بالصغار والضعفاء منها ( اش 40: 11 وحز 34: 3 و 4 و 16 وزك 11: 9 ).يحمل الراعي عادة عصا طويلة لقيادة الغنم وجمعها معاً والدفاع عنها وتأديب العصاة منها ( مز 23: 4 وميخا 7: 14 وزك 11: 7 ). يحرص الراعي على أن يرافقه كلب ( اي 30: 1 ) ليحذر بنباحه إذا اقترب من الغنم وحش مفترس.وقد دعي الله راعي إسرائيل سيما المؤمنين منهم ( تك 49: 24 ) كما قال الرب يسوع المسيح عن نفسه بأنه هو الراعي الصالح ( يو 10: 1-18 ).وكان ينظر إلى الأنبياء والكهنة والملوك بأنهم رعاة ( حز 34 ).وفي الكنيسة المسيحية يعتبر الشيوخ أو القسوس أو الأساقفة رعاة عيّنهم رئيس الراعة الأعظم لرعاية شعبه ( 1 بط 5: 1-4 ).'),
(1564, 'رعَويَّة', 'وردت هذه الكلمة في العهد الجديد مرتين: الأولى في اع 22: 28 والحديث فيها عن الرعوية للإمبراطورية الرومانية أي أن المرء يصير من أتباعها. والثانية في اف 2: 12 والحديث فيها عن رعوية إسرائيل أي أن يكون المرء من جماعة بني إسرائيل.'),
(1565, 'رَغيف', '( 1 أخبار 16: 3 ) ( انظر (( خبز ))  ).'),
(1566, 'رَفائيل', 'اسم عبري معناه (( شفى الله )) وهو بواب من اللاويين ( 1 أخبار 26: 7 ).'),
(1567, 'رَفائيون', 'اسم عبري معناه (( ظلال الموتى )) (( أرواح الراحلين ))، (( جبابرة )) عشيرة من الجبابرة سكنوا قديماً في فلسطين شرقي الأردن وغربه حتى قبل وصول إبراهيم ( تك 14: 5 و 15: 20 وتث 2: 11 و 20 و 3: 11 ويش 17: 15 ). وعندما دخل العبرانيون كنعان يبدو أن بقية من الرفائيين اختبأوا بين الفلسطينيين ( 2 صم 21: 16-21 ).وادي الرفائيين:واد مشهور بالخصب بين بيت أورشليم ( 2 صم 23: 13 ويش 15: 8 و 18: 16 ) ويسمى أيضاً وادي رفايم ( اش 17: 5 ). وفيه انتصر داود مرتين على الفلسطينيين ( 2 صم 5: 18-25 1 أخبار 14: 9-11 ). وربما كان الرفائيون هم أول من استوطنوه. ويرّجح أنه وادي البقاع الذي يقع جنوبي غرب أورشليم.'),
(1568, 'رَفايا', 'اسم عبري معناه (( يهوه قد شفى ))( 1 ) رجل من نسل داود ( 1 أخبار 3: 21 ).( 2 ) قائد من بني شمعون عاش في أيام حزقيا ( 1 أخبار 4: 42 ).( 3 ) رجل من بني يساكر ( 1 أخبار 7: 2 ).( 4 ) رجل من نسل شاول ( 1 أخبار 9: 43 ) ويدعى أيضاً رافة ( 1 أخبار 8: 37 ).( 5 ) ابن حور ورئيس نصف دائرة أورشليم ( نح 3: 9 ).'),
(1569, 'رَفايم', '( انظر (( رفائيون ))  ).'),
(1570, 'رَفح', 'اسم عبري معناه (( ثروة )) رجل من نسل افرايم ( 1 أخبار 26: 7 ).'),
(1571, 'مُرتفَعة-مرتفعات', 'أماكن مرتفعة على رؤوس الهضاب أو قمم الجبال، كانت في بداية الأمر تفضل لإقامة المذابح عليها لعبادة الله ( تك 12: 7 و 8 و 22: 2 و 31: 54 ). وقبل أن يبنى الهيكل ويخصص للعبادة رأى الشعب أن إقامة المذابح على المرتفعات هو أكثر الأماكن لياقة ( قض 6: 25 و 26 و 1صم 9: 12 و 19 و 25 و 1 أخبار 16: 39 و 21: 29 ). لكن الأمم كانوا في نفس الوقت يبنون المرتفعات لعبادتهم الوثينية ولهذا حذر الله شعبه منها قبل دخولهم أرض كنعان ( عد 33: 52 وتث 33: 29 ).وبعد ما أقيم الهيكل صارت تعتبر الأماكن رجسة لأنها تنجست جميعها بعبادة الأصنام حتى قيل عن يوثام أحد ملوك يهوذا الصالحين بأنه عمل ما هو مستقيم (( إلا أن المرتفعات لم تنتزع )) ( 2 مل 15: 35 ).وهذه هي أماكن المرتفعات التي ورد ذكرها في الكتاب المقدس:آون ( هو 10: 8 )، ارنون ( عد 21: 28 )، بعل ( عد 22: 41 )، توفة ( ار 7: 31 )، جبعون ( 1 مل 3: 4 )، مرتفعة ( حز 20: 29 ).والذين بنوا مرتفعات كهذه هم:يربعام ( 1 مل 12: 31 )، يهورام ( 2 أخبار 21: 11 )، آحاز ( 2 أخبار 28: 25 )، منسى ( 2 مل 21: 3 و 2 أخبار 33: 3 )، بنو إسرائيل ( 1 مل 13: 32 و 33 و 2 مل 17: 9 ). فاقتدى بهم بنو يهوذا ( 1 مل 14: 23 ).بل إن سليمان نفسه الذي وصلت المملكة في أيامه إلى عصرها الذهبي أغوته زوجاته الامميات فبنى مرتفعات لألهتهن، لعشتورت رجاسة الصيدونيين، وكموش رجاسة الموآبيين، وملكوم كراهة بني عمون ( 1 مل 11: 7 ).وكانت العبادة الوثنية على هذه المرتفعات تقترن بأقبح أنواع الرذائل والفجور ( هو 4: 11-14 وار 3: 2 و 2 أخبار 21: 11 ) علاوة على أنها ازاعت شعب الله عن العبادة الحقيقية، ولهذا بذل الكثيرون من ملوك يهوذا الصالحين جهدهم لإزالتها أمثال آسيا ( 2 أخبار 14: 3 و 5 و 15: 17 ) ويوشافاط ( 2 أخبار 17: 6 )  وحزقيا ( 2 مل 18: 4 و 2 أخبار 31: 1 ) ويوشيا ( 2 مل 23: 8 و 2 أخبار 34: 3 ). لكنها لم تنتزع في أيام يهوآش ( 2 مل 12: 3 ) وامصيا ( 2 مل 14: 4 ) وعزريا ( 2 مل 15: 4 ) ويوثام ( 2 مل 15: 35 ). وقد كشف التنقيب عن بعض المرتفعات الوثنية القديمة في بيت إيل وجازر والبترا.'),
(1572, 'رَفيعة', '( انظر (( قربان )) ).'),
(1573, 'رِفقة', 'اسم عبري ربما كان معناه (( رباط, أو حبل قيد )) وهي ابنة بتوئيل وأخت لابان ( تك 24: 15 و29 ) لما كبر اسحق كلف أبوه أحد خدامه ليبحث له عن زوجة من بين فتيات عشيرته فذهب الخادم وإذ طلب إرشاد الله وفقه لاختيار رفقة فتزوجت اسحق ( تك 24 ) وبعد زواجها بنحو عشرين سنة ولدت يعقوب وعيسو وأعلن لها بأن السيادة ستكون ليعقوب. فأحبت يعقوب أكثر من عيسو ودبرت الخطة ليعقوب لينال من أبيه البركة التي كانت منتظرة لعيسو ( تك 25: 28 و27 : 1 - 18 : 5 ). وقد ماتت رفقة قبل اسحق في الوقت الذي كان ابنها يعقوب عند خاله لابان ودفنت في مغارة المكفيلة عند قبر ابراهيم ( تك 49 : 31 ).'),
(1574, 'رَفيديم', 'اسم عبري معناه (( متسعات )) وهي محلة لبني إسرائيل بين برية سين وسينا, حطوا فيها رحالهم أثناء ارتحالهم في البرية ( خر 17: 1 و19: 2 وعد 33: 12 - 15 ). لم يكن فيها ماء فتذمر الشعب وضرب موسى الصخرة وخرج منها ماء ( خر 17: 5 و 6 ).وفيها هزم يشوع عماليق وقومه إذ وقف موسى على تل ورفع يده مشيراً إلى الله الذي عضدهم في القتال وكان إذا رفع موسى يديه غلب شعبه, وإذا خفضهما كان عماليق يغلب. فلما صارت يداه ثقيلتين دعمهما هرون وحور فكانتا ثابتتين إلى غروب الشمس فغلب شعبه وهزم عماليق ( خر 17: 8 - 13 ).وإليها جاء يثرون حمو موسى مع أهل بيته ونزل ضيفاً على موسى وسجد للرب مع شيوخ إسرائيل ( خر 18: 1 -12 ).أما مكانها فغير معروف على وجه التحقيق ولعلها في وادي رفايد شمال غربي جبل موسى. وهنالك وادي ردوا - وهو مجرى مياه باردة - يتصل بوادي رفايد وبه واحة عند سفح جبل رفايد.'),
(1575, 'رَفا', 'كلمة آرامية معناها (( فارغ )) (( أي خاوٍ )) أو (( تافه )) ( مت 5: 22 ) وهو تعبير يفيد معنى الازدراء.'),
(1576, 'رَقيب', '( حز 33: 2 - 9 ) ( انظر (( حرس حارس ))  ).'),
(1577, 'رَقْص', 'يمارس الرقص في المناسبات المفرحة ( مز 30: 11 وجا 3: 4 ومراثي 5: 15 ولو 15: 25 ). كانت النساء العبرانيات يمارسنه إما أفراداً أو جماعات سيما في استقبال الجيش المنتصر ( قض 11: 34 و 1 صم 18: 6 و 7و 29: 5 ).كان لبعض العادات اليونانية خطرها الأدبي على اليهود. ولعل الرقص الخليع الذي رقصته سالومي ابنة هيروديا أمام هيرودوس كان إحدى تلك العادات ( مت 14: 6 ومر 6: 22 ).ويبدو أن الرقص, كجزء من الاحتفالات الدينية, وكانت النساء تمارسه بصفة خاصة ( خر 15: 20 وقض 21:21 و 23 ) وفي بعض الأحيان كان يمارسه الرجال كما نرى في الحادثة المعروفة ساعة رقص داود أمام التابوت ( 2 صم 6: 14 - 23 و 1 أخبار 15: 29 ).وكان الرقص أمام التماثيل شائعاً بين عبادة الأوثان ( خر 32: 19 و 1 مل 18: 26 ).'),
(1578, 'رُقاق', 'كعك من الدقيق كان يقدم مع تقدمات متنوعة وكان يلتّ ( يعجن ) بالزيت ( خر 16: 31 و29: 2 و23 ولا 2: 4 و 7: 12 و8: 26 وعد 6: 15 و19 ).'),
(1579, 'رُقوق', 'جلود الغنم أو المعيز المعدة للكتابة عليها بدلاً من الورق. كان الجلد أولاً ينقع في محلول الجير لنزع الشعر أو الصوف عنه. ثم يحلق فيغسل ويجفف ويشد وينعم يقول هيرودوتس بأن الأيونيين, هم أسلاف اليونانيين, استعملوا جلود المعيز والغنم لأن أوراق البردي كانت نادرة. ويروي التقليد أن الرقوق استعملت أولاً في برغامس.وفي سنة 1923 وُجدت في دورا - الواقعة على نهر الفرات - بعض المستندات المدونة على رقوق تحمل تواريخ تقابل 196 - 195 و 190 - 189 ق.م.كانت أوراق البردي هي المستعملة عادة للكتابة ( 2 يو 12 ) لكن الرسول بولس يشير إلى رقوقه التي أولاها عناية خاصة ( 2 تي 4: 13 ).'),
(1580, 'رَقَّة', 'اسم ارامي معناه (( شاطئ )) وهي مدينة محصنة لنفتالي ( يش 19: 35 ) على الشاطئ الغربي لبحر الجليل. ويزم علماء اليهود أنها كانت في موقع طبرية الحالية لكن لعلها كانت في موقع تل اقلاتية جنوب المجدل ( انظر (( طبرية ))  ).'),
(1581, 'رَقَّون', 'اسم عبري لعل معناه (( شاطئ )) هي مدينة لدان ( يش 19: 46 ). ويزعم كوندر أن موقعها في تل الرقيت وتبعد ميلين ونصف ميل عن مصب نهر العوِجة شمالاً، أو 6 أميال عن يافا شمالاً.'),
(1582, 'يَرْقى. راقٍ', '( انظر (( حلم )) (( عرافة ))  ).'),
(1583, 'ركاب', 'اسم عبري معناه (( فارس )).( 1 ) أبو يهوناداب ( 2 مل 10: 15 و 23 و 1 أخبار 2: 55 ) ومؤسس أسرة الركابيين ( ار 35: 2-19 ).( 2 ) ابن رامون وأحد رئيسي الغزاة الذين تآمرا ضد ايشبوشت ( 2 صم 4: 2 ).( 3 ) أبو ملكيا أحد الذين ساهموا في ترميم أسوار أورشليم ( نح 3: 14 ).'),
(1584, 'رَكابيّون', 'قوم من القينيين أو المديانيين ( 1 أخبار 2: 55 ) تسلسلوا من يوناداب ( أو يهوناداب ) بن ركاب ( 2 مل 10: 15 ) وتسموا باسم ركاب ( ار 35: 2-19 ) وعاشو بين بني إسرائيل. وقد سن يوناداب بن ركاب لذريته ( الركابيين ) شريعة لكي يظلوا شعباً مستقلاً ممتازاً وعشيرة معتزلة بعيدة عن عبادة الأصنام وتلخص الشريعة فيما يلي:أولاً: أن يمتنعوا عن شرب الخمر وكل شراب مسكر.ثانياً: أن لا يسكنوا في بيوت.ثالثاً: أن لا يزرعوا زرعاً ولا يغرسوا كرماً.رابعاً: أن يكون سكنهم في خيام ( ار 35: 6 و 7 ).وكان القصد من ذلك أن يحتفظوا ببساطة عاداتهم البدائية. وقد أطاع الركابيون هذه الوصية وظلوا شعباً مستقلاً محباً للسلام وسكنوا الخيام، وكانوا يتنقلون من مكان إلى لآخر حسب مقتضيات الظروف. ولما غزا نبوخذ نصر اليهودية هرب الركابيون إلى أورشليم طلباً للنجاة. وعندما أراد ارميا بعد ذلك ببضع سنوات أن يختبر طاعتهم لوصية أبيهم وجد أنهم لا يزالون على ولائهم الأول فوبخ اليهود لعدم طاعتهم لوصية الله. وهم يرون هؤلاء الركابيين في كامل الولاء لوصية أبيهم. ومن أجل هذا الولاء قطع لهم الرب هذا الوعد (( لا ينقطع ليوناداب بن ركاب إنسان يقف أمامي كل الأيام )) ( ار 35: 1-19 ).ويقال أنه لا تزال بقية منهم إلى الآن في العراق واليمن ويعرفون بني خيبر. لكن ليست لهم علاقة مع أخوتهم اليهود المشتتين في آسيا الذين يعتبرونهم أخوة كذبة لعدم محافظتهم على الشريعة على أنهم لا يزالون يحفظون السبت.'),
(1585, 'رُكبة', 'المعنى الحرفي معروف. لكنها تستعمل مجازياً فهي مركز القوة ( تث 28: 35 واي 4: 4 واش 35: 3 ونا 2: 10 وعب 12: 12 ).\nوكان استقبال المولود على الركبتين يعتبر علامة للتبني ( تك 30: 3 و 50: 23 ). وكان وضع الرأس بين الركبتين يعتبر من علامات التذلل والتضرع ( 1 مل 18: 42 ).\nويعتبر الجثو على الركبة من علامات التذلل والتواضع والتصاغر، ولذلك كثيراً ما تمت الصلاة بالجثو على الركب ( 2 مل 1: 13 واش 45: 23 ودا 6: 10 و 11 ولو 22: 41 واع 9: 40 و 20: 36 ورو 11: 4 واف 3: 14 وفي 2: 10 ).'),
(1586, 'مَركبة', 'كان للمركبة قديماً عجلتان ولها أشكال مختلفة، وكانت تجرها الخيل ( 2 صم 8: 4 ). وكانت تستعمل في الأغراض الحربية ( خر 14: 9 و 1صم 13: 5 ) وفي مظاهر العظمة ( تك 41: 43 و 2صم 15: 1 و 1 مل 1: 5 ) وللأغراض الخاصة ( تك 46: 29 و 2مل 5: 9 واع 8: 28 ).كانت المركبة الحربية تصنع من الحديد ( يش 17: 16-18 وقض 1: 19 و 4: 3 ) وكانت العجلات تصنع غالباً من المعدن.لم تكن المركبة تلائم جبال فلسطين لذلك لم تستعمل فيها بكثرة. ولكنها كانت تستعمل كثيراً في أودية كنعان ( يش 17: 16 وقض 4: 3 ).وبين المصريين ( اش 31: 1 ) والاثيوبيين ( الكوشيين ) ( 2 أخبار 16: 8 ) والسوريين ( الاراميين ) ( 2 مل 5: 9 ) والحثيين ( 2 مل 7: 6 ) والآشوريين ( نا 2: 3 و 4 و 3: 2 ).وأول من ادخلها في الجيش العبراني داود ( 2 صم 8: 4 ) وكان لسليمان 1400 مركبة. والأرجح أنها أُحضرت من مصر.وكان يرافق المحارب في المركبة الحربية سائق المركبة وحامل الترس.أما مركبات الشمس ( 2 مل 23: 11 ) فأخذها يستعملها اليهود إقتداء بالآشوريين. وقد أحرق يوشيا هذه المركبات وأباد خيلها.وقد شوهد إيليا-لدى صعوده إلى السماء حياً-يركب مركبة من نار يجرها خيل من نار ( 2 مل 2: 11 و 12 ).أما رؤساء المركبات ( 1 مل 22: 33 ) فكانوا قواداً ذوي رتبة عالية.'),
(1587, 'رَامح', '( اع 23: 23 ) تشير إلى الجند المشاة المسلحين برماح.'),
(1588, 'رَمَة', 'اسم عبري معناه (( ارتفاع )) مدينة لبني يساكر ( يش 9: 21 ) وهي راموت ( 1 أخبار 6: 73 ) ويرموث ( يش 21: 29 ) ويظن البعض أنها كانت في موقع الرامة. ويظن آخرون أنها في موقع كوكب الهوى قرب بيسان.'),
(1589, 'رَمَتَ لَحْيٍ', 'اسم عبري معناه (( اكمة عظيمة الفك )) اسم المكان الذي قتل فيه شمشون ألف رجل من الفلسطينيين بلحي حمار ( قض 15: 17 ) ( انظر (( لحي ))  ).'),
(1590, 'رَمْث. أرماث', '( 1 مل 5: 9 و 2 أخبار 2: 16 ) مجموعة جذوع أشجار تعوم على الماء لنقلها من مكان لآخر.'),
(1591, 'رُمْح', '( انظر (( سلاح ))  ).'),
(1592, 'رَماد', 'يقصد به أحياناً شخص أو شيء تافه لا قيمة له ( تك 18: 27 ).وكانت تعتبر تذرية الرماد على الرأس والجلوس عليه من علامات التقشف وإنكار الذات والتذلل والحزن المفرط أو الندم ( 2 صم 13: 19 واش 4: 3 واي 2: 8 وار 6: 26 ومرا 3: 16 ويون 3: 6 ومت 11: 21 ). أما العبارة (( يرعى ( يأكل ) رماداً )) ( اش 44: 20 ) فيقصد بها إتباع ديانة خالية من الغذاء الروحي.\nوأما أكل الرماد المذكور في مز 102: 9 فيشير إلى التغفل الناشئ عن شدة الحزن. كذلك ماء رماد العجول التي كانت تقدم في يوم الكفارة العظيم فكان يستعمل للتطهير ( عد 19: 17 و 18 ) ( انظر (( عجل ))  ).'),
(1593, 'رَمْز', 'هو ما عينه الله إشارة إلى أمر أعظم منه عتيد أن يكون في نظام ملكوته وهذا سمي المرموز إليه. وقد يكون الرمز حدثاً تاريخياً مثل شرب شعب إسرائيل من الماء الخارج من الصخرة فقد كان يرمز إلى شرب الماء الروحي من الصخرة التي هي المسيح ( 1 كو 10: 4 )، أو خدمة طقسية مثل ذبيحة خروف الفصح التي كانت ترمز إلى ذبيحة المسيح، أو شخصاً معيناً مثل ملكي صادق الذي كان يرمز إلى المسيح.'),
(1594, 'رَمْفان', 'إله يقترن به كوكب سيار ( لعله هو زخل ) كان يُعبد قديماً، وقد عبده اليهود في البرية ( اع 7: 43 ). وكانت تصنع لهذا الإله تماثيل تحفظ في صناديق وتنقل من مكان لآخر كما كانت تصنع الهياكل لارطاميس ( اع 19: 24 واش 46: 7 ). وهي التي يشير إليها كل من عاموس النبي ( عا 5: 26 ) واستفانوس الشهيد ( اع 7: 43 ).'),
(1595, 'رَمْل', 'يوجد الرمل على شواطئ البحار وضفاف الأنهار وفي الجبال بالقرب من الصخور الرملية وفي الصحارى. ويكثر وجوده في مصر سيما في الصحراء الغربية المتصلة بصحراء أفريقياالكبرى.وقد وضع الرب الرمل تخماً للبحر فريضة أبدية ( ار 5: 22 ).ويصلح الرمل لإخفاء أي شيء بسرعة إذا طمر فيه ( خر 2: 12 ).ويكنى بالرمل عن كثرة العدد ( تك 32: 12 و 41: 49 )، وعن الثقل ( اي 6: 3 وام 27: 3 ).وورد عن زبولون ويساكر أنهما (( يرتضعان من فيض البحار وذخائر مطمورة في الرمل )) ( تث 33: 19 ). وقيل أن العبارة الأخيرة تشير إلى الأصداف المطمورة في رمال نهر الزرقاء قرب عكا التي يلون بها أهل صور الأرجوان علاوة على أن الرمال المذكورة يصنع منها الزجاج.'),
(1596, 'أرْمَلة', 'كانت الأرملة منذ القديم تلبس ثياباً خاصة ( تك 38: 14 و 19 ) وتنزع عنها حليها وترخي شعرها ولا تدهن وجهها. ولم يكن يسمح لرئيس الكهنة بأن يتزوج بأرملة.يعني الله بالضعفاء الذين لا عائل لهم سيما الأرامل ( تث 10: 18 ومز 68: 5 و 146: 9 وام 15: 25 وار 49: 11 ).وقد أوصت الشريعة بإنصاف الأرامل والعطف عليهن وهددت مخالفي هذه الوصية بقصاص مروع ( خر 22: 22 وتث 14: 29 و 16: 11 و 14 واش 1: 17 وار 7: 6 وزك 7: 10 ). كذلك فعل الرب يسوع المسيح ( مر 12: 40 ).وكانت الكنيسة في عصر الرسل تعني بالأرامل الفقيرات ( اع 6: 1 ويع 1: 27 ).وفي الكنائس التي كانت تحت إشراف تيموثاوس كانت الأرامل اللواتي هن بالحقيقة أرامل-وليس لهن أولاد أو حفدة-تدون أسماؤهن في سجل خاص فتعنى بهن الكنيسة. وكان يشترط أن لا يقل عمر الواحدة عن ستين سنة، وأن لا تكون قد تزوجت سوى مرة واحدة، وأن يكون مشهوداً لها بأعمال صالحة ( 1 تي 5: 3-16 ). وهؤلاء كانت وظيفتهن خدمة الكنيسة والإشراف على النسوة بين شعبها سيما الأرامل والأيتام.وقد أوصت الشريعة الموسوية بأنه إذا مات رجل ولم يخلف نسلاً اتخذ أخوه زوجته الأرملة زوجة له أولاً لكي تبقى أملاك الميت لآله، وثانياً لكي يقام له اسم إذ كان أول ابن يولد ينسب للميت ( تث 25: 5 ). وإذا رفض الأخ التزوج بأرملة أخيه تزوجها اقرب الأقرباء بعد الأخ أو التالي له، كما حدث مع بوعز وراعوث.'),
(1597, 'رَمَلْيا', 'اسم عبري معناه (( منّجم أو العرّاف الذي ليهوه )) وهو أبو فقح الذي فتن على فقحيا ملك إسرائيل وقتله ( 2 مل 15: 25 ).'),
(1598, 'رُمَّان', 'ثمر معروف واسمه باللاتينية Punica granatum وترتفع شجرته نحو خمسة أمتار ( عد 13: 23 وتث 8: 8 ونس 4: 3 و 6: 7 ). تصنع الخمر من عصيره ( نش 8: 2 ).\nكان يزين ثوب رئيس الكهنة وافوده برمان مطرز ( خر 28: 33 و 34 و 39: 24-26 ).ونحتت رمانات في أماكن شتى من الهيكل ( 1 مل 7: 18 ).'),
(1599, 'رمون', 'اسم عبري معناه رمانة(1)	رجل بنيامي كان ابناه رئيسين عند ايشبوشت بن شاول فغدرا به وقتلاه(2 صم 4: 2_8).(2)	مدينة جنوب يهوذا بالقرب من عين (يش 15: 32 و1 اخبار 4: 32 وزرك 14: 10). وقد نقلت بسرعة الى نصيب شمعون مع عين ومدن اخرى (يش 19: 7). ويظن البعض انها ام الرمامين التي تبعد 9 اميال شمالا عن بئر سبع والى الجنوب الغربي من الخليل(3)	مدينة في حدود نصيب زبولون (يش 19: 13) لكنها خصصت لللاويين ودعيت رمونو ( 1 اخبار 6: 77) وتدعى ايضا دمنة (انظر دمنة) ولعلها هي رمانة الحلية التي تبعد ستة اميال عن الناصرة شمالا.(4)	صخرة بالقرب من جبعة لجأ 600 من بني المنهزمين وظلوا مختبئين فيها اربة اشهر (قض 20: 45_ 47 و 21: 13) وتوجد قرية معروفة بهذا الأسم على قمة اكمة بين إيل والأردن.'),
(1601, 'رَمون فارص', 'اسم عبري معناه (( رمانة الثلمة أو الثغرة )) وهي محلة لبني إسرائيل حطوا فيها رحالهم أثناء ارتحالهم في البرية ( عد 33: 19 و20 ) ولعلها هي نقب البار.'),
(1602, 'رِمُّونو', 'انظر (( رمون3 )).'),
(1603, 'رَمْيا', 'اسم عبري معناه (( يهوه مرتفع )) أحد الذين اتخذوا نساء غريبة من بني السبي وأقنعهم عزا بإبعادهن ( عز 10: 25 ).'),
(1605, 'رِنَّة', 'اسم عبري معناه (( هتاف )) رجل من بني يهوذا وهو ابن شيمون ( 1 أخبار 4: 20 ).'),
(1606, 'رَهَب', 'اسم عبري معناه (( عاصفة )) وهو اسم أطلق على مصر ( اش 30: 7 و 51: 9 واي 9: 13 و 26: 12 ) ويظن بعض المفسرين أنها تشير إلى تنين يمثل مصر.'),
(1607, 'رُهْجة', 'اسم عبري معناه (( تراب. ضجة )) رئيس لبني اشير وهو من بني شامر ( 1 أخبار 7: 34 ).'),
(1608, 'رَهْن', 'كانت هنالك في الناموس وصايا كثيرة بصدد الرهن أهمها أنه لم يكن يسمح للدائن بدخول بيت المديون لأخذ الرهن بل كان عليه أن يبقى أمام الباب حتى يؤتى به إليه ( تث 24: 11 و 12 ). وإذا اخذ الثوب رهناً وجب رده قبل غروب الشمس لأنه ربما يكون هو لباسه في النوم ( خر 22: 26 و 27 ) ولا يجوز رهن ثوب الأرملة ( تث 24: 17 ). ولا يجوز رهن الجزء العلوي أو السفلي من الرحى لئلا تتعطل عملية طحن الحنطة للقوت اليومي ( تث 24: 6 ).'),
(1609, 'رُهَناء', '( 2 مل 14: 14 و 2 أخبار 25: 24 ) وهم أشخاص يؤخذون لضمان الصلح.'),
(1610, 'رُوجل', 'انظر (( عين روجل )).'),
(1611, 'رُوجَليم', 'اسم عبري معناه (( قصارون. جواسيس )) مدينة في أرض جلعاد عاش فيها برزلاي ( 2 صم 17: 27 و 19: 31 ). ويرّجح أن مكانها اليوم في وادي الرجيلة بالقرب من اربد.'),
(1612, 'رُوح', 'الروح كائن غير مادي قد يلبس أو لا يلبس جسداً ( 0تبتهج روحي بالله مخلصي )) ( لو 1: 47 )، (( الله روح )) ( يو 4: 24 ). وقيل عن الملائكة الذين لا جسد لهم أنهم (( جميعهم أرواح خادمة )) ( عب 1: 14 ).\nويتكون الإنسان من ثلاثة عناصر: روح ونفس وجسد. وهذا ما يؤيده قول الرسول بولس (( ولتحفظ روحكم ونفسكم وجسدكم كاملة بلا لوم )) ( 1 تس 5: 23 ). فالجسد هو الجزء المادي في تكوين الإنسان أما النفس فهي عنصر الحياة الحيوانية، وفيها يشترك الإنسان مع الحيوان عليها يتوقف الفهم والحركة والحساسية، وهي تتوقف عند الموت. والروح هي العقل، العنصر الخالد، مصدر الإرادة والضمير. خلق الله الإنسان بإعطاء حياة للجسد الذي صوره ثم بخلق روح عاقلة وهبها للإنسان ( تك 2: 7 ). وعند الموت (( يرجع التراب ( الجسد ) إلى الأرض كما كان وترجع الروح إلى الله الذي أعطاها )) ( جا 12: 7 ).\nالنفس الحية في الحيوان ( تك 1: 21 و 24 ) هي مجرد النفس الحيوانية وهي نفسية ومادية في طبيعتها وتهلك مع الجسد، وهي التي قيل عنها (( لا تأكلوا دم جسد ما لأن نفس كل جسد هي دمه )) ( لا 17: 11 و 14 و تث 12: 23 ). أما النفس الحية في الإنسان ( تك 2: 7 ) فهي عنصر اسمي، هي النفس العاقلة التي نفخها الخالق في الإنسان وجعلها على صورته. ولهذا ففي كثير من الأحيان تستعمل لفظة (( الروح )) في الكتاب بذل نفسه، ولفظة النفس بدل الروح ولهذا يعتقد كثيرون أن هناك عنصرين فقط في الإنسان الجسد ويشمل النفس الإنسانية والروح.'),
(1613, 'الروح القدس', 'هو روح الله، الأقنوم الثالث في الثالوث. وقد ذكر هذا التعبير ثلاث مرات فقط ( مز 51: 11 واش 63: 10 و 11 ) لكنه يتضمن إشارات عديدة لعمله أما في العهد الجيد فقد ذكره مراراً.وقد سمي روحاً لأنه مبدع الحياة، ودعي قدوساً لأن من ضمن عمله تقديس قلب المؤمن ويدعى روح الله وروح المسيح.ويعلمنا الكتاب المقدس بكل وضوح عن ذاتية الروح القدس وعن ألوهيته، إذ نسب إليه أسماء الله الحي، وصفاته، وأعماله، وعبادته.( 1 ) فنسب إليه أسماء الله كيهوه ( اع 28: 25 مع اش 6: 9 وعب 3: 7 و 9 مع خر 17: 7 وار 31: 31 و 34 مع عب 10: 15 و16 )، الله ( اع 5: 3 و 4 )، الرب ( 2 كو 3: 17 و 18 ).( 2 ) ونسب إليه الصفات الإلهية كالعلم بكل شيء ( 1 كو 2: 10 و 11 واش 40: 13 و 14 ). والوجود في كل مكان ( مز 139: 7 واف 2: 17 و 18 ورو 8: 26 و 27 )، والقدرة على كل شيء ( لو 1: 35 )، والأزلية ( عب 9: 14 ).( 3 ) ونسب إليه الأعمال الإلهية كالخلق ( مز 104: 30 واي 33: 4 ).( 4 ) ونسب إليه العبادة الواجبة لله ( اش 6: 3 ومت 28: 19 و 2 كو 13: 14 ).ومن ضمن أعماله أيضاً أنه يهب القوة ( قض 3: 10 و 14: 6 و 19 و 15: 14 و 1 صم 11: 6 و 16: 13 و 1 أخبار 12: 18 ). والحكمة والفهم والمعرفة ( خر 31: 3 ) يهب قلباً جديداً وروحاً جديداً ( حز 36: 26 و 27 ) ويبكت العام علة خطية وعلى بر وعلى دينونة. ويعلم كل شيء ويذكر بكل ما قيل ( يو 14: 26 ) ويعزى ( يو 14: 16 و 26 و15: 26 و 16: 7 ) ويهب روح التبني ( رو 8: 26 ). ويحي المائتيين بالخطايا والآثام ويقدسهم ويطهرهم وهكذا يؤهلهم لتمجيد الله والتمتع به إلى الأبد ( رو 5: 5 و 8: 2-13 ).وقال عنه اشعياء النبي أنه (( روح الحكمة والمعرفة، وروح المشورة والقوة، روح المعرفة ومخافة الرب )) ( اش 11: 2 ).وإذ حبلت السيدة العذراء حبل بالمسيح فيها من الروح القدس ( مت 1: 18-20 ).ولما كتب الاباء والأنبياء والرسل أسفار الكتاب المقدس كانوا مسوقين من الروح القدس الذي أرشدهم فيما كتبوا وعضدهم وحفظهم من الخطأ وفتح بصائرهم في بعض الحالات ليكتبوا عن أمور مستقبلة ( 2 بط 1: 21 و 2 تي 3: 16 ) ( انظر ((  تمييز الرواح  ))   ).تمييز الأرواح:هذه هي إحدى مواهب الروح القدس ( 1 كو 12: 10 ) وبها يمكن امتحان الأرواح وتمييزها لكي تعرف إن كانت من الله ( 1 يو 4: 1 ) وفي العصور الأولى من تاريخ الكنيسة كثر الأنبياء الكذبة والأرواح الشريرة والتعاليم الغربية.بواسطة هذه المواهب ( القدرة على تمييز الأرواح ) اكتشف بطرس كذب حنانيا وعرف بولس خداع عليم الساحر.'),
(1614, 'رَوْدا', 'اسم يوناني معناه (( شجرة ورد )) وهي امة كانت في بيت مريم أم يوحنا الملقب مرقس ( اع 12: 13 ). ولما أطلق بولس من السجن ذهب إلى بيت مريم وطرق الباب جاءت رودا لتستمع وتجيب الطارق.'),
(1615, 'رُودُس', 'اسم يوناني معناه (( شجرة ورد )) وهي جزيرة في البحر الأبيض المتوسط في الجنوب الغربي من آسيا الصغرى. طولها حوالي 45 ميلاً وعرضها حوالي 30 ميلاً. مشهورة بالبرتقال والموالح. اشتهرت قديماً بمركزها التجاري الذي كاد يجاري مركز كل من الإسكندرية وقرطاجنة. اشتهرت-عاصمتها-وتسمى باسمها-بتمثالها الهائل، وهو فنار عالٍ يبلغ ارتفاعه حوالي 105 أقدام اقيم بين سنة 300 وسنة 288 ق.م. وظل قائماً نحو 56 سنة ثم هدمته الزلازل. وقيل أن نحاسه حمل على 900 جمل. وقد رست في رودس السفينة التي كان الرسول بولس مسافراً فيها من ميليتس إلى باترا ( اع 21: 1 ).\nوقد ظلت الجزيرة زمناً طويلاً محتفظة باستقلالها يسكنها أمراء فرسان القديس يوحنا الذين استمروا يتحدّون الأتراك الذين احتلوها أخيراً سنة 1522 م. وسمحوا للأمراء بالانتقال إلى جزيرة مالطة. وتخضع الجزيرة الآن لحكم الدولة اليونانية.'),
(1616, 'رُوش', 'اسم عبري معناه (( رأس )).( 1 ) ابن لبنيامين واحد الذين نزلوا إلى مصر مع يعقوب وبنيه ( تك 46: 21 ).( 2 ) اسم شعب من شعوب الشمال ذكر مع ماشك وتوبال ( خر 38: 2 و 39: 1 ).ويزعم البعض أنه ربما يكون هو اسم قديم لروسيا. وقد ورد في بعض الترجمات كاسم عام لا اسم علم وترجم (( رئيس )).'),
(1617, 'رُفُس', 'اسم لاتيني معناه (( أحمر )) وهو ابن لسمعان القيرواني أو القيريني الذي سخر لحمل صليب المسيح ( مر 15: 21 ). ولعله هو ذاته روفس الذي كان في رومية وبعث إليه بولس تحياته ( رو 16: 13 ) لكن ليس هنالك دليل كاف لتأييد هذا الرأي.'),
(1618, 'رَوْضَة', 'عشب أخضر ( تك 41: 2 ).'),
(1619, 'رِواق', 'فناء في الجانب الغربي لدار الهيكل الخارجية ( 1 أخبار 26: 18 ) وكان يحتوي على غرف للموظفين وزرائب للماشية ( 2 مل 23: 11 ) وقد علّم الرب يسوع في رواق سليمان في الهيكل ( يو 10: 23 ).'),
(1620, 'رُواقيُّون', 'شيعة من الفلاسفة اليونان سميت كذلك نسبة إلى الرواق الذي كان يعلم فيه زينو مؤسس هذا المذهب الفلسفي وكان زينو هذا من كتّيم أو قبرس وقد التقى بولس مع جماعة الرواقيين وكذلك مع الأبيقورين في أثينا ( اع 17: 18 ) وقد ولد زينو مؤسس هذا المذهب في قبرس حوالي عام 336 ق.م. ثم انتقل من مسقط رأسه إلى أثينا وبقي هناك يعلم الفلسفة مدة 58 عاماً في رواق في السوق العام. وكانت فلسفته بوجه عام (( بنثيانية )) أي تعتقد بألوهية الكائنات. وقد اعتبر الرواقيون أنفسهم من ضمن أتباع سقراط وتمثلوا به في نظريته عن الحياة. وقد فرقوا تفريقاً بيناً واضحاً بين ما هو خير أدبياً وبين ما هو موافق أو لائق فأعلنوا أن العمل يكون خيراً أو شراً في ذاته وأنه لا ينبغي أن يكون السرور الغاية من أي عمل ما. وأسمى خير هو الفضيلة. وكانت الفضيلة في عرفهم في العيشة بحسب الفطرة والتشبه بالطبيعة وموافقة السلوك الإنساني لقوانين الكون وموافقة الإرادة الإنسانية للإرادة الإلهية موافقة تصل إلى حد الإستسلام التام للقضاء والقدر.وأعظم الفضائل في عرفهم هي الحكمة العملية بالنسبة إلى ما هو خير أو شر, والشجاعة والفطنة وضبط النفس والعدل. وقد شجع زينو أتباعه من أن يضبطوا مشاعرهم ضبطاً محكماً حتى يصبحوا بقدر المستطاع مستقلين تمام الإستقلال عن كل المؤثرات المقلقة مهما كان نوعها وبالرغم عما يمكن أن يحدث.وقد بقيت الرواقية قوة فعّالة مدة أربعمئة عام وكان أعظم أساتذتها أبكتيتس الذي كان عبداً, وسنسكا الفيلسوف, والإمبراطور ماركوس أوريليوس.'),
(1621, 'رُومة', 'اسم عبري معناه (( ارتفاع أو مكان مرتفع )) موطن لجد يهوياقيم ( 2 مل 23: 36 ) وربما كانت هي خربة رومة التي تبعد مسافة ستة أميال شمال الناصرة.'),
(1622, 'رُومَمْتي عَزر', 'اسم عبري معناه (( عظمت مساعدته )) وهو ابن هيمان ورئيس الفرقة الرابعة والعشرين من المرنمين أيام داود ( 1 أخبار 25: 4 و 31 ).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1623, 'رُومية', 'أسسها سنة 753 ق.م. روميولس الذي صار أول ملك لها. وقد بناها على أكمة واحدة من الأكام السبع هناك, ومع الزمن امتدت فشغلت كل الأكام. وإذ ازداد نفوذ هذه المملكة الصغيرة الناشئة - رومية - شيئاً فشيئاً صارت فيما بعد امبراطورية. ثم استولت على حو	ض البحر الأبيض المتوسط كله. فكانت رومية - عاصمة الإمبراطورية الرومانية, قبلة أنظار العالم كله, وملتقى ساسة العالم وقادته. ومن رومية انتشرت العلوم والآداب والفلسفة, ولا يزال القانون الروماني يدرّس في كل أرجاء العالم إلى الآن. لكن بالرغم من كل هذا فقد كانت المدينة غارقة في أرجاس العبادة الوثنية وقبائحها واستولت عليها الخزعبلات - الأمر الذي يتضح مما ورد في الإصحاح الأول من الرسالة إلى أهل رومية.\nوعندما ولد المسيح كان بالمدينة كثيرون من اليهود الذين تشتتوا فيما بعد في كل أرجاء العالم.ولا يعلم يقيناً الوقت الذي فيه دخلتها المسيحية ولا على يد مَنْ دخلتها. لكن الأرجح أنه تمّ على يد اليهود أهل رومية الذين كانوا في أورشليم يوم الخمسين ( اع 2: 10 ) إذ امتلأوا من الروح القدس وعادوا إلى رومية بدأوا يذيعون الإنجيل فيها. وقد احتدمت المناقشات - ولا تزال - فيما إذا كان بطرس هو أول من نادى بالإنجيل في رومية. فالكنيسة البابوية تنادي بهذا الرأي, وتخالفها فيه أغلب الكنائس الأخرى.ولأن رومية كانت أهم مدينة في العالم فقد اشتهى بولس أن يذهب إليها للمناداة فيها بالإنجيل.لكن حالت عوائق كثيرة دون تحقيق أمنيته هذه (( إنني مراراً كثيرة قصدت أن آتي إليكم ومنعت حتى الآن )) ( رو 1: 13 ). لكن الله رتب له أن يذهب إليها لكي يحاكم أمام قيصر على أساس أن هذا كان حقاً يعطى لكل روماني. وكان بولس قد اكتسب الرعوية الرومانية بالمولد ( اع 22: 25 - 28 و 25: 11 و 12 ). وبناء على رغبته أُرسل إلى رومية فوصل إليها فعلاً وأقام فيها سنتين ( اع 28: 16 و 30 ).وفي سنة 64 م قام الإمبراطور نيرون وأثار اضطهاداً شديداً على مسيحيي رومية, ثم قام من بعده دومتيانوس وجدد الاضطهاد سنة 81 ب.م. وخلفه تراجانوس حيث أثار عليهم اضطهاداً أشد بين سنة 97 وسنة 117.وفي رومية استشهد كل من بطرس وبولس حوالي سنة 68 م. الأول بصلبه منكس الرأس, والثاني بقطع رأسه.أما شهرة رومية الحديثة فلا تنحصر في عظمتها الحالية فحسب بل أيضاً فيما تذخر به من الآثار القديمة النفيسة والكنائس الكثيرة, وأهمها كنيسة القديس بطرس التي تعتبر من أفخم كنائس العالم وأكثرها اتساعاً. وبها أيضاً الفاتيكان مقر البابا رئيس الكنيسة الكاثوليكية.رسالة بولس إلى أهل رومية:هي السفر السادس من أسفار العهد الجديد, وقد كتبها بولس سنة 58 م. من مدينة كورنثوس قبيل مغادرتها ليذهب إلى أورشليم حاملاً معه المساعدة المالية التي جمعها للقديسين والفقراء فيها ( رو 15: 25 و26 ). وأُرسلت على يد فيبي خادمه ( أو شماسة ) الكنيسة التي في كنخريا من أعمال كورنثوس ( رو 16: 1 ). ظل بولس الرسول سنوات كثيرة مشتاقاً إلى زيارة رومية ( ص 1: 10 - 12 و 15: 23 ) وإذ انتهت خدمته في الشرق قصد أن يزورها في طريقه إلى إسبانيا بعد تقديم المساعدة المالية لأهل أورشليم ( 15: 28 ). وقد أرسل هذه الرسالة إلى مسيحيي رومية حيث كان له هناك أصدقاء كثيرون إذ اعتقد أن كنيسة رومية تدخل ضمن دائرة خدمته كرسول للأمم ( انظر ص 15: 15 و16 ).أما السبب في تقديم رسالة رومية على سائر رسائل بولس فيرجع إلى عدة أسباب:1- إنها أكثرها بلاغة وعباراتها منطقية.2- سمو تعاليمها.3- كثرة إصحاحاتها.4- عظمة المدينة التي كتبت إليها.ويقال أن يوحنا فم الذهب كان يأمر بأن تقرأ هذه الرسالة مرتين في الأسبوع.وأما موضوع الرسالة فيلخص في إحدى العبارات الواردة فيها (( إنجيل المسيح قوة الله للخلاص لكل من يؤمن.... لأن فيه معلن بر الله بإيمان لإيمان )) ( ص 1: 16 و 17 ). وقد اتبع الرسول في هذه الرسالة - كما في أغلب رسائله - طريقته الخاصة. وهي أن الجزء الأول تعليمي ( ص 1 - 11 ) والجزء الأخير عملي ( ص 12 - 16 ).أولاً: ويعلمنا الجزء التعليمي عن:طريق الخلاص ( 1 ) فأساسه مبني على التبرير لا بأعمال الأمم التي تمليها عليهم الطبيعة ( ص 1 ) ولا بأعمال اليهود التي بالناموس ( ص 2 و 3 ) لكن فقط بالإيمان بيسوع المسيح ( ص 3: 21 إلخ وص 4 ).( 2 )ودرجات ذلك الخلاص هي: السلام مع الله ( ص 5 ) التقديس ( ص 6 و 7 ) التمجيد ( ص 8 ).( 3 ) الأشخاص الذين يخلصون: هم الأمم واليهود ( ص 10 و 11 ) وذلك يتوقف على اختيار النعمة ( ص 9 ).ثانياً: الجزء العملي وفيه نجد ( 1 ) تعاليم وإرشادات منوعة تفيد جميع المسيحيين ( ص 12 ) ( 2 ) إرشادات تفيدنا كأعضاء في هيئة مدنية ( ص 13 ) و( 3 ) مباديء وقوانين تفيد المسيحيين في معاملتهم بعضهم بعضاً كأعضاء في الكنيسة المسيحية ( ص 14 و 15: 1 - 14 ).( ختام الرسالة ) وفيه نراه يعتذر لكتابته لهم ( ص 15: 14 - 16 ) ويعطيهم ملخصاً عن نفسه وعن عمله ( ع 17 - 21 ) ويعدهم بزيارتهم ( ع 22 - 29 ) ويرجوهم أن يصلوا لأجله ( ع 30 - 33 ) ويهدي سلامه لأشخاص عديدين ( ص 16: 1 - 16 )ويحذرهم من الذين يسببون الشقاقات ( ع 17 - 20 ) ويبلغ سلام أصدقائه لهم أيضاً ( ع 21 - 23 ) وأخيراً يختم الرسالة بطلب البركة الرسولية لهم وتمجيد الله ( ع 24 - 27 ).المملكة الرومانية:بدأ تأسيس مدينة رومية عام 753 ق.م. صارت هذه المملكة الصغيرة تزداد اتساعاً وأهمية وتستولي على ما جاورها من المدن إلى أن صارت جمهورية. وصار عامة الشعب يطالبون بامتياز إلى أن أصبح لكل روماني صوت في الحكومة. وفي مدة الجمهورية وسعت رومية حدودها حتى شملت أولاً كل إيطاليا, وأخيراً كل حوض البحر الأبيض المتوسط.حدث احتكاك رومية بآسيا أولاً سنة 190 ق.م. حين هزم الجيش الروماني أنتيوخس الكبير ملك سورية, ثم مدت رومية نفوذها في آسيا الصغرى. وفي سنة 63 ق.م. خضعت اليهودية لرومية ولكن سمح لها بأن تعين ولاة من بنيها.وفي سنة 31 ق.م. تولى الحكم الإمبراطور أوكتافيون ( أوكتافيوس ) وأطلق على نفسه لقب أوغسطس وهكذا بدأت الإمبراطورية الرومانية.وفي أثناء حكم أوغسطس ولد المسيح, وفي أثناء حكم خلفه طيباريوس تمّ الصلب.وفي أثناء حكم كلوديوس قتل يعقوب أخو يوحنا ( اع 11: 28 و 12: 1 و 2 ). وإلى نيرون رفع بولس دعواه ( اع 25: 11 ). وهو الذي قتل بولس بقطع رأسه وبطرس بصلبه. أما خراب أورشليم الذي تنبأ عنه الرب ( مت 24 ومر 13 ولو 19 و 21 ) فقد تم في سنة 70 م. على يد تيطس الذي صار فيما بعد إمبراطوراً.وفي أيام أوغسطس كانت حدود الإمبراطورية نهر الفرات شرقاً والمحيط الأطلنطكي غرباً, والصحراء الإفريقية جنوباً, وبحر الشمال ونهري الرين والدانوب شمالاً. وفي أيام كلوديوس تمّ غزو جزء من بريطانيا. وفي أيام تراجان اتسعت الإمبراطورية إلى ما بعد نهر الفرات, وهكذا صارت الإمبراطورية تزداد اتساعاً إلى أن شملت معظم العالم المتمدن. وإذ بدأ سوس الفساد ينخر في عظام الإمبراطورية في الداخل بدأت تضعف وبالتالي بدأ سقوطها. كان آخر إمبراطور يملك على كل الإمبراطورية ثيودوسيوس ( 379 - 395 ). وعند موته انقسمت الإمبراطورية بين ابنيه ولم تعد متحدة فيما بعد.وقد دأب الأباطرة على اضطهاد المسيحية منذ بدايتها وكان أشدهم عنفاً نيرون ودومتيانوس ودقلديانوس. لكن بالرغم من هذا الاضطهاد كانت المسيحية تزداد قوة وانتشاراً. وفي أيام قسطنطين في أوائل القرن الرابع صضارت المسيحية الديانة الرسمية للإمبراطورية.وكانت سلطة الإمبراطورية الرومانية وسيلة لانتشار المسيحية إذ قضت على القرصنة وقطاع الطرق, ومهدت طرقاً كثيرة بين الممالك المختلفة, ووضعت قوانين للحكم نافعة لا يزال باقياً منها القانون الروماني, وازدهرت التجارة, وانتشرت اللغة اللاتينية في الغرب واللغة اليونانية في الشرق. وهكذا تيسر انتشار المعارف والتبشير. وفي أيام الرسل بشرت أكثر الولايات الرومانية.'),
(1624, 'رِيباي', 'اسم عبري معناه ((  يهوه يجاهد )) بنياميني من جبعة وأبو إِتّاي أحد أبطال داود ( 2 صم 23: 29 و أخبار 11: 31 ).'),
(1625, 'رِيح', 'كان العبرانيون يعتقدون أن هنالك أربعة أنواع من الريح: الريح الشرقية والريح الغربية والريح الشمالية والريح الجنوبية ( ار 49: 36 وحز 37: 9 ورؤ 7: 1 ). والله هو الذي خلق ( عا 4: 13 ) وهي خاضعة لأمره وتطيعه وتتمم إرادته ( اي 28: 25 ومز 78: 26 و 107: 25 و 135: 7 و 148: 8 ومت 8 : 26 ).كانت الريح التي تهب من الغرب والجنوب الغربي والشمال الغربي تحمل الأمطار إلى فلسطين. وتقترن بالعواصف ( 1 مل 18: 43 - 45 ومز 147: 18 وحز 13: 13 ).وكثيراً ما دمرت الريح المنازل والسفن ( اي 1: 19 ومز 48: 7 ومت 7: 27 ). أما الريح اللافحة فكانت تجفف الأنهار وتلفح المزروعات ( تك 41: 6 واش 11: 15 وحز 19: 12 ويون 4: 8 ).والرياح الجنوبية, والجنوبية الشرقية التي تعبر صحراء العرب جافة وحارة ( اي 37: 17 ولو 12: 55 ). والريح الشمالية لطيفة.وينتفع بالريح في درس القمح والحبوب لتنقيتهامن التبن ( اي 21: 18 ). وينتفع الملاحون أيضاً بالريح لقيادة سفنهم ( اع 27: 40 ). وقدشبه السيد المسيح عمل الروح القدس بهبوب الريح ( يو 3: 8 ) ( انظر (( أوركليدون )). (( مطر )). (( أوقات )). (( زوبعة )) ).'),
(1626, 'رِيسا', 'ربما كان اسماً آرامياً معناه (( الرأس )) أحد أسلاف زربابل, وقد ورد ذكره ضمن سلسلة نسب المسيح ( لو 3: 27 ).'),
(1627, 'رِيعى', 'اسم عبري معناه (( ودود )) وهو رجل بقي موالياً لداود عندما تمرد أدونيا محاولاً اغتصاب العرش من أخيه سليمان ( 1 مل 1: 8 ).'),
(1628, 'رِيعْيوُن', 'مدينة من أصل يوناني على ساحل إيطاليا مواجهة لميسينا في صقلية. حطت سفينة بولس رحالها فيها يوماً واحداً إذ كان في طريقه إلى رومية ( اع 28: 13 ). وهي تدعى الآن ريجيو.'),
(1629, 'رِيفاث', 'شعب من نسل جومر ( تك 10: 3 و1 أخبار 1: 6 ). ويقرر يوسيفوس أنهم هم البفلجونيون. ولعل جبال الريفيين تنتسب إليهم, وكان يعتقد بأن هذه الجبال هي حدود العالم في الشاطيء الشمالي.'),
(1630, 'رَيكة', 'مكان في نصيب سبط يهوذا ( 1 أخبار 4: 12 ).'),
(1631, 'راية', 'تنوب هذه الكلمة في الترجمة العربية عن كلمتين في الأصل العبري, إحداهما (( نيس)) بمعنى سارية على رأسها مشعل ( مز 60: 4 واش 18: 3 و 62: 10 وار 4: 6 و 21 و 15: 12 و 27 ), والأخرى (( دِجِل )) بمعنى العلم أو اللواء المصنوع من قماش والمصور عليه بعض الصور ( عد 1: 52 ومز 20: 5 ونش 2: 4 و 6: 4 ).\nقال بعض علماء اليهود أنه على راية يهوذا رسمت صورة أسد, وعلى راية رأوبين صورة رجل, وعلى راية أفرايم صورة ثور, وعلى راية دان صورة نسر ( انظر (( رجسة))  ).'),
(1632, 'زاباد', 'اسم عبري معناه (( الله قد وهب )) أو (( هبة )).( 1 ) ابن لناثان بن عَتَّاي بن العبد المصري يرحع ( 1 أخبار 2: 34 - 36 ). ويظن أنه كان أحد أبطال داود ( 1 أخبار 11: 41 ).( 2 ) أفرايمي قتله رجال جت عند محاولته سرقة ماشيتهم ( 1 أخبار 7: 21 ).( 3 )ابن شمعة العمونية وأحد الفاتنين والقاتلين للملك يوآش, انتقاماً لدماء زكريا بن يهوياداع الكاهن ( 2 أخبار 24: 22 و 25 و 26 ) ويدعى أيضاً يوزاكار ( 2 مل 12: 21 ).( 4 ) اسم ثلاثة رجال من الذين اتخذوا لأنفسهم زوجات غريبات ( عز 10: 27 و 33 و 43 ).'),
(1633, 'زابُود', 'اسم عبري معناه (( موهوب )), وهو ابن ناثان النبي, وكان زابود كاهناً في عهد سليمان كما كان صديقاً له ( 1 مل 4: 5 ).'),
(1634, 'زَارَح', 'اسم عبري معناه (( بزوغ النور )), وقد تسمى بهذا الاسم عدد من رجال العهد القديم:( 1 ) أدومي من سلالة اسماعيل وحفيد عيسو وابن رعوئيل ( تك 36: 3 و 4 و 13 و 17 و 33 و 1 أخبار 1: 37 و 44 ).( 2 ) أحد التوأمين اللذين ولدتهما ثامار ليهوذا حميها, ومؤسس عشيرة الزارحيين في سبط يهوذا ( عد 26: 20 ) وإلى هذه العشيرة انتمى عخان بن كرمى ( يش 7: 1 و 17 و 18 و 24 ) كما انتمى إليها اثنان من القادة في جيش داود ( 1 أخبار 27: 11 و 13 ).( 3 ) واحد من بني شمعون, مؤسس عشيرة الزارحيين في سبط شمعون ( عد 26: 12 و 13 و 1 أخبار 4: 24 ) ويدعى أيضاً صوحر ( تك 46: 10 وخر 6: 15 ).( 4 ) لاوي من بني جرشوم ( 1 أخبار 6: 21 و 41 ).( 5 ) ملك كوشي خرج بجيش جرار بلغ مليون رجل لمحاربة يهوذا في أيام آسا الملك, وانهزم في مريشة في وادي صفاتة ( 2 أخبار 14: 9 ) ويظن بعضهم أنه (( أوسوركون الأول أو الثاني))من الأسرة الثانية والعشرين التي حكمت مصر. كما يعتقد آخرون أن اللفظ (( كوشي))يرجع إلى أصل عربي فيكون الملك زارح وجيشه من الأعراب بحسب هذا الاعتقاد.'),
(1635, 'زَارَحيُّون', 'هم ذرية زارح بن يهوذا ( عد 26: 20 ويش 7: 17 و 1 أخبار 27: 11 و 13 ).وكانت هناك عشيرة أخرى من الزارحيين في سبط شمعون ( عد 26: 12 و 13 و 1 أخبار 4: 24 ).'),
(1636, 'زَارَد', 'اسم عبري معناه (( ازدهار )), وهو جدول ماء يخرج من جبل عباريم ويصب في بحر لوط ( البحر الميت ) في الجزء الجنوبي الشرقي منه. وهو الحد الطبيعي بين أدوم وموآب, وكان من آخر العقبات في طريق العبرانيين من مصر إلى كنعان ( تث 2: 13 وعد 21: 12 ), وهو معروف في هذه الأيام بوادي الحصى.'),
(1637, 'زَازَا', 'اسم عبراني ربما كان معناه (( تحرك)) ابن يوناثان وهو من نسل يهوذا ( 1 أخبار 2: 32 ).'),
(1638, 'زَاكِر', 'اسم عبري معناه (( تذكار))رجل من بني بنيامين ( 1 أخبار 8: 31 ) ويدعى أيضاً زكريا ( 1 أخبار 9: 37 ).'),
(1639, 'زَايِن', 'هو الحرف السابع في الأبجدية العبرية ويشبه الخنجر في رسمه, ويرمز إلى سبعة في الحساب, ويقابله في العربية حرفان هما (( ذ))و (( ز )), وحرف الزاين مكتوب على رأس القسم السابع في مزمور 119 لأن كل عدد في هذا القسم يبدأ بهذا الحرف في العبرية.'),
(1640, 'زَانُوح', 'اسم عبري معناه (( مستنقع))أو (( أجمة))وقد أطلق على مدينتين في يهوذا: إحداهما في السهل ( يش 15: 34 ). وقد سكنت بعد السبي ( نح 11: 30 ) واشترك سكانها في ترميم أبواب أورشليم ( نح 3: 13 ). والمدينة الثانية في الجبال في يهوذا ( يش 15: 56 ).'),
(1641, 'زَاهَم', 'اسم عبري معناه (( كراهة )), وكان أحد أبناء رحبعام ( 2 أخبار 11: 19 ).'),
(1642, 'زَبَاي', 'اسم عبري معناه (( نقى أو المتنقل من مكان لآخر)) وهو اسم لرجل اتخذ امرأة غريبة ( عز 10: 28 ) وربما كان هو نفسه الشخص الذي يذكره نحميا ( 3: 20 ) على أنه ابن باروخ كان من مرممي سور أورشليم.'),
(1643, 'زَبيب', 'ثمار الكرمة بعدما تجفف, ولما كان العنب يزرع بكثرة في أراضي فلسطين لذلك كان الزبيب كثير الاستعمال. وقد ورد ذكره في الكتاب كهدايا مقبولة ( 1 صم 25: 18 و 2 صم 16: 1 و 1 أخبار 12: 40 ) أو كطعام منعش ولذيذ ( 1 صم 30: 12 ) أو كإحدى مواد العبادة الوثنية المكروهة مقدماً في شكل أقراص ( هو 3: 1 ) وربما يكون كلام إرميا مشيراً إليه في 7: 18 و 44: 19.'),
(1644, 'زَبُّود', 'اسم عبري معناه (( معطى من الله )), رئيس عائلة رجعت مع عزرا من بابل ( عز 8: 14 ).'),
(1645, 'زَنْبِيل', 'تصحيف زبيل وهو نوع من السلال الكبيرة ( 2 كو 11: 33 ). والكلمة اليونانية الأصلية تفيد معنى الشيء المضفور, ولعلها كانت مصنوعة من الحبال لتتحمل ثقل رجل, وتاريخ هذه الصناعة يُظهر أنواعاً مختلفة من السلال الصغيرة والكبيرة, بعضها مصنوع من ليف النخل أو سعفه, أو من الحَلفاء أو من أعواد الخيزران, أو من الحبال. وتستعمل في نواح شتى كحمل الفواكه والخبز واللحم والسمك ولوازم البناء. وقد اعتاد الفلاح اليهودي أن يضع تقدماته وعشوره من الزرع في نوع من مثل هذه السلال إلى حين صعوده إلى الهيكل ( اطلب (( سل))- (( قفة))  ).'),
(1646, 'زَبَح', 'اسم عبري معناه (( ذبيحة))أحد ملكي مديان اللذين أمسكهما جدعون وقتلهما ( قض 8: 5 - 21 ومز 83: 11 ).'),
(1647, 'زُبدة', 'هي المادة الدسمة التي تستخرج من اللبن وتسمى بالقشدة ( القشطة ) ( تك 18: 8 وقض 5: 25 ومز 55: 21 واش 7: 15 ). وظن بعضهم أن كلمة زبدة كانت تطلق أحياناً على اللبن الخاثر.'),
(1648, 'زَبَدِي', 'اسم عبري معناه (( يهوه قد أعطى))( مر 1: 19 و 20 ) يُظن أنه زوج سالومة وأب لأثنين من الرسل. ولم يكن عاملاً كأحد الفعلة الذين يحملون طعام كل يوم بيومه بل كان أرفع مرتبة من ذلك, إذ كان معه أجرى في سفينته على ما هو مذكور صريحاً عند عودة ربنا لابنيه يعقوب ويوحنا ليكونا في جملة رسله. وقد كانت إمرأته تنفق من مالها على السيد لسد احتياجاته كما ذكر عنها أكثر من مرة.'),
(1650, 'زَبَدْيا', 'اسم عبري معناه (( يهوه قد أعطى )), وهو اسم أطلق على تسعة أشخاص في العهد القديم كانوا قليلي الأهمية.( 1 و 2 و 3 ) من بني بنيامين وهم ابن بريعة وابن الفعل ( 1 أخبار 8: 15 - 17 ) وابن يروحام وهو أحد الأبطال الذين جاءوا إلى داود ( 1 أخبار 12: 7 ).( 4 ) لاوي من بني قورح ( 1 أخبار 26: 2 ) وهو أحد (( حراس الأبواب )).( 5 ) ابن عسائيل أخي يوآب ( 1 أخبار 27: 7 ).( 6 و 7 ) رجلان عيّنهما الملك يهوشافاط لتعليم الشريعة الإلهية وتنفيذها في مدن يهوذا. فكان أحدهما لاوياً معلّماً ( 2 أخبار 17: 8 ) وكان الثاني قاضياً وابن يشمعئيل رئيس بيت يهوذا ( 2 أخبار 19: 11 ).( 8 و 9 ) رجلان جاء ذكرهما مع عزرا, كان أحدهما ابناً لشفطيا رجع مع عزرا على رأس ثمانين شخصاً من عائلته ( عز 8: 8 ) وكان الثاني كاهناً اتخذ إمرأة غريبة بعد الرجوع من بابل ( عز 10: 20 ).'),
(1651, 'زَبْدِيئِيل', 'اسم عبري معناه (( الله أعطى )), اسم لثلاثة رجال:( 1 ) والد يشبعام رئيس الحراس في الفرقة الأولى عند داود ( 1 أخبار 27: 2 ).( 2 ) ابن هجدوليم وكان كاهناً شهيراً في أورشليم بعد الرجوع من السبي ( نح 11: 14 ).( 3 ) أمير عربي في سنة 145 ق.م. اغتال الإسكندر بالاس ملك سوريا ( سفر المكابين الأول 11: 17 ).'),
(1652, 'زَبَرْجَد', 'نوع من الحجارة الكريمة الشديدة الصلابة ذات اللون الأخضر الفاتح, وقد كانت الجوهرة العاشرة على صدرة رئيس الكهنة من الزبرجد ( خر 28: 20 ). كذلك يحدثنا الرائي في سفر الرؤيا عن الزبرجد كأحد الحجارة الكريمة التي زينت أسوار أورشليم الجديدة( رؤ 21: 20 ) كما ذكرت في مواضع أخرى في الكتاب ( نش 5: 14 وحز 1: 16 و 10: 9 و 28: 13 ودا 10: 6 ). والكلمة اليونانية لزبرجد هي خرسوليثوس.'),
(1653, 'زَبُول', 'اسم عبري معناه (( مسكن أو منزل))وهو اسم رجل كان وكيلاً لأبيمالك في مدينة شكيم مدة حروبه مع الكنعانيين ( قض 9: 28 - 41 ).'),
(1654, 'زَبُولُون', 'اسم عبري معناه (( سكن, إقامة )).( 1 ) هو الابن السادس لليئة والعاشر ليعقوب وعند ولادته ظنت أمه أن رجلها سوف يساكنها فدعته زبولون ( تك 30: 20 ).( 2 ) هو السبط الذي كان زبولون مؤسساً له, وقد تكونت عشائر هذا السبط واتسعت من أولاد زبولون الثلاثة وهم سارد وإيلون وياحلئيل ( عد 26: 26 ). وعندما أحصي الشعب في المرة الأولى بلغ عدد هذا السبط 57400 ( عد 1: 30 و 31 ). وفي المرة الثانية بلغ 60500 ( عد 26: 27 ). وكان ممثل هذا السبط أحد الستة الواقفين على جبل عيبال عند        كنعان لأجل النطق باللعنات على العصاة والأشرار بالنسبة للوصية الإلهية ( تث 27: 13 - 26 ). وقد كوّن جنود هذا السبط مع جنود سبط نفتالي الجانب المهم في جيش باراق ودبورة في حربهم ضد قوات سيسرا ( قض 4: 6 - 10 و 5: 14 و 18 ) كما كونوا جزءاً من جيش جدعون في حربه ضد مديان ( قض 6: 35 ). من هذا السبط قام قاض حكم على إسرائيل عشر سنين اسمه إيلون الزبولوني ( قض 12: 11 و 12 ). وقد اشتركت قواتهم الحربية وعددها 50000 رجل مع سائر قوات بني إسرائيل في تتويج داود ملكاً على إسرائيل في حبرون ( 1 أخبار 12: 33 - 40 ). وبالنسبة لوقوع أرض هذا السبط بعيداً عن المراكز المهمة فلم تكن لهم يد في أكثر الأحداث الكبرى في تاريخ اليهود. كذلك قصروا في طرد سكان الأرض الأصليين الوثنيين بل خالطوهم وصاهروهم ضد الوصية الإلهية كما أنهم عبدوا الأوثان سريعاً ( قض 1: 30 ), وشتموا رسل حزقيا ( 2 أخبار 30: 10 - 18 ) لذلك أعلن غضب الله عليهم مبكراً في مجيء تغلث فلاسر وتعذيبه إياهم ثم أخذهم لهم أسرى ( 2 مل 15: 29 ). وقد رأى حزقيال في رؤياه أحد أبواب أورشليم الجديدة وقد تسمى باسم زبولون ( حز 48: 33 ).( 3 ) الأرض التي كانت من نصيب هذا السبط في أرض كنعان. وقد أشار إليه أبوه بروح النبوة قبل أن يكون فعلاً بزمان طويل-وهو عبارة عن المكان الواقع في شمال فلسطين وإلى الغرب من بحر الجليل ( تك 49: 13 ). وفي داخل حدود زبولون وُجدت مدينة صغيرة باسم بيت لحم. وقد دعيت بيت لحم أرض زبولون لتميزها عن بيت لحم التي في حدود يهوذا. وكذلك في حدودها جبل تابور الذي ظن بعضهم أنه جبل التجلي.'),
(1655, 'زّبِيدة', 'اسم عبري معناه (( ممنوح ))، وهي امرأة يوشيا وأم يهوياقم الملك ( 2 مل 23: 36 ).'),
(1656, 'زَتُّو', 'هو الذي رجع بنوه مع زربابل من السبي مكونين عشيرة ذات قيمة ( عز 2: 8 ونح 7: 13 ). وقد اتخذ بعضهم نساء غريبات لأنفسهم ( عز 10: 27 ) ( نح 10: 14 ). وممثل هذه العشيرة كان من ضمن الذين ختموا العهد.'),
(1657, 'زُجاج', 'كانت صناعة الزجاج معروفة عند القدماء على الأخص كما يظهر هذا في الاكتشافات المتنوعة للقطع الزجاجية والأواني المختلفة المكتوب عليها أسماء بعض الفراعنة والتي يرجع بعضها إلى سنة 2000ق.م . أو أكثر. وبسبب الاتصالات الوثيقة بين مصر وسوريا في تلك الأيام نقل الفينيقيون هذه الصناعة واشتهروا بها.وقد أشاع بلينيوس المؤرخ أن صناعة الزجاج بدأت صدفة في مكان بالقرب من حيفا، إذ أن مركباً محملاً بالنطرون رسى في البحر بقرب هذا الموضع ونزل البحارة ليطبخوا غذائهم، ولما لم يجدوا حجارة يسندون عليها آنيتهم أسندوها على كتل من النطرون فأذابته النار واختلط بالرمل مكوناً الزجاج. إلا أن هذا الزعم مشكوكاً فيه نظراً لقدم هذه الصناعة التي وجدت آثارها في مصر.أما عن ورود ذكر الزجاج في الكتاب المقدس فقد ذُكر قليلاً مع أنه كان معروفاً لليهود ( اي 28: 17 و 18 ورؤ 4: 6 و 15: 2 و 21: 18 ). ويظن أن الزجاج المقصود في أغلب هذه الشواهد هو الزجاج الطبيعي المسمى البلور الذي كان يوجد في الصخور. كما يظن بعض الباحثين أن الكأس المذكورة في ام 23: 31 والزق المذكورة في مز 56: 8 صنعتا غالباً من الزجاج. ومن الطريف أن توجد أواني زجاجية بكميات ضخمة في بعض المقابر القديمة في بلاد العبرانيين. ولعلها تشير إلى كميات الدموع التي سكبت على الراحلين وحفظت ضمن هذه الأواني في قبورهم حسب العادة دلالة على قمية أولئك الراحلين وعلى شدة الفجيعة فيهم.وقد صنعت حلى كثيرة للنساء من الزجاج. ولكن من الخطأ أن نظن أن المرايا كانت تصنع قديماً من أنها كانت من المعادن فقط. مثل النحاس أو الفضة أو البرونز بعد صقلها وتلميعها جيداً.'),
(1658, 'الزاحفة', 'اسم لفظة في العبرية زوحلث ومعناه (( زاحف )) كالثعبان أو غيره. وهو اسم حجر قرب عين روجل جنوب غربي أورشليم، أقام ادونيا حوله وليمة يوم تتويجه عندما تمرد على أبيه ( 1 مل 1: 9 ).\nولعله حجر قدسه الشعب قديماً لوجوده قرب عين ماء، وذلك حسب التقاليد الكنعانية في تلك الأيام فقد كانوا يرون في عيون الماء مصادر للحياة قدسوها كأنّ إلهاً حال فيها.'),
(1659, 'زَرُبَّابِل', 'اسم اكادي معناه (( زرع بابل))أو (( المولود في بابل ))، ابن شالتيئيل ( عز 3: 2 و 8 ونح 12: 1 وحج 1: 1 و 12 و 14 و 2: 2 و 23 ومت 1: 12 و 13 ولو 3: 27 ). ونستطيع أن نفهم ما ورد في 1 أخبار 3: 17-19 أن شالتيئيل مات بدون ذرية. ولعل فدايا أخو شالتيئيل تزوج بامرأته وأقام نسلاً لأخيه حسب الناموس ( تث 25: 5 و 6 )، فصار زربابل ابناً لشالتيئيل... ورجع اليهود من بابل إلى اليهودية في أول دفعة تحت قيادته ( عز 2: 2 ) واشترك زربابل مع يشوع بن يوصاداق وأخوته الكهنة في بناء المذبح لاصعاد المحرقات وتنظيم العبادة ( عز 3: 1-9 ) وهو من بيت داود ( مت 1: 12 ولو 3: 13 )، وقد تسلم من كورش الآنية المقدسة التي ردت إلى أورشليم ثم أنه أقيم والياً ووضع أساس الهيكل ( زك 4: 6-10 )، وكانت له اليد الطولى في إرجاع الطقوس الدينية الاعتيادية للشعب، وكان محباً لشعبه وسعى في إقامة البناء المقدس ثانية. حيث كان الشعب يعبدون إله آبائهم، فعرف الهيكل باسم زربابل، وقد أكمل البناء في سنة 515 ق.م. وظل قائماً حتى سنة 20 ق.م. عندما بدأ هيرودس الأكبر مشروعه لبناء الهيكل الجديد. وقد كانت حماسة زربابل للبناء داعية للنبي حجي أن يرى فيه شخصية المسيا المنتظر ( حج 2: 20-23 ) ويرجح أن اسم شيشبصّر اسم آخر لزربابل ( عز 1: 8 و 11 ) ( انظر (( هيكل )) ).'),
(1660, 'زَرَحْيا', 'اسم عبري معناه (( الرب اشرق )) وهو:( 1 ) كاهن من نسل العازر ( 1 أخبار 6: 6 و51 وعز 7: 4 ).( 2 ) رجل رجع بنوه من بابل مع عزرا ( عز 8: 4 ).'),
(1661, 'زَرَش', 'اسم من اصل فارسي، وربما كان معناه (( الشخص العابس المكدّر، أو صاحب الشعر المشعث، أو الشخص الطافر فرحاً ))، وهي زوجة هامان ( اش 5: 10 و 14 و 6: 13 ).'),
(1662, 'زَرَع', 'زارع ( مت 13: 3 ) ( انظر (( فلاحة ))، زرع ( تك 8: 22 ) ( انظر (( وقت )) ).'),
(1663, 'مِزْراق', '( انظر (( سلاح )) ).'),
(1664, 'زَعْوَان', 'اسم عبري معناه (( مضطرب وغير هادئ ))، وهو اسم لأحد أبناء إِيصر من أمراء الحوريين ( تك 36: 27 و 1 أخبار 1: 42 ).'),
(1665, 'زِفت', 'ليس في الامكان تحديد المادة التي قصدها الكتاب في العهد القديم، إذ نجد عدة أسماء مستعملة وهي زفت، حمر، قار، وفي الترجمة السبعينية (( إسفلت )). ويظن الباحثون أنها مادة معدنية يغلب عليها اللون الأسود، تخرج من بطن الأرض قرب بابل وتوجد بكميات كبيرة غرب البحر الميت، أو خارجه من قاعه، كما توجد لها مناجم هامة في حاصبيا قرب جبل حرمون كما أنها توجد في شمال سوريا.\nولعل من الممكن معرفة شيء عن طبيعته باستعراض استعمالات الاقدمين له، فنوح يستعمله في طلاء الفلك ( تك 6: 14 )،ثم أن أم موسى تستعمله في طلاء السفط ( خر 2: 3 )، أما بنائي برج بابل فاستخدموه كالملاط ( تك 11: 3 ). وقد كان في القديم يصدر إلى مصر ليستخدم عند البابليين قديماً في صناعة الأعمدة المزينة بقطع الأحجار الملونة التي تثبت في الأعمدة بمادة الزفت. وهو على آي حال مادة سريعة الاشتعال كما يذكر في اشعيا 34: 9.'),
(1666, 'زِفْرُون', 'ربما كان اسماً أرامياً معناه (( رائحة ))، وهو اسم مدينة واقعة على التخم الشمالي لأملاك بني إسرائيل ( عد 34: 9 ). ربما تكون هي زعفرانة الوقعة على الطريق حمص وحماة، جنوباً إلى الجنوب الشقي من حماة.'),
(1667, 'زَفَسْ', 'وهو رئيس الآلهة عند اليونان، وقد سماه الرومان جوبتر، وقد كان متسلطاً على جميع الآلهة الوثنية. ولقد هدف الحكام الوثنيون من يونانيين ورومانيين إلى تعميم مبادة زفس في العالم كله فأقاموا معابد وتماثيل له في كل المدن الهامة تقريباً. وعندما أقام انتيخوس الرابع مذبحاً لزفس فوق مذبح المحرقات في الهيكل في أورشليم اشتعل غضب اليهود وبدأوا ثورتهم بقيادة يهوذا المكابي وأخوته، التي انتهت برجوع الحرية إلى اليهود بعد أربعمائة سنة تقريباً، قضوها في السبي وفي العبودية.\nوحدثنا سفر الأعمال 14: 8-18 عن شفاء المقعد في لسترة واعتقاد أهلها ليكأونية أن بولس وبرنابا إنما هما آلهة تشبهت بالناس ونزلت إليهم، وزعموا أن ظهور الرسولين تكراراً لما جاء في خرافاتهم من أن زفس وهرمس افتقدا مقاطعتهم في سالف الأيام ولذلك دعوا برنايا زفس وبولس هرمس إذ كان هو المتقدم في الكلام. والأرجح أن ذلك كان بسبب فصاحته وحسن بيانه، وبناء على زعمهم هذا خرج كاهن زفس ( الإله الحارس للمدينة والذي كان هيكله قدام الأبواب ) بالثيران والأكاليل وكان مزمعاً أن يذبح للرسولين ويسجد لهما هو والشعب لو لم يلح عليه الرسولان أن يرجعوا عن هذه الأباطيل.'),
(1668, 'زِكْرِي', 'اسم عبري معناه (( مذكور )) وهو اسم لكثيرين:( 1 ) ثلاثة من رؤساء بنيامن ( 1 أخبار 4: 19 و 23 و 27 ).( 2 ) لاوي ( 1 أخبار 9: 15 ) ويسمى أيضاً زبدي ( نح 11: 17 ).( 3 ) لاوي آخر ( 1 أخبار 26: 25 ).( 4 ) رأوبيني ( 1 أخبار 27: 16 ).( 5 ) أب لأحد قادة جيش يهوشافاط ( 2 أخيار 17: 16 ).( 6 ) أب لأحد الذين ملكوا يوآش ( 2 أخبار 23: 1 ) وربما كان هو نفس الشخص المذكور في ( 4 ).( 7 ) افرايمي أشتهر في الحرب بين فقح وآحاز ( 2 أخبار 28: 7 ).( 8 ) بنياميني ( نح 11: 9 ).( 9 ) كاهن من الذين عادوا مع زربابل في أيام الكاهن العظيم يوقايم ( نح 12: 17 ).( 10 ) لاوي من عشيرة قهات ( خر 6: 21 ) وقد ورد اسمه في الترجمة العربية المتداولة (( ذكري )).'),
(1669, 'زَكَرِيَّا', 'اسم عبري معناه (( يهوه قد زكر ))، وقد تسمى بهذا الاسم اثنان وثلاثون شخصاً في الكتاب المقدس وأهمه ستة سنذكرهم بالتفصيل. أما الباقون فمنهم ستة لاويين ( 1 أخبار 9: 21 و 15: 18 و 24: 25 و 26: 11 و 2 أخبار 20: 14 و 34: 12 )، وخمسة كهنة ( 1 أخبار 15: 24 و 2 أخبار 35: 8 و نح 11: 12 و 12: 16 و 41 ) ورئيسان عادا مع عزرا من بابل ( عز 8: 3 و 11 )، وأبو يدّو حاكم منسّى في جلعاد ( 1 أخبار 27: 21 )، والابن الرابع لملك يهوشافاط ( 2 أخبار 21: 2 )، وأحد الأمراء الذين أرسلهم يهوشافاط ليعلوا شعب يهوذا ( 2 أخبار 17: 7 ) إلى آخره أما أهم الأشخاص بينهم فهم:( 1 ) ابن يربعام الثاني ملك إسرائيل وخليفته ( 2 مل 14: 29 )، ملك نحو ستة أشهر ثم وقع في أيدي شلّوم فقتله وملك عوضاً عنه ( 2 مل 15: 8-11 وعا 7: 9 ).( 2 ) كاهن من فرقة ابيا ( لو 1: 5 )- انظر (( ابيا ))- وهو أبو يوحنا المعمدان. وقد ذكرت صفاته وصفات امرأته بأبسط العبارات وأتمها وضوحاً، وكانا كلاهما ورعين بارين سالكين في جميع وصايا الرب وباذلين وسعهما ليحصلا على نعمة الروح القدس ( لو 1: 6 ). أما مولد يوحنا فأعلن بطريقة عجيبة خارقة للعادة. فلم يصدق بل شك وطلب علامة غير اعتيادية دفعاً لما في نفسه من الريبة فكانت آيته أن فقد قوة النطق وبقي صامتاً إلى اليوم الثامن بعد ميلاد الصبي إذ دعاه يوحنا حسب قول الملاك له، وفي الحال انطلق لسانه وعاودته قوة النطق. فأخذ يشكر الله ويحمده مملوءاً من الروح القدس ومسبحاً الرب بنشيد أشبه بالتسابيح العبرانية القديمة ( لو 1: 57-80 ).( 3 ) زكريا بن يهوياداع ( ويرجح أنه كان حفيده ) وكان كاهناً للرب في أيام اخزيا ويوآش وبعد موت يهوياداع الموقر الذي كان يتمتع بالاحترام والإكرام الذي والذي كان صديقاً وحامياً ومرشداً ليوآش قام الملك والشعب وتركوا بيت الرب إله آبائهم وعبدوا الآلهة اشيرة والأصنام.أما كريا يهوياداع هذا، وكان قد تنصب كاهناً، وقد رأى ما كان من الملك والشعب قام بإرشاد الروح وتوعدهم على تمردهم وشر قلوبهم وهو في حالة الغيظ الشديد، غير أن خطابه أثار غضبهم ففتنوا عليه ورجموه بحجارة في دار بيت الرب بين المذبح والهيكل بأمر الملك الناكر للجميل، أما هو فرفع دعواه إلى الله واستغاث بملك يهوذا العظيم لينتقم له من أعدائه إذ قال (( الرب ينظر ويطالب )) ( 2 أخبار 24: 20-22 ). وقد أشار المسيح إلى هذه الحادثة الفظيعة في مت 23: 35 ولو 11: 51، وقد دعاه المسيح في متى زكريا بن برخيا ولعله استعمل لفظة (( ابن )) هنا كما تستعمل في مواضع كثيرة في الكتاب للدلالة على النسب فقط فيكون برخيا أحد أجداد زكريا كما يقال أن يسوع ابن داود.( 4 ) رجل عاش في أرض يهوذا في أيام عزيا ولا يعلم عنه وعن خلقه ووظيفته إلا القليل غير أنه كان فاهماً بمناظر الرب ( 2 أخبار 26: 5 ) وربما يراد بهذا التعبير أنه كان تقياً أو كان له نوع خاص من النبوة. أما مشورته على عزيا فكانت مرشدة له لإتمام واجباته وكانت سبباً لفلاحه. وربما كان هو أباً (( لأبي )) أو (( أبيه )) امرأة آحاز وجداً لحزقيا ( 2 مل 18: 2 و 2 أخبار 29: 1 ).( 5 ) هو ابن برخيا الذي اتخذه اشعياء شاهداً أميناً على علاقته مع (( النبية )) التي حبلت منه وولدت له أبناً ( اش 8: 2 ) والأرجح أنه كان من بني آساف (( 2 أخبار 29: 13 ).( 6 ) زكريا بن برخيا بن عدّو، وهو الحادي عشر بين الأنبياء الصغار، وفي عز 5: 1 و 6: 14 يذكر أنه (( ابن عدّو )). وسبب ذلك، على الأرجح، هو أن أباه برخيا مات في ريعان الشباب فنسب حسب العوائد إلى جده عدّو الذي مشهوراً أكثر من أبيه. ويظهر أنه كان من نسل لاوي ولذلك كان مستحقاً وظيفة كاهن ونبي ( نح 12: 16 ). وقد تنبأ زكريا في الشهر الثامن من السنة الثانية لداريوس الملك وذلك في غضون المدة التي أذن فيها لرجال يهوذا أن يرجعوا من سبي بابل فكان من أم الأمور لديه أن يقوي عزائم الشعب الضعيف وينهض هممهم الساقطة لينزعوا عنهم نير بابل ويعززوا روح التقوى فيما بينهم ويرجعوا اليهودية إلى ما كانت عليه من عز وقوة. فيرى رؤى مشجعة ويقدم رسائل روحية عظيمة بخصوص الصوم والطاعة كما يقدم نبوات متنوعة بخصوص المسيح ومجيئه وجروحه، كما يرتفع بالفكر إلى نهاية الأيام وملك المسيح.ويذكر التقليد اليهودي أن زكريا هذا طالت أيامه وعاش في بلاده ودفن بجانب حجي الذي كان زميلاً له.سفر زكريا:هو السفر الحادي عشر بين مجموعة الأسفار التي تسمى (( بالأنبياء الصغار )) والرأي السائد هو أن هذا السفر كتب في العصر الفارسي أثناء حكم داريوس الأول أو حوالي عام 520 ق.م. وقد ظن بعض الباحثين أن الإصحاحات من 9-14 كتبت قبل السبي وظن آخرون أنها كتبت في القرن الثاني ق.م. ولكن قد أيدت دراسة المخطوطات ودراسة النصوص، وحدة السفر وأنه كتب بقلم زكريا. وقد أشار إليه يشوع بن سيراخ إشارة ضمنية عندما ذكر سفر الأثني عشر وكان هذا حوالي 200 ق.م.وينقسم السفر إلى أربعة أقسام:أولاً: مقدمة السفر، سلسلة من ثمان رؤى ص 1: 1-6: 8.ثانياً: أعمال رمزية تشمل تتويج رئيس الكهنة ص 6: 9-15.ثالثاً: وفد من بيت أيل يسأل عن الصوم وجواب النبي ص 7 و 8.رابعاً: سلسلة من النبوات تنبئ بهلاك أعداء الله ومجيء المسيا، ومجيء ملكوت الله ص 9-14 وفي هذا القسم نبوات عن المسيح وهي:( 1 ) دخول الانتصاري إلى أورشليم ( 9: 9 قارنه مع مت 21: 5 ).( 2 ) تسليمه بثلاثين من الفضة ( 11: 12 قارنه مع مت 27: 9 و 10 ).( 3 ) ثقب يديه ( 12: 10 و 13: 6 قارنه مع يو 19: 37 ).( 4 ) الراعي المتألم ( 13: 7 قارنه مع مت 26: 31 ).( 5 ) حكم المسيح يسود على الجميع ( 9: 10 ).'),
(1670, 'زَكَّاي', 'ربما كان اسماً عبرياً اختصاراً (( زكريا )) جد لسبع مائة وستين شخصاً رجعو من بابل مع زربابل ( عز 2: 9 ونح 7: 14 ).'),
(1671, 'زَكُّور', 'اسم عبري معناه (( متذكر، متنبه )).( 1 ) أب لرئيس سبط رأوبين الذي أرسل ليتجسس أرض كنعان ( عد 13: 4 ).( 2 ) شمعوني جاء في سلالة مشماع ( 1 أخبار 4: 26 ).( 3 ) لاوي من بني مراري ( 1 أخبار 24: 27 ).( 4 ) لاوي من بني جرشون، ومن أبناء آساف ورئيس فرقة من المرنمين في أيام داود ( 1 أخبار 25 و 2 و 10 ونح 12: 35 ).( 5 ) ابن مراري وقد ساعد في بناء السور حول أورشليم ( نح 3: 2 ).( 6 ) لاوي قام بختم العهد ( نح 10: 12 ).( 7 ) ابن متينا وهو لاوي وعمل ابنه حانان كأحد الخزنة في أيام نحميا ( نح 13: 13 ).'),
(1672, 'زَلزَلة', 'اهتزاز أو اضطراب أرضي. وفي الحالات العنيفة وفي المناطق التي تتركز فيها الزلزلة تتحرك الجبال جيئة وذهاباً ( ار 4: 24 )، وترتعش أسس الجبال ( مز 18: 7 ). وتنشق الأرض ( زك 14: 4 و 5 )، وتنفتح عدة مرات وتبتلع الناس والبيوت ( عد 16: 31-33 ). وقد حدثت في فلسطين زلازل عديدة دمرت قرى كثيرة بجملتها، منها زلزلة حدثت في يهوذا في أيام عزيا ويربعام صارت أساساً يرجعون إليه في تاريخ حوادثهم ( عا 1: 1 وزك 14: 5 )، كذلك في ايام هيرودس الكبير حدثت زلزلة عظيمة في السنة السابعة لحكمه أهلكت عدداً كبيراً من المواشي وقتلت أكثر من 10000نفس. وقد ذكرت الزلازل المدمرة مع البلايا التي كانت تنذر بخراب أورشليم في مت 24: 7. ويوسيفس وغيره من المؤرخين يثبتون إتمام النبوة حرفياً.\nأما في أيام المسيح فقد تميزت الزلزلة الحادثة وقت الصلب بظلام شديد حزناً على قتل البار القدوس ( مت 27: 45 و 51-54 )، كما تميزت الزلزلة الحادثة وقت قيامته بأنها زلزلة ظافرة مرعبة للأشرار ( مت 28: 2 و 4 )، ويحدثنا لوقا في سفر الأعمال 16: 26 عن حدوث زلزلة في مقدونيا عندما كان بولس وسيلا في السجن نتج عنها تفتح أبواب السجن وسقوط القيود من أيدي المسجونين في فيلبي، فلم تكن الزلازل ضربات إلهية.'),
(1673, 'زِلْفَة', 'اسم عبري ربما كان معناه (( قصيرة الأنف )) وهي جارية أعطاها لابان إلى ليئة في وقت زواجها بيعقوب ( تك 29: 24 ). وقد صارت سرية ليعقوب بناء على طلب زوجته لتنجب له بنين أكثر، فأنجبت جاد واشير ( تك30: 9-13 ).'),
(1674, 'مِزْمَار', '( تك 4: 21 ) آلة طرب مؤلفة من سبع أو ثمان قطع من القصب مختلفة الطول لم تزل تستعمل بين الرعاة حتى يومنا الحاضر، انظر (( غناء )).'),
(1675, 'مَزْمُور-مَزَامِير', 'مجموعة من الأشعار الدينية الملحنة وغرضها تمجيد الله وشكره كانت ترنم على صوت المزمار وغيره من الآلات الموسيقية، وفي العبرانية يسمى (( كتاب الحمد )) كما دعاه المسيح (( كتاب المزامير )) ( لو 20: 42 )، وقد عرف باسم مزامير داود أو (( داود )) فقط بالنسبة لعدد المزامير التي نسبت لداود. وبلغت 73 من 150 مزموراً.وتقسم هذه المزامير إلى خمسة كتب، تنتهي كل منها بتسبيحة وتكرار لفظة آمين مرتين، أضافها جامعوا الكتاب لا مؤلفو المزامير. ولعل هذا التقسيم الخماسي يرمز على الأسفار الموسوية الخمسة، وهو تقسيم قديم جداً يرى بوضوح في الترجمة السبعينية كما في الأصول العبرانية القديمة. وهذه الأقسام تبتدئ بالمزامير الآتية بالترتيب 1 و 42 و 73 و 90 و 107.1- ويتضمن القسم الأول 41 مزموراً، منها 37 لداود، أما أربعة منها وهي 1 و 2 و 10 و 33 لمؤلفين غير معروفين، لذلك يدعونها المزامير اليتيمة نظراً لعدم وجود أب لها، أما في السبعينية فيندمج مزمور 10 مع 9، أما مزمور 33 فينسب لداود.2- أما القسم الثاني أو الكتاب الثاني، فيتضمن 31 مزموراً، أي من 42-72 منها 7 لبني قورح ومزمور واحد لآساف و 18 لداود ز 4 لمؤلفين غير معروفين ومزمور لسليمان أو عن سليمان. وينتهي هذا القسم بالقول (( آمين ثم آمين. تمت صلوات داود بن يسّى )) ( مز 72: 19 ).3- ويتضمن الكتاب الثالث سبعة عشراً مزموراً، أي من 73-89، منها 11 لآساف و 3 لني قورح وواحد لداود ( 86 ) وواحد لهيمان الازراحي وبني قورح معاً ( 88 ) وواحد لايثان الازراحي ( 89 ).4- ويتضمن الكتاب الرابع 17 مزموراً أيضاً، أي من 90-106، منها مزمور لموسى ( 90 ) و 2 لداود ( 101 و 103 ) والبقية لمؤلفين غير معروفين.5- ويتضمن الكتاب الخامس 44 مزموراً، أي من 107-150، منها 15 لداود وواحد لسليمان والبقية لمؤلفين غير معروفين. وفي هذا القسم مجموعة ترنيمات المصاعد، وهي التي استخدمها الشعب في صعوده إلى المدينة المقدسة ( وهي من 120-134 ). كما أنه يحوي مزامير التهليل ( 146-150 ) وهي تتمة السفر كله.وقد نسب هذا التقسيم إلى عصر نحميا كما ورد هذا التقسيم في الترجمة السبعينية. غير أن بعض الآباء المسيحيين رفضوه لزعمهم أنه مخالف لما كتبه الرسول إذ قال: (( في سفر المزامير )) ( اع 1: 20 ) ولم يقل أسفار أو كُتُب وقد ظن بعض العلماء أن هذا التقسيم مؤسس على مشابهة لأسفار موسى الخمسة أو على نظام تاريخي, أو على تتابع المؤلفين, أو على نوع متضمنات مزاميرها أو على مناسبتها للعبادة وغير ذلك, غير أنه يظهر أن التقسيم كان مؤسساً على مباديء مختلفة حسب مطالب العبادة. وقد تكررت بعض المزامير في الكتب المختلفة, قارن مزموري 14 و 53, ثم أن تتمة مزمور 40 تجدها في مزمور 108, وذلك مما يبين أن الكتب الخمسة كانت في الأصل مجاميع مختلفة ولا يمكن تحديد وقت جمع الكتب كلها في سفر واحد, غير أنه يرجح من مادة الكتابين الأخيرين كيفية التعبير في بعض مزاميرهما أنهما جمعا بعد سبي بابل, ويرجح أن الجمع قد أكمل في أيام عزرا وأنه قد تم تدريجياً, إذ أن المزامير تظهر العواطف المختلفة للقرون العديدة منذ عصر داود إلى الرجوع من سبي بابل.العنوانات:لكل المزامير عنوانات عدا 34 منها - ويسمي التلمود هذه المزامي التي بلا عنوان (( مزامير يتيمة )). وقد ظن بعضهم أن العبارة (( هللويا. احمدوا الرب )) في صدر عدة مزامير هي عنوان لها, ويجعلون المزامير اليتيمة على هذا الأساس 24 فقط. ولا يعرف أصل هذه العناوين غير أنه يظن أن جامعي هذ الكتب أضافوها كما أضافوا عنوانات الأناجيل والرسائل وهي على أية حال قديمة ومفيدة للتفسير وموجودة في جميع النسخ العبرانية. وقد ضاع معنى بعضها فلم تترجمها السبعينية, غير أن ما نفهمه منها نقلاً عن التقاليد الشائعة قبل الترجمة المذكورة, يفيدنا باسم المؤلف ونوع الشعر وبالآلة التي استعملت في ترتيله وبالنغمة وبالموجب التاريخي أو الشخصي لتأليفه. إلا أن الملاحظات الأخيرة تختص بمزامير داود وأكثرها تشير إلى حوادث حياته وكثير منها منسوج حرفياً من الأسفار التاريخية ( قابل عنوان مز 52 مع 1 صم 22: 9 ومز 54 مع 1 صم 23: 19 ومز 56 مع 1 صم 21: 11 - 15 ). وقد اشتد النزاع في معنى لفظة سلاه والمرج‍ّح أنها تشير إلى شيء مختص بالترتيل.متضمَّنات المزامير:من العجيب أن هذه المزامير التي كتبها عبرانيون أتقياء قبل المسيح بقرون, تستعمل اليوم في عبادة الكنيسة المسيحية وتناسب ذوق جميع الطوائف على حد سواء. ولعل هذا دليل على كونها موحى بها من الله وهي صادرة من أعماق القلب الإنساني في نسبته لله تعالى وتعبر عن حاسيات الشكر والحمد والتوبة والحزن والغم والرجاء والفرح عامة, على نحو يجعل كل نفس تقية في كل عصر وكل بلد تشعر بمناسبتها لاحتياجاتها. وإن لم تشعر كل الشعور بقوة كل ذلك فما ذلك إلا لعدم إدراك جميع الظروف المحيطة بتأليفه. والمزامير شعر ويقتضي لفهمه أحاسيس مناسبة لأحاسيس الشاعر لكي يتضح للقاريء معناه العميق. لذلك فمن المزامير ما لا يمكن فهمه إلا في أوقات التجربة والضيق. ومنها ما لا يمكن فهمه إلا في أوقات الاضطهاد وغيرها في وقت الفرح والابتهاج. غير أنه على قدر زيادة اختبارنا الروحي تكون زيادة إدراكنا لمناسبة المزامير لكل ظروف الحياة. وهكذا نلاحظ أن أكثر الأسفار الكتابية قراءة هي الأناجيل والمزامير. كذلك صارت المزامير أساس كثير من ترانيمنا المسيحية المستعملة فبي العبادة الفردية والجمهورية في كل المسكونة. وسوف تظل كذلك مدى الدهور. ولا ريب أنه اخنبار عجيب يجوزه كل مسيحي يفكر في هذه الترنيمات ويدرك أنها هي بعينها كانت سبباً في التأثير في أحاسيس موسى وداود وآساف قديماً.مؤلفو المزامير:قد استمر تأليف المزامير مدة نحوألف سنة, من أيام موسى إلى العودة من السبي البابلي, أو حتى بعدها بقليل في أيام عزرا. غير أن أكثرها كتب في أيام داود وسليمان. وينسب 73 مزموراً منها لداود حسب عناوينها هي 3 - 9 و 11 - 32 و 34 - 41 و 51 - 65 و 68 - 70 و 86 و 101 و 103 و 108 - 110 و 122 و 124 و 131 و 133 و 138 - 145. وكان هو أشهر المؤلفين ورئيس المرتمين في إسرائيل. لذلك كثيراً ما سميت كمجموع (( مزاميرداود )). وهذه المزامير بسيطة وقوية العبارة تجتمع فيها الرقة مع الإيمان. وتسم أمامنا صورة إنسان مجاهد ضد العقبات الداخلية والخارجية في الطريق إلى مدينة الله.7- مزامير تعليمية.( أ ) في خصائص الأبرار والأشرار ونصيبهم 1 و 5 و 7 و 9 - 12 و 15 و 14 و 17 و 24 و 25.( ب ) في جودة شريعة الله 19 و 119.( ج ) في بُطل حياة الإنسان 39 و 49 و 90.( د ) في واجبات الحكام 82 و 101.8- مزامير دُعاء ضد الخطاة وأكثرها لداود 35 و 52 و 59 و 68 و 109 و 137.'),
(1676, 'زِمْرَان', 'اسم سامي ربما كان معناه (( بقر الوحش )) ( تك 25: 2 و 1 أخبار 1: 32 ). وهو بكر قطورة من إبراهيم ويظن بعضخم أن ذريته تسكن زبرام - وهي مدينة غربي مكة قرب البحر الأحمر, ويظن آخرون أنهم يكونون قبيلة الزمريين في أواسط بلاد العرب.'),
(1677, 'زُمُرُّد', '( حز 28: 13 ) حجر كريم لونه أحمر قرمزي إذا وقعت عليه أشعة الشمس ترقد كجمرة نار. ولا نعلم تماماً إذا كان هو الزمرد المعروف الآن أم غيره, وتستعمل الترجمة السبعينية ويوسيفس والفولجاتا كلمة بهرمان بدلاً من زمرد ( خر 28: 17 و 39: 10 ).'),
(1678, 'زِمْرِي', 'اسم عبري ربما كان معناه (( من يشبه بقر الوحش )) وهو اسم لخمسة:( 1 ) ابن زارح وحفيد يهوذا ( 1 أخبار 2: 6 ) ودعي في يشوع 7: 1 و 17 و 18 باسم زبدي.( 2 ) رئيس في سبط شمعون قتله فينحاس بن ألعازار الكاهن في شطيم في غيرته للرب ( عد 25: 14 ).( 3 ) بنياميني من نسل يوناثان ابن شاول ( 1 أخبار 8: 36 و 9: 42 ).( 4 )قائد في جيش إسرائيل كان على نصف مركبات أيلة بن بعشا ملك إسرائيل. ثم فتن على سيده وقتله متمماً القضاء الإلهي على بيت بعشا وجلس هو ملكاً سبعة أيام في ترصة حتى جاء عمري رئيس الجيش كله فاضطر زمري إلى الانتحار بإحراق قصر الملك عليه, فمات ( 1 مل 16: 8 - 20 ). وقد اعتقد بعضهم أنه من نسل شاول وقد حاول أن يسترجع الملك القديم ( 1 أخبار 8: 36 ).( 5 ) شعب غير معروف ( ار 25: 25 ) وربما كان من سلالة زمران, لكن الدليل على هذا غير متيسر.'),
(1679, 'زَمزُمِيّون', 'اسم سامي معناه (( متزمرون أو صانعو الضجيج أو الطنين )). وهم شعب أقدم من الكنعانيين ( تك 14: 5 وتث 2: 20 ). وكانوا طوال القامة أشداء البأس يقطنون الأرض شرقي الأردن والبحر الميت. وكانوا يدعون بالوفائيين. وهم الذين سطا عليهم كدر لعومر وغلبهم وبعدئذ جاء العمونيون وطردوهم. وقد عرفوا أيضاً باسم الزوزيون.'),
(1680, 'زِمَام القَصَبَة', 'اسم مدينة للفلسطينيين لفظه في الأصل العبري (( مثج هأمّة )) ( 2 صم 8: 1 ) وهي جت ( 1 أخبار 18: 1 ).'),
(1681, 'زِمَّة', 'اسم عبري معناه (( مشورة أو خدعة )) وهو اسم للاوي من بني جرشوم وابن لشمعي وحفيد يحث ( 1 أخبار 6: 20 و 42 و 43 و 2 أخبار 29: 12 ).'),
(1682, 'زَمَان', 'كان الزمن في الأيام الأولى يحدد بشروق الشمس وغروبها, أو بالنسبة لمواقع القمر وبعض النجوم. وأول إشارة كتابية إلى الوقت في الكتاب المقدس جاءت في سفر التكوين 1: 5 عندما فصل الله بين النور والظلمة وخلق الشمس لحكم النهار والقمر والنجوم لحكم الليل ( 1: 14 و 16 ).وكانت هذه الأنوار كما قال الله لآيات ولأوقات ولأيام وسنين. فصارت أساس التفكير المتطور بخصوص الوقت وتنظيمه.وعندما بدأ الإنسان يزرع ويفلح الأرض ازداد إدراكه لقيمة الوقت وبدأ ترتيب الوقت بالنسبة للزراعة وتقويم جازر في القرن العاشر ق.م. يسجل شيئاً من هذا الإدراك على قطعة من الحجر المرمري الأملس. والكتاب يذكر هذه الأوقات في تك 8: 22 وخر 34: 21 ولا 26: 5 ومز 74: 17 وزك 14: 8 و 2 صم 21: 9. وقد ربط اليهود تاريخهم بالأحداث الكبيرة مثل الخروج من مصر ( خر 12: 40 ) أو السبي البابلي ( حز 33: 21 و 40: 1 ) أو بناء الهيكل ( 1 مل 6: 1 ) أو الزلزلة ( عا 1: 1 وزك 14: 5 ) أو بالنسبة إلى سني حكم الملوك ( 2 ملو 3: 1, وغيره ).وهكذا لم يفت اليهود أن يلاحظوا تغييرات الطقس والأزمنة. وكانت الساعة أصغر أقسام الوقت عندهم. ويظهر أنهم كانوا يقيسونها بواسطة آلة شمسية سميت درجات ( 2 مل 20: 11 ). وكان يومهم الديني يبدأ بغروب الشمس أما النهار الطبيعي فيبدأ بشروقها. ثم قسموا الليل إلى ثلاثة أقسام سُمّي كل منها هزيعاً. وقد ذكر منها الهزيع الأوسط ( قض 7: 19 ) وهزيع الصبح ( السحر ) ( خر 14: 24 و 1 صم 11: 11 ).ويظهر الترتيب الروماني الليل في العهد الجديد. فنراه منقسماً إلى أربع هزع ( مر 13: 35 ). هزيع المساء, ونصف الليل, وصياح الديك, والصباح. وكان اليوم منقسماً إلى 24 ساعة في كل منها 60 دقيقة وفي كل دقيقة 60 ثانية. وقد نقلوا هذا عن السومريين. وقد أطلق الكلدانيون أسماء على الأيام ترتبط بالشمس وبالقمر وبالنجوم, كما حدد الفلكي نبوريمانو أيام السنة بـ365 يوماً وست ساعات و15 دقيقة و41 ثانية. وهذا قريب للغاية من طول السنة الحقيقي إذ أنه يزيد على طولها الحقيقي ست وعشرين دقيقة وخمس وخمسين ثانية.أما الأسبوع العبراني فكان سبعة أيام تنتهي بالسبت وقد قسم المصريين القدماء شهرهم وهو 30 يوماً إلى ثلاثة أقسام في كل منها عشرة أيام. وكان البابليون يحرمون القيام ببعض الأعمال في اليوم السابع. ويلاحظ أن العبرانيين بنوا فكرة الأسبوع وتوقيت الزمن على ترتيب الله في الخليقة ( تك ص 1و 2 ). ولم تكن لديهم أسماء للأيام فقد أعطوها أرقاماً إلاّ أنهم ميزوها بسبب القراءات الكتابية اليومية.أما الشهر العبراني فقد كانت شهراً قمرياً, كان أول يوم فيه يسمى الهلال وكان عيداً. وفي أسفار موسى الخمسة ويشوع والقضاة وراعوث لم يُذكر سوى شهر واحد باسمه وهو ابيب. ثم عدّت الشهور حسب ترتيبها فقيل الشهر الثاني والثالث إلخ ... وفي سفر الملوك الأول تُذكر أسماء ثلاثة من الأشهر هي زيو ( الثاني ) ( 1 مل 6: 1 ) وإيثانيم ( السابع ) ( 1 مل 8: 2 ) وبول ( الثامن ) ( 1 مل 6: 38 ). ولم يذكر غير هذه من أسماء الشهور في أسفار ما قبل السبي.والشهور العبرية هي: ابيب ( نيسان ) وزيو وسيوان وتموز وآب وأيلول وإيثانيم ( تشري ) وبول وكسلو وطيبيت وشباط وآذار. وآذار ( وهو الشهر الثالث عشر المضاف ).أما السنة العبرية فكانت تتألف من اثنتي عشر شهراً قمرياً ابتداؤها أول نيسان. ثم كانوا يضيفون إليها شهراً يكون بمثابة الشهر الثالث عشر وذلك عندما ينتهي الشهر الثاني عشر قبل اعتدال الليل والنهار مما يمنع تقديم باكورات غلة الشعير في منتصف الشهر التالي, وكذلك تقديم بقية التقدمات في أوانها. وكانت السنة قبل السبي تبدأ في الخريف ثم صارت بعد السبي تبدأ في الربيع.وقد استخدم اليهود السنة الشمسية بعد السبي مع احتفاظهم بالسنة القمرية لأجل الحياة الدينية. وكانت أيام شهورها 29 يوماً ونصف يوم. ثم حاولوا أن يوفقوا بين السنة الشمسية والقمرية بإضافة شهر قمري إلى السنة أسموه آذار الثاني وذلك سبع مرات في خلال دورة تسعة عشر عاماً ( Metonic Cycle ) وذلك في السنة الثالثة والسادسة والثامنة والحادية عشرة والرابعة عشرة والسابعة عشرة والتاسعة عشرة. وقد نقل اليهود هذا النظام عن البابليين.وكان لليهود المتأخرين مبتدآن للسنة, فقد كان الشهر السابع المدني هو الشهر الأول للسنة المقدسة. كما ابتدأت السنون السابعة واليوبيلية في الشهر السابع ويرجح أن ذلك كان مرتباً لأسباب زراعية.وأما أعيادهم وأيامهم المقدسة فكانت قليلة في الأزمنة الأولى, وانحصرت في السبوت والأهلة, ثم في أربعة أعياد كبيرة وصوم واحد. والأعياد هي عيد الفصح وعيد الأسابيع وعيد الأبواق وعيد المظال. أما الصوم فكان صوم يوم الكفارة. إلا أنه بعد السبي أضيفت أعياد وأصوام كثيرة منها: عيد الفوريم وعيد التدشين وصيام وهذه جعلت الشعب يفكرون في مصائبهم وقت السبي. أما السنة السابعة فكانت سنة راحة تبتدئ سنة اليوبيل يوم الفصح في نهاية 49 سنة وكانت شبيهة بالسنة السابعة إلا أنها كانت أكثر أهمية منها.وقد كان اليهود يعدون السنين لملوكهم من وقت نهايتها وليس من تاريخ بداية الملك وتبوء العرش . ويمكننا تمييز حِقَّب الوقت اليهودي على وجه تقريبي فإذا هي سبع حِقَّب:( 1 ) من الخليقة إلى ارتحال أبرام من حاران, ويصعب تحقيق هذه الفترة لأنها كانت في عصور سحيقة ولأننا لا نعرف على وجه التحقيق الطريقة التي اتبعت في حساب تلك الأزمنة ولا نعرف أيضاً إذا كان الوحي قد ذكر كل الأسماء بالتسلسل أم ذكر أسماء الأشخاص البارزين فقط التي عندنا أولاً من آدم إلى نوح وأولاده ( تك 5: 3 - 32 ), ثم من سام إلى أبرام ( تك 11: 10 - 26 ).( 2 ) من ارتحال أبرام من حاران إلى الخروج, ويظن بعضهم أنها 430 سنة ( غل 3: 17 ).وقال آخرون بأنها 645 سنة ( خر 12: 40 ) بحسب النص العبري.( 3 )من الخروج إلى تأسيس هيكل سليمان وقد اعتبرت هذه الحقبة اثنتي عشرة فترة تبلغ كل واحدة منها أربعون سنة, فتكون كلها 480 سنة ( 1 مل 6: 1 ). وأول أجزاء هذه الحقبة الفترة في البرية ( خر 16: 35 و 13 وعد: 33 ), كما استغرق عصر القضاة ست فترات أو ثمانية ( قض 3: 11 و 30 و 5: 31 و 8: 28 و 13: 1 و 10: 1 - 3 و 12: 7 - 14 ), ثم عهد كهانة عالي ( 1 صم 4: 18 ), ثم عهد ملك شاول ( اع 13: 21 ), وأخيراً عهد ملك داود ( 1 مل 2: 11 ).ولا ينبغي أن يفوتنا أن ندرك أن أجزاء هذه الحقبة لم تكن كلها متساوية تماماً لأن الأربعين سنة في كل منها كانت تتفاوت طولاً وقصراً, كما أن الاختلاف في تحديد وقت هذه الحقبة راجع إلى الاختلاف في طريقة حسابها وحدودها.( 4 ) من تأسيس هيكل سليمان إلى خرابه, وتاريخ هذه الحقبة أكثر دقة ويقيناً بالنسبة لشهادة الآثار وشهادة تاريخ الأمم للتاريخ المقدس في حدود 1000 سنة ق.م. وطول هذه المدة يبلغ 425 سنة منها 37 للمملكة قبل انقسامها, وقد كانت مدة مملكة يهوذا 388 سنة تقريباً, أما مدة مملكة إسرائيل فكانت 255 سنة تقريباً.( 5 ) من خراب هيكل سليمان إلى الرجوع من السبي ( سبي بابل ) وهي سبعون سنة كما قالت النبوة, ويصعب تحديدها, فيظن أنها مدة سبعين سنة التي تسلطت فيها بابل على فلسطين والمشرق ( ار 25 ) لاتفاقها مع أول سنة لنبوخذ ناصر ( ار 25: 1 و 46: 2 ) وانتهاؤها بسقوط بابل وإشهار كورش الأمر بعودة المسببين في السنة الأولى لملكه ( ار 29: 10 ).( 6 ) من الرجوع من السبي إلى مجيء المسيح، وتقدر هذه المدة بحوالي 530 سنة. وفي هذه الحقبة أعيد بناء الهيكل ثم أتى إلى حكم الاغريق والبطالسة على فلسطين وظهر في الأفق نجم المكابيين من سنة 166 ق.م. حتى سنة 37 ق.م. عندما جلس هيرودس على العرش وأعاد بناء الهيكل. وبدأ عهد الرومان في فلسطين بعد أن استولى القائد بمباي على أورشليم في سنة 63 ق.م.( 7 ) من ميلاد المسيح إلى خراب الهيكل، وتقدر ب 74 سنة.وهاك تواريخ بعض الحوادث الهامة التي وردت في الكتاب المقدس والتي يكاد الباحثون يجمعون على أنها حدثت في هذه التواريخ على وجه التقريب:بناء هيكل سليمان		967 ق.م.انقسام المملكة			931 ق.م.سقوط السامرة			722 أو 721 ق.م.رجوع بعض اليهود إلى أورشليم 538 ق.م.أتمام بناء لهيكل زربابل		515 ق.م.تكريس الهيكل على يديهوذا المكابي			165 ق.م.ميلاد المسيح حوالي 		4 ق.م.الصليب والقيامة حوالي		30 ميلادية.اهتداء شاول الطرسوسي( بولس الرسول ) حوالي 		35 ميلادية.استشهاد بولس حوالي 		67 أو 68 ميلادية.'),
(1683, 'زَمِيرة', 'اسم عبري معناه (( ترنيمة )) أو (( شاب أنيق وبدون لحية )) أو (( صغير الحجم )) رجل من نسل بنيامين ( 1 أخبار 7: 8 ).'),
(1684, 'زَنبق-زنابق', 'يطلق هذا الاسم في أيامنا هذه على عدة انواع من الفصيلة الزنبقية حتى وعلى أزهار من فصائل أخرى وهكذا في أيام المسيح كانت لفظة الزنبق تدل على أنواع شتى وعبثاً تعب الذين أرادوا أن يقيدوها بنوع دون غيره ( مت 6: 28 ). \nوهذا التقييد لا يزال الوضوح على ما قصده المسيح فلقد قصد الإشارة إلى جمال الأزهار لا إلى أسمائها العلمية.'),
(1685, 'زُنبور', 'نوع من الحشرات الشديدة الضرر استعمله الله لتأديب الوثنيين ( خر 23: 28 وتث: 20 ويش 24: 12 ). وربما فيه إشارة رمزية إلى القوة التي يرسلها الله لمعونة المؤمنين به ولنصرتهم على أعدائهم.'),
(1686, 'زُنَّار', 'انظر (( ثوب )).'),
(1687, 'زِنا', 'خطيئة تلوث حياة الإنسان ونفسه وتنجسه وتستحق عقاب الله الصارم حسب إعلاناته. وهي:( 1 ) المعنى الموسوي-كل اتصال جنسي غير شرعي. كأن يضاجع رجل امرأة غيره، أو فتاة مخطوبة لرجل آخر، أو فتاة حرة غير مخطوبة الخ. وكان عقاب هذه الخطيئة الرجم والموت ( لا 20: 10 و تث 22: 22-29 ). وهناك تفاصيل عديدة بخصوص هذه الخطيئة وطريقة إظهارها ومعاقبتها في أسفار موسى ( عد 5: 11-31 ).( 2 ) المعنى المسيحي-كل نجاسة في الفكر والكلام والأعمال. وكل ما يشتم منه شيء من ذلك ولعل هذا المعنى مأخوذ من الوصية السابعة بتفسير المسيح في موعظته على الجبل ( خر 20: 14 وتث 5: 18 ومت 5: 27 و 28 ).( 3 ) المعنى المجازي-الانحراف عن العبادة للإله الحقيقي إلى الآلهة الوثنية. أو كل عدم أمانة بالنسبة للعهد مع الله ( ار 3: 8 و 9 وحز 23: 37 و 43 هو 2: 2-13 ). وقد وردت هذه اللفظة في الكتاب المقدس كثيراً للدلالة على خيانة شعب الله ونكثهم للعهود المقدسة وكأن الله يطلب كل قلوبنا المحبة باعتباره زوجاً ينتظر من عروسه كل قلبها.ابن زِنى:انظر (( ولد )) ابن ولد نتيجة لعلاقة غير شرعية بين رجل وامرأة. وقد حرّم على ابن الزنى أن يدخل في جماعة الرب ( تث 23: 2 ).'),
(1688, 'زَانٍ زانية', 'تطلق هاتان الكلمتان على الرجل والمرأة الفاجرين. وأول زانية تحدث عنها الكتاب هي ثامار ( تك 38: 6 و 24 ). وقصة الزانيتين في 1 مل 3: 16-28 تبرز حكمة سليمان وقد نهى الناموس الموسوي الآباء عن تعريض بناتهم للزنى وحكم بإحراق ابنة الكاهن عندما تزني ( لا 21: 9 ). وكانت الزانية تحسب نجسة. وقد قرن اسمها باسم الكلب ( تث 23: 18 ). وشبهت بالهوة العميقة والحفرة الضيقة ( ام 23: 27 ). وحذّر الشباب أشد تحذير من معاشرتها ( ام 7: 10-27 و 29: 3 ). وتستعمل في الكتاب المقدس لتدل على تعدي بني إسرائيل على حق الله وعهده واتباعهم لعبادة الأصنام وللنجاسة ( اش 1: 21 وار 2: 20 و 3: 1 وحز 16: 15 وهو 2: 2 و 4: 15 ونا 3: 4 ) انظر (( راحاب )).'),
(1689, 'زَبِينَا', 'اسم أرامي معناه (( مشتري ))، وهو من أبناء نبو وقد أقنعه عزرا بترك زوجته الأجنبية بعد الرجوع من بابل ( عز 10: 42 ).'),
(1690, 'زَكَّا', 'ربما كان اسم عبرياً اختصاراً (( زكريا )) ( لو 19: 1-10 )، وهو رجل من أغنياء اليهود في أريحا ورئيس لجباة الضرائب هناك. كان اليهود يعتبرونه خاطئاً باعتباره يخدم الحكام الرومان الوثنيين ويتعاون معهم. وإذ عرف بمرور المسيح صعد على جميزة وفي قلبه أفكار ورغبات عرفها المسيح فمكث عنده ذلك النهار، وتحت تأثير الروح القدس حصل له ولبيته الخلاص، ولا يراد بالقول (( إذ هو أيضاً ابن إبراهيم )) أنه كان من نسله حسب الطبيعة بل أنه صار شريكاً له في الإيمان والمواعيد. ويظن بعضهم أن إعلانه العظيم من إعطاء نصف أمواله للمساكين ورده أربعة أضعاف لمن وشى بهم إنما يكشف عن أمرين: أولاً-بداية حياة جديدة، ثانياً: يشف عن حياة نزيهة عفيفة اختلفت عن حياة زملائه جباة الضرائب. وقد أظهر التنقيب في (( تلول العليق )) بالقرب من أريحا منازل حوائطها منقوشة، وربما كان لزكا منزل كأحد هذه المنازل.'),
(1691, 'زُهَرَة', 'كوكب منير، وهو الكوكب الذي يظهر في الصباح الباكر معلناً نهاية الظلام، وهو المع النجوم في السماء في ذلك الوقت. ويسمونه فينس، أو لوسيفر باللاتينية. وقد شبه النبي اشعياء مجد ملك بابل بهاء هذا الكوكب ابن الصباح ( اش 14: 12 ). ويحدثنا الكتاب في مواضع أخرى عن المسيح ككوكب الصبح المنير ( رؤ 22: 16 و 2 بط 1: 19 ).\nأما الاعتقاد العام بأن الشيطان هو هذا الكوكب. وأنه الملاك الساقط كالبرق من السماء فقد بدأ في القرن الثالث لاسيما بين الشعراء، ولعله مبني على التفسير الخاطيء الذي يربط بين قول المسيح في لو 10: 18 وبين اش 14: 12 أو رؤ 9: 1 و 12: 7-10.'),
(1692, 'زُوحيت', 'اسم عبري معناه (( متكبر )) ابن يشعي من سبط يهوذا ( 1 أخبار 4: 20 ).'),
(1693, 'زُوزِيُّون', '( تك 14: 5 ) ربما كان هذا اسماً آخر للزممزميين. وهم قبيلة من سكان المقاطعة الواقعة شرقي بحر لوط والأردن وكانوا جبابرة في أجسامهم وقوتهم وقد غزاهم كدر لعومر فهزمهم ثم طردهم بنو عمّون.'),
(1694, 'زوفا', 'اسم نبات ذُكر عدة مرات في العهد القديم ولم يستطع علماء من القطع بشيء نهائي بخصوصه. والرأي التقليدي بين اليهود أنه الزعتر أو السعتر واسمه باللاتينية Origanum inaru وبالإنكليزية Marjoram  أو Thyme ويظهر من الكتاب المقدس أن هذا النبات استعمل استعمالات متنوعة فاستعمل للتطهير من البرص ( لا 14: 4 و 6 ) ومن الخطيئة ( مز 51: 7 ) ومن الأوبئة ( لا 14: 49 و51 ) وللطهارة الطقسية ( عد 19: 6 و 18 ) كما استعمل واسطة لرش الدم ( خر 12: 22 وعب 9: 19 ) كما استعمل لرفع الاسفنجة المملوءة خلاً للمسيح على الصليب ( يو 19: 29 ). والزوفا أيضاً نبات عطري الرائحة، له طعم حار في البداية ثم يحدث برودة في الفم لذلك يروي ويبرد أكثر من الماء وينبت في الجدران وفي الصخور، وأوراقه مشعرّة صغيرة، ويستخدم في شكل حزم صغيرة يمكن أن تحمل السوائل في داخلها للرش وربما كانت إضافة الزوفا أو أوراقها إلى الخل في اسفنجة يخفف آلام المصلوبين.'),
(1695, 'زِوَان', 'عشب اسمه باللاتينية Lolium وهو ذو أطراف ليفية كثيرة، ينبت كثيراً بين الحنطة بدون زراعة ومرّلت ينثر حبوبه وهو عشب سام يحدث أكله دواراً وارتعاشاً وربما يسبب موتاً. يتعذر التفريق بينه وبين الحنطة في البداية، ولكن الفرق يظهر بعد النضج والإثمار. ولا يمكن اقتلاع الزوان من وسط الحنطة والا حدثت أضرار بالغة للحنطة، فيضطر صاحب الحقل إلى التأني عليه حتى وقت الحصاد فتنشغل النسوة والأولاد في جمعه أولاً للحريق وربما يستخدم لإطعام الدجاج، ثم تجمع الحنطة بعد ذلك ( مت 13: 24-30 و 36-43 ). والزوان يصوّر عمل إبليس لتعطيل ملكوت المسيح كما أن الزوان هم من الأشرار في داخل الكنيسة.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1696, 'زَاوِية', 'جمعها زوايا. وردت في الشريعة الموسوية إشارات متعددة إلى الحقل أي أركانه، ووجوب تركها في الحصاد للفقير واليتيم والأرملة ( لا 19: 9 و 23: 22 ). وهذه ناحية من نواحي تتميم الناموس في محبة القريب كالنفس. وقد استعملت الكلمة مضافة إلى شيء مثل زاوية موآب وتعني تخوم تلك البلاد ( ار 48: 45 ) أو زاوية السرير، أي الموضع الممتاز في البيت الذي يفرش عليه الدمقس ( عا 3: 12 ). والزاوية في زك 10: 4 تشير إلى حجر الزاوية أي المسيح ( مت 21: 42 واف 2: 20 و 1 أخبار و 1 بط 2: 6 ).'),
(1697, 'زَيت', 'استعمله العبرانيون القدامى وكان على الأغلب زيت الزيتون وفي العادة ينضج ثمر الزيتون في الخريف، ثم تهز الشجرة أو تضرب للحصول على ثمرها ( تث 24: 20 واش 17: 6 و 24: 13 ). ثم يعصر الزيت بالدوس عليه بالأرجل ( تث 33: 24 ومي 6: 15 ) أو بالوضع في آلات لهذا الغرض، ثم يرسب كل ما فيه من أوساخ. ويؤخذ الزيت النقي للاستعمال.\nوقد كان الزيت إنتاجاً أساسياً في فلسطين ( عد 18: 12 وتث 7: 13 ونح 10: 39 و 13: 5 ) وقد استعمل للإضاءة بوضعه في السرج ( خر 25: 6 و مت 25: 3 ). وكان الزيت النقي المرضوض يجهز بطريقة خاصة للإضاءة المستمرة في القدس ( خر 27: 20 ) كما استعمل الزيت في الطعام ( 1 أخبار 12: 40 و حز 16: 13 ) وفي صنع الخبز ( 1 مل 17: 12 ).\nكما كانت تقدمات كثيرة تخلط وترش بالزيت قبل تقديمها ( لا 2: 1-7 ) كما استخدم الزيت في معالجة الجراح ( اش 1: 6 مر 13 ولو 10: 34 ). وقد استخدم الزيت في دهن الأجسام والرؤوس بعد تعطيره بالعطور الشرقية لاسيما في المواسم في المواسم والاحتفالات وكان استخدامه بهذه الصورة دليلاً على الفرح والسرور ( مز 23: 5 ) وعدم استخدامه دليلاً على الحزن ( مت 6: 17 ). وقد استخدم أيضاً في مسح الملوك ( 1 صم 10: 1 و 16: 1 و 13 و 1 مل 1: 39 و 2 مل 9: 3 و 6 ). وقد سمي زيت مقدساً بالنسبة لأنه كان باسم الله ( مز 89: 20 ). كما استخدم في مسح الكهنة ورؤساء الكهنة بعد تجهيزه بصورة خاصة بل في مسح خيمة الاجتماع، والتابوت، والمائدة والمنارة والمرحضة وقاعدتها والمذبحين ( خر 30: 22-33 ) انظر (( زيتون )).'),
(1698, 'زيت مرضوض', 'زيت مضروب جيداً ومجهز لاستعمالات خاصة انظر (( زيتون )).شجرة الزيت:( اش 41: 19 ) شجرة زيتون بري يمكن استخلاص الزيت منها لكنه زيت أردأ بكثير من الزيت المستخلص من شجر الزيتون والاسم العبري (( شمن )) أي دهن ربما يوضح نوع زيتها. وهي موجودة في المناطق الواقعة قرب حبرون والسامرة وجبل تابور. وقد ترجمت الكلمة العبرانية الموجودة في سفر الملوك الأول 6 غالباً بكلمة زيتون ومرة زيتون بري ( نح 8: 15 ) وفي هذه الآية الأخيرة يذكر الزيتون أيضاً فلا بد أن يكون المراد من شجرة الزيت غير المقصود من الزيتون. ويظن أن خشب هذه الشجرة استخدم في صناعة الكروبيم في هيكل سليمان. وكان كل كروب يرتفع إلى عشرة أذرع ( 1 مل 6: 23 و 26 ). وكذلك في صنع زوايا الأبواب في الهيكل ( 1 مل 6: 31-33 ).'),
(1699, 'زَيْتَان', 'اسم عبري معناه (( شجرة زيتون )) رئيس لبنيامين ( 1 أخبار 7: 10 ).'),
(1700, 'زيتون', 'شجرة معروفة من القديم في فلسطين ( خر 23: 11 وتث 6: 11 ويش 24: 13 وقض 15: 5 و 1 صم 8: 14 ). كما كانت تنبت في أشور ( 2 مل 18: 32 ). ويظهر بهاؤها في أن أوراقها خضراء في الأعالي وسنجابية فضية من أسفل حتى إذا هزها الهواء ظهرت الشجرة من بعيد كأنها مغطاة ببرقع فضي شفاف جميل جداً ( هو 14: 6 ). وزهره أبيض وكثيراً ما ينتثر فتشبه به العاقر حينئذ ( اي 15: 33 ). وبؤكل حب الزيتون إلا أن قيمته العظمى في زيته ( اي 24: 11 وحز 27: 17 ). ولم يزل الثمر يجمع بخبط الشجرة ( تث 24: 20 ) أو النفض ( اش 17: 6 ). وقد أوصى الإسرائيليون بأن يبقوا خصاصة الزيتون للفقراء ( تث 24: 20 ).ويعيش الزيتون مئات السنين ويحمل في الشيبة كالأرز وكالنخل ( قرن مز 92: 12 و 14 ). أما معاصره فقد كانت منقورة في الصخر ( اي 29: 6 ) كما تشهد بذلك الآثار أيضاً في تلك البلاد. مرات كانوا يدوسون حبوبه بالرجل ( مي 6: 15 ). وقد استعمل خشب الزيتون في صنع بعض أجزاء الهيكل ومتعلقاته ( 1 مل 6: 23 و 31 و 33 ). أما تطعيم الزيتونة في البرية في زيتونة جيدة فيشير إليه بولس في رسالة رومية 11: 17-24 مصوراً دخول الأمم إلى الإيمان، كما وتغيير الطبيعة الشريرة بالتطعيم بطبيعة أخرى يشير إلى عمل النعمة في القلب البشري الشرير.وأول ما حملته الحمامة إلى نوح بعد الطوفان كان ورقة شجرة الزيتون ( تك 8: 11 ). لذلك صار غصن الزيتون شعار السلام وعلامته. وكذلك شجرة الزيتون علامة تشير إلى النجاح والبركة الإلهية ( مز 52: 8 وار 11: 16 وهو 14: 6 ). وعندما تتقدم شجرة الزيتون في العمر تكثر من حولها نباتات الزيتون الصغيرة النامية ( مز 128: 3 ). وقد كانت النساء تتزين في بعض المناسبات بإكليل من زهوره كما كان إكليل الزهر الذي يطوق عنق المنتصر في الألعاب الأولمبية في اليونان مكوناً من أوراق الزيتون.جبل الزيتون:يشرف هذا الجبل على أورشليم من الجهة الشرقية فترى من قمته كل شوارع المدينة وبيوتها ولا شك أن اسمه مأخوذ من شجر الزيتون الذي كان موجوداً فيه بكثرة. ولا تزال توجد فيه بعض أشجاره الكبيرة الحجم والقديمة العهد إلى الآن.ويكثر ذكر هذا الجبل في العهد القديم تحت أسماء مختلفة، كجبل الزيتون ( 2 صم 15: 30 وزك 14: 4 ) والجبل ( نح 8: 15 ) والجبل الذي تجاه أورشليم ( 1 مل 11: 7 ) والجبل الذي على شرقي المدينة ( حز 11: 23 ) وجبل الهلاك ( 2 مل 23: 13 ). كما يذكر في العهد الجديد في علاقته بحياة المسيح رب المجد على الأرض ( مت 21: 1 و 24: 3 و 26: 30 ومر 11: 1 و 13: 3 و 14: 26 ولو 19: 29 و 37 و21: 37 و 22: 39 ويو 8: 1 واع 1: 12 ).ويفصل هذا الجبل عن أورشليم وادي قدرون ( 2 صم 15: 14 و 23 و 30 ). وقد حسبت المسافة بين أقصى قممه الشمالية وبين أورشليم بسفر سبت ( اع 1: 12 )، أو كما قال يوسيفس أو ست غلوات.على هذا الجبل صعد داود عاري القدمين وباكياً وهارباً أمام ابشالوم. وعلى هذا الجبل ظهر الرب لحزقيال في رؤياه ( حز 11: 23 ). كما ظهر لزكريا بروح النبوة واقفاً على هذا الجبل شافعاً في شعبه ( زك 14: 4 ). وطالما صعد المسيح إليه، وفي وقت نزوله منه قبل الصلب بأيام قليلة استقبلته الجموع بالهتاف والترحيب وكان هو يبكي على المدينة ومصيرها القريب ( لو 19: 37-44 )، وقد تحدث من سفح ذلك الجبل عن خراب الهيكل وتدمير المدينة ( مت 24: 3 ومر 13: 3 )، وقبل الفصح الأخير صعد إلى هناك حيث بستان جثسيماني في غرب الجبل. وقد كانت بيت عنيا وبيت فاجي في شرقه.وفي الوقت الحاضر توجد مدينة صغيرة تسّمى العازرية مكان بيت عنيا حيث كان لعازر ومرثا ومريم، وحيث أقيم لعازر من الأموات، وبالقرب من هذا المكان صعد المسيح إلى السماء ( لو 24: 50 و 51 ).ويسّمي العرب جبل الزيتون في الوقت الحاضر جبل الطور. وفي الحقيقة أن هذا الجبل عبارة عن سلسلة من الجبال تمتد بعض سلاسله إلى الميل طولاً، وله رؤوس ستة تسمى تلالاً أو قمماً، منها قمتان جانبيتان: 1- قمة ممتدة في الشمال الغربي وترتفع إلى 737ر2 قدماً تسمى حسب تسمية يوسيفس تل سكوبس. 2- قمة ممتدة في الجنوب الغربي وترتفع إلى 549ر2 قدماً وتسمى تل المشورة الرديئة نسبة إلى التقليد الذي يقول بأن قيافا كان يحتفظ ببيت ريفي في هذا الجانب، وفيه تمت مشورته مع الكهنة على قتل المسيح ( يو 11: 47-53 ). 3-قمة في الشمال ترتفع إلى 723ر2 قدماً وتسّمى في الوقت الحاضر كرم السيد، وكانت تدعى قبلاً تل الجليل نسبة على نزول الجليليين في هذه البقعة أيام الأعياد والمواسم، أو ربما بسبب الاعتقاد الذي تبلور في القرن الرابع عشر عن ارتفاع المسيح من هناك، بناء على قول الملاكين للرسل (( أيها الرجال الجليلييون )). 4- قمة الصعود وهي في مواجهة الباب الشرقي لأورشليم وترتفع إلى 643ر2 قدماً فوق سطح البحر، وقد عرفت بهذا الاسم من عام 315 م. وقد توّج قسطنطين هذه القمة بقبة وبكنيسة عظيمة، وقد تكاثرت الكنائس هناك باسم كنيسة الصعود. 5-قمة الأنبياء نسبة إلى وجود قبور الأنبياء على جانبها. 6-قمة المعصية نسبة إلى الاعتقاد أنه هناك بني سليمان مذابحه الوثنية لزوجاته الوثنيات.وتعتبر قمة الصعود من قمة تل الأنبياء حتى أن بعضهم يعتبرها قمة واحدة.ولا توجد بين الرؤوس الأربعة الأخيرة انخفاضات عميقة، وقد كان جبل الزيتون مكسواً قديماً بالزيتون والتين والبطم والسنديان، وبالنخل في بعض المواضع كبيت عنيا. وكانت بقرب قمته شجرتان من الأرز وتحتها أربعة حوانيت لبيع الحمام لخدمة الهيكل ، ولم يبقى من كل ذلك شيء سوى الزيتون والتين.وعند أقدام جبل الزيتون وبالقرب من جثسيماني يمتد طريق متفرع إلى أربعة فروع: فرع إلى بيت عنيا وأريحا وقد بناه الخليفة عبد الملك بن مروان في القرن السابع الميلادي، والفرع الثاني يتجه عبر القمة إلى بيت فاجي وبيت عنيا، أما الفرعان الآخران فيتعرجان كثيراً في طريقهما إلى القمة. ومن وادي قدرون إلى الأردن يمتد طريق روماني قديم.'),
(1701, 'زِيثار', 'اسم فارسي معناه (( ضارب ))، خصي لاحشويروش ( اس 1: 10 ).'),
(1702, 'زِيثَام', 'اسم عبري معناه (( شجرة زيتون ))، لاوي ( 1 أخبار 23: 8 و 26: 2 ).'),
(1703, 'زِيج', '( عا 7: 7 و 8 ) وهو المطمار أو خيط تعلق به رصاصة يستعمله البناؤون لتحقيق ارتفاع البناء عمودياً.'),
(1704, 'زِيزَا', 'اسم عبري ربما كان معناه (( كثرة أو ازدواج )).( 1 ) رئيس من بني شمعون ( 1 أخبار 4: 37 ).( 2 ) من أولاد رحبعام وامة معكة ( 2 أخبار 11: 20 ).'),
(1705, 'زيزَة', 'اسم عبري ربما كان معناه (( كثرة أو ازدواج )) لاوي من بني جرشون ( 1 أخبار 23: 11 ) ويدعى أيضاً زينا ( 1 أخبار 23: 10 ).'),
(1706, 'زِيع', 'اسم عبري ربما كان معناه (( حركة أو مرتعش )) رجل من بني جاد ربما كان رئيس عشيرة ( 1 أخبار 5: 13 ).'),
(1707, 'زِيف', '( 1 ) رجل من يهوذا من بيت يهللئيل ( 1 أخبار 4: 16 ).( 2 ) مدينة في جنوب يهوذا تُعرف اليوم باسم الزيفة جنوبي غربي كرنوب ( يش 15: 24 ).( 3 ) مدينة في المنطقة الجبلية في يهوذا ( يش 15: 55 ) بالقرب من برية زيف ( 1 صم 23: 14-24 و 26: 2 )، وقد حصنها رحبعام ( 2 أخبار 11: 8 ). وتعرف اليوم باسم تل زيف، وهو هضبة ترتفع إلى 882، 2 قدماً فوق سطح البحر وتبعد أربعة أميال إلى الجنوب الشرقي من حبرون. إلى هذه المدينة هرب داود ممن وجه شاول واختبأ فيها، وأما الغاب المذكور في 1 صم 23: 15 وربما كان بالقرب من زيف.'),
(1708, 'زِيفَة', 'رجل من نسل يهوذا ( 1 أخبار 4: 16 ).'),
(1709, 'زِيفِيُّون', 'هم سكان زيف ( 1 صم 23: 19 وعنوان مز 54 ).'),
(1710, 'زِيْنَا', 'انظر (( زيزا )).'),
(1711, 'زِينَاس', 'اسم يوناني اختصار اسم (( زينودورس )) ومعناه (( هبة زفس )) هو رجل كان يعرف (( بالناموسي )) لأنه كان منعكفاً على درس الناموس، وكان من رجال القانون، وجال في كريت هو وابولوس حيث حثَ بولس تيطس ليعاونهما على القيام برحلتهما ( تي 3: 13 )'),
(1712, 'زِيُو', 'اسم شهر عبراني انظر (( شهر )).'),
(1713, 'ساراف', 'اسم عبراني معناه (( احتراق أو حية )) وساراف رجل من نسل شيلا بن يهوذا، وكان من أصحاب مواب ( 1 أخبار 4: 22 ).'),
(1714, 'ساراي', 'اسم عبراني معناه (( المجاهدة )) وهو الاسم الأصلي لسارة زوجة إبراهيم، انظر تكوين 11: 29.'),
(1715, 'سارة', 'اسم عبراني معناه (( أميرة )). وهي زوجة إبراهيم، وكانت في الأصل تدعى ساراي.تزوجت سارة من إبراهيم في أور الكلدانيين وكانت أصغر منه بعشر سنوات ( تكوين 11: 29-31 و 17: 17 ). وعندما خرج إبراهيم من حاران كان عمر سارة 65 سنة ( تكوين 12: 4 ) ولكنها كانت جميلة بالرغم مما بلغت من العمر، وكانت محتفظة بقوتها وشبابها.وبعد مغادرة حاران وقبل النزول إلى مصر، تحدث إبراهيم مع سارة وطلب منها أن تخفي أنها زوجته وتقول أنها أخته، وقد كانت بالفعل أخته ابنة أبيه ليست ابنة أمه ( تك 20: 12 ). وكان سبب طلب إبراهيم ذلك خوفه من أن جمال سارة يلفت نظر المصرين إليها، فيقتلونه ويأخذونها. وأطاعت سارة زوجها. فأخذها ملك مصر، ولكن الله منعه من الاقتراب إليها. ووبخ فرعون زوجها عندما أعلن له الله الأمر.وبعد عدة سنين سكن إبراهيم في جرار وقال عن سارة أنها أخته، فطلب ابيمالك أن يتزوج منها، ربما لغرض إيجاد تحالف مع الأمير البدوي القوي. وهنا أيضاً منع الله ابيمالك من الإساءة إلى سارة ( تكوين 20: 1-18 ).وعندما كان عمر سارة 75 سنة ضعف إيمانها في إتمام وعد الله من حيث حصولها على نسل، فأشارت على زوجها أن يتزوج من جاريتها هاجر، فولدت هاجر إسماعيل ( تكوين 16: 1-16 ).وعندما بلغت سارة سن 89 جاءها الموعد بميلاد إسحق الذي ولدته بعد سنة. وغيَّر الله اسم ساراي إلى سارة في ذلك الوقت-وقت الموعد ( تك 17: 15-22 و 18: 9-15 و 21: 1-5 ).وعندما فطم إسحق أقام والداه وليمة عظيمة.. ولاحظت سارة أن إسماعيل يمزح، وقد قيل أنه كان يصوب سهامه على إسحق مهدداً بقتله من باب التخويف، فطلبت سارة من إبراهيم أن يطرد الجارية مع ابنها. وقد ظن البعض أن ذلك كان قساوة وشراً من سارة، غير أن البعض الآخر يعتقد أن سارة لم تطلب طرد هاجر إلا إلى الخيام الأخرى لإبراهيم والتي كان يقيم فيها عبيده الآخرون، أي أن سارة منعت الجارية وابنها من السكن في خيمة السيد، وجعلتها تأخذ مكانها كجارية فقط.واختلفت الآراء في سارة، ولكنها كانت في الحق مؤمنة فاضلة وزوجة أمينة وأماً مثالية. وقد ماتت سارة في سن 127 سنة، بعد ولادة إسحق بما يزيد عل 36 سنة، ودفنها إبراهيم في حقل المكفيلة الذي اشتراه لهذا الغرض.'),
(1716, 'سارَح', 'اسم عبري معناه (( شارح )) وهو الشخص الذي يوضح.وسارح هي ابنة اشير، وقيل أنها الابنة الوحيدة، كما قيل أنها كانت متميزة بشخصيتها وبجمالها، وكان هذا سبب ذكر اسمها ( عدد 26: 46 تك 46: 17 و 1 أخبار 7: 30 ).'),
(1717, 'سارَد', 'اسم عبري معناه (( خوف )). وهو اسم رجل من زبولون قيل أنه بكره، وقد ورد اسمه مرتين في سلسلة الأنساب ( تكوين 46: 14 وعدد 26: 26 ).'),
(1718, 'ساردِس', 'كانت مدينة من أهم وأقدم مدن آسيا الصغرى، وكانت في أول الأمر تابعة للميونيّين ثم أتبعت لليديّين. وكانت واقعة في سفح جبل تمولوس على شاطئ نهر بكتولوس في وسط إقليم خصب وعلى مسافة خمسين ميلاً شرقي سميرنا.في سنة 546 قبل الميلاد استولى كورش الكبير على المدينة، وكانت إذ ذاك عاصمة لليديين وملكها ( كريسوس ) الملك الغني جداً. وصارت عاصمة المقاطعة الفارسية. وقد احرقها الاثينويون سنة 499 ق.م. وكان هذا سبب غزو فارس لبلاد اليونان على يد داريوس واكسركسيس. وفي سنة 334 ق.م. خضعت للإسكندر الكبير، وفي سنة 214 استولى عليها انطيوخس الكبير، ولكنه لم يقدر على الاحتفاظ بها بسبب هزيمته أمام الرومان في مغنيزيا سنة 190 ق.م. وقد ألحقها  الرومان بمملكة برغامس ولكنهم عندما أنشأوا إقليم آسيا سنة 129 ق. م. وقعت ساردس ضمن ولاية آسيا.وسكن اليهود فيها، وتأسست فيها كنيسة مسيحية ( رؤيا 1: 11 و 3: 1 و 4 ). وكانت على ما يُستنتج كنيسة كبيرة مشهورة، ولكنه لم تحفظ مكانها إذ انزلقت في الشهوات والنجاسات، ويبدو أن الثروة ضيعتها، فقد كانت الثروة تعود إلى الذهب الذي وُجد في رمال نهر باكتولوس، وهناك سُكت أول عملة ذهبية وفضية. وظلت الكنيسة محتفظة بمظاهر الحياة القوية ولكنها في الحقيقة ميتة. نعم كانت وسط حضارة عظيمة جداً، ولكنها لم تؤدي الرسالة الصحيحة، وقد جاءها الإنذار فلم تهتم به، ولذلك فقدت مكانها.وساردس اليوم قرية صغيرة اسمها (( سرت )) وتوجد في مكان المدينة العظيمة بقايا هيكل ارطاميس العظيم الذي بني في القرن الرابع قبل الميلاد، وكان هذا الهيكل قد بني في مكان هيكل أعظم، قيل أنه كان لسبيلي ( Cybele ). ونجد ملاصقاً لبقايا الهيكل في الجهة الشمالية بقايا جدران كنيسة مسيحية بنيت قبل القرن الرابع للميلاد.وقد اكتشف الباحثون أن بعض اللصوص كانوا يسكنون جبل تمولوس وكانوا ينزلون بين آن وآخر لسرقة آي مكان في ساردس، ولعل هذا سر إشارة الرب إلى مجيئه كلص ( رؤيا 3: 3 ) ويرّجح أن (( صفارد )) في عوبديا 20 تشير إلى ساردس.'),
(1719, 'سارَديُّون', 'انظر (( سارد )).'),
(1720, 'سارُون', 'هذه هي الصيغة اليونانية لكلمة عبرانية معناها (( سهل )) لم ترد إلا في سفر الأعمال 9: 35 بهذه الصيغة، ولكنها وردت في أماكن أخرى باسم شارون-اطلب (( شارون )).'),
(1721, 'ساريد', 'اسم عبراني معناه (( باقٍ بعد غيره ))، وهي قرية على حدود زبولون ( يش 19: 10 و 12 ). وقيل أن ساريد هي تل اشدود إلى الجهة الشمالية من سهل يزرعيل، مرج بن عامر على بعد خمسة أميال إلى الجنوب الغربي من الناصرة.'),
(1722, 'ساعير', 'انظر (( سعير )).'),
(1723, 'سَأف', 'وردت هذه الكلمة في بعض النسخ العربية ترجمة للكلمة (( شحف )) وهو طائر بحري، يسمّى زُمَّجُ الماء أو النورس، أجنحته وظهره بيضاء اللون مشربة باللون الرمادي، طويل الأجنحة، مكفّف الأرجل. وهو من الطيور النجسة عند اليهود ( لا 11: 16 ) وكان يكثر عند شواطئ فلسطين. وهذا الاسم عام لا يُطلق على طائر واحد، بل عدة أنواع من الطيور.'),
(1724, 'ساف', 'اسم سامي معناه (( حوض أو عتبة )) أحد جبابرة الفلسطينيين ( 2 صموئيل 21: 18 ) ويدعى أيضاً سفَّاي ( 1 أخبار 20: 4 ).'),
(1725, 'سَاكار', 'اسم عبراني معناه (( اجرة )).( 1 ) واحد من الهراريين، وهو أبو اخيام أحد أبطال داود ( 1 أخبار 11: 35 ) وقد وردت في 2 صموئيل 23: 33 شارار.( 2 ) بوّاب من اللاويين، وهو رابع أولاد عوبيد ادوم ( 1 أخبار 26: 4 ).'),
(1726, 'سَالَع أو سَالِع أو سِلَع', 'اسم عبراني معناه (( صخرة )) وهي أمنع موقع في أرض ادوم، كان يهرع إليها الادوميون كقلعة حصينة لا تُقهر وقت الحصار الحربي، لأنها تقع على قمة جبل. وقد وصف عوبديا اطمئنان الادوميين إليها في عوبديا 3.أخذها امصيا ملك يهوذا من ادوم ودعها يقتئيل ( 2 ملوك 14: 7 ) وقد تكون الإشارة على الصخرة التي وردت في قضاة 1: 36 عن هذا المكان. ويغلب انها هي المقصودة في 2 أخبار 25: 12 واشعيا 42: 11 وعوبديا 3، وربما أيضاً اشعياء 16: 1. وقد أقام من سكانها في الأعالي في شقوق الصخر ( عوبديا3 ). ويدعوا اليونانيون المكان (( بترا )) التي معناها صخر وترجمة كلمة سالع.وتقع سالع بقرب سفح جبل هور، في منتصف المسافة بين أريحا وجبل سيناء، وترتفع الجبال التي تخفي هذه المدينة فوق الحدود الشرقية للعربة التي هي الوادي العميق الممتد من البحر الميت إلى خليج العقبة.وفي القرن الرابع ق.م. انتقلت (( بترا )) من الادوميين إلى (( العرب النبطيين )) الذين جعلوها من أفضل البقاع الزراعية، بفضل نظام الري الرائع وخزانات المياه، فعمّروا الصحراء، كما استخدموا أفضل الأساليب الحربية المعروفة وقتئذ، وادخلوا عليها التحسينات. وكانت بلادهم مركز التجارة القادم من الشمال والجنوب والشرق والغرب وكانت الأسرة الحاكمة تضم عدداً من الملوك باسم (( الحارث )) ورد ذكر أحدهم في 2 كورنثوس 11: 32. وقد تزوج هيرودس ابنة الحارث، ولكنه طلقها حين تزوج امرأة أخيه.وانتهت مملكة النبطيين سنة 105 بعد المسيح، عندما هاجمها الإمبراطور الروماني تراجان، وصارت مدينة الصخر العربية الجميلة مقاطعة رومانية. وقد كشف مكانها المستكشف والرائد المشهور بركهادت عام 1812 بعد أن أخربت في عام 629، فتمت فيها نبوة ارميا ( 49: 16 و 17 ).ويزور سالع اليوم سياح كثيرون، ويمكن الوصول إليها من جهة الشرق عن طريق جسر اسمه السيق، ويبلغ طوله ميلاً واحداً، وهو محاط من جميع نواحيه بصخور ذات ألوان طبيعية رائعة تختلف عن فعل الماء. ويسمى هذا الجسر أيضاً باسم وادي موسى، ويزعم الاعراب الساكنون هناك أنه تخلف عندما ضرب موسى الصخرة بعصاه.ويخترق وادي السيق طولاً نهر صغير اسمه عين موسى، وجدران الوادي من صخور رملية منضدة ملونة بألوان قرمزية ونيلية وصفراء وارجوانية.وتجاه نهاية السيق هيكل منحوت في الصخر يسمى خزنة فرعون، يبلغ ارتفاعه 85 قدماً، وتفاصيل نحته محفوظة جيداً، ولا تزال خمسة من أعمدته الستة قائمة حتى اليوم.وداخل باب هذا الهيكل دار مربعة طولها وعرضها ستة وثلاثون قدماً، وارتفاعها خمسة وعشرون قدما، وعلى بعد نحو ست مئة قدم منه توجد بقايا مسرح عظيم، هو فخر سالع، قطره 117 قدماً، وفيها ثلاثة وثلاثون صفاً من المقاعد التي تسع بين ثلاثة وأربعة آلاف متفرج.ومن جملة غرائب سالع قصر فرعون وقوس النصر مع عدة هياكل وقبور، بعضها ذات شأن. ويتجشم الزائرون كثيراً من المتاعب قبل الوصول إليها، لأنها في داخل الصحراء.ويقول التقليد المسيحي أن بولس الرسول زار سالع هذه عندما ذهب إلا البلاد العربية ( غلاطية 1: 17 ) ولكن لا يوجد دليل على صدق هذا التقليد. غير أن المسيحية وصلت إليها غالباً عن طريق قوافل التجارة التي كانت تمر بها. واسم قلعة سالع اليوم (( أم البيَّارة )).'),
(1727, 'سالو', 'اسم عبراني ربما كان معناه (( موزون )). وهو اسم رئيس لبني شمعون ( عدد 25: 14 ) وهو أبو زمري الذي قتله فينحاس مع المرأة المديانية التي أدخلها زمري إلى خيام إسرائيل.'),
(1728, 'سالومة', 'اسم عبري مؤنث سليمان.( 1 ) زوجة زبدي وأم يعقوب ويحنا ( قارن متى 27: 56 ومرقس 10: 40 و 16: 1 ) وكانت إحدى النساء اللواتي أتبعن المسيح في الجليل وخدمنه ( مرقس 15: 40 و 41 )، وإحدى اللواتي شاهدن الصلب ( متى 27: 56 ) وذهبت إلى القبر مع النساء وهن يحملن الاطياب ( مرقس 16: 1 )، وهي التي طلبت من المسيح أن يجلس واحد من أولادها يمينه والآخر عن يساره ( متى 20: 2024 ). ويرجح البعض أنها أخت مريم أم يسوع مستنتجين ذلك من يوحنا 19: 25.( 2 ) يقال أن سالومة ابنة هيرودية هي التي رقصت في حفلة عيد ميلاد هيرودس، وطلبت رأس يوحنا المعمدان على طبق ( متى 14: 3-11 ) ولكن الأناجيل لا تورد اسمها.'),
(1729, 'ساليم', 'اسم عبراني معناه (( سلام )).( 1 ) مكان بالقرب من مياه عين نون حيث كان يوحنا المعمدان يجري تعميد الناس ( يوحنا 3: 23 ). والمكان غير معروف الآن بالضبط، على انه بحسب ما جاء في كتابات جيروم فإِن عين نون وساليم تقعان في وادي الأردن على بعد ثمانية أميال رومانية من سكيثوبوليس، والمظنون أن المكان هو احد المكنة الآتية:المكان المدعو الآن الدير حيث يوجد إلى جواره سبعة ينابيع، كما توجد خرائب كثيرة. أو انه المعروف بأم المعمدان وهو إلى الشرق قليلاً من المكان الأول.أو انه الخرائب والنبع في سفح تل ردغة التي لا تبعد عن سكيثوبوليس إلا سبعة أميال رومانية.أما في الخريطة الحديثة، فنحن نقرأ فعلاً أسماء عين نون وساليم، فهناك مدينة اسمها ساليم تقع على مسافة أربعة أميال شرق شكيم على سهل ممتد جنوب وادي فارعة. وعين نون هي خرائب ممتدة على منحدر وادي طوباس على بعد نحو عشرة أميال شمال شرق شكيم، وأربعة أميال شمال وادي فارعة.على أن اعتراضاً يقوم ضد اعتبار هذين المكانين هما مكان معمودية يوحنا، وهو ان عين نون هذه ليست بقرب ساليم، فإن المسافة بين القريتين تزيد على ثمانية أميالن ووادي فارعة يقع بينهما، كما أن عين نون أقرب إلى شكيم منها على ساليم، خصوصاً وانه يربط بينها وبين شكيم طريق. فإذا تركنا عين نون جانباً، فإننا نجد وادي فارعة الذي لا يبعد عن ساليم بأكثر من ثلاثة أميال ... أو الينبوعين اللذين يغذيان ساليم بالماء. ولكن هذه العيون توجد في أقليم السامرة، ويظن أن يوحنا المعمدان قد عمَّد في السامرة ( انظر متى 3: 5 و 10: 5 ولوقا 3: 3 ).ويرى آخرون أن المكان المقصود هو شلحيم، وهو مترجم في السبعينية (( سليم ))، ويقع في البرية في الجنوب الأقصى لليهودية، ويوجد بالقرب من مكان يدعى (( عين )) ( يشوع 15: 32 ) غير أن عين تُذكر دائماً مع رمون وليس مع شحليم.والأرجح أن عين نون هي في وادي فارعة آخر، وهو وادي منعزل فيه ينابيع فائضة على مسافة ستة أميال شمال شرق أورشليم. وأقرب الظن أن هذا المكان هو عين نون، لا لأن عين نون ذكرت، لكن لأنه يوجد على مسافة ميلين من هذا المكان وادي سليم.( 2 ) اختصار طبيعي لأورشليم مدينة السلام، أو أساس السلام ( مزمور 76: 2 ) ورما أيضاً ( تكوين 14: 18 )، انظر (( ملكي صادق )).'),
(1730, 'سام', 'اسم عبراني معناه (( اسم )) وهو أكبر أبناء نوح ( تك 5: 32 و 9: 23 - 27 و 10: 1 و 21 ) ولد حين كان عمر نوح خمسمئة سنة, وكان متزوجاً وقت الطوفان, لكن لم يكن له أولاد وقتئذ ( تك 7: 7 و 1 بط 3: 20 ) وبسبب تغطية عري أبيه مع أخيه يافث بعد الطوفان نال بركة, وفي البركة أن عبادة الله ستستمر في نسله ( تك 9: 23 - 27 ).\nوقد ولد ابنه الأكبر أرفكشاد حين كان عمره مئة سنة ( تك 11: 10 ) ثم رزقه الله بأولاد وبنات في السنوات الخمسمئة التي تلت ذلك حتى موته. وقد سكن نسله من أولاده الخمسة في رقعة الأرض الممتدة من عيلام في غرب آسيا حتى شرق البحر الأبيض المتوسط, ومن نسل سام اليهود والآراميون والآشوريون والعرب, ولذلك تدعى اللغات التي يتكلم بها نسل سام اللغات السامية نسبة إليه, مثل اللغة العربية واللغة العبرانية.'),
(1731, 'السامرة', 'اسم عبراني معناه (( مركز الحارس )).( 1 ) عاصمة الأسباط العشرة أثناء أطول مدة في تاريخهم. وقد بُنيت المدينة أو أصلح بناؤها أيام عمري بن آخاب ملك إسرائيل ( 876 - 842 ق.م. ) على تل اشتراه بوزنتين من الفضة ( أي نحو ألف جنيه مصري ) وكان صاحب الأرض اسمه (( شامر )) الذي يعني (( مراقب )) أو (( حارس )).والمدينة الواقعة على تل, أسماها عمري شوميرون بمعنى (( مكان المراقبة )) ( 1 مل 16: 24 ) وكانت فعلاً محصنة ببرج عظيم في الجنوب الغربي, وكان حولها سور عرضه خمسة أقدام. وقد أطلق عليها أحياناً بسبب تحصينها (( جبل السامرة )) ( عاموس 4: 1و 6: 1 ) وكانت قائمة في وسط وادٍ خصيب ( أشعياء 28: 1 ) وقد كان المكان حسناً جداً حتى أنه بقي عاصمة للملكة الشمالية إلى وقت السبي, وكان الملوك الحاكمون يقيمون فيها, وعند موتهم يدفنون فيها ( 1 ملوك 16: 28 و 29 و 20: 43 و 22: 10 و 37 و 51 ).وما أن بُنيت السامرة حتى قام نزاع بين بنهدد ملك آرام وعمري, فقد أقام ملك آرام أسواقاً في السامرة ( 1 ملوك 20: 34 ) وفي أيام آخاب قامت حرب أيضاً انكسر فيها آرام. وكانت هناك بركة قريبة غسلوا فيها العربة التي جرح فيه آخاب في موقعة راموت جلعاد. وقد أظهرت الاستكشافات قصر الملك آخاب وما كان فيه من أواني عاجية, وفي مبنى مجاور وُجدت لوحة مكتوب عليها بالعبرانية غالباً في أيام يورام عندما هاجم آرام السامرة وفشل ( 2 ملوك 6: 8 - 7: 20 ). وقد قتل شيوخ السامرة أبناء آخاب رغبة في إرضاء ياهو الذي قام على آخاب بثورة.كانت السامرة من البداءة مدينة وثنية وبنى فيها آخاب هيكلاً للبعل ( 1 ملوك 16: 32 ) ثم جلس أنبياء السواري أو أشيرة على مائدة الملكة إيزابل ( 1 ملوك 18: 19 ) وظل الوثن إلى أن قام ياهوبثورته ( 2 ملوك 13: 6 ) فحارب هذه الوثنية لكنها عادت فتملكت الأرض ( هوشع 8: 4 - 6 وعاموس 8: 14 ).وفي عام 724 ق.م. هاجم شلمنأَصر ملك أشور مدينة السامرة( 2 ملوك 17: 3 - 6 ) وتغلب عليها في عام 720 على يد خلفه سرجون الذي أخذ المدينة, وأسكن فيها الغزاة الأجانب.وفي عام 332 ق.م. استولى على المدينة الإسكندر الكبير ونقل سكانها إلى شكيم وأسكن بدلاً منهم مقدونيين وسوريين.وفي عام 120 ق.م. حاصرها يوحنا هيركانوس حصاراً طويلاً صمدت له المدينة سنة كاملة, ثم سقطت في يده بسبب الجوع. وأراد يوحنا هيركانوس أن يمحو ذكر المدينة ومسحها إلى الأرض. ولكنها عادت وعمرت بالسكان في أيام إسكندر جانيوس, وألحقها بومبي بمقاطعة سوريا, وحصنها جابينيوس من جديد. ثم أعاد هيرودس الكبير بناءها وتحصينها ودعاها سيباسطة, وهو اسم المؤنث من سيباسطوس الذي هو الاسم اليوناني لأغسطس القيصر الروماني. وبنى هيودس في السامرة هيكلاً رائع الجمال فوق موقع قصور الملوك الإسرائيليين القدامى, ولا زالت آثار هيكل هيرودس باقية إلى اليوم.وقد ذهب فيلبس الشماس إلى السامرة وبشر فيها, فآمن عدد كبير بالمسيح واعتمدوا, ومن بينهم سيمون الساحر. وأرسلت الكنيسة في أورشليم بطرس ويوحنا ليتفقدا أحوال الكنيسة في أورشليم بطرس ويوحنا ليتفقدا أحوال الكنيسة هناك ( أعمال 8: 5 - 25 ).وتقع مدينة سيباسطة أو السامرة على تل على مسافة خمسة أميال ونصف شمال غرب شكيم, والتل منحدر, ولكن القمة مستوية. ويبلغ طولها ميلاً من الشرق إلى الغرب. والقرية موجودة على هذا التل تسمى (( سبسطية )).( 2 ) السامرة أيضاً اسم الإقليم الذي عاصمته مدينة السامرة, وهو الذي احتله الأسباط العشرة. والسامرة اسم المملكة الشمالية, وعندما نقول السامرة نقصد مملكة إسرائيل ( 1 ملوك 21: 1و 2 ملوك 17: 24 وأشعياء 7: 9 وإرميا 31: 5 وحزقيال 16: 46 ).( 3 ) إقليم السامرة ويضم وسط فلسطين ويقع بين الجليل في الشمال واليهودية في الجنوب. وقد وصف يوسيفوس هذا الإقليم كما كان في أيام المسيح, وتمر الحدود الشمالية فيه في قرية تدعى جينية وهي غالباً المكان المعروف اليوم باسم جنين. وامتدت السامرة إلى الأردن شرقاً, ولكنها لم تصل إلى البحر الأبيض المتوسط. ويقول التلمود اليهودي أن حدها الغربي أنتيباترس, وقد ضمت أراضي منسى غرب الأردن وأفرايم ويساكر وجزءاً من بنيامين. وفي عام 63 ألحقها بومبي بولاية سوريا وفي عام 6 ق.م. أقام الإمبراطور أغسطس عليها حاكماً, وكانت هذه حالتها وقت ظهور يسوع المسيح.'),
(1732, 'السامريون', 'المرة الوحيدة التي وردت فيها هذه الكلمة في العهد القديم في سفر الملوك الثاني 17: 29 وتعني السكان المتصلون بالمملكة الشمالية.وفي كتابات العبرانيين المتأخرة التي جاءت بعد السبي كان معناها سكان إقليم السامرة الذي يقع في وسط فلسطين ( لوقا 17: 11 ).وعندما غزا سرجون السامرة عام 722 ق.م. سبي من سكانها 27280 شخصاً. وترك بعض السكان الأصليين, وإذ وجد أنهم متمردون دبّر خطة يقتل بها وطنيتهم الثائرة, فنقل شعباً من بابل وحماة والعربية إلى السامرة ( 2 ملوك 17: 24 ) وصار هؤلاء هم السامريين, وظلوا يمارسون عباداتهم التي اعتادوها قبل المجيء إلى السامرة.وكان بسبب الحروب المتواصلة أن قلّ عدد السكان, فكثرت الوحوش البرية في الأرض التي استعملها الله عصا تأديب وقد قتلت بعض تلك الوحوش سكان الأرض الجدد, فاعتقدوا أن (( إله الأرض )) غاضب عليهم, فأرسلوا يستغيثون بملك آشور, الذي أرسل إليهم أحد الكهنة ليعلمهم فرائض إله الأرض وجاء الكاهن وسكن في بيت إيل. على أن الكاهن لم يقدر أن يجعلهم يتركون عبادات أصنامهم, فظلوا يمارسون عبادة الله كما في أسفار موسى. كما يمارسون عبادة الأصنام ( 2 ملوك 17: 25 - 33 ) وظلوا يمارسون هذه العبادة المزدوجة حتى سقوط أورشليم عام 586 ق.م. ( 2 ملوك 17: 34 - 41 ) وظل أسرحدون ينفذ الخطة التي نفذها جده سرجون ( عزرا 4: 2 ).وحدث أن اليهود ثاروا على عبادة الأصنام ( 2 أخبار 34: 6 و 7 ) فتناقضت تلك العبادة... ثم ضرب يوشيا الملك الوثنية ضربة أخرى. وبعد عشرات السنين كان بعض السامريين يذهبون إلى الهيكل في أورشليم للعبادة أو الزيارة. وعندما عاد المسبيون جاء السامريون وطلبوا من زربابل أن يشتركوا معه في بناء الهيكل قائلين أنن كنا نعبد الر إله إسرائيل منذ أيام أسرحدون ( عزرا 4: 2 ) ولكن زربابل رفض الطلب, فلم يطلب أهل السامرة الاشتراك في البناء مرة أخرى, بل عملوا على محاربة اليهود في البناء, وانضموا إلى أعداء اليهود في تعطيل البناء, كما عملوا بعد ذلك على تعطيل بناء السور ( نحميا 4: 1 - 23 ). وكان قائدهم في هذه الحركة الأخيرة سنبلط الحوروني. وكان منسى الكاهن, وهو واحد من بني يوياداع بن ألياشيب الكاهن العظيم صهراً لسنبلط, فطرده نحميا من الكهنوت, فاغتاظ سنبلط من ذلك كثيراً وساعد نسيبه الذي التجأ إليه فبنى هيكلاً في جرزيم وكان بعض اليهود الهاربين من القانون في أورشليم يذهبون إلى هيكل جرزيم للعبادة, فكانوا يقابلونهم بترحيب كبير.واستمر عداء السامريين لليهود, فعندما نجَس أنطيوخس أبيفانيس هيكل أورشليم بتقديم خنزيرة على مذبحه, أعلن السامريون أنهم لا ينتمون إلى الأصل اليهودي أبداً, وأعلنوا ولاءهم للطاغية بأن كرسوا هيكلهم على جبل جرزيم هيكلاً للإله زفس حامي الغرباء.وفي عام 128 استولى يوحنا هيركانوس على شكيم وجرزيم وأخرب الهيكل هناك بعد بنائه بمئتي سنة, ولكن السامريين ظلّوا يقدّمون قرابينهم على الجبل حيث كان هيكلهم. وكانوا يفعلون هذا حتى جاء المسيح إلى أرضنا ( يوحنا 4((20 و 21 ). وفي عام 6 ق.م. ألقى بعض السامريين عظاماً نجسة في هيكل أورشليم, فصار اليهودي يستنكف من أن ينجس شفتيه بنطق كلمة (( سامري )), وكان يحسب طعام السامري نجساً كلحم الخنزير.وهكذا كان العداء مستحكماً بين اليهود والسامريين, ولم يكن اليهود يسمحون بأي علاقة اجتماعية أو دينية مع السامريين.وفي زمن المسيح لم تكن عقائدهم اللاهوتية تختلف عن عقائد اليهود وخصوصاً عقائد الصدوقيين منهم, وكانوا مثلهم ينتظرون (( المسيا )) على أنهم لم يقبلوا من العهد القديم إلا أسفار موسى.وقد قبل السامريون رسالة المسيح بعد أن رأوا الآيات العظيمةعلى يد فيلبس, كما أن المسيحية اتسعت لقبولهم, بخلاف الديانة اليهودية الضيقة,. لكن إقبالهم كان ضعيفاً.ولا تزال هناك جماعة قليلة من السامريين تقيم في نابلس ( شكيم القديمة ) وحولها. وهم يصعدون إلى جبلهم جرزيم ثلاث مرات في السنة, وفي عيد الفصح وعيد الأسابيع وعيد المظال, وهم يعيّدون الأعياد الموسويّة, ويذبحون ذبائح دمويّة في عيد الفصح..التوراة السامرية:احتفظ السامريون بالتوراة العبرانية ( الأسفار الخمسة الأولى من العهد القديم ) وقد اقتبس من هذه التوراة السامرية جيروم ويوسابيوس وغيرهما من الآباء المسيحيين. ومعظم الأدراج السامرية التي تشتمل الأسفار الخمسة كلها أو جزءاً منها لا ترجع إلى ما قبل القرن العاشر الميلادي مع أنه توجد نسخة تدعي أن الأجزاء القديمة منها كتبت في عام 656 ميلادي. ولدى جماعة من السامريين في نابلس نسخة خطية يقولون أنها نسخت عام 13 بعد فتح كنعان. إلا أن جمهور العلماء يقولون أن الخط الذي كتبت به  هذه النسخة يرجع إلى القرن الثالث عشر الميلادي. وهذه الأدراج مكتوبة بالخط السامري الذي يشبه الخط الموجود على النقود التي وصلتنا من عصر المكابين. وكان العبراني يكتب بهذا الخط قبل البدء في استخدام الحروف المربعة المستعملة في الوقت الحاضر.ويختلف النص السامري عن النص العبري فيما يقرب من ستة آلاف موضع, فمثلاً أبدلت التوراة السامرية عيبال بجرزيم ( تث 27: 4 و 8 ) زيادة في إكرام جبلهم المقدس وتعزى معظم هذه الاختلافات إلى أخطاء في النقل وقعت من النساخ وقت الكتابة أو إلى أخطاء متعمدة قاموا بها عن قصد وإصرار. ويتفق نص التوراة السامرية مع الترجمة السبعينية في ألف وتسعمائة موضع من هذه المواضع مما يدل على أن مترجمي السبعينية استخدموا نسخة عبرية تتفق مع النسخة السامرية إلا أن هذه الاختلافات ليست ذات بال. وربما ترجع التوراة السامرية إلى العصر الذي طرد فيه منسى حفيد ألياشيب رئيس الكهنة وصهر سنبلط من أورشليم ( نح 13: 23 - 30 آثار يوسيفوس 11: 7 و 8 ) والتجأ إلى السامريين, فبنى هيكلاً على جبل جرزيم لينافس هيكل أورشليم, وإذا كانت قانونية الأسفار الخمسة قد تقررت حوالي عام 400 ق.م. فلا تكون التوراة السامرية في هذه الحالة قد تأثرت بالتوراة التي كانت في حوزة اليهود بعد ذلك التاريخ. ويظهر أن الشقاق بين اليهود والسامريين وقع قبل تقرير قانونية الأنبياء.وينبغي أن لا يختلط الأمر علينا في أن التوراة السامرية تختلف عن النسخة السامرية للتوراة التي وضعت في لهجة السامريين في أوائل العهد المسيحي وفي حوزة السامريين ترجمة عربية أيضاً ترجمت في القرن الحادي عشر أو القرن الثاني عشر. وكذلك سفر يشوع مبني على سفر يشوع القانوني وكتب في القرن الثالث عشر الميلادي وغيره.'),
(1733, 'ساموثراكي', 'اسم يوناني معناه (( مرتفع ثراكي )) وهي جزية في بحر إيجة بالقرب من شاطيء ثراكي. وتزيد مساحتها قليلاً على ثلاثين ميلاً مربعاً, وفيها جبال يزيد أعلاها ارتفاعاً على 1500 متراً. وكانت جزيرة ساموثراكي مشهورة بكثرة المعابد واللوحات والأبنية التذكارية الدينية المختلفة. وقد اتجهت سفينة بولس الرسول من ترواس إليها مباشرة ( أعمال 16: 11 ) ومع أنه كان من الصعب وجود موسى صالح للسفن إلا أن البحارة كانوا يفضلون قضاء الليل فيها على المخاطرة ليلاً في وسط البحر.'),
(1734, 'ساموس', 'اسم يوناني معناه (( مرتفع )) وهي جزيرة جبلية تبلغ مساحتها 165 ميلاً مربعاً في بحر اليونان, وترتفع جبالها إلى علو أكثر من 1500 متراً فوق سطح البحر, ومن هذا الارتفاع أخذت اسمها وتقع ساموس بالقرب من شاطيء آسيا الصغرى إلى الجنوب الغربي من أفسس, وكانت مشهورة بالفخار النفيس كما كانت مركزاً لعبادة الآلهة هيرا.\nوقد مرت جزيرة ساموس في ظروف سياسية مختلفة ما بين لستقلال, وحماية رومانية, وعندما زارها بولس الرسول في طريق عودته من رحلته التبشيرية الثالثة كانت في دائرة النفوذ الروماني ( أعمال 20: 15 ) وسكان ساموس مشهورون بالتجارة, ومحاصيلها بالزيت والخمر والبرتقال والعنب والزبيب والحرير.'),
(1735, 'سَبا', 'ويحتمل أن هذا هو اللفظ العربي للاسم شبا:( 1 ) أكبر أبناء كوش ( تكوين 10: 7 و 1 أخبار 1: 9 ).( 2 ) بلاد سبا: في جنوب جزيرة العرب, ويردُ ذكرها في الكتاب مع مصر والحبشة, ( مزمور 72: 10 وأشعياء 43: 3 ).أما سبا أو سبأ في ملوك الأول 10: 1 و 4 و 10 و 13 و 2 أخبار 9: 1 و 3 و 9 و 12 وأيوب 6: 19 فهي شبأ في الأصل العبراني, رغم أنها مترجمة سبا ( انظر (( شبا ))  )ملكة سبا:يروي السفر المقدس أن ملكة سبا قد زارت الملك سليمان بعد أن سمعت عن حكمته ( ملوك الأول 10 ). ويسميها المسيح ملكة التيمن, بمعنى ملكة الجنوب ( متى 12: 42 ).وتقول التقاليد العربية أن اسمها بلقيس. وأنها ولدت ابناً من سليمان, ولكن لا يوجد دليل تاريخي صحيح يبرهن هذه التقاليد.ويقول التقليد الحبشي أن سلسلة ملوك الحبشة يرجع نسبها إلى سليمان عن طريق هذه الملكة ولذلك يلقب ملك الحبشة نفسه بالأسد الخارج من سبط يهوذا.'),
(1736, 'سَبَئِيُّون', 'أهل سبا, وهم المذكورون في أيوب 1: 15, وهم شعب طوال القامة ( أشعياء 45: 14 ) وكانوا يغيرون على البلاد ويسبون ساكنيها كما فعلوا مع أيوب, وهم يتنقلون من بلد إلى بلد ( أيوب 6: 19 ) وكانوا يتاجرون في العبيد ( يوئيل 3: 8 ) ويعتقد دلمان أنهم فرع من الكوشيين. وقد يكونون من نسل سبا بن كوش ( تكوين 10: 7 ) وكانوا أهل حضارة ويشتغلون بالتجارة فتاجروا في الذهب والعطور, ولم تقتصر تجارتهم على حاصلاتهم المحلية بل امتدت إلى حاصلات الهند والحبشة. وانتشر أهل سبا في الأراضي حتى وصلوا إلى شمال غرب بلاد العرب في أيام الآشوريين في القرن الثامن قبل الميلاد, ووصلوا إلى شمال الصحراء مع النبطيين, كما امتزجوا بالقبائل الأخرى عن طريق الزواج والارتباطات السياسية, وكان من تأثير ذلك أن اختلطت سلاسل أنسابهم.ومن قصة زيارة ملكتهم لسليمان في أورشليم نرى أنهم كانوا يعطون المرأة مكاناً عظيماً, وكانوا لا يتزوجون أكثر من واحدة... وكانوا يعبدون الشمس.وكانت عاصمتهم تسمى مأرب وقد أقاموا فيها سداً عظيماً ذا شهرة واسعة ثم بنوا هيكلاً للقمر يسمى الآن هيكل (( بلقيس )) وقد اكتشف هذا الهيكل حديثاً, ووجد فيه شيء من بدائع الفن والأعمدة الهائلة والأسوار المنيعة والتماثيل الرائعة.'),
(1737, 'سبائيون', 'انظر (( سبئيون ))'),
(1738, 'سبولت', 'انظر (( شبولت )).'),
(1739, 'سَبْت', 'كلمة عبرانية معناها (( راحة )), وقد بدأ التفكير في يوم السبت على أنه اليوم الذي يترك فيه الإنسان أشغاله المادية حتى يستريح قديماً, وذلك تذكاراً لليوم السابع من الخليقة (( لذلك بارك الله يوم السبت وقدسه لأن فيه استراح الرب من جميع أعماله )) ( تكوين 2: 1 - 3 ) ويقول سفر الخروج 20: 8 - 11 يجب أن نستريح في اليوم السابع لأن الله استراح فيه من الخليقة. وقد منع الله نزول المن لإسرائيل في اليوم السابع حتى يستريحوا ( خروج 16: 22 - 30 ).ثم تطور التفكير عن يوم السبت حين أمر الله في الوصية الرابعة بحفظ السبت لأن: (( الله بارك يوم السبت وقدسه )). وأمر الله أن يستريح الإنسان والحيوان ونزيل البيت في السبت, لا لأنه استراح فيه فحسب, بل لأنه باركه وقدسه أيضاً. وعلى هذا فإنه عندما كسر أحد اليهود السبت قتلوه بدون رحمة ( عدد 15: 32 - 36 ).ومن هذا نرى أن يوم السبت كان يوماً واضح المعالم مرعياً في إسرائيل من الجانب الديني للعبادة, ومن الجانب الاجتماعي لإراحة المشتغلين والعبيد ( تثنية 5: 12 - 15 ).وقد حاول البعض أن يرجعوا السبب في حفظ يوم السبت إلى حفظ البابليين له, فقد كان هؤلاء يحفظون اليوم السابع والرابع عشر والحادي والعشرين والثامن والعشرين من كل شهر, مهما كان اسم اليوم, وكانت شرائعهم تقول أن الملك لا يأكل اللحم المطبوخ على الفحم في هذه الأيام, ولا يغير ثياب جسده, ولا يلبس ثياباً نظيفة, ولا يقم ذبيحته, ولا يركب في عربة, ولا يتكلم في قضية, ولا يجوز للرائي في هذه الأيام أن يقدم للناس ما يرى, ولا يجوز للطبيب أن يضع يده على جسد إنسان. وعند المساء يأتي الملك بتقدماته للآلهة.لكن هذه الأيام كانت تحفظ تبعاً لنور القمر, وواضح من التاريخ الكتابي أن حفظ يوم السبت عند اليهود كانت تبعاً لنور أعظم من نور القمر, وهو الأمر الإلهي ونور الإعلان السماوي, فلا علاقة أساسية بين الغرض من تقديس يوم الله ويوم بابل, فإن السبت العبري يوم عينته إرادة الله.وقد بقي اليهود يحفظون يوم السبت بمواظبة, حتى تطرفوا في ذلك, فحفظوه حفظاً حرفياً أحياناً, وخلطوه بعبادات الأوثان أحياناً أخرى, فأرسل لهم الله الأنبياء ليرشدوهم إلى حفظ السبت حفظاً روحياً, حسب رغبة الله ( 2 ملوك 4: 23 وعاموس 8: 5 وهوشع 2: 11 وأشعياء 1: 13 وحزقيال 46: 3 ).وفي فترة السبي التي قضاها اليهود في بابل نسوا حفظ السبت, فبدأ رجال الله يشدّدون على حفظه بعد العودة إلى كنعان, وجاهد نحميا جهاد الأبطال ليعيد إلى يوم السبت مكانته القديمة ( نحميا 10: 31 و 13: 15 - 22 ).وفي فترة ما بين العهدين انتشرت مجامع اليهود, فكانوا يقضون يوم السبت في دراسة الناموس وفي الراحة من أشغالهم العالمية. وقد شدّدوا في حفظ يوم السبت حتى أنهم لم يرفعوا سلاحاً ضد مهاجميهم في هذا اليوم المقدس, فأهلك المهاجمون منهم كثيرين ( 1 مكابين 2: 29 - 38 ). ولكنهم عادوا وتجاوزوا عن الحرب في يوم السبت للدفاع عن النفس في حالة الهجوم عليهم ( 1 مكابين 2: 39 - 41 ).وفي الفترة الواقعة بين عزرا والمسيح زاد اليهود عدداً من القوانين التقليدية التي يجب حفظها في يوم السبت, تاركين الرحمة والحق التي هي الأمور الرئيسية الواجبة فيه. وعندما جاء المسيح كان موضوع حفظ السبت هو مادة النزاع الأولى بين المسيح وبين شيوخ اليهود. فقد أرادوا حفظ اليوم حرفياً كعبيد للسبت, بينما علم المسيح أن السبت إنما جعل لأجل الإنسان ( مرقس 2: 27 ).ولم يجرد المسيح يوم السبت من قيمته كيوم للعبادة, فقد ذهب دوماً إلى المجامع للصلاة في يوم السبت ( لوقا 4: 16 ) ولكنه كان يتحنن ويعمل المعجزات في يوم السبت لأنه رب السبت ( مرقس 2: 28 ). وكان يريد ليوم السبت أن يكون يوم الخدمة وعمل الرحمة.وقد قدس المسيحيون الأولون يوم السبت, ولكن اليوم الأول من الأسبوع أي ( الأحد ) حلّ تدريجياً محل اليوم السابع, وكان المسيحيون الأولون يجتمعون فيه للصلاة, فقد جعلت قيامة ربنا قيمة خاصة لهذا اليوم الأول من الأسبوع.وفي قرار المجمع المسيحي الأول لم يفرض قادة الكنيسة الأولى حفظ يوم السبت اليهودي على أحد ( أعمال 15: 28 إلخ  ) فلم تعد هناك إلزامية حفظ يوم السبت اليهودي.. وقد نقل المسيحيون إلى اليوم الأول من الأسبوع أفضل ما في السبت اليهودي, وتخلصوا من كل الأخطاء التي ألصقها به اليهود.على أن هذا لا يعني عدم حفظ يوم الأحد بدقة, فإن السبت كناموس أدبي أمر باقٍ. والسنَّة التي بني عليها لا تتغير بتغيير السبت إلى الأحد, فإنه يجب علينا أن نستريح يوماً في كل أسبوع بعد الكدّ والتعب.. كما أننا ينبغي أن نعطي الله سبع الوقت مكرساً تماماً له.غير أن غاية المسيحي من حفظ الأحد تختلف عن غاية اليهودي من حفظ السبت, فإن المسيحي ينظر إلى يوم الأحد واثقاً بالفادي الذي قام فيه منتصراً من الأموات ليتمم له عمل الفداء..وهناك جماعة من المسيحيين يفتكرون أن المسيحيين ينبغي أن يحفظوا يوم السبت لا يوم الأحد... لكن قيامة المسيح غيّرت يوم السبت إلى الأحد بقوة إلهية, وقد اعتاد المسيحيون الأولون أن يجتمعوا للعبادة المسيحية في أول الأسبوع كما هو ظاهر في الإنجيل. وكان بعض المسيحيين الأولين يحفظون كلاً من السبت اليهودي ويوم الرب المسيحي واستمر هذا مدة أربعة قرون, ثم انتهى أمره بعد أن منعه مجمع لاودكية الكنسي في عام 364 م. واعتمدوا في ذلك على اجتماع المسيح بتلاميذه في اليوم الأول من الأسبوع دوماً.ويخبرنا تاريخ الكنيسة أنها حفظت اليوم الأول من الأسبوع بناء على أوامر الرسل. وقد كتب أغناطيوس داعياً بحفظ يوم الأحد كيوم الرب الذي به قيامة الحياة لنا, وقال الشهيد جستينوس: (( نجتمع سويّة يوم الأحد لأنه اليوم الأول الذي فيه غيّر الله الظلمة إلى نور, والعدم إلى وجود. وفي هذا اليوم قام مخلصنا يسوع المسيح من الأموات )), وشهد أثناسيوس الإسكندري: (( إن الله قد غيّر يوم السبت إلى يوم الرب )): وقال يوسيبيوس العالم في أصول الديانة المسيحية: (( والكلمة ( المسيح ) بالعهد الذي قطعهمعنا غيّر وليمة السبت إلى نور الصباح وأعطانا المخلّص يوم الرب رمز الراحة الحقيقية. ففي هذا اليوم يجب أن نسلك بموجب الشريعة الروحية, وكل ما يمكننا أن نعمله يوم السبت فقد نُقل إلى يوم الرب, وقد أُعلن لنا أنه يجب أن نجتمع في مثل هذا اليوم )).ومن الأدلة الكتابية على حفظ الأحد بدل السبت يوحنا 20: 19 وأعمال 20: 7 و 1 كورنثوس 1: 2 ورؤيا 1: 10.سَفَر سبت:( انظر (( مقياس ))  ).'),
(1740, 'سَبْتَا وسَبْتَة', 'اسم الابن الثالث من أبناء كوش ( تكوين 10: 7 و 1 أخبار 1: 9 ) والمظنون أن أولاده أقاموا في جنوب بلاد العرب وأن اسم مدينتهم كان شبوة عاصمة حضرموت.'),
(1741, 'سَبَتْكا', 'اسم الابن الخامس والأصغر من أولاد كوش ( تكوين 10: 7 و 1 أخبار 1: 9 ). والمظنون أن أولاده سكنوا في مكان ما من بلاد العلاب عند الخليج الفارسي.'),
(1742, 'تسبيحة', 'جاء في العهد الجديد ذكر التسابيح مع المزامير والأغاني الروحية ( أفسس 5: 19 وكولوسي 3: 16 ) وقد كان بولس وسيلا يصليان (( ويسبحان الله )) وهما في سجن فيليبي ( أعمال 16: 25 ). وقد كان المسيح في هذا مثالاً للمسيحيين جميعاً عندما سبح وخرج إلى جبل الزيتون استعداداً للصليب ( متى 26: 30 ).والمسيحية هي الديانة الوحيدة التي تحتوي على التسابيح والأغاني الروحية لأنها ديانة الفرح الروحي العميق.'),
(1743, 'سَبرائم', 'كلمة عبرانية ربما كان معناها (( الأمل المضاعف )) وهي بقعة مرتفعة في شمال فلسطين بين تخوم دمشق وحماة ( حزقيال 47: 16 ), ولا يستبعد أن تكون هي سفروايم ( 2 ملوك 17: 24 و 31 ) التي أخربها شلمنأَصر, كما قد تكون خربة سنبرية هي مكان سبرائم القديمة, وهي تقع على الضفة الغربية من نهر الحصباني على بعد ثلاثة أميال جنوب شرقي آبِل.'),
(1744, 'سبط', 'اسم من كلمة عبرانية لفظها (( شبط )) ومعناها (( عصا )) أو (( جماعة يقودها رئيس بعصا )), وكانت تطلق عادة على كل من أفرايم ومنسى ابن يوسف. ولم يكن سبط لاوي محسوباً من ضمن الأسباط, فكان عدد الأسباط إثني عشر سبطاً, لأن ألإرايم ومنسى أُضيفا بدل يوسف ( عدد 26: 28 ) وهكذا تقسمت أرض كنعان إلى إثني عشر قسماً, أما سبط لاوي  فقد تعين للخدمة في الهيكل, وكان باقي الأسباط يعولونخم.وكان لكل سبط رئيس ( عدد 1: 16 و 1 أخبار 27: 22 ) كما كان لكل سبط استقلال ذاتي ولكنه كان يرتبط بمعاهدة مع باقي الأسباط. وكثيراً ما حارب سبط مع سبط آخر أو على حدة ( قضاة 1: 3 و 1 أخبار 4: 42 و 43 و 5: 10 و 18 - 22 ) كما كان بعض القضاة على سبط واحد أو على عدة أسباط.وبقي الأسباط الإثنا عشر مرتبطين في مملكة واحدة حتى مات الملك سليمان, فحدثت بينهم مخاصمات ومشاحنات, وحدثت خصومة بين يهوذا وأفرايم ( 2 صموئيل 2: 4 - 9 و 19: 41 - 43 ) انتهت إلى انقسام المملكة إلى قسمين: فانحاز يهوذا وبنيامين إلى رحبعام ابن الملك سليمان ودعوا مملكتهما باسم (( مملكة يهوذا )) أو (( المملكة الجنوبية )), وانحاز الأسباط العشرة الباقون إلى يربعام بن نباط, ودعوا أنفسهم (( مملكة إسرائيل )) أو (( المملكة الشمالية )).وقد عيّن المسيح اثني عشراً رسولاً بناءً على عدد الأسباط الاثني عشر. وفي سفر الرؤيا يقسم يوحنا المناظر السماوية التي رآها كالأختام والأبواب والأساسات إلى أثني عشر ( رؤيا 7: 4-8 و 21: 10-21 ).وأسماء أسباط بني إسرائيل حسب الترتيب الأبجدي هي: اشير، افرايم، بنيامين، جاد، دان، رأوبين، زبولون، شمعون، لاوي، منسى، نفتالي، يساكر، يهوذا، ولمعرفة شيء عن كل منهم اطلب كل اسم في مكانه.'),
(1745, 'سبعة. سابع', 'ويرمز العدد سبعة في الكتاب المقدس إلى التمام والكمال، فعدد أيام الأسبوع سبعة ( تكوين 2: 2 ) وحذر الله نوحاً قبل الطوفان، ثم قبل نزول المطر بسبعة أيام وعندما أرسل نوح الغراب والحمامة كان ذلك بعد سبعة أيام ( تكوين 7: 4 و 8: 10 و 12 ) وكان عدد الحيوانات الطاهرة التي دخلت الفلك سبعة ( تكوين 7: 2 ) والعاصفة التي جاءت بعد الطوفان ضُبطت في آخر اليوم السادس، وأول يوم أشرق بالصحو كان اليوم السابع، وكذا كان السابع هو الذي استقر فيه الفلك وقُدمّت فيه ذبائح الشكر. وفي حلم فرعون الذي فسَّره يوسف كان عدد البقرات والسنابل سبعة ( تكوين 41: 2-7 ) وكان اليهود يحتفلون باليوم السابع للعبادة، وبالسنة السابعة، وكانت سنة اليوبيل سبع سنين سبع مرات، وكانت أعياد الفطير والمظال سبعة أيام وكانت الذبائح فيها سبعة, وكان الدم يرش على المذبح في يوم الكفارة سبع مرات ( لاويين 16: 14 و 19 ) وكانت المنارة ذات سبع فروع, وطاف الكهنة حول أسوار أريحا يضربون بسبعة أبواق, وفي اليوم السابع طافوا سبع مرات, وكتب يوحنا الرائي في سفر الرؤيا إلى سبع كنائس, ورأى سبع منائر وسبعة أرواح وسبعة ختوم وسبعة أبواق وسبعة رعود وسبع جامات وسبع ضربات. وبالاختصار ورد ذكر السبعات أكثر من ست مئة مرة في الكتاب المقدس.وكان البابليون الساميون يطلقون كلمة واحدة على العدد سبعة وعلى كلمة (( كل )), كما كان العدد سبعة تعبيراً عن أ\'ظم قوة وعن كمال العدد.أما عن مضاعفات السبعة فلها نصيب مهم: فالعدد 14 مهم في حساب عيد الفصح ( خروج 12: 6 و 16 ) والعدد 49 كان يحدّد اليوبيل ويوم الخمسين وحلول الروح القدس, والعدد 70 كان يشير إلى عدد كبير من الجمهور ( خروج 1: 5 و 24: 1 قضاة 1: 7 ) والعدد 77 كان يظهر الفخامة, ويظهر في كلمات لأمك ( تكوين 4: 24 ) وفي عظمة التقدمات ( عزرا 8: 35 ) أما سبعين مرة سبع مرات فيشير إلى الدوام ( متى 18: 22 ) والعدد سبعة آلاف فقد أشار إلى عظمة العدد أيضاَ ( 1 ملوك 19: 18 ورومية 11: 4 ).أما نصف العدد سبعة فكان يشير إلى الضيق وهو نصف الكمال ( رؤيا 11: 11 ودانيال 7: 25 ).'),
(1747, 'سابق', 'وردت في عبرانيين 6: 20 ويُراد بها المسيح الذي دخل سابقاً المجد لأجلنا, إلى حضرة الله, إلى ما وراء الحجاب. والسابق هو من يذهب ليجهز المكان أولاً, أو ليقود إليه.والسابق في ثمر التين هو الباكورة ( 1 كورنثوس 15: 20 و 23 ).كما أن السابق هو من يسبق الحصان أو العربة لتهيئة الطريق أمام عظيمٍ ( استير 6: 9 ).'),
(1748, 'سَبْكاي', 'اسم عبراني ربما كان معناه, (( غابة يهوه )) وهو أحد أبطال الملك داود المعدودين, ويُطلق عليه اسم مبوناي ( 2 صموئيل 23: 27 ) وهو الذي قتل سأف أو سفاي الذي هو أحد أولاد رافا ( 2 صموئيل 21: 18 و 1 أخبار 11: 29 ).'),
(1749, 'سَبْمَة سِبْمَة', 'إحدى مدن الرعي التي وقعت في قرعة رأوبين وجاد ( عدد 32: 38 ) وفي يشوع 13: 9 وردت سِبْمة, ولا ندري كيف استرجعها موآب بعد مدة. وقد كانت مشهورة بكرومها ( أشعياء 16: 8 و 9 ) ويقول جيروم أنها واقعة على مسافة نصف ميل من حشبون, وقد تكون هي سومية الحالية التي تقع على الجانب الغربي من وادي حسبان, على بعد ميلين من حسبان, ولازالت آثار معاصر العنب الحجرية القديمة باقية فيها إلى اليوم. وورد اسم هذه المدينة بصورة (( سام )) أو (( شباء )) في عدد 32: 3.'),
(1750, 'سَبي', 'السبي هو حالة الوجود تحت عبودية الأسر, على الأخص في أرض غريبة, وقد اعتاد الكلدانيون أن ينقلوا أهل البلاد التي يفتحونهاإلى بلاد أخرى حتى يفقدوا حماسهم الوطني, بعيداً عن ذكريات الوطن.ويذكر الكتاب المقدس سببين هامين وقعاً للعبرانيين بسبب الخطيئة والبعد عن الله:السبي الأول سبي الأسباط العشرة أو مملكة إسرائيل الشمالية, ففي عام 842 ق.م. كان ياهو يؤدي الجزية إلى شلمنأَصر ملك آشور, وأثناء حكم تغلث فلاسر( 746 - 728 ق.م ) بدأ الآشوريون يسبون سكان الأرض. وفي أثناء حكم فقح سبي سبط نفتالي ( 2 ملوك 15: 29 ) ثم سبي بقية السكان من الروأبينيين والجاديين ونصف سبط منسى إلى ما بين النهرين ( 1 أخبار 5: 26 ) ثم حوصرت مدينة السامرة وسقطت بيد سرجون عام 722 ق.م. وسبي السكان إلى مادي وما بين النهرين ( 2 ملوك 17: 5 و 6 و 18 ) أما الباقون في الأرض فقد دفعوا الجزية. وملأ الآشوريين الأرض بغرباء أتوا بهم من بلاد أخرى, فامتلأت الأرض بعبادة أصنام هؤلاء الغرباء.وبعد أن ردّ الرب سبي الشعب بقي كثيرون من اليهود في الخارج, وكانوا يزورون أورشليم بين آن وآخر ( أعمال 2: 8 و 9 ).أما السبي الثاني الهام فهو سبي يهوذا, وقد سبق أشعياء وتنبأ عن هذا السبي قبل وقوعه بمئة وخمسين عاماً ( أشعياء 6: 11 و 12 ) وتنبأ إرميا أن مدة السبي ستكون سبعين عاماً ( 25: 1 و 11 و 12 ).وقد تم هذا السبي على يد نبوخذ نصر في أربع مراحل في عام 605 ق.م  597 ق.م. و587 ق.م. ثم في عام 582 ق.م. ( 2 أخبار 36: 2 - 7 ) فأخذ نبوخذ نصر عظماء البلاد ومنهم دانيال ورفاقه, والعمال الفنيين, كما أخذ آنية الهيكل وأخربه بعد ذلك.وقد وصل اليهود في السبي إلى مراكز محترمة جداً, فامتلكوا البيوت, وكان لهم الخدم, واشتغلوا بالتجارة ( عزرا 2: 65 ودانيال 2: 48 ونحميا 1: 11 ) وكان كهنتهم يعلمونهم هناك, كما كان حزقيال يتنبأ لهم ( عزرا 1: 5 وحزقيال 1: 1 ) ثم سقطت بابل في عام 539 ق.م. في يد كورش الفارسي, فسمح بعودة اليهود إلى أرضهم, ولكن كثيرين منهم فضلوا البقاء في بابل, فصار اسمهم يهود الشتات. وعاد بعضهم إلى أرض آبائهم تحت قيادة زربابل ( عزرا 2: 2 ) ثم تحت قيادة عزرا ( عزرا 7: 1 - 7 ) ثم تحت قيادة نحميا ( نحميا 7: 5 - 66 ).بنو السبي:كلمتان كثيرتان الورود في الكتاب المقدس ( انظر عزرا 4: 1 ) ويقصد بهما المسببون وأولادهم, كما تجد العبارات أن الله رد سبي صهيون ( مزمور 126: 1 ) وارد سبيكم ( إرميا 29: 14 ) وارجع سبيهن ( حزقيال 16: 53 ) وهي تعني رجوع الشعب إلى أرضه. وقد تستعمل كلمة السبي للدلالة على الذل والخذلان, وفي أيوب 42: 10 ردّ الله السبي أيوب بمعنى أنه خلصه من المصائب والارتباك والخسائر التي كان مسبياً فيها, وجعله يفرح في رضا الله.ويقال عن المسيح سبي سبياً ( أفسس 4: 8 ) بمعنى أنه أخضع السبي فأطلق شعبه ( غلاطية 4: 24 وعبرانيين 2: 15 و 2 بطرس 2: 19 ).'),
(1751, 'ستر - أستار', 'ورد في سفر الخروج وسفر العدد ذكر للأستار, وهي الستائر أو الحجب التي كانت تعلّق على جدران دار الخيمة المصنوعة من الشقق ( خروج 27: 9 و 35: 17 وعدد 3: 26 و 4: 26 ).'),
(1752, 'سِتْري', 'اسم عبراني معناه (( ملجأ )) أو (( مخبأ )) والمقصود (( الله ملجأ )), شخص من سبط لاوي من عشيرة قهات ( خروج 6: 22 ).'),
(1753, 'سَتُور', 'اسم عبراني بمعنى (( مخفي )) ابن ميخائيل وهو أحد الرجال الذين ذهبوا ليتجسسوا أرض كنعان, وقد أرسل نائباً عن سبط أشير ( عدد 13: 13 ).'),
(1754, 'سَجد - يسجد - سجود', 'السجود يدل على تقديم الاحترام والإكرام والتحية المتواضعة ( تكوين 37: 10 و 1 ملوك 1: 53 ومتى 9: 18 ). وهذا النوع من السجود لا يزيد عما يقدمه الناس لمن يكرمونهم من الأمراء أو الحكام, ولا يزيد عن الانحناء أمامهم.\nويوجد سجود آخر يفهم من القرينة أنه تقديم التعبد لله ( تكوين 24: 48 ويوحنا 4: 24 ). والسجود بمعنى التعبد لغير الله ضرب من ضروب العبادة الوثنية ( دانيال 3: 4 - 18 وأعمال 10: 25 ).'),
(1755, 'سَجْف , سِجْف', 'وهو اصطلاح كتابي يقصد به الحجب أو الستائر, وقد وُضع سجف أمام باب الخيمة ( خروج 26: 36 ) وأمام مدخل الدار ( خروج 27: 16 ) وتستعمل هذه الكلمة في جملة أماكن مضافة إلى حجاب ( خروج 35: 12 و 39: 34 و 40: 21 وعدد 4: 5 ).'),
(1756, 'مسَجَّل', 'مسجل الملك هو سكرتيره ومشيره أيضاً وهو موظف عظيم المقام في المملكة, وقد أنشأ داود الملك هذه الوظيفة وبقيت بعده, فكان المسجل يؤتمن على أهم أعمال المملكة, فيسجلها في سفر ليذكر الملك بها عند اللزوم. كان المسجل من أكبر رجال المملكة في أيام داود وسليمان ( 2 صموئيل 8: 16 و 1 ملوك 4: 3 ) كما ناب عن الملك حزقيا ( 2 ملوك 18: 18 و 37 ) وكذلك اؤتمن على الإصلاحات في الهيكل في أيام يوشيا ( 2 أخبار 34: 8 ).'),
(1757, 'سِجْن', 'أول ما نسمع عن السجن في مصر، فقد كان هناك مكان للتحفظ على المذنبين، وكان عادة جزء من بيت موظف عسكري. وكان المجرمون يقيدون ويوضعون فيه ( تكوين 40: 3 و 4 و 42: 16 و 17 و 24 ). كما نقرأ أن الفلسطينيين كانوا يخصصون مكاناً لحبس المذنبين، وقد اقتيد إليه شمشون بعد أن قلعوا عينيه ( قضاة 16: 21 ).أما بين العبرانيين فلم يكن السجن معروفاً في أول الأمر، وكانوا يحاكمون المجرمين بعدالقبض عليهم حالاً، ولم يكن السجن موضع قصاص عندهم إلا بعد تأسيس المملكة، غير أننا نلاحظ أنه عند مرور بني إسرائيل في البرية وضعوا أناساً مجرمين في المحرس ( لاوين 24: 12 وعدد 15: 34 ) ومن تكوين 37: 34 وارميا 38: 6-13 يستدل أنهم استعملوا بئراً أو جبّاً لهذه الغاية. وفي أيام الملوك شغل السجن قسماً من القصر الملكي ( ارميا 32: 2 ).ودام الحال كذلك في أيام هيرودس وخلفائه ( أعمال 23: 35 ). واستعمل الرومانيون برج انطونيا في أورشليم سجناً، كما استعملوا المعسكر في قيصرية كذلك ( أعمال 23: 10 ) وكان هناك سجن تابع للرؤساء الدينيين في أورشليم ( أعمال 5: 18-23 و 8: 3 ).'),
(1758, 'سَجُوت', 'اسم عبري معناه (( مرتفع )).( 1 ) الابن الأصغر لحيئيل الذي أعاد بناء أريحا، وقد مات الابن عندما فرغ أبوه من بناء المدينة ( 1 ملوك 16: 34 ) وبذلك تمّت اللعنة التي نطق بها يشوع ( يش 6: 26 ).( 2 ) من نسل يهوذا، وهو ابن لحصرون ولدته ابنة ماكير ( 1 أخبار 2: 21 و 22 ) وسجوب ولد ياثير الذي كان له ثلاث وعشرون مدينة في أرض جلعاد.'),
(1759, 'سَحاب-سحابة', 'هي الغيم ( أمثال 25: 14 ) وكان مجيء السحاب من الغرب علامة المطر في أرض فلسطين ( لوقا 12: 54 ). وقد قاد الله بني إسرائيل بعمود من السحاب وهم في طريق خروجخم من أرض مصر، وهو سحاب معجزي غير معتاد ( خروج 13: 21 و 22 ). وعندما يجيء المساء كان الله يرسل النور فيه ليضيء لهم. وكان السحاب يستر مجد الله ( خروج 16: 10 ) كما وقد ظللت سحابة المسيح والرسل الثلاثة على جبل التجلي ( متى 17: 5 ). وعندما انتهت خدمة المسيح على الأرض صعد على سحابة ( أعمال 1: 9 ) وسيأتي ثانية على سحاب المجد ( متى 24: 30 ) ويكون ذلك لدينونة الأمم ( رؤيا 14: 14 ).\nوقد يعبّر بالسحابة عن قصر الزمن ( أيوب 30: 15 ) كما تعني كثرة الجماهير ( اشعياء 60: 8 ).'),
(1760, 'سِحْر-ساحر-سحرة', 'الساحر هو من يدعي لنفسه قدرة فوق الطبيعة أو معرفة الغيب بطرق مختلفة، وعلى الأخص بالأرواح الشريرة ( خروج 7: 11 ) وقد أدّعى السحرة أن لهم سيطرة على حوادث المستقبل لتغيريها أو تعديلها بطرق سرية، وزعموا أن لهم علاقة بالجان أو بالآلهة. وقد كان هناك سحرة في مصر وفي أشور ( ناحوم 3: 4 ) كما وُجدوا في بابل ( اشعياء 47: 9 ) وفي بعض البلاد الأخرى.وقد شدّد الكتاب المقدس ضد السحرة وتهددهم ومن يثقون فيهم بالعقاب الشديد ( ملاخي 3: 5 ورؤيا 22: 15 ). وأمر الله بمنعهم بتاتاً ( تثنية 18: 10-12 ) كما منع العرافون من الإقامة في أرض العبرانيين ( خروج 22: 18 ).غير أن شعب إسرائيل تهاون في حفظ هذه الوصية الإلهية، فبدأ الشعب يلجأ إلى السحرة عند الحاجة، فلجأ الملك شاول إلى عرافة عين دور بعد مفارقة روح الرب له ( 1 صموئيل 28: 3-20 ) كما أن العهد الجديد يحدثنا عن سيمون الساحر ( أعمال 8: 9 ) وعن بار يشوع ( أعمال 13: 6 ) وعن بني سكاوا ( أعمال 19: 13 ).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1761, 'السَديم', 'اسم عبري معناه (( اخاديد )) (( حقول )) وهو واد ملآن بحفر الزفت المعدني ( الذي يتكون من 85 في المئة من الفحم و12 في المئة من الايدروجين وثلاثة في المئة من الأكسجين ). ويقع هذا الوادي في منطقة بحر الملح الذي هو البحر الميت. وفي هذا الوادي جرت الحرب بين أربعة ملوك وخمسة. وهي الحرب التي انتصر فيها كدرلعومر على ملك سدوم وحلفائه ( تكوين 14: 3 و 8 و 10 ). وهي اليوم منطقة مغمورة بالمياه في جنوب البحر الميت.'),
(1762, 'سَدوم', 'إحدى مدن السهل الخمسة التي أحرقتها النار التي نزلت من السماء بسبب خطيئة أهلها العظيمة ( تكوين 19: 24 ) وقد ورد ذكر سدوم للمرة الأولى في التوراة في الحديث عن حدود أرض كنعان ( تكوين 10: 19 ) ثم اختارها لوط مدينة للسكن بعد انفصاله عن إبراهيم لمعرفته بخصب أرضها وسهولة الري فيها ( تكوين 13: 10 ).وهاجم كدرلعومر وحلفاؤه وهزمها فأسرع إبراهيم ورجاله وردّوا الغنيمة المسلوبة ( تكوين 14 ) ولكن لوط عاد وسكن في سدوم، وعاودت سدوم سيرتها الأولى في الشر والبعد عن الله، فأرسل الله ناراً من السماء فأحرقتها ( تكوين 19 ). ولم ينجِ منها سوى لوط وابنتيه.وقد صارت خطيئة سدوم مضرب الأمثال، وكذلك صار مصيرها ( طالع متى 10: 15 ويهوذا 7 ورؤيا 11: 8 ) كما أن خطيئة (( السدومية )) أو الشذوذ الجنسي أخذت اسمها من سدوم ( تكوين 19: 5 ).وتقع سدوم تحت الماء اليوم في جنوب البحر الميت، وتجري المحاولات لكشف مكانها بواسطة الغواصين الذين يغوصون تحت الماء بمعداتهم الحديثة.\nجفنة سدوم:( تثنية 32: 32 ) أنظر (( جفنة )).'),
(1763, 'سَذْاب', 'نبات بين العشب والشجر ويصل أحياناً إلى حجم العليقة، وكان الفريسيسون يهتمّون بدفع العشور عنه ( لوقا 11: 42 ) واسمه باللاتينية Ruta graveolens ويتراوح ارتفاعه بين قدمين أربعة أقدام، وله رائحة نفّاذة، قد تكون غير مقبولة، وكانوا يزرعونه للحصول على دواء منه، كما كانوا يأخذون شعبة منه ويضعونها على غطاء رأس الطفل كطلسم لحفظه من الحسد. والشذاب من محاصيل البحر الأبيض المتوسط، وكان لبعض يستعملونه للطعام. وهو ليس نباتاً برياً كما يظن البعض، والا ما كان هناك سبب لتقويم العشور عنه.'),
(1764, 'سَرافيم', 'كلمة عبرانية يغلب أن يكون معناها (( كائنات مشتعلة )) أو ربما كان معناها (( شرفاء )) وهي في صيغة الجمع، ولم ترد إلا في نبوة اشعياء 6: 2 و 6 تسمية للأرواح التي كانت تخدم عرش الرب وظهرت لاشعياء في رؤياه ويصف اشعياء السرافيم دون أن يذكر عددهم، فيقول أن لهم وجوهاً وأيدي وأرجلاً وأجنحة، ولكل منهم ستة أجنحة، باثنين يغطي وجهه وباثنين يغطي رجليه وباثنين يطير، وذلك لأنه لا يستحق أن يرى وجه الله، ولأنه لا يريد أن يرى الله رجليه، ولنه يطير ليصنع مشيئة الله. وقد طار واحد منهم بجمرة من على المذبح ووضعها على شفتي اشعياء لتطهيرهما.ويبدوا أن السرافيم كالكروبيم نوعان ساميان من الملائكة الذين يخدمون الله. ويقول لنا اشعياء أن السرافيم كانوا يرنمون ويرددون (( قدوس.... )) تمجيداً لله. وكما كان اليهود يتكلمون عن الكروبيم في حلوله على التابوت في سحاب، هكذا تكلم اشعياء عن السرافيم على أنهم لامعون ساطعون.ويتحدذ سفر الرؤيا عن الحيوانات ذات الأجنحة والعيون، والتي تخدم الله ( رؤ 4: 8 ) ولكننا لا نجد معلومات أكثر من هذه في الكتاب المقدس عن أي من هذه المخلوقات.'),
(1765, 'سِراكوسا', 'مدينة كانت على شيء من الأهمية وكانت تقع على ساحل جزيرة صقلية الشرقي، قضى فيها بولس الرسول ثلاثة أيام في طريقه من مليطة إلى روما ( أعمال 28: 12 ). أسس المدينة المستعمرون الكورنثيون عام 758 ق.م. ونجحت نجاحاً عظيماً، ثم حاصرها الرومان وأخذوها عام 212 ق.م. بعد أن قتل المهندس المشهور ارخميدس في الحصار، وقد كان يعاون بخبرته الهندسية في الدفاع عنها.\nكانت المدينة مركزاً تجارياً هاماً وميناء من أفضل مواني جزيرة صقلية. قال فيها شيشرون أنها من أعظم المدن اليونانية وأجملها قاطبة. وكانت سفن نقل الحنطة ترسوا فيها لحسن مرفأها وجودة مياهها. وسيراكوسا الحديثة لا تتمتع بالأهمية التجارية التي كانت للمدينة القديمة.'),
(1766, 'سَرايا', 'اسم عبري معناه (( الله قد غلب )).( 1 ) كاتب لداود ( 2 صموئيل 8: 17 ) ويدعى أيضاً شيوا ( 2 صموئيل 20: 25 ) ويدعى شيشا ( 1 ملوك 4: 3 ) كما يدعى أيضاً شوشا ( 1 أخبار 18: 16 ).( 2 ) الكاهن العظيم في ملك صدقيا، أخذه نبوخذ نصر مع كثيرين من عظماء المملكة مسبيّاً وقتل ( ارميا 52: 24 و 27 ) وقد ورد اسمه في قائمة رؤساء الكهنة ( 1 أخبار 6: 14 ) وهو جدّ عزرا ( عزرا 7: 1 ).( 3 ) ابن تنحومث النطوفاتي، وأحد الأبطال الذين خلصوا أنفسهم من غضب نبوخذ نصر بعد أن قتلوا جدليا الحاكم الذي تواطأ مع الكلدانيين المستعمرين (  2 ملوك 25: 23-25 ).( 4 ) ابن قناز وأخو عثنيئيل الأصغر، وهو والد يوآب أبي وادي الصنّاع ( 1 أخبار 4: 13 و 14 ).( 5 ) جدّ ياهو وهو من سبط شمعون ( 1 أخبار 4: 35 ).( 6 ) كاهن عاد من بابل إلى أورشليم مع زربابل ( عزرا 2: 2 نحميا 7: 7 ) ويدعى أيضاً عزريا. وهو أحد الذين أمضوا العهد بعدم زواج العبرانيين من أجنبيات ( نحميا 10: 2 ) وقد صار رئيساً لبيت الله بعد بنائه، لأنه من سلالة الكهنة ( نحميا 11: 11 ).( 7 ) ابن عزرئيل وهو أحد الذين أمرهم الملك بالقبض على ارميا وعلى سكرتيره باروخ ( ارميا 36: 26 ).( 8 ) ابن نيريا الذي ذهب إلى السبي مع صدقيا ملك يهوذا، وقد طلب منه النبي ارميا أن يحمل معه إلى بابل سفراً حوى كل الشرور الآتية على بابل، وطلب منه أن يقرأه ثم يربطه بحجر ويلقيه في وسط الفرات رمزاً لهلاك بابل ( ارميا 51: 59-64 ).'),
(1767, 'سَراب', 'ورد ذكره في الترجمة العربية التي بين أيدينا اشعياء 35: 7 ولكن البعض يظنون أن الأصل يعني (( حرارة محرقة )) والسراب عبارة عن رمال الصحراء تراها في ضوء النهار وكأنها ماء حين تنعكس عليها أشعة الشمس، فيظهر سطح الأرض المقفرة وكأنه بحيرة ماء صافية.\nويقصد النبي بهذا القول أن السراب الخداع سيصبح حقيقة وواقعة صحيحة، فتصير الأرض القفر بحيرة ماء تنمو حولها الأشجار، وهو قول نبوي يشير إلى زمن مجيء المسيح.'),
(1768, 'سِراج', 'وعاء كان يصنع من فخَّار أو نحاس يوضع فيه سائل قابل للاشتغال كالزيت أو النفط أو القطران، ثم يوضع فيه فتيل يشعلونه ليضيء في الظلام. وكانت توجد في الهيكل سبعة سرج في المنارة مصنوعة من ذهب ( خروج 37: 23 و 1 ملوك 7: 49 ). وكانوا يشعلونها بفتائل من ثياب الكهنة البالية وبزيت الزيتون ( خروج 27: 20 ) وكانوا يبقون السرج مشتعلة طوال الليل، لأن إطفائها علامة الفقر الشديد أو هجر البيت ( أيوب 18: 6 وارميا 25: 10 ). وكانت إضاءة السرج تشير إلى اتساع ثروة الصديق ( أمثال 13: 9 ) وإلى حسن تدبير المرأة المنزلي ( أمثال 31: 18 ).وكان الحديث إلى السراج يرمز إلى الهداية ( مزمور 119: 105 ) كما كان يرمز إلى نفس الإنسان ( أمثال 20: 27 ) أو إلى الابن الذي سيخلف أباه (  1 ملوك 15: 4 ).وقد تكلم المسيح في العهد الجديد كثيراً عن السرج، فطلب من تلاميذه الاستعداد بالسرج الموقدة التي ترمز إلى التهيؤ ( لوقا 12: 35 ) كما ضرب مثل العذارى الحكيمات وسرجهنّ المنيرة ( متى 25 ) وقال أن نور المسيحي يجب أن ينير على الجميع كما ينير السراج على كل الجالسين في البيت ( متى 5: 15 ).'),
(1769, 'سَرجون', 'الصيغة العبرية لكلمة اكادية معناها (( الملك المثبت )) (( شارو كين )). ولم يرد ذكر اسم الملك سرجون إلا في نبوة اشعياء 20: 1 كما أشير إليه فقط في الملوك الثاني 17: 6، وهو ملك أشور وخليفة شلمنأصر وأبو سنحاريب. وقد ملك سرجون من عام 722 إلى 705 ق.م.ويظن أن سرجون اغتصب العرش من شلمنأصر وعزا إلى نفسه شرف فتح السامرة التي كان قد حاصرها شلمنأصر لمدة ثلاث سنوات ثن سبى أهلها (  2ملوك 17: 6 ) ويستدل من الكتابات التي وُجدت على آثار نينوى أن مملكة يهوذا كانت تدفع له الجزية.وقد كان سرجون رجل حرب عظيماً، واشتهر بالانتصارات العظيمة على الممالك التي حوله، الممتدة من بابل في الجنوب ومادي في الشرق إلى كبدوكية في الشمال وسوريا وفسطين ومصر في الغرب، كما اشتهر بحسن الرسم وإتقان البناء.وقد عصا عليه مردوخ بلادان في بابل، بعد أن كان يدفع الجزية لشمنأصر، فتوجه إليه سرجون على رأس جيش قوي وهزمه في عام 710 ق.م. وطده من عاصمته بيت ياكين. وتوج نفسه مكانه ملكاً على بابل وفي عام 705 ق.م. مات مقنولاً في قصره، وملك ابنه سنحاريب مكانه. وقد كشف الملقبون عن قصر سرجون في خورسباد بالقرب من نينوى وقد وجدت في القصر آثار كثيرة من ضمنها ثور مجنح هائل الحجم محفوظ في جامعة شيكاغو.'),
(1770, 'سَرجيوس بولس', 'اسم لاتيني وهو الوالي الروماني على جزيرة قبرص والذي آمن بالمسيح على يد بولس الرسول. وكانت وظيفته الرسمية (( نائب قنصل )) وقد أظهر فهماً وإخلاصاً في تقصي الحقيقة عن الدين الذي ينادي به بولس وبرنابا في أثناء رحلتهما التبشيرية الأولى ( أعمال 13: 5-12 ) وآمن بعد أن رأى العجيبة مع عليم الساحر. وقد زعم بعضهم ان شاول الطرسوسي قد جعل اسمه بولس اعجاباً بسرجيوس بولس، لكن الحقيقة أن شاول كان له اسم بولس على الأرجح من قبل ذلك، وأن اتفاق الاسمين مصادفة وعندما حاول عليم الساحر ان يقف بين بولس وبين الوالي، وربما ذكر بولس حينئذ أن اسمه كاسم الوالي، كان على بولس ألا يترك الساحر يفسد إيمان الوالي فضرب عليم بالعمى. وقد تجدد الوالي لا عندما تأثر بكلمات النعمة الخارجة من فم بولس.'),
(1771, 'مسارح', 'اطلب (( مدينة )).'),
(1772, 'سِرّ', 'وردت كلمة سر في العهد الجديد بمعنى حقيقة روحية عميقة لا يقدر إنسان أن يدركها بعقله الطبيعي ولا بفكره الجسدي، كما أنه لا يقدر أن يفهمها فهماً صحيحاً في هذا العالم لأنها تفوق الإدراك الطبيعي.\nومن الأسرار المذكورة في الإنجيل سر اقتبال الأمم إلى الإيمان بالمسيح ( رومية 11: 25 ) وسر قيامة الأموات ( 1 كورنثوس 15: 51 ) وسر ميلاد المسيح من عذراء. واتحاد لاهوته بناسوته ( 1 تيموثاوس 3: 16 ) وسر اتحاد المسيح بالكنيسة ( افسس 5: 32 ).\nعلى أن الرسول بولس يتحدث عن الإنجيل ويقول انه سر ( افسس 6: 19 ) وهي تحمل معنى غير ما تقدم، إذ تعني الشيء المكتوم في الصدر إلى أن يجيء وقت الإفصاح عنه، فيصبح العلم به في حيز الامكان، والرسول يعني ان المجهول يصير معلوماً باذاعته. وتستعمل كلمة سر في سفر الرؤيا للدلالة على شيء رمزي فمثلاً سر السبعة كواكب ( 1: 20 ) وسر المرأة المتسربلة بالقرمز ( 17: 7 ). وقد كانت  بعض الهيئات اليونانية تحفظ أسراراً لها, فلا تُفضي بها إلا إلى الأخصاء من تابعيها, وذلك بعد حصولهم عاى اختبارات معينة.'),
(1773, 'سِرِيّة', 'زوجة شرعية حسب الناموس اليهودي, ولكنها في درجة أقل من درجة سيدة البيت. وقد كان هذا جائزاً في نظام تعدد الزوجات, فكانت السراري يؤخذن عادة من العبيد ويشترين بثمن, نظير هاجر ( تكوين 16: 2 و 3 ) وبلهة ( تكوين 29: 29 ) وسرية جدعون ( قضاة 8: 31 ). وكنّ أحياناً من الفتيات اللواتي يبيعهن آباؤهن, أو من أسيرات الحرب. وقد كان طلاق السرية أسهل من طلاق السيدة, ولكن حقوقها محفوظة بحسب الشريعة الموسوية ( خروج 21: 7 - 11 وتثنية 21: 10 - 14 ). ولم يكن الزواج بسراري أجنبيات شرعياً في الناموس اليهودي. ولكن يتضح في العهد الجديد سواء في نصه أم روحه أن الزواج يقتصر على زوجة واحدة لا غير.'),
(1774, 'سَرير', 'أحد أثاث البيت للنوم عليها, وكان الفقراء. والمسافرون ينامون غالباً على الأرض ويستعملون الرداء كغطاء ( تكوين 28: 11 ). وقد يكون السرير أحياناً مجرد حصير أو سجادة يمكن أن تطوى وتحمل باليد كما حمل مريض بيت حسدا سريره ( متى 9: 6 ) ولكن الفرش المرتفعة عن الأرض كانت مستعملة منذ القديم ( 2 ملوك 1: 4 و 6 و 4: 10 ) وكانت مصنوعة بقوائم من الخشب أو الحديد ( تثنية 3: 11 ونشيد 3: 9 ) كما كانت تصنع من العاج عند الأغنياء ( عاموس 6: 4 ) وفراش من حرير ( عاموس 3: 12 ) وأغطية غالية ( ام 7: 16 ).'),
(1775, 'سَرْ سَخيم', 'اسم أكادي ربما كان معناه (( رئيس العبيد )) وكان يعمل في قصور نبوخذ نصر وقد دخل أورشليم مع عظماء ملك بابل ( ارميا 39: 3 ).'),
(1776, 'سارق', 'كانت السرقة عند اليهود من الجرائم التي يعاقب مرتكبيها عقاباً صارماً, وفي البلاد التي تعيش عيشة بدائية يعاقب السارق بقطع يده اليمنى. وقد ذكرت شريعة موسى شيئاً عن سرقة المواشي وسرقة الناس, وكانت سرقة المواشي أكثر شيوعاً ( خروج 22: 1 - 4 ). وفي الوصايا العشر وصية تنهي عن السرقة عموماً. والحكم على السارق في الشريعة الموسوية أن يرد خمسة أضعاف ما سرق من بقر أو أربعة أضعاف إذا كان المسروق غنماً, ويتحتم تنفيذ الحكم ولو يباع السارق نفسه, وكان التعويض يصل أحياناً إلى سبعة أضعاف ( أمثال 6: 31 ) ولو أعطى في ذلك كل قنية بيته. وكان الذي لا يرد الوديعة التي عنده أو يخبيء اللقطة التي وجدها يردها بزيادة خمس قيمتها ( لاويين 6: 1 - 5 ). ومن هذه القوانين نفهم لماذا أعطى زكا نصف أمواله للمساكين وردّ من سلبه أربعة أضعاف ( لوقا 19: 8 ).'),
(1777, 'سَرْو', 'السرو شجرة كبيرة توجد بكثرة في سوريا وفلسطين وتنمو مع الأرز في لبنان ( 1 ملوك 5: 8 ). ويبلغ طولها من عشرة إلى خمسة وعشرين متراً, ولونها أخضر باصفرار, ولها رائحة جميلة دائمة. واسم شجرة السرو باللاتينية Cupressus sempervirens والكلمة العلابية سرو ترجمة لكلمة عبرانية قد تشير إلى شجر الصنوبر.ومن السرو الشربين البري وأغصانه ممتدة, وهيئة الشجرة بيضاوية بخلاف السرو الذي تصعد أغصانه إلى أعلى وتكوين هيئة الشجرة مخروطية مستطيلة.ويستعمل خشب السرو البري لبناء المراكب ( حزقيال 27: 5 ) ولعمل آلات الطرب ( 2صموئيل 6: 5 ) ولبناء وتشييد البيوت مع خشب الأرز ( 1 ملوك 5: 8 ) ولعمل الرماح ( ناحوم 2: 3 ) ولشدة ارتفاعه اختاره اللقلق ليبني عشه فيه ( مزمور 104: 17 ).ويكنّى بأشجار السرو عن القوة والعظمة ( 2 ملوك 19: 23 وأشعياء 14: 8 ). ويقابل اهتزاز أغصانه مع الريح اهتزاز الرماح في الحروب ( ناحوم 2: 3 ) ويشار بتفريخ السرو إلى الخضرة وإلى الخصب. ( اطلب (( صنوبر ))  ).'),
(1778, 'سَرُوج', 'اسم سامي معناه (( يصوّب )) أو (( غصن )) وهو اسم ابن رعو وأبو ناحور وسلف إبراهيم وهو أحد الآباء من سلالة سام بعد الطوفان ( تكوين 11: 20 - 23 و 1 أخبار 1: 26 ولوقا 3: 35 ). وكانت هناك مدينة بهذا الاسم بالقرب من حاران.'),
(1779, 'سَراويل', 'السراويل قطعة الثياب الداخلية, وقد أوثق رجال نبوخذ نصر الفتية الثلاثة في سراويلهم وأقمصتهم وأرديتهم ولباسهم وألقوا بهم في وسط النار المتقدة, الأمر الذي يؤدي إلى زيادة الاشتعال, ولكنه بقوة الرب أدّى إلى زيادة التعجب من قوة المعجزة ( دانيال 3: 21 ).'),
(1780, 'سَارية', 'ورد ذكرها في أشعياء 30: 17 على أنها علامة أو دليل منصوب على مكان مرتفع حتى يراه المسافرون والشائحون للإرشاد إلى طريق أو للتحذير من خطر.\nوكانت السواري تنصب على الأماكن المرتفعة حتى يجتمع الناس حولها لعبادة البعل ( تثنية 12: 2 و 3 و 16: 21 و 2 ملوك 23: 6 ). وقد اعتاد الملوك المنحرفون عن عبادة الرب إقامة تلك المرتفعات لعبادة الأصنام, ولكن الملوك الأتقياء كانوا ينزعونها. وقد أظهر البحث الحديث أن الكلمة العبرية (( أشيرة )) تشير إما إلى آلهة ( قض 3: 7 ) أو تشير إلى السارية الخشبية التي كانت تقام نصباً رمزاً إلى هذه الآلهة ( خر 34: 13 ). وقد ذكرت هذه الآلهة في لوحات أوغريت التي اكتشفت في رأس الشمرة.'),
(1781, 'سُرياني', 'تكلم المسيح في لوقا 4: 27 عن نعمان السرياني الذي شفاه اليشع النبي, والسرياني هو أحد أهالي سوريا أو آرام, ولم تكن كلمة سوريا مستخدمة في العهد القديم\', بل كان يظلق عليهم الآراميون.'),
(1782, 'سِرْيون', 'كلمة صيدونية معناها (( درع )), وهو الاسم الذي كان الصيدونيون يطلقونه على جبل حرمون ( تثنية 3: 9 ومزمور 29: 6 ). ويبدو أن هذا الاسم لم يطلق على جزء معين من سلسلة جبال حرمون, بل كان يطلق على الجزء الذي كان يرى من خرمون عند الوقوف على شاطيء بلاد الصيدونيين.'),
(1783, 'سِمسَاي', 'رجل من سبط يهوذا من عائلة حصرون من بيت يرحمئيل ( 1 أخبار 2: 40 ).'),
(1784, 'سِطْنة', 'اسم عبراني معناه (( خصام )) وهو اسم البئر الثانية التي حفرها اسحق في وادي جرار, والتي تخاصم عليها رعاة جرار مع رعاة مواشي اسحق, وتقع بين بئر سبع وبين رحوبوت في واد صغير يطلق عليه اليوم سلطنة الرحيبة.. وفي هذا الاسم الحديث نرى أحياء لاسم سلطنة واسم رحوبوت التي كانت بقرب السلطنة ( تك 26: 22 ).'),
(1785, 'سَعُوريم', 'اسم عبراني معناه (( شعير )) وهو من سلالة هارون, ونمت عائلته فصارت فرقة من فرق الكهنة في أيام داود, وسعوريم هذا رئيس الفرقة الرابعة ( 1 أخبار 24: 8 ).'),
(1786, 'ساعٍ وجمعه سُعاة', 'وهم الجنود المشاة وكانوا يحرسون الملك عادة, وكان بنياهو بن يهوياداع رئيساً لهم في أيام داود الملك ( 2 صموئيل 8: 18 ) وكان السعاة ذوي سرعة عظيمة في المشي, وكانوا متمرنين على طول السير إن في المدة أو في المسافة, حتى كان يصل الساعي منهم أورشليم إلى صور في أربع وعشرين ساعة. وكان لدى أحشويرش الملك سعاة يمشون ليلاً ونهاراً حتى يوصّلوا أوامره إلى أنحاء مملكته الواسعة الأطراف بعجلة وسرعة ( استير 3: 13 ).'),
(1787, 'غرفة السعادة', 'هي المخدع الذي كان سعاة الملك يقيمون فيه ليكونوا قريبين من الملك ليحملوا رسائله في أي وقت ( 1 ملوك 14: 28 ).'),
(1789, 'سَعير', 'كلمة عبرانية معناها (( كثير الشعر )):( 1 ) اسم أمير حوري أطلق اسمه على المناطق الجبلية التي سكنها ( تكوين 36: 20 ).( 2 ) اسم الأرض التي كان يسكنها الحوريون ( تكوين 14: 6 ) ثم استولى عليها عيسو ونسله ( تكوين 32: 3 ) وكانت تسمى أيضاً جبل سعير لأنها أرض جبلية على الجانب الشرقي من البرية العربية, ويصل ارتفاع أعلى قمة في هذه الأرض إلى 1600 متراً وهي قمة جبل هور. وقد حاول بنو إسرائيل أن يعبروا تلك الأرض في طريقهم من مصر إلى كنعان, ولكن الأدوميون رفضوا السماح لهم, فدخل العبرانيون البرية العربية شرقي أرض سعير, وساروا في أرض وعرة قاسية حتى يتفادوا المرور في سعير ( اطلب (( أدوم ))  ).( 3 ) جبل في أرض يهوذا ( يشوع 15: 10 ) بين قرية يعاريم وبيت شمس, وربما كان سلسلة الجبال التي تقع عليها قرية ساريس إلى الجنوب الغربي من قرية يعاريم وإلى الشمال الغربي من أورشليم. ولا زالت آثار الغابات التي كانت تنمو فوقه موجودة إلى اليوم.'),
(1790, 'سَعِيرة', 'اسم عبراني معناه (( أرض جبلية تغطيها الغابات )), وهو الموضع الذي هرب إليه أهود بعد أن قتل عجلون ملك موآب ( قضاة 3: 26 ) وربما كان موقعه جبل أفرايم, ولعل مرور أهود في الجلجال يقرب هذا الفرض ( قضاة 3: 9 ).'),
(1791, 'سَفوح الفِسجة', 'هو مكان كان يطل على البحر الميت من جهة الشرق ( تثنية 3: 17 و 4: 49 ويشوع 12: 3 و 13: 20 ). ويرجّح أنه عيون موسى ( اطلب (( فسجة ))  ).'),
(1792, 'سَفار', 'اسم سامي معناه (( عد )) أو (( إحصاء )) مكان لم يرد ذكره إلا في تكوين 10: 30 كتخم من حدود بني يقظان, ولعلها ظفر الحالية, ولكن هناك مكانات باسم ظفر: أحدهما إلى الجنوب من صنعاء, والآخر على الشاطيء في منطقة شحر إلى الشرق من حضرموت. ولعل الموضع الأخير هو نفس سفار القديمة.بيت الأسفار:هو البيت الذي كانت تحفظ فيه أسفار تواريخ ملوك فارس, وكان أحد البيوت خزائن الملك ( عزرا 5: 17 و 6: 1 ).الأسفار القانونية:في هذا البحث سنحاول أن نوضح كيف حصلنا على الكتب الستة والستين في كتابنا المقدس. ونحن لا نسأل عمن كتب هذه الأسفار, بل عمّن جمعها معاً, وأ\'طاها صفة الاستعمال القانوني المقدس. وقد أطلقت الكنيسة المسيحية اسم الأسفار القانونية على أسفار الكتاب المقدس لتكون القانون الوحيد المعصوم للإيمان والأعمال.( 1 ) العهد القديم:هناك رأي يقول أن الذي أضفى صفة القانونية على أسفار العهد القديم هم كتَّاب الأسفار أنفسهم, وقد شعروا وهم يكتبون بدافع من الروح القدس أنهم يكتبون قوانين الحياة للشعب, ثم أظهرت محتويات ما كتبوه صحة رأيهم وصوابه, فقد كان ما كتبوه قانوناً إلهياً حقاً.وهناك رأي آخر يقول أن الذي أعطى صفة القانونية لهذه الأسفار هم الكتّاب المقودون بالروح القدس, ومعهم قادة الدين من اليهود والمسيحيين الذين قبلوا هذه الأسفار بإرشاد من الروح القدس أيضاً.غير أنه لم تجمع أسفار العهد القديم معاً إلا بعد السبي, وقد زعم اليهود أن عزرا الكاتب وهو الذي جمعها على هيئتها الحاضرة, وقد عاونه في ذلك رجال المجمع الكبير.وقد قسم اليهود أسفار العهد القديم إلى ثلاثة أقسام: ( أ ) التوراة أو الناموس - وهي أسفار موسى الخمسة. ( ب ) الأنبياء - وينقسمون إلى قسمين: الأنبياء المتقدمون وهي أسفار يشوع والقضاة وسفرا صموئيل وسفرا الملوك ( وكل من الأخيرين اعتبرا سفراً واحداً ) ثم الأنبياء المتأخرون وهم أشعياء وإرميا وحزقيال, والأنبياء الإثنا عشر الصغار ( وقد اعتبرت أسفارهم سفراً واحداً ) ومجموع عدد الأسفار ثمانية,.( جـ ) والقسم الثالث هو الكتب. وعدد أسفاره أحد عشر سفراً وهي المزامير والأمثال وأيوب والنشيد والجامعة وراعوث والمراثي واستير, ودانيال, ثم عزرا ونحميا كسفر واحد, وسفرا الأخبار كسفر واحد. ومجموع عدد هذه الأسفار أربعة وعشرون سفراً ثم عاد اليهود وأضافوا سفر راعوزث إلى القضاة, ومراثي إرميا إلى سفر إرميا, فصار عدد الأسفار القانونية 22 سفراً فقط, بعدد حروف الأبجدية العبرية.أما عن حفظ الكتابات المقدسة فيقول سفر الخروج 40: 20 أن موسى أخذ لوحي الشهادة ووضعهما في لتابوت, كما يقول سفر التثنية 31: 9 أن موسى كتب التوراة وسلمها للكهنة بني لاوي, وأمرهم بوضعها بجانب التابوت ( تثنية 31: 24 - 26 ) ويتضح من ملوك الأول 8: 9 أن لوحي الشهادة كانا محفوظين حتى زمن سليمان, كما أن يهوياداع الكاهن قدّمهما إلى يوآش ( 2 ملوك 11: 12 ) وقد نقل رجال حزقيا الملك أمثال سليمان ( أمثال 25: 1 ).ثم أن هذه الكتابات المحفوظة كانت ذان سلطان على حياة الناس, ويتضح هذا من قصة سفر الشريعة الذي غيّر حياة الشعب ( 2 ملوك 22: 8 - 20 ) كما يظهر هذا من نحميا 8: 8 وكذلك انظر زكريا 7: 12 ودانيال 9: 2 كما أن بولس يقول هذا في رسالته الثانية إلى تيموثاوس 3: 16.وقد عمل على حفظ كتابات العهد القديم ترجمتها إلى اليونانية بالترجمة المعروفة (( السبعينية )) والتي بدأت حوالي 250 وأكملت حوالي 150 ق.م. وقد بدأت هذه الترجمة بأمر من بطليموس فيلادلفوس الذي كان يحب الكتب, والذي حكم في مصر من سنة 285 ق.م. ومن العهد الجديد نتأكد أن العهد القديم كما هو الآن كامل وله سلطان مطلق, فقد اقتبس منه 275 شاهداً كما اقتبست منه قصص وأفكار, مما يبرهن لنا صحة العهد القديم كما هو بين أيدينا اليوم. وقد قبل رجال الكنيسة الأولون العهد القديم كما هو بين أيدينا اليوم., ويتضح ذلك من كتابات جستن مارتر حوالي نحو 164 م. ومن كتابات أسقف ساردس الذي ذهب إلى فلسطين خصيصاً من أجل هذا الأمر للفحص والبحث في نحو 170 م. ومن أوريجانوس الإسكندري حوالي سنة 254 م.ويوجد في نسخ الترجمة السبعينية أسفار لم تجمع ضمن أسفار العهد القديم العبرية وهذه الأسفار تعرف (( بالابوكريفا )).( 2 ) العهد الجديد:وينقسم تاريخ جمع أسفار العهد الجديد إلى ثلاثة أقسام:( أ ) القسم الأول من البدء حتى عام 170 م. وقد تمت كتابة جميع أسغار العهد الجديد في القرن الأول المسيحي, وكان لهذه الكتابات السلطان المقدس, فقد طلب بولس الرسول أن تكون لكتاباته السلطة الرسولية الواجبة ( 1 تسالونيكي 5: 27 وكولوسي 4: 16 ) وحذّر يوحنا الجميع من إضافة شيء أو حذف شيء مما كتبه في سفر الرؤيا ( رؤيا 22: 18 و 19 ) واعتبر الرسول بطرس كتابات بولس الرسول ذات سلطة قانونية كبقية الكتب المقدسة ( 2 بطرس 3: 16 ). وقد كان الآباء الأولون يتبادلون الرسائل والكتابات لتعمّ الفائدة, وهكذا صارت كتابات العهد الجديد معروفة للمسيحيين جميعاً. وقد اقتبس أكليمندس الروماني في عام 95 م. من متى ولوقا ورسالة العبرانيين ورومية وكورنثوس وتيموثاوس وتيطس وأفسس, وذلك في رسالة كتبها إلى مسيحيي روما.وكتب أغناطيوس رسالة عام 115 م. إلى بوليكاربوس أسقف سميرنا اقتبس فيها الكثير من مختلف أسفار العهد الجديد.وقد قامت هرطقات كثيرة فكرية لمقاومة المسيحيين, فقام الآباء الأولون بالرد عليها من الأناجيل الأربعة وبقية كتابات الرسل. ومن الآباء المدافعين عن الإيمان جستن مارتر وأرستيدس ومليتو من ساردس وثاوفيلس من أنطاكية.( ب ) القسم الثاني من 170 إلى 220 م. وهو الوقت الذي انشغلت فيه الكنيسة المسيحية بالمباحث اللاهوتية والعقائدية. وبرز فيه إيرناوس في آسيا الصغرى وإكليمندس الإسكندري وترتليان في شمال إفريقيا. ومن كتاباتهم يتضح أنه لم يكن هناك نزاع حول الأسفار القانونية في العهد الجديد, إلا أن تحديدها لم يكن واضحاً.( جـ ) القسم الثالث ومدته القرنان الثالث والرابع الميلاديان: اجتمع المجمع المسيحي المسكوني في قرطجنة عام 397 م. وقرر أن الأسفار القانونية هي الأسفار السبعة والعشرون التي بين أيدينا اليوم, والتي تقبلها الكنيسة المسيحية اليوم بكل طوائفها.'),
(1793, 'سَفير - سُفراء', 'هو ممثل الدولة التي ينتمي إليها في الدولة التي يقيم فيها, وذلك بتعيين من دولته وقد قال الرسول بولس أن مبشري الإنجيل هم سفراء المسيح في مملكة هذا العالم ( 2 كورنثوس 5: 20 ).'),
(1794, 'سِفارة', 'جماعة من دولة تمثل الدولة التي تنتمي إليها في دولة أخرى لأداء مهمة خاصة ( لوقا 14: 32 ).'),
(1795, 'سَفَرْوَايم', 'اسم بلدة, وهي كلمة عبرانية في صورة المثنى, مما أدى إلى الظن أنهما بلدتا سفارة اللتان تقعان على ضفتي نهر الفرات على بعد 16 ميلاً جنوب غرب بغداد , وقد كانتا مركزاً هاماً لعبادة الإله البابلي إله الشمس (( شاماش ))وعبادة الآلهتين أشتار وأنونيت. وكان اسم أحد المدينتين سفار شاماش والأخرى سفار أنونيت, ويقول المستكشف هورمزد راسام في عام 1881 أن موقعهما اليوم هو (( أبو حبّة )).غير أن صعوبة تقوم أمامنا فإن 2 ملوك 19: 13 يقول أنه كان لها ملك, ولكننا نعلم من التاريخ أنها كانت تابعة لبابل ولم يكن لها ملك, ويظن بعضهم أن سفروايم هي سبرائم المذكورة في حزقيال 47: 16 ويظن آخرون أن مكانها اليوم شومورية شرقي بحيرة حمص.وسفروايم هي البلد الذي أتى منه ملك آشور بمهاجرين ليسكنوا في السامرة بدل سكانها الذين نقلهم إلى بلاد أخرى ( 2 ملوك 7: 24 و 18: 34 وأشعياء 36: 19 ).'),
(1796, 'سَفَرْوَايميّثون', 'هم أهل سفروايم ( 2 ملوك 17: 31 ) وكان ملك آشور قد وطنهم في السامرة, ولكنهم استمروا يحرقون أولادهم كتقدمات للآلهة الوثنية.'),
(1797, 'سَفَط', 'سلّة من البردي ( خروج 2: 3 ) صنعتها أم موسى وطلتها بالحمر والزفت حتى لا يتسرب الماء إليها, ووضعت فيها ابنها, وسلمته لإبنتها مريم لتضعه بين الحلفاء النامية على شاطيء النيل.'),
(1798, 'سِفَّاي', 'اسم عبراني معناه (( حوض )) أو (( عتبة )) وهو جبار فلسطيني ( 1 أخبار 20: 4 ) ويدعى أيضاً ساف ( 2 صموئيل 21: 18 ).'),
(1799, 'سَفِّيرة', 'اسم آرامي معناه (( جميلة )) وهي زوجة حنانيا, وقد ماتت هي وزوجها لأنهما كذبا الروح القدس ( أعمال 5: 1 - 10 ) ( اطلب (( حنانيا ))  ).'),
(1800, 'سِفْمُوث', 'اسم عبراني ربما كان معناه (( شوارب )) وهو مكان لجأ إليه داود عندما طارده الملك شاول ( 1 صموئيل 30: 28 ) وأرسل إليه هدايا من غنيمة صقلغ, ولعلها في جنوب يهوذا, وقد كان زبدي الشفمي من أهلها ( 1 أخبار 27: 27 ).'),
(1801, 'سَفِينة', 'كان اليهود أهل رعي, ولم يكونوا يركبون البحر كثيراً, ولم يكن في بلادهم سوى ميناء واحد بناه سمعان المكابي في يافا. وفي زمن الملك هيرودس كان ميناء قيصرية عامراً, ولكنه كان تحت  إشراف الرومانيين. وقد استعار الشعراء العبرانيون صور مخاطر البحار من حياة الصوريين والمصريين, أكثر مما استعاروها من حياتهم هم.وقد بدأ الناس يركبون البحر منذ فجر التاريخ وذلك على نهر النيل ونهر الفرات, وكانت دعائم السفن مصنوعة من خشب الصفصاف, ويعومها رجلان يحمل كل منهما عارضة خشبية يدفعان بها السفينة وكانت أكبر سفينة تحمل خمسة آلاف وزنة, وكانت السفن التي تسير على النيل مصنوعة من خشب السنط.ولا شك في أن العبرانيين استعملوا سفن الصيد على بحر الجليل منذ زمان طويل, ولكن العهد القديم لا يذكر لنا ذلك, بينما يفيض العهد الجديد في ذكر ذلك ( مثلا يوحنا 6: 22 ).وكان سليمان أول من بدأ استخدام السفن التجارية بين العبرانيين, وقد حاول الملك يهوشافاط أن يحذو حذوه ولكنه لم ينجح, وقد اعتمد سليمان ويهوشافاط على البحارة الفينيقيين, لا على الإسرائيليين, وقد حمل البحارة الصيدونيون أخشاب بناء هيكل سليمان على أطواف إلى يافا ( 1 ملوك 5: 9 ) كما حملوا أخشاب بناء الهيكل الثاني كذلك ( عزرا 3: 7 ). وبحارة السفينة التي حملت يونان النبي الهارب كانوا من الأجانب ( يونان 1: 5 ).وقد كان البحر الأبيض المتوسط مسرحاً لكثير من السفن التجارية والعسكرية ( عدد 24: 24 ودانيال 11: 30 ). وقد كانت السفن الحربية تحمل الجنود. كما كانت تشترك في المعارك الحربية, إذ كان بعضها مجهزاً بمقدمة مسنونة تطعن بها سفن العدو فتغرقها. وكان بعضها يسير بالقلوع والبعض الآخر بالقلوع والمجاديف معاً. وكانت هناك سفن فاخرة للسفر يقدم لنا حزقيال وصف إحداها, فيقول إن ألواحها كانت من خشب السرو ذي الرائحة الجميلة, وسواريها من أرز لبنان, وشراعها من كتان مطرز, أما غطاؤها الذي يظلّل الركاب من الشمس فقد كان من الأسمانجوني والأرجوان ! وكان لها قائد وبحارة ينفذون تعليماته ( حزقيال 27 ).وكانت هناك سفن ضخمة تحمل الكثير من المسافرين والبضائع, فقد كانت سفينة بولس المسافرة إلى مالطة تحمل 276 راكباً عدا حمولة القمح, ويقول يوسيفوس: أنه ركب سفينة تحمل ست مئة راكب وألف طن من الحمولة. وكانت دفة السفينة صغيرة جداً بالنسبة إلى حجمها ( يعقوب 3: 4 ). وكانت أحياناً مجذافاً كبيراً يُربط بجانب السفينة وينزع عند اللزوم ( أعمال 27: 40 ). وكانت تزين بتماثيل وعليها صور كعلامة توأمي زفس ( الجوزاء ) يتيمّن بها البحارة الوثنيون ( أعمال 28: 11 ). وكان لها مراسي تشبه مراسي سفن اليوم, وفيها حبل للقياس وحبال أخرى لحزم السفينة عند اللزوم لحفظها من التفكك بفعل العواصف.ويقدم لنا سفر الأعمال وصفاً رائعاً لسفن تلك الأيام والسفر بها, وذلك في وصف رحلات بولس التبشيرية.'),
(1802, 'سَقْف', 'السقف للبيت هو الذي يحميه من المطر والشمس, وقد صنع سقف هيكل سليمان وأضلاعه من خشب الأرز ( 1 ملوك 6: 9 و 15 و 7: 3 ). وكانت سقوف بعض البيوت مصنوعة من طوب فخاري يتداخل بعضه في بعض يمكن أن يُرفع ويمكن أن يعاد إلى مكانه بسهولة, ولهذا استطاع أهل المفلوج أن يرفعوا السقف ليدلوا مريضهم أمام المسيح ( مرقس 2 ).'),
(1803, 'ساقٍ', 'موظف عظيم القيمة في بلاط الملوك القدماء, كان يقدم الشراب للملك بعد أن يشرف على تجهيزه بنفسه ( تكوين 40: 1 - 13 ونحميا 1: 11 ). وكانت العادة سابقاً أن يقدم الكأس باليد ولا يوضع على المائدة. وكان ساقي الملك معروفاً بإخلاصه وأمانته قبل أن توكل إليه هذه المسؤولية خوفاً من دس السم للملك. وكان للسقاة رئيس في بيت الملك ( دانيال 1: 11 ).'),
(1804, 'سَقْي بالرِّجْل', 'يُراد بهذه العبارة سهولة ري الأرض، وذلك بواسطة أخاديد صغيرة في الأرض تسد أو تفتح بالرِجل، أو بواسطة دولاب يُدار بالرِجل ويرفع الماء إلى القناة.'),
(1805, 'سَكَاكَة', 'اسم عبري معناه (( غطاء )) أو (( مكان كثير الغاب )) وهي إحدى المدن الست في برية يهوذا ( يشوع 15: 61 ). وتقع في الأرض غير المزروعة غربي البحر الميت، ولا زالت ترعى فيها اليوم أغنام البدو، وتظهر فيها آثار العمران القديم. ويعتقد كوندر أن خربة السكة التي تقع على بعد ميلين غربي بيت عنيا ( قرية العازرية ) هي مكان سكاكة القديمة.'),
(1806, 'سَكَاوي', 'رجل يهودي رئيس كهنة من افسس، وكان أولاده السبعة يشتغلون بالسحر، والأغلب أنه لم يكن يشتغل بالكهانة، ولعله كان من عائلة الكهنوت فقط. وقد رأى أولاد سكاوي السبعة بولس الرسول وهو يخرج الأرواح الشريرة بسلطان اسم المسيح، فرأوا أن يستغلوا الاسم في ربح المال من المرضى، فذهبوا إلى رجل به روح شرير وسمّو على الروح الشرير باسم المسيح الذي ينادي به بولس مبشراً، فهجم عليهم الرجل الذي به الروح الشرير، ومزق ثيابهم وجرّح أجسادهم، وطردهم من البيت، فكان هذا برهاناً على صحة الإنجيل الذي ينادي به بولس في نظر أهل افسس، فأقبل كثيرون إلى الإيمان المسيحي، وجاءوا بعدد كبير من كتبالسحر التي تبلغ قيمتها خمسين ألفاً من الفضة واحرقوها ( أعمال 19: 14-19 ).'),
(1807, 'سَكيب', 'اطلب (( تقدمة )).'),
(1808, 'سُكْر', 'اطلب (( شرب. خمر. مسكر )).'),
(1809, 'مُسْكر', 'كان العبرانيون يصنعون المسكر من العنب، فكانوا يجمعون الثمر الناضج، ويعصرونه في معصرة منحوتة من الحجر، بأن يدوسه رجال متخصصين بأقدامهم. وكان العصير يخرج من ثقوب خاصة من الحجر إلى مخزن منحوت في الصخر أيضاً ( اشعياء 5: 2 ونحميا 13: 15 ). وكان الرجال الذين يعصرون العنب بأقدامهم يستندون إلى حبال مدلاة من سقف المكان، ويشتغلون وهم يغنون بصوت مرتفع ( اشعياء 16: 10 ) ثم كانوا يأخذون العصير الطازج ويضعونه في زقاق ( قرَب الجلد ) أو في أواني فخارية أو خزفية ( أيوب 32: 19 ومتى 9: 17 ) حتى يتخمر، ثم كانوا يصفونه خوفاً من وجود حشرات فيه ( لاويين 11: 20 و 23 و 41 و 42 ) ولعل حديث المسيح في متى 23: 24 كان يشير إلى ذلك.وكان اليهود يشربون العصير طازجاً قبل أن يختمر إذا أرادوا ( يوئيل 2: 24 واشعياء 62: 8 و 9 و 65: 8 ) على أن بعض اليهود كانوا يفصلون العصير بعد أن يختمر ( هوشع 4: 11 ولوقا 5: 39 ). ويعتقد الكثيرون أن المسيح قدم نتاج الكرمة أي العصير غير المختمر إلى تلاميذه في العشاء الرباني ( متى 26: 29 ).أما الخمر المتخمرة فقد كانت تستعمل طيباً ( 1 تيموثاوس 5: 23 ) وكان الولع بالخمر الكثير ممنوعاً ( 1 تيمو 3: 8 ). ومع أن البعض كانوا يسكرون (  1 تس 5: 7 ) إلا أن الكتاب ينهي عن المسكر ( أمثال 20: 1 واشعياء 5: 11 ).'),
(1810, 'سُكُّوت', 'اسم عبري معناه (( مظلات )) وهو:( 1 ) المكان الذي رحل إليه يعقوب بعد أن ترك أخاه عيسو، وقد أطلق عليه هذا الاسم بعد  أن أقام فيه مظلات له ولبنيه ولمواشيه ( تكوين 33: 17 ). وقد وقعت سكوت من نصيب سبط جاد، وجاء ذكرها مع بيت نمرة ( يشوع 13: 27 ). وقد عاقب جدعون أهلها بعد عودته من حروب ذبح وصلمناع ملكي المديانيين منتصراً، فقد درس شيوخ سكوت مع شوك البرية وقتل أهلها وهدم برج فنوئيل، وكان قد طلب عونهم ضد المديانيين فرفضوا ( قضاة 8: 5-8 و 14-16 ) وكان بين سكوت وصرتان مسابك للنحاس سكبوا فيها آنية الهيكل في أيام سليمان ( 1 ملوك 7: 46 و 2 أخبار 4: 17 ).وتقع سكوت شرق الأردن وشمال مخاضة يبوق ومن مزموري 60: 6 و 108: 7 نعرف أنها كانت تقع في واد. ومكانها اليوم تل اخصاص غربي دير علة بالقرب من اليبوق ( نهر الزرقاء ) وعلى بعد أربعة أميال شرقي الأردن.( 2 ) أول محلة وقف فيها العبرانيون بعد خروجهم من مصر ( خروج 12: 37 و13: 20 وعدد 33: 5 ) والبعد بينها وبين ايثام يدل على أنها تقع في وادي الطميلات، ولكن المكان غير معروف بالضبط. وكانت في سفر يوم من رعمسيس، فظن البعض أنها تل المسخوطة، وظن آخرون أنها المدينة المحيطة بفيثوم.'),
(1811, 'سُكُّوث بَنُوت', 'وهو اسم صنم أقامه المستعمرون البابليون في السامرة، وبنى له المهاجرون من بابل هيكلاً هناك ( 2 ملوك 17: 30 ) ويقول الأستاذ دليش: أن سكوت بنوث هو الاسم العبري للإله اكاد الحاكم الأعظم للعالم عند البابليين. ويظن آخرون أن هذه الصيغة العبرية لاسم إلهة بابلية لفظ الاسم فيها (( صاربنيتو )).'),
(1812, 'سِكِّيُّون', 'اسم قبيلة في إفريقيا، لعلها قريبة من شواطئ البحر الأحمر، وقد جاء بعض جنود منها مع جنود لوبيين وأحباش تحت قيادة شيشق ملك مصر لغزو يهوذا ( 2 أخبار 12: 30 ).'),
(1813, 'مَسكن-مساكن', 'كان الآباء الأولون يسكنون الكهوف ( تكوين 19: 30 ). ولكن المطر كان يحيلها إلى مستنقعات تصعب فيها الحياة، ففكر الناس في طريقة أخرى للسكن، فسكن جانب كبير من القبائل القديمة في خيام مؤلفة من عواميد من الخشب تمتد فوقها أغطية من شعر المعزى، وهي مشدودة بحبال ومثبتة بأوتدة ( اشعياء 54: 2 ). وكانت تنقسم أحياناً إلى غرف بواسطة قطع من شعر المعزى. وكانت تفرش أرضها بالبسط والسجاد، وكان الباب ستارة من القماش تفتح برفعها أو بدفعها إلى جانب وكانوا يوقدون النار في موقدة في أرض الخيمة، وهذا كله يشبه ما يفعله البدو في خيامهم حتى اليوم. وقد أخذ بولس الرسول الخيمة ليشبه بها إقامة الإنسان في الجسد، لأن ساكن الخيمة غريب، ولأن الخيمة معرضة للزوال ( 2 كورنثوس 5: 1-10 ).ثم تغيرت عوائد الناس وأخذوا يبنون بيوتاً مستديمة، وأتقنوا فن البناء منذ عصر قديم ولكن العبرانيين لم يبدأوا بناء البيوت إلا بعد استقرارهم في أرض كنعان. غير أن الآباء الأولين مقل إبراهيم وإسحق ويعقوب كانوا يسكنون الخيام.وتتكون ثلاثة أرباع مساكن فلسطين من طابق واحد ذي غرفة واحدة، وينقسم داخله إلى قسمين، احدهما أعلى من الآخر ببضعة أقدام. ويقود الباب إلى الجزء الأول الذي هو مسكن الحيوانات، ثم يصعد إلى الداخل إلى الجزء الثاني ببضع درجات، حيث يسكن الناس. وجدران البيت مصنوعة من الطين أو الطوب الأخضر، ويُصنع السقف من أغصان الشجر والنخيل ثم يغطى بطبقة كثيفة من الطين، وهذه البنية لا تتحمل المطر ( حزقيال 13: 10-16 ).\nأما بيوت الطبقة الغنية فتختلف عن ذلك جداً، ويقدم الكتاب المقدس لنا في قصصه وأحاديثه ما يعطي فكرة واضحة عن البيت القديم. فقد كان الأغنياء يبنون بيوتاً كثيرة الثمن ( عاموس 3: 15 ) وكانت غُرف البيت مرّتبة حول صالة مربعة فسيحة، وباب الدخول في صدر الصالة، وكثيراً ما كانت تكون عند المدخل دار خارجية أو دهليز فيه البواب ( أعمال 12: 13 ) أما الصالة فتسمى الوسط ( لوقا 5: 19 ). وهي التي تمد الغرف المختلفة بالنور والهواء، وكثيراً ما كانت تكون مبلطة كما في أيامنا هذه، وفي وسطها بئر ( 2 صموئيل 17: 18 ) وحول هذه الصالة رواق مسقوف. وكانت الصالة المربعة موضع أجتماع الضيوف ( استير 1: 5 ). وكانوا أحياناً يمدون فوقها الستائر لتمنع دخول أشعة الشمس إليها في الصيف، ولعل المرنم يشير إلى ذلك وهو يقول (( الباسط السموات كشقة )) ( مزمور 104: 2 ). وكان صاحب البيت يجلس في الصالة ( استير 5: 1 ) وقد تكون عالية فيصعدون إليها بسلم بسيط أو بمرتقى منحدر ( 1 ملوك 6: 8 ). وكانت حجرات النساء في أقصى البيت الداخلي ( استير 2: 3 ) وكثيراً ما كانت مزينة، وكانت الحجرات العليا في الطابق الثاني تبرز فوق الطريق، كما انه قد تبنى علية للضيوف فوق السطح في وسطه، ويصعدون إليها بسلم منفصل من خارج البيت، مثل العلية التي بنوها لاليشع النبي ( 2 ملوك 4: 10 ) ومثل علية البرود للملك عجلون ( قضاة 3: 20-24 ) وقد هرب اهود من السلم الخاص بالعلية بعد أن قتل عجلون. وكان هناك المخدع-وهو غرفة صغيرة منعطفة عن بقية البيت ( متى 6: 6 ).أما سطح البيت فقد كان لابد له من أن يحاط بسياج مبني ليمنع سقوط الذين يصعدون فوق السطح حسب أمر الناموس ( تثنية 22: 8 ) وكان ينبت على السطح عشب ( 2 ملوك 19: 26 ) وكانوا يصنعون المظال على السطوح ( نحميا 8: 16 ) كما كانوا يتمشون عليه ( 2 صموئيل 11: 2 ) وكانوا يستعملونه لتجفيف الحبوب والتين ( يشوع 2: 6 ) كما كانوا ينصبون الخيام عليه ( 2 صموئيل 16: 22 ) وكانوا يجتمعون عليه للمشورة ( 1 صموئيل 9: 25 ) وكانوا أحياناً يستعملونه لعبادة الأوثان ( 2 ملوك 23: 12 ).أما كوى المسكن فكانت تفتح في الغالب إلى الدار، غير أن بعضها يشرف على الطريق ( قضاة 5: 28 و 2 ملوك 9: 30 ).ولم يكن للبيوت القديمة مداخن، فكانت الكوى تخرج الدخان ( هوشع 13: 3 ). وكانوا يستخدمون المواقد للتدفئة في الشتاء ( ارميا 36: 22 ). وكانت بيوت الأغنياء من الحجارة المنحوتة ( عاموس 5: 11 ) أو من رخام وحجارة ثمينة ( استير 1: 6 ). وكانوا يستعملون للسقوف أو لتغطية الحوائط خشب الأرز ( ارميا 22: 14 ) وكانوا ينتحتون البيوت ويزينونها بذهب وفضة وعاج ( 1 ملوك 22: 39 ومزمور 45: 8 وعاموس 3: 15 ) أما بيوت الفقراء فكانت تبنى من حجارة غير منحوتة. أو من تراب أو من لبن أو قضبان مورقة بعد تغطيتها بالطين.وقد جرت العادة أن يدشن العبرانيون بيوتهم ليباركها الله ( تثنية 20: 5 ).'),
(1814, 'مَسكونة', 'وردت هذه العبارة في الكتاب بعدة معاني:( 1 ) المكان الذي يوجد فيه سكان ( أيوب 15: 28 ).( 2 ) الكون ( 1 أخبار 16: 30 ).( 3 ) شعوب الأرض ( مزمور 96: 13 و 98: 9 ).( 4 ) الأرض ( مزمور 33: 8 ).( 5 ) الإمبراطورية الرومانية ( لوقا 2: 1 ).( 6 ) قيلت مجازاً عن الأرض المقدسة ومجاوراتها ( أعمال 17: 6 ).'),
(1815, 'مَسكِين', 'المقصود بهذه الكلمة الفقير إلى المال، كما أنها تعني المسكين بالروح الذي له ملكوت السماوات. أما عن المسكنة المادية فإن الله لا يرضها. وقد طلب توزيع الدخل توزيعاً عادلاً ( خروج 6: 4 و 8 ). وعندما كان العبراني يبيع أرضه بسبب الحاجة كان يستردها في سنة اليوبيل ( لاويين 25: 13 و 23 ) كما أن جائع كان يمكن أن يقطف السنابل ( تثنية 23: 24 و 25 ) كما يجمع البقية التي يتركها الحصادون عن قصد في الحقول والكروم ( لاويين 19: 9 وتثنية 24: 19-21 ) وكان المسكين يأكل ثمر الأرض في السنة السابعة والسنة الخمسين ( لاويين 25: 4-7 و 11 و 12 ) ويستلف أي مبلغ من المال أو يبيع خدماته لرجل غني ( لاويين 25: 38-42 وتثنية 15: 7-10 ) على أن المسكنة بقيت بالرغم من كل هذه التشريعات، وكان السبب في ذلك يرجع إلى سوء تصرف بعض الناس في أموالهم، كالابن الضال في الكورة البعيدة، أو بسبب الضعف الروحي وعدم مراعاة هذه النواميس الدينية ( اشعياء 1: 23 وحزقيال 22: 7 و 29 ).وقد اهتمت الكنيسة المسيحية بالمساكين فأعطى الغني المحتاج بسخاء ( أعمال 2: 45 و 4: 32 و 1 كورنثوس 16: 1-3 ).أما المسكنة الروحية فتعني أن يشعر الإنسان بأن ما لديه هو من يد الله، ولا فضل له فيه، كما تعني الأعتراف بالخطيئة ونوال الغفران بدم المسيح.'),
(1816, 'سَكُونْدُس', 'اسم لاتيني معناه (( الثاني )). وهو مسيحي من تسالونيكي وكان من مرافقي بولس الرسول في سفره من مقدونية إلى آسيا الصغرى ( أعمال 20: 4 ) وكانوا قد سبقوا بولس إلى تراوس وانتظروه هناك. فإذا كان سكوندس واحداً من ممثلي الكنائس اليونانية التي قدمت عونها المالي لكنيسة أورشليم، فلا بد أنه سافر مع بولس حتى أورشليم ( أعمال 24: 17 و 2 كورنثوس 8: 23 ).'),
(1817, 'سِكِيثيّ', 'ورد ذكر هذا الجزء من الناس في كولمبي 3: 11 وهم قبائل غير متحضرة متجوّلة من شمال البحر الأسود وبحر قزوين، وهم من البدو الذين لا يزرعون بل ينتقلون في عربات تحمل كل متاعهم.ولهم عادات غريبة فهم لا يغتسلون بالماء أبداً، ويشربون دم الضحية الأولى في المعركة، ويستعملون جماجم الموتى كأواني للشرب. وكانوا يعبدون السيف. وكانوا يقدمون جسد رجل من كل مئة من أسراهم لهذا الإله الغريبن وكانوا يشتغلون في الحرب فقط، فيغيرون على البلاد كالجراد. وقد سكن بعضهم في بيت شان.'),
(1818, 'سَلاميس', 'مدينة على شاطئ جزيرة قبرص الشرقي وتقع على بعد ثلاثة أميال شمالي فاماغوستا الحالية على ضفة نهر يديئيوس الذي يجري في داخل الجزيرة حتى مدينة نيقوسيا العاصمة الحالية وقد كانت ميناء عظيماً عامراً بالسكان وناجحاً أثناء الحكم الروماني واليوناني، وكان أهلها خليطاً من اليونانيين والفينقيين الذين كانوا يتاجرون مع أهل صقلية وسوريا. وكانوا يتعبدون لإله زفس.زار بولس وبرنابا هذه المدينة ومعهما يوحنا مرقس في الرحلة التبشيرية الأولى، بعد أن تركا ميناء سلوكية، وقد قطعوا من رحلتهم 130 ميلاً، وذهب بولس وبرنابا يبشران بالإنجيل في مجامع المدينة، وكان لليهود مجمع هناك ( 1 أعمال 13: 5 ) كما كان لليهود عدة مجامع في بلاد مختلفة من جزيرة قبرص. ولم يرجع بولس إلى سلاميس مرة أخرى، ولكن برنابا عاد في رحلته التبشيرية الثانية ( أعمال 15: 39 ) ويقول التقليد أنه مات هناك شهيداً بأمر من نيرون في المكان بالقرب من سلاميس.وفي عام 116 ميلادية ثار اليهود هناك وقتلوا مئتين وأربعين ألفاً من اليونان والرومان، فأخمد ثورتهم الإمبراطور هادريان، وهدم جزء من المدينة، ثم آتت الزلازل على الجزء الباقي منها. ولكن الإمبراطور المسيحي قسطنطين الثاني بناها، وكان فيها أسقف مسيحي ممتاز ثار ضد الهراطقة واسمه ابيفانيوس. وقد أخربت في عام 647 م. وتوجد اليوم في مكانها عواميد محطمة وصهاريج مشققة وأساسات الأبنية القديمة، واسمها فاماغوستا القديمة.'),
(1819, 'سِلاح', 'كانت الأسلحة نوعين: نوع للهجوم وآخر للدفاع، ويقدم لنا الكتاب أسماء أسلحته الهجوم والدفاع التي استعملها جليات الفلسطيني الجبار ( 1 صموئيل 17: 5-7 ) والتي جهز بها الملك عزيا جيشه ( 2 أخبار 26: 14 ) ثم يقدم لنا النبي ارميا أسماء الأسلحة التي استعملها جيش فرعون نحو ( ارميا 46: 3 و 4 ) وفي العهد الجديد يتكلم بولس الرسول عن أسلحة المؤمن الروحية، مستعيراً اياها من أسلحة الجندي الروماني ( افسس 6: 10-17 ).( أ ) أسلحة الهجوم:( 1 ) العصا - وهي سلاح الراعي، وكان يعلقها على ذراعه بخيط، ومع أنه كان يستعملها لقيادة غنمه ولتشجيعها ولعدها ( مزمور 23: 4 ولاويين 27: 32 ) إلا أنه كان يستخدمها أحياناً للضرب ( اشعياء 10: 5 و 15 ) ولعل داود استخدم عصاه لقتل الأسد والدب. وكان المحارب يضرب بالعصا أو يرمي عدوه بها.( 2 ) المقلاع - وهو سلاح عادي مصنوع من الجلد العريض في الوسط عرضاً يكفي لوضع حجر فيه، ويمسك الشخص بطرفي الجلد ويطوح المقلاع بشدة بعد وضع الحجر إلى مرماه. وكان البنيامينيون ماهرين في الرمي بالمقلاع باليد اليسرى واليمنى على السواء ( قضاة 20: 16 ). وكان المقلاع يستعمل في الحرب ( 2 ملوك 3: 25 ) وهو الذي قتل به داود جليات.( 3 ) السهم والقوس - وهو سلاح يمكن أن يستعمله المشاة أو راكبو الخيل، وقد صُنعت السهام أولاً من خشب خفيف، والقوس من خشب مرن أو نحاس ( مزمور 18: 34 ) أما الوتر فكان يصنع من شعر الخيل أو الجلد وكانت القوس تخطئ أحياناً ( مزمور 78: 57 ) إذ تنثني إلى الوراء فتجرح صاحبها، أو أنها تضعف فلا توصل السهم إلى غايته. وكانت السهم أحياناً مسموة ( أيوب 6: 4 ) كما كان بعض المقاتلين يضعون جمراً في طرف السهم ليشعل النار في العدو ( مزمور 120: 4 ).( 4 ) الرمح - وكان يُصنع من الحديد ويبلغ طوله خمسة وعشرين قدماً أحياناً ( 1 صموئيل 17: 7 ) وكان المحارب يحمله معه، فإذا نام يركزه عند رأسه ( 1 صموئيل 22: 6 و 26: 7 ) وكان يصدر عنه بريق في الحرب ( ناحوم 3: 3 ).( 5 ) السيف - وكان يُستخدم في الدفاع أو الهجوم، وكان حدّه من الحديد، وكان يتدلى من حزام في الوسط إلى جهة الشمال، وقد يكون مسنون الحدين ( قضاة 3: 16 ) وكان يحفظ في الغمد ( 1 صموئيل 17: 5 ) وكان استلال السيف معناه البدء في الحرب ( حزقيال 21: 3 ) ويسمّي بولس الرسول كلمة الله سيف الروح ( افسس 6: 17 ) ويقول كاتب الرسالة على العبرانين أن كلمة الله أمضى من كل سيف ذي حدين ( عبرانيين 4: 12 ).( 6 ) الفأس - وهي من أدوات الحرب وتعرف الفأس الحربية عند العامة بالبلطة ( ارميا 51: 20 ).( ب ) أسلحة الدفاع:( 1 )الترس أو المجن - والترس هو الصغير والمجن هو الترس الكبير، وكان يصنع اسم يوناني معناه الخشب، وكثيراً ما حمله رجل خاص ( 1 صموئيل 17: 7 و 41 ). وكانت أحياناً تلون بألوان مختلفة على هيئة دوائر في النصف ( ناحوم 2: 3 ). ويقول الكتاب أن خشب اتراس جوج سيكفي إسرائيل وقوداً سبع سنوات ( حزقيال 39: 9 ) وكثيراً ما كانوا يغطونه بالجلد، ويغمسون الجلد في الزيت حتى لا يتشقق ( اشعياء 21: 5 ) وكان لدى سليمان مئتا ترس من الذهب الخالص ( 1 ملوك 10: 16 و 17 ). وكان المحارب يحمل الترس بحزام جلدي على ظهره، فإذا جاءت المعركة نزعه ليستعمله بيده اليسرى، بإدخال اليد تحت سيرين من الجلد على مؤخر الترس وقبض الأصابع على سير صغير عند حافته ( اشعياء 22: 6 ). وكان الترس يستعمل لحماية المحارب من السهام والرماح والحجارة وقطع الفحم الملتهبة التي كانت تلقى على الجنود. ويقول الكتاب أن الله ترس المؤمن ومجنه ( تكوين 15: 1 ومزمور 35: 2 ) كما يقول بولس الرسول أن المؤمن يطفئ بترس الإيمان جميع سهام الشرير الملتهبة.( 2 ) الخوذة - وهي لباس للرأس كان يلبسه الملوك والقواد وغيرهم من المحاربين ( 1 صموئيل 17: 38 ) وقد عمل عزيا خوذات لكل جنوده ( 2 أخبار 26: 14 ) وهكذا صنع فرعون نخو ( ارميا 46: 4 ) وكانت الخوذة من جلد أو نحاس تتزين قمتها بريش أو بعرف. ويقول بولس الرسول أن الخلاص هو الخوذة لرأس المؤمن.( 3 ) الدرع - وكان يغطي الصدر والظهر والبطن، وهو يتألف من جزئين موصولين عند الجنب، وهو إحدى القطع التي جهزها عزيا لجنوده ( 2 أخبار 26: 14 ) وكان آخاب يلبسه عندما مات في معركة رامعوت جلعاد ( 1 ملوك 22: 34 ) ويقول اشعياء أن الله يلبس البر كدرع والخلاص كخوذة ( 59: 17 ) ويحث بولس الرسول على لبس درع البر ( افسس 6: 14 ) وكان الدرع يُصنع من صفائح من نحاس أو من الجلد أو الكتان المبطن.( 4 ) الجرموق وهو جورب من النحاس كان يلبس على الرجل، ولم يرد ذكره إلا في أسلحة جليات ( 1 صموئيل 17: 6 ) مما يظهر أن العبرانيين لم يكونوا يستعملونه، لكن الأشوريين والمصريين استعملوه.( 5 ) المنطقة - حزام على الوسط كان المحارب يدلى منه سيفه على جهة الشمال ( 1 صموئيل 18: 4 ).'),
(1820, 'سَلْخَة أو سَلْكَة', 'اسم عبري معناه (( سياحة )) أو (( سلوك السبيل )) وهو اسم مدينة ورد للمرة الأولى في تثنية 3: 10 لتعيين حدود باشان الشرقية، وكانت إحدى البلاد التي حكمها عوج ملك باشان ( يشوع 12: 5 ) ثم وقعت في التقسيم في نصيب نصف سبط منسى ( يشوع 13: 29 ) ومن 1 أخبار 5: 11 نرى أن جاد سكن مقابل رأوبين في أرض باشان حتى إلى سلخة، ونستدلّ من ذلك على أن الأسباط غيّروا حدود أقسامهم.\nوسلخة القديمة هي سلخد الحالية التي تقع عند طرف  جبل الدروز الجنوبي ( جبل باشان ) وتقوم قلعتها على تلة بركانية يبلغ ارتفاعها 300 قدم تشرف على السهل، وكان الرومانيون قد بنوها، وهي محاطة بخندق عميق مطمور بالأحجار، وعلى بعض اعتابها رسوم نسور روماينة وكتابات يونانية وعربية. ويوجد حول تلك القلعة 800 بيت مسقوفة بالحجارة، على أن سكانها اليوم قليلون.'),
(1821, 'سَلَد', 'اسم عبراني معناه (( ابتهاج ))، وهو رجل من بني يهوذا، ابن ناداب، وقد مات دون أن يترك نسلاً ( 1 أخبار 2: 30 ).'),
(1822, 'سِلسلة. سلاسل', '( 1 ) كانت تُستعمل للزينة وتلبس حول الرسغ ( اشعياء 3: 20 ) وحول الرقبة كقلائد ( نشيد 4: 9 ) كما استعملها سليمان أمام المحراب في هيكله ( 1 ملوك 6: 21 ).( 2 ) كانت تستعمل كقيود للأسرى، وكانت تصنع من نحاس ( ارميا 39: 7 ) وكان الرومان يربطون يد الأسير الواحدة بيد عسكري أو يربطون يديه بيدي عسكريين على جانبيه ( أعمال 12: 6 و 7 و 21: 33 ) كما كانوا يربطون المجانين بالسلاسل ( مرقس 5: 3 و 4 ).( 3 ) كانت تستعمل جزءاً من ملابس رئيس الكهنة ( خروج 28: 14 ).\n( 4 ) كانت تُستعمل بمعنى مجازي في الأسر والضيق ( 2 بطرس 2: 4 ورؤيا 20: 1 ).'),
(1823, 'مُسَلَّط', 'وكيل السلطان أو نائب الملك أو مندوب صاحب الأمر وقد كان يوسف متسلطاً على أرض مصر بتفويض من الملك فرعون ( تكوين 42: 6 ).'),
(1824, 'سَلُّ. سَلَّة', 'وعاء يصنعونه من عيدان الشجر، وبالأخص عيدان شجر الصفصاف، وكان يُستعمل لحفظ الأشياء أو حملها أو نقلها ( تكوين 40: 17 وخروج 29: 3 ). اطلب (( قفة )) و (( زنبيل )) ( تحت زبل ).'),
(1825, 'سِلَّى', 'مكان فيه قام عبيد الملك يوآش وقتلوه في بيت القلعة حيث كان نازلاً ( 2 ملوك 12: 20 ) وحيثما يكون بيت القلعة هذا فإن سِلَى في أسفله، لكن المكان غير معروف اليوم.'),
(1826, 'سَلاَّي', 'اسم عبراني معناه (( الوفاء فوراً )) أو (( تكريس )) وهو:( 1 ) رجل بنياميني سكن في أورشليم بعد عودته من السبي ( نحميا 11: 8 ).( 2 ) كاهن عاد إلى أورشليم ( نحميا 12: 20 ).'),
(1827, 'سَلُّو', 'اسم عبراني معناه (( وفيّ فوراً )) أو (( مكرس )) وهو:( 1 ) رجل من سبط بنيامين ( نحميا 11: 7 و 1 أخبار 9: 7 ).( 2 ) كاهن عاد إلى أورشليم بعد السبي مع زربابل بن شألتئيل ( نحميا 12: 7 ).'),
(1828, 'سَلام', 'حالة الراحة من الاضطراب الخارجي الذي يحدث بهجوم جيش في الحرب، أو من الاضطراب الداخلي براحة النفس. وكانت هذه الكلمة عبارة التحية عند اليهود، وهم يقصدون بها الأطمئنان والسرور والراحة، كما يقصدون بها الأمن الخارجي والتحرر من الاستبعاد السياسي. وهو تحية المسيحيين أيضاً، ولكنهم يريدون به السلام القلبي العميق الذي اشتراه المسيح بدمه، سلاماً لا تُنيله الدنيا ولا تنزعه كل عواصفها. وهو السلام الذي يملك على قلب المؤمن نتيجة مصالحته مع الله ( رومية 5: 1 وافسس 2: 14 ).\nوكان اليهود يُطيلون السلام والتحية فتتعطل المصالح ويضيع الوقت، وعلى هذا فقد طلب اليشع النبي من غلامه جيحزي ألا يطيل السلام على أحد، إذ قصد أن يسرع إلى الصبي الميت ليضع عكازه عليه ( 2 ملوك 4: 29 ) كما هى المسيح تلاميذه من التسليم على أحد في الطريق لنفس السبب ( لوقا 10: 4 ).'),
(1829, 'سُلُّم', 'رأى يعقوب سلماً منصوبة على الأرض ورأسها يمس السماء وذلك في رؤيا ليلا وهو في طريقه من بيت خاله لابان هارباً من وجه عيسو، وكانت ملائكة الله تصعد وتنزل عليها. وفي هذا تعبير عن عناية الله الخاصة بيعقوب، فقد كانت هذه السلم واسطة اتصال السماء بالأرض التي تأتي عليها الملائكة لخدمة الإنسان. وكان الله نفسه على رأس السلم. وسلم بيت إيل يشير إلى ناسوت المسيح الذي التقت فيه السماء بالأرض.'),
(1830, 'سَلْمَا', 'اسم عبراني معناه (( لابس ))، وهو ابن حور من نسل كالب، وأبو بيت لحم،ويُظن بعضهم انه سلمون ( 1 أخبار 2: 51 ).'),
(1831, 'سَلْمَاي', 'اسم عبراني معناه (( كساء )) أو (( مكسوّ )) وهو أب لبعض النثينيم الذين عادوا إلى أورشليم ( نحميا 7: 48 ) ويدعى أيضاً شمُلاوي أو سلْمَاي ( عزرا 2: 46 ).'),
(1832, 'سَلْمو أو سَلْمَا', 'أبو بوعز ( 1 أخبار 2: 11 ) اطلب (( سلمون )).'),
(1833, 'سَلْمُون', 'اسم عبراني معناه (( كساء )) أو (( مكسو )) وهو أبو بوعز زوج راعوث وجدّ يسى أبي داود ( راعوث 4: 20 و 21 ) وقد ورد اسمه في سلسلة انساب المسيح ( متى 1: 4 و 5 ) ومن هذا نرى أنه تزوج راحاب وولد منها بوعز.'),
(1834, 'سَلْمُوني', 'اسم ميناء في كريت، توقفت فيه سفينة بولس الرسول بعد أن منعتها الرياح المضادة من متابعة الرحلة إلى روما مباشرة ( أعمال 27: 7 ) وموقعها اليوم بلد اسمه رأس سيديرو في الكن الشمالي الشرقي من جزيرة كريت.'),
(1835, 'سَلاه', 'تعبير موسيقي ورد إِحدى وسبعين مرة في تسعة وثلاثين مزموراً، كما وردت ثلاث مرات في سفر حبقوق. ويظن البعض أن الكلمة تعني تقوية اللحن وتوقيعه بشدة، وفي هذا المعنى يتوقف المرنمون لتسمع الآلة الموسيقية وحدها. ويظن آخرون أن معناها وقفة موسيقية، فتتوقف الآلات الموسيقية ويصمت المرنمون. ويقول يعقوب الذي من الرها أنها تشبه آمين التي يرددها المرنمون المسيحيون بعد سماع البركة، فكأن سلاه تعني: (( أعط بركتك )). ولكن المعنى الأساسي المقصود من هذه الكلمة غير معروف.'),
(1836, 'بِرْكَة سِلْوام', 'وسلوام اسم عبراني معناه (( مُرسَل ))، وهو اسم البركة القريبة من أورشليم والتي يطلقعليها اسم شيلوه ( اشعياء 8: 6 ) والتي تجري مياهها في هدوء وسكوت، وتقع عند جنينة الملك ( نحميا 3: 15 ) ولو أن هذا لا يحدد موقعها بالضبط. ويقول المؤرخ يوسيفوس أنها تقع أسفل وادي الجبانين، وهي البركة التي تسمى اليوم بركة سلوان، ويبلغ طولها 58 قدماً، وعرضها 18 قدماً وعمقها 19 قدماً، وجوانبها مبنية بالأحجار-ولو أن جانبها الغربي تحطم، وفيها ماء جار ملح المذاق، ولو أنه اليوم غير نقي لأن الغسالات يغسلن فيه، كما يغطس فيه الدباغون جلودهم ويأتي الماء إلى بركة سلوام من عين العذراء في قناة ملتوية منحوتة في الصخر 1708 قدماً ويفيض الماء ليروي بعض الحدائق في وادي قدرون.وقد دخل صبي من جانب القناة المواجهة للبركة، فأكتشف ستة سطور من الكتابة في عام 1880، وبعد فحص الكتابة أتضح أنها باللغة العبرية القديمة، وترجع إلى زمن حزقيا الملك، ولو أن بعض السطور قد محيت بمرور الزمن، إلا أن المعنى المقصود من الكلام المنحوت واضح، وهو أن العمال بدأوا ينحتون الصخر من جانب العين ومن جانب البركة وظلا يعملان حتى التقيا أخيراً، ووصل ماء النبع إلى البركة.وقد اعتاد اليهود في احتفالهم باليوم الأخير العظيم من عيد المظلات أن يذهب كاهن بابريق من ذهب إلى بركة سلوام، ويغترف به ثلاث مرات من الماء، ثم يعود بالإبريق المليء في موكب عظيم إلى الهيكل مجتازاً باب الماء، ثم يصب الماء في وعاء فضي على جانب المذبح الغربي وسط الترنيم: (( احمدوا الرب فإنه صالح، وأن إلى الأبد رحمته )). ولعل المسيح أشار إلى هذا الاحتفال عند قوله: (( إن عطش أحد فليقبل إلي ويشرب )) ( يوحنا 7: 37 و 38 ).وقد أرسل المسيح الرجل المولد أعمى في يوم السبت إلى البركة ليغتسل فيها فيبصر ( يوحنا 9: 7-11 ).بُرج سلوام:هو البرج الذي سقط على ثماني عشرة شخصاً وقتلهم ( لوقا 13: 4 ). ولا يعرف مكانه اليوم بالضبط، ولعله كان في القرية التي تسمى اليوم سلوان عند جبل الزيتون بقرب المكان الذي بنى فيه سليمان الهياكل لكموش وعشتاروت وملكوم. واسمه اليوم جبل الفساد.'),
(1837, 'سِلوانس', 'انظر (( سيلا )).'),
(1838, 'سَلوكية', 'ميناء إنطاكية التي ركب منها بولس وبرنابا في رحلتهما التبشيرية الأولى ( أعمال 13: 4 ) وهي على شاطئ البحر على بعد خمسة أميال شمال فم نهر العاصي، وعلى بعد 16 ميلاً غرب إنطاكية. وكانت سلوكية القديمة على منحدر جبل كوريفيوس ( اسمه الآن جبل موسى ). وقد أسسها سلوقس نيكاتور الذي توفي عام 280 ق. م. وقد كانت أثناء حكم الرومان ذات جمال عظيم وتنظيم رائع، كما كانت ميناؤها حسنة ولا زالت كذلك إلى يومنا، ولها رصيفان أحدهما باسم بولس والآخر باسم برنابا. وقد اجتمعت في سلوكية بعض مجامع الكنيسة الأولى، ولكن أهميتها صارت إلى الاضمحلال منذ القرن السادس والسابع الميلاديين. واسمها اليوم السويدية ويوجد بقرب خرائب سلوكية القديمة قرية اسمها القبوصي.'),
(1839, 'سَلْوَى', 'طيور ترحل من إفريقية في الجنوب إلى الشمال في أسراب كثيرة العدد جداً، وقد صيد منها في إيطاليا مئة ألف طائر في يوم واحد، وهي تطير في أسراب فتشبه السحاب الكثيف ويُسمى السلوى باللغة اللاتينية Coturnix dactylisonas. وقد أرسل الله كمية كبيرة من هذه الطيور إلى محلة\nالعبرانيين ليأكلوا لحمها بعد أن تذمروا على موسى ( خروج 16: 13 وعدد 11: 31 ). وقد طارت أسرابها من الجنوب عن طريق البحر الأحمر، فقطعت خليجي العقبة والسويس، ووصلت إلى البرية في شبه جزيرة سيناء متعبة مرهقة، وإذ بدخان محلة العبرانيين يعاكسها فتسقط بالآلاف على الأرض، فيسهل إمساكها باليد والسلوى حلوة المذاق، وهي تبيض من 12 إلى عشرين بيضة وتحتضنها في عش على الأرض، وتطير على ارتفاع صغير (( نحو ذراعين فوق وجه الأرض )) ( عدد 11: 31 ) وقد نشر العبرانيون السلوى ليجففوها باسطين إياها حول الملحلة. وقد كان إرسال السلوى بهذه الكمية الوافرة مدة شهر كامل علامة من علامات عناية الله الكاملة ( مزمور 78: 26-30 ).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1840, 'سُليمان', 'اسم عبري معناه (( رجل سلام )) وهو ابن الملك داود الذي خلفه على عرش بني إسرائيل، فكان أعظم ملك، وقد ملك أربعين سنة. ومع أنه كان لديه ستة أخوة من أمهات مختلفات وهم: امنون وكيلآب وابشالوم وادونيا شفطيا ويثرعام، إلا أن سليمان هو الذي ملك. وهو ابن بثشبع ( 1 ملوك 1: 11 ) التي كانت زوجة لاوريا الحثي. وقد أحب داود سليمان لأنه كان ابن زوجته المفضلة، وأطلق عليه اسم سليمان متمنياً له سلام بلا حرب، ولكن الله أعطاه اسم يديديا أي محبوب يهوه ( 2 صموئيل 12: 24 و 25 ). وكان شديد الذكاء واستوعب كل الدراسات التي تلقاها غالباً على يد ناثان النبي ( 1 ملوك 4: 32 و 33 ).وكان داود قد وعد بثشبع أن يملك ابنها سليمان على الشعب بعده، وذلك بعد خيانة ابشالوم ( 1 ملوك 1: 17 ) وقد حاول ادونيا بن داود أن يأخذ الملك قبل وفاة والده، ولكنه فشل واسلم نفسه لسليمان ( 1 ملوك 1: 53 ) وهكذا صار سليمان وريث العرش بدون منازع.وبدأ سليمان حكمه بالزواج من ابنة الملك فرعون حتى يكون ذلك مصدر أمن له، وقد تزوج سليمان زيجاته لغرض دبلوماسي، فبقي بنوا إسرائيل أربعين سنة في سلام بدون حروب ( 1 ملوك 4: 24 ).وقد رأى سليمان في بدء حكمه حلماً، سأله فيه الله عما يطلب فلم يطلب غنى ولا عظمة ولا طول أيام، بل طلب الحكمة ( 1 ملوك 3: 5-9 ) وقد أعطاها له الله، فظهرت حكمته في ذهابه إلى المذبح الذي بناه موسى في البرية وذبح ألف محرقة هناك ( 1 ملوك 3: 4 ). كما ظهرت حكمته في الحكم بين السيدتين المتنازعتين على الطفل الحي ( 1 ملوك 3: 16-28 ) وقد تحقق في حكم سليمان وعد الله لإبراهيم إذ ملك سليمان من نهر مصر إلى نهر الفرات الكبير ( تكوين 15: 18 ).وكان يعاون سليمان في ملكه اثنا عشر وكيلاً، هم رئيس الكهنة ومعه كاهنان، وكاتبان ومسجل، ورئيس الجيش، ورئيس على الوكلاء، وصاحب لملك، ورئيس للبيت، ورئيس للتسخير ( 1 ملوك 4: 2-6 ) كما كان له اثنا عشر وكيلاً آخرين، كل وكيل على قسم من الشعب، يجمع من قسمه نفقات شهر لبيت الملك، وكان اثنان من هؤلاء الوكلاء متزوجين من ابنتين لسليمان ( 1 ملوك 4: 7-19 ) وقد زاد عدد الشعب في حكمه على الأربعة ملايين، لكنهم كانوا مثقلين بالضرائب.وقد كان سليمان على صداقة مع حيرام ملك صور، فقدم سليمان الطعام لعبيده، مقابل مواد بناء الهيكل ( 1 ملوك 5: 1-12 ) وقد اعتمد سليمان على الشعب في أعمال السخرة للأبنية العامة ولبناء الهيكل ( 1 ملوك 5: 13-18 ) وكان سليمان بناءً عظيماً مثل رمسيس الثاني، فبنى الهيكل، وكان أبو داود قد جهز الكثير من مواد البناء قبل موته، ولم يسمح له الرب بالبناء كان رجل دماء ( 1 أخبار 22: 8 ).وقد استغرق بناء هيكل سليمان سبع سنوات على مثال الرسم الذي أعطاه الله لموسى، ولكن بضعف حجمه ( 1 ملوك 6 ) وكان موقع البناء على جبل المريا حيث قدم إبراهيم ابنه اسحق، وعلى بيدر ارونة اليبوسي. وكان الهيكل أعظم أعمال سليمان بلا جدال. ودشن سليمان الهيكل بعد يوم الكفارة العظيم وقبل عيد المظال، في احتفال رائع، وامتلأ بيت الله بالسحاب وملأ مجد الله البيت ( 2 أخبار 5 ) وصلى سليمان صلاة طويلة للرب إله السماء، نجدها في 1 ملوك 8: 23-53. وقدم سليمان محرقات عظيمة لتدشين هيكله، وفي هذا نرى أن سليمان أخذ مكان هرون ومكان موسى؛وبنى سليمان أبنية أخرى مثل بيت وعر لبنان، ورواق الأعمدة، ورواق الكرسي الذي كان موضع القضاء، كما بنى بيتاً لابنة فرعون ( 1 ملوك 7: 2-8 ) وبنى قلعة لحماية الهيكل ( 1 ملوك 9: 24 ). وحصون عديدة في أنحاء المملكة ( 1 ملوك 9: 15-19 ) لحماية المملكة والدفاع عنها. ولم يترك سليمان مجالاً لملك آخر ليبني بعده، فقد أكمل الملوك أو رمموا ما بدأ سليمان به.وعند منتصف أيام حكم الملك سليمان زارته ملكة سبا بعد أن سمعت عن حكمته، فأراها سليمان عاصمة ملكه وابنتيه ونظام حكمه، فأدهشها هذا جداً حتى لم يَبق فيها روح بعد، وقد فاق سليمان كل ما سمعته عن حكمته، بعد أن أجاب على كل أسئلتها، فطوبت رجاله الذين يسمعون حكمته، وقدمت له هدية عظيمة ( 1 ملوك 10 ).وكانت عظمة غنى سليمان مذهلة، وكان عصره عصر نجاح اقتصادي، ولم تكن هناك حروب تستنزف مال الشعب، وقد أتته سفنه مرة محملة بأربع مئة وعشرين وزنة ذهب ( وزنة الذهب قيمتها عشرة آلاف جنيه مصري ) وكانت له أساطيل تجارية في بحر الهند والبحر الأبيض المتوسط، فجلبت له الذهب والفضة والنحاس والعاج والأبنوس والبوص والخيل والمركبات والقردة والطواويس ( 1 ملوك 10: 22 ) وكان في خدمته عشرة آلاف يأكلون من مائدته. وكان له آنية فضية مثل الحجارة وخشب الأرز مثل الجميز. وتقدر قيمة دخل سليمان سنوياً بما يساوي عشرة ملايين دولار تقريباً ( 1 ملوك 9: 26-28 ).وكانت رحابة قلب سليمان عجيبة جداً ( 1 ملوك 4: 29 ) فقد درس كل العلوم التي يمكن أن تدرس وفاق فيها كل علماء عصره المشهورين، فدرس علم النبات وعلم الحيوان وعلم الطيور، وكتب الأمثال وكتب الحكمة والقصائد ( 1 ملوك 4: 29-34 ) وقد ضاع كثير مما كتبه سليمان، ولم يبقى إلا بعضه، مما ورد في سفر الأمثال ( ام 1: 1 و 10: 1 و 25: 1 ) والأغلب أنه هو الذي كتب سفر الجامعة ونشيد الانشاد.على أن السنوات الأخيرة من حكم سليمان كانت مؤسفة، فقد بدأ بتعدد الزوجات، وأحب نساء غريبة كثيرة مع بنت فرعون، فكان له سبع مئة من الزوجات وثلاثة مئة من السراري ( 1 ملوك 11: 1-8 ) فأملنَ قلبه إلى الآلهة الغريبة حتى بنى أماكن لعبادة الأوثان إِرضاء لهن، فغضب الرب عليه، وهدده بتمزيق المملكة عنه، وأقام له خصوماً ( 1 ملوك 11: 9-25 ) وقد طلب سليمان قتل يربعام  الذي قال له النبي اخيا أن معظم المملكة سيكون له فهرب يربعام إلى مصر ( 1 ملوك 11: 26-40 ).وهكذا نرى أن العظمة والغنى والنجاح قد قادت سليمان إلى نهاية بعيدة عن الله... وإذا ببسحابة داكنة تخيم على مساء اليوم الصاحي المشرق.... (( واضطجع سليمان مع آبائه ودُفن في مدينة داود )) فإن حكمة سليمان لم تنطبق على حياة سليمان لم تنطبق على حياة سليمان؛ (( وهوذا أعظم من سليمان ههنا )).وقد أظهر التنقيب في مجدو اصطبلات لخيل سليمان تسع حوالي خمسمئة من الخيل. وكذلك أظهر التنقيب في عصيون جابر مسابك للنحاس ترجع إلى عصر سليمان مما يدل على سعة ملكه وثروته.بِرَك سليمان:ثلاث برك توجد في الجنوب الغربي من بيت لحم، على بعد عشرة أميال من أورشليم، ويقول التقليد أن سليمان بناها ليتجمع فيها الماء ويصل إلى أورشليم في قناة ( جامعة 2: 6 ) وبعضها منحوت في الصخر والبعض الآخر مبني بناءً متيناً. ويتجمّع الماء النازل من المطر من التلال المحيطة، ويصل إلى البرك عن طريق قناة تحت الأرض لحفظ الماء من التبخر. وهي برك عظيمة الحجم، كانت تمون أورشليم بالماء، وقد عمل بيلاطس البنطي اصلاحات كثيرة في هذه البرك ورممها.رواق سليمان:الرواق ممشى مسقوف، وكان رواق سليمان شرقي الهيكل ودار الأمم ( يوحنا 10: 23 وأعمال 3: 11 و 5: 12 ). وكان سقفه من خشب الأرز ويستند على صفين من الأعمدة الرخامية البيضاء المنحوتة على النظام الكورنثي.عبيد سليمان:يذكر سفر عزرا عودتهم من السبي إلى أورشليم لبناء الهيكل ( عزرا 2: 55 و 58 ونحميا 7: 57 و 60 ) ويرد ذكرهم دوماً بعد النثينيم مما يظهر أن لهم خدمة في الهيكل ( قارن عزرا 7: 24 ). والأغلب أنهم يخدمون اللاوين ولم يكونوا من أصل عبراني، ولكنهم دخلوا في قائمة خدم الهيكل بعد أن فرغوا من بنائه، وقد قدمّهم لذلك سليمان'),
(1841, 'سَمْجَر نبو', 'اسم اكادي ربما كان معناه (( تحنن يانبو )) ( ارميا 39: 3 ) أحد رجال نبوخذ نصر ملك بابل الذين دخلوا أورشليم وجلسوا في الباب الأوسط.'),
(1842, 'سِمعان', 'اسم عبراني معناه (( مستمع )) وهو:( 1 ) سمعان الشيخ ( لوقا 2: 25 ) رجل تقي سكن أورشليم، وأوحى إليه أنه سيعيش حتى يرى المسيح المتجسد. وقد انقاد بالروح إلى الهيكل عندما ذهب يوسف ومريم بالطفل يسوع إلى هناك، وأخذ الطفل بين ذراعيه وشكر الله، ثم طلب أن يطلقه بسلام. وتقول بعض التقاليد أنه ابن هلليل الربي اليهودي ووالد غمالائيل، ولكن لا ظل من الصحة لهذه التقاليد، كما انها بلا سند.( 2 ) سمعان بطرس اطلب (( بطرس )).( 3 ) سمعان القانوني ( متى 10: 4 ) وهو سمعا الغيور أحد الرسل الاثني عشر، وكان ينتمي إلى حزب الغيورين، والكلمة قانوني كلمة ارامية معناها غيور وقد أطلق عله لقب غيور للتفريق بينه وبين سمعان بطرس.( 4 ) سمعان الإسخريوطي, وهو والد يهوذا الإسخريوطي ( يوحنا 6: 71 ).( 5 )سمعان الأبرص من بيت عنيا, وقد سكبت مريم أخت لعازر الطيب على رأس المسيح في بيته, ويظهر أنه كان أبرص وأبرأه المسيح, ويظهر أيضاً أنه كان صديقاً للعازر ومريم ومرثا ( متى 26: 6 ويو 12: 1 - 7 ).( 6 ) سمعان الفريسي رجل فريسي غسلت امرأة خاطئة قدمي المسيح بالدموع ومسحتهما بشعر رأسها في بيته ( لوقا 7: 36 و 40 ).( 7 ) سمعان أخو الرب ( متى 13: 55 ومرقس 6: 3 ).( 8 ) سمعان القيرواني وكان من قريني ليديا ولذا يجب أن يكون لقبه القريني. وهو الرجل الذي أجبروه على حمل صليب المسيح عندما سقط تحته ( متى 27: 32 ) وهو أبو الكسندر وروفس المعروفين في الدوائر الكنسية في رومية ( مرقس 15: 21 ورومية 16: 13 ).( 9 ) سمعان نيجر ( أعمال  13: 1 ) أحد الأنبياء والمعلمين في كنيسة أنطاكية وقد استنتج بعضهم من لقبه أنه كان أسود اللون.( 10 ) سمعان الدباغ رجل أضاف بطرس الرسول في يافا ( أعمال 9: 43 ) وبيته عند البحر، وذلك بسبب ناموس الطهارة عند اليهود، أو لأسباب صحية.'),
(1843, 'سَمَك', 'خلق الله السمك وسلّط الإنسان عليه كما سلطه على الأشجار والحيوان ( تكوين 1: 26 و28 ومزمور 8: 8 ) غير أن الناموس ميز بين السمك الطاهر والسمك النجس، فالذي له زعانف وحراشف طاهر، والذي ليس له زعانف وحراشف كان نجساً ( لاويين 11: 9-12 ). وكان لسليمان الملك معرفة بأسماك فلسطين ( 1 ملوك 4: 33 ) غير أن الكتاب المقدس لا يحدثنا عن أنواع السمك، بل يذكر النوع عموماً. وتجارة السمك مريحة، وقد خسر المصريون كثيراً عندما ضرب الله ماء النيل فصار دماً، ومات فيه السمك، وقد اشتاق العبرانيون إلى سمك النيل وهم في طريقهم إلى كنعان ( عدد 11: 5 ) وقد تنبأ النبي أشعيا. عن نقص مياه مصر فيقل السمك وتسوء حالة صياديه ( أشعيا 19: 5-10 ).( 10 ) ويصاد السمك بالشباك ( متى 4: 18 ) وبالصنارة وبالحراب ( أيوب 41: 7 ) ويكثر السمك في مياه فلسطين ولا سيما في بحر الجليل, وهناك أكثر من ثلاثين نوعاً من السمك الصالح للطعام ( متى 13: 47 و 48 ) وقد كان أربعة من تلاميذ المسيح على الأقل يشتغلون بصيد السمك ( متى 4: 18 - 21 ) وكان الصيادون يأتون بالسمك للبيع في أورشليم ويدخلون به من باب السمك ( 2 أخبار 33: 14 ) ومن الملاحظ أن بيت صيدا تعني بيت الصيد, لأن أغلب أهلها اشتغلوا بصيد السمك.وقد كانت الآلهة (( أتارجتس )) التي كانت تعبد في أشقلون نصفها الأعلى على شكل إنسان ونصفها السفلي على شكل سمكة.وقد دعا المسيح تلاميذه من صيد السمك ليكونوا (( صيادي الناس )) ( مرقس 1: 17 ) كما أنه شبّه ملكوت السموات بشبكة تجمع مختلف أنواع السمك ( متى 13: 47 ) ويقول حزقيال على سبيل المجاز أن مياه المقدس التي وصلت إلى البحر الميت قد أصلحت مياهه, فعاش فيها السمك وبدأ الصيادون في صيده ( حزقيال 47: 1 - 10 ).وقد رمز المسيحيون الأولون بالسمكة إلى إيمانهم, فكانت علامة التعارف بينهم, والواقع أن حروف السمكة في اليونانية هي بدء كلمات الجملة (( يسوع المسيح ابن الله مخلص )).باب السمك:باب في أورشليم ويرجح أن صيادي السمك كانوا يدخلون منه ببضاعتهم لبيعها لأهل المدينة, ولعله كان بالقرب من بركة السمك ( 2 أخبار 33: 14 ) وربما كان في السور الذي كان على الجانب الشمالي للمدينة اطلب (( أورشليم )).'),
(1844, 'سَمَكْيَا', 'اسم عبراني معناه (( من عضده يهوه )) وهو اسم بواب لاوي من نسل عوبيد أدوم ( 1 أخبار 26: 7 ).'),
(1845, 'سَمْلَة', 'اسم عبراني معناه (( ثوب )) وهو اسم ملك من ملوك أدوم القدماء وقد ملك قبل أن يكون ملك في إسرائيل, وكانت عاصمة مملكته مسريقة ( تكوين 36: 36 و 1 أخبار: 47 ).'),
(1846, 'سَماء', 'الكلمة تشير إلى كل ما هو ليس أرضاً, فيقول التكوين أن الله خلق السموات والأرض, بمعنى أنه خلق النجوم والغيوم.وهناك السماء الهيولية والسماء الروحية, أما الهيولية فهي التي تظهر فوق رؤوسنا وتسمى القبة الزرقاء, وكان العبرانيون يقولون أنها الجلد ( تكوين 1: 14 ) ويقولون مجازاً أن بها كوى ومصاريع ينزل منها المطر والصقيع والثلج ( تك 7: 11 ومزمور 78: 23 ويعقوب 5: 18 وأيوب 38: 29 ). وقد سميت النجوم نجوم السماء وجند السماء وأنوار الجلد ( ناحوم 3: 16 وتثنية 4: 19 وتكوين 1: 14 ) وسوف يأتي اليوم الذي فيه تضمحل هذه السماء مع الأرض وتظهر بدلاً منهما أرض جديدة ( 2 بطرس 3: 10 ورؤيا 21: 1 ).أما السماءالروحية فهي مسكن الله الخاص, ولذلك يقال أن الله في السماء وأنه إله السماء, ومشيئته نافذة هناك ولذلك نصلي قائلين: (( لتكن مشيئتك كما في السماء كذلك على الأرض )) ويقال عن المسيح أنه الرب من السماء ( متى 5: 45 و 1 كورنثوس 15: 47 ) وقد نزل المسيح من السماء وصعد إليها وهو فيها ( يوحنا 3: 13 ) هناك تسكن الملائكة, ويسود الفرح والسلام. وقد هيأ المسيح فيها منازل كثيرة للمؤمنين به ( لوقا 19: 38 ويوحنا 14: 2 ) وقد صعد إيليا في عاصفة إليها ( 2 ملوك 2: 1 ) كما أن لكل مؤمن ميراثاً فيها وهو يكنز فيها كنوزه ( 1 بطرس 1: 4 ومتى 6: 20 ) والكلمتان الفردوس وحضن إبراهيم تشيران إلى نفس الشيء ( لوقا 23: 43 و 16: 22 ).أما الهاوية فهي ضد السماء, وقد هبط الشيطان من السماء إلى الهاوية ( لوقا 10: 18 و 2 بطرس 2: 4 ).\nوقد قال الربيون أن هناك سبع سموات، ولكن بولس الرسول يقول أنه صعد إلى السماء الثالثة في رؤياه ( 2 كورنثوس 12: 2 ) وهي ما يعبر بها عن سماء السموات، فالسماء الأولى سماء السحب والطيور، والسماء الثانية سماء الكواكب والنجوم، أما السماء الثالثة فهي مظهر المجد الإلهي ومسكن المسيح بالجسد والملائكة والقديسين. وحالتها فوق فهمنا وإدراكنا ( افسس 1: 3 ويوحنا 14: 2 و 3 عبرانيين 4: 11 ).'),
(1847, 'ملكوت السماء. وملكوت السماوات', 'اطلب (( ملكوت )).'),
(1848, 'سِميْرنَا', 'مدينة على الساحل الغربي لآسيا الصغرى، تأسست كمستعمرة يونانية عام ألف قبل الميلاد، ثم أصبحت مدينة عظيمة أمتد سلطانها شرقاً وغرباً وعظم نفوذها، ثم هزمها الياتيس ملك ليدية ودمرها عام 60 ق. م. فأعاد اليونانيون بناءها في القرن الثالث ق.م. وأقاموا فيها مجالس نيابية ومعاهد للتعليم، وفي عام 195 ق.م. بنت سميرنا هيكلاً وخصصته لمعبودات رومان فتوطدت أواصر الصداقة بينها وبين روما.وقد نقش أهل سميرنا على عملتهم القول: (( سميرنا أجمل وأكبر مدن آسيا )) ويقول المؤرخ استرابوا: (( إن جمال سميرنا يعزى إلى نظافة شوارعها وأناقة أرصفتها، وفخامة مبانيها، وروعة البحر الذي يحدق بها، وجلال الآكام التي تكتنفها، وجمال أشجار السرو والسنديان التي تظللها )).وقد كان أهل سيمرنا على أخلاق حسنة، غير أن السواد الأعظم منهم كانوا يتعبدون في هيكل باخوس إله الخمر، ثم تأسست فيها كنيسة مسيحية أمتدحها الرسول يوحنا في سفر الرؤيا ( رؤيا 2: 10 ) وكان بوليكاربوس تلميذ يوحنا الرسول أسقفاً لها، وقد مات فيها شهيداً في عام 155م. وقبره يقوم على تل فيها إلى اليوم وقد أرسلت كنيسة سيمرنا نائباً عنها إلى مجمع نيقية المسيحي الذي أجتمع عام 325م.وأزمير الحالية هي سيمرنا القديمة، وهي تقع على بعد خمسين ميلاً شمال افسس.'),
(1849, 'سَنَاءَة', 'اسم عبري ربما كان معناه (( مكروه )) يذكر عزرا ونحميا أن بني سناءة كونوا جماعة عادت بعد السبي مع زربابل إلى أرض كنعان ( عزرا 2: 35 ونحميا 7: 38 ) وقد قيل عنهم أنهم بنو هسناءة ( نحميا 3: 3 ) ولعلهم من سبط بنيامين ( 1 أخبار 9: 7 ). وربما كان مكانها اليوم محلة (( الشيخ تروني )) على مسافة سبعة أميال شمال أريحا.'),
(1850, 'سَنْبَلَّط', 'اسم اكادي وسن هو إله القمر وبلط معناها أعطى الحياة، فيكون معنى الاسم (( إله القمر أعطى الحياة )). وهو شخص عظيم النفوذ ( نحميا 2: 10 ) وكنيته الحوروني، وليس معنى ذلك أنه كان مواطناً من حورونايم في مآب،بل كان من بيت حورون. وقد قاوم بناء الهيكل وأسوار أورشليم بأن هيج العرب وغيرهم من جيش السامرة، ثم تآمر على قتل نحميا وحاول تلويث سمعته. وكان سنبلط معاصراً للكاهن العظيم الياشيب، وكان متفقاً معه ومع طوبيا العبد العموني ضد نحميا وجماعته، وذلك في السنة العشرين لحكم ارتكزركسيس ( نحميا 3: 1 و 4: 7 ) وقد جاء في أوراق البردي التي اكتشفت في الجزيرة في اسوان أنه كان حاكماً للسامرة قبل عام 407 ق.م. مباشرة، وذلك في السنة السابعة عشر لداريوس نوثوس. وقد زوّج سنبلط ابنته إلى منسى ابن يوياداع ابن الاشيب الكاهن العظيم، ولذلك طرد نحميا الياشيب بعد أن عزله ( نحميا 13: 28 ).'),
(1851, 'سِنْتِيخِي', 'اسم يوناني معناه (( سعيد الحظ )) وهو اسم سيدة مسيحية في كنيسة فيلبي، ذكر بولس اسمها واسم سيدة أخرى هي افودية، وكان بينهما خصام، فطلب منهما أن تتصالحا، كما طلب من شخص غير معروف لنا ولكنه شريكه المخلص أن يساعدهما على الصلحن وقد كان بولس يعرف كنيسة فيلبي جيداً، ولعل سنتيخي كانت شماسة هناك ( فيلبي 4: 2 ).'),
(1852, 'سَنْحاريب', 'اسم اكادي معناه (( الإله القمر زاد عدد الأخوة )). وهو ملك أشور ( 704-682 ق.م ) وقد اعتلى العرش بعد وفاة والده سرجون، وترك بابل التي فتحها أبوه، فعاد مردوخ بلادان إلى حكمها. وأراد مردوخ أن يثير الحرب على سنحاريب، فأرسل إلى حزقيا رسلاً يسألون عنه في مرضه ( 2 ملوك 20: 12-19 ) وشعر سنحاريب بذلك فقام بحرب على بابل هزم فيها مردوخ بلادان، وجعل على بابل ملكاً سواه. وبدأ سنحاريب يتجه نحو الغرب،وكان حزقيا قد بدأ يجهز دفاعه عن بلاده، فأرسل هدايا إلى مصر بالرغم من معارضة اشعياء ( اش 30: 1-4 ). وبنى بركاً وقناة لجلب الماء إلى أورشليم إذا حوصرت ( 2 ملوك 20: 20 ) وبدأ سنحاريب يزحف نحو أورشليم، فأرسل له حزقيا هدايا كي يسترضيه فرجع عنه ( 2 ملوك 18: 14 ). ثم ثارت بابل في السنة التالية على سنحاريب، فعول الحاكم الذي أقامه هناك، ومات مردوخ وأقام سنحاريب أبنه ملكاً على بابل.\nوعاد حزقيا وعصى سنحاريب فأرسل إليه سنحاريب رسائل يهزأ فيها بإلهه، فبسط حزقيا الرسائل أمام الله وصلى، فاستجاب الرب له وضرب جيش الآشوريين الذي كان يحاصر أورشليم، فمات منهم 185 ألفاً في ليلة واحدة، فرفع سنحاريب الحصار وعاد إلى عاصمته ( 2 ملوك 19: 35 و 36 ) وبعد ذلك بحوالي عشرين سنة كان سنحاريب ساجداً في بيت نسروخ إلهه، فضربه ابناه ادرملك وشرآصر بالسيف ( 2 ملوك 19: 37 ) فمات.\nوقد كان سنحاريب محارباً عظيماً، وقد ترك من أثار فتوحاته وغزواته الكثير. وقد كشف التنقيب عن نقوش كتبت في عصره وفيها يشير إلى أنه حاصر حزقيا كما يحاصر الطائر في قفصه ولكن يتضح من هذه النقوش أيضاً انه لم يأخذ أورشليم.'),
(1853, 'سِنْدِيان', 'يوجد في فلسطين تسعة أنواع مختلفة من أشجار السنديان، بعضها يحمل ثمراً يؤكل وبعضها للظل فقط، كما أن بعضها ينمو في الجبال العالية والبعض الآخر في الأودية والسهول المنخفضة. على أنه لا يمكن تحديد أي أنواع من السنديان هو الذي ورد ذكره في الكتاب، فليس من السهل تحقيق النوع المقصود. واسمه باللاتينية Quercus والبلوط والبطم نوعان من أشجار السنديان. وقد ورد ذكره في الكتاب المقدس في اش 41: 19 و 44: 14 و 60: 13ز اطلب (( بطم )) و (( بلوط )).'),
(1854, 'سنْسَنَة', 'اسم عبراني معناه (( سعف النخل ))، وهي اسم موضع في النقب ( يشوع 15: 31 ) وهي إحدى المدن التي وقعت في نصيب سبط يهوذا، ويدعى اسمها أيضاً حصر سوسة ( يشوع 19: 5 ) ومعناها (( دار الخيل ))، كما أنها تسمى أيضاً حصر سوسيم ( 1 أخبار 4: 31 ). ولعل موقعها اليوم خربة الشمسانيات التي تقع على بعد 10 أميال شمالي شرق بئر سبع.'),
(1855, 'سَنْط', 'نوع من الشجر كان ينمو في وادي الأردن من بحر الجليل إلى البحر الميت، كما كان ينمو بكثرة في سيناء. واسمه باللاتينية Acacia وقد عمل موسى التابوت وعصويه من خشب السنط، كما عمل أيضاً المائدة وألواح المسكن والعوارض، فإن خشبه يصلح لصنع الأثاث ( خروج 25: 5 و 10 و 13 و 23 و 26: 15 ). وكان المصريون يصنعون السفن منه وخشب الصنت ثقيل جداً وصلب يبقى أمداً طويلاً، ولون لبه أسمر مائل إلى الحمرة، وأغصانه ذات شوك، واوراقه ريشية مزدوجة، وأزهاره صغيرة مجتمعة في رؤوس.. ومن أغصان السنط يستخدمون الصمغ العربي وكثيراً ما يصنعون منه فحماً جيداً.وادي السَنْط:واد جاف وغير مثمر، لم يكن ينمو فيه سوى أشجار السنط فقط، ولعله وادي النار الذي ينحدر من أورشليم شرقاً في اتجاه البحر الميت. وقد تكلم النبي يوئيل عن وادي السنط ( يوئيل 3: 18 ) لأن مياهه غير حية، ولأن صخوره قاحلة، وهو يقول أنه في يوم الرب الذي يأتي فيه بالدينونة على الأمم البعيدة عن الله، سيفيض الله بالبركة على شعبه وأرضهم، حتى أن وادي السنط القاحل يروي ويثمر.'),
(1856, 'سَنْطير', 'آلة موسيقية من ذوات الأوتار ( دانيال 3: 5 ) وقد عملها الصيدونيون أولاً، وكان جسمها من الخشب أو المعدن، وكانت أوتارها تصنع من أمعاء بعض الحيوانات، وكان عددها من عشرة إلى أربعين وتراً.'),
(1857, 'سِنُّ', 'قال ناموس موسى (( عين بعين وسن بسن )) ( خروج 21: 24 ) وقد فهم اليهود من ذلك أن هذا القانون يبيح الأخذ بالثأر، ولكن الناموس أعطى القاضي وحده حق استعمال هذا القانون. غير أن المسيح عمل بناموس جديد هو ناموس المحبة والمغفرة وطول الأناة، فنادى بمحبة الأعداء ( متى 5: 38 و 39 ) وألغى بهذا القانون الجديد الناموس القديم (( عين بعين وسن بسن )).\nنظافة الأسنان:\nتعبير أورده النبي عاموس بمعنى الجوع الذي لا يترك للإنسان ما يأكله، فتبقى أسنانه نظيفة ( عاموس 4: 6 ). ويتكلم الكتاب أيضاً عن صرير الأسنان، بمعنى أن يصِرّ المرء بأسنانه  علامة الخوف والغضب واليأس والندم ( متى 8: 12 ) كما يتكلم الكتاب أيضاً عن ضَرَس أسنان الأبناء عندما يأكل الوالدون الحصرم ( حزقيال 18: 21 ). وذلك في حديث عن خطايا الآباء التي تترك تأثيرها السيئ في أولادهم، وهي تعني أيضاً أراض الوراثةْ.'),
(1858, 'سَنَه', 'اسم عبري معناه (( شوك ))، وهي صخرة حادة تقابل صخرة حادة أخرى اسمها بوصيص، كانت تعترضان الطريق من مخماس إلى جبع من ناحتيتي الشرق والغرب. وقد جاز يوناثان بين هاتين الصخرتين وهو في طرقه لمهاجمة الفلسطينيين ( 1 صموئيل 14: 4 و 5 ).'),
(1859, 'سَنهَدريم', 'مجلس اليهود الكبير في أيام حياة مخلصنا على الأرض، وقد أطلق المؤرخون هذا الاسم على هذا المجلس باعتباره المحكمة العليا للأمة اليهودية.وكان السنهدريم يمثل الشعب أمام الرومان، ويتكون من واحد وسبعين عضواً، سبعين منهم مثل عدد الشيوخ الذين. عاونوا موسى، والحادي والسبعون هو رئيس الكهنة. وقد قبض مجلس السنهدريم على المسيح وحاكمه ( مرقس 14: 43 ومتى 26: 59 ). وقد توقف عمل السنهدريم بعد عام 70 م وذلك بعد خراب أورشليم.'),
(1860, 'سُنونة', 'طائر صغير الحجم طويل الجناح نسبياً، وسريع الطيران حتى لتسمع صوت جناحيه يضربان الهواء ( أمثال 26: 2 ومزمور 84: 3 ). واسمه باللاتينية Hirundo وهو يبني عشه من الطين في مساكن الناس أو أبنية العبادة، وهو ستأنس بالبشر، ويرنم بموسيقى هادئة جميلة.\nوقد ورد ذكر السنونة في ترجمة الكتاب العربية المتداولة في اش 38: 14 وارميا 8: 7 ولكن جمهور العلماء يظنون أن الإشارة هنا إلى الكركي لا إلى السنونة.'),
(1861, 'سنة', 'قد كانت السنة عند العبرانيين شمسية تتكون من أثني عشر شهراً، ولكن شهورهم كانت قمرية، وكانوا يوفقون ذلك بإضافة أيام إلى التقويم، إذ يضيفون شهراً إلى كل سنة كبيسة، ولكن هذا غير مذكور في الكتاب المقدس.كانت السنة العبرية تبدأ بشهر ابيب ( ابريل-نيسان ) ( خروج 12: 2 و 23: 15 ) وكانت سنتهم الزراعية تبدأ في الخريف ( خروج 23: 16 و 34: 22 ولاويين 25: 4 و 9 ). وقد اعتبر اليهود الذين عادوا من السبي اليوم الأول من الشهر السابع عيداً لرأس السنة ( عزرا 3: 6 ونحميا 8: 2 ).وقد ذكر العبرانيون من الفصول الصيف والخريف والشتاء ( مزمور 74: 17 وارميا 36: 22 وزكريا 14: 8 وعاموس 3: 15 ).\nأما أعيادهم فقد كانت تتبع المواسم الزراعية ( اطلب (( عيد )) ).'),
(1862, 'سنة اليوبيل', 'اطلب (( يوبيل )).'),
(1863, 'سنة السبت', 'كانت السنة السابعة تُسمى سنة السبت ( لاوين 25: 2 ) كما كانت تُسمى أيضاً سنة الإبراء ( تثنية 31: 10 ). وكانت سنة السبت  سنة راحة لا يزرعون فيها، ويتركون محاصيل الأرض الذاتية للفقير والغريب والوحوش أيضاً ( لاوين 25: 1-7 ). وقد ارتبط اعتبارهم لهذه السنة باعتبارهم ليوم السبت ( خروج 23: 10-12 ). \nوقد كانت هناك ثلاثة أهداف لهذه السنة: هدف اجتماعي واقتصادي وديني. أما الاجتماعي فقد كان الاهتمام بالفقير والمسكين، والاقتصادي فقد كان إراحة الأرض حتى تجود بثمر أكثر، أما الديني فهو الهدف الأكبر، وهو إشعار الشعب كله أن الأرض للرب، وأن مصدر رزقهم من عنده، كما أنهم هم أيضاً له، ويجب أن يضعوا ثقتهم فيه وهو يعتني بهم.\nوقد كان بدء سنة السبت يوافق يوم الكفارة في الشهر السابع، وكان يليه عيد المظال الذي كان يستمر خمسة عشر يوماً، فكان الناموس يُقرأ أمام الشعب كله ( تثنية 31: 10-13 ). وكان الشعب يقضي السنة في التعرّف على كلمات الناموس، كما كان مسموحاً لهم أن يصيدوا وأن يربوا النحل وأن يرعوا المواشي ويصلحوا الأبنية ويمارسوا التجارة.'),
(1864, 'سَنوبر أو صَنوْبر', 'شجر دائم الخضرة يبلغ ارتفاعه أربعون قدماً، وخشبه شديد الصلد، واسمه باللاتينية Pinus، وقد ورد ذكره مرة واحدة في الكتاب المقدس ( اش 44: 14 ). وتوجد منه أنواع كثيرة، أشهرها السنوبر الحلبي والسنوبر السنوبري.'),
(1865, 'سَنِير', 'اسم اموري ربما كان معناه (( جبل السنا أو النور )). وهو الاسم الذي أطلقه الاموريون على جبل حرمون ( تثنية 3: 9 ) ولكن في 1 أخبار 5: 23 نجد أن سنير وحرمون مذكوران كجبلين مختلفين، والأغلب أن سنير اسم قسم من جبل حرمون ( جبل الشيخ ).'),
(1866, 'سَوَا', 'ملك مصر الذي أرسل إليه هوشع ملك إسرائيل يطلب عونه ضد شلمنأصر ملك أشور بعد أن رفض دفع الجزية له ( 2 ملوك 17: 3 و 4 ). ولكن سوا لم ينفع هوشع بشيء. وجاء شلمنأصر وغلب السامرة وأخذها بعد حصار دام ثلاث سنوات. وقد قام الملك سوا بعد ذلك بحرب ضد أشور فهزمه سرجون الذي خلف شلمنأصر على العرش، وكان ذلك في موقعة رفح عام 720 ق.م. ويُرجح أن فرعون سوا هو سبئة الذي كان قائداً في جيش مصر.'),
(1867, 'سوباترس', 'اسم يوناني معناه (( صالح الابوين ))، وهو رجل من بيرية، كان رفيقاً لبولس الرسول في سفره من اليونان إلى آسيا وذلك في طريق عودة الرسول من رحلته التبشيرية الثالثة ( أعمال 20: 4 ).'),
(1868, 'سُوح', 'اسم عبراني معناه (( كناسات )) وهو ابن صوفج من سبط اشير ( 1 أخبار 7: 36 ).'),
(1869, 'ساحة', 'ساحة المدينة مكان واسع داخل أسوار المدينة العبرية القديمة، وقد تكون الساحة عند الباب حيث يجلس شيوخ المدينة، أو قد تكون في منتصف المدينة. وكانت الساحة متروكة لاجتماع الناس ولبيع الحاجيات والمأكولات، كما كان الغرباء يبيتون فيها، وكانت الساحة مجلس عظماء المدينة ( تكوين 19: 2 ونحميا 8: 1 وأيوب 29: 7 ).'),
(1870, 'سوخار', 'اسم بلدة ورد ذكرها مرة واحدة في الكتاب المقدس في إنجيل يوحنا 4: 5 عند زيارة المسيح لبئر يعقوب، فقد كان مجتازاً السامرة في طريقه من اليهودية إلى الجليل، قرب الضيعة التي وهبها يعقوب ليوسف ابنه.وسوخار قرية غير مهمة، ورد ذكر اسمها لأنها قرية من بئر يعقوب. ويرجح أنها قرية (( عسكر )) على بعد نصف مل شمالي بئر يعقوب.'),
(1871, 'سيدي', 'لقب معلم الدين العبراني قديماً، وهي تقابل معنى حاخام  ( متى 26: 25 ومرقس 9: 5 ). وكان اليهود يميّزون بين رب بمعنى سيد، وبين ربي بمعنى سيدي، وبين زبوني بمعنى سيدي الكبير ( يوحنا 20: 16 ). وقد كان الفريسيون يحبون أن يحييهم الناس وينادوهم بسيدي، لأنهم متكبرون يحبون المدح من الناس ( متى 23: 7 ).'),
(1872, 'سيدة', 'السيدة هي المرأة التي لديها خادمات وإماء ( مزمور 123: 2 ). وقد لقبت مملكة بابل بسيدة الممالك لكثرة مستعمراتها ( اشعياء 47: 5 و 7 ).'),
(1873, 'سُودِي', 'اسم عبري معناه (( صاحب السر ))، وهو أبو جديئيل الجاسوس الذي ذهب من سبط زبولون ليتجسس في أرض كنعان ( عدد 13: 10 ).'),
(1874, 'سِوار-أساور-أسورة', 'وهذه حلية للزينة كانت تُلبس على الساعد أو أعلى المرفق، وكان يلبسها الرجال والنساء على السواء ( حزقيال 16: 11 ) فقد كان الملك شاول لابساً سواراً على ذراعه ( 2 صموئيل 1: 10 ) كما ألبس اليعازار الدمشقي عبد إبراهيم رفقة خطيبة اسحق سوارين على يديها ( تكوين 24: 22 ) وقد أهدى العبرانيون أسورة من ذهب كهدية منهم وتبرع لبناء خيمة الاجتماع ( عدد 31: 50 ) وكانت الاسورة تُصنع على هيئة سلاسل أو أشرطة أو صفائح.'),
(1875, 'سُورق', 'اسم عبراني معناه (( كرم مُختار )). وقد أطلق هذا الاسم على الوادي الذي سكنت فيه دليلة صديقة شمشون ( قضاة 16: 4 ). واسمه اليوم وادي الصّرار الذي يبدأ على بعد ثلاثة عشر ميلاً غرب أورشليم ويمتد إلى البحر الأبيض المتوسط.\nويشقّه نبع يصب في البحر على بعد ثمانية أميال ونصف جنوب يافا. وهناك وادٍ به خرائب شمال وادي الصرار اسمه (( خربة سوريق ))، على بعد ميلين من صرعة مكان ميلاد شمشون.'),
(1876, 'سورية', '( متى 4: 24 ولوقا 2: 2 ) اسم البلاد التي تمتد على شاطئ البحر الأبيض المتوسط وإلى الداخل، وهي التي أطلق عليها العبرانيون اسم آرام، ويقول البعض أن الاسم سورية هو اختصار لكلمة اشور. وقد جرى استعمال هذا الاسم المختصر بعد أن غزا الاسكندر الأكبر هذه البلاد.وتتكون سورية من مرتفعات وسهول تمتد من الشمال إلى الجنوب بحذاء شاطئ البحر الأبيض المتوسط، وتبدأ من الغرب بسهل خصيب يزيد ارتفاعه إلى الشرق حتى يبلغ 500 قدم، ويبلغ عرضه في بعض الأماكن ثمانية أميال، وتقع فيه مدن اللاذقية وطرابلس وبيروت. ويتلو هذا السهل جبال أعلاها جبلا كاسيوس الذي يبلغ ارتفاعه 5750 قدماً، وجبل صنين الذي يبلغ ارتفاعه 8780 قدماً، وتقع في جنوب هذه الجبال برية التيه. ثم تتلو سلسلة الجبال هذه جبال أخرى وهي تواجه لبنان وتمتد للجنوب إلى جبل حرمون، وتقع في جنوبها جلعاد وموآب وآدوم، وفي هذه السلسة جبال الدروز. ثم تلو هذه الجبال إلى الشرق أرض ضيقة مزروعة يجري فيها نهر ابانة ( بردى ) وهو مصدر حياة مدينة دمشق التي تقع في هذا الجزء، ثم يلي هذا بعض الأنهار والينابيع التي تتحكم الجبال في سيرها وكمية مياهها.وتعتبر الأجزاء الغربية أغنى من الشرقية، ويُزرع فيها النخل وقصب السكر والقمح والذرة والعدس، كما توجد بها مراعي متسعة، لكن جبال الأجزاء الشرقية غنية بالمعادن لأن فيها الفحم والقار والكبريت، غير أن المنطقة كلها كانت طريقاً للتجارة الناجحة.وقد سكن الحثيون في هذه البلاد أولاً وغيرهم من نسل حام، وفي سنة 2750 ق.م. بدأت العلاقة بين سورية وبابل حتى صارت تحت حكم نارام سن البابلي، وقد جعل الملك حمورابي اللغة البابلية لغة المراسلات الدبلوماسية. ثم وقعت سورية تحت حكم مصر عندما فتحها تحتمس الأول عام 1600 ق.م. ثم تلاه تحتمس الثالث الذي سجّل انتصاراته على حوائط معبد الكرنك بالأقصر، وظلت تحت حكم امنحتب الرابع وسيتي الأول، ولكن نفوذ مصر عليها تقلص حتى إعادة رعمسيس الثاني إلى ما كان عليه وكانت سورية منقسمة بين ملوك كثيرين مثل ملوك دمشق ورحوب وصوبة وجشور ( 1 ملوك 10: 29 و 2 ملوك 7: 6 ) وقد فتح يشوع بعض نواحي لبنان وجبل الشيخ ( يشوع 11: 2-18 ) كما استعمر داود دمشق وأخضعها له ( 2 صموئيل 8: 3-13 ) وبقيت تحت حكم سليمان حتى استقلت قرب نهاية حكمه ( 1 ملوك 4: 21 و 11: 23-25 ). وقد كان ملوك دمشق أعداء للعبرانيين وكثيراً ما حاربوهم ( 1 ملوك 15: 18-20 و 2 ملوك 6: 8-33 ).وعندما حاول ملك سورية وملك بني إسرائيل أن يفتحا يهوذا استعان آحاز بملك أشور، وبعد نهاية الحرب انضمت مملكة سورية إلى مملكة أشور، ثم صارت جزءاً من مملكة الاسكندر الأكبر وذلك عام 333 ق.م، ثم وقعت في سلوقس الذي إنطاكية عاصمة له، وقد خلفه انطيوخس ابيفانيس الذي ظلم اليهود وقدم خنزيراً على مذبح هيكل أورشليم لتنجيسه... ثم سقطت سورية تحت الحكم الروماني عام 64 ق.م. واستمرت إنطاكية عاصمة لها.وقد دخلت المسيحية إلى سورية عن طريق مناداة بولس الرسول ( أعمال 15: 23 و 41 و 18: 18 وغلاطية 1: 21 ).'),
(1877, 'سُوستانيس', 'اسم يوناني معناه (( سليم القوة ))، وهي اسم رئيس مجمع اليهود في كورنثوس أثناء زيارة بولس الرسول للمدينة، وقد ضربه بعض اليونانيين ضرباً شديداً أمام الوالي الروماني غاليون دون أن يمد الوالي يداً لينقذه، وذلك لأن سوستانيس واليهود كانوا قد اضطهدوا بولس الرسول ( أعمال 18: 17 ) والأغلب أن سوستانيس صار مسيحياً بعد ذلك، فإننا نقرأ هذا الاسم في فاتحة هذا الاسم في فاتحة الرسالة إلى الكورنثيين حيث ذُكر كمن يرسل سلامه مع بولس الرسل إلى الكورنثيين، ويعتبره بولس الرسول أخاً له ( 1 كورنثوس 1: 1 ).'),
(1878, 'سَوْسَن', 'نبات ينمو في المراعي حيث تأكل وترعى الغزلان والأغنام ( نشيد 2: 16 و 4: 5 و 6: 3 ) كما ينمو بين الأشواك ( نشيد 2: 2 ) كذلك كانوا يزرعونه في الحدائق الخاصة ( نشيد 6: 2 ) لكنه كان ينمو في الأودية بكثرة حتى تسمّى بسوسنة الأودية ( نشيد 2: 1 ) وكان ذا رائحة طيبة ويقطر مراً مائعاً ( نشيد 5: 13 ) حتى كانت رائحته تعطّر الجو المحيط به. وكانت الأمثال تضرب بجمال السوسن ( هوشع 14: 5 ) وكانوا ينحتون هيئة السوسن على رؤوس العواميد ( 1 ملوك 7: 19 و 2 أخبار 4: 5 ) ووجدت عدة أنواع من السوسن في فلسطين, ولا ندري أي نوع منها قصده الكتاب في كل مرة ورد فيها ذكر السوسن والأغلب أن زهرة اللوتس المصرية هي نوع من السوسن.'),
(1879, 'سَوْسّنَة', 'مفرد السوسن ( نشيد 2: 1 ) اطلب (( السوسن )).'),
(1882, 'سوسي', 'اسم عبري معناه ((فارس))وهو ابو جدي الجسوس مثل سبط منسى (عدد13: 11).'),
(1883, 'سوسيباترس', 'اسم يوناني معناه ((خلاص أب)) وهو اسم شخص ارسل سلامه مع لوكيوس وياسون الى مسيحيي روما (رومية 16: 21) وهو نسيب بولس بمعنى انه يهودي مثله (قارن رومية 9: 3 و 16: 10) ولعله هو نفسه سوباترس الذي صاحب بولس في رحلته التبشيرية الثالثة، وكان معه في كورنتوس وقت كتابة الرسالة الى رومية (اعمال 30: 4)'),
(1884, 'سوسيم', 'اخبار 4: 31 (اطلب \"حصر سوسيم\").'),
(1885, 'سوط_سياط', 'كان الضرب بالسياط واسطة من وسائط التعذيب والعقاب، وقد سمح الناموس الضرب بالسياط على الا يزيد ذلك على اربعين جلدة، فكان المحكوم عليه بالجلد بالسياط يحني جسده الى الأمام وتربط يداه الى عمود ثم يضرب ظهره العاري (تثنية 25: 2و3) وقد تكلم الملك رحبعام عن الضرب بالسياط كعقوبة معروفة (1 ملوك 12: 11و14)وقد استخدمه انطيوخس ابيفانيس ليضطر اليهود الى اكل لحم الخنزير وقد استخدم اليهود المتأخرون الضرب بالسياط كعقوبة على الذنوب التي لم ترد عقوبتها في التوراة، كما عاقبوا بها من خالف حكم السنهدريم والهرطوقيين وكان الجلد يوقع بأمر المجمع(متى 10: 17) كما كان يوقع بأمر السنهدريم (اعمال 5: 40)\nاما الرومانيون فقد كانوا يجلدون المتهمين ليستخلصوا منهم الأعترافات، ولكن القانون المتأخر اعفى الروماني من الجلد بالسياط وسمح به لغير الرومانين فقط، لأنه كان عقابا مذلا وكان المحكوم عليه بالصلب يجلد حتى كان كثيرون يموتون قبل ان يصلبوا! وقد ضرب فادينا بالسياط حتى تمزق جسده الطاهر، وبحبره (وجلداته) شفينا (اشعيا. 53: 5) وقد ضرب مسيحيو سميرنا بالسياط حتى تمزقت اجسادهم وظهرت اجزاء اجسادهم الداخلية، فماتوا شهدا.\nوكان السوط يدا من الخشب لها سيور من الجلد وتوضع بها قطع من الحديد او المعدن لتمزيق الجسد المضروب، وكثيرا ما استخدمت العصي في الجلد (2 كورنتوس 11: 25)'),
(1886, 'سوطاي', 'اسم عبراني ربما كان معناه ((معرج)) رجل عاد احد خلفائه الى اليهودية مع زربابل، وهو احد عبيد سليمان (عزرا 2: 55 ونحميا 7: 57)'),
(1887, 'ساعة', 'قسم من الوقت معروف الآن بجزء من اربعة وعشرين جزءاً من النهار، وقد قسم العبرانيون النهار الى اقسام هي: الصباح والظهر والمساء( مزمور 55: 17 ودانيال 6: 10) وقد تحدثوا عن هذه الأقسام بوصفها الشروق وحر النهار والغروب. وقد بدأ استعمال الساعات بعد السبي، فانقسم اليوم من شروق الشمس حتى غروبها الى اثنتي عشرة ساعة (مت 20: 1 _12 ويوحنا 11: 9) وكانت الساعة السادسة ساعة الظهر ( يوحنا 4: 6 وأعمال 10: 9 ). وكانت الساعة التاسعة ساعة الصلاة ( أعمال 3: 1 ).أما الليل فقد قسموه إلى ثلاثة أقسام هي الهزيع الأول من غروب الشمس حتى منتصف الليل, والهزيع الثاني من منصف الليل إلى صياح الديك, والهزيع الثالث من صياح الديك إلى الشروق ( خروج 14: 24 وقضاة 7: 19 ومراثي 2: 19 ) ثم قسم اليونان والرومان الليل إلى أربعة أقسام, وهذا ما يظهر في العهد الجديد ( مرقس 6: 48 ولوقا 12: 38 ) ثم انقسم الليل إلى اثنتي عشر ساعة ( قارن أعمال 23: 23 ).وأول استعمال لكلمة ساعة في الكتاب المقدس جاء في أيوب 15: 21 ثم دانيال 3: 6, إلا أنها لا تشير إلى مدة معينة, وقد اختلفت أطوال الساعات باختلاف طول الليل والنهار,وذلك حسب شروق الشمس وغروبها. أما المصريون فقد حسبوا للنهار إثنتي عشرة ساعة, ومثلها لليل.'),
(1888, 'سُوف', 'اسم عبراني معناه (( قصب الغاب )) وهو اسم بلدة في عبر الأردن ( تثنية 1: 1 ). وربما كان مكانها اليوم الموضع المسمى (( خربة سوفة )) على بعد ثلاثة أميال وثلاثة أرباع الميل جنوبي مادبا ( انظر (( بحر سوف )) ).'),
(1889, 'سُوفَة', 'اسم عبراني معناه (( ريح عاصفة )) وهو اسم مقاطعة في موآب ( عدد 21: 14 ).'),
(1890, 'سُوفَرث', 'كلمة عبرانية معناها (( كاتب )), وهي اسم رجل عاد وهو وبعض من نسله إلى أورشليم مع زربابل ( نحميا 7: 57 ). ويسمى أيضاً هسوفرث ( عزرا 2: 55 ) والهاء في ابتاء الاسم هي أداة التعريف في العبرانية, ويظن البعض أن هذا الاسم هو اسم وظيفة لبعض عبيد سليمان - هي وظيفة الكاتب.'),
(1891, 'سُوق', 'هو المكان الذي يجتمع فيه الناس للبيع والشراء أو لغير ذلك, وقد وردت كلمتان عبريتان بهذا المعنى في الكتاب المقدس, أولهما ترجمت سوق ( حزقيال 27: 13 و 17 و 19 ) وترجمت الأخرى (( متجرة )) ( أشعياء 23: 3 ) كما وردت كلمة يونانية بهذا المعنى معناها مكان اجتماع الناس. وقد تكون الأسواق شوارع طويلة أو قصيرة على جانبيها متاجر, وقد تكون مسقوفة أو غير مسقوفة, كما قد تكون ساحات فسيحة بجانب باب المدينة أو في وسطها يجتمع فيها الناس ( مرقس 6: 56 ). وقد استعملت ساحة الأسواق كملعب للأولاد يرقصون فيه ويلعبون ( متى 11: 16 و 17 ) كما كانت مكاناً يجتمع فيه العاطلون من الفعلة في انتظار من يستخدمهم, فيساومون في مقدار أجرهم ( متى 20: 1 - 16 ). وكانت الأسواق أيضاً مكاناً يعرض فيه المتكبرون من رجال الدين اليهودي أنفسهم فيحييهم الناس باحترام وهم يجرون أذيال أثوابهم الطويلة, فحذر المسيح تلاميذه من هذا ( متى 23: 3 - 7 ). وقد استخدم بعض الناس الأسواق كمكان لاجتماع المرضى طلباً للشفاء, فوجد المسيح في هذا فرصة لإجراء معجزاته ( مرقس 6: 56 ) وكثيراً ما أجريت المخاكمات في الأسواق كمحاكمة بولس وسيلا في فيليبي ( أعمال 16: 19 ). وقد استخدم الرسل الأسواق كمكان للوعظ والتعليم ( أعمال 17: 17 ) كما استخدمها الفلاسفة لنشر آرائهم وفلسفاتهم.'),
(1892, 'سوكاتيم', 'عائلة من الكتبة كانوا يسكنون مكاناً غير معروف لنا اليوم اسمه يعبيص ( 1 أخبار 2: 55 ).'),
(1893, 'سُوكو - سُوكوه', 'كلمة عبرانية معناها (( أشواق )) وهي اسم مكانين في يهوذا أولهما ورد ذكره مع عدلام وعزيقة ( يشوع 15: 35 ) وقد جمع الفلسطينيون جيوشهم للحرب فيه ( 1 صموئيل 17: 1 ) كما كانت بلداً من التي تورد لسليمان حاجياته فيختار منها ما يطلب ( 1 ملوك 4: 10 ). ويدعى هذا المكان اليوم خربة الشويكة حيث ينحني وادي الشور إلى الغرب ويصير اسمه وادي السنط, على بعد تسعة أميال من بيت جبرين.\nأما المكان الثاني فقد ورد اسمه مع شامير ويتير ( يشوع 15: 48 ) ويقع على بعد عشرة أميال جنوب غرب حبرون واسمه اليوم خربة الشويكة أيضاً.'),
(1894, 'سِيئون', 'كلمة عبرانية معناها (( قمة )) وهو اسم أطلق على جبل حرمون الذي هو جبل الشيخ ( تثنية 4: 48 ) وربما أطلق هذا الاسم على قمة عالية فقط من الجبل مغطاة بالثلوج في الجنوب.'),
(1895, 'سياج - سياجات', 'هو السور الذي يحيط بالحقل أو الحديقة لمنع الناس من الدخول والدوس فيها ( ميخا 7: 4 ). وكانوا يقيمونه من حجارة مرصوصة بغير طين, كما كانوا يقيمونه من أغصان الأشجار الشائكة ( أمثال 15: 19 ) وكثيراً ما يزرع الصبير أو العوسج أو نباتات أخرى متشابكة الفروع بجوار السياج. وكان بعض عابري السبيل يلجأون إلى ظل السياجات طلباً للوقاية من حرارة الشمس ( لوقا 14: 23 ).'),
(1896, 'سِيحون', 'اسم أموري لا يعرف معناه وهو اسم ملك الأموريين حاول أن يمنع العبرانيين من المرور في أرضه وهم في طريقهم إلى أرض كنعان, بلا جدوى, فانهزم جيشه وانقسمت بلاده بين أسباط العبرانيين ( عدد 21: 21 - 29 ) وقد ورد سفر المزامير اسمه كتحذير لمن يقاوم الرب أو شعب الرب ( مزمور 135: 10 - 12 و 136: 18 و 19 ).'),
(1897, 'سِيخو', 'كلمة عبرانية معناها (( التل أو المرتفع )) وهي اسم مكان بين جبعة والرامة، أي بين مسكن صموئيل وشاول، ولم يرد ذكره إلا في 1 صموئيل 19: 22. وترجع شهرة المكان إلى بئر عظيمة أو خزان ماء كان هناك ويعتقد أنها ربما كانت شويكة التي تقع على بعد ثلاثة أميال شمالي الرام.'),
(1898, 'سير-سيور الحذاء', 'اطلب (( ثوب )).'),
(1899, 'سِيرة', 'سيرة الإنسان هي تصرفه وطريقة معاملته للناس، فيتحدث بولس عن طريقة معاملته للكنيسة قبل إيمانه على أنها سيرته ( غلاطية 1: 13 ) كما يطلب الرسول بطرس قداسة السيرة ( 1 بطرس 1: 15 ).\nأما كلمة سيرة الواردة في فيلبي 3: 20 فهي في اليونانية المملكة أو الدولة التي نحن رعيتها. والمقصود أن المؤمنين يهتمون بالسماويات لأن وطنهم السماء (( أورشليم العليا هي امنا )) ( غلاطية 4: 26 ) وهم رعايا السماء الذين يتمتعون بكل امتيازاتها.'),
(1900, 'السيرة', 'بئر السيرة هو المكان الذي جاءوا منه بانبير إلى يوآب ليقتله ( 2 صموئيل 3: 26 ) وقد يكون هو المكان المعروف اليوم بعين سارة وهو نبع على الجانب الغربي من الطريق بين حبرون وأورشليم، وعلى بعد ميل من حبرون. ويقول آخرون أنه المكان المعروف اليوم بحمَّام سارة قرب رامة الخليل.'),
(1901, 'السِّيرِتس', 'شاطئ رملي متقلب يتكون فيه تيار من الماء يدفع السفن بعيداً عن خط سيرها ( أعمال 27: 17 ) وكان هناك السيرتس الكبير والسيرتس الصغير، أما الكبير فكان شمال ليبيا بالقرب من مدينة طرابلس. وكان الصغير إلى الشرق من تونس.'),
(1902, 'سِيسرا', '( 1 ) قائد جيش يابين ملك حاصور، وقد جاءت قصته في الإصحاح الرابع من سفر القضاة نثراً، كما جاءت في الإصحاح الخامس شعراً. كان سيسرا يسكن على شاطئ نهر قيشون الشرقي مسيطراً على الطريق من السهل إلى البحر، فأذاق العبرانيين مرارة الذل. وقد شجعت دبورة باراق على محاربة سيسرا، فجرت الموقعة عند سفح جبل تابور، فانهزم سيسرا، وهرب على قدميه إلى الشمال الشرقي، إلى خيام حابر القيني، فلاقته زوجته باعيل وقتلته بأن ضربت وتد الخيمة في صدغه بعد نومه.( 2 ) اسم عائلة من النثيينيم عادت بعد السبي، ( عزرا 2: 53 ونحميا 7: 55 ).'),
(1903, 'سِيعا-سيعها', 'أب عائلة عادت مع عزرا إلى أورشليم ( نحميا 7: 47 ) ويدعى أيضاً سيعها ( عزرا 2: 44 ).'),
(1904, 'سيف', 'اطلب (( سلاح )).'),
(1905, 'سَيَّاف', 'كانت وظيفة السياف في أيام العهد القديم وظيفة شريفة. وكان السياف الذي أتى برأس يوحنا المعمدان واحداً من بطانة الملك ( مرقس 6: 27 ).'),
(1906, 'سِيلا', 'اسم يوناني مأخوذ عن الأصل الارامي لفظة (( شئيلا )) أو (( شاول )) ومعناه (( المسئول )) وقد خدم مع بولس الرسول ( 2 كورنثوس 1: 19 ) وكان احد أعضاء كنيسة أورشليم البارزين وكان مواطناً رومانياً ( أعمال 16: 37 ) ولذا فقد دعي أحياناً باسمه اللاتيني (( سلوانس )) ورافق بولس في جزء من رحلته التبشيرية الثانية ( أعمال 15: 18-22 ) وبدأ ذلك عندما اتخذت الكنيسة الأولى قرارها بإعفاء الأمم من الختان، فأوكلت إلى سيلا أن يذهب مع يهوذا الملقب بارسابا مرافقين بولس وبرنايا لتبليغ كنائس إنطاكية وسورية وكليكية بذلك. ولأن سيلا كان رجلاً متقدماً في الأخوة اختاروه ليذهب ليثبت شهادة بولس شفاهاً، ويقول الكتاب أن سيلا ذهب ووعظ الأخوة بكلام كثير وقواهم لأنه كان نبياً ( أعمال 15: 22-33 ).\nوقد رافق سيلا بولس بعد انفصال برنابا عنه ( أعمال 15: 40 ) فزار سورية وكيلكية وغلاطية وفريجية وميسية ومعهما تيموثاوس ( أعمال 15: 41 إلى 16: 8 ). وعندما ظهرت لهما رؤيا الرجل المقدوني الذي يطلب العون ذهبا إلى بلاد اليونان مارين بساموثراكي وروسيا في نيابوليس ( أعمال 16: 9-11 ) وذهبا إلى فيلبي للكرازة هناك ( أعمال 16: 12-39 ) ومنها تسالونيكي ثم إلى بيرية ( أعمال 16: 40-17: 10 ) ثم لحق سيلا ببولس في أثينا ( أعمال 17: 11-15 ). ولعلا سيلا مضى من أثينا إلى فيلبي ( فيلبي 4: 15 ) ثم عاد والتقى ببولس في كورنثوس ( 2 كورنثوس 1: 19 و 11: 9 ) حيث وعظ فيها... ويقول بطرس عن سيلا أنه أخ أمين ( 1 بطرس 5: 12 ).'),
(1907, 'سِيمون', 'اسم عبراني معناه (( السامع )) وفي الأصل لفظه نفس لفظ الاسم (( سمعان )) وردت قصة سيمون في الإصحاح الثامن من سفر الأعمال ( 9-24 ) وكان يدهش شعب السامرة بسحره، فكانوا يقولون أن سحره شيء عظيم، واعتقدوا أن قوة الله العظيمة حلت فيه! وجاء فيلبس المبشر والشماس يكرز بالإنجيل في السامرة ورأى سيمون المعجزات التي تجري على يد فيلبس، فأيقن أنها تجري بقوة أعظم من سحره، فآمن واعتمد ولازم فيلبس مندهشاً من المعجزات التي يجريها. ويبدو أن إيمانه لم ينشأ عن توبة إنما عن ثقة في قوة سحرية أقوى من قوة سحره. وسمع بطرس ويوحنا عن عمل الله في السامرة، فنزلا إليها. وأجرى الرب بهما معجزات أخرى شبيهة بتلك التي حدثت يوم الخمسين ( أعمال 2 ) فأندهش سيمون أكثر، وأسرع طالباً معرفة تلك القوة السحرية العظيمة مقدماً المال ثمناً لذلك، فوبخه بطرس بشدة وطلب منه أن يتوب. وقد عرفت الكنيسة شناعة هذه الخطيئة فأطلقت اسم السيمونية على كل من يتاجر في الوظائف الكنسية.وقد واجه الكارزون الأولون بالمسيحية مقاومة من السحرة، مثلما جرى مع عليم الساحر الذي قاوم بولس الرسول ( أعمال 13: 6 و 7 ). ولكن قوة معجزات التلاميذ هزمت السحرة الكاذبين.وقد لسيمون أتباع اسمهم السيمونيون اعتبروا سيمون مسيحهم وفاديهم. وهم شيعة صغيرة من شيع الغنوسيين، يقول اوريجانوس عنهم أنهم ليسوا مسيحيين لأنهم يعتبرون سيمون مظهر قوة الله. ويقول ايريناوس أن سيمون هذا هو أبو العنوسيين، ولكن أصل ومصدر الهرطقة الغنوسية غير معروف تماماً. ولعل الصواب جانب الآباء المسيحيين الأولين الذين ربطوا بين سيمون الساحر ( أعمال 8 ) مع فكرة الغنوسية.'),
(1908, 'سِين', '( 1 ) مدينة مصرية ورد ذكرها في حزقيال 30: 15 و 16 أطلق اليونانيون عليها اسم بلوسيون وقد جرت عندها مواقع كثيرة، لذلك يسميها حزقيال (( حصن مصر )). وموقعها الآن تل الفرماء الذي يقع على بعد مسافة 20 ميلاً شمال شرقي القنطرة.( 2 ) برية سين ( عدد 33: 11 ) أول برية سيناء، وصل إليها العبرانيون بعد أن عبروا البحر الأحمر من ايليم ( خروج 16: 1 ) إلى رفيديم. وفيها انزل الله المنَّ للمرة الأولى للشعب. ولعل مكانها اليوم دبة الرملة وهي كومة رمال عند سفح جبل التيه.'),
(1909, 'سِيناء', 'اسم جبل يطلق عليه أيضاً جبل حوريب واسم البرية المحيطة بهز ويذكر الكتاب المقدس برية سيناء وجبل سيناء 35 مرة، وفي 17 مرة تسمى حوريب وقد قضى العبرانيون عند هذا الجبل سنة في طريقهم من مارَّة وايليم والبحر الأحمر، من مايو إلى ابريل، ووصلوا إليه بعد قيامهم من مصر بثلاثة أشهر ( خروج 19: 1 ). وكان يبعد عن قادش برنيع مسيرة إحدى عشر يوماً عن طريق جبل ساعير ( تثنية 1: 2 ) وعلى مسيرة ثلاثة أيام من مصر ( خروج 5: 3 ). وكانت تحيط بهذا الجبل برية كافية لأن يعسكر فيها العبرانيون كلهم لمدة سنة ( خروج 19: 2 ) وكانت البرية قريبة من الجبل حتى يمكن مسّه ( خروج 19: 12 ) وقد أعطى الله الشعب الوصايا العشر من على هذا الجبل، وعمل معهم العهد أن يكون إلهاً لهم وأن يكونوا شعباً له ( خروج 20: 1 و 24: 8 ) ولا يسجل لنا الكتاب المقدس أن أحداً زار هذا الجبل بعد ذلك إلا إيليا حين هرب من وجه ايزابل ( 1 ملوك 19: 8 ).\nوهناك رأيان عن موقع جبل سيناء، الأول أنه جبل سربال في وادي فيران، ويؤيد هذا القول يوسابيوس، ولكن لا توجد عند جبل سربال برية تكفي لأن يعسكر فيها العبرانيون كلهم لمدة سنة.\nوالقول الآخر انه جبل موسى، ويوافق على ذلك جستنيان ويقول يوسيفوس أن جبل موسى عظيم الارتفاع ومن المستحيل تسلقه لأنه حاد الصخور وشديد الانحدار ولا يستطيع أحد ان يطيل النظر إليه دون أن تؤلمه عيناه لأنه شديد الضوء.. ويظن يوسيفوس أن هذه الأحوال الطبيعية تجعل جبل موسى أكثر استعداداً لأن يكون الجبل من فوقه أعطيت الشريعة.\nويقول البعض أن موقع سيناء في أرض آدوم ولكن لا يتفق هذا القول مع ما ورد في الكتاب المقدس عن رحلات العبرانيين في البرية.\nويقع وادي الرحلة أسفل هذا الجبل، وتبلغ مساحته أربعة أميال مربعة، وهو يكفي لأن يعسكر فيه العبرانيون.\nوتوجد اليوم عند جبل موسى أديرة وكنائس اكتشفت فيها بعض النسخ القديمة من الأسفار المقدسة، في اللغات اليونانية والسريانية والجورجية والأثيوبية والسلافية والعربية وغيرها. وقد اكتشفت النسخة السينائية للكتاب المقدس في اللغة اليونانية في دير القديسة كاثرين وقد كتبت في القرن الرابع الميلادي.'),
(1910, 'سِينِْيم', 'قال النبي اشعياء: (( هؤلاء من بعيد يأتون، وهؤلاء من الشمال ومن المغرب، وهؤلاء من أرض سينيم )) ( اشعياء 49: 12 ) ويعني أن الشتتين سيأتون من ارض بعيدة. ولا بد أن تكون هذه البلاد في الشرق أو في الجنوب، لأن النبي يذكر الشمال والغرب. وقد اختلفت الآراء في مكان سينيم هذه:\n( 1 ) هناك رأي يقول أنها جنوب فلسطين حيث برية سين، ولكن هذه ليست بلاداً بعيدة.\n( 2 ) ويقول رأي آخر أنها عيلام التي كانت تعرف بالسينيم آي الرض المرتفعة.\n( 3 ) ويقول أنها الصين التي بدأ اليهود بالهجرة إليها في القرن الثالث ق.م. وكان التجار الصينيون قد بدأوا يسافرون إلى الهند منذ القرن العاشر ق.م. فكان هذا مدعاة لتعرف اليهود عليهم.'),
(1911, 'سِيْنِي', 'قبيلة كنعانية ورد اسمها مع الحويين والعرقيين ( تكوين 10: 17 ) ويقول جيروم عن وجود خرائب بلد اسمها سين قرب عرقة عند سفح جبل لبنان.'),
(1912, 'سِيوان', 'اطلب (( شهر )).'),
(1913, 'شِآريا شوب', 'اسم رمزي أعطاه اشعياء لابنه الأكبر الذي رافقه في زيارته لآحاز ( اش 7: 3 ) ومعناه (( سترجع بقية )). وقد أعطى اشعياء ابنه هذا الاسم رمزا لإتمام النبوة التي نادى بها، برجوع بقية اليهود إلى فلسطين بعد السبي. أنظر اش 10: 21.'),
(1914, 'شَآل', 'اسم عبري معناه (( طلب )) وهو اسم لأحد أبناء باني من نسل الكهنة. وقد عاش في عصر عزرا الكاتب. وهو ممن طلب منهم عزرا أن ينفصلوا عن نسائهم الأجنبيات، ويقدموا ذبيحة اثم أمام الله ( عز 10: 29 ).'),
(1915, 'شَاجاي', 'اسم عبري معناه (( تائه )) وهو اسم والد أحد أبطال داود ( 1 أخبار 11: 34 ). ويعتقد بعض المفسرين أن هذا اسم آخر (( لأَجي الهراري )) بناء على ما ورد في 2 صم 23: 11. ويعتقد آخرون أنه اسم آخر (( لشمة الهراري )) الذي ورد ذكره في 2 صم 23: 32 و 33. انظر (( شمة )).'),
(1916, 'شَارار', 'اسم ارامي معناه (( حارم )) وهو اراري، والد أحد أبطال داود ( 2 صم 23: 33 ) ودعي أيضاً ساكار ( 1 أخبار 11: 35 ).'),
(1917, 'شَاراي', 'اسم عبري معناه (( حرره يهوه )). وهو من بني باني، من نسل الكهنة. وقد طلب منه عزرا الكاتب أن ينفصل عن زوجته الغريبة ( عز 10: 40 ).'),
(1918, 'شَارَش', 'اسم عبري ربما كان معناه (( أصل )) أو (( نبتة )). وهو من عائلة ماكير من سلالة منسى ( 1 أخبار 7: 16 ).'),
(1919, 'شَاروحَيْن', 'اسم عبري معناه (( المسكن الحسن )) وهي إحدى مدن اليهودية، كان يسكنها شمعون ( يش 19: 6 ). ويظهر أنها المكان المسمى (( شعرايم )) في 1 أخبار 4: 31، والمسمى (( شلحيم )) في يش 15: 32. وهو المعروف اليوم بتل الفارعة ويبعد 15 ميلاً جنوب شرقي غزة. انظر ( شعرايم )) و (( شلحيم )).'),
(1920, 'شَارُون', 'كلمة معناها (( سهل )). ويسمى أيضاً (( سارون )) في اع 9: 35 وهو:( 1 ) الساحل الممتد بين يافا جنوباً، وجبل الكرمل شمالاً. ويمتد من الشرق إلى تلال السامرة. وطوله نحو 50 ميلاً، وعرضه يتراوح بين 9-10 أميال. وهو غير مسطح تماماً، ولكنه يتراوح بين الارتفاع والانخفاض. وقد نمت في الجزء الشمالي منه غابات أشجار البلوط. وهو سهل خصيب، لو استثنينا بعض التلال البسيطة، والبقاع القليلة التي يكثر فيها الشوك والحسك ( اش 35: 2 ). وهو أيضاً مرعى للبقر ( 1 أخبار 27: 29 واش 65: 10 ). ومشهور بنرجس شارون. انظر (( نرجس )) و (( سوسنة )).( 2 ) مرعى شرقي الأردن ( 1 أخبار 5: 16 ) أقام فيها سبط جاد ويرّجح البعض أن الأرض التي يحدها نهر ارنون ومدينة حشبون، هي شارون المقصودة هنا ( تث 2: 10 ).'),
(1921, 'شَارُوني', 'لقب أعطي لشطراي، الذي كانت له السلطة على البقر السائم في شارون، في عهد الملك داود (( 1 أخبار 27: 29 ).'),
(1922, 'شاشاي', 'اسم عبري ومعناه (( شاحب اللون )) أو (( ضارب البياض )) وهو احد أبناء باني، من بني الكهنة، ممن طلب منهم عزرا الانفصال عن زوجاتهم الأجنبيات ( عز 10: 40 ).'),
(1923, 'شاشق', 'اسم عبري ومعناه (( رغبة )) وهو أحد أبناء الفَعَل من سبط بنيامين ( 1 أخبار 8: 14 و 25 ).'),
(1924, 'شَاعَف', 'كلمة ارامية معناها (( بلسان )) وهو \n( 1 ) أحد أبناء يهداي من كالب من سبط يهوذا ( 1 أخبار 2: 47 ).\n( 2 ) ابن كالب من جاريته معكة، ومنه جاء سكان مدّمنة ( 1 أخبار 2: 49 ).'),
(1925, 'شافاط', 'اسم عبري معناه (( قضى )) وهو:( 1 ) الجاسوس الذي أرسل من سبط شمعون للتجسس على أرض كنعان ( عد 13: 5 ).( 2 ) اسم والد اليشع النبي ( 1 مل 19: 16 و 19 و 2 مل 3: 11 و 6: 31 ).( 3 ) ابن عدلاي أحد رعاة داود ( 1 أخبار 27: 29 ).( 4 ) ابن شمعيا من نسل داود الملك، من سبط يهوذا ( 1 أخبار 3: 22 ).( 5 ) ورد هذا الاسم مرتين في 1 أخبار 5: 12 ففي الأول يجب أن يكون (( شافام )) حسب الأصل العبري وفي الثاني (( شافاط )) وهما رئيسان للجاديين (( انظر شافام )).'),
(1926, 'شافام', 'اسم رئيس في سبط جاد، سكن في باشان. وقد جاء اسمه في 1 أخبار 5: 12 شافاط، ولكنه شافام. انظر شافاط. وقد حارب شافام ورئيسه يوئيل المهاجرين في أيام شاول الملك ( 5: 7-10 و 18-22 ).'),
(1927, 'شافان', 'كلمة عبرية ومعناها (( وبر )) أو (( أرنب الصخر )). وشافان كاتب عاش في عهد يوشيا الملك. كان عليه أن يجمع تعهدات الشعب لإصلاح الهيكل ويقدمها لحلقيا الكاهن ( 2 مل 22: 3-7 ) وعندما وجد حلقيا كتاب الشريعة أثناء ترميم الهيكل، أعطاه لشافان. فقرأ شافان السفر وحده أولاً، ثم قرأ للملك فذهب حلقيا الكاهن وشافان وآخرون، بناء على أمر الملك. إلى خلدة النبيّة، ليسألوها نبوة عن يهوذا بخصوص ألوان العقاب التي وردت في هذا السفر، وهل سيعاقب الله شعبه بها أم لا ( 2 مل 22: 8-14 ).وأبناؤه اخيقام الذي كان واقفاً إلى جانب ارميا النبي يحميه من القتل ( ار 26: 24 و 29: 14 ) والعاسة ( ار 29: 3 )، وجمريا ( ار 36: 10 )، ويازنيا ( حز 8: 11 ) وشافان هو جد جدليا ( 2 مل 25: 22 ).'),
(1928, 'شافَر', 'اسم عبري معناه (( جمال )). وهو جبل في البرية حل بجواره بنو إسرائيل وهم في طريقهم على كنعان ( عد 33: 23 و 24 ).'),
(1929, 'شافِير', 'اسم عبري معناه (( جميل )) مدينة في يهوذا ( مي 1: 1 ) ويقول بعضهم أن مكانها اليوم السوافير التي تقع على بعد ثلاثة أميال ونصف الميل جنوب شرقي اشدود.'),
(1930, 'شاقِل', 'اطلب (( وزن )).'),
(1931, 'شألتيئيل أو شألتئيل', 'اسم عبري ومعناه (( سألت الله )) وهو أب زربابل ( عز 3: 2 ) وأبوه نيري ( لو 3: 27 ) وهو أيضاً الابن الكبر للمكل يكنيا ( 1 أخبار 3: 17 ومت 1: 12 ). فيظهر أنه كان عم زربابل ( 1 أخبار 3: 17-19 ). وتفسير ذلك انه ليس ابن يكنيا بحسب الجسد، ولكنه في الخليفة الشرعي لعرش يكنيا، وبوفاته صار زربابل ولي العهد. فلقد كان حلقة الوصل بين يكنيا وزربابل انظر (( زربابل )).'),
(1932, 'شالَح', 'اسم سامي معناه (( برعم )) أو (( نبتة )) وهو ابن ارفكشاد ووالد عابر ( تك 10: 24 و 11: 12-15 و 1 أخبار 1: 18 ).'),
(1933, 'شالَش', 'اسم عبري معناه (( مطيع )) وهو من أبطال سبط اشير، ابن هيلام ( 1 أخبار 7: 35 ).'),
(1934, 'شالَف', 'اسم سامي ربما كان معناه (( حرث الأرض )) وهو من الشعب السامي الذي تسلسل من يقطان ( تك 10: 26 و 1 أخبار 1: 20 ). وكان يسكن جنوب الجزيرة العربية. وهذا الاسم مشهور حتى الآن في بلاد اليمن.'),
(1935, 'شالِيم', 'اسم عبري معناه (( سلام )) ( تك 14: 18 )، ويحتمل أنها أورشليم وقد ورد هذا الايم في بعض الترجمات بصورة (( ساليم )) في مز 76: 2 وعب 7: 2.'),
(1936, 'شاماع', 'اسم عبري معناه (( قد سمع ( الله )  )) وهو ابن حوثام العروعيري، أحد أبطال داود الملك ( 1 أخبار 11: 44 ).'),
(1937, 'شامر', 'اسم عبري معناه (( حارث )) وهو:( 1 ) صاحب جبل شامر الذي سمّي فيما بعد جبل السامرة. وقد اشتراه عمري ملك إسرائيل وبني عليه مدينة السامرة ( 1 مل 16: 24 ).( 2 ) ابن محلي، لاوي من أبناء مراري ( 1 أخبار 6: 46 ).( 3 ) من بني اشير ( 1 أخبار 7: 34 ). وترجم أيضاً شومير ( 1 أخبار 7: 32 ).( 4 ) من بني بنيامين ( 1 أخبار 8: 12 ).'),
(1938, 'شامَع', 'اسم عبري معناه (( خبر )) وهو:( 1 ) ابن حبرون من سبط يهوذا ( 1 أخبار 2: 43 و 44 ).( 2 ) رأوبيني، ابن يوئيل ( 1 أخبار 5: 8 ) وقد ورد هذا الاسم في الأصل بصورة (( شمع )).'),
(1939, 'شامور أو شامير', 'اسم عبري معناه (( شوك أو صوان )) وهو:\n( 1 ) لاوي من بني ميخا ( 1 أخبار 24: 24 ) وقد ورد في قراءة بصورة (( شامور )) وفي أخرى بصورة (( شامير )) وهو نفس الشخص.\n( 2 ) مدينة في جبل يهوذا ( يش 15: 48 ) ولازال اسمها باقياً في سومرة التي تبعد خرائبها نحو 13 ميلاً جنوب غربي حبرون.\n( 3 ) مدينة في جبل افرايم، أقام فيها تولع القاضي ودفن فيها، رغم أنه من يساكر ( قض 10: 1 و 2 ) وربما كانت هي ساتور الواقعة بين السامرة وجنين.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(1940, 'شاول', 'اسم عبري معناه (( سُئل ( من الله )  )) وهو:( 1 ) ملك من ملوك ادوم، من رحبوت التي تقع على نهر افراتة ( تك 36: 37 ).( 2 ) ابن قيس، من سبط بنيامين أول ملوك إسرائيل. انظر (( قيس )).تقدم النبي صموئيل في العمر، وكان من الواضح أن ابنيه لا يسران في طريق الرب، ولا يمكن أن يخلفه احدهما. وكانت الأمم المحيطة بإسرائيل تنتهز الفرصة للإيقاع به ( 1 صم 8: 1 و 3 و 20 و 12: 12 ). وجاء شيوخ إسرائيل إلى صموئيل النبي، طالبين تغير نظام الحكم. فطلبوا إليه أن يسمح لهم ملكاً كسائر الأمم المجاورة ( 1 صم 8: 4 و 5 و 19 و 20 ). ورغم أنه كان في تدبير الله أن يكون لهذا الشعب ملوك ( تك 17: 6 و 16 و 35: 11 وتث 17: 14-20 )، إلا أن شيوخ إسرائيل طلبوا إقامة ملك ليواجه الأزمة الراهنة، ويحارب الأمم المجاورة. كان هذا، ولا شك، دليلاً على ضعف إيمانهم في الله. وبدلاً من الإيمان بالله الملك غير المنظور، وضعوا ثقتهم في ملك منظور.شاول يجلس على العرش:حدث أن ضلت بعض حمير قيس، فذهب شاول يفتش عليها. وكان عمر شاول في ذلك الوقت حوالي 35 عاماً. وكان طويل القامة جداً. ولما لم يجد الحمير هّم بالعودة ولكن خادم شاول أوعز إليه بالاتجاه إلى النبي صموئيل قبل العودة، عله يرشدهما إلى ضالتهما المنشودة وكان شاول وصموئيل منذ أيام جبعة ( 1 صم 10: 14-16 ). وكان الرب قد أعلن لصموئيل أن شاول سيذهب إليه، وأنه الملك المختار. فأخبر صموئيل شاول بعودة الحمير، ثم أعلن له قصد الله فيه شخصياً. وقبل عودة شاول، أخذ صموئيل قنينة دهن، وصبها على رأسه فمسحه ملكاً.\nدعا صموئيل الشعب إلى المصفاة، وهناك وقعت القرعة على شاول، لقد اختار الله شاول لأن مظهره، سيحوز رضى الشعب وثقتهم، ولأنه من سبط بنيامين، وبنامين يتوسط افرايم ويهوذا وبذلك يستجيب لرغبة سكان الشمال والجنوب في فلسطين.وفي بدء أيامه انتصر على ناحاش وجيش العمونيين في حصار يابيش جلعاد، وبعد ذلك عيد الشعب في الجلجال، عيد النصرة ( 1 صم 11: 1-12: 25 ).عهد شاول الملك:كُّون شاول جيشاً من 3000 جندي. وعندما تجمع الفلسطينيون في مخماس، ذهب شاول والشعب إلى الجلجال مدة اسبوع. ولما تأخر صموئيل عن الحضور قدم شاول الذبائح والمحرقات. ولهذا السبب أخذ الله الملك من بيت شاول إلى بيت داود الملك ( 1 صم 13: 8-14 ). وانتصر شاول هذه المرة أيضاً ( 1 صم 14: 31 ). وكانت خطيئة شاول الثانية أنه في حربه مع عماليق عفى عن القطيع الممتاز وعن الملك. لهذا لم يثق الله في خضوع شاول له وطاعته إياه، فرفع عنه الملك ( 1 صم 15: 1-35 ). ومنذ ذلك الوقت استولى عليه روح نجس، وجنوني وتعين داود ملكاً. وانهزم شاول في معركة جبل جلبوع انهزاماً ذريعاً. مات في المعركة أبناؤه الثلاثة، وجرح هو جرحاً خطيراً، فسقط على سيفه ومات ( 1 صم 31: 1-7 ).شاول  في عين دور:قبل معركة جبل جلبوع، اراد شاول أن يستشير صاحبه جان ورغم أن صاحبة الجان عرفت أن شاول على مقربة من عين دور، لكنها تظاهرت بعدم المعرفة. أما أن كانت المرأة قد صرخت بصوت عظيم، فقد كان ذلك لأنها حبكت الحيلة. ولكنها لابد عرفت شاول لطول قامته، وشخصيته.أما صفات صمؤئيل التي ذكرتها المرأة فيمكن أن تصف بها أي شخص متقدم في السن. أما اتمام النبوة التي قالها الروح فهو ما يتوقعه أي إنسان.لقد فشل شاول في كل شيء حتى أنه ذهب ليستشير صاحبة الجان، والله لا يرضى عليه. فإن النبوة لابد أن تتحقق هي أن شاول سيفشل في الحرب. كما أنه يكون من الغريب جداً أن الله بعد أن رفض أن يجيب شاول بالأحلام والأنبياء أن يرسل له روح صموئيل لأننا لا نفهم من النص الكتابي أن شاول رأى صموئيل لأننا لا نفهم من النص الكتابي أن شاول رأى صموئيل بل ما نفهمه أن المرأة هي التي قالت أنها رأت صموئيل.وقد اكتشف قصر الملك شاول في جبعة شاول في مكان يدعى اليوم (( تل الفول )) ويقع شمالي أورشليم فقد اكتشفوا حوائط سميكة وبعض الأسلحة الحديدية ومن ضمنها سنان حربة.( 3 ) الاسم العبري للرسول بولس ( اع 5: 58 و13: 9 ) انظر (( بولس )).( 4 ) ابن شمعون من امرأة كنعانية ( تك 46: 10 و خر 6: 15 و 1 أخبار 4: 24 ). ومنه الشاؤليون ( عد 26: 13 ).( 5 ) لاوي من القهاتيين ( 1 أخبار 6: 24 ).'),
(1941, 'شَاؤليون', 'انظر (( شاول )) 4.'),
(1942, 'شَبَا', 'شبا وددان أخوان ابنا رعمة من بني كوش ( تك 10: 7 ). وهما أيضاً ابنا يقطان بن إبراهيم من قطورة زوجته ( تك 25: 3 ). ويتضح من تك 10: 28 أنهما من نسل سام.\nويظهر من هذه الشواهد الكتابية أن شبا قبيلة عربية من نسل سام. ويظهر أن بعض أفراد تلك القبيلة هاجروا إلى الحبشة، مما كان دافعاً لأن تلقب بعض عائلات تلك القبيلة بلقب كوشية ( تك 10: 7 ). وانتساب تلك الأسرة لإبراهيم ( تك 25: 3 ). يدل على أن بعض عائلات  تلك القبيلة أقامت في سوريا. والحقيقة أن هذه القبيلة أقامت جنوبي غربي الجزيرة العربية ( تك 10: 28 ). وامتدوا فيما بعد إلى الشمال الغربي للجزيرة العربية، وإلى سوريا.\nسكان سبا أو شبا كانوا مشهورين، بأنهم تجار ذهب وتوابل وأحجار كريمة. وهم أيضاً تجار رقيق ( يوئيل 3: 8 )، وحراس صحراء ( أيوب 1: 15 و 6: 19 ) ولغتهم العربية.\nوقصة ملكة سبا وزيارتها المشهورة لسليمان، تدل على أن المرأة كانت تتمتع بمركز رفيع في تلك القبيلة. ويغلب الظن أنها عندهم كانت مساوية للرجل، اجتماعياً، ودينياً، وعسكيراً.'),
(1943, 'شَباريم', 'اسم عبري معناه (( خراب )) أو (( محاجر )) موضع تقهقر إليه بنو إسرائيل بعدما انهزموا في عاي ( يش 7: 5 ). إلا أن موقع شباريم غير معروف حتى الآن.'),
(1944, 'شباط', 'انظر (( شهر )).'),
(1945, 'شَبَام', 'انظر (( سبمة )).'),
(1946, 'شَبُّولت', 'كلمة عبرية معناها (( سنبلة أو مجرى ماء )). عندما حارب يفتاح الجلعادي افريم وانتصر عليهم، أقام اناساً على الأردن. فالذين هبروا من الافراميين، كانوا يسألونهم: (( هل أنت افرايمي؟ )) فإن أجاب: (( لا ))، طلبوا منه أن يقول: (( شبولت )) فإن اخطأ وقال: (( سبولت )) قتلوه في الحال والسبب في ذلك أن هذه الكلمة تميز لهجة الافراميين عن الجلعاديين. فالافرامييون لا ينطقون الشين في شبولت. وقتل في ذلك اليوم 42000 افرايمي ( قص 12: 5 و 6 ).'),
(1947, 'شَبَتاي', 'اسم عبري معناه (( مولود يوم السبت )) وهو لاوي قائد في أيام عزرا ( عز 10: 15 نح 8: 7 و 11: 16 ).'),
(1948, 'شِبِث', '( مت 23: 23 ) نبات من الفصيلة الصيوانية أو ذات الأزهار الخيمية، وبذوره تشبه بذور الكراوية ويستعمل في الطبخ كما تستعمل التوابل. وكذلك يستخدم في الطب في معالجة بعض حالات المعدة والأمعاء. وكان الفريسيون يعشرون بذور الشبث وسيقانه وأوراقه.'),
(1949, 'شِبْر', '( خر 25: 25 و 1 مل 7: 26 ) عرض كفة اليد. وتستخدم أحياناً إشارة إلى المدة القصيرة ( مز 39: 5 ).'),
(1951, 'شَبَع', 'اسم عبري معناه (( سبعة )) أو (( قَسَم )) وهو:( 1 ) مدينة في نصيب شمعون وتقع بين بئر سبع ومولادة ( يش 19: 2 ). وربما كان موضعها الحالي على بعد ثلاثة أميال من بئر سبع، ويعرف بتل السبع.( 2 ) ابن بكري البنياميني، الذي عصا على داود، فأسرع يوآب وراءه، وأدركه في حصن آبل بيت معكة. وهناك قطعت امرأة رأسه، وألقتها من السور ( 2 صم 20: 1-22 ).( 3 ) رئيس للجاديين، يقيم في جلعاد، في باشان ( 1 أخبار 5: 13 ).'),
(1952, 'شِبْعَة', 'اسم عبري معناه (( سبعة )) أو (( حلف )) وهي لبئر التي أعاد حفرها خدام اسحق، وسميت بئر سبع ( تك 26: 33 ).'),
(1953, 'شَبَقْتني', '( مت 27: 46 ) وهي كلمة جاءت في صلاة المسيح على الصليب قائلاً: (( ايلي ايلي، لما شبقتني )). وهي ارامية معناها (( تركتني )).'),
(1954, 'شَبَكة ( وشباك )', 'استخدمت هذه الكلمة ليس في صيد السمك فقط كما جاء في مت 13: 47 و 48، بل أيضاً في صيد الطيور والحيوانات ( اش 51: 20 ). كما استخدمت رمزاً لاجتذاب الكثيرين إلى الملكوت ( مت 13: 47 و 48 ). استخدمت أيضاً لتعني الحيلة والغدر ( جا 7: 26 )، والايقاع في الشراك ( حز 12: 13 و 19: 8 ).والشباك على أنواعها فمنها ما يطرح في البحر لصيد السمك، ومنها النوع الكبير الذي يلقى بالقوارب على مساحة كبيرة من الماء لحجز ما فيها من أسماك، ومنها النوع الذي استخدم قديماً للصيد وللقنص ( اش 51: 20 وأيوب 19: 6 وحز 12: 13 ومز 9: 15 ).'),
(1955, 'شُبَّاكة نحاسية', 'انظر (( مذبح )).'),
(1956, 'شَبْنَا', 'ورد هذا الاسم بصورة شبنة في 2 مل 18: 18 إلى أخ. وربما كان الاسمان مختصراً لاسم (( شبينا )). انظر (( شبينا )).\nوكان شبنا وكيلاً وناظراً على قصر حزقيا الملك، وكان له سلطان عظيم ويظهر أنه كان غريب الجنس. وبنى لنفسه مقبرة، كعادة الناس في تلك الأيام ( اش 22: 16 ). وبخه اشعياء، ودعاه (( خزي بيت الملك )) ( اش 22: 17-25 ). وقد تمت النبوة حوالي 701 ق.م. عندما أصبح الياقيم ناظراً على بيت حزقيا الملك وكان شبنا كاتباً ( 2 مل 18: 18 و 26 و 37 و 19: 2 ).'),
(1957, 'شِبْنَة', 'انظر (( شَبنا )).'),
(1958, 'شَبَنْيا', 'اسم عبري ربما يكون معناه (( أعادني يهوه )) وهو:( 1 ) كاهن نفخ بالبوق عندما وصل التابوت ( 1 أخبار 15: 24 ) وذلك في عهد داود.( 2 ) واحد من أسرة كهنوتية، في جيل ما بعد السبي ( نح 12: 14 )، وختم العهد أحد ممثلي تلك العائلة ( نح 10: 4 ). انظر (( شكنيا )).( 3 ) لاوي، عاون في خدمة عيد المظال في عهد عزرا ( نح 9: 4 و 5 )، وختم العهد نيابة عن أسرته ( نح 10: 10 ).( 4 ) لاوي آخر ختم العهد ( نح 10: 12 ).'),
(1959, 'شَبُوئيل', 'اسم عبري معناه (( عُد يا الله )) وهو:( 1 ) ابن جرشوم وحفيد موسى ( 1 أخبار 23: 16 ). وكان رئيساً على الخزائن ( 26: 24 ). وفي 1 أخبار 24: 20 ودعى (( شوبائيل )).( 2 ) ابن هيمان. عاش في عصر داود ( 1 أخبار 25: 4 ). وفي ع 20 ذكر اسمه (( شوبائيل )).'),
(1960, 'شَبْيَا', 'اسم عبري معناه (( عُد يا يهوه )). وهو بنيامني، ابن شحرايم ( 1 أخبار 8: 10 ). وقد ورد هذا الاسم بصورة (( سكية )) وفي بعض المخطوطات.'),
(1961, 'شَتات', 'كلمة يقصد بها بنوا إسرائيل بعد أن تشتتوا في بلاد أخرى كثيرة غير أرض فلسطين ( يو 7: 35 ويع 1: 1 ) وبناء على أمر الشريعة، كان الشتات عقاباً للشعب الذي رفض أن يعيش بموجب شريعة موسى ( لا 26: 14 و 33 وتث 4: 27 و 28: 64-68 ). وبناء على ذلك أُسر السبطان اللذان أقاما في المملكة الجنوبية، وعند العودة من السبي البابلي بقيت الغالبية العظمى منها في مكانها، ورفضت العودة إلى أرض فلسطين. ومن بابل اتخذ بنوا إسرائيل طرق سفرهم للتجارة إلى مصر وسوريا وفارس ومعظم بقاع الأرض. ونتج عن هذا أن بُنيت مجامعهم في كل مكان، وانتشرت عقائدهم وتحولت العبادة من الهيكل في أورشليم إلى المجامع المحلية ( اع 2: 5-11 و 1 بط 1: 1 ).\nوعند المناداة بالإنجيل، انتشرت البشارة أولاً بين أهل الشتات، ثم نودي بها بين الأمم.'),
(1962, 'شَتربوزناي', 'اسم فارسي قديم ومعناه (( مخلص الدولة )). وهو ضابط فارسي أراد منع اليهود العائدين من السبي من بناء الهيكل ( عز 5: 3 و 6 و 6: 13 ).'),
(1963, 'شتاء', 'انظر (( فصل )).'),
(1964, 'شَجَرة-أشجار', 'الكتاب المقدس مليء بأسماء الأشجار المتنوعة. وفي حالات كثيرة كانت ترتبط مسئوليات مقدسة بالأشجار. فكثيراً ما كانت الأشجار أمكنة ظهور الله للبشر، كما حدث مع موسى ( خر 3: 2 ). واعتبر العبرانيون الأشجار عطايا من الله، فقدموا باكورات أثمارها لله ( خر 23: 19 )، وكان الكل من ثمار الشجرة الجديدة في أعوامها الأربعة الأولى ممنوعاً بحكم الشريعة ( لا 19: 23-25 ).والأشجار تستخدم في تعبيرات مجازية كثيرة. كقصة يوثام ( قض 9: 7-15 )، ومثل سليمان ( 1 مل 4: 33 )، ومثل المسيح عن الشجرة غير المثمرة ( مت 7: 17-20 ولو 13: 6-9 ).وللأشجار الخاصة ميزات خاصة. فأشجار الزيتون مثلاً رمز الرجاء، لأنها قبل أن تموت الشجرة القديمة، تكون قد أنبتت مكانها شجرة أخرى جديدة تتحول إليها حياتها ( قارن أيوب 14: 7-9 ).'),
(1965, 'شَجر البُكا', 'ربما يقصد به شجر البلسم أو ما يشبهه. ففي بلاد العرب، قرب مكة شجر بهذا الاسم. يشبه شجر البلسم أو البلسان، وله عصارة بيضاء لاسعة. وقد سمى شجر البُكا، نسبة لن تلك الأشجار تنضج بالصموغ، أو نسبة لقطرات الندى التي تقع عليه.تنتشر تلك الأشجار في وادي الرفائين ( 2 صم 5: 22-24 و 1 أخبار 14: 14 و 15 ). أما وادي البكاء المذكور في مز 84: 6 فربما يكون بقعة جغرافية. ولكن يرجح انه مجرد فكرة تحمل معنى عميقاً، فإن أولئك الذين لهم اختبار طيب مع الرب، بنعمته تتحول المآسي في حياتهم إلى افراح.'),
(1966, 'شَجرة الحياة', 'شجرة وسط الجنة ( تك 2: 9 ). ثمرها يمنح الإنسان حياة خالدة. عندما اخطأ آدم وحواء طردهما السيد من الجنة لئلا يأكلا من شجرة الحياة ويعيشا على الأبد. ولو كانا يعيشان إلى الأبد بخطيتهما لصار العالم جحيماً مقيماً. لقد دبر الله طريقاً آخر لإعادة الإنسان إلى الحياة.وسرعان ما صار التعبير (( شجرة الحياة )) تعبيراً شعريا، استخدمه كاتب الأمثال إشارة إلى مصادر البركة العظيمة في حياة الإنسان ام 3: 18. ثم استخدمها يوحنا الرائي مشيراً إلى الامتيازات المجيدة العظيمة التي تنتظر المفيدين في العالم الآخر ( رؤ 2: 7 و 22: 2 و 14 ).'),
(1967, 'شَجرة الزيت', 'العبارة التي ترجمت فيها شجرة الزيت في اش 41: 19، هي نفس العبارة التي ترجمت (( خشب الزيتون )) في 1 مل 6: 23 و (( أغصان الزيتون بري )) في نح 8: 15. ومن هذا الخشب عمل سليمان كروب الهيكل، وباب المحراب وقوائم مدخل الهيكل ( 1 مل 6: 23 و 26 و 31-33 ).شجرة الزيت أو الزيتون البري, هي شجيرة لها زهور عطرة الرائحة, وأوراقها خضراء تعطي زيتاً أقل قيمة من زيت الزيتون. وهذه الأشجار وفيرة في فلسطين قرب حبرون والسامرة وجبل تابور.'),
(1968, 'شَجرة شارقة', 'الكلمة العبرية المترجمة (( شارقة )) في مز 37: 35 ترجمت في الترجمة السبعينية وبعض الترجمات الحديثة (( بارز لبنان )). إلاّ أن هذه الكلمة تعني حرفياً نباتاً أخضر ينبت على أرضه. فليس هو مستورداً من الخارج لكنه نبات وطني. ونفس الكلمة هي التي ترجمت (( وطني )) في الآيتين لا 16: 29 و 18: 26. والفكرة فيها, هي أن الشرير يزهر في العالم, لأنه من العالم وليس غريباً عنه.'),
(1969, 'شَجرة معرفة الخير والشر', 'انظر (( آدم )).'),
(1970, 'شَجَوِيَّة', 'كلمة ربما كانت من أصل عبري ومعناها (( تائه )) أو (( ضال )) أو ربما كانت من أصل أكادي فيكون معناها (( ترنيمة شجوى وحزن )). وهي تعبير موسيقي وردت في صيغة المفرد عنواناً لمزمور 7, وفي صيغة الجمع في حب 3: 1.'),
(1971, 'شَحْرَايم', 'اسم عبري معناه (( الفجران )). وهو بنياميني أب لأسرة كبيرة ( 1 أخبار 8: 8 ).'),
(1972, 'شَحَرْيا', 'اسم عبري معناه (( يهوه هو الفجر )) وهو بنياميني, ابن يروحام ( 1 أخبار 8: 26 ).'),
(1973, 'شَحْصِيمَة', 'اسم عبري ربما كان معناه (( الأماكن المرتفعة )). وهي مدينة على حدود يساكر بين تابور والأردن يش 19: 22 وقراءة ثانية تقول (( شحصومة )) وهي قرية القرم الحالية جنوبي شرقي جبل الطور.'),
(1974, 'شَحْم', 'قدّم هابيل الشحم من أبكار غنمه للرب ( تك 4: 4 ). وقضت الشريعة الموسوية فيما بعد بأن الشحم من كل الذبائح ملك للرب ( لا 3: 16 و 7: 23 و 25 عد 18: 17 ). وكان أكل الدم والشحم ممنوعاً ( لا 3: 17 ), بل كان يحرق ويقدم على المذبح في نفس يوم الذبح تقدمة ليهوه ( خر 29: 13 و 22 و لا 3: 3 - 5 و 4: 8 - 10 ) وقود رائحة سرور للرب ( لا 4: 31 ) وعندما أقام الشعب في كنعان يظهر أن هذا القانون أصبح غير نافذ بالنسبة للبهائم من البقر أو الغنم التي تذبح للأكل, نظراً لبعد أماكن إقامة الشعب عن المذبح ( تث 12: 15 و 16 و 21 - 24 ). ولعل الفكرة يقصد بها إنكار الذات, وتقديم أحسن شيء ليهوه.\nعلى أن كلمة (( شحم )) استخدمت في الكتاب المقدس بمعنى الوفرة والرخاء كما جاء في تث 32: 15.'),
(1975, 'شَدْرَخ', 'اسم بابل أعطي لحنانيا أحد الأربعة فتيان اليهود الذين أُسروا من أورشليم وأُخذوا إلى بابل للخدمة الملكية ( دا 1: 7 ). ويُرّجح أن اسم (( شدرخ )) معناه (( الأمر من أكو )) ( إله القمر ). وقد رفض هو وزملاؤه أن يأكلوا من أكل الملك ( دا 1: 12 ), ورفضوا السجود لتمثال الملك نبوخذ نصر ( دا 3: 12 ) فطرحهم الملك في أتون النار ولكن الله أنقذهم منه ( دا 3: 19 - 30 ).'),
(1976, 'شَدَيْئود', 'اسم عبري ويرجّح أن معناه (( شداي يضيء )) وهو أبو اليصور الذي كان رئيساً للرؤبينيين في البرية ( عد 1: 5 و 2: 10 و 7: 30 و 35 و 10: 18 ).'),
(1977, 'شَر', 'أصل الشر مشكلة خطيرة حيّرت أعظم المفكرين. فالله ليس أصل الشر, رغم أنه سمح به وفي سماحه بالشر تظهر رحمته لمن يرفض الشر, ويظهر عدله في عقاب من يختار الشر. انظر (( خطيئة )).'),
(1978, 'شَرِآصرو شراصر', 'اسم أكادي معناه (( احفظ الملك )) وهو:( 1 ) ابن سنحاريب الذي تآمر مع أخيه على قتل أبيهما ( 2 مل 19: 37 واش 37: 38 ).( 2 ) رجل أرسل نت بيت إيل إلى الكهنة في أورشليم  ليسأل هل تُحفظ الصيامات وقد زالت أسبابها ( زك 7: 2 ).'),
(1979, 'شَوارب', '( خر 24: 22 ) كان العبرانيون يغطّون شواربهم أو شفاههم علامة الحزن.'),
(1980, 'شَرَبْيَا', 'اسم عبري معناه (( حرارة من يهوه )) وهو:( 1 )لاوي, رئيس عائلة. جاء من بابل مع زربابل ( نح 12: 8 ).( 2 ) لاوي رئيس أسرة, وقد رجع مع عزرا من بابل ( عز 8: 18 ). وربما كان شربياً هذا هو المقصود في عز 8: 24 و 25 وكان كاهناً وهو الذي ائتمنه عزرا على الفضة والذهب ومقسات الهيكل لحفظها. وربما كان هو نفس الشخص الذي عاون عزرا في قراءة سفر الشريعة وتفسيره ( نح 8: 7 ) واشترك في الخدمة الاعترافية الجهرية بعد عيد المظال ( نح 9: 4 ). وكان من ضمن الذين ختموا العهد مع نحميا ( نح 10: 12 ).'),
(1981, 'شَرْبين', 'شجرة في لبنان اش 41: 19 و 60: 13. وتنتمي إلى النوع الصنوبري, ويطلق هذا الاسم على السرو البري واسمه في اللاتينية Cupressus sempervirens .'),
(1982, 'شرق - مشرق - أبناء الشرق', 'ويقصد بالشرق الاتجاه نحو شروق الشمس ( يش 11: 3 ). وقد كان الباب الشرقي في هيكل أورشليم منفذاً لتدخل منه الشمس المشرقة, فتبعت رجاءً مجيداً بمجد حلول الله.أبناء المشرق هم الذين سكنوا الدول الشرقية كعمون وموآب وغيرهما من الشعوب التي كانت تسكن شرق الأردن وفي الجزء الشمالي من الجزيرة العربية ( حز 25: 4 و 10 ).'),
(1983, 'شَرَك', 'حبالة يصطاد بها الطير والحيوان ( اش 8: 14 و عا 3: 5 ). انظر (( صيد )).'),
(1984, 'شَرِكة', 'هي الأخوة والاشتراك بين مسيحي والمسيح أو بين مسيحي وغيره من المسيحيين ( 1 كو 10: 16 ). وبما أن العشاء الرباني يهبنا هذا الروح, لذا سمي شركة جسد المسيح ودمه على سبيل التعظيم. ويراد بشركة الروح القدس ( 2 كو 13: 14 ) حلوله فينا.'),
(1985, 'شَريعة', 'انظر (( ناموس )).'),
(1986, 'شِصّ-شُصوص السمك', 'يظهر من اي 41: 1 وعا 4: 2 إن صيد السمك بالشصوص كان معروفاً في القديم انظر (( صيد )).'),
(1987, 'شِطْرَاي أو شَطْرَاي', 'اسم عبري ربما كان معناه (( كاتب ليهوه )) رئيس رعاة داود في شارون ( 1 أخبار 27: 29 ).'),
(1988, 'شِطِّيم', 'اسم عبري معناه (( شجر السنط )) وهو:( 1 ) مخيم هام لبني إسرائيل في سهول موآب في شرقي الأردن مقابل أريحا ( قارن عد 22: 1 و 25: 1 ). وهو يقع من بيت يشيموت إلى آيل شطيم ( عد 33: 49 ) مسافة طولها يزيد على الثلاثة الأميال والنصف. هناك حاول بلعام أن يلعن الشعب ( عد 22-24 )، وهناك ضل الشعب وراء بنات موآب ومديان وبعل فغور ونالوا عقاب ذلك ( عد 25 ). وهناك تم الإحصاء الثاني للشعب ( عد 26 ) وأعلن يشوع جهراً بأنه خليفة موسى بعد أن صعد موسى على جبل نبو ومات ( عد 27: 12-23 ). ومن هناك أرسل يشوع جاسوسين لاختبار أسوار أريحا. ومن هناك عبر بنوا إسرائيل نهر الأردن. ومكانه اليوم هو تل الحمَّام.( 2 ) وادي شطيم، أو وادي السنط ( يوئيل 3: 18 ) واد جاف غير مثمر ربما قصد به الوادي الذي يبدأ شمال غربي أورشليم وينحدر إلى شرق المدينة فاصلاً إياها عن جبل الزيتون ثم يسير إلى الجنوب الشرقي نحو البحر الميت. وربما كان هو وادي النار حالياً.'),
(1989, 'شِظاظ-اشظة', 'كانت هذه شصوص أو أساور من ذهب أو نحاس لإيصال شقق الخيمة بعضها ببعض ( خر 26: 6 و 11 ).'),
(1990, 'شُعْبَه-شُعَب', 'صدع في الجبل يتجمع فيه المطر ( 2 صم 2: 29 ).'),
(1991, 'شَعْر', 'غطاء الرأس الطبيعي. في مصر قديماً يحلقون شعور رؤوسهم، وفي حالة الحزن لا يحلقونه والآشوريون أطلقوا شعرهم إلى الكتفين أما بنوا إسرائيل فقد تركوا شعرهم ينمو، وقصّوه فقط حتى لا يطول جداً ( عد 6: 5 و 2 صم 14: 26 ). وقد منعت الشريعة قص الشعر مستديراً كالوثنيين ( لا 19: 27 ).كان العبرانيون لا يحلقون شعورهم بموس أو بسكين ( اش 7: 20 وحز 5: 1 ) إلا أنهم كانوا يتركونه غير محلوق وفاء لنذر أو إتماماً لفرض ( قض 13: 5 ). اطلب (( نذير )). وكانوا أحياناً يقصّونه بالمقص ( حز 44: 20 ). وفي ذلك تجنبوا تطرف كهنة الوثنيين الذين يحلقون رؤوسهم. وكان سكان أرض كنعان يحلقون بعض أجزاء رؤوسهم. وكانوا يدهنون الشعر بزيت عطر سواء عند السيدات أو الرجال ( مز 23: 5 ومت 6: 17 ). وكانوا يزينون الشعر بالجواهر والأحجار الكريمة ويضفرونه ( 1 تي 2: 9 و 1 بط 3: 3 ). فمثلاً كان شعر شمشون مضفوراً في سبع خُصل.وفي أيام بولس لم يكن الرجال يرخون الشعر ( 1 كو 11: 14 ). وقد ندد الكتاب المقدس بالإسراف في تزين الشعر ( 1 تي 2: 9 و1 بط 3: 3 ).وأمرت الشريعة بأن تحلق رأس المرأة الأسيرة التي تتزوج من يهودي، والأبرص يوم تطهيره، والنذير يوم انتهاء نذره ( لا 14: 8 و 9 وتث 21: 12 وعد 6: 18 ). أما حلق الرأس للدلالة على الحزن والتوبة عن الخطيئة فكان دأب الأمم قديماً ( اي 1: 20 وار 7: 29 ).'),
(1993, 'شعْرَايم', 'اسم عبري معناه (( بابان )) وهو:( 1 ) مدينة في يهوذا ( يش 15: 36 ). وتقع في وادي السنط بالقرب من تل زكريا.( 2 ) مدينة في شمعون ( 1 أخبار 4: 31 ). انظر (( شاروحين )).'),
(1994, 'شَعَرْيا', 'اسم عبري معناه (( قدرة يهوه )) رجل من نسل شاول ( 1 أخبار 8: 38 و 9: 44 ).'),
(1995, 'شَعَشْغاز', 'اسم فارسي لا يعرف معناه، وهو خصي حارس بيت النساء الثاني لملك احشويرش، وكانت استير في حراسته ( اس 2: 14 ).'),
(1996, 'مَشاعِل', 'جمع مشعل وهو يحمل باليد ( يو 18: 3 ) ويتكون من خشب قطراني أو مادة ما مجففة، موضوع في الزيت ومحترق. ويحمل إما على قضيب أو عصا.'),
(1997, 'الشَّعْلَبُوني', 'لقب اليحبا، أحد أبطال داود السبعة والثلاثين. من سكان شعلبين وهي بلدة غير معروف مكانها. ويحتمل أنها شعبليم ( 2 صم 23: 32 و 1 أخبار 11: 33 ). انظر (( شعبليم )).'),
(1998, 'شَعْلُبيّم-شَعْلبَين', 'اسم عبري ومعناه (( ثعالب )) مدينة في دان ( يش 19: 42 )، لم يسلمها الاموريون ( قض 1: 35 )، حتى اقام العبرانيون في أرضهم زمناً ( 1 مل 4: 9 ) وقد تكون هي (( سلبيط )) التي تبعد ثلاثة أميال شمال غربي عجلون، ويغلب على الظن أن شعلبيم هي نفس شعلبين.'),
(1999, 'شَعْليم', 'اسم عبري يحتمل أن يكون معناه (( ثعالب )) وهو يطلق على مقاطعة في افرايم، اجتازها شاول الملك بعد أن ترك شليشة بحثاً عن الحمير التائهة ( 1 صم 9: 4 ).'),
(2000, 'شَعِير', '( خر 9: 31 ) نوع من الحبوب التي يصنع منه الخبز ( قض 7: 13 ويو 6: 9 و 13 ) ويستعمل علفاً للخيل والجمال ( 1 مل 4: 28 ). يزرع في فلسطين ( را 1: 22 ) في شهر تشرين الأول فما بعده ويحصدونه في شهر آذار فصاعداً. وكان الشعير طعام الفقراء.'),
(2001, 'شَفام', 'وهي بلدة في الشمال الشرقي من كنعان بجوار ربلة ( عد 34: 10 و 11 ). ومنها زبدي الشَّفمي المشرف على خزائن الخمر التي كانت للملك داود ( 1 أخبار 27: 27 ).'),
(2002, 'شِفْرة', 'اسم عبري معناه (( جمال )) وهو اسم إحدى القابلتين العبرانيتين اللتين رفضتا أمر فرعون بقتل الذكور الأطفال من العبرانيين ( خر 1: 15 ).'),
(2003, 'شِفْطَان', 'اسم عبري معناه (( قضاء )) وهو أمير افرايمي، أبو قموئيل ( عد 34: 24 ).'),
(2004, 'شَفَطْيَا-شَفْطِيَّا', 'اسم عبري معناه (( يهوه أوقع القضاء )) وهو:( 1 ) بطل من أبطال بنيامين، كان مع داود في صقلغ ( 1 أخبار 12: 5 ).( 2 ) ابن داود الخامس ( 2 صم 3: 4 و 1 أخبار 3: 3 )، الذي ولد في حبرون من ابيطال.( 3 ) ابن معكة، ورئيس للشمعونيين في أيام داود ( 1 أخبار 27: 16 ).( 4 ) بنياميني سكن أورشليم بعد السبي ( 1 أخبار 9: 8 ).( 5 ) أمير، ابن متان، وهو أحد الذين نصحوا صدقيا الملك بقتل ارميا النبي، حيث أن نبواته غير المقبولة كانت مدعاة لفشل المدافعين عن أورشليم أثناء حصار الملك نبوخذ نصر ( ار 38: 1-4 ).( 6 ) أحد أبناء الملك يهوشافاط ( 2 أخبار 21: 2 ).( 7 ) أبو أسرة، عاد 372 من أفرادها من السبي مع زربابل ( عز 2: 4 ونح 7: 9 )، ثم عاد أيضاً 81 منهم مع عزرا ( عز 8: 8 ).( 8 ) شخص من يهوذا، عاش قبل السبي ( نح 11: 4 ).( 9 ) من نسل عبيد سليمان، عاد من السبي مع زربابل ( عز 2: 57 ونح 7: 59 ).'),
(2005, 'شَفع-شَفيع-شَفاعة', 'وهي التوسط بين شخص وآخر. وهي دليل محبة الإنسان لأخيه الإنسان. كما أنها مؤسسة على أن معاملة الله للبشر معاملة ليست فردية فحسب، بل جماعية أيضاً. والصلاة الشفاعية قديمة قدم نوح ( تك 8: 20 و 22 )، وإبراهيم ( تك 17: 18 و 23-33 )، وموسى ( خر 15: 25 ). وخليفة موسى الذي رفع صلواته كقاضي وكاهن ونبي هو صموئيل ( 1 صم 7: 5 و 8 ) وحياة المسيح مليئة بالصلوات الشفاعية. بل إن الصلاة الربانية تحمل روح الشفاعة في طلب الملكوت، ومغفرة ذنوب الآخرين.\nوالصلاة الشفاعية يرفعها الإنسان لأجل صديق أو لأجل عدو ( مت 5: 44 ) أما الروح القدس فهو يشفع فينا ( رو 8: 26 ).\n أما المسيح في حياته الشخصية وموته على الصليب فهو شفيعنا، الذي ساقته شفاعته للموت على الصلب كفارة لخطايا البشرية. انظر (( وسيط )).'),
(2006, 'شِفْعِي', 'اسم عبري معناه (( وافر )) أب أمير الشمعونيين في حزقيا ( 1 أخبار 4: 37 ).'),
(2007, 'شُفيِّم', 'ربما كان اختصار (( شفوفام )) وهو:( 1 ) لاوي خدم بواب للأقداس ( 1 أخبار 26: 16 ).( 2 ) بنياميني ( 1 أخبار 7: 12 و 15 ). ويدعى أيضاً شفوفام ( عد 26: 39 ).'),
(2008, 'شفْميّ', 'انظر شفام'),
(2009, 'شفاه', 'وردت هذه الكلمة في معان مختلفة في الكتاب:( 1 ) النجس الشفتين: وهو الذي تلوثت شفتاه بالخطأ الكلامي ( اش 6: 5 ).( 2 ) عجول شفاهنا: آي ذبائح الحمد والشكر ( عب 13: 15 ).( 3 ) الشفتان المتوقدتان: تعبير عن كلام الخبث والحسد والعواطف الرديئة ( اع 9: 1 ). ويظن البعض أن هذا التعبير إشارة لكلام التظاهر بالتقوى والبر.'),
(2010, 'شَفوُ', 'انظر (( شفي )).'),
(2011, 'شَفُوفَام أو شفُوفَان', 'اسم عبري معناه (( حية )) وهو بنياميني رأس عشيرة الشفوفاميين. وكان الاسم ينطق أحياناً (( شُفِّيم )) ( 1 أخبار 7: 12 ). وهو أيضاً المسمى (( مُفِّيم )) في تك 46: 21. وربما هو نفسه المعروف بشفوفان ( 1 أخبار 8: 5 ).'),
(2012, 'شَفوفاميين', 'انظر (( شفوفام )).'),
(2013, 'شَفوفان', 'انظر (( شفوفام )).'),
(2014, 'شَفِي أو شفو', 'اسم عبري معناه (( عرى )). وهو من بني شوبال، من بني سعير الحوري ( تك 36: 23 و 1 أخبار 1: 40 ).'),
(2016, 'شِقاق وانشقاق', '( 1 كو 12: 25 وغل 5: 20 ) يراد بهما الانفصال. غير أنه يحتمل أيضاً معنى التباعد والتنافر بين مسيحيين، بحيث يمنع اتحادهما الروحي وإن كانا غير منفصلين، أما خطيئة الانشقاق فليست على المنشق، بل على الذي يُسبب الانشقاق.'),
(2018, 'شَكِرْوُن', 'اسم عبري معناه (( شُكر )). مدينة على الحدود الشمالية ليهوذا بن عقرون وينيئيل ( يش 15: 11 ).'),
(2019, 'شَكَم', 'انظر (( شكيم )).'),
(2020, 'شَكمِيون', 'انظر (( شكيم )).'),
(2021, 'شَكُنيْا أو شَكَنبْيا', 'كلمة ارامية معنها (( يهوه سكن )).( 1 ) أحد الكهنة من نسل هرون. وهو احد رؤوس الآباء للفرقة العاشرة من الفرق الثلاث والعشرين، التي قسم إليها داود الكهنة ( 1 أخبار 24: 1 و 11 ).( 2 ) لاوي في عهد حزقيا الملك ( 2 أخبار 31: 15 ).( 3 ) رئيس كهنة عاد مع زربابل من بابل ( نح 12: 3 و 7 ).( 4 ) من نسل داود، ومؤسس أسرة ( 1 أخبار 3: 21 و 22 ).( 5 ) أب لرفاق عزرا، الذين عادوا من السبي البابلي ( عز 8: 3 ).( 6 ) ابن يحزيئيل. عاد مع عزرا من السبي البابلي ( عز 8: 5 ).( 7 ) ابن يحبئيل، من بني عيلام. هو الذي اعترف بخطيئته لزواجه من نساء غريبة. واقترح على عزرا أن تخرج كل النساء الغريبة والذين ولدوا منهن ( عز 10: 2-4 ).( 8 ) أبو شمعيا حارس باب الشرق ( نح 3: 29 ) وربما كان لاوياً.( 9 ) حمو طوبيا العموني، ابن آرح ( نح 6: 18 ).'),
(2022, 'شَكيم', 'اسم عبري معناه (( كتف )) أو (( منكب )).( 1 ) مدينة لها سور ( تك 33: 18 و 34 و 20 )، عند سفح جبل جرزيم ( قض 9: 7 ) على أرض افرايم المرتفعة ( يش 20: 7 ).شكيم بلدة قديمة. فلقد خيم بالقرب منها إبراهيم ( تك 12: 6 )، وكان الكنعانيون يسكنون فيها. وفي أيام يعقوب، عندما عاد إلى كنعان، كان الحويون يقيمون فيها ( تك 34: 2 ). وفيها ابتاع يعقوب قطعة حقل نصب فيها خيمته. وقد ذكر استفانوس ذلك في عظته المعروفة ( اع 7: 16 ). وهناك أيضاً دفن جسد يوسف ( يش 24: 32 ).وإذ أساء شكيم بني حمور الحوي إلى دينه ابنة يعقوب، ذهب أخويها شمعون ولاوي وقتلا كل ذكر في المدينة ( تك 34: 25-29 ). وقد غضب يعقوب لهذا التصرف ( تك 34: 30-31 ).بقرب شكيم رعى أخوة يوسف أغنامهم ( تك 37: 12 و 13 ). وفيها قرأ يشوع سفر شريعة يهوه ( يش 8: 30 ). وهناك سمع الشعب خطاب يشوع الوداعي ( يش 24: 1 ). وقد اختيرت شكيم كإحدى مدن الملجأ ( يش 20: 7 و 21: 21 )، علما بأنها كانت مدينة لاوية.وفي عصر القضاة، أشير إلى وجود مذبح للإله بعل-بريث في شكيم ( قض 8: 33 و 9: 4 ). وهناك أقام ابيمالك بن يربعل حتى غدر به أهل شكيم ( قض 9: 23 و 45 ).في شكيم ثار عشرة أسباط من بني إسرائيل ضد رحبعام، وأقاموا يربعام بن نباط ملكاً عليهم ( 1 مل 12: 1-19 )، فصارت شكيم عاصمة إسرائيل في عهد يربعام ( 1 مل 12: 25 ) وبع سقوط المملكة الشمالية بقيت شكيم ( ار 41: 5 )، وصارت مركزاً للسامريين.وشكيم أو (( نابلس )) وهو اسمها الحالي، تبعد 5, 31 ميلاً شمالي أورشليم، 5,5 ميلاً جنوب شرقي السامرة. وهي تقع في الوادي الأعلى المحاط بجبل عيبال من الشمال، وجبل جرزيم من الجنوب. وهي تقع في الوادي المنحصر بينهما. سار في ذلك الوادي فلافيس فسبلسيان الإمبراطور الروماني من عماوس إلى أريحا. وإذ استولى عليها، أعاد بناءها، وسماها (( فلافيا نيابوليس )) أي (( فلافيا المدينة الجديدة )). ومنها الاسم الجديد (( نابلس )).يُحتمل أن يكون مكانها الأصلي في شرقي الوادي المعروف (( بتل البلاطة )). ولكن المدينة اليوم تقع على غربي الوادي 1870 قدماً فوق سطح البحر. وليس فيها الآن سوى قلائل اسم يوناني معناه السامريين، ولهم كنيس يعبدون فيه أيام السبت، ومدرسة لتعليم اللغة السامرية. وقد كشف التنقيب عن هيكل بعل بريث واسوار المدينة الباقية منذ عصر الكنعانيين المبنية من حجارة كبيرة الحجم.( 2 ) ابن جمر الحموي، أمير شكيم، الذ اغتصب دينة ابنة يعقوب، وقتله أخويها شمعون ولاوي ( تك 34 ).( 3 ) ابن جلعاد، مؤسس عشير الشكميين. واسمه ورد هنا بلفظ (( شكيم )) ( عد 26: 31 ويش 17: 2 ).( 4 ) ابن شميداع، رجل من منسى ( 1 أخبار 7: 19 ).'),
(2023, 'شَكِيمة', '( اش 37: 29 ). وهي الحديدة من اللجام المعترضة في فم الفرس. جميعها شكيم، وشُكُم، وشكائم.'),
(2024, 'شَلِحْي', 'اسم عبري معناه (( يهوه أرسل )). وهو جد يهوشافاط ( 1 مل 22: 42 و 2 أخبار 20: 31 ).'),
(2025, 'شِلْحيم', 'اسم عبري معناه (( قذائف )) مدينة في أقصى جنوب يهوذا ( يش 15: 32 ). وربما تكون شلخة خربة على بعد خمسة أميال غربي بين جبرين. انظر (( شاروحين )).'),
(2026, 'شِلْشَة', 'اسم عبري معناه (( ثلاثي )) أو (( مثلث )). وهو ابن صوفج، رئيس للاشيريين ( 1 أخبار 7: 37 ).'),
(2027, 'شَلَّكة', 'اسم عبري معناه (( رمي )) وهو باب من أبواب هيكل سليمان، يتجه للغرب ( 1 أخبار 26: 16 ) كان يرمى منه كناسة الهيكل ورماده وزبالته وصنع ممراً من قصره إلى هذا الباب فوق ورادي الجبانيين. ويحتمل أنه باب السلسلة في سور الحرم على بعد 600 قدم شمال زاويته الجنوبية الغربية.'),
(2028, 'شَلُّوم', 'اسم عبري معناه (( جزاء )) وهو:( 1 ) ابن نفتالي ( 1 أخبار 7: 13 ). ويدعى أيضاً شِلَيم ومعناه (( الرب صنع جزاء )). وهو مؤسس عشيرة مشليميين ( تك 46: 24 وعد 26: 49 ).( 2 ) رجل من شمعون ( 1 أخبار 4: 24 و 25 ).( 3 ) رجل من يهوذا ( 1 أخبار 2: 40 و 41 ).( 4 ) رئيس بوابي الأقداس ( 1 أخبار 9: 17 و 18 ). يحتمل أنه هو الذي أشار إليه ارميا ( ار 35: 4 ). وهو ابن قوري. كان هو وعائلته يعملون في حراسة الأقداس ( 1 أخبار 9: 19 ).( 5 ) ابن بابيش، الذي قتل الملك زكريا، وملك عوضاً عنه على العشرة الأسباط. غير أنه خلال شهر قتله منحيم وملك عوضاً عنه ( 2 مل 15: 8-15 ).( 6 ) اب يحزقيا، من بني افرايم ( 2 أخبار 28: 12 ).( 7 ) أحد أفارد أسرة رؤساء الكهنة التي من صادوق، وسابق لعزرا، عاش قبل حصار نبوخذ نصر لأورشليم ( 1 أخبار 6: 12-15 وعز 7: 2 ). وهو المدعو مشلام في 1 أخبار 9: 11 انظر (( مشلام )).( 8 ) زوج خلدة النبية، في عهد الملك يوشيا. وكان مسئولاً عن ثياب الكهنة أو الملك ( 2 مل 22: 14 و 2 أخبار 34: 22 ).( 9 ) عم ارميا النبي، وأب حنمئيل ( ار 32: 7 و8 ). وكان ابنه يسكن في عناثوث، حيث أقام الكهنة الذين من نسل ايثامار، ومنهم ابياثار ( 1 مل 2: 26 ).( 10 ) اسم ثان ليهوآحاز بن يوشيا، ملك يهوذا، وقد ملك ثلاثة أشهر ( 2 مل 23: 30 وار 22: 11 ). انظر (( يهو آحاز )).( 10 ) اسم ثان ليهوآحاز بن يوشيا، ملك يهوذا، وقد ملك ثلاثة أشهر ( 2 مل 23: 30 وار 22: 11 ). انظر (( يهو آحاز )).( 11، 12 ) بواب للهيكل، واحد أبناء باني، أخذا زوجتين أجنبيتين، طلب منهما عزرا إخراج زوجتيهما ( عز 10: 24 و 42 ).( 13 ) أحد البنائين الذي اشتغل مع بناته في بناء سور أورشليم ( نح 3: 12 ).'),
(2029, 'شَلوُّن', 'اسم عبري معناه (( جزاء )) ربما يكون هو نفس الاسم (( شلوم )) وكان رئيس دائرة المصفاة وقد رمم باب العين بأورشليم ( نح 3: 15 ).'),
(2030, 'شِلّيم', 'انظر (( شلوم )).'),
(2031, 'شَلمْان', 'اسم ملك من ملوك أشور، الذي أخرب بيت اربئيل هو 10: 14. ويعتقد البعض أن هذا الاسم هو مختصر شلمناسر. ويكون بذلك بيت اربئيل في الجليل أول موضع يحتمل أن شلمناسر استولى عليه انظر (( شلمنأصر )).'),
(2032, 'شَلمنأسر', 'اسم اشوري معناه (( شلمان رئيس )). وهذا هو اسم ملوك آشوريين كثيرين منهم:( 1 ) شلمنأسر الثالث، حكم من 860-825 ق.م وكان أول ملك آشوري اصطدم مع بني إسرائيل، وقد حارب آخاب ملك السامرة وملوك ارام وغيرهم في معركة قرقر سنة 853 ق.م. وفي سنة 842 ق.م. هزم حزائيل ملك دمشق وملوك صور وصيدا وقد أرسل ياهو ملك بني إسرائيل الجزية لشلمنأسر حينئذ.( 2 ) شلمنآسر الخامس، خليفة تغلب فلاسر. حكم من 728-722 ق.م. وتبعه سرجون. ويذكر يسويفوس المؤرخ اليهودي أن شلمنآسر هذا حكم فينيقية عام 725ق.م. وحارب بني إسرائيل مرة تلو مرة حتى انتصر عليهم، وأخذ منهم الجزية ( 2 مل 17: 3 ). ولكن هوشع تحالف مع سَوَا ملك مصر. فصعد إليه شلمنآسر الذي اسر هوشع وحاصر السامرة، وأخذها بعد حصار دام ثلاث سنين، ونقل أهاها إلى ما بين النهرين وأتمّ سرجون خليفته ذلك ( 2 مل 17: 4 و 5 ) انظر (( سرجون )).'),
(2033, 'شَلَمْيَا وشَلْمِيَا', 'اسم عبري معناه (( يهوه يكافئ )) وهو:( 1 ) أحد البوابين في الهيكل، في عهد داود ( 1 أخبار 26: 14 ) انظر (( مشلميا )).( 2 ) ابن كوشي عاصر عهد الملك يهوياكين ( ار 36: 14 ).( 3 ) ابن عبدئيل ( ار 36: 26 ).( 4 ) ابن حنانيا ( ار 37: 13 ). أبو ناظر الحراس الذي قبض على ارميا النبي.( 5 ) أب يوخل ( ار 37: 3 و 38: 1 )، أحد الذين طلبوا قتل ارميا، وألقوه في الجب( 6 و 7 )  من نسل باني، شخصان طلب منهما عزرا إخراج زوجتيهما الغريبتين ( عز 10: 39 و 40 ).( 8 )  أبو حنينا الذي عاون في بناء سور أورشليم ( نح 3: 30 ).( 9 )  أحد ثلاثة كهنة، عَينهم نحميا على الخزائن، لتوزيع العشور على اللاويين ( نح 13: 13 ).'),
(2034, 'شَلوموت', 'انظر (( شلوميث )).'),
(2035, 'شَلومي', 'اسم عبري معناه (( في سلام-كمال )) وهو أبو اخيهود رئيس في أشير. عاصر أيام موسى في البرية ( عد 34: 27 ).'),
(2036, 'شَلوميئيل', 'اسم عبري معناه (( الله سلام )) رئيس شمعون في البرية ( عد 1: 6 و 2: 12 و 7: 36 و 41 و 10: 19 ).'),
(2037, 'شَلُومِية', 'انظر (( شلوميث )).'),
(2038, 'شَلُوميث', 'مؤنث  (( شلومي )) وهي:( 10 ) بنت دبري من سبط دان. وأم الشخص الذي رجم في البرية لتجديفه. وابوه مصري وقد ورد الاسم هنا بلفظ (( شلومية )) ( لا 24: 11 ).( 11 ) لاوي من عشيرة قهات ( 1 أخبار 23: 18 ). ويسمى أيضاً شلوموث في 1 أخبار 24: 22.( 12 ) لاوي عينه داود الملك مع اخوته حراساً على الخزائن في بيت الله ( 1 أخبار 26: 25 و 26 ).( 13 ) ابن أو بنت رحبعام ( 2 أخبار 11: 20 ).( 14 ) لاوي من بني شمعي، من عشيرة جرشون ( 1 أخبار 23: 9 ). قد ورد الاسم أيضاً بصيغة (( شلوموث )).( 15 ) ابن يوسفيا ( عز 8: 10 ) عاد مع اولاده من السبي البابلي مع عزرا.( 16 ) ابنة زربابل ( 1 أخبار 3: 19 ).'),
(2039, 'شَليشَة', 'اسم عبري معناه (( الطرف الثالث )) مقاطعة في جبل افرايم، حيث ذهب شاول بحثاُ عن الحمير الضالة ( 1 صم 9: 4 ). وربما تكون شمال شرقي اللد على المنحدر الغربي من الجبل.'),
(2041, 'شُماتي', 'إحدى عشائر قرية يعاريم ( 1 أخبار 2: 53 ).'),
(2042, 'شَمَاع', 'اسم عبري معناه (( خبر )) وهي قرية كانت تقع في نصيب يهوذا ( يش 15: 26 ) وهي تل سعوة في النقب.'),
(2043, 'شَمَاعة', 'اسم عبري معناه (( خبر أو سمعة )). وهو بنياميني من جبعة. اجتمع بداود في صقلع ( 2 أخبار 12: 3 ).'),
(2044, 'شِمْآم', 'بنياميني، وهو ابن مقلوث. كان مقيماً في أورشليم ( 1 أخبار 9: 38 ). وقد دعى نفسه الشخص (( شماة )) في 1 أخبار 8: 32.'),
(2045, 'شمْأة', 'انظر (( شمآم )).'),
(2046, 'شَمْئِيبر', 'ملك صبويم، واحد المتحالفين من مدن السهول، الذين غزاهم كدلعور ( تك 14: 2 و 8 و 10 ).'),
(2047, 'شَمْجَر', 'اسم من اصل حوري ومعناه (( الإله شميك أعطى )). قاض ن قضاة إسرائيل. قتل ستمئة فلسطيني بمنساس البقر. وكان ذلك قبل أيام دبورة ( قض 3: 31 ).'),
(2048, 'شَمْحُوث', 'اسم عبري معناه (( دمار )) وهو رئيس فرقة في عهد داود الملك ( 1 أخبار 27: 8 ).'),
(2049, 'شِمْرَة', 'اسم عبري معناه (( المراقب )) أو (( الحارس )) بنياميني، ابن شمعي ( 1 أخبار 8: 21 ).'),
(2050, 'شِمْرُون', 'اسم عبري معناه (( مراقب )) أو (( حارس )) وهو:( 1 ) ابن يساكر الرابع، وهو مؤسس عشيرة الشمرونيين ( تك 46: 13 و عد 26: 24 و 1 أخبار 7: 1 ).( 2 ) مدينة كنعانية على حدود زبولون. كان ملكها يابين ( يش 11: 1 و 19: 15 ).ويرجح أنها نفس المدينة التي دعيت شمرون مرأون ( يش 12: 20 ). ويرجح أيضاً أن هذا الاسم هو الاسم الكامل للمدينة أما شمرون وحدها فهي مختصر الاسم. وربما كانت هي قرية السميرية لحالية التي تقع على بعد ساعة ونصف الساعة من عكا على طريق صور.'),
(2051, 'شمْرون مرأُون', 'انظر (( شمرون )).'),
(2052, 'شمْرونيون', 'انظر (( شمرون )).'),
(2053, 'شِمْرِي', 'اسم عبري معناه (( يهوه يحرس ويحفظ )) وهو:( 1 ) لاوي من عشيرة مراري في أيام داود ( 1 أخبار 26: 10 ).( 2 ) شمعوني ( 1 أخبار 4: 37 ).( 3 ) أب أحد حراس داود ( 1 أخبار 11: 45 ).( 4 ) لاوي عاش في أيام حزقيا الملك ( 2 أخيار 29: 13 ).'),
(2054, 'شَمَريْا', 'اسم عبري معناه (( يهوه يحفظ )) وهو:( 1 ) أحد جنود داود في صقلغ ( 1 أخبار 12: 5 ).( 2 و 3 )  اثنان من الذين اخذوا نساء غريبة، وأمرهم عزرا بإبعادهن ( عز 10: 32 و41 ).( 4 ) ابن رحبعام ( 2 أخبار 11: 19 ).'),
(2055, 'شِمْريت', 'اسم موآبي معناه (( ساهر أو متيقظ )) وهو اسم امرأة موآبية كانت أم أحد قتلة الملك يوآش ( 2 أخبار 24: 26 ). وتسمى أيضاً شومير ( 2 مل 12: 21 ).'),
(2056, 'شَمْس', 'مركز الأجرام السماوية، خلقها الله ( تك 1: 16 ) نوراً للنهار. وهو حافظها ( ار 31: 35 ومت 5: 45 ). وهي تساعد النبات على النمو ( تث 33: 14 و 2 صم 23: 4 ). وقد استخدم اسم الشمس في التشبيهات الشعرية. فخروج العروس من غرفتها كشروقها ( مز 19: 4-6 ). وإقبال الموت، أو تحقيق خسارة فادحة يشبّه بغروبها في قلب النهار ( ار 15: 9 وعا 8: 9 ومي 3: 6 ).ظن الشعراء قديماً أن لها مسكناً تدخل إليه وتخرج منه ( حب 3: 10 و 11 ومز 19: 4-6 ).ويشار إلى طهارتها في نش 6: 10. ويرمز بزيادة لمعانها بعد شروقها عن تقدم الرجل الصالح نحو الكمال ( ام 4: 18 ).وكانت الشمس معبود الشعوب التي عاصرت العبرانيين كالبابليين والآشوريين باسم (( شَمَش )) والمصريين باسم (( رع )). وقد ادخل منسى الملك عبادة الشمس في يهوذا على نظام عبادتها في آشور ( 2 مل 21: 3 و 5 ). ودشَن هو وخليفته آمون خيلا وعجلات للشمس، وأحرقوا لها بخوراً على السطوح ( 2 مل 23: 5 و 11 ).وقد أوقف إجابة لطلب يشوع سيرها في جبعون لتسهيل غلبة العبرانيين على الأعداء. والآيات المقتطفة في هذا المضمار مأخوذة من سفر ياشر ( يش 10: 12-14 ). وأثبت رجوع ظل الشمس عن درجات آحاز وعَد الله بشفاء الملك حزقيا ( 2 ملو 20: 11 واش 38: 8 ). ودل كسوفها عند صلب المسيح على تسلطه عليها كبارئها وربها ( مت 27: 45 ومر 15: 33 ولو 23: 44 ).\nذكرت ضربة الشمس في ( مز 121: 6 و 2 مل 4: 19 ).'),
(2057, 'شَماس', 'لفظ اللمة اليونانية (( ديا كونس )) جمعها (( ديا كونوي )) ومعناها خادم. والكلمة (( ديا كونس )) هي نفسها المترجمة (( خادم )) في مت 2: 26 و 23: 4 ). وهي تستخدم للمسيح ( رو 15: 8 ) ولخدمة الدين ( 1 كو 3: 5 وكو 4: 7 ). إلا أنها اختصت بالسبعة رجال، المشهود لهم، الممثلون من الروح القدس والحكمة، الذين تعينوا لخدمة الموائد. وبذلك صار الرسل أحراراً وخصصوا ذواتهم كلّية للصلاة وخدمة الكلمة.وإذ تذمر اليونانيون على غيرهم لسبب المحاباة في توزيع الحسنات بين اراملهم، انتخب الشمامسة السبعة من اليونانيين، وافرزوا بالصلاة ووضع أيدي الرسل ( اع 6: 1-6 ).وخدمة الشماس خدمة روحية، تظهر في 1 تي 3: 8-13. وكان للشماس أيضاً أن يشهد للمسيح بالوعظ أو بأية طريقة أخرى. فإن استفانوس وفيلبس كانا شماسين وواعظين بإنجيل المسيح. وفي كنيسة فيلبي، كان عدد ليس بقليل من الشمامسة يشاركون الأساقفة في خدمة الرب ( في 1: 1 ).أما الشمّاسات، فكان عملهنّ بين السيدات عمل الشمامسة بين الرجال. وهذه خدمة ترجع إلى عصر مبكر جداً في الكنيسة الرسولية. ونرى من رو 16: 1 أن فيبي كانت شماسة الكنيسة مما يدل على أن وظيفة شماسة أصبحت من الرتب الكنسية، ويسجل (( بليني )) في رسائله إلى الإمبراطور تراجان، حماً عن سيدتين متقدمتين في الأيام، كانت خادمتين في كنيسة بيثينية. وترجع رسالة بليني هذه إلى حوالي عام 100 ب.م تقريباً.'),
(2058, 'شِمْشَاي', 'اسم عبري معناه (( مشمس )) وهو كاتب رحوم، صاحب القضاء، وكان ممن انضم إلى الذين عارضوا في بناء الهيكل الثاني أيام زربابل ( عز 4: 8 و 9 و 17 و 23 ).'),
(2059, 'شَمْشَرَاي', 'بنياميني، من أبناء يروحام ( 1 أخبار 8: 26 ).'),
(2060, 'شَمْشون', 'اسم عبري معناه (( شمس )) وهو ابن منوح. وكان قاضاً لبني إسرائيل قضى مدة عشرين سنة ( قض 13: 2-24 ). انظر (( منوح )) وكان معروفاً بقوته العجيبة. وعند اكتمال شبابه تزوج بإمرأة من تمنة، وهي مدينة فلسطينية، وكان ذلك بدون رضى والديه، وضد الناموس ( خر 34: 16 وتث 7: 3 )، وكان في ذهابه إلى تلك المدينة أنه قابل اسداً وقتله ( قض 14: 5-9 ). ثم وجد بعد ذلك في جثة الأسد دبراً من النحل، فأكل من العسل، وأعطى والديه. فعندما كان في الاحتفال بزفافه، حاجى الفلسطينيين أحجية مؤسسة على هذه الحادثة، ووعد بهدية ثمينة لمن يحلها في سبعة أيام الوليمة. واشترط عليهم أن يقدموا مثل هذه الهدية له إن لم يقدروا على حل الأحجية. ولما لم يقدروا، استنجدوا بزوجته التي ألحت عليه، حتى عرفت منه الجواب. وإذ كان الفلسطينيون قد هدّدوها هي وأهلها تهديداً شديداً، أخبرتهم بالحل وعرف شمشون ما حدث، وقدم لهم الهدية، ولكنه قدمها على حساب ثلاثين من بني شعبهم ومواطنيهم الذين قتلهم، ثم هجر امرأته التي خانته.وبعد مضي وقت، عاد إلى تمنة ليصالح امرأته، فوجد أنها كانت قد تزوجت بشخص آخر ورفضوا أن يسمحوا له بأن يراها. فأمسك ثلاث مئة بنات آوى، ووضع مشعلاً بين كل ذنبين، ثم أضرم المشاعل ناراً، واطلق بنات آوى بين مزارع الفلسطينيين وكرومهم، وأحرقها. وعقاباً لشمشون، أقتص الفلسطينيون من امرأته، فأحرقوها هي وأهلها بالنار. ولما رأى شمشون ذلك العمل الوحشي، قتل منهم عدداً كبيراً ( قض 15: 1-8 ).ثم لجأ شمشون على صخرة عظيمة في أرض يهوذا. فأتى إليه الفلسطينيون للانتقام منه، وضربوا كل البلاد حواليه، فجاء ثلاثة آلاف من رجال يهوذا إلى شمشون ولاموه لأنه أثار عليهم الفلسطينيين. فأذن لهم أن يوثقوه ويسلموه إلى أيديهم. ففرحوا بأن يأخذوه ويأسروه، وإذا به يحل الوثاق من ذراعيه، ويقبض على فك حمار، ويضرب به ألفاً من الفلسطينيين. وفي ذلك الوقت عطش، ففتح لله ينبوع ماء في الكفة، فشرب وانتعش.وبعد ذلك ذهب شمشون إلى غزّة، حيث تعرف بدليلة، امرأة بغي. وقد احتالت دليلة عليه حتى كشفت سر قوته العظيمة، وعرفت أنه كان في شعره، لأنه كان نذير الرب من بطن أمه ( قض 16: 17 ). فأتى الفلسطينيون عليه وهو نائم، وجزوا شعره، وأوثقوه بسلاسل من نحاس، وقلعوا عينيه، ثم أخذوه إلى غزّة ووضعوه في السجن.وإذ كانوا قد أسروا عدوهم القوي، اجتمع أقطاب الفلسطينيون للفرح. وطلبوا شمشون ليسخروا منه. وكان في وسط البيت عمودان كبيران. فقاده صبي إلى ما بينهما، وكان قد اجتمع في البيت جمهور من شرفاء الفلسطينيين، ووقف على السطح نحو 3000، ليروا شمشون يلعب. فطلب شمشون أذناً بأن يستند على العمودين، وانحنى بكل قوته، فزعزعهما. وسقط البيت عليه وعلى من فيه، فماتوا جميعاً.وقد حُسب شمشون ضمن سحابة المؤمنين ( عب 11: 32 و 33 ). ولم يكن له من قوة العقل مثلما كان له من قوة الجسم. والظاهر أنه لم يعلم، فكانت عواطفه الجامحة مستولية عليه. ولم يكن يعتبر مقامه كقض لبني إسرائيل، إلا أنه لم يخلُ من حب لوطن.'),
(2061, 'شمْع', 'مادة معروفة. سهلة الذوبان بالحرارة ( مز 22: 14 و 97: 5 ومي 1: 4 ).'),
(2063, 'شِمْعَا', 'انظر (( شِمعي )).'),
(2064, 'شِمْعَى', '( بفتح العين ) اسم عبري معناه (( الله يسمع )) وهو:( 1 ) لاوي من عائلة مراري ( 1 أخبار 6: 30 ).( 2 ) لاوي من عائلة جرشوم ( 1 أخبار 6: 39-43 ).( 3 ) أخو الملك داود ( 2 صم 13: 3 ). ورد بلفظ (( شِمعا )) في 1 أخبار 20: 7، ودعي شمّة في 1 صم 16: 9 و 17: 13 انظر (( شَمّة )).( 4 ) ابن داود من بثشوع ( 1 أخبار 14: 4 ). انظر (( شمُّوع )).\n\n/p>'),
(2065, 'شَمْعَاتِيم', 'اسم سامي معناه (( خبر )). وهي عمونية، أم أحد قاتلي الملك يوشيا ( 2 مل 12: 21 ).'),
(2066, 'شمعة', 'اسم معناه (خبر) وهي عمونية، ام احد قاتلي الملك يوشيا (2 مل 12: 21).'),
(2067, 'شِمْعُون', 'اسم عبراني معناه (( سماع )).( 1 ) وهو ابن يعقوب من ليئة ( تك 29: 33 ). وقد غضب يعقوب لتصرف شمعون ولاوي في أمر الشكيميين، في دفاعهما عن دينة أختهما ( انظر (( دينة )) ) تك 34، ولهذا كانت النبوة عن نسله أن يكون مشتتاً موزعاً في كل بني إسرائيل. وقد قصد موسى ألاَّ يبارك شمعون ( تث 33 ).( 2 ) سبط شِمعون، من شمعون بن يعقوب. وكان عند دخول بني إسرائيل أرض كنعان أن كان شمعون أصغر الأسباط، ولم يكن له في ذلك الوقت سوى 200، 22 رجل مسلّح، بينما كان عددهم في برية سيناء 300، 59 ( عد 1: 22 و 26: 12-14 ). ولم يحصلوا على الميراث، سوى قطعة من نصيب يهوذا ( يش 19: 1 ). وسكن شمعون تسع عشر مدينة في أرض يهوذا، أكثرها جنوبي دان على شاطئ البحر ( يش 19: 2-9 ). وبعد ذلك استولى يهوذا على بعض مدنه كحرمة وبئر سبع وأخيراً صقلغ. وفي أيام حزقيا ملكوا بعض المواقع في جبل سعير ( 1 أخبار 4: 42 ) وبعد انقسام الأسباط، يظهر أن أملاك هذا السبط تغيرت إذ التصق بعض الأسباط بالأسباط العشرة. بينما اشترك الباقون في الإصلاح أيام آسيا ويوآش ( 2 أخبار 15: 9 و 34: 6 ).( 1 ) واحد من سلسلة نسب المسيح، عاش بعد داود وقبل زربابل ( لو 3: 30 ).( 2 ) أحد الذين تزوجوا بنساء غريبة ( عز 10: 31 ).'),
(2068, 'شِمْعونيون', 'سبط شمعون ( عد 25: 14 و 26: 14 ). ولم يشتهر أحد من هذا السبط إلا يهوديت، إحدى النساء المشهورات في الابوكرفا. ويقول تقليد يهودي إن هذا السبط أُلحق باللاويين في مهنة التعليم.'),
(2069, 'شِمْعِي', '( بكسر العين ) اسم عبري معناه (( يهوه يسمع )).( 1 ) لاوي، ابن جرشوم وحفيد لاوي. أسس عشيرة الشمعيين ( خر 6: 17 وعد 3: 18 و 1 أخبار 23: 7 و10 وزك 12: 13 ).( 2 ) لاوي من عائلة جرشون، رئيس آباء للعدان ( 1 أخبار 23: 9 ).( 3 ) لاوي، من عائلة جرشوم ( 1 أخبار 6: 42 ).( 4 ) لاوي، ابن هيمان. عاصر حزقيا، وأعانه في تطهيره الهيكل ( 2 أخبار 29: 14 ).( 5 ) لاوي من نسل مراري ( 1 أخبار 6: 39 ).( 6 ) بنياميني من بيت شاول من سكان بحوريم. عندما ملك ابشالوم أورشليم وكان داود هارباً ( 2 صم 16: 5-13 )، رآه شمعي ولعنه. ولم يرد داود أن يبطش به. ولما عاد داود منصراً طلب شمعي المغفرة منه فعفا عنه، ولما داود منصراً طلب شمعي المغفرة منه فعفا عنه، واقسم آلا يميته  (  2صم 19: 23 ). ولكن سليمان فيما بعد أمره ألا يبرح أورشليم وإذ خالف الأمر وبارحها، أمر سليمان بقتله ( 1 مل 2: 46 ).( 7 ) بنياميني كان من أبطال داود وظل من ضباط سليمان. وابن ايلا ( 1 مل 1: 8 و 4: 18 ).( 8 ) بنياميني رئيس بيت في عجلون ( 1 أخبار 8: 21 ). دعي شمع في ع 13.( 9 ) أخو زربابل ( 1 أخبار 3: 19 ).( 10 ) شمعوني، كان له 16 ولداً و 6 بنات ( 1 أخبار 4: 26 و 27 ).( 11 ) رأوبيني ( 1 أخبار 5: 4 ).( 12 ) رئيس الفرقة العاشرة من ذوي آلات الطرب في خدمة الهيكل ( 1 أخبار 25: 17 ).( 13 ) حارس لكرم داود، ويدعى شمعي الرامي ( 1 أخبار 27: 27 ).( 14 ) أحد سلفاء مردخاي ( اس 2: 5 ).( 15 ) لاوي في أيام حزقيا أقيم رئيساً على ذخائر الهيكل ( 2 أخبار 31: 12 و 13 ). وربما هو نفس الشخص الذي ورد ذكره في ( 4 ) أعلاه.( 16 و 17 و 18 ) ثلاثة أشخاص، واحد لاوي، والثاني ابن جشوم، والثالث ابن باني. وقد طلب إليهم عزرا إخراج زوجاتهم الغريبات ( عز 10: 23 و 33 و 38 ).'),
(2070, 'شَِمْعيا وشَمعْيا', 'معناهما ( يهوه يسمع )).( 1 ) شمعوني ( 1 أخبار 4: 37 ).( 2 ) رأوبيني ابن يوئيل ( 1 أخبار 5: 4 ).( 3 ) لاوي، رئيس على بني اليصافان وعددهم 200، وهم الذين ساهموا في نقل تابوت عهد الرب من بيت عوبيد إلى جبل صهيون ( 1 أخبار 15: 8-11 ).( 4 ) لاوي، ابن نثنئيل، كان كاتباً في عهد داود. وهو الذي كتب أسماء إلى 24 فرقة من الكهنة ( 1 أخبار 24: 6 ).( 5 ) الابن البكر لعوبيد ادوم، الذي كان مع ابنائه بوابين للخيمة ( 1 أخبار 26: 4 و 6 و 7 ).( 6 ) نبي في أيام رحبعام، ومؤرخ لملكه. نصح الملك بعدم مهاجمة الأسباط العشرة عند انقسام المملكة، وعندما غزا شيشق البلاد أعلن النبي أن الغزو كان عقاباُ على خطيئة يهوذا ( 1 مل 12: 22 و 2 أخبار 11: 2 و 12: 5 و 15 ).( 7 ) لاوي أرسله يهوشافاط ليعلم الشعب ( 2 أخبار 17: 8 ).( 8 ) لاوي، ابن يدوثون عاون في تطهير الهيكل في عهد حزقيا الملك ( 2 أخبار 29: 14 و 15 ) وربما كان هو نفس الشخص المذكور في 1 أخبار 9: 16. وربما كان هو أيضاً المدعو شمّوع في نح 11: 17 انظر (( شمّوع )).( 9 ) لاوي في عهد حزقيا الملك. كان عليه مع آخرين أن يوزعوا العشور على اللاويين في مدنهم ( 2 أخبار 31: 15 ).( 10 ) لاوي، رئيس اللاويين في عهد يوشيا، قدم للفصح ذبائح وفيرة ( 2 أخبار 35: 9 ).( 11 ) أبو اوريا النبي من قرية يعاريم ( ار 26: 20 ).( 12 ) أبو دلايا، أمير في عهد يهوياقيم ( ار 36: 12 ).( 13 ) النحلامي، نبي كاذب ظهر في أيام السبي البالي تنبأ بسرعة العودة إلى أورشليم. ولكن ارميا تنبأ بتأخر العودة. لهذا تنبأ النحلامي ضد ارميا، فتنبأ ارميا ضده ( ار 29: 24 و 31 و 32 ).( 14 ) رئيس كهنة عاد مع زربابل من السبي البابلي ( نح 12: 6 و 7 ). ثم صار هذا الاسم فيما بعد اسم عشيرة ( ع 18 ).( 15 ) من بني ادونيقام. عاد مع عزرا من السبي ( عز 8: 13 ).( 16 ) رئيس ممن أرسلهم عزرا إلى إِدُّو في بابل ليأتوا باللاويين للخدمة في الهيكل في أورشليم ( عز 8: 16 ).( 17 و 18 ) كاهن من نسل حاريم ورجل آخر من بني حاريم تزوجا من النساء الغريبات. وطالبهما عزرا بإخراج زوجتيهما ( عز 10: 21 و 31 ).( 19 ) رجل من سلالة زربابل ( 1 أخبار 3: 2 ). انظر (( شكينا )).( 20 ) حارس الباب الشرقي، ويحتمل أنه لاوي، بني جزءاً من الحائط في أيام نحميا ( نح 3: 29 ). انظر (( شكينا )).( 21 ) لاوي من نسل بورّني. كان مشرفاً على العمل الخارجي لبيت الله في أيام نحميا ( نح 11: 15 و 1 أخبار 9: 14 ).( 22 ) ابن دلايا بن مهيطبئيل. خائن أستأجره طوبيا وسنبلط لإرهاب نحميا بنوبات كاذبة ( نح 6: 10-13 ).( 23 ) كاهن ختم العهد نيابة عن أسرته ( نح 10: 8 و 12: 6 و 18 و 35 ).( 24 ) أحد رؤساء اليهود، اشترك في تدشين الهيكل ( نح 12: 34 ).( 25 ) لاوي من نسب آساف ( نح 12: 35 ).( 26 ) أحد المغنيين عند تدشين السور ( نح 12: 36 ).( 27 ) كان كاهناً يضرب البوق عند تدشين الهيكل ( نح 12: 42 ).'),
(2071, 'شَمْعِيون', 'انظر (( شِمعي )).'),
(2072, 'شَمَّا', 'اسم عبري معناه (( دمار )) ابن صوفج رئيس في اشير ( 1 أخبار 7: 37 ).'),
(2073, 'شَمَّاي', 'اسم عبري معناه (( يهوة يسمع )) وهو:( 1 ) ابن اونام من أسرة يرحمئيل من سبط يهوذا ( 1 أخبار 2: 28 ).( 2 ) ابن راقم من بيت كالب من سبط يهوذا ( 1 أخبار 2: 44 ).( 3 ) ابن عزرة في نسب يهوذا ( 1 أخبار 4: 17 ).'),
(2074, 'شَمْلاي', 'انظر (( سلماي )).'),
(2075, 'شَمَّة', 'اسم عبري معناه (( خراب )) وهو:( 1 ) رجل من نسل عيسو واسماعيل ( تك 36: 3 و 4 و 13 و 17 ). صار رئيساً في ادوم ( ع 17 ).( 2 ) ابن يسى الثالث ( 1 صم 16: 9 و17: 13 ). ويسمى أيضاً شِمعي ( 2 صم 21: 21 ).( 3 ) أحد أبطال داود من الرتبة الأولى. ابن اجي الهراري ( 2 صم 23: 11 ). ودعى شمنة الهراري ( ع33 ).( 4 ) الحرودي أحد أبطال داود ( 2 صم 23: 25 ). وجاء مرة في صيفة الجمع شمّوت الهروري ( 1 أخبار 11: 27 ). وربما كان هو شمحوت اليزراحي ( 1 أخبار 27: 8 ).'),
(2076, 'شَمُوت', 'انظر (( شمّة )) 4.'),
(2077, 'شَمُّوع', 'اسم عبري معناه (( مسموع ( من الله )) ) وهو:( 1 ) جاسوس من رأوبين أرسل للتجسس على أرض كنعان ( عد 13: 4 ).( 2 ) ابن لداود من بثشوع، ولد له في أورشليم ( 2 صم 5: 14 و 1 أخبار 14: 4 ). ويسمى أيضاً شمعي ( 1 أخبار 3: 5 ).( 3 ) لاوي ( نح 11: 17 ). انظر (( شمعيا )) 8.( 4 ) كاهن في أيام يوياقيم ( نح 12: 18 ).'),
(2078, 'شَمُوئيل', 'اسم عبري معناه (( اسمه الله )). وهذا الاسم في الأصل العبري هو نفسه الذي ورد في الترجمات العربية باسم (( صموئيل )).( 1 ) رئيس من شمعون ناب عن سبطه عند تقسيم كنعان ( عد 34: 20 ).( 2 ) رئيس من يساكر من عائلة تولع ( 1 أخبار 7: 2 ).'),
(2079, 'شَمِيداع', 'اسم عبري معناه (( سيعرف الاسم )). رجل من منسى. رئيس عشيرة الشميداعيين ( عد 26: 32 ويش 17: 2 و 1 أخبار 7: 19 ).'),
(2080, 'شِميداعيون', 'انظر (( شميداع )).'),
(2081, 'شَمِيراموث', 'اسم عبري معناه (( اسم مرتفعات )) وهو:( 1 ) لاوي موسيقار في أيام داود ( 1 أخبار 15: 18 و 20 و 16: 5 ).( 2 ) لاوي أرسله يهوشافاط لتعليم الشعب ( 2 أخبار 17: 8 ).'),
(2082, 'شِنآب', 'اسم اكّادي معناه (( الاله سن هو الاب )). وهو ملك ادمة في عمق السديم، عندما هجم كدرلعومر على أرض سدوم ( تك 14: 2 ).'),
(2083, 'شِنْاصَّر', 'اسم اكادي معناه (( احمني يا سن ( إله القمر )  )). وهو رجل من نسل يكينا ( 1 أخبار 3: 18 ).'),
(2084, 'شِنْعار', 'وهي المنطقة التي تمتد بين بابل وارك وكلنة واكد، وكانت ضمن مملكة نمرود ( تك 10: 10 ) وفي هذه المنطقة أقيم برج بابل ( تك 11: 2 ) وكان امرافل ملك شنعار معاصراً لإبراهيم ( تك 14: 1 ) وقد أخذ بعض اليهود في السبي إلى هذا المكان ( اش 11: 11 وزكريا 5: 11 ).'),
(2085, 'أُشْنان', '( ار 2: 22 ومل 3: 2 ). ينمو في البلاد المقدسة عدة أنواع من نباتات قلوية كحشيشة القلي وهي الاشنان. وتكثر في نواحي دمشق وجرود وبحر لوط وعلى الشواطئ البحرية. يحرقها العرب ويستخرجون مقداراً كبيراً من النطرون مع الزيت والشحم تولد الصابون المعروف المستعمل للتنظيف وقد وردت في بعض الترجمات باسم (( صابون )) وأهل جرود يسحقون الاشنان بدون حرق في النار، ويبيعونه لفلاحي تلك النواحي، فيستعملونه لغسل الثياب. ولا يخفى أن عمل الصابون هو أحد الأعمال المهمة في سوريا وفلسطين.'),
(2086, 'شَاهد-شهادة', 'قضت الشريعة الموسوسية بشهادة شاهدين أو ثلاثة لتثبيت أمر ما ( تث 17: 6 و 7 ). وكانوا إذا حكموا على أحد بالرجم، رجمه الشهود أولاً. انظر (( رجم )) فإذا شهد شاهد زُوراً عوقب بنفس القصاص الذي كان يعاقب به المتهم.أما شهادة الروح مع أرواحنا ( رو 8: 16 ) فيراد بها الشعور قل أو كثر، بفعل الروح في عقولنا منيراً أذهاننا، ودافعاً إياناً إلى عمل مشيئة الله.أما الشاهد الأمين في السماء ( مز 89: 37 ). فيظن أن المراد به القمر ( راجع 33: 20 ) الذي يسود على الليل وسيبقى ما دام قائماً كل الأيام ( تك 22 ).ويشير يوحنا مراراً عديدة إلى الإنجيل كشاهدة ( 1 يو 5: 9 ). ويسمي المسيح نفسه الشاهد الأمين الصادق ( رو 1: 5 و 3: 14 ). وذلك ليس لمجد الآب وكماله فقط، بل لإرساليته ( أي المسيح ) الإلهية ولتعميم ملكوته.'),
(2087, 'مَشْهَد', 'مسرح ( اع 19: 29 و 31 ). حيث كانوا يجتمعون لغايات مختلفة كإجراء القضاء والقيام بالأعمال التجارية والعامة، والتمثيل. انظر صورة المسرح تحت (( افسس )).'),
(2088, 'شَهيد-شهداء', '( اع 22: 20 ) إنسان يقدم حياته شهادة للحق. ولم ترد لفظة شهيد أو شهداء في العهد الجديد سوى في هذه الآية وفي رؤ 2: 13 و 17: 6. أما لفظة شهيد فجاءت في اي 16: 19 وهي تفيد معنى الشاهد.');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(2089, 'شَهر-شهور', 'مدة دوران القمر حول الأرض. وهو جزء من اثني عشر جزءاً من السنة. وكان العبرانيون القدماء يعبرّون عن الشهور بالأعداد، فيسمونها الشهر الأول والثاني والثالث والرابع الخ. وقد بدأوا يسمونها بأسمائها الخاصة في عصر مبكر من تاريخهم. فقد ذكرت أسماء أربعة شهور قبل سبي بابل وهي: ابيب ( خر 13: 4 )، وزيو ( مل 6: 37 )، وايثانيم ( 1 مل 8: 2 )، وبول ( 1 مل 6: 38 ).وكان لهم نظام للسنة المدنية أو الهلالية تبدأ بهلال تشرين الأول، وجربوا عليها في الاشغال المدنية والزراعية فقط. وقد كشف التنقيب في جاز عن تقويم مكتوب على الحجر وفيه اثنا عشر شهراً ويرجع إلى عصر سليمان. أما بدء السنة المقدسة فهو هلال نيسان لأنهم خرجوا من مصر في 15 من هذا الشهر واستخدموا هذا التقويم في حساب أعيادهم وما شابه ذلك. وهذا النظام هو المستخدم في الأنبياء. ومن جميل الظروف أن يكون يوم الفصح من سنة هو يوم إشراق البدر الكامل. أما الشهور الهلالية فقد عرفت في عصر مبكر ( تك 1: 14 ومز 104: 19 ). وإشراق الهلال يرتبط بتقديم الذبائح ليهوه ( عد 10: 10 و 28: 11-14 و 2 أخبار 2: 4 ) علماً بأن الشهر كان 30 يوماً قبل الطوفان وبعده ( تك 7: 11 و 24 و 8: 3 و 4 ).وفي الجدول التالي ترتيب الشهور العبرانية المدنية بإزاء الشهور المقدسة، ونسبتها إلى شهور سنتنا الحالية:الشهور المدنية 	الشهور المقدسة		             يبدأ بهلال7	   1 نيسان ( نح 2: 1 ) أو بيب ( خر 13: 4 )    آذار ونيسان، مارس-ابريل8       2 زيو ( 1 مل 6: 1 )		        نيسان أو أيار، ابريل- مايو9      3 سيوان ( اس 8: 9 ) 		        أيار أو حزيران، مايو-يونيو10    4 تموز ( لم يذكر في الكتاب )	                حزيران أو تموز، يونيو-يوليو11    5 آب ( لم يذكر في الكتاب )	                تموز أو آب، يوليو-اغسطس12	  6 أيلول ( نح 6: 15 )                         آب أو أيلول، اغسطس-سبتمبر1     7 ايثانيم ( 1مل 8: 2 ) أو تشري      أيلول أو تشرين الأول، سبتمبر-اكتوبر2     8 بول ( 1 مل 8: 2 )                 تشرين الأول أو الثاني، اكتوبر-نوفمبر3     9 كسلو ( نح 1: 1 وزك 7: 1 )      تشرين الثاني أو كانون الأول، نوفمبر-ديسمبر4     10 طيبيت ( اس 2: 16 )             كانون الأول أو الثاني، ديسمبر- يناير5     11 شباط ( زك 1: 7 )                كانون الثاني أو شباط، يناير-فبراير6     12 آذار ( اس 3: 7 )                 شباط أو آذار، فبراير-مارسوبما أن الشهور الاثني عش القمرية لم تكن إلاّ 354 يوماً عن السنة الرومانية. ولسبب ذلك ادخل اليهود شهراً ثالث عشر، كل ثلاث سنوات سموه (( فيادار )) أو (( آذار الثاني ). وهكذا جعلوا السنة القمرية تعادل السنة الشمسية تقريباً.'),
(2090, 'اشتهى-شهوة-شهوات', 'الشهوة هي حركة النفس ونزوعها إلى ما تريد. فإن اشتهت النفس ما ليس لها الحق فيه فإن الشهوة، شهوة غير شرعية ( 1 كو 10: 6 و 1 بط 2: 11 و 4: 2 و 2 بط 2: 10 ). ورغم أن الكلمة في أصلها تعني الرغبة الملحة القوية، وكان يمكن أن تستخدم للحسن والسيء، ولكنها وردت في الغالب للاستعمال السيء. واستخدمها الكتاب المقدس للتعبير عن النوازع الحسيَّة التي تلد الخطيئة ( يع 1: 14 و 15 ).'),
(2091, 'شَوَا', 'اسم عبري معناه (( بُطل )) أو ربما كانت قريبة من الكلمة العربية (( سواء )) فيكون معناها (( شبيه )).( 1 ) ابن حصرون، من بيت كالب، من سبط يهوذا. من نسله جاء المكبينيون والجبعيون ( 1 أخبار 2: 49 ).( 2 ) كاتب في عهد الملك داود ورد اسمه في الترجمة العربية شيوا ( 2 صم 20: 25 ) انظر (( شيوا )).'),
(2092, 'شَوَى', 'اسم عبري معناه (( سهل )) وهو واد سمي فيما بعد وادي الملك، رب شاليم، حيث قابل ملك سدوم إبراهيم بعد هزيمة كدرلعومر ( تك 14: 17 و 18 ). وفيها بنى ابشالوم نصباً تذكارياً له ( 2 صم 18: 18 ) وهو-كما يذكر يوسيفوس-يبعد نحو ربع ميل عن مدينة أورشليم. ربما كان هو وادي الجوز الذي يقع شمالي أورشليم ويتصل بوادي قدرون.'),
(2093, 'شَوَى قِرْيَتَايم', 'اسم عبري معناه (( سهل قريتايم )) هو سهل بالقرب من مدينة قريتايم في أرض موآب ( تك 14: 5 )، التي صارت بعئذ لرأوبين ( عد 32: 37 ويش 13: 19 ). سها من قبل الايميون ( تك 14: 5 ). أما مكانها فهو غير معروف الآن تماماً.'),
(2094, 'شُوبَائيل', 'انظر (( شَبوئيل )).'),
(2095, 'شُوبَاب', 'اسم عبري معناه (( مرتد )) وهو:( 1 ) ابن بثشوع من داود، ولد لهما في أورشليم ( 2 صم 5: 14 و 1 أخبار 3: 5 و 14: 4 ).( 2 ) ابن حصرون، من بيت كالب، من سبط يهوذا ( 1 أخبار 2: 18 ).'),
(2096, 'شُوبَال', '( 1 ) ابن سعير الحوري، ويرجح أنه كان شيخاً للحوريين ( تك 36: 20 و 1 أخبار 1: 38 ).( 2 ) ابن حور، من بيت كالب، من سبط يهوذا، عائلة حصرون. من نسله جاء سكان قرية يعاريم ( 1 أخبار 2: 50 و 52 و 4: 1 و 2 و 4 ).'),
(2097, 'شُوباي', 'اسم عبري وربما كان معناه (( آخذ الاسرى )) لاوي، من عائلة البوابين، عاد أولاده مع زربابل ( عز 2: 42 ونح 7: 45 ).'),
(2098, 'شُوبك', 'اسم ارامي معناه (( ساكب )) رئيس جيش هدر عزر ملك ارام، وقد حارب داود جيشه وهزمه وقتل شوبك رئيس الجيش ( 2 صم 10: 16 و 1 أخبار 19: 16 و 18 )'),
(2099, 'شُوبي', 'اسم عموني معناه (( آخذ الاسرى )) ابن ناحاش من ربّة. قدموا طعاماً وبعض الضروريات لداود وللشعب الذي كان معه في محنايم ( 2 صم 17: 27 ).'),
(2100, 'شُوبيق', 'اسم عبري معناه (( من يسبق أو من ينتصر )). وهو احد رؤساء اليهود الذين ختموا العهد مع نحميا ( نح 10: 24 ).'),
(2101, 'شُوتالح', '( 1 ) ابن افرايم، ومؤسس عشيرة الشوتالحيين ( عد 26: 35 و 36 و 1 أخبار 7: 20 ).( 2 ) آخر من نسل افرايم ( 1 أخبار 7: 21 ).'),
(2102, 'شُتالحيون', 'انظر (( شوتالح )).'),
(2103, 'شُوح', 'اسم سامي معناه (( منخفض )). ابن إبراهيم من قطورة زوجته ( تك 25: 2 ). ومنه قبيلة عربية، التي منها بلدد الشوحي. وكانت غالباً قرب أرض عوص ( أيوب 2: 11 ).'),
(2104, 'شُوحَام', 'ابن دان، مؤسس عشيرة الشوحاميين ( عد 26: 42 ) وقد سمّي حوشيم في تك 46: 23. انظر (( حوشيم )).'),
(2105, 'شُوحَاميون', 'انظر (( شوحام )).'),
(2106, 'شَوحَة', 'اسم عبري معناه (( منخفض )) وهو رجل من نسل يهوذا ( 1 أخبار 4: 11 ).'),
(2107, 'شُوحِي', 'انظر (( شوح )).'),
(2108, 'شُور', 'اسم عبري معناه (( سور )). وهو موضع في البرية جنوب فلسطين أو على الأخص جنوب بئر لحي رئي ( تك 16: 7 و 25: 18 )، وشرق مصر ( 1 صم 15: 7 و 28: 8 ). سار فيها بنوا إسرائيل ثلاثة أيام حال عبورهم البحر الأحمر ( خر 15: 22 ). وهذه كانت تسمى أحياناً برية ايثام ( عد 33: 8 ).وقد جاء ذكر شور أولاً في قصة هرب هاجر ( تك 16: 7 ). ثم صارت بعدئذ مسكناً للإسماعيليين ( تك 25: 18 ). وسكن إبراهيم بين قادش وشور ( تك 20: 1 ).مُشير:لقب أطلق على يوسف الرامي ( لو 23: 50 ) ويدل اللقب على أنه كان عضواً في السنهدريم ويظهر أن بعض المشيرين قد صادقوا على دعوة المسيح وقاوموا بالحيلة وبالعنف اللذين استخدمها الأكثرية ضده وضد تابعيه.'),
(2109, 'شُوْشَا', 'اسم ارامي ربما كان معناه (( الشمس )) كاتب للمك داود ولسليمان من بعده ( 1 أخبار 18: 16 ). وسمى أيضاً شيشا ( 1 مل 4: 3 ) وسرايا ( 2 صم 8: 17 ) وشيوا ( 2 صم 20: 25 ).'),
(2110, 'شُوشان وشوشن', 'مدينة شهيرة معروفة عند اليونانيين بسوسا في عيلام، التي هي جزء من سوسيانا. وتسمّى في نح 1: 1 وغير ذلك شوشن القصر. أما عيلام فكان ابن سام، وكان إقليم هيلام يقع إلى شرقي أرض ما بين النهرين. وكان كدرلعومر ملكاً لإقليم عيلام ( تك 14: 1 ). وقد ذكرت أرض عيلام في نبوات اشعياء وارميا وحزقيال. ويرجّح أنها كانت في بادئ الأمر مستقلة عن بابل، وربما اقوى منها. إلا أنها خضعت أخيراً لفارس ( تك 10: 22 و 14: 1 واش 21: 2 وار 49: 34 وحز 32: 24 ).\nأما مدينة سوسا فكانت قديمة جداً، واسمها مدّون في سجلات أشور باني بال الآشوري سنة 650 ق.م التي افتتحها. وصارت شوشن للبابليين بعد انقسام المملكة الآشورية في أيام سياكسريس ونابويولسر. وفي سنة بيلشاصر الأخيرة كان دانيال في شوشن القصر، وهناك رأى الرؤيا ( دا 8: 2 ). \nوعندما افتتح كورش بابل وقعت شوشن في أيدي الفرس، وجعلها داريوس وخلفاؤه قصبة المملكة. وأسس هذا الملك القصر العظيم الموصوف في اس 1: 4-6. وكان المناخ في شوشن أكثر برودة منه في بابل. وإذ كان ماؤها عذباً لاق أن تكون عاصمة للملكة الفارسية، وفيها أقام الملوك معظم السنة، ولم يفارقوها سوى في الصيف حين كانوا ينتقلون إلى اكبتانا بين الجبال. وبعد موقعة اربيلا، وجد فيها الإسكندر الأكبر ذخائر قيمتها أكثر من اثني عشر مليون جنيهاً، وكل جواهر الملك. غير أنه إذ فضّل بابل عليها انحط شأنها، فلم تعد عاصمة المملك. وقدزال مجدها تماماً بعد القرن السابع الميلادي.\nوكشفت شوشن في أيامنا، فهي تشغل موضع القرية شوشن أو سوسن بين نهري الخرخة وأُولاي، وهما فرعا نهر، ولحد يتشعب على بعد عشرين ميلاً من شوشن. ولهذا يصح قول دانيال ( دا 8: 2 و 16 ) بأنه كان عند نهر اولاي وبين اولاي.ززز أما موقعها فهو شرقي بابل، وشمال خليج العجم. والخرائب تشغل مسافة محيطها 3 أميال، وهي أربع اكوام. وقد كشفت آثار القصر العظيم الذي بناه داريوس أبو ارتحشتا الملك. وكان طول الدار الوسطى في هذا البناء 343 قدماً وعرضها 244. ويظن أنها كانت موضع الاحتفالات العظيمة. وقد وجدت القواعد الأربع للأعمدة الرئيسية، ومواضع جميع الأعمدة الاثنين والسبعين التي كانت في القصر.\nوقد جرت في هذا القصر وما يجاوره حوادث سفر استير. ويُظن أن باب الملك حيث جلس مردخاي ( اس 2: 21 ) كان داراً طولها 100 قدم وعرضها كذلك 100 قدم في مركزها أعمدة.وقد كانت على بعد 150 قدماً من الرواق الشمالي. ويُظن أن بينهما الدار الداخلية حيث وقفت استير ( اس 5: 1 ). وكان بيت الملك والحريم وراء الدار الكبيرة إلى الجنوب، أو بين الدار الكبيرة والقلعة، ومتصلة بواسطة جسر فوق الوادي. أما دار جنة القصر فكانت امام الرواق الشرقي أو الغربي، وفيها عمل الملك احشويرش وليمة لكل الشعب سبعة أيام ( اس 1: 5 و 6 ). وقد لأثبت ما كُشف من آثار هذه المدينة التاريخ الوارد في الكتب المقدسة. وبقرب النهر في الأرض المنخفضة قبر يَظنه السكان قبر دانيال. ومن بين الأشياء التي اكتشفت عمو أسود نقشت عليه شرائع حمورابي-ملك بابل. وقد نقله العيلاميون من بابل إلى شوشن.'),
(2111, 'شُوشنيين', 'سكان شوشن الذين أتى بهم الآشوريون ليسكنوا في السامرة ( عز 4: 9 ).'),
(2112, 'شُوع', 'اسم سامي معناه (( غنى )) وهو:( 1 ) كنعاني، ابنته أخذها يهوذا زوجة له، وأولاده منها عيرا واونان وشيلة ( تك 38: 2 و 12 و 1 أخبار 2: 3 ).( 2 ) اشيرية، ابنة جابر، وقد وردت بلفظ (( شوعا )) ( 1 أخبار 7: 32 ).( 3 ) اسم قبيلة ارامية سكنت شرقي بابل ( حز 23: 23 ). وكانت هذه القبيلة في حرب دائمة مع الآشوريين وكان الآشوريون يدعونهم في سجلاتهم باسم (( سوتو )).'),
(2113, 'شُوعا', 'انظر شوع 2.'),
(2114, 'شُوعَال', 'اسم عبري معناه (( ثعلب )) وهو:( 1 ) اشيري، ابن صوفج ( 1 أخبار 7: 36 ).( 2 ) أرض شوعال، مقاطعة ذهب إليها إحدى فرق المخربين من محلة الفلسطينيين في مخماس ( 1 صم 13: 17 ). وكانت بالقرب من عفرة وإلى شمالي مخماس.'),
(2115, 'شُوفان', 'عطروت شوفان ( عد 32: 35 ). ومعناها (( أكاليل الوكر )). انظر (( عطروت )).'),
(2116, 'شَوك-شوكة', 'ذكرت أولاً في ( تك 3: 18 ) مع لفظة حسك. ولا يراد بهاتين اللفظين أنواعاً خاصة من النبات، بل كل نبات فيه شوك وحسك، يؤذي الناس ويعيق عملهم. ويشار على النباتات كثيرة الأشواك في عد 33: 55 وام 15: 19 و 22: 5 واش 5: 6 وهو 2: 6 و 10: 8 وغيرها من آيات الكتاب، لكونها تعيق الإنسان وترهقه وهي آفة له. والأرض لم تزل تنبتها حسب لعنة الله الأصلية.أما التكليل بالشوك:( مت 27: 29 )، فلم يكن جزءاً من القصاص القانوني. وإنما كان اختراع العسكر الروماني. ولا يمكن تحقيق نوع الشوك الذي استعملوه، ولكنه يرجّح أنه كان (( الفندول )) أو (( البلاّن )). وظن بعضهم أن الاكليل صنع من النبق أو السنط مع العلم بأن هذين النوعين لا ينبتان في أورشليم. ولا يخفى أنه ينبت قرب أورشليم أنواع كثيرة من الشوك كالخصوان والدردار والشنداب والقريص والعلّيق والجنبوط وغيرها. ولم تكن غاية الرومانيين تعذيب المسيح فقط بل السخرية به وبادعائه أنه ملك.'),
(2117, 'شوكة بولس في الجسد', '( 2 كو 12: 7-9 ). اختلف المختصون فيما يختص بهذه الآية فمنهم من قال أن هذه الشوكة كانت عبارة عن اضطهاد وقع على الرسول من خصم أو عدو. ومنهم من قال إن الشوكة كانت عبارة عن ميل إلى الخطيئة يعذب نفسه ويزهق روحه، غير أنه لا يقوم دليل كتابي من القرينة على شيء من ذلك. إنما ما دام الرسول يقول: (( شوكة في الجسد )) فمن الجلي أنها كانت عبارة عن مرض جسماني. ومع أنها كانت ملاكاً من الشيطان إلاّ أن الله وقوة المسيح ولا يعرف نوع هذا المرض الجسماني وربما كان ضعفاً في العينين قارن غلاطية 4: 13-15.'),
(2118, 'شُولِمّيت', 'فتاة مذكورة في نش 6: 13 من شونم التي هي شولم. وهي اليوم سولم، ويظن بعضهم أن هذا الاسم هو مؤنث (( سليمان )) في العبرية.'),
(2119, 'شُومير', 'اسم سامي معناه (( حافظ )) وهو:\n( 1 ) رجل من اشير ( 1 أخبار 7: 32 ). ويدعى أيضاً شامر ( 1 أخبار 7: 34 ).\n( 2 ) موآبي. أبو يهوزاباد الذي قتل يوآش ( 2 مل 12: 21 ). ويعى أيضاً شمريث ( 2 أخبار 24: 26 ).'),
(2120, 'شُونَم', '( يش 19: 18 ) مدينة كنعانية مقابل جبل جلبوع كانت من نصيب يساكر. حلّ الفلسطينيون فيها قبل موقعة جلبوع العظيمة. ومنها ابيشج الشونمية ( 1 مل 1: 3 ) حاضنة داود الملك. ومنها أيضاً المرأة التي أضافت اليشع ( 2 مل 4: 8 )، وتنبأ لها اليشع بالولادة. وعندما مات الولد احياه اليشع ثانية. وهي المسماة اليوم (( سولم )) على جانب جبل الدحي الجنوبي الغربي 3 أميال ونصف شمال يزرعيل، وخمسة أميال شمالي الطرف الغربي لجبل جلبوع، وعلى بعد 16 ميلاً من جبل الكرمل حيث ذهبت الشونمية لتبحث عن اليشع. وهذه القرية محاطة ببساتين وحقول وفيها عين ماء.'),
(2121, 'شُونمي', 'انظر (( شونم وشولِمّيث )).'),
(2122, 'شُوني', 'ابن جاد، ومؤسس عشيرة. ونسله شونيون ( تك 46: 16 عد 26: 15 ).'),
(2123, 'شُونيون', 'انظر (( شوني )).'),
(2124, 'شُونِيز', '( اش 28: 25 و 27 ) نبات من الفصيلة الشقيقية، يسمى الشونيز المزروع واسمه باللاتيني Nigella Sativa. وهو ذو أزهار خيمية شبيهة بنبات اليانسون. بذره يسمى حبة البركة. والشونيز لا يدرس بل يُخبط بالعصا.'),
(2125, 'شَاة', 'انظر (( غنم )).'),
(2126, 'شُوهم', 'لاوي من عائلة مراري ( 1 أخبار 24: 27 ).'),
(2127, 'شِيئُون', 'اسم عبري معناه (( هلاك أو حراب )) موضع في يساكر ( يش 19: 19 ) ويرجح أنها شاعين، ثلاثة أميال شمالي جبل تابور.'),
(2128, 'شِيت', 'اسم سامي معناه (( مُعْين )) أو (( بديل )) ابن آدم وحواء. وقد ولد بعد أن قتل هابيل فكان بديلاً له وعوضاً عنه، وولد لآدم عندما كان عمره 130 سنة، فعاش 912 سنة ( تك 5: 3 ).'),
(2129, 'شِيئار', 'اسم فارسي ربما كان معناه (( سيد )). وهو احد رؤساء فارس ( اس 1: 14 ).'),
(2130, 'شِيحُور', 'اسم مصري معناه (( بحيرة حورس )) اسم لنهر النيل ( 1 أخبار 13: 5 واش 23: 3 وار 2: 18 ) انظر (( نيل )).'),
(2131, 'شِيحُورلِبْنَة', 'اسم عبري معناه (( أسود أبيض )) وهو تخم من تخوم اشير ( يش 19: 26 ). ويظن بأن هذا يشير إلى نهر جنوبي اشير أحاط بدور. ويرجح أنه الزرقاء.'),
(2132, 'شيخ-شيوخ-أشياخ', 'أطلق ها اللقب على البكر في الأسرة إذا كان الأبكار يخلفون آبائهم في رئاسة البيت أو الأسرة أو القبيلة ( قض 8: 14-16 و 1مل 8: 1-3 ). وقد كان للمداينيين والمآبيين قديماً شيوخ ( عد 22: 4 و 7 ).واستخدم لقب شيخ إشارة لأصحاب المراكز السامية ( تك 50: 7 )، الذين لهم سلطة على الآخرين ( تث 27: 1 وعز 10: 8 )، والذين يمثلون الدولة رسمياً ( خر 3: 18 وقض 11: 5-11 و 1 صم 8: 4 ) في إكرام الضيوف والترحيب بهم ( خر 18: 12 ). وكان لهم حق ممارسة قطع العهد ( 2 صم 5: 3 ) وممارسة بعض الأعمال الدينية ( لا 4: 13-15 ويش 7: 6 ).أما اختيار الشيوخ قديماً فكان مؤسساً على تقدم السن أو باعتبار وظيفة ما. فكان الشيوخ يكونون مجلساً للشورى في أماكن مختلفة ( خر 4: 29 وتث 19: 12 و 21: 2-9 ويش 20: 4 وقض 8: 14 ). كانت هذه المشيخة محلية في كل مدينة أو قرية.وقد اشترك مع موسى 70 شخصاً ( عد 11: 16 و 24 ) لإعانته في القضاء والحكم. وقد جعل الله عليهم روح الحكمة والفهم. وفي جبل سيناء كان مع موسى سبعين من شيوخ إسرائيل ( خر 24: 1 و 9 ). وفي أيام اليهود المتأخرة أقيم مجلس من سبعين من الشيوخ يعرف بالسنهدريم ( أي مجمع ) ( مر 14: 55 ). انظر (( مجمع )).وكانت وظيفة شيخ تُمارس في عهد المكابيين، أي نحو 175 سنة ق.م. ( 1 مك 7: 33 و 12: 6 ). وحتى أيام الدولة الرومانية في اليهودية، كان رؤوس العائلات ( الشيوخ ) يمارسون أعمالها الدينية والمدنية ( تث 19: 12 و 21: 2 وار 4: 2-11 و 1 صم 11: 3 وعز 10: 14 )، وكانت لهم هذه الحقوق شرعاً ( مت 15: 2 و 21: 23 و 26: 3 و 47 ).وفي العهد الجديد، أطلق لقب شيخ واسقف على الوظيفة الواحدة ( اع 20: 17 وتي 1: 5  و 7 ) غير أن لقب شيخ كان يقصد به الكرامة التي تليق به. ولقب (( اسقف )) كان يقصد به نوع العمل الذي يؤديه كمشرف على الكنيسة ولكن لم يظهر التمييز بين هاتين الوصيتين تماماً إلا في القرن الثاني الميلادي ووظيفة شيخ كانت فعلاً منذ بدء الكنيسة الأولى. ففي كنيسة أورشليم تعين شيوخ ( اع 11: 30 ). وبولس في رحلته التبشيرية الأولى عين شيوخاً في كل كنيسة ذهب إليها ( اع 14: 23 ). وفي الكنائس التي لم يؤسسها بولس تعين الشيوخ للخدمة ( يع 5: 14 و 1 بط 5: 1 ). ولعله من الواضح أن فكرة المشيخة في الكنيسة المسيحية الأولى هي وليدة النظام المشيخي في المجتمع اليهودي. بل كان لهم نفس العمل ونفس المسئوليات. اشترك الشيوخ في سياسة وتنظيم الكنيسة ( اع 15: 2 و 4 و 6 و 22 و 23 و 16: 4 قارن 21: 18 ). وكانوا مشرفين على الكنائس المحلية ( اع 20: 17 و 18 وتي 1: 5 ). وكانت تقع عليهم مسئولية القيام بالخدمات الروحية للكنيسة، وتنفيذ تعليماتها، وتعليم أبنائها ( 1 تي 3: 4 و 5 و 5: 17 وتي 1: 9 ويع 5: 14 و 1 بط 5: 1-4 قارن عب 13: 17 )، ورسامة موظفي الكنيسة ( 1 تي 4: 14 ).وفي العهد الرسولي تعين أساقفة ( فل 1: 1 ) وشيوخ ( اع 11: 30 ) في كنائس محلية. وكما كانت الحال في مجامع اليهود، هكذا كانت في الكنيسة في عصرها الأول، وكان الوعظ من أهم المسؤوليات التي يقوم بها الشيخ ولكن كانت عليه مسؤوليات أخرى. وكذلك كان التعليم ضمن المسؤوليات المهمة التي كان يؤديها الشيخ ( 1 تي 3: 2 وتي 1: 9 ). ولا يفوتنا أن لفظ (( شيوخ )) ترجم (( قسوساًً )) في اع 20: 17 فالرتبة واحدة، يقوم بها أكثر من في كنيسة محلية واحدة. وفي فيلبي 1: 1 يسلم بولس على الأساقفة والشمامسة دون ذكر الشيوخ. وهو طبعاً يقصد (( الشيوخ والشمامسة )). ومن هذا يظهر أنه كان يوجد أكثر من أسقف واحد إسرائيل الكنيسة الواحدة. ولم يذكر العهد الجديد وقت تأسيس المشيخة كما ذكر وقت تأسيس وظيفة الشمامسة ( اع 6 ). ويرجح أن ذلك لكونها موجودة من الابتداء. وينقسم الشيوخ في نظر المصلح كالفن إلى فريقين: شيوخ معلمون مدبرون، وشيوخ مدبرون فقط ( 1 تي 5: 17 ).\nويذكر في سفر الرؤيا 24 شيخاً حول العرش. وربما يكون هذا إشارة إلى 12 مؤسس لأسباط إسرائيل، و12 رسولاً للمسيح اللذين أسسوا الكنيسة المسيحية في العالم ( رؤ 4: 4 و 10 و 5: 5 و 6 و 8 و 14 و 7: 11-13 و 11: 16 و 19: 4 ).'),
(2133, 'شَيخات', 'ذكرت الشيخات مع الشيوخ في زك 8: 4 للدلالة على حلول السلام في أورشليم. وكان لبعض الأرامل في الكنيسة المسيحية وظيفة ما ( 1 تي 5: 3-10 ) غير أننا لا نعلم تماماً ماذا كانت حدودها.'),
(2134, 'شِيدُ', '( تث 27: 2 ) وهو الكلس. وقد كان نادر الوجود في الأبنية القديمة إذ كانوا يتقنون نحت الحجارة للبناء بحيث لم يلزم لها الكلس، ويستعملون له طيناً أو طفالة. انظر (( كلس )).'),
(2135, 'شِيزَة', 'اسم عبري معناه (( نسيبة )) ابنة افرايم. بنت بيت حورون السفلى والعليا وأُزين شيرة ( 1 أخبار 7: 24 ).'),
(2136, 'شِيزَا', 'أبو عَدِينا، قائد رأوبيني ( 1 أخبار 11: 42 ).'),
(2137, 'شِيشا', 'انظر (( شَوشا )).'),
(2138, 'شِيشان', 'اسم عبري ربما كان معناه (( مبيض )). وهو أحد خلفاء يهوذا، من عائلة حصرون، بيت يرحمئيل ( 1 أخبار 2: 31 و 34 و 35 ). لم يكن له أبناء بل بنات زوّج ابنته لعبده المصري.'),
(2139, 'شِيشاي', 'اسم كنعاني معناه (( مبيض )) أحد أبناء عناق الثلاثة. أقام في حبرون وطرده كالب ( عد 13: 22 و يش 15: 14 وقض 1: 10 ).'),
(2140, 'شِيشْبَصَّر', 'اسم بابلي معناه (( يا إله الشمس.. احفظ السيد ( أو الابن )  )). وهو أمير في يهوذا جعله كوش حاكماً. وقد أعاد إليه الآنية المقدسة التي اخذها نبوخذ نصر الملك إلى بابل. عاد شيشبصر إلى أورشليم لبناء الهيكل ( عز 1: 8 و 11 و 5: 14 و 16 ). يحتمل أن هذا الاسم البابلي لزربابل.'),
(2141, 'شِيشَق', 'واسمه المصري (( شِيشَنْق )) أو (( شِيشُنق )) وهو أول ملوك الأسرة 22 من ملوك مصر. حكم من عام 945-934 ق. م. عندما طلب سليمان قتل يربعام، هرب إلى مصر حيث كان في حمى شيشنق فرعون مصر ( 1 مل 6: 38 و 7: 1 و 9: 10 و 24 و 11: 27 ).وفي السنة الخامسة لرحبعام ( 1 مل 14: 25 )، انتهز شيشنق انقسام إسرائيل إلى دولتين بعد موت سليمان، وزحف إلى اليهودية، ونهب الهيكل ( 1 مل 14: 25 و 26 و 2 أخبار 12: 2-9 ). وتوجد في الكرنك كتبة تشرح ما تم. ومن مجموع الأسرى الذين نقشت رسومهم على جدران الكرنك اسير تدل تقاطيع وجهه على أنه عبراني، وبجانبه كتب (( مملكة يهوذا )). وقد اكتشفت مقبرته وكما اكتشف جسمه المحنط من صان الحجر في الوجه البحري.'),
(2142, 'شِيشَك', 'اسم رمزي لبابل معناه (( الانحدار )) وهو اسم عبري نتج عن قلب الحروف في الأبجدية العبرية. فمثلاً: الألف العبرية تعتبر رقم 1 والباء العبرية رقم 2 وهكذا إلى نهاية الأبجدية. وبناء على ترقم آخر تبدأ الأرقام من واحد فصاعداً من آخر حرف في الأبجدية. وبناء على ذلك يكون الحرف الثاني من أول الأبجدية هو الباء، ومن آخر الأبجدية هو الشين. والحرف الثاني عشر من أول الأبجدية هو اللام، ومن آخر الأبجدية هو الكاف. فإن قلب الاسم (( بابل )) بناء على هذا الأسلوب، يجعل اسمها (( شيشك )) ( ار 25: 26 و 51: 41 ).'),
(2143, 'شَيطان', '( 1 ) ترجمة الكلمة العبرية شطن ومعناها (( مقاوم )) ويسمى في اليونانية (( ديابولس )) ومعناها (( مشتك )). ويسمى أيضاً ابدّون وابولّيون أي مهلك وملاك الهاوية ( رؤ 9: 11 )، وبعلزبول ( مت 12: 24 )، وبليعال ( 2 كو 6: 15 )، ورئيس هذا العالم ( يو 12: 31 )، ورئيس الشياطين ( مت 9: 34 ) ورئيس سلطان الهواء، الروح الذي يعمل الآن في أبناء المعصية ( اف 2: 2 )، وإله هذا الدهر ( 2 كو 4: 4 ) وإبليس والقتال، وكذاب أبو الكذاب ( يو 8: 44 )، والمشتكى على الأخوة ( رؤ 12: 10 )، وخصمنا الأسد الزائر ( 1 بط 5: 8 )، والتنين أي الحية القديمة ( رؤ 12: 9 ). وهو كبير الأرواح الساقطة ( رؤ 12: 9 ومت 4: 8-11 و 13: 38 و 39 و 25: 41 وقارن يه 6 ).والشيطان كائن حقيقي. وهو اعلى شأناً من الإنسان. ورئيس رتبة من الأرواح النجسة ( مت 12: 24 ). ويسجل لنا الكتاب المقدس طبيعته وصفاته وحالته وكيفية اشتغاله وأعماله ومقاصده.أما طبيعة الشيطان فهي روحية. وهو ملاك سقط بسبب الكبرياء. ورغم ذلك فهو يمتاز بكل امتيازات هذه الرتبة من الكائنات سواء أكانت عقلية كالإدراك والذاكرة والتمييز أو حسيّة كالعواطف والشهوات أو إرادية كالاختيار ( اف 6: 12 ). وهو خبيث. فإنه قائد العصاة على الله. يعمل ضد البر والقداسة، ومملوء بالكبرياء والمكر والقساوة. حالته تنطبق على صفاته. فلكونه عدو الله، هو مطرود من وجهه، ومحبوس مع رفاقه في موضع العذاب، حيث يعاقب على العواطف النجسة التي فيه، والأعمال الخبيثة الناتجة عنها ( 2 بط 2: 4 ويه 6 ).غير أن طرده إلى عالم الظلمة لا يمنع اشتغاله في الأرض كإله هذا العالم، وعدو الإنسان اللدود ( 1 بط 5: 8 و 2 كو 2: 11 ). فكره مشتغل على الدوام بالمقصاد والأعمال التي مآلها قلب مقاصد الله وأعماله. وهو في ذلك كسائر ملائكته جسور مغامر. أما عمله بين الناس منذ البدء، فهو الغدر والمخاصمة والظلم والقساوة. وهو بشخصه أو بواسطة ملائكته يجرب الناس للخطيئة أو يصدهم عن القداسة ويشتكي عليهم بالخطيئة والضعف وعدم الثبات نحو بعضهم ونحو الله. ويعرّضهم للشقاوة الحالية والمستقبلة ( أيوب 1 و 2 و مت 4: 1-11 ويو 8: 44 واع 26: 18 و 1 كو 7: 5 و 2 كو 2: 11 واف 6: 11 و 1تس 3: 5 ورؤ 12: 10 ). وعلى المؤمن أن يقاوم بشدة ولا يخضع له ( اف 4: 27 ويع 4: 7 ). فعند بذر البذار الحية، أي كلمة الله، يسرقها الشيطان ( لو 8: 12 )، أو يزرع معها زواناً ( مت 13: 38 ). وهو بهذا يريد أن يتسلط على الناس ( اع 10: 38 ).أما أعوانه في هذه التجارب فهم عصبة الأرواح الساقطة الذين شاركوه في العصيان الأول، ويعملون معه لمخالفة إرادة الله، وضرر أولاد الأبرياء ( اف 6: 11 و 2تي 2: 26 ). ويظهر أن الشيطان بسماح من الله اكتسب بعض السلطان على عناصر العالم الهيولية، وهو يستخدمها لمقاصده الخبيثة. علماً بأنه ذكي، يعرف صفات الإنسان، وطباعه، وأمياله، ويستخدمها للإيقاع به في الخطيئة. ومن يتشرب بتلك صار وكيلاً للشيطان في التغرير بالأخرين وإسقاطهم في مهاوي الشر والرذيلة. فمن يصير لهم هذا المشرب وتلك الطباع يلقبون بأبناء الشيطان ( يو 8: 44 و 1 يو 3: 8 و 10 ). بل إن المسيح دعا يهوذا الاسخريوطي شيطاناً ( يو 6: 70 ).أما كيفية الإيقاع بالناس في الخطيئة وتجربتهم فهي مزدوجة: طريق الغش وطريق الاحتيال. فالشيطان يتقلد مظهر ملاك نور أحياناً ( 2 كو 11: 14 ) وتنّين أحياناً أخرى. وأحياناً و أحياناً يحتال على الناس بأن يقدم الصورة المقبولة لهم ( تك 3: 1-13 ). ويخفي وراءها شراً مميتاً. ويمنع الناس عن فعل الخير ( مر 4: 15 ) وذلك بأن يصدهم عن إتمام مقاصدهم ( زك 3: 1و 2 ).فإن الشيطان، منذ أن أخضع آدم وحواء في جنة عدن، أخضع كل جنسنا تحت صولته الظالمة، لأنه خدع الجميع ( اف 2: 1-3 ورؤ 12: 9 ). وقد جرّب الجميع للخطيئة حتى المسيح في البرية ولكن المسيح قهره وانتصر عليه ( مت 4: 1 ويو 12: 13 ).وقد أطلقت كلمة شياطين في الجمع على الأرواح الشريرة وهي رسل مرسلة من قبل الشيطان، وتحت أمره وسلطانه ( مت 9: 34 و 12: 24 ). أما تلك الأرواح الشريرة، فقد كانت تدخل الناس والبهائم فتحدث فيهم أعراض الجنون والصراع.وكان دخول الشياطين في الناس أمراً حقيقياً، ظهر على هيئة أمراض جسدية وعقلية والخرس ( مت 9: 32 ) والعمي ( مت 12: 22 ) والصرع ( مر 9: 17-27 ) والجنون ( مت 8: 28 ). وقد أخرج الرب هؤلاء الشياطين فعلاً. وقد ظهر أن اولئك الشياطين قد عرفوا يسوع يقيناً وخافوا الدينونة. وكانوا يتكلمون وينتقلون من إلى آخر وإلى البهائم. وقد وجدت الشياطين نفسها مرغمة على الاعتراف بالمسيح رباً وإلهاً. وفي ضوء هذا الشرح نفهم قصة المجنون الذي خرج من بين القبور ( مر 5: 1-20 ). وقد صرح المسيح بأن هذه الأعراض أحياناً ما تكون من نتيجة عمل الشيطان ( مت 12: 24-28 ولو 4: 35 و 10: 18 ). ولكن لا يجب أن ننسى أن المسيح جاء لكي ينقض أعمال إبليس ( 1 يو 3: 8 ). أما نهاية الشيطان فإنه سيقبض عليه ويقيد بالسلسلة ويطرح في الهاوية ويختم عليه لكي لا يضل الأمم فيما بعد. وفي النهاية يطرح في بحيرة النار والكبريت ويعذب نهاراً وليلاً إلى الأبد الأبدين ( رؤ 20: 1 و 2و 10 ).'),
(2144, 'شِيعَة', 'تستعمل هذه الكلمة في ترجمتنا للدلالة على حزب أو طائفة ( اع 5: 17 و 34: 5 و 14 ). على أن الكلمة الأصلية معناها (( هرطقة )) وترجمت (( بدعة )) ( 1 كو 11: 19 وغل 5: 20 و 2 بط 2: 1 ) و (( مذهب )) ( اع 15: 5 و 28: 22 ).'),
(2145, 'شِيْلَة', 'اسم عبراني معناه (( طلب )) وكان ثالث أولاد يهوذا ( تك 38: 5 و 11 و 14 و 26 و 46: 12 و عد 26: 20 و 1 أخبار 2: 3 و 4: 21 ).'),
(2146, 'شِيلو', 'انظر (( شيلوه )) 1.'),
(2147, 'شِيلون', 'انظر (( شيلوه ))2.'),
(2148, 'شِلوني-شيلونيون', 'سكان شيلوه. أعطى هذا اللقب لأخيَّا النبي ( 1 مل 11: 29 ) ولعائلة في يهوذا أقامت في أورشليم بعد السبي ( 1 أخبار 9: 5 ونح 11: 5 ).'),
(2149, 'شَيلوه', 'اسم عبري معناه (( موضع الراحة )) وهي:( 1 ) مدينة شمالي بيت إيل. في منتصف الطريق بين بيتين وشكيم ( أي نابلس ) قض 21: 19. ويرّجح أنها هي المسماة الآن سيلون، التي تبعد 17 ميلاً شمالي أورشليم.وقد اختار يشوع شيلوه مقراً للتابوت والخيمة. وفيها قسّم يشوع البلاد وزعها على الأسباط ( يش 18: 1 و 8-10 ). وفيها بقيت الخيمة ثلاث مائة سنة. وفي عهد القضاة كان الشعب يعيد هناك كل سنة، وكانت ترقص بنات شيلوه ابتهاجاً للعيد. وفي إحدى تلك الأعياد، خطف البنياميون 200 منهن وتزوجوا بهن ( قض 21: 19-23 و 1 صم 1: 3 ).وكانت شيلوه مسكن عالي الكاهن وصموئيل. وهناك سمع عالي بخير أخذ الفلسطينيين التابوت ( 1 صم 4: 12 ). وأشير بذلك مراراً إلى أن الله رفض مسكن شيلوه. أو (( شيلو )) كما ترجمت أحياناً في مز 78: 6 وغيره. ولم يعد التابوت بعدها إلى شيلوه بل أصعده داود إلى أورشليم ( 2 صم 6 ). ونُقِلت الخيمة إلى نوب ( 1 صم 21: 1-9 )، ومن هناك إلى جبعون ثم إلى أورشليم ( 2 أخبار 1: 3و 4 ) وسكن هناك اخيا النبي ( 1 مل 14: 1-16 ).يظهر أن شيلوه كانت خربة أيام ارميا النبي ( ار 7: 12 و 14 و 26: 6 و 9 )، وفي أيام جيروم. وموقعها هو (( سيلون )) حيث ترى آثار أبنية وأساسات قديمة. وعلى التل دار مفتوحة طولها 412 قدماً وعرضها 77 قدماً، بعضها منحوت في الصخر. وربما كانت تلك الفسحة مقراً لخيمة التابوت. وعلى بعد نصف ميل توجد دار تدعى نبع سيلون، وفيها بئر يقولون أن في ذلك الموضع اختطف البنيامينيون بنات شيلوه. وبجواره قبور منحوتة يظن أنها قبور على الكاهن وأولاده. وقد كشف التنقيب في سيلون أن المدينة أخربت في زمن القصاء كما ورد ذكر الكتاب المقدس.( 2 ) شيلوه المترجمة (( شيلون )) في تك 49: 10 وقد حار العلماء في تفسير وفهم المقصود منها. وفي ذلك ثلاثة آراء:أ- إن شلون اسم يشير إلى المسيا الذي يأتي من نسل يهوذا. فإن يعقوب في نبؤته رأى أن رأوبين بسبب خطيئته قد فقد البركة والبكورية. ثم شمعون ولاوي فقداها أيضاً بسبب خطيئتهما. فنقلت البركة والبكورية ليهوذا. أما البركة فهي التي وُعد بها آدم لأنتصار نسل المرأة الذي يسحق رأس الحية ( تك 3: 15 )، والبركة الممنوحة لسام ( تك 9: 26 و 27 ) بركة الرئاسة على اخويه، والبركة الموعود بها لإبراهيم بوجود نسل رحي وميراث أرض الموعد ( تك 17 ). كل هذه منحت ليهوذا.لهذا نرى انتقال السلطة من يد موسى اللاوي، ويشوع الافرايمي، وشاول الملك البنياميني، إلى داود الذي من يهوذا، والذي من نسله جاء المسيا الموعد به.ب- جاء ترجمة تك 49: 10 في بعض الترجمات هكذا: (( لا يزول قضيب من يهوذا ومشترع من بين رجليه حتى يأتي إلى شيلوه )). وهذه الترجمة مشكوك فيها. رغم أن فكر العلماء الذين ينادون بها يرجع إلى استخدام شيلوه مركزاً للتابوت أيام يشوع. وهي إشارة لراحة شعب الله بعد هزيمة كنعان.ج- تنقسم الكلمة (( شيلوه )) إلى ثلاث مقاطع (( شي )) و (( ل )) و (( وه )). ومعنى العبارة هو: (( الذي له )) كما يظهر أيضاً من حز 21: 27. وتتفق هذه مع الترجمة السبعينية. وهذا الرأي أيضاً يعود بناءاً إلى فكرة انتظار المسيا. وترجمتها التفسيرية هي:(( حتى يأتي المسيا إلى ما يخصه )).مياه شيلوه:انظر (( سلوام )) (  اش 8: 6 ).'),
(2151, 'شَيمون', 'رجل من نسل يهوذا ( 1 أخبار 4: 20 ).'),
(2152, 'شِيوا', 'انظر (( شَوَا )).'),
(2153, 'صَادوق', 'اسم عبري معناه (( عادل )) أو (( بار )) وهو:( 1 ) ابن اخيطوب من ذرية العازر بن هارون ( 1 أخبار 24: 3 ) وهو على الأرجح الغلام الذي جاء إلى داود إلى حبرون مع رؤساء بني إسرائيل ليحولوا مملكة شاول إلى داود ( 1 أخبار 12: 27 و 28 ). وكان أحد الكاهنين العظيمين في أيام داود وكان ابياثار الذي يسمى أيضاً اخيمالك هو الكاهن الآخر ( 2 صم 17 ). وفي أثناء فتنة ابشالوم هرباً مع داود من أورشليم وهما يحملان تابوت العهد ولكن الملك رغب إليهما أن يعودا بالتابوت إلى العاصمة ويقيما فيها حتى ينتهي الخصام ( 2 صم 15: 24-29 ). وبعد موت ابشالوم أرسل داود اليهما طالباً أن يقنعا شيوخ يهوذا بإرجاعه إلى الحكم ( 2 صم 19: 11 ). ولما شاخ داود وأراد ادونيا أن يغتصب الملك بقي صادوق أميناً لداود. ولكن زميله ابياثار تآمر مع ادونيا على أخذ الملك له ( 1 مل 1: 7 و 8 ) ولما كشف داود النقاب عن المؤامرة أمر صادوق وناثان النبي أن يمسح سليمان ملكاً ( الاعداد 32-45 ) فطرد سليمان ابياثار عن الكهنوت وبقي صادوق وحده الكاهن العظيم حتى وفاته في أيام سليمان ( اصحاح 2: 26-27 وراجع اصحاح 4: 4 ). وهكذا عادت كرامة الكاهن العظيم إلى سلالة العازر.( 2 ) كاهن من سلالة الكهنة العظام أبو شلوم وابن اخيطوب الثاني ( 1 أخبار 6: 12 وعزرا 7: 2 ) من مرايوث الثاني ( 1 أخبار 9: 11 ونحميا 11: 11 ).( 3 ) أبو يروشا امرأة الملك عزيا ( 2 مل 15: 33 ).( 4 ) ابن بعنا. رمّم جزءاً من السور ( نحميا 3: 4 ) ولعله هو صادوق الذي ختم الميثاق شخص واحد ( نحميا 10: 21 ).( 5 ) كاهن ابن أمير وقد رمّم السور مقابل بيته ( نحميا 3: 29 ). ولعله هو الكاتب الذي أقيم خازناً ( إصحاح 13: 13 ).\n( 6 ) أحد أسلاف المسيح ( متى 1: 14 ).'),
(2154, 'صَارَث الشَحْر', 'اسم عبري معناه (( بهجة الفجر )) مدينة في رأوبين على جبل يشرف على واد لا ريب أنه وادي البحر الميت ( يش 13: 19 ) ولعلها زارت بقرب مصب نهر الزرقاء على الضفة الشرقية من البحر الميت.'),
(2155, 'صَافون', 'اسم عبري معناه (( الشمال )) وهو اسم مدينة في جاد شرقي الأردن في الوادي ( يش 13: 27 ). وربما عبارة (( إلى جهة الشمال )) الواردة في قضاة 12: 1 تشير إلى هذه المدينة لأن العبارة في الترجمة السبعينية كتبت (( إلى صافون )). وقال التلمود أنها عماثو فتكون تل عماتة في واديالأردن إلى شرقي النهر وعلى نحو ثمانية أميال من مصب يبوق إلى الشمال الشرقي. ويرّجحون اليوم انها تل القوس في وادي الأردن.'),
(2156, 'صَالاف', 'اسم عبري معناه (( نبات الكبير )) وهو اسم أبي حانون أحد مرممي سور أورشليم ( نحميا 3: 30 ).'),
(2157, 'صَالَق', 'اسم عموني معناه (( شق )) عموني وهو احد أبطال داود ( 2 صم 23: 37 و 1 أخبار 11: 39 ).'),
(2158, 'صانان', 'مدينة في يهوذا ( مي 1: 11 ). اطلب (( حنان )).'),
(2159, 'صباح', 'اطلب (( يوم )).'),
(2160, 'صِبْعُون', 'اسم حوي أو حوري معناه (( ضبع )) وهو رجل حوي أو حوري ( تك 36: 2 ). ولعله هاجر مع عائلته إلى جبل سعير فصارت منه قبيلة الحوريين ( العددان 20 و 24 ). ويحكمها أمير وكان سلفاً لإحدى نساء عيسو ( العددان 2 و 25 ).'),
(2161, 'صَبغُ', '( صباغ ) صناعة اشتهر بها القدماء ولاسيما الفينقيين والمصرين ويتضح من خر 26: 1 و 14 و 35: 25 إن العبرانيين اكتسبوا هذه الصناعة. وفي أثناء خروجهم من مصر مارسوها في إعداد لوازم الخيمة. وتجد في الرسوم على قبور مصر وهياكلها تفاصيل هذه الصناعة. وكانت ليديا بائعة ارجوان في مدينة ثياتيرا ( اع 16: 14 ). وكانت تلك المدينة مشهورة بالصبغ فكانت لأرباب هذه الصناعة جمعية كما تشهد لذلك بعض الكتابات على آثار المدينة. وقد اشتهرت صيدا القديمة بصبغ الأقمشة بلون ارجواني يستخرج من نوع من الصدف. وعثر على مثل هذا الصدف في منية البيضاء مرفأ اوجاريت ( رأس شمرة ) مما يدل على أن الارجوان كان يصنع فيها في الألف الثانية قبل الميلاد.'),
(2162, 'صَبُوغيم', 'اسم عبري معناه (( ضباع )) وهو.( 1 ) موضع سكنه البنيامينيون بعد السبي ( نح 11: 34 ) ولا يعلم مكانه الآن.( 2 ) واد في بنيامين إلى شرقي مخماس إلى جهة البرية ( 1 صم 13: 16-18 ) ويسمى أبو ضباع اليوم.'),
(2163, 'صَبوييم', 'اسم عبري معناه (( ظباء )) إحدى مدن الدائرة ( تك 10: 19 ) كسر ملكها كدرلعومر ( تك 14: 2 و 8 و 10 ) احرقها الله مع بقية مدن الدائرة بنار من السماء ( تك 19: 17-29 وتث 29: 23 وهو 11: 8 ).'),
(2164, 'صاحب القضاء', '( عز 4: 8 و 17 ) لقبٌ لُقّب به رحوم والي السامرة في أيام الفرس.'),
(2165, 'صَخرة الزلقات', '(( صخرة الافتراق أو الهرب )) وهو حصن طبيعي في برية معون إلى الشرق الجنوبي من حبرون وسمي كذلك لأن داود لما كان على جهة من المرتفع نجا من شاول الذي كان على الجهة الأخرى ( 1 صم 23: 28 ). وقال كوندر أن هذه الصخرة في وادي الملاقي إلى شرقي معون.'),
(2166, 'صَدَد', 'اسم سامي ربما كان معناه (( جانب الجبل )) وهو موقع وربما كان برجاً على الحدود الشمالية لكنعان ( عد 34: 8 وحز 47: 15 ). وظن أكثرهم إنها صدد الحالية التي تبعد 75 ميلاً إلى الجنوب الشرقي من حمص على طريق ربلة إلى تدمر. والمسيحيون هناك من السريان الأرثوذكس.'),
(2167, 'صدَقة', '( صدقات ) لم ترد هذه الكلمة في العهد القديم غير أنه كثرت الإشارة إلى وجوب فعل الرحمة والسخاء في الطعاء. ومما وجب على بني إسرائيل ترك بقايا المواسم والحصاد في زوايا الحقل والكرم ليلتقطها الفقراء ( لا 19: 9 و 10 و 23: 22 وتث 15: 11 و 24: 19 ). وكان مطلوباً منهم أن يأتوا بتقدمة من اول ثمر أرضهم للكاهن ليقدمها للرب ( تث 26: 2-13 ). ويظهر من قصة راعوث أن عادة الإلتقاط التي لم تزل إلى الآن، كانت جارية في أيامها. وكل سنة ثالثة كان يعطى عشر محاصيل الأرض للاوي واليتيم والغريب والأرملة ( تث 14: 28 و 29 ) وكذلك مدح من تصدق على الفقراء في أماكن اخرى من الكتاب ( اي 31: 17 ومز 41: 1 و 112: 9 ). وكان في الهيكل صندوق لقبول عطايا لتربية أولاد الفقراء. وكان التصدق من جملة أعمال الفريسيين التي افتخروا بها. ولم يكن ربنا يوبخهم لأجل صدقتهم بل لأجل افتخارهم بها ( مت 6: 2 ). وكثرت الإشارة في العهد الجديد إلى الصدقة وكيفيتها ( اع 10: 31 ورومية 15: 25-27 و 1 كو 16: 1-4 ).'),
(2168, 'صَدُّوقي، صدوقيون', 'يظهرهم العهد الجديد ويوسفيوس أنهم والفريسيين طائفتان متخاصمتان في اليهودية. والصدوقيون فرقة صغيرة نسبياً ولكنها مؤلفة من ميثقفين جلهم أغنياء وذوو مكانة مرموقة. وقد عم الرأي أن اسمها مشتق من صادوق. وذلك لأن هذه الطائفة مؤلفة من رؤساء الكهنة والارستقراطية الكهنوتية. وقد كان صادوق رئيس كهنة في أيام داود وسليمان وفي عائلته حفظت رئاسة الكهنوت حتى عصر المكابيين فسمى خلفاؤه وأنصاره صدوقيين.\nوبخلاف الفريسيين الذين كانوا يؤكدون تقليد الشيوخ، حصر الصدوقيين تعاليمهم في نص الكتاب قائلين أن حرف الناموس المكتوب وحده ملزم حتى إن قاد الناموس إلى شدة في المقاضاة. وبخلاف الفريسيين فهم انكروا: ( 1 )- القيامة والثواب في الجسد ذاهبين إلى النفس تموت مع الجسد ( مت 22: 23-33 واع 23: 5 ). ( 2 )- وجود الملائكة والأرواح ( اع 23: 8 )، ( 3 )- الجبرية فقالوا بحرية الإرادة وإنا قادرون على أعمالنا وأننا سبب الخير وإننا نتقبل الشر من اجل حماقة أفعالنا وأن لا دخل الله في صنعنا الخير أو إعراضنا عن الشر.\nوإما أصل الصدوقيين ونشوؤهم فيذهب إلى أن أسرة صادوق الكهنوتية التي كانت تقود الشؤون في القرنين الرابع والثالث في العصرين الفارسي واليوناني اخذت، وربما غير واعية، تضع الاعتبارات السياسية فوق الدينية. وفي زمن انطيوخوس ابيفانيس ( 175-163 ق. اسم يوناني معناه ) كان عدد كبير من الكهنة محباً للثقافة اليونانية ( 2 مكابيين 4: 14-16 ) وكان رؤساء الكهنة ياسون ومينيلاوس والقيموس الداعين إلى الهيلينية فوق الشعب إلى جانب المكابيين للذود عن نقاوة الدين اليهودي. وبانتصار هذا الفريق وتأمين المكابيين ورئاسة الكهنوت انسحبت خلفاء صادوق وأنصارهم وزجوا أنفسهم في السياسة فكانوا يصرّون على إهمالهم لعادت الشيوخ وتقاليدهم والتقرب إلى الثقافة والنفوذ اليونانيين. أما يوحنا هرقانوس وارستوبولس واسكندريناوس ( 135-78 ق. م. ) فقد أبدوا ميلاً للصدوقيين فكانت القيادة السياسية إلى حد كبير في أيديهم في زمن الرومان والهيرودوسين وكان رؤساء الكهنة انذاك منهم. وإن الصدوقيين الذين جاؤوا إلى يوحنا المعمدان في البرية خاطبهم كما خاطب الفريسسن قائلاً: إنهم اولاد الأفاعي ( مت 3: 7 ). وانضموا إلى الفريسيين ليسألوا من الرب آية من السماء ( مت 16: 1-4 ). وحذر الرب تلاميذه من الفريقين ( الأعداد 6-12 ) وقد حاول الصدوقيين أن يوقعوه بشباكهم بطرحهم عله سؤلاً محرجاً عن القيامة ولكنه رد واسكتهم ( مت 22: 23-33 ). ووافق الصدوقين والفريسيون على الشكوى عليه والحكم بصلبه وكان حنانيا وقيافا صدوقيين. واتفقوا فيما بعد مع الكهنة وقائد الهيكل على اضطهاد بطرس ويوحنا ( اع 4: 1-22 ) وكان الفريسيون في المجمع الذي حاكم بولس فأوقع الرسول بحكمته الشقاق فيما بينهم بشأن القيامة ( اع 23: 6-10 ).'),
(2169, 'صِدِّيم', 'اسم عبري معناه (( جوانب )) مدينة مسورة في نفتالي ( يش 19: 35 ) وسماها التلمود كفر حطية مما قاد إلى اعتبار موضعها في حطين على بعد خمسة أميال ونصف إلى غربي طبريا شمالاً، ودون الميل شمالاً من قرن حطين.'),
(2170, 'صُدْرة', 'جزء من ملابس رئيس الكهنة الرسمية ( خر 28: 15 ) وكانت تصنع من ذهب واسما نجوني وارجوان وقرمز وكتان مبروم وكانت مربعة مثنية طولها شبر وعرضها شبر وكانت مرصعة بصفوف من حجارة كريمة ثلاثة حجارة في كل صف وينقش على كل حجر اسم سبط من أسباط بني إسرائيل ( اطلب رئيس كهنة في كلمة كاهن ) وكانت زاويتاها العلويتان مرتبطتين بالرداء ولم تكن الصدرة تنزع عن الرداء ( خر 28: 28 ) أما زاويتاها السفليتان فبالزنار. وكانت الحلقات وبقية أدوات ربطها مصنوعة من ذهب أو تطريز وسميت تذكاراً ( خر 28: 12 و 29 ) لأنها كانت تذكر الكاهن بكونه نائب الأسباط الاثني عشر. ويقال لها أيضاً صدرة قضاء ( خر 28: 15 ). وربما سميت هكذا لأنها كانت موضوعة أمام صدر من كان مصدر العدل والقضاء لبني إسرائيل ولعلها سميت كذلك لأن الأوريم والتميم جعلا في الصدرة على قلب رئيس الكهنة وبواسطتهما كان رئيس الكهنة يعرف حكم الله.'),
(2171, 'صِدْقِيَّا', 'اسم عبري معناه (( يهوه عدل أو عدل يهوه )) وهو:( 1 ) آخر ملوك يهوذا وكان ابن يوشيا واسم أمه حموطل (  2 مل 24: 18 ). واسمه الحقيقي متنيا غير أن نبوخذنصر غيره إلى صدقيا عندما ملكه عوضاً عن ابن أخيه يهوياكين ( 2 مل 24: 17 و 1 أخبار 3: 15 ) وفي 2 أخبار 36: 9 و 10 دعي أخاً ليهوياكين أي نسيبه أو من أصل واحد. وكان له من العمر إحدى وعشرين سنة لما اعتلى العرش وملك إحدى عشر سنة من السنة 597 إلى 587 ق.م ( 2 مل 24: 18 و 2 أخبار 36: 11 ). ولم يصغ هو ولا شعبه إلى كلمة الرب التي تكلم بها بفم ارميا ( 2 أخبار 36: 12 وار 37: 1 و 2 ) فنجس الهيكل بالوثنية ( 2 أخبار 36: 14 ) ولم يقض بالعدل ( ار 21: 11 و 12 ).وقد انحاز إلى جانب الملك حزب عظيم في الدولة يعاونه أنبياء كذبة ليخلع النير الأجنبي ( ار 27: 12-22 ) ففي بداية ملك صدوقيا جاء إليه رسل من ادوم وموآب وعمون وصيدون إلى أورشليم ليرسموا خطة ثورة موحده على ملك بابل ولكن الله أوحى إلى ارميا أن يشجب مقاصدهم ( اعداد 2-11 ) وقد بعث صدقيا سفارة إلى نبوخذنصر غالباً ليؤكد له ولاءه ( ار 29-3 ) وفي السنة الرابعة لمكله ذهب هو إلى بابل ( ار 51: 59 ) وأخيراً جسر على التمرد. وفي اليوم العاشر من الشهر العاشر في السنة التساعة لملك صدوقيا عسكر الملك البابلي أمام أورشليم وأخذ يبني حصوناً حولها لأن قوتها حالت دون اقتحامها فحاصرها البابليون إلا أن تقدم المصرين أرغمهم على الانسحاب إلى حين ( ار 37: 5 ) ولكنهم ما لبثوا أن عادوا. وفي اليوم التاسع من الشهر الرابع السنة الحادية عشرة لملك صدقيا نفذ القوت من العاصمة المحاصرة. في تلك الليلة غادر صدقيا الموقع مع كل رجاله وتسلّل بين القلاع البابلية وفر شرقاً نحو الأردن. فلما علم بذلك الجيش البابلي بذلك طارده وأدركه في سهل أريحا فذهب به أسيراً إلى نبوخذنصر إلى ربلة إلى الشمال من فلسطين. وهناك حوكم وحكم عليه فقتل أولاده أمامه وأقتلعت عيناه وربط بسلاسل من نحاس وسيق إلى بابل ( 2 مل 24: 17-20 و 25: 1-7 و 2 أخبار 36: 11-21 وار 39: 1-14 ). وحبس حتى موته ( ار 52: 11 ) وقد باشر ارميا عمله النبوي طوال ملك صدقيا.( 2 ) ابن معسيا. نبي كاذب زان تنبأ ارميا أن نبوخذنصر سيقليه بالنار ( ار 29: 21-23 ).( 3 ) أحد الرؤساء في بلاط يهوياقيم ( ار 36: 12 ).( 4 ) ابن كنعنة. أنضم إلى أنبياء كذبة أخرين وحصنَّ آخاب على محاولة الاستيلاء على راموت جلعاد وبعد أ، تنبأ أن آخاب سغلب السوريين هاج لما تنبأ ميخا نبي يهوه على عكس ما تنبأ به وضرب رجل الله على الفلك وأهانه فقال له ميخا سأتي يوم يعرف فيه خطأه ( 1 مل 22: 11-25 ).( 5 ) ابن يكينا ( 1 أخبار 3: 16 ) ويقول بعض الشراح أن لفظة (( ابن )) هنا تؤخذ بمعنى خلف.( 6 ) أحد الذين ختموا العهد مع نحميا ( نح 10: 1 ).'),
(2172, 'صَرتان', 'قرية شرقي يرعيل بين مدينتي بيت شان وادام ( يش 3: 16 و 1 مل 4: 12 ) في أرض منسّى. في أرض الخزف في غور الأردن بين سكوت. وكانت صرتان تصنع آنية الهيكل من نحاس مصقول ( 1 مل 7: 46 ). وقد ورد ذكرها في قضاة 7: 22 و 2 أخبار 4: 17 باسم صردة. ويقول البعض أن موقعها الآن (( قرن صرطبة )) ويقول آخرون أن مكانها (( تل سليخات )) ويقول آخرون أن مكانها (( تل السعيدية )).'),
(2173, 'صَرَث', 'اسم عبري ربما كان معناه (( بهاء )) أحد أعقاب يهوذا ( 1 أخبار 4: 7 ).'),
(2174, 'صَرَدة', 'اسم عبري ربما كان معناه (( برد )).( 1 ) قرية جاء منها ناباط أبو يربعام وربما جاء منها يربعام نفسه ( 1 ملو 11: 26 ) وربما كانت هي (( دير غسّانة )) بالقرب من عين صردة على مسافة 15 ميلاً جنوبي غربي شكيم.( 2 ) صردة المذكورة في قضاة 7: 22 و أخبار 4: 17 هي نفس صرتان وقد ورد اسمها في بعض المخطوطات في قضاة 7: 22 بصورة (( صريرة )).'),
(2175, 'صَرير الأسنان', '( مت 8ك 12 قابل مز 112: 10 ) علامة اليأس والألم.'),
(2176, 'مُصارعة', '( انظر (( لعب )) ).'),
(2177, 'مصروع', 'إن اللفظة اليونانية مشتقة من القمر لاعتقاد القدماء أن داء الصرع يعتري صاحبه بسبب ضوء القمر أو في رؤوس الأهلة. وهذا يختلف عن سكنى الشياطين ( مت 4: 24 ) لأنه كثيراً ما كان ناتجاً عن أسباب أخرى. ولكن سكنى الشيطان كان يمكن أن تنشيء هذا الداء ( راجع متى 17: 15 ومر 9: 17 ) وإن مقابلة هذين الموضعين قادت إلى الرأي القائل أن الكلمة اليونانية تعني داء الصرع.'),
(2178, 'مَصاريع', '( 1 مل 7: 50 ) يرّجح أن مصاريع أبواب البيوت كانت تدور على نجران ( راجع (( صائر )) ).'),
(2179, 'صُرْعَة', 'اسم عبري ربما كان معناه (( ضربة أو زنبور )) ( يش 15: 33 ونح 11: 29 ) مدينة في ساحل يهوذا أعطيت بعد وقت لدان ( يش 15: 33 و 19: 41 ). وكانت مسقط رأس شمشون ( قض 13: 2 و 25 و 16: 31 ). ومن صرعة أرسل الدانيون جواسيس ليفتشوا الأرض عن موضعها للسكن ( قض 18: 2 ) وقد حصنها رحبعام ( 2 أخبار 11: 10 ) وسكنها بعض الراجعين من السبي ( نح 11: 29 ). وهي قائمة الآن واسمها صرعة على الضفة الشمالية من وادي سورق ( وادي الصرار ) على بعد 14 ميلاً تقريباً غربي القدس.'),
(2180, 'صرْعي صرعيون', '( 1 أخبار 2: 53 و 54 و 4: 2 ) أهل صرعة.'),
(2181, 'صَيارِفة', 'كلما أجرى إحصاء في بني إسرائيل كان على كل من بلغ العشرين من العمر، فقيراً كان أم غنياً، أن يدفع نصف شاقل إلى خزينة الرب فدية عن نفسه ( خر 30: 13-15 ) وفيما بعد صارت هذه الضريبة تدفع كل سنة حسب رواية موسى بن ميمون. وإلى جانب هذه الضريبة كان الإسرائيليون الأتقياء يبذلون من المال طوعاً فيلقونه في خزائن موضوعة في دار النساء ( مر 12: 41 ) وتحتّم أن يكون هذا النقد وطنياً لأن نقوداً كثيرة كانت تستعمل فلسطين في زمن المسيح. وحيث أن الجماهير من اليهود كانت تختّلف إلى أورشليم من بلاد غريبة في الفصح حاملة معها عملة بلادها ظهرت الحاجة إلى صيارفة لإبدال هذه العملة بالنقد الوطني مقابل عمولة. وكان لهؤلاء مجال في المدينة. وعند اقتراب العيد كانوا يقبلون إلى الهيكل ويضعون موائدهم في دار الأمم. وفي مناسبتين مختلفتين قلب يسوع موائد الصيارفة وطرد باعة الحمام والمواشي لأن وجودهم وأحياناً إعوجاجهم وبخلهم كانت لا تتفق وقدسية المكان والهدوء الضروري للعبادة ( يو 2: 14-16 ومت 21: 12 و 13 ).'),
(2182, 'صَرْفة', 'مدينة فينيقية من أعمال صيدون ( 1 مل 17: 9 ولو 4: 26 ) أقام فيها إيليا لما نضب نهر كريث وكانت قد استقبلته أرملة فيها ما طالت المجاعة إذ وثقت في كلمته التي نطق بها باسم الرب. وثواباً لإيمانها لم يفرغ الدقيق ولم ينقص الزيت من عندها من عندها وأعاد النبي لولدها الحياة ( 1 مل 17: 8-24 ) واستسلمت المدينة لسنحاريب في سنة 701 ق.م وتنبأ عوبديا عن رجوعها إلى بني إسرائيل ( عوبديا عدد 20 ) وتسمى اليوم صرفند- وهي ضيعة قائمة على تلة قرب البحر على بعد 14 ميلاً إلى شمالي صور و 8 أميال إلى جنوبي صيدا وأما المدينة القديمة فكانت عند البحر وعلى شواطئه تمتد خرائبها ميلاً أو يزيد.'),
(2183, 'صَرُور', 'اسم عبري معناه (( حزمة وربما حصاة )) أحد أسلاف شاول ( 1 صم 9: 1 ).'),
(2184, 'صَرُوعة', 'اسم عبري معناه (( مضروب، أبرص )) أم يربعام ( 1 مل 11: 26 ).'),
(2185, 'صَرُويَة أو صُروُِيَّة', 'اسم عبري غالباً معناه (( المعطر بالميعة )) قابل ضرو بالعربية. اخت داود وأم يوآب ( 1 صم 26: 6 و 1 أخبار 2: 16 ).'),
(2186, 'صَرِي', 'اسم عبري معناه (( مكّون أو صائغ )) ابن يدوثون ( 1 أخبار 25: 4 ) وفي العدد 11 سمي يصرى.'),
(2187, 'صَريرة', 'انظر (( صردة )).'),
(2188, 'صُعود', 'انظر (( المسيح )).'),
(2189, 'صَعَنَِيم', 'موضع على تخم نفتالي ( يش 19: 33 ) بقرب قادش ( قض 4: 11 ) وهو صعنايم انظر (( بلوطات صعنايم )) ولعلها خان التجار على بعد ثلاثة أميال شمالاً إلى الشمال الشرقي من جبل تابور. أما كوندر فيتبع السبعينية التي يقرأ فيه اسم هذه المدينة بسمين وهي كلمة مؤلفة من حرف الباء واسم العلم ويعتقد أن الموضع كان قريباً جداً من بسوم غربي بحر الجليل على بعد ثلاثة أميال من جبل تابور حوالي خمسة أميال غربي الطرف الجنوبي من بحر الجليل.'),
(2190, 'صَعنايم', 'انظر (( صعننيم )).'),
(2191, 'صَعير', 'اسم عبري معناه (( صغير )) موضع في ادوم حيث غلب يورام الادوميين ( 2 مل 8: 21 راجع و 2 أخبار 21: 9 ). قيل أنها صعير الحالية التي هي قرية تبعد خمسة أميال عن حبرون إلى الشمال الشرقي حيث قبر عيسو.'),
(2192, 'صَفا', 'باليونانية كيفاس علم مأخوذ عن الآرامية كيفا أي صخرة أو حجر، وبالعربية الصفاة الصخرة الصلبة الملساء. وهو اسم أعطاه يسوع المسيح لسمعان ويقابله باليونانية بطرس ( يو 1: 42 و 1 كو 1: 12 و 3: 22 و 9: 5 و 15: 5 و غلا 2: 9 ).'),
(2193, 'صَفاة', 'اسم كنعاني معناه (( برج حارس )) مدينة كنعانية إلى جنوبي البلاد نحو حدود ادوم أعطيت لسبط شمعون واحتلها بنو شمعون بمؤازرة يهوذا وسموها حرمة ( قض 1: 17 ) راجع (( حرمة )).وادي صَفاتة:واد في يهوذا بقرب مريشة ( 2 أخبار 14: 10 ) وهناك واد ينحدر إلى بيت جبرين ومن ساحل الفلسطينيين ويظن بعضهم أنه الوادي المشار إليه ههنا.'),
(2194, 'صَفارَد أو سَفَارَد', 'هو موضع كان فيه بعض اهل السبي من أورشليم ( عو 20 ) قيل أنها اسبانيا. ويرجّح أنها باردس في آسيا الصغرى إذ أن اليهود الذين كانوا هناك في عصر الفرس دعوا ساردس باسم صفارد.'),
(2195, 'صَفَرَ يَصْفُرُ صَفِيرا', '( 1 ) علامة الهزء ( 1 مل 9: 8 وار 19: 8 وحز 27: 36 ومي 6: 16 ).( 2 ) علامة النداء ( اش 5: 26 و 7: 18 وزك 10: 8 ).'),
(2196, 'صَفْصَاف', '( 1 ) شجرة من النوع الذي يسمى باللاتينية Salix وتسمى صفصافة بالعبرية ( حز 17: 5 ) ومنها أصناف كثيرة في فلسطين.( 2 ) اسم لشجرة أخرى اسمها بالعبرية عرابة. كان العبرانيون يجعلون من أغصانها ومن أغصان غيرها من الشجرة مظالاً في عيد المظال ( لا 23: 40 ) وكانت تنبت على مجاري المياه ( اش 44: 4 ) وتحيط ببيهموث العظيم ( اي 40: 22 ) وكانت الشجرة التي علق المسبيون في بابل أوتارهم عليها ( مز 137: 2 ). ولكن الترجمة السبعينية والفولغاتا نقلتا هذه الكلمة والكلمة الأولى بلفظة واحدة وهي الصفصاف. ولعله الصفصاف المستحي الذي يسمى باللاتينية Salix babylonica الذي ينمو كثيراً في وادي الفرات وفلسطين. ومع ذلك فإن بعض المفسرين ذهبوا إلى أن الشجر المعروف في العربية بالغرب واللفظة العبرية المماثلة لها (( عرابة )) تدل على الحور الفراتي المعروف باللاتينية باسم Populus euphratica.وّادِي الصَفْصَاف:واد على تخم موآب الجنوبي ( اش 15: 7 ). وهو وادي الاحصى الحالي وإلى جانبه سهل صغير ينمو فيه الصفصاف ولعله وادي العربة ( عا 6: 14 ).'),
(2197, 'صُفُوف', 'تشير إلى العسكر إذا اصطف على نظام معلوم للتسليم أو الحركات الحربية ( 2 مل 11: 8 و 15 و 2 أخبار 23: 14 ).'),
(2198, 'صِفُّور', 'اسم موآبي معناه (( عصفور )) أبو بالاق ملك موآب ( عد 22: 3 و 4 و 10 و 16 و 23: 18 ويش 24: 9 وقض 17: 25).'),
(2199, 'صِفُّورة', 'اسم مدياني معناه (( عصفورة )) ابنة يثرون الكاهن المدياني اقترنت بموسى وولدت له ابنين ( خر 2: 21 و 22 ). قاومت ختانة ابنها الثاني ولكن فيما كانت العائلة سائرة إلى مصر وكانت حياة زوجها في خطر بسبب نكث العهد رضخت ( خر 4: 18-26 ) ولعلها ذهبت إلى أبيها مع ولديها في ذلك الوقت ولكن الأرجح أنها رافقت موسى إلى مصر وبعد الخروج فيما كان قائد بني إسرائيل يدنو من جبل سيناء ارسلت إلى يثرون لتعلمه بما صنع الله لموسى ولبني إسرائيل وكيف أن الرب أخرجه من مصر ( خر 18: 1 ) وقد عاد معهم يثرون إلى رفيديم ( خر 18: 2-6 ).'),
(2200, 'صفنات فعنيح', 'اسم مصري قديم معناه قوت الأرض هو الحي او هذا الحي وهو اسم يوسف الذي اطلقه عليه فرعون(تك 41 : 45).'),
(2201, 'صفنيا', 'اسم عبري معناه (( يهوه يستر، يكنز ))( 1 ) لاوي قهاتي ( 1 أخبار 6: 36-38 ).( 2 ) كاهن معسيا واحد رسل الصلة بين الملك صدقيا وارميا ( ار 21: 1 و 37: 3 ). قرأ لارميا رسالة واردة له من بابل من نبي كاذب يدعى شمعيا طلب فيها هذا الأخير إلى صفنيا أن يزجر ارميا ( ار 29: 24-32 ) كانت له نظارة الهيكل وكان الكاهن الثاني بعد سرايا. وبعد أن استولى البابليون على أورشليم سبق صفنيا إلى الموت في ربلة ( 2 مل 25: 18-21 وار 52: 24-27 ).( 3 ) رجل عاش ابنه يوشيا في أيام زربابل وزكريا النبي ( زك 6: 10 و 14).( 4 ) نبي يعود نسبه إلى حزقيا إذ أنه الجيل الرابع منه ( صف 1: 1) ويرّجح أن يكون هذا الجلد هو الملك حزقيا نفسه لموافقة الزمن. إذ أن النبي نفسه عاش في أيام يوشيا الملك.سفر صنفنيا هو تاسع الأنبياء الصغار ومن التاريخ المذكور في افتتاحية السفر ( إصحاح 1: 1) نعلم عن الزمن الذي عاش فيه على وجه التحقيق فإنه لا يذكر جت عند ذكره مدن الفلسطينيين ( إصحاح 2: 4) ولأن نينوى كانت لا تزال قائمة ( عدد 13) ولعدم إشارته إلى الكلدانيين نقدر أن نحدّد زمانه. أن أساس نبوته هو ذلك التعليم العظيم عن دينونة شاملة.( 1 ) الدينونة الشاملة كالطوفان في الإفناء ( إصحاح 1: 2 و 3 ) وإن الوثنية ستقطع من أورشليم ( الأعداد 4-6 ) وسيفتقد الله خطيئة يهوذا فإنَ الله يعد ذبيحة عظيمة ( أعداد 7-13) وسيكون ذلك اليوم يوم سخط على الناس جميعاً بسبب فجورهم ( أعداد 14-18) وعند اقتراب الجحافل السكّيثية أعلن النبي قرب مجيء يوم الرب.( 2 ) الدعوة إلى التوبة هي طريق النجاة الوحيدة ( إصحاح 2: 1 و 2 ) ولاسيما حث المساكين وخائفي الله أن يطلبوه لعلهم يجدون خلاصاً ( عدد 3 ) وهذا يؤكده أن الله سيجازي الأمم الباقية بسبب شرها ( أعداد 4: 15) ولن تنجوا أورشليم لأنها لا تتوب والرب في وسطها عادل ( إصحاح 3: 1-8 ).( 3 ) النتيجة المباركة للدينونة. تعود الأمم إلى الرب ( العددان 9 و 10 ) بقية بني إسرائيل تتكل على الرب وستكون مقدسة ( الأعداد 11-13 ) والرب سيملك بمجد في وسط شعبه ( الأعداد 14-18 ) وهو سيجمعهم ويصيرهم (( تسبيحة )) في الأرض كلها ( العددان 19 و 20 ).ويبدو أن النبوة أعلنت قبل الإصلاح الديني الذي قام به يوشيا في سنة 621 ق.م. ( 2 مل 22: 3 و 2أخبار 34: 8 إلى 35: 9 ). وكان صفنيا معاصراً لحبقوق وتنبأ بضع سنين قبله ويمكن أن تعود نبوته إلى زمن الاحتلال السكيثي لشواطئ البحر الأبيض المتوسط ( بين 630 و 624 ق.م. )'),
(2202, 'صَفْو', 'اسم عبري معناه (( حراسة )) وهو ابن اليفاز ابن عيسو. انشأ قبيلة ( تك 36: 11 و 15 ) ويكتب أيضاً اسمه صفي ( 1 أخبار 1: 36 ).'),
(2203, 'صِفُون', 'اسم عبري معناه (( حراسة )) هو ابن جاد ( عد 26: 15 ) ويدعى أيضاً صفيون ( تك 46: 16 ).'),
(2204, 'صِفُونيون', 'المتسلسلون من صفون ( عد 26: 15 ).'),
(2205, 'صَفِي', 'انظر (( صفو )).'),
(2206, 'صِفِيون', 'انظر (( صفون )).'),
(2207, 'صِقْلَغ', 'مدينة في الجنوب الأقصى من يهوذا ( يش 15: 31 ) أعطيت بعد حين لشمعون ( يش 19: 5 و 1 أخبار 4: 30 ). وفي زمن شاول كانت بين أيدي الفلسطينيين. وأعطاها ملكهم أخيش لداود ( 1 صم 27: 6 و 1 أخبار 12: 1-22 ). ولما كان داود بعيداً عنها غزاها العمالقة وضربوها وأحرقوها فلحقهم داود وأدركهم واسترد الغنيمة وأرسل منها إلى أصحابه في مدن كثيرة ( 1 صم 30: 1-31 و 2 صم 1: 1 و 4: 10 ). وبعد استيلاء داود على صقلغ صارت نهائياً ليهوذا ( 1 صم 27: 6 ). وسُكنت بعد السبي ( نحميا 11: 28 ). ويرّجح أنها نل الخويلفة الذي يبعد نحو 10 أميال شرقي تل الشريعة.'),
(2208, 'صَلَبَ يَصْلُبُ صَلباً. صَلِيب', 'صلب الضحية تعليقها على صليب تنفيذاً لحكم الإعدام فيها. وكان يتم ذلك بربط اليدين والرجلين به أو بصورة أفظع بتسمير عن طريق الأجزاء اللحمية. وكانت طريقة القصاص هذه معروفة لدى أمم كثيرة. فقد حكم الاسكندر الكبير على ألف صوري بالصلب. وروى يوسيفوس أن كورش في الأمر الذي أصدره بإعادة اليهود من بابل هدّد بالصليب كل من سعى أن يحول دون تحقيق أمره هذا. وقد توعد داريوس الفارسي بهذه الميتة من يخالف أوامره ( عز 6: 11 ). وقد صلب انطيوخوس ابيفانس يهوداً أتقياء رفضوا أن يذعنوا لأمره بترك دينهم وقد صلب اسكندر يناويس أعداءه على رواية يوسيفوس. وأما عند الرومان فكان الصلب قصاصاً للعبيد أو لمن يرتكب أقبح الجرائم. وأما المواطن الروماني العادي فقد عفاه القانون صراحة من هذا القصاص ولكن في ظل الإمبراطورية فرض على المواطنين أنفسهم حتى ألغاه قسطنطين الملك لأسباب دينية. وكثيراً ما كان يسبق الصلب تعذيب الضحية بالجلد ( مت 27: 26 ومر 15: 15 ويو 19: 1 ). \nوبعد هذا التبريح كان عليه أن يحمل صليبه إلى حيث يصلب. ويروي يوسيفوس أن الواليين فلوروس وتيطس جلداً من اعد للصلب. ولما كانت الضحية تعلق على الصليب تعليقاً فإنها ما كانت تموت إلا بعد فعل الجوع والعطش وأحياناً هذه كانت الحال لما كانت اليدان والرجلان مسمرتين بالمسامير. وإذا كان من الضروري لسب من الأسباب التخلص من الضحايا قبل دنو أجلهم كان يوضع حد لحياتهم بكسر سيقانهم كما صنع باللصين المصلوبين مع يسوع ( يو 19: 31-33 ). وقد صلب كثير من اليهود بعد استيلاء تيطس على أورشليم.\nوأما لفظة الصليب فغير واردة في العهد القديم وقد استعمل يسوع هذه الكلمة بمعنى مجازي ( مت 10: 38 و 16: 24 ) ومن وصف حادثة الصلب يتبين أن الصليب كان خشباً وكان ثقيلاً، ولكن ما كان ثقله يتجاوز قدرة رجل قوي على حمله ( مت 27: 32 ومر 15: 21 ولو 23: 26 ويو 19: 17 ). ومن الصعوبة بمكان إذن أن يكون من ذلك الحجم الضخم الذي تصوره بعض الرسامين. وكان يرفع عن الأرض قبل أن تكون الضحية قد علقت عليه أو بعد. وعلى الأرجح وفي أغلب الأحوال، قبل رفعه. وللصلبان نماذج رئيسية ثلاثة: أحدها المدعو صليب القديس اندراوس وهو على شكل × وثانيهما بشكل + وثالثهما بشكل السيف. وهو المعروف بالصليب اللاتيني. ولعل صليب المسيح كان من الشكل الأخير كما يعتقد الفنانون، الأمر الذي كان يسهل وضع اسم الضحية وعنوان علتها على القسم الأعلى منه ( مت 27: 37 ومر 15: 26 ولو 23: 38 ويو 19: 19 ). وإلى موت المسيح وحتى بعده كان الصليب علامة الذل والعار ( يو 19: 31 و 1 كو 1: 23 وغلا 3: 13 وفي 2: 8 وعب 12: 2 و13: 13 ) وحمل الصليب كان يعني حمل الإهانة ولكن بعد الام يسوع صار أتباعه ينظرون إلى الصليب نظرة مختلفة بالكلية فقد افتخر بولس بصليب المسيح ( غلا 2: 16 وكو 1: 20 ).');
INSERT INTO `wp_my_bible_dictionary` (`id`, `term`, `definition`) VALUES
(2209, 'صِلّتاي', 'اسم عبري معناه (( يهوه ظل أو ملجأ )) وهو أحد رؤوس الألوف الذين التحقوا بداود في صقلغ ( 1 أخبار 12: 20 ).'),
(2210, 'صَالَح يُصَالِحُ مُصَالحَة', '( اف 2: 16 ) هي أن يعود الإنسان لرضى الله ونعمته بواسطة كفارة يسوع المسيح وغلبته ويشترك في ذلك الطرفان فإن الله يصالح الإنسان إذ يكفر عنه المسيح بذبيحته ويصالح الإنسان الله إذ يذعن لإرادته كأب حنون وينتزع من نفسه الكفر فيتوب إلى الرب ويحبه. وقد أعطى الله الرسل كسفراء عن المسيح أن يتمموا خدمة المصالحة بين الله والناس ( 2 كو 5: 18-20 ). وقد بحث هذا الموضوع بتفصيل في رسائل العهد الجديد ( رو 3-8 وعب 7-10 الخ ).\nأما كفارة المسيح بذبيحته فهي أساس الإيمان المسيحي وبواسطتها يعود الخاطئ إلى رضى الله مع كونه بالطبيعة ابن الغضب ويخلص من الدينونة ويصير وارث الحياة والمجد الأبديين وبواسطتها تستر الخطايا ( مز 32: 1 ) إذ يصير المسيح لعنة لأجلنا. والمسيح كفارة ليس لخطايانا فقط بل لخطايا كل العالم ( 1 يو 2: 2 و 4: 10 ).'),
(2211, 'صَلْصَح', 'موضع في نصيب بنيامين بقرب قبر راحيل ( 1 صم 10: 2 ).'),
(2212, 'صَلُفْحَاد', 'اسم عبري معناه (( ظل ضد الخوف أي حماية من الخوف )) وهو أحد أعقاب منسى في البرية ولم يعقب سوى بنات فحكم بأن ترث الإناث إذا لم يكن وارث سواهن من الذكور على شرط الآ يتزوجن خارج سبطهن ( عد 26: 33 و 27: 1 و 7 و 36: 2 و 6 و 10 و11 ويش 17: 3 و 1 أخبار 7: 15 ).'),
(2213, 'أَصْلْع', 'من ذهب شعر رأسه لغير علة البرص ( لا 13: 41 ).'),
(2214, 'صِلّ', 'هو نوع من الحيات ( تث 32: 33 واي 2: 14 و 16 ومز 58: 4 واش 11: 8 ورو 3: 13 ) السامة وتعيش في الأشجار أو الأسراب ولعل الكلمة العبرية المترجمة صل تشير إلى الفصيلة التي تسمى باللاتينية Naja haje ( اش 11: 8 ) وهي التي تعيش في فلسطين ومصر. وهي من نوع الكوبرا الهندية التي يستعملها الحواة عندما يمثلون.'),
(2215, 'صِلَّة', 'اسم عبري معناه (( ظل أو ملجأ )) إحدى امرأتي لامك ( تك 4: 19 و 22 و 23 ).'),
(2217, 'صَِلْمُنَّاع', 'اسم مدياني ربما كان معناه (( لم يعد له ظل أي ملجأ أو أن الإله صلم أي المظلم أو زحل يحكم )) وهو أحد ملكي مديان قتله جدون ( قض 8: 5-21 ومز 83: 11 ).'),
(2218, 'صَلْمُون', '( 1 ) أحد أبطال داود ( 2 صم 23: 28 ) ويسمى أبضاً عيلاي ( 1 أخبار 11: 29 ).( 2 ) جبل مشجر بقرب شكيم قطع منه ابيمالك أغصان لإحراق برج شكيم ( قض 9: 48 و 49 ومز 68: 14 ) ويرجّح أنه جزء من جبل جزريم.'),
(2219, 'صَلْمُونَة', 'اسم عبري معناه (( مظلم ذو ظل )) محلة لبني إسرائيل في البرية ( عد 33: 41 و 42 ) ولعلها شرقي جبل هارون عند بئر مذكور.'),
(2220, 'صَلىّ يُصِلّي صَلَوة', 'الصلاة شركة مع الله شخص قادر ويريد أن يستمع إلينا وأنه خلق العالم ويحفظه ويرعى خلائقه كلها وليس هو عبداً لنواميسه وأنه ينشئ ما يريد بمراقبته لنواميس الطبيعة هذه. وهو قادر على التأثير في قلوب الناس وأذهانهم أكثر بكثير مما يستطيع الإنسان في حمل أقرانه على العمل. لقد سبق الله فوضع الصلاة واستجابتها. كانت له مقاصد منذ البدء وهو يتمم هذه المقاصد بالطريقة التي وضع فيها الكون ونواميسه وأيضاً بحضوره الدائم في هذا الكون وضبطه ومراقبته.\nلقد فطر الإنسان على الصلاة فهو في حاجته القصوى يصرخ إلى الله، والله يطلب الصلاة من الناس جميعاً ولكن الصلاة تستجاب إن قامت بيننا وبينه علاقة مستقيمة. فلصلاة المقبولة لديه هي فقط تلك التي يرفعها البار وصلاة الأشرار رجس ( ام 15: 29 و 28: 9 ). فمن عاد من الخطيئة يستطيع وحده أن يتقرب إلى الله بالصلاة. وأما الذي عصى أمر الله فيدنو منه إذا رجع من عصيانه وتاب. الصلاة هي شركة الابن مع أبيه السماوي. هي سجود وشكر واعتراف وطلب ( نح 1: 4-11 ودا 9: 3-19 وفي 4: 6 ). وقد الزم بها شعب الله منذ البداءة.وهكذا تبدو الصلاة التعبير الطبيعي عن الشعور الديني وتمنح بركات الله استجابة للصلاة ( 1 مل 9: 3 وحز 36: 37 ومت 7: 7 ). والله ينصت لكل دعاء يقدم له باستقامة واسمه في الكتاب (( سامع الصلاة )) ( مز 65: 2 ) وإذا ذكر يعقوب الرسول التاريخ قال: (( طلبة البار تقتدر كثيراً في فعلها )) ( يع 5: 16 ) وخاطب يسوع تلاميذه قائلاً: (( مهما سألتم باسمي فذلك أفعله )) ( يوحنا 14: 13 ). وشعب الله يقدم له الطلبات ويدعه يقرر إذا كان من الحكمة أن يستجيب الطلبة أو لا يستجيب لأنهم عالمون بأن الله وحده يعرف إذا كانت تلك الاستجابة نافعة لهم أو للملكوت أو عائدة لمجد الله. وقد حدد شرط الصلاة الرسول يوحنا حيث قال: (( هذه هي الثقة التي لنا عنده أنه عن طلبنا شيئاً حسب مشيئته يسمع لنا )) ( يوحنا 5: 14 ). ويكون الجواب حسبما نريده إن كنا حقاً من المستنيرين. وكثيراً ما يبارك الله أبناءه ببركة أعظم عندما لا يلبي لهم طلباتهم. وإذا صلوا فهم يرغبون إليه ألا يلبي نداءهم إن كان يرى أن ذلك خيراً لهم.والصلاة انفرادية واجتماعية في كل الأيام وبين الشعوب جميعاً فتقدم في البيت أو في الكنيسة ( اع 2: 42 و 13: 2 و 20: 17-11 ). ويجب علينا أن نصلي لأجل غيرنا كما نصلي لأجل أنفسنا ( يع 5: 16 )، ولاسيما لأجل الملوك وكل الذين هم في منصب ( 1 تي 2: 2 )، ولأجل الأقرباء والخلان والأعداء والذين يلعنوننا ( مت 5: 44 ). ويجوز لنا أن نطلب جميع ما نحتاج إليه للجسد والنفس حتى خبزنا اليومي على أن نطلب أولاً ملكوت الله وبره ( مت 6: 33 ).يجب أن نصلي باسم المسيح لأن الإنسان الخاطئ لا يستطيع أن يدنوا إلى الله. ويجب أن نقترب إليه غير فارضين عليه أن ينال حقاً من حقوقنا ولكن باسم الذي غسل خطايانا بدمه وجعلنا كهنة الله.توّجه الصلاة لله كثالوث قدوس كما توّجه أصلاً للآب باسم الابن وبواسطة الروح القدس كما ترسل لكل من الاقانيم الثلاثة لمساواة الابن والروح في الجوهر والأزلية مع الآب. والصلاة لكل من الآب والابن والروح القدس متضمنة في البركة الرسولية (( نعمة ربنا يسوع المسيح ومحبة الله وشركة الروح القدس فلتكن معكم جميعاً )) ( 2 كو 13: 14 ). وكانت الصلاة ترفع للمسيح الناهض من القبر وكان المسيحيون يدعون باسمه ( 1 كو 1: 2 ) وصلى استفانوس إليه مباشرة، وبولس يضرع إليه ويشكر. والمخلصون يرفعون له المجد والسجود ( اع 7: 59 و 60 و 2 كو 12: 8 و 9 و 1 تس 3: 11 و 1 تي 1: 12 ورؤ 1: 5 و 6 ) أما الروح القدس فيعلمنا كيف نصلي ( رو 8: 26 ).وكان جميع الآباء والقديسين مشهورين بالصلاة ومن جملتهم إبراهيم ( تك 20: 17 ) ويعقوب ( تك 32: 26-31 ) وموسى ( عد 11: 2 وتث 9: 19 و 20 ) ويشوع ( يش 10: 12 ) وصموئيل ( 1 صم 12: 18 ) وداود ( اطلب كل مزاميره ) وإيليا ( 1 مل 17: 1 و 18: 42 و 45 ويع 5: 17 و 18 ) واليشع ( 2 مل 4: 33 و 34 ) وحزقيا ( 2 مل 19: 15-20 و 20: 2-6 ) ودانيال ( دا 6: 10 ) وحنّة ( 1 صم 1: 10-17 ) وحنّة النبية ( لو 2: 37 ) والرسل ( اع 1: 14 و 24 و 2: 42 و 4: 31 و 6: 4 و 8: 15 و 12: 12 و 16: 25 و 26 و 20: 36 و 21: 5 ورو 1: 9 و 12 و 1 تس 5: 17 ).وكثيراً ما انفرد المسيح مخلصنا في البراري ليصلي ( مت 14: 23 و 26: 39 ومر 1: 35 ولو 5: 16 ) وقد علم تلاميذه كيف يصلون ( مت 6: 9-13 ولو 11: 2-4 ).وتستعمل بعض الكنائس المسيحية كتباً للصلاة تجمع فيها الطلبات والابتهالات والاعترافات التي نطق بها القديسون في كثير من أجيال الكنيسة وهي تعبّر عن اختبارات روحية، وتستعمل كنماذج للمؤمنين فيما يجب أن تحتوي عليه الصلاة. وهي تعين على جمع أفكار العابدين ووضعها بصورة متناسقة متوافقة. وأما الصلوات الارتجالية ففضلها أنها توّضح أفكار الفرد الذي يصلي بنوع خاص وتحفظ حريته وتنوع حسب الظروف.ساعات الصلاة:(( الله روح والذين يسجدون له فبالروح والحق ينبغي أن يسجدوا )) ( يو 4: 24 ). ولا تنحصر الصلاة في موضع ولا في زمن بل يجوز أن يصلي في أي موضع كان ( 1 تس 5: 17 ) على أنه يليق حفظ أوقات معينة للصلاة فكان اليهود والرسل يصلون عند الساعة الثالثة والسادسة والتاسعة من النهار وعند بداية الليل ونهايته وعند مناولة الطعام ( مز 55: 17 ودا 6: 10 ولو 18: 1 واع 3: 1 و 10: 3 و 9 و 30 ). وألف المسيحيون الأولون الصلاة والشكر عند تناول الغذاء ( 1 تي 4: 3 و 4 ). وقد أخذت بعض الكنائس عن اليهود تلاوة مزامير وصلوات معينة في هذه الساعات.وهاك جدول أهم الصلوات المدونة في الكتاب المقدس:الأشخاص 			الشواهد 		المواضيعآسيا 		2 أخبار 14: 11		عندما حارب زارح الكوشيابرام			تك 15: 2 و 3			لأجل ولدإبراهيم 		تك 17: 18			لقبول إسماعيلاجور		ام 30: 7-9			للاعتدالارميا 		ار 24:7-9 و 19-22		وقت الجوع العظيمارميا 		ار 15: 15-18		للتعزيةاستفانوس		اع 7: 59 و 60 	     تسليم نفسه للرب. طلب المغفرة لقاتليهبنو إسرائيل 		تث 21: 7-8			كفارة عن القتل إذ لم يكشف القاتلبنو إسرائيل 		تث 26: 5-10			شكر عند تقديم الباكوراتبنو إسرائيل 		تث 26: 13-15		صلاة سنة تقديم العشوراليشع 		2 مل 6: 17			لفتح عيني خادمهاليشع 		2 مل 6: 18 			لتضرب جماعة ارام بالعمىاليشع 		2 مل 6: 20			لتفتح عيون جيش اراماليعازر الدمشقي	تك 24: 12-14		لأجل ارشاد الله أمر في الزوجةالمعينة لاسحاقايليا 			1 مل 17: 20 و 21		ليعيش ابن الأرملةايليا			1 مل 18: 36 و 37		ليشهد الله بهايليا 			1 مل 19: 4			للموتحبقوق		حب 3: 1-19			لاحياء عمل الربحزقيا		حز 9: 8			لأجل خلاص بقة بني إسرائيلحزقيا 	2 مل 19: 15-19 اش 37: 16-20	 لحماية الله مملكته من سنحاريبحزقيا 	2 أخبار 30: 18-19			لأجل من أكلوا الفصح بدون استعداددانيال 	دا 9: 4-19				لأجل إعادة أورشليمداود 	2 صم 7: 18-29			لأجل البركة على بيتهداود 	2 صم 24: 17				لكي لا ينتقم الله من الشعب لأجلخطيئته في إحصائه بني إسرائيلداود 	1 أخبار 29: 10-19			شكر الله عند نهاية حياته وطلبالحكمة لسليمانداود 	مزامير متعددة 				مواضيع مختلفةالرسل 	اع 1: 24 و 25 			لأجل الإرشاد في انتخاب رسولسليمان 	1 مل 3: 6-9				لإعطائه الحكمة في تدبير الشعبسليمان 	1 مل 8: 23-53